import{e as p}from"./app.630af5f2.js";import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";const e={},r=p('<h1 id="\u7B2C\u516B\u7AE0-\u6BD2\u763E" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u516B\u7AE0-\u6BD2\u763E" aria-hidden="true">#</a> \u7B2C\u516B\u7AE0 \u6BD2\u763E</h1><p>\u98CE\u4ECE\u5C71\u5916\u9001\u6765\u6D53\u6D53\u7684\u79CB\u610F\uFF0C\u81EA\u7136\u754C\u5F00\u59CB\u51CB\u96F6\uFF0C\u9ED1\u591C\u6E10\u957F\u4E8E\u767D\u663C\u3002</p><p>\u6E05\u6668\u975E\u5E38\u51C9\u723D\uFF0C\u70ED\u4E86\u6574\u6574\u4E00\u590F\uFF0C\u4EBA\u4EEC\u603B\u7B97\u53EF\u4EE5\u5598\u53E3\u6C14\u4E86\u3002</p><p>\u9E21\u8FC7\u4E09\u5DE1\uFF0C\u9732\u6C34\u8FD8\u6CA1\u6709\u892A\u5C3D\uFF0C\u9752\u77F3\u677F\u8DEF\u4E0A\u6643\u6643\u60A0\u60A0\u5730\u8FC7\u6765\u4E00\u9876\u4E8C\u4EBA\u5C0F\u8F7F\uFF0C\u4E00\u4E2A\u4FCF\u4E3D\u7684\u4E2B\u5934\u8D70\u5728\u524D\u9762\u3002</p><p>\u57CE\u95E8\u521A\u5F00\uFF0C\u4E24\u4E2A\u5B88\u57CE\u536B\u5175\u6253\u7740\u5475\u6B20\u6765\u56DE\u8D70\u52A8\uFF0C\u770B\u5230\u5C0F\u8F7F\u8FC7\u6765\uFF0C\u6765\u4E86\u70B9\u7CBE\u795E\uFF0C\u597D\u6B79\u6709\u70B9\u4E8B\u53EF\u5E72\u4E86\u3002</p><p>\u300C\u7AD9\u4F4F\uFF0C\u68C0\u67E5\u3002\u300D</p><p>\u4E2B\u5934\u6C89\u4E86\u8138\uFF0C\u300C\u778E\u4E86\u72D7\u773C\uFF0C\u4E5F\u4E0D\u770B\u770B\u662F\u8C01\u3002\u300D</p><p>\u300C\u54DF\uFF0C\u5988\u7684\uFF0C\u5C0F\u5C0F\u5E74\u7EAA\u5634\u633A\u81ED\uFF0C\u7BA1\u4ED6\u5929\u738B\u8001\u5B50\u90FD\u8981\u68C0\u67E5\u3002\u300D</p><p>\u4E00\u4E2A\u8863\u7740\u4E0D\u6574\u4F46\u50CF\u4E2A\u5C0F\u957F\u5B98\u6A21\u6837\u7684\u5BB6\u4F19\u4ECE\u57CE\u95E8\u697C\u91CC\u94BB\u4E86\u51FA\u6765\uFF0C\u8FB9\u6263\u8863\u88F3\u8FB9\u9A82\u9053\uFF0C\u300C\u5435\u6B7B\uFF0C\u5927\u6E05\u65E9\u7684\uFF0C\u4E0D\u8BA9\u4EBA\u7761\u5566\u3002\u300D</p><p>\u4E24\u4E2A\u5175\u7ACB\u6B63\u656C\u793C\uFF0C\u300C\u4E2D\u961F\u957F\u3002\u300D</p><p>\u4E2D\u961F\u957F\u51D1\u5230\u8F7F\u95E8\u8FB9\uFF0C\u8BF4\uFF0C\u300C\u6211\u4EB2\u81EA\u770B\u770B\u4E0D\u5C31\u5F97\u5566\u3002\u300D</p><p>\u4E2B\u5934\u5FD9\u53EB\u9053\uFF0C\u300C\u91CC\u9762\u662F\u53BF\u957F\u592A\u592A\u3002\u300D</p><p>\u5979\u558A\u5F97\u8FDF\u4E86\uFF0C\u4E2D\u961F\u957F\u7684\u624B\u5DF2\u7ECF\u63ED\u5F00\u4E86\u8F7F\u5E18\uFF0C\u4E0E\u91CC\u9762\u7684\u4EBA\u53CC\u76EE\u76F8\u5BF9\u3002</p><p>\u771F\u662F\u51A4\u5BB6\u805A\u9996\uFF0C\u51B7\u5982\u971C\u5728\u57CE\u95E8\u53E3\u78B0\u5230\u7684\u7ADF\u7136\u662F\u5979\u6700\u538C\u6076\u4E00\u8F88\u5B50\u6700\u4E0D\u60F3\u89C1\u5230\u7684\u4EBA\uFF0C\u4E8C\u559C\u5B50\u3002</p><p>\u4E8C\u559C\u5B50\u4E00\u6123\uFF0C\u968F\u5373\u6EE1\u9762\u5806\u6B22\uFF0C\u300C\u539F\u6765\u662F\u592A\u592A\uFF0C\u6807\u4E0B\u771F\u662F\u8BE5\u6B7B\u3002\u300D</p><p>\u51B7\u5982\u971C\u8C61\u541E\u4E86\u4E00\u53EA\u82CD\u8747\uFF0C\u6076\u5FC3\u5F97\u60F3\u5410\u3002\u4E8C\u559C\u5B50\u7684\u7B11\u5BB9\u91CC\u4F3C\u4E4E\u4E5F\u8574\u542B\u7740\u90AA\u6DEB\uFF0C\u4F60\u53BF\u957F\u592B\u4EBA\u4EC0\u4E48\u4E86\u4E0D\u8D77\uFF0C\u8001\u5B50\u4E0D\u4E5F\u5DEE\u70B9\u6252\u4E86\u4E2A\u7CBE\u5149\u5417\uFF1F</p><p>\u5979\u7A81\u7136\u8BF4\uFF0C\u300C\u91D1\u5B9D\uFF0C\u638C\u4ED6\u7684\u5634\u3002\u300D</p><p>\u4E8C\u559C\u5B50\u8868\u60C5\u5446\u6EDE\u4E86\uFF0C\u7ED3\u7ED3\u5DF4\u5DF4\u5730\u8BF4\uFF0C\u300C\u6807\u2026\u2026\u6807\u4E0B\u804C\u8D23\u2026\u2026\u6240\u5728\u2026\u2026\u300D</p><p>\u91D1\u5B9D\u95FB\u8A00\u65E9\u5C31\u51B2\u4E0A\u6765\uFF0C\u72E0\u72E0\u5730\u6247\u4E86\u4ED6\u6B63\u53CD\u4E24\u5DF4\u638C\uFF0C\u77AA\u7740\u4ED6\uFF0C\u9887\u4E3A\u89E3\u6068\u3002</p><p>\u4ECE\u51B7\u5982\u971C\u6124\u6012\u800C\u4EC7\u89C6\u7684\u76EE\u5149\u4E2D\uFF0C\u4E8C\u559C\u5B50\u604D\u7136\u660E\u767D\u4E86\u4EC0\u4E48\uFF0C\u60BB\u60BB\u5730\u6478\u4E86\u6478\u6709\u4E9B\u53D1\u70ED\u7684\u8138\uFF0C\u51B2\u7740\u5C0F\u8F7F\u97A0\u4E86\u4E00\u8EAC\u9053\uFF0C\u300C\u5F97\u7F6A\u592A\u592A\u4E86\u3002\u300D</p><p>\u5C0F\u8F7F\u8FDC\u53BB\uFF0C\u4E8C\u559C\u5B50\u8FFD\u601D\u524D\u4E8B\uFF0C\u8138\u8272\u4E00\u53D8\u518D\u53D8\u3002</p><p>\u4E0D\u8001\u5CF0\u4E0A\u767D\u4E91\u98DE\uFF0C\u8046\u542C\u7740\u5CF0\u9876\u89C2\u97F3\u5EB5\u7684\u66AE\u9F13\u6668\u949F\uFF0C\u51B7\u5982\u971C\u62FE\u9636\u800C\u4E0A\uFF0C\u5FC3\u4E2D\u5145\u6EE1\u7740\u8654\u8BDA\u548C\u8083\u7A46\u3002</p><p>\u6D77\u68E0\u5931\u8E2A\u540E\uFF0C\u5218\u6EA2\u4E4B\u5305\u7792\u4E86\u5927\u90E8\u5206\u7684\u771F\u76F8\uFF0C\u575A\u6301\u4E0D\u80AF\u544A\u8BC9\u5979\u6D77\u68E0\u7684\u4E0B\u843D\uFF0C\u5979\u8FD8\u662F\u80FD\u591F\u731C\u5F97\u51FA\u51E0\u5206\uFF0C\u4E0E\u81EA\u5DF1\u7EDD\u5BF9\u8131\u4E0D\u4E86\u5E72\u7CFB\uFF0C\u4E00\u5FF5\u53CA\u6B64\uFF0C\u5C31\u5FC3\u5982\u5200\u5272\uFF0C\u96BE\u4EE5\u5165\u7720\u3002</p><p>\u5979\u4E0D\u613F\u8FC7\u591A\u8D23\u602A\u4E08\u592B\uFF0C\u4ED6\u7ACB\u573A\u4E0D\u540C\uFF0C\u804C\u8D23\u6240\u5728\uFF0C\u65E0\u53EF\u539A\u975E\u3002\u53EA\u6709\u5C06\u4E00\u5207\u7F6A\u5B7D\u627F\u63FD\u5728\u81EA\u5DF1\u8EAB\u4E0A\uFF0C\u65E5\u65E5\u5FF5\u7ECF\u8BF5\u4F5B\uFF0C\u4F01\u56FE\u6D88\u9664\u4E1A\u5B7D\uFF0C\u51E0\u4E4E\u6BCF\u9694\u6570\u65E5\u5C31\u8981\u5230\u4E0D\u8001\u5CF0\u4E0A\u7684\u89C2\u97F3\u5EB5\u53BB\u70E7\u9999\u3002</p><p>\u9762\u5BF9\u83BD\u83BD\u5927\u5C71\uFF0C\u79C0\u7F8E\u7684\u4E1B\u6797\uFF0C\u6D77\u68E0\u4FCF\u4E3D\u7684\u9762\u5BB9\u4E0D\u77E5\u4E0D\u89C9\u53C8\u6D6E\u73B0\u4E86\u51FA\u6765\u3002</p><p>\u5979\u771F\u7684\u80FD\u5F97\u5230\u6551\u8D4E\u5417\uFF1F</p><p>\u51B7\u5982\u971C\u4F3C\u6709\u70B9\u51B7\uFF0C\u62B1\u7D27\u8EAB\u5B50\uFF0C\u4E00\u58F0\u957F\u957F\u7684\u53F9\u606F\u3002</p><p>\u300C\u554A\u5440\u2026\u2026\u300D</p><p>\u6D77\u68E0\u75DB\u82E6\u5730\u5C16\u53EB\u7740\uFF0C\u4E00\u7F15\u7F15\u4E71\u53D1\u6CBE\u5728\u5E03\u6EE1\u4E86\u5206\u4E0D\u6E05\u662F\u6C57\u6C34\u3001\u6CEA\u6C34\u8FD8\u662F\u9F3B\u6D95\u53E3\u6C34\u7684\u8138\u4E0A\u3002</p><p>\u5979\u8EAB\u65E0\u5BF8\u7F15\uFF0C\u6574\u4E2A\u8EAB\u5B50\u5377\u5367\u5728\u4E00\u4EBA\u89C1\u65B9\u7684\u6728\u5236\u72D7\u7B3C\u4E2D\uFF0C\u9888\u4E0A\u5957\u7740\u4E00\u53EA\u9EC4\u725B\u76AE\u5E26\u72D7\u5708\uFF0C\u6813\u5728\u680F\u6746\u4E0A\u3002</p><p>\u6B64\u65F6\uFF0C\u5979\u72B6\u82E5\u75AF\u5B50\uFF0C\u5728\u7B3C\u91CC\u7FFB\u6EDA\u568E\u53EB\uFF0C\u50CF\u5F97\u4E86\u759F\u75BE\u4E00\u822C\u5267\u70C8\u75C9\u6469\u3002</p><p>\u767D\u5929\u5FB7\u548C\u674E\u8D35\u7AD9\u5728\u7B3C\u5916\u89C2\u770B\u3002\u767D\u5929\u5FB7\u62FF\u7740\u4E00\u6839\u624B\u6756\u4ECE\u6805\u680F\u4E2D\u7A7F\u8FC7\u53BB\uFF0C\u4F7F\u52B2\u6345\u4E86\u6345\u5979\u9F13\u6DA8\u7684\u5976\u5B50\uFF0C\u6D77\u68E0\u604D\u7136\u672A\u89C9\u3002</p><p>\u674E\u8D35\u9053\uFF0C\u300C\u6CA1\u60F3\u5230\u9E26\u7247\u763E\u53D1\u4F5C\u8D77\u6765\u4F1A\u5982\u6B64\u5389\u5BB3\u3002\u300D</p><p>\u767D\u5929\u5FB7\u9053\uFF0C\u300C\u90A3\u662F\u5F53\u7136\uFF0C\u8FD9\u4E48\u591A\u5929\u5916\u718F\u5185\u670D\uFF0C\u8FDE\u7EED\u5F3A\u5316\uFF0C\u8FBE\u4E0D\u5230\u8FD9\u4E2A\u6548\u679C\u624D\u602A\u5462\uFF0C\u5012\u662F\u6D6A\u8D39\u4E86\u8001\u5B50\u4E0D\u5C11\u538B\u7BB1\u5E95\u7684\u597D\u836F\uFF0C\u771F\u6B63\u7EAF\u7684\u54E9\u3002\u300D</p><p>\u300C\u80FD\u9A6F\u670D\u8FD9\u5934\u70C8\u9A6C\uFF0C\u503C\u554A\u3002\u300D</p><p>\u767D\u5929\u5FB7\u7B11\u4E86\u7B11\uFF0C\u300C\u5012\u4E5F\u662F\uFF0C\u8FD9\u4E48\u591A\u5E74\u4E0D\u89C1\uFF0C\u8FD9\u5149\u677F\u513F\u4ED6\u5988\u7684\u8D8A\u53D1\u6807\u81F4\u6709\u97F5\u5473\u4E86\u3002\u300D</p><p>\u300C\u56E2\u957F\u60A8\u603B\u53EB\u5979\u5149\u677F\u513F\uFF0C\u5230\u5E95\u662F\u4E48\u5B50\u610F\u601D\u7F57\u3002\u300D</p><p>\u300C\u4F60\u5C0F\u5B50\u522B\u6025\uFF0C\u4F1A\u660E\u767D\u7684\u3002\u300D</p><p>\u81EA\u4ECE\u4E0A\u6B21\u4EBA\u72D7\u5927\u6218\u540E\uFF0C\u4E0D\u5C11\u4EBA\u5927\u547C\u8FC7\u763E\uFF0C\u8981\u767D\u5929\u5FB7\u591A\u6765\u51E0\u573A\uFF0C\u4E0D\u60F3\u767D\u5929\u5FB7\u53CD\u8D77\u4E86\u79C1\u5FC3\uFF0C\u89C9\u5F97\u8FD9\u4E48\u6807\u81F4\u7684\u4E00\u6735\u82B1\u513F\u8FD8\u6CA1\u7ED9\u81EA\u5DF1\u591A\u91C7\u51E0\u4E0B\u5C31\u8FD9\u4E48\u5B8C\u4E86\u5B9E\u5728\u662F\u66B4\u6B84\u5929\u7269\uFF0C\u4E8E\u662F\u5C06\u6D77\u68E0\u53C8\u79D8\u5BC6\u9001\u5230\u4E86\u767D\u5BB6\u5821\u81EA\u5DF1\u7684\u8001\u5DE2\uFF0C\u8981\u597D\u597D\u8C03\u6559\u8C03\u6559\u5979\u3002</p><p>\u4E0D\u8FC7\u4ED6\u4E5F\u77E5\u9053\u8FD9\u599E\u4ECE\u5C0F\u5C31\u8FA3\u5F97\u5F88\uFF0C\u4E0D\u7136\u4E5F\u4E0D\u4F1A\u6210\u4E00\u65B9\u532A\u9996\uFF0C\u975E\u5F97\u60F3\u5F97\u4EC0\u4E48\u62DB\u964D\u4F4F\u5979\u3002</p><p>\u6700\u597D\u7684\u4E00\u62DB\u5F53\u7136\u5C31\u662F\u5927\u70DF\u4E86\u3002</p><p>\u5B9E\u9645\u4E0A\u5728\u6D77\u68E0\u88AB\u64D2\u7684\u521D\u671F\uFF0C\u9634\u9669\u7684\u767D\u5929\u5FB7\u5DF2\u7ECF\u5728\u5979\u7684\u996D\u98DF\u4E2D\u4E0B\u4E86\u9E26\u7247\u7C89\u548C\u6625\u836F\u7684\u6DF7\u5408\u7269\uFF0C\u5F53\u65F6\u6D77\u68E0\u5C31\u5728\u4E0D\u77E5\u4E0D\u89C9\u4E2D\u5DF2\u67D3\u4E0A\u6BD2\u763E\u3002</p><p>\u73B0\u5728\u6D77\u68E0\u5F53\u7136\u4E0D\u4F1A\u542C\u4ECE\u767D\u5929\u5FB7\u53BB\u5438\u98DF\u9E26\u7247\uFF0C\u767D\u5929\u5FB7\u5C31\u5343\u65B9\u767E\u8BA1\u5730\u5F3A\u704C\uFF0C\u70B9\u71C3\u4E86\u653E\u5728\u9F3B\u5B50\u5E95\u4E0B\u718F\uFF0C\u518D\u5C31\u62FF\u94F6\u53F6\u6765\u5A01\u80C1\uFF0C</p><p>\u8FD9\u8FC7\u7A0B\u5F53\u7136\u4E0D\u90A3\u4E48\u987A\u5229\uFF0C\u6D77\u68E0\u7684\u610F\u5FD7\u975E\u5E38\u575A\u5F3A\uFF0C\u4E5F\u683C\u5916\u6297\u62D2\uFF0C\u603B\u662F\u60F3\u5C3D\u529E\u6CD5\u6765\u53CD\u6297\u3002\u4F46\u767D\u5929\u5FB7\u4E0D\u7740\u6025\uFF0C\u6D77\u68E0\u73B0\u5728\u5728\u548C\u81EA\u5DF1\u6597\uFF0C\u548C\u81EA\u5DF1\u7684\u8EAB\u4F53\u3001\u601D\u60F3\u6597\uFF0C\u5C3D\u65E9\u4F1A\u57AE\u6389\u7684\u3002</p><p>\u4ED6\u6599\u5F97\u4E0D\u9519\uFF0C\u6D77\u68E0\u4E0D\u662F\u795E\uFF0C\u7EC8\u7A76\u53EA\u662F\u4E2A\u666E\u901A\u4EBA\uFF0C\u65E5\u5B50\u4E00\u957F\uFF0C\u6BD2\u763E\u7EC8\u4E8E\u6DF1\u6DF1\u690D\u5165\u4E86\u5979\u7684\u8EAB\u4F53\uFF0C\u4F9D\u8D56\u65E5\u91CD\uFF0C\u518D\u96BE\u6446\u8131\u8FD9\u6BD2\u7269\u7684\u63A7\u5236\u3002</p><p>\u767D\u5929\u5FB7\u8FD9\u5929\u6709\u610F\u65AD\u4E86\u4E00\u5929\uFF0C\u8BD5\u63A2\u4E00\u4E0B\u6D77\u68E0\u7684\u53CD\u5E94\u3002</p><p>\u7ED3\u679C\u975E\u5E38\u7406\u60F3\uFF0C\u6B64\u65F6\u7684\u6D77\u68E0\u8C61\u5782\u6B7B\u7684\u6CE5\u9CC5\u4E00\u626D\u4E00\u626D\u7684\uFF0C\u5728\u7EDD\u671B\u7684\u6DF1\u6E0A\u4E2D\u6323\u624E\u7740\u3002</p><p>\u767D\u5929\u5FB7\u62FF\u51FA\u4E00\u76D2\u9E26\u7247\u818F\uFF0C\u8E72\u4E0B\u8EAB\uFF0C\u6162\u6162\u51D1\u5230\u6D77\u68E0\u7684\u9F3B\u7AEF\u524D\u3002</p><p>\u90A3\u6EA2\u51FA\u6D53\u9999\u7684\u73A9\u610F\u5BF9\u8FD9\u4E9B\u763E\u541B\u5B50\u6765\u8BF4\u7B80\u76F4\u5C31\u662F\u5723\u7269\u3002\u6D77\u68E0\u5728\u6CA1\u5165\u6DF1\u6E0A\u4E4B\u9645\u603B\u7B97\u770B\u5230\u4E86\u4E00\u6839\u6551\u547D\u7684\u7A3B\u8349\uFF0C\u7A81\u7136\u77AA\u5706\u5927\u773C\uFF0C\u8D2A\u5A6A\u5730\u76EF\u7740\u5B83\uFF0C\u4E00\u7728\u4E5F\u4E0D\u7728\u3002</p><p>\u5979\u7684\u53CC\u624B\u4E5F\u6162\u6162\u5730\u4F38\u4E86\u8FC7\u6765\u3002</p><p>\u90AA\u7247\u818F\u53C8\u6536\u56DE\u53BB\u4E86\u4E00\u70B9\uFF0C\u505C\u5728\u6D77\u68E0\u591F\u4E0D\u5230\u7684\u5730\u65B9\u3002</p><p>\u6D77\u68E0\u90A3\u79CD\u7531\u6781\u5927\u7684\u5E0C\u5180\u8F6C\u4E3A\u7EDD\u671B\u7684\u8868\u60C5\u5B9E\u5728\u8BA9\u4EBA\u4E0D\u5FCD\u5352\u7779\uFF0C\u5979\u6162\u6162\u671B\u5411\u4E3B\u5BB0\u7740\u9E26\u7247\u818F\u547D\u8FD0\u7684\u767D\u5929\u5FB7\uFF0C\u5C31\u50CF\u770B\u7740\u4E3B\u5BB0\u4E86\u5979\u7684\u547D\u8FD0\u7684\u795E\u4E00\u822C\uFF0C\u672C\u6765\u832B\u7136\u65E0\u795E\u7684\u5927\u773C\u775B\u4E2D\uFF0C\u4E00\u70B9\u70B9\u5730\u6D41\u9732\u51FA\u4F01\u601C\u7684\u76EE\u5149\u3002</p><p>\u300C\u4F60\u7EC8\u4E8E\u80AF\u9A6F\u670D\u4E8E\u8001\u5B50\u4E86\u5417\uFF1F\u300D\u767D\u5929\u5FB7\u7684\u58F0\u97F3\u5F77\u4F5B\u4ECE\u5929\u9645\u4F20\u6765\uFF0C\u90A3\u4E48\u5A01\u4E25\u548C\u96BE\u4EE5\u6297\u62D2\u3002</p><p>\u6D77\u68E0\u4E0D\u8A00\u3002</p><p>\u534A\u664C\uFF0C\u6162\u6162\u5730\u70B9\u4E86\u4E0B\u5934\uFF0C\u773C\u775B\u4E00\u7728\uFF0C\u4E00\u9897\u6676\u83B9\u7684\u6CEA\u73E0\u4ECE\u773C\u89D2\u6EDA\u4E86\u51FA\u6765\u3002</p><p>\u767D\u5929\u5FB7\u54A7\u5634\u60F3\u7B11\uFF0C\u7EC8\u751F\u751F\u5FCD\u4F4F\uFF0C\u7EE7\u7EED\u7528\u521A\u624D\u7684\u8BED\u8C03\u8BF4\uFF0C\u300C\u90A3\u597D\uFF0C\u8868\u793A\u4E00\u4E0B\uFF0C\u628A\u4F60\u7684\u4E24\u53EA\u811A\u6253\u5F00\uFF0C\u628A\u9A9A\u6D1E\u7FFB\u7ED9\u8001\u5B50\u770B\u3002\u300D</p><p>\u6D77\u68E0\u7684\u6BD2\u763E\u867D\u7136\u8FD8\u5728\u53D1\u4F5C\uFF0C\u4F46\u521A\u624D\u72E0\u55C5\u4E86\u51E0\u53E3\u9999\u6C14\uFF0C\u5E73\u590D\u4E86\u4E00\u70B9\uFF0C\u884C\u52A8\u867D\u7136\u5C3A\u7F13\uFF0C\u8EAB\u4F53\u81F3\u5C11\u8FD8\u662F\u53EF\u4EE5\u81EA\u4E3B\u4E86\u3002</p><p>\u8FD9\u4E00\u6B21\u5979\u6CA1\u6709\u592A\u591A\u7684\u8FDF\u7591\uFF0C\u4E24\u53EA\u672C\u6765\u7EDE\u5728\u4E00\u8D77\u7684\u4FEE\u957F\u7684\u5927\u817F\u7F13\u7F13\u5F20\u5F00\uFF0C\u5F20\u5230\u7B3C\u4E2D\u80FD\u5F20\u7684\u6781\u9650\uFF0C\u6DF1\u7EA2\u80A5\u817B\u7684\u7389\u6237\u5766\u9732\u4E86\u51FA\u6765\u3002</p><p>\u300C\u52A8\u4F5C\u5FEB\u70B9\uFF0C\u78E8\u78E8\u8E6D\u8E6D\u8001\u5B50\u8D70\u4EBA\u4E86\u3002\u300D</p><p>\u6D77\u68E0\u8138\u8272\u4E00\u60E8\uFF0C\u81CA\u5F97\u901A\u7EA2\uFF0C\u5438\u53E3\u6C14\uFF0C\u7EC8\u4E8E\u8FD8\u662F\u5C06\u4E00\u53EA\u624B\u642D\u5230\u81EA\u5DF1\u7684\u4E0B\u8EAB\u5904\uFF0C\u8471\u8471\u7389\u6307\u5C06\u4E24\u7247\u868C\u8089\u4E00\u70B9\u70B9\u6252\u5F00\uFF0C\u9732\u51FA\u4E00\u7EBF\u6E29\u6DA6\u6F6E\u6E7F\u7684\u6D1E\u53E3\uFF0C\u9634\u8482\u90A3\u5757\u7EA2\u6DA6\u7684\u5AE9\u8089\u7531\u4E8E\u6781\u5EA6\u7684\u7D27\u5F20\u548C\u7F9E\u803B\u90FD\u7ACB\u4E86\u8D77\u6765\uFF0C\u5728\u98A4\u5371\u5371\u5730\u6B59\u52A8\u3002</p><p>\u767D\u5929\u5FB7\u611F\u5230\u8EAB\u4E0A\u70ED\u6D41\u6D8C\u52A8\uFF0C\u300C\u5988\u7684\uFF0C\u90A3\u72D7\u8FD8\u6CA1\u628A\u8FD9\u9A9A\u6D1E\u6345\u70C2\u5417\uFF1F\u300D</p><p>\u6D77\u68E0\u7684\u610F\u8BC6\u53C8\u5F00\u59CB\u6A21\u7CCA\u8D77\u6765\uFF0C\u6839\u672C\u6CA1\u6709\u5FC3\u601D\u53BB\u5206\u6790\u767D\u5929\u5FB7\u7684\u6DEB\u8BCD\u79FD\u8BED\u3002</p><p>\u767D\u5929\u5FB7\u62FF\u624B\u6756\u8F7B\u8F7B\u70B9\u4E86\u70B9\u6D77\u68E0\u7684\u4E0B\u4F53\uFF0C\u300C\u60F3\u65E9\u70B9\u62BD\u818F\u5C31\u628A\u9A9A\u7A74\u633A\u8D77\u6765\u3002\u300D</p><p>\u8FD9\u53E5\u8BDD\u6D77\u68E0\u5012\u662F\u542C\u8FDB\u53BB\u4E86\uFF0C\u5979\u4E0D\u987E\u4E00\u5207\u5730\u5C06\u8EAB\u5B50\u53CD\u5F13\u8D77\u6765\uFF0C\u6BDB\u8338\u8338\u7684\u9634\u6237\u6B63\u597D\u8D34\u8FD1\u4E86\u7B3C\u5B50\u4E0A\u65B9\u7684\u4E00\u4E2A\u65B9\u683C\u3002</p><p>\u767D\u5929\u5FB7\u5F2F\u8170\uFF0C\u4F38\u5DE6\u624B\uFF0C\u5C06\u4E00\u4E1B\u957F\u957F\u7684\u9634\u6BDB\u5377\u5728\u4E2D\u6307\u548C\u65E0\u540D\u6307\u95F4\uFF0C\u6697\u6697\u8FD0\u529B\u4F7F\u52B2\u4E00\u626F\uFF0C\u5AE9\u8089\u6025\u98A4\uFF0C\u53EA\u542C\u5F97\u6D77\u68E0\u60E8\u53EB\u4E00\u58F0\uFF0C\u6342\u7740\u4E0B\u8EAB\u8DCC\u5012\u5728\u5730\uFF0C\u7537\u4EBA\u624B\u4E2D\u591A\u4E86\u4E00\u7C07\u5E26\u7740\u8840\u73E0\u7684\u6BDB\u53D1\u3002</p><p>\u767D\u5929\u5FB7\u8E22\u4E86\u8E22\u7B3C\u5B50\uFF0C\u559D\u9053\uFF0C\u300C\u5FEB\u70B9\uFF0C\u7EE7\u7EED\uFF0C\u5927\u70DF\u53EF\u5728\u7B49\u7740\u4F60\u3002\u300D</p><p>\u6D77\u68E0\u54ED\u7740\u5C06\u8EAB\u4F53\u518D\u5EA6\u5F13\u8D77\u3002\u60E8\u53EB\u3002\u7FFB\u6EDA\u3002\u53C8\u5F13\u8D77\u3002</p><p>\u5468\u800C\u590D\u59CB\u3002</p><p>\u9634\u6BDB\u4E00\u7C07\u7C07\u5730\u79BB\u5F00\u4E86\u8EAB\u4F53\uFF0C\u8840\u73E0\u4E5F\u4E00\u9897\u9897\u5730\u4ECE\u88AB\u626F\u6389\u7684\u5730\u65B9\u5192\u4E86\u51FA\u6765\uFF0C\u4E0D\u591A\u65F6\uFF0C\u4E0B\u8EAB\u80BF\u6210\u4E86\u4E00\u4E2A\u8840\u7403\u3002</p><p>\u7537\u4EBA\u5F88\u8010\u5FC3\u4E5F\u5F88\u6109\u5FEB\u5730\u7B49\u5F85\u7740\u5973\u4EBA\u81EA\u5DF1\u9001\u4E0A\u524D\u6765\u53D7\u8650\uFF0C\u54EA\u6015\u65F6\u95F4\u4E00\u6B21\u6BD4\u4E00\u6B21\u957F\uFF0C\u4E00\u70B9\u70B9\u5730\u628A\u4ED6\u8BA4\u4E3A\u662F\u7D2F\u8D58\u7684\u4E1C\u897F\u4EB2\u624B\u6D88\u706D\u5E72\u51C0\u3002</p><p>\u5BF9\u5973\u4EBA\u6765\u8BF4\uFF0C\u552F\u4E00\u7684\u597D\u5904\u662F\u5728\u5267\u70C8\u7684\u75DB\u82E6\u4E2D\u6682\u65F6\u538B\u5012\u4E86\u6BD2\u763E\uFF0C\u4E0D\u81F3\u4E8E\u53D7\u5230\u53CC\u91CD\u714E\u71AC\u3002</p><p>\u5F53\u6700\u540E\u4E00\u7F15\u9634\u6BDB\u98D8\u5230\u5730\u4E0A\u7684\u65F6\u5019\uFF0C\u767D\u5929\u5FB7\u65B9\u624D\u793A\u610F\u4E00\u65C1\u76EE\u77AA\u53E3\u5446\u7684\u674E\u8D35\u7ED9\u6D77\u68E0\u7AEF\u4E0A\u5927\u70DF\u67AA\u3002</p><p>\u6D77\u68E0\u8FEB\u4E0D\u53CA\u5F85\u5730\u62A2\u5230\u624B\u91CC\uFF0C\u5495\u565C\u5495\u565C\u731B\u62BD\u8D77\u6765\u3002</p><p>\u767D\u5929\u5FB7\u62FF\u8FC7\u4E00\u6761\u6E7F\u624B\u5DFE\uFF0C\u6E29\u67D4\u5730\u62B9\u53BB\u5973\u4EBA\u8138\u4E0A\u7684\u6CEA\u8FF9\uFF0C\u53C8\u6765\u62B9\u5979\u9C9C\u8840\u6DCB\u6F13\u7684\u4E0B\u8EAB\u3002\uFF08\u6587_\u5187_\u4EBA\uFF0D\u5187-\u4E66\uFF0D\u5C4B\uFF0D\u8D3C\u5427\uFF3Aei\uFF18\u3002\uFF23\uFF2F\uFF2D\u7535\u5B50\u4E66\uFF09</p><p>\u6D77\u68E0\u7684\u8EAB\u5B50\u6296\u52A8\u4E86\u4E00\u4E0B\uFF0C\u6CA1\u6709\u518D\u53CD\u6297\uFF0C\u53CD\u800C\u5FAE\u5FAE\u5F20\u5F00\u6765\uFF0C\u4EFB\u51ED\u7537\u4EBA\u52A8\u4F5C\u3002</p><p>\u9C9C\u8840\u6B62\u4F4F\u4E86\uFF0C\u6574\u4E2A\u7389\u6237\u867D\u7136\u8FD8\u662F\u4E00\u7247\u7EA2\u80BF\uFF0C\u4F46\u6CA1\u6709\u6BDB\u53D1\u7684\u906E\u63A9\uFF0C\u5982\u540C\u70C8\u65E5\u4E0B\u7684\u5C71\u4E18\uFF0C\u5973\u6027\u6700\u9690\u79D8\u7684\u98CE\u666F\u5F53\u771F\u662F\u4E00\u89C8\u65E0\u4F59\u3002</p><p>\u767D\u5929\u5FB7\u62CD\u62CD\u624B\u7AD9\u8D77\u6765\uFF0C\u8BF4\uFF0C\u300C\u770B\u5230\u4E86\u5417\uFF1F\u8FD9\u5C31\u662F\u5149\u677F\u5B50\u3002\u300D</p><p>\u4ED6\u6253\u5F00\u7B3C\u5B50\uFF0C\u62CE\u7740\u94C1\u94FE\u628A\u5973\u4EBA\u63D0\u4E86\u8D77\u6765\uFF0C\u6D77\u68E0\u65F1\u5F97\u72E0\u4E86\uFF0C\u6B63\u62BD\u5F97\u6B22\uFF0C\u8FD8\u6CA1\u8FC7\u8DB3\u763E\u5C31\u88AB\u538B\u53BB\u4E86\u70DF\u67AA\uFF0C\u4E0D\u7531\u5F97\u50CF\u88AB\u593A\u53BB\u4E86\u7231\u7269\u7684\u5A74\u513F\u4E00\u6837\u60B2\u9E23\u4E86\u4E00\u58F0\u3002</p><p>\u7537\u4EBA\u51B2\u5979\u7684\u4FCF\u8138\u4E0A\u62BD\u4E86\u4E00\u5DF4\u638C\uFF0C\u559D\u9053\uFF0C\u300C\u653E\u660E\u767D\u7F57\uFF0C\u8001\u5B50\u662F\u6765\u6536\u56DE\u5341\u5E74\u524D\u9003\u8DD1\u7684\u5974\u96B6\u7684\uFF0C\u81ED\u5A4A\u5B50\u8981\u641E\u6E05\u695A\u81EA\u5DF1\u7684\u8EAB\u4EFD\u3002\u300D</p><p>\u5973\u4EBA\u832B\u7136\u5730\u8BF4\uFF0C\u300C\u662F\u7684\uFF0C\u6211\u660E\u767D\uFF0C\u6211\u660E\u767D\u3002\u300D</p><p>\u300C\u660E\u767D\u4EC0\u4E48\u5566\uFF1F\u8BF4\uFF01\u300D</p><p>\u300C\u767D\u677F\u2026\u2026\u767D\u677F\u513F\u6C38\u8FDC\u662F\u5C11\u7237\u7684\u5974\u96B6\u3002\u300D</p><p>\u6D77\u68E0\u518D\u4E5F\u7981\u4E0D\u4F4F\u8FD9\u5D29\u6E83\u7684\u611F\u89C9\uFF0C\u4F0F\u5230\u5730\u4E0A\u5927\u58F0\u555C\u6CE3\u3002</p><p>\u300C\u674E\u8D35\uFF0C\u770B\u591F\u4E86\u6CA1\u6709\uFF0C\u628A\u94DC\u73AF\u62FF\u8FC7\u6765\u3002\u300D</p><p>\u767D\u5929\u5FB7\u4ECE\u674E\u8D35\u7684\u624B\u4E2D\u63A5\u8FC7\u4E00\u4E2A\u5C0F\u94DC\u52FE\uFF0C\u770B\u4E0A\u53BB\u50CF\u4E00\u6839\u52A0\u7C97\u4E86\u7684\u94A2\u9488\uFF0C\u4E00\u7AEF\u5C16\u5229\uFF0C\u8EAB\u5B50\u5374\u662F\u6241\u5E73\u7684\u3002</p><p>\u300C\u767D\u677F\uFF0C\u62AC\u8D77\u5934\u6765\uFF0C\u8001\u5B50\u7ED9\u4F60\u88C5\u4E2A\u9F3B\u73AF\u3002\u300D</p><p>\u6D77\u68E0\u6050\u60E7\u5730\u77AA\u5927\u4E86\u773C\uFF0C\u300C\u4E0D\u2026\u2026\u554A\u4E0D\u2026\u2026\u300D</p><p>\u767D\u5929\u5FB7\u6839\u672C\u4E0D\u7406\u4F1A\u5979\uFF0C\u53EB\u674E\u8D35\u628A\u5979\u7684\u8111\u888B\u7528\u529B\u5939\u7D27\uFF0C\u8BA9\u5979\u52A8\u5F39\u4E0D\u5F97\uFF0C\u624B\u6307\u63D2\u5230\u5973\u4EBA\u7684\u9F3B\u5B50\u91CC\uFF0C\u634F\u4E86\u634F\uFF0C\u53C8\u5728\u8F6F\u7EC4\u7EC7\u7684\u5730\u65B9\u6413\u4E86\u6413\uFF0C\u7136\u540E\u5C06\u94DC\u52FE\u950B\u5229\u7684\u4E00\u5934\u4ECE\u5973\u4EBA\u9F3B\u5B54\u5185\u4FA7\u6CBF\u7740\u8F6F\u9AA8\u7684\u7F1D\u9699\u94BB\u4E86\u8FDB\u53BB\uFF0C\u52A8\u4F5C\u575A\u51B3\uFF0C\u6BEB\u4E0D\u624B\u8F6F\u3002</p><p>\u4E00\u80A1\u5C16\u9510\u7684\u6FC0\u75DB\u4ECE\u9F3B\u7AEF\u8FC5\u901F\u8513\u5EF6\u5230\u5168\u8EAB\uFF0C\u53C8\u96C6\u4E2D\u5230\u5934\u8111\u4E2D\u3002\u6D77\u68E0\u75DB\u5F97\u6D51\u8EAB\u53D1\u6296\uFF0C\u60F3\u6323\u624E\u53C8\u88AB\u674E\u8D35\u6B7B\u547D\u6309\u4F4F\uFF0C\u53EA\u6709\u773C\u7741\u7741\u5730\u770B\u7740\u7684\u9488\u5934\u5728\u81EA\u5DF1\u9F3B\u5B54\u4E2D\u4ECE\u4E00\u4FA7\u94BB\u900F\uFF0C\u4ECE\u53E6\u4E00\u4FA7\u8840\u6DCB\u6DCB\u5730\u94BB\u51FA\u6765\u3002</p><p>\u5C11\u5E74\u65F6\u88AB\u4EBA\u62FF\u70E7\u7EA2\u7684\u70D9\u94C1\u5F80\u8EAB\u5B50\u4E0A\u70D9\u7684\u5669\u68A6\u91CD\u73B0\u4E86\u3002</p><p>\u5979\u60F3\u6B7B\u6389\uFF0C\u81F3\u5C11\u6655\u5012\uFF0C\u597D\u9003\u907F\u8FD9\u6781\u5EA6\u7684\u75DB\u82E6\u548C\u7F9E\u8FB1\uFF0C\u53EF\u662F\u90FD\u4E0D\u80FD\u5982\u613F\u3002\u8EAB\u5B50\u5E95\u4E0B\u7A81\u7136\u6E7F\u4E86\u4E00\u6EE9\uFF0C\u5931\u7981\u4E86\u3002</p><p>\u9C9C\u8840\u5927\u9897\u5927\u9897\u5730\u4ECE\u9F3B\u5B54\u4E2D\u6EF4\u4E86\u51FA\u6765\u3002</p><p>\u6216\u8005\u8FD9\u5C31\u662F\u5730\u72F1\u4E48\uFF1F</p><p>\u767D\u5929\u5FB7\u62FF\u8FC7\u4E00\u628A\u94C1\u5939\u5B50\uFF0C\u7528\u5C3D\u4E8C\u864E\u4E5D\u725B\u4E4B\u529B\u5C06\u94DC\u52FE\u7684\u4E24\u5934\u5F2F\u8D77\u6765\uFF0C\u5939\u6210\u4E00\u4E2A\u7C7B\u4F3C\u692D\u5706\u7684\u5706\u73AF\u3002\u53C8\u5C06\u5979\u7684\u5934\u6309\u5230\u7827\u677F\u65C1\u8FB9\uFF0C\u5706\u73AF\u5E73\u6446\u5728\u7827\u677F\u4E0A\uFF0C\u62FF\u5C0F\u94C1\u9524\u5C0F\u5FC3\u800C\u7528\u529B\u5730\u9524\u7D27\uFF0C\u539F\u6765\u7684\u4E24\u7AEF\u5408\u5F97\u4E25\u4E25\u5B9E\u5B9E\u7684\uFF0C\u4E0D\u7559\u795E\u8FD8\u770B\u4E0D\u51FA\u6765\u3002</p><p>\u767D\u5929\u5FB7\u7ED9\u6D77\u68E0\u4E0A\u4E86\u70B9\u4E91\u5357\u767D\u836F\uFF0C\u6B62\u4F4F\u8840\uFF0C\u53C8\u62FF\u6E7F\u5DFE\u62B9\u53BB\u5979\u8138\u4E0A\u7684\u6C61\u8FF9\u3002\u4E0D\u7531\u5F97\u8D5E\u53F9\u9053\uFF0C\u300C\u771F\u6F02\u4EAE\uFF0C\u8FD9\u624D\u50CF\u6211\u7684\u5C0F\u5974\u96B6\u767D\u677F\u513F\u561B\u3002\u300D</p><p>\u53EA\u89C1\u6D77\u68E0\u6CEA\u8FF9\u672A\u5E72\u7684\u8138\u4E0A\uFF0C\u50CF\u6C34\u725B\u4E00\u6837\u591A\u4E86\u4E00\u53EA\u88C5\u9970\u7CBE\u7F8E\u7684\u94DC\u73AF\uFF0C\u7AEF\u7AEF\u6B63\u6B63\u5728\u6302\u5728\u9F3B\u7AEF\uFF0C\u6563\u53D1\u51FA\u6B8B\u5FCD\u5996\u8273\u7684\u5149\u6CFD\u3002</p><p>\u767D\u5929\u5FB7\u6B23\u8D4F\u4E86\u4E00\u4F1A\uFF0C\u5FFD\u7136\u8BF4\uFF0C\u300C\u8001\u5B50\u8981\u62C9\u5C3F\u4E86\u3002\u300D</p><p>\u89C1\u6D77\u68E0\u6CA1\u6709\u52A8\u9759\uFF0C\u4ED6\u8138\u8272\u5F00\u59CB\u53D1\u7EA2\uFF0C\u518D\u4E00\u6B21\u7F13\u6162\u800C\u6C89\u91CD\u5730\u8BF4\uFF0C\u300C\u8001\u5B50\u8981\u62C9\u5C3F\u4E86\u3002\u300D</p><p>\u6D77\u68E0\u7EC8\u4E8E\u542C\u660E\u767D\u4E86\uFF0C\u62AC\u8D77\u4E86\u8EAB\u5B50\uFF0C\u6162\u6162\u8DEA\u5750\u5728\u7537\u4EBA\u811A\u4E0B\uFF0C\u624B\u6307\u89E3\u5F00\u7537\u4EBA\u7684\u88E4\u5E26\uFF0C\u638F\u51FA\u90A3\u6839\u51B2\u5929\u800C\u7ACB\u7C97\u58EE\u60CA\u4EBA\u7684\u8089\u68D2\u3002</p><p>\u6276\u4F4F\u8089\u6367\uFF0C\u7EA2\u5507\u5F20\u5F00\uFF0C\u6162\u6162\u5730\u628A\u4F1E\u5F62\u524D\u7AEF\u542B\u8FDB\u53E3\u4E2D\u3002</p><p>\u4E00\u4F1A\uFF0C\u4E00\u80A1\u9EC4\u6D4A\u7684\u5C3F\u67F1\u51B2\u4E86\u51FA\u6765\uFF0C\u72E0\u72E0\u5730\u6253\u5230\u6D77\u68E0\u7684\u53E3\u8154\u6DF1\u5904\u3002</p><p>\u8165\u81ED\u5473\u662F\u90A3\u4E48\u6D53\u70C8\uFF0C\u90A3\u4E48\u964C\u751F\uFF0C\u53C8\u662F\u90A3\u4E48\u719F\u6089\u3002</p><p>\u6D77\u68E0\u5DEE\u70B9\u5455\u4E86\u51FA\u6765\uFF0C\u7709\u5934\u7D27\u8E59\uFF0C\u300C\u5495\u675C\u300D\u4E00\u58F0\uFF0C\u4FEE\u957F\u7684\u9888\u5B50\u7FD5\u52A8\uFF0C\u62DA\u547D\u54BD\u4E0B\u4E86\u7B2C\u4E00\u53E3\u5C3F\u6DB2\u3002</p><p>\u5C0F\u5C4B\u4E2D\uFF0C\u5168\u8EAB\u8D64\u88F8\u7684\u5973\u4EBA\u8DEA\u5728\u5730\u4E0A\uFF0C\u4E00\u53E3\u63A5\u4E00\u53E3\u559D\u4E0B\u4E86\u7537\u4EBA\u81ED\u54C4\u54C4\u7684\u5C3F\u6DB2\uFF0C\u6765\u4E0D\u53CA\u54BD\u7684\u5C3F\u6C34\u548C\u7740\u6B8B\u8840\u4ECE\u5973\u4EBA\u7684\u53E3\u4E2D\u6EA2\u4E86\u51FA\u6765\uFF0C\u957F\u957F\u5730\u6302\u5728\u5973\u4EBA\u9971\u6EE1\u7684\u80F8\u524D\u3002</p><p>\u674E\u8D35\u88AB\u8FD9\u5996\u8273\u65E0\u5339\u7684\u6C14\u6C1B\u5F04\u5F97\u5982\u75F4\u5982\u9189\u3002</p>',105);function t(i,n){return r}var o=a(e,[["render",t]]);export{o as default};
