import{e as p}from"./app.630af5f2.js";import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";const e={},r=p('<h1 id="\u7B2C\u4E8C\u5341\u4E8C\u7AE0-\u8BBF\u5BA2" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u5341\u4E8C\u7AE0-\u8BBF\u5BA2" aria-hidden="true">#</a> \u7B2C\u4E8C\u5341\u4E8C\u7AE0 \u8BBF\u5BA2</h1><p>\u300C\u5F53\u2026\u2026\u300D</p><p>\u60A0\u957F\u7684\u949F\u58F0\u4E00\u58F0\u63A5\u7740\u4E00\u58F0\uFF0C\u50CF\u4E00\u6CE2\u53C8\u4E00\u6CE2\u7684\u7EC6\u6D6A\u6F2B\u8FC7\u5C71\u8C37\u3002\u8F7B\u4E91\u8584\u96FE\u95F4\uFF0C\u68B5\u97F3\u9F50\u8BF5\uFF0C\u524E\u90A3\u82B1\u5F00\uFF0C\u604D\u82E5\u4EBA\u95F4\u4ED9\u5883\uFF0C\u8D85\u7136\u51FA\u5C18\u3002</p><p>\u5C71\u4E2D\u65B9\u4E00\u65E5\uFF0C\u4E16\u95F4\u5DF2\u662F\u4E94\u5E74\u8FC7\u53BB\u3002</p><p>\u300C\u7B03\u7B03\u300D\u5927\u95E8\u6572\u4E86\u4E24\u4E0B\uFF0C\u8FC7\u4E00\u4F1A\uFF0C\u53C8\u6572\u4E86\u4E24\u4E0B\uFF0C\u4E0D\u6025\u4E0D\u5F90\u3002</p><p>\u89C2\u97F3\u5EB5\u5982\u6B64\u6E05\u9759\u65E0\u4E3A\u4E71\u4E91\u98DE\u6E21\u4E4B\u5730\uFF0C\u6709\u8C01\u4F1A\u6765\u6253\u6270\u5462\uFF1F</p><p>\u8001\u5C3C\u6167\u6E05\u5C06\u5BFA\u95E8\u62C9\u5F00\u4E00\u7EBF\uFF0C\u95E8\u5916\u662F\u4E00\u4F4D\u88C5\u626E\u6734\u7D20\u7684\u7F8E\u4E3D\u5C11\u5973\uFF0C\u62AB\u7740\u6668\u971E\u7684\u4F59\u6656\u3002</p><p>\u6167\u6E05\u53CC\u624B\u5408\u5341\uFF0C\u6253\u4E86\u4E2A\u558F\uFF0C\u300C\u672C\u5EB5\u6B63\u5728\u6668\u8BFE\uFF0C\u5973\u65BD\u4E3B\u89C1\u8C05\u3002\u300D</p><p>\u5C11\u5973\u5FAE\u7B11\u9053\uFF0C\u300C\u6211\u6765\u627E\u4EBA\uFF0C\u627E\u4E00\u4E2A\u53EB\u51B7\u5982\u971C\u7684\u5973\u4EBA\u3002\u300D</p><p>\u6167\u6E05\u5FAE\u5FAE\u4E00\u6014\uFF0C\u5782\u4E0B\u773C\u7751\u9053\uFF0C\u300C\u90A3\u5973\u65BD\u4E3B\u53EF\u5C31\u8981\u5931\u671B\u4E86\uFF0C\u672C\u5EB5\u6CA1\u6709\u60A8\u8981\u627E\u7684\u4EBA\u3002\u300D</p><p>\u5C11\u5973\u4F3C\u65E9\u5728\u610F\u6599\u4E4B\u4E2D\uFF0C\u62FF\u51FA\u4E00\u4EF6\u9648\u65E7\u7684\u7AE5\u886B\uFF0C\u786C\u585E\u8FDB\u8001\u5C3C\u7684\u624B\u4E2D\uFF0C\u300C\u90A3\u4E48\uFF0C\u6211\u8BF7\u6C42\u5927\u5E08\u60A8\uFF0C\u628A\u8FD9\u4E2A\u4E1C\u897F\u5E26\u8FDB\u53BB\u770B\u770B\uFF0C\u6211\u5C31\u5728\u5916\u9762\u7B49\u7740\uFF0C\u597D\u5417\uFF1F\u300D</p><p>\u300C\u963F\u5F25\u9640\u4F5B\u3002\u300D\u8001\u5C3C\u97A0\u4E00\u8EAC\uFF0C\u9ED8\u9ED8\u9616\u95E8\u9000\u56DE\u3002</p><p>\u665A\u8BFE\u58F0\u4E2D\u65AD\u4E86\uFF0C\u95E8\u540E\u4F3C\u6709\u4E00\u4E9B\u538B\u4F4E\u55D3\u97F3\u7684\u4E89\u6267\u3002</p><p>\u4E0D\u5230\u4E00\u4E2A\u65F6\u8FB0\uFF0C\u5EB5\u95E8\u518D\u5EA6\u5431\u5440\u4E00\u58F0\u6253\u5F00\uFF0C\u6362\u4E86\u4E00\u4F4D\u5E74\u8F7B\u7684\u6BD4\u4E18\u5C3C\u51FA\u6765\uFF0C\u5C31\u50CF\u4E00\u9053\u5149\u8292\u95EA\u8FC7\uFF0C\u8BA9\u5C11\u5973\u7981\u4E0D\u4F4F\u7787\u4E0A\u4E86\u773C\u775B\u3002\u8FD9\u5C3C\u59D1\u6D17\u51C0\u94C5\u534E\uFF0C\u7D20\u9762\u671D\u5929\uFF0C\u5FAE\u8E59\u7684\u7709\u5934\u6DE1\u67D3\u6625\u5C71\uFF0C\u80A4\u767D\u5F97\u50CF\u4E00\u6574\u5757\u6C49\u767D\u7389\u96D5\u51FA\u6765\u7684\u4ED5\u5973\uFF0C\u53C8\u82CD\u767D\u5F97\u4EE4\u4EBA\u4E0D\u6562\u903C\u89C6\u3002</p><p>\u5C11\u5973\u8BBE\u60F3\uFF0C\u5982\u679C\u5979\u7B11\u7684\u65F6\u5019\u4E00\u5B9A\u5F02\u5E38\u59A9\u5A9A\u6E29\u67D4\u3002</p><p>\u300C\u4E0D\u7528\u731C\u7591\uFF0C\u6211\u4EEC\u4E0D\u66FE\u8BA4\u8BC6\u7684\u3002\u6211\u53EB\u963F\u6708\uFF0C\u600E\u4E48\u79F0\u547C\u60A8\u5462\uFF0C\u5218\u592B\u4EBA\u8FD8\u662F\u5982\u971C\u59D0\uFF1F\u300D</p><p>\u300C\u963F\u5F25\u9640\u4F5B\uFF0C\u65BD\u4E3B\uFF0C\u5982\u971C\u5DF2\u6B7B\uFF0C\u8D2B\u5C3C\u89C9\u6094\u3002\u300D</p><p>\u5C11\u5973\u53C8\u7B11\u4E86\uFF0C\u5F88\u5A9A\u7684\u90A3\u79CD\uFF0C\u89C9\u6094\u53D1\u73B0\u5979\u5F88\u50CF\u4E00\u4E2A\u4EBA\uFF0C\u4E00\u4E2A\u5728\u5FC3\u4E2D\u6B7B\u53BB\u5F88\u4E45\u7684\u4EBA\u3002</p><p>\u300C\u6CA1\u6709\u5173\u7CFB\uFF0C\u6211\u53EA\u662F\u60F3\u544A\u8BC9\u5979\uFF0C\u60F3\u89C1\u5230\u8DDF\u8FD9\u4EF6\u8863\u88F3\u6709\u5173\u7684\u4EBA\u5C31\u8BF7\u5373\u523B\u8DDF\u6211\u4E0B\u5C71\uFF0C\u5426\u5219\uFF0C\u5979\u5C06\u6C38\u8FDC\u5931\u53BB\u4ED6\u3002\u300D</p><p>\u5979\u8BF4\u5F97\u795E\u95F2\u6C14\u5B9A\uFF0C\u9752\u5E74\u5C3C\u59D1\u5374\u662F\u8138\u8272\u5267\u53D8\uFF0C\u8BF4\u4E0D\u51FA\u662F\u559C\u662F\u60B2\uFF0C\u662F\u60CA\u662F\u60E7\uFF0C\u8BF4\u8BDD\u4E5F\u98A0\u4E09\u5012\u56DB\uFF0C\u300C\u8FDE\u751F\uFF0C\u4ED6\uFF0C\u4ED6\u771F\u7684\u8FD8\u6D3B\u7740\uFF1F\u5728\u54EA\u91CC\uFF0C\u5FEB\u5E26\u6211\u53BB\uFF01\u300D</p><p>\u300C\u89C9\u6094\uFF0C\u4F60\u5FC3\u4E71\u4E86\u3002\u300D\u8001\u5C3C\u4E00\u58F0\u65AD\u559D\uFF0C\u8BD5\u56FE\u5C06\u9752\u5E74\u5C3C\u59D1\u4ECE\u9B54\u969C\u4E2D\u5524\u9192\u3002</p><p>\u300C\u662F\uFF0C\u5E08\u5085\u3002\u300D\u89C9\u6094\u542B\u6CEA\u5408\u638C\u3002</p><p>\u300C\u7E41\u534E\u7686\u6210\u68A6\uFF0C\u7EA2\u7C89\u5C3D\u9AB7\u9885\uFF0C\u5C18\u4E16\u95F4\u79CD\u79CD\uFF0C\u548C\u4F60\u8FD8\u6709\u4EC0\u4E48\u5173\u7CFB\u5462\uFF1F\u300D</p><p>\u5C11\u5973\u53D1\u51FA\u4E24\u58F0\u8BA5\u7B11\u3002</p><p>\u9752\u5E74\u5C3C\u59D1\u5659\u7740\u6CEA\uFF0C\u8DEA\u4E0B\uFF0C\u6574\u4E2A\u8EAB\u5B50\u90FD\u5728\u6FC0\u70C8\u7684\u6296\u52A8\uFF0C\u6296\u52A8\uFF0C\u7EC8\u4E8E\u78D5\u4E0B\u5934\u53BB\uFF0C\u300C\u5E08\u5085\uFF0C\u8FD9\u51E0\u5E74\u6765\uFF0C\u65E5\u65E5\u601D\u91CF\uFF0C\u5F7B\u591C\u96BE\u7720\uFF0C\u89C9\u6094\u8FD8\u662F\u653E\u4E0D\u4E0B\uFF0C\u7F6A\u5B7D\u4E5F\u592A\u6DF1\u91CD\uFF0C\u4E0D\u914D\u505A\u4F5B\u95E8\u5F1F\u5B50\u554A\u3002\u300D</p><p>\u6167\u6E05\u4E00\u58F0\u6D69\u53F9\uFF0C\u300C\u4F60\u53EF\u60F3\u597D\u4E86\uFF0C\u518D\u56DE\u5934\u5DF2\u662F\u767E\u5E74\u8EAB\u3002\u300D</p><p>\u6574\u4E2A\u5EB5\u91CC\u7684\u5C3C\u59D1\u7AD9\u5728\u6167\u6E05\u8EAB\u540E\uFF0C\u9F50\u58F0\u559D\u558F\uFF0C\u300C\u963F\u5F25\u9640\u4F5B\u2026\u2026\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u65E5\u5934\u6E10\u8D77\uFF0C\u6574\u4E2A\u4E0D\u8001\u5CF0\u5C71\u5934\u9996\u5148\u6C90\u6D74\u5230\u6E29\u6696\u7684\u9633\u5149\u3002</p><p>\u89C9\u6094\uFF0C\u4E0D\uFF0C\u73B0\u5728\u8FD8\u4FD7\u56DE\u5230\u4E86\u51B7\u5982\u971C\u7684\u8EAB\u4EFD\uFF0C\u4E60\u60EF\u4E86\u4E0D\u8001\u5CF0\u7684\u9633\u5149\uFF0C\u4ECA\u5929\uFF0C\u5C31\u8981\u8FDC\u79BB\u8FD9\u719F\u6089\u7684\u4E00\u5207\u4E86\uFF0C\u5FC3\u4E2D\u5206\u5916\u7559\u604B\u3002</p><p>\u4E94\u5E74\u524D\uFF0C\u6C85\u9547\u57CE\u9677\u540E\uFF0C\u571F\u532A\u5E76\u6CA1\u6709\u80FD\u5F97\u610F\u591A\u4E45\uFF0C\u4ECE\u7701\u5E9C\u8C03\u9063\u8FC7\u6765\u7684\u6B63\u89C4\u519B\u8FC5\u901F\u63A8\u8FDB\uFF0C\u5C06\u571F\u532A\u9A71\u6563\uFF0C\u53C8\u5C06\u57CE\u9547\u6536\u590D\u56DE\u6765\u3002\u732B\u9F20\u5176\u5B9E\u662F\u4E00\u5BB6\uFF0C\u53EA\u4E0D\u8FC7\u662F\u6362\u4E2A\u724C\u5B50\u800C\u5DF2\uFF0C\u8C01\u6765\u90FD\u8981\u70E7\u6740\u6D17\u63A0\u4E00\u9053\uFF0C\u53EA\u82E6\u4E86\u8001\u767E\u6027\uFF0C\u6C11\u4E0D\u804A\u751F\uFF0C\u4E00\u5EA7\u7E41\u534E\u7684\u91CD\u9547\u7ECF\u6B64\u4E00\u5F79\u4E5F\u662F\u5143\u6C14\u5927\u4F24\uFF0C\u4E45\u4E45\u96BE\u4EE5\u590D\u5143\u3002</p><p>\u5175\u8352\u9A6C\u4E71\u4E2D\uFF0C\u767D\u5929\u5FB7\u629B\u5F03\u4E86\u4ED6\u7684\u5B50\u6C11\uFF0C\u4E5F\u4E22\u6389\u4E86\u65B0\u5A36\u7684\u5BB6\u5BA4\uFF0C\u5E26\u7740\u5341\u591A\u6761\u4EBA\u67AA\u4E0D\u77E5\u6240\u8E2A\u3002</p><p>\u5927\u96BE\u4E34\u5934\u5404\u81EA\u98DE\uFF0C\u4ED6\u7684\u65B0\u592A\u592A\u53F2\u51DD\u5170\u4E5F\u4E0D\u793A\u5F31\uFF0C\u9887\u4E3A\u8BC6\u5927\u4F53\u987E\u5927\u5C40\uFF0C\u7ACB\u523B\u4E0B\u5AC1\u7ED9\u4E86\u8361\u5BC7\u6709\u529F\u7684\u56FD\u519B\u65B0\u7F16\u4E8C\u5E08\u5468\u56E2\u957F\u505A\u5C0F\u8001\u5A46\uFF0C\u636E\u8BF4\u5C0F\u65E5\u5B50\u8FC7\u5F97\u8FD8\u633A\u6ECB\u6DA6\u3002</p><p>\u51B7\u5982\u971C\u4E5F\u8D81\u4E71\u9003\u51FA\u4E86\u5929\u9999\u697C\uFF0C\u56DB\u5904\u6253\u63A2\u4E0D\u5230\u513F\u5B50\u7684\u6D88\u606F\uFF0C\u8FD8\u5DEE\u4E00\u70B9\u88AB\u571F\u532A\u63B3\u63A0\uFF0C\u65E0\u5904\u53EF\u53BB\uFF0C\u5FC3\u7070\u610F\u51B7\u4E4B\u4E0B\u6295\u5954\u6DF1\u5C71\uFF0C\u843D\u53D1\u4E3A\u5C3C\u3002</p><p>\u5979\u603B\u662F\u4ECE\u5669\u68A6\u4E2D\u60CA\u9192\uFF0C\u4E00\u65F6\u662F\u8840\u6DCB\u6DCB\u7684\u5B69\u5B50\uFF0C\u4E00\u65F6\u662F\u72F0\u72DE\u7684\u767D\u5929\u5FB7\uFF0C\u8FD8\u6709\u4E8C\u559C\u5B50\u548C\u4FDD\u5B89\u56E2\u4E00\u5E72\u4EBA\uFF0C\u8BA9\u5979\u96BE\u5F97\u5B89\u751F\uFF0C\u75DB\u54ED\u5931\u58F0\u3002</p><p>\u8FD9\u4E2A\u65F6\u5019\uFF0C\u4E3B\u6301\u6167\u6E05\u5C31\u4F1A\u5B88\u5019\u5728\u5979\u8EAB\u8FB9\uFF0C\u4E3A\u5979\u957F\u8BF5\u89C2\u97F3\u5492\u548C\u91D1\u89C2\u7ECF\uFF0C\u6E05\u9664\u9B54\u969C\u3002\u8FD9\u4E48\u591A\u5E74\u8FC7\u53BB\uFF0C\u9752\u706F\u53E4\u4F5B\u76F8\u4F34\uFF0C\u603B\u7B97\u5E73\u9759\u4E86\u3002</p><p>\u60F3\u4E0D\u5230\u8FD9\u4E2A\u53EB\u963F\u6708\u7684\u964C\u751F\u5973\u5B50\uFF0C\u5374\u7A81\u7136\u5E26\u6765\u4E86\u9739\u96F3\u4E00\u822C\u7684\u6D88\u606F\uFF0C\u5979\u7684\u5B69\u5B50\u8FD8\u6D3B\u7740\uFF0C\u5C31\u50CF\u70C8\u706B\u71CE\u539F\uFF0C\u518D\u4E5F\u65E0\u6CD5\u63A7\u5236\u3002\u5979\u5FC3\u4E0B\u660E\u767D\uFF0C\u5176\u5B9E\u8FD9\u4E8B\u6765\u5F97\u5B9E\u5728\u8BE1\u5F02\uFF0C\u5176\u95F4\u8FF7\u969C\u91CD\u91CD\uFF0C\u751A\u81F3\u53EF\u80FD\u8981\u91CD\u65B0\u63A5\u53D7\u547D\u8FD0\u7684\u8BC5\u5492\uFF0C\u56DE\u5230\u6BD4\u6B7B\u8FD8\u53EF\u6015\u7684\u70BC\u72F1\u4E2D\u3002\u60B2\u54C0\u7684\u662F\uFF0C\u5979\u522B\u65E0\u9009\u62E9\u3002</p><p>\u5979\u80FD\u9003\u62E9\u5417\uFF0C\u4E94\u5E74\u4E86\uFF0C\u8FDC\u5728\u6DF1\u5C71\u53E4\u5BFA\u90FD\u6CA1\u80FD\u9003\u8131\uFF0C\u5979\u8FD8\u80FD\u9003\u5230\u54EA\u53BB\u5462\uFF1F</p><p>\u963F\u6708\u5634\u5DF4\u5012\u662F\u4E0D\u95F2\u7740\uFF0C\u6CA1\u8BDD\u627E\u8BDD\uFF0C\u300C\u5982\u971C\u59D0\uFF0C\u90FD\u8BF4\u4F60\u957F\u5F97\u795E\u4ED9\u59D0\u59D0\u4E00\u6837\uFF0C\u5C31\u7B97\u5243\u5149\u5934\uFF0C\u8FD8\u662F\u90A3\u4E48\u6F02\u4EAE\uFF0C\u771F\u8BA9\u6211\u7FA1\u6155\u6B7B\u4E86\u3002\u300D</p><p>\u51B7\u5982\u971C\u4E0D\u60F3\u7B54\u7406\u5979\uFF0C\u75BE\u6B65\u4E4B\u4E0B\uFF0C\u5BBD\u5927\u7684\u7070\u8272\u50E7\u8863\u4E00\u6643\u4E00\u6643\u7684\u6446\u52A8\uFF0C\u9690\u7EA6\u53EF\u89C1\u7A88\u7A95\u7684\u8EAB\u6750\u3002</p><p>\u5C71\u4E0B\uFF0C\u4E00\u8F86\u9A6C\u8F66\u6B63\u5728\u5B89\u9759\u5730\u7B49\u7740\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5979\u4EEC\u7684\u65B9\u5411\uFF0C\u662F\u7AF9\u6797\u6DF1\u5904\uFF0C\u83BD\u83BD\u6797\u6D77\u3002</p><p>\u51FA\u4E86\u5B98\u9053\uFF0C\u53C8\u8D70\u6C34\u8DEF\uFF0C\u518D\u8FDB\u5BC6\u6797\uFF0C\u8DEF\u8D8A\u8D70\u8D8A\u957F\uFF0C\u8D8A\u8D70\u8D8A\u504F\uFF0C\u4F3C\u4E4E\u603B\u6709\u8DEF\u53EF\u4EE5\u8D70\uFF0C\u6781\u5176\u9690\u5BC6\u7684\u8DEF\uFF0C\u6BCF\u5230\u4E00\u4E2A\u8F6C\u6298\u6362\u9053\u7684\u5730\u513F\uFF0C\u90FD\u4F1A\u6709\u4E00\u4E9B\u6C89\u9ED8\u5E72\u7EC3\u7684\u4EBA\u51FA\u73B0\uFF0C\u4E3A\u5979\u4EEC\u6253\u70B9\uFF0C\u4E00\u70B9\u5DEE\u9519\u4E5F\u6CA1\u6709\uFF0C\u867D\u4E0D\u663E\u5C71\u9732\u6C34\uFF0C\u5185\u4E2D\u8574\u542B\u7684\u529B\u91CF\u4E4B\u5927\u4EE4\u4EBA\u548B\u820C\u3002</p><p>\u8FD9\u4E00\u5207\u4E0D\u5F97\u4E0D\u8BA9\u51B7\u5982\u971C\u6000\u7591\u8FD9\u4E2A\u963F\u6708\u7684\u8EAB\u4EFD\uFF0C\u770B\u4E0A\u53BB\u5E74\u7EAA\u4E0D\u5927\uFF0C\u6A21\u6837\u6E05\u7EAF\uFF0C\u7709\u76EE\u95F4\u8FD8\u6709\u51E0\u5206\u81EA\u5DF1\u7684\u6C14\u8D28\uFF0C\u5BF9\u5979\u4E00\u76F4\u5BA2\u6C14\u800C\u758F\u8FDC\uFF0C\u504F\u504F\u4E00\u8EAB\u532A\u6C14\uFF0C\u6CA1\u6709\u51E0\u53E5\u5B9E\u8BDA\u8BDD\uFF0C\u603B\u662F\u6349\u6478\u4E0D\u5B9A\u611F\u89C9\u8BA9\u5979\u4E0D\u8212\u670D\u3002</p><p>\u96BE\u9053\u662F\u5728\u6B3A\u9A97\u5979\u5417\uFF0C\u4F46\u90A3\u4E48\u5927\u6392\u573A\uFF0C\u52A8\u7528\u4E86\u90A3\u4E48\u591A\u4EBA\u529B\u7269\u529B\uFF0C\u5C31\u4E3A\u4E86\u5979\u4E00\u4E2A\u4E00\u65E0\u6240\u6709\u7684\u51FA\u5BB6\u4EBA\uFF0C\u672A\u514D\u4E5F\u592A\u532A\u5937\u6240\u601D\u4E86\uFF0C\u800C\u4E14\u90A3\u4EF6\u7AE5\u88C5\u5206\u660E\u662F\u771F\u7684\uFF0C\u4F3C\u4E4E\u8FD8\u80FD\u55C5\u5230\u5B69\u5B50\u7684\u4F53\u9999\u3002</p><p>\u65E0\u6765\u7531\u5730\uFF0C\u5979\u611F\u53D7\u5230\u4E86\u4E00\u79CD\u90AA\u6076\u7684\u6C14\u606F\uFF0C\u8FD9\u6C14\u606F\u4E3A\u5979\u6700\u5BB3\u6015\u7684\u67D0\u4EBA\u6240\u6709\uFF0C\u8D8A\u5F80\u524D\u8D70\uFF0C\u8FD9\u79CD\u611F\u89C9\u8D8A\u660E\u663E\u3002</p><p>\u4E5F\u8BB8\u4ECE\u4E00\u5F00\u59CB\uFF0C\u5979\u5C31\u731C\u5230\u8FD9\u4E2A\u7ED3\u5C40\uFF0C\u800C\u53EA\u662F\u6545\u610F\u4E0D\u53BB\u591A\u60F3\u5427\u3002</p><p>\u6574\u6574\u4E09\u65E5\uFF0C\u5979\u4EEC\u624D\u4ECE\u5BC6\u6797\u4E2D\u7A7F\u51FA\u6765\uFF0C\u4EE5\u4E3A\u51FA\u6765\u4E86\uFF0C\u5B9E\u9645\u624D\u53D1\u73B0\uFF0C\u5979\u4EEC\u6240\u5728\u7684\u4F4D\u7F6E\uFF0C\u53EA\u662F\u65E0\u8FB9\u65E0\u9645\u5BC6\u6797\u7684\u8179\u5730\u4E2D\u4E00\u7247\u5927\u9762\u79EF\u7684\u8349\u5761\u5730\u800C\u5DF2\u3002</p><p>\u300C\u554A\uFF01\u300D\u5C31\u7B97\u662F\u89C1\u8FC7\u4E86\u5927\u4E16\u9762\u7684\u51B7\u5982\u971C\uFF0C\u4E5F\u4E0D\u7981\u4E3A\u773C\u524D\u7684\u666F\u8C61\u6240\u9707\u64BC\u3002</p><p>\u773C\u524D\u8038\u7ACB\u7740\u4E00\u680B\u4E09\u5C42\u9AD8\u7684\u5E9E\u5927\u7684\u57CE\u5BE8\u3002</p><p>\u57CE\u5BE8\u5468\u56F4\uFF0C\u5927\u7247\u5927\u7247\u7684\u5C71\u5761\u5730\u957F\u7740\u4E00\u79CD\u5947\u602A\u7684\u690D\u7269\uFF0C\u7ED3\u7740\u5927\u91CF\u9752\u8272\u7684\u679C\u5B50\u3002</p><p>\u5730\u91CC\uFF0C\u56DB\u4E0B\u91CC\u70B9\u7F00\u4E00\u4E9B\u6234\u6597\u7B20\u7684\u5E74\u9752\u5973\u5B50\uFF0C\u90FD\u9887\u6709\u4E9B\u59FF\u8272\uFF0C\u8EAB\u6750\u6253\u626E\u5F02\u5E38\u60F9\u706B\uFF0C\u4E0A\u4E0B\u53EA\u6709\u4E24\u5757\u5E03\u6761\u7F20\u4F4F\u5973\u4EBA\u7684\u6027\u5F81\uFF0C\u5927\u7247\u767D\u5AE9\u7684\u808C\u80A4\u8892\u9732\u5728\u65E5\u5149\u4E0B\u3002</p><p>\u96BE\u602A\u5728\u4E00\u65C1\u76D1\u89C6\u7684\u6301\u67AA\u58EB\u5175\u4F1A\u6309\u637A\u4E0D\u4F4F\uFF0C\u5DF2\u7136\u6709\u4E2A\u5973\u5B50\u88AB\u7C97\u66B4\u7684\u6309\u5230\u5730\u4E0A\uFF0C\u5E03\u6761\u64A9\u8D77\u5230\u4E86\u8170\u4E0A\uFF0C\u9732\u51FA\u5149\u6E9C\u6E9C\u7684\u4E0B\u8EAB\uFF0C\u7537\u4EBA\u7684\u5C41\u80A1\u8038\u52A8\u7740\u5E72\u5F97\u53EF\u6B22\u3002</p><p>\u5176\u5B83\u5973\u4EBA\u770B\u90FD\u4E0D\u770B\u4E00\u773C\uFF0C\u57CB\u7740\u5934\u505A\u81EA\u5DF1\u7684\u4E8B\u60C5\uFF0C\u7ED9\u90A3\u4E9B\u690D\u7269\u6D47\u6C34\u3002</p><p>\u51B7\u5982\u971C\u6599\u4E0D\u5230\u4F1A\u89C1\u5230\u8FD9\u7B49\u810F\u4E8B\uFF0C\u8D76\u7D27\u95ED\u4E0A\u773C\uFF0C\u76F4\u5FF5\u963F\u5F25\u9640\u4F5B\u3002</p><p>\u963F\u6708\u770B\u4E0A\u53BB\u4E60\u4EE5\u4E3A\u5E38\u4E86\uFF0C\u53EA\u558A\u4E86\u4E00\u55D3\u5B50\uFF0C\u300C\u522B\u8FC7\u4EFD\u554A\uFF0C\u4E3B\u4EBA\u53EF\u4E0D\u9AD8\u5174\u4F60\u4EEC\u538B\u574F\u4E86\u8D27\u3002\u300D</p><p>\u4E00\u4FA7\u89C2\u6218\u7684\u58EB\u5175\u7B11\u9053\uFF0C\u300C\u4E3B\u4EBA\u51FA\u53BB\u5566\uFF0C\u7BA1\u4E0D\u7740\u3002\u300D</p><p>\u300C\u96BE\u602A\u8001\u864E\u4E0D\u5728\uFF0C\u7334\u5B50\u7FFB\u5929\u54EA\u3002\u300D</p><p>\u300C\u54A6\uFF0C\u6708\u59D1\uFF0C\u60A8\u8001\u4EBA\u5BB6\u51FA\u53BB\u8FD9\u4E48\u591A\u65E5\uFF0C\u5C31\u5E26\u56DE\u4E86\u4E2A\u5C3C\u59D1\u5440\uFF0C\u662F\u4E0D\u662F\u5916\u9762\u7684\u5973\u4EBA\u90FD\u6B7B\u7EDD\u5566\uFF1F\u6B63\u597D\uFF0C\u501F\u6211\u4EEC\u6CC4\u6CC4\u706B\u5427\u3002\u300D</p><p>\u300C\u653E\u4F60\u5A18\u7684\u72D7\u5C41\uFF0C\u627E\u4F60\u5988\u53BB\u5427\u3002\u300D\u963F\u6708\u9A82\u7684\u810F\u8BDD\u6765\u4E5F\u662F\u6BEB\u4E0D\u900A\u8272\uFF0C\u90A3\u4E9B\u5927\u5175\u5012\u633A\u53D7\u7528\uFF0C\u5475\u5475\u7B11\u7740\u4E0D\u4F5C\u58F0\u4E86\u3002</p><p>\u8BF4\u8BDD\u95F4\uFF0C\u5979\u4EEC\u5DF2\u8FDB\u4E86\u5B88\u536B\u68EE\u4E25\u7684\u57CE\u5BE8\u91CC\u5934\u3002</p><p>\u300C\u6211\u5148\u5E26\u4F60\u968F\u5904\u770B\u770B\u5427\u3002\u300D</p><p>\u300C\u6211\u7684\u5B69\u5B50\u5462\uFF1F\u300D\u51B7\u5982\u971C\u53EA\u76EF\u7740\u8FD9\u4E00\u6761\uFF0C\u65E9\u5DF2\u5FC3\u6025\u5982\u711A\u3002</p><p>\u300C\u522B\u7740\u6025\uFF0C\u4E3B\u4EBA\u56DE\u6765\uFF0C\u4F60\u5C31\u4F1A\u89C1\u7740\u4E86\u3002\u300D</p><p>\u300C\u4F60\u4EEC\u4E3B\u4EBA\u662F\u8C01\uFF1F\u300D</p><p>\u963F\u6708\u9732\u51FA\u795E\u79D8\u7684\u5FAE\u7B11\uFF0C\u300C\u8FD9\u4E2A\uFF0C\u4E5F\u6682\u65F6\u4FDD\u5BC6\u3002\u300D</p><p>\u57CE\u5BE8\u91CC\u9762\u6BD4\u5916\u9762\u770B\u8FD8\u8981\u58EE\u89C2\u5F97\u591A\uFF0C\u5706\u5F62\u7ED3\u6784\uFF0C\u5730\u4E0A\u4E09\u5C42\uFF0C\u5730\u4E0B\u8FD8\u6709\u4E09\u5C42\uFF0C\u56F4\u51FA\u4E00\u4E2A\u53C8\u6DF1\u53C8\u5BBD\u7684\u5929\u4E95\uFF0C\u5979\u4EEC\u8FDB\u95E8\u7B49\u4E8E\u662F\u7AD9\u5728\u7B2C\u56DB\u5C42\u7684\u697C\u68AF\u53E3\u3002</p><p>\u963F\u6708\u6307\u70B9\u9053\uFF0C\u300C\u4F60\u770B\uFF0C\u516D\u697C\u662F\u5C97\u54E8\u548C\u6652\u836F\u5929\u53F0\uFF0C\u4E94\u697C\uFF0C\u4E3B\u4EBA\u4F4F\u7740\uFF0C\u56DB\u697C\u662F\u58EB\u5175\uFF0C\u4E09\u697C\uFF0C\u4E5F\u5C31\u662F\u5730\u4E0B\u4E00\u5C42\u71AC\u836F\u8F66\u95F4\uFF0C\u4E8C\u5C42\u4ED3\u5E93\uFF0C\u4E00\u697C\u5C31\u662F\u5173\u5973\u5974\u548C\u6BCD\u725B\u4EEC\u7684\u5730\u65B9\uFF0C\u5973\u5974\u521A\u624D\u4F60\u89C1\u7740\u4E86\uFF0C\u5E26\u4F60\u770B\u770B\u6BCD\u725B\uFF0C\u5F00\u5F00\u773C\u3002\u300D</p><p>\u51B7\u5982\u971C\u677F\u7740\u8138\u8BF4\uFF0C\u300C\u6211\u4E0D\u53BB\u3002\u300D</p><p>\u300C\u90A3\u4E5F\u968F\u4F60\uFF0C\u6211\u5C31\u5FD9\u81EA\u5DF1\u7684\u53BB\u4E86\u554A\u3002\u300D</p><p>\u51B7\u5982\u971C\u4E0D\u5F97\u4E0D\u968F\u5979\u4E0B\u5230\u5E95\u5C42\uFF0C\u56DB\u5468\u9759\u9759\u7684\uFF0C\u4E5F\u7B97\u5F97\u5E72\u51C0\uFF0C\u5973\u5974\u7684\u623F\u95F4\u91CC\u5168\u90E8\u7528\u6728\u677F\u94FA\u6210\u901A\u94FA\uFF0C\u5E8A\u5934\u6A2A\u6760\u7740\u4E00\u6839\u4E24\u7AEF\u5D4C\u5165\u5899\u4E2D\u7684\u957F\u94C1\u68CD\u3002</p><p>\u963F\u6708\u89E3\u91CA\u8BF4\uFF0C\u300C\u5973\u5974\u4EEC\u4F11\u606F\u65F6\uFF0C\u90FD\u8981\u4E24\u624B\u4E3E\u8FC7\u5934\u9876\uFF0C\u94D0\u5728\u94C1\u68CD\u4E0A\uFF0C\u8FD9\u6837\u5C31\u4E0D\u4F1A\u9003\u8DD1\u3002\u300D</p><p>\u518D\u8FC7\u4E00\u95F4\u623F\uFF0C\u91CC\u9762\u9ED1\u6D1E\u6D1E\u9634\u68EE\u53EF\u6016\uFF0C\u6302\u6EE1\u4E86\u5404\u5F0F\u5404\u6837\u7684\u5211\u5177\uFF0C\u963F\u6708\u82E6\u7740\u8138\u8BF4\u9053\uFF0C\u300C\u8FD9\u662F\u5211\u623F\uFF0C\u6709\u5927\u90E8\u5206\u5211\u5177\u90FD\u662F\u5BF9\u4ED8\u6211\u4EEC\u5973\u4EBA\u7684\uFF0C\u770B\u5230\u6446\u5728\u90A3\u91CC\u94B3\u5B50\u4E00\u6837\u7684\u4E1C\u897F\u6CA1\u6709\uFF0C\u5916\u4FA7\u7528\u6765\u5C06\u9634\u9053\u6269\u5F00\u5230\u6781\u70B9\uFF0C\u5185\u4FA7\u7684\u954A\u5B50\u4F38\u8FDB\u53BB\u5C06\u5B50\u5BAB\u5939\u4F4F\u62D6\u51FA\u6765\uFF0C\u4F60\u8BF4\u60E8\u4E0D\u60E8\u3002\u300D</p><p>\u53E3\u91CC\u8BF4\u60E8\uFF0C\u8868\u60C5\u5374\u662F\u8F7B\u677E\u4E4B\u6781\uFF0C\u5012\u662F\u51B7\u5982\u971C\u6BCF\u542C\u4E00\u53E5\uFF0C\u90FD\u8981\u5FF5\u4E00\u53E5\u4F5B\u3002</p><p>\u300C\u6765\u6765\uFF0C\u6709\u8DA3\u7684\u6765\u4E86\uFF0C\u770B\u6BCD\u725B\u7F57\u3002\u300D</p><p>\u5176\u5B9E\u5E76\u975E\u771F\u6B63\u7684\u6BCD\u725B\uFF0C\u800C\u662F\u4E00\u6E9C\u4E03\u516B\u4E2A\u5E74\u9752\u7684\u5973\u4EBA\uFF0C\u4F53\u6001\u4E30\u8174\uFF0C\u56DB\u80A2\u7740\u5730\u5728\u5730\u4E0A\u722C\u884C\uFF0C\u5404\u6709\u4E00\u5BF9\u60CA\u4EBA\u7684\u5927\u5976\uFF0C\u8DB3\u8DB3\u8D85\u8FC7\u5E38\u4EBA\u7684\u4E09\u500D\uFF0C\u5927\u6728\u74DC\u9F13\u80C0\u9F13\u80C0\u540A\u5728\u80F8\u524D\uFF0C\u6C89\u91CD\u5730\u6643\u6765\u6643\u53BB\uFF0C\u6709\u7684\u5976\u5934\u90FD\u5FEB\u64E6\u5230\u5730\u4E86\u3002</p><p>\u5979\u4EEC\uFF08\u6216\u662F\u5B83\u4EEC\uFF09\u90FD\u5F88\u5B89\u9759\uFF0C\u50CF\u732A\u4E00\u6837\u5C16\u8D77\u5634\u63D2\u8FDB\u957F\u69FD\uFF0C\u5728\u4E00\u5806\u5206\u4E0D\u6E05\u4EC0\u4E48\u4E1C\u897F\u91CC\u62F1\u6765\u62F1\u53BB\uFF0C\u5403\u5F97\u5F88\u9999\u7684\u6837\u5B50\u3002</p><p>\u963F\u6708\u8200\u8D77\u4E00\u74E2\u6765\u95FB\u95FB\uFF0C\u4F5C\u51FA\u6076\u5FC3\u7684\u6837\u5B50\uFF0C\u300C\u8FD9\u5E2E\u5C0F\u5B50\u574F\u900F\u4E86\uFF0C\u53C8\u628A\u5C3F\u6492\u5728\u91CC\u9762\u8BA9\u5B83\u4EEC\u5403\u3002\u975E\u5F97\u6559\u8BAD\u6559\u8BAD\u4E0D\u884C\u3002\u300D</p><p>\u300C\u8BDD\u53C8\u8BF4\u56DE\u6765\uFF0C\u522B\u770B\u5B83\u4EEC\u4E2A\u5934\u4E0D\u5982\u771F\u6B63\u7684\u6BCD\u725B\uFF0C\u4EA7\u8D77\u5976\u6765\u4E0D\u4F1A\u5DEE\u54DF\uFF0C\u53C8\u65B0\u9C9C\u53C8\u8425\u517B\uFF0C\u9664\u4E86\u4E3B\u4EBA\u6D17\u6FA1\u6D17\u811A\u6D17\u5C41\u80A1\uFF0C\u8FD8\u80FD\u7ED9\u8FD9\u91CC\u7684\u7537\u4EBA\u6BCF\u5929\u90FD\u80FD\u559D\u4E0A\u4E00\u7897\u3002\u300D</p><p>\u5979\u6572\u6572\u6302\u5728\u58C1\u4E0A\u7684\u94DC\u9523\uFF0C\u6240\u6709\u7684\u6BCD\u725B\u90FD\u6D51\u8EAB\u4E00\u6296\uFF0C\u7ACB\u523B\u722C\u4E86\u8FC7\u6765\uFF0C\u4E89\u5148\u6050\u540E\u5730\u5C06\u4E24\u53EA\u80A5\u5976\u4F38\u51FA\u680F\u5916\u3002</p><p>\u963F\u6708\u62FF\u8D77\u4E00\u53EA\u74F7\u7897\uFF0C\u8E72\u4E0B\u53BB\uFF0C\u63E1\u4F4F\u4E00\u53EA\u5976\u5B50\u7684\u524D\u7AEF\uFF0C\u8F7B\u8F7B\u4E00\u634F\u5C31\u6709\u4E00\u80A1\u6DE1\u9EC4\u767D\u8272\u7684\u5976\u5B50\u7BAD\u4E00\u822C\u5730\u6FC0\u5C04\u51FA\u6765\uFF0C\u5F88\u5FEB\u63A5\u6EE1\u4E00\u7897\uFF0C\u5976\u5B50\u8FD8\u770B\u4E0D\u51FA\u6709\u591A\u5C11\u53D8\u5F62\u3002</p><p>\u300C\u4ECA\u5929\u4E0D\u80FD\u767D\u6765\uFF0C\u54B1\u4EEC\u4E5F\u5077\u559D\u4E00\u7897\uFF0C\u4E0D\u8BA9\u4ED6\u4EEC\u77E5\u9053\u4E86\u3002\u6765\uFF0C\u8D81\u70ED\u3002\u300D</p><p>\u51B7\u5982\u971C\u6728\u6728\u5730\u63A5\u8FC7\u53BB\uFF0C\u671B\u7740\u8FD9\u65B0\u9C9C\u7684\u6BCD\u4E73\uFF0C\u76F4\u7591\u6B64\u5904\u662F\u5426\u8FD8\u662F\u4EBA\u95F4\uFF0C\u6123\u4E86\u4E00\u4F1A\u513F\uFF0C\u7A81\u7136\u72E0\u72E0\u5730\u7838\u5230\u5730\u4E0A\uFF0C\u51B2\u5230\u95E8\u5916\u5927\u58F0\u5455\u5410\u8D77\u6765\uFF0C\u8FB9\u5455\uFF0C\u6CEA\u6C34\u6B62\u4E0D\u4F4F\u5730\u6D41\u51FA\u6765\u3002</p>',84);function t(i,n){return r}var _=a(e,[["render",t]]);export{_ as default};
