import{e as p}from"./app.630af5f2.js";import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";const e={},r=p('<h1 id="\u7B2C\u4E8C\u5341\u56DB\u7AE0-\u6D77\u68E0" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u5341\u56DB\u7AE0-\u6D77\u68E0" aria-hidden="true">#</a> \u7B2C\u4E8C\u5341\u56DB\u7AE0 \u6D77\u68E0</h1><p>\u51B7\u5982\u971C\u9192\u5F97\u65E9\uFF0C\u7A97\u5916\u8FD8\u53EA\u8499\u8499\u4EAE\u3002</p><p>\u8FD9\u4E00\u665A\uFF0C\u5979\u7559\u5BBF\u5728\u767D\u5929\u5FB7\u7684\u5BDD\u5BAB\uFF0C\u7761\u7684\u5374\u4E0D\u662F\u5E8A\uFF0C\u8116\u5B50\u4E0A\u5957\u4E86\u4E00\u4E2A\u91D1\u8272\u7684\u94DC\u5708\uFF0C\u4E00\u6839\u7EC6\u7EF3\u5C06\u5979\u62F4\u5728\u5E8A\u811A\u8FB9\uFF0C\u8FD9\u4F7F\u5979\u610F\u8BC6\u5230\uFF0C\u81EA\u5DF1\u540C\u72D7\u7A9D\u7684\u6D77\u68E0\u4E00\u6837\uFF0C\u53EA\u4E0D\u8FC7\u662F\u7537\u4EBA\u7684\u4E00\u6761\u6BCD\u72AC\u800C\u5DF2\u3002</p><p>\u72D7\u94FE\u7684\u957F\u5EA6\u53EA\u591F\u5979\u7FFB\u8EAB\u5750\u8D77\uFF0C\u5446\u5446\u5730\u770B\u7740\u56DB\u5468\u3002</p><p>\u767D\u5929\u5FB7\u7761\u5F97\u6B63\u9999\uFF0C\u56DB\u80A2\u644A\u5F00\u9F3E\u58F0\u5982\u96F7\uFF0C\u5728\u4ED6\u7684\u5927\u817F\u95F4\uFF0C\u5C0F\u5973\u5B69\u8111\u888B\u6795\u7740\u7537\u4EBA\u7684\u5927\u817F\uFF0C\u5C0F\u5634\u5DF4\u8FD8\u8D34\u5728\u7537\u4EBA\u7684\u8089\u68D2\u4E0A\u9762\u3002</p><p>\u6628\u665A\uFF0C\u4E91\u96E8\u6570\u5EA6\u4E4B\u540E\uFF0C\u963F\u6708\u5C06\u5C0F\u5973\u5B69\u62B1\u4E86\u56DE\u6765\uFF0C\u5C0F\u5973\u5B69\u770B\u6765\u662F\u4E60\u60EF\u4E86\uFF0C\u4E00\u6765\u5C31\u81EA\u89C9\u5730\u5C06\u4ED6\u4EEC\u4E0B\u8EAB\u7684\u6C61\u79FD\u4E00\u70B9\u70B9\u8214\u5E72\u51C0\u3002\u8FD9\u4E2A\u96EA\u767D\u7C89\u5AE9\u7684\u5A03\u5A03\u600E\u4E48\u8D8A\u770B\u8D8A\u50CF\u6D77\u68E0\u554A\uFF0C\u6CA1\u9519\uFF0C\u5C0F\u4E86\u597D\u51E0\u53F7\u7684\u6D77\u68E0\uFF0C\u6BD4\u5979\u5988\u5988\u957F\u5F97\u767D\uFF0C\u5929\u751F\u7684\u7F8E\u4EBA\u80DA\u5B50\uFF0C\u4ECE\u5C0F\u5C31\u751F\u957F\u5728\u9B54\u7A9F\uFF0C\u771F\u662F\u53EF\u601C\u3002</p><p>\u5979\u4E5F\u4ECE\u7537\u4EBA\u53E3\u4E2D\u77E5\u9053\u4E86\u963F\u6708\u7684\u8EAB\u4EFD\uFF0C\u540D\u4E49\u4E0A\u662F\u8FD9\u4E2A\u57CE\u5BE8\u7684\u603B\u7BA1\uFF0C\u7BA1\u7406\u5973\u5974\u548C\u5185\u52A1\uFF0C\u53C8\u4E0D\u4F3C\u53EA\u662F\u603B\u7BA1\u90A3\u4E48\u7B80\u5355\uFF0C\u66F4\u5947\u602A\u7684\u662F\u8FD8\u8981\u5728\u90A3\u4E9B\u5175\u4E01\u4EEC\u9762\u524D\u8D64\u8EAB\u88F8\u4F53\uFF0C\u771F\u662F\u96BE\u89E3\u800C\u75AF\u72C2\u7684\u5730\u65B9\u3002</p><p>\u65E5\u4E0A\u4E09\u7AFF\uFF0C\u7537\u4EBA\u9192\u4E86\u3002\u963F\u6708\u5E26\u5934\uFF0C\u6628\u65E5\u89C1\u5230\u7684\u5982\u610F\u3001\u595A\u70DF\u7B49\u51E0\u4E2A\u7F8E\u4E3D\u5973\u5B50\u4F9D\u65E7\u88F8\u7740\u4E0B\u8EAB\u7AEF\u7740\u4E0D\u540C\u7684\u7269\u662F\u8FDB\u5F97\u95E8\u6765\u3002</p><p>\u963F\u6708\u5C06\u719F\u7761\u7684\u5C0F\u5973\u5B69\u62B1\u4E86\u51FA\u53BB\uFF0C\u5982\u610F\u4FEF\u6491\u5728\u767D\u5929\u5FB7\u80F8\u53E3\u4E0A\u65B9\uFF0C\u62FF\u6E29\u6DA6\u7684\u5976\u5B50\u7ED9\u4ED6\u64E6\u8EAB\uFF0C\u595A\u70DF\u722C\u5728\u4ED6\u7684\u80EF\u4E0B\uFF0C\u53FC\u4F4F\u7537\u4EBA\u7684\u8089\u68D2\uFF0C\u767D\u5929\u5FB7\u5374\u4E00\u811A\u5C06\u5979\u8E6C\u5F00\uFF0C\u51B2\u963F\u6708\u626B\u4E86\u4E00\u773C\u3002</p><p>\u963F\u6708\u86EE\u8170\u626D\u4E86\u4E00\u626D\uFF0C\u5A9A\u7B11\u9053\uFF0C\u300C\u4ECA\u5929\u6211\u7537\u4EBA\u8981\u6765\u4E86\u54E9\u3002\u300D</p><p>\u767D\u5929\u5FB7\u770B\u8D77\u6765\u975E\u5E38\u53D7\u7528\uFF0C\u8C03\u7B11\u9053\uFF0C\u300C\u6B63\u662F\u4F60\u7537\u4EBA\u6765\uFF0C\u8001\u5B50\u624D\u641E\u5F97\u4E00\u6B21\u662F\u4E00\u6B21\u561B\u3002\u300D</p><p>\u963F\u6708\u5F53\u7136\u4E0D\u4F1A\u5F53\u771F\u5728\u4E4E\uFF0C\u4E0D\u5F85\u7537\u4EBA\u8BF4\u5B8C\u8BF4\u4E0A\u524D\u5E72\u6D3B\u4E86\uFF0C\u5979\u7684\u820C\u529F\u751A\u597D\uFF0C\u5957\u5F04\u4E0B\u6765\uFF0C\u7537\u4EBA\u7684\u9633\u5177\u5934\u50CF\u4E00\u628A\u7D2B\u9ED1\u5C0F\u4F1E\u575A\u786C\u5730\u5F20\u5F00\u6765\uFF0C\u521A\u88AB\u8E22\u5F00\u7684\u595A\u70DF\u4E56\u5DE7\u5730\u94BB\u5230\u4E0B\u9762\uFF0C\u820C\u5C16\u5728\u5375\u86CB\u548C\u5C41\u773C\u95F4\u6765\u56DE\u6DFB\u5F04\uFF0C\u76F4\u81F3\u65E9\u8D77\u7684\u7B2C\u4E00\u6CE8\u7CBE\u5728\u963F\u6708\u7684\u5589\u7BA1\u6DF1\u5904\u7206\u53D1\u3002</p><p>\u5982\u6B64\u8FD9\u822C\u4E4B\u540E\uFF0C\u767D\u5929\u5FB7\u62AB\u8863\u4E0B\u5E8A\uFF0C\u300C\u5495\u565C\u5495\u565C\u300D\u559D\u4E0B\u4E00\u5927\u7897\u9C9C\u5976\uFF08\u51B7\u5982\u971C\u60F3\u8D77\u4E86\u90A3\u4E9B\u6BCD\u725B\uFF09\uFF0C\u5976\u6C34\u64E6\u624B\u8138\uFF0C\u6E05\u6C34\u6FEF\u51C0\uFF0C\u624D\u795E\u6E05\u6C14\u723D\uFF0C\u957F\u53F9\u4E00\u58F0\uFF0C\u300C\u7F8E\u597D\u7684\u4E00\u5929\u54EA\u3002\u300D</p><p>\u4ED6\u659C\u7768\u4E86\u59CB\u7EC8\u8C61\u5C40\u5916\u4EBA\u4E00\u822C\u65C1\u89C2\u7684\u51B7\u5982\u971C\uFF0C\u300C\u5728\u8FD9\u91CC\u662F\u4E0D\u662F\u770B\u89C1\u4E86\u4E0D\u5C11\u719F\u4EBA\u54EA\uFF1F\u6BD4\u5982\u8BF4\u53F8\u9A6C\u592B\u4EBA\u3002\u300D\u5149\u811A\u677F\u5C06\u595A\u70DF\u7684\u5934\u6309\u5728\u5730\u4E0A\uFF0C\u5728\u5979\u5149\u6D01\u7684\u8138\u86CB\u4E0A\u63C9\u6765\u63C9\u53BB\u3002</p><p>\u595A\u70DF\u5F53\u5E74\u4E5F\u662F\u6C85\u9547\u6709\u6570\u7684\u7F8E\u4EBA\uFF0C\u6B64\u65F6\u90A3\u59E3\u7F8E\u7684\u9762\u5B54\u5374\u88AB\u8DF5\u8E0F\u5728\u7537\u4EBA\u811A\u4E0B\uFF0C\u8E42\u8E8F\u5F97\u4E0D\u6210\u4EBA\u5F62\uFF0C\u504F\u751F\u8FD8\u5F97\u5F3A\u9732\u7B11\u610F\uFF0C\u6BD4\u54ED\u8FD8\u96BE\u770B\uFF0C\u53E3\u4E2D\u8F7B\u8F7B\u5524\u9053\uFF0C\u300C\u4E3B\u4EBA\uFF0C\u70DF\u5974\u77E5\u9519\u4E86\u3002\u300D</p><p>\u300C\u653E\u5C41\uFF0C\u8001\u5B50\u8FD8\u6CA1\u5F00\u53E3\uFF0C\u4F60\u5C31\u6653\u5F97\u54EA\u91CC\u9519\u5566\u3002\u971C\u5974\uFF0C\u53F8\u9A6C\u5357\u51FA\u5356\u4E86\u4F60\u7537\u4EBA\uFF0C\u8001\u5B50\u7B97\u662F\u66FF\u4F60\u62A5\u4E86\u4EC7\u5427\u3002\u300D</p><p>\u51B7\u5982\u971C\u65E0\u8A00\uFF0C\u5FC6\u8D77\u5F80\u4E8B\uFF0C\u5FC3\u4E2D\u6CE2\u6F9C\u8D77\u4F0F\u3002</p><p>\u300C\u8BB0\u4F4F\u4E86\uFF0C\u4E0B\u6B21\u8981\u5B66\u4F1A\u8FD9\u6837\u5B50\u4F3A\u5019\u3002\u300D\u767D\u5929\u5FB7\u9053\uFF0C\u53C8\u8F6C\u5411\u963F\u6708\uFF0C\u300C\u971C\u5974\u4EA4\u7ED9\u4F60\u8C03\u6559\u4E00\u4E0B\uFF0C\u8001\u5B50\u6E9C\u72D7\u53BB\u4E86\u3002\u300D</p><p>\u51B7\u5982\u971C\u5728\u88AB\u963F\u6708\u5E26\u53BB\u6C90\u6D74\u7684\u9014\u4E2D\uFF0C\u6B63\u597D\u779F\u5230\u4E86\u65E2\u5C06\u51FA\u5BE8\u95E8\u7684\u767D\u5929\u5FB7\uFF0C\u624B\u4E0A\u633D\u7740\u4E24\u6761\u7C97\u7EF3\uFF0C\u4E00\u6839\u7CFB\u7740\u4E00\u5934\u51F6\u731B\u7684\u5927\u9ED1\u72AC\uFF0C\u53E6\u4E00\u6839\u7CFB\u7740\u4E00\u4E2A\u56DB\u80A2\u7740\u5730\u819D\u884C\u7684\u8D64\u88F8\u5973\u4EBA\uFF0C\u540E\u80CC\u4E0A\u7EB9\u7740\u6574\u5E45\u7684\u523A\u9752\uFF0C\u53E4\u94DC\u5065\u7F8E\u7684\u808C\u80A4\u7ED9\u9633\u5149\u9540\u4E0A\u4E86\u4E00\u5C42\u871C\u8272\u7684\u5149\u8F89\u3002</p><p>\u72FC\u72D7\u663E\u5F97\u5341\u5206\u5174\u594B\uFF0C\u5728\u4E3B\u4EBA\u8EAB\u524D\u8EAB\u540E\u8DD1\u6765\u8DF3\u53BB\uFF0C\u5973\u72AC\u4E00\u76F4\u62D6\u5728\u8EAB\u540E\uFF0C\u4F46\u5F88\u67D4\u987A\uFF0C\u4E00\u6B65\u63A5\u4E00\u6B65\u722C\u5F97\u4ECE\u5BB9\u81EA\u7136\uFF0C\u6843\u578B\u5C41\u80A1\u9AD8\u9AD8\u6485\u8D77\u6765\u6643\u554A\u6643\u3002</p><p>\u963F\u6708\u9119\u89C6\u5730\u8BF4\uFF0C\u300C\u770B\u5230\u4E86\u5427\uFF0C\u5403\u4E86\u836F\u5C31\u4E56\u4E86\uFF0C\u8D31\u3002\u300D</p><p>\u4E00\u6574\u5929\uFF0C\u51B7\u5982\u971C\u6C90\u6D74\uFF0C\u5243\u6BDB\uFF0C\u66F4\u8863\uFF0C\u718F\u9999\uFF0C\u6362\u4E0A\u4E00\u4EF6\u505A\u5DE5\u7CBE\u7EC6\u5374\u77ED\u81F3\u809A\u8110\u7684\u8D34\u8EAB\u5C0F\u8863\uFF0C\u9664\u6B64\u4E4B\u5916\u518D\u4E5F\u6CA1\u6709\u4E86\uFF0C\u521A\u522E\u5E72\u51C0\u7684\u9690\u79C1\u5904\u51C9\u55D6\u55D6\u5730\u6781\u4E0D\u597D\u53D7\uFF0C\u4E5F\u6781\u4E3A\u96BE\u582A\u3002</p><p>\u5BA4\u5916\u52B3\u52A8\u7684\u5973\u5974\u8FD8\u6709\u5757\u5E03\u5305\u4F4F\u4E0B\u8EAB\uFF0C\u9047\u5230\u7684\u90A3\u4E9B\u4F8D\u5973\u867D\u4E5F\u4E0D\u7740\u4E0B\u88F3\uFF0C\u4F46\u4E0A\u8863\u4E0B\u6446\u52C9\u5F3A\u4E5F\u80FD\u906E\u4F4F\u534A\u8FB9\u5C41\u80A1\uFF0C\u552F\u72EC\u5BF9\u5979\u5982\u6B64\u82DB\u5F85\u5462\uFF1F</p><p>\u963F\u6708\u731C\u5230\u4E86\u5979\u7684\u5FC3\u601D\uFF0C\u89E3\u91CA\u9053\uFF0C\u300C\u522B\u4ECB\u610F\uFF0C\u89C4\u77E9\u5C31\u662F\u8FD9\u6837\uFF0C\u7537\u4EBA\u6700\u5927\uFF0C\u5973\u4EBA\u662F\u5974\u96B6\uFF0C\u8EAB\u4E0A\u7684\u4E00\u5207\u5668\u5B98\u90FD\u662F\u4E3A\u7537\u4EBA\u670D\u52A1\u7684\uFF0C\u5728\u5974\u96B6\u4E2D\u95F4\uFF0C\u53C8\u5206\u4E09\u516D\u4E5D\u7B49\uFF0C\u6BCD\u725B\u6700\u4E0B\u7B49\uFF0C\u5728\u9E26\u7247\u56ED\u52B3\u52A8\u7684\u5973\u5974\u6B21\u4E0B\u7B49\uFF0C\u4F8D\u5973\u6BD4\u5979\u4EEC\u8981\u9AD8\u4E00\u7B49\uFF0C\u800C\u4F60\uFF0C\u6BD4\u5979\u4EEC\u518D\u9AD8\u4E00\u7B49\uFF0C\u7B97\u662F\u6700\u9AD8\u7EA7\u7684\u5973\u5974\u4E86\uFF0C\u8FD9\u4E2A\u533A\u522B\uFF0C\u4E00\u662F\u770B\u4F60\u4EEC\u8116\u5B50\u4E0A\u6302\u7684\u9888\u5708\uFF0C\u5206\u91D1\u94F6\u94DC\u94C1\u56DB\u8272\u3002\u300D</p><p>\u51B7\u5982\u971C\u4F4E\u5934\u770B\uFF0C\u624D\u6CE8\u610F\u5230\u81EA\u5DF1\u83B9\u767D\u5982\u7389\u7684\u8116\u5B50\u4E0A\u60AC\u6302\u7684\u679C\u7136\u662F\u4E00\u53EA\u91D1\u8272\u7684\u7EC6\u73AF\uFF0C\u800C\u90A3\u4E9B\u4F8D\u5973\u4EEC\u6302\u7684\u662F\u767D\u94F6\u73AF\u3002\u963F\u6708\u5374\u6CA1\u6709\u73AF\u3002</p><p>\u963F\u6708\u7EED\u9053\uFF0C\u300C\u4E8C\u662F\u770B\u8863\u88F3\u7684\u957F\u77ED\uFF0C\u4E00\u822C\u6765\u8BB2\uFF0C\u9664\u4E86\u5916\u51FA\uFF0C\u5305\u62EC\u6211\u5728\u5185\uFF0C\u6CA1\u6709\u4E3B\u4EBA\u7684\u7279\u522B\u547D\u4EE4\uFF0C\u5728\u5BE8\u91CC\u90FD\u53EA\u80FD\u5149\u7740\u5C41\u80A1\u5594\uFF0C\u300D\u5979\u7B11\u4E86\u7B11\uFF0C\u300C\u94DC\u94C1\u4E24\u8272\u7684\u5973\u5974\u90FD\u53EF\u4EE5\u4F9B\u58EB\u5175\u4EEC\u968F\u610F\u73A9\u5F04\uFF0C\u91D1\u94F6\u5973\u5974\u4E0D\u53EF\u4EE5\uFF0C\u4E3A\u4E86\u5E73\u606F\u58EB\u5175\u7684\u6028\u6C14\uFF0C\u4E3B\u4EBA\u89C4\u5B9A\u4E86\u8D8A\u662F\u9AD8\u7B49\u7684\u5973\u5974\uFF0C\u8863\u88F3\u8D8A\u7A7F\u7740\u5C11\uFF0C\u65B9\u4FBF\u58EB\u5175\u4EEC\u9971\u9971\u773C\u798F\uFF0C\u4ED6\u4EEC\u4E5F\u53EF\u4EE5\u968F\u65F6\u8981\u6C42\u4F60\u505A\u4E00\u4E9B\u4E8B\uFF0C\u5374\u4E0D\u53EF\u4EE5\u52A8\u624B\u52A8\u811A\uFF0C\u66F4\u4E0D\u80FD\u5F3A\u66B4\u4F60\uFF0C\u5426\u5219\u60E9\u7F5A\u5F88\u91CD\u7684\u3002\u8BB0\u4F4F\u4E86\uFF0C\u4E0D\u8981\u6015\u4ED6\u4EEC\uFF0C\u4E5F\u4E0D\u8981\u5F97\u7F6A\u4ED6\u4EEC\u3002\u300D</p><p>\u542C\u4E86\u8FD9\u4E9B\u8BDD\uFF0C\u51B7\u5982\u971C\u5BB3\u6015\u5730\u8EB2\u5728\u623F\u91CC\u4E0D\u6562\u51FA\u6765\uFF0C\u4F46\u5403\u996D\u662F\u788D\u4E0D\u8FC7\u53BB\u4E86\uFF0C\u53EA\u597D\u7A7F\u7740\u8FD9\u4E00\u8EAB\u60F9\u706B\u7684\u8863\u88F3\uFF0C\u8EB2\u8EB2\u95EA\u95EA\u5730\u51FA\u4E86\u95E8\u3002</p><p>\u771F\u662F\u6015\u4EC0\u4E48\u6765\u4EC0\u4E48\uFF0C\u521A\u51FA\u95E8\u8FD9\u88AB\u4E24\u4E2A\u521A\u4E0B\u5C97\u7684\u5175\u4E01\u6321\u4F4F\uFF0C\u4E24\u773C\u653E\u5149\uFF0C\u76EF\u7740\u5979\u4E0A\u4E0A\u4E0B\u4E0B\u6253\u91CF\u3002\u300C\u54A6\uFF0C\u54E5\u4EEC\uFF0C\u65B0\u6765\u7684\u54E9\uFF0C\u771F\u662F\u6F02\u4EAE\uFF0C\u8FD8\u662F\u5149\u5934\u3002\u300D</p><p>\u300C\u542C\u5144\u5F1F\u4EEC\u8BF4\u6628\u5929\u6765\u4E86\u4E2A\u5C3C\u59D1\uFF0C\u6015\u5C31\u662F\u4F60\u5427\u3002\u300D</p><p>\u51B7\u5982\u971C\u672C\u80FD\u5730\u5E76\u62E2\u53CC\u817F\uFF0C\u4E24\u624B\u4EA4\u53C9\u906E\u4F4F\u7F9E\u5904\u3002</p><p>\u300C\u662F\u4E2A\u91D1\u5708\u3002\u300D\u4E00\u4E2A\u4EBA\u63D0\u9192\u9053\u3002</p><p>\u300C\u64CD\uFF0C\u597D\u7684\u90FD\u8BA9\u8001\u5927\u9738\u6389\u4E86\uFF0C\u626B\u5174\u3002\u5E72\u4E0D\u6210\uFF0C\u770B\u4E5F\u770B\u4E2A\u5C3D\u5174\uFF0C\u9A9A\u8D27\uFF0C\u628A\u624B\u653E\u5F00\uFF0C\u4E00\u6761\u817F\u6401\u5230\u6276\u680F\u4E0A\uFF0C\u81EA\u5DF1\u628A\u9A9A\u7A74\u63B0\u5F00\uFF0C\u2026\u2026\u5FEB\u70B9\uFF0C\u6162\u541E\u541E\u7684\u3002\u300D</p><p>\u4E24\u4E2A\u8111\u888B\u51D1\u5728\u51B7\u5982\u971C\u7684\u80EF\u4E0B\u7EC6\u7EC6\u89C2\u8D4F\uFF0C\u8BC4\u5934\u8BBA\u8DB3\uFF0C\u9F3B\u5B50\u55B7\u51FA\u7684\u70ED\u6C14\u90FD\u75D2\u75D2\u5730\u6251\u5230\u4E86\u5979\u7684\u82B1\u74E3\u4E0A\u3002\u300C\u54C7\uFF0C\u8FD9\u4E2A\u6D1E\u597D\u5C0F\uFF0C\u8FD8\u662F\u9C9C\u7EA2\u8272\uFF0C\u80AF\u5B9A\u7528\u5F97\u4E0D\u591A\u3002\u300D</p><p>\u300C\u5C41\u80A1\u4E5F\u597D\u7A84\uFF0C\u6015\u662F\u751F\u4E0D\u51FA\u7537\u5A03\u3002\u300D</p><p>\u300C\u8FD9\u4F60\u5C31\u770B\u8D70\u773C\u4E86\uFF0C\u5218\u592A\u592A\u65E9\u5C31\u751F\u4E86\u4E2A\u7537\u5A03\uFF0C\u4F60\u6CA1\u770B\u5230\u90A3\u4E2A\u5C0F\u6742\u79CD\uFF0C\u8DDF\u4ED6\u8001\u5B50\u4E00\u4E2A\u6837\u2026\u2026\u300D</p><p>\u51B7\u5982\u971C\u4E00\u9635\u9635\u7729\u6655\uFF0C\u9AD8\u9AD8\u4E3E\u8D77\u4E00\u6761\u817F\uFF0C\u5206\u5F00\u5973\u4EBA\u6700\u7F9E\u803B\u7684\u5730\u65B9\uFF0C\u7ED9\u8FD9\u4E9B\u72D7\u6837\u7684\u5BB6\u4F19\u77A7\uFF0C\u8FD8\u8981\u5982\u4F55\u5FCD\u53D7\u4ED6\u4EEC\u7684\u6DEB\u8BCD\u79FD\u8BED\uFF0C\u771F\u662F\u53C8\u7F9E\u53C8\u6124\uFF0C\u96BE\u4EE5\u575A\u6301\u3002</p><p>\u597D\u5728\u767D\u5929\u5FB7\u8FC7\u6765\u4E86\uFF0C\u5C06\u4ED6\u4EEC\u559D\u8D70\uFF0C\u5E2E\u51B7\u5982\u971C\u653E\u4E0B\u9178\u9EBB\u7684\u817F\uFF0C\u62CD\u62CD\u5979\u7684\u67D4\u80A9\uFF0C\u300C\u59D4\u5C48\u4E86\u5427\u3002\u300D\u51B7\u5982\u971C\u5FC3\u5934\u4E00\u9178\uFF0C\u6CEA\u6C34\u4E0D\u7531\u81EA\u4E3B\u5730\u6DCC\u4E86\u51FA\u6765\u3002</p><p>\u300C\u4E60\u60EF\u5C31\u597D\u4E86\uFF0C\u7B49\u4F1A\u968F\u6211\u51FA\u53BB\u63A5\u4E2A\u5BA2\u4EBA\u3002\u300D</p><p>\u65E5\u66AE\u65F6\u5206\uFF0C\u51B7\u5982\u971C\u88AB\u5E26\u5230\u4E86\u5BE8\u95E8\u53E3\uFF0C\u8FC7\u4E00\u4F1A\uFF0C\u767D\u5929\u5FB7\u4E58\u5750\u7740\u4E00\u8F86\u53CC\u8F6E\u5C0F\u8F66\u51FA\u6765\u4E86\uFF0C\u5C0F\u8F66\u505A\u5F97\u5F88\u79C0\u6C14\uFF0C\u7C7B\u4F3C\u4E8E\u51AC\u5929\u6ED1\u96EA\u7684\u96EA\u6A47\uFF0C\u62C9\u8F66\u7684\u975E\u9A6C\u975E\u9A74\uFF0C\u6B63\u662F\u90A3\u6761\u5A01\u731B\u7684\u72FC\u72D7\u548C\u9888\u80A9\u4E0A\u65B0\u5957\u4E86\u62C9\u8F66\u7EF3\u7684\u6D77\u68E0\u3002\u6D77\u68E0\u722C\u884C\u8FC7\u6765\uFF0C\u5782\u7740\u5934\uFF0C\u6839\u672C\u6CA1\u770B\u51B7\u5982\u971C\u4E00\u773C\u3002</p><p>\u767D\u5929\u5FB7\u62DB\u547C\u51B7\u5982\u971C\u5750\u4E0A\u8F66\uFF0C\u62B1\u5728\u6000\u91CC\uFF0C\u4E00\u53EA\u624B\u76F4\u63A5\u5C31\u63D2\u8FDB\u4E86\u5979\u7684\u5927\u817F\u4E4B\u95F4\u629A\u5F04\uFF0C\u53E6\u4E00\u53EA\u624B\u6301\u7740\u4E00\u6839\u957F\u97AD\uFF0C\u5728\u7A7A\u4E2D\u547C\u5578\u4E00\u58F0\uFF0C\u556A\u5728\u843D\u5728\u6D77\u68E0\u7D27\u5B9E\u7684\u5C41\u80A1\u4E0A\uFF0C\u6253\u51FA\u4E00\u6761\u8840\u75D5\u3002\u542C\u5230\u97AD\u54CD\uFF0C\u72FC\u72D7\u7BAD\u4E00\u822C\u5730\u7A9C\u4E86\u51FA\u53BB\uFF0C\u672C\u6765\u662F\u819D\u884C\u7684\u6D77\u68E0\u4E5F\u53EA\u5F97\u5C06\u817F\u62AC\u9AD8\uFF0C\u5C48\u7740\u819D\u5FEB\u901F\u722C\u52A8\u8D77\u6765\u3002</p><p>\u4EBA\u8F66\u6CBF\u7740\u7EA2\u8272\u7684\u7530\u91CE\u5784\u95F4\u5411\u8FDC\u65B9\u5954\u53BB\u3002</p><p>\u7537\u4EBA\u53E3\u4E2D\u8377\u8377\u4F5C\u54CD\uFF0C\u8FDE\u7740\u51E0\u97AD\u90FD\u62BD\u6253\u5728\u6D77\u68E0\u8EAB\u4E0A\uFF0C\u6BCF\u4E00\u97AD\uFF0C\u6D77\u68E0\u90FD\u7981\u4E0D\u4F4F\u54C6\u55E6\u4E00\u4E0B\uFF0C\u7136\u540E\u52A0\u5FEB\u901F\u5EA6\uFF0C\u4ECE\u56DB\u80A2\u7740\u5730\u5230\u4E24\u811A\u5954\u8DD1\uFF0C\u867D\u7136\u6CA1\u6709\u76F4\u7ACB\uFF0C\u4F46\u8EAB\u4F53\u8D34\u7740\u5730\u9762\u8D8A\u8DD1\u8D8A\u5FEB\uFF0C\u80F8\u524D\u9971\u6EE1\u7684\u4E73\u623F\u6709\u8282\u594F\u5730\u4E0A\u4E0B\u98A0\u52A8\uFF0C\u96EA\u767D\u7684\u5934\u53D1\u5411\u540E\u98D8\u52A8\uFF0C\u843D\u65E5\u4F59\u6656\u4E0B\uFF0C\u50CF\u4E00\u5934\u7F8E\u4E3D\u7684\u96CC\u517D\u5728\u5E7F\u88A4\u7684\u5927\u5730\u4E0A\u72C2\u5954\u3002</p><p>\u5927\u7EA6\u8D70\u4E86\u4E24\u516C\u91CC\u5DE6\u53F3\uFF0C\u4ED6\u4EEC\u6765\u5230\u4E00\u4E2A\u5C71\u53E3\uFF0C\u6D77\u68E0\u4E0E\u72FC\u72D7\u90FD\u5DF2\u7D2F\u5F97\u8FD1\u4E4E\u762B\u75EA\uFF0C\u6D77\u68E0\u5168\u8EAB\u5927\u6C57\u6CE0\u6CE0\uFF0C\u4E00\u505C\u4E0B\u5C31\u8EBA\u5012\u5728\u5730\u722C\u4E0D\u8D77\u6765\uFF0C\u72FC\u72D7\u4E5F\u662F\u5410\u51FA\u820C\u5934\u5927\u5598\u6C14\u3002</p><p>\u8FCE\u9762\u5DF2\u6709\u51E0\u4E2A\u4EBA\u5728\u7B49\u5F85\uFF0C\u770B\u4ED6\u4EEC\u7684\u6A21\u6837\u90FD\u975E\u5584\u7C7B\u3002</p><p>\u767D\u5929\u5FB7\u4E0B\u8F66\uFF0C\u6402\u4F4F\u4E3A\u9053\u7684\u5BB6\u4F19\u5927\u7B11\uFF0C\u300C\u660C\u5144\uFF0C\u597D\u4E45\u4E0D\u89C1\uFF0C\u60F3\u6B7B\u5144\u5F1F\u4E86\u3002\u600E\u4E48\u6837\uFF0C\u505A\u8001\u5927\u7684\u6ECB\u5473\u8FD8\u662F\u597D\u5427\u3002\u300D</p><p>\u7533\u660C\u5634\u89D2\u4E00\u54A7\uFF0C\u300C\u54EA\u6709\u4F60\u8001\u5144\u6ECB\u6DA6\u54EA\uFF0C\u8D22\u5BCC\u5982\u5C71\uFF0C\u7F8E\u5973\u5982\u4E91\u3002\u300D</p><p>\u300C\u8FD9\u8BDD\u8BF4\u5F97\u96BE\u542C\uFF0C\u54B1\u5144\u5F1F\u8FD9\u4E48\u591A\u5E74\u8FDE\u624B\uFF0C\u6211\u7684\u4E0D\u5C31\u662F\u4F60\u7684\u5417\uFF1F\u66F4\u4F55\u51B5\uFF0C\u6211\u8FD9\u5C31\u8D70\u4E86\uFF0C\u8FD9\u4E00\u644A\u5B50\u90FD\u4EA4\u7ED9\u5144\u5F1F\u4E86\u3002\u300D</p><p>\u300C\u771F\u7684\u8981\u8D70\u5417\uFF1F\u300D</p><p>\u300C\u662F\u554A\uFF0C\u91D1\u4E09\u89D2\u90A3\u8FB9\u6211\u65B0\u8F9F\u7684\u57FA\u5730\u5DF2\u7ECF\u6210\u5F62\uFF0C\u800C\u4E14\u6C14\u5019\u571F\u58E4\u8FD8\u6709\u5468\u8FB9\u73AF\u5883\u90FD\u6BD4\u6B64\u5904\u66F4\u597D\uFF0C\u4E0D\u51FA\u5341\u5E74\u80AF\u5B9A\u4F1A\u6210\u4E3A\u4E00\u4E2A\u4E2D\u5FC3\u3002\u300D</p><p>\u300C\u90A3\u5C31\u662F\u8BF4\u4EA4\u7ED9\u5144\u5F1F\u6211\u7684\u662F\u4E00\u4E2A\u7A7A\u58F3\u7F57\u3002\u300D</p><p>\u300C\u8BF4\u7B11\u5427\uFF0C\u6211\u767D\u67D0\u4EC0\u4E48\u4EBA\u4F60\u8FD8\u4E0D\u6E05\u695A\uFF0C\u6211\u53EA\u5E26\u8D70\u8FD9\u4E24\u4E2A\uFF0C\u300D\u4ED6\u6307\u4E86\u6307\u4E00\u4FA7\u7684\u6D77\u68E0\u548C\u51B7\u5982\u971C\uFF0C\u300C\u5176\u5B83\u7684\u4E00\u5207\u5305\u62EC\u4ECA\u5E74\u7684\u6536\u6210\u3001\u6E20\u9053\u7EDF\u7EDF\u5F52\u4F60\uFF0C\u8FD8\u4E0D\u591F\u610F\u601D\u5417\uFF1F</p><p>\u4E0D\u662F\u4F60\u8001\u5F1F\uFF0C\u6211\u8FD8\u820D\u4E0D\u5F97\u653E\u54E9\u3002\u300D</p><p>\u300C\u54C8\u54C8\uFF0C\u521A\u624D\u662F\u8DDF\u8001\u5144\u5F00\u73A9\u7B11\u5566\u3002\u8BDD\u8BF4\u56DE\u6765\uFF0C\u4F60\u867D\u7136\u53EA\u5E26\u8D70\u4E24\u4E2A\uFF0C\u53EF\u662F\u6700\u9876\u5C16\u7684\u4E24\u4E2A\u5566\uFF0C\u8001\u5F1F\u6211\u770B\u7740\u53EF\u6709\u4E9B\u5FC3\u75D2\u75D2\u7684\u3002\u300D</p><p>\u767D\u5929\u5FB7\u5FC3\u4E2D\u6697\u9A82\uFF0C\u6068\u4E0D\u5F97\u4E00\u5DF4\u638C\u63B4\u6B7B\u4ED6\u3002\u4E94\u5E74\u524D\uFF0C\u4ED6\u4E0E\u7533\u660C\u8FBE\u6210\u79D8\u5BC6\u534F\u8BAE\uFF0C\u5171\u8C0B\u5E72\u6389\u4E86\u699C\u7237\uFF0C\u7533\u660C\u53D6\u4EE3\u699C\u7237\u5750\u4E0A\u4E86\u9F99\u5934\u8001\u5927\u7684\u4F4D\u7F6E\uFF0C\u800C\u767D\u5929\u5FB7\u5219\u4E13\u6CE8\u4E8E\u9E26\u7247\u79CD\u690D\u3002</p><p>\u4E24\u4EBA\u72FC\u72C8\u4E3A\u5978\uFF0C\u628A\u4E00\u4E2A\u6BD2\u54C1\u751F\u610F\u505A\u5F97\u7EA2\u7EA2\u706B\u706B\uFF0C\u4F46\u53C8\u4E92\u6709\u5FC3\u7ED3\uFF0C\u4E92\u76F8\u63D0\u9632\uFF0C\u65F6\u65F6\u60F3\u4FB5\u541E\u4E86\u5BF9\u65B9\u3002</p><p>\u6B64\u65F6\uFF0C\u4E2D\u56FD\u5883\u5185\u7684\u519B\u9600\u6DF7\u6218\u6E10\u606F\uFF0C\u4F46\u65B0\u7684\u5185\u6218\u53C8\u8D77\uFF0C\u6218\u706B\u4E00\u8DEF\u70E7\u5230\u4E86\u6E58\u897F\u8FB9\u5883\u3002\u767D\u5929\u5FB7\u9690\u7EA6\u9884\u611F\u5230\u5371\u673A\u6765\u4E34\uFF0C\u65E9\u5728\u4E24\u5E74\u524D\u6709\u8BA1\u5212\u79FB\u5E08\u6D77\u5916\uFF0C\u6B63\u597D\u5C06\u8FD9\u4E00\u5757\u5F03\u7ED9\u7533\u660C\uFF0C\u6EE1\u8DB3\u4ED6\u7684\u72FC\u5B50\u91CE\u5FC3\u3002</p><p>\u7ECF\u8FC7\u8FD9\u4E48\u591A\u5E74\u7684\u6253\u62FC\uFF0C\u767D\u5929\u5FB7\u5DF2\u57CE\u5E9C\u6DF1\u6C89\uFF0C\u4FE8\u7136\u6709\u4E86\u4E00\u65B9\u9738\u4E3B\u7684\u6D3E\u5934\uFF0C\u542C\u4E86\u7533\u660C\u7684\u5C41\u8BDD\u867D\u662F\u4E0D\u5FEB\uFF0C\u5374\u5FAE\u5FAE\u4E00\u7B11\uFF0C\u907F\u91CD\u5C31\u8F7B\u9053\uFF0C\u300C\u8FD9\u4E2A\u597D\u8BF4\u5566\uFF0C\u8001\u5F1F\u6709\u5174\u81F4\uFF0C\u4ECA\u665A\u5C31\u53EB\u5979\u4E24\u4E2A\u966A\u4F60\u73A9\u4E2A\u5C3D\u5174\u3002\u300D</p><p>\u7537\u4EBA\u8BF4\u8BDD\u95F4\uFF0C\u5973\u4EBA\u548C\u72D7\u90FD\u8FDC\u8FDC\u907F\u5728\u4E00\u65C1\uFF0C\u7533\u660C\u5E26\u6765\u7684\u4EBA\u6563\u5728\u56DB\u5468\uFF0C\u773C\u775B\u7EA2\u7EA2\u5730\u76EF\u7740\u4E24\u4E2A\u8863\u4E0D\u906E\u4F53\u7684\u5973\u5B50\u3002</p><p>\u7533\u660C\u548C\u767D\u5929\u5FB7\u8D70\u4E86\u8FC7\u6765\uFF0C\u53EA\u542C\u5230\u7533\u660C\u9053\uFF0C\u300C\u2026\u2026\u2026\u5C0F\u5F1F\u521A\u624D\u6240\u8A00\u90FD\u662F\u73A9\u7B11\u800C\u5DF2\uFF0C\u73A9\u7B11\u800C\u5DF2\uFF0C\u4E3A\u4E86\u7ED9\u8001\u5144\u996F\u884C\uFF0C\u8D39\u5C3D\u5FC3\u529B\uFF0C\u7279\u5730\u627E\u6765\u4E86\u4EF6\u793C\u7269\u3002\u300D</p><p>\u8BDD\u97F3\u521A\u843D\uFF0C\u4ECE\u6797\u540E\u8F6C\u51FA\u6765\u4E24\u4E2A\u6C49\u5B50\uFF0C\u8D39\u529B\u5730\u62AC\u7740\u4E00\u53EA\u9EBB\u888B\u3002</p><p>\u9EBB\u888B\u843D\u4E0B\uFF0C\u89E3\u5F00\uFF0C\u4E00\u4E2A\u5149\u6E9C\u6E9C\u7684\u5973\u4EBA\u6EDA\u843D\u5728\u5730\uFF0C\u5468\u8EAB\u8BA9\u7EF3\u7D22\u6346\u5F97\u6B7B\u6B7B\u7684\uFF0C\u5634\u91CC\u585E\u4E86\u5E03\u6761\u3002</p><p>\u767D\u5929\u5FB7\u60CA\u8BB6\u5730\u8BF4\uFF0C\u300C\u54A6\uFF0C\u662F\u94F6\u53F6\u8FD9\u5C0F\u5A4A\u5B50\u5440\u3002\u300D</p><p>\u7533\u660C\u5F97\u610F\u5730\u8BF4\uFF0C\u300C\u8FD9\u53EF\u53EB\u5F97\u6765\u5168\u4E0D\u8D39\u5DE5\u592B\u3002\u6211\u624B\u4E0B\u65E0\u610F\u4E2D\u63A2\u542C\u5230\u6709\u4E2A\u5973\u4EBA\u5728\u75AF\u72C2\u5730\u627E\u4F60\u5BFB\u4EC7\uFF0C\u8FDE\u57FA\u5730\u7684\u60C5\u51B5\u90FD\u6478\u4E86\u4E2A\u5927\u6982\uFF0C\u6211\u60F3\u8FD9\u8FD8\u4E86\u5F97\uFF0C\u4F60\u7684\u4E8B\u5C31\u662F\u8001\u5F1F\u7684\u4E8B\uFF0C\u5C31\u53EB\u4EBA\u628A\u5979\u7ED1\u4E86\u6765\uFF0C\u8FD9\u5A4A\u5B50\u5F88\u6CFC\u8FA3\uFF0C\u8D39\u4E86\u70B9\u624B\u811A\u3002\u300D</p><p>\u300C\u6211\u8BB0\u5F97\u90A3\u65F6\u5979\u8FD8\u5E72\u6389\u4E86\u6211\u7684\u4E00\u4E2A\u526F\u5B98\uFF0C\u6211\u4E0B\u4EE4\u8981\u5904\u6B7B\u5979\uFF0C\u540E\u6765\u6253\u4ED7\u5C31\u628A\u8FD9\u4E8B\u7ED9\u6401\u4E0B\u4E86\uFF0C\u8BB0\u5F97\u90A3\u65F6\u8FD9\u5A4A\u5B50\u8FD8\u6709\u4E00\u8EAB\u75C5\u54EA\uFF0C\u600E\u4E48\u8FD8\u6CA1\u6B7B\u5417\uFF1F\u300D</p><p>\u300C\u8C01\u77E5\u9053\u5462\uFF1F\u8BB8\u662F\u8001\u5929\u5F00\u4E86\u6069\uFF0C\u81EA\u5DF1\u6CBB\u597D\u4E86\u5427\u3002\u53CD\u6B63\u8001\u5B50\u600E\u4E48\u62F7\u6253\u5C31\u662F\u4E0D\u5F00\u53E3\u3002\u300D</p><p>\u767D\u5929\u5FB7\u72DE\u7B11\u9053\uFF0C\u300C\u8FD9\u53EF\u662F\u8001\u5929\u5BF9\u6211\u5F00\u4E86\u6069\uFF0C\u53EB\u5979\u53C8\u843D\u56DE\u6211\u624B\u91CC\uFF0C\u5144\u5F1F\uFF0C\u6B63\u597D\u4E34\u884C\u6CA1\u4EC0\u4E48\u8282\u76EE\uFF0C\u4ECA\u665A\u5C31\u53EB\u4F60\u770B\u573A\u597D\u620F\u3002\u300D</p><p>\u4E94\u5E74\u7684\u5149\u9634\u5728\u94F6\u53F6\u7684\u8EAB\u4E0A\u8FD8\u662F\u6253\u4E0A\u4E86\u5370\u8BB0\uFF0C\u5979\u5DF2\u4E0D\u590D\u5F53\u5E74\u7684\u5C0F\u5973\u5B69\u4E86\uFF0C\u4EC7\u6068\u66F4\u662F\u5C06\u4E00\u5F20\u4FCA\u8138\u523B\u753B\u5F97\u5200\u524A\u4E00\u822C\u5C16\u786C\uFF0C\u5979\u53D7\u5230\u4E86\u6781\u5927\u7684\u9650\u5236\uFF0C\u8FD8\u662F\u5C3D\u529B\u626D\u52A8\uFF0C\u5FFD\u7136\uFF0C\u770B\u5230\u4E86\u8DB4\u5728\u5730\u4E0A\u4EF0\u7740\u5934\u671B\u5411\u8FDC\u65B9\u7684\u6D77\u68E0\uFF0C\u5446\u4F4F\u4E0D\u52A8\u4E86\uFF0C\u773C\u6CEA\u6D41\u4E86\u4E0B\u6765\u3002</p><p>\u51B7\u5982\u971C\u4E5F\u6B63\u5728\u671B\u7740\u6D77\u68E0\uFF0C\u987A\u7740\u5979\u7684\u89C6\u7EBF\u770B\u8FC7\u53BB\uFF0C\u5C71\u5D16\u8FB9\uFF0C\u6B63\u6709\u7740\u4E00\u7C07\u6012\u653E\u7684\u6D77\u68E0\u82B1\uFF0C\u830E\u4E3A\u4F1E\uFF0C\u53F6\u4E3A\u88D9\uFF0C\u671B\u53BB\u4F3C\u4EAD\u4EAD\u5C11\u5973\uFF0C\u80ED\u8102\u822C\u7684\u82B1\u6735\u5728\u6B8B\u9633\u4E0B\u5982\u8840\u4E00\u822C\u9C9C\u7EA2\uFF0C\u4F3C\u79CB\u4E00\u6837\u60B2\u51C9\uFF0C\u5728\u51B0\u51B7\u7684\u5C71\u5D16\u95F4\u3001\u51DB\u51BD\u7684\u5BD2\u98CE\u4E2D\u745F\u745F\u6296\u52A8\uFF0C\u663E\u5F97\u662F\u90A3\u4E48\u7684\u72EC\u7ACB\u3001\u5014\u5F3A\uFF0C\u800C\u53C8\u662F\u90A3\u4E48\u7684\u51C4\u8273\uFF0C\u60B2\u51C9\u3002</p><p>\u8FD9\u662F\u6D77\u68E0\u7684\u547D\u8FD0\u5417\uFF1F\u6291\u6216\u5728\u5979\u4EEC\u5171\u540C\u7684\u547D\u8FD0\uFF1F</p><p>\u5979\u4EEC\u90FD\u662F\u90A3\u4E48\u7684\u7F8E\u4E3D\uFF0C\u9A84\u50B2\uFF0C\u66FE\u7ECF\u4E5F\u4E00\u5EA6\u62E5\u6709\u8FC7\u5C0A\u8363\u7684\u751F\u6D3B\u548C\u672A\u6765\uFF0C\u5982\u679C\u751F\u5728\u548C\u5E73\u5E74\u4EE3\uFF0C\u5979\u4EEC\u90FD\u8BE5\u662F\u591A\u4E48\u7684\u9A84\u50B2\u5E78\u798F\u7684\u4EBA\u513F\u554A\u3002\u662F\u4EC0\u4E48\u8BA9\u5979\u4EEC\u5728\u7A81\u7136\u4E4B\u95F4\u8F93\u6389\u4E86\u8FD9\u4E00\u5207\uFF0C\u8F93\u5F97\u8FD9\u4E48\u5F7B\u5E95\uFF0C\u8FD9\u4E48\u6DF1\u91CD\uFF0C\u4EE5\u81F3\u4E8E\u4E00\u56DE\u9996\u4E5F\u96BE\u89C1\u767E\u5E74\u8EAB\u5462\uFF1F</p>',70);function t(i,n){return r}var _=a(e,[["render",t]]);export{_ as default};
