import{e as p}from"./app.630af5f2.js";import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";const e={},r=p('<h1 id="\u7B2C\u4E8C\u5341\u4E00\u7AE0-\u523A\u9752" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u5341\u4E00\u7AE0-\u523A\u9752" aria-hidden="true">#</a> \u7B2C\u4E8C\u5341\u4E00\u7AE0 \u523A\u9752</h1><p>\u5BCC\u542B\u786B\u9178\u7684\u6E29\u6CC9\u6C34\u6C69\u6C69\u6D41\u51FA\uFF0C\u6C47\u5230\u5D16\u4E0B\u7684\u5C0F\u5858\uFF0C\u817E\u8D77\u767D\u8499\u8499\u7684\u96FE\u6C14\u3002\u5728\u7FE0\u7AF9\u6D77\uFF0C\u5979\u5E38\u5E38\u548C\u59D0\u59B9\u4EEC\u4E00\u8D77\uFF0C\u8131\u5F97\u5149\u6E9C\u6E9C\u7684\uFF0C\u5728\u6E29\u6CC9\u91CC\u620F\u800D\uFF0C\u7537\u4EBA\u561B\uFF0C\u90FD\u53EA\u6709\u628A\u98CE\u7684\u4EFD\uFF0C\u8C01\u6562\u5077\u77A7\u5C31\u6316\u773C\u775B\uFF0C\u5F53\u7136\uFF0C\u53EA\u662F\u8BF4\u8BF4\u800C\u5DF2\u3002</p><p>\u8FD9\u65E5\u5B50\u771F\u662F\u5F00\u5FC3\u54EA\uFF0C\u91D1\u82B1\u3001\u94F6\u53F6\u603B\u8981\u56F4\u7740\u5979\uFF0C\u6478\u7740\u5979\u7D27\u5B9E\u6709\u529B\u7684\u808C\u8089\u767E\u822C\u7FA1\u6155\uFF0C\u5979\u4E5F\u559C\u6B22\u770B\u7740\u8FD9\u4E9B\u5973\u5B69\u5B50\u65E0\u62D8\u65E0\u675F\u5730\u6B22\u7B11\uFF0C\u563B\u620F\uFF0C\u6253\u6C34\u4ED7\uFF0C\u94F6\u94C3\u822C\u7684\u7B11\u58F0\u6D12\u4E86\u4E00\u6C60\u3002</p><p>\u4E00\u4F1A\u6CA1\u7559\u610F\uFF0C\u59D0\u59B9\u4EEC\u5148\u540E\u8D70\u4E86\u4E2A\u5E72\u51C0\uFF0C\u4F59\u4E0B\u5979\u4E00\u4E2A\u4EBA\u8FD8\u6CE1\u5728\u6C34\u91CC\uFF0C\u6C34\u6E29\u7A81\u7136\u6CB8\u817E\u8D77\u6765\uFF0C\u8D8A\u6765\u8D8A\u70EB\uFF0C\u5979\u60F3\u4E0A\u5CB8\uFF0C\u53EF\u52A8\u5F39\u4E0D\u5F97\uFF0C\u6C60\u6C34\u5192\u6CE1\u4E86\uFF0C\u5495\u565C\u5495\u565C\u54CD\uFF0C\u6CA1\u6709\u4E00\u4E2A\u4EBA\u5728\u8EAB\u8FB9\uFF0C\u4E0D\uFF0C\u8FD9\u6837\u4E0D\u884C\uFF0C\u8981\u4E0A\u53BB\uFF0C\u6551\u547D\u554A\u2026\u2026</p><p>\u6D77\u68E0\u60CA\u9192\u8FC7\u6765\uFF0C\u624D\u53D1\u89C9\u81EA\u5DF1\u8116\u9888\u4EE5\u4E0B\u7684\u8EAB\u4F53\u679C\u7136\u662F\u6D78\u6CE1\u5728\u6E29\u6C34\u91CC\uFF0C\u4E00\u4E2A\u5C0F\u5973\u5B69\u8FD8\u5728\u5F80\u91CC\u6D47\u6CE8\u70ED\u6C34\uFF0C\u624B\u811A\u5448\u5927\u5B57\u72B6\u5927\u5288\u53C9\u6346\u5728\u56DB\u89D2\uFF0C\u52A8\u5F39\u4E0D\u5F97\uFF0C\u8FD8\u6709\u4E00\u4E2A\u4E2D\u5E74\u5987\u5973\u6B63\u5728\u624B\u7EDE\u7740\u4E1D\u74DC\u79E7\u5236\u6210\u7684\u5237\u5B50\uFF0C\u540C\u65F6\u7528\u529B\u6413\u6D17\u7740\u5979\u7684\u8EAB\u4F53\uFF0C\u9690\u5BC6\u5904\u66F4\u662F\u64E6\u5F97\u4ED4\u7EC6\uFF0C\u5C0F\u5C0F\u7684\u6298\u7F1D\u90FD\u4E0D\u653E\u8FC7\u3002</p><p>\u5728\u8FD9\u822C\u65E2\u75DB\u53C8\u9EBB\u7684\u5F3A\u70C8\u523A\u6FC0\u4E0B\uFF0C\u6D77\u68E0\u7684\u8EAB\u4F53\u9010\u6E10\u8D77\u4E86\u53CD\u5E94\uFF0C\u4E0B\u8EAB\u7535\u51FB\u822C\u6D8C\u51FA\u4E00\u80A1\u70ED\u6D41\uFF0C\u4E2D\u5E74\u5987\u5973\u770B\u5728\u773C\u91CC\uFF0C\u795E\u8272\u95F4\u5206\u660E\u5728\u9119\u5937\u5730\u8BF4\uFF0C\u300C\u9A9A\u8D27\u3002\u300D\u6478\u51FA\u4E00\u628A\u5C0F\u5200\u5B50\uFF0C\u7ED9\u5979\u522E\u8D77\u4E0B\u8EAB\u7684\u6BDB\u53D1\u6765\u3002</p><p>\u6D77\u68E0\u7684\u610F\u8BC6\u603B\u7B97\u56DE\u5230\u4E86\u73B0\u5B9E\u5F53\u4E2D\uFF0C\u8840\u6D1E\uFF0C\u86EE\u5B50\uFF0C\u767D\u5929\u5FB7\uFF0C\u51FA\u5356\uFF0C\u9F3B\u73AF\uFF0C\u521A\u521A\u7FFB\u8FC7\u53BB\u7684\u4E00\u9875\u53C8\u5728\u5FC3\u4E2D\u95F9\u817E\u8D77\u6765\uFF0C\u5C0F\u5200\u5B50\u5728\u5FC3\u5934\u4E00\u70B9\u70B9\u5730\u952F\uFF0C\u76F4\u5230\u5FC3\u91CC\u4E5F\u662F\u9C9C\u8840\u6DCB\u6F13\u3002</p><p>\u53EF\u6015\u7684\u662F\uFF0C\u90A3\u4E0D\u662F\u5669\u68A6\u3002</p><p>\u9634\u6BDB\u522E\u5149\u540E\uFF0C\u63A5\u7740\u662F\u814B\u6BDB\uFF0C\u4F53\u6BDB\uFF0C\u518D\u662F\u70ED\u6C34\u51B2\u5237\uFF0C\u6574\u6574\u82B1\u4E86\u597D\u51E0\u4E2A\u65F6\u8FB0\uFF0C\u5979\u7684\u8EAB\u4F53\u5982\u540C\u5A74\u513F\u822C\u7684\u6D01\u51C0\uFF0C\u8FDE\u540C\u4F24\u75D5\u90FD\u597D\u4E86\u4E0D\u5C11\uFF0C\u6563\u53D1\u51FA\u6021\u4EBA\u7684\u82B3\u9999\u3002</p><p>\u8FD9\u60C5\u666F\u4E0D\u7981\u8BA9\u4EBA\u8054\u60F3\u8D77\u2014\u2014\u6740\u732A\u6D17\u732A\u7684\u60C5\u5F62\u3002</p><p>\u6D77\u68E0\u65E0\u80FD\u53CD\u6297\uFF0C\u53EA\u6709\u542C\u4EFB\u522B\u4EBA\u5C06\u5979\u5305\u8D77\uFF0C\u9001\u5165\u53E6\u4E00\u623F\u95F4\uFF0C\u623F\u91CC\u6B63\u4E2D\u592E\u6446\u7740\u4E00\u6761\u5305\u7740\u8F6F\u57AB\u7684\u957F\u9526\u8F6F\u51F3\u3002\u5979\u88AB\u6309\u7740\u9762\u671D\u4E0B\u8D64\u6761\u6761\u5730\u8DB4\u5728\u9526\u51F3\u4E0A\uFF0C\u624B\u811A\u9501\u5728\u957F\u51F3\u56DB\u4E2A\u811A\u7684\u5730\u9762\u94C1\u73AF\u4E0A\uFF0C\u809A\u8179\u4E0B\u8FD8\u585E\u8FDB\u4E00\u4E2A\u6795\u5934\uFF0C\u4F7F\u5979\u6843\u5F62\u7684\u5C41\u80A1\u9AD8\u9AD8\u7FD8\u4E86\u8D77\u6765\uFF0C\u7F9E\u4EBA\u7684\u59FF\u5F0F\u597D\u50CF\u5728\u7B49\u5F85\u7740\u4EC0\u4E48\u3002</p><p>\u767D\u5929\u5FB7\u8EAB\u62AB\u5BBD\u8863\u8F6F\u888D\uFF0C\u65BD\u65BD\u7136\u8D70\u8FDB\u6765\uFF0C\u5148\u56F4\u7740\u5979\u770B\u4E86\u4E00\u5708\uFF0C\u7279\u610F\u5728\u5979\u91CD\u65B0\u53D8\u6210\u767D\u864E\u7684\u9634\u6237\u591A\u76EF\u4E86\u51E0\u773C\uFF0C\u53C8\u5728\u5979\u53D8\u5F97\u6ED1\u817B\u7684\u5C41\u80A1\u4E0A\u8F7B\u8F7B\u62CD\u4E86\u62CD\uFF0C\u957F\u671F\u953B\u70BC\u4E0B\u7684\u81C0\u8089\u5341\u5206\u7D27\u5B9E\uFF0C\u6CA1\u6709\u60EF\u5E38\u7684\u98A4\u52A8\u3002\u767D\u5929\u5FB7\u6EE1\u610F\u7684\u5439\u4E86\u58F0\u53E3\u54E8\uFF0C\u8F6C\u5230\u524D\u9762\uFF0C\u5750\u5230\u5730\u4E0A\uFF0C\u6258\u8D77\u6D77\u68E0\u7684\u4E0B\u5DF4\uFF0C\u8BA9\u5979\u7684\u773C\u775B\u6B63\u9762\u76F8\u5BF9\u3002</p><p>\u300C\u767D\u677F\u513F\uFF0C\u4F60\u662F\u4E0D\u662F\u5931\u671B\u4E86\uFF1F\u4EE5\u4E3A\u9E21\u5DF4\u8FD9\u4E48\u5FEB\u6345\u5230\u4F60\u7684\u9A9A\u6D1E\u91CC\uFF1F\u5F53\u7136\u4F1A\uFF0C\u4E0D\u7740\u6025\uFF0C\u65F6\u95F4\u8FD8\u957F\u5F97\u5F88\u54EA\uFF0C\u8001\u5B50\u82B1\u4E86\u8FD9\u4E48\u5927\u5DE5\u592B\uFF0C\u4ED8\u51FA\u90A3\u4E48\u5927\u4EE3\u4EF7\uFF0C\u5F53\u7136\u8981\u503C\u56DE\u7968\u4EF7\u3002\u660E\u4EBA\u4E0D\u505A\u6697\u4E8B\uFF0C\u5BF9\u4F60\u662F\u8FD9\u4E48\u6253\u7B97\u7684\uFF0C\u5148\u5F04\u51FA\u4E00\u4E2A\u5A03\u5A03\u6765\uFF0C\u7537\u5A03\u4E0D\u8981\uFF0C\u5973\u5A03\u7559\u7740\uFF0C\u597D\u597D\u517B\u5927\uFF0C\u4E0D\u4EC5\u662F\u4F60\uFF0C\u5305\u62EC\u4F60\u7684\u540E\u4EE3\uFF0C\u4E16\u4E16\u4EE3\u4EE3\u90FD\u8981\u505A\u6211\u767D\u5BB6\u7684\u6027\u5974\u3002\u300D</p><p>\u300C\u2026\u2026\u2026\u2026\u300D</p><p>\u300C\u522B\u6025\uFF0C\u6CA1\u5B8C\u5462\uFF0C\u6211\u7231\u60DC\u4F60\u6709\u4E00\u8EAB\u597D\u76AE\u80A4\uFF0C\u83AB\u6D6A\u8D39\u4E86\uFF0C\u5728\u4F60\u80CC\u4E0A\u523A\u4E00\u5E45\u771F\u6B63\u7684\u753B\u513F\uFF0C\u753B\u4E48\u5B50\u4EE5\u540E\u4F60\u5C31\u6653\u5F97\u4E86\uFF0C\u5982\u679C\u6293\u5F97\u7D27\uFF0C\u8FD9\u4E24\u4EF6\u4E8B\u8FD8\u53EF\u4EE5\u540C\u65F6\u5B8C\u6210\u5462\u3002\u300D</p><p>\u4ED6\u62CD\u62CD\u624B\uFF0C\u4ECE\u95E8\u5916\u8FDB\u6765\u4E00\u4E2A\u5E72\u762A\u5C16\u7626\u7684\u7CDF\u8001\u5934\u5B50\uFF0C\u63D0\u7740\u4E00\u4E2A\u5C0F\u5DE5\u5177\u7BB1\u3002</p><p>\u767D\u5929\u5FB7\u770B\u4ED6\u98A4\u5371\u5371\u7684\u6A21\u6837\uFF0C\u76B1\u4E86\u4E0B\u7709\uFF0C\u300C\u6BB7\u516C\u516C\uFF0C\u4F60\u8FD8\u62FF\u5F97\u7A33\u7B14\u5417\uFF1F\u300D</p><p>\u8001\u592A\u76D1\u767D\u7709\u52A8\u4E86\u52A8\uFF0C\u8BF4\u8BDD\u95F4\u7FFB\u51FA\u4E00\u53E3\u9C9C\u7EA2\u7684\u7259\u5E8A\uFF0C\u300C\u767D\u7237\uFF0C\u54B1\u5BB6\u5C31\u662F\u5E72\u8FD9\u6D3B\u513F\u7684\uFF0C\u6CA1\u6709\u4E09\u4E24\u4E09\uFF0C\u8FD8\u6562\u4E0A\u6881\u5C71\u5417\uFF1F\u300D</p><p>\u300C\u90A3\u662F\uFF0C\u5C31\u52B3\u70E6\u516C\u516C\u52A8\u624B\u5427\u3002\u300D</p><p>\u300C\u522B\u6025\uFF0C\u54B1\u5BB6\u5148\u770B\u770B\u8FD9\u76AE\u5B50\u3002\u300D</p><p>\u8001\u592A\u76D1\u67AF\u7626\u7684\u624B\u6307\u5728\u6D77\u68E0\u5149\u6ED1\u7684\u80CC\u808C\u4E0A\u5212\u8FC7\uFF0C\u6307\u5C16\u9677\u5165\u808C\u8089\u534A\u5206\uFF0C\u987A\u7740\u66F2\u7EBF\u5212\u5230\u81C0\u90E8\u4E0A\uFF0C\u53E3\u4E2D\u8D5E\u53F9\u4E0D\u5DF2\uFF0C\u300C\u771F\u662F\u5929\u5DE5\u9020\u7269\uFF0C\u8FD9\u5F20\u76AE\u5B50\u7EB9\u7406\u7EC6\u817B\uFF0C\u7D27\u5B9E\u6709\u529B\uFF0C\u5BCC\u6709\u5F39\u6027\uFF0C\u5F88\u4E45\u6CA1\u6709\u9047\u5230\u8FD9\u4E0A\u7B49\u7684\u6750\u6599\u4E86\uFF0C\u4E0A\u4E00\u6B21\u8BB0\u5F97\u8FD8\u662F\u5149\u7EEA\u5E74\u95F4\uFF0C\u73CD\u5983\u5A18\u5A18\u90A3\u5F20\u76AE\u2026\u2026\u300D\u663E\u7136\u662F\u5931\u4E86\u8A00\uFF0C\u4FBF\u7A81\u7136\u4F4F\u53E3\u3002</p><p>\u767D\u5929\u5FB7\u563B\u563B\u7B11\uFF0C\u300C\u5927\u6E05\u671D\u706D\u4E86\u90FD\u8FD9\u4E48\u591A\u5566\uFF0C\u6709\u4EC0\u4E48\u5FCC\u8BB3\u7684\uFF0C\u6709\u7A7A\u8BF4\u8BF4\u73CD\u5983\u7684\u4E8B\u554A\u3002\u300D</p><p>\u8001\u592A\u76D1\u4E0D\u518D\u7406\u4ED6\uFF0C\u6253\u5F00\u5DE5\u5177\u7BB1\uFF0C\u5168\u662F\u523B\u5200\u3001\u91D1\u526A\u3001\u94F6\u9488\u3001\u989C\u7C89\u4E00\u6574\u5957\u9F50\u5168\u7684\u7EB9\u8EAB\u5668\u5177\uFF0C\u6446\u653E\u5F97\u9F50\u6574\u3002\u6BDB\u7B14\u5316\u5F00\uFF0C\u70B9\u4E0A\u4E00\u70B9\u67D3\u6599\uFF0C\u6EF4\u5230\u808C\u80A4\u4E0A\u3002</p><p>\u6E05\u51C9\u7684\u6C34\u6E0D\u70B9\u5728\u8EAB\u4F53\u4E0A\uFF0C\u51B0\u5F97\u6D77\u68E0\u6D51\u8EAB\u8D77\u7599\u7629\uFF0C\u5979\u60F3\u626D\u52A8\u8EAB\u4F53\uFF0C\u53EF\u60DC\u675F\u7F1A\u5979\u7684\u5E76\u975E\u666E\u901A\u7684\u9EBB\u7EF3\uFF0C\u800C\u662F\u94C1\u94FE\uFF0C\u5B8C\u5168\u662F\u5F92\u52B3\u65E0\u76CA\u7684\u6323\u624E\u3002</p><p>\u8FD9\u8FB9\u6B63\u5FD9\u4E4E\u7740\uFF0C\u767D\u5929\u5FB7\u4E00\u65C1\u770B\u4E86\u4F1A\uFF0C\u6709\u70B9\u8010\u4E0D\u4F4F\u4E86\uFF0C\u8D70\u5230\u6D77\u68E0\u7684\u8EAB\u540E\uFF0C\u4E00\u633A\u8170\uFF0C\u5C06\u7C97\u58EE\u7684\u8089\u68D2\u6345\u8FDB\u5973\u4EBA\u5DF2\u7ECF\u6709\u4E00\u70B9\u6E7F\u6DA6\u7684\u9634\u95E8\u4E2D\uFF0C\u62BD\u63D2\u8D77\u6765\uFF0C\u53E3\u91CC\u5374\u9053\uFF0C\u300C\u4F60\u5FD9\u4F60\u7684\uFF0C\u6211\u5FD9\u6211\u7684\uFF0C\u4E24\u4E0D\u788D\u4E8B\u5427\u3002\u300D</p><p>\u8001\u592A\u76D1\u4E07\u822C\u4E0D\u613F\uFF0C\u4E5F\u4E0D\u597D\u51B2\u649E\u4E86\u96C7\u4E3B\uFF0C\u51B7\u51B7\u8BF4\u9996\uFF0C\u300C\u767D\u7237\u8981\u5C3D\u5174\uFF0C\u54B1\u5BB6\u4E5F\u4E0D\u597D\u591A\u8BF4\uFF0C\u53EA\u4E0D\u8981\u5F04\u51FA\u52A8\u9759\u592A\u5927\uFF0C\u8BA9\u9488\u5934\u504F\u4E86\u4F4D\u7F6E\u3002\u300D</p><p>\u767D\u5929\u5FB7\u5C06\u6D77\u68E0\u7684\u5C41\u80A1\u649E\u5F97\u556A\u556A\u4F5C\u54CD\uFF0C\u8001\u592A\u76D1\u5168\u5F53\u6CA1\u6709\u611F\u89C9\uFF0C\u5FC3\u65E0\u65C1\u9E5C\u5730\u63CF\u51FA\u4E86\u5927\u81F4\u8F6E\u5ED3\uFF0C\u767D\u5929\u5FB7\u8FB9\u5E72\u8FB9\u6307\u51FA\u4FEE\u6539\u4E4B\u5904\u3002\u8001\u592A\u76D1\u7787\u7F1D\u7740\u773C\uFF0C\u5DE6\u7784\u53C8\u770B\uFF0C\u53CD\u590D\u589E\u5220\uFF0C\u76F4\u5230\u5929\u9ED1\u65F6\u5206\u65B9\u51FA\u6765\u4E00\u4E2A\u5E95\u5B50\u3002</p><p>\u6B21\u65E5\u7EE7\u7EED\uFF0C\u59FF\u6001\u4F9D\u65E7\u3002\u7B2C\u4E00\u9488\u624E\u8FDB\u5979\u540E\u9888\u7684\u808C\u80A4\uFF0C\u8FF8\u51FA\u7C73\u7C92\u5927\u7684\u8840\u73E0\u3002\u8001\u592A\u76D1\u62FF\u5E72\u68C9\u5438\u6389\u3002</p><p>\u4E00\u9488\uFF0C\u63A5\u7740\u4E00\u9488\uFF0C\u70B9\u523A\uFF0C\u67D3\u6599\u968F\u7740\u70B9\u523A\u7EE3\u5165\u808C\u7406\u4E4B\u4E2D\u3002</p><p>\u6BCF\u523A\u4E00\u9488\uFF0C\u6D77\u68E0\u7684\u8EAB\u5B50\u5C31\u8981\u75DB\u5F97\u5FAE\u98A4\u4E00\u4E0B\uFF0C\u5979\u54AC\u7259\u5FCD\u7740\uFF0C\u5C31\u662F\u4E0D\u80AF\u547B\u541F\u51FA\u58F0\u6765\u3002</p><p>\u8001\u592A\u76D1\u7684\u624B\u6CD5\u975E\u5E38\u5A34\u719F\uFF0C\u523A\u5F97\u5E76\u4E0D\u91CD\uFF0C\u4F46\u53C8\u5BC6\u53C8\u5B9E\uFF0C\u4E0D\u662F\u5267\u70C8\u7684\u75BC\u75DB\uFF0C\u4F46\u50CF\u88AB\u5C71\u4E2D\u7AF9\u53F6\u9752\u54CD\u4E86\u4E00\u53E3\uFF0C\u6BD2\u6DB2\u4E00\u70B9\u4E00\u70B9\u5730\u6E17\u5165\u5979\u7684\u4F53\u5185\uFF0C\u6269\u6563\u5F00\u6765\u3002</p><p>\u8FD9\u79CD\u7EF5\u957F\u7684\u75DB\u82E6\u662F\u6700\u96BE\u4EE5\u5FCD\u53D7\u7684\uFF0C\u6FD2\u4E34\u5D29\u6E83\u7684\u65F6\u5019\uFF0C\u5979\u7981\u4E0D\u4F4F\u6000\u5FF5\u90A3\u79CD\u66FE\u7ECF\u8BA9\u5979\u6B7B\u53BB\u6D3B\u6765\u7684\u4E1C\u897F\uFF0C\u81F3\u5C11\uFF0C\u53EF\u4EE5\u8BA9\u5979\u6682\u65F6\u9003\u907F\u773C\u524D\u7684\u78E8\u96BE\u3002</p><p>\u6CA1\u6709\uFF0C\u767D\u5929\u5FB7\u6839\u672C\u6CA1\u6709\u6253\u7B97\u51CF\u8F7B\u54EA\u6015\u4EFB\u4F55\u4E00\u70B9\u513F\u6298\u78E8\uFF0C\u76F8\u53CD\uFF0C\u8FD8\u5728\u60F3\u6CD5\u8BBE\u6CD5\u589E\u52A0\u3002</p><p>\u4ED6\u8FD9\u4E00\u6BB5\u65F6\u671F\u6BD4\u8F83\u5FD9\u788C\uFF0C\u5728\u5916\u9762\u7684\u65F6\u95F4\u591A\uFF0C\u6709\u7A7A\u5C31\u60E6\u8BB0\u7740\u5230\u5DE5\u623F\u6765\u770B\u770B\uFF0C\u770B\u8FDB\u5EA6\uFF0C\u4E5F\u987A\u4FBF\u73A9\u5F04\u4E00\u4E0B\u5973\u4EBA\u7684\u8138\u86CB\u548C\u5976\u5B50\uFF0C\u5076\u5C14\u5728\u5979\u7684\u5C41\u773C\u91CC\u5E72\u4E0A\u4E00\u628A\uFF0C\u5374\u575A\u6301\u7740\u4E0D\u5C04\u7CBE\u51FA\u6765\u3002</p><p>\u540E\u6765\u53C8\u6709\u65B0\u82B1\u6837\uFF0C\u5C06\u6536\u96C6\u6765\u7684\u4E0D\u540C\u79CD\u7C7B\u6DEB\u836F\u6D82\u62B9\u5728\u5979\u7684\u4E0B\u9634\u8BD5\u6548\u679C\uFF0C\u4EE4\u6D77\u68E0\u6574\u65E5\u6574\u591C\u5730\u5904\u5728\u6027\u4EA2\u594B\u72B6\u6001\u53C8\u65E0\u5904\u6E32\u6CFB\uFF0C\u5408\u62E2\u53CC\u817F\u81EA\u5DF1\u78E8\u64E6\u4E00\u4E0B\u90FD\u4E0D\u53EF\u80FD\uFF0C\u4E0B\u8EAB\u80BF\u80C0\u4E0D\u582A\uFF0C\u9EBB\u75D2\u4E4B\u6781\uFF0C\u6709\u65F6\u5B9E\u5728\u53D7\u4E0D\u4F4F\u4E86\uFF0C\u610F\u8BC6\u6A21\u7CCA\uFF0C\u53E3\u89D2\u6D41\u6D8E\uFF0C\u53D1\u51FA\u8377\u8377\u7684\u58F0\u97F3\u3002</p><p>\u8001\u592A\u76D1\u770B\u5979\u5B9E\u5728\u53EF\u601C\uFF0C\u8EAB\u4F53\u52A8\u6765\u52A8\u53BB\u4E5F\u4E0D\u597D\u4E0B\u9488\uFF0C\u597D\u5728\u5E74\u8F7B\u65F6\u4E5F\u966A\u5BAB\u5973\u73A9\u8FC7\u5047\u51E4\u865A\u51F0\u7684\u6E38\u620F\uFF0C\u6709\u65F6\u5C31\u575A\u51FA\u4E24\u6839\u6307\u5934\uFF0C\u63D2\u8FDB\u5973\u9634\u4E2D\u6316\u51E0\u4E0B\uFF0C\u8FD9\u65F6\uFF0C\u6D77\u68E0\u7684\u5C41\u80A1\u4F1A\u8F7B\u8F7B\u6446\u52A8\uFF0C\u5AE9\u8089\u5C06\u67AF\u5E72\u7684\u624B\u6307\u54AC\u5F97\u7D27\u7D27\u7684\uFF0C\u5F88\u5FEB\u5C31\u6CC4\u51FA\u4E00\u5927\u6EE9\u6DEB\u6C41\u3002</p><p>\u4ECE\u65E9\u4E0A\u5230\u4E0B\u5348\uFF0C\u4E00\u65E5\u4E4B\u5185\u8DB3\u6709\u5927\u534A\u7684\u65F6\u95F4\u8981\u82B1\u5728\u523A\u9752\u4E0A\uFF0C\u4E4B\u540E\u5C31\u662F\u4E00\u9879\u5FC5\u505A\u7684\u529F\u8BFE\uFF0C\u4E3A\u4E86\u4E0D\u635F\u4F24\u80CC\u90E8\u7684\u5DE5\u827A\uFF0C\u4F1A\u628A\u6D77\u68E0\u56DB\u80A2\u60AC\u7A7A\u540A\u5728\u6760\u4E0A\uFF0C\u4E24\u817F\u6253\u5F00\u5BF9\u6298\u4E0E\u624B\u81C2\u6346\u5728\u4E00\u8D77\uFF0C\u770B\u4E0A\u53BB\u50CF\u5728\u65A9\u6740\u4E00\u5934\u767D\u51C0\u7684\u732A\u3002\u767D\u5929\u5FB7\u6B64\u65F6\u624D\u4F1A\u5C06\u9633\u5177\u6DF1\u6DF1\u5730\u63D2\u5165\u76F4\u8FBE\u82B1\u5FC3\uFF0C\u6025\u4FC3\u5730\u62BD\u52A8\u4E4B\u540E\uFF0C\u517B\u4E86\u4E00\u5929\u7684\u6D53\u7CBE\u4FBF\u4F1A\u503E\u6CFB\u800C\u51FA\uFF0C\u70ED\u70EB\u70EB\u5730\u6253\u5230\u5B50\u5BAB\u9888\u53E3\u5B50\u4E2D\u592E\u3002</p><p>\u6D77\u68E0\u54C6\u55E6\u4E86\u4E00\u4E0B\uFF0C\u5FC3\u4E2D\u60B2\u82E6\uFF0C\u5979\u660E\u767D\uFF0C\u8FD9\u4E00\u523B\uFF0C\u5979\u662F\u767D\u5929\u5FB7\u7684\u64AD\u79CD\u5DE5\u5177\u3002</p><p>\u4ECE\u88AB\u4FD8\u83B7\u7684\u90A3\u4E00\u523B\u8D77\uFF0C\u5979\u5C31\u9009\u62E9\u4E86\u6C89\u9ED8\uFF0C\u518D\u75DB\u82E6\u518D\u6124\u6012\u4E5F\u4E0D\u8BF4\u8BDD\uFF0C\u53EA\u6709\u5728\u5FCD\u53D7\u4E0D\u4E86\u7684\u60C5\u51B5\u4E0B\u624D\u53D1\u51FA\u51E0\u58F0\u547B\u541F\u548C\u5C16\u53EB\uFF0C\u51B3\u4E0D\u4F1A\u5C48\u670D\uFF0C\u53EA\u8981\u6709\u4E00\u4E1D\u6E05\u9192\uFF0C\u90FD\u4F1A\u6297\u4E89\u5230\u5E95\uFF0C\u773C\u4E2D\u55B7\u5C04\u51FA\u7684\u53EA\u6709\u4EC7\u6068\u7684\u5149\u8292\uFF0C\u7740\u5B9E\u8BA9\u767D\u5929\u5FB7\u4E3A\u4E86\u9632\u8303\u5979\u8017\u8D39\u4E86\u66F4\u591A\u7684\u65F6\u95F4\u7CBE\u529B\u3002</p><p>\u4F46\u662F\uFF0C\u5979\u7684\u547D\u8FD0\u7EC8\u7A76\u638C\u63A7\u5728\u522B\u4EBA\u624B\u4E2D\uFF0C\u60F3\u7EDD\u98DF\uFF0C\u4F1A\u6709\u597D\u51E0\u4E2A\u8EAB\u5F3A\u529B\u58EE\u7684\u5BB6\u4F19\u6309\u7740\u5979\uFF0C\u634F\u4F4F\u5979\u7684\u9F3B\u5B50\u5C06\u98DF\u6C34\u704C\u8FDB\u53BB\uFF1B\u60F3\u9003\u8DD1\uFF0C\u624B\u811A\u76F8\u8FDE\u7684\u94C1\u9563\u624B\u94D0\u4ECE\u4E0D\u79BB\u8EAB\uFF0C\u5979\u60F3\u81EA\u6740\uFF0C\u53E3\u91CC\u603B\u662F\u585E\u7740\u5E03\u6761\u6216\u8F6F\u7403\uFF0C\u8BA9\u5979\u6B32\u632F\u4E4F\u529B\uFF0C\u52AA\u529B\u90FD\u7EC8\u7A76\u4ED8\u4E4B\u6D41\u6C34\u3002</p><p>\u996D\u540E\uFF0C\u767D\u5929\u5FB7\u90FD\u4F1A\u5E26\u5979\u51FA\u53BB\u6563\u6B65\u4E00\u4F1A\u513F\uFF0C\u4E00\u5219\u662F\u4E3A\u4E86\u70AB\u8000\uFF0C\u4E8C\u5219\u4E5F\u662F\u8FD0\u52A8\u8FD0\u52A8\uFF0C\u4E0D\u8BA9\u5979\u5728\u623F\u91CC\u8EBA\u574F\u4E86\uFF0C\u4FDD\u6301\u4F53\u5F62\u548C\u5065\u5EB7\u3002\u8BF4\u662F\u6563\u6B65\uFF0C\u5B9E\u5219\u5982\u540C\u5BCC\u4EBA\u6E9C\u72D7\uFF0C\u4E00\u6761\u957F\u94C1\u94FE\u6263\u4F4F\u5979\u7684\u624B\u94D0\uFF0C\u4E00\u5934\u56FA\u5B9A\u62F4\u5728\u4E00\u5339\u9AD8\u5934\u5927\u9A6C\u7684\u9A6C\u978D\u4E0A\uFF0C\u8FD8\u6709\u4E00\u6761\u7EC6\u94F6\u94FE\u7CFB\u4F4F\u5979\u7684\u9F3B\u73AF\uFF0C\u7531\u9A91\u5728\u9A6C\u4E0A\u7684\u767D\u5929\u5FB7\u624B\u6307\u5934\u7F20\u7740\u3002</p><p>\u9A6C\u6162\u6162\u8D70\uFF0C\u5979\u5374\u5FC5\u987B\u7D27\u6B65\u8D76\uFF0C\u56E0\u4E3A\u811A\u9563\u9650\u5236\u4E86\u4E24\u811A\u8FC8\u6B65\u7684\u957F\u5EA6\uFF0C\u4E0D\u5C0F\u8DD1\u5C31\u4F1A\u8DCC\u5012\uFF0C\u8BA9\u9A6C\u513F\u5728\u5730\u4E0A\u6A2A\u62D6\u3002</p><p>\u6BCF\u65E5\u91CC\uFF0C\u767D\u5929\u5FB7\u7275\u7740\u8D64\u8EAB\u88F8\u4F53\u7684\u6D77\u68E0\u5728\u6751\u91CC\u6E9C\u51E0\u5708\uFF0C\u7ECF\u8FC7\u6709\u4EBA\u7684\u5730\u65B9\u65F6\uFF0C\u4EBA\u4EEC\u90FD\u4F1A\u505C\u4E0B\u624B\u4E2D\u7684\u6D3B\u8BA1\uFF0C\u6CE8\u76EE\u6813\u5728\u9A6C\u540E\u72D7\u4E00\u822C\u7684\u6F02\u4EAE\u5973\u4EBA\u3002</p><p>\u300C\u5927\u5BB6\u77E5\u9053\u5979\u662F\u8C01\u5417\uFF1F\u8457\u540D\u7684\u9ED1\u51E4\u51F0\u5440\u2026\u2026\u8FD8\u8BB0\u5F97\u4E24\u5E74\u524D\uFF0C\u8001\u5B50\u5C31\u5728\u8FD9\u91CC\u8BB2\u8FC7\uFF0C\u8981\u9ED1\u51E4\u51F0\u6D17\u5E72\u51C0\u5C41\u80A1\u7B49\u7740\uFF0C\u8001\u5B50\u4E0D\u98DF\u8A00\uFF0C\u628A\u6D17\u5E72\u51C0\u5C41\u80A1\u7684\u5973\u571F\u532A\u5E26\u7ED9\u5927\u4F19\u513F\u770B\u54EA\uFF01\u300D\u767D\u5929\u5FB7\u5F97\u610F\u5730\u626C\u8D77\u9A6C\u97AD\uFF0C\u5927\u58F0\u5406\u559D\u9053\u3002</p><p>\u4EBA\u7FA4\u8F70\u5730\u4E00\u58F0\uFF0C\u5979\u5C31\u662F\u9ED1\u51E4\u51F0\u554A\uFF0C\u90A3\u76EE\u5149\u987F\u65F6\u53D8\u5F97\u590D\u6742\u8D77\u6765\u3002</p><p>\u6D77\u68E0\u4F4E\u7740\u5934\uFF0C\u5931\u53BB\u8840\u8272\u7684\u8138\u4E0A\u5446\u6EDE\u7740\u6CA1\u6709\u4EFB\u4F55\u53CD\u5E94\u3002\u9F3B\u73AF\u84E6\u7136\u626F\u7D27\uFF0C\u6FC0\u75DB\u4E4B\u4E0B\uFF0C\u5979\u88AB\u8FEB\u4EF0\u8D77\u5934\u6765\uFF0C\u8FCE\u9762\u671D\u5411\u56F4\u89C2\u7684\u4EBA\u7FA4\u3002</p><p>\u8FD9\u4E2A\u5F80\u65E5\u4E16\u5916\u6843\u6E90\u7684\u4E00\u822C\u7684\u6751\u5B50\uFF0C\u4E5F\u5728\u53D1\u751F\u7740\u6539\u53D8\uFF0C\u81EA\u4ECE\u88AB\u767D\u5929\u5FB7\u4E00\u4F19\u4EBA\u5360\u636E\u5E76\u501F\u6D77\u68E0\u4E4B\u624B\u6740\u6389\u4E86\u53CD\u6297\u7684\u51E0\u4E2A\u653E\u6392\u6C49\u540E\uFF0C\u5168\u6751\u767E\u59D3\u5168\u6CA6\u4E3A\u4E86\u4EBA\u8D28\uFF0C\u5728\u66B4\u529B\u4E0B\u52B3\u52A8\uFF0C\u4E00\u680B\u680B\u65E7\u623F\u5B50\u63A8\u5012\u4E86\uFF0C\u65B0\u7684\u5927\u578B\u57CE\u5BE8\u62D4\u8D77\u800C\u8D77\uFF0C\u9664\u4E00\u5C0F\u90E8\u5206\u519C\u7530\u79CD\u690D\u7CAE\u98DF\u548C\u852C\u83DC\u5916\uFF0C\u5927\u90E8\u5206\u91CD\u65B0\u7FFB\u8015\uFF0C\u57CB\u4E0B\u4E86\u5927\u7247\u4ECE\u672A\u89C1\u8FC7\u3001\u50CF\u9EA6\u7C92\u822C\u7684\u79CD\u5B50\u3002</p><p>\u7B2C\u4E8C\u4E2A\u6708\u5F00\u59CB\uFF0C\u6D77\u68E0\u7684\u7678\u6C34\u6CA1\u6709\u5982\u671F\u800C\u81F3\u3002</p><p>\u80CC\u4E0A\u7684\u523A\u9752\u4E5F\u5728\u8FDB\u5C55\u4E4B\u4E2D\uFF0C\u4E0D\u5C11\u4EBA\u7684\u6CE8\u610F\u529B\u9010\u6E10\u4ECE\u5979\u7684\u4E0B\u8EAB\u8F6C\u79FB\u5230\u540E\u80CC\u4E0A\uFF0C\u6307\u6307\u70B9\u70B9\uFF0C\u5567\u5567\u8D5E\u53F9\u3002</p><p>\u7B2C\u4E94\u4E2A\u6708\uFF0C\u5C0F\u8179\u5DF2\u7ECF\u73B0\u6000\uFF0C\u6BCF\u9686\u8D77\u4E00\u5206\uFF0C\u6D77\u68E0\u773C\u4E2D\u7684\u7EDD\u671B\u4FBF\u6DF1\u4E86\u4E00\u5206\u3002</p><p>\u5979\u6362\u4E86\u4E00\u5F20\u65B0\u7684\u5DE5\u5E8A\uFF0C\u6309\u7167\u5979\u8179\u90E8\u7684\u4F4D\u7F6E\u6316\u7A7A\u4E86\u4E00\u4E2A\u6D1E\uFF0C\u4EE5\u514D\u4FEF\u8EAB\u65F6\u809A\u5B50\u53D7\u5230\u538B\u8FEB\u3002</p><p>\u516B\u4E2A\u6708\u540E\uFF0C\u6D77\u68E0\u5C0F\u4EA7\uFF0C\u8BDE\u4E0B\u4E00\u4E2A\u7537\u5A74\u3002</p><p>\u767D\u5929\u5FB7\u5C06\u4EA7\u540E\u865A\u5F31\u7684\u5979\u63A8\u5230\u6652\u8C37\u576A\u4E2D\uFF0C\u53EC\u96C6\u5168\u6751\u767E\u59D3\uFF0C\u4EE4\u4EBA\u5F53\u4F17\u5C06\u521A\u526A\u8110\u5E26\u7684\u5A74\u513F\u6D3B\u6D3B\u6390\u6B7B\uFF0C\u53EF\u601C\u90A3\u51A4\u9B42\u6765\u5230\u4EBA\u4E16\uFF0C\u8FDE\u53E3\u5976\u90FD\u6CA1\u559D\u4E0A\u3002</p><p>\u6240\u6709\u4EBA\u88AB\u8FD9\u7A7A\u524D\u7684\u6B8B\u66B4\u60CA\u5446\u4E86\uFF0C\u56DB\u4E0B\u4E00\u7247\u6B7B\u5BC2\u3002</p><p>\u6D77\u68E0\u4EE5\u4E3A\u81EA\u5DF1\u4F1A\u5D29\u6E83\uFF0C\u4E5F\u4E0D\u77E5\u5E78\u8FD8\u662F\u4E0D\u5E78\uFF0C\u5979\u633A\u4E86\u8FC7\u6765\uFF0C\u53EA\u89C9\u5F97\u90A3\u4E00\u77AC\u95F4\uFF0C\u610F\u8BC6\u51FA\u79BB\u4E8E\u8EAB\u4F53\u4E86\uFF0C\u6D6E\u5728\u7A7A\u4E2D\u98D8\u6765\u8361\u53BB\u3002</p><p>\u7537\u4EBA\u72E0\u72E0\u5730\u6482\u4E0B\u4E00\u53E5\u8BDD\uFF0C\u300C\u4ECE\u6765\u518D\u6765\u8FC7\u3002\u300D</p><p>\u5F53\u6D77\u68E0\u8179\u4E2D\u7684\u7B2C\u4E8C\u4E2A\u5B69\u5B50\u73B0\u6000\u7684\u65F6\u5019\uFF0C\u5927\u5730\u4E0A\u5F00\u6EE1\u4E86\u7EA2\u8273\u8273\u7684\u5C0F\u82B1\uFF0C\u6F2B\u5929\u6F2B\u5730\uFF0C\u7F8E\u5F97\u5996\u8273\uFF0C\u8D64\u88F8\uFF0C\u4EE4\u4EBA\u7A92\u606F\uFF0C\u6563\u53D1\u51FA\u4EE4\u4EBA\u8FF7\u9189\u7684\u6E05\u9999\u3002</p><p>\u6F2B\u957F\u7684\u523A\u9752\u5DE5\u827A\u4E5F\u7EC8\u4E8E\u7AE3\u5DE5\u3002</p><p>\u767D\u5929\u5FB7\u7B2C\u4E00\u6B21\u5728\u623F\u4E2D\u653E\u7F6E\u4E86\u4E24\u9762\u5927\u7ACB\u955C\uFF0C\u6D77\u68E0\u7684\u8EAB\u524D\u8EAB\u540E\u5404\u4E00\u9762\uFF0C\u9053\uFF0C\u300C\u4F60\u81EA\u5DF1\u4E5F\u6B23\u8D4F\u6B23\u8D4F\u3002\u300D</p><p>\u955C\u4E2D\uFF0C\u5E73\u6ED1\u5149\u6D01\u7684\u7389\u80CC\u6D88\u5931\u4E86\uFF0C\u5448\u73B0\u5728\u773C\u524D\u7684\uFF0C\u662F\u5927\u5E45\u5982\u6B64\u8BE1\u5F02\u800C\u9707\u64BC\u7684\u753B\u9762\u3002</p><p>\u4E00\u6761\u9AD8\u5927\u51F6\u731B\u7684\u72FC\u72D7\u50B2\u7136\u5C79\u7ACB\u5728\u53F3\u4E0A\u65B9\uFF0C\u4EF0\u5934\u957F\u5578\uFF0C\u6839\u6839\u9752\u6BDB\u7AD6\u8D77\uFF0C\u4F3C\u4E4E\u5728\u5E86\u8D3A\u5F81\u670D\u7684\u80DC\u5229\u3002\u5B83\u7684\u5F81\u670D\u8005\uFF0C\u662F\u4E00\u5934\u5F02\u5E38\u7F8E\u4E3D\u7684\u9ED1\u8272\u51E4\u51F0\uFF0C\u7FFB\u8FC7\u8EAB\u5B50\u659C\u8EBA\u5728\u753B\u9762\u7684\u5DE6\u4E0B\u65B9\uFF0C\u7FBD\u6BDB\u4F9D\u7136\u5149\u9C9C\u4EAE\u6D01\uFF0C\u4F46\u9A84\u50B2\u7684\u51E4\u5934\u5DF2\u5C48\u8FB1\u5730\u6B6A\u5411\u4E00\u8FB9\uFF0C\u773C\u795E\u4E2D\u900F\u51FA\u65E0\u5C3D\u7684\u6050\u60E7\u548C\u54C0\u6028\u3002</p><p>\u51FA\u5F69\u7684\u662F\uFF0C\u9ED1\u51E4\u51F0\u7FFB\u8F6C\u8FC7\u6765\u7684\u809A\u76AE\u4E0A\uFF0C\u7531\u7EC6\u7FBD\u548C\u9634\u5F71\u6784\u6210\u4E86\u5973\u4EBA\u7684\u80F8\u4E73\u7684\u5F62\u72B6\uFF0C\u8FD8\u9690\u7EA6\u53EF\u89C1\u7C89\u7EA2\u7684\u5976\u5934\uFF0C\u4E0E\u6574\u4E2A\u753B\u9762\u5E76\u4E0D\u7A81\u5140\uFF0C\u6D51\u7136\u5929\u6210\uFF0C\u72FC\u72D7\u7684\u4E24\u53EA\u524D\u80A2\u5C31\u6DF1\u6DF1\u9677\u5165\u5728\u8FD9\u9971\u6EE1\u7684\u4E73\u623F\u91CC\u3002\u800C\u51E4\u51F0\u7684\u4E0B\u90E8\u5E95\u7AEF\u6070\u597D\u6536\u5728\u6D77\u68E0\u7684\u809B\u95E8\u5904\uFF0C\u8089\u68D2\u4E00\u6345\u5165\u5C41\u773C\uFF0C\u6574\u4E2A\u9ED1\u51E4\u51F0\u7684\u8EAB\u4F53\u90FD\u597D\u50CF\u5728\u98A4\u6296\uFF0C\u7ED9\u4EBA\u65E0\u9650\u9050\u60F3\u7684\u7A7A\u95F4\u3002</p><p>\u72FC\u72D7\u4E0E\u9ED1\u51E4\u51F0\u5468\u56F4\uFF0C\u70B9\u7F00\u7740\u65E0\u6570\u9C9C\u7EA2\u5982\u8840\u7684\u6D77\u68E0\u82B1\uFF0C\u5BC6\u5BC6\u531D\u531D\uFF0C\u94FA\u5929\u76D6\u5730\uFF0C\u5145\u76C8\u4E86\u6574\u4E2A\u7A7A\u95F4\u3002</p><p>\u753B\u9762\u592A\u8FC7\u5DE5\u5DE7\uFF0C\u6BDB\u53D1\u7EC6\u8282\u7686\u9C9C\u6D3B\uFF0C\u4EE5\u5DE5\u827A\u800C\u8A00\uFF0C\u771F\u5165\u4E86\u5316\u5883\uFF0C\u547C\u4E4B\u6B32\u51FA\uFF0C\u4EE5\u753B\u610F\u800C\u8A00\uFF0C\u5145\u6EE1\u4E86\u65E0\u7A77\u7684\u8BF1\u60D1\u548C\u6DEB\u79FD\u3002</p><p>\u6D77\u68E0\u770B\u7740\u770B\u7740\uFF0C\u5410\u51FA\u4E86\u4E00\u53E3\u9C9C\u8840\uFF0C\u6655\u6B7B\u8FC7\u53BB\u3002</p><p>\u4F9D\u7A00\u542C\u5230\u7537\u4EBA\u7684\u72C2\u7B11\uFF0C\u300C\u767D\u677F\u513F\uFF0C\u8BB0\u597D\u4E86\uFF0C\u8FD9\u5C31\u662F\u4F60\u7684\u5BBF\u547D\u5440\uFF01\u300D</p>',66);function i(t,n){return r}var _=a(e,[["render",i]]);export{_ as default};
