import{e as p}from"./app.630af5f2.js";import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";const e={},r=p('<h1 id="\u7B2C\u4E5D\u7AE0-\u8F83\u91CF" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E5D\u7AE0-\u8F83\u91CF" aria-hidden="true">#</a> \u7B2C\u4E5D\u7AE0 \u8F83\u91CF</h1><p>\u300C\u4E8C\u559C\u5B50\u524D\u6765\u62A5\u544A\uFF01\u300D</p><p>\u300C\u8FDB\u6765\u3002\u300D</p><p>\u4E8C\u559C\u5B50\u6EE1\u9762\u98CE\u5C18\uFF0C\u8377\u67AA\u5B9E\u5F39\u8D70\u8FDB\u95E8\u6765\uFF0C\u300C\u556A\u300D\u5730\u4E00\u4E2A\u7ACB\u6B63\u3002</p><p>\u767D\u5929\u5FB7\u6B63\u6402\u7740\u4E00\u4E2A\u7F8E\u8C8C\u5987\u4EBA\u8EBA\u5728\u77EE\u69BB\u4E0A\uFF0C\u5973\u4EBA\u51B7\u6DE1\u5730\u770B\u4E86\u4ED6\u4E00\u773C\uFF0C\u4E3E\u8D77\u4E00\u6746\u957F\u957F\u7684\u70DF\u67AA\u6B6A\u5230\u4E00\u8FB9\u541E\u4E91\u5410\u96FE\u53BB\u4E86\uFF0C\u6021\u7136\u81EA\u5F97\u3002</p><p>\u4E8C\u559C\u5B50\u81EA\u7136\u4E86\u89E3\u9762\u524D\u7684\u4E3D\u4EBA\u662F\u5EB7\u8001\u7237\u5B50\u7684\u4E03\u59E8\u592A\uFF0C\u6050\u6015\u5DF2\u662F\u516C\u5F00\u7684\u79D8\u5BC6\uFF0C\u53EF\u80FD\u5C31\u7792\u7740\u5EB7\u8001\u7237\u5B50\u4E00\u4E2A\u4EBA\u4E86\u3002</p><p>\u4E8C\u559C\u5B50\u62A5\u544A\uFF0C\u300C\u8D27\u5DF2\u5B89\u5168\u9001\u5230\uFF0C\u94B1\u5C06\u5728\u4E09\u65E5\u5185\u7531\u5BF9\u65B9\u8D1F\u8D23\u62BC\u8FD0\u8FC7\u6765\uFF0C\u8FD9\u662F\u51ED\u6761\u3002\u300D</p><p>\u767D\u5929\u5FB7\u968F\u4FBF\u770B\u4E86\u770B\uFF0C\u585E\u5230\u6000\u91CC\uFF0C\u70B9\u5934\u9053\uFF0C\u300C\u529E\u5F97\u597D\uFF0C\u60F3\u8981\u4EC0\u4E48\u8D4F\u8D50\u5440\uFF1F\u300D</p><p>\u4E8C\u559C\u5B50\u7ACB\u9A6C\u60F3\u8D77\u4E86\u6D77\u68E0\u4FEE\u957F\u8D64\u88F8\u7684\u8EAB\u5B50\u3002</p><p>\u767D\u5929\u5FB7\u770B\u51FA\u4E86\u4ED6\u7684\u5FC3\u601D\uFF0C\u9053\uFF0C\u300C\u5C0F\u5154\u513F\u5B50\uFF0C\u60F3\u5973\u4EBA\u5566\uFF1F\u300D</p><p>\u300C\u6807\u4E0B\u4E0D\u6562\u3002\u300D</p><p>\u300C\u653E\u5C41\uFF0C\u5728\u8001\u5B50\u9762\u524D\u8FD8\u8BB2\u4E0D\u5F97\u771F\u8BDD\u5417\uFF1F\u4F60\u628A\u4E8B\u513F\u529E\u6210\u4E86\uFF0C\u8001\u5B50\u4E0D\u4F1A\u4E8F\u5F85\u4F60\uFF0C\u4F60\u5230\u8D26\u623F\u9886\u5341\u4E2A\u5927\u6D0B\uFF0C\u518D\u5230\u540E\u53A2\u623F\u5019\u7740\u3002\u300D</p><p>\u4E8C\u559C\u5B50\u559C\u5F62\u4E8E\u8272\uFF0C\u5F2F\u8170\u97A0\u8EAC\uFF0C\u300C\u591A\u8C22\u56E2\u5EA7\u3002\u300D\u8F6C\u8EAB\u79BB\u53BB\u3002</p><p>\u4E03\u59E8\u592A\u61D2\u61D2\u5730\u8BF4\uFF0C\u300C\u8FD9\u79CD\u4EBA\u6E23\u4F60\u8FD8\u7559\u7740\u5E72\u561B\uFF1F\u300D</p><p>\u767D\u5929\u5FB7\u6402\u7740\u5979\uFF0C\u5728\u5979\u6ED1\u5AE9\u7684\u8138\u4E0A\u4EB2\u4E86\u4E00\u53E3\uFF0C\u563B\u7B11\u7740\u8BF4\u9053\uFF1A\u300C\u8001\u5B50\u81EA\u5DF1\u5C31\u662F\u4EBA\u6E23\uFF0C\u6015\u751A\u3002\u300D</p><p>\u300C\u4ED6\u8111\u540E\u6709\u53CD\u9AA8\uFF0C\u6562\u80CC\u53DB\u9ED1\u51E4\uFF0C\u96BE\u8BB2\u4ECA\u540E\u4E0D\u53DB\u4F60\u3002\u6211\u8FD8\u542C\u5230\u4E00\u4E2A\u4F20\u95FB\uFF0C\u8BF4\u4ED6\u8FD8\u5BF9\u5218\u592B\u4EBA\u65E0\u793C\u8FC7\uFF0C\u4F60\u6536\u7559\u4ED6\uFF0C\u5218\u53BF\u957F\u6015\u6709\u75A5\u8482\u3002\u300D</p><p>\u300C\u4F60\u8BB2\u7684\u6709\u7406\uFF0C\u4E0D\u8FC7\u8FD9\u5BB6\u4F19\u6709\u70B9\u672C\u4E8B\uFF0C\u8001\u5B50\u73B0\u5728\u8FD8\u5F97\u7528\u4ED6\u3002\u300D</p><p>\u8BF4\u7F62\u632F\u8863\u800C\u8D77\uFF0C\u9053\uFF0C\u300C\u4F60\u63D0\u8D77\u9ED1\u51E4\uFF0C\u8001\u5B50\u4ECA\u5929\u5B89\u6392\u4E86\u4E00\u573A\u597D\u620F\uFF0C\u6709\u6CA1\u6709\u5174\u8DA3\u770B\u3002\u300D</p><p>\u4E03\u59E8\u592A\u4E0D\u5C51\u9053\uFF0C\u300C\u8FD8\u4E0D\u53C8\u662F\u4EBA\u72D7\u5978\u7684\u628A\u620F\u3002\u300D</p><p>\u767D\u5929\u5FB7\u6B63\u8272\u9053\uFF0C\u300C\u6BD4\u90A3\u53EF\u7CBE\u5F69\u591A\u5566\u3002\u300D</p><p>\u4E03\u59E8\u592A\u8EAB\u5B50\u6B6A\u5411\u91CC\u8FB9\uFF0C\u300C\u4E0D\u53BB\u3002\u7537\u4EBA\u6CA1\u4E00\u4E2A\u597D\u4E1C\u897F\u3002\u300D</p><p>\u300C\u5C0F\u9A9A\u8D27\u3002\u300D\u767D\u5929\u5FB7\u5728\u5979\u80A5\u81C0\u4E0A\u8F7B\u51FB\u4E00\u638C\u3002</p><p>\u540E\u53A2\u623F\u4E2D\uFF0C\u4E8C\u559C\u5B50\u8E31\u6765\u8E31\u53BB\uFF0C\u5FC3\u91CC\u70E7\u8D77\u4E00\u56E2\u706B\u3002\u53EA\u6709\u4E00\u4E2A\u4EBA\u7684\u5F71\u5B50\u5728\u4ED6\u9762\u524D\u6643\u6765\u6643\u53BB\uFF1A\u6D77\u68E0\u6D77\u68E0\u6D77\u68E0\u2026\u2026</p><p>\u767D\u5929\u5FB7\u771F\u4F1A\u5927\u65B9\u5F97\u5C06\u6D77\u68E0\u9001\u7ED9\u4ED6\u54C1\u5C1D\uFF1F</p><p>\u4E8B\u5B9E\u4E0A\uFF0C\u5230\u76EE\u524D\u4E3A\u6B62\uFF0C\u767D\u5929\u5FB7\u867D\u7136\u5E76\u6CA1\u6D77\u68E0\u5F53\u6210\u4E86\u7981\u25A1\uFF0C\u4F46\u4E5F\u4E0D\u662F\u90A3\u4E48\u8F7B\u6613\u7684\uFF0C\u7279\u522B\u662F\u8FDB\u5165\u5BC6\u5BA4\u8C03\u6559\u4E4B\u540E\uFF0C\u65E0\u4EBA\u518D\u80FD\u67D3\u6307\u4E86\u3002</p><p>\u4E00\u4E2A\u602F\u751F\u751F\u7684\u58F0\u97F3\u5973\u58F0\u5728\u95E8\u5916\u8BF4\uFF0C\u300C\u5974\u5A62\u4F3A\u5019\u5927\u7237\u3002\u300D</p><p>\u58F0\u97F3\u4F3C\u662F\u5F88\u719F\u6089\uFF0C\u5374\u4E0D\u662F\u6D77\u68E0\u3002\u95E8\u5F00\u5904\uFF0C\u4E00\u4E2A\u8863\u7740\u5355\u8584\u7684\u5C11\u5973\u5782\u7740\u5934\u8D70\u4E86\u8FDB\u6765\uFF0C\u8DEA\u5230\u4E8C\u559C\u5B50\u8DDF\u524D\u3002</p><p>\u300C\u94F6\u53F6\uFF1F\u300D\u4E8C\u559C\u5B50\u60CA\u9053\u3002</p><p>\u5C11\u5973\u6D51\u8EAB\u8F7B\u9707\uFF0C\u5934\u4ECD\u6CA1\u6709\u62AC\uFF0C\u606D\u987A\u5730\u56DE\u7B54\uFF0C\u300C\u5974\u4E0D\u662F\u94F6\u53F6\uFF0C\u53EA\u662F\u8001\u7237\u7684\u4E00\u6761\u72D7\u3002\u300D</p><p>\u4E8C\u559C\u5B50\u6258\u7740\u5979\u7684\u4E0B\u5DF4\u628A\u5979\u7684\u5934\u62AC\u4E86\u8D77\u6765\uFF0C\u867D\u7136\u7EA2\u6DA6\u5C16\u4FCF\u7684\u8138\u4E0A\u5931\u53BB\u4E86\u8840\u8272\uFF0C\u7075\u52A8\u7684\u5927\u773C\u775B\u5931\u53BB\u4E86\u795E\u91C7\uFF0C\u795E\u60C5\u51B0\u51B7\uFF0C\u4F46\u5206\u660E\u5C31\u662F\u5931\u8E2A\u591A\u65F6\u7684\u94F6\u53F6\u3002</p><p>\u4E8C\u559C\u5B50\u5F20\u4E86\u5F20\u53E3\uFF0C\u8BF4\u4E0D\u51FA\u8BDD\u6765\u3002</p><p>\u4ED6\u53EF\u4EE5\u9ED1\u8D77\u5FC3\u80A0\u80CC\u53DB\u4EFB\u4F55\u4EBA\uFF0C\u5305\u62EC\u6D77\u68E0\uFF0C\u552F\u72EC\u5BF9\u94F6\u53F6\u5FC3\u4E2D\u8FD8\u6709\u6127\u759A\u3002</p><p>\u662F\u94F6\u53F6\u9ED8\u9ED8\u5355\u604B\u4ED6\uFF0C\u6BEB\u65E0\u4FDD\u7559\u5730\u732E\u51FA\u4E86\u4E00\u9897\u5C11\u5973\u7684\u75F4\u5FC3\uFF0C\u662F\u94F6\u53F6\u59CB\u7EC8\u5728\u5173\u5FC3\u4ED6\uFF0C\u7EF4\u62A4\u4ED6\uFF0C\u8BA9\u4ED6\u5728\u5C71\u4E0A\u5BC2\u5BDE\u7684\u65E5\u5B50\u91CC\u611F\u53D7\u5230\u5BB6\u7684\u6E29\u6696\uFF0C\u662F\u94F6\u53F6\u5728\u4ED6\u94F8\u4E0B\u5927\u9519\u9762\u4E34\u6740\u8EAB\u4E4B\u7978\u65F6\u633A\u8EAB\u800C\u51FA\u6551\u4E86\u4ED6\u3002</p><p>\u800C\u4ED6\u5BF9\u94F6\u53F6\u53C8\u505A\u4E86\u4EC0\u4E48\u5462\uFF1F\u8BA9\u5979\u8E0F\u8FDB\u9677\u9631\uFF0C\u75DB\u5931\u4EB2\u4EBA\uFF0C\u53D7\u5C3D\u51CC\u8FB1\u3002\u771F\u662F\u4E00\u573A\u6076\u68A6\u554A\u3002</p><p>\u4E8C\u559C\u5B50\u4E0D\u7531\u5F97\u76F8\u5411\u8DEA\u4E86\u4E0B\u6765\uFF0C\u300C\u94F6\u53F6\uFF0C\u5BF9\u4E0D\u8D77\u3002\u6211\u2026\u2026\u300D</p><p>\u94F6\u53F6\u51B7\u6DE1\u5730\u8BF4\uFF0C\u300C\u5927\u7237\u6709\u4EC0\u4E48\u5429\u5490\u53EA\u7BA1\u5429\u5490\uFF0C\u8001\u7237\u8BF4\u4E86\uFF0C\u4E0D\u628A\u60A8\u4F3A\u5019\u597D\uFF0C\u4ED6\u4F1A\u6252\u4E86\u5974\u7684\u76AE\u3002\u300D</p><p>\u8BF4\u7F62\uFF0C\u8471\u767D\u7684\u5C0F\u624B\u4E00\u7C92\u7C92\u89E3\u5F00\u8863\u88F3\u7684\u94AE\u6263\uFF0C\u91CC\u9762\u6CA1\u7A7F\u5185\u8863\uFF0C\u96EA\u767D\u7684\u80F8\u4E00\u70B9\u70B9\u91CA\u653E\u51FA\u6765\uFF0C\u80F8\u5C0F\u5982\u9E3D\uFF0C\u7EC6\u5AE9\u67D4\u8F6F\u3002</p><p>\u4E8C\u559C\u5B50\u5446\u5446\u5730\u770B\u7740\uFF0C\u770B\u7740\u8FD9\u4E2A\u719F\u6089\u800C\u964C\u751F\u7684\u5973\u5B50\u3002</p><p>\u8FD9\u662F\u90A3\u4E2A\u89C6\u8D1E\u6D01\u5982\u751F\u547D\u7684\u5C11\u5973\u5417\uFF1F\u8FD9\u662F\u90A3\u4E2A\u521A\u521A\u5931\u53BB\u4EB2\u59D0\u59D0\u7684\u94F6\u53F6\u5417\uFF1F</p><p>\u94F6\u53F6\u7684\u786E\u9A6F\u670D\u4E86\u3002</p><p>\u975E\u4EBA\u7684\u66B4\u8650\u538B\u57AE\u4E86\u8FD9\u4E2A\u67D4\u5F31\u5F97\u50CF\u6839\u7A3B\u8349\u4E00\u822C\u7684\u5C11\u5973\uFF0C\u91D1\u82B1\u7684\u60E8\u6B7B\u66F4\u5982\u540C\u4E00\u573A\u65E0\u8FB9\u7684\u5669\u68A6\uFF0C\u8BA9\u5979\u6E05\u6E05\u695A\u695A\u5730\u770B\u5230\u4E86\u81EA\u5DF1\u7684\u4E0B\u573A\u3002</p><p>\u4ECE\u660F\u8FF7\u4E2D\u9192\u6765\u540E\uFF0C\u5F85\u4E0D\u5230\u7528\u66F4\u6B8B\u9177\u7684\u624B\u6BB5\u52A0\u8EAB\uFF0C\u94F6\u53F6\u4E3B\u52A8\u6253\u5F00\u4E86\u53CC\u817F\uFF0C\u732E\u51FA\u5904\u5973\u7684\u8D1E\u64CD\u6765\u4F3A\u5949\u8FD9\u5E2E\u9B54\u738B\u3002</p><p>\u5F53\u767D\u5929\u5FB7\u7C97\u8FC7\u4E24\u6307\u7684\u8089\u68D2\u51F6\u608D\u5730\u6345\u7A7F\u4E86\u90A3\u5C42\u67D4\u5F31\u7684\u8584\u819C\uFF0C\u5C31\u50CF\u6345\u7A7F\u4E86\u6574\u4E2A\u8EAB\u5B50\uFF0C\u5927\u91CF\u7684\u9C9C\u8840\u6D8C\u4E86\u51FA\u6765\uFF0C\u67D3\u7EA2\u4E86\u767D\u751F\u751F\u7684\u8EAB\u5B50\uFF0C\u683C\u5916\u89E6\u76EE\u3002</p><p>\u94F6\u53F6\u75DB\u5F97\u60F3\u6B7B\u3002</p><p>\u5979\u7684\u8138\u4E0A\u4E0D\u518D\u6709\u7B11\u5BB9\uFF0C\u4F46\u662F\u7684\u7684\u786E\u786E\u4E5F\u4E0D\u518D\u53CD\u6297\uFF0C\u53EF\u4EE5\u670D\u4ECE\u4EFB\u4F55\u547D\u4EE4\uFF0C\u505A\u4EFB\u4F55\u4E8B\u3002</p><p>\u5C31\u8FD9\u6837\uFF0C\u5979\u83B7\u5F97\u4E86\u8D66\u514D\uFF0C\u6210\u4E86\u670D\u4F3A\u767D\u5929\u5FB7\u7684\u5BB6\u5974\uFF0C\u4E5F\u662F\u767D\u5929\u5FB7\u7528\u4E8E\u8D4F\u8D50\u5F1F\u5144\u4EEC\u7684\u6027\u5974\u3002\u65E0\u8BBA\u662F\u54EA\u4E00\u4E2A\u89D2\u8272\uFF0C\u5979\u90FD\u505A\u5F97\u5C3D\u5FC3\u5C3D\u529B\u3002</p><p>\u8C01\u53C8\u80FD\u8D23\u602A\u5979\u5462\uFF1F\u6216\u8005\uFF0C\u8C01\u53C8\u4F1A\u60B2\u60AF\u5979\u5462\uFF1F</p><p>\u6709\u5FC3\u60B2\u60AF\u8D23\u602A\u5979\u7684\u4EBA\u6216\u8BB8\u81EA\u8EAB\u8FD8\u96BE\u4FDD\u554A\u3002</p><p>\u767D\u5BB6\u5927\u9662\u91CC\uFF0C\u4E00\u573A\u8BE1\u5F02\u7684\u8F83\u91CF\u6B63\u5728\u8FDB\u884C\u3002</p><p>\u767D\u5929\u5FB7\u5BF9\u6D77\u68E0\u3002</p><p>\u5E26\u523A\u7684\u62A4\u8155\u62A4\u819D\u3001\u5168\u526F\u7684\u77ED\u6253\u88C5\u626E\u3001\u795E\u91C7\u98DE\u626C\u7684\u767D\u5929\u5FB7\u5BF9\u7740\u5168\u8EAB\u8D64\u88F8\uFF0C\u9762\u5BB9\u6194\u60B4\uFF0C\u5C40\u4FC3\u4E0D\u5B89\u7684\u7AD9\u5728\u4E00\u4FA7\uFF0C\u9F3B\u5B50\u4E0A\u7A7F\u7740\u94DC\u9F3B\u73AF\uFF0C\u50CF\u72D7\u4E00\u6837\u7CFB\u7740\u957F\u957F\u7684\u7EF3\u5B50\u7684\u6D77\u68E0\u3002</p><p>\u4EE5\u8EAB\u624B\u8BBA\uFF0C\u6D77\u68E0\u7684\u8EAB\u624B\u67AA\u6CD5\u5728\u532A\u5E2E\u4E2D\u662F\u51FA\u4E86\u540D\u7684\u72E0\u8FA3\uFF0C\u5B9E\u6218\u7ECF\u9A8C\u9887\u4E30\uFF0C\u767D\u5929\u5FB7\u7EB5\u4F7F\u624E\u624E\u5B9E\u5B9E\u5B66\u8FC7\u591A\u5E74\u897F\u6D0B\u62F3\u672F\uFF0C\u4E5F\u4E0D\u89C1\u5F97\u80FD\u80DC\u8FC7\u5979\u3002\u65E0\u5948\u6B64\u65F6\u7684\u6D77\u68E0\u5907\u53D7\u6467\u6B8B\uFF0C\u8EAB\u5FC3\u5C48\u670D\uFF0C\u6BEB\u65E0\u6597\u5FD7\u53EF\u8A00\u3002</p><p>\u8FD9\u5C31\u5F88\u663E\u7136\u4E86\uFF0C\u8FD9\u573A\u8F83\u91CF\u6CA1\u6709\u4E00\u4E1D\u516C\u5E73\u53EF\u8A00\uFF0C\u53EA\u5177\u5907\u5A31\u4E50\u6027\uFF0C\u7EAF\u7CB9\u4E3A\u767D\u5929\u5FB7\u548C\u5468\u8FB9\u51E0\u4E2A\u56E2\u4E01\u589E\u6DFB\u6076\u8DA3\u5473\u800C\u5DF2\u3002</p><p>\u767D\u5929\u5FB7\u821E\u8D77\u4E00\u5957\u82B1\u62F3\u7EE3\u817F\uFF0C\u5012\u4E5F\u864E\u864E\u751F\u98CE\uFF0C\u56E2\u4E01\u4EEC\u4E0D\u7531\u5F97\u4E00\u9635\u559D\u91C7\u3002\u6D77\u68E0\u4E00\u5473\u7684\u89C1\u62DB\u62C6\u62DB\uFF0C\u53C8\u8981\u6CE8\u610F\u4E0D\u8BA9\u7EF3\u5B50\u628A\u9F3B\u5B50\u626F\u88C2\u4E86\uFF0C\u8FC8\u7740\u7EC6\u6B65\u56F4\u7740\u573A\u5B50\u79FB\u6765\u79FB\u53BB\uFF0C\u80F8\u524D\u53CC\u5CF0\u8DF3\u8DC3\u4E2A\u4E0D\u505C\uFF0C\u770B\u5F97\u56E2\u4E01\u4EEC\u9F3B\u8840\u6DCC\u4E2A\u4E0D\u505C\u3002</p><p>\u7F20\u6597\u591A\u65F6\uFF0C\u767D\u5929\u5FB7\u4E00\u4E2A\u9ED1\u864E\u638F\u5FC3\u5F80\u5979\u80F8\u53E3\u51FB\u53BB\uFF0C\u6D77\u68E0\u614C\u5FD9\u53CC\u638C\u6321\u4F4F\uFF0C\u4F46\u767D\u5929\u5FB7\u52BF\u5927\u529B\u6C89\uFF0C\u5973\u4EBA\u8FDE\u9000\u51E0\u6B65\u8FD8\u662F\u5750\u5012\u5728\u5730\u3002</p><p>\u638C\u58F0\u56DB\u8D77\u3002</p><p>\u56E2\u5175\u4EEC\u7EDD\u4E0D\u653E\u8FC7\u5927\u62CD\u9A6C\u5C41\u7684\u673A\u4F1A\u3002\u300C\u56E2\u5EA7\u771F\u662F\u82F1\u660E\u795E\u6B66\uFF01\u300D\u300C\u6D77\u68E0\u5A4A\u5B50\u54EA\u62B5\u5F97\u4E0A\u56E2\u957F\u7684\u4E00\u6839\u5C0F\u6307\u5934\u3002\u300D</p><p>\u8FD8\u6709\u8BF4\u7684\uFF0C\u300C\u56E2\u5EA7\u60A8\u8001\u4EBA\u5BB6\u53EF\u6BD4\u90A3\u5927\u9ED1\u72D7\u5F3A\u591A\u4E86\uFF01\u300D</p><p>\u767D\u5929\u5FB7\u557C\u7B11\u7686\u975E\uFF0C\u5FC3\u60C5\u597D\uFF0C\u61D2\u5F97\u8DDF\u8FD9\u4E9B\u6CA1\u6587\u5316\u7684\u5BB6\u4F19\u8BA1\u8F83\uFF0C\u54C8\u54C8\u4E00\u7B11\u3002</p><p>\u51E0\u756A\u4E0B\u6765\uFF0C\u767D\u5929\u5FB7\u81EA\u7136\u5360\u5C3D\u4E0A\u98CE\uFF0C\u4F46\u6D77\u68E0\u9632\u536B\u5F97\u5F53\uFF0C\u4E5F\u6CA1\u8BA9\u767D\u5929\u5FB7\u771F\u5360\u5230\u591A\u5C11\u4FBF\u5B9C\u3002</p><p>\u8FDE\u56E2\u4E01\u4E5F\u770B\u51FA\u6D77\u68E0\u6CA1\u5C3D\u5168\u529B\uFF0C\u559D\u91C7\u58F0\u8D8A\u6765\u8D8A\u4F4E\u843D\u3002\u767D\u5929\u5FB7\u89C9\u5F97\u65E0\u8DA3\uFF0C\u9A82\u9053\uFF1A\u300C\u5988\u7684\uFF0C\u81ED\u5A4A\u5B50\uFF0C\u73A9\u8001\u5B50\u554A\uFF0C\u4E0D\u51C6\u5B88\uFF01\u6253\u8D77\u7CBE\u795E\u6765\uFF0C\u4EAE\u51FA\u81ED\u817F\u6765\uFF0C\u5426\u5219\u65AD\u4E86\u4F60\u7684\u708A\u3002\u300D</p><p>\u8BF4\u7F62\u6076\u72E0\u72E0\u5730\u6325\u62F3\u800C\u4E0A\uFF0C\u6740\u6C14\u6BD5\u73B0\uFF0C\u6D77\u68E0\u88AB\u8FEB\u8BA4\u771F\u5E94\u4ED8\uFF0C\u4EE5\u653B\u5BF9\u653B\uFF0C\u89C1\u62DB\u62C6\u62DB\uFF0C\u6E10\u6E10\u5FD8\u5374\u4E86\u8EAB\u5904\u7684\u56F0\u5883\uFF0C\u773C\u524D\u53EA\u5269\u4E0B\u4E00\u4E2A\u5F3A\u5927\u800C\u90AA\u6076\u7684\u654C\u4EBA\uFF0C\u4E00\u8EAB\u6B66\u827A\u4E5F\u65BD\u5C55\u5F00\u6765\u3002</p><p>\u6D77\u68E0\u7684\u817F\u529F\u6700\u5F3A\uFF0C\u4E00\u53CC\u7389\u817F\u5065\u7F8E\u4FEE\u957F\uFF0C\u6700\u662F\u7F8E\u4E3D\u6027\u611F\uFF0C\u4E5F\u662F\u6740\u4EBA\u7684\u5229\u5668\uFF0C\u8FC8\u5F00\u4E4B\u65F6\u5A07\u5065\u5F02\u5E38\uFF0C\u5149\u79C3\u79C3\u7684\u7389\u6237\u4E5F\u82E5\u9690\u82E5\u73B0\uFF0C\u6625\u5149\u65E0\u9650\u3002</p><p>\u56E2\u4E01\u4EEC\u7684\u9F3B\u8840\u5954\u6D8C\u3002</p><p>\u767D\u5929\u5FB7\u6599\u4E0D\u5230\u5BF9\u624B\u4E00\u4E0B\u7ADF\u4F1A\u53D8\u5F97\u8FD9\u4E48\u5F3A\uFF0C\u653B\u5B88\u4E4B\u52BF\u6613\u624B\uFF0C\u8FDE\u8FDE\u540E\u9000\u3002\u6D77\u68E0\u89C5\u5F97\u7834\u7EFD\uFF0C\u98DE\u8D77\u817F\u6765\u4E00\u4E2A\u6F02\u4EAE\u7684\u4FA7\u8E22\uFF0C\u5149\u811A\u677F\u72E0\u72E0\u5730\u62BD\u51FB\u5728\u4ED6\u7684\u5DE6\u8138\u4E0A\u3002\u767D\u5929\u5FB7\u773C\u524D\u4E00\u9ED1\uFF0C\u8E09\u8DC4\u51E0\u6B65\u7EC8\u8DCC\u5012\u5728\u5730\u3002</p><p>\u56E2\u4E01\u4EEC\u6B62\u4E0D\u4F4F\u7206\u53D1\u51FA\u5C16\u9510\u7684\u7B11\u58F0\uFF0C\u53C8\u50CF\u5272\u65AD\u5589\u5499\u4E00\u6837\u621B\u7136\u800C\u6B62\u3002</p><p>\u6D77\u68E0\u6F20\u7136\u7AD9\u5728\u4E2D\u592E\uFF0C\u6D51\u8EAB\u6563\u53D1\u51FA\u51CC\u4EBA\u7684\u6C14\u52BF\uFF0C\u773C\u795E\u900F\u51FA\u51F6\u608D\u4E4B\u6C14\uFF0C\u76EF\u7740\u5012\u5728\u5730\u4E0A\u7684\u767D\u5929\u5FB7\uFF0C\u50CF\u770B\u7740\u4E00\u6761\u6B7B\u72D7\u3002</p><p>\u767D\u5929\u5FB7\u722C\u4E86\u8D77\u6765\uFF0C\u8138\u8272\u9635\u7EA2\u9635\u767D\uFF0C\u8F93\u4E00\u573A\u5E76\u6CA1\u6709\u4EC0\u4E48\u5927\u4E0D\u4E86\uFF0C\u60CA\u6050\u7684\u662F\u6D77\u68E0\u4F3C\u4E4E\u53C8\u5F00\u59CB\u6062\u590D\u8C03\u6559\u4E4B\u524D\u7684\u81EA\u4FE1\uFF0C\u8981\u5F7B\u5E95\u9A6F\u670D\u8FD9\u5934\u7F8E\u4E3D\u7684\u96CC\u517D\u771F\u4E0D\u662F\u4E00\u4EF6\u5BB9\u6613\u7684\u4E8B\u3002</p><p>\u767D\u5929\u5FB7\u53EB\u5973\u4EBA\u8DEA\u4E0B\u3002</p><p>\u6D77\u68E0\u7F6E\u82E5\u7F54\u95FB\uFF0C\u53CC\u624B\u62B1\u5728\u80F8\u524D\uFF0C\u6324\u51FA\u4E00\u6761\u6DF1\u6DF1\u7684\u4E73\u6C9F\u3002</p><p>\u767D\u5929\u5FB7\u8138\u8272\u8D8A\u6765\u8D8A\u72F0\u72DE\uFF0C\u5236\u6B62\u4E86\u56E2\u4E01\u7684\u51B2\u52A8\uFF0C\u5C31\u8981\u4ECE\u6C14\u52BF\u4E0A\u538B\u57AE\u5979\uFF0C\u8BA9\u5979\u81EA\u5DF1\u6C42\u9976\u3002\u4ED6\u6709\u738B\u724C\u5728\u624B\uFF0C\u4E0D\u6015\u5979\u4E0D\u91CD\u65B0\u5C48\u670D\u3002</p><p>\u300C\u767D\u677F\uFF1F\uFF01\u300D\u767D\u5929\u5FB7\u60A0\u60A0\u5730\u8BF4\uFF0C\u58F0\u97F3\u8F7B\u67D4\uFF0C\u8138\u4E0A\u5374\u662F\u6740\u6C14\u3002\u4ED6\u7684\u624B\u6307\u4E5F\u8F7B\u8F7B\u626F\u4E86\u626F\u90A3\u6839\u957F\u7EF3\u3002</p><p>\u867D\u7136\u6CA1\u6709\u592A\u7528\u529B\uFF0C\u6D77\u68E0\u7684\u9F3B\u5B50\u8FD8\u662F\u611F\u5230\u4E86\u75BC\u75DB\u3002</p><p>\u8FD9\u53EA\u662F\u8B66\u544A\uFF0C\u66F4\u5927\u7684\u60E9\u7F5A\u8FD8\u5728\u540E\u9762\u3002</p><p>\u77ED\u6682\u7684\u6C89\u5BC2\u4E4B\u540E\uFF0C\u6D77\u68E0\u660E\u767D\u4E86\u81EA\u5DF1\u7684\u5BF9\u6297\u662F\u4F55\u7B49\u611A\u8822\u548C\u4E0D\u5408\u65F6\u5B9C\u3002\u5979\u51B3\u5B9A\u653E\u5F03\u3002</p><p>\u4FCF\u8138\u867D\u7136\u8FD8\u7EF7\u5F97\u7D27\u7D27\u7684\uFF0C\u4F46\u9510\u5229\u7684\u773C\u795E\u6D88\u5931\u4E86\uFF0C\u8EAB\u5B50\u4E5F\u7F13\u7F13\u4E0B\u6C89\u3002</p><p>\u767D\u5929\u5FB7\u8981\u597D\u597D\u6559\u8BAD\u6559\u8BAD\u8FD9\u4E2A\u4E0D\u77E5\u597D\u6B79\u7684\u5974\u96B6\uFF0C\u8DD1\u5230\u573A\u8FB9\u62FF\u4E86\u6839\u9A6C\u97AD\uFF0C\u559D\u4EE4\u81EA\u5DF1\u628A\u4E00\u6761\u817F\u6273\u8FC7\u5934\u9876\u3002</p><p>\u5973\u4EBA\u65E2\u5C48\u8FB1\u53C8\u65E0\u5948\uFF0C\u660E\u77E5\u9053\u4ED6\u8981\u5BF9\u81EA\u5DF1\u5E72\u4EC0\u4E48\u5374\u4E0D\u80FD\u53CD\u6297\uFF0C\u8FD9\u79CD\u611F\u89C9\u6BD4\u6B7B\u8FD8\u96BE\u53D7\u3002</p><p>\u4E00\u6761\u4FEE\u76F4\u7684\u5927\u817F\u6162\u6162\u4E3E\u8FC7\u4E86\u5934\u9876\uFF0C\u80EF\u4E0B\u98CE\u5149\u4E00\u89C8\u65E0\u4F59\uFF0C\u88AB\u62D4\u5149\u4E86\u6BDB\u7684\u82B1\u74E3\u5728\u706B\u5149\u4E4B\u4E0B\u7EA4\u6BEB\u6BD5\u73B0\u3002</p><p>\u767D\u5929\u5FB7\u72E0\u72E0\u4E00\u97AD\u5B50\u5C31\u51B2\u7740\u90A3\u5BC6\u5904\u62BD\u4E86\u4E0B\u53BB\u3002\u6D77\u68E0\u5440\u7684\u4E00\u58F0\u60E8\u53EB\uFF0C\u62B1\u7740\u4E0B\u8EAB\u6EDA\u5012\u5728\u5730\uFF0C\u4E00\u6761\u8840\u75D5\u4ECE\u5927\u817F\u76F4\u8D2F\u5C0F\u8179\u3002</p><p>\u300C\u624B\u62FF\u5F00\uFF0C\u4E0D\u51C6\u62A4\u7740\u3002\u300D\u767D\u5929\u5FB7\u5486\u54EE\u7740\uFF0C\u5288\u5934\u5288\u8111\u5730\u53C8\u62BD\u4E86\u51E0\u97AD\uFF0C\u6253\u5F97\u6D77\u68E0\u6EE1\u573A\u6EDA\uFF0C\u867D\u7136\u4E0D\u518D\u75DB\u5F97\u53EB\u5524\uFF0C\u4F46\u8EAB\u4E0A\u5E73\u6DFB\u591A\u5904\u4F24\u75D5\u3002</p><p>\u767D\u5929\u5FB7\u7565\u51FA\u8FD9\u53E3\u6076\u6C14\uFF0C\u5C06\u97AD\u6254\u6389\uFF0C\u62B9\u4E86\u4E00\u628A\u6C57\uFF0C\u300C\u91CD\u65B0\u6765\u8FC7\uFF0C\u597D\u70B9\u6253\uFF0C\u542C\u5230\u5566\uFF1F\u300D</p><p>\u6D77\u68E0\u7EC6\u58F0\u82E5\u868A\u5730\u7B54\u9053\uFF0C\u300C\u542C\u5230\u5566\u3002\u300D</p><p>\u300C\u653E\u4EC0\u4E48\u5C41\u54EA\uFF0C\u5927\u58F0\u70B9\u4F1A\u6B7B\u4EBA\u554A\u3002\u300D</p><p>\u6D77\u68E0\u633A\u8D77\u80F8\uFF0C\u773C\u7736\u7EA2\u4E86\uFF0C\u7F9E\u803B\u800C\u5927\u58F0\u5730\u56DE\u7B54\uFF0C\u300C\u767D\u677F\u660E\u767D\u4E86\uFF0C\u5C11\u7237\u3002\u300D</p><p>\u540E\u9762\u7684\u6BD4\u6B66\u4E2D\uFF0C\u6D77\u68E0\u518D\u4E5F\u4E0D\u6562\u8FD8\u624B\uFF0C\u4E00\u5473\u8EB2\u95EA\u3002\u767D\u5929\u5FB7\u73A9\u8D77\u4E86\u8001\u9E70\u6293\u5C0F\u9E21\u7684\u6E38\u620F\uFF0C\u5728\u573A\u5185\u6765\u4E86\u573A\u8FFD\u9010\u6218\uFF0C\u6D77\u68E0\u53D7\u7EF3\u5B50\u6240\u9650\uFF0C\u79FB\u52A8\u7684\u4F59\u5730\u4E0D\u5927\uFF0C\u7528\u4E0D\u4E86\u591A\u65F6\u5C31\u4F1A\u88AB\u767D\u5929\u5FB7\u902E\u5230\u3002</p><p>\u767D\u5929\u5FB7\u53D1\u4E86\u5174\u5934\uFF0C\u6EE1\u8EAB\u5927\u6C57\uFF0C\u4E0A\u8863\u8131\u6389\uFF0C\u9732\u51FA\u4E00\u8EAB\u80A5\u8089\uFF0C\u72DE\u7B11\u7740\u5728\u5973\u4EBA\u5468\u56F4\u8F6C\u6765\u8F6C\u53BB\uFF0C\u4E13\u6311\u5979\u7684\u79C1\u5BC6\u5904\u4E0B\u624B\uFF0C\u5728\u5976\u5B50\u4E0A\u6293\u4E00\u628A\u5728\u5C41\u80A1\u4E0A\u8E22\u4E00\u811A\uFF0C\u8F7B\u4F7B\u4E4B\u6781\u3002</p><p>\u65F6\u4E0D\u65F6\u8FD8\u8981\u6765\u70B9\u65E0\u8D56\u624B\u6BB5\uFF0C\u6D77\u68E0\u8EB2\u5F97\u72E0\u4E86\uFF0C\u4ED6\u5C31\u626F\u4F4F\u7EF3\u5B50\u628A\u5979\u62D6\u8FC7\u6765\u3002</p><p>\u6D77\u68E0\u6253\u4E86\u4E2A\u5475\u6B20\uFF0C\u763E\u53C8\u4E0A\u6765\u4E86\uFF0C\u6B64\u65F6\u5979\u904D\u4F53\u90FD\u662F\u4F24\uFF0C\u67D4\u5AE9\u5904\u9752\u80BF\u4E0D\u582A\uFF0C\u5C31\u7B97\u771F\u6B63\u653E\u624B\u4E00\u640F\u4E5F\u6CA1\u6709\u4E86\u4E1D\u6BEB\u8FD8\u624B\u4E4B\u529B\u3002</p><p>\u6700\u540E\u4E00\u51FB\uFF0C\u767D\u5929\u5FB7\u72E0\u72E0\u4E00\u811A\u6311\u5728\u5979\u7684\u4E0B\u8179\u3002</p><p>\u300C\u6069\uFF01\u300D\u5973\u4EBA\u53D1\u51FA\u4E00\u58F0\u82E6\u95F7\u5730\u547B\u541F\uFF0C\u5149\u8EAB\u5B50\u4EF0\u9762\u51CC\u7A7A\u98DE\u8D77\uFF0C\u5212\u51FA\u4E00\u6761\u767D\u8272\u7684\u5F27\u7EBF\uFF0C\u957F\u53D1\u7529\u8FC7\uFF0C\u5728\u7A7A\u4E2D\u6563\u5F00\uFF0C\u7136\u540E\u91CD\u91CD\u5730\u6454\u5728\u5730\u4E0A\u3002\u6ED1\u884C\u8FC7\u7A0B\u4E2D\uFF0C\u5931\u53BB\u4FDD\u62A4\u7684\u9F3B\u5B50\u53C8\u88AB\u9F3B\u73AF\u626F\u88C2\u5F00\u6765\uFF0C\u6D77\u68E0\u518D\u5EA6\u4E00\u58F0\u5C16\u53EB\uFF0C\u9C9C\u8840\u540C\u65F6\u4ECE\u9F3B\u5B54\u548C\u5634\u89D2\u6302\u4E86\u51FA\u6765\u3002</p><p>\u6D77\u68E0\u8FD9\u6B21\u518D\u4E5F\u7AD9\u4E0D\u8D77\u6765\u4E86\uFF0C\u50CF\u4E00\u53EA\u8089\u866B\u5728\u5730\u4E0A\u7FFB\u6EDA\uFF0C\u8815\u52A8\uFF0C\u547B\u541F\u3002</p><p>\u300C\u7ED9\u6211\u2026\u2026\u5927\u70DF\u2026\u2026\u300D</p><p>\u767D\u5929\u5FB7\u638F\u51FA\u4E00\u9897\u9E26\u7247\u4E38\uFF0C\u8BF4\uFF0C\u300C\u60F3\u8981\u7684\u8BDD\uFF0C\u5C31\u628A\u4F60\u7684\u81ED\u5C41\u80A1\u7FD8\u8D77\u6765\u3002\u300D</p><p>\u4FEE\u6D01\u7684\u8EAB\u5B50\u8815\u52A8\u4E86\u4E00\u4E0B\uFF0C\u75DB\u5F97\u8138\u90FD\u626D\u66F2\u53D8\u5F62\uFF0C\u8FD8\u662F\u62DA\u547D\u7FFB\u8FC7\u8EAB\u6765\uFF0C\u53D8\u6210\u72D7\u8DB4\u5F0F\uFF0C\u5C06\u6843\u578B\u7684\u5C41\u80A1\u51D1\u5230\u767D\u5929\u5FB7\u7684\u9762\u524D\u3002</p><p>\u767D\u5929\u5FB7\u8E72\u4E0B\u6765\uFF0C\u629A\u5F04\u4E86\u4E00\u4E0B\u5973\u4EBA\u5706\u6DA6\u7684\u5C41\u80A1\uFF0C\u575A\u786C\u7684\u6307\u7532\u6CBF\u7740\u81C0\u6C9F\u4ECE\u5C3E\u690E\u4E00\u8DEF\u522E\u4E0B\u6765\uFF0C\u522E\u8FC7\u67D4\u5AE9\u7684\u83CA\u95E8\uFF0C\u505C\u7559\u5728\u6709\u70B9\u5145\u8840\u52C3\u8D77\u7684\u9634\u8482\u4E0A\uFF0C\u5973\u4EBA\u54C6\u55E6\u4E86\u4E00\u4E0B\u3002</p><p>\u81C0\u90E8\u8F7B\u6447\u4E86\u51E0\u6447\uFF0C\u4F3C\u5728\u6073\u6C42\uFF0C\u53C8\u4F3C\u4E5E\u601C\u3002</p><p>\u767D\u5929\u5FB7\u9732\u51FA\u620F\u8C11\u7684\u7B11\u5BB9\uFF0C\u5C06\u4E00\u9897\u9E26\u7247\u4E38\u653E\u5728\u6D77\u68E0\u7684\u809B\u53E3\uFF0C\u5973\u4EBA\u4E0D\u77E5\u9053\u4ED6\u5728\u5E72\u4EC0\u4E48\uFF0C\u611F\u89C9\u5F88\u7D27\u5F20\uFF0C\u81C0\u8089\u7EF7\u5F97\u975E\u5E38\u7D27\uFF0C\u83CA\u95E8\u4E5F\u6536\u6210\u4E86\u4E00\u6761\u7EBF\u3002</p><p>\u300C\u628A\u5C41\u773C\u653E\u677E\u70B9\uFF0C\u5426\u5219\u8001\u5B50\u5C31\u628A\u70DF\u571F\u6254\u7ED9\u732A\u5403\u3002\u300D</p><p>\u808C\u8089\u653E\u677E\u4E86\u3002\u767D\u5929\u5FB7\u987A\u5229\u5730\u7528\u4E00\u6839\u624B\u6307\u5C06\u9E26\u7247\u4E38\u9876\u8FDB\u4E86\u5979\u7684\u4F53\u5185\uFF0C\u63A8\u5165\u80A0\u8154\u6DF1\u5904\u3002</p><p>\u770B\u7740\u5973\u4EBA\u7684\u624B\u5C31\u8981\u6293\u8FC7\u6765\uFF0C\u767D\u5929\u5FB7\u628A\u5979\u7684\u624B\u62CD\u6389\uFF0C\u300C\u6025\u4E48\u5B50\uFF0C\u8FD8\u5192\u5B8C\u54E9\u3002\u300D</p><p>\u5982\u6CD5\u70AE\u5236\uFF0C\u4ED6\u5C06\u53E6\u4E00\u9897\u9E26\u7247\u4E38\u63A8\u8FDB\u4E86\u5973\u4EBA\u5E72\u71E5\u6E29\u6696\u7684\u7389\u6237\u6DF1\u5904\u3002</p><p>\u521A\u4E00\u653E\u624B\uFF0C\u6D77\u68E0\u5C31\u8FEB\u4E0D\u53CA\u5F85\u5730\u4E24\u624B\u63A2\u5230\u4E0B\u8EAB\uFF0C\u624B\u6307\u53C9\u8FDB\u7389\u6237\u91CC\u5BFB\u89C5\u3002\u5728\u65C1\u4EBA\u770B\u6765\uFF0C\u8FD9\u4E2A\u7F8E\u4E3D\u7684\u5973\u5B50\u5C31\u50CF\u662F\u5F53\u7740\u4F17\u4EBA\u7684\u9762\uFF0C\u4E24\u817F\u5927\u5F00\uFF0C\u6BEB\u65E0\u7F9E\u803B\u5730\u81EA\u6170\u3002</p><p>\u8FD9\u573A\u666F\u5B9E\u5728\u523A\u6FC0\uFF0C\u770B\u5F97\u767D\u5929\u5FB7\u548C\u624B\u4E0B\u4EEC\u8C37\u7CBE\u4E0A\u5934\u3002</p><p>\u6D77\u68E0\u611F\u89C9\u8D8A\u6765\u8D8A\u4E0D\u597D\uFF0C\u8D8A\u6765\u8D8A\u7126\u6025\uFF0C\u6839\u672C\u987E\u4E0D\u5F97\u65C1\u4EBA\u7684\u773C\u5149\uFF0C\u51E0\u4E4E\u8981\u5C06\u6574\u53EA\u624B\u90FD\u8981\u63D2\u8FDB\u81EA\u5DF1\u7684\u9634\u7A74\u4E2D\u91CC\uFF0C\u4F53\u6DB2\u6EA2\u4E86\u51FA\u6765\uFF0C\u9E26\u7247\u4E38\u53D8\u5F97\u66F4\u6ED1\u6E9C\uFF0C\u51E0\u6B21\u89E6\u5230\u4E86\u90FD\u6CA1\u638C\u63E1\u4F4F\uFF0C\u53CD\u800C\u8FDB\u5165\u5F97\u8D8A\u6765\u8D8A\u6DF1\uFF0C\u53EF\u80FD\u90FD\u8FDB\u5230\u5B50\u5BAB\u53E3\u53BB\u4E86\u3002</p><p>\u597D\u4E0D\u5BB9\u6613\u624D\u5C06\u90A3\u9897\u5C0F\u4E38\u5B50\u7528\u6307\u5C16\u631F\u4F4F\uFF0C\u5C31\u8981\u53D6\u5C06\u51FA\u6765\uFF0C\u767D\u5929\u5FB7\u7A81\u7136\u5C06\u5149\u811A\u677F\u538B\u5728\u4E86\u5979\u7684\u9634\u961C\u4E0A\uFF0C\u5927\u811A\u8DBE\u6345\u8FDB\u8089\u82B1\u4E2D\u6405\u52A8\uFF0C\u9E26\u7247\u4E38\u518D\u5EA6\u8131\u624B\u800C\u53BB\u3002</p><p>\u5973\u4EBA\u53D1\u51FA\u4E00\u58F0\u513F\u557C\u822C\u7684\u54ED\u58F0\u3002</p><p>\u767D\u5929\u5FB7\u9053\uFF0C\u300C\u53D6\u540E\u9762\u7684\u3002\u300D</p><p>\u5973\u4EBA\u4E0D\u6562\u76F8\u4E89\uFF0C\u53CC\u624B\u53EA\u5F97\u8F6C\u5411\u809B\u9053\u3002\u53EF\u601C\u6B64\u5904\u72ED\u5C0F\u5F02\u5E38\uFF0C\u8700\u9053\u96BE\u884C\uFF0C\u4E00\u6839\u624B\u6307\u8FDB\u53BB\u4E5F\u5ACC\u7C97\uFF0C\u96BE\u5EA6\u5927\u4E0A\u6570\u500D\u4E0D\u6B62\u3002</p><p>\u6D77\u68E0\u4ECE\u672A\u5728\u81EA\u5DF1\u540E\u9762\u7684\u6392\u6CC4\u5904\u5982\u6B64\u6DEB\u5F04\uFF0C\u4E0D\u7531\u5F97\u7389\u9762\u98DE\u7EA2\uFF0C\u75DB\u82E6\u7F9E\u6012\u9EBB\u75D2\u4E94\u5473\u6742\u9648\uFF0C\u96BE\u4EE5\u81EA\u5DF2\u3002</p><p>\u671B\u7740\u5973\u4EBA\u7684\u4E00\u6839\u7EA4\u7EA4\u7389\u6307\u6345\u8FDB\u81EA\u5DF1\u7684\u5C41\u773C\u91CC\uFF0C\u81EA\u5DF1\u73A9\u81EA\u5DF1\uFF0C\u767D\u5929\u5FB7\u5927\u7B11\uFF0C\u300C\u4F60\u4EEC\u8FD9\u5E2E\u5154\u513F\u5B50\u53EF\u89C1\u8FC7\u8FD9\u7B49\u597D\u620F\uFF1F\u300D</p><p>\u56E2\u4E01\u4EEC\u8F70\u7136\u7B54\uFF0C\u300C\u591A\u8C22\u56E2\u5EA7\u8BA9\u6211\u4EEC\u5F00\u773C\u5566\u3002\u300D</p><p>\u767D\u5929\u5FB7\u60F3\u8D77\u4E00\u4E8B\uFF0C\u4E0D\u7981\u773C\u775B\u53D1\u5149\uFF0C\u300C\u674E\u8D35\u554A\uFF0C\u4F60\u8BF4\u8BF4\uFF0C\u5973\u4EBA\u4E0A\u9762\u7684\u90A3\u5F20\u5634\u662F\u62BD\u5927\u70DF\u4E0A\u763E\u4E86\uFF0C\u4E0B\u9762\u7684\u4E24\u5F20\u5634\u4F1A\u4E0D\u4F1A\u4E5F\u80FD\u4E0A\u763E\u5462\uFF1F\u300D</p><p>\u674E\u8D35\u9053\uFF0C\u300C\u8FD9\u4E2A\uFF0C\u56E2\u5EA7\u4E0D\u77E5\u6709\u4F55\u5999\u8BA1\uFF1F\u300D</p><p>\u767D\u5929\u5FB7\u5475\u5475\u7B11\u9053\uFF0C\u300C\u8001\u5B50\u5C31\u50CF\u8FD9\u6837\uFF0C\u6BCF\u5929\u62FF\u70B9\u5927\u70DF\u6CAB\u5B50\u62B9\u5728\u5979\u7684\u81ED\u5C41\u80A1\u91CC\uFF0C\u65E5\u5B50\u957F\u4E86\u5174\u8BB8\u6709\u70B9\u4F5C\u7528\u54E9\uFF0C\u60F3\u4E00\u60F3\uFF0C\u5230\u90A3\u65F6\u8FD9\u5A4A\u5B50\u4E0A\u4E0B\u4E00\u9F50\u53D1\u9A9A\u653E\u6D6A\u7684\u6837\u5B50\u3002\u300D</p><p>\u4ED6\u6478\u6478\u4E0B\u5DF4\uFF0C\u60F3\u5230\u7F8E\u5999\u7684\u524D\u666F\uFF0C\u773C\u775B\u7787\u6210\u4E86\u4E00\u6761\u7EBF\u3002</p><p>\u4ED6\u7684\u811A\u677F\u8E29\u7740\u7684\u5973\u4EBA\u79C1\u5904\u65E9\u5DF2\u6CDB\u6EE5\u6210\u707E\uFF0C\u5C31\u50CF\u8E0F\u5728\u4E00\u4E2A\u79EF\u6C34\u7684\u5C0F\u8089\u5305\u4E0A\u3002</p><p>\u5973\u4EBA\u8FD8\u5728\u52AA\u529B\u5BFB\u627E\u7740\u81EA\u5DF1\u4F53\u5185\u7684\u90A3\u9897\u9E26\u7247\u4E38\uFF0C\u8EBA\u5728\u5730\u4E0A\uFF0C\u79C1\u5904\u8E29\u5728\u7537\u4EBA\u7684\u811A\u4E0B\uFF0C\u773C\u795E\u8FF7\u79BB\uFF0C\u75DB\u82E6\u5730\u8815\u52A8\u3001\u547B\u541F\uFF0C\u54EA\u91CC\u8FD8\u6709\u6614\u65E5\u9ED1\u51E4\u51F0\u4E1D\u6BEB\u7684\u795E\u91C7\u3002</p><p>\u767D\u5929\u5FB7\u80F8\u4E2D\u5347\u817E\u8D77\u5F3A\u70C8\u7684\u81EA\u8C6A\u3002</p><p>\u9ED1\u51E4\u51F0\u9EEF\u7136\u6D88\u5931\u4E86\uFF0C\u4EE3\u4E4B\u7684\u662F\u7A7A\u957F\u7740\u9ED1\u51E4\u7F8E\u4E3D\u8EAF\u58F3\u7684\u8089\u5974\u3002</p><p>\u2026\u2026\u2026\u2026</p>',121);function t(i,n){return r}var _=a(e,[["render",t]]);export{_ as default};
