import{e as p}from"./app.630af5f2.js";import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";const e={},r=p('<h1 id="\u7B2C\u4E03\u7AE0-\u6597\u517D" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E03\u7AE0-\u6597\u517D" aria-hidden="true">#</a> \u7B2C\u4E03\u7AE0 \u6597\u517D</h1><p>\u5185\u82B1\u5385\u3002</p><p>\u51E0\u4E2A\u5982\u72FC\u4F3C\u864E\u7684\u6C49\u5B50\u51B2\u8FDB\u95E8\u6765\uFF0C\u628A\u8EBA\u5728\u5E8A\u4E0A\u7684\u6D77\u68E0\u6309\u4F4F\uFF0C\u5148\u7528\u9EBB\u7EF3\u53CD\u6346\uFF0C\u518D\u5378\u4E86\u94C1\u94D0\uFF0C\u4E00\u70B9\u4E5F\u4E0D\u6562\u5927\u610F\u3002</p><p>\u6D77\u68E0\u627E\u4E0D\u5230\u4EFB\u4F55\u673A\u4F1A\u53CD\u6297\uFF0C\u53EA\u5F97\u4EFB\u4EBA\u5BB0\u5272\u3002</p><p>\u8499\u4E0A\u773C\uFF0C\u4F3C\u4E4E\u5750\u4E86\u597D\u957F\u4E00\u622A\u9A6C\u8F66\uFF0C\u53C8\u4E0B\u8F66\uFF0C\u4E00\u8DEF\u63A8\u63A8\u6387\u6387\uFF0C\u5728\u4E00\u4E2A\u964C\u751F\u7684\u5730\u65B9\u8F6C\u6765\u8F6C\u53BB\uFF0C\u53C8\u4E0B\u4E86\u9636\u68AF\uFF0C\u542C\u89C1\u6C34\u6EF4\u58F0\uFF0C\u706B\u7130\u71C3\u70E7\u58F0\uFF0C\u94C1\u5668\u4EA4\u9519\u58F0\uFF0C\u663E\u5F97\u5341\u5206\u7A7A\u65F7\u3002</p><p>\u5979\u88AB\u5E26\u5230\u4E00\u4E2A\u5730\u65B9\u7ACB\u4F4F\uFF0C\u53CC\u624B\u9AD8\u9AD8\u4E3E\u8D77\uFF0C\u7F1A\u5728\u4E00\u8D77\u5F80\u4E0A\u62C9\u7D27\u62C9\u76F4\uFF0C\u53CC\u811A\u5206\u5F00\u6813\u4F4F\uFF0C\u6700\u4E3A\u7F9E\u803B\u7684\u662F\uFF0C\u8863\u88F3\u7EC8\u4E8E\u4E5F\u88AB\u4E00\u4EF6\u4EF6\u5265\u6389\uFF0C\u76F4\u81F3\u4E00\u4E1D\u4E0D\u6302\uFF0C\u76F4\u89C9\u4E2D\u6709\u4E0D\u5C11\u70ED\u8FA3\u8FA3\u7684\u773C\u5149\u5411\u5979\u6295\u5C04\u800C\u6765\uFF0C\u8FD9\u79CD\u88F8\u88CE\u76F8\u5BF9\u7684\u6ECB\u5473\u6BD4\u6B7B\u8FD8\u96BE\u53D7\u3002</p><p>\u8FD9\u4E00\u5929\u7EC8\u4E8E\u6765\u4E86\uFF0C\u5979\u89C9\u5F97\u6709\u4E9B\u7D27\u5F20\uFF0C\u53E3\u91CC\u53D1\u5E72\u3002</p><p>\u5979\u611F\u89C9\u5230\u6709\u4EBA\u8FD1\u524D\u7684\u547C\u5438\u58F0\uFF0C\u662F\u7537\u4EBA\u5473\u6D53\u91CD\u7684\u81ED\u6C14\u3002</p><p>\u300C\u767D\u677F\uFF1F\u300D</p><p>\u4E24\u4E2A\u5B57\u5982\u540C\u5F3A\u70C8\u7684\u7535\u51FB\uFF0C\u6253\u5F97\u6D77\u68E0\u54C6\u55E6\u4E86\u4E00\u4E0B\u3002</p><p>\u8FD9\u662F\u4E2A\u591A\u4E48\u4FAE\u8FB1\u4EBA\u7684\u540D\u5B57\uFF0C\u8FD9\u4E48\u591A\u5E74\u4E86\uFF0C\u53EA\u6709\u4E00\u4E2A\u4EBA\u66FE\u7ECF\u5C31\u662F\u8FD9\u4E48\u53EB\u8FC7\u5979\uFF0C\u5929\u5929\u53EB\u5979\uFF0C\u542C\u4E00\u6B21\u5C31\u5982\u540C\u62FF\u5200\u5728\u5979\u5FC3\u574E\u4E0A\u5212\u4E00\u6B21\uFF0C\u4F7F\u5979\u5728\u6076\u68A6\u4E2D\u5C16\u53EB\uFF0C\u5728\u6050\u60E7\u4E2D\u53D1\u6296\uFF0C\u6CA1\u9519\uFF0C\u53EA\u6709\u4ED6\uFF0C\u767D\u5BCC\u8D35\uFF01</p><p>\u300C\u770B\u6765\u4F60\u8FD8\u8BB0\u5F97\u6211\uFF0C\u5B89\u51E4\u513F\u3002\u300D\u7537\u4EBA\u4F4E\u6C89\u5730\u7B11\u4E86\u3002</p><p>\u8499\u9762\u5E03\u7F13\u7F13\u53D6\u4E0B\uFF0C\u767D\u5929\u5FB7\u4E5F\u5C31\u662F\u767D\u5BCC\u8D35\u90A3\u5F20\u5145\u6EE1\u90AA\u6076\u7684\u8138\u6D6E\u73B0\u5728\u773C\u524D\u3002</p><p>\u5341\u5E74\u4E86\uFF0C\u51A4\u5BB6\u8FD8\u662F\u7EC8\u805A\u9996\uFF0C\u5979\u4E5F\u7EC8\u9003\u4E0D\u8FC7\u547D\u8FD0\u7684\u5B89\u6392\uFF0C\u518D\u4E00\u6B21\u843D\u5230\u4E86\u767D\u5BB6\u7684\u624B\u4E2D\u3002</p><p>\u767D\u5929\u5FB7\u683C\u683C\u5927\u7B11\u8D77\u6765\u3002</p><p>\u300C\u60F3\u4E0D\u5230\u5427\uFF0C\u5B89\u51E4\u513F\uFF0C\u6211\u4EEC\u8FD8\u662F\u89C1\u9762\u4E86\uFF0C\u4F60\u4E3A\u4E86\u627E\u8001\u5B50\uFF0C\u6740\u4E86\u6211\u8001\u7238\uFF0C\u8E0F\u5E73\u5F97\u767D\u5BB6\u5821\uFF0C\u5E72\u5F97\u597D\uFF0C\u5E72\u5F97\u6F02\u4EAE\u4E4B\u6781\u554A\u3002\u300D</p><p>\u4ED6\u5207\u9F7F\u9053\uFF1A\u300C\u53EF\u60DC\u4F60\u665A\u4E86\u4E00\u6B65\uFF0C\u6211\u65E9\u5C31\u5230\u5916\u5730\u53BB\u4E86\uFF0C\u8BFB\u4E66\uFF0C\u7ECF\u5546\uFF0C\u6CBB\u75C5\u3002\u300D</p><p>\u4ED6\u8138\u4E0A\u6D6E\u51FA\u8BE1\u5F02\u7684\u8868\u60C5\uFF0C\u628A\u5634\u5DF4\u51D1\u5230\u5979\u7684\u8033\u8FB9\uFF0C\u6084\u6084\u8BF4\uFF1A\u300C\u5BF9\u4E86\uFF0C\u5FD8\u8BB0\u544A\u8BC9\u4F60\uFF0C\u6211\u90A3\u75C5\u6839\u8BA9\u897F\u65B9\u5927\u592B\u6CBB\u597D\u4E86\uFF0C\u690D\u4E86\u73E0\uFF0C\u5973\u4EBA\u90FD\u7231\u6B7B\u4E86\u5B83\uFF0C\u4F60\u8981\u4E0D\u8981\u8BD5\u8BD5\uFF1F\u300D</p><p>\u6D77\u68E0\u5934\u8111\u4E2D\u55E1\u55E1\u4F5C\u54CD\uFF0C\u4E00\u7247\u7A7A\u767D\u3002</p><p>\u5979\u6740\u767D\u656C\u8F69\u65F6\u7684\u786E\u5BFB\u627E\u8FC7\u767D\u5BCC\u8D35\uFF0C\u4E00\u76F4\u6CA1\u627E\u7740\uFF0C\u5FC3\u91CC\u5C31\u4E00\u76F4\u4E0D\u8E0F\u5B9E\u3002</p><p>\u81EA\u4ECE\u767D\u5929\u5FB7\u5230\u4E86\u6C85\u9547\uFF0C\u5979\u5C31\u6709\u4E0D\u7965\u7684\u9884\u611F\uFF0C\u5931\u53BB\u4E86\u7075\u6027\uFF0C\u624D\u4F1A\u4E00\u518D\u88AB\u52A8\uFF0C\u4E5F\u8BB8\u767D\u5BCC\u8D35\uFF08\u767D\u5929\u5FB7\uFF09\u771F\u662F\u5979\u547D\u4E2D\u7684\u514B\u661F\u3002</p><p>\u767D\u5929\u5FB7\u4F38\u51FA\u624B\u6765\uFF0C\u6349\u4F4F\u5979\u575A\u633A\u7684\u4E73\u623F\uFF0C\u6162\u6162\u5730\u63C9\u634F\u7740\uFF0C\u5938\u9053\uFF1A\u300C\u597D\u7ED3\u5B9E\u7684\u5976\u5B50\uFF0C\u8D8A\u957F\u8D8A\u6F02\u4EAE\u4E86\u3002\u300D</p><p>\u53C8\u6478\u81F3\u5C0F\u8179\uFF0C\u5728\u6DF1\u9083\u7684\u809A\u8110\u773C\u6DEB\u6D6A\u5730\u6345\u4E86\u4E00\u6345\uFF0C\u63A5\u89E6\u5230\u6BDB\u8338\u8338\u7684\u4E0B\u8EAB\u65F6\u7B11\u8BF4\u4E86\u4E00\u53E5\u3002</p><p>\u300C\u8FD8\u662F\u767D\u677F\u513F\u597D\u3002\u300D</p><p>\u6D77\u68E0\u95ED\u4E0A\u773C\uFF0C\u5C06\u5934\u626D\u5230\u4E00\u4FA7\uFF0C\u7F9E\u6124\u6B32\u6B7B\u3002</p><p>\u9B54\u624B\u4E00\u8DEF\u6478\u5230\u4E86\u4FEE\u957F\u5706\u6DA6\u7684\u5927\u817F\uFF0C\u957F\u5E74\u91CE\u5916\u953B\u70BC\u4F7F\u5927\u817F\u808C\u8089\u7EF7\u5F97\u94C1\u4E00\u822C\u786C\uFF0C\u53C8\u5145\u6EE1\u5F39\u6027\u3002</p><p>\u767D\u5929\u5FB7\u50CF\u5728\u68C0\u9605\u81EA\u5DF1\u7684\u9886\u5730\uFF0C\u4E00\u8DEF\u6478\u4E00\u8DEF\u8D5E\uFF0C\u5C06\u6D77\u68E0\u8E81\u5F97\u6EE1\u8138\u901A\u7EA2\u3002</p><p>\u767D\u5929\u5FB7\u7A81\u7136\u5927\u58F0\u8BF4\uFF1A\u300C\u5728\u9ED1\u51E4\u51F0\u80CC\u540E\u7684\uFF0C\u4F60\u4EEC\u770B\u5230\u4E86\u4E48\u5B50\u5440\uFF1F\u300D</p><p>\u6D77\u68E0\u8EAB\u540E\u51E0\u4E2A\u4FDD\u5B89\u56E2\u5458\u4E71\u53EB\u9053\uFF1A\u300C\u770B\u5230\u4E86\u5C41\u80A1\u86CB\u3002\u300D</p><p>\u767D\u5929\u5FB7\u7B11\u9A82\u3002\u300C\u64CD\u4F60\u7956\u5B97\uFF0C\u8001\u5B50\u95EE\u90A3\u5C41\u80A1\u86CB\u4E0A\u6709\u4E48\u5B50\u73A9\u610F\u6CA1\u6709\uFF1F\u300D</p><p>\u4F17\u4EBA\u7B54\u3002</p><p>\u300C\u6709\u5B57\u3002\u300D</p><p>\u300C\u4F55\u5B57\uFF1F\u300D</p><p>\u300C\u767D\u5B57\u3002\u300D</p><p>\u300C\u53EF\u6653\u5F97\u767D\u5B57\u6709\u4E48\u5B50\u610F\u601D\uFF1F\u300D</p><p>\u300C\u4E0D\u6653\u5F97\u3002\u300D</p><p>\u767D\u5929\u5FB7\u634F\u7740\u6D77\u68E0\u5C16\u4FCF\u7684\u4E0B\u5DF4\uFF0C\u62AC\u8D77\u6765\uFF0C\u5F3A\u8FEB\u5979\u770B\u7740\u4ED6\u51F6\u66B4\u7684\u773C\u775B\uFF0C\u4E00\u5B57\u4E00\u987F\u5730\u8BF4\uFF1A\u300C\u5C0F\u5B50\u4EEC\uFF0C\u542C\u597D\u7F57\uFF0C\u51E1\u662F\u5C41\u80A1\u4E0A\u6709\u4E2A\u767D\u5B57\u7684\uFF0C\u5C31\u8868\u793A\u8FD9\u4E2A\u4EBA\u8FC7\u53BB\u662F\uFF0C\u73B0\u5728\u662F\uFF0C\u5C06\u6765\u6C38\u8FDC\u662F\u8001\u5B50\u767D\u5BB6\u7684\u5974\u96B6\uFF0C\u751F\u751F\u4E16\u4E16\u4E5F\u522B\u60F3\u7FFB\u8EAB\uFF01\u300D</p><p>\u4E00\u756A\u5BF9\u7B54\u5982\u652F\u652F\u5229\u7BAD\u76F4\u5C04\u6D77\u68E0\u7684\u5FC3\u5934\uFF0C\u4E00\u70B9\u70B9\u51FB\u788E\u4E86\u5979\u7684\u5C0A\u4E25\u548C\u4FE1\u5FC3\u3002</p><p>\u66F4\u7CDF\u7684\u662F\uFF0C\u5728\u767D\u5929\u5FB7\u7684\u629A\u5F04\u4E4B\u4E0B\uFF0C\u5979\u7684\u8EAB\u4F53\u7ADF\u53C8\u8D77\u4E86\u53CD\u5E94\uFF0C\u6843\u6E90\u6D1E\u53E3\u53D8\u5F97\u6FE1\u6E7F\uFF0C\u4E00\u80A1\u6676\u4EAE\u7684\u6DEB\u6C41\u6EA2\u4E86\u51FA\u6765\u3002</p><p>\u771F\u662F\u4E00\u79CD\u5012\u9519\u800C\u5D29\u6E83\u7684\u611F\u89C9\u3002</p><p>\u767D\u5929\u5FB7\u4E0D\u653E\u8FC7\u4EFB\u4F55\u63F6\u63C4\u5979\u7684\u673A\u4F1A\uFF0C\u9053\uFF1A\u300C\u53C8\u53D1\u9A9A\u4E86\u5417\uFF1F\u653E\u5FC3\uFF0C\u8001\u5B50\u7ED9\u4F60\u53D1\u6CC4\u7684\u673A\u4F1A\uFF0C\u770B\u524D\u9762\u2026\u2026\u300D</p><p>\u706B\u628A\u71C3\u8D77\uFF0C\u628A\u56DB\u4E0B\u91CC\u7167\u5F97\u901A\u660E\u3002</p><p>\u4ED6\u4EEC\u6240\u5904\u662F\u5728\u4E00\u4E2A\u5730\u4E0B\u6EB6\u6D1E\u4E2D\uFF0C\u4E2D\u592E\u5929\u9876\u5782\u4E0B\u7684\u51E0\u652F\u5012\u949F\u4E73\u77F3\u4E0A\u7CFB\u7740\u51E0\u652F\u706B\u76C6\uFF0C\u7A7A\u95F4\u5F88\u5927\uFF0C\u94C1\u680F\u5728\u5E7F\u573A\u4E0A\u56F4\u4E86\u4E00\u4E2A\u51E0\u5341\u5E73\u65B9\u7C73\u7684\u5927\u5708\uFF0C\u5468\u56F4\u9AD8\u9AD8\u7684\u6697\u5904\u5F71\u5F71\u7EF0\u7EF0\u5730\u6709\u4E00\u4E9B\u4EBA\u5F71\uFF0C\u6574\u4E2A\u5F62\u72B6\u50CF\u6781\u4E86\u53E4\u4EE3\u7684\u6597\u517D\u573A\uFF0C\u53EA\u662F\u8F83\u7B80\u964B\u7F62\u4E86\u3002</p><p>\u56F4\u680F\u4FA7\u8FB9\u6709\u4E2A\u7B3C\u5B50\uFF0C\u5173\u7740\u4E00\u6761\u683C\u5916\u9AD8\u5927\u7684\u9ED1\u72FC\u72D7\uFF0C\u8D64\u7EA2\u4E86\u773C\uFF0C\u4E0D\u77E5\u662F\u5728\u53D1\u6625\u8FD8\u662F\u53D1\u75AF\uFF0C\u4E0D\u505C\u5728\u56F4\u7740\u7B3C\u5B50\u6253\u8F6C\uFF0C\u65F6\u4E0D\u65F6\u51B2\u7740\u4EBA\u7FA4\u55E5\u53EB\u51E0\u58F0\u3002</p><p>\u7B3C\u5B50\u9876\u7AEF\u6709\u51E0\u6839\u94C1\u94FE\u6813\u7740\uFF0C\u4E0A\u9762\u6709\u673A\u5173\u63A7\u5236\uFF0C\u53EF\u4EE5\u968F\u65F6\u628A\u7B3C\u5B50\u540A\u653E\u3001\u79FB\u4F4D\u3002</p><p>\u767D\u5929\u5FB7\u8BF4\u9053\uFF1A\u300C\u8001\u5B50\u82B1\u4E86\u5F88\u591A\u5FC3\u8840\u624D\u5EFA\u6210\u4E86\u8FD9\u4E2A\u6597\u72D7\u573A\uFF0C\u539F\u6765\u662F\u6253\u7B97\u8D4C\u72D7\uFF0C\u6B63\u597D\u4ECA\u5929\u6709\u5927\u540D\u9F0E\u9F0E\u7684\u9ED1\u51E4\u51F0\u6765\u526A\u5934\u5F69\uFF0C\u8FD8\u8BF7\u4E86\u4E0D\u5C11\u8FBE\u5B98\u8D35\u4EBA\u6765\u89C2\u8D4F\uFF0C\u5B89\u51E4\u5B9D\u8D1D\u513F\uFF0C\u4F60\u6740\u4E86\u6211\u7236\uFF0C\u6211\u90FD\u53EF\u4EE5\u653E\u8FC7\uFF0C\u4F46\u4ECA\u513F\u4E2A\u53EF\u5F97\u5356\u70B9\u6C14\u529B\uFF0C\u4E0D\u80FD\u7ED9\u8001\u5B50\u4E22\u8138\u3002\u300D</p><p>\u6D77\u68E0\u65B9\u624D\u660E\u767D\u4E86\u4ED6\u9669\u6076\u7684\u7528\u610F\uFF0C\u7F9E\u6012\u4EA4\u52A0\uFF0C\u4E00\u53E3\u5478\u9053\uFF0C\u300C\u755C\u751F\uFF0C\u6211\u5C31\u662F\u6B7B\uFF0C\u4E5F\u4E0D\u8BA9\u4F60\u5982\u613F\u3002\u300D</p><p>\u767D\u5929\u5FB7\u65E9\u5DF2\u6599\u5230\u5979\u7684\u53CD\u5E94\uFF0C\u4E5F\u4E0D\u52A8\u6C14\uFF0C\u9053\uFF1A\u300C\u83AB\u6025\uFF0C\u4F60\u4F1A\u7B54\u5E94\u7684\u3002\u300D</p><p>\u6D77\u68E0\u7D22\u6027\u9616\u4E0A\u773C\u3002</p><p>\u767D\u5929\u5FB7\u51B7\u7B11\u4E00\u58F0\uFF0C\u62CD\u4E86\u62CD\u624B\uFF0C\u4E24\u4E2A\u5927\u6C49\u5C06\u4E00\u4E2A\u8D64\u6761\u6761\u4F24\u75D5\u7D2F\u7D2F\u7684\u5973\u5B50\u62D6\u8FDB\u4E86\u573A\u5185\uFF0C\u6254\u5728\u5730\u4E0A\uFF0C\u53C8\u5C06\u4E00\u76C6\u9EC4\u6D4A\u7684\u6C34\u5012\u5728\u5979\u7684\u4E0B\u8EAB\u3002</p><p>\u90A3\u5973\u5B50\u6323\u624E\u4E86\u826F\u4E45\u624D\u722C\u8D77\u6765\uFF0C\u53C8\u91CD\u91CD\u5730\u8DCC\u5012\u5728\u5730\u3002</p><p>\u767D\u5929\u5FB7\u9AD8\u558A\u4E00\u58F0\u3002</p><p>\u300C\u5F00\u95F8\uFF01\u300D</p><p>\u542C\u5F97\u4F17\u4EBA\u7684\u6B22\u547C\u58F0\uFF0C\u6D77\u68E0\u5F20\u5F00\u773C\uFF0C\u7ADF\u662F\u601D\u5FF5\u591A\u65E5\u7684\u91D1\u82B1\uFF0C\u4E0D\u7531\u5F97\u6CEA\u6C34\u76C8\u7736\uFF0C\u75DB\u53EB\u9053\uFF1A\u300C\u4E0D\uFF01\u300D</p><p>\u5F85\u4E0D\u5230\u94C1\u7B3C\u5B8C\u5168\u5730\u5347\u8D77\uFF0C\u72FC\u72D7\u4E00\u7F69\u5C31\u51B2\u51FA\u6765\u4E86\uFF0C\u7728\u773C\u95F4\u6C14\u52BF\u6C79\u6C79\u6251\u5230\u91D1\u82B1\u8DDF\u524D\uFF0C\u91D1\u82B1\u60C5\u6025\u4E4B\u4E0B\uFF0C\u865A\u6325\u4E00\u62F3\uFF0C\u52C9\u529B\u7AD9\u8D77\u8EAB\u6765\u3002</p><p>\u6076\u72D7\u8D77\u5148\u6478\u4E0D\u51C6\u5E95\u7EC6\uFF0C\u5403\u4E86\u4E00\u60CA\uFF0C\u5F80\u540E\u8DF3\u4E86\u4E00\u6B65\uFF0C\u56F4\u7740\u91D1\u82B1\u6253\u5708\u5B50\uFF0C\u5BFB\u627E\u7834\u7EFD\u3002</p><p>\u8FDE\u65E5\u7684\u6298\u78E8\u65E9\u5C31\u8BA9\u91D1\u82B1\u4F53\u529B\u900F\u652F\uFF0C\u773C\u524D\u53D1\u82B1\uFF0C\u75B2\u60EB\u4E0D\u582A\uFF0C\u521A\u7AD9\u76F4\u5C31\u662F\u4E00\u4E2A\u8E09\u8DC4\uFF0C\u6839\u672C\u8C08\u4E0D\u5230\u4E0E\u51F6\u731B\u654F\u6377\u7684\u6076\u72D7\u5BF9\u6297\u3002</p><p>\u6076\u72D7\u5F88\u5FEB\u7ED5\u5230\u4E86\u5979\u7684\u540E\u9762\uFF0C\u4E00\u8DC3\u800C\u8D77\uFF0C\u5728\u4F17\u4EBA\u7684\u60CA\u547C\u58F0\u4E2D\u51C6\u786E\u5730\u54AC\u4F4F\u4E86\u91D1\u82B1\u7684\u4E00\u5757\u81C0\u8089\uFF0C\u91D1\u82B1\u60E8\u53EB\u4E00\u58F0\uFF0C\u751F\u751F\u8BA9\u72D7\u628A\u4E00\u5757\u8840\u6DCB\u6DCB\u7684\u8089\u6495\u626F\u4E86\u4E0B\u6765\u3002</p><p>\u91D1\u82B1\u59D4\u987F\u5728\u5730\uFF0C\u5012\u5728\u81EA\u5DF1\u7684\u8840\u6CCA\u5F53\u4E2D\u3002</p><p>\u6076\u72D7\u8EB2\u5F97\u8FDC\u8FDC\u7684\uFF0C\u628A\u8089\u541E\u6389\uFF0C\u8840\u7EA2\u7684\u773C\u775B\u91CC\u8FD8\u5728\u95EA\u52A8\u7740\u8D2A\u6B32\u7684\u5149\u8292\uFF0C\u6298\u4E86\u56DE\u6765\uFF0C\u76EF\u7740\u5730\u4E0A\u7684\u91D1\u82B1\uFF0C\u5927\u5634\u518D\u5EA6\u5F20\u5F00\u3002</p><p>\u6D77\u68E0\u5FC3\u75DB\u5982\u7EDE\u3002</p><p>\u300C\u653E\u5F00\u5979\uFF01\u300D</p><p>\u767D\u5929\u5FB7\u5728\u4E00\u4FA7\u51B7\u7B11\u9053\uFF1A\u300C\u73B0\u5728\u8BB2\u53EF\u6709\u70B9\u665A\u4E86\u3002\u300D</p><p>\u8BF4\u8BDD\u95F4\uFF0C\u6076\u72D7\u518D\u5EA6\u6251\u4E86\u8FC7\u6765\uFF0C\u524D\u80A2\u628A\u5931\u53BB\u62B5\u6297\u80FD\u529B\u7684\u5C11\u5973\u8E29\u5728\u811A\u4E0B\uFF0C\u51B2\u5929\u568E\u53EB\u4E86\u4E00\u58F0\uFF0C\u6446\u51FA\u4E00\u5E45\u80DC\u5229\u8005\u7684\u59FF\u6001\u3002\u5B83\u9F3B\u5B50\u55C5\u4E86\u55C5\uFF0C\u53C8\u56F4\u7740\u91D1\u82B1\u8F6C\u4E86\u51E0\u5708\uFF0C\u597D\u50CF\u611F\u89C9\u5230\u4EC0\u4E48\uFF0C\u4E00\u4E0B\u5B50\u5174\u594B\u8D77\u6765\uFF0C\u4F4E\u4E0B\u5934\u5728\u5C11\u5973\u7684\u80EF\u95F4\u90E8\u4F4D\u4F7F\u52B2\u55C5\u3002</p><p>\u573A\u8FB9\u6709\u4EBA\u5927\u53EB\u8D77\u6765\u3002</p><p>\u300C\u641E\u5979\uFF0C\u641E\u5979\uFF01\u300D</p><p>\u6076\u72D7\u4F3C\u4E4E\u5728\u4F17\u4EBA\u7684\u9F13\u52B1\u4E4B\u4E0B\u8D8A\u53D1\u6625\u60C5\u52C3\u53D1\uFF0C\u4E5F\u4E0D\u7406\u4F1A\u91D1\u82B1\u7684\u81C0\u808C\u8FD8\u5728\u6DCC\u7740\u9C9C\u8840\uFF0C\u72D7\u722A\u5B50\u5C06\u660F\u8FF7\u7684\u5C11\u5973\u6252\u7FFB\u4E2A\u8FB9\uFF0C\u6446\u6210\u4FEF\u5367\u7684\u59FF\u5F0F\uFF0C\u5750\u4E0B\u8EAB\u5B50\uFF0C\u8981\u4ECE\u540E\u9762\u5C06\u72D7\u9E21\u5DF4\u6345\u8FDB\u53BB\u3002</p><p>\u773C\u89C1\u72D7\u5978\u4EBA\u7684\u4E00\u51FA\u597D\u620F\u5C31\u8981\u4E0A\u6F14\u4E86\uFF0C\u4F17\u4EBA\u770B\u5F97\u6FC0\u52A8\u96BE\u5B89\uFF0C\u72C2\u547C\u4E71\u53EB\uFF0C\u7FA4\u9B54\u4E71\u821E\u3002</p><p>\u53EF\u60DC\u91D1\u82B1\u5944\u5944\u4E00\u606F\uFF0C\u8EAB\u5B50\u626D\u66F2\uFF0C\u72D7\u9E21\u5DF4\u6839\u672C\u627E\u4E0D\u7740\u8FDB\u53BB\u7684\u6D1E\u53E3\uFF0C\u6076\u72D7\u6025\u5F97\u62FF\u5634\u54AC\uFF0C\u62FF\u5934\u9876\uFF0C\u722A\u5B50\u6320\uFF0C\u628A\u7389\u80CC\u4E0A\u7684\u8089\u54AC\u5F97\u7A00\u70C2\uFF0C\u53EF\u601C\u91D1\u82B1\u53D8\u6210\u8840\u4EBA\u4F3C\u7684\uFF0C\u65E0\u58F0\u65E0\u606F\u3002</p><p>\u6076\u72D7\u6025\u706B\u653B\u5FC3\uFF0C\u7D22\u6027\u4E00\u53E3\u54AC\u65AD\u4E86\u91D1\u82B1\u7684\u5589\u7BA1\uFF0C\u4E00\u7F15\u9999\u9B42\u7EC8\u5F97\u5B89\u606F\u3002</p><p>\u300C\u91D1\u82B1\u2026\u2026\u300D</p><p>\u6D77\u68E0\u53D1\u51FA\u4E00\u58F0\u6495\u5FC3\u88C2\u80BA\u5730\u54ED\u558A\uFF0C\u6655\u6B7B\u8FC7\u53BB\u3002</p><p>\u300C\u5192\u610F\u601D\uFF0C\u5192\u770B\u5934\u3002\u300D</p><p>\u4F17\u4EBA\u53D1\u51FA\u4E0D\u6EE1\u8DB3\u7684\u62B1\u6028\u58F0\u3002</p><p>\u767D\u5929\u5FB7\u63D0\u6765\u4E00\u6876\u51B7\u6C34\uFF0C\u4ECE\u5979\u5934\u4E0A\u6DCB\u4E0B\u53BB\uFF0C\u5C06\u5979\u5F04\u9192\uFF0C\u8BF4\uFF1A\u300C\u60F3\u901A\u4E86\u4E48\uFF0C\u4E0A\u4E0D\u4E0A\uFF1F\u300D</p><p>\u6D77\u68E0\u7684\u77B3\u5B50\u91CC\u5145\u6EE1\u4EC7\u6068\u3002</p><p>\u767D\u5929\u5FB7\u9053\uFF1A\u300C\u5230\u65F6\u4F60\u4F1A\u6C42\u8001\u5B50\u4E0A\u3002\u300D</p><p>\u6709\u4EBA\u64CD\u7EB5\u673A\u5173\uFF0C\u5C06\u6076\u72D7\u7F69\u4F4F\uFF0C\u628A\u91D1\u82B1\u7684\u5C38\u4F53\u62D6\u4E86\u51FA\u53BB\u3002</p><p>\u4E0D\u4E45\uFF0C\u53C8\u4E00\u4E2A\u540C\u6837\u8D64\u88F8\u7684\u5973\u5B50\u88AB\u63A8\u5230\u4E86\u573A\u4E2D\u592E\uFF0C\u5979\u88AB\u521A\u624D\u7684\u60E8\u5267\u5413\u5F97\u8138\u8272\u5237\u767D\uFF0C\u4EE5\u81F3\u4E8E\u90FD\u5FD8\u8BB0\u4E86\u7F9E\u803B\u53BB\u6440\u4F4F\u4E0B\u8EAB\u548C\u5976\u5B50\uFF0C\u5446\u7ACB\u534A\u664C\uFF0C\u7A81\u7136\u6655\u5012\u5728\u5730\u3002</p><p>\u5305\u62EC\u6D77\u68E0\u5728\u5185\uFF0C\u51E0\u4E4E\u6240\u6709\u4EBA\u90FD\u53D1\u51FA\u60CA\u547C\u58F0\uFF0C\u522B\u4EBA\u60CA\u7684\u662F\u8FD9\u4E2A\u599E\u7ADF\u4E0E\u521A\u6B7B\u7684\u91D1\u82B1\u4ECE\u4E00\u4E2A\u6A21\u5B50\u91CC\u523B\u51FA\u6765\u7684\uFF0C\u4E0D\u77E5\u9053\u7684\u771F\u4EE5\u4E3A\u6B7B\u9B3C\u8FD8\u9B42\u3002</p><p>\u6D77\u68E0\u60CA\u7684\u662F\u7559\u5B88\u5C71\u5BE8\u7684\u94F6\u53F6\u7ADF\u4E5F\u843D\u5165\u4E86\u654C\u4EBA\u4E4B\u624B\uFF01</p><p>\u96BE\u9053\u5C71\u5BE8\u51FA\u4E86\u610F\u5916\uFF1F</p><p>\u767D\u5929\u5FB7\u770B\u51FA\u4E86\u5979\u7684\u5FC3\u601D\uFF0C\u5F97\u610F\u5730\u8BF4\u9053\uFF1A\u300C\u6CA1\u9519\uFF0C\u4F60\u90A3\u72D7\u7A9D\u8BA9\u8001\u5B50\u527F\u5F97\u5E72\u5E72\u51C0\u51C0\uFF0C\u6B7B\u5728\u6B7B\uFF0C\u6293\u7684\u6293\u3002\u4E0D\u4FE1\uFF1F\u627E\u4E2A\u4EBA\u51FA\u6765\u7ED9\u4F60\u89C1\u89C1\u3002\u300D</p><p>\u4ED6\u6697\u793A\u4E86\u4E00\u4E0B\uFF0C\u674E\u8D35\u5E26\u7740\u4E00\u4E2A\u4EBA\u8D70\u5230\u8DDF\u524D\u3002</p><p>\u4E8C\u559C\u5B50\uFF01</p><p>\u6D77\u68E0\u4E00\u4E0B\u5B50\u5168\u660E\u767D\u4E86\uFF0C\u5550\u9053\uFF0C\u300C\u53DB\u5F92\uFF01\u300D</p><p>\u4E8C\u559C\u5B50\u8D77\u5148\u8FD8\u6709\u4E9B\u754F\u7F29\uFF0C\u5F85\u89C1\u5230\u6D77\u68E0\u65E0\u52A9\u7684\u7F9E\u803B\u6A21\u6837\uFF0C\u53C8\u88AB\u8FCE\u5934\u9A82\u4E86\u4E00\u53E5\uFF0C\u4E00\u4E0B\u5B50\u52FE\u8D77\u4E86\u6CFC\u76AE\u672C\u6027\uFF0C\u53D8\u4E86\u4E00\u526F\u6025\u8272\u6A21\u6837\uFF0C\u518D\u4E5F\u627E\u4E0D\u5230\u5F80\u65E5\u7684\u4E49\u6C14\uFF0C\u6B7B\u7785\u7740\u6D77\u68E0\u90A3\u9971\u6EE1\u575A\u633A\u7684\u5976\u5B50\u563F\u563F\u4E00\u7B11\uFF0C\u300C\u68E0\u59D0\uFF0C\u4E0D\u8981\u602A\u6211\uFF0C\u4F60\u505A\u5F97\u521D\u4E00\uFF0C\u5144\u5F1F\u5C31\u505A\u5F97\u5341\u4E94\uFF0C\u4F60\u4E0D\u4EC1\u5728\u5148\uFF0C\u6211\u4E0D\u4E49\u5728\u540E\uFF0C\u626F\u5E73\u4E86\u3002\u300D</p><p>\u6D77\u68E0\u6012\u9053\uFF1A\u300C\u6211\u53EA\u602A\u81EA\u5DF1\u6536\u4E86\u4E00\u6761\u72D7\u3002\u300D</p><p>\u4E8C\u559C\u5B50\u602A\u58F0\u602A\u6C14\u5730\u8BF4\uFF0C\u300C\u6211\u770B\u5F85\u4F1A\uFF0C\u4F60\u8FDE\u72D7\u90FD\u4E0D\u5982\u3002\u300D</p><p>\u767D\u5929\u5FB7\u4E0D\u8010\u70E6\u4E86\uFF0C\u53EB\u4E8C\u559C\u5B50\u9000\u5230\u4E00\u8FB9\uFF0C\u9053\uFF1A\u300C\u5B89\u51E4\u513F\uFF0C\u770B\u5728\u6211\u4EEC\u591A\u5E74\u7684\u4EA4\u60C5\u4EFD\u4E0A\u4E0D\u96BE\u4E3A\u4F60\uFF0C\u53EA\u8981\u4F60\u8BF4\u4E2A\u4E0D\u5B57\uFF0C\u8001\u5B50\u5C31\u4EFB\u4F60\u5728\u573A\u8FB9\u770B\uFF0C\u770B\u90A3\u4E9B\u4E48\u5B50\u91D1\u554A\u94F6\u554A\u674F\u554A\u4E4B\u7C7B\u7684\uFF0C\u4E00\u4E2A\u4E2A\u966A\u72D7\u73A9\uFF0C\u53CD\u6B63\u6B7B\u4E00\u4E2A\u8FD8\u6709\u4E00\u5927\u7968\uFF0C\u4E0D\u7740\u6025\u3002\u300D</p><p>\u6D77\u68E0\u95ED\u4E0A\u773C\uFF0C\u6CEA\u6C34\u6F7A\u6F7A\u800C\u4E0B\uFF0C\u9053\uFF1A\u300C\u628A\u5979\u4EEC\u90FD\u653E\u4E86\u3002\u300D</p><p>\u767D\u5929\u5FB7\u51B7\u7B11\u9053\uFF1A\u300C\u4F60\u6709\u8D44\u683C\u548C\u8001\u5B50\u8C08\u6761\u4EF6\u5417\uFF0C\u4F60\u4E0A\uFF0C\u5979\u4EEC\u5C31\u4E0B\uFF0C\u4F60\u4E0D\u4E0A\uFF0C\u5979\u4EEC\u4E0A\u3002\u300D</p><p>\u6D77\u68E0\u7684\u4FCF\u8138\u56E0\u75DB\u82E6\u800C\u53D8\u5F62\uFF0C\u7EC8\u4E8E\u5C06\u5934\u53D1\u5F80\u540E\u4E00\u7529\uFF0C\u6BC5\u7136\u9053\uFF1A\u300C\u6211\u4E0A\u3002\u300D</p><p>\u767D\u5929\u5FB7\u9F13\u638C\uFF0C\u5927\u58F0\u9053\uFF1A\u300C\u5144\u5F1F\u4EEC\u770B\u597D\u7F57\uFF0C\u9ED1\u51E4\u51F0\u4EB2\u81EA\u4E0A\u9635\uFF0C\u4EBA\u72D7\u5927\u6218\u3002\u300D</p><p>\u8FD9\u4E00\u6B21\u7684\u5439\u547C\u58F0\u6BD4\u4E0A\u6B21\u5927\u4E86\u6570\u500D\u4E0D\u6B62\u3002</p><p>\u6D77\u68E0\u9759\u9759\u5730\u7AD9\u5728\u573A\u5730\u4E2D\u592E\uFF0C\u9ED1\u53D1\u6325\u6563\u4E0B\u6765\uFF0C\u5728\u706B\u5149\u7684\u6C90\u6D74\u4E0B\uFF0C\u5979\u50CF\u4E00\u5C0A\u8D64\u8EAB\u7684\u5973\u795E\uFF0C\u5B8C\u7F8E\u65E0\u7455\uFF0C\u5065\u7F8E\u65E0\u5339\uFF0C\u662F\u529B\u4E0E\u7F8E\u7684\u5316\u8EAB\uFF0C\u4E5F\u662F\u60B2\u6124\u4E0E\u4EC7\u6068\u7684\u6DF7\u5408\u3002</p><p>\u5979\u4E0E\u7B3C\u4E2D\u7684\u6076\u72D7\u5BF9\u89C6\u7740\uFF0C\u5F7C\u6B64\u770B\u5230\u4E86\u5BF9\u65B9\u7684\u6740\u6C14\uFF0C\u5979\u8981\u7528\u8D64\u624B\u7A7A\u62F3\u6740\u6389\u8FD9\u5934\u6076\u72D7\uFF0C\u4E3A\u51A4\u6B7B\u7684\u91D1\u82B1\u62A5\u4EC7\u3002</p><p>\u7167\u4F8B\u6709\u4EBA\u7AEF\u7740\u4E00\u76C6\u6C34\u8FC7\u6765\uFF0C\u51B2\u7740\u5979\u7684\u4E0B\u8EAB\u6CFC\u53BB\uFF0C\u597D\u6D53\u70C8\u7684\u8165\u9A9A\u5F02\u5473\uFF0C\u5979\u65B9\u624D\u660E\u767D\u539F\u6765\u662F\u72D7\u5C3F\u3002</p><p>\u4E00\u58F0\u9523\u54CD\uFF0C\u767D\u5929\u5FB7\u5174\u594B\u5730\u9AD8\u558A\u3002</p><p>\u300C\u5F00\u95F8\uFF01\u300D</p><p>\u7B3C\u5B50\u540A\u8D77\u3002</p><p>\u6076\u72D7\u547C\u5730\u7A9C\u4E86\u51FA\u6765\uFF0C\u8FD9\u4E00\u6B21\uFF0C\u5B83\u611F\u89C9\u5230\u65B0\u5BF9\u624B\u4E0D\u540C\u5BFB\u5E38\uFF0C\u6CA1\u6709\u4E0A\u6B21\u7684\u56A3\u5F20\uFF0C\u79BB\u6D77\u68E0\u8FDC\u8FDC\u5730\uFF0C\u8B66\u60D5\u5730\u6253\u91CF\u7740\u5979\u3002</p><p>\u50F5\u6301\u4E86\u4E00\u9635\uFF0C\u6D77\u68E0\u8C28\u614E\u5730\u79FB\u52A8\u7740\u811A\u6B65\uFF0C\u671D\u6076\u72D7\u9760\u8FD1\u3002</p><p>\u5BF9\u4ED8\u5C71\u91CC\u7684\u91CE\u517D\u6D77\u68E0\u9887\u6709\u7ECF\u9A8C\uFF0C\u4EB2\u624B\u5C31\u6253\u8FC7\u4E0D\u5C11\u91CE\u732A\uFF0C\u6597\u4E00\u652F\u6076\u72D7\u81EA\u7136\u4E0D\u5728\u8BDD\u4E0B\uFF0C\u53EF\u4E00\u5219\u5979\u4ECE\u672A\u7ECF\u5386\u8FC7\u5982\u6B64\u7F9E\u803B\u7684\u73AF\u5883\uFF0C\u4E00\u4E1D\u4E0D\u6302\u5730\u8BA9\u4EBA\u73AF\u4F3A\uFF0C\u96BE\u514D\u5206\u5FC3\uFF1B\u4E8C\u5219\u624B\u65E0\u5BF8\u94C1\uFF0C\u7528\u4E00\u53CC\u8089\u638C\u5BF9\u4ED8\u51F6\u6027\u5927\u53D1\u7684\u6076\u72D7\u94A2\u7259\uFF0C\u7684\u786E\u96BE\u5EA6\u592A\u9AD8\uFF1B\u4E09\u5219\u7EDD\u98DF\u4E86\u4E00\u65E5\uFF0C\u997F\u5F97\u524D\u80F8\u8D34\u540E\u80CC\uFF0C\u53EA\u6709\u901F\u6218\u901F\u51B3\uFF0C\u54EA\u6709\u529B\u6C14\u8FC7\u591A\u5730\u7EA0\u7F20\uFF1F</p><p>\u50F5\u5C40\u5F88\u5FEB\u6253\u7834\uFF0C\u8FD8\u662F\u6076\u72D7\u5FCD\u8010\u4E0D\u4F4F\uFF0C\u7387\u5148\u51B2\u4E86\u8FC7\u6765\uFF0C\u5230\u4E86\u8DDF\u524D\u5F80\u4E0A\u8DF3\u8D77\uFF0C\u76F4\u5954\u5589\u7BA1\uFF0C\u8FC5\u731B\u4E4B\u6781\u3002</p><p>\u7535\u95EA\u4E4B\u95F4\uFF0C\u6D77\u68E0\u6025\u6446\u5934\u8EB2\u8FC7\u4E00\u52AB\uFF0C\u5316\u638C\u4E3A\u5200\u671D\u6076\u72D7\u7684\u8EAB\u5B50\u5207\u53BB\uFF0C\u8FD9\u6076\u72D7\u53CD\u5E94\u591F\u5FEB\uFF0C\u7A7A\u4E2D\u6765\u4E86\u4E00\u4E2A\u7FFB\u8EAB\uFF0C\u7A33\u7A33\u5730\u843D\u5728\u5730\u4E0A\u3002</p><p>\u8D64\u88F8\u5973\u5927\u6218\u6076\u517D\uFF0C\u7B2C\u4E00\u4E2A\u56DE\u5408\u5C31\u7CBE\u5F69\u4E07\u5206\uFF0C\u4F17\u4EBA\u5927\u9971\u773C\u798F\uFF0C\u54C4\u7136\u9F13\u638C\uFF0C\u6240\u6709\u4EBA\u7684\u773C\u775B\u90FD\u6B7B\u6B7B\u5730\u76EF\u5728\u6D77\u68E0\u4E0A\u4E0B\u6D8C\u52A8\u7684\u6CE2\u5CF0\u548C\u82E5\u9690\u82E5\u73B0\u7684\u6EAA\u8C37\u4E0A\u9762\uFF0C\u4E0D\u7981\u4E00\u4E2A\u4E2A\u8840\u8109\u8D32\u5F20\uFF0C\u90FD\u611F\u4E0D\u865A\u6B64\u884C\uFF0C\u6068\u4E0D\u5F97\u8FD9\u573A\u602A\u5F02\u7684\u6BD4\u8D5B\u8D8A\u4E45\u8D8A\u597D\u3002</p><p>\u6D77\u68E0\u548C\u6076\u72D7\u5728\u8F83\u91CF\u4E2D\u90FD\u53D1\u73B0\u4F4E\u4F30\u4E86\u5BF9\u65B9\uFF0C\u7B2C\u4E8C\u4E2A\u56DE\u5408\u76F8\u6301\u66F4\u4E45\uFF0C\u6D77\u68E0\u7A81\u7136\u611F\u89C9\u4E0B\u8EAB\u5947\u75D2\uFF0C\u5176\u5B9E\u5979\u4E0D\u660E\u767D\uFF0C\u767D\u5929\u5FB7\u7ED9\u5979\u4E0B\u7684\u8FD9\u79CD\u6162\u6027\u6625\u836F\u6700\u662F\u5BB3\u4EBA\uFF0C\u975E\u5F97\u6709\u4EBA\u6216\u662F\u81EA\u5DF1\u5F04\u51FA\u9AD8\u6F6E\u6765\u628A\u706B\u6CC4\u6389\uFF0C\u5426\u5219\u8D8A\u662F\u5FCD\u8010\uFF0C\u8D8A\u662F\u96BE\u53D7\uFF0C\u65F6\u95F4\u8D8A\u957F\uFF0C\u6414\u75D2\u8D8A\u5389\u5BB3\u3002</p><p>\u4F46\u4E00\u65E6\u5979\u4E60\u60EF\u81EA\u6170\uFF0C\u5374\u53C8\u4F1A\u5C0A\u4E25\u5D29\u6E83\uFF0C\u843D\u5165\u4E07\u52AB\u4E0D\u590D\u7684\u6DF1\u6E0A\u3002</p><p>\u6240\u4EE5\u65E0\u8BBA\u5979\u600E\u4E48\u505A\uFF0C\u90FD\u9003\u8131\u4E0D\u4E86\u767D\u5929\u5FB7\u8FD9\u6076\u68CD\u7684\u7B97\u8BA1\u3002</p><p>\u521A\u624D\u767D\u5929\u5FB7\u6709\u610F\u6311\u8D77\u5979\u7684\u6B32\u706B\uFF0C\u8BA9\u5979\u5728\u6B64\u65F6\u7206\u53D1\uFF0C\u9634\u9669\u4E4B\u6781\uFF0C\u53EF\u6D77\u68E0\u5DF2\u6CA1\u6709\u529F\u592B\u53BB\u60F3\u8FD9\u4E48\u591A\uFF0C\u53EA\u6709\u82E6\u82E6\u6491\u7740\uFF0C\u53CC\u817F\u4E0D\u7981\u7EDE\u5728\u4E00\u8D77\uFF0C\u773C\u524D\u6A21\u7CCA\uFF0C\u6B65\u5B50\u79FB\u52A8\u4E5F\u53D8\u5F97\u8FDF\u949D\u8D77\u6765\u3002</p><p>\u72E1\u733E\u7684\u6076\u72D7\u53D1\u73B0\u4E86\u7834\u7EFD\uFF0C\u5DE6\u53F3\u6251\u4E86\u4E00\u4E0B\uFF0C\u98DE\u5FEB\u5730\u7ED5\u5230\u4E86\u6D77\u68E0\u7684\u8EAB\u540E\uFF0C\u53C8\u60F3\u91CD\u65BD\u6545\u8BA1\u3002</p><p>\u8FD9\u4E0B\u5374\u4E0A\u4E86\u6D77\u68E0\u7684\u5708\u5957\uFF0C\u5979\u6709\u610F\u5356\u4E86\u4E2A\u5173\u5B50\uFF0C\u5F80\u524D\u8DE8\u4E86\u4E00\u5927\u6B65\uFF0C\u8BA9\u6076\u72D7\u54AC\u4E86\u4E2A\u7A7A\uFF0C\u5F85\u5F97\u6076\u72D7\u53BB\u52BF\u5C06\u5C3D\uFF0C\u53CD\u8EAB\u4E00\u811A\u8E22\u5728\u6076\u72D7\u7684\u5C0F\u8179\u4E0A\uFF0C\u8FD9\u4E00\u8E22\u6765\u5F97\u91CD\uFF0C\u6709\u529B\u7684\u817F\u52B2\u8E22\u5F97\u6076\u72D7\u60E8\u53EB\u4E00\u58F0\uFF0C\u98DE\u4E86\u51FA\u53BB\uFF0C\u6D77\u68E0\u9664\u6076\u52A1\u5C3D\uFF0C\u5728\u6076\u72D7\u6CA1\u6765\u53CA\u5598\u606F\u4E4B\u9645\uFF0C\u5C31\u94B3\u4F4F\u5B83\u7684\u9888\u5B50\uFF0C\u624B\u81C2\u6CE8\u6EE1\u529B\u91CF\uFF0C\u5C31\u5F85\u4E00\u4E0B\u626D\u8F6C\u72D7\u5934\u5F04\u6B7B\u5B83\u3002</p><p>\u300C\u4F4F\u624B\uFF01\u300D</p><p>\u573A\u5916\u4E00\u58F0\u65AD\u559D\uFF0C\u767D\u5929\u5FB7\u62FF\u67AA\u6307\u7740\u94F6\u53F6\u7684\u5934\u3002</p><p>\u300C\u628A\u72D7\u653E\u5F00\uFF0C\u5426\u5219\u8001\u5B50\u4E00\u67AA\u5D29\u4E86\u5979\u3002\u300D</p><p>\u6D77\u68E0\u60B2\u6124\u4E4B\u6781\uFF0C\u53C8\u4E0D\u6562\u4E0D\u4ECE\uFF0C\u624B\u52B2\u7A0D\u677E\uFF0C\u6076\u72D7\u5C31\u6D3B\u8F6C\u4E86\u8FC7\u6765\uFF0C\u53CD\u53E3\u54AC\u5728\u6D77\u68E0\u7684\u88F8\u817F\u4E0A\uFF0C\u6D77\u68E0\u60E8\u547C\u4E00\u58F0\uFF0C\u52C9\u529B\u6323\u624E\u5F00\u6765\uFF0C\u4F46\u5DF2\u662F\u7259\u75D5\u5B9B\u7136\uFF0C\u9C9C\u8840\u8FF8\u5F00\uFF0C\u75DB\u4E0D\u6B32\u751F\u3002</p><p>\u6B64\u65F6\uFF0C\u6D77\u68E0\u4E0B\u8EAB\u7684\u9A9A\u75D2\u5DF2\u8513\u5EF6\u5230\u4E86\u5168\u8EAB\uFF0C\u521A\u624D\u96C6\u4E2D\u7CBE\u529B\u7684\u6700\u540E\u4E00\u51FB\u89C6\u4E3A\u65E0\u6548\u4E4B\u540E\uFF0C\u6700\u6709\u529B\u91CF\u7684\u817F\u90E8\u4E5F\u53D7\u4E86\u91CD\u4F24\uFF0C\u4E00\u8FB9\u8981\u4E0E\u5185\u5FC3\u7684\u714E\u71AC\u4F5C\u6597\u4E89\uFF0C\u4E00\u8FB9\u5916\u4F24\u6D41\u8840\u4E0D\u6B62\uFF0C\u518D\u4E5F\u7EC4\u7EC7\u4E0D\u8D77\u6709\u6548\u7684\u8FDB\u653B\uFF0C\u4E00\u76F4\u88AB\u52A8\u5730\u9632\u5FA1\u3002</p><p>\u518D\u597D\u7684\u9632\u5FA1\u4E5F\u6709\u653B\u7834\u7684\u65F6\u5019\uFF0C\u5728\u6D77\u68E0\u4E00\u4E0B\u5931\u795E\u95F4\uFF0C\u53EA\u89C1\u773C\u524D\u9ED1\u5F71\u4E4D\u73B0\uFF0C\u98CE\u58F0\u54CD\u8D77\uFF0C\u4E00\u5934\u5927\u7269\u5C06\u5979\u91CD\u91CD\u5728\u538B\u5728\u5730\u4E0A\uFF0C\u4E24\u652F\u524D\u80A2\u8E0F\u5728\u5979\u67D4\u8F6F\u7684\u4E24\u5CF0\u4E0A\uFF0C\u540E\u80A2\u7AD9\u5728\u5979\u7684\u80EF\u95F4\uFF0C\u53D1\u51FA\u80DC\u5229\u8005\u7684\u957F\u957F\u55F7\u53EB\u3002</p><p>\u300C\u4E0D\uFF01\u300D</p><p>\u5979\u773C\u524D\u91D1\u661F\u76F4\u5192\uFF0C\u5F77\u4F5B\u770B\u5230\u4E86\u6B7B\u795E\u7FE9\u7FE9\u800C\u6765\u3002</p><p>\u4F46\u662F\uFF0C\u6076\u72D7\u5E76\u4E0D\u60F3\u6740\u5979\uFF0C\u800C\u662F\u628A\u5979\u6252\u62C9\u8FC7\u6765\uFF0C\u50CF\u4E4B\u524D\u5BF9\u4ED8\u91D1\u82B1\u90A3\u6837\u8981\u5978\u5979\u3002</p><p>\u6D77\u68E0\u7684\u529B\u6C14\u5DF2\u7528\u5C3D\uFF0C\u5C31\u7B97\u660E\u767D\u8FD9\u6076\u72D7\u8981\u5E72\u4EC0\u4E48\uFF0C\u4E5F\u6CA1\u6709\u529E\u6CD5\u53CD\u6297\uFF0C\u640F\u6597\u4E2D\u8EAB\u4E0A\u53C8\u6709\u591A\u5904\u54AC\u4F24\uFF0C\u7EC8\u7A76\u5982\u72D7\u4E4B\u610F\u88AB\u8FEB\u7FFB\u8F6C\u4E86\u8FC7\u6765\u3002</p><p>\u6076\u72D7\u4E0D\u505C\u5730\u62E8\u5F04\u7740\u5979\u7684\u5C41\u80A1\uFF0C\u5FC3\u6025\u5982\u711A\uFF0C\u53EF\u6D77\u68E0\u5C1A\u8FD8\u7559\u4E86\u4E00\u7EBF\u795E\u667A\uFF0C\u62B5\u6B7B\u4E0D\u4ECE\u3002</p><p>\u773C\u770B\u53C8\u4E00\u573A\u60E8\u5267\u8981\u4E0A\u6F14\uFF0C\u767D\u5929\u5FB7\u5BF9\u674E\u8D35\u8BF4\uFF1A\u300C\u53BB\u5E2E\u5E2E\u5B83\u3002\u300D</p><p>\u8FDB\u573A\u6765\u4E24\u4E2A\u4EBA\uFF0C\u6349\u4F4F\u6D77\u68E0\u7684\u624B\u811A\uFF0C\u786C\u662F\u5F80\u5979\u7684\u5C0F\u8179\u4E0B\u585E\u8FDB\u4E00\u6839\u5927\u5706\u6728\uFF0C\u8BA9\u5979\u7684\u5C41\u80A1\u9AD8\u9AD8\u7FD8\u8D77\u6765\uFF0C\u6076\u72D7\u4E24\u80A2\u642D\u5728\u5979\u7684\u7389\u80CC\u4E0A\uFF0C\u5F97\u610F\u5730\u53EB\u4E86\u4E00\u58F0\uFF0C\u5C06\u7C97\u5927\u7684\u72D7\u9E21\u5DF4\u72E0\u72E0\u5730\u6324\u8FDB\u6D77\u68E0\u72ED\u7A84\u7684\u8C37\u9053\u4E4B\u4E2D\u3002</p><p>\u300C\u6885\u795E\u554A\u2026\u2026\u300D\u6D77\u68E0\u7981\u4E0D\u4F4F\u53D1\u51FA\u6495\u5FC3\u88C2\u80BA\u7684\u5C16\u53EB\u3002</p><p>\u5728\u72D7\u7684\u62BD\u63D2\u4E2D\uFF0C\u6D77\u68E0\u88AB\u6625\u836F\u5F7B\u5E95\u8FF7\u5931\u4E86\u81EA\u6211\uFF0C\u5468\u8EAB\u88AB\u6B32\u706B\u711A\u70E7\uFF0C\u5C31\u50CF\u5728\u6781\u75DB\u4E0E\u6781\u4E50\u4EA4\u754C\u7684\u4E16\u754C\uFF0C\u4E00\u65F6\u6E05\u9192\uFF0C\u4E00\u65F6\u80E1\u6D82\uFF0C\u4E0D\u77E5\u8EAB\u5728\u4F55\u65B9\uFF0C\u4E0D\u77E5\u81EA\u5DF1\u662F\u4F55\u4EBA\uFF0C\u751A\u81F3\u5728\u90A3\u8840\u8FF9\u6591\u6591\u7684\u8138\u4E0A\uFF0C\u8FD8\u6302\u51FA\u4E00\u4E1D\u8BE1\u5F02\u7684\u5FAE\u7B11\u3002</p><p>\u300C\u68E0\u59D0\uFF01\u300D\u521A\u521A\u82CF\u9192\u7684\u94F6\u53F6\u6CEA\u6D41\u6EE1\u9762\u3002</p><p>\u300C\u65E0\u804A\u3002\u300D</p><p>\u5218\u6EA2\u4E4B\u518D\u4E5F\u6309\u637A\u4E0D\u4F4F\uFF0C\u6124\u6124\u7136\u62C2\u8896\u800C\u53BB\u3002</p><p>\u767D\u5929\u5FB7\u7B11\u7740\u76EE\u9001\u4ED6\uFF0C\u610F\u5473\u6DF1\u957F\u3002\u56DE\u671B\u573A\u4E2D\uFF0C\u5583\u5583\u81EA\u8BED\u9053\uFF1A\u300C\u8001\u7238\uFF0C\u4F60\u53EF\u4EE5\u5B89\u606F\u4E86\uFF0C\u513F\u5B50\u4E0D\u4F46\u8981\u8BA9\u5979\u88AB\u72D7\u5978\uFF0C\u8FD8\u8981\u8BA9\u5979\u8FD9\u8F88\u5B50\u6C42\u751F\u4E0D\u5F97\uFF0C\u6C42\u6B7B\u4E0D\u80FD\uFF0C\u540E\u6094\u6789\u505A\u5973\u4EBA\u3002\u300D</p>',132);function t(i,o){return r}var _=a(e,[["render",t]]);export{_ as default};
