import{e as p}from"./app.630af5f2.js";import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";const e={},r=p('<h1 id="\u7B2C\u4E09\u7AE0-\u7ED1\u7968" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E09\u7AE0-\u7ED1\u7968" aria-hidden="true">#</a> \u7B2C\u4E09\u7AE0 \u7ED1\u7968</h1><p>\u591C\u5E55\u7B3C\u7F69\u4E86\u4E09\u6E58\u5927\u5730\uFF0C\u6D77\u68E0\u4E09\u4EBA\u501F\u591C\u8272\u63A9\u62A4\uFF0C\u518D\u5EA6\u6F5C\u56DE\u4E86\u57CE\u91CC\uFF0C\u6D77\u68E0\u72EC\u81EA\u884C\u52A8\u4E86\u4E00\u4E2A\u65F6\u8FB0\uFF0C\u65B9\u56DE\u6765\u5E26\u4E0A\u4E8C\u59DD\u3002</p><p>\u91D1\u82B1\u53D1\u73B0\u5979\u4EEC\u53BB\u7684\u65B9\u5411\u5E76\u4E0D\u662F\u4FDD\u5B89\u56E2\uFF0C\u4E0D\u7981\u95EE\u9053\uFF1A\u300C\u6211\u4EEC\u4E0D\u662F\u8981\u53BB\u6551\u9752\u7EA2\u59D0\u5417\uFF1F\u300D</p><p>\u6D77\u68E0\u4E00\u76F4\u4E0D\u4F5C\u58F0\uFF0C\u53EA\u5E26\u7740\u5979\u4EEC\u6765\u5230\u4E00\u5904\u5927\u5B85\u7684\u5899\u8DDF\u4E0B\uFF0C\u65B9\u9053\uFF1A\u300C\u654C\u4EBA\u52BF\u5927\uFF0C\u4E0D\u80FD\u660E\u53D6\uFF0C\u53EA\u597D\u51FA\u6B64\u4E0B\u7B56\u3002\u300D</p><p>\u5979\u6307\u7740\u9662\u5185\uFF1A\u300C\u8FD9\u91CC\u662F\u53BF\u957F\u7684\u79C1\u5B85\u3002\u300D</p><p>\u94F6\u53F6\u604D\u7136\u8BF4\uFF0C\u300C\u6211\u660E\u767D\u4E86\uFF0C\u6211\u4EEC\u8981\u7ED1\u67B6\u53BF\u957F\uFF0C\u4EE5\u4EBA\u6362\u4EBA\u3002\u300D</p><p>\u6D77\u68E0\u8D5E\u8BB8\u9053\uFF1A\u300C\u8111\u7B4B\u4E0D\u9519\uFF0C\u4E0D\u8FC7\u4E0D\u662F\u7ED1\u67B6\u53BF\u957F\uFF0C\u53BF\u957F\u7ED1\u4E86\u5C31\u6CA1\u7528\u4E86\uFF0C\u6211\u4EEC\u8981\u7ED1\u7684\u662F\u4ED6\u65B0\u592A\u592A\u7684\u7968\uFF0C\u6211\u4EEC\u4E0D\u662F\u89C1\u8FC7\u5979\u4E86\u5417\u3002\u6211\u8FD8\u6253\u542C\u5230\uFF0C\u53BF\u957F\u4E0B\u5348\u52A8\u8EAB\u53BB\u4E86\u7701\u5E9C\uFF0C\u800C\u4E14\u4E3A\u4E86\u5728\u4FDD\u5B89\u56E2\u8BBE\u5708\u5B50\u6293\u6211\u4EEC\uFF0C\u9632\u5B88\u7684\u536B\u5175\u8FD8\u8C03\u8D70\u4E86\u51E0\u4E2A\uFF0C\u6B64\u5730\u624D\u771F\u6B63\u662F\u677E\u61C8\u3002\u300D</p><p>\u91D1\u82B1\u9AD8\u5174\u4E86\u8D77\u6765\uFF0C\u4E00\u628A\u62B1\u4F4F\u6D77\u68E0\uFF0C\u9053\uFF1A\u300C\u771F\u662F\u6885\u795E\u76F8\u52A9\uFF0C\u68E0\u59D0\uFF0C\u5BF9\u4E0D\u8D77\uFF0C\u6211\u4EEC\u8FD8\u8BF4\u4F60\u6CA1\u4FE1\u5FC3\u4E86\uFF0C\u5176\u5B9E\u4F60\u6C38\u8FDC\u662F\u6211\u4EEC\u6700\u4E86\u4E0D\u8D77\u7684\u5927\u59D0\u5934\u3002\u300D</p><p>\u6D77\u68E0\u51B7\u5CFB\u4E86\u5F88\u4E45\u7684\u8138\u4E0A\u603B\u7B97\u7EFD\u5F00\u4E86\u4E00\u4E1D\u5FAE\u7B11\uFF0C\u53EA\u662F\u6709\u70B9\u82E6\u6DA9\uFF0C\u53EE\u5631\u9053\uFF1A\u300C\u8BB0\u4F4F\uFF0C\u53EA\u80FD\u6210\u529F\uFF0C\u4E0D\u80FD\u5931\u8D25\u3002\u300D</p><p>\u7ED1\u67B6\u884C\u52A8\u6BD4\u60F3\u8C61\u7684\u66F4\u5BB9\u6613\uFF0C\u6839\u672C\u6CA1\u4EBA\u60F3\u5230\u6709\u54EA\u4E2A\u80C6\u5927\u5305\u5929\u7684\u5BB6\u4F19\u4F1A\u5728\u592A\u5C81\u5934\u4E0A\u52A8\u571F\uFF0C\u53BF\u957F\u4E0D\u5728\uFF0C\u6709\u51E0\u4E2A\u62A4\u536B\u4E5F\u6E9C\u51FA\u53BB\u8D4C\u94B1\u4E86\uFF0C\u53E6\u5916\u4E24\u4E2A\u5F53\u73ED\u7684\u62A4\u536B\u88AB\u94F6\u53F6\u7684\u5439\u9488\u548C\u6D77\u68E0\u7684\u624B\u5200\u653E\u5012\u5728\u5730\uFF0C\u5185\u5B85\u7531\u6B64\u6D1E\u5F00\u65E0\u963B\u3002</p><p>\u665A\u996D\u540E\uFF0C\u51B7\u5982\u971C\u8DDF\u7740\u4E03\u59E8\u592A\u53BB\u8FD1\u90CA\u7684\u5927\u620F\u56ED\u5B50\u770B\u4E86\u4E00\u51FA\u5F53\u5730\u6D41\u884C\u7684\u50A9\u5267\uFF0C\u5218\u6EA2\u4E4B\u6D3E\u62A4\u5175\u6765\u544A\u77E5\u5979\u53BB\u4E86\u7701\u5E9C\u5F00\u4F1A\uFF0C\u63A5\u5979\u65E9\u70B9\u56DE\u53BB\u6B47\u606F\uFF0C\u5979\u6B63\u597D\u65E0\u751A\u6ECB\u5473\uFF0C\u4FBF\u5A49\u62D2\u4E86\u4E03\u59E8\u592A\u7EE7\u7EED\u6253\u724C\u7684\u9080\u8BF7\uFF0C\u5E26\u7740\u91D1\u5B9D\u5F80\u5BB6\u8D70\u3002</p><p>\u884C\u81F3\u6865\u5934\uFF0C\u6B64\u65F6\u6708\u6717\u98CE\u6E05\uFF0C\u706F\u706B\u9611\u73CA\uFF0C\u884C\u4EBA\u7A00\u843D\uFF0C\u56DB\u5468\u7AF9\u697C\u6728\u697C\u9519\u843D\u6709\u81F4\uFF0C\u597D\u4E00\u6D3E\u5F02\u4E61\u98CE\u60C5\u666F\u8C61\u3002</p><p>\u6865\u4E0B\u9759\u9759\u5730\u6DCC\u8FC7\u4E00\u4E32\u6392\uFF0C\u6CBF\u6C5F\u800C\u4E0B\uFF0C\u8239\u4E0A\u51E0\u6761\u7CBE\u8D64\u4E0A\u8EAB\u7684\u6C49\u5B50\u90FD\u62FF\u773C\u5F80\u51B7\u5982\u971C\u8EAB\u4E0A\u77A7\uFF0C\u4E00\u4E2A\u7B11\uFF0C\u300C\u86EE\u5B50\uFF0C\u6709\u80C6\u628A\u6865\u4E0A\u7684\u7F8E\u5973\u62B1\u4E0B\u6765\u3002\u300D</p><p>\u53EB\u86EE\u5B50\u7684\u653E\u6392\u6C49\u563F\u563F\u7B11\uFF0C\u8FD8\u6CA1\u8BF4\u4EC0\u4E48\uFF0C\u8FDC\u968F\u5728\u51B7\u5982\u971C\u8EAB\u540E\u7684\u62A4\u5175\u51B2\u4E0A\u524D\u54D7\u54D7\u62C9\u67AA\u6813\uFF0C\u300C\u5988\u7684\uFF0C\u778E\u4E86\u72D7\u773C\uFF0C\u4E0D\u60F3\u6D3B\u5566\uFF1F\u300D</p><p>\u653E\u6392\u6C49\u5410\u4E86\u5410\u820C\u5934\uFF0C\u6C89\u9ED8\u4E0B\u6765\uFF0C\u6D88\u901D\u5728\u8FDC\u65B9\u3002</p><p>\u51B7\u5982\u971C\u9887\u4E0D\u4EE5\u4E3A\u7136\uFF0C\u89C9\u5F97\u653E\u6392\u6C49\u7684\u7387\u771F\u5927\u80C6\u53EF\u6BD4\u8FD9\u4E9B\u62A4\u5175\u7684\u72D0\u5047\u864E\u5A01\u53EF\u7231\u5F97\u591A\u3002</p><p>\u51C4\u6E05\u60A0\u626C\u7684\u4E8C\u80E1\u58F0\u968F\u98CE\u98D8\u6765\uFF0C\u4E00\u4F4D\u957F\u886B\u8001\u8005\u6B63\u76D8\u811A\u5750\u5728\u4E86\u6CB3\u8FB9\uFF0C\u8FCE\u7740\u51B7\u6708\u62C9\u7434\uFF0C\u795E\u60C5\u5B64\u9AD8\u3002</p><p>\u51B7\u5982\u971C\u672C\u662F\u597D\u4E50\u4E4B\u4EBA\uFF0C\u7ACB\u65F6\u5C31\u88AB\u8FD9\u4E2A\u4E50\u58F0\u6253\u52A8\uFF0C\u773C\u524D\u5C55\u5F00\u4E86\u4E00\u5E45\u5E45\u7684\u753B\u5377\uFF0C\u7686\u662F\u5C18\u4E16\u95F4\u4E00\u5207\u5927\u60B2\u75DB\u4E4B\u90C1\u7ED3\uFF0C\u53C8\u5982\u4EBA\u4E4B\u5982\u5B87\u5B99\u82CD\u5929\u4E4B\u4E0B\u7684\u5B64\u72EC\u548C\u65E0\u529B\uFF0C\u542C\u5F97\u75F4\u4E86\uFF0C\u4E0D\u7981\u5782\u4E0B\u6CEA\u6765\u3002</p><p>\u8001\u8005\u7434\u58F0\u4E00\u6536\uFF0C\u70B9\u5934\u53F9\u9053\uFF0C\u300C\u679C\u7136\u662F\u7978\u6C34\u3002\u300D</p><p>\u51B7\u5982\u971C\u4E00\u6014\u9053\uFF0C\u300C\u5148\u751F\u5728\u4E0E\u6211\u8BF4\u8BDD\u5417\uFF1F\u300D</p><p>\u8001\u8005\u5374\u5408\u773C\u4E0D\u8A00\u4E86\uFF0C\u80E1\u7434\u53C8\u54BF\u54BF\u5440\u5440\u5730\u54CD\u4E86\u8D77\u6765\uFF0C\u8FD9\u756A\u53D8\u4E86\u4E00\u66F2\uFF0C\u8001\u8005\u54D1\u7740\u58F0\u5531\u9053\uFF0C\u300C\u773C\u89C1\u4ED6\u8D77\u9AD8\u697C\uFF0C\u773C\u89C1\u4ED6\u5BB4\u5BBE\u5BA2\uFF0C\u773C\u89C1\u4ED6\u697C\u584C\u4E86\u2026\u2026\u300D</p><p>\u91D1\u5B9D\u6251\u54E7\u7B11\uFF0C\u300C\u8FD9\u8001\u75AF\u5B50\u3002\u300D</p><p>\u6D17\u6F31\u5B8C\u6BD5\uFF0C\u51B7\u5982\u971C\u5BF9\u955C\u68B3\u5934\uFF0C\u8FD8\u5728\u7422\u78E8\u90A3\u8001\u8005\u7684\u8BDD\uFF0C\u91D1\u5B9D\u8BF4\u4ED6\u662F\u8FD9\u5C0F\u57CE\u91CC\u51FA\u4E86\u540D\u7684\u8001\u75AF\u5B50\uFF0C\u4E00\u5929\u5230\u665A\u5BF9\u522B\u4EBA\u8BF4\u778E\u8BDD\uFF0C\u547D\u554A\u8FD0\u7684\u5413\u552C\u4EBA\uFF0C\u8981\u5979\u522B\u4FE1\u3002</p><p>\u51B7\u5982\u971C\u81EA\u5632\u5730\u4E00\u7B11\uFF0C\u8BB8\u662F\u81EA\u5DF1\u592A\u591A\u5FC3\u4E86\u7F62\u3002</p><p>\u7A81\u7136\uFF0C\u955C\u4E2D\u591A\u51FA\u4E86\u4E00\u6837\u4E1C\u897F\uFF0C\u4E00\u652F\u9A73\u58F3\u67AA\u6307\u4F4F\u4E86\u5979\u7684\u5934\u3002</p><p>\u51B7\u5982\u971C\u5FC3\u4E0B\u60CA\u60E7\uFF0C\u9762\u4E0A\u5374\u5F3A\u81EA\u9547\u9759\uFF0C\u9053\uFF0C\u300C\u4EC0\u4E48\u4EBA\uFF1F\u300D</p><p>\u4E2A\u5B50\u4E0D\u9AD8\u7684\u8499\u9762\u5973\u5B50\u9053\uFF0C\u300C\u571F\u532A\u7ED1\u7968\u61C2\u4E0D\u61C2\u554A\uFF1F\u300D</p><p>\u53E6\u4E00\u5973\u5B50\u4F4E\u559D\u9053\uFF0C\u300C\u5FEB\u5E72\u6D3B\uFF0C\u5C11\u8BF4\u5E9F\u8BDD\u3002\u300D</p><p>\u571F\u532A\uFF1F\u6765\u6E58\u897F\u4E4B\u524D\u65E9\u5C31\u542C\u8BF4\u6709\u571F\u532A\u4E00\u8BF4\uFF0C\u4EE5\u4E3A\u90A3\u662F\u9065\u8FDC\u7684\u4E8B\u60C5\uFF0C\u6CA1\u60F3\u5230\u81EA\u5DF1\u6765\u6C85\u9547\u7684\u7B2C\u4E00\u5929\u5C31\u906D\u9047\u4E86\u3002</p><p>\u5979\u8FD8\u6765\u4E0D\u53CA\u60F3\u66F4\u591A\u5C31\u773C\u524D\u4E00\u9ED1\uFF0C\u4E0D\u9192\u4EBA\u4E8B\u3002</p><p>\u51B7\u5982\u971C\u5835\u53E3\u53CD\u7ED1\u7740\u88C5\u8FDB\u4E86\u7279\u7F6E\u7684\u6728\u7BB1\uFF0C\u91D1\u82B1\u94F6\u53F6\u63A8\u7740\u4F2A\u88C5\u597D\u7684\u72EC\u8F6E\u8F66\uFF0C\u8FC5\u901F\u6D88\u5931\u5728\u591C\u8272\u4E2D\u3002</p><p>\u5730\u4E0A\u53EA\u6709\u51E0\u4E2A\u88AB\u6253\u660F\u7684\u5BB6\u4EBA\u548C\u62A4\u5175\uFF0C\u8FD8\u6709\u4E00\u5C01\u6307\u5B9A\u4E24\u65E5\u540E\u4EE5\u4EBA\u6362\u4EBA\u7684\u4FE1\u7B3A\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u767D\u5929\u5FB7\u6B63\u5728\u628A\u5BF9\u51B7\u5982\u971C\u7684\u6B32\u706B\u6089\u6570\u53D1\u6CC4\u5728\u4E86\u9752\u7EA2\u8EAB\u4E0A\uFF0C\u53EF\u601C\u9752\u7EA2\u5DF2\u662F\u51E0\u5EA6\u660F\u8FF7\uFF0C\u8EAB\u5B50\u8F6F\u8F6F\u5730\u5E73\u653E\u5728\u4E00\u5F20\u77EE\u51E0\u4E0A\uFF0C\u56DB\u80A2\u5927\u5F00\u3002</p><p>\u4EE5\u5979\u6B64\u65F6\u7684\u72B6\u6001\u4E5F\u6CA1\u5FC5\u8981\u52A0\u4EFB\u4F55\u675F\u7F1A\u4E86\uFF0C\u767D\u5929\u5FB7\u7C97\u5927\u7684\u8089\u68D2\u63D2\u5728\u9752\u7EA2\u7684\u5C41\u773C\u91CC\u4F7F\u52B2\u505A\u7740\u6D3B\u585E\u8FD0\u52A8\uFF0C\u4E0B\u5782\u7684\u5934\u90E8\u4E5F\u88AB\u4E00\u53CC\u7CD9\u624B\u6367\u7740\uFF0C\u53E6\u4E00\u6761\u7C97\u5927\u7684\u8089\u68D2\u6324\u5F00\u5979\u5E72\u71E5\u7684\u5634\u5507\uFF0C\u4E00\u76F4\u6DF1\u5165\u5589\u5934\uFF0C\u6CA1\u6709\u8F6E\u5230\u7684\u5C31\u634F\u5976\u5B50\u5E72\u763E\uFF0C\u51E0\u4EBA\u5E72\u5F97\u5012\u662F\u723D\uFF0C\u53EA\u770B\u89C1\u9752\u7EA2\u767D\u751F\u751F\u7684\u5927\u817F\u65E0\u529B\u5730\u5728\u4E24\u4FA7\u6643\u52A8\u3002</p><p>\u5F85\u5F97\u767D\u5929\u5FB7\u83B7\u77E5\u5218\u6EA2\u4E4B\u7684\u65B0\u592B\u4EBA\u88AB\u7ED1\u7968\u7684\u6D88\u606F\uFF0C\u6D77\u68E0\u7B49\u4EBA\u65E9\u5DF2\u9003\u8FDB\u4E86\u832B\u65E0\u8FB9\u9645\u7684\u7AF9\u6797\u6D77\u4E2D\u3002</p><p>\u767D\u5929\u5FB7\u6068\u5F97\u60F3\u6740\u4EBA\uFF0C\u6700\u7EC8\u8C01\u4E5F\u6CA1\u6740\uFF0C\u53EA\u662F\u5F80\u9752\u7EA2\u7684\u5C0F\u809A\u5B50\u4E0A\u72E0\u72E0\u8E22\u4E86\u4E00\u811A\uFF0C\u559D\u9053\uFF1A\u300C\u5144\u5F1F\u4EEC\u522B\u73A9\u4E86\uFF0C\u628A\u8FD9\u5A4A\u5B50\u5F04\u6B8B\u4E86\u5C31\u4EA4\u4E0D\u4E86\u5DEE\u5566\u3002\u300D</p><p>\u7AF9\u6D77\u6DF1\u5904\uFF0C\u9664\u4E86\u8302\u5BC6\u7684\u7AF9\u6797\uFF0C\u4E5F\u6709\u4E0D\u5C11\u4F4E\u77EE\u7684\u704C\u6728\u548C\u6811\u6728\uFF0C\u5C71\u52BF\u8FDE\u7EF5\uFF0C\u5229\u6253\u8FD0\u52A8\u6218\uFF0C\u6240\u4EE5\u4FDD\u5B89\u56E2\u867D\u82B1\u4E86\u5927\u529B\u6C14\u62C9\u7F51\u641C\u5C71\uFF0C\u5BF9\u719F\u6089\u5730\u5F62\u7684\u6D77\u68E0\u6765\u8BF4\uFF0C\u9003\u9038\u751A\u81F3\u53CD\u51FB\u4E00\u67AA\u90FD\u662F\u5BB9\u6613\u7684\u4E8B\u3002</p><p>\u51E0\u4EBA\u7075\u6D3B\u5730\u5728\u7AF9\u6797\u95F4\u7A7F\u68AD\uFF0C\u6362\u4E86\u6D77\u68E0\u80CC\u7740\u51B7\u5982\u971C\uFF0C\u80F8\u524D\u9AD8\u8038\u8D77\u4F0F\uFF0C\u4E24\u6761\u5065\u7F8E\u7684\u957F\u811A\u5728\u6C9F\u58D1\u95F4\u8DF3\u6765\u8DF3\u53BB\uFF0C\u50CF\u4E00\u652F\u7F8E\u4E3D\u7684\u7075\u9E7F\u3002</p><p>\u8FD4\u56DE\u5C45\u4F4F\u7684\u5927\u6EB6\u6D1E\u5DF2\u7ECF\u662F\u6B21\u65E5\u7684\u65E9\u4E0A\uFF0C\u592A\u9633\u6321\u5728\u4E91\u5C42\u540E\u9762\uFF0C\u4E00\u5C42\u8584\u96FE\u62C9\u8D77\u6797\u4E2D\uFF0C\u5931\u6C34\u7684\u7AF9\u6797\u65E9\u5C31\u5931\u53BB\u4E86\u6D77\u4E00\u822C\u7684\u6C14\u52BF\uFF0C\u8F6F\u8F6F\u5730\u5782\u4E0B\u53F6\u5B50\uFF0C\u65E2\u4FBF\u5982\u6B64\uFF0C\u6B64\u5730\u4E5F\u8FD8\u662F\u5982\u4ED9\u5883\u4E00\u822C\u7684\u7F8E\u4E3D\u3002</p><p>\u770B\u5230\u6D77\u68E0\u7B49\u4EBA\u5E73\u5B89\u56DE\u6765\uFF0C\u4F17\u4EBA\u9AD8\u5174\u574F\u4E86\uFF0C\u867D\u7136\u6CA1\u80FD\u6551\u51FA\u9752\u7EA2\uFF0C\u4F46\u6709\u51B7\u5982\u971C\u5728\u624B\uFF0C\u8FD8\u662F\u770B\u5230\u4E86\u5E0C\u671B\u3002</p><p>\u51B7\u5982\u971C\u4ECE\u5E03\u888B\u91CC\u653E\u51FA\u6765\u65F6\uFF0C\u4E0D\u8BBA\u662F\u7537\u4EBA\u8FD8\u662F\u5973\u4EBA\uFF0C\u773C\u775B\u90FD\u770B\u76F4\u4E86\uFF0C\u5982\u971C\u662F\u5927\u5BB6\u95FA\u79C0\uFF0C\u51FA\u8EAB\u4E8E\u4E66\u9999\u95E8\u5F1F\uFF0C\u4E3E\u6B62\u95F4\u81EA\u7136\u6D41\u9732\u7740\u96CD\u5BB9\u534E\u8D35\u7684\u6C14\u8D28\uFF0C\u4E0E\u773C\u524D\u8FD9\u4E9B\u4E61\u4E0B\u571F\u532A\u81EA\u6709\u7740\u4E91\u6CE5\u4E4B\u522B\u3002</p><p>\u7559\u5B88\u7684\u6885\u5B50\u6084\u7B11\u7740\u5BF9\u91D1\u82B1\u8BF4\uFF1A\u300C\u4ECA\u5929\u8BF7\u56DE\u6765\u7684\u8FD9\u4F4D\u59D0\uFF0C\u53EF\u628A\u6211\u4EEC\u5927\u59D0\u5934\u6BD4\u4E0B\u53BB\u4E86\u3002\u300D</p><p>\u91D1\u82B1\u4E0D\u5C51\u9053\uFF1A\u300C\u8C01\u8BF4\u7684\uFF0C\u4E0D\u5C31\u662F\u767D\u4E00\u70B9\u561B\uFF0C\u8981\u6211\u770B\uFF0C\u8FD8\u662F\u68E0\u59D0\u6F02\u4EAE\u3002\u300D</p><p>\u94F6\u53F6\u542C\u5230\u4E86\u8BF4\uFF1A\u300C\u8981\u6211\u662F\u7537\u4EBA\uFF0C\u628A\u68E0\u59D0\u548C\u8FD9\u4F4D\u592A\u592A\u4E00\u8D77\u5A36\u8FC7\u6765\uFF0C\u5C31\u662F\u83AB\u5927\u7684\u798F\u6C14\u4E86\u3002\u300D</p><p>\u300C\u554A\u5478\uFF01\u760C\u86E4\u87C6\u5403\u5929\u9E45\u8089\uFF0C\u7F9E\u4E0D\u7F9E\u554A\u3002\u300D</p><p>\u51E0\u4E2A\u5973\u4EBA\u7B11\u7740\u4E00\u8D77\u5550\u5979\u3002</p><p>\u94F6\u53F6\u8BF4\u7684\u5012\u662F\u5927\u5B9E\u8BDD\uFF0C\u6D77\u68E0\u4E0E\u51B7\u5982\u971C\u6C14\u8D28\u8FE5\u5F02\uFF0C\u4E00\u4E2A\u9634\u67D4\u5A07\u5F31\uFF0C\u4E00\u4E2A\u9AD8\u6311\u5065\u7F8E\uFF0C\u4E00\u4E2A\u80A4\u767D\u5982\u7389\uFF0C\u4E00\u4E2A\u9ED1\u91CC\u900F\u7EA2\uFF0C\u4E00\u4E2A\u5982\u540C\u5BC6\u5BA4\u4E2D\u7684\u6C34\u4ED9\uFF0C\u4E00\u4E2A\u6070\u4F3C\u7530\u91CE\u6012\u653E\u7684\u5C71\u83CA\uFF0C\u4F46\u4ECE\u4F53\u6001\u5230\u4E94\u5B98\uFF0C\u65E0\u8BBA\u8C01\u7684\u7EC6\u8282\u90FD\u7ECF\u5F97\u8D77\u63A8\u6572\uFF0C\u7EDD\u5BF9\u90FD\u662F\u4E07\u91CC\u6311\u4E00\u7684\u7F8E\u4EBA\uFF0C\u80FD\u5A36\u5230\u5176\u4E2D\u4E00\u4E2A\u59D4\u5B9E\u5DF2\u662F\u83AB\u5927\u7684\u798F\u6C14\u3002</p><p>\u6D77\u68E0\u88C5\u4F5C\u6CA1\u6709\u542C\u5230\u5979\u624B\u4E0B\u7684\u8C03\u7B11\uFF0C\u4E00\u76F4\u5F85\u51B7\u5982\u971C\u5F88\u5BA2\u6C14\uFF0C\u89E3\u5F00\u7EF3\u5B50\u540E\u8BF7\u5979\u4E00\u8D77\u5403\u996D\uFF0C\u51B7\u5982\u971C\u5374\u662F\u65E2\u538C\u6076\u53C8\u5BB3\u6015\uFF0C\u62B1\u7740\u80A9\u7ACB\u7740\u4E0D\u52A8\uFF0C\u679C\u662F\u51B7\u5982\u51B0\u971C\u3002</p><p>\u6D77\u68E0\u65E0\u5948\uFF0C\u4FBF\u53EB\u4E8C\u559C\u5B50\u628A\u5979\u5148\u5173\u5230\u5185\u6D1E\u91CC\u53BB\u3002</p><p>\u4E0A\u5C71\u4E4B\u524D\uFF0C\u6D77\u68E0\u5DF2\u7ECF\u6258\u5173\u7CFB\u627E\u5230\u4E86\u5EB7\u8001\u7237\u4F5C\u4EA4\u6362\u7684\u4E2D\u4ECB\u4EBA\u3002</p><p>\u8BF4\u8D77\u8FD9\u5C42\u5173\u7CFB\u5F88\u5FAE\u5999\uFF0C\u571F\u532A\u62A2\u52AB\u4E86\u8D22\u7269\u4E4B\u540E\uFF0C\u4E00\u822C\u90FD\u8981\u6709\u9500\u8D43\u7684\u6E20\u9053\uFF0C\u4E00\u822C\u52BF\u529B\u5927\u7684\u591A\u4ECE\u9ED1\u9053\u8D70\uFF0C\u50CF\u5F53\u5730\u532A\u5E2E\u7684\u9F99\u5934\u699C\u7237\uFF0C\u52BF\u529B\u8F83\u5F31\u7684\u532A\u5E2E\u5F80\u5F80\u503E\u5411\u4E8E\u8D70\u767D\u9053\uFF0C\u901A\u8FC7\u6B63\u5F53\u7ECF\u8425\u7684\u5546\u4EBA\u624D\u4E0D\u4F1A\u6709\u9ED1\u5403\u9ED1\u4E4B\u865E\uFF0C\u5F53\u7136\uFF0C\u53CC\u65B9\u7684\u98CE\u9669\u540C\u6837\u5F88\u5927\u3002</p><p>\u6D77\u68E0\u5386\u7ECF\u5468\u6298\u624D\u642D\u4E0A\u4E86\u5EB7\u8001\u7237\u8FD9\u6761\u7EBF\uFF0C\u53EF\u4EE5\u8BF4\u5EB7\u8001\u7237\u7684\u5BB6\u4EA7\u6709\u76F8\u5F53\u4E00\u90E8\u5206\u5C31\u662F\u8FD9\u4E48\u6765\u7684\uFF0C\u53EA\u662F\u5F7C\u6B64\u5FC3\u7167\u4E0D\u5BA3\u800C\u5DF2\u3002\u6D77\u68E0\u4ECE\u6CA1\u51FA\u8FC7\u9762\uFF0C\u90FD\u662F\u901A\u8FC7\u795E\u79D8\u7684\u7B2C\u4E09\u65B9\u5728\u7275\u7EBF\uFF0C\u5305\u62EC\u8FD9\u4E00\u6B21\u3002</p><p>\u56E0\u5E72\u7CFB\u592A\u5927\uFF0C\u6D77\u68E0\u7A0D\u4E8B\u4F11\u606F\u4E86\u4E00\u4E0B\uFF0C\u4FBF\u5E26\u7740\u53CC\u59DD\u51E0\u4E2A\u6F5C\u4E0B\u5C71\u53BB\u4E86\u3002</p><p>\u5165\u591C\uFF0C\u5BE8\u91CC\u4F17\u4EBA\u4E5F\u65E9\u65E9\u6B47\u606F\u3002</p><p>\u4E00\u6761\u9ED1\u5F71\u5077\u5077\u6E9C\u5165\u5185\u6D1E\uFF0C\u706B\u628A\u6620\u8FC7\uFF0C\u6B63\u662F\u5F53\u591C\u73ED\u7684\u4E8C\u559C\u5B50\u3002</p><p>\u5E38\u8A00\u9053\uFF0C\u82F1\u96C4\u96BE\u8FC7\u7F8E\u4EBA\u5173\uFF0C\u4F55\u51B5\u50CF\u4E8C\u559C\u5B50\u672C\u5C31\u662F\u4E61\u4E95\u65E0\u8D56\u51FA\u8EAB\u7684\u4E8C\u6D41\u5B50\u3002</p><p>\u4E8C\u559C\u5B50\u5F53\u5E74\u6B20\u4E86\u9AD8\u5229\u8D37\u88AB\u4EBA\u8FFD\u6740\u8EB2\u8FDB\u5C71\u4E2D\uFF0C\u751F\u6B7B\u6538\u5173\u4E4B\u9645\u6B63\u5DE7\u88AB\u6D77\u68E0\u6240\u6551\uFF0C\u4ECE\u6B64\u4E0A\u4E86\u8D3C\u8239\u3002\u4ED6\u9887\u6709\u70B9\u673A\u667A\uFF0C\u4E0B\u4E09\u6EE5\u7684\u95E8\u574E\u7CBE\uFF0C\u9ED1\u767D\u4E24\u9053\u6E38\u5203\u6709\u5982\u3002\u6709\u4E86\u4E8C\u559C\u5B50\u7684\u8F85\u4F50\uFF0C\u6D77\u68E0\u6709\u5982\u795E\u52A9\uFF0C\u58F0\u52BF\u5927\u5F20\uFF0C\u77ED\u77ED\u51E0\u5E74\u80FD\u4ECE\u4E0D\u5230\u5341\u4EBA\u6269\u5F20\u5230\u4E86\u4E8C\u5341\u591A\u4EBA\u67AA\uFF0C\u6240\u4EE5\u6D77\u68E0\u5BF9\u4ED6\u4E00\u76F4\u5341\u5206\u4F9D\u91CD\u548C\u4FE1\u8D56\u3002</p><p>\u4E0D\u8FC7\u4E8C\u559C\u5B50\u6709\u70B9\u6BDB\u75C5\uFF0C\u597D\u8D4C\u517C\u597D\u8272\uFF0C\u4F46\u81EA\u4ECE\u4E0A\u5F97\u5C71\u6765\u5374\u6536\u655B\u4E86\u4E0D\u5C11\uFF0C\u4E00\u5219\u89C4\u77E9\u751A\u4E25\uFF0C\u6D77\u68E0\u5BF9\u5978\u6DEB\u4E4B\u4E8B\u5C24\u4E3A\u75DB\u6068\uFF0C\u4E00\u5411\u90FD\u662F\u6740\u65E0\u8D66\uFF1B\u4E8C\u5219\u4E8C\u559C\u5B50\u5BF9\u6D77\u68E0\u6709\u4E86\u7231\u6155\u4E4B\u5FC3\uFF0C\u8FFD\u968F\u65E5\u4E45\uFF0C\u6B64\u5FC3\u8D8A\u76DB\uFF0C\u5E7B\u60F3\u7740\u6709\u671D\u4E00\u65E5\u6D77\u68E0\u88AB\u4ED6\u6253\u52A8\u4E0B\u5AC1\u4E8E\u4ED6\uFF0C\u504F\u504F\u6D77\u68E0\u4E0D\u77E5\u4F55\u6545\uFF0C\u5BF9\u7537\u5973\u4E4B\u4E8B\u6BEB\u65E0\u5174\u8DA3\uFF0C\u4ECE\u4E0D\u6D41\u9732\u4E00\u4E1D\u611F\u60C5\uFF0C\u4E5F\u5C31\u6E10\u6E10\u6709\u4E9B\u6DE1\u4E86\u3002</p><p>\u5176\u5B9E\u4ED6\u4E5F\u77E5\u9053\u94F6\u53F6\u5BF9\u4ED6\u4E00\u76F4\u843D\u82B1\u6709\u610F\uFF0C\u4F46\u6709\u6D77\u68E0\u6BD4\u7740\uFF0C\u4EFB\u4ED6\u83BA\u83BA\u71D5\u71D5\u90FD\u76F4\u5982\u82B1\u8349\uFF0C\u53EA\u6709\u6D41\u6C34\u65E0\u60C5\u4E86\u3002</p><p>\u6BD5\u7ADF\u662F\u5E74\u9752\u4F22\u5B50\uFF0C\u706B\u6C14\u65FA\u3002\u51B7\u5982\u971C\u60CA\u4EBA\u7684\u7F8E\u8273\u8BA9\u4ED6\u76EE\u77AA\u53E3\u5446\uFF0C\u5728\u626F\u5979\u7684\u5C0F\u81C2\u5E26\u5979\u8D70\u65F6\uFF0C\u90A3\u4E00\u4E0B\u6ED1\u817B\u65E0\u9AA8\u7684\u611F\u89C9\uFF0C\u8BA9\u4ED6\u6D51\u8EAB\u53D1\u98A4\uFF0C\u5DEE\u70B9\u5F53\u573A\u51FA\u4E11\uFF0C\u6574\u5929\u90FD\u6709\u70B9\u5931\u9B42\u843D\u9B44\u3002\u665A\u996D\u65F6\u6885\u5B50\u8FD8\u5173\u5FC3\u4ED6\u662F\u4E0D\u662F\u75C5\u4E86\uFF0C\u5374\u6CA1\u7559\u610F\u4ED6\u7CBE\u795E\u4E0A\u7684\u53CD\u5E38\u3002</p><p>\u8D3C\u5FC3\u65E9\u5C31\u6709\u4E86\uFF0C\u8D3C\u80C6\u547C\u5730\u4E00\u4E0B\u4E5F\u751F\u4E86\u8D77\u6765\u3002</p><p>\u4E8C\u559C\u5B50\u5FC3\u9053\uFF0C\u5988\u7684\uFF0C\u8C41\u51FA\u53BB\u4E86\uFF0C\u9752\u7EA2\u843D\u5728\u4FDD\u5B89\u56E2\u624B\u91CC\u80AF\u5B9A\u88AB\u73A9\u6B8B\u4E86\uFF0C\u8001\u5B50\u4E00\u62A5\u8FD8\u4E00\u62A5\uFF0C\u73A9\u4E00\u73A9\u53BF\u957F\u7684\u5973\u4EBA\uFF0C\u5C31\u5F53\u662F\u4E3A\u9752\u7EA2\u62A5\u4EC7\uFF0C\u6599\u60F3\u6D77\u68E0\u770B\u5728\u81EA\u5DF1\u51FA\u751F\u5165\u6B7B\u5356\u547D\u7684\u4EFD\u4E0A\u4E0D\u4F1A\u592A\u4E3A\u96BE\u81EA\u5DF1\u3002</p><p>\u53CD\u590D\u601D\u91CF\u4E4B\u4E0B\uFF0C\u4ED6\u7EC8\u6A2A\u4E0B\u4E86\u4E00\u9897\u5FC3\uFF0C\u4E0D\u987E\u4E00\u5207\u4E5F\u8981\u5360\u6709\u8FD9\u5757\u5929\u9E45\u7F8E\u8089\u518D\u8BF4\u3002</p><p>\u4ED6\u4E3B\u52A8\u8DDF\u8D35\u751F\u63D0\u51FA\u66FF\u4ED6\u503C\u5348\u591C\u54E8\u65F6\uFF0C\u8D35\u751F\u8FD8\u9887\u6709\u4E9B\u611F\u6FC0\u3002\u591C\u6DF1\u4EBA\u9759\u4E4B\u540E\uFF0C\u4E8C\u559C\u5B50\u611F\u89C9\u5FC3\u8DF3\u8D8A\u6765\u8D8A\u5FEB\uFF0C\u4E5F\u8D8A\u6765\u8D8A\u5174\u594B\uFF0C\u786E\u8BA4\u5927\u5BB6\u90FD\u5DF2\u6DF1\u7761\uFF0C\u4FBF\u5077\u5077\u79BB\u5C97\u6E9C\u8FDB\u4E86\u5173\u62BC\u51B7\u5982\u971C\u7684\u5C0F\u6D1E\u3002</p><p>\u51B7\u5982\u971C\u4E00\u6574\u5929\u6CA1\u6709\u8FDB\u98DF\uFF0C\u5750\u5728\u7B80\u6613\u7684\u7AF9\u677F\u5E8A\u8FB9\u5FC3\u4E71\u5982\u9EBB\uFF0C\u5FD0\u5FD1\u4E0D\u5B89\u3002\u5979\u662F\u524D\u6E05\u9AD8\u5B98\u7684\u540E\u4EE3\uFF0C\u6B63\u9EC4\u65D7\u4EBA\uFF0C\u5BB6\u9053\u4E2D\u843D\u540E\u79FB\u5C45\u957F\u6C99\uFF0C\u7F6E\u4E86\u4E9B\u7530\u4EA7\uFF0C\u4F5C\u4E3A\u638C\u4E0A\u660E\u73E0\uFF0C\u7236\u6BCD\u5BF9\u5979\u671F\u8BB8\u751A\u9AD8\uFF0C\u8BFB\u4E66\u8BC6\u5B57\u3001\u9488\u7EE3\u5973\u7EA2\u3001\u5929\u6587\u5730\u7406\u90FD\u6709\u6D89\u730E\uFF0C\u4F7F\u5979\u517C\u5177\u4E86\u65B0\u65E7\u5973\u6027\u7684\u7F8E\u5FB7\uFF0C\u79C0\u5916\u6167\u4E2D\u3002</p><p>\u5218\u6EA2\u4E4B\u4E16\u4EA4\u5B50\u5F1F\uFF0C\u5374\u65E0\u7EA8\u88E4\u4E4B\u98CE\uFF0C\u7279\u522B\u4E0A\u8FDB\u52AA\u529B\uFF0C\u4E24\u5BB6\u7ED3\u4EB2\u53EF\u8C13\u95E8\u5F53\u6237\u5BF9\uFF0C\u6C34\u5230\u6E20\u6210\u3002\u65B0\u5A5A\u624D\u6570\u6708\u4FBF\u968F\u4E08\u592B\u4ECE\u7701\u5E9C\u6765\u5230\u6B64\u5730\u4F5C\u5B98\uFF0C\u8FD8\u4EE5\u4E3A\u5F53\u5730\u6C11\u98CE\u6DF3\u6734\uFF0C\u54EA\u6599\u60F3\u4F1A\u6709\u5982\u6B64\u60CA\u53D8\u3002</p><p>\u6D77\u68E0\u7B49\u4EBA\u867D\u4E3A\u532A\uFF0C\u5374\u591A\u662F\u8FD9\u7B49\u7F8E\u4E3D\u7684\u4E0A\u4E58\u5973\u5B50\uFF0C\u5F85\u5979\u5C1A\u8FD8\u5BA2\u6C14\uFF0C\u4E0D\u50CF\u6076\u4EBA\uFF0C\u4F46\u4ECE\u4ED6\u4EEC\u7684\u53E3\u98CE\u4E2D\u542C\u51FA\u662F\u8981\u62FF\u5979\u4EA4\u6362\u4E00\u4E2A\u4EC0\u4E48\u4EBA\uFF0C\u4E07\u4E00\u4E0D\u6210\u529F\uFF0C\u5374\u4E5F\u96BE\u4FDD\u5979\u4EEC\u4E0D\u4E0B\u6BD2\u624B\uFF0C\u6050\u6015\u6027\u547D\u90FD\u96BE\u4FDD\u4E86\u3002\u4E00\u65F6\u95F4\u6101\u80A0\u767E\u8F6C\uFF0C\u60B2\u4ECE\u4E2D\u6765\uFF0C\u6F78\u7136\u6CEA\u4E0B\uFF0C\u8FDE\u4E8C\u559C\u5B50\u5230\u4E86\u8EAB\u8FB9\u4E5F\u6CA1\u89C9\u5BDF\u3002</p><p>\u4E8C\u559C\u5B50\u6D8E\u7740\u8138\u4F4E\u7B11\u4E00\u58F0\uFF0C\u300C\u522B\u54ED\u5440\u7F8E\u4EBA\uFF0C\u77E5\u9053\u4F60\u662F\u60F3\u6211\u5566\uFF0C\u4E0D\u6025\uFF0C\u54E5\u54E5\u4ECA\u5929\u4FDD\u7BA1\u8BA9\u4F60\u75DB\u5FEB\u3002\u300D</p><p>\u4ED6\u4E0D\u6562\u591A\u8A00\uFF0C\u8038\u8EAB\u800C\u4E0A\u4FBF\u73AF\u62B1\u4F4F\u51B7\u5982\u971C\u538B\u5012\u5728\u5E8A\uFF0C\u300C\u7F8E\u4EBA\u4E56\u4E56\u300D\u5730\u4E71\u53EB\uFF0C\u561F\u8D77\u4E00\u5F20\u5634\u5DF4\u5C31\u5F80\u5979\u7684\u7389\u8138\u7C89\u9888\u4EB2\u53BB\u3002</p><p>\u51B7\u5982\u971C\u522B\u8BF4\u662F\u60B4\u4E0D\u53CA\u9632\uFF0C\u5C31\u7B97\u6765\u5F97\u53CA\uFF0C\u4EE5\u5979\u4E00\u4E2A\u67D4\u5F31\u5973\u5B50\u54EA\u91CC\u62B5\u5F97\u5F80\u7CBE\u58EE\u4E4B\u864E\u72FC\u5462\uFF0C\u5F53\u4E0B\u4FBF\u538B\u5F97\u4E86\u4E2A\u7ED3\u7ED3\u5B9E\u5B9E\uFF0C\u52A8\u5F39\u4E0D\u5F97\uFF0C\u8FD8\u6709\u4E00\u80A1\u53E3\u4E2D\u81ED\u6C14\u55B7\u6765\uFF0C\u5FC3\u4E2D\u5927\u9A87\uFF0C\u76F4\u89C9\u5929\u5E95\u4E0B\u6700\u6076\u5FC3\u6700\u7F9E\u803B\u4E4B\u4E8B\u964D\u4E34\u5728\u5979\u8EAB\u4E0A\uFF0C\u672C\u80FD\u5730\u626D\u52A8\u7740\u8EAB\u4F53\u4F01\u56FE\u9003\u5F00\uFF0C\u5F20\u5634\u6B32\u547C\u3002\u4E8C\u559C\u5B50\u65E9\u5C31\u9632\u4E86\u6B64\u62DB\uFF0C\u4E00\u6761\u5E03\u5DFE\u5F53\u4E0B\u585E\u4E86\u4E2A\u6EE1\u53E3\u3002</p><p>\u4E8C\u559C\u5B50\u867D\u8BF4\u5174\u594B\u5F97\u80EF\u4E0B\u9633\u7269\u65E9\u5C31\u6DA8\u5927\u4E86\u4E24\u500D\u4E0D\u6B62\uFF0C\u8FD8\u662F\u4E0D\u6562\u5927\u610F\uFF0C\u6478\u7D22\u7740\u5C06\u51B7\u5982\u971C\u7684\u53CC\u624B\u53CD\u526A\u5728\u80CC\u540E\u62FF\u5E26\u5B50\u7ED1\u4E86\u8D77\u6765\uFF0C\u4F7F\u5979\u57FA\u672C\u4E0A\u5931\u53BB\u53CD\u6297\u80FD\u529B\uFF0C\u624D\u7565\u62AC\u8D77\u4E0A\u8EAB\uFF0C\u5F97\u610F\u5730\u6B23\u8D4F\u7740\u8EAB\u4E0B\u5F85\u5BB0\u7684\u7F94\u7F8A</p><p>\u8863\u88F3\u7EF7\u5F97\u5F88\u7D27\uFF0C\u770B\u5F97\u5230\u67D4\u8F6F\u5982\u9E3D\u7684\u80F8\u812F\u5728\u6025\u4FC3\u8D77\u4F0F\u3002</p><p>\u4ED6\u62BD\u51FA\u4E00\u652F\u624B\u6765\uFF0C\u9694\u7740\u8F7B\u8584\u7684\u7EF8\u8863\uFF0C\u8FD1\u4E4E\u8654\u8BDA\u5730\u6CBF\u7740\u90A3\u6761\u7EF7\u5F97\u7D27\u7D27\u7684\u4F18\u7F8E\u7684\u66F2\u7EBF\u8F7B\u8F7B\u6E38\u8D70\uFF0C\u4EAB\u53D7\u7740\u90A3\u80A1\u5F02\u6837\u8212\u5766\u7684\u611F\u89C9\u3002</p><p>\u771F\u662F\u5C24\u7269\u554A\u3002\u4ED6\u5FC3\u4E2D\u957F\u957F\u5730\u53F9\u4E86\u4E00\u58F0\u3002</p><p>\u51B7\u5982\u971C\u7D27\u95ED\u53CC\u773C\uFF0C\u6CA1\u6709\u653E\u5F03\u6323\u624E\u7684\u52AA\u529B\u3002</p><p>\u7537\u4EBA\u89C9\u5F97\u5973\u4EBA\u771F\u662F\u611A\u6627\uFF0C\u660E\u660E\u662F\u767D\u8D39\u6C14\u529B\u7684\u4E8B\u60C5\u8FD8\u4E0D\u80AF\u8BA4\u547D\uFF0C\u5BB3\u6015\u52A8\u9759\u592A\u5927\u60CA\u52A8\u4F17\u4EBA\uFF0C\u7D22\u6027\u65AD\u4E86\u5979\u7684\u5FF5\u60F3\uFF0C\u4E00\u5C41\u80A1\u9A91\u5230\u5973\u4EBA\u7684\u5C0F\u809A\u5B50\u4E0A\uFF0C\u4ECE\u8170\u4E0A\u6478\u51FA\u4E00\u628A\u5C16\u5229\u7684\u5315\u9996\uFF0C\u6A2A\u7740\u5728\u5979\u4FEE\u957F\u7684\u9888\u5B50\u4E0A\u4F5C\u52BF\u62D6\u8FC7\uFF0C\u6076\u72E0\u72E0\u5730\u8BF4\u9053\uFF1A\u300C\u8FD8\u6562\u4E71\u52A8\u5C31\u6345\u6B7B\u4F60\uFF0C\u5978\u6B7B\u4F60\uFF0C\u518D\u6254\u5230\u5C71\u91CC\u5582\u72FC\u3002\u5A4A\u5B50\uFF01\u300D</p><p>\u51B7\u51B7\u7684\u950B\u5203\u900F\u51FA\u4E86\u6D53\u539A\u7684\u6B7B\u4EA1\u6C14\u606F\u3002</p><p>\u5979\u7684\u8111\u6D77\u55E1\u5730\u4E00\u58F0\u3002\u6B7B\u7684\u6050\u60E7\u662F\u5982\u6B64\u5F3A\u70C8\uFF0C\u5982\u6B64\u8FEB\u8FD1\uFF0C\u6392\u5C71\u5012\u6D77\u5411\u5979\u88AD\u6765\uFF0C\u5C06\u6DF1\u6DF1\u7684\u5C48\u8FB1\u4E5F\u6682\u65F6\u538B\u5012\u5728\u4E00\u8FB9\uFF0C\u65E0\u6CD5\u62B5\u6321\u3002\u5973\u4EBA\u957F\u957F\u7684\u773C\u776B\u6BDB\u4E00\u9635\u6025\u98A4\uFF0C\u53CD\u6297\u660E\u663E\u5730\u5F31\u4E86\u4E0B\u6765\u3002</p><p>\u4E8C\u559C\u5B50\u65E0\u58F0\u5730\u7B11\u4E86\uFF0C\u987A\u5229\u5730\u5C06\u5979\u7FFB\u4E86\u4E2A\u8EAB\uFF0C\u9762\u671D\u4E0B\uFF0C\u5315\u9996\u4ECE\u80CC\u5FC3\u5C0F\u5FC3\u5212\u5F00\uFF0C\u51E0\u4E4E\u4E00\u70B9\u58F0\u606F\u6CA1\u6709\u3002</p><p>\u540E\u80CC\u5927\u7247\u96EA\u767D\u7684\u808C\u80A4\u8892\u9732\u4E86\u51FA\u6765\uFF0C\u53EA\u5269\u4E0B\u51E0\u6839\u7CFB\u7740\u4EB5\u8863\u7684\u5E26\u5B50\uFF0C\u6625\u5149\u65E0\u9650\u3002</p><p>\u4F53\u9999\u6251\u9F3B\uFF0C\u4E2D\u4EBA\u6B32\u9189\u3002\u808C\u80A4\u767D\u5F97\u6643\u773C\uFF0C\u50CF\u662F\u4E00\u7247\u5149\u628A\u8FD9\u6B7B\u6C14\u6C89\u6C89\u7684\u6D1E\u58C1\u90FD\u7167\u4EAE\u4E86\u3002</p><p>\u4E8C\u559C\u5B50\u7A81\u7136\u6709\u79CD\u60F3\u54ED\u7684\u611F\u89C9\uFF0C\u88AB\u8001\u5929\u7237\u7684\u6177\u6168\u8D50\u4E88\u611F\u52A8\u5F97\u60F3\u54ED\uFF0C\u5BFB\u601D\u7956\u4E0A\u8BB8\u662F\u79EF\u4E86\u5927\u5FB7\u5427\u3002</p><p>\u51B7\u5982\u971C\u7259\u5173\u7D27\u54AC\uFF0C\u5979\u60F3\u8FC7\u56BC\u820C\uFF0C\u5374\u7EC8\u4E8E\u7F3A\u4E4F\u9C7C\u6B7B\u7F51\u7834\u7684\u6700\u540E\u90A3\u70B9\u52C7\u6C14\u3002\u66FE\u7ECF\u4EE5\u4E3A\u81EA\u5DF1\u591A\u4E48\u8D1E\u6D01\uFF0C\u4E5F\u66FE\u7ECF\u4EE5\u4E3A\u81EA\u5DF1\u591A\u4E48\u9AD8\u50B2\uFF0C\u8FD9\u610F\u5FD7\u53EA\u4E0D\u8FC7\u8584\u5982\u7F57\u88F3\uFF0C\u90FD\u5728\u4E00\u679A\u8584\u8584\u7684\u950B\u5203\u548C\u7537\u4EBA\u8086\u65E0\u5FCC\u60EE\u7684\u90AA\u6076\u4E0B\u4E00\u70B9\u70B9\u5D29\u6E83\u3002</p><p>\u4F34\u968F\u7740\u80CC\u5FC3\u4E00\u7247\u51B0\u51C9\uFF0C\u5979\u7684\u5FC3\u5934\u4E5F\u4E00\u7247\u51B0\u51C9\uFF0C\u5973\u4EBA\u4E00\u751F\u4E2D\u6700\u5B9D\u8D35\u7684\u4E1C\u897F\u773C\u770B\u5373\u5C06\u5931\u53BB\uFF0C\u5979\u771F\u7684\u80FD\u76F4\u9762\u8FD9\u6B8B\u9177\u7684\u73B0\u5B9E\u5417\uFF1F</p><p>\u6E05\u6CEA\u4ECE\u51E4\u76EE\u4E2D\u65E0\u58F0\u5730\u6DCC\u4E86\u51FA\u6765\u3002</p><p>\u4E8C\u559C\u5B50\u7729\u6655\u4E86\u7247\u523B\uFF0C\u5F88\u5FEB\u53C8\u88AB\u66F4\u591A\u9700\u8981\u5F81\u670D\u7684\u5723\u5730\u6240\u5438\u5F15\uFF0C\u5976\u5B50\uFF0C\u5927\u817F\uFF0C\u795E\u79D8\u7684\u4E09\u89D2\u533A\u57DF\uFF0C\u5929\u54EA\uFF0C\u592A\u5962\u4F88\u4E86\u3002</p><p>\u4ED6\u7684\u624B\u6307\u98A4\u6296\u8D77\u6765\uFF0C\u5F80\u4E0B\u7A0D\u7A0D\u7528\u529B\uFF0C\u65B0\u716E\u9E21\u86CB\u822C\u96EA\u767D\u7684\u53CC\u4E18\u5C31\u50CF\u892A\u53BB\u4E91\u5F69\u7684\u5723\u6D01\u96EA\u5C71\uFF0C\u6162\u6162\u5730\uFF0C\u4E00\u70B9\u70B9\u5730\uFF0C\u5265\u9732\u5728\u4ED6\u7684\u9762\u524D\u3002</p><p>\u300C\u545C\u2026\u2026\u300D</p><p>\u51B7\u5982\u971C\u88AB\u5835\u4F4F\u7684\u5634\u5DF4\u91CC\u53D1\u51FA\u4E86\u6700\u540E\u4E00\u58F0\u957F\u957F\u7684\u60B2\u9E23\u3002</p><p>\u300C\u7239\uFF0C\u5A18\uFF0C\u6EA2\u4E4B\uFF0C\u6551\u6211\u554A\u2026\u2026\u300D</p><p>\u4E8C\u559C\u4E4B\u7684\u8138\u5DF2\u7ECF\u5B8C\u5168\u626D\u66F2\uFF0C\u5728\u706B\u5149\u4E0B\u663E\u5F97\u90A3\u4E48\u72F0\u72DE\u3002</p><p>\u7A81\u7136\uFF0C\u4E00\u58F0\u949D\u54CD\uFF0C\u4E8C\u559C\u5B50\u8111\u540E\u53D7\u5230\u91CD\u51FB\uFF0C\u6574\u4E2A\u8EAB\u5B50\u59D4\u987F\u5728\u5730\u3002</p><p>\u80CC\u540E\uFF0C\u7AD9\u7740\u8138\u8272\u94C1\u9752\u7684\u6D77\u68E0\u548C\u91D1\u82B1\u3002</p><p>\u6708\u8272\u51B7\u51B7\uFF0C\u70DB\u706B\u6447\u6447\u3002</p><p>\u51B7\u5982\u971C\u5DF2\u6362\u4E0A\u6D77\u68E0\u7684\u8863\u670D\uFF0C\u8138\u8272\u6728\u7136\u5730\u5750\u5728\u5E8A\u8FB9\uFF0C\u8138\u51B2\u6D1E\u58C1\uFF0C\u65E0\u60B2\u65E0\u559C\u3002</p><p>\u521A\u624D\u7684\u6253\u51FB\u5C31\u7B97\u5BF9\u4E00\u822C\u7684\u5973\u4EBA\u6765\u8BF4\u90FD\u5B9E\u5728\u662F\u592A\u5927\u4E86\uFF0C\u867D\u7136\u6CA1\u6709\u6700\u540E\u5931\u5B88\uFF0C\u4F46\u5931\u8D1E\u7684\u75DB\u82E6\u611F\u53D7\u6709\u8FC7\u4E4B\u800C\u65E0\u4E0D\u53CA\u3002</p><p>\u6D77\u68E0\u53EA\u6482\u4E0B\u4E86\u4E00\u53E5\u8BDD\uFF0C\u300C\u6211\u4F1A\u7ED9\u4F60\u4E00\u4E2A\u4EA4\u5F85\u3002\u300D\u8BF4\u7F62\u63D0\u67AA\u8D70\u51FA\u95E8\u5916\u3002</p><p>\u4E8C\u559C\u5B50\u88AB\u6346\u5728\u4E00\u6839\u5927\u9752\u7AF9\u4E0A\uFF0C\u9762\u8272\u4ED3\u60F6\uFF0C\u5C71\u5BE8\u4F17\u4EBA\u90FD\u56F4\u5728\u4E00\u65C1\uFF0C\u6C14\u6C1B\u5341\u5206\u51DD\u91CD\u3002</p><p>\u6D77\u68E0\u8D70\u51FA\u53BB\u65F6\uFF0C\u6B63\u597D\u770B\u5230\u94F6\u53F6\u51B2\u5230\u4E8C\u559C\u5B50\u9762\u524D\uFF0C\u72E0\u72E0\u6247\u4E86\u4ED6\u4E00\u4E2A\u5927\u5634\u5DF4\uFF0C\u6CEA\u6C34\u4E5F\u6B62\u4E0D\u4F4F\u6EDA\u843D\u4E0B\u6765\u3002</p><p>\u6D77\u68E0\u8981\u94F6\u53F6\u9000\u5F00\uFF0C\u5207\u9F7F\u9053\uFF1A\u300C\u4E8C\u559C\u5B50\uFF0C\u6211\u4F1A\u7ED9\u4F60\u591A\u70E7\u51E0\u67F1\u9999\uFF0C\u5FF5\u51E0\u5377\u7ECF\uFF0C\u597D\u8BA9\u4F60\u5230\u4E86\u960E\u7F57\u7237\u90A3\u91CC\u80FD\u65E9\u70B9\u6295\u80CE\u3002\u300D</p><p>\u4E8C\u559C\u5B50\u568E\u53EB\u8D77\u6765\uFF0C\u300C\u6211\u5728\u66FF\u83B2\u9999\u62A5\u4EC7\u54C7\uFF01\u68E0\u59D0\uFF0C\u4E8C\u559C\u5B50\u4E3A\u4F60\u51FA\u751F\u5165\u6B7B\uFF0C\u6CA1\u6709\u529F\u52B3\u4E5F\u6709\u82E6\u52B3\u5440\uFF01\u300D</p><p>\u300C\u4F60\u4E0D\u662F\u4E0D\u77E5\u9053\uFF0C\u6211\u751F\u5E73\u6700\u6068\u7684\u5C31\u662F\u5978\u6DEB\u4E4B\u4E8B\uFF0C\u8C01\u72AF\u5929\u6761\u5C31\u662F\u627E\u6B7B\u3002\u300D\u6D77\u68E0\u8BF4\u7F62\uFF0C\u62AC\u67AA\u8981\u6253\u3002</p><p>\u94F6\u53F6\u7A81\u7136\u8F6C\u5230\u6D77\u68E0\u524D\u5934\u8DEA\u4E86\u4E0B\u6765\uFF0C\u626F\u4F4F\u5979\u7684\u8863\u8896\uFF0C\u54ED\u9053\uFF1A\u300C\u68E0\u59D0\uFF0C\u90FD\u662F\u81EA\u5BB6\u5144\u5F1F\uFF0C\u4E00\u8D77\u6D41\u8FC7\u8840\uFF0C\u5171\u8FC7\u60A3\u96BE\u7684\uFF0C\u653E\u4E00\u6761\u751F\u8DEF\u5427\u3002\u300D</p><p>\u91D1\u82B1\u968F\u5373\u8DEA\u4E0B\uFF0C\u4F17\u4EBA\u5168\u90FD\u8DEA\u4E86\u4E0B\u6765\uFF0C\u300C\u6C42\u68E0\u59D0\u5F00\u6069\u3002\u300D</p><p>\u6D77\u68E0\u5176\u5B9E\u4E5F\u662F\u77DB\u76FE\u75DB\u82E6\u4E4B\u6781\uFF0C\u5979\u53C8\u4F55\u5C1D\u5BF9\u4E8C\u559C\u5B50\u6CA1\u6709\u5144\u5F1F\u4E4B\u60C5\uFF0C\u751F\u6B7B\u4E4B\u4E49\uFF0C\u53C8\u4F55\u5C1D\u613F\u610F\u81EA\u65AD\u8180\u81C2\uFF0C\u6253\u51FB\u58EB\u6C14\uFF0C\u6068\u53EA\u6068\u554A\u8FD9\u4E8C\u559C\u5B50\u4E0D\u4E89\u6C14\uFF0C\u81EA\u53D6\u706D\u4EA1\uFF0C\u6068\u53EA\u6068\u554A\u81EA\u5DF1\u5FC3\u80A0\u592A\u8F6F\uFF0C\u7EC8\u96BE\u75DB\u4E0B\u6740\u5FC3\u3002</p><p>\u6D77\u68E0\u770B\u7740\u8DEA\u4E86\u4E00\u5730\u7684\u5144\u5F1F\u59D0\u59B9\uFF0C\u4E0D\u7531\u5F97\u5FC3\u4E2D\u957F\u53F9\uFF0C\u7F62\u4E86\u7F62\u4E86\u3002\u8138\u4E0A\u4F9D\u7136\u9634\u973E\u6D53\u91CD\uFF0C\u5389\u58F0\u9053\uFF1A\u300C\u5C31\u7B97\u6B7B\u7F6A\u53EF\u514D\uFF0C\u6D3B\u7F6A\u96BE\u9003\uFF0C\u8D35\u751F\uFF0C\u62BD\u4ED6\u516B\u5341\u91CD\u97AD\uFF0C\u62BD\u6B7B\u6D3B\u8BE5\uFF0C\u62BD\u4E0D\u6B7B\u6254\u5230\u5C71\u4E0B\u53BB\uFF0C\u7531\u4ED6\u81EA\u751F\u81EA\u706D\u3002\u300D\u8BF4\u7F62\u5934\u4E5F\u4E0D\u56DE\u8FDB\u4E86\u5185\u6D1E\u3002</p><p>\u4ECE\u6D1E\u53E3\u770B\u8FC7\u53BB\uFF0C\u6B63\u597D\u53EF\u4EE5\u770B\u5230\u4E8C\u559C\u5B50\u53D7\u5211\u7684\u573A\u9762\uFF0C\u97AD\u62A1\u5F97\u547C\u547C\u6D77\u54CD\uFF0C\u8840\u82B1\u56DB\u6E85\u3002\u8D35\u751F\u7EB5\u7136\u624B\u4E0B\u7559\u4E86\u70B9\u60C5\uFF0C\u516B\u5341\u97AD\u4E5F\u4E0D\u662F\u4E00\u822C\u4EBA\u7ECF\u5F97\u8D77\u7684\uFF0C\u4E8C\u559C\u5B50\u5F88\u5FEB\u6210\u4E86\u4E2A\u8840\u4EBA\uFF0C\u8FD9\u5BB6\u4F19\u5012\u4E5F\u786C\u6C14\uFF0C\u54AC\u7740\u7259\u4E00\u76F4\u62BD\u5230\u6655\u6B7B\u4E5F\u6CA1\u5F04\u51FA\u591A\u5927\u52A8\u9759\u3002</p><p>\u4EB2\u773C\u89C1\u7740\u6C61\u8FB1\u81EA\u5DF1\u7684\u4EBA\u53D7\u5230\u4E86\u4E25\u60E9\uFF0C\u51B7\u5982\u971C\u5FC3\u4E2D\u603B\u7B97\u597D\u8FC7\u4E86\u4E00\u70B9\u3002</p><p>\u6D77\u68E0\u5750\u5230\u5E8A\u8FB9\uFF0C\u67D4\u58F0\u8BF4\uFF1A\u300C\u59B9\u5B50\uFF0C\u6211\u80FD\u4F53\u4F1A\u4F60\u6B64\u65F6\u7684\u5FC3\u60C5\uFF0C\u6211\u4E5F\u662F\u4E2A\u88AB\u7537\u4EBA\u5BB3\u60E8\u8FC7\u7684\u82E6\u547D\u4EBA\u554A\u3002\u300D</p><p>\u5979\u4E0D\u7BA1\u51B7\u5982\u971C\u4F1A\u4E0D\u4F1A\u542C\uFF0C\u81EA\u987E\u81EA\u5C31\u628A\u8BDD\u5323\u5B50\u62C9\u5F00\u4E86\u3002</p>',111);function t(n,i){return r}var _=a(e,[["render",t]]);export{_ as default};
