import{e as p}from"./app.630af5f2.js";import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";const e={},r=p('<h1 id="\u7B2C\u5341\u516B\u7AE0-\u590D\u4EC7" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u5341\u516B\u7AE0-\u590D\u4EC7" aria-hidden="true">#</a> \u7B2C\u5341\u516B\u7AE0 \u590D\u4EC7</h1><p>\u591C\u6DF1\u4EBA\u5026\uFF0C\u5373\u4FBF\u662F\u98CE\u6708\u9601\u4E5F\u4ECE\u55A7\u56A3\u6162\u6162\u5F52\u4E8E\u5E73\u9759\u3002</p><p>\u4E09\u697C\u7684\u4E00\u95F4\u7F15\u91D1\u96D5\u82B1\u7684\u623F\u95F4\uFF0C\u95E8\u7A97\u7D27\u95ED\uFF0C\u9690\u7EA6\u4F20\u6765\u4E0D\u65AD\u606F\u7684\u7537\u5973\u4EA4\u5ABE\u7684\u5598\u606F\u58F0\u3001\u547B\u541F\u58F0\u3002</p><p>\u300C\u5A4A\u5B50\uFF0C\u6211\u4EEC\u4E5F\u7B97\u662F\u60A3\u96BE\u89C1\u771F\u60C5\u4E86\uFF0C\u5148\u6765\u4E2A\u9E33\u68A6\u91CD\u5706\u5427\u3002\u300D</p><p>\u51B7\u5982\u971C\u6700\u540E\u4E00\u5C42\u906E\u63A9\u7269\u65E9\u5DF2\u892A\u5C3D\uFF0C\u5728\u7537\u4EBA\u7684\u6387\u62FE\u4E0B\uFF0C\u5F04\u6210\u4E86\u72D7\u8DB4\u7684\u59FF\u5F0F\uFF0C\u5979\u7701\u8D77\u8FD9\u6B63\u662F\u4E00\u5E74\u524D\u5979\u88AB\u6D77\u68E0\u7ED1\u67B6\u4E0A\u5C71\uFF0C\u738B\u559C\u8BD5\u56FE\u4FB5\u72AF\u5979\u65F6\u672A\u9042\u7684\u6A21\u6837\uFF0C\u7ED3\u679C\u62EF\u6551\u53CA\u65F6\uFF0C\u738B\u559C\u8FD8\u5DEE\u70B9\u4E22\u4E86\u5C0F\u547D\uFF0C\u60F3\u4E0D\u5230\u8FD8\u60E6\u8BB0\u7740\u3002</p><p>\u8FD9\u6076\u68CD\u7684\u62A5\u590D\u5FC3\u597D\u53EF\u6015\u554A\u3002</p><p>\u867D\u7136\u5411\u4E0B\u8DB4\u7740\u7684\u59FF\u5F0F\u8BA9\u7537\u4EBA\u65E0\u6CD5\u5C3D\u7779\u73AB\u7470\u82B1\u56ED\u7684\u5999\u5904\uFF0C\u4F46\u8038\u7ACB\u7684\u96EA\u81C0\uFF0C\u7C89\u7EA2\u7684\u7389\u809B\u5DF2\u7136\u6EE1\u8DB3\u4ED6\u7684\u89C6\u5978\uFF0C\u4ECA\u65F6\u4E0D\u540C\u5F80\u65E5\uFF0C\u4ED6\u518D\u4E5F\u4E0D\u5FC5\u987E\u8651\u4EC0\u4E48\uFF0C\u6614\u65E5\u9AD8\u9AD8\u5728\u4E0A\u7684\u51B0\u5C71\u7F8E\u5973\u5DF2\u6210\u5979\u5634\u91CC\u7684\u4E00\u5757\u80A5\u8089\uFF0C\u53EA\u5F85\u4ED6\u600E\u4E48\u4E0B\u53E3\u4E86\u3002\u767D\u5929\u5FB7\uFF0C\u53BB\u4ED6\u5988\u7684\uFF0C\u8001\u5B50\u8FD8\u771F\u6015\u4E86\u4ED6\u4E0D\u6210\u3002</p><p>\u65F6\u95F4\u8FD8\u6709\u597D\u957F\u597D\u957F\u3002</p><p>\u4ED6\u5634\u89D2\u6302\u7740\u4E00\u4E1D\u6DEB\u7B11\uFF0C\u5927\u6A21\u5927\u6837\u5C06\u624B\u677F\u4ECE\u80EF\u95F4\u5F80\u524D\u6284\u53BB\u3002\u90A3\u79CD\u6E05\u51C9\u67D4\u8F6F\u7684\u611F\u89C9\u8BA9\u4ED6\u5FC3\u5E95\u723D\u5230\u4E86\u6781\u70B9\uFF0C\u4E0B\u8EAB\u4E00\u54C6\u55E6\uFF0C\u4ECE\u9F9F\u5934\u55B7\u6D12\u51FA\u4E00\u80A1\u6DB2\u4F53\uFF0C\u6253\u5728\u5973\u4EBA\u5C16\u7FD8\u7684\u5C41\u80A1\u8089\u4E0A\uFF0C\u6CC4\u4E86\u3002</p><p>\u51B7\u5982\u971C\u9ED8\u4E0D\u4F5C\u58F0\uFF0C\u738B\u559C\u81EA\u5DF1\u96BE\u582A\uFF0C\u5F88\u5C11\u5728\u5973\u4EBA\u9762\u524D\u51FA\u8FD9\u6837\u7684\u4E11\uFF0C\u5C31\u7B97\u4E0A\u6B21\u5728\u767D\u5929\u5FB7\u7684\u540E\u82B1\u56ED\u4E2D\u96C6\u4F53\u8F6E\u5978\u51B7\u5982\u971C\u65F6\u4E5F\u6CA1\u7FFB\u8239\uFF0C\u53EA\u89C9\u5F97\u4F59\u97F5\u672A\u5C3D\uFF0C\u53CD\u800C\u5728\u6700\u4E0D\u8BE5\u53D1\u751F\u7684\u65F6\u5019\u53D1\u751F\u4E86\uFF0C\u4E0D\u7531\u5F97\u4F4E\u58F0\u9A82\u4E86\u4E00\u53E5\uFF0C\u300C\u64CD\uFF01\u300D</p><p>\u5973\u4EBA\u626F\u8FC7\u8349\u7EB8\uFF0C\u81EA\u5DF1\u5C06\u810F\u7269\u64E6\u5E72\u51C0\uFF0C\u5E73\u8EBA\u5728\u5E8A\u4E0A\u3002\u5979\u4E0D\u8BF4\u8BDD\uFF0C\u795E\u6001\u95F4\u5374\u5206\u660E\u5145\u6EE1\u4E86\u8511\u89C6\uFF0C\u4F3C\u4E4E\u5728\u8BF4\u4F60\u4E8C\u559C\u5B50\u5C31\u662F\u4E2A\u94F6\u6837\u8721\u67AA\u5934\uFF0C\u65E0\u80FD\u5E9F\u7269\u3002\u8FD9\u5BB6\u4F19\u53D7\u4E0D\u4E86\u8FD9\u523A\u6FC0\uFF0C\u62A5\u590D\u6027\u5730\u62B1\u7740\u5973\u4EBA\u7684\u80F4\u4F53\u62DA\u547D\u6478\u5543\uFF0C\u624B\u6307\u56DB\u4E0B\u91CC\u4E71\u62A0\u4E71\u6478\u3002\u7B49\u5F85\u4E0B\u4E00\u8F6E\u7684\u52C3\u8D77\u3002</p><p>\u51B7\u5982\u971C\u91C7\u53D6\u975E\u62B5\u6297\u4E0D\u5408\u4F5C\u7684\u5BF9\u7B56\uFF0C\u53CC\u817F\u7EDE\u5F97\u7D27\u7D27\u7684\uFF0C\u4EFB\u51ED\u7537\u4EBA\u600E\u4E48\u5F04\uFF0C\u5C31\u50CF\u5177\u5C38\u4F53\u4E00\u6837\u4E00\u52A8\u4E0D\u52A8\u3002</p><p>\u5979\u6CA1\u6599\u5230\u7684\u662F\u8FD9\u6837\u8FD8\u4E0D\u884C\uFF0C\u7537\u4EBA\u814B\u4E0B\u6709\u80A1\u72D0\u81ED\uFF0C\u626B\u8FC7\u6765\u65F6\u90A3\u6D53\u70C8\u7684\u81BB\u5473\u771F\u628A\u51B7\u5982\u971C\u6076\u5FC3\u5F97\u60F3\u6B7B\u6389\uFF0C\u53EA\u597D\u5F3A\u884C\u5C4F\u4F4F\u547C\u5438\uFF0C\u5C06\u5934\u626D\u5230\u4E00\u8FB9\u3002</p><p>\u738B\u559C\u6CE8\u610F\u5230\u4E86\uFF0C\u66F4\u662F\u72C2\u6012\uFF0C\u4F30\u6478\u7740\u4E3B\u610F\uFF0C\u5FFD\u7136\u90AA\u7B11\u4E86\u7B11\uFF0C\u653E\u5F00\u624B\uFF0C\u5149\u7740\u811A\u5C31\u4E0B\u5E8A\u53BB\u4E86\uFF0C\u53BB\u4E86\u597D\u4E00\u9635\u65F6\u95F4\u4E5F\u6CA1\u56DE\u6765\u3002</p><p>\u51B7\u5982\u971C\u4E0D\u77E5\u9053\u4ED6\u53BB\u5E72\u4EC0\u4E48\uFF0C\u4E5F\u4E0D\u60F3\u77E5\u9053\uFF0C\u88AB\u6298\u817E\u4E86\u8FD9\u4E48\u4E45\uFF0C\u4E5F\u5B9E\u5728\u72AF\u56F0\u4E86\uFF0C\u6253\u8D77\u76F9\u6765\u3002</p><p>\u8FF7\u7CCA\u4E2D\u5979\u611F\u89C9\u5230\u81EA\u5DF1\u7684\u8EAB\u5B50\u88AB\u4FA7\u4E86\u8FC7\u6765\uFF0C\u4E24\u53EA\u624B\u8155\u62D6\u5230\u8EAB\u540E\uFF0C\u7528\u7EC6\u7EF3\u6346\u5728\u4E00\u8D77\uFF0C\u4E0D\u592A\u91CD\uFF0C\u8EAB\u5B50\u53C8\u653E\u5E73\uFF0C\u4E24\u53EA\u811A\u5927\u5927\u5730\u6253\u5F00\u6765\u3002</p><p>\u5979\u7684\u610F\u8BC6\u5C1A\u672A\u6E05\u9192\uFF0C\u5BFB\u601D\u5927\u4E0D\u4E86\u53C8\u662F\u7537\u4EBA\u53D8\u6001\u7684\u4E3E\u52A8\u7F62\u4E86\uFF0C\u8EAB\u4F53\u53EA\u662F\u88AB\u52A8\u5730\u968F\u4EBA\u6446\u5E03\u3002</p><p>\u597D\u50CF\u5979\u72ED\u5C0F\u7684\u7389\u6237\u88AB\u624B\u6307\u6491\u5F00\u6765\uFF0C\u6709\u4E2A\u4E1C\u897F\u6491\u5728\u91CC\u9762\uFF0C\u6709\u4E9B\u75BC\u75DB\uFF0C\u4E00\u4E9B\u4EC0\u4E48\u4E1C\u897F\u503E\u5012\u8FDB\u4E86\u5979\u65E0\u906E\u65E0\u6321\u7684\u6D1E\u91CC\u2026\u2026</p><p>\u300C\u554A\uFF01\u2026\u2026\u545C\u2026\u2026\u300D\u51B7\u5982\u971C\u7A81\u7136\u60E8\u53EB\u51FA\u58F0\uFF0C\u7ACB\u523B\u53C8\u88AB\u7537\u4EBA\u91CD\u91CD\u6440\u4F4F\uFF0C\u5F3A\u884C\u585E\u8FDB\u4E00\u6761\u6C57\u5DFE\u3002</p><p>\u4E0D\u77E5\u9053\u7537\u4EBA\u5F80\u5979\u7684\u4E0B\u8EAB\u585E\u4E86\u4EC0\u4E48\u4E1C\u897F\uFF0C\u51B7\u5982\u971C\u53D1\u73B0\u9634\u6237\u5230\u5C0F\u8179\u5C31\u50CF\u88AB\u70C8\u706B\u707C\u8FC7\uFF0C\u6781\u75DB\u6781\u9EBB\u6781\u75D2\uFF0C\u4EA4\u7EC7\u5728\u4E00\u8D77\uFF0C\u597D\u50CF\u540C\u65F6\u5C06\u4E16\u754C\u4E0A\u7684\u9177\u5211\u52A0\u8BF8\u5979\u4E00\u8EAB\uFF0C\u8FD8\u662F\u4ECE\u5185\u5F80\u5916\u7206\u53D1\uFF0C\u771F\u662F\u6BD4\u6B7B\u8FD8\u96BE\u53D7\u7684\u611F\u89C9\u3002\u4E24\u817F\u4E0D\u80FD\u5E76\u62E2\uFF0C\u66F4\u4E0D\u80FD\u4E71\u52A8\uFF0C\u5FAE\u4E00\u52A8\u5F39\u90A3\u79CD\u6298\u78E8\u5C31\u662F\u4E00\u6CE2\u63A5\u4E00\u6CE2\u7684\u4FB5\u88AD\u3002</p><p>\u5979\u5F00\u59CB\u6CA1\u80FD\u660E\u767D\uFF0C\u8EAB\u4F53\u6323\u624E\u4E86\u51E0\u4E0B\uFF0C\u75DB\u75D2\u5F97\u5DEE\u70B9\u53D1\u75AF\uFF0C\u773C\u775B\u6CDB\u767D\u3002\u53CC\u624B\u53CD\u6346\u5728\u80CC\u540E\uFF0C\u53EA\u6709\u96EA\u767D\u7684\u5927\u817F\u9AD8\u4E3E\u5728\u7A7A\u4E2D\u65E0\u529B\u65E0\u52A9\u5730\u8E39\u7740\uFF0C\u50CF\u4E00\u53EA\u5782\u6B7B\u7684\u9752\u86D9\u3002</p><p>\u738B\u559C\u7AD9\u5728\u5E8A\u6CBF\uFF0C\u6DEB\u7B11\u5730\u7740\u770B\uFF0C\u5C31\u50CF\u89C2\u8D4F\u4E00\u573A\u6B8B\u5FCD\u7684\u8868\u6F14\uFF0C\u8FD8\u60A0\u7136\u5730\u70B9\u4E0A\u4E00\u6839\u70DF\u3002</p><p>\u51B7\u5982\u971C\u989D\u5934\u5DF2\u7ECF\u662F\u7EC6\u6C57\u6CE0\u6CE0\uFF0C\u53EF\u80FD\u662F\u6709\u4E00\u70B9\u70B9\u9002\u5E94\u4E86\uFF0C\u4F53\u5185\u603B\u7B97\u6CA1\u6709\u518D\u7FFB\u6C5F\u5012\u6D77\uFF0C\u4F46\u5979\u4E5F\u53EA\u80FD\u5C06\u4E24\u817F\u5C48\u819D\u6253\u5F00\u7740\uFF0C\u8FD8\u662F\u5C3D\u91CF\u6253\u5F00\u5230\u6781\u81F4\uFF0C\u624D\u80FD\u7A0D\u5FAE\u6CA1\u90A3\u4E48\u96BE\u53D7\u3002</p><p>\u5F53\u7136\uFF0C\u4E5F\u4E0D\u80FD\u518D\u963B\u6B62\u738B\u559C\u62FF\u4E00\u53CC\u8272\u773C\u6B7B\u6B7B\u5730\u5F80\u5979\u7EA4\u6BEB\u6BD5\u73B0\u7684\u80EF\u95F4\u91CC\u77A7\u3002</p><p>\u5979\u6068\u6068\u5730\u76EF\u7740\u4ED6\uFF0C\u773C\u91CC\u55B7\u51FA\u706B\u3002</p><p>\u738B\u559C\u7B11\u7B11\uFF0C\u6162\u6162\u4F38\u51FA\u4E00\u6839\u624B\u6307\uFF0C\u4F7F\u52B2\u6309\u5728\u5973\u4EBA\u7684\u9634\u6237\u4E0A\u7AEF\uFF0C\u5AE9\u6ED1\u7684\u809A\u76AE\u4E0A\u3002</p><p>\u5973\u4EBA\u518D\u6B21\u75AF\u72C2\u5730\u626D\u52A8\u8D77\u6765\uFF0C\u53E3\u91CC\u4ECE\u585E\u6EE1\u624B\u5DFE\u7684\u7F1D\u9699\u4E2D\u53D1\u51FA\u4F4E\u6C89\u7684\u5636\u543C\u58F0\uFF0C\u9634\u6237\u5DF2\u7ECF\u7FFB\u7EA2\u53D8\u80BF\u3002</p><p>\u5F85\u5F97\u5E73\u9759\u4E0B\u6765\uFF0C\u51B7\u5982\u971C\u5DF2\u662F\u6CEA\u6D41\u6EE1\u9762\uFF0C\u773C\u795E\u4E2D\u7EC8\u4E8E\u9732\u51FA\u4E5E\u601C\u7684\u76EE\u5149\u3002</p><p>\u300C\u628A\u820C\u5934\u4F38\u51FA\u6765\u3002\u300D\u738B\u559C\u547D\u4EE4\u9053\u3002\u51B7\u5982\u971C\u8FD9\u6B21\u542C\u8BDD\u5730\u4F38\u51FA\u4E86\u7C89\u7EA2\u7684\u820C\u5C16\uFF0C\u300C\u4F38\u957F\u2026\u2026\u518D\u4F38\u957F\u2026\u2026\u592B\u4EBA\u542C\u8BDD\u7684\u65F6\u5019\uFF0C\u8FD8\u771F\u50CF\u4E00\u5934\u53D1\u6625\u7684\u6BCD\u72D7\u5462\uFF0C\u5475\u5475\u2026\u2026\u300D</p><p>\u51B7\u5982\u971C\u6B32\u54ED\u65E0\u6CEA\u3002</p><p>\u7537\u4EBA\u628A\u957F\u957F\u7684\u70DF\u7070\u5F39\u5230\u5979\u7684\u820C\u5934\u4E0A\uFF0C\u53EB\u5979\u541E\u8FDB\u809A\u91CC\u3002\u53C8\u5C06\u72D0\u81ED\u7684\u814B\u7A9D\u67B6\u5728\u5979\u7684\u9F3B\u5B54\u4E0A\uFF0C\u4EE4\u5979\u5927\u58F0\u5438\uFF0C\u65E0\u58F0\u547C\uFF0C\u51B7\u5982\u971C\u4E5F\u4E56\u4E56\u7167\u529E\uFF0C\u7EB5\u7136\u5E72\u5455\u4E86\u597D\u51E0\u58F0\uFF0C\u521A\u624D\u7684\u50B2\u6C14\u8361\u7136\u65E0\u5B58\u3002</p><p>\u738B\u559C\u65B9\u624D\u7B11\u9053\uFF0C\u300C\u8FD9\u624D\u4E56\u561B\u2026\u2026\u81ED\u5A4A\u5B50\uFF0C\u4E0D\u7ED9\u70B9\u989C\u8272\u786C\u662F\u4E0D\u6653\u5F97\u8001\u5B50\u59D3\u751A\u540D\u8C01\uFF0C\u8001\u5B50\u5BF9\u4ED8\u4F60\u591A\u7684\u662F\u529E\u6CD5\u3002\u672C\u60F3\u7528\u5728\u6D77\u68E0\u90A3\u81ED\u5A4A\u5B50\u8EAB\u4E0A\u7684\uFF0C\u5148\u7ED9\u4F60\u4EAB\u53D7\u4EAB\u53D7\uFF0C\u60F3\u6653\u5F97\u662F\u4EC0\u4E48\u4E1C\u897F\u6574\u5F97\u4F60\u6B7B\u53BB\u6D3B\u6765\u5417\uFF1F\u300D</p><p>\u4ED6\u9A91\u5230\u5973\u4EBA\u7684\u8138\u4E0A\uFF0C\u626F\u51FA\u585E\u53E3\u5DFE\uFF0C\u628A\u52C3\u8D77\u8001\u9AD8\u7684\u9633\u5177\u63D2\u8FDB\u53E3\u4E2D\u3002\u5973\u4EBA\u7684\u6A80\u53E3\u7740\u5B9E\u592A\u79C0\u6C14\uFF0C\u5C31\u7B97\u7537\u4EBA\u7684\u9E21\u5DF4\u4E0D\u5927\u4E5F\u53EA\u54BD\u5F97\u4E0B\u4E00\u5C0F\u534A\u3002\u738B\u559C\u4E00\u8FB9\u7528\u529B\u5F80\u5979\u5589\u5934\u6324\u4E00\u8FB9\u81EA\u5DF1\u56DE\u7B54\uFF0C\u300C\u732A\u9B03\u7684\u788E\u5C51\uFF0C\u786C\u5EA6\u4E0D\u9519\uFF0C\u97E7\u5EA6\u591F\u52B2\uFF0C\u597D\u597D\u73A9\u5427\u3002\u300D</p><p>\u51B7\u5982\u971C\u88AB\u63D2\u5F97\u4E24\u773C\u7FFB\u767D\uFF0C\u504F\u751F\u4E24\u6761\u817F\u8FD8\u5F97\u8D39\u529B\u9AD8\u4E3E\u7740\u4E0D\u6562\u7A0D\u52A8\uFF0C\u60F3\u6B7B\u7684\u5FC3\u90FD\u6709\uFF0C\u54EA\u8FD8\u6709\u4E1D\u6BEB\u4ED6\u8BB2\u5F97\u597D\u73A9\u3002</p><p>\u300C\u5BF9\u4E86\uFF0C\u6211\u8FD8\u8981\u8BB2\u4E00\u4EF6\u62DB\u4F60\u6068\u7684\u4E8B\uFF0C\u300D\u738B\u559C\u7684\u8EAB\u4F53\u4E0E\u611F\u53D7\u90FD\u6500\u4E0A\u4E86\u5FEB\u611F\u7684\u6781\u81F4\uFF0C\u300C\u4F60\u90A3\u4E2A\u4E2B\u5934\u5C0F\u91D1\u5B9D\uFF0C\u6B7B\u5728\u8001\u5B50\u624B\u91CC\u4E86\uFF0C\u8001\u5B50\u628A\u5979\u7684\u809A\u5B50\u5256\u5F00\uFF0C\u5978\u6B7B\u4E86\u5979\uFF01</p><p>\u6068\u6211\u5427\uFF0C\u54C8\u54C8\u3002\u300D</p><p>\u300C\u545C\u2026\u2026\u300D\u51B7\u5982\u971C\u4ECE\u5589\u5934\u53D1\u51FA\u4E00\u58F0\u957F\u54ED\u3002</p><p>\u90A3\u4E00\u970E\uFF0C\u738B\u559C\u7EC8\u4E8E\u7206\u53D1\uFF0C\u5927\u91CF\u7684\u7CBE\u6DB2\u55B7\u5C04\u51FA\u6765\uFF0C\u6D8C\u6EE1\u5973\u4EBA\u7684\u5589\u7BA1\u2026\u2026</p><p>\u6708\u8FC7\u56DB\u66F4\uFF0C\u7537\u4EBA\u65E9\u5DF2\u7CBE\u75B2\u529B\u7AED\uFF0C\u8DB4\u5728\u51B7\u5982\u971C\u80F8\u812F\u4E0A\u6C89\u6C89\u7761\u53BB\uFF0C\u4E00\u53EA\u624B\u8FD8\u7D27\u7D27\u634F\u7740\u5979\u7684\u4E00\u53EA\u4E73\u5934\u3002</p><p>\u51B7\u5982\u971C\u5706\u7741\u7740\u8D64\u7EA2\u7684\u773C\u775B\uFF0C\u6346\u7740\u7684\u624B\u653E\u5F00\u4E86\uFF0C\u4F9D\u7136\u6CA1\u6709\u4E00\u70B9\u7761\u610F\uFF0C\u5979\u600E\u4E48\u7761\u5F97\u7740\u5462\uFF1F\u4E24\u6761\u817F\u65E9\u5DF2\u9178\u75DB\u81F3\u9EBB\u6728\u4E5F\u65E0\u6CD5\u5408\u62E2\u653E\u4E0B\uFF0C\u7389\u6237\u65E9\u5C31\u80BF\u4E86\uFF0C\u50CF\u4E2A\u767D\u9762\u5C0F\u9992\u5934\uFF0C\u4F53\u5185\u8FD8\u5728\u6301\u7EED\u4E0D\u65AD\u5730\u9EBB\u75D2\u75BC\u75DB\uFF0C\u4F7F\u5979\u65E0\u6CD5\u96C6\u4E2D\u7CBE\u529B\u53BB\u60B2\u4F24\u3002</p><p>\u8FD9\u771F\u662F\u51FA\u79BB\u75DB\u82E6\u7684\u75DB\u82E6\u4E86\u3002</p><p>\u5979\u6CA1\u7559\u610F\u623F\u95E8\u6084\u6084\u5730\u6253\u5F00\u4E86\uFF0C\u4E00\u6761\u9ED1\u5F71\u8E51\u624B\u8E51\u811A\u8D70\u5230\u5E8A\u524D\uFF0C\u4E3E\u8D77\u4E00\u628A\u5200\u5B50\uFF0C\u5F80\u738B\u559C\u88F8\u9732\u7684\u80CC\u4E0A\u7528\u529B\u63D2\u53BB\u3002</p><p>\u8FD9\u4E00\u5200\u53EF\u80FD\u6B63\u63D2\u7740\u9AA8\u5934\uFF0C\u7ADF\u6CA1\u8FDB\u53BB\u591A\u6DF1\uFF0C\u7537\u4EBA\u5DF2\u7ECF\u75DB\u9192\uFF0C\u6B63\u5F85\u7FFB\u8EAB\u800C\u8D77\uFF0C\u4E0D\u6599\u51B7\u5982\u971C\u4F38\u51FA\u80F3\u818A\u6B7B\u6B7B\u62B1\u4F4F\u4E86\u4ED6\u3002</p><p>\u7EA0\u7F20\u95F4\u7B2C\u4E8C\u5200\u843D\u4E0B\uFF0C\u6B63\u4E2D\u5FC3\u80BA\uFF0C\u968F\u5373\u7B2C\u4E09\u5200\u7B2C\u56DB\u5200\u2026\u2026</p><p>\u4E4C\u9ED1\u7684\u9C9C\u8840\u51B2\u4E0A\u5E10\u9876\uFF0C\u53E3\u9F3B\u4E2D\u4E5F\u6EA2\u51FA\u8840\u6C41\uFF0C\u6EE1\u8EAB\u8840\u4EBA\u75AF\u72C2\u5730\u51B2\u5F00\u675F\u7F1A\uFF0C\u6F2B\u65E0\u76EE\u7684\u5730\u5728\u5730\u4E0A\u8F6C\u4E86\u4E24\u5708\uFF0C\u683D\u5012\u5728\u5730\uFF0C\u6323\u624E\u62BD\u6410\u4E86\u51E0\u4E0B\uFF0C\u65E0\u58F0\u65E0\u606F\u5730\u6B7B\u4E86\u3002</p><p>\u8FD9\u4E00\u5207\u7684\u53D1\u751F\u4E5F\u4E0D\u8FC7\u5728\u51E0\u79D2\u4E4B\u5185\uFF0C\u7535\u5149\u706B\u77F3\u5C31\u662F\u4E00\u6761\u4EBA\u547D\u62A5\u9500\u3002</p><p>\u51B7\u5982\u971C\u60CA\u9B42\u672A\u5B9A\uFF0C\u521A\u624D\u7684\u884C\u4E3A\u5B8C\u5168\u662F\u76F4\u89C9\u4F7F\u7136\uFF0C\u8FDE\u4E0B\u8EAB\u7684\u6298\u78E8\u90FD\u4E00\u65F6\u629B\u5F00\uFF0C\u6B64\u523B\u53C8\u52A0\u500D\u56DE\u6765\u4E86\uFF0C\u4E0D\u7981\u75DB\u82E6\u5730\u547B\u541F\u4E00\u58F0\u3002</p><p>\u51F6\u624B\u672C\u6B32\u79BB\u53BB\uFF0C\u542C\u5230\u547B\u541F\u58F0\u53C8\u6298\u8FD4\u8FC7\u6765\uFF0C\u95EE\uFF0C\u300C\u4F60\u8FD9\u662F\u600E\u4E48\u5566\uFF1F\u300D</p><p>\u5979\u7ADF\u7136\u662F\u94F6\u53F6\uFF01\u4E0D\u662F\u770B\u5230\u767D\u5929\u5979\u88AB\u5173\u5728\u7AD9\u7B3C\u4E2D\u53D7\u5230\u6B8B\u5FCD\u7684\u8F6E\u5978\u5417\uFF1F\u6765\u4E0D\u53CA\u7EC6\u60F3\uFF0C\u51B7\u5982\u971C\u628A\u539F\u59D4\u544A\u8BC9\u4E86\u5979\u3002</p><p>\u94F6\u53F6\u6F20\u65E0\u8868\u60C5\u5730\u8BF4\uFF0C\u300C\u77E5\u9053\u4E86\uFF0C\u7B49\u4E00\u4E0B\u3002\u300D</p><p>\u5979\u6084\u7136\u6E9C\u4E86\u51FA\u53BB\uFF0C\u8D70\u8DEF\u8FD8\u662F\u4E0D\u592A\u5229\u7D22\u3002\u4E0D\u591A\u65F6\uFF0C\u5E26\u4E86\u4E00\u6761\u80A5\u732A\u8089\u548C\u4E00\u76C6\u6E29\u6C34\u8FC7\u6765\uFF0C\u8BF4\uFF0C\u300C\u5FCD\u7740\u554A\u3002\u300D</p><p>\u80A5\u8089\u6761\u5728\u6E29\u6C34\u91CC\u6D78\u6CE1\u4E86\u4E00\u4E0B\uFF0C\u50CF\u9633\u5177\u4E00\u6837\u4E00\u70B9\u70B9\u63D2\u8FDB\u51B7\u5982\u971C\u7684\u9634\u6237\uFF0C\u90A3\u79CD\u751F\u6B7B\u4E0D\u80FD\u7684\u611F\u89C9\u91CD\u65B0\u964D\u4E34\uFF0C\u51B7\u5982\u971C\u6B7B\u6B7B\u54AC\u4F4F\u88AB\u89D2\uFF0C\u5F3A\u8FEB\u81EA\u5DF1\u4E0D\u53D1\u51FA\u58F0\u97F3\u6765\u3002</p><p>\u94F6\u53F6\u628A\u8089\u6761\u62BD\u51FA\u6765\u65F6\uFF0C\u4E0A\u9762\u5DF2\u7ECF\u5E03\u6EE1\u4E86\u9ED1\u9ED1\u7C97\u77ED\u7684\u6BDB\u53D1\u5C51\uFF0C\u89E6\u76EE\u60CA\u5FC3\u3002</p><p>\u300C\u771F\u662F\u4E2A\u755C\u751F\uFF01\u300D\u94F6\u53F6\u9A82\u9053\uFF0C\u5207\u4E0B\u53E6\u4E00\u5757\u8089\u6761\uFF0C\u518D\u585E\u8FDB\u9634\u6237\u4E2D\u3002</p><p>\u53CD\u590D\u51E0\u6B21\u4E4B\u540E\uFF0C\u8089\u6761\u51FA\u6765\u65F6\u7EC8\u4E8E\u5E72\u5E72\u51C0\u51C0\uFF0C\u867D\u7136\u9634\u6237\u4F9D\u7136\u7EA2\u80BF\uFF0C\u4F46\u4F53\u5185\u518D\u4E5F\u6CA1\u6709\u6298\u78E8\uFF0C\u8F7B\u677E\u4E86\u597D\u591A\uFF0C\u957F\u677E\u4E86\u4E00\u53E3\u6C14\u3002</p><p>\u51B7\u5982\u971C\u62AB\u8863\u5750\u8D77\uFF0C\u5BF9\u57CB\u5934\u6536\u62FE\u4E1C\u897F\u7684\u94F6\u53F6\u8877\u5FC3\u8BF4\u9053\uFF0C\u300C\u8C22\u8C22\u4F60\uFF0C\u59B9\u5B50\u3002\u300D</p><p>\u94F6\u53F6\u5934\u4E5F\u4E0D\u56DE\uFF0C\u51B7\u51B7\u8BF4\u9053\uFF0C\u300C\u4E0D\u8981\u8C22\u6211\uFF0C\u6211\u662F\u53EF\u601C\u4F60\uFF0C\u4F9D\u6211\u7684\u672C\u610F\u662F\u8FDE\u4F60\u4E00\u5757\u6740\u6389\u7684\u3002\u300D</p><p>\u51B7\u5982\u971C\u51C4\u7136\u9053\uFF0C\u300C\u771F\u662F\u90A3\u6837\u5C31\u597D\u4E86\uFF0C\u6211\u4E5F\u4E00\u6B7B\u767E\u4E86\uFF0C\u4E0D\u7528\u518D\u53D7\u8FD9\u822C\u78E8\u96BE\u3002\u300D</p><p>\u94F6\u53F6\u54FC\u4E00\u58F0\uFF0C\u300C\u60F3\u6B7B\uFF0C\u90A3\u8FD8\u4E0D\u5BB9\u6613\uFF0C\u6211\u5012\u662F\u60F3\u6D3B\uFF0C\u53EF\u6068\u7684\u8001\u5929\u5374\u4E0D\u7ED9\u6211\u673A\u4F1A\u4E86\u3002\u300D</p><p>\u51B7\u5982\u971C\u60CA\u9053\uFF0C\u300C\u90A3\u662F\u4E3A\u4F55\uFF1F\u300D</p><p>\u6162\u6162\u5730\u56DE\u8FC7\u5934\u6765\uFF0C\u94F6\u53F6\u6D88\u7626\u7684\u8138\u4E0A\u5DF2\u662F\u6E05\u6CEA\u4E24\u884C\uFF0C\u300C\u6211\u59D0\u59D0\u88AB\u90A3\u822C\u755C\u751F\u6298\u78E8\u6B7B\u540E\uFF0C\u6211\u7684\u5FC3\u4E5F\u968F\u5979\u6B7B\u4E86\uFF0C\u53EF\u6211\u8FD8\u662F\u6D3B\u4E86\u4E0B\u6765\uFF0C\u6211\u8981\u62A5\u4EC7\uFF0C\u672C\u6765\u6709\u4E00\u4E2A\u6700\u597D\u7684\u673A\u4F1A\uFF0C\u53EF\u4EE5\u5E72\u6389\u767D\u72D7\uFF0C\u53EF\u662F\u2026\u2026\u53EF\u662F\u2026\u2026\u300D</p><p>\u5979\u7684\u76EE\u5149\u9510\u5229\u5730\u770B\u7740\u51B7\u5982\u971C\uFF0C\u300C\u53EF\u662F\u4F60\u7684\u6B7B\u9B3C\u8001\u516C\u4E0D\u81EA\u91CF\u529B\uFF0C\u90A3\u4E00\u665A\u53BB\u88AD\u51FB\u767D\u72D7\uFF0C\u642D\u4E0A\u81EA\u5DF1\u4E00\u6761\u4E0D\u8BF4\uFF0C\u8FD8\u574F\u4E86\u6211\u7684\u5927\u8BA1\uFF0C\u4ECE\u6B64\u540E\u518D\u4E5F\u6CA1\u6709\u529E\u6CD5\uFF0C\u4F60\u8BF4\uFF0C\u6211\u6068\u4E0D\u6068\u4F60\uFF1F\u300D</p><p>\u51B7\u5982\u971C\u65B9\u624D\u660E\u767D\u94F6\u53F6\u5BF9\u5979\u7684\u654C\u610F\u5E76\u4E0D\u5B8C\u5168\u662F\u4E3A\u4E86\u6D77\u68E0\u3002</p><p>\u300C\u6211\u8FD8\u662F\u5FCD\uFF0C\u5C31\u7B97\u90A3\u7FA4\u72D7\u4F7F\u52B2\u7CDF\u8E4B\u6211\uFF0C\u6211\u4E5F\u5FCD\uFF0C\u6211\u4E00\u518D\u5B89\u6170\u81EA\u5DF1\uFF0C\u603B\u6709\u4E00\u5929\u6211\u4F1A\u62A5\u4EC7\u7684\uFF0C\u7ED3\u679C\uFF0C\u7ED3\u679C\u2026\u2026\u300D</p><p>\u5979\u5931\u58F0\u75DB\u82E6\uFF0C\u4E24\u624B\u5C06\u81EA\u5DF1\u5355\u8584\u7C97\u52A3\u7684\u5E03\u888D\u4E0B\u6446\uFF0C\u5979\u7EC6\u957F\u7626\u5F31\u7684\u817F\u6746\u4E0A\u5E03\u6EE1\u4E86\u75A4\u75D5\uFF0C\u66F4\u53EF\u6015\u7684\u8FD8\u662F\u5979\u7684\u4E0B\u8EAB\uFF0C\u96C6\u4E2D\u5728\u9634\u6237\u548C\u5927\u817F\u5185\u4FA7\uFF0C\u7ADF\u957F\u6EE1\u4E86\u6697\u7EA2\u8272\u4E11\u964B\u7684\u75B9\u5768\uFF0C\u6709\u7684\u5F00\u59CB\u6E83\u70C2\u3002</p><p>\u51B7\u5982\u971C\u542C\u5982\u610F\u8BB2\u8FC7\uFF0C\u98CE\u6708\u5757\u4E0A\u6700\u53EF\u6015\u7684\u4E8B\u83AB\u8FC7\u4E8E\u67D3\u4E0A\u810F\u75C5\uFF0C\u6709\u4E9B\u810F\u75C5\u65E0\u836F\u53EF\u6CBB\uFF0C\u53EA\u6709\u7B49\u6B7B\uFF0C\u5993\u5BE8\u91CC\u5F53\u7136\u4E5F\u4E0D\u4F1A\u767D\u6254\u94B1\u7ED9\u4F60\u53BB\u6CBB\u75C5\uFF0C\u5979\u5C31\u4EB2\u773C\u770B\u5230\u8FC7\u591A\u5C11\u59D0\u59B9\u4E0D\u662F\u88AB\u9A71\u8D76\u51FA\u53BB\uFF0C\u5C31\u662F\u88AB\u4E00\u5F20\u8349\u5E2D\u5305\u7740\u62AC\u51FA\u53BB\u3002</p><p>\u51B7\u5982\u971C\u4E00\u4E0B\u660E\u767D\u4E86\uFF0C\u94F6\u53F6\u4E5F\u4E0D\u5E78\u67D3\u4E0A\u4E86\u6076\u75BE\uFF0C\u6765\u65E5\u65E0\u591A\uFF0C\u53D7\u4E86\u8FD9\u4E48\u591A\u82E6\u96BE\uFF0C\u800C\u62A5\u4EC7\u5927\u8BA1\u6108\u53D1\u6E3A\u832B\uFF0C\u600E\u4E0D\u4EE4\u5979\u60B2\u4ECE\u4E2D\u6765\u3002</p><p>\u94F6\u53F6\u64E6\u64E6\u773C\u775B\uFF0C\u8BF4\u9053\uFF0C\u300C\u4E5F\u7B97\u6885\u795E\u53EF\u601C\u6211\u5427\uFF0C\u8BA9\u6211\u4ECA\u5929\u77A7\u89C1\u4E86\u738B\u559C\u8FD9\u4E2A\u72D7\u755C\u751F\uFF0C\u4E00\u5207\u7978\u5BB3\u90FD\u662F\u4ED6\u5E26\u6765\u7684\uFF0C\u767D\u72D7\u6740\u4E0D\u4E86\uFF0C\u81EA\u4F1A\u6709\u4EBA\u6740\uFF0C\u738B\u72D7\u6211\u62DA\u6B7B\u4E5F\u4E0D\u4F1A\u653E\u8FC7\u4ED6\uFF01\u300D</p><p>\u51B7\u5982\u971C\u4F38\u51FA\u624B\uFF0C\u94F6\u53F6\u5374\u8EB2\u5F00\u4E86\uFF0C\u6062\u590D\u4E86\u758F\u79BB\u7684\u8868\u60C5\uFF0C\u8BF4\uFF0C\u300C\u4E0D\u8981\u78B0\u6211\uFF0C\u6211\u548C\u4F60\u4E0D\u662F\u4E00\u7C7B\u4EBA\uFF0C\u4F60\u4E0D\u7528\u53EF\u601C\u6211\uFF0C\u6211\u4E5F\u4E0D\u4F1A\u540C\u60C5\u4F60\u3002\u300D</p><p>\u5979\u987F\u4E86\u987F\uFF0C\u7EED\u9053\uFF0C\u300C\u4F60\u653E\u5FC3\uFF0C\u6211\u505A\u7684\u4E8B\uFF0C\u6211\u4E00\u4EBA\u62C5\uFF01\u300D</p><p>\u51B7\u5982\u971C\u8FD8\u6CA1\u6765\u5F97\u53CA\u8BF4\u4EC0\u4E48\uFF0C\u94F6\u53F6\u5DF2\u626D\u5934\u62C9\u5F00\u4E86\u623F\u95E8\uFF0C\u7AD9\u5728\u56DE\u5ECA\u51B2\u7740\u7A7A\u8361\u8361\u7684\u5929\u4E95\u5927\u58F0\u558A\u9053\uFF0C\u300C\u6211\u6740\u4EBA\u4E86\uFF01\u72D7\u65E5\u7684\u738B\u559C\u8BA9\u6211\u5BB0\u6389\u5566\uFF01\u300D</p><p>\u65CB\u5373\uFF0C\u6574\u4E2A\u5929\u9999\u9601\u9A9A\u52A8\u4E86\uFF0C\u8863\u88F3\u4E0D\u6574\u7684\u7537\u7537\u5973\u5973\u4ECE\u5404\u4E2A\u89D2\u843D\u6D8C\u4E86\u51FA\u6765\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u767D\u5929\u5FB7\u6247\u4E86\u51B7\u5982\u971C\u4E00\u8BB0\u8033\u5149\uFF0C\u300C\u5A4A\u5B50\uFF0C\u4F60\u505A\u7684\u597D\u4E8B\u3002\u300D</p><p>\u51B7\u5982\u971C\u65E0\u8A00\uFF0C\u773C\u5149\u907F\u8FC7\u4ED6\u9510\u5229\u7684\u950B\u8292\uFF0C\u843D\u5230\u5730\u4E0A\u3002</p><p>\u767D\u5929\u5FB7\u521A\u521A\u8D76\u5230\uFF0C\u5C4B\u5B50\u91CC\u7684\u5C38\u4F53\u65E9\u62AC\u51FA\u53BB\u4E86\uFF0C\u94F6\u53F6\u4E5F\u675F\u624B\u5C31\u64D2\uFF0C\u6CA1\u6709\u8D39\u4EFB\u4F55\u5468\u6298\uFF0C\u767D\u5929\u5FB7\u8FD8\u662F\u89C9\u5F97\u6C14\u607C\u96BE\u5E73\uFF0C\u5012\u4E0D\u662F\u53EF\u60DC\u4E86\u738B\u559C\u7684\u4E00\u6761\u72D7\u547D\uFF0C\u4ED6\u4E5F\u65E9\u6709\u6740\u5FC3\u4E86\uFF0C\u501F\u5200\u6740\u4EBA\u4E5F\u4E0D\u9519\uFF0C\u95EE\u9898\u662F\u6740\u65E9\u4E86\uFF0C\u66F4\u5927\u7684\u95EE\u9898\u662F\u8131\u79BB\u4E86\u4ED6\u7684\u63A7\u5236\uFF0C\u5728\u4ED6\u5B8C\u5168\u4E0D\u77E5\u60C5\u7684\u60C5\u51B5\u4E0B\u53D1\u751F\u7684\u4E8B\u4EF6\u3002</p><p>\u5982\u679C\u662F\u51B2\u4ED6\u6765\u7684\u5462\uFF1F\u767D\u5929\u5FB7\u89C9\u5F97\u540E\u6015\u3002</p><p>\u7A97\u5916\uFF0C\u4F20\u6765\u94F6\u53F6\u7684\u75BE\u547C\uFF0C\u300C\u6C85\u9547\u7684\u8001\u5C11\u7237\u4EEC\uFF0C\u4F60\u4EEC\u7684\u597D\u65E5\u5B50\u4E5F\u5230\u5934\u5566\uFF01\u4F60\u4EEC\u4E00\u4E2A\u94DC\u677F\u5E72\u8001\u5A18\uFF0C\u8001\u5A18\u5C31\u628A\u4E00\u8EAB\u810F\u75C5\u5168\u90FD\u4F20\u4F60\u4EEC\u5566\uFF0C\u7B49\u7740\u6536\u5C38\u5427\uFF0C\u54C8\u54C8\u54C8\u2026\u2026\u300D</p><p>\u5979\u5927\u58F0\u72C2\u7B11\u8D77\u6765\u3002</p><p>\u767D\u5929\u5FB7\u51B2\u5230\u4E86\u7A97\u53E3\uFF0C\u66B4\u6012\u5728\u9053\uFF0C\u300C\u4F60\u4EEC\u5403\u5C4E\u7684\u554A\uFF0C\u8FD8\u4E0D\u628A\u5979\u7684\u81ED\u5634\u7ED9\u6211\u5C01\u8D77\u6765\uFF01\u300D</p><p>\u4ED6\u50CF\u53EA\u56F0\u517D\u5728\u5C4B\u91CC\u6253\u8F6C\uFF0C\u300C\u5988\u7684\uFF0C\u8001\u5B50\u8981\u7EDE\u6B7B\u5979\uFF0C\u4E00\u5B9A\u8981\u7EDE\u6B7B\u5979\u3002\u300D</p><p>\u6B63\u5728\u6B64\u65F6\uFF0C\u8FDC\u8FDC\u4F20\u6765\u5BC6\u96C6\u7684\u67AA\u58F0\u548C\u7206\u70B8\u58F0\uFF0C\u767D\u5929\u5FB7\u60CA\u5413\u5F97\u4E00\u54C6\u55E6\uFF0C\u7247\u523B\u540E\uFF0C\u674E\u8D35\u8FDE\u6EDA\u5E26\u722C\u5730\u95EF\u8FDB\u6765\uFF0C\u60CA\u60F6\u5931\u63AA\u5730\u558A\u9053\uFF0C\u300C\u53BF\u957F\u4E0D\u597D\u5566\uFF0C\u571F\u532A\u6253\u8FC7\u6765\u4E86\uFF01\u300D</p><p>\u5728\u6C85\u9547\u7684\u53BF\u5FD7\u4E0A\u8FD9\u6837\u8BB0\u8F7D\uFF0C\u300C\u6C11\u56FD\u5341\u516B\u5E74\uFF0C\u6625\uFF0C\u532A\u60A3\u7A81\u53D1\uFF0C\u7EA0\u96C6\u5343\u4F59\u532A\u4F17\u7316\u72C2\u8FDB\u72AF\uFF0C\u89C4\u6A21\u4E4B\u5DE8\uFF0C\u5386\u65F6\u4E4B\u957F\uFF0C\u53F2\u6240\u7F55\u89C1\u3002\u300D</p><p>\u65B0\u5E74\u521A\u8FC7\uFF0C\u5927\u5730\u56DE\u6625\u65F6\u8282\uFF0C\u4E0A\u5343\u7684\u571F\u532A\u4ECE\u6C85\u9547\u7684\u56DB\u9762\u516B\u65B9\u5192\u4E86\u51FA\u6765\uFF0C\u547C\u5578\u7740\u5F00\u59CB\u56F4\u653B\u4E00\u4E2A\u519B\u4E8B\u91CD\u9547\uFF0C\u6218\u4E89\u7EC8\u4E8E\u964D\u4E34\u5230\u4E86\u8FD9\u5757\u5C1A\u672A\u56DE\u6696\u7684\u571F\u5730\u4E0A\uFF0C\u5E73\u9759\u7684\u65E5\u5B50\u4E00\u53BB\u4E0D\u8FD4\u3002</p>',84);function t(i,c){return r}var h=a(e,[["render",t]]);export{h as default};
