import{e as p}from"./app.630af5f2.js";import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";const e={},r=p('<h1 id="\u7B2C\u5341\u4E8C\u7AE0-\u957F\u591C" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u5341\u4E8C\u7AE0-\u957F\u591C" aria-hidden="true">#</a> \u7B2C\u5341\u4E8C\u7AE0 \u957F\u591C</h1><p>\u5929\u7834\u6653\uFF0C\u4E0B\u4E86\u4E00\u591C\u8C6A\u96E8\uFF0C\u7A7A\u6C14\u4E2D\u90FD\u662F\u6E7F\u6F09\u6F09\u7684\u3002</p><p>\u5F53\u767D\u5929\u5FB7\u5E26\u7740\u674E\u8D35\u3001\u4E8C\u559C\u5B50\u548C\u51E0\u4E2A\u56E2\u4E01\u5927\u6447\u5927\u6446\u5730\u8D70\u8FDB\u5218\u5B85\u7684\u65F6\u5019\uFF0C\u51B7\u5982\u971C\u6B63\u895F\u5371\u5750\u5728\u5802\u5C4B\u9636\u524D\uFF0C\u8138\u8272\u548C\u8863\u88F3\u4E00\u6837\u5168\u8EAB\u7D20\u767D\uFF0C\u91D1\u5B9D\u8083\u7A46\u5730\u4F8D\u7ACB\u5728\u8EAB\u540E\u3002</p><p>\u51B7\u5982\u971C\u6574\u6574\u5750\u4E86\u5927\u534A\u591C\uFF0C\u542C\u7740\u67AA\u58F0\u8D77\uFF0C\u67AA\u58F0\u7A00\u843D\uFF0C\u67AA\u58F0\u6D88\u5931\uFF0C\u5218\u6EA2\u4E4B\u5374\u4E00\u76F4\u672A\u5F52\uFF0C\u5FC3\u5934\u5DF2\u6709\u51F6\u5146\uFF0C\u89C1\u5230\u6765\u4EBA\uFF0C\u5C31\u660E\u767D\u6700\u574F\u7684\u7ED3\u5C40\u51FA\u73B0\u4E86\uFF0C\u82B3\u5FC3\u5BF8\u65AD\uFF0C\u76F4\u5760\u5165\u4E07\u4E08\u6DF1\u6E0A\uFF0C\u770B\u5230\u4E86\u4EBA\u7FA4\u540E\u8EB2\u8EB2\u95EA\u95EA\u7684\u4E00\u4E2A\u4EBA\uFF0C\u53C8\u62B1\u4E86\u4E00\u4E1D\u4FA5\u5E78\uFF0C\u8BF4\u9053\uFF0C\u300C\u53F8\u9A6C\u5148\u751F\uFF0C\u6211\u7684\u4E08\u592B\u5462\uFF1F\u300D</p><p>\u53F8\u9A6C\u5357\u53EA\u5F97\u73B0\u8EAB\u51FA\u6765\uFF0C\u6EE1\u9762\u7F9E\u6127\uFF0C\u671F\u671F\u827E\u827E\u5730\u8BF4\uFF0C\u300C\u5BF9\u4E0D\u8D77\u592A\u592A\uFF0C\u53F8\u9A6C\u5357\u4E0D\u53EF\u80FD\u505A\u505A\u4E0D\u5230\u7684\u4E8B\u60C5\u3002\u300D</p><p>\u4E8B\u5B9E\u4E0A\uFF0C\u767D\u5929\u5FB7\u81EA\u5DF1\u5FC3\u4E2D\u6709\u9B3C\uFF0C\u867D\u5C31\u4E00\u76F4\u5728\u76D1\u89C6\u5218\u6EA2\u4E4B\u7B49\u4EBA\u7684\u4E3E\u52A8\uFF0C\u5218\u6EA2\u4E4B\u4F1A\u89C1\u5EB7\u8001\u7237\u5B50\u3001\u4E0E\u53F8\u9A6C\u5357\u5BC6\u8C0B\u90FD\u5728\u767D\u7684\u638C\u63E1\u4E4B\u4E2D\uFF0C\u53EA\u662F\u6CA1\u60F3\u5230\u4ED6\u4F1A\u4E0B\u624B\u90A3\u4E48\u5FEB\uFF0C\u4F46\u767D\u5929\u5FB7\u53CD\u5E94\u66F4\u5FEB\uFF0C\u5229\u7528\u4ED6\u4EEC\u5206\u5175\u51FA\u51FB\u7684\u5F31\u70B9\u8FC5\u901F\u7EC4\u7EC7\u8D77\u6709\u6548\u7684\u529B\u91CF\u5404\u4E2A\u51FB\u7834\uFF0C\u5E76\u631F\u5236\u4E86\u53F8\u9A6C\u5357\u7684\u5BB6\u4EBA\uFF0C\u903C\u8FEB\u4ED6\u4E34\u9635\u53CD\u6C34\uFF0C\u63A7\u5236\u4E86\u5C40\u52BF\uFF0C\u53EF\u601C\u5218\u6EA2\u4E4B\u79C0\u624D\u5E26\u5175\u5341\u5E74\u4E0D\u6210\uFF0C\u6D3B\u6D3B\u846C\u9001\u5728\u767D\u5929\u5FB7\u7684\u67AA\u53E3\u4E4B\u4E0B\u3002</p><p>\u767D\u5929\u5FB7\u5E72\u54B3\u4E00\u58F0\u3002</p><p>\u51B7\u5982\u971C\u6839\u672C\u4E0D\u7406\u4ED6\uFF0C\u773C\u7736\u53D1\u7EA2\uFF0C\u53EA\u76EF\u4F4F\u53F8\u9A6C\u5357\uFF0C\u91CD\u590D\u9053\uFF0C\u300C\u6211\u7684\u4E08\u592B\u5462\uFF1F\u300D</p><p>\u53F8\u9A6C\u5357\u773C\u795E\u95EA\u907F\uFF0C\u5782\u4E0B\u5934\u53BB\u3002\u767D\u5929\u5FB7\u53EB\u4ED6\u9000\u5F00\uFF0C\u7B11\u9053\uFF0C\u300C\u592A\u592A\uFF0C\u5218\u6EA2\u4E4B\u5BF9\u6211\u4E0D\u4EC1\uFF0C\u6211\u767D\u67D0\u5BF9\u4ED6\u6709\u4E49\uFF0C\u600E\u4E48\u4E0D\u4F1A\u8BA9\u4F60\u4EEC\u592B\u59BB\u76F8\u89C1\u5462\uFF1F\u300D</p><p>\u4EBA\u7FA4\u4E24\u5206\uFF0C\u4E00\u5E45\u62C5\u67B6\u62AC\u4E86\u51FA\u6765\uFF0C\u505C\u4E8E\u573A\u5730\u4E2D\u592E\uFF0C\u9000\u5F00\u3002\u767D\u5E03\u63ED\u5F00\uFF0C\u5218\u6EA2\u4E4B\u5E73\u8EBA\u5728\u4E0A\u9762\uFF0C\u80F8\u53E3\u6B63\u4E2D\u4E00\u4E2A\u5F39\u5B54\uFF0C\u6D78\u5F00\u6210\u7897\u5927\u7684\u8840\u82B1\uFF0C\u65E9\u5DF2\u6C14\u7EDD\u8EAB\u4EA1\u3002</p><p>\u300C\u6EA2\u4E4B\u2026\u2026\u300D</p><p>\u51B7\u5982\u971C\u773C\u524D\u4E00\u9ED1\uFF0C\u6655\u6B7B\u8FC7\u53BB\u3002</p><p>\u60A0\u60A0\u9192\u8F6C\u65F6\uFF0C\u91D1\u5B9D\u8FD8\u6402\u7740\u5979\uFF0C\u773C\u6CEA\u6C6A\u6C6A\u3002</p><p>\u770B\u6765\u65F6\u95F4\u4E0D\u957F\uFF0C\u4F17\u4EBA\u7686\u5728\u539F\u5730\uFF0C\u59FF\u6001\u4E5F\u65E0\u53D8\u5316\uFF0C\u90FD\u50CF\u5728\u5B89\u9759\u5730\u7B49\u5F85\u7740\u5979\u3002</p><p>\u767D\u5929\u5FB7\u76EF\u7740\u8FD9\u53EA\u5F85\u5BB0\u7684\u7F94\u7F8A\uFF0C\u6D6E\u51FA\u6696\u6627\u7684\u7B11\u610F\u3002</p><p>\u5927\u60B2\u75DB\u4E4B\u540E\uFF0C\u51B7\u5982\u971C\u5012\u6709\u4E9B\u9547\u5B9A\u4E86\u4E0B\u6765\uFF0C\u53EA\u662F\u51B0\u51C9\u6CEA\u6C34\u6B62\u4E0D\u4F4F\u5730\u6D41\u4E86\u51FA\u6765\uFF0C\u300C\u59D3\u767D\u7684\uFF0C\u770B\u6765\u4F60\u4E5F\u4E0D\u6253\u7B97\u653E\u8FC7\u6211\u4EEC\u4E86\u3002\u300D</p><p>\u767D\u5929\u5FB7\u6F2B\u9053\uFF0C\u300C\u6089\u542C\u592A\u592A\u5C0A\u4FBF\u3002\u300D</p><p>\u51B7\u5982\u971C\u8BF4\u9053\uFF0C\u300C\u8D22\u4EA7\u4F60\u968F\u4FBF\u62FF\uFF0C\u6211\u7684\u8EAB\u5B50\u6E05\u6E05\u767D\u767D\uFF0C\u51B3\u610F\u4E00\u6B7B\uFF0C\u53EA\u6709\u4E00\u4E2A\u8BF7\u6C42\uFF0C\u6C42\u4F60\u653E\u8FC7\u91D1\u5B9D\u4ED6\u4EEC\uFF0C\u4ED6\u4EEC\u65E0\u8F9C\u3002\u300D</p><p>\u91D1\u5B9D\u54ED\u9053\uFF0C\u300C\u6211\u968F\u4F60\u8D70\uFF0C\u592A\u592A\u3002\u300D</p><p>\u767D\u5929\u5FB7\u6C89\u541F\u4E86\u4E00\u4F1A\uFF0C\u6325\u624B\u9053\uFF0C\u300C\u4F60\u4EEC\u90FD\u9000\u4E0B\uFF0C\u8BA9\u6211\u548C\u592A\u592A\u8BF4\u53E5\u8BDD\u3002\u300D\u4F17\u4EBA\u7686\u9000\u51FA\u95E8\u5916\u3002\u300C\u8FD8\u6709\u4F60\uFF0C\u5C0F\u91D1\u5B9D\u3002\u300D\u4ED6\u770B\u7740\u6CEA\u4EBA\u4F3C\u7684\u91D1\u5B9D\u3002</p><p>\u91D1\u5B9D\u62DA\u547D\u5730\u6447\u5934\uFF0C\u51B7\u5982\u971C\u5B89\u7965\u5730\u8BF4\uFF0C\u300C\u5149\u5929\u5316\u65E5\uFF0C\u795E\u7075\u662D\u662D\uFF0C\u6CA1\u6709\u5173\u7CFB\u7684\uFF0C\u4F60\u5148\u5230\u540E\u9662\u6536\u62FE\u4E1C\u897F\u5427\u3002\u300D</p><p>\u7A7A\u65F7\u7684\u9662\u5B50\u4E2D\u53EA\u4F59\u4E0B\u4E24\u4EBA\u3002</p><p>\u767D\u5929\u5FB7\u9053\uFF0C\u300C\u767D\u67D0\u4EBA\u5F88\u5766\u723D\uFF0C\u4ECA\u5929\u6765\u4E00\u4E0D\u4E3A\u8D22\uFF0C\u4E8C\u4E0D\u7B97\u8001\u5E10\uFF0C\u5C31\u53EA\u5E0C\u671B\u4E0E\u592A\u592A\u4E00\u4EB2\u82B3\u6CFD\uFF0C\u800C\u4E14\u6211\u786E\u4FE1\u592A\u592A\u4F1A\u7B54\u5E94\u3002\u300D</p><p>\u51B7\u5982\u971C\u773C\u7751\u4F4E\u5782\uFF0C\u604D\u82E5\u672A\u95FB\u3002</p><p>\u300C\u7406\u7531\u6709\u4E8C\uFF0C\u4E00\u662F\u65E9\u5C31\u542C\u95FB\u592A\u592A\u5BB6\u4E2D\u9AD8\u4EB2\u662F\u6EE1\u6E05\u8D35\u5191\uFF0C\u5929\u5B50\u95E8\u4E0B\uFF0C\u60F3\u5FC5\u6700\u91CD\u8138\u9762\uFF0C\u5982\u679C\u592A\u592A\u5BFB\u6B7B\uFF0C\u6211\u767D\u67D0\u4EBA\u5C06\u592A\u592A\u8D64\u6761\u6761\u7684\u8EAB\u5B50\u6302\u5728\u8D35\u8001\u592A\u7237\u7684\u5927\u95E8\u53E3\uFF0C\u4E0D\u77E5\u51E0\u4F4D\u8001\u4EBA\u5BB6\u548C\u4E61\u90BB\u4E61\u4EB2\u4F1A\u4F5C\u4F55\u611F\u60F3\uFF1F\u300D</p><p>\u51B7\u5982\u971C\u5168\u8EAB\u5267\u9707\uFF0C\u5FCD\u4E0D\u4F4F\u9A82\u9053\uFF0C\u300C\u5351\u9119\u65E0\u803B\uFF01\u300D</p><p>\u300C\u627F\u9022\u5938\u5956\uFF0C\u767D\u67D0\u5927\u6D41\u6C13\u4E00\u4E2A\uFF0C\u4EE5\u6B64\u4E3A\u8363\u554A\u3002\u81F3\u4E8E\u4E8C\u561B\uFF0C\u300D\u4ED6\u7684\u72FC\u773C\u6E9C\u6E9C\u5730\u671B\u5411\u51B7\u5982\u971C\u5706\u9686\u7684\u809A\u5B50\uFF0C\u300C\u542C\u8BF4\u5218\u53BF\u957F\u6709\u5B50\u55E3\u4E86\uFF0C\u8FD8\u6CA1\u6765\u5F97\u53CA\u8D3A\u559C\u554A\uFF0C\u53C8\u542C\u8BF4\u4ED6\u662F\u5BB6\u4E2D\u72EC\u5B50\uFF0C\u5509\u5440\u53EF\u60DC\uFF0C\u5982\u679C\u6709\u4EBA\u4E0D\u5C0F\u5FC3\u628A\u90A3\u521A\u6210\u5F62\u7684\u5B69\u5B50\u5F04\u6CA1\u4E86\uFF0C\u5218\u5BB6\u5C82\u4E0D\u7EDD\u4E86\u540E\uFF1F\u300D</p><p>\u51B7\u5982\u971C\u989D\u5934\u51B7\u6C57\u6CE0\u6CE0\uFF0C\u8138\u8272\u60E8\u767D\uFF0C\u67D4\u5F31\u7684\u8EAB\u5B50\u8C61\u98CE\u4E2D\u84B2\u67F3\u4E00\u822C\u98A4\u6296\u8D77\u6765\u3002</p><p>\u767D\u5929\u5FB7\u8F7B\u58F0\u8BF4\uFF0C\u300C\u597D\u597D\u8003\u8651\u8003\u8651\uFF0C\u522B\u5306\u5FD9\u505A\u51B3\u5B9A\u554A\uFF0C\u51B3\u5B9A\u4E86\u5C31\u6765\u540E\u9662\u627E\u6211\uFF0C\u597D\u5417\uFF1F\u592A\u592A\u3002\u300D\u4ED6\u628A\u592A\u592A\u4E24\u4E2A\u5B57\u52A0\u91CD\u4E86\u8BED\u6C14\u3002</p><p>\u65E0\u529B\u8DEA\u5750\u5728\u5218\u6EA2\u4E4B\u7684\u5C38\u9996\u65C1\uFF0C\u51B7\u5982\u971C\u629A\u7740\u4E08\u592B\u51B0\u51B7\u7684\u8138\uFF0C\u65E0\u58F0\u545C\u54BD\uFF0C\u6E05\u6CEA\u957F\u6D41\u3002</p><p>\u8EAB\u8FB9\u811A\u6B65\u58F0\u5F80\u6765\u9891\u7E41\uFF0C\u5BB6\u4EBA\u7684\u54ED\u58F0\u548C\u56E2\u4E01\u7684\u6012\u9A82\u58F0\u3001\u642C\u52A8\u4E1C\u897F\u58F0\u3001\u7838\u6BC1\u82B1\u74F6\u58F0\u4E0D\u7EDD\u4E8E\u8033\uFF0C\u4F46\u5979\u90FD\u542C\u4E0D\u5230\uFF0C\u770B\u4E0D\u5230\uFF0C\u767D\u75F4\u4E00\u822C\u5730\u5750\u7740\u3002</p><p>\u4E00\u4E2A\u65F6\u8FB0\u8FC7\u53BB\u4E86\uFF0C\u4E24\u4E2A\u65F6\u8FB0\u8FC7\u53BB\u4E86\uFF0C\u65E5\u5934\u4ECE\u4E1C\u632A\u5230\u4E86\u897F\u8FB9\u3002</p><p>\u91D1\u5B9D\u5C06\u8336\u676F\u7AEF\u8FC7\u6765\uFF0C\u5979\u4F9D\u7136\u6CE5\u5851\u6728\u96D5\uFF0C\u52A8\u4E5F\u4E0D\u52A8\u3002</p><p>\u968F\u5373\u91D1\u5B9D\u5C31\u5C16\u53EB\u7740\u88AB\u4ED6\u4EEC\u62D6\u4E86\u8FDB\u53BB\u3002</p><p>\u767D\u5929\u5FB7\u51FA\u53BB\u4E86\u4E00\u8D9F\uFF0C\u53EC\u96C6\u653F\u5E9C\u4EBA\u5458\u548C\u4E61\u7EC5\u5F00\u4E86\u4E00\u4E2A\u7D27\u6025\u4F1A\u8BAE\uFF0C\u62A5\u544A\u6628\u665A\u6709\u5C0F\u80A1\u571F\u532A\u5165\u4FB5\u5230\u57CE\u91CC\uFF0C\u7ECF\u8FC7\u4FDD\u5B89\u56E2\u7684\u594B\u52C7\u6218\u6597\uFF0C\u6BD9\u4F24\u571F\u532A\u82E5\u5E72\uFF0C\u4F46\u53BF\u957F\u5218\u6EA2\u4E4B\u4E0D\u5E78\u4E2D\u5F39\uFF0C\u5149\u8363\u6B89\u804C\u3002\u53F8\u9A6C\u5357\u4EE3\u8868\u53BF\u5E9C\u5BA3\u5E03\uFF0C\u5728\u6B64\u671F\u95F4\u7531\u767D\u5929\u5FB7\u6682\u4EE3\u53BF\u957F\uFF0C\u7F72\u7406\u4E00\u5207\u4E8B\u5B9C\uFF0C\u540C\u65F6\u5C06\u8BE6\u60C5\u4E0A\u62A5\u7701\u5E9C\u3002</p><p>\u5728\u53F8\u9A6C\u5357\u3001\u674E\u8D35\u7B49\u4EBA\u7684\u64CD\u7EB5\u4E0B\uFF0C\u81EA\u7136\u606D\u8D3A\u58F0\u4E00\u7247\uFF0C\u767D\u5929\u5FB7\u5FD7\u5F97\u610F\u6EE1\u5730\u53D1\u8868\u4E86\u91CD\u8981\u8BB2\u8BDD\uFF0C\u8A93\u6B7B\u4FDD\u536B\u4E00\u65B9\u767E\u59D3\u7684\u5E73\u5B89\uFF0C\u4E0D\u94F2\u5C3D\u4E07\u6076\u7684\u571F\u532A\u51B3\u4E0D\u7F62\u4F11\u3002</p><p>\u968F\u540E\uFF0C\u5510\u725B\u9C9C\u8840\u6DCB\u6F13\u7684\u5C38\u4F53\u88AB\u60AC\u6302\u5728\u57CE\u95E8\u53E3\u793A\u4F17\u3002</p><p>\u767D\u5929\u5FB7\u56DE\u5230\u5218\u5B85\u65F6\uFF0C\u5DF2\u662F\u591C\u6DF1\u65F6\u5206\uFF0C\u6574\u4E2A\u8FD9\u91CC\u6212\u5907\u68EE\u4E25\uFF0C\u65E0\u4EBA\u77E5\u6653\u91CC\u9762\u5728\u53D1\u751F\u4EC0\u4E48\u4E8B\u60C5\u3002</p><p>\u4E00\u4F19\u4EBA\u6B63\u5728\u51CC\u8FB1\u91D1\u5B9D\uFF0C\u5C0F\u59D1\u5A18\u88AB\u6309\u5728\u5E8A\u4E0A\u5978\u5F97\u633A\u60E8\uFF0C\u521A\u521A\u7834\u74DC\u7684\u4E0B\u534A\u8EAB\u8840\u7CCA\u7CCA\u7684\uFF0C\u4E00\u6839\u9EC4\u74DC\u7C97\u7684\u8089\u68D2\u6B63\u6345\u5728\u83CA\u809B\u91CC\u6405\u6765\u6405\u53BB\uFF0C\u809B\u8089\u65E9\u5C31\u6495\u7206\u4E86\uFF0C\u4F24\u4E0A\u52A0\u4F24\uFF0C\u5634\u91CC\u8FD8\u53EB\u4E0D\u51FA\u6765\uFF0C\u56E0\u4E3A\u5634\u91CC\u4E5F\u662F\u8BA9\u4E00\u6839\u6076\u5FC3\u7684\u5BB6\u4F19\u585E\u5F97\u6EE1\u6EE1\u7684\uFF0C\u545B\u5F97\u6D41\u6CEA\uFF0C\u5C0F\u8EAB\u5B50\u4E0A\u5E03\u6EE1\u4E86\u6293\u75D5\u548C\u9752\u7D2B\u3002</p><p>\u6B63\u5728\u5978\u5C0F\u59D1\u5A18\u5C41\u773C\u7684\u5C31\u662F\u4E8C\u559C\u5B50\uFF0C\u5979\u8EAB\u4E0A\u7684\u4F24\u591A\u662F\u4E8C\u559C\u5B50\u7559\u4E0B\u6765\u7684\uFF0C\u522B\u4EBA\u90FD\u4E0D\u77E5\u9053\u4ED6\u4E3A\u5565\u4E0B\u624B\u8FD9\u4E48\u72E0\u3002</p><p>\u767D\u5929\u5FB7\u8FDC\u8FDC\u5730\u770B\u4E86\u770B\uFF0C\u5BF9\u80F8\u812F\u5E73\u5766\u7684\u5C0F\u59D1\u5A18\u6CA1\u591A\u5C11\u5174\u8DA3\uFF0C\u78B0\u90FD\u4E0D\u78B0\u3002\u8E31\u6B65\u8FDB\u4E86\u5218\u6EA2\u4E4B\u7684\u5C45\u5BA4\uFF0C\u623F\u95F4\u633A\u5927\uFF0C\u6734\u7D20\u65E0\u534E\uFF0C\u5168\u662F\u4E66\u5377\uFF0C\u4E66\u684C\u4E0A\u4E00\u672C\u6848\u5448\u6279\u6CE8\u7684\u58A8\u8FF9\u90FD\u4F3C\u672A\u5E72\uFF0C\u4E00\u5F20\u5927\u9EC4\u94DC\u5E8A\u6446\u5728\u4E2D\u592E\u3002\u767D\u5929\u5FB7\u6765\u56DE\u8D70\u4E86\u51E0\u6B65\uFF0C\u5FC3\u60C5\u8FD8\u662F\u4E0D\u592A\u5E73\u9759\uFF0C\u4FBF\u5750\u5230\u8EBA\u6905\u4E0A\uFF0C\u80FD\u6E05\u695A\u542C\u5230\u81EA\u5DF1\u5FC3\u810F\u7684\u8DF3\u52A8\u3002\u4ED6\u4E00\u76F4\u5728\u7B49\u5F85\uFF0C\u7B49\u5F85\u4E00\u4E2A\u4EBA\uFF0C\u7B49\u5F85\u4E00\u4E2A\u6FC0\u52A8\u4EBA\u5FC3\u7684\u65F6\u523B\u3002</p><p>\u5979\u5E94\u8BE5\u6765\u4E86\uFF0C\u5979\u771F\u7684\u4F1A\u6765\u5417\uFF1F</p><p>\u51B7\u5982\u971C\u7AD9\u5728\u95E8\u53E3\uFF0C\u77ED\u77ED\u7684\u51E0\u4E2A\u65F6\u8FB0\u8C61\u8DE8\u8FC7\u4E86\u51E0\u5341\u5E74\uFF0C\u53D8\u6210\u4E86\u53E6\u5916\u4E00\u4E2A\u6837\u5B50\uFF0C\u5F62\u5BB9\u6E05\u5CFB\uFF0C\u6CA1\u6709\u4E00\u4E1D\u8840\u8272\uFF0C\u773C\u775B\u91CC\u5374\u5E03\u6EE1\u4E86\u8840\u4E1D\uFF0C\u6574\u4E2A\u4EBA\u50CF\u5E7D\u7075\u4E00\u822C\u98D8\u98D8\u8361\u8361\u3002</p><p>\u767D\u5929\u5FB7\u7AD9\u8D77\u6765\uFF0C\u7B11\u9053\uFF0C\u300C\u60F3\u660E\u767D\u4E86\u5417\uFF1F\u300D</p><p>\u51B7\u5982\u971C\u5636\u58F0\u9053\uFF0C\u300C\u53EB\u4F60\u90A3\u4E9B\u532A\u5175\u628A\u91D1\u5B9D\u653E\u5F00\uFF0C\u6211\u4E0E\u4F60\u8C08\u6761\u4EF6\u3002\u300D</p><p>\u300C\u5982\u82E5\u4E0D\u5462\uFF1F\u300D</p><p>\u300C\u6211\u4E00\u5934\u649E\u6B7B\u5728\u8FD9\u91CC\uFF0C\u4F60\u4EEC\u4EC0\u4E48\u4E5F\u5F97\u4E0D\u5230\u3002\u300D</p><p>\u767D\u5929\u5FB7\u4E0D\u6B32\u903C\u5979\u592A\u751A\uFF0C\u8D70\u51FA\u95E8\uFF0C\u9AD8\u58F0\u53EB\u4E86\u4E00\u58F0\u3002\u4E8C\u559C\u5B50\u4ED6\u4EEC\u505C\u4E86\u4E0B\u6765\uFF0C\u91D1\u5B9D\u5F2F\u8D77\u8EAB\u5B50\uFF0C\u75DB\u82E6\u5730\u547B\u541F\u7740\u3002</p><p>\u300C\u597D\u5566\uFF0C\u8BF4\u5427\u3002\u300D</p><p>\u300C\u4E0D\u884C\uFF0C\u4F60\u8981\u653E\u5979\u8D70\uFF0C\u8FD8\u6709\u90A3\u4E9B\u5BB6\u4EBA\uFF0C\u6211\u770B\u7740\u4ED6\u4EEC\u8D70\u3002\u300D</p><p>\u767D\u5929\u5FB7\u76B1\u8D77\u7709\u5934\uFF0C\u53EB\u4E24\u4E2A\u56E2\u4E01\u628A\u91D1\u5B9D\u6276\u8D77\u6765\uFF0C\u62B9\u4E86\u62B9\u8EAB\u4E0A\u7684\u6C61\u8FF9\uFF0C\u80E1\u4E71\u5957\u4E0A\u8863\u670D\u3002</p><p>\u51B7\u5982\u971C\u671B\u7740\u7A97\u5916\uFF0C\u76EE\u9001\u91D1\u5B9D\uFF0C\u51E0\u4E2A\u5BB6\u4EBA\u76F8\u6400\u76F8\u6276\u8D70\u51FA\u95E8\u53BB\u3002\u5979\u6CA1\u6709\u6CE8\u610F\u5230\u767D\u5929\u5FB7\u51B2\u4E8C\u559C\u5B50\u4F7F\u4E86\u4E2A\u773C\u8272\uFF0C\u4E8C\u559C\u5B50\u6084\u6084\u4ECE\u540E\u95E8\u6E9C\u4E86\u51FA\u53BB\u3002</p><p>\u7EC8\u4E8E\u5230\u644A\u724C\u7684\u65F6\u523B\u4E86\uFF0C\u51B7\u5982\u971C\u6B32\u8A00\u53C8\u6B62\uFF0C\u90A3\u8BDD\u59CB\u7EC8\u8BF4\u4E0D\u51FA\u53E3\u3002</p><p>\u767D\u5929\u5FB7\u51B7\u51B7\u8BF4\u9053\uFF0C\u300C\u8001\u5B50\u7684\u8010\u6027\u53EF\u662F\u6709\u9650\u7684\u3002\u300D</p><p>\u51B7\u5982\u971C\u5FC3\u4E2D\u4E00\u9178\uFF0C\u9053\uFF0C\u300C\u4F60\u8981\u53D1\u4E2A\u6BD2\u8A93\uFF0C\u4E00\u751F\u4E00\u4E16\u4E0D\u51C6\u52A8\u6211\u8FD9\u4E2A\u5B69\u5B50\u7684\u4E00\u6839\u6BDB\u53D1\uFF0C\u8FD8\u8981\u4FDD\u62A4\u4ED6\u4E0D\u53D7\u522B\u4EBA\u7684\u4F24\u5BB3\u3002\u300D</p><p>\u767D\u5929\u5FB7\u9053\uFF0C\u300C\u8001\u5B50\u51ED\u4EC0\u4E48\u8981\u7B54\u5E94\uFF0C\u300D</p><p>\u51B7\u5982\u971C\u76F4\u89C6\u7740\u4ED6\u7684\u773C\u775B\uFF0C\u4E00\u5B57\u4E00\u987F\u5730\u8BF4\u9053\uFF1A\u300C\u51ED\u6211\u7684\u8D1E\u6D01\uFF0C\u6211\u7684\u8EAB\u4F53\uFF0C\u591F\u4E0D\u591F\uFF1F\u300D</p><p>\u5979\u7684\u773C\u795E\u65E0\u6BD4\u60B2\u6006\uFF0C\u58F0\u97F3\u98A4\u6296\uFF0C\u867D\u5A07\u67D4\u65E0\u529B\uFF0C\u4F46\u662F\uFF0C\u6BCF\u4E00\u4E2A\u5B57\u90FD\u6E05\u6E05\u695A\u695A\u5730\u5728\u5927\u5730\u56DE\u8361\uFF0C\u90A3\u4E00\u77AC\u5982\u540C\u5373\u5C06\u4ED8\u51FA\u727A\u7272\u7684\u5973\u795E\u822C\u53D1\u6563\u51FA\u5723\u6D01\u7684\u5149\u8F89\u3002</p><p>\u767D\u5929\u5FB7\u4E5F\u4E0D\u7981\u5728\u6C14\u52BF\u4E0A\u88AB\u538B\u5012\u4E86\uFF0C\u54BD\u4E86\u4E00\u53E3\u53E3\u6C34\u65B9\u90AA\u7B11\u9053\uFF0C\u300C\u90A3\u4F60\u4E5F\u53D1\u4E2A\u6BD2\u8A93\uFF0C\u4E00\u751F\u4E00\u4E16\u505A\u6211\u767D\u5929\u5FB7\u7684\u5974\u96B6\uFF0C\u4E0D\u5F97\u8FDD\u6297\u3002\u300D</p><p>\u79BD\u517D\u822C\u7684\u5BB6\u4F19\u9738\u5360\u4E86\u5218\u6EA2\u4E4B\u7684\u623F\u95F4\uFF0C\u4ED6\u7684\u8D22\u4EA7\uFF0C\u5373\u5C06\u628A\u9B54\u638C\u4F38\u5411\u4ED6\u6700\u5FC3\u7231\u7684\u5973\u4EBA\u3002</p><p>\u4EA4\u6613\u8FBE\u6210\u4E86\u3002</p><p>\u5C4B\u91CC\u53EA\u5269\u4E0B\u4E24\u4E2A\u4EBA\uFF0C\u767D\u5929\u5FB7\uFF0C\u51B7\u5982\u971C\u3002</p><p>\u95E8\u6CA1\u5173\uFF0C\u5C4B\u5916\u56F4\u4E0A\u4E86\u4E00\u5708\u4EBA\uFF0C\u51C6\u786E\u5730\u8BF4\uFF0C\u662F\u540D\u4E49\u4E0A\u53EB\u4EBA\u7684\u4E24\u811A\u79BD\u517D\u3002</p><p>\u767D\u5929\u5FB7\u77E5\u9053\uFF0C\u5982\u679C\u5141\u8BB8\u624B\u4E0B\u8FD9\u4E9B\u6076\u68CD\u8F6E\u5978\uFF0C\u522B\u8BF4\u80CE\u513F\uFF0C\u5C31\u662F\u5927\u4EBA\u4E5F\u4F1A\u6D3B\u6D3B\u5978\u6B7B\uFF0C\u518D\u8BF4\uFF0C\u597D\u4E0D\u5BB9\u6613\u624D\u5C06\u8FD9\u4E2A\u671D\u601D\u66AE\u60F3\u7684\u5C24\u7269\u63A7\u4E8E\u624B\u4E2D\uFF0C\u6CA1\u5C3D\u5174\u4E4B\u524D\u4E5F\u6709\u70B9\u820D\u4E0D\u5F97\u7531\u4EFB\u4ED6\u4EEC\u4F5C\u8DF5\u3002\u4F46\u662F\u4ED6\u5403\u8089\u4E0D\u8BA9\u5F1F\u5144\u4EEC\u559D\u4E00\u70B9\u6C64\u4E5F\u6446\u4E0D\u5E73\uFF0C\u4FBF\u4E0D\u987E\u51B7\u5982\u971C\u7684\u6FC0\u70C8\u53CD\u5BF9\uFF0C\u540C\u610F\u5C06\u95E8\u7A97\u90FD\u5927\u5F00\uFF0C\u8BA9\u4ED6\u4EEC\u9971\u89C8\u79C0\u8272\u3002</p><p>\u7FA4\u72FC\u73AF\u4F3A\u4E2D\uFF0C\u51B7\u5982\u971C\u773C\u4E2D\u84C4\u7740\u6D53\u5F97\u5316\u4E0D\u5F00\u7684\u60B2\u610F\uFF0C\u7D20\u8155\u8F7B\u62AC\uFF0C\u7279\u5730\u4E3A\u5B55\u5987\u8BA2\u5236\u7684\u5BBD\u8863\u5927\u8896\u886B\u65E0\u58F0\u5730\u6ED1\u843D\u5728\u5730\u4E0A\uFF0C\u5979\u7684\u52A8\u4F5C\u975E\u5E38\u6162\uFF0C\u591A\u4E48\u5E0C\u671B\u8FD9\u4E2A\u65F6\u5019\u6709\u4E00\u4E2A\u6D69\u7136\u6B63\u6C14\u7684\u58F0\u97F3\u5927\u559D\uFF0C\u300C\u4F4F\u624B\uFF01\u300D\u4F46\u662F\u6CA1\u6709\u5947\u8FF9\u51FA\u73B0\u3002</p><p>\u5012\u82B1\u857E\u5F62\u7684\u7EE3\u82B1\u62B9\u80F8\u548C\u7EA2\u7EF8\u5185\u88E4\u4E00\u4EF6\u4EF6\u9664\u53BB\uFF0C\u8D64\u88F8\u51FA\u683C\u5916\u767D\u7699\u817B\u6ED1\u7684\u8EAB\u5B50\uFF0C\u4E00\u624B\u6A2A\u7740\u6440\u4F4F\u80F8\u4E73\uFF0C\u4E00\u624B\u63A9\u4F4F\u4E0B\u8EAB\uFF0C\u7AD9\u5728\u4EBA\u7FA4\u4E2D\u95F4\uFF0C\u7F9E\u6124\u5F97\u62AC\u4E0D\u8D77\u5934\u6765\u3002</p><p>\u51B7\u5982\u971C\u7684\u4E73\u623F\u4E0D\u592A\u5927\uFF0C\u50CF\u4E24\u53EA\u5706\u6DA6\u7CBE\u81F4\u7684\u7389\u7897\u5012\u6263\u5728\u80F8\u812F\u4E0A\uFF0C\u5FEB\u8981\u505A\u6BCD\u4EB2\u7684\u4EBA\u4E86\uFF0C\u4E73\u6655\u8FD8\u662F\u7C89\u7EA2\u8272\u7684\uFF0C\u4E73\u5934\u66F4\u662F\u5C0F\u5DE7\u7684\u53EF\u7231\uFF0C\u7C73\u7C92\u4E00\u822C\u3002\u81F3\u4E8E\u4E0B\u8EAB\uFF0C\u9634\u6BDB\u4E5F\u53EA\u6709\u7A00\u758F\u7684\u4E00\u5C0F\u7C07\uFF0C\u7EC6\u7EC6\u5730\u7D27\u8D34\u5728\u5FAE\u5FAE\u575F\u8D77\u7684\u9634\u961C\u4E0A\uFF0C\u7389\u6237\u7684\u989C\u8272\u4E5F\u4E0E\u808C\u80A4\u76F8\u5DEE\u65E0\u51E0\uFF0C\u663E\u5F97\u975E\u5E38\u5E72\u51C0\u3002</p><p>\u5C0F\u8179\u524D\u7AEF\u6709\u5C0F\u5C0F\u7684\u51F8\u8D77\uFF0C\u6BD4\u8D77\u5927\u591A\u6570\u5973\u6027\u6765\uFF0C\u4F9D\u7136\u8FD8\u7B97\u90A3\u4E48\u7EA4\u7EC6\u3002</p><p>\u4F17\u4EBA\u6728\u5076\u4E00\u822C\u50F5\u4F4F\u4E86\uFF0C\u4E00\u751F\u4E4B\u4E2D\u54EA\u6709\u5982\u6B64\u8273\u798F\u80FD\u5F97\u89C1\u5982\u6B64\u7F8E\u4E3D\u7684\u5973\u4F53\uFF0C\u54EA\u91CC\u4E0D\u662F\u73B2\u73D1\u6709\u81F4\uFF0C\u52A8\u4EBA\u9B42\u9B44\uFF1F</p><p>\u5305\u62EC\u767D\u5929\u5FB7\u5728\u5185\uFF0C\u6240\u6709\u4EBA\u7ADF\u6709\u597D\u4E00\u9635\u5931\u795E\uFF0C\u968F\u540E\u624D\u9F50\u58F0\u300C\u54D7\u300D\u5730\u9192\u8FC7\u795E\u6765\uFF0C\u6709\u4EBA\u4E0D\u7981\u5439\u8D77\u4E86\u53E3\u54E8\uFF0C\u8FD8\u6709\u7684\u5F00\u59CB\u6413\u4E0B\u8EAB\u7684\u9E21\u5DF4\u3002</p><p>\u300C\u624B\u653E\u5F00\u3002\u300D\u767D\u5929\u5FB7\u559D\u9053\u3002</p><p>\u51B7\u5982\u971C\u8138\u4E0A\u672C\u6765\u5931\u53BB\u4E86\u8840\u8272\uFF0C\u6B64\u65F6\u5374\u53C8\u53D8\u5F97\u7EEF\u7EA2\u3002\u6162\u6162\u5C06\u624B\u653E\u5F00\u4E24\u8FB9\u3002</p><p>\u300C\u54D7\uFF01\u300D\u4F17\u4EBA\u7684\u773C\u7403\u518D\u5EA6\u7206\u51FA\u3002</p><p>\u65E2\u6709\u5C11\u5973\u7684\u6E05\u7EAF\uFF0C\u53C8\u6563\u53D1\u51FA\u5C11\u5987\u7684\u59A9\u5A9A\uFF0C\u51B7\u5982\u971C\u5B9E\u5728\u662F\u5929\u751F\u7684\u5C24\u7269\u3002</p><p>\u767D\u5929\u5FB7\u5931\u7B11\u9053\uFF0C\u300C\u60F3\u4E0D\u5230\u592A\u592A\u7684\u8EAB\u4F53\u6BD4\u5C0F\u59B9\u5B50\u8FD8\u9C9C\u5AE9\uFF0C\u5218\u53BF\u957F\u771F\u662F\u628A\u592A\u592A\u4FDD\u517B\u5F97\u597D\u554A\u3002\u300D</p><p>\u4E00\u63D0\u5230\u5218\u6EA2\u4E4B\uFF0C\u51B7\u5982\u971C\u5C31\u5982\u906D\u91CD\u51FB\uFF0C\u8138\u8272\u9635\u7EA2\u9635\u767D\u3002</p><p>\u767D\u5929\u5FB7\u504F\u4E0D\u653E\u8FC7\u5979\uFF0C\u9053\uFF0C\u300C\u4E0D\u77E5\u6628\u65E5\u5218\u53BF\u957F\u4E0E\u592A\u592A\u641E\u4E86\u6CA1\u6709\uFF1F\u300D</p><p>\u51B7\u5982\u971C\u54AC\u4F4F\u8D1D\u9F7F\u3002</p><p>\u674E\u8D35\u559D\u9053\uFF0C\u300C\u56E2\u5EA7\u95EE\u4F60\u8BDD\u5462\uFF0C\u5FEB\u8BF4\u641E\u4E86\u6CA1\u6709\uFF1F\u300D</p><p>\u4F17\u4EBA\u7686\u559D\uFF0C\u300C\u641E\u4E86\u6CA1\u6709\uFF1F\u300D</p><p>\u51B7\u5982\u971C\u8F7B\u8F7B\u70B9\u70B9\u5934\uFF0C\u73E0\u6CEA\u6B32\u5760\u3002</p><p>\u4F17\u4EBA\u5927\u7B11\u8D77\u6765\uFF0C\u5F77\u4F5B\u5F97\u5230\u6781\u5927\u6EE1\u8DB3\u3002</p><p>\u767D\u5929\u5FB7\u6CA1\u7B11\uFF0C\u51B7\u7136\u9053\uFF0C\u300C\u65E2\u7136\u8EAB\u5B50\u810F\u4E86\uFF0C\u90A3\u5C31\u5FEB\u53BB\u6D17\u6D17\u3002\u300D</p><p>\u8FD9\u8BDD\u5B9E\u5728\u592A\u7F9E\u8FB1\u4EBA\u4E86\uFF0C\u5168\u6C85\u9547\u518D\u65E0\u51B7\u5982\u971C\u4E00\u822C\u9AD8\u96C5\u7D20\u51C0\u4E4B\u5973\uFF0C\u7ADF\u4F1A\u8BA9\u8FD9\u822C\u6BD4\u571F\u532A\u8FD8\u6076\u5FC3\u7684\u5BB6\u4F19\u5ACC\u810F\uFF1F\uFF01</p><p>\u672C\u6765\u51B7\u5982\u971C\u4E3A\u4E86\u809A\u91CC\u7684\u5B69\u5B50\uFF0C\u5DF2\u51B3\u610F\u4ED8\u51FA\u6240\u6709\u7684\u4EE3\u4EF7\uFF0C\u5979\u5DF2\u60F3\u597D\uFF0C\u53EA\u6709\u51E0\u4E2A\u6708\u4E86\uFF0C\u5982\u679C\u751F\u7684\u5973\u5B69\uFF0C\u5979\u5C31\u4E0E\u5B69\u5B50\u540C\u5F52\u4E8E\u5C3D\uFF0C\u5982\u679C\u751F\u7684\u7537\u5B69\uFF0C\u5979\u5C31\u60F3\u5C3D\u529E\u6CD5\u5C06\u7537\u5B69\u9001\u5230\u5B89\u5168\u7684\u5730\u65B9\u518D\u5BFB\u6B7B\uFF0C\u53CD\u6B63\u8EAB\u5B50\u5DF2\u7ECF\u80AE\u810F\uFF0C\u518D\u4E5F\u65E0\u989C\u89C1\u4E5D\u6CC9\u4E0B\u7684\u4E08\u592B\uFF0C\u53EA\u8981\u80FD\u4E3A\u8001\u5218\u5BB6\u7559\u4E0B\u4E00\u70B9\u9999\u706B\uFF0C\u4E5F\u6B7B\u5F97\u669D\u76EE\u4E86\u3002\u6CA1\u6599\u5230\u5979\u9762\u5BF9\u7684\u6BD4\u60F3\u8C61\u7684\u66F4\u8981\u5C48\u8FB1\u767E\u500D\uFF0C\u5DEE\u70B9\u5C06\u5979\u5B8C\u5168\u6467\u57AE\u3002</p><p>\u8BB8\u4E45\uFF0C\u51B7\u5982\u971C\u624D\u6728\u7136\u5F80\u5916\u8D70\u3002</p><p>\u767D\u5929\u5FB7\u53EB\u4F4F\u4E86\u5979\uFF0C\u300C\u54EA\u91CC\u53BB\uFF0C\u5C31\u5728\u8FD9\u91CC\u6D17\u3002\u300D</p><p>\u51B7\u5982\u971C\u7684\u773C\u775B\u7EA2\u7EA2\u7684\uFF0C\u50CF\u5931\u53BB\u7075\u9B42\u7684\u73A9\u5076\uFF0C\u8D64\u88F8\u7740\u8EAB\u5B50\uFF0C\u62FF\u8FC7\u94DC\u76C6\u6765\uFF0C\u6253\u4E0A\u4E00\u70B9\u6E29\u6C34\uFF0C\u8E72\u5728\u4F17\u4EBA\u4E2D\u95F4\uFF0C\u7272\u53E3\u4E00\u6837\u4E0D\u77E5\u7F9E\u803B\u5730\u6D17\u8D77\u4E0B\u8EAB\u6765\u3002</p><p>\u6D17\u5B8C\u4E86\uFF0C\u767D\u5929\u5FB7\u8981\u5979\u722C\u4E0A\u5E8A\u53BB\uFF0C\u6446\u51FA\u5218\u6EA2\u4E4B\u5E72\u5979\u7684\u59FF\u5F0F\u6765\u3002</p><p>\u51B7\u5982\u971C\u4E3A\u4E86\u4FDD\u62A4\u8179\u4E2D\u7684\u5B69\u5B50\uFF0C\u65E0\u8A00\u5730\u8DEA\u4E0B\uFF0C\u53CC\u624B\u6491\u5730\uFF0C\u50CF\u72D7\u4E00\u822C\u633A\u51FA\u5C41\u80A1\uFF0C\u542C\u51ED\u81EA\u5DF1\u7684\u9690\u5BC6\u82B1\u56ED\u66B4\u9732\u4E8E\u4E00\u53CC\u53CC\u8272\u773C\u4E4B\u4E0B\u3002</p><p>\u767D\u5929\u5FB7\u8FB9\u8131\u88E4\u5B50\u8FB9\u803B\u7B11\u9053\uFF0C\u300C\u539F\u6765\u5802\u5802\u7684\u5218\u53BF\u957F\u662F\u4E00\u6761\u72D7\uFF0C\u5929\u5929\u5C31\u662F\u8FD9\u4E48\u5E72\u7684\u3002\u300D</p><p>\u4F17\u4EBA\u7686\u6DEB\u7B11\u4E0D\u5DF2\u3002</p><p>\u5F53\u7C97\u5927\u6EDA\u70EB\u7684\u8089\u68D2\u76F4\u9876\u9876\u7684\u6345\u5165\u51B7\u5982\u971C\u7684\u72ED\u7A84\u7684\u82B1\u5F84\u65F6\uFF0C\u51B7\u5982\u971C\u518D\u4E5F\u5FCD\u4E0D\u4F4F\u592A\u91CD\u7684\u60B2\u6124\uFF0C\u5931\u58F0\u75DB\u54ED\u4E86\u8D77\u6765\uFF0C\u4E3A\u4E86\u8EAB\u5B50\u4E0E\u5FC3\u7075\u7684\u53CC\u91CD\u75DB\u82E6\u3002</p><p>\u5979\u77E5\u9053\uFF0C\u4ECE\u8FD9\u4E00\u523B\u8D77\uFF0C\u5815\u5165\u4E86\u82E6\u96BE\u65E0\u8FB9\u7684\u963F\u9F3B\u5730\u72F1\uFF0C\u518D\u4E5F\u65E0\u6CD5\u56DE\u5934\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u83BD\u83BD\u5927\u5C71\u4E2D\uFF0C\u4E00\u4E2A\u5973\u4EBA\u5728\u9ED1\u6697\u7684\u6797\u4E2D\u5C0F\u5F84\u4E0A\u6253\u6EDA\uFF0C\u568E\u53EB\u3002</p><p>\u5979\u7684\u529B\u6C14\u662F\u5982\u6B64\u4E4B\u5927\uFF0C\u7ECF\u81F3\u4E8E\u88AB\u5979\u6525\u7740\u7684\u5927\u7AF9\u7AFF\u90FD\u64BC\u52A8\u4E86\uFF0C\u679D\u53F6\u7D22\u7D22\u53D1\u6296\u3002</p><p>\u5979\u6574\u4E2A\u4EBA\u4E5F\u6BD4\u8FD9\u679D\u53F6\u6296\u52A8\u5F97\u66F4\u5389\u5BB3\u3002</p><p>\u8D64\u88F8\u7684\u8EAB\u5B50\u4E0A\u5E03\u6EE1\u4E86\u6CE5\u6D46\uFF0C\u50CF\u4E00\u6761\u8089\u866B\u4E0D\u505C\u5730\u8815\u52A8\uFF0C\u7FFB\u6EDA\u3002</p><p>\u300C\u554A\u5440\u2026\u2026\uFF01\u300D</p><p>\u5E26\u7740\u54ED\u8154\u7684\u4E00\u58F0\u957F\u5636\uFF0C\u631F\u7740\u65E0\u5C3D\u7684\u6124\u6012\u4E0E\u5C48\u8FB1\uFF0C\u76F4\u4E0A\u4E91\u9704\uFF0C\u60CA\u8D77\u6797\u4E2D\u5BBF\u9E1F\uFF0C\u6251\u5566\u5566\u5730\u4E71\u98DE\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u91D1\u5B9D\u8E09\u8E09\u8DC4\u8DB4\u5730\u8DD1\u4E0A\u6C85\u6C34\u6865\uFF0C\u8DE8\u8FC7\u53BB\u5C31\u662F\u5B98\u9053\uFF0C\u8863\u88F3\u4E0D\u6574\uFF0C\u8EAB\u65E0\u534A\u6587\uFF0C\u5468\u8EAB\u75BC\u75DB\u4E0D\u582A\uFF0C\u591C\u534A\u4E09\u66F4\u4E4B\u9645\uFF0C\u8FD8\u4E0D\u77E5\u9053\u5982\u4F55\u624D\u80FD\u9003\u51FA\u751F\u5929\u3002</p><p>\u5979\u4E00\u5934\u5DEE\u70B9\u649E\u5230\u4E00\u4E2A\u4EBA\u7684\u8EAB\u4E0A\uFF0C\u90A3\u4EBA\u563B\u7B11\u9053\uFF0C\u300C\u522B\u7740\u6025\uFF0C\u8001\u5B50\u5E72\u6389\u4E86\u5176\u5B83\u4EBA\u4E4B\u540E\u5C31\u4E13\u7A0B\u5728\u8FD9\u91CC\u7B49\uFF0C\u53EF\u662F\u7B49\u4F60\u597D\u4E45\u4E86\u3002\u300D</p><p>\u91D1\u5B9D\u5927\u60CA\u5931\u8272\uFF0C\u56E0\u4E3A\u8BF4\u8BDD\u90A3\u4EBA\u6B63\u662F\u4E8C\u559C\u5B50\uFF0C\u6A21\u6837\u7F69\u5728\u9ED1\u6697\u4E2D\uFF0C\u5012\u662F\u4ED6\u624B\u4E2D\u63E1\u7740\u7684\u767D\u8499\u8499\u7684\u5315\u9996\u5728\u6708\u8272\u4E0B\u4E00\u6E05\u4E8C\u695A\uFF0C\u51E0\u7EBF\u8840\u7EB9\u8FD8\u5728\u6D41\u52A8\u3002</p><p>\u91D1\u5B9D\u8DEA\u5728\u4E8C\u559C\u5B50\u9762\u524D\uFF0C\u300C\u5927\u7237\uFF0C\u6211\u540C\u60A8\u65E0\u6028\u65E0\u4EC7\uFF0C\u653E\u8FC7\u6211\u5427\u3002\u300D</p><p>\u300C\u5B9E\u8BDD\u544A\u8BC9\u4F60\uFF0C\u8001\u5B50\u51FA\u5A18\u80CE\u8D77\u6253\u8FC7\u4E0D\u5C11\u4EBA\uFF0C\u4E5F\u6328\u8FC7\u4E0D\u5C11\u6253\uFF0C\u8FD8\u4ECE\u6765\u6CA1\u6709\u5973\u4EBA\u5728\u8001\u5B50\u7684\u8138\u4E0A\u7ED3\u7ED3\u5B9E\u5B9E\u6247\u51E0\u5DF4\u638C\uFF0C\u4F60\u662F\u5934\u4E00\u4E2A\uFF0C\u8001\u5B50\u656C\u4F69\u4F60\uFF0C\u4E5F\u4F1A\u62A5\u7B54\u4F60\uFF0C\u81ED\u5A4A\u5B50\u3002\u300D</p><p>\u4E8C\u559C\u5B50\u6293\u4F4F\u5931\u53BB\u62B5\u6297\u80FD\u529B\u7684\u91D1\u5B9D\uFF0C\u5229\u7D22\u5730\u5C06\u5979\u5265\u5149\uFF0C\u624B\u811A\u90FD\u7ED1\u4E86\u8D77\u6765\uFF0C\u5634\u91CC\u585E\u4E0A\u4E00\u56E2\u788E\u5E03\u3002</p><p>\u300C\u5C0F\u59B9\u5B50\uFF0C\u4ECA\u5929\u5927\u54E5\u6211\u8981\u73A9\u4E00\u4E2A\u50B2\u7684\uFF0C\u5F00\u5F00\u773C\u5427\u3002\u300D</p><p>\u4E8C\u559C\u5B50\u602A\u58F0\u602A\u6C14\u5730\u7B11\u7740\uFF0C\u5200\u5C16\u5728\u91D1\u5B9D\u7684\u809A\u8110\u773C\u4E0A\u6BD4\u5212\u4E86\u4E00\u4E0B\u3002\u91D1\u5B9D\u6050\u60E7\u5730\u5C06\u773C\u775B\u90FD\u77AA\u5706\u4E86\u3002</p><p>\u5200\u5C16\u7EC8\u523A\u4E86\u4E0B\u53BB\uFF0C\u5728\u809A\u8110\u4E0A\u6DF1\u6DF1\u5730\u5212\u4E86\u4E2A\u5341\u5B57\uFF0C\u6D53\u6D53\u7684\u9C9C\u8840\u7ACB\u65F6\u6D8C\u4E86\u51FA\u6765\uFF0C\u968F\u5373\u67D3\u6210\u7EA2\u8272\u7684\u80A0\u5B50\u4E5F\u6EDA\u51FA\u4E00\u622A\u3002</p><p>\u5267\u75DB\u4E2D\u91D1\u5B9D\u6B7B\u547D\u6323\u624E\uFF0C\u53C8\u88AB\u7262\u7262\u538B\u4F4F\uFF0C\u52A8\u5F39\u4E0D\u5F97\u3002</p><p>\u4E8C\u559C\u5B50\u5174\u594B\u5730\u89E3\u5F00\u4E86\u88E4\u5E26\uFF0C\u5C06\u4E00\u67F1\u64CE\u5929\u7684\u9E21\u5DF4\u6296\u51FA\u6765\uFF0C\u7ADF\u5C06\u9F9F\u5934\u538B\u5728\u809A\u8110\u773C\u4E0A\uFF0C\u4E00\u70B9\u4E00\u70B9\u5730\u6491\u5F00\u4F24\u53E3\u6324\u4E86\u8FDB\u53BB\u3002</p><p>\u91D1\u5B9D\u518D\u6B21\u5267\u70C8\u6296\u52A8\uFF0C\u8EAB\u4F53\u4E00\u9635\u9635\u75C9\u6469\u3002</p><p>\u575A\u786C\u7684\u8089\u68D2\u5DF2\u7ECF\u6DF1\u6DF1\u5730\u63D2\u5165\u4E86\u5C0F\u59D1\u5A18\u7684\u8179\u8154\u3002\u8FD9\u79CD\u611F\u89C9\u7279\u522B\u5947\u602A\uFF0C\u5B9E\u8D28\u4E0A\uFF0C\u53EA\u6709\u809A\u76AE\u4E0D\u7B97\u539A\u7684\u8102\u80AA\u5C42\u624D\u6709\u8DB3\u591F\u7684\u78E8\u64E6\u529B\uFF0C\u8179\u8154\u5185\u53CD\u800C\u663E\u5F97\u7A7A\u8361\uFF0C\u4F46\u662F\u63D2\u5728\u4E00\u5927\u56E2\u6ED1\u817B\u6E29\u70ED\u7684\u76D8\u80A0\u4E4B\u95F4\uFF0C\u80A5\u539A\u67D4\u8F6F\u591A\u6C41\u7684\u80A0\u4F53\u5305\u88F9\u7740\u8089\u68D2\u6EDA\u6765\u6EDA\u53BB\uFF0C\u5219\u522B\u662F\u4E00\u756A\u5E38\u4EBA\u96BE\u53CA\u7684\u97F5\u5473\u3002</p><p>\u300C\u723D\u554A\u3002\u300D\u4E8C\u559C\u5B50\u53EB\u51FA\u58F0\u6765\u3002</p><p>\u8089\u68D2\u6BCF\u6DF1\u5165\u4E00\u6B21\uFF0C\u8FDE\u5E26\u8179\u8089\u90FD\u5377\u4E86\u8FDB\u53BB\uFF0C\u5F80\u56DE\u62BD\u65F6\uFF0C\u53C8\u628A\u4E00\u7247\u8840\u82B1\u8840\u80A0\u5E26\u4E86\u51FA\u6765\u3002\u5C0F\u91D1\u5B9D\u5728\u604D\u60DA\u4E2D\u591A\u6B21\u6655\u6B7B\uFF0C\u751F\u547D\u6162\u6162\u8870\u7AED\u3002</p><p>\u6708\u513F\u6B8B\u7167\uFF0C\u6708\u8272\u8840\u7EA2\uFF0C\u65E0\u8A00\u5730\u4FEF\u89C6\u7740\u5927\u5730\u4E4B\u4E0A\u4EBA\u95F4\u81F3\u60E8\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u767D\u5929\u5FB7\u771F\u662F\u4E2A\u7CBE\u6DB2\u6784\u6210\u7684\u6076\u9B54\uFF0C\u6574\u6574\u4E24\u4E2A\u65F6\u8FB0\uFF0C\u5C04\u4E86\u56DB\u6B21\u5728\u5979\u4F53\u5185\uFF0C\u4F11\u606F\u7247\u523B\u53C8\u80FD\u7FFB\u8EAB\u518D\u5EA6\u9A91\u5728\u5979\u8EAB\u4E0A\u3002</p><p>\u51B7\u5982\u971C\u7684\u4E0B\u8EAB\u672C\u5DF2\u9EBB\u6728\uFF0C\u4E5F\u5E0C\u671B\u81EA\u5DF1\u8C61\u6B7B\u5C38\u4E00\u6837\u8EBA\u7740\uFF0C\u4E0D\u8A00\u4E0D\u8BED\u4E5F\u4E0D\u52A8\uFF0C\u7136\u800C\u9634\u6237\u5185\u8FC7\u5EA6\u7684\u6469\u64E6\u5DF2\u7ECF\u70E7\u5E72\u4E86\u751F\u7406\u4E0A\u5F3A\u884C\u5206\u6CCC\u51FA\u6765\u7684\u4E00\u70B9\u7231\u6DB2\uFF0C\u5B8C\u5168\u4F9D\u9760\u524D\u6B21\u6B8B\u7559\u4E0B\u6765\u7684\u7CBE\u6DB2\u5728\u6DA6\u6ED1\u3002</p><p>\u5F53\u6BD4\u5E38\u4EBA\u7C97\u58EE\u7684\u8089\u68D2\u63D2\u5165\uFF0C\u5728\u5E72\u71E5\u7684\u8089\u58C1\u4E2D\u94BB\u884C\uFF0C\u90A3\u5C42\u8584\u6DB2\u6839\u672C\u4E0D\u591F\uFF0C\u6CA1\u6709\u51E0\u4E0B\u5C31\u5C06\u5979\u7684\u611F\u89C9\u786C\u751F\u751F\u5730\u62C9\u4E86\u56DE\u6765\uFF0C\u6CA1\u6709\u5FEB\u611F\uFF0C\u53EA\u6709\u5267\u75DB\uFF0C\u6BCF\u8FD0\u52A8\u4E00\u4E0B\u90FD\u50CF\u76F4\u6345\u5230\u5979\u7684\u8111\u95E8\u91CC\uFF0C\u8BA9\u5979\u611F\u53D7\u5230\u94BB\u5FC3\u7684\u75BC\u75DB\u3002</p><p>\u5979\u54AC\u7740\u7259\u62DA\u547D\u5FCD\u53D7\uFF0C\u4E00\u80CC\u90FD\u662F\u6C57\u6C34\u3002</p><p>\u767D\u5929\u5FB7\u8FD8\u5C06\u5979\u7684\u957F\u53D1\u6563\u5F00\uFF0C\u6E7F\u6E7F\u5730\u6643\u52A8\uFF0C\u522B\u6709\u4E00\u756A\u5F02\u6837\u7684\u7F8E\u611F\u3002</p><p>\u300C\u554A\u554A\uFF01\u300D\u5973\u4EBA\u7EC8\u4E8E\u5FCD\u4E0D\u4F4F\u5C16\u53EB\u4E86\u51FA\u6765\u3002</p><p>\u51E0\u7F15\u9C9C\u8840\u7F20\u7ED5\u5728\u767D\u5929\u5FB7\u7684\u8089\u68D2\u4E0A\u5E26\u4E86\u51FA\u6765\u3002</p><p>\u300C\u56E2\u5EA7\u628A\u8FD9\u5A4A\u5B50\u641E\u51FA\u8840\u5566\u3002\u300D</p><p>\u300C\u662F\u505A\u597D\u4E8B\u5427\u3002\uFF08\u6765\u6708\u7ECF\u7684\u610F\u601D\uFF09\u300D</p><p>\u300C\u653E\u5C41\uFF0C\u6000\u6BDB\u6BDB\u4E86\u54EA\u8FD8\u4F1A\u505A\u597D\u4E8B\uFF0C\u732A\u8111\u5B50\u3002\u300D</p><p>\u54ED\u6CE3\u58F0\u4E2D\uFF0C\u767D\u5929\u5FB7\u4E5F\u5230\u4E86\u5174\u594B\u7684\u9876\u70B9\uFF0C\u4E24\u53EA\u5927\u624B\u7528\u529B\u631F\u7D27\u5979\u7684\u808B\u4E0B\uFF0C\u5C06\u5979\u7684\u81C0\u90E8\u4F7F\u52B2\u5F80\u56DE\u9001\uFF0C\u4ED6\u7684\u5C41\u80A1\u4E5F\u6B7B\u547D\u5F80\u524D\u9876\uFF0C\u6DA8\u5230\u6781\u5904\u7684\u9F9F\u5934\u5DF2\u6DF1\u5165\u5230\u82B1\u5FC3\u4E4B\u4E2D\uFF0C\u54C6\u55E6\u51E0\u4E0B\uFF0C\u70ED\u6D41\u6D8C\u51FA\uFF0C\u65B9\u56DE\u8FC7\u6C14\u6765\uFF0C\u7F13\u7F13\u62BD\u4E86\u51FA\u6765\u3002</p><p>\u51B7\u5982\u971C\u5DEE\u70B9\u7FFB\u4E86\u767D\u773C\uFF0C\u51E0\u6B32\u6B7B\u53BB\uFF0C\u762B\u8F6F\u5728\u5E8A\u4E0A\u3002</p><p>\u7EA2\u767D\u76F8\u95F4\u7684\u810F\u6DB2\u4ECE\u6D1E\u5F00\u7684\u7389\u6237\u53E3\u6302\u4E86\u51FA\u6765\u3002</p><p>\u767D\u5929\u5FB7\u771F\u6709\u4E9B\u7D2F\u4E86\uFF0C\u5598\u9053\uFF0C\u300C\u5988\u7684\uFF0C\u8001\u5B50\u8FD9\u6837\u8F9B\u82E6\u4E0D\u6653\u5F97\u4E3A\u4E86\u5565\u3002\u5A4A\u5B50\u7684\uFF0C\u5FEB\u6D17\u6D17\u3002\u300D</p><p>\u5973\u4EBA\u7684\u8089\u4F53\u8270\u96BE\u7684\u632A\u52A8\u7740\uFF0C\u4E0B\u4E86\u5E8A\uFF0C\u4E00\u4E2A\u8E09\u8DC4\u5DEE\u70B9\u683D\u5012\u3002</p><p>\u300C\u628A\u8FD9\u76C6\u6C34\u559D\u4E86\uFF0C\u4ECA\u665A\u5C31\u5230\u6B64\u4E3A\u6B62\u3002\u300D</p><p>\u76C6\u4E2D\u54EA\u8FD8\u662F\u6C34\uFF0C\u5168\u662F\u7C98\u7A20\u7684\u6DB2\u4F53\uFF0C\u770B\u7740\u5C31\u6076\u5FC3\u3002</p><p>\u5973\u4EBA\u9ED8\u9ED8\u5730\u7AEF\u8D77\u5C0F\u76C6\u5F80\u5634\u91CC\u9001\uFF0C\u4E00\u8FDE\u704C\u4E86\u597D\u51E0\u53E3\u4E0B\u53BB\uFF0C\u7ACB\u523B\u53C8\u8FDE\u672C\u5E26\u5229\u5730\u4ECE\u80C3\u91CC\u53CD\u51FA\u6765\uFF0C\u54C7\u5566\u5410\u4E86\u4E00\u5730\uFF0C\u82E6\u80C6\u90FD\u5FEB\u5410\u51FA\u6765\u4E86\u3002</p><p>\u5C4B\u91CC\u5F25\u6F2B\u7740\u6D53\u6D53\u7684\u7CBE\u6DB2\u5473\uFF0C\u5C3F\u5473\uFF0C\u9178\u81ED\u5473\u3002</p><p>\u767D\u5929\u5FB7\u5C4F\u4F4F\u547C\u5438\uFF0C\u76B1\u7709\u5ACC\u6076\u9053\uFF0C\u300C\u7B97\u4E86\u7B97\u4E86\uFF0C\u6D17\u6D17\u5E72\u51C0\u3002\u300D</p><p>\u51B7\u5982\u971C\u5BF9\u7740\u955C\u5B50\u618E\u6076\u5730\u770B\u7740\u81EA\u5DF1\u7684\u8EAB\u4F53\uFF0C\u673A\u68B0\u5730\u51B2\u6D17\u4E0B\u8EAB\uFF0C\u4E00\u6B21\uFF0C\u4E24\u6B21\u2026\u2026</p><p>\u300C\u4E0D\u5E72\u51C0\u4E86\u5417\uFF1F\u300D</p>',141);function t(i,n){return r}var o=a(e,[["render",t]]);export{o as default};
