import{e as p}from"./app.630af5f2.js";import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";const e={},r=p('<h1 id="\u7B2C\u5341\u4E5D\u7AE0-\u6218\u4E89" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u5341\u4E5D\u7AE0-\u6218\u4E89" aria-hidden="true">#</a> \u7B2C\u5341\u4E5D\u7AE0 \u6218\u4E89</h1><p>\u6D77\u68E0\u7684\u4E34\u65F6\u6307\u6325\u90E8\u8BBE\u5728\u8DDD\u79BB\u6C85\u9547\u4E0D\u8DB3\u4E8C\u5341\u91CC\u7684\u90CA\u533A\u4E00\u6240\u6C11\u623F\u91CC\uFF0C\u56DB\u4E0B\u91CC\u7AD9\u6EE1\u4E86\u4EBA\uFF0C\u90FD\u662F\u5404\u6D1E\u548C\u5404\u5BE8\u7684\u5F53\u5BB6\u4EBA\u6216\u662F\u4E8C\u5F53\u5BB6\uFF0C\u5305\u62EC\u9EC4\u4E91\u754C\u7684\u7533\u660C\uFF0C\u6D77\u68E0\u7684\u684C\u9762\u4E0A\u653E\u4E86\u4E00\u9053\u4EE4\u724C\uFF0C\u5927\u4F19\u90FD\u5FC3\u91CC\u660E\u767D\uFF0C\u4EE4\u724C\u4E00\u51FA\uFF0C\u4EE3\u8868\u7684\u662F\u699C\u7237\u672C\u4EBA\uFF0C\u4E0D\u670D\u4EE4\u724C\u8005\uFF0C\u7B49\u4E8E\u662F\u516C\u7136\u4E0E\u699C\u7237\u53EB\u677F\uFF0C\u65E0\u4EBA\u6709\u8FD9\u4E2A\u80C6\u5B50\uFF0C\u56E0\u6B64\uFF0C\u4EE4\u884C\u7981\u6B62\uFF0C\u8981\u4EBA\u7ED9\u4EBA\uFF0C\u8981\u7269\u7ED9\u7269\uFF0C\u6CA1\u4EC0\u4E48\u597D\u8BF4\u7684\u3002</p><p>\u8138\u8272\u6781\u5EA6\u96BE\u770B\u7684\u6D77\u68E0\u6B63\u5728\u5927\u53D1\u96F7\u9706\u4E4B\u6012\u3002</p><p>\u4E8B\u60C5\u7F18\u4E8E\u6570\u65E5\u524D\u653B\u6253\u767D\u5BB6\u5821\u7684\u884C\u52A8\uFF0C\u5730\u65B9\u6B66\u88C5\u62B5\u6297\u751A\u662F\u987D\u5F3A\uFF0C\u65B0\u4FEE\u7684\u57CE\u5BE8\u4E5F\u7ED9\u532A\u5E2E\u5236\u9020\u4E86\u4E0D\u5C0F\u7684\u9EBB\u70E6\uFF0C\u5F88\u662F\u6298\u4E86\u4E00\u4E9B\u5144\u5F1F\uFF0C\u9677\u843D\u540E\uFF0C\u9664\u4E86\u6740\u4E86\u51E0\u4E2A\u767D\u5BB6\u4E3B\u4E8B\u4E4B\u4EBA\uFF0C\u8D22\u4EA7\u6D17\u52AB\u4E00\u7A7A\u5916\uFF0C\u6709\u51E0\u4E2A\u5C0F\u571F\u532A\u6CA1\u6709\u7167\u89C4\u77E9\u529E\uFF0C\u5F3A\u5978\u4E86\u767D\u5BB6\u7684\u4E00\u4E2A\u95FA\u5973\u6CC4\u6124\uFF0C\u81F4\u4F7F\u5176\u7F9E\u6124\u81EA\u6740\uFF0C\u591A\u6DFB\u4E86\u4E00\u4EFD\u8840\u503A\u3002</p><p>\u300C\u4F60\u4EEC\u544A\u8BC9\u6211\uFF0C\u600E\u4E48\u4E86\u7ED3\uFF1F\u300D</p><p>\u300C\u6709\u4EC0\u4E48\u5927\u4E0D\u4E86\u7684\uFF0C\u4EBA\u4E5F\u6740\u4E86\uFF0C\u8D22\u4E5F\u62A2\u4E86\uFF0C\u51A4\u6B7B\u4E2A\u628A\u5973\u4EBA\u4E5F\u5C31\u90A3\u4E48\u56DE\u4E8B\u3002\u300D</p><p>\u4E00\u4E2A\u59D3\u77F3\u7684\u6D1E\u4E3B\u6F2B\u4E0D\u5728\u4E4E\u5730\u8BF4\uFF0C\u65BD\u66B4\u7684\u4EBA\u91CC\u9762\uFF0C\u5927\u90FD\u662F\u4ED6\u7684\u624B\u4E0B\uFF0C\u4ED6\u5F53\u7136\u8981\u62A4\u77ED\u4E86\u3002</p><p>\u300C\u4F9D\u6211\u770B\uFF0C\u5404\u9000\u4E00\u6B65\uFF0C\u77F3\u6D1E\u4E3B\uFF0C\u4F60\u90A3\u5E2E\u624B\u4E0B\u662F\u4E0D\u8C61\u8BDD\uFF0C\u6253\u4E00\u987F\uFF0C\u5173\u51E0\u5929\uFF0C\u770B\u4ED6\u4EEC\u4E0B\u6B21\u8FD8\u6562\u4E0D\u6562\uFF0C\u6D77\u5BE8\u4E3B\u5462\uFF0C\u4E5F\u522B\u592A\u8F83\u771F\uFF0C\u54EA\u6709\u732B\u513F\u4E0D\u5077\u8165\u7684\uFF0C\u6211\u4EEC\u662F\u571F\u532A\uFF0C\u4E0D\u662F\u653F\u5E9C\u519B\uFF0C\u70E7\u6740\u62A2\u63A0\u662F\u672C\u884C\uFF0C\u7684\u786E\u4E5F\u6CA1\u4EC0\u4E48\u5927\u4E0D\u4E86\uFF0C\u653E\u4E00\u9A6C\u7B97\u4E86\u3002\u300D\u7533\u660C\u51FA\u6765\u505A\u4E2A\u548C\u4E8B\u4F6C\uFF0C</p><p>\u300C\u6765\u4E4B\u524D\u89C4\u77E9\u662F\u600E\u6837\u5B9A\u7684\uFF1F\u767E\u59D3\u4E0D\u80FD\u6740\uFF0C\u5987\u5973\u4E0D\u80FD\u5978\uFF0C\u8FDD\u8005\u65A9\uFF01\u300D</p><p>\u300C\u89C4\u77E9\u4E0D\u8FD8\u662F\u4EBA\u5B9A\u7684\u561B\uFF0C\u773C\u4E0B\u653B\u6253\u6C85\u9547\u57CE\u4E0D\u662F\u592A\u987A\u624B\uFF0C\u4E34\u9635\u6740\u81EA\u5BB6\u5F1F\u5144\u672A\u514D\u6298\u4E86\u58EB\u6C14\uFF0C\u4E0D\u5982\u628A\u4ED6\u4EEC\u9001\u5230\u524D\u7EBF\uFF0C\u6234\u7F6A\u7ACB\u529F\u5427\u3002\u300D</p><p>\u300C\u4E0D\u884C\uFF0C\u7EDD\u4E0D\u80FD\u59D1\u606F\uFF01\u300D\u6D77\u68E0\u559D\u9053\uFF0C\u624B\u638C\u5728\u684C\u4E0A\u731B\u51FB\u4E00\u638C\uFF0C\u773C\u524D\u5F77\u4F5B\u51FA\u73B0\u4E86\u53DB\u5F92\u4E8C\u559C\u5B50\u7684\u5F71\u5B50\uFF0C\u5F53\u65F6\u6B63\u662F\u653E\u4E86\u4ED6\u4E00\u9A6C\uFF0C\u624D\u4F7F\u5F97\u5BE8\u5B50\u5168\u519B\u8986\u6CA1\u3002\u5979\u773C\u4E2D\u900F\u51FA\u6D53\u6D53\u7684\u6740\u6C14\uFF0C\u4E3E\u8D77\u699C\u7237\u7684\u4EE4\u724C\uFF0C\u300C\u6740\uFF01\u300D</p><p>\u95E8\u5916\u4E24\u58F0\u67AA\u54CD\uFF0C\u5BA4\u5185\u4F17\u4EBA\u76F8\u4E92\u770B\u4E86\u4E00\u773C\uFF0C\u5664\u82E5\u5BD2\u8749\uFF0C\u77F3\u6D1E\u4E3B\u6068\u6068\u5730\u54FC\u4E86\u58F0\uFF0C\u62D4\u817F\u51B2\u4E86\u51FA\u53BB\uFF0C\u7533\u660C\u9634\u7740\u8138\uFF0C\u8868\u60C5\u590D\u6742\uFF0C\u5F80\u5929\u82B1\u677F\u4E0A\u770B\u3002</p><p>\u6D77\u68E0\u6325\u6325\u624B\uFF0C\u75B2\u60EB\u5730\u8BF4\uFF0C\u300C\u90FD\u6563\u4E86\u5427\u3002\u300D</p><p>\u6218\u4E89\u8FDB\u884C\u5230\u4E86\u7B2C\u5341\u4E03\u65E5\uFF0C\u8FDB\u5165\u4E86\u6B8B\u9177\u7684\u62C9\u952F\u76F8\u6301\u9636\u6BB5\u3002</p><p>\u4FDD\u5B89\u56E2\u6BD5\u7ADF\u7B97\u662F\u6B63\u89C4\u519B\uFF0C\u5728\u521D\u671F\u7684\u60CA\u60F6\u5931\u63AA\u540E\uFF0C\u4F9D\u6258\u6C85\u6C34\u6CB3\u5929\u7136\u5C4F\u969C\uFF0C\u7EC4\u7EC7\u8D77\u6709\u6548\u7684\u62B5\u6321\uFF0C\u571F\u532A\u7EB5\u7136\u4EBA\u591A\u52BF\u4F17\u4E5F\u662F\u4E4C\u5408\u4E4B\u4F17\uFF0C\u7EC4\u7EC7\u677E\u6563\uFF0C\u706B\u5668\u4E0D\u591A\uFF0C\u5F88\u591A\u8FD8\u662F\u5927\u5200\u957F\u77DB\uFF0C\u6218\u6597\u529B\u5DEE\uFF0C\u672C\u662F\u4E0D\u8010\u4E45\u6218\uFF0C\u5E78\u597D\u8FD8\u6709\u6D77\u68E0\uFF0C\u9886\u5BFC\u51FA\u8272\uFF0C\u6253\u4ED7\u65F6\u8EAB\u5148\u58EB\u5352\uFF0C\u820D\u6B7B\u51B2\u5728\u524D\uFF0C\u632F\u4F5C\u4E86\u571F\u532A\u7684\u58EB\u6C14\uFF0C\u624D\u4E00\u76F4\u6CA1\u80FD\u8BA9\u767D\u5929\u5FB7\u5360\u5230\u4E0A\u98CE\u3002</p><p>\u6240\u6709\u7684\u5916\u56F4\u636E\u70B9\u5747\u5DF2\u8083\u6E05\uFF0C\u6C85\u9547\u6210\u4E86\u4E00\u5EA7\u88AB\u91CD\u91CD\u56F4\u56F0\u7684\u5B64\u57CE\uFF0C\u767D\u5929\u5FB7\u7684\u5F62\u52BF\u8D8A\u6765\u8D8A\u4E0D\u5999\uFF0C\u5982\u679C\u6CA1\u6709\u5916\u63F4\uFF0C\u53EA\u5F85\u4E00\u573A\u8840\u6218\uFF0C\u4FBF\u53EF\u6C5F\u5C71\u5927\u5B9A\u3002</p><p>\u300C\u7ECF\u8FC7\u8FD9\u4E48\u957F\u7684\u65F6\u95F4\uFF0C\u65F6\u673A\u5E94\u8BE5\u6210\u719F\u4E86\uFF0C\u6211\u5DF2\u62A5\u544A\u699C\u7237\u51C6\u5907\u53D1\u52A8\u603B\u653B\uFF0C\u660E\u65E5\u51CC\u6668\u5B50\u65F6\u5F00\u59CB\uFF0C\u80E1\u5BE8\u4E3B\uFF0C\u8BF7\u4F60\u7684\u90E8\u961F\u5728\u6865\u5934\u5168\u529B\u4F6F\u653B\uFF0C\u5438\u5F15\u767D\u72D7\u7684\u5175\u529B\uFF0C\u674E\u5F53\u5BB6\u7684\uFF0C\u901A\u77E5\u4F60\u7684\u624B\u4E0B\u5728\u5B50\u65F6\u524D\u4E24\u523B\u5DE6\u53F3\u5C06\u51C6\u5907\u597D\u7684\u8239\u653E\u5165\u8D75\u5BB6\u6E21\u53E3\uFF0C\u7533\u4E8C\u5F53\u5BB6\uFF0C\u4F60\u5E26\u9886\u5144\u5F1F\u4EEC\u4ECE\u8D75\u5BB6\u6E21\u5904\u6E21\u6CB3\uFF0C\u77F3\u6D1E\u4E3B\u4F5C\u9884\u5907\u961F\uFF0C\u8FD8\u6709\u95EE\u9898\u5417\uFF1F\u300D</p><p>\u300C\u6211\u6709\u95EE\u9898\u3002\u300D\u7533\u660C\u63A5\u8BDD\u3002</p><p>\u300C\u8BB2\u3002\u300D</p><p>\u300C\u4E0D\u7740\u6025\uFF0C\u6709\u4EBA\u4F1A\u8BB2\u3002\u300D</p><p>\u55BD\u5570\u6765\u62A5\uFF0C\u300C\u699C\u7237\u7684\u4F7F\u8005\u5230\u3002\u300D</p><p>\u4F17\u4EBA\u5373\u8138\u8272\u4E00\u8083\uFF0C\u6D77\u68E0\u9053\uFF0C\u300C\u6709\u8BF7\u3002\u300D</p><p>\u6765\u8005\u662F\u4E2A\u5973\u4EBA\uFF0C\u8EAB\u6750\u7A88\u7A95\uFF0C\u63ED\u5F00\u8499\u9762\u5934\u7F69\u540E\uFF0C\u5374\u662F\u5A9A\u6001\u5341\u8DB3\u7684\u5C0F\u5973\u4EBA\u963F\u6708\u3002</p><p>\u300C\u600E\u4E48\u662F\u4F60\uFF1F\u300D\u6D77\u68E0\u6DE1\u7136\u9053\uFF0C\u5979\u5BF9\u963F\u6708\u7D20\u65E0\u597D\u611F\uFF0C\u53EA\u628A\u5979\u5F53\u4F5C\u699C\u7237\u8EAB\u8FB9\u7684\u4F8D\u5973\u3002\u963F\u6708\u7B11\u7B11\uFF0C\u626C\u4E86\u626C\u4FE1\u7269\uFF0C\u9053\uFF0C\u300C\u53EF\u4E0D\uFF0C\u5175\u8352\u9A6C\u4E71\u7684\uFF0C\u8981\u4E0D\u662F\u6015\u8BEF\u4E86\u8001\u7237\u5B50\u7684\u4E8B\uFF0C\u6211\u53EF\u4E0D\u60F3\u6765\u3002\u300D</p><p>\u963F\u6708\u5C55\u4FE1\uFF0C\u5BF9\u4F17\u4EBA\u9053\uFF0C\u300C\u699C\u7237\u6709\u4EE4\uFF0C\u9ED1\u51E4\u51F0\u6B8B\u6740\u624B\u8DB3\uFF0C\u5144\u5F1F\u4EEC\u4E0D\u670D\uFF0C\u6F84\u6E05\u4E8B\u5B9E\u524D\uFF0C\u6B64\u5730\u6307\u6325\u6743\u6682\u4EA4\u7533\u4E8C\u5F53\u5BB6\uFF0C\u6240\u643A\u6B66\u5668\u4E5F\u4E00\u5E76\u4EA4\u51FA\u3002\u300D</p><p>\u6D77\u68E0\u6012\u9053\uFF0C\u300C\u54EA\u4E2A\u5728\u80CC\u540E\u80E1\u8BF4\u516B\u9053\uFF01\u300D\u950B\u5229\u7684\u76EE\u5149\u903C\u5411\u77F3\u6D1E\u4E3B\uFF0C\u77F3\u6D1E\u4E3B\u51B7\u54FC\u4E00\u58F0\uFF0C\u671B\u5411\u522B\u5904\uFF0C\u6C14\u6C1B\u5F92\u7136\u7D27\u5F20\u8D77\u6765\u3002</p><p>\u963F\u6708\u663E\u5F97\u4E0D\u77E5\u6240\u63AA\uFF0C\u300C\u8FD9\u53EF\u90FD\u662F\u699C\u7237\u7684\u4EA4\u4EE3\uFF0C\u4E0D\u662F\u6211\u8BF4\u7684\u3002\u300D</p><p>\u7533\u660C\u6E05\u54B3\u4E24\u58F0\uFF0C\u9053\uFF0C\u300C\u770B\u6765\u5176\u4E2D\u6709\u4E9B\u8BEF\u4F1A\uFF0C\u9ED1\u51E4\u51F0\uFF0C\u4F60\u8EAB\u6B63\u4E0D\u6015\u5F71\u659C\uFF0C\u8BDD\u662F\u8BF4\u5F97\u6E05\u7684\uFF0C\u5148\u59D4\u66F2\u51E0\u5929\u597D\u4E0D\u597D\u3002\u2026\u2026\u6765\u5440\uFF0C\u628A\u9ED1\u5F53\u5BB6\u7684\u7684\u67AA\u4E0B\u4E86\uFF0C\u8BF7\u5979\u56DE\u53BB\u4F11\u606F\u4F11\u606F\u3002\u5927\u4F19\u90FD\u4E0D\u5F97\u96BE\u4E3A\u9ED1\u5F53\u5BB6\u7684\uFF0C\u542C\u660E\u767D\u4E86\u5427\u3002\u300D</p><p>\u300C\u522B\u52A8\u6211\uFF0C\u6211\u81EA\u5DF1\u6765\u3002\u6CA1\u6709\u4EC0\u4E48\u597D\u6015\u7684\uFF0C\u699C\u7237\u81EA\u4F1A\u8FD8\u6211\u4E00\u4E2A\u516C\u9053\u3002\u7533\u4E8C\u5F53\u5BB6\u7684\uFF0C\u65E0\u8BBA\u5982\u4F55\uFF0C\u4ECA\u665A\u4E00\u5B9A\u8981\u884C\u52A8\uFF0C\u6D3B\u6349\u767D\u5929\u5FB7\u5FC5\u80FD\u6210\u529F\u3002\u5426\u5219\u63F4\u519B\u5230\u5C31\u529F\u8D25\u5782\u6210\u4E86\u3002\u300D</p><p>\u300C\u4E0D\u7528\u64CD\u5FC3\uFF0C\u59D3\u7533\u7684\u6211\u53EF\u4ECE\u6765\u90FD\u4E0D\u662F\u5403\u7D20\u7684\u3002\u300D</p><p>\u6D77\u68E0\u5750\u5728\u81EA\u5DF1\u7684\u5C0F\u7AF9\u697C\u91CC\uFF0C\u5FC3\u70E6\u610F\u4E71\uFF0C\u699C\u7237\u4ECE\u6765\u6CA1\u6709\u5E72\u6D89\u8FC7\u5979\u7684\u8BA1\u5212\uFF0C\u504F\u504F\u5728\u6700\u7D27\u8981\u7684\u5173\u5934\u6765\u4E86\u8FD9\u4E48\u4E00\u624B\uFF0C\u8FD9\u662F\u4E3A\u4F55\u5462\uFF1F\u662F\u771F\u7684\u6709\u4EBA\u80CC\u540E\u544A\u5201\u72B6\uFF0C\u8FD8\u662F\u62C5\u5FC3\u5979\u4E34\u9635\u5931\u5229\uFF0C\u6216\u8005\u53E6\u6709\u9690\u60C5\u5462\uFF1F\u603B\u4E0D\u50CF\u662F\u597D\u5146\u5934\u3002</p><p>\u5979\u7684\u8EAB\u8FB9\u6CA1\u6709\u4E00\u4E2A\u53EF\u771F\u6B63\u4FE1\u8D56\u7684\u4EBA\uFF0C\u4E00\u76F4\u90FD\u662F\u4FE1\u5FF5\u5728\u652F\u6491\u7740\u5979\uFF0C\u6216\u662F\u786C\u6491\u7740\u5979\uFF0C\u4E00\u65E6\u52A8\u6447\uFF0C\u624D\u53D1\u73B0\u81EA\u5DF1\u662F\u591A\u4E48\u5B64\u72EC\uFF0C\u9047\u4E8B\u8FDE\u4E2A\u5546\u91CF\u7684\u4EBA\u90FD\u6CA1\u6709\u3002\u7533\u660C\u9760\u5F97\u4F4F\u5417\uFF1F\u7684\u786E\uFF0C\u51FA\u5F81\u4EE5\u6765\uFF0C\u4ED6\u5E2E\u4E86\u5979\u5F88\u591A\uFF0C\u5904\u5904\u4E3A\u5979\u7740\u60F3\uFF0C\u5374\u59CB\u7EC8\u8FD8\u9694\u7740\u4E00\u5C42\uFF0C\u6349\u6478\u4E0D\u900F\uFF0C\u5176\u5B83\u4EBA\uFF0C\u7B97\u4E86\uFF0C\u7C97\u9119\u4E0D\u6587\u3002</p><p>\u8FD9\u51E0\u4E2A\u65F6\u8FB0\u8FC7\u5F97\u597D\u6162\u597D\u6162\u3002</p><p>\u6CA1\u6709\u67AA\u58F0\uFF01</p><p>\u65E9\u5DF2\u8FC7\u4E86\u5B50\u65F6\u65F6\u5206\uFF0C\u4E3A\u4F55\u6218\u4E8B\u8FD8\u6CA1\u6253\u54CD\uFF0C\u83AB\u975E\u53C8\u751F\u53D8\u6545\uFF1F</p><p>\u5979\u51B2\u5230\u95E8\u524D\uFF0C\u62C9\u95E8\uFF0C\u88AB\u53CD\u9501\u4E86\uFF0C\u51B2\u5230\u7A97\u53E3\uFF0C\u4E24\u4E2A\u964C\u751F\u55BD\u5570\u6301\u67AA\u6321\u4F4F\u4E86\u5979\uFF0C\u300C\u5BF9\u4E0D\u8D77\uFF0C\u9ED1\u5F53\u5BB6\u7684\uFF0C\u60A8\u4E0D\u80FD\u5916\u51FA\u3002\u300D</p><p>\u300C\u628A\u7533\u660C\u7ED9\u6211\u53EB\u6765\u3002\u300D</p><p>\u300C\u8981\u7533\u660C\u90A3\u6839\u7259\u7B7E\u505A\u4E48\u5B50\uFF0C\u6211\u8001\u77F3\u5C31\u80FD\u6EE1\u8DB3\u9ED1\u5F53\u5BB6\u7684\u5566\u3002\u300D\u968F\u7740\u6DEB\u8BCD\u79FD\u8BED\uFF0C\u77F3\u6D1E\u4E3B\u9694\u7740\u6728\u7A97\u680F\u5C06\u81ED\u8138\u51D1\u5230\u9762\u524D\u3002</p><p>\u7531\u8DDF\u5979\u6709\u8FC7\u8282\u7684\u4EBA\u6765\u4EB2\u81EA\u770B\u5B88\uFF0C\u5F62\u52BF\u8D8A\u53D1\u4E0D\u5BF9\u5934\u4E86\u3002</p><p>\u300C\u6EDA\u5F00\u3002\u300D\u6D77\u68E0\u618E\u6012\u9053\u3002</p><p>\u300C\u563B\u563B\uFF0C\u4E0D\u6015\u544A\u8BC9\u4F60\uFF0C\u961F\u4F0D\u65E9\u5DF2\u5F00\u62D4\uFF0C\u8FD9\u91CC\u662F\u8001\u5B50\u7684\u5929\u4E0B\u5566\uFF0C\u8BC6\u76F8\u70B9\u7684\uFF0C\u4E56\u4E56\u7ED9\u8001\u5B50\u8214\u9E21\u5DF4\uFF0C\u5426\u5219\u2026\u2026\u300D\u624B\u638C\u4F38\u8FDB\u6728\u680F\u60F3\u8F7B\u8584\u4E00\u4E0B\u6D77\u68E0\u7684\u8138\u3002</p><p>\u65E0\u58F0\u65E0\u606F\uFF0C\u6D77\u68E0\u731B\u7684\u4E00\u62F3\uFF0C\u5C06\u77F3\u6D1E\u4E3B\u4F38\u5165\u4E00\u534A\u7684\u624B\u638C\u72E0\u72E0\u5730\u9489\u5728\u680F\u6746\u4E0A\uFF0C\u529B\u9053\u672A\u5C3D\uFF0C\u76F4\u5C06\u513F\u81C2\u7C97\u7684\u6728\u68CD\u6253\u65AD\u3002\u77F3\u6D1E\u4E3B\u770B\u6765\u662F\u6307\u9AA8\u6298\u4E86\uFF0C\u6367\u7740\u624B\u75DB\u5F97\u5728\u5730\u4E0A\u6253\u6EDA\u3002</p><p>\u300C\u628A\u4ED6\u5E26\u8D70\u3002\u300D\u7533\u660C\u7EC8\u4E8E\u51FA\u73B0\u4E86\uFF0C\u76B1\u7740\u7709\u5934\u53EB\u624B\u4E0B\u67B6\u5F00\u4E86\u90A3\u4E2A\u81EA\u8BA8\u82E6\u5403\u7684\u5BB6\u4F19\u3002\u95E8\u5F00\u4E86\u3002</p><p>\u300C\u77E5\u9053\u4F60\u4F1A\u627E\u6211\uFF0C\u6211\u81EA\u5DF1\u6765\u4E86\u3002\u300D</p><p>\u300C\u59D3\u7533\u7684\uFF0C\u4F60\u4E3A\u4EC0\u4E48\u4E0D\u8FDB\u653B\uFF1F\u300D</p><p>\u300C\u9ED1\u51E4\u51F0\uFF0C\u8B66\u544A\u4F60\uFF0C\u4E0D\u8981\u7528\u8FD9\u79CD\u8BED\u6C14\u8BF4\u8BDD\uFF0C\u4F60\u5DF2\u7ECF\u4E0D\u662F\u603B\u6307\u6325\u4E86\u3002\u4E0D\u59A8\u544A\u8BC9\u4F60\uFF0C\u767D\u5929\u5FB7\u5DF2\u7ECF\u5411\u699C\u7237\u6295\u964D\uFF0C\u4E0D\u8D39\u4E00\u5175\u4E00\u5352\uFF0C\u6C85\u9547\u5C31\u50CF\u4E2A\u5A4A\u5B50\uFF0C\u628A\u5927\u817F\u53C9\u5F00\uFF0C\u7B49\u7740\u6211\u5E26\u5F1F\u5144\u4EEC\u524D\u53BB\u4EAB\u5C3D\u8363\u534E\u5BCC\u8D35\u5566\uFF0C\u54C8\u54C8\u54C8\u3002\u300D</p><p>\u300C\u4E0D\u53EF\u80FD\uFF01\u8FD9\u4E00\u5B9A\u662F\u767D\u72D7\u7684\u8BE1\u8BA1\u3002\u300D</p><p>\u300C\u968F\u4F60\u4FE1\u4E0D\u4FE1\u5566\u3002\u770B\u5728\u540C\u9053\u7684\u4EFD\u4E0A\uFF0C\u6211\u4E5F\u4E0D\u7792\u4F60\uFF0C\u6570\u65E5\u524D\uFF0C\u767D\u5929\u5FB7\u901A\u8FC7\u77F3\u6D1E\u4E3B\uFF0C\u77F3\u6D1E\u4E3B\u7B54\u5E94\u66FF\u4ED6\u5E26\u4FE1\u7ED9\u699C\u7237\uFF0C\u5F00\u51FA\u7684\u6761\u4EF6\u6253\u52A8\u4E86\u699C\u7237\uFF0C\u65B9\u6709\u4ECA\u65E5\u4E4B\u53D8\u6545\u3002\u8FD9\u4E8B\u6211\u4E5F\u662F\u4E8B\u540E\u624D\u77E5\u9053\u3002\u300D</p><p>\u6D77\u68E0\u96BE\u4EE5\u7F6E\u4FE1\uFF0C\u300C\u96BE\u9053\u6211\u7ED9\u7684\u8FD8\u4E0D\u591F\u4F18\u539A\uFF1F\u699C\u7237\u8FD8\u4EB2\u53E3\u7ED9\u4E86\u627F\u8BFA\u3002\u300D</p><p>\u7533\u660C\u51B7\u7B11\uFF0C\u300C\u767D\u5929\u5FB7\u8981\u4E3A\u699C\u7237\u5F00\u8F9F\u4E00\u4E2A\u6700\u5927\u7684\u70DF\u571F\u79CD\u690D\u56ED\uFF0C\u6536\u76CA\u4E8C\u516B\u5206\u6210\uFF0C\u70DF\u571F\u80FD\u8D5A\u591A\u5C11\u94B1\u4F60\u5FC3\u91CC\u4E5F\u6E05\u695A\uFF0C\u8FD9\u662F\u4F60\u505A\u5F97\u5230\u7684\u5417\uFF1F\u53EF\u6028\u4E0D\u5F97\u699C\u7237\uFF0C\u4EBA\u5728\u6C5F\u6E56\uFF0C\u5229\u5B57\u5F53\u5934\u554A\u3002\u300D\u4ED6\u53E3\u53E3\u58F0\u58F0\u8BF4\u4E0D\u8981\u6028\u699C\u7237\uFF0C\u8A00\u4E0B\u4E4B\u610F\u5374\u662F\u5904\u5904\u5728\u5F71\u5C04\u4EC0\u4E48\u3002\u300D</p><p>\u6D77\u68E0\u4E00\u542C\u5C31\u77E5\u9053\u8FD9\u4E8B\u662F\u771F\u7684\uFF0C\u867D\u7136\u5E76\u975E\u90A3\u4E48\u4FE1\u4EFB\u699C\u7237\uFF0C\u4F46\u88AB\u518D\u5EA6\u51FA\u5356\u7684\u611F\u89C9\u8FD8\u662F\u50CF\u4E00\u6761\u6BD2\u866B\u5927\u53E3\u5927\u53E3\u5543\u98DF\u7740\u5979\u7684\u5FC3\uFF0C\u706B\u8FA3\u8FA3\u7684\u75DB\uFF0C\u53C8\u50CF\u6B63\u5728\u6EBA\u5165\u6C34\u4E2D\uFF0C\u5373\u5C06\u6CA1\u9876\uFF0C\u62DA\u547D\u8981\u6293\u4F4F\u4E00\u6839\u7A3B\u8349\uFF0C\u300C\u767D\u5929\u5FB7\u5462\uFF1F\u8FD8\u5728\u4E0D\u5728\u57CE\u91CC\u3002\u300D</p><p>\u300C\u963F\u6708\u5E26\u6765\u4E86\u8001\u7237\u5B50\u7684\u53E6\u4E00\u5F20\u624B\u4EE4\uFF0C\u300D\u4ED6\u62FF\u51FA\u4E00\u5F20\u5199\u6EE1\u5B57\u7684\u9EC4\u7EB8\u6643\u4E86\u6643\uFF0C\u300C\u6628\u5929\u665A\u4E0A\uFF0C\u5DF2\u7ECF\u8BA9\u5F00\u4E00\u6761\u9053\uFF0C\u653E\u4ED6\u4EEC\u9003\u8D70\u4E86\u3002\u300D</p><p>\u8D39\u5C3D\u5FC3\u8840\uFF0C\u7EC8\u4ED8\u6D41\u6C34\uFF0C\u6D77\u68E0\u773C\u524D\u4E00\u9ED1\uFF0C\u52C9\u5F3A\u6276\u4F4F\u5899\u58C1\u624D\u6CA1\u6709\u683D\u5012\u4E0B\u53BB\u3002\u300C\u653E\u6211\u8D70\uFF0C\u653E\u6211\u8D70\uFF0C\u653E\uFF0C\u6211\uFF0C\u8D70\uFF01\u300D</p><p>\u6700\u540E\u51E0\u5B57\u5979\u51E0\u4E4E\u662F\u543C\u51FA\u6765\u7684\uFF0C\u5B57\u5B57\u6CE3\u8840\u3002</p><p>\u4E00\u5929\u8FC7\u53BB\u4E86\u3002</p><p>\u7533\u660C\u518D\u6765\u770B\u5979\uFF0C\u53EB\u55BD\u5570\u4EEC\u90FD\u9000\u5F00\u4E86\u597D\u8FDC\uFF0C\u8D70\u8FDB\u5979\u4E34\u65F6\u7684\u76D1\u7262\u91CC\u3002\u6D77\u68E0\u7F29\u5728\u89D2\u843D\uFF0C\u8336\u996D\u672A\u52A8\uFF0C\u95ED\u7740\u773C\u775B\uFF0C\u5F62\u5BB9\u67AF\u69C1\uFF0C\u4E00\u4E0B\u5B50\u8C61\u82CD\u8001\u4E86\u597D\u591A\u5C81\u3002\u7533\u660C\u5728\u5979\u9762\u524D\u4E5F\u76D8\u817F\u5750\u4E0B\uFF0C\u76F8\u5BF9\u65E0\u8A00\uFF0C\u5750\u4E86\u597D\u4E45\u3002</p><p>\u300C\u8FD8\u662F\u6211\u5148\u8BF4\u5427\u3002\u6211\u662F\u4E2A\u7C97\u4EBA\uFF0C\u5728\u6C5F\u6E56\u4E0A\u574F\u4E8B\u505A\u7EDD\uFF0C\u4E0D\u662F\u4E2A\u597D\u9E1F\uFF0C\u4F46\u76F4\u6765\u76F4\u53BB\uFF0C\u4FE1\u8A00\u5B88\u8BFA\u4E5F\u662F\u51FA\u4E86\u540D\u7684\uFF0C\u6211\u4E5F\u4E0D\u85CF\u7740\u814B\u7740\uFF0C\u8BF4\u4E2A\u660E\u767D\u7684\uFF0C\u767D\u5929\u5FB7\u80FD\u4E0E\u8001\u7237\u5B50\u505A\u4E2A\u4EA4\u6613\u628A\u4F60\u51FA\u5356\uFF0C\u6211\u4E5F\u60F3\u4E0E\u4F60\u505A\u4E2A\u4EA4\u6613\uFF0C\u5E72\u6389\u8001\u7237\u5B50\uFF0C\u53EA\u8981\u4F60\u7B54\u5E94\uFF0C\u6210\uFF0C\u653E\u4F60\u4E00\u6761\u751F\u8DEF\uFF0C\u8FD8\u628A\u767D\u5929\u5FB7\u7684\u6211\u4EA4\u5230\u4F60\u624B\u4E0A\uFF0C\u8D25\uFF0C\u6211\u4E3A\u4F60\u62A5\u4EC7\u3002\u300D</p><p>\u6D77\u68E0\u62AC\u8D77\u5934\u6765\uFF0C\u76EE\u5149\u70AF\u70AF\u903C\u89C6\u7740\u4ED6\uFF0C\u300C\u4F60\u81EA\u5DF1\u4E5F\u53EF\u4EE5\u4E0B\u624B\u7684\u3002\u300D</p><p>\u7533\u660C\u6CF0\u7136\u81EA\u82E5\uFF0C\u300C\u4E0D\u9519\uFF0C\u6211\u4E5F\u53EF\u4EE5\uFF0C\u4F46\u7531\u6211\u6740\uFF0C\u96BE\u9003\u72AF\u4E0A\u4E4B\u7F6A\uFF0C\u4ECA\u540E\u5144\u5F1F\u4EEC\u5982\u4F55\u670D\u6211\uFF0C\u7531\u4F60\u6740\uFF0C\u80CC\u4FE1\u5F03\u4E49\uFF0C\u4EBA\u7686\u8BDB\u4E4B\uFF0C\u540D\u6B63\u8A00\u987A\u3002\u300D</p><p>\u300C\u6211\u600E\u4E48\u6653\u5F97\u4F60\u4E0D\u662F\u4E0E\u767D\u72D7\u4E32\u901A\u4E00\u6C14\uFF0C\u501F\u5200\u6740\u4EBA\uFF0C\u518D\u706D\u6211\u53E3\u3002\u300D</p><p>\u7533\u660C\u51F6\u8138\u4E0A\u54A7\u5F00\u5634\u7B11\u7B11\uFF0C\u5374\u6CA1\u6709\u534A\u5206\u7B11\u610F\u3002</p><p>\u300C\u4F60\u522B\u5FD8\u4E86\uFF0C\u767D\u5BB6\u706D\u65CF\u53EF\u90FD\u662F\u6211\u7533\u67D0\u4EBA\u6253\u7684\u524D\u950B\uFF0C\u767D\u5929\u5FB7\u6068\u4E0D\u5F97\u628A\u6211\u5BDD\u76AE\u98DF\u8089\uFF0C\u5F53\u7136\u4E5F\u662F\u6211\u8981\u526A\u9664\u7684\u4E0B\u4E00\u4E2A\u654C\u4EBA\uFF0C\u5728\u8FD9\u4E00\u70B9\u4E0A\uFF0C\u6211\u4EEC\u53EF\u662F\u4E00\u81F4\u7684\u3002\u300D</p><p>\u6D77\u68E0\u4E00\u76F4\u5728\u6349\u6478\uFF0C\u5176\u5B9E\u5979\u4E5F\u6CA1\u6709\u5176\u5B83\u9009\u62E9\u4E86\uFF0C\u5979\u5982\u4ECA\u6210\u4E86\u771F\u6B63\u7684\u5B64\u5BB6\u5BE1\u4EBA\uFF0C\u5C31\u7B97\u4E0D\u6B7B\u5728\u8FD9\u91CC\uFF0C\u4E5F\u518D\u6CA1\u6709\u5176\u5B83\u53EF\u501F\u52A9\u7684\u529B\u91CF\uFF0C\u66F4\u8C08\u4E0D\u4E0A\u8FFD\u8E2A\u767D\u5929\u5FB7\u7684\u4E0B\u843D\uFF0C\u62A5\u4EC7\u4E8C\u5B57\u7B80\u76F4\u6210\u4E86\u7B11\u8BDD\uFF0C\u5979\u4F1A\u7518\u5FC3\u5417\uFF1F</p><p>\u300C\u6211\u8981\u5148\u8003\u8651\u4E00\u4E0B\u3002\u300D</p>',64);function t(i,n){return r}var o=a(e,[["render",t]]);export{o as default};
