import{e as p}from"./app.630af5f2.js";import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";const e={},r=p('<h1 id="\u7B2C\u5341\u516D\u7AE0-\u8F6E\u5978" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u5341\u516D\u7AE0-\u8F6E\u5978" aria-hidden="true">#</a> \u7B2C\u5341\u516D\u7AE0 \u8F6E\u5978</h1><p>\u5E74\u5173\u5C06\u8FD1\uFF0C\u767D\u5E9C\u65B0\u5B85\u91CC\u5F20\u706F\u7ED3\u5F69\uFF0C\u559C\u6C14\u6D0B\u6D0B\u3002</p><p>\u8FD9\u4E00\u5E74\u5BF9\u767D\u5929\u5FB7\u6765\u8BF4\u592A\u91CD\u8981\u4E5F\u592A\u6F02\u4EAE\u4E86\uFF0C\u9664\u4E86\u6D77\u68E0\u7684\u5F97\u800C\u590D\u5931\u4E00\u70B9\u70B9\u90C1\u95F7\uFF0C\u5176\u4F59\u4E8B\u60C5\u771F\u662F\u5FC3\u60F3\u4E8B\u6210\uFF0C\u4E8B\u4E8B\u9042\u610F\u3002</p><p>\u6C85\u9547\u6700\u51FA\u540D\u7684\u51E0\u4E2A\u7F8E\u4EBA\u90FD\u6536\u5165\u4E86\u4ED6\u7684\u6000\u62B1\uFF0C\u60F3\u600E\u4E48\u6446\u5E03\u5C31\u600E\u4E48\u6446\u5E03\uFF0C\u5B98\u573A\u4E0A\u4ED6\u4E0E\u7701\u5E9C\u7684\u5434\u7763\u519B\u642D\u4E0A\u4E86\u7EBF\uFF0C\u98DE\u9EC4\u817E\u8FBE\u6307\u65E5\u53EF\u5F85\uFF0C\u751F\u610F\u573A\u4E0A\uFF0C\u501F\u4ED6\u7684\u5B98\u5A01\uFF0C\u4ED6\u7684\u5BB6\u65CF\u5DF2\u4ECE\u519C\u6751\u8D70\u5411\u57CE\u9547\uFF0C\u63A7\u5236\u4E86\u6574\u4E2A\u6C85\u9547\u7684\u76D0\u94C1\u4E13\u5356\uFF0C\u9010\u6B65\u5411\u5468\u8FB9\u8F90\u5C04\u3002</p><p>\u767D\u5BB6\u672C\u65CF\u5144\u5F1F\u7709\u98DE\u8272\u821E\u5730\u5927\u8086\u5E86\u529F\uFF0C\u767D\u5929\u5FB7\u5374\u5F88\u51B7\u9759\uFF0C\u4ED6\u770B\u5230\u4E86\u4E00\u4E2A\u66F4\u6709\u524D\u9014\u548C\u300C\u94B1\u300D\u9014\u7684\u884C\u5F53\uFF0C\u9E26\u7247\uFF01</p><p>\u6E58\u897F\u5C71\u9AD8\u7687\u5E1D\u8FDC\uFF0C\u5730\u5E7F\u4EBA\u7A00\uFF0C\u65E0\u8BBA\u4ECE\u4EBA\u6587\u6761\u4EF6\u8FD8\u662F\u5730\u7406\u6761\u4EF6\u90FD\u5F97\u5929\u72EC\u539A\uFF0C\u7981\u70DF\u4EE4\u4E0B\u4E86\u591A\u5E74\uFF0C\u8FD8\u662F\u6709\u4E00\u4E9B\u96F6\u6563\u519C\u6237\u5728\u5077\u79CD\uFF0C\u5C31\u662F\u83B7\u5229\u5B9E\u5728\u8BF1\u4EBA\u3002\u4ED6\u4E0D\u4EC5\u60F3\u8981\u628A\u9E26\u7247\u8D70\u79C1\u8D29\u8FDB\u6765\uFF0C\u8FD8\u8981\u501F\u7981\u70DF\u4E3A\u540D\uFF0C\u626B\u6E05\u79C1\u79CD\u6237\uFF0C\u81EA\u5DF1\u641E\u5927\u9762\u79EF\u79CD\u690D\uFF0C\u518D\u5356\u5411\u5168\u56FD\uFF0C\u90A3\u8BE5\u662F\u4E2A\u4EC0\u4E48\u6D77\u8D5A\u6CD5\uFF1F</p><p>\u6B64\u4E8B\u4E00\u6210\uFF0C\u771F\u662F\u505A\u68A6\u90FD\u4F1A\u7B11\u9192\u3002</p><p>\u6240\u4EE5\uFF0C\u4ED6\u6253\u7B97\u65B0\u5E74\u4E00\u8FC7\u5C31\u5168\u529B\u8FD0\u4F5C\uFF0C\u4E0D\u8FC7\u4E4B\u524D\u5185\u90E8\u8FD8\u5F97\u706D\u706B\uFF0C\u53F8\u9A6C\u5357\u53D7\u826F\u5FC3\u8C34\u8D23\u592A\u91CD\uFF0C\u65E9\u5DF2\u8F9E\u804C\u79FB\u5C45\u4ED6\u4E61\u517B\u75C5\u4E86\uFF0C\u674E\u8D35\u3001\u4E8C\u559C\u5B50\u8FD9\u4E9B\u5BB6\u4F19\u8FD8\u5728\uFF0C\u6043\u529F\u800C\u9A84\uFF0C\u4E0D\u77E5\u9053\u5929\u9AD8\u5730\u539A\uFF0C\u59D4\u5B9E\u6709\u4E9B\u8BA8\u538C\uFF0C\u96BE\u602A\u8D75\u7687\u5E1D\u8981\u5154\u6B7B\u72D7\u70F9\uFF0C\u8001\u5B50\u73B0\u5728\u8FD8\u7528\u5F97\u7740\u4F60\u4EEC\uFF0C\u5E10\u6162\u6162\u518D\u7B97\u5427\uFF0C\u603B\u6709\u90A3\u4E00\u5929\u7684\u3002</p><p>\u540E\u82B1\u56ED\u8349\u576A\u4E0A\u6446\u4E86\u4E09\u5F20\u5927\u684C\uFF0C\u597D\u83DC\u597D\u70DF\u597D\u9152\uFF0C\u5750\u7684\u90FD\u662F\u968F\u767D\u5929\u5FB7\u51FA\u751F\u5165\u6B7B\u7684\u5FC3\u8179\u6B7B\u515A\uFF0C\u89E5\u7B79\u4EA4\u9519\uFF0C\u9152\u8FC7\u591A\u5DE1\uFF0C\u5927\u90E8\u5206\u4EBA\u5747\u5DF2\u8138\u8272\u7823\u7EA2\uFF0C\u5F62\u9AB8\u653E\u6D6A\uFF0C\u73B0\u51FA\u539F\u5F62\u6765\u3002</p><p>\u767D\u5929\u5FB7\u7AD9\u8D77\u6765\uFF0C\u4E3E\u8D77\u4E00\u76CF\u767D\u9152\uFF0C\u9AD8\u58F0\u53EB\u9053\uFF0C\u300C\u5F1F\u5144\u4EEC\uFF01\u300D</p><p>\u55A7\u95F9\u58F0\u5E73\u606F\u4E0B\u6765\u3002</p><p>\u300C\u6211\u767D\u67D0\u6709\u4ECA\u5929\uFF0C\u6700\u611F\u8C22\u7684\u4E0D\u662F\u4E0A\u5929\uFF0C\u4E0D\u662F\u7236\u6BCD\uFF0C\u800C\u662F\u5728\u5EA7\u7684\u5404\u4F4D\u5144\u5F1F\u3002\u767D\u67D0\u5728\u8FD9\u91CC\u53EA\u8BB2\u4E00\u53E5\u8BDD\uFF0C\u53EA\u656C\u4E00\u676F\u9152\uFF0C\u8FD9\u676F\u9152\u4E4B\u540E\uFF0C\u6709\u798F\u540C\u4EAB\uFF0C\u6709\u96BE\u540C\u5F53\uFF0C\u6717\u6717\u6B64\u5FC3\uFF0C\u5929\u5730\u5171\u9274\uFF01\u300D</p><p>\u4F17\u4EBA\u54C4\u7136\u800C\u8D77\uFF0C\u4E00\u8FB9\u8BF4\u7740\u7C7B\u4F3C\u7684\u8A93\u8BCD\uFF0C\u4E00\u8FB9\u5171\u559D\u4E86\u4E00\u676F\u3002\u6742\u4E71\u4E2D\u5374\u6709\u602A\u58F0\u626C\u8D77\uFF0C\u300C\u53EA\u6015\u6709\u96BE\u53EF\u4EE5\u5F53\uFF0C\u6709\u798F\u6CA1\u5904\u4EAB\u3002\u300D</p><p>\u767D\u5929\u5FB7\u9762\u4E0D\u6539\u8272\uFF0C\u5927\u7B11\u9053\uFF0C\u300C\u653E\u4F60\u5A18\u7684\u72D7\u5C41\uFF0C\u8001\u5B50\u4ECA\u5929\u5C31\u4E0E\u5927\u5BB6\u5171\u798F\u3002\u300D</p><p>\u4ED6\u6253\u4E86\u4E2A\u624B\u52BF\uFF0C\u5FFD\u7136\u4ECE\u5C0F\u6E56\u4E2D\u592E\u7684\u51C9\u5385\u98D8\u6765\u4E00\u9635\u60A0\u626C\u7684\u53E4\u7434\u58F0\u3002</p><p>\u4F17\u4EBA\u7684\u76EE\u5149\u4E0D\u7EA6\u800C\u540C\u5730\u5438\u5F15\u8FC7\u53BB\uFF0C\u65B9\u624D\u6CE8\u610F\u5230\u5C0F\u51C9\u4EAD\u56DB\u5468\u6302\u4E0A\u4E86\u8F7B\u7F57\u5E54\uFF0C\u5728\u5FAE\u98CE\u4E2D\u8F7B\u8F7B\u6446\u52A8\uFF0C\u5E7D\u5E7D\u7434\u58F0\u5C31\u662F\u4ECE\u8FD9\u5E54\u540E\u98D8\u51FA\uFF0C\u53EA\u95FB\u5176\u58F0\u4E0D\u89C1\u5176\u4EBA\u3002</p><p>\u6B64\u65F6\u5DF2\u662F\u508D\u665A\u65F6\u5206\uFF0C\u66AE\u4E91\u56DB\u5408\uFF0C\u6E56\u9762\u9752\u84DD\uFF0C\u7434\u58F0\u51C4\u51B7\uFF0C\u4F3C\u54BD\u4F3C\u545C\uFF0C\u4E0D\u7ECF\u610F\u95F4\u5374\u9690\u7740\u83AB\u5927\u7684\u75DB\u82E6\uFF0C\u7A0D\u901A\u97F3\u97F5\u8005\u65E0\u4E0D\u4E3A\u4E4B\u52A8\u5BB9\u3002</p><p>\u53EA\u53EF\u60DC\u5728\u5EA7\u7684\u53EF\u4E0D\u662F\u53D4\u7259\u4F2F\u5E73\u4E4B\u6D41\u96C5\u4E4B\u58EB\uFF0C\u800C\u662F\u4E00\u7FA4\u7C97\u83BD\u65E0\u77E5\u7684\u8822\u7269\uFF0C\u8D77\u5148\u8FD8\u80FD\u5F3A\u884C\u514B\u5236\uFF0C\u4E0D\u591A\u65F6\u4FBF\u8010\u4E0D\u4F4F\u4E86\uFF0C\u53EB\u56B7\u8D77\u6765\uFF0C\u300C\u767D\u8001\u5927\u5C31\u662F\u53EB\u8001\u5B50\u4EEC\u542C\u8FD9\u4E2A\u72D7\u5C41\u5440\uFF0C\u4E0D\u5982\u5230\u5929\u9999\u9601\u542C\u5341\u516B\u6A21\u8FC7\u763E\u54E9\u3002\u300D</p><p>\u767D\u5929\u5FB7\u4E0D\u7981\u76B1\u7709\uFF0C\u8FD8\u662F\u8010\u5FC3\u5730\u8BF4\uFF0C\u300C\u7A0D\u5B89\u52FF\u8E81\u5566\uFF0C\u7CBE\u5F69\u7684\u8FD8\u5728\u540E\u9762\u3002\u300D</p><p>\u5929\u8272\u5DF2\u6697\uFF0C\u56DB\u4E0B\u91CC\u70B9\u4EAE\u4E86\u7535\u706F\uFF0C\u53EA\u6709\u51C9\u4EAD\u8FD8\u662F\u9ED1\u6C89\u6C89\u7684\uFF0C\u7434\u58F0\u4E0D\u7EDD\u3002</p><p>\u5FFD\u7136\uFF0C\u8F7B\u5E54\u5185\u4EAE\u8D77\u4E86\u706F\u5149\uFF0C\u4E00\u76CF\u3001\u4E24\u76CF\uFF0C\u4E00\u5171\u56DB\u76CF\uFF0C\u653E\u7F6E\u5728\u5730\u4E0A\uFF0C\u628A\u6574\u4E2A\u51C9\u4EAD\u7167\u5F97\u620F\u53F0\u4E00\u822C\u901A\u900F\u4EAE\u5802\u3002</p><p>\u8FD9\u4E0B\u629A\u7434\u8005\u518D\u4E5F\u65E0\u6240\u9041\u5F62\uFF0C\u662F\u4E00\u4E2A\u4FA7\u50CF\uFF0C\u9694\u7740\u8F7B\u5E54\uFF0C\u53EF\u89C1\u5F97\u662F\u4E00\u540D\u8EAB\u6750\u7A88\u7A95\u7684\u5973\u5B50\u3002</p><p>\u8BB8\u591A\u4EBA\u7684\u7B2C\u4E00\u53CD\u5E94\u5C31\u662F\u51B7\u5982\u971C\uFF0C\u53C8\u4E0D\u6562\u76F8\u4FE1\u5979\u662F\u51B7\u5982\u971C\u3002</p><p>\u767D\u5929\u5FB7\u62CD\u62CD\u624B\uFF0C\u7434\u58F0\u505C\u4E86\u3002</p><p>\u629A\u7434\u5973\u5B50\u7684\u8EAB\u5F71\u505C\u987F\u4E86\u4E00\u4F1A\uFF0C\u6162\u6162\u8D77\u8EAB\uFF0C\u7EA4\u957F\u7684\u624B\u6307\u6478\u5411\u9886\u53E3\uFF0C\u968F\u5373\uFF0C\u4E0A\u8863\u89E3\u4E86\u5F00\u6765\uFF0C\u6254\u4E0B\uFF0C\u63A5\u7740\u662F\u89E3\u5F00\u4E00\u4EF6\u809A\u515C\u4E4B\u7C7B\u7684\u4E1C\u897F\u3002</p><p>\u773C\u5C16\u7684\u4EBA\u5DF2\u53D1\u73B0\uFF0C\u5973\u5B50\u52A8\u4F5C\u53D8\u52A8\u95F4\uFF0C\u4E24\u53EA\u6D51\u56ED\u7CBE\u5DE7\u7684\u4E73\u623F\u5F39\u8DF3\u53EF\u8FA8\u3002</p><p>\u867D\u9694\u7740\u4E00\u5C42\u5E03\uFF0C\u4F46\u6BCF\u4E00\u4E2A\u7EC6\u8282\u51E0\u4E4E\u90FD\u80FD\u770B\u5F97\u4E00\u6E05\u4E8C\u695A\uFF0C\u4E0D\u5931\u771F\uFF0C\u751A\u81F3\u6BD4\u64A4\u53BB\u5E18\u5B50\u9762\u5BF9\u9762\u8FD8\u591A\u51E0\u5206\u6687\u601D\uFF0C\u66F4\u4EE4\u4EBA\u8840\u8109\u8D32\u5F20\u3002</p><p>\u521A\u8FD8\u5728\u5F39\u594F\u9AD8\u96C5\u4E50\u5668\uFF0C\u8F6C\u773C\u5C31\u8868\u6F14\u8D77\u4E86\u8131\u8863\u79C0\uFF0C\u53D8\u5316\u4E4B\u5FEB\u3001\u573A\u9762\u4E4B\u523A\u6FC0\u4EE4\u5728\u573A\u6240\u6709\u4EBA\u9F3B\u8840\u90FD\u6765\u4E0D\u53CA\u6D41\u51FA\u6765\u3002\u5589\u5934\u96C6\u4F53\u54CD\u4EAE\u5730\u5495\u561F\u4E00\u58F0\u3002</p><p>\u5F85\u5973\u5B50\u4ECE\u4E0B\u8EAB\u626F\u51FA\u4E00\u4E2A\u5E03\u6761\u4E00\u822C\u7684\u4E1C\u897F\uFF0C\u767D\u5929\u5FB7\u5FAE\u7B11\u9053\uFF0C\u300C\u5927\u5BB6\u4E0D\u59A8\u731C\u731C\u8FD9\u4EAD\u4E2D\u4EBA\u6B63\u5728\u8131\u4EC0\u4E48\u4E1C\u897F\u3002\u300D</p><p>\u4E00\u4E0B\u8C03\u52A8\u4E86\u6C14\u6C1B\uFF0C\u4F17\u4EBA\u4E03\u5634\u516B\u820C\u5730\u53EB\uFF0C\u300C\u624B\u5DFE\uFF01\u300D\u300C\u5E15\u5B50\uFF01\u300D\u300C\u5E95\u88E4\uFF01\u300D\u300C\u8001\u5B50\u8BF4\u662F\u6708\u4E8B\u5E26\uFF01\u300D\u300C\u64CD\uFF0C\u4F60\u5C0F\u5B50\u8FD9\u90FD\u77E5\u9053\uFF1F\u300D\u300C\u54C8\u54C8\u54C8\u2026\u2026\u300D</p><p>\u6D6A\u7B11\u95F4\uFF0C\u5973\u5B50\u5DF2\u5C06\u5168\u8EAB\u9664\u5F97\u5149\u5149\uFF0C\u6B63\u9762\u770B\u53BB\u518D\u65E0\u5BF8\u7F15\u3002</p><p>\u4ECE\u82B1\u56ED\u53E6\u4E00\u4FA7\u4E0D\u77E5\u5728\u54EA\u4E2A\u89D2\u843D\u54CD\u8D77\u4E86\u4E00\u652F\u53E4\u66F2\u7684\u6C11\u4E50\u5408\u594F\uFF0C\u66F2\u98CE\u8FE5\u5F02\uFF0C\u6B22\u5FEB\u6D41\u7545\u3002</p><p>\u5973\u5B50\u7F13\u7F13\u968F\u7740\u53E4\u66F2\u8D77\u821E\uFF0C\u957F\u817F\u7EC6\u8170\uFF0C\u8D64\u8EAB\u76D8\u53D1\uFF0C\u4F3C\u6566\u714C\u98DE\u5929\uFF0C\u4F3C\u4ED9\u5973\u7FE9\u8DF9\uFF0C\u52A8\u4F5C\u6781\u5176\u4F18\u7F8E\u96C5\u81F4\uFF0C\u8EAB\u4F53\u5374\u53C8\u5145\u6EE1\u8089\u4F53\u7684\u6B32\u671B\u3002</p><p>\u4ECE\u6765\u6CA1\u6709\u5C06\u9AD8\u96C5\u4E0E\u4F4E\u4FD7\u7ED3\u5408\u5F97\u5982\u6B64\u5B8C\u7F8E\u7684\u3002</p><p>\u8F7B\u5E54\u4E00\u70B9\u70B9\u62C9\u5F00\uFF0C\u821E\u8005\u7EC8\u4E8E\u4E0E\u56F4\u89C2\u8005\u88F8\u88CE\u76F8\u5BF9\u3002</p><p>\u51B7\u5982\u971C\uFF0C\u679C\u771F\u662F\u7F8E\u7EDD\u4EBA\u5BF0\u7684\u51B7\u5982\u971C\u3002</p><p>\u8FD9\u9AD8\u50B2\u7684\u7F8E\u5987\uFF0C\u8FD9\u6781\u7F8E\u7684\u7CBE\u7075\uFF0C\u5728\u4E00\u7FA4\u755C\u751F\u9762\u524D\uFF0C\u518D\u4E00\u6B21\u4E3B\u52A8\u6253\u5F00\u4E86\u81EA\u5DF1\u8D1E\u6D01\u7684\u8EAB\u4F53\u3002\u5982\u679C\u8BF4\u7B2C\u4E00\u6B21\u5979\u7684\u4F53\u6001\u8FD8\u6709\u4E9B\u81C3\u80BF\uFF0C\u795E\u60C5\u8FD8\u6709\u88AB\u8FEB\u540E\u7684\u6194\u60B4\uFF0C\u90A3\u4E48\u8FD9\u4E00\u6B21\uFF0C\u5979\u7684\u4E00\u5207\u90FD\u662F\u90A3\u4E48\u5B8C\u7F8E\u3002</p><p>\u751A\u81F3\u8FD8\u5E26\u7740\u4E00\u4E1D\u5FAE\u7B11\u3002</p><p>\u9664\u4E86\u767D\u5929\u5FB7\uFF0C\u8C01\u4E5F\u4E0D\u77E5\u9053\u8FD9\u5FAE\u7B11\u80CC\u540E\u662F\u591A\u5C11\u82E6\u6DA9\u3002</p><p>\u4F17\u4EBA\u5DF2\u7136\u6C89\u9189\uFF0C\u5C0F\u8001\u5F1F\u4EEC\u96C6\u4F53\u7ACB\u6B63\u81F4\u656C\u3002</p><p>\u4E00\u66F2\u7EC8\u4E86\uFF0C\u8D64\u6761\u6761\u7684\u51B7\u5982\u971C\u6B3E\u6B3E\u901A\u8FC7\u4E5D\u66F2\u56DE\u5ECA\uFF0C\u6B65\u5411\u4EBA\u7FA4\u3002</p><p>\u767D\u5929\u5FB7\u9053\uFF0C\u300C\u521A\u624D\u5927\u5BB6\u9694\u5F97\u8FDC\uFF0C\u6CA1\u77A7\u6E05\u695A\uFF0C\u4F60\u7AD9\u4E0A\u684C\u6765\uFF0C\u5C55\u793A\u4E00\u4E0B\u3002\u300D</p><p>\u51B7\u5982\u971C\u8138\u8272\u82CD\u767D\uFF0C\u4E0D\u53D1\u4E00\u8A00\uFF0C\u8E0F\u4E0A\u77EE\u51F3\uFF0C\u7AD9\u5230\u77F3\u684C\u4E4B\u4E0A\uFF0C\u7136\u540E\u5C06\u4E00\u6761\u817F\u76F4\u76F4\u5730\u6273\u8D77\u6765\uFF0C\u677F\u8FC7\u5934\u9876\uFF0C\u4E0B\u8EAB\u6700\u9690\u79D8\u5904\u4E00\u89C8\u65E0\u4F59\uFF0C\u5BB4\u4F1A\u4E4B\u524D\uFF0C\u5979\u88AB\u8FEB\u5C06\u672C\u5C31\u4E0D\u751A\u8302\u5BC6\u7684\u4E0B\u8EAB\u6BDB\u53D1\u5C3D\u6570\u522E\u53BB\uFF0C\u6B64\u65F6\u770B\u4E0A\u53BB\u5982\u5E7C\u5973\u4E00\u822C\u6D01\u51C0\u3002</p><p>\u767D\u5929\u5FB7\u7B11\u9053\uFF0C\u300C\u8001\u5B50\u6700\u559C\u6B22\u5149\u677F\u5B50\uFF0C\u5144\u5F1F\u4EEC\u968F\u4FBF\u77A7\uFF0C\u968F\u4FBF\u6478\u3002\u300D</p><p>\u8FD9\u8BDD\u597D\u751F\u719F\u6089\uFF0C\u597D\u50CF\u5728\u54EA\u542C\u5230\uFF0C\u51B7\u5982\u971C\u5FC3\u4E2D\u5FFD\u5730\u4E00\u75BC\uFF0C\u5FC6\u8D77\u6D77\u68E0\u66FE\u7ECF\u8BF4\u8D77\u7684\u5F80\u4E8B\uFF0C\u65B9\u624D\u604D\u609F\uFF0C\u773C\u524D\u7684\u767D\u5929\u5FB7\u6B63\u662F\u5F53\u5E74\u51CC\u8FB1\u6D77\u68E0\u7684\u767D\u5BCC\u8D35\uFF0C\u60F3\u4E0D\u5230\u4E16\u4E8B\u8F6E\u8F6C\uFF0C\u5669\u8FD0\u964D\u4E34\u5230\u4E86\u5979\u7684\u5934\u4E0A\u3002</p><p>\u5728\u51B7\u5982\u971C\u7684\u8BB0\u5FC6\u4E2D\uFF0C\u8FD9\u662F\u6700\u6F2B\u957F\u6700\u9ED1\u6697\u4E5F\u662F\u6700\u5907\u53D7\u714E\u71AC\u7684\u4E00\u591C\uFF0C\u6C38\u65E0\u6B62\u5883\u3002</p><p>\u8BB0\u4E0D\u6E05\u662F\u5341\u51E0\u4E2A\u8FD8\u662F\u51E0\u5341\u4E2A\u4EBA\u6251\u5230\u5979\u7684\u8EAB\u5B50\u4E0A\uFF0C\u5C06\u5979\u6402\u5F97\u6B7B\u6B7B\u7684\uFF0C\u4E00\u53EA\u53C8\u4E00\u53EA\u80AE\u810F\u7684\u624B\u638C\u634F\u5411\u5979\u7684\u8EAB\u4F53\u4EFB\u4F55\u90E8\u4F4D\uFF0C\u4E00\u6839\u63A5\u4E00\u6839\u4E11\u964B\u7684\u4E1C\u897F\u585E\u8FDB\u5979\u7684\u4F53\u5185\uFF0C\u72E0\u72E0\u6363\u5F04\u4E00\u9635\uFF0C\u54C6\u54C6\u55E6\u55E6\u5730\u653E\u51FA\u4E00\u56E2\u6C61\u6C41\u3002</p><p>\u5979\u5F88\u60F3\u80CC\u5BF9\u7740\u8FD9\u4E9B\u79BD\u517D\uFF0C\u4F46\u662F\u6709\u4E9B\u5BB6\u4F19\u5C31\u559C\u6B22\u9762\u5BF9\u9762\uFF0C\u770B\u7740\u5979\u82CD\u767D\u65E0\u795E\u7684\u9762\u5BB9\u683C\u5916\u5174\u594B\uFF0C\u628A\u5979\u62C9\u5230\u5E8A\u8FB9\uFF0C\u4E24\u53EA\u811A\u9AD8\u9AD8\u4E3E\u8D77\u5411\u4E24\u8FB9\u5206\u5F00\uFF0C\u7537\u4EBA\u7AD9\u5728\u5730\u4E0A\u5E72\uFF0C\u53CC\u624B\u5728\u5979\u67D4\u8F6F\u7684\u53CC\u5CF0\u4E0A\u5927\u529B\u63C9\u6413\u3002\u5979\u552F\u4E00\u80FD\u505A\u7684\u53EA\u6709\u9EBB\u75F9\u81EA\u5DF1\u7684\u795E\u7ECF\uFF0C\u5F53\u4F5C\u5728\u4F5C\u4E00\u573A\u6076\u68A6\uFF0C\u4E0D\u77E5\u9053\u4EC0\u4E48\u65F6\u5019\u9192\u6765\u3002</p><p>\u4E0D\u5E78\u4E4B\u5E78\u662F\u4E00\u6B21\u53EA\u6709\u4E00\u4E2A\u4EBA\u4E0A\uFF0C\u767D\u5929\u5FB7\u8FD8\u7EA6\u675F\u4ED6\u4EEC\u4E0D\u51C6\u5BF9\u5979\u9634\u6237\u4EE5\u5916\u7684\u90E8\u4F4D\u6253\u4E3B\u610F\uFF0C\u624D\u514D\u53D7\u66F4\u6076\u52A3\u7684\u6467\u6B8B\u3002</p><p>\u7CBE\u6DB2\u6BEB\u65E0\u4F8B\u5916\u5730\u968F\u7740\u4E0D\u540C\u578B\u53F7\u7684\u8089\u68D2\u72E0\u72E0\u51B2\u649E\uFF0C\u6BEB\u4E0D\u7559\u60C5\u5730\u6DF1\u6DF1\u6253\u5165\u5979\u7684\u4F53\u5185\uFF0C\u6709\u7684\u6DF1\u5165\u5230\u4E86\u5B50\u5BAB\u53E3\uFF0C\u8FD8\u6709\u7684\u6349\u72CE\u5730\u5C04\u5230\u4E86\u5979\u7684\u8138\u4E0A\u3001\u773C\u775B\u91CC\u3001\u8033\u6735\u91CC\u3001\u9F3B\u5B54\u4E2D\u2026\u2026</p><p>\u8EAB\u5B50\u810F\u5F97\u72E0\u4E86\uFF0C\u5979\u5C31\u4F1A\u81EA\u5DF1\u722C\u4E0B\u684C\u5B50\uFF0C\u6D17\u4E00\u6D17\u4E0B\u8EAB\uFF0C\u518D\u4E0A\u684C\uFF0C\u8DB4\u7740\uFF0C\u6216\u662F\u8EBA\u7740\uFF0C\u5F20\u5F00\u53CC\u817F\uFF0C\u8FCE\u5019\u4E0B\u4E00\u8F6E\u72CE\u73A9\u3002</p><p>\u8D77\u5148\u5979\u8FD8\u5F02\u5E38\u7F9E\u803B\uFF0C\u505A\u5F97\u591A\u4E86\u5C31\u9EBB\u6728\u4E86\uFF0C\u4E0D\u4EC5\u662F\u8EAB\u4F53\uFF0C\u5305\u62EC\u7075\u9B42\uFF0C\u673A\u68B0\u800C\u719F\u7EC3\u5730\u91CD\u590D\u7740\u8FD9\u4E00\u5957\u7A0B\u5E8F\u3002</p><p>\u5979\u89C9\u5F97\u81EA\u5DF1\u8C61\u6B63\u5728\u4EA4\u914D\u7684\u6BCD\u732A\uFF0C\u6216\u662F\u6BCD\u732A\u90FD\u4E0D\u5982\uFF0C\u81F3\u5C11\u6CA1\u6709\u90A3\u4E48\u591A\u516C\u732A\u540C\u65F6\u4E0A\u5979\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u957F\u8857\u4E0A\uFF0C\u51B7\u5982\u971C\u8DCC\u8DCC\u649E\u649E\u5730\u6025\u6B65\u8D70\u7740\uFF0C\u5934\u53D1\u51CC\u6563\uFF0C\u8EAB\u4E0A\u53EA\u88F9\u4E86\u4E00\u4EF6\u7537\u4EBA\u7684\u957F\u8863\uFF0C\u4E0B\u8EAB\u8FD8\u662F\u8D64\u88F8\u88F8\u7684\uFF0C\u7C98\u7CCA\u7684\u7CBE\u6DB2\u5728\u5979\u7684\u5927\u817F\u4E4B\u95F4\u4E00\u70B9\u70B9\u5730\u6ED1\u51FA\u6765\u3002</p><p>\u987E\u4E0D\u5F97\u8FD9\u4E48\u591A\u4E86\uFF0C\u53EA\u8981\u5B69\u5B50\u62B1\u5728\u624B\u91CC\uFF0C\u80FD\u987A\u5229\u5730\u9003\u51FA\u751F\u5929\uFF0C\u5F62\u8C61\u4E0A\u96BE\u770B\u4E00\u70B9\u53C8\u7B97\u5F97\u4E86\u4EC0\u4E48\uFF1F</p><p>\u8FDB\u5165\u4E0B\u534A\u591C\uFF0C\u90A3\u4E9B\u7537\u4EBA\u4EEC\u603B\u7B97\u9152\u4E5F\u9189\u4E86\uFF0C\u53D1\u6CC4\u5F97\u4E5F\u5DEE\u4E0D\u591A\u4E86\uFF0C\u4E00\u4E2A\u4E2A\u4E1C\u5012\u897F\u6B6A\u8EBA\u4E86\u4E00\u5730\uFF0C\u4E00\u7247\u72FC\u85C9\u3002</p><p>\u51B7\u5982\u971C\u6CE8\u610F\u5230\u5F80\u5E38\u95E8\u53E3\u7684\u5C97\u54E8\u4E5F\u9189\u5012\u4E86\uFF0C\u6ED1\u5728\u95E8\u8FB9\u6253\u9F3E\uFF0C\u8FD9\u53EF\u771F\u662F\u4E00\u4E2A\u592A\u597D\u7684\u673A\u4F1A\uFF0C\u5979\u8BD5\u4E86\u8BD5\u628A\u817F\u4E3E\u8D77\u6765\uFF0C\u5374\u662F\u94BB\u5FC3\u7684\u75DB\uFF0C\u4E5F\u4E0D\u77E5\u54EA\u6765\u7684\u6C14\u529B\uFF0C\u786C\u662F\u5C06\u521B\u4F24\u7F6E\u4E4B\u5EA6\u5916\uFF0C\u5077\u62B1\u51FA\u719F\u7761\u7684\u8FDE\u751F\uFF0C\u5728\u591C\u8272\u7684\u63A9\u62A4\u4E0B\u6E9C\u4E86\u51FA\u6765\u3002</p><p>\u524D\u8FB9\u5DF2\u662F\u6C85\u6C34\u6865\u3002</p><p>\u6865\u4E0A\u6709\u51E0\u4EBA\uFF0C\u60A0\u95F2\u5730\u6563\u6B65\uFF0C\u770B\u5230\u5979\u4EB2\u70ED\u5730\u6253\u4E86\u58F0\u62DB\u547C\uFF0C\u300C\u53BB\u54EA\u554A\uFF1F\u300D</p><p>\u51B7\u5982\u971C\u75DB\u82E6\u5730\u547B\u541F\u4E00\u58F0\uFF0C\u300C\u5929\u54EA\uFF01\u300D\u8EAB\u5B50\u8F6F\u5012\u5728\u5730\u3002</p><p>\u5F53\u524D\u4E00\u4EBA\uFF0C\u7ADF\u662F\u767D\u5929\u5FB7\u3002</p><p>\u767D\u5929\u5FB7\u51B7\u7B11\u9053\uFF0C\u300C\u771F\u662F\u91CE\u72D7\u96BE\u9A6F\uFF0C\u96BE\u4E3A\u8001\u5B50\u5904\u5904\u7684\u7EF4\u62A4\u7740\u4F60\uFF0C\u8FD8\u662F\u4E00\u5FC3\u60F3\u8DD1\u54C7\u3002\u300D</p><p>\u51B7\u5982\u971C\u4FA7\u8FC7\u8138\uFF0C\u77E5\u9053\u6B64\u52AB\u96BE\u9003\u4E86\uFF0C\u4E5F\u4E0D\u77E5\u4F1A\u5F04\u51FA\u4EC0\u4E48\u7A00\u5947\u53E4\u602A\u7684\u6CD5\u5B50\u6765\u5904\u7F5A\u5979\uFF0C\u53EA\u8981\u4E0D\u4F24\u53CA\u5B69\u5B50\uFF0C\u5979\u4E5F\u8BA4\u4E86\u3002</p><p>\u300C\u628A\u5C0F\u6742\u79CD\u7ED9\u6211\u3002\u300D\u767D\u5929\u5FB7\u4E00\u53CD\u5E38\u6001\u7684\u67D4\u548C\uFF0C\u8FD9\u8BA9\u51B7\u5982\u971C\u66F4\u52A0\u6050\u60E7\u3002</p><p>\u300C\u5594\u4E0D\uFF01\u300D\u5979\u628A\u5B69\u5B50\u62B1\u5F97\u6B7B\u6B7B\u7684\uFF0C\u6D41\u4E0B\u6CEA\u6765\u3002</p><p>\u300C\u5982\u679C\u9A6C\u4E0A\u7ED9\u6211\uFF0C\u6211\u51B3\u4E0D\u4F24\u5BB3\u8FD9\u5C0F\u6742\u79CD\u534A\u5206\uFF0C\u5426\u5219\uFF0C\u6211\u5C31\u628A\u4ED6\u6254\u8FDB\u6CB3\u91CC\u3002\u300D</p><p>\u5B69\u5B50\u8F6C\u773C\u5C31\u5230\u4E86\u767D\u5929\u5FB7\u7684\u6000\u91CC\uFF0C\u767D\u5929\u5FB7\u5C06\u4ED6\u4EA4\u7ED9\u4E86\u4E00\u4E2A\u624B\u4E0B\uFF0C\u7136\u540E\u628A\u51B7\u5982\u971C\u62C9\u8D77\u6765\uFF0C\u53CC\u624B\u6367\u4F4F\u5979\u7684\u8138\u8F7B\u8F7B\u629A\u6478\uFF0C\u300C\u4F60\u8BF4\uFF0C\u6211\u8BE5\u600E\u6837\u5904\u7F5A\u4F60\u5462\uFF0C\u6211\u771F\u662F\u5F88\u82E6\u607C\u554A\u3002\u300D</p><p>\u4ED6\u50CF\u5728\u4E0E\u5979\u5546\u91CF\uFF0C\u53C8\u50CF\u81EA\u8A00\u81EA\u8BED\uFF0C\u6839\u672C\u4E0D\u9700\u8981\u56DE\u7B54\uFF0C\u300C\u8FD9\u6837\u597D\u5417\uFF1F\u4ECE\u4ECA\u5929\u5F00\u59CB\uFF0C\u5C0F\u6742\u79CD\u6211\u7ED9\u4ED6\u8BF7\u5976\u5988\uFF0C\u6CA1\u6709\u6211\u7684\u5141\u8BB8\uFF0C\u4E0D\u8BB8\u518D\u89C1\u4ED6\u3002\u300D</p><p>\u300C\u2026\u2026\u300D</p><p>\u300C\u8FD8\u4ECE\u4ECA\u5929\u5F00\u59CB\uFF0C\u6211\u4F1A\u628A\u4F60\u5356\u5230\u5929\u9999\u9601\uFF0C\u4F60\u4E00\u5B9A\u4F1A\u6210\u4E3A\u90A3\u91CC\u7684\u5934\u724C\u5A4A\u5B50\uFF0C\u4F60\u6240\u6709\u63A5\u5BA2\u8D5A\u7684\u94B1\uFF0C\u4E00\u5206\u4E00\u5398\u90FD\u5F52\u6211\uFF0C\u7B97\u662F\u62A5\u7B54\u6211\u5BF9\u4F60\u4EEC\u6BCD\u5B50\u7684\u5BBD\u5927\u3002\u300D</p><p>\u5356\u8EAB\u4E3A\u5993\uFF01\u51B7\u5982\u971C\u5982\u6674\u5929\u9739\u96F3\uFF0C\u300C\u6211\uFF0C\u6211\u6B7B\u4E5F\u4E0D\u5E72\u3002\u300D</p><p>\u767D\u5929\u5FB7\u76EF\u7740\u5979\u7684\u773C\u775B\uFF0C\u8868\u60C5\u8F6C\u5411\u72F0\u72DE\uFF0C\u624B\u52B2\u52A0\u5927\uFF0C\u634F\u7D27\u5979\u7684\u4E0B\u5DF4\uFF0C\u300C\u56DE\u7B54\u9519\u8BEF\uFF0C\u8BF4\u971C\u5974\u5F88\u613F\u610F\u3002\u300D</p><p>\u300C\u6211\u4E0D\u613F\u610F\uFF01\u300D</p><p>\u300C\u628A\u90A3\u5C0F\u6742\u79CD\u6254\u6CB3\u91CC\uFF01\u300D</p><p>\u300C\u554A\u4E0D\uFF01\u2026\u2026\u6211\u2026\u2026\u6211\uFF0C\u613F\u610F\uFF01\u300D</p><p>\u300C\u8C01\u613F\u610F\uFF1F\u300D</p><p>\u300C\u971C\u5974\uFF0C\u5F88\u613F\u610F\u3002\u300D\u51B7\u5982\u971C\u518D\u4E5F\u96BE\u6291\u5FC3\u4E2D\u7684\u60B2\u6124\uFF0C\u6252\u5230\u6865\u5934\u5931\u58F0\u75DB\u54ED\u8D77\u6765\uFF0C\u5435\u9192\u4E86\u4E0D\u61C2\u4E8B\u7684\u8FDE\u751F\uFF0C\u4E5F\u8DDF\u7740\u54C7\u54C7\u5927\u54ED\u3002</p><p>\u6C85\u6C34\u6CB3\u9759\u9759\u6D41\u8FC7\u3002\u5979\u5E76\u4E0D\u77E5\u9053\u4E0D\u4E45\u524D\uFF0C\u91D1\u5B9D\u5C31\u60E8\u6B7B\u5728\u8FD9\u6865\u4E0A\uFF0C\u8FD8\u4EE5\u4E3A\u5979\u4EEC\u5DF2\u5B89\u8FD4\u6545\u91CC\u3002</p><p>\u82CD\u5929\u65E0\u8BED\uFF0C\u4E00\u5730\u6E05\u51B7\u7684\u6708\u5149\u3002</p>',80);function t(i,n){return r}var _=a(e,[["render",t]]);export{_ as default};
