import{e as p}from"./app.630af5f2.js";import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";const e={},r=p('<h1 id="\u7B2C\u5341\u4E09\u7AE0-\u5047\u76F8" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u5341\u4E09\u7AE0-\u5047\u76F8" aria-hidden="true">#</a> \u7B2C\u5341\u4E09\u7AE0 \u5047\u76F8</h1><p>\u51B7\u5982\u971C\u4E00\u76F4\u6CA1\u6709\u5408\u773C\uFF0C\u773C\u4E2D\u5E03\u6EE1\u4E86\u8840\u4E1D\u3002</p><p>\u65E5\u4E0A\u4E09\u7AFF\u4E86\uFF0C\u5979\u8FD8\u8EBA\u5728\u81EA\u5DF1\u7684\u7EE3\u82B1\u5E8A\u4E0A\uFF0C\u8EBA\u5728\u4E00\u4E2A\u964C\u751F\u7537\u4EBA\u7684\u6000\u91CC\u3002</p><p>\u56DB\u4E0B\u91CC\u5F88\u5B89\u9759\uFF0C\u5B89\u9759\u5F97\u80FD\u591F\u542C\u5230\u5C4B\u91CC\u897F\u6D0B\u949F\u7684\u949F\u6446\u548C\u5C4B\u5916\u536B\u5175\u6765\u56DE\u8D70\u52A8\u7684\u811A\u6B65\u58F0\u3002</p><p>\u5C31\u5728\u524D\u4E00\u65E5\uFF0C\u5C31\u5728\u540C\u4E00\u5904\uFF0C\u5979\u8FD8\u5728\u548C\u4E08\u592B\u7F20\u7EF5\uFF0C\u8F6C\u77AC\u95F4\u5929\u4EBA\u6C38\u9694\uFF0C\u800C\u5979\u5219\u5815\u5165\u4E86\u70BC\u72F1\u3002</p><p>\u300C\u6211\u8FD9\u6837\u727A\u7272\u503C\u5F97\u5417\uFF1F\u300D</p><p>\u5979\u770B\u7740\u5E8A\u9876\u7D2B\u7EA2\u7684\u7F28\u7EDC\uFF0C\u4E0D\u505C\u5730\u95EE\uFF0C\u95EE\u81EA\u5DF1\uFF0C\u95EE\u9B3C\u795E\uFF0C\u95EE\u82CD\u5929\u3002</p><p>\u6CA1\u6709\u8C01\u80FD\u591F\u56DE\u7B54\uFF0C\u53EA\u6709\u73B0\u5B9E\u6B8B\u9177\u5730\u6446\u5728\u773C\u524D\u3002</p><p>\u5979\u8D64\u88F8\u7684\u8EAB\u5B50\u5E73\u8EBA\u5728\u767D\u5929\u5FB7\u7684\u81C2\u5F2F\u5F53\u4E2D\uFF0C\u7537\u4EBA\u7684\u53E6\u4E00\u53EA\u624B\u6B63\u8D8A\u8FC7\u5979\u5706\u9686\u7684\u5C0F\u8179\uFF0C\u642D\u5728\u5979\u7684\u80EF\u95F4\uFF0C\u624B\u638C\u6B63\u5DE7\u6440\u4F4F\u4E86\u5979\u7684\u7389\u6237\u3002\u7537\u4EBA\u9F3E\u58F0\u5982\u96F7\uFF0C\u800C\u5979\u5374\u4E0D\u6562\u7A0D\u7A0D\u4FA7\u4FA7\u8EAB\u5B50\uFF0C\u6446\u8131\u8FD9\u4E2A\u6781\u4E3A\u96BE\u582A\u7684\u59FF\u5F0F\u3002</p><p>\u4E0B\u8EAB\u8FD8\u5728\u75BC\u75DB\uFF0C\u65E0\u60C5\u5730\u63D0\u9192\u7740\u5979\u6628\u591C\u66FE\u906D\u53D7\u8FC7\u4E00\u573A\u600E\u6837\u7684\u98CE\u96E8\u6467\u6B8B\u3002\u4E0A\u4E86\u836F\u818F\uFF0C\u6B62\u4F4F\u4E86\u8840\uFF0C\u4F46\u4F24\u53E3\u8FD8\u6CA1\u6709\u5B8C\u5168\u6108\u5408\u3002</p><p>\u5979\u66F4\u62C5\u5FC3\u7684\u662F\u8FD9\u6837\u66B4\u529B\u7684\u8F6E\u5978\u4F1A\u4E0D\u4F1A\u5BF9\u5979\u809A\u91CC\u7684\u5B69\u5B50\u6709\u5F71\u54CD\u3002</p><p>\u5982\u679C\u6BCF\u5929\u5982\u6B64\uFF0C\u5979\u552F\u6709\u4E00\u6B7B\u4E86\u3002</p><p>\u5B69\u5B50\u554A\u5B69\u5B50\uFF0C\u5988\u5988\u4E00\u5207\u90FD\u662F\u4E3A\u4E86\u4F60\u554A\u2026\u2026</p><p>\u7537\u4EBA\u7684\u8EAB\u4F53\u52A8\u4E86\u52A8\uFF0C\u9192\u4E86\uFF0C\u4E00\u773C\u5C31\u770B\u5230\u4E86\u6000\u4E2D\u7684\u53EF\u4EBA\u513F\uFF0C\u5982\u540C\u7B3C\u4E2D\u7684\u91D1\u4E1D\u96C0\u4E00\u822C\u745F\u7F29\u4E0D\u5B89\uFF0C\u4E0D\u7981\u7B11\u4E86\u3002</p><p>\u642D\u5728\u7389\u6237\u4E0A\u7684\u624B\u4FCF\u76AE\u5730\u5728\u90A3\u56E2\u5AE9\u8089\u4E0A\u6293\u4E86\u4E24\u4E0B\uFF0C\u5973\u4EBA\u54C6\u55E6\u4E86\u4E00\u4E0B\uFF0C\u8FD9\u624D\u53D1\u89C9\u5973\u4EBA\u539F\u672C\u5BC6\u5408\u5149\u6D01\u7684\u82B1\u7A74\u6B64\u65F6\u53D8\u5F97\u677E\u8F6F\uFF0C\u8C41\u5F00\u4E00\u9053\u53E3\u5B50\uFF0C\u610F\u8BC6\u5230\u524D\u591C\u73A9\u5F97\u6709\u4E9B\u8FC7\u706B\u4E86\u3002</p><p>\u51B7\u5982\u971C\u95ED\u4E0A\u773C\uFF0C\u7EC6\u9ED1\u7EF5\u957F\u7684\u776B\u6BDB\u8986\u5728\u82CD\u767D\u6194\u60B4\u7684\u8138\u4E0A\u5206\u5916\u60F9\u4EBA\u601C\u60DC\uFF0C\u8FDE\u51B7\u9177\u5982\u767D\u5929\u5FB7\u4E5F\u4E0D\u7981\u5FC3\u80A0\u6709\u70B9\u53D1\u8F6F\u3002</p><p>\u300C\u5B9D\u8D1D\u513F\uFF0C\u6CA1\u4F24\u7740\u5427\u3002\u300D\u8FB9\u8BF4\u8FB9\u628A\u81ED\u54C4\u54C4\u7684\u5927\u5634\u5F80\u5979\u6A31\u5507\u4E0A\u51D1\uFF0C\u60F3\u4EB2\u5979\u3002</p><p>\u51B7\u5982\u971C\u538C\u6076\u5730\u628A\u5934\u626D\u8FC7\u53BB\u3002</p><p>\u767D\u5929\u5FB7\u62FF\u773C\u4E00\u77AA\uFF0C\u6B63\u5F85\u8981\u53D1\u4F5C\uFF0C\u5FFD\u542C\u5916\u9762\u674E\u8D35\u524D\u6765\u62A5\u544A\uFF0C\u300C\u56E2\u5EA7\uFF0C\u6709\u8BAF\u606F\u4F20\u6765\uFF0C\u8BF4\u5218\u592A\u592A\u7684\u7236\u6BCD\u6B63\u5728\u6765\u6C85\u9547\u7684\u8DEF\u4E0A\uFF0C\u4F30\u8BA1\u8FD8\u6709\u534A\u65E5\u7684\u8DEF\u7A0B\u3002\u300D</p><p>\u767D\u5929\u5FB7\u8FD8\u6CA1\u6765\u5F97\u53CA\u53CD\u5E94\uFF0C\u8EAB\u4E0B\u7684\u5973\u4EBA\u5DF2\u6655\u8FC7\u53BB\u3002\u767D\u5929\u5FB7\u6025\u6390\u4EBA\u4E2D\u65B9\u624D\u60A0\u60A0\u9192\u8F6C\u3002</p><p>\u51B7\u5982\u971C\u4E0D\u8A00\u8BED\uFF0C\u767D\u5929\u5FB7\u8FD8\u662F\u4ECE\u5979\u7684\u773C\u4E2D\u770B\u5230\u4E86\u4E5E\u601C\u548C\u6050\u60E7\uFF0C\u5982\u679C\u8BA9\u7236\u6BCD\u77E5\u9053\u4E86\u5979\u975E\u4EBA\u7684\u906D\u9047\uFF0C\u90A3\u5C06\u662F\u5BF9\u8001\u4EBA\u6BC1\u706D\u6027\u7684\u6253\u51FB\uFF0C\u8FD9\u662F\u5979\u5B81\u6B7B\u4E5F\u4E0D\u613F\u770B\u5230\u7684\u3002\u800C\u8FD9\u6070\u6070\u4E5F\u662F\u767D\u5929\u5FB7\u7684\u613F\u671B\uFF0C\u4ED6\u8981\u5C3D\u529B\u5C06\u8FD9\u4E00\u8D77\u8C0B\u9006\u4E4B\u4E8B\u9690\u7792\uFF0C\u76F4\u81F3\u987A\u987A\u5229\u5229\u5750\u4E0A\u53BF\u957F\u5B9D\u5EA7\u3002</p><p>\u4ED6\u5728\u51B7\u5982\u971C\u5706\u6EDA\u6EDA\u7684\u809A\u5B50\u4E0A\u6478\u4E86\u6478\uFF0C\u4F3C\u5728\u63D0\u9192\u5979\u4E0D\u8981\u5FD8\u8BB0\u4E86\u6D3B\u4E0B\u53BB\u7684\u7406\u7531\uFF0C\u8BF4\u9053\uFF1A\u300C\u5982\u679C\u4F60\u771F\u542C\u8BDD\u7684\u8BDD\uFF0C\u767D\u67D0\u53EF\u4EE5\u52A9\u4F60\u7ED9\u8001\u4EBA\u5BB6\u6F14\u51FA\u597D\u620F\u3002\u8BA9\u8001\u4EBA\u9AD8\u9AD8\u5174\u5174\u6765\uFF0C\u9AD8\u9AD8\u5174\u5174\u8D70\u3002\u300D</p><p>\u6B64\u8A00\u679C\u771F\u51FB\u4E2D\u4E86\u51B7\u5982\u971C\u7684\u5FC3\u4E8B\uFF0C\u767D\u5929\u5FB7\u53C8\u9644\u8033\u8BF4\u4E86\u51E0\u53E5\uFF0C\u5979\u867D\u7136\u4E0D\u53EF\u80FD\u5FEB\u6D3B\u8D77\u6765\uFF0C\u81F3\u5C11\u8138\u8272\u6CA1\u6709\u90A3\u822C\u51DD\u91CD\u4E86\uFF0C\u79C0\u7709\u8212\u5C55\u4E86\u4E00\u4E9B\u3002</p><p>\u5FC3\u4E2D\u5374\u90C1\u96C6\u4E86\u4E00\u4E2A\u7ED3\uFF0C\u4E0E\u6740\u592B\u4EC7\u4EBA\u5408\u8C0B\u6B3A\u9A97\u81EA\u5DF1\u7684\u7236\u6BCD\uFF0C\u9053\u4E49\u4F55\u5728\uFF1F\u8FD9\u4E2A\u96BE\u9898\u53EA\u5728\u4E0D\u7ECF\u610F\u95F4\u5212\u8FC7\uFF0C\u5E76\u6CA1\u7559\u4E0B\u592A\u591A\u75D5\u8FF9\uFF0C\u53C8\u5728\u4E0D\u7ECF\u610F\u95F4\u5F00\u59CB\u4E00\u70B9\u70B9\u504F\u79BB\u9053\u5FB7\u7684\u8F68\u8FF9\u3002</p><p>\u767D\u5929\u5FB7\u5728\u5979\u5149\u6D01\u7684\u5C41\u80A1\u4E0A\u62CD\u4E86\u62CD\uFF0C\u5927\u5EA6\u5730\u8BF4\uFF0C\u300C\u73B0\u5728\u53BB\u628A\u8863\u88F3\u7A7F\u4E0A\u5427\u3002\u300D</p><p>\u51B7\u5982\u971C\u542C\u8BDD\u5730\u4E0B\u5E8A\uFF0C\u62C8\u8D77\u5C0F\u8863\uFF0C\u767D\u5929\u5FB7\u5374\u8BF4\uFF0C\u300C\u5185\u8863\u90FD\u522B\u7A7F\u4E86\u3002\u300D</p><p>\u51B7\u5982\u971C\u8138\u7EA2\u5230\u6839\u4E0A\uFF0C\u65E0\u5948\u4E0B\u5C06\u5B55\u88C5\u5957\u4E0A\uFF0C\u767D\u5929\u5FB7\u5ACC\u4E0D\u597D\u770B\uFF0C\u53C8\u5426\u51B3\u4E86\uFF0C\u51B7\u5982\u971C\u53EA\u5F97\u6253\u5F00\u8863\u7BB1\uFF0C\u5149\u7740\u8EAB\u5B50\u5728\u767D\u5929\u5FB7\u9762\u524D\u8868\u6F14\u8D77\u4E86\u7A7F\u8863\u79C0\uFF0C\u6709\u7684\u8863\u88F3\u592A\u5C0F\uFF0C\u7EF7\u4E0D\u4F4F\u5979\u53D1\u798F\u7684\u8EAB\u5B50\uFF0C\u6709\u7684\u5219\u4E0D\u5408\u7537\u4EBA\u7684\u53E3\u5473\uFF0C\u6700\u540E\u624D\u627E\u5230\u4E00\u4EF6\uFF0C\u662F\u5979\u5E74\u8F7B\u51E0\u5C81\u65F6\u7A7F\u8FC7\u7684\u4E00\u4EF6\u9526\u82B1\u65E0\u8896\u65D7\u888D\uFF0C\u957F\u5EA6\u53CA\u819D\uFF0C\u5927\u5C0F\u6B63\u597D\uFF0C\u6B63\u662F\u6D3B\u52A8\u65F6\u6709\u70B9\u7D27\uFF0C\u800C\u4E14\u7559\u610F\u7684\u8BDD\uFF0C\u8FD8\u4F1A\u53D1\u73B0\u4E24\u4E2A\u4E73\u5934\u5728\u8863\u9762\u4E0A\u51F8\u51FA\u4E24\u4E2A\u5C0F\u70B9\u3002</p><p>\u4E00\u756A\u52A8\u4F5C\uFF0C\u65E9\u8BA9\u767D\u5929\u5FB7\u770B\u5F97\u6B32\u706B\u5927\u70BD\uFF0C\u628A\u51B7\u5982\u971C\u53EB\u5230\u5E8A\u8FB9\uFF0C\u6307\u4E86\u6307\u81EA\u5DF1\u9AD8\u4E3E\u7684\u8089\u68D2\u3002</p><p>\u51B7\u5982\u971C\u6167\u81F3\u7075\u5FC3\uFF0C\u5C31\u7B97\u4E0E\u5218\u6EA2\u4E4B\u6CA1\u6709\u8BD5\u8FC7\u5973\u4E0A\u7537\u4E0B\u7684\u59FF\u5F0F\uFF0C\u7ECF\u8FC7\u6628\u591C\u4E00\u665A\u7684\u5F3A\u8BAD\uFF0C\u5F53\u7136\u4E5F\u660E\u767D\u4E86\u662F\u8981\u5979\u81EA\u5DF1\u5750\u4E0A\u53BB\u3002\u4F46\u6B64\u65F6\u6709\u6240\u6C42\uFF0C\u65F6\u95F4\u4E5F\u8FEB\u8FD1\u4E86\uFF0C\u987E\u4E0D\u5F97\u7F9E\u803B\uFF0C\u64A9\u5F00\u65D7\u888D\u7684\u4E0B\u6446\uFF0C\u5C06\u767D\u751F\u751F\u7684\u5927\u817F\u8DE8\u8FC7\u7537\u4EBA\u7684\u8EAB\u4F53\uFF0C\u7EA4\u624B\u6276\u4F4F\u70AE\u53E3\uFF0C\u5BF9\u7740\u81EA\u5DF1\u7684\u82B1\u7A74\u4E00\u70B9\u70B9\u5750\u4E86\u4E0B\u53BB\u3002</p><p>\u300C\u554A\u3002\u300D\u5973\u4EBA\u79C0\u7709\u8F7B\u8E59\uFF0C\u547B\u541F\u51FA\u58F0\u3002</p><p>\u8FD9\u662F\u730E\u53D6\u51B7\u5982\u971C\u4EE5\u6765\u5979\u7B2C\u4E00\u6B21\u771F\u6B63\u7684\u4E3B\u52A8\uFF0C\u767D\u5929\u5FB7\u5FC3\u4E2D\u5F97\u5230\u4E86\u65E0\u9650\u7684\u6EE1\u8DB3\u3002</p><p>\u4E34\u8D70\u4E4B\u65F6\uFF0C\u767D\u5929\u5FB7\u987A\u624B\u4ECE\u679C\u76D8\u4E2D\u62FF\u4E86\u4E09\u7C92\u5927\u9752\u67A3\u585E\u8FDB\u4E86\u5979\u7684\u4E0B\u4F53\uFF0C\u53EE\u5631\u5979\u7528\u9634\u6DB2\u6CE1\u7740\uFF0C\u4E0D\u51C6\u5F04\u51FA\u6765\u3002</p><p>\u51B7\u5982\u971C\u604D\u7136\u89C9\u5F97\u5728\u54EA\u513F\u542C\u8FC7\u7C7B\u4F3C\u7684\u8BDD\uFF0C\u56DE\u8FC7\u795E\u6765\uFF0C\u767D\u5929\u5FB7\u5DF2\u8D70\u3002</p><p>\u767D\u5929\u5FB7\u8BF4\u5230\u505A\u5230\uFF0C\u534A\u5929\u4E4B\u5185\u5C06\u5218\u5B85\u8FDB\u884C\u4E86\u7B80\u5355\u4FEE\u7F2E\uFF0C\u8868\u9762\u4E0A\u5DF2\u770B\u4E0D\u51FA\u7834\u7EFD\uFF0C\u66F4\u6362\u4E86\u4E00\u6279\u5F04\u574F\u7684\u5668\u5177\uFF0C\u51B7\u5982\u971C\u4E0D\u77E5\u9053\u539F\u6765\u7684\u5BB6\u4EBA\u4EEC\u5DF2\u5168\u88AB\u6740\uFF0C\u6B63\u5728\u62C5\u5FC3\u4EBA\u7684\u95EE\u9898\uFF0C\u767D\u5929\u5FB7\u5C06\u81EA\u5BB6\u7684\u51E0\u4E2A\u4EC6\u4EBA\u6D3E\u4E86\u6765\uFF0C\u5305\u62EC\u8B66\u536B\uFF0C\u8FD8\u6709\u4E00\u4E2A\u4F8D\u5973\u3002</p><p>\u51B7\u5982\u971C\u4E00\u89C1\u5230\u8FD9\u4E2A\u76EE\u5149\u70AF\u70AF\u7684\u5C11\u5973\u5C31\u611F\u5230\u719F\u6089\uFF0C\u5C11\u5973\u4F3C\u5BF9\u5979\u9690\u542B\u654C\u610F\uFF0C\u51B7\u51B7\u7B54\u9053\uFF0C\u300C\u6211\u53EB\u94F6\u53F6\u3002\u300D</p><p>\u300C\u6211\u60F3\u8D77\u6765\u4E86\uFF0C\u4F60\u662F\u6D77\u68E0\u8EAB\u8FB9\u7684\u4EBA\uFF0C\u6653\u5F97\u6D77\u68E0\u600E\u4E48\u6837\u4E86\uFF1F\u300D</p><p>\u300C\u6CA1\u6B7B\uFF0C\u8DD1\u4E86\u3002\u300D</p><p>\u300C\u90A3\u2026\u2026\u8FD8\u597D\uFF0C\u4F60\u8FD8\u6709\u4E2A\u540C\u80DE\u59D0\u59B9\u5427\uFF1F\u300D</p><p>\u300C\u6B7B\u4E86\u3002\u300D</p><p>\u300C\u2026\u2026\u300D</p><p>\u8BDD\u4E0D\u6295\u673A\uFF0C\u4E24\u4EBA\u76F8\u5411\u65E0\u8A00\u3002</p><p>\u4F59\u4E0B\u7684\u534A\u5929\uFF0C\u51B7\u5982\u971C\u683C\u5916\u96BE\u53D7\uFF0C\u4E0D\u4EC5\u662F\u94F6\u53F6\u548C\u90A3\u4E9B\u65B0\u5BB6\u4EBA\u66A7\u6627\u7684\u76EE\u5149\uFF0C\u8FD8\u6709\u4F53\u5185\u4E09\u7C92\u67A3\u5B50\u7684\u6298\u78E8\uFF0C\u4EE4\u5979\u5750\u7ACB\u4E0D\u5B89\u3002</p><p>\u9EC4\u660F\u65F6\u5206\uFF0C\u4E24\u8001\u7B11\u5475\u5475\u5730\u5230\u4E86\uFF0C\u4ED6\u4EEC\u8981\u53BB\u8D35\u5DDE\u770B\u671B\u5C0F\u513F\u5B50\uFF0C\u7ED5\u9053\u6C85\u9547\u770B\u770B\u5973\u513F\u5973\u5A7F\u3002</p><p>\u89C1\u5230\u4EB2\u4EBA\uFF0C\u51B7\u5982\u971C\u5C31\u6251\u5230\u6BCD\u4EB2\u6000\u91CC\uFF0C\u6CEA\u6C34\u6B62\u4E0D\u4F4F\u5730\u6D41\u51FA\u6765\u3002</p><p>\u51B7\u8001\u592A\u592A\u4EE5\u4E3A\u5979\u8FD8\u662F\u601D\u5FF5\u6240\u81F3\uFF0C\u8DDF\u7740\u62B9\u6CEA\uFF0C\u9053\uFF0C\u300C\u5929\u504F\u5730\u8FDC\u7684\uFF0C\u82E6\u4E86\u6211\u7684\u4E56\u5973\u513F\u4E86\u3002\u300D</p><p>\u8001\u7237\u5B50\u9053\uFF0C\u300C\u6EA2\u4E4B\u505A\u5927\u4E8B\u4E4B\u4EBA\uFF0C\u5C06\u6765\u5B9A\u6709\u51FA\u606F\uFF0C\u4E0D\u4F1A\u56F0\u5B88\u4E00\u9685\u7684\u3002\u300D</p><p>\u51B7\u5982\u971C\u542C\u4E86\u6B64\u8A00\u5DEE\u70B9\u5931\u53BB\u63A7\u5236\uFF0C\u7EC8\u6291\u4F4F\u4F24\u60B2\uFF0C\u5C06\u4E24\u8001\u8BA9\u81F3\u5802\u5C4B\uFF0C\u89E3\u91CA\u8BF4\u56E0\u4E3A\u9644\u8FD1\u6709\u571F\u532A\uFF0C\u6EA2\u4E4B\u53BB\u4E86\u7701\u5E9C\u6C42\u52A9\uFF0C\u81EA\u5DF1\u8EAB\u6000\u6709\u5B55\uFF0C\u5728\u5BB6\u9759\u517B\u3002</p><p>\u94F6\u53F6\u4E00\u76F4\u677F\u7740\u8138\u7AD9\u5728\u4E00\u4FA7\uFF0C\u8981\u51B7\u5982\u971C\u63D0\u9192\u51E0\u6B21\u624D\u53BB\u7EED\u8336\uFF0C\u5176\u5B83\u4E0B\u4EBA\u4E5F\u4E0D\u89C1\u8E2A\u5F71\uFF0C\u8001\u592A\u592A\u770B\u4E0A\u53BB\u5F88\u751F\u6C14\uFF0C\u6CA1\u6709\u9A6C\u4E0A\u53D1\u4F5C\u3002</p><p>\u51B7\u5982\u971C\u53EA\u6709\u6536\u62FE\u5FC3\u60C5\uFF0C\u5F3A\u989C\u6B22\u7B11\uFF0C\u5C3D\u529B\u4F5C\u4E9B\u63A9\u9970\u3002</p><p>\u8BF4\u8BDD\u95F4\uFF0C\u767D\u5929\u5FB7\u548C\u4E03\u59E8\u592A\u5230\u4E86\uFF0C\u51B7\u5982\u971C\u4ECB\u7ECD\u8FD9\u662F\u6C85\u9547\u7684\u4FDD\u5B89\u56E2\u957F\u3002\u4E03\u59E8\u592A\u63D2\u8FDB\u6765\u4E00\u53E5\uFF0C\u300C\u4E5F\u662F\u5218\u53BF\u957F\u7684\u597D\u5144\u5F1F\u554A\u3002\u300D</p><p>\u51B7\u5982\u971C\u5F3A\u7B11\u9053\uFF0C\u300C\u4E0D\u9519\uFF0C\u767D\u56E2\u957F\u662F\u6EA2\u4E4B\u7684\u2026\u2026\u597D\u5144\u5F1F\u3002\u300D\u8BB2\u7684\u662F\u5B57\u5B57\u6CE3\u8840\u3002</p><p>\u4E24\u8001\u81EA\u7136\u5F88\u70ED\u60C5\uFF0C\u767D\u5929\u5FB7\u66F4\u662F\u54C8\u54C8\u6253\u4E2A\u6CA1\u505C\u3002</p><p>\u4E03\u59E8\u592A\u6084\u6084\u626F\u4E86\u626F\u51B7\u5982\u971C\u7684\u8896\u5B50\uFF0C\u8F7B\u7B11\u9053\uFF0C\u300C\u6211\u4EEC\u59D0\u59B9\u53BB\u91CC\u5C4B\u8BF4\u8BDD\u53EF\u597D\uFF1F\u300D</p><p>\u4ECE\u4E00\u5F00\u59CB\uFF0C\u4E03\u59E8\u592A\u7684\u8138\u4E0A\u59CB\u7EC8\u6302\u7740\u7B11\u5BB9\uFF0C\u4E0E\u4EE5\u5F80\u7684\u8C26\u5351\u8BA8\u597D\u6709\u6839\u672C\u7684\u533A\u522B\uFF0C\u8FD9\u7B11\u5BB9\u91CC\u5305\u542B\u7740\u5C45\u9AD8\u51CC\u4E0B\u7684\u50B2\u6C14\u548C\u5632\u5F04\u3002</p><p>\u51B7\u5982\u971C\u9ED8\u9ED8\u5730\u8D77\u8EAB\uFF0C\u4E24\u4EBA\u8D70\u5165\u91CC\u5C4B\u3002</p><p>\u4E03\u59E8\u592A\u563B\u563B\u7B11\u9053\uFF0C\u300C\u59B9\u5B50\uFF0C\u7ED9\u59D0\u59D0\u5403\u4E2A\u67A3\u5B50\u5427\u3002\u300D</p><p>\u51B7\u5982\u971C\u8138\u8272\u53D1\u767D\uFF0C\u9053\uFF0C\u300C\u679C\u76D8\u4E2D\u591A\u7684\u662F\uFF0C\u5F85\u59B9\u59B9\u4E3A\u59D0\u59D0\u53D6\u6765\u3002\u300D</p><p>\u300C\u6211\u8981\u7684\u67A3\u5B50\u4E0A\u5E26\u7740\u5973\u4EBA\u7684\u4F53\u9999\uFF0C\u53EF\u4E0D\u540C\u4E8E\u4E00\u822C\u5594\u3002\u300D</p><p>\u300C\u59D0\u59D0\u8BF4\u7684\u662F\u4EC0\u4E48\uFF0C\u59B9\u59B9\u8FD8\u771F\u542C\u4E0D\u61C2\u3002\u300D</p><p>\u4E03\u59E8\u592A\u53D8\u8272\u9053\uFF0C\u300C\u5C11\u88C5\u80E1\u6D82\u4E86\uFF0C\u4E00\u5B9A\u8981\u6211\u5F85\u4F1A\u513F\u5F53\u7740\u8001\u7237\u5B50\u7684\u9762\u627E\u4F60\u8981\u624D\u7ED9\u5417\uFF1F\u300D</p><p>\u51B7\u5982\u971C\u642A\u585E\u4E0D\u8FC7\u53BB\uFF0C\u53EA\u5F97\u7F9E\u803B\u5730\u8BF4\uFF0C\u300C\u90A3\u8BF7\u59D0\u59D0\u80CC\u8FC7\u8138\u53BB\u3002\u300D</p><p>\u4E03\u59E8\u592A\u6076\u6BD2\u7684\u8BF4\uFF0C\u300C\u7537\u4EBA\u90FD\u770B\u538C\u7684\u4E1C\u897F\uFF0C\u8FD8\u6015\u6211\u770B\u5417\uFF1F\u300D</p><p>\u7247\u523B\u4E4B\u540E\uFF0C\u4E24\u4EBA\u624D\u4ECE\u623F\u5185\u51FA\u6765\uFF0C\u4E03\u59E8\u592A\u5728\u524D\uFF0C\u624B\u91CC\u4E3E\u7740\u4E00\u9897\u54AC\u4E86\u4E00\u5927\u53E3\u7684\u9752\u67A3\uFF0C\u7B11\u5BB9\u66A7\u6627\uFF0C\u51B2\u7740\u51B7\u8001\u7237\u5B50\u9053\uFF0C\u300C\u4F60\u5973\u513F\u8FD9\u91CC\u7684\u67A3\u5B50\u6700\u597D\u5403\uFF0C\u591A\u5403\u70B9\u3002\u300D\u51B7\u5982\u971C\u8DDF\u5728\u540E\u9762\uFF0C\u795E\u6001\u6781\u4E0D\u81EA\u7136\u3002</p><p>\u51B7\u8001\u7237\u5B50\u4E0D\u77E5\u5176\u6240\u4E91\uFF0C\u53EA\u597D\u70B9\u5934\u79F0\u662F\u3002</p><p>\u767D\u5929\u5FB7\u72E0\u72E0\u5730\u77AA\u4E86\u4E03\u59E8\u592A\u4E00\u773C\u3002</p><p>\u665A\u9910\u653E\u5728\u540E\u82B1\u56ED\u6C34\u69AD\uFF0C\u5403\u5F97\u6C89\u95F7\u65E0\u5473\uFF0C\u5404\u6000\u5FC3\u601D\uFF0C\u4E4B\u540E\uFF0C\u767D\u5929\u5FB7\u4E8C\u4EBA\u544A\u8F9E\u800C\u53BB\u3002</p><p>\u8001\u7237\u5B50\u63D0\u51FA\u51FA\u53BB\u8D70\u8D70\uFF0C\u51B7\u5982\u971C\u5BB3\u6015\u4ED6\u4EEC\u542C\u5230\u4E00\u4E9B\u4EC0\u4E48\uFF0C\u63A8\u8BF4\u592A\u70ED\uFF0C\u62D6\u4F4F\u4ED6\u4EEC\u5750\u5728\u6C34\u69AD\u91CC\u4E58\u51C9\u804A\u5929\uFF0C\u591C\u6DF1\u65B9\u6563\u3002</p><p>\u4E24\u8001\u5B89\u987F\u4E8E\u5218\u6EA2\u4E4B\u751F\u524D\u7684\u623F\u95F4\uFF0C\u5979\u81EA\u5DF1\u56DE\u95FA\u623F\u3002</p><p>\u521A\u8FDB\u95E8\u5979\u5C31\u4ECE\u80CC\u540E\u88AB\u4E00\u53CC\u624B\u73AF\u62B1\u4F4F\uFF0C\u521A\u8981\u60CA\u53EB\uFF0C\u542C\u5F97\u540E\u5934\u4E4B\u4EBA\u8A00\u9053\uFF0C\u300C\u522B\u558A\u5B9D\u8D1D\u513F\uFF0C\u662F\u6211\u5440\u3002\u300D</p><p>\u767D\u5929\u5FB7\u95EA\u8EAB\u51FA\u6765\uFF0C\u4E00\u8138\u574F\u7B11\u3002</p><p>\u51B7\u5982\u971C\u6599\u4E0D\u5230\u4ED6\u8FDE\u8FD9\u79CD\u65F6\u5019\u4E5F\u4E0D\u653E\u8FC7\u5979\uFF0C\u54C0\u6C42\u9053\uFF0C\u300C\u7B49\u6211\u7239\u5A18\u8D70\u4E86\u4E4B\u540E\u518D\u4F3A\u5019\u60A8\u597D\u4E0D\u597D\u3002\u300D</p><p>\u767D\u5929\u5FB7\u9053\uFF0C\u300C\u53EF\u4EE5\uFF0C\u53EA\u8981\u4F60\u628A\u6211\u65E9\u4E0A\u7ED9\u7684\u4E09\u7C92\u67A3\u5B50\u62FF\u51FA\u6765\u3002\u300D</p><p>\u51B7\u5982\u971C\u54D1\u53E3\u65E0\u8A00\uFF0C\u660E\u660E\u77E5\u9053\u4E03\u59E8\u592A\u5DF2\u5F04\u8D70\u4E86\u4E00\u9897\uFF0C\u53EA\u4F59\u4E0B\u4E24\u7C92\u4E86\uFF0C\u54EA\u91CC\u8FD8\u53D8\u5F97\u51FA\u539F\u6570\u6765\u3002</p><p>\u767D\u5929\u5FB7\u4E00\u628A\u5C06\u5979\u62B1\u8D77\u6765\u5F80\u5E8A\u8FB9\u8D70\uFF0C\u300C\u90A3\u5C31\u602A\u4E0D\u5F97\u8001\u5B50\u4E86\uFF0C\u6765\uFF0C\u6211\u4EEC\u5230\u5E8A\u4E0A\u53BB\u6162\u6162\u638F\u3002\u300D</p><p>\u51B7\u5982\u971C\u7684\u5E8A\u8FD8\u662F\u53E4\u5178\u5F0F\u7684\u96D5\u82B1\u6728\u5E8A\uFF0C\u5BBD\u5927\u8212\u9002\uFF0C\u6697\u9999\u6D6E\u52A8\uFF0C\u868A\u5E10\u653E\u4E0B\u6765\u5C31\u6210\u4E86\u4E00\u4E2A\u81EA\u7531\u7684\u72EC\u7ACB\u738B\u56FD\u3002\u51B7\u5982\u971C\u4FA7\u5367\u5728\u5E8A\u4E0A\uFF0C\u54AC\u7259\u5F3A\u5FCD\u7740\uFF0C\u7531\u4EFB\u767D\u5929\u5FB7\u4E00\u53EA\u624B\u5728\u5979\u7684\u4E0B\u4F53\u5185\u6405\u3002</p><p>\u5927\u534A\u65E5\u91CC\u67A3\u5B50\u5728\u5973\u4EBA\u8154\u9053\u5185\u6469\u6765\u64E6\u53BB\uFF0C\u4EFB\u662F\u77F3\u5973\u4E5F\u4F1A\u52A8\u60C5\uFF0C\u7537\u4EBA\u6478\u65F6\uFF0C\u5E95\u4E0B\u65E9\u5DF2\u6E7F\u6F09\u6F09\u7684\u6C34\u6F2B\u91D1\u5C71\u4E86\uFF0C\u8F7B\u6613\u5C31\u5C06\u4E24\u4E2A\u6307\u5934\u63D2\u4E86\u8FDB\u53BB\u3002</p><p>\u767D\u5929\u5FB7\u8C03\u4F83\u9053\uFF0C\u300C\u592A\u592A\u539F\u6765\u4E5F\u662F\u5999\u4EBA\u513F\u3002\u300D</p><p>\u51B7\u5982\u971C\u8138\u7EA2\u5230\u4E86\u8033\u6839\u5B50\u4E0A\uFF0C\u5979\u5BF9\u6027\u4E8B\u539F\u8FC7\u4E8E\u62D8\u8C28\uFF0C\u5218\u6EA2\u4E4B\u4E5F\u53EA\u662F\u5E38\u4EBA\u4E4B\u80FD\uFF0C\u4ECE\u4E0D\u77E5\u9AD8\u6F6E\u4E3A\u4F55\u7269\uFF0C\u76F4\u81F3\u6628\u591C\u5728\u6781\u5EA6\u7F9E\u8FB1\u4E4B\u4E0B\u8BA9\u8FD9\u4E9B\u4EBA\u5F3A\u8FEB\u9AD8\u6F6E\u8FBE\u6570\u6B21\u4E4B\u591A\u3002</p><p>\u800C\u5728\u767D\u5929\u5FB7\u53E3\u4E2D\uFF0C\u628A\u5979\u8BB2\u6210\u4E86\u6DEB\u8361\u4E4B\u4EBA\uFF0C\u5B9E\u4EE4\u5979\u4E0D\u582A\u4EE5\u5BF9\u3002</p><p>\u51B7\u5982\u971C\u53EA\u80FD\u8F7B\u8F7B\u6446\u52A8\u4E00\u4E0B\u5C41\u80A1\uFF0C\u4EE5\u793A\u6297\u8BAE\u3002</p><p>\u6B63\u5728\u767D\u5929\u5FB7\u8981\u638F\u51FA\u6700\u540E\u4E00\u9897\u6D78\u900F\u4E86\u5973\u4EBA\u9634\u6DB2\u7684\u9752\u67A3\u4E4B\u9645\uFF0C\u95E8\u53E3\u4F20\u6765\u94F6\u53F6\u5927\u58F0\u7684\u8BE2\u95EE\uFF0C\u300C\u8001\u592A\u592A\uFF0C\u8FD9\u4E48\u665A\u4E86\u4F60\u6709\u4E8B\u5417\uFF1F\u300D</p><p>\u8001\u592A\u592A\u8BF4\uFF0C\u300C\u6211\u627E\u5973\u513F\u8BF4\u8BF4\u8BDD\u3002\u300D</p><p>\u51B7\u5982\u971C\u82B1\u5BB9\u5931\u8272\uFF0C\u5982\u679C\u8BA9\u6BCD\u4EB2\u770B\u5230\u6709\u7537\u4EBA\u5728\u5979\u623F\u91CC\u8FD8\u4E86\u5F97\uFF1F\u800C\u767D\u5929\u5FB7\u80AF\u5B9A\u4E5F\u4E0D\u4F1A\u4E3A\u4E86\u5979\u8EB2\u8D77\u6765\u3002</p><p>\u679C\u7136\u767D\u5929\u5FB7\u9053\uFF0C\u300C\u4F60\u628A\u868A\u5E10\u653E\u4E0B\u6765\uFF0C\u8BF4\u4F60\u7761\u4E86\u3002\u300D</p><p>\u51B7\u5982\u971C\u4F9D\u8A00\u653E\u4E0B\u5E10\u5B50\uFF0C\u91CC\u5916\u76F8\u9694\u770B\u4E0D\u6E05\u695A\uFF0C\u52A0\u4E0A\u8001\u592A\u592A\u773C\u795E\u4E0D\u597D\uFF0C\u4E0D\u9738\u86EE\u6380\u5F00\u7684\u8BDD\u8BB8\u4F1A\u6DF7\u8FC7\u53BB\u5427\u3002</p><p>\u51B7\u5982\u971C\u53EA\u6709\u7948\u7977\u4E0A\u82CD\u4FDD\u4F51\u4E86\u3002</p><p>\u8001\u592A\u592A\u8FDB\u6765\u4E86\uFF0C\u4E3A\u94F6\u53F6\u7684\u963B\u62E6\u751F\u4E86\u6C14\uFF0C\u53E3\u4E2D\u5524\u9053\uFF0C\u300C\u5973\u513F\uFF0C\u4F60\u7761\u7740\u4E86\u5417\uFF1F\u300D</p><p>\u51B7\u5982\u971C\u4F5C\u51FA\u61D2\u61D2\u7684\u58F0\u97F3\uFF0C\u300C\u5988\uFF0C\u6211\u8EAB\u5B50\u91CD\uFF0C\u6709\u70B9\u75B2\u5026\u4E86\u3002\u300D</p><p>\u8001\u592A\u592A\u5750\u5230\u5E8A\u8FB9\uFF0C\u51B7\u5982\u971C\u5FC3\u90FD\u5FEB\u8E66\u5230\u55D3\u5B50\u773C\u4E86\uFF0C\u5E78\u597D\u5979\u8BF4\uFF0C\u300C\u90A3\u4F60\u8EBA\u7740\uFF0C\u5988\u9694\u7740\u5E18\u5B50\u548C\u4F60\u8BF4\u8BF4\u3002\u300D</p><p>\u767D\u5929\u5FB7\u7684\u624B\u4ECE\u5979\u7684\u65E0\u8FB9\u8896\u53E3\u6ED1\u8FDB\u53BB\uFF0C\u63E1\u4F4F\u4E86\u5979\u6CA1\u6709\u5185\u8863\u906E\u63A9\u7684\u4E73\u623F\u3002</p><p>\u51B7\u5982\u971C\u6B64\u65F6\u7684\u5904\u5883\u751A\u4E8E\u9177\u5211\uFF0C\u5916\u6709\u6BCD\u4EB2\uFF0C\u5185\u6709\u6076\u9B54\uFF0C\u81EA\u5DF1\u7684\u4E3E\u6B62\u5E94\u5BF9\u4E0D\u80FD\u6709\u4E1D\u6BEB\u95EA\u5931\uFF0C\u771F\u662F\u5D29\u6E83\u7684\u611F\u89C9\u3002</p><p>\u8001\u592A\u592A\u8FD8\u5728\u5520\u53E8\uFF0C\u300C\u5973\u513F\u554A\uFF0C\u4F60\u662F\u600E\u4E48\u7BA1\u6559\u4E0B\u4EBA\u7684\u554A\uFF0C\u6CA1\u89C4\u6CA1\u77E9\uFF0C\u54EA\u662F\u5927\u6237\u4EBA\u5BB6\u4F5C\u6D3E\uFF0C\u6EA2\u4E4B\u4E0D\u5728\u5BB6\uFF0C\u6211\u600E\u4E48\u653E\u5F97\u5FC3\u8BA9\u5979\u4EEC\u6765\u670D\u4F3A\u4F60\u3002\u300D</p><p>\u7537\u4EBA\u628A\u5979\u7684\u8033\u73E0\u542B\u5728\u5634\u91CC\u8F7B\u54AC\u7740\uFF0C\u70ED\u817E\u817E\u7684\u9F3B\u606F\u6251\u5230\u5979\u7684\u8138\u4E0A\u3002</p><p>\u300C\u4F60\u5FEB\u8981\u4E34\u76C6\u4E86\uFF0C\u51E1\u4E8B\u8981\u5C0F\u5FC3\uFF0C\u522B\u5E72\u91CD\u6D3B\uFF0C\u522B\u52A8\u4E86\u80CE\u6C14\uFF0C\u8FD9\u53EF\u4E0D\u4EC5\u662F\u5218\u5BB6\u7684\u540E\u4EE3\uFF0C\u4E5F\u662F\u6211\u51B7\u5BB6\u7684\u547D\u6839\u5B50\u554A\uFF0C\u6211\u5012\u60F3\u5E72\u8106\u7559\u4E0B\u6765\u7167\u987E\u4F60\u5750\u5B8C\u6708\u5B50\uFF0C\u53EF\u662F\u73B0\u5728\u4E0D\u884C\uFF0C\u4EE5\u540E\u518D\u8BF4\u5427\u3002\u300D</p><p>\u51B7\u5982\u971C\u8D77\u521D\u8EAB\u5B50\u4E00\u7D27\uFF0C\u542C\u5230\u540E\u9762\u53C8\u677E\u4E86\u53E3\u6C14\u3002\u7537\u4EBA\u8D8A\u53D1\u7316\u72C2\u4E86\uFF0C\u5F00\u59CB\u626F\u7740\u5979\u65D7\u888D\u7684\u4E0B\u6446\u5F80\u4E0A\u63D0\uFF0C\u767D\u7699\u7684\u5927\u817F\u5DF2\u9732\u4E86\u4E00\u5927\u622A\uFF0C\u8FD8\u4E0D\u7F62\u4F11\uFF0C\u8981\u5C06\u5979\u6574\u4E2A\u4E0B\u8EAB\u90FD\u88F8\u51FA\u6765\u3002\u51B7\u5982\u971C\u4E0D\u6562\u8A00\u8BED\uFF0C\u4E5F\u4E0D\u6562\u516C\u7136\u53CD\u6297\uFF0C\u53EA\u6709\u7528\u529B\u628A\u8EAB\u5B50\u5F80\u4E0B\u538B\uFF0C\u7ED9\u4ED6\u5C3D\u53EF\u80FD\u5730\u8BBE\u7F6E\u4E00\u70B9\u963B\u529B\u3002</p><p>\u8001\u592A\u592A\u7EED\u9053\uFF0C\u300C\u6211\u548C\u4F60\u7236\u4EB2\u521A\u624D\u8FD8\u5728\u8BB2\uFF0C\u770B\u90A3\u767D\u4EC0\u4E48\u56E2\u957F\u90A3\u4E24\u53E3\u5B50\u4E0D\u50CF\u662F\u597D\u4EBA\uFF0C\u7709\u773C\u95F4\u6709\u4E9B\u72E1\u8BC8\u2026\u2026\u300D</p><p>\u7537\u4EBA\u8D8A\u53D1\u653E\u8086\uFF0C\u5DF2\u7ECF\u5C06\u5979\u5149\u6D01\u6ED1\u817B\u7684\u5927\u817F\u63B0\u5F00\uFF0C\u624B\u6307\u4ECE\u5979\u7684\u9634\u6237\u91CC\u638F\u51FA\u4E9B\u6C41\u6C34\u5F80\u5979\u83CA\u809B\u4E0A\u62B9\u3002</p><p>\u300C\u4F60\u8981\u63D0\u9192\u6EA2\u4E4B\uFF0C\u4E0D\u8981\u8F7B\u4FE1\u4EBA\uFF0C\u9632\u4EBA\u4E4B\u5FC3\u4E0D\u53EF\u65E0\u554A\u3002\u300D</p><p>\u51B7\u5982\u971C\u8BA9\u767D\u5929\u5FB7\u5F04\u5F97\u8BF4\u4E0D\u51FA\u7684\u9EBB\u75D2\u96BE\u53D7\uFF0C\u66F4\u96BE\u53D7\u7684\u662F\u6BCD\u4EB2\u7684\u8BDD\uFF0C\u5FCD\u7740\u6CEA\u9053\uFF0C\u300C\u5973\u513F\u90FD\u8BB0\u4E0B\u4E86\u3002\u300D</p><p>\u7537\u4EBA\u63E1\u7740\u5979\u5976\u5B50\u7684\u624B\u7A81\u7136\u7528\u529B\u634F\u4E86\u4E00\u4E0B\uFF0C\u51B7\u5982\u971C\u731D\u4E0D\u53CA\u9632\uFF0C\u7981\u4E0D\u4F4F\u8F7B\u547C\u4E86\u4E00\u58F0\uFF0C\u8001\u592A\u592A\u542C\u89C1\u4E86\uFF0C\u5FD9\u9053\uFF0C\u300C\u4F60\u4E0D\u788D\u5427\uFF0C\u6211\u770B\u770B\u3002\u300D</p><p>\u6BCD\u4EB2\u4F38\u51FA\u624B\u6765\uFF0C\u5F71\u5B50\u6620\u5728\u868A\u5E10\u4E0A\u3002\u90A3\u4E00\u77AC\u95F4\uFF0C\u51B7\u5982\u971C\u5DEE\u70B9\u6025\u75AF\u3002</p><p>\u8FD9\u771F\u662F\u4E00\u5E45\u8BF4\u4E0D\u51FA\u540A\u8BE1\u7684\u753B\u9762\uFF0C\u5E8A\u8FB9\uFF0C\u5E74\u8FC8\u7684\u6BCD\u4EB2\u6B63\u895F\u5371\u5750\uFF0C\u7D6E\u7D6E\u53E8\u53E8\uFF0C\u5E8A\u5185\uFF0C\u4EE5\u6E05\u9AD8\u8D1E\u6D01\u7740\u79F0\u7684\u51B7\u5982\u971C\u5374\u6B64\u65F6\u6BD4\u5993\u5973\u8FD8\u6DEB\u8D31\uFF0C\u9165\u80F8\u534A\u9732\uFF0C\u4E0B\u534A\u622A\u8EAB\u5B50\u5E72\u8106\u5168\u88F8\uFF0C\u767D\u82B1\u82B1\u7684\u5927\u817F\u53C9\u5F00\u642D\u5728\u4E00\u4E2A\u7537\u4EBA\u8EAB\u4E0A\uFF0C\u7537\u4EBA\u4E00\u624B\u63E1\u7740\u5979\u7684\u4E00\u53EA\u5976\u5B50\uFF0C\u53E6\u4E00\u624B\u6349\u4F4F\u5979\u7684\u9634\u6237\u8086\u610F\u628A\u73A9\uFF0C\u800C\u8FD9\u622A\u7136\u4E0D\u540C\u7684\u4E16\u754C\u76F8\u9694\u4EC5\u53EA\u6709\u4E00\u5C42\u8584\u8584\u7684\u868A\u5E10\u3002</p><p>\u4F46\u6B64\u523B\uFF0C\u8FDE\u8FD9\u5C42\u8584\u5E10\u90FD\u8981\u6380\u5F00\u4E86\u3002</p><p>\u8FD9\u4E00\u63ED\uFF0C\u53EF\u80FD\u5C31\u662F\u51E0\u6761\u4EBA\u547D\u3002</p><p>\u51B7\u5982\u971C\u7684\u8111\u4E2D\u4E00\u7247\u7A7A\u767D\uFF0C\u6839\u672C\u60F3\u8C61\u4E0D\u51FA\u540E\u679C\uFF0C\u60C5\u6025\u4E4B\u4E0B\u53EA\u6709\u4E0B\u610F\u8BC6\u8BF4\u9053\uFF1A\u300C\u5988\uFF0C\u60A8\u4F11\u606F\u53BB\u5427\uFF0C\u5B69\u5B50\u5728\u8E22\u6211\u5462\uFF0C\u4E0D\u788D\u4E8B\u3002\u300D</p><p>\u8001\u592A\u592A\u8FDF\u7591\u4E86\u4E00\u4F1A\uFF0C\u624B\u6162\u6162\u6536\u4E86\u56DE\u53BB\uFF0C\u9053\uFF0C\u300C\u4F60\u4E5F\u4E0D\u5C0F\u4E86\uFF0C\u8981\u5B66\u4F1A\u81EA\u5DF1\u7167\u987E\u81EA\u5DF1\u3002\u77A7\u6211\u8001\u4E86\uFF0C\u4E00\u8BF4\u8D77\u6765\u5C31\u6CA1\u4E2A\u5B8C\uFF0C\u665A\u4E86\uFF0C\u7761\u5427\uFF0C\u660E\u65E9\u6211\u548C\u4F60\u7236\u4EB2\u5C31\u8D70\u4E86\u3002\u300D</p><p>\u51B7\u5982\u971C\u65B9\u624D\u89C9\u5F97\u989D\u5934\u51B0\u51C9\uFF0C\u51B7\u6C57\u6CE0\u6CE0\uFF0C\u300C\u5988\u60A8\u597D\u8D70\uFF0C\u6211\u8981\u94F6\u53F6\u9001\u4E00\u4E0B\u3002\u300D</p><p>\u300C\u4E0D\u7528\u4E86\uFF0C\u5979\u53EF\u6BD4\u5343\u91D1\u5C0F\u59D0\u7684\u67B6\u5B50\u5927\u591A\u5566\u3002\u300D</p><p>\u623F\u95E8\u91CD\u65B0\u63A9\u4E0A\u4E86\uFF0C\u767D\u5929\u5FB7\u563B\u563B\u7B11\u9053\uFF0C\u300C\u60F3\u4E0D\u5230\u4F60\u5BB6\u8001\u592A\u592A\u7684\u773C\u5149\u6BD4\u4F60\u8001\u516C\u8FD8\u51C6\u3002\u300D</p><p>\u51B7\u5982\u971C\u9ED8\u7136\u4E0D\u8BED\uFF0C\u53C8\u7F9E\u53C8\u6068\uFF0C\u5DEE\u70B9\u4EB2\u624B\u846C\u9001\u4E86\u6BCD\u4EB2\u7684\u6027\u547D\uFF0C\u800C\u8FD9\u4E00\u5207\u90FD\u662F\u8EAB\u540E\u8FD9\u6076\u9B54\u9020\u5C31\u7684\uFF0C\u771F\u662F\u6B32\u54ED\u65E0\u6CEA\u3002</p><p>\u767D\u5929\u5FB7\u50CF\u662F\u4E0D\u660E\u767D\u51B7\u5982\u971C\u521A\u624D\u5728\u751F\u6B7B\u5173\u4E0A\u8F6C\u4E86\u4E00\u5708\uFF0C\u4E24\u624B\u5C06\u5979\u96EA\u767D\u7684\u81C0\u8089\u7FFB\u5F00\uFF0C\u9053\uFF0C\u300C\u5218\u6EA2\u4E4B\u89C1\u4E86\u4F60\u524D\u9762\u7684\u7EA2\uFF0C\u8001\u5B50\u4ECA\u5929\u8981\u89C1\u89C1\u4F60\u540E\u9762\u7684\u7EA2\u3002\u300D</p><p>\u51B7\u5982\u971C\u8D77\u5148\u641E\u4E0D\u6E05\u4ED6\u5728\u8BF4\u4EC0\u4E48\uFF0C\u5F85\u5230\u4E00\u6761\u8F6F\u4E4E\u4E4E\u6E29\u6E7F\u7684\u5927\u820C\u5934\u8214\u5230\u4E86\u5979\u7684\u83CA\u95E8\u4E0A\u624D\u6709\u4E9B\u660E\u767D\u8FC7\u6765\uFF0C\u51B3\u6599\u4E0D\u5230\u4ED6\u5BF9\u6392\u6CC4\u80AE\u810F\u4E4B\u5904\u611F\u5174\u8DA3\uFF0C\u5927\u60CA\u5931\u8272\uFF0C\u4E0D\u7531\u5F97\u5C06\u8EAB\u5B50\u626D\u52A8\u8D77\u6765\u3002</p><p>\u767D\u5929\u5FB7\u5A01\u80C1\u9053\uFF0C\u300C\u8001\u592A\u592A\u521A\u8D70\u6CA1\u591A\u8FDC\uFF0C\u4ED6\u4EEC\u5C31\u4F4F\u5728\u9644\u8FD1\uFF0C\u62DB\u6765\u4E86\u8001\u5B50\u53EF\u4E0D\u8D1F\u8D23\u3002\u300D</p><p>\u51B7\u5982\u971C\u679C\u7136\u542C\u8BDD\u591A\u4E86\uFF0C\u552F\u5FC3\u4E2D\u66F4\u662F\u82E6\u75DB\u3002</p><p>\u73A9\u8FC7\u90A3\u4E48\u591A\u7684\u5973\u4EBA\uFF0C\u767D\u5929\u5FB7\u4E5F\u8FD8\u662F\u7B2C\u4E00\u6B21\u8214\u5973\u4EBA\u7684\u5C41\u773C\uFF0C\u5ACC\u810F\uFF0C\u4F46\u51B7\u5982\u971C\u7684\u8EAB\u5B50\u6240\u6709\u7EC6\u8282\u90FD\u663E\u5F97\u90A3\u4E48\u5E72\u51C0\uFF0C\u8FD8\u6D6E\u52A8\u7740\u4E00\u5C42\u82E5\u6709\u82E5\u65E0\u7684\u6697\u9999\uFF0C\u683C\u5916\u8C03\u52A8\u4ED6\u7684\u6027\u8DA3\uFF0C\u5FCD\u4E0D\u4F4F\u5728\u5979\u8EAB\u5B50\u4E0A\u751A\u81F3\u662F\u9690\u79C1\u5904\u90FD\u8214\u5F04\u4E86\u4E00\u4F1A\uFF0C\u5482\u820C\u9053\uFF0C\u300C\u771F\u662F\u597D\u5473\u54E9\u3002\u300D</p><p>\u65B9\u624D\u5C06\u70AE\u53E3\u67B6\u4E0A\uFF0C\u62DF\u76F4\u5165\u6B63\u9898\u3002</p><p>\u5979\u7684\u83CA\u82B1\u95E8\u5B9E\u5728\u5C0F\u5DE7\uFF0C\u5C11\u5C11\u7684\u76B1\u7EB9\u4E5F\u7EC6\u5BC6\u5F97\u5F88\uFF0C\u5C31\u7B97\u767D\u5929\u5FB7\u521A\u624D\u73A9\u5F04\u4E86\u90A3\u4E48\u4E45\u4E5F\u4E0D\u89C1\u5176\u6E7F\u6DA6\u3002</p><p>\u8089\u68D2\u5728\u4E0A\u9762\u6233\u4E86\u597D\u4E00\u4F1A\uFF0C\u83CA\u95E8\u53CD\u800C\u8D8A\u6233\u8D8A\u7D27\uFF0C\u8D8A\u6536\u8D8A\u5C0F\uFF0C\u603B\u4E0D\u5F97\u5176\u95E8\u800C\u5165\uFF0C\u4E0D\u7531\u5F97\u6709\u70B9\u7126\u71E5\u8D77\u6765\uFF0C\u4E3E\u624B\u5728\u5979\u7684\u5C41\u80A1\u4E0A\u51FB\u4E86\u4E00\u638C\uFF0C\u9053\uFF0C\u300C\u653E\u677E\u4E00\u70B9\uFF0C\u8001\u5B50\u53C8\u4E0D\u662F\u5728\u6740\u732A\u3002\u300D</p><p>\u51B7\u5982\u971C\u53EA\u5F97\u5C3D\u529B\u653E\u8F6F\u4E86\u8EAB\u5B50\uFF0C\u767D\u5929\u5FB7\u62FF\u4E2D\u6307\u8BD5\u4E86\u8BD5\uFF0C\u786E\u5B9E\u592A\u5E72\uFF0C\u4E00\u6839\u624B\u6307\u90FD\u6709\u70B9\u4E3A\u96BE\uFF0C\u4FBF\u53EB\u9053\uFF0C\u300C\u94F6\u53F6\uFF0C\u62FF\u70B9\u706F\u6CB9\u6765\u3002\u300D</p><p>\u94F6\u53F6\u7AEF\u7740\u706F\u6CB9\u63A8\u95E8\u800C\u5165\uFF0C\u770B\u5230\u4E86\u4E24\u4EBA\u7684\u60C5\u5F62\u4FBF\u660E\u767D\u4E86\uFF0C\u5979\u4E0E\u51B7\u5982\u971C\u7684\u76EE\u5149\u63A5\u89E6\uFF0C\u6F20\u65E0\u8868\u60C5\u3002</p><p>\u767D\u5929\u5FB7\u9053\uFF0C\u300C\u4F60\u6765\u628A\u706F\u6CB9\u62B9\u5230\u5979\u7684\u5C41\u773C\u4E0A\uFF0C\u5988\u7684\uFF0C\u8001\u5B50\u5C31\u4E0D\u4FE1\u5F04\u4E0D\u8FDB\u53BB\u3002\u300D</p><p>\u5728\u7537\u4EBA\u5A01\u903C\u4E0B\uFF0C\u51B7\u5982\u971C\u6728\u7136\u5730\u8F6C\u8FC7\u8EAB\uFF0C\u7AD9\u5728\u5E8A\u6CBF\uFF0C\u4E0A\u8EAB\u8DB4\u5728\u5E8A\u4E0A\uFF0C\u5C41\u80A1\u4E3E\u8D77\u6765\u51B2\u5411\u94F6\u53F6\uFF0C\u8863\u88F3\u8FD8\u7A7F\u5728\u8EAB\u4E0A\uFF0C\u5374\u662F\u9AD8\u9AD8\u7684\u7FFB\u5728\u8170\u95F4\uFF0C\u6574\u4E2A\u4E0B\u534A\u8EAB\u6CDB\u51FA\u8089\u6B32\u7684\u5149\u6CFD\u3002\u94F6\u53F6\u7684\u624B\u6307\u7EC6\u5C16\uFF0C\u5C06\u706F\u6CB9\u6311\u8D77\uFF0C\u4E00\u4E1D\u4E0D\u82DF\u5730\u4E00\u70B9\u70B9\u62B9\u8FDB\u5979\u7684\u809B\u95E8\u548C\u5927\u80A0\u58C1\u3002</p><p>\u51B7\u5982\u971C\u89C9\u5F97\u5C41\u773C\u91CC\u6ED1\u817B\u817B\u7684\uFF0C\u8BF4\u4E0D\u51FA\u7684\u6076\u5FC3\u3002</p><p>\u94F6\u53F6\u5C06\u4E24\u6839\u624B\u6307\u5E76\u62E2\u8BD5\u7740\u63D2\u4E86\u63D2\uFF0C\u5F88\u987A\u5229\u5C31\u541E\u8FDB\u53BB\u4E86\u4E00\u4E2A\u6307\u8282\u3002</p><p>\u767D\u5929\u5FB7\u6478\u6478\u5979\u7684\u5934\uFF0C\u4EE5\u793A\u8912\u5956\uFF0C\u8FD9\u624D\u8D64\u811A\u4E0B\u5F97\u5E8A\u6765\uFF0C\u7AD9\u5728\u51B7\u5982\u971C\u8EAB\u540E\uFF0C\u4EE4\u5979\u81EA\u5DF1\u628A\u5C41\u80A1\u63B0\u5F00\uFF0C\u518D\u6B21\u5C06\u4E11\u964B\u7684\u9633\u7269\u9876\u4F4F\u4E86\u90A3\u4E2A\u72ED\u5C0F\u7684\u53E3\u5B50\uFF0C\u5FAE\u4E00\u8FD0\u529B\uFF0C\u501F\u52A9\u706F\u6CB9\u7684\u6ED1\u6DA6\uFF0C\u5927\u5934\u679C\u771F\u4E00\u70B9\u4E00\u70B9\u5730\u6324\u4E86\u8FDB\u53BB\u3002</p><p>\u867D\u7136\u4E00\u70B9\u58F0\u606F\u90FD\u6CA1\u6709\uFF0C\u5728\u51B7\u5982\u971C\u7684\u611F\u89C9\u4E2D\u5374\u662F\u5C71\u5D29\u5730\u88C2\uFF0C\u5C31\u50CF\u8EAB\u5B50\u5728\u4E00\u70B9\u4E00\u70B9\u5728\u5288\u5F00\u6210\u4E24\u534A\u3002</p><p>\u8089\u68D2\u8FD8\u5728\u633A\u8FDB\uFF0C\u809B\u53E3\u7684\u4E00\u5708\u5AE9\u8089\u54AC\u5F97\u6B7B\u6B7B\u7684\uFF0C\u968F\u540C\u8089\u68D2\u4E00\u8D77\u7FFB\u4E86\u8FDB\u53BB\u3002\u8D8A\u5F80\u524D\u8D8A\u884C\u8FDB\u4E0D\u52A8\uFF0C\u809B\u6D1E\u5DF2\u6DA8\u5F00\u81F3\u6781\u9650\u3002</p><p>\u51B7\u5982\u971C\u53E3\u4E2D\u7D27\u7D27\u54AC\u7740\u9526\u88AB\u7684\u4E00\u89D2\uFF0C\u82E6\u5FCD\u7740\u65B9\u4E0D\u80FD\u54ED\u51FA\u58F0\u6765\u3002</p><p>\u767D\u5929\u5FB7\u505C\u4E0B\u6765\u5598\u4E86\u53E3\u6C14\uFF0C\u94F6\u53F6\u61C2\u4E8B\u5730\u7ED9\u4ED6\u62B9\u62B9\u80CC\u4E0A\u7684\u6C57\u3002\u8089\u68D2\u9000\u56DE\u5C11\u8BB8\uFF0C\u53C8\u9000\u56DE\u5C11\u8BB8\uFF0C\u5728\u5973\u4EBA\u4EE5\u4E3A\u7ED3\u675F\u4E86\u6709\u6240\u653E\u677E\u4E4B\u9645\uFF0C\u7A81\u7136\u8FD0\u529B\u5411\u524D\u731B\u8FDB\uFF0C\u5FAE\u5FAE\u7684\u300C\u6251\u300D</p><p>\u4E00\u58F0\uFF0C\u6574\u6839\u57CB\u5165\u96EA\u767D\u7684\u81C0\u8089\u4E4B\u4E2D\uFF0C\u809B\u6D1E\u6491\u7206\u4E86\uFF0C\u67D3\u4E0A\u4E00\u7247\u8273\u4E3D\u7684\u7EA2\u3002</p><p>\u4E0E\u6B64\u540C\u65F6\uFF0C\u51B7\u5982\u971C\u5982\u906D\u91CD\u51FB\uFF0C\u5589\u5934\u4E00\u751C\uFF0C\u6655\u6B7B\u8FC7\u53BB\u3002</p><p>\u4F8D\u7ACB\u5728\u8EAB\u540E\u7684\u94F6\u53F6\u7ADF\u5FAE\u5FAE\u5730\u4E00\u7B11\uFF0C\u773C\u4E2D\u6CA1\u6709\u534A\u5206\u540C\u60C5\uFF0C\u6EE1\u84C4\u7684\u662F\u5E78\u707E\u4E50\u7978\u4E4B\u8272\u3002</p>',132);function t(i,n){return r}var _=a(e,[["render",t]]);export{_ as default};
