import{e as p}from"./app.630af5f2.js";import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";const e={},r=p('<h1 id="\u7B2C\u5341\u4E94\u7AE0-\u6551\u5175" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u5341\u4E94\u7AE0-\u6551\u5175" aria-hidden="true">#</a> \u7B2C\u5341\u4E94\u7AE0 \u6551\u5175</h1><p>\u6691\u6C14\u6536\u5C3D\u4E4B\u65F6\uFF0C\u6B7B\u6C89\u6C89\u7684\u6C85\u9547\u8857\u5934\u53C8\u70ED\u95F9\u4E86\u8D77\u6765\uFF0C\u4EBA\u4EEC\u5FD9\u7740\u91C7\u529E\u8D27\u7269\uFF0C\u4E3A\u5373\u5C06\u5230\u6765\u7684\u65B0\u5E74\u4F5C\u51C6\u5907\uFF0C\u4E5F\u5728\u5FD9\u7740\u8BAE\u8BBA\u65B0\u8FD1\u53D1\u751F\u7684\u51E0\u4EF6\u5927\u4E8B\u3002</p><p>\u8FD9\u51E0\u4EF6\u4E8B\u90FD\u4E0E\u767D\u5929\u5FB7\u6709\u5173\u3002\u5148\u662F\u4ED6\u7684\u53BF\u592A\u7237\u7684\u59D4\u4EFB\u72B6\u4E0B\u6765\u4E86\uFF0C\u517C\u4EFB\u4FDD\u5B89\u56E2\u56E2\u957F\uFF0C\u53EA\u662F\u628A\u4E00\u8EAB\u620E\u88C5\u6362\u6210\u4E86\u7EF8\u7F0E\u957F\u888D\uFF0C\u897F\u670D\u88E4\uFF0C\u5934\u9876\u56ED\u5F62\u793C\u5E3D\uFF0C\u8DB3\u4E0A\u4E00\u53CC\u4E4C\u9ED1\u53D1\u4EAE\u7684\u725B\u76AE\u978B\uFF0C\u5E73\u6DFB\u4E86\u51E0\u5206\u5112\u96C5\u6C14\u8D28\uFF0C\u4E50\u5475\u5475\u5730\u5728\u5929\u9999\u697C\u5927\u5BB4\u5BBE\u5BA2\u3002</p><p>\u63A5\u4E0B\u6765\u662F\u767D\u5929\u5FB7\u5927\u5A5A\uFF0C\u6B63\u5F0F\u8FCE\u5A36\u6B7B\u9B3C\u5EB7\u8001\u7237\u5B50\u7684\u4E03\u59E8\u592A\u53F2\u51DD\u5170\uFF0C\u65B0\u623F\u8BBE\u5728\u4E86\u539F\u6765\u51B7\u6E05\u4E86\u5F88\u4E45\u7684\u5218\u6EA2\u4E4B\u7684\u5E9C\u5E95\uFF0C\u53EA\u662F\u628A\u9EC4\u6A80\u6728\u7684\u300C\u5218\u5B85\u300D\u6362\u6210\u4E86\u70EB\u5E95\u91D1\u5B57\u5916\u52A0\u62AB\u7EA2\u6302\u5F69\u7684\u300C\u767D\u5E9C\u300D\uFF0C\u53C8\u662F\u5439\u9523\u6253\u9F13\u70ED\u95F9\u4E86\u4E00\u5411\u3002</p><p>\u53EA\u662F\u539F\u6765\u7684\u5973\u4E3B\u4EBA\uFF0C\u5218\u53BF\u957F\u7684\u672A\u4EA1\u4EBA\u51B7\u5982\u971C\uFF0C\u5DF2\u7136\u5728\u4EBA\u4EEC\u7684\u89C6\u7EBF\u4E2D\u6D88\u901D\u5F88\u4E45\u4E86\uFF0C\u4F46\u603B\u6709\u4EBA\u8A00\u4E4B\u51FF\u51FF\u5730\u8BF4\u770B\u5230\u8FC7\u5979\uFF0C\u8FD8\u5728\u6C85\u9547\uFF0C(\u8D3C\u5427\uFF3Aei\uFF18.\uFF23\uFF2F\uFF2D\u7535\u5B50\u4E66)\u505A\u4E86\u767D\u53BF\u957F\u7684\u5730\u4E0B\u59E8\u592A\u592A\uFF0C\u8FD8\u6000\u4E0A\u4E86\u6BDB\u6BDB\u3002\u542C\u8005\u65E0\u4E0D\u9050\u60F3\u8FDE\u7FE9\uFF0C\u56DE\u9996\u8D77\u66FE\u7ECF\u9999\u8273\u7684\u5F80\u4E8B\u6765\uFF0C\u5148\u662F\u6447\u53E3\uFF0C\u7EE7\u800C\u611F\u53F9\uFF0C\u5410\u53E3\u53E3\u6C34\u9053\uFF0C\u300C\u53EF\u89C1\u5F97\u662F\u4E2A\u8D31\u4EBA\u3002\u300D</p><p>\u51B7\u5982\u971C\u53EF\u5E78\u6CA1\u542C\u5230\u8FD9\u4E9B\u810F\u8BDD\uFF0C\u5374\u5728\u6BD4\u810F\u8BDD\u8FD8\u5C48\u8FB1\u4E07\u5206\u7684\u5883\u5730\u4E2D\u751F\u6D3B\u3002</p><p>\u5979\u968F\u7740\u767D\u5929\u5FB7\u592B\u5987\u642C\u56DE\u4E86\u8001\u5B85\uFF0C\u719F\u6089\u7684\u4E00\u8349\u4E00\u6728\uFF0C\u4E00\u4EAD\u4E00\u697C\u66FE\u7ECF\u5E26\u7ED9\u5979\u591A\u5C11\u6B22\u4E50\u548C\u5C0A\u8363\uFF0C\u73B0\u5728\u5C31\u5E26\u7ED9\u5979\u500D\u8BA1\u7684\u75DB\u82E6\u3002\u5979\u4E3B\u8981\u662F\u4F3A\u5019\u9E20\u5360\u9E4A\u5DE2\u7684\u4E03\u59E8\u592A\uFF0C\u8FD8\u5F97\u5411\u767D\u5929\u5FB7\u4F8D\u5949\u51FA\u81EA\u5DF1\u7EAF\u6D01\u7684\u8089\u4F53\uFF0C\u53CC\u91CD\u7684\u6298\u78E8\u538B\u8FEB\u5F97\u5979\u5598\u4E0D\u8FC7\u6C14\u6765\uFF0C\u5982\u679C\u4E0D\u662F\u8179\u4E2D\u7684\u5B69\u5B50\uFF0C\u5979\u4E5F\u8BB8\u65E9\u5C31\u5D29\u6E83\u4E86\u3002</p><p>\u5C31\u5728\u8FD9\u714E\u71AC\u4E2D\uFF0C\u5B69\u5B50\u8BDE\u4E0B\u6765\u4E86\uFF0C\u662F\u4E2A\u7537\u5B69\u3002\u51B7\u5982\u971C\u65E9\u5C31\u53D6\u597D\u4E86\u540D\uFF0C\u5218\u8FDE\u751F\uFF0C\u300C\u601C\u751F\u300D\uFF0C\u53EF\u601C\u4F60\u771F\u4E0D\u8BE5\u751F\u5230\u8FD9\u82E6\u96BE\u7684\u4E16\u754C\u4E2D\u6765\u3002</p><p>\u65E5\u5B50\u7684\u6D41\u901D\u603B\u4E0D\u4EE5\u4EBA\u7684\u610F\u5FD7\u4E3A\u8F6C\u79FB\uFF0C\u5317\u98CE\u521D\u8D77\u7684\u65F6\u5019\uFF0C\u8FDE\u751F\u6EE1\u6708\u4E86\uFF0C\u8D8A\u957F\u8D8A\u50CF\u4ED6\u6B7B\u53BB\u7684\u7236\u4EB2\uFF0C\u6402\u7740\u8FD9\u4E2A\u82E6\u547D\u7684\u5B69\u5B50\uFF0C\u51B7\u5982\u971C\u603B\u662F\u5FCD\u4E0D\u4F4F\u5782\u6CEA\uFF0C\u5C06\u4E73\u5934\u585E\u5230\u5B69\u5B50\u7684\u5C0F\u5634\u91CC\uFF0C\u671B\u7740\u4ED6\u9999\u751C\u7684\u5403\u76F8\uFF0C\u672C\u5DF2\u51B7\u81F3\u51B0\u70B9\u7684\u5FC3\u6709\u4E86\u4E00\u4E1D\u4E1D\u6696\u610F\uFF0C\u4E00\u4E1D\u4E1D\u671F\u5F85\u3002</p><p>\u767D\u5929\u5FB7\u631F\u7740\u4E00\u80A1\u51B7\u98CE\u95EF\u4E86\u8FDB\u6765\uFF0C\u53E3\u4E2D\u9A82\u9A82\u54A7\u54A7\uFF0C\u300C\u5988\u7684\uFF0C\u5929\u6C14\u53D8\u5F97\u771F\u5FEB\uFF0C\u6765\u7897\u70ED\u7684\u3002\u54A6\u2026\u2026\u4F60\u4ED6\u5988\u5728\u5E72\u4E48\u5B50\uFF1F\u300D</p><p>\u51B7\u5982\u971C\u5413\u5F97\u4E00\u54C6\u55E6\uFF0C\u5DEE\u70B9\u628A\u8FDE\u751F\u6454\u5230\u5730\u4E0A\uFF0C\u8D76\u7D27\u5C31\u52BF\u8DEA\u4E86\u4E0B\u6765\uFF0C\u8FDE\u751F\u5413\u5F97\u54C7\u54C7\u5927\u54ED\u3002</p><p>\u767D\u5929\u5FB7\u72F0\u72DE\u7740\u9053\uFF0C\u300C\u5A4A\u5B50\uFF0C\u8001\u5B50\u5BF9\u4F60\u597D\u4E00\u70B9\u786C\u662F\u4E0D\u884C\uFF0C\u7D22\u6027\u5C06\u8FD9\u5C0F\u6742\u79CD\u6254\u51FA\u53BB\u5582\u72D7\u3002\u300D</p><p>\u51B7\u5982\u971C\u8138\u8272\u82CD\u767D\uFF0C\u8D8A\u53D1\u628A\u5B69\u5B50\u62B1\u5F97\u6B7B\u6B7B\u7684\uFF0C\u5934\u53E9\u5230\u4E86\u5730\u4E0A\uFF0C\u300C\u5BF9\u4E0D\u8D77\u8001\u7237\uFF0C\u971C\u5974\u77E5\u9519\u4E86\uFF0C\u971C\u5974\u4E00\u5B9A\u6539\u3002\u300D</p><p>\u539F\u6765\u51B7\u5982\u971C\u751F\u80B2\u540E\uFF0C\u767D\u5929\u5FB7\u5FFD\u7136\u8FF7\u604B\u4E0A\u4E86\u6BCD\u4E73\uFF0C\u81F3\u5C11\u6BCF\u65E5\u6E05\u6668\u90FD\u8981\u559D\u4E0A\u4E00\u7897\u70ED\u4E4E\u4E4E\u7684\u65B0\u9C9C\u4EBA\u5976\uFF0C\u5E73\u65F6\u5219\u968F\u5174\u8DA3\u6765\uFF0C\u8FD8\u6307\u5B9A\u975E\u51B7\u5982\u971C\u7684\u4E0D\u884C\u3002\u504F\u751F\u51B7\u5982\u971C\u4E73\u623F\u5C0F\u5DE7\uFF0C\u672C\u5C31\u4EA7\u91CF\u4E0D\u8DB3\uFF0C\u5927\u4EBA\u8FD8\u4E0D\u80FD\u5B8C\u5168\u6EE1\u8DB3\uFF0C\u4F55\u51B5\u8FD8\u6709\u4E2A\u55F7\u55F7\u5F85\u54FA\u7684\u5A74\u513F\uFF0C\u4E8E\u662F\u4E03\u59E8\u592A\u5C31\u7ED9\u5979\u7ACB\u4E86\u4E2A\u89C4\u77E9\uFF0C\u53EA\u8BB8\u51B7\u5982\u971C\u7ED9\u5B69\u5B50\u5582\u7C73\u6C64\uFF0C\u53EF\u601C\u8FD9\u8FDE\u751F\u603B\u662F\u997F\u5F97\u54C7\u54C7\u53EB\u3002\u51B7\u5982\u971C\u5FCD\u4E0D\u4F4F\u5077\u5582\u4E86\u4E24\u53E3\uFF0C\u5C31\u8BA9\u767D\u5929\u5FB7\u6293\u4E86\u4E2A\u73B0\u573A\u3002</p><p>\u4E03\u59E8\u592A\u95FB\u5F97\u5435\u95F9\u58F0\u8D76\u4E86\u8FC7\u6765\uFF0C\u6068\u5F97\u634F\u4F4F\u51B7\u5982\u971C\u7684\u5976\u5934\u76F4\u62E7\uFF0C\u62E7\u5F97\u51B7\u5982\u971C\u6CEA\u773C\u6C6A\u6C6A\uFF0C\u300C\u4E0D\u77E5\u89C4\u77E9\u7684\u8D31\u8D27\uFF01\u300D\u76F4\u56B7\u56B7\u8981\u62FF\u9488\u624E\u7A7F\u7F1D\u8D77\u6765\u3002\u767D\u5929\u5FB7\u8BA8\u538C\u5979\u603B\u662F\u62A5\u79C1\u6028\u90A3\u4E00\u5957\uFF0C\u4FBF\u5C06\u5979\u63A8\u5F00\uFF0C\u9053\uFF0C\u300C\u4ECA\u5929\u7B97\u4E86\uFF0C\u5F53\u4E2A\u6559\u8BAD\u5427\uFF0C\u8001\u5B50\u8FD8\u8981\u5F00\u4F1A\u5462\u3002\u300D</p><p>\u7F8E\u7F8E\u5730\u5C06\u4E00\u5927\u7897\u6563\u53D1\u7740\u7518\u751C\u7684\u4E73\u6C41\u5927\u53E3\u704C\u5165\u809A\u4E2D\uFF0C\u51B7\u5982\u971C\u8FD8\u5782\u9996\u7AD9\u7740\uFF0C\u957F\u53D1\u62AB\u6563\u4E0B\u6765\uFF0C\u83B9\u767D\u5982\u7389\u7684\u80F8\u812F\u8FD8\u88F8\u9732\u5728\u5BD2\u51B7\u7684\u7A7A\u6C14\u4E2D\uFF0C\u4E24\u4E2A\u6D51\u56ED\u7684\u5976\u5B50\u8C61\u5012\u6263\u7684\u7CBE\u5DE7\u7684\u7389\u7897\uFF0C\u770B\u4E0D\u51FA\u4E00\u70B9\u6324\u7A7A\u7684\u8FF9\u8C61\u3002\u65E0\u8BBA\u6B23\u8D4F\u4E86\u591A\u5C11\u6B21\uFF0C\u767D\u5929\u5FB7\u770B\u5230\u8FD9\u5B8C\u7F8E\u7684\u8EAB\u4F53\u90FD\u4F1A\u7830\u7136\u5FC3\u52A8\uFF0C\u5047\u4EC1\u5047\u4E49\u5730\u5C06\u5979\u62AB\u5F00\u7684\u8863\u895F\u5F80\u4E2D\u95F4\u626F\u4E86\u626F\uFF0C\u865A\u63A9\u4F4F\u6000\uFF0C\u624B\u6307\u62B9\u53BB\u5979\u8138\u4E0A\u51B0\u51B7\u7684\u6CEA\u75D5\uFF0C\u9053\uFF0C\u300C\u65E9\u8981\u542C\u8BDD\u561B\uFF0C\u4E0D\u662F\u8981\u5C11\u53D7\u597D\u591A\u82E6\uFF0C\u5BF9\u4E0D\u5BF9\uFF1F\u300D</p><p>\u51B7\u5982\u971C\u6728\u7136\u3002</p><p>\u4E34\u51FA\u95E8\u524D\uFF0C\u767D\u5929\u5FB7\u53C8\u56DE\u5934\u8BF4\u9053\uFF1A\u300C\u5DEE\u70B9\u5FD8\u4E86\uFF0C\u8FD8\u8BB0\u5F97\u65E9\u51E0\u4E2A\u6708\u7B54\u5E94\u4E86\u6211\u624B\u4E0B\u5F1F\u5144\u4EEC\u7684\u4E8B\u60C5\u5427\uFF0C\u90FD\u662F\u751F\u6B7B\u5173\u4E0A\u6253\u8FC7\u6EDA\u7684\u4EBA\uFF0C\u7C97\u9C81\u4E86\u70B9\uFF0C\u4EBA\u4E0D\u574F\uFF0C\u4ECA\u540E\u6709\u4E2A\u4EC0\u4E48\u4E8B\u6765\u4E86\u8FD8\u5F97\u9760\u4ED6\u4EEC\u6321\uFF0C\u6CA1\u529E\u6CD5\uFF0C\u4F60\u5FC3\u91CC\u6709\u4E2A\u51C6\u5907\u6539\u5929\u6211\u5B89\u6392\u4E00\u4E0B\uFF0C\u8BA9\u4ED6\u4EEC\u4E50\u5475\u4E50\u5475\uFF0C\u554A\u3002\u300D</p><p>\u300C\u556A\u300D\u4E00\u58F0\uFF0C\u74F7\u7897\u6389\u5230\u5730\u4E0A\uFF0C\u7838\u4E86\u4E2A\u7C89\u788E\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u9EC4\u4E91\u754C\u662F\u4E00\u4E2A\u4F20\u8BF4\u4E2D\u7684\u5730\u65B9\uFF0C\u6DF9\u6CA1\u5728\u5927\u6E58\u897F\u7684\u5341\u4E07\u5927\u5C71\u4E2D\uFF0C\u51E0\u4E4E\u6CA1\u6709\u4EBA\u77E5\u9053\u5B83\u7684\u786E\u5207\u6240\u5728\uFF0C\u4E5F\u51E0\u4E4E\u6CA1\u6709\u4EBA\u6562\u5FFD\u89C6\u5B83\u7684\u5B58\u5728\uFF0C\u8FD9\u4E00\u5207\u53EA\u7F18\u4E8E\u4E00\u4E2A\u80FD\u6B62\u513F\u557C\u7684\u540D\u5B57\u2014\u2014\u59DA\u5927\u699C\u699C\u7237\u3002</p><p>\u8FD9\u4E2A\u7EB5\u6A2A\u6E58\u897F\u51E0\u5341\u5E74\u4F5C\u6076\u65E0\u6570\u706D\u6237\u4E07\u5343\u7684\u9B54\u5934\u7EB5\u4F7F\u795E\u618E\u9B3C\u538C\uFF0C\u6E05\u653F\u5E9C\u4E5F\u597D\u6C11\u56FD\u653F\u5E9C\u4E5F\u597D\u90FD\u62FF\u4ED6\u65E0\u53EF\u5948\u4F55\uFF0C\u635F\u5175\u6298\u5C06\u4E4B\u540E\u90FD\u5B66\u4E56\u4E86\uFF0C\u542C\u4EFB\u5176\u5750\u5927\uFF0C\u7EC8\u6210\u6E58\u897F\u532A\u5E2E\u4E4B\u9F99\u5934\u3002</p><p>\u6B64\u65F6\uFF0C\u5374\u6709\u4E00\u4E2A\u5973\u4EBA\u957F\u8DEA\u5728\u9EC4\u4E91\u754C\u9690\u5BC6\u7684\u5C71\u5BE8\u524D\uFF0C\u76EE\u89C6\u7740\u524D\u65B9\uFF0C\u53CC\u624B\u6367\u5728\u80F8\u524D\uFF0C\u638C\u5FC3\u4E2D\u662F\u4E00\u5C0A\u7EFF\u83B9\u83B9\u7684\u7389\u4F5B\uFF0C\u6839\u672C\u5C31\u65E0\u89C6\u4ECE\u5BE8\u5934\u6D1E\u53E3\u4F38\u51FA\u6765\u7684\u51E0\u652F\u4E4C\u4EAE\u7684\u67AA\u53E3\uFF0C\u8FD9\u4E2A\u5973\u4EBA\u662F\u5403\u4E86\u8C79\u5B50\u80C6\u8FD8\u662F\u53D1\u751F\u795E\u7ECF\u5462\uFF1F</p><p>\u4E24\u4E2A\u5C97\u54E8\u4E5F\u5728\u8BA8\u8BBA\u8FD9\u4E2A\u95EE\u9898\uFF0C\u300C\u6211\u8BF4\u5144\u5F1F\uFF0C\u8FD9\u4E48\u6C34\u7075\u7684\u5A46\u5A18\uFF0C\u6211\u6253\u5C0F\u5C31\u6CA1\u89C1\u8FC7\uFF0C\u8001\u5934\u5B50\u4E0D\u60F3\u8981\uFF0C\u7D22\u6027\u54B1\u5144\u5F1F\u6D88\u53D7\u4E86\u5427\u3002\u300D</p><p>\u300C\u64CD\uFF0C\u4F60\u65B0\u6765\u7684\u5427\uFF0C\u77E5\u9053\u8FD9\u5A46\u5A18\u662F\u8C01\u5417\uFF1F\u5927\u540D\u9F0E\u9F0E\u7684\u9ED1\u51E4\u51F0\u5440\uFF0C\u6740\u4EBA\u5982\u9EBB\uFF0C\u51F6\u608D\u6CFC\u8D56\u53EF\u662F\u51FA\u4E86\u540D\u7684\u72E0\u4E3B\uFF0C\u4F60\u6562\u6D88\u53D7\u5979\uFF0C\u53EF\u522B\u8FDE\u9AA8\u5934\u6E23\u5B50\u90FD\u7ED9\u56BC\u4E86\u53BB\u3002\u300D</p><p>\u300C\u957F\u5F97\u6E05\u6E05\u79C0\u79C0\u7684\u53EF\u770B\u4E0D\u51FA\u2026\u2026\u90A3\u5979\u4E0D\u5728\u5C71\u5BE8\u91CC\u5446\u7740\uFF0C\u4E00\u4E2A\u4EBA\u8DD1\u5230\u8FD9\u513F\u6765\u505A\u4E48\u5B50\u3002\u300D</p><p>\u300C\u542C\u8BF4\u662F\u906D\u4E86\u96BE\u5457\uFF0C\u4E00\u51C6\u627E\u8001\u5934\u5B50\u642C\u5175\u6765\u4E86\u3002\u300D</p><p>\u300C\u8DEA\u4E86\u6574\u6574\u4E00\u5929\u4E00\u591C\uFF0C\u8001\u5934\u5B50\u591F\u72E0\uFF0C\u8FD9\u5A46\u5A18\u4E5F\u591F\u5014\u7684\u3002\u300D</p><p>\u300C\u8FD9\u5A46\u5A18\u7684\u9762\u8272\u53D1\u9EC4\uFF0C\u8981\u4E48\u662F\u5E26\u4F24\u5728\u8EAB\uFF0C\u8981\u4E48\u91CD\u75C5\u624D\u6108\uFF0C\u6211\u770B\u54EA\u6491\u4E0D\u4E86\u591A\u4E45\u4E86\uFF0C\u4E0D\u4FE1\u54B1\u6253\u4E0D\u4E2A\u8D4C\u2026\u2026\u54CE\u5440\u563F\uFF0C\u8D4C\u4E2A\u5C41\uFF0C\u4EBA\u8FD8\u771F\u5012\u4E86\u3002\u300D</p><p>\u5C4B\u5B50\u5F88\u5C0F\uFF0C\u53EA\u6709\u4E00\u9762\u58C1\u4E0A\u6302\u7740\u4E24\u76CF\u957F\u660E\u706F\uFF0C\u7167\u4EAE\u4E86\u534A\u4E2A\u623F\u95F4\uFF0C\u53E6\u534A\u95F4\u8D8A\u53D1\u663E\u5F97\u5E7D\u6697\u83AB\u540D\u3002</p><p>\u4E00\u5F20\u5BBD\u5927\u7684\u864E\u76AE\u6905\u9690\u5728\u8FD9\u5E7D\u6697\u4E4B\u4E2D\uFF0C\u5305\u62EC\u5750\u5728\u6905\u5B50\u4E0A\u7684\u7537\u4EBA\uFF0C\u5012\u662F\u8DEA\u5750\u5728\u5730\u4E0A\uFF0C\u5934\u67D4\u987A\u5730\u8DB4\u4F0F\u5728\u7537\u4EBA\u819D\u5934\uFF0C\u957F\u5F97\u50CF\u732B\uFF0C\u795E\u60C5\u4E5F\u50CF\u732B\u7684\u7F8E\u8C8C\u5C0F\u5973\u4EBA\u6E05\u6670\u53EF\u8FA8\uFF0C\u4E00\u53EA\u80A5\u80D6\u7684\u7537\u4EBA\u624B\u642D\u5728\u5C0F\u5973\u4EBA\u7684\u5934\u4E0A\uFF0C\u7231\u601C\u5730\u629A\u6478\u7740\u3002\u8FD9\u4E00\u5207\u90FD\u663E\u5F97\u8BE1\u5F02\u6696\u6627\u3002</p><p>\u699C\u7237\u8001\u4E86\uFF0C\u4E0D\u4EC5\u8001\uFF0C\u8FD8\u80D6\uFF0C\u53C8\u8001\u53C8\u80D6\u7684\u4EBA\u901A\u5E38\u90FD\u4F1A\u6BD4\u8F83\u61D2\u60F0\uFF0C\u4E8E\u662F\u4ED6\u5E38\u5E38\u5C31\u8EB2\u5230\u8FD9\u5E7D\u6697\u4E4B\u4E2D\uFF0C\u628A\u4E00\u5207\u4E8B\u60C5\u4EA4\u7ED9\u552F\u4E00\u7684\u5F1F\u5B50\u2014\u2014\u94BB\u5C71\u8C79\u7533\u660C\u6765\u6253\u7406\uFF0C\u7533\u660C\u4E94\u5927\u4E09\u7C97\uFF0C\u6EE1\u8138\u6A2A\u8089\uFF0C\u5C31\u50CF\u300C\u571F\u532A\u300D\u4E8C\u5B57\u5C31\u523B\u5728\u6CB9\u4EAE\u7684\u5149\u5934\u4E0A\uFF0C\u884C\u4E8B\u5374\u662F\u7C97\u4E2D\u6709\u7EC6\uFF0C\u7279\u522B\u5728\u699C\u7237\u9762\u524D\uFF0C\u4E56\u5F97\u6BD4\u5C0F\u5973\u4EBA\u8FD8\u50CF\u4E00\u53EA\u5C0F\u732B\u3002</p><p>\u4ED6\u5F88\u5FE0\u8BDA\uFF0C\u6709\u65F6\u5019\uFF0C\u5C0F\u5973\u4EBA\u8131\u5F97\u7CBE\u5149\uFF0C\u7EC6\u76AE\u5AE9\u8089\u7684\u80F4\u4F53\u722C\u5728\u699C\u7237\u7684\u8EAB\u4E0A\u53AE\u78E8\u7740\uFF0C\u4F8D\u7ACB\u5728\u4E00\u8FB9\u7684\u4ED6\u53EF\u4EE5\u4E0D\u779F\u4E00\u773C\uFF0C\u88E4\u88C6\u91CC\u4E5F\u6CA1\u6709\u4EFB\u4F55\u53CD\u5E94\uFF0C\u8FD9\u4EFD\u5B9A\u529B\u548C\u5FE0\u8BDA\u4EE4\u699C\u7237\u5F88\u662F\u611F\u6168\u3002</p><p>\u4ED6\u5782\u624B\u5411\u699C\u7237\u6C47\u62A5\uFF0C\u300C\u6309\u60A8\u7684\u5429\u5490\uFF0C\u9ED1\u51E4\u51F0\u62AC\u4E86\u56DE\u6765\uFF0C\u67E5\u9A8C\u662F\u4F53\u529B\u4E0D\u652F\u865A\u8131\u4E86\uFF0C\u65E0\u5927\u788D\u3002\u300D</p><p>\u9690\u5728\u6697\u4E2D\u7684\u699C\u7237\u50CF\u4E00\u56E2\u5DE8\u5927\u7684\u5F71\u5B50\uFF0C\u4E00\u52A8\u4E0D\u52A8\u3002</p><p>\u300C\u8FD9\u662F\u5979\u624B\u4E2D\u62FF\u7740\u7684\u4E1C\u897F\uFF0C\u597D\u50CF\u662F\u60A8\u7684\u4FE1\u7269\uFF0C\u4E0D\u8FC7\u7533\u660C\u8FD8\u6CA1\u4EB2\u773C\u77A7\u89C1\u8FC7\u3002\u300D</p><p>\u5F71\u5B50\u52A8\u4E86\u52A8\uFF0C\u6162\u541E\u541E\u5730\u8BF4\uFF0C\u300C\u90A3\u5E74\u6211\u4E2D\u4E86\u5B98\u5175\u57CB\u4F0F\uFF0C\u5DEE\u70B9\u9003\u4E0D\u8FC7\u90A3\u4E00\u52AB\uFF0C\u9ED1\u864E\u62DA\u6B7B\u5C06\u6211\u6551\u4E86\u51FA\u53BB\uFF0C\u540E\u6765\u6211\u505A\u4E86\u8FD9\u6E58\u897F\u4E94\u6D1E\u5341\u516B\u5BE8\u7684\u5927\u9F99\u5934\uFF0C\u5F53\u7740\u5927\u5BB6\u7684\u9762\u6211\u7ED9\u4E86\u9ED1\u864E\u8FD9\u4E2A\u4FE1\u7269\u7389\u4F5B\uFF0C\u5341\u591A\u5E74\u7684\u8001\u8D27\u4E86\uFF0C\u4F60\u53C8\u5982\u4F55\u770B\u8FC7\u3002\u300D</p><p>\u300C\u4E8B\u9694\u591A\u5E74\uFF0C\u9ED1\u864E\u4E5F\u4E0D\u5728\u4E86\uFF0C\u6CA1\u5FC5\u8981\u7406\u5979\uFF0C\u5F1F\u5B50\u5E72\u8106\u628A\u5979\u6254\u5230\u6E0A\u91CC\u5582\u86C7\u738B\u5F97\u4E86\uFF0C\u6C38\u7EDD\u540E\u60A3\u3002\u300D</p><p>\u300C\u7389\u4F5B\u653E\u5230\u8C01\u624B\u91CC\u90FD\u662F\u4E00\u6837\uFF0C\u53EA\u8981\u662F\u4E0E\u9ED1\u864E\u6709\u5173\u7CFB\u7684\u4EBA\uFF0C\u300D\u699C\u7237\u54C2\u9053\uFF0C\u300C\u51E1\u6709\u6240\u6C42\uFF0C\u5FC5\u6709\u6240\u62A5\uFF0C\u8F7B\u8A00\u5BE1\u4FE1\u5C82\u662F\u6211\u59DA\u5927\u699C\u6240\u4E3A\uFF1F\u300D</p><p>\u94BB\u5C71\u8C79\u6D51\u8EAB\u4E0D\u81EA\u5728\uFF0C\u9762\u7EA2\u8033\u8D64\uFF0C\u5FC3\u91CC\u662F\u4E0D\u670D\u6C14\u7684\uFF0C\u5FC3\u60F3\u571F\u532A\u8BB2\u4EC1\u4E49\uFF0C\u90A3\u6BCD\u732A\u4E5F\u4E0A\u6811\u4E86\uFF0C\u8BF4\u5F97\u8FD9\u4E48\u6F02\u4EAE\u90A3\u628A\u4EBA\u5BB6\u667E\u4E86\u4E00\u6574\u5929\u53C8\u7B97\u600E\u4E48\u56DE\u4E8B\u5462\uFF1F\u4F46\u4ED6\u51B3\u4E0D\u4F1A\u8822\u5230\u53BB\u4E89\u8FA9\uFF0C\u5FAE\u4E00\u8EAC\u8EAB\uFF0C\u4E0D\u518D\u5F00\u8A00\uFF0C\u8F6C\u8EAB\u51FA\u53BB\u4E86\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6D77\u68E0\u9759\u9759\u5730\u7AD9\u5728\u4EAE\u5149\u91CC\uFF0C\u9F3B\u5B50\u4E0A\u60CA\u5FC3\u7684\u94DC\u73AF\u5DF2\u7ECF\u53D6\u6389\uFF0C\u6614\u65E5\u7684\u795E\u91C7\u6062\u590D\u4E86\u4E03\u516B\u5206\u3002</p><p>\u5979\u65E9\u5C31\u79BB\u5F00\u4E86\u86EE\u5B50\uFF0C\u90A3\u4E2A\u7EAF\u6734\u7684\u5C71\u91CC\u653E\u6392\u6C49\u5B50\uFF0C\u662F\u5728\u80FD\u7A0D\u7A0D\u514B\u5236\u6BD2\u763E\u540E\u7684\u4E00\u4E2A\u6DF1\u591C\u6084\u6084\u8D70\u7684\uFF0C\u629B\u5F03\u5728\u5979\u6700\u56F0\u96BE\u7684\u65F6\u5019\u4F38\u51FA\u63F4\u624B\u7684\u6069\u4EBA\uFF0C\u5185\u5FC3\u7684\u786E\u6709\u6127\u759A\uFF0C\u4F46\u5979\u6CA1\u5F97\u9009\u62E9\uFF0C\u4E5F\u4E0D\u80FD\u56DE\u5934\uFF0C\u66F4\u4E0D\u613F\u8FDE\u7D2F\u65E0\u8F9C\uFF0C\u53EA\u6709\u5728\u5FC3\u4E2D\u8D77\u8A93\uFF0C\u6709\u671D\u4E00\u65E5\uFF0C\u6709\u4EC7\u7684\u5FC5\u62A5\u4EC7\uFF0C\u6709\u6069\u7684\u5FC5\u62A5\u6069\u3002</p><p>\u5979\u72EC\u81EA\u6F5C\u56DE\u7FE0\u7AF9\u6D77\u9644\u8FD1\uFF0C\u5728\u6DF1\u5C71\u4E2D\u6574\u6574\u4F11\u517B\u4E86\u4E24\u4E2A\u591A\u6708\uFF0C\u5F7B\u5E95\u6212\u6389\u4E86\u6BD2\u763E\uFF0C\u624D\u7740\u624B\u8FDB\u884C\u601D\u8651\u5F88\u4E45\u7684\u8BA1\u5212\u3002</p><p>\u5E73\u89C6\u7740\u9ED1\u6697\u4E2D\u5E9E\u5927\u6A21\u7CCA\u7684\u5F71\u5B50\uFF0C\u5979\u6CA1\u89C1\u8FC7\u699C\u7237\uFF0C\u4F46\u77E5\u9053\u90A3\u4E00\u5B9A\u662F\u699C\u7237\uFF0C\u5979\u4ECE\u90A3\u56E2\u5F71\u5B50\u4E2D\u611F\u53D7\u5230\u4E86\u4E00\u80A1\u8FEB\u4EBA\u7684\u538B\u529B\u3002\u7EB5\u4F7F\u5728\u751F\u6B7B\u5173\u4E0A\u6EDA\u8FC7\u4E86\u51E0\u906D\uFF0C\u8FD8\u662F\u5FC3\u5934\u6709\u6B64\u6035\u7136\u3002</p><p>\u50CF\u732B\u4E00\u6837\u7684\u5C0F\u5973\u4EBA\u8BF4\u8BDD\u4E86\uFF0C\u300C\u699C\u7237\u95EE\u4F60\u60F3\u5E72\u4EC0\u4E48\uFF1F\u300D</p><p>\u300C\u62A5\u4EC7\uFF01\u300D\u6D77\u68E0\u773C\u7736\u53D1\u7EA2\uFF0C\u4E00\u5B57\u4E00\u987F\uFF0C\u300C\u6309\u9053\u4E0A\u7684\u89C4\u77E9\uFF0C\u6C42\u699C\u7237\u4E3A\u6211\u4E3B\u6301\u516C\u9053\u3002\u300D</p><p>\u300C\u5982\u4F55\u62A5\u6CD5\uFF1F\u300D</p><p>\u300C\u6253\u8FDB\u6C85\u9547\u57CE\uFF0C\u6740\u5C3D\u767D\u5BB6\u4EBA\uFF0C\u6CB9\u70F9\u767D\u5929\u5FB7\uFF01\u300D</p><p>\u5F71\u5B50\u5495\u54DD\u4E86\u51E0\u53E5\uFF0C\u5C0F\u5973\u4EBA\u62AC\u9AD8\u4E86\u58F0\u8C03\uFF0C\u50CF\u662F\u8BAD\u65A5\uFF0C\u300C\u6C11\u4E0D\u4E0E\u5B98\u6597\uFF0C\u4F60\u8FD9\u662F\u81EA\u5BFB\u6B7B\u8DEF\uFF0C\u8FD8\u8981\u62FF\u5144\u5F1F\u4EEC\u57AB\u80CC\uFF0C\u9053\u4E0A\u53EF\u6CA1\u8FD9\u89C4\u77E9\uFF0C\u9ED1\u864E\u7684\u4EBA\u60C5\u4E5F\u6CA1\u6709\u8FD9\u4E48\u5927\uFF0C\u56DE\u53BB\u5427\u3002\u300D</p><p>\u6D77\u68E0\u51B7\u7B11\u9053\uFF0C\u300C\u539F\u6765\u5802\u5802\u699C\u7237\u4E5F\u6015\u5B98\u6015\u4E8B\u4E86\u3002\u300D</p><p>\u300C\u653E\u8086\uFF0C\u638C\u5634\uFF01\u300D\u5C0F\u5973\u4EBA\u5C16\u58F0\u53EB\u9053\u3002</p><p>\u6D77\u68E0\u6BEB\u4E0D\u72B9\u8C6B\uFF0C\u4E3E\u624B\u5F80\u81EA\u5DF1\u8138\u4E0A\u62BD\u53BB\uFF0C\u62BD\u5F97\u5F88\u91CD\uFF0C\u6CA1\u51E0\u4E0B\u5C31\u5634\u89D2\u6EA2\u8840\uFF0C\u5014\u5F3A\u7684\u795E\u8272\u5374\u4E1D\u6BEB\u4E0D\u53D8\u3002</p><p>\u300C\u505C\u4E0B\u5427\uFF0C\u300D\u8FD9\u6B21\u6362\u4E86\u61D2\u61D2\u7684\u7537\u4EBA\u7684\u58F0\u97F3\uFF0C\u5E26\u7740\u82CD\u8001\u548C\u65E0\u5EB8\u7F6E\u7591\u7684\u6743\u5A01\uFF0C\u300C\u4E0D\u8FC7\u5C31\u662F\u51FA\u5175\u4E48\uFF1F\u81EA\u4ECE\u6211\u5F53\u4E86\u8FD9\u4E2A\u6709\u540D\u65E0\u5B9E\u7684\u9F99\u5934\u76DF\u4E3B\uFF0C\u5012\u662F\u6709\u597D\u4E45\u6CA1\u6253\u8FC7\u4ED7\u4E86\u3002\u300D</p><p>\u6D77\u68E0\u542C\u51FA\u4E86\u4E00\u7EBF\u5E0C\u671B\uFF0C\u300C\u5982\u679C\u699C\u7237\u80AF\u5F00\u6069\u501F\u7ED9\u6211\u4E00\u652F\u5175\uFF0C\u6211\u613F\u53EA\u8981\u4EBA\uFF0C\u767D\u5BB6\u5821\u548C\u6C85\u9547\u6240\u6709\u7684\u8D22\u7269\u90FD\u5F52\u60A8\u6240\u6709\uFF0C\u5305\u62EC\u6211\u7FE0\u7AF9\u6D77\u5386\u5E74\u6240\u79EF\u3002\u300D</p><p>\u6709\u4E24\u9053\u5149\u96BE\u5F97\u5BDF\u89C9\u5730\u5FAE\u4EAE\u4E86\u4E00\u4E0B\uFF0C\u968F\u5373\u6DE1\u6DE1\u8BF4\u9053\uFF0C\u300C\u8FD8\u6709\u5417\uFF1F\u300D</p><p>\u6D77\u68E0\u5F88\u5FEB\u660E\u767D\u4E86\u8BDD\u7684\u610F\u601D\uFF0C\u6697\u4E2D\u54AC\u54AC\u7259\uFF0C\u65AD\u7136\u5C06\u81EA\u5DF1\u7684\u8863\u88F3\u626F\u5F00\uFF0C\u5265\u4E0B\uFF0C\u5979\u7684\u80F8\u90E8\u5BBD\u800C\u4E30\u76C8\uFF0C\u5982\u5FAE\u98CE\u5439\u52A8\u7684\u6CE2\u6D6A\uFF0C\u8F7B\u8F7B\u97F5\u52A8\uFF0C\u8FD9\u662F\u4E00\u5177\u591A\u4E48\u7F8E\u597D\u800C\u8089\u611F\u7684\u80F4\u4F53\u554A\u3002</p><p>\u9ED1\u6697\u4E2D\u7684\u5F71\u5B50\u4E5F\u4E0D\u7981\u54BD\u4E86\u53E3\u53E3\u6C34\uFF0C\u6C89\u9ED8\u4E86\u534A\u664C\uFF0C\u4F3C\u5728\u6B23\u8D4F\uFF0C\u4E5F\u4F3C\u5728\u611F\u53F9\uFF0C\u300C\u771F\u662F\u6F02\u4EAE\uFF0C\u53EF\u60DC\u554A\uFF0C\u6211\u8001\u4E86\uFF0C\u4E0D\u4F1A\u6B23\u8D4F\u4E86\uFF0C\u4F60\u770B\uFF0C\u50CF\u963F\u6708\u8FD9\u822C\u9C9C\u5AE9\u7684\u82B1\u513F\u6211\u4E5F\u53EA\u80FD\u95FB\u95FB\u5473\u800C\u5DF2\u3002\u300D</p><p>\u4EAE\u5149\u4E2D\u7684\u624B\u52A8\u4E86\u52A8\uFF0C\u634F\u4E86\u634F\u5C0F\u5973\u4EBA\u7C89\u5AE9\u7684\u816E\u5E2E\uFF0C\u5C0F\u5973\u4EBA\u7F9E\u6DA9\u5730\u7B11\u7740\uFF0C\u53CC\u77B3\u526A\u6C34\uFF0C\u5C0F\u5C0F\u5E74\u7EAA\u7ADF\u4E5F\u5A9A\u6001\u5341\u8DB3\uFF0C\u6D77\u68E0\u6014\u4E86\u6014\uFF0C\u603B\u89C9\u5F97\u5979\u6709\u4E9B\u719F\u8BC6\uFF0C\u795E\u6001\u95F4\u4E5F\u6709\u4E9B\u50CF\u51B7\u5982\u971C\u3002</p><p>\u5979\u4E00\u65F6\u6CA1\u6709\u60F3\u8D77\uFF0C\u5C0F\u5973\u4EBA\u5374\u662F\u523B\u9AA8\u94ED\u5FC3\uFF0C\u539F\u6765\u5979\u5C31\u662F\u5EB7\u8001\u7237\u5B50\u4E34\u6B7B\u524D\u6536\u8FDB\u623F\u7684\u90A3\u4E2A\u53EB\u963F\u6708\u7684\u5973\u5B66\u751F\uFF0C\u540E\u6765\u88AB\u5EB7\u5BB6\u4EBA\u5356\u5230\u7A91\u5B50\u91CC\uFF0C\u53C8\u5728\u4E00\u6B21\u5916\u51FA\u7684\u9014\u4E2D\u88AB\u63B3\u5230\u4E86\u5C71\u5BE8\uFF0C\u5979\u5012\u662F\u5F7B\u5E95\u8BA4\u547D\u4E86\uFF0C\u53EF\u5BF9\u4E8E\u6BC1\u4E86\u5979\u4E00\u751F\u7684\u4EBA\u53C8\u600E\u4E48\u4F1A\u7A0D\u6709\u5FD8\u6000\u5462\uFF1F</p><p>\u699C\u7237\u4E0D\u54B8\u4E0D\u6DE1\u7684\u51E0\u53E5\u8BDD\u8BA9\u6D77\u68E0\u4E0D\u77E5\u9053\u63A5\u4E0B\u6765\u5E94\u8BE5\u8BF4\u4EC0\u4E48\uFF0C\u7A7A\u6C14\u5F88\u51B7\uFF0C\u5438\u9644\u5230\u5979\u88F8\u9732\u7684\u808C\u80A4\u4E0A\uFF0C\u53EA\u597D\u62B1\u7D27\u53CC\u81C2\u3002</p><p>\u699C\u7237\u5E7D\u5E7D\u5730\u8BF4\uFF0C\u300C\u963F\u6708\uFF0C\u7237\u8981\u5C3F\u4E86\u3002\u300D</p><p>\u5C0F\u5973\u4EBA\u5E94\u558F\u4E00\u58F0\uFF0C\u65E0\u58F0\u5730\u6ED1\u5230\u89D2\u843D\uFF0C\u53D6\u8FC7\u591C\u58F6\u6765\uFF0C\u7D20\u624B\u4E3A\u699C\u7237\u89E3\u5F00\u88E4\u5E26\uFF0C\u6367\u51FA\u90A3\u6839\u9ED1\u4E4E\u4E4E\u7684\u5B9D\u8D1D\u3002</p><p>\u699C\u7237\u5374\u4E0D\u52A8\u4F5C\uFF0C\u53EA\u8BF4\uFF0C\u300C\u6211\u60F3\u8D77\u4E00\u4E2A\u62FF\u4EBA\u7684\u5634\u5DF4\u5F53\u5C3F\u58F6\u7684\u4F20\u8BF4\uFF0C\u963F\u6708\uFF0C\u4F60\u89C1\u8FC7\u5417\uFF1F\u300D</p><p>\u963F\u6708\u563B\u563B\u5730\u7B11\uFF0C\u6076\u610F\u5730\u770B\u5411\u6D77\u68E0\uFF0C\u300C\u7237\uFF0C\u90A3\u53EF\u591A\u810F\u3002\u300D</p><p>\u300C\u5C0F\u5C41\u5B69\u4F60\u8FD8\u522B\u4E0D\u4FE1\uFF0C\u54B1\u65B9\u56ED\u51E0\u5341\u91CC\u53EF\u5C31\u6709\u8FD9\u6837\u7684\u4EBA\uFF0C\u5C31\u662F\u6211\u8001\u5BB6\u4F19\u6CA1\u8FD9\u822C\u798F\u6C14\u3002\u300D</p><p>\u6D77\u68E0\u8EAB\u4E0A\u7684\u9C9C\u8840\u4E00\u4E0B\u5B50\u5168\u8E7F\u5230\u8138\u4E0A\uFF0C\u770B\u4F3C\u6F2B\u4E0D\u7ECF\u5FC3\u7684\u5BF9\u8BDD\u5982\u652F\u652F\u5229\u7BAD\u547D\u4E2D\u5979\u5DF2\u7136\u7834\u635F\u7684\u5FC3\u3002\u5979\u5C82\u4F1A\u542C\u4E0D\u51FA\u90A3\u8001\u6076\u68CD\u7684\u5F26\u5916\u4E4B\u8A00\uFF0C\u679C\u7136\u5929\u4E0B\u4E4C\u9E26\u4E00\u822C\u9ED1\uFF0C\u5BF9\u6551\u547D\u6069\u4EBA\u7684\u672A\u4EA1\u4EBA\u4E5F\u6562\u5982\u6B64\u6298\u8FB1\uFF0C\u53EF\u662F\uFF0C\u5979\u53C8\u6709\u4EC0\u4E48\u9009\u62E9\u5417\uFF1F</p><p>\u5979\u8DEA\u4E0A\u524D\uFF0C\u5B57\u5B57\u6CE3\u8840\uFF0C\u300C\u699C\u7237\uFF0C\u53EF\u5426\u542C\u6D77\u68E0\u4E00\u8A00\uFF1F\u300D</p><p>\u699C\u7237\u4E0D\u52A8\u58F0\u8272\uFF0C\u6F2B\u9053\uFF0C\u300C\u54E6\uFF1F\u300D</p><p>\u6D77\u68E0\u773C\u4E2D\u5659\u6CEA\uFF0C\u300C\u53EA\u8981\u80FD\u62A5\u6B64\u8840\u6D77\u6DF1\u4EC7\uFF0C\u522B\u8BF4\u4F3A\u5019\u60A8\u8001\u4EBA\u5BB6\uFF0C\u5C31\u7B97\u505A\u725B\u505A\u9A6C\u4E5F\u662F\u613F\u610F\u7684\u3002\u300D</p><p>\u699C\u7237\u6CA1\u4F5C\u58F0\uFF0C\u4F3C\u9677\u5165\u4E86\u719F\u7761\u3002\u6D77\u68E0\u8DEA\u884C\u51E0\u6B65\uFF0C\u5DF2\u5230\u699C\u7237\u80EF\u95F4\uFF0C\u4E00\u6761\u8F6F\u53ED\u53ED\u7684\u957F\u866B\u8037\u62C9\u7740\uFF0C\u6563\u53D1\u51FA\u8001\u5E74\u4EBA\u7279\u6709\u7684\u9178\u81ED\u5473\u3002</p><p>\u6D77\u68E0\u5C4F\u4F4F\u547C\u5438\uFF0C\u751F\u751F\u6291\u4F4F\u6076\u5FC3\u6B32\u5455\u7684\u611F\u89C9\uFF0C\u5F20\u5F00\u5634\u8F7B\u8F7B\u53FC\u8D77\u9F9F\u5934\u542B\u5165\u53E3\u4E2D\uFF0C\u820C\u5C16\u719F\u7EC3\u81EA\u7136\u5730\u9876\u4F4F\u9F9F\u5934\u7684\u9876\u7AEF\u78E8\u3002</p><p>\u8089\u866B\u4E00\u70B9\u70B9\u98A4\u52A8\uFF0C\u6CA1\u6709\u52C3\u8D77\uFF0C\u8001\u4EBA\u8212\u670D\u5F97\u957F\u53F9\u4E00\u58F0\u3002</p><p>\u7247\u523B\uFF0C\u8089\u866B\u518D\u6B21\u8815\u52A8\u4E86\u4E00\u4E0B\uFF0C\u4E00\u80A1\u8165\u81CA\u7684\u6DB2\u4F53\u65AD\u65AD\u7EED\u7EED\u6D41\u4E86\u51FA\u6765\uFF0C\u8FC5\u901F\u5145\u76C8\u4E86\u5979\u7684\u53E3\u8154\u3002</p><p>\u6D77\u68E0\u7684\u76EE\u5149\u8EB2\u5F00\u4E86\u8EAB\u8FB9\u5C0F\u5973\u4EBA\u60CA\u8BB6\u800C\u9119\u5937\u7684\u795E\u8272\uFF0C\u54CD\u4EAE\u5730\u5495\u561F\u58F0\u4E2D\uFF0C\u4E00\u53E3\u5C06\u5C3F\u6DB2\u5F3A\u884C\u54BD\u5165\u8179\u4E2D\u3002</p>',75);function i(t,n){return r}var _=a(e,[["render",i]]);export{_ as default};
