import{e as p}from"./app.630af5f2.js";import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";const e={},r=p('<h1 id="\u7B2C\u5341\u4E00\u7AE0-\u6293\u6355" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u5341\u4E00\u7AE0-\u6293\u6355" aria-hidden="true">#</a> \u7B2C\u5341\u4E00\u7AE0 \u6293\u6355</h1><p>\u9EC4\u660F\u65F6\uFF0C\u873B\u8713\u4F4E\u98DE\u3002</p><p>\u6C85\u9547\u8857\u5934\u65E9\u65E9\u5C31\u7F55\u89C1\u4EBA\u884C\u4E86\u3002\u5929\u6C14\u95F7\u70ED\u5F97\u65E0\u5904\u8EB2\u85CF\uFF0C\u5C31\u7B97\u5750\u5728\u5BB6\u4E2D\u4E5F\u662F\u4E00\u8EAB\u7EC6\u6C57\u3002</p><p>\u8001\u4EBA\u8BB2\uFF0C\u8FD9\u4E00\u591C\u4F1A\u6709\u5927\u96F7\u96E8\u3002</p><p>\u300C\u556A\uFF01\u300D</p><p>\u5218\u6EA2\u4E4B\u4E00\u62CD\u684C\u5B50\uFF0C\u6781\u4E3A\u9707\u6012\u3002\u300C\u7ADF\u6709\u6B64\u4E8B\uFF0C\u5B9E\u5728\u7316\u72C2\uFF01\u300D</p><p>\u300C\u6C42\u9752\u5929\u5927\u8001\u7237\u4E3A\u5C0F\u6C11\u505A\u4E3B\u54C7\u3002\u300D\u5934\u7F20\u767D\u5E03\u6761\u4E00\u8138\u75C5\u5BB9\u7684\u5EB7\u8001\u7237\u65E9\u5C31\u4E0D\u6210\u4E2A\u4EBA\u5F62\uFF0C\u8FD8\u5728\u52AA\u529B\u8981\u5750\u8D77\u6765\u7ED9\u5218\u6EA2\u4E4B\u78D5\u5934\u3002</p><p>\u767D\u5929\u5FB7\u90A3\u65E5\u903C\u5F97\u4E00\u7EB8\u534F\u8BAE\u540E\uFF0C\u518D\u4E0D\u5BB9\u60C5\uFF0C\u6B21\u65E5\u4FBF\u6309\u518C\u6E05\u70B9\u8D22\u4EA7\uFF0C\u7531\u767D\u5BB6\u6765\u63A5\u7BA1\uFF0C\u5BF9\u5916\u540D\u4E49\u4E0A\u662F\u5EB7\u5BB6\u56E0\u6545\u5916\u8FC1\uFF0C\u8F6C\u7ED9\u4E86\u767D\u5BB6\u7ECF\u8425\uFF0C\u767D\u5BB6\u52BF\u529B\u7531\u6B64\u5728\u6C85\u9547\u7531\u4E61\u5165\u57CE\uFF0C\u8FC5\u901F\u6269\u5F20\u3002</p><p>\u53E6\u4E00\u65B9\u9762\uFF0C\u767D\u5929\u5FB7\u5C06\u5EB7\u5BB6\u7684\u4EC6\u5F79\u5C3D\u6563\uFF0C\u6D3E\u56E2\u4E01\u62FF\u9A6C\u8F66\u5C06\u5EB7\u8001\u7237\u5B50\u4E00\u5BB6\u8FDC\u8FDC\u9001\u8D70\uFF0C\u4E0D\u51C6\u56DE\u5934\u3002</p><p>\u8FD9\u4E8B\u5728\u5F53\u5730\u9887\u4E3A\u8F70\u52A8\uFF0C\u7269\u8BAE\u751A\u591A\uFF0C\u5EB7\u8001\u7237\u5B50\u5DF2\u4E00\u75C5\u4E0D\u8D77\uFF0C\u5EB7\u5BB6\u4EBA\u6015\u767D\u5929\u5FB7\u4E0B\u9ED1\u624B\uFF0C\u4E07\u822C\u65E0\u5948\u4E4B\u4E2D\u6325\u6CEA\u79BB\u53BB\uFF0C\u6253\u843D\u7259\u5F80\u809A\u91CC\u541E\uFF0C\u4E0D\u6562\u8A00\u8BED\u771F\u76F8\u3002</p><p>\u884C\u81F3\u4E2D\u9014\uFF0C\u5EB7\u8001\u7237\u5B50\u75C5\u4F53\u6C89\u91CD\uFF0C\u65F6\u65E5\u65E0\u591A\uFF0C\u4E0D\u5B9C\u8FDC\u884C\uFF0C\u4E3B\u8981\u5B50\u4F84\u90FD\u4E0D\u5728\u4ED6\u8EAB\u8FB9\uFF0C\u53EA\u5F97\u4E58\u591C\u6E9C\u56DE\u6765\uFF0C\u4E0D\u6562\u8FDB\u57CE\uFF0C\u5728\u4E61\u4E0B\u80E1\u4E71\u627E\u95F4\u7834\u623F\u4F4F\u4E86\uFF0C\u5EB7\u8001\u7237\u5B50\u7684\u4E00\u4E2A\u5802\u4F84\u4F5C\u4E3B\u5077\u5077\u6D3E\u4EBA\u628A\u5218\u6EA2\u4E4B\u8BF7\u4E86\u8FC7\u6765\u544A\u4E86\u767D\u5929\u5FB7\u4E00\u72B6\u3002</p><p>\u5218\u6EA2\u4E4B\u5E74\u8F7B\u6709\u8840\u6C14\uFF0C\u4E00\u542C\u679C\u7136\u6012\u706B\u52C3\u53D1\uFF0C\u4E00\u8FED\u58F0\u5730\u8981\u628A\u767D\u5929\u5FB7\u62FF\u4E0B\u6765\u662F\u95EE\u3002</p><p>\u653F\u5E9C\u79D8\u4E66\u53F8\u9A6C\u5357\u5012\u662F\u51B7\u9759\uFF0C\u5728\u4E00\u65C1\u529D\u6170\u9053\uFF0C\u300C\u6B64\u4E8B\u4E0D\u5B9C\u6025\uFF0C\u56DE\u53BB\u4E4B\u540E\u518D\u4ECE\u957F\u8BA1\u8BAE\u3002\u300D</p><p>\u5218\u6EA2\u4E4B\u51B7\u7B11\u9053\uFF0C\u300C\u4ECE\u957F\u8BA1\u8BAE\uFF0C\u4ECE\u957F\u8BA1\u8F83\uFF0C\u4EC0\u4E48\u90FD\u8981\u4ECE\u957F\u8BA1\u8BAE\u9EC4\u82B1\u83DC\u90FD\u51C9\u4E86\uFF0C\u600E\u4E48\u5904\u7406\u6B64\u4E8B\u4F60\u4EEC\u9A6C\u4E0A\u62FF\u4E2A\u529E\u6CD5\u6765\uFF0C\u7ED9\u5EB7\u8001\u4E00\u4E2A\u4EA4\u5F85\u3002\u300D</p><p>\u53F8\u9A6C\u5357\u53EA\u5F97\u7B54\u9053\uFF0C\u300C\u662F\uFF0C\u6211\u4EEC\u4E00\u5B9A\u52A0\u7D27\u529E\u3002\u300D</p><p>\u5218\u6EA2\u4E4B\u8BF4\u5F97\u4E25\u5389\uFF0C\u4E5F\u77E5\u6B64\u4E8B\u68D8\u624B\uFF0C\u65E0\u5FC3\u505C\u7559\uFF0C\u5BF9\u5EB7\u8001\u7237\u5B50\u629A\u6170\u4E86\u51E0\u53E5\u4FBF\u5306\u5306\u56DE\u57CE\u3002</p><p>\u591C\u6DF1\u4E86\uFF0C\u96E8\u8FD8\u6CA1\u4E0B\u5F97\u6765\u3002\u51B7\u5982\u971C\u534A\u8EBA\u5728\u51C9\u5E2D\u4E0A\u501A\u7740\u7AF9\u6795\u8F7B\u8F7B\u6253\u6247\uFF0C\u4E0D\u77E5\u662F\u5426\u5929\u6C14\u7684\u7F18\u6545\uFF0C\u5FC3\u7EEA\u6709\u4E9B\u8E81\u52A8\u4E0D\u5B81\u3002</p><p>\u5979\u7684\u5C0F\u8179\u5FAE\u9686\uFF0C\u4E1A\u5DF2\u9732\u6000\uFF0C\u518D\u6709\u4E09\u6708\u7684\u5149\u666F\u5C31\u6709\u4E00\u4E2A\u65B0\u7684\u751F\u547D\u5471\u5471\u843D\u5730\u4E86\u3002</p><p>\u6000\u5B55\u7684\u6D88\u606F\u8BA9\u5218\u6EA2\u4E4B\u6B23\u559C\u82E5\u72C2\uFF0C\u4ED6\u662F\u5BB6\u4E2D\u7684\u72EC\u5B50\uFF0C\u4F20\u5B97\u63A5\u4EE3\u7684\u91CD\u4EFB\u5168\u6307\u671B\u5728\u4ED6\u8EAB\u4E0A\uFF0C\u8FD9\u4E00\u6765\u8D8A\u53D1\u6068\u4E0D\u5F97\u628A\u51B7\u5982\u971C\u542B\u5728\u5634\u91CC\uFF0C\u75BC\u7231\u4E07\u5206\uFF0C\u51B7\u5982\u971C\u4E5F\u8C28\u5B88\u5987\u9053\uFF0C\u4E00\u822C\u4E0D\u518D\u51FA\u5916\u5E94\u916C\uFF0C\u628A\u81EA\u5DF1\u4FDD\u517B\u5F97\u6C34\u5149\u6ECB\u6DA6\u7684\uFF0C\u672C\u6765\u524A\u7626\u7684\u8EAB\u5B50\u773C\u89C1\u5F97\u6709\u4E9B\u53D1\u5706\u4E86\u3002\u592B\u5987\u95F4\u4EE5\u5F80\u7684\u4E00\u70B9\u5C0F\u82A5\u8482\u81EA\u7136\u518D\u4E5F\u65E0\u4EBA\u63D0\u8D77\u3002</p><p>\u542C\u5F97\u5218\u6EA2\u4E4B\u56DE\u6765\u7684\u58F0\u97F3\uFF0C\u51B7\u5982\u971C\u5FD9\u6B20\u8D77\u8EAB\u5B50\uFF0C\u7528\u529B\u5927\u4E86\uFF0C\u809A\u5B50\u91CC\u4E00\u9635\u7EDE\u75DB\uFF0C\u51B7\u5982\u971C\u4E0D\u7531\u5F97\u547B\u541F\u4E86\u4E00\u58F0\u3002</p><p>\u300C\u5F53\u5FC3\u5566\u592B\u4EBA\u3002\u300D\u5218\u6EA2\u4E4B\u614C\u5FD9\u62A2\u8FDB\u623F\u6765\uFF0C\u5C0F\u5FC3\u6276\u8D77\u51B7\u5982\u971C\uFF0C\u4FA7\u5750\u5728\u5E8A\u8FB9\u3002</p><p>\u300C\u4E0D\u788D\uFF0C\u4E0D\u788D\u3002\u300D\u51B7\u5982\u971C\u542B\u7B11\u9053\u3002</p><p>\u4E24\u4EBA\u76F8\u62E5\u800C\u5750\uFF0C\u5FC3\u5934\u7F20\u7EF5\u3002\u5218\u6EA2\u4E4B\u4E00\u624B\u8F7B\u629A\u7740\u51B7\u5982\u971C\u5706\u8D77\u7684\u5C0F\u8179\uFF0C\u65E0\u9650\u7231\u601C\u5730\u8BF4\uFF0C\u300C\u4F60\u548C\u5B69\u5B50\u5C31\u662F\u6211\u7684\u547D\u6839\u4E86\uFF0C\u4E07\u4E07\u95EA\u5931\u4E0D\u5F97\u3002\u300D</p><p>\u300C\u4E0A\u6B21\u4F60\u8BF4\u7ED9\u5B69\u5B50\u53D6\u4E2A\u540D\u5B57\uFF0C\u60F3\u597D\u4E86\u6CA1\u6709\uFF1F\u300D</p><p>\u300C\u6211\u5E0C\u671B\u5C06\u6765\u5B69\u5B50\u957F\u5F97\u50CF\u4F60\u8FD9\u4E48\u7F8E\uFF0C\u53EB\u971C\u7F8E\u5982\u4F55\uFF0C\u5C0F\u540D\u7F8E\u7F8E\uFF1F\u300D</p><p>\u300C\u4F60\u51C6\u77E5\u9053\u662F\u4E2A\u5973\u5B69\u554A\uFF1F\u300D</p><p>\u300C\u633A\u77DB\u76FE\u7684\uFF0C\u6211\u5462\u771F\u60F3\u8981\u4E00\u4E2A\u5973\u5B69\u5B50\uFF0C\u8001\u592A\u7237\u4ED6\u4EEC\u975E\u903C\u6211\u4EEC\u751F\u4E2A\u7537\u5B69\u4F20\u5B97\u63A5\u4EE3\uFF0C\u8981\u4E0D\uFF0C\u4E00\u6B21\u751F\u4E24\u4E2A\uFF0C\u4E00\u7537\u4E00\u5973\u3002\u300D</p><p>\u300C\u5478\uFF0C\u60F3\u5F97\u7F8E\uFF0C\u6211\u7684\u809A\u5B50\u4E0D\u4F1A\u7206\u4E86\u554A\u3002\u300D</p><p>\u51B7\u5982\u971C\u53EA\u7A7F\u7740\u4EF6\u8D34\u8EAB\u5C0F\u8902\uFF0C\u5E73\u65E5\u91CC\u906E\u63A9\u5F97\u4E25\u5B9E\u7684\u8EAB\u5B50\u6B64\u65F6\u6BD5\u73B0\u73B2\u73D1\uFF0C\u96EA\u767D\u7684\u5927\u817F\u5766\u5728\u5916\u5934\uFF0C\u5FAE\u5FAE\u95EA\u52A8\u7684\u70DB\u5149\u7ED9\u51B7\u8273\u7684\u808C\u80A4\u9540\u4E0A\u4E86\u4E00\u5C42\u8089\u6B32\u7684\u5149\u8F89\u3002\u5979\u6BD5\u7ADF\u662F\u8FD9\u5C0F\u57CE\u50FB\u5730\u7F55\u6709\u7684\u7F8E\u4EBA\uFF0C\u4E00\u59FF\u4E00\u5F0F\uFF0C\u4E00\u98A6\u4E00\u7B11\u7686\u64A9\u4EBA\u4E4B\u81F3\u3002</p><p>\u5218\u6EA2\u4E4B\u770B\u5F97\u5446\u4E86\uFF0C\u8EAB\u4F53\u7A81\u7136\u6CE8\u5165\u4E86\u4E00\u80A1\u70ED\u6D41\uFF0C\u7ED9\u51B7\u5982\u971C\u9644\u8033\u8BF4\u4E00\u53E5\u8BDD\uFF0C\u51B7\u5982\u971C\u7EA2\u4E86\u8138\uFF0C\u5550\u4E86\u4E00\u53E3\uFF0C\u4F4E\u4E0B\u5934\u53BB\uFF0C\u5A07\u7F9E\u65E0\u9650\u3002</p><p>\u70DB\u706B\u5F04\u5C0F\u4E86\u4E00\u70B9\u513F\uFF0C\u5218\u6EA2\u4E4B\u4FEF\u8EAB\u8F7B\u543B\u5979\u7684\u9999\u816E\uFF0C\u4E00\u624B\u6E29\u67D4\u5730\u64A9\u5F00\u51B7\u5982\u971C\u7684\u8863\u88F3\uFF0C\u5728\u6E05\u51C9\u6ED1\u817B\u7684\u808C\u80A4\u4E0A\u629A\u6478\u7740\u3002</p><p>\u5C0F\u8863\u65E0\u58F0\u6ED1\u843D\uFF0C\u9165\u4E73\u5766\u9732\u4E86\u51FA\u6765\uFF0C\u7EC6\u817B\u7684\u808C\u80A4\u84E6\u7136\u5E03\u6EE1\u4E86\u4E00\u5C42\u5C0F\u5C0F\u7684\u7599\u7629\uFF0C\u7C89\u7EA2\u7684\u5C0F\u5976\u5934\u5FAE\u5FAE\u98A4\u52A8\u3002</p><p>\u7537\u4EBA\u52A8\u8D77\u60C5\u6765\u4E00\u53D1\u4E0D\u53EF\u6536\u62FE\uFF0C\u5598\u606F\u58F0\u8D8A\u6765\u8D8A\u5927\uFF0C\u624B\u52B2\u4E5F\u8D8A\u6765\u8D8A\u8DB3\uFF0C\u74F7\u767D\u5C0F\u5DE7\u7684\u5976\u5B50\u5728\u5927\u624B\u7684\u634F\u5F04\u4E0B\u53D8\u4E86\u5F62\u72B6\uFF0C\u4E00\u6761\u6761\u96EA\u767D\u7684\u4E73\u8089\u4ECE\u6307\u7F1D\u4E2D\u9F13\u4E86\u51FA\u6765\u3002</p><p>\u300C\u5662\u2026\u2026\u300D\u51B7\u5982\u971C\u60C5\u4E0D\u81EA\u7981\u5730\u5624\u549B\u4E86\u58F0\uFF0C\u5979\u672C\u975E\u60C5\u6B32\u65FA\u76DB\uFF0C\u5C31\u7B97\u6210\u5A5A\u4E00\u5E74\u6709\u5947\uFF0C\u5BF9\u6B64\u592B\u5987\u4EBA\u4F26\u4E4B\u4E8B\u4F9D\u7136\u7F9E\u6DA9\uFF0C\u6B64\u65F6\u661F\u7738\u5FAE\u95ED\uFF0C\u5728\u592B\u541B\u6267\u7740\u7684\u7231\u629A\u4E0B\uFF0C\u8EAB\u4F53\u4E5F\u6E10\u6E10\u9165\u9EBB\u3002</p><p>\u5979\u523B\u610F\u627F\u6B22\uFF0C\u5C06\u5E73\u65E5\u91CC\u505A\u4E0D\u6765\u7684\u4E00\u4E9B\u513F\u5973\u60C5\u6001\u4E5F\u62FF\u4E86\u51FA\u6765\uFF0C\u67D4\u8F6F\u5982\u6C34\uFF0C\u5728\u7537\u4EBA\u7684\u8EAB\u4F53\u4E0A\u8F7B\u8F7B\u6ED1\u8FC7\u3002</p><p>\u60C5\u8FF7\u610F\u4E71\u4E4B\u4E2D\uFF0C\u51B7\u5982\u971C\u53EA\u6765\u5F97\u53CA\u8F7B\u58F0\u63D0\u9192\u4E00\u53E5\uFF0C\u300C\u53EF\u522B\u538B\u574F\u4E86\u3002\u300D</p><p>\u5927\u98CE\u8FC7\u6765\u4E86\uFF0C\u9634\u4E91\u56DB\u5408\u3002</p><p>\u5C4B\u5185\u7684\u4E91\u96E8\u6E10\u6536\u3002\u51B7\u5982\u971C\u53EA\u7CFB\u7740\u4E00\u4E2A\u5C0F\u809A\u515C\uFF0C\u4F38\u51FA\u85D5\u81C2\u73AF\u62B1\u7740\u5218\u6EA2\u4E4B\u7684\u8170\uFF0C\u61D2\u61D2\u5730\u4F0F\u5728\u592B\u541B\u7684\u6000\u4E2D\uFF0C\u5FFD\u7136\u8BF4\uFF0C\u300C\u4F60\u628A\u6D77\u68E0\u653E\u4E86\u5427\u3002\u300D</p><p>\u5979\u539F\u4EE5\u4E3A\u5218\u6EA2\u4E4B\u4F1A\u52C3\u7136\u5927\u6012\uFF0C\u6CA1\u60F3\u5230\u4ED6\u53EA\u8F7B\u8F7B\u53F9\u4E86\u53E3\u6C14\uFF0C\u8BF4\uFF0C\u300C\u662F\u554A\uFF0C\u5F53\u521D\u53EF\u80FD\u771F\u4E0D\u8BE5\u6293\u5979\uFF0C\u6211\u611F\u89C9\u662F\u4E0A\u4E86\u767D\u5929\u5FB7\u90A3\u65E0\u8D56\u7684\u5F53\u3002\u300D</p><p>\u4ED6\u628A\u5EB7\u8001\u7237\u5B50\u7684\u4E8B\u60C5\u8BF4\u4E86\u4E00\u904D\uFF0C\u5F52\u7EB3\u9053\uFF0C\u300C\u4E8B\u5B9E\u4E0A\uFF0C\u767D\u5929\u5FB7\u901A\u8FC7\u8FD9\u4E9B\u624B\u6BB5\uFF0C\u94F2\u9664\u4E86\u5BF9\u4ED6\u4E0D\u5229\u7684\u5206\u5B50\uFF0C\u73B0\u5728\u53D8\u6210\u4E86\u6C85\u9547\u4E00\u9738\uFF0C\u65E0\u6CD5\u65E0\u5929\uFF0C\u8D8A\u6765\u8D8A\u6CA1\u628A\u6211\u8FD9\u4E2A\u53BF\u957F\u653E\u5728\u773C\u91CC\u4E86\u3002\u300D</p><p>\u51B7\u5982\u971C\u72B9\u8C6B\u4E86\u7247\u523B\uFF0C\u54AC\u4E86\u54AC\u8D1D\u9F7F\uFF0C\u8BF4\uFF0C\u300C\u6EA2\u4E4B\uFF0C\u6709\u4EF6\u4E8B\u6211\u4E0D\u77E5\u9053\u5F53\u4E0D\u5F53\u544A\u8BC9\u4F60\u3002\u300D</p><p>\u300C\u6211\u4EEC\u592B\u59BB\u8FD8\u6709\u4F55\u8BDD\u4E0D\u80FD\u660E\u8A00\uFF1F\u300D</p><p>\u300C\u4E0A\u6B21\u6211\u770B\u5230\u4E00\u4E2A\u4EBA\uFF0C\u5728\u767D\u5929\u5FB7\u7684\u624B\u4E0B\u5F53\u4E86\u4E2D\u961F\u957F\u3002\u300D</p><p>\u300C\u4F60\u662F\u8BF4\u7684\u738B\u559C\u5427\uFF0C\u53EA\u6709\u4ED6\u662F\u65B0\u6765\u7684\uFF0C\u767D\u5929\u5FB7\u5F15\u8350\u7684\u3002\u300D</p><p>\u51B7\u5982\u971C\u6D41\u4E0B\u6CEA\u6765\uFF0C\u300C\u4ED6\u7684\u6D51\u540D\u662F\u4E8C\u559C\u5B50\uFF0C\u539F\u672C\u5728\u6D77\u68E0\u90A3\u91CC\uFF0C\u4ED6\uFF0C\u4ED6\u5C31\u662F\u51CC\u8FB1\u6211\u7684\u4EBA\u3002\u300D</p><p>\u5218\u6EA2\u4E4B\u7684\u8EAB\u4F53\u7ACB\u523B\u50F5\u786C\u4E86\uFF0C\u534A\u664C\uFF0C\u4E00\u8A00\u4E0D\u53D1\u5730\u62AB\u8863\u8D77\u5E8A\uFF0C\u5F80\u95E8\u5916\u8D70\u3002</p><p>\u51B7\u5982\u971C\u542B\u6CEA\u9053\uFF0C\u300C\u4F60\u53BB\u54EA\u91CC\uFF1F\u300D</p><p>\u300C\u6211\u53BB\u6536\u62FE\u90A3\u4E24\u4E2A\u755C\u751F\u3002\u300D</p><p>\u5C4B\u5916\u9739\u96F3\u4E00\u58F0\u3002</p><p>\u66B4\u98CE\u631F\u5E26\u9AA4\u96E8\u679C\u7136\u5982\u671F\u800C\u81F3\uFF0C\u4ECE\u9AD8\u5929\u4E0A\u7838\u4E86\u4E0B\u6765\u3002</p><p>\u51B7\u5982\u971C\u5403\u4E86\u4E00\u60CA\uFF0C\u722C\u8D77\u6765\u770B\u7740\u7A97\u5916\uFF0C\u95EA\u7535\u63A0\u8FC7\uFF0C\u5979\u7684\u8138\u8272\u4E5F\u662F\u5237\u767D\u3002</p><p>\u4E00\u8FDE\u4E32\u91CD\u5927\u7684\u53D8\u6545\u6B63\u5728\u66B4\u96E8\u7684\u63A9\u76D6\u4E0B\u7D27\u9523\u5BC6\u9F13\u5730\u8FDB\u884C\u7740\u3002</p><p>\u5218\u6EA2\u4E4B\u8FDE\u591C\u53EC\u96C6\u6765\u53F8\u9A6C\u5357\u5546\u91CF\u5BF9\u7B56\u3002</p><p>\u767D\u5929\u5FB7\u7684\u4FDD\u5B89\u56E2\u4E0B\u8F96\u4E09\u4E2A\u4E2D\u961F\uFF0C\u4E00\u4E2D\u961F\u961F\u957F\u7531\u526F\u56E2\u957F\u674E\u8D35\u517C\u4EFB\uFF0C\u662F\u767D\u5929\u5FB7\u7684\u5FC3\u8179\uFF0C\u662F\u6253\u51FB\u571F\u532A\u7684\u4E3B\u529B\u519B\uFF0C\u6218\u6597\u529B\u6700\u5F3A\uFF0C\u4E8C\u4E2D\u961F\u961F\u957F\u7531\u53F8\u9A6C\u5357\u517C\u4EFB\uFF0C\u4E00\u822C\u7528\u6765\u4FDD\u536B\u9547\u653F\u5E9C\uFF0C\u9632\u5B88\u6C85\u9547\uFF0C\u4E09\u4E2D\u961F\u662F\u5728\u767D\u5929\u5FB7\u624B\u4E2D\u65B0\u6210\u7ACB\u7684\uFF0C\u4F5C\u7528\u4E5F\u4E0D\u660E\u663E\uFF0C\u4EE5\u5E72\u6742\u6D3B\u4E3A\u4E3B\uFF0C\u4E2D\u961F\u957F\u5C31\u662F\u738B\u559C\u3002</p><p>\u8981\u6293\u6355\u767D\u5929\u5FB7\u4E0E\u738B\u559C\uFF0C\u4E8C\u4E2D\u961F\u5C31\u4F1A\u8981\u548C\u53E6\u5916\u4E24\u4E2A\u4E2D\u961F\u706B\u62DA\uFF0C\u6B63\u9762\u51B2\u7A81\u51F6\u591A\u5409\u5C11\uFF0C\u53EA\u80FD\u7A81\u88AD\u65B9\u6709\u80DC\u7B97\u3002</p><p>\u53F8\u9A6C\u5357\u4E3B\u5F20\u5229\u7528\u4E09\u4E2A\u4E2D\u961F\u4E0D\u5728\u4E00\u8D77\u9A7B\u9632\u7684\u7279\u70B9\uFF0C\u6253\u86C7\u6253\u4E03\u5BF8\uFF0C\u653E\u5F03\u738B\u559C\uFF0C\u96C6\u4E2D\u529B\u91CF\u5168\u529B\u8FDB\u653B\u4E00\u4E2D\u961F\uFF0C\u6293\u6355\u767D\u5929\u5FB7\u3002</p><p>\u5218\u6EA2\u4E4B\u4E0D\u540C\u610F\uFF0C\u90A3\u6837\u7684\u8BDD\u4E8C\u559C\u5B50\u5C31\u4F1A\u8DD1\u4E86\uFF0C\u5728\u4E24\u8005\u4E4B\u95F4\u9009\u62E9\u4ED6\u751A\u81F3\u503E\u5411\u4E8E\u5148\u5BF9\u4ED8\u738B\u559C\u3002</p><p>\u53F8\u9A6C\u5357\u89C9\u5F97\u6709\u70B9\u83AB\u540D\u5176\u5999\uFF0C\u4E0D\u660E\u5176\u6545\uFF0C\u53EA\u597D\u4FEE\u6539\u539F\u8BA1\u5212\uFF0C\u540C\u65F6\u51FA\u51FB\u3002</p><p>\u4E8C\u4E2D\u961F\u7684\u5B98\u5175\u5728\u7761\u68A6\u4E2D\u7D27\u6025\u96C6\u5408\uFF0C\u9876\u7740\u5927\u96E8\u5206\u4E24\u8DEF\u51FA\u51FB\u3002</p><p>\u540C\u4E00\u65F6\u523B\uFF0C\u767D\u5929\u5FB7\u6402\u7740\u4E03\u59E8\u592A\u8D64\u6761\u6761\u5730\u8EBA\u5728\u70DF\u69BB\u4E0A\uFF0C\u62BD\u4E0A\u4E86\u4E00\u7BA1\u5927\u70DF\uFF0C\u624B\u6307\u5728\u4E03\u59E8\u592A\u9634\u6BDB\u6D53\u5BC6\u6C41\u6C34\u4E30\u539A\u7684\u9634\u6237\u91CC\u62A0\u5F04\u7740\u3002</p><p>\u540C\u6837\u8D64\u88F8\u7684\u94F6\u53F6\u6E29\u987A\u5730\u8DEA\u5728\u69BB\u4E0B\uFF0C\u4F38\u51FA\u820C\u5934\u4E00\u6839\u6839\u8214\u7740\u4ED6\u81ED\u54C4\u54C4\u7684\u811A\u8DBE\u3002</p><p>\u5F53\u6E29\u8F6F\u7684\u820C\u5C16\u626B\u8FC7\u811A\u8DBE\u7F1D\uFF0C\u767D\u5929\u5FB7\u8212\u670D\u5F97\u7787\u4E0A\u4E86\u773C\u775B\u3002</p><p>\u4ED6\u7684\u624B\u52A8\u5F97\u8D8A\u6765\u8D8A\u6162\uFF0C\u94F6\u53F6\u7684\u773C\u775B\u4E5F\u8D8A\u6765\u8D8A\u4EAE\u3002</p><p>\u540C\u4E00\u65F6\u523B\uFF0C\u4E8C\u559C\u5B50\u6B63\u6CE1\u5728\u5993\u5BE8\u91CC\uFF0C\u4ED6\u662F\u8FD9\u91CC\u7684\u719F\u5BA2\u4E86\uFF0C\u4E5F\u662F\u5929\u9999\u697C\u6700\u8BA8\u538C\u53C8\u4E0D\u597D\u5F97\u7F6A\u7684\u5BA2\u4EBA\uFF0C\u8C01\u4E5F\u4E0D\u613F\u610F\u63A5\u4ED6\uFF0C\u8001\u9E28\u6D2A\u59E8\u88AB\u7F20\u5F97\u6CA1\u6CD5\uFF0C\u53EA\u597D\u5C06\u65B0\u6536\u6765\u8FD8\u6CA1\u6765\u5F97\u53CA\u8C03\u6559\u7684\u4E00\u4E2A\u7A1A\u5993\u63A8\u7ED9\u4E86\u4ED6\uFF0C\u4E8C\u559C\u5B50\u4E00\u770B\u5C31\u4E24\u773C\u53D1\u5149\uFF0C\u56E0\u4E3A\u8FD9\u5973\u5B69\u5B50\u9762\u5BB9\u6709\u4E9B\u795E\u4F3C\u51B7\u5982\u971C\uFF0C\u95EE\u5979\u7684\u540D\u5B57\u4E0D\u80AF\u8BF4\uFF0C\u4FBF\u53D8\u7740\u6CD5\u513F\u5730\u5F04\u5979\uFF0C\u6298\u817E\u4E86\u5927\u534A\u4E2A\u65F6\u8FB0\uFF0C\u5973\u5B69\u65E9\u5C31\u53D7\u4E0D\u4E86\uFF0C\u5509\u54DF\u5509\u54DF\u5730\u53EB\u5524\uFF0C\u6D41\u7740\u6CEA\u8BF4\u5979\u53EB\u963F\u6708\u3002</p><p>\u540C\u4E00\u65F6\u523B\uFF0C\u4E24\u4E2A\u56E2\u4E01\u563B\u563B\u54C8\u54C8\u5730\u524D\u53BB\u5730\u7262\u63A5\u73ED\u3002\u4ECE\u767D\u5BB6\u5927\u9662\u56DE\u6765\u540E\uFF0C\u6D77\u68E0\u4E00\u822C\u5C31\u5173\u62BC\u5728\u4FDD\u5B89\u56E2\u7684\u5730\u7262\u4E2D\uFF0C\u4E13\u5C5E\u4ED6\u4E2A\u4EBA\u6240\u6709\uFF0C\u4E0D\u8BA9\u522B\u4EBA\u6DEB\u8FB1\u3002</p><p>\u8F6E\u6D41\u503C\u5B88\u8FD9\u4E48\u4E00\u4F4D\u5982\u82B1\u4F3C\u7389\u7684\u5927\u7F8E\u5973\u5374\u5403\u4E0D\u5230\u5634\uFF0C\u56E2\u4E01\u4EEC\u4E0D\u514D\u7262\u9A9A\u6EE1\u8179\uFF0C\u597D\u5728\u6BCF\u5929\u5206\u53D1\u70DF\u571F\u7684\u6743\u5229\u5728\u4ED6\u4EEC\u624B\u91CC\uFF0C\u5C31\u5229\u7528\u8FD9\u4E48\u4E00\u4E2A\u673A\u4F1A\u6765\u8981\u631F\u90A3\u4E2A\u53EF\u601C\u7684\u5973\u532A\u505A\u51FA\u8BF8\u591A\u4E0D\u6781\u5EA6\u4E0D\u582A\u7684\u52A8\u4F5C\uFF0C\u610F\u6DEB\u4E2A\u591F\u65B9\u624D\u5F97\u5230\u6EE1\u8DB3\uFF0C\u8FD9\u4E5F\u7B97\u67AF\u71E5\u7684\u7262\u72F1\u751F\u6D3B\u4E2D\u4E00\u70B9\u5C0F\u4E50\u8DA3\u4E86\u3002</p><p>\u8FD9\u4E24\u4E2A\u5BB6\u4F19\u559D\u4E86\u4E00\u70B9\u9EC4\u9152\uFF0C\u9189\u610F\u6726\u80E7\uFF0C\u6B63\u597D\u5230\u90A3\u4E2A\u5973\u4EBA\u7684\u8EAB\u4F53\u4E0A\u53D1\u6CC4\u53D1\u6CC4\u3002</p><p>\u5730\u7262\u91CC\u563B\u563B\u54C8\u54C8\u55A7\u95F9\u4E0D\u5DF2\uFF0C\u53EA\u770B\u89C1\u4E00\u4E2A\u5927\u767D\u5C41\u80A1\u9AD8\u9AD8\u4E3E\u8D77\u6324\u5728\u7262\u95E8\u94C1\u680F\u7684\u5939\u7F1D\u4E2D\uFF0C\u7C97\u5927\u7684\u7EA2\u70DB\u6345\u5728\u5C41\u773C\u91CC\uFF0C\u71C3\u878D\u7684\u70DB\u6CEA\u5DF2\u91CD\u91CD\u76D6\u4F4F\u4E86\u809B\u95E8\u5468\u56F4\u7684\u808C\u80A4\uFF0C\u5C41\u80A1\u4E0D\u505C\u5730\u98A4\u52A8\uFF0C\u706B\u82D7\u4E5F\u968F\u4E4B\u6446\u52A8\u4E0D\u5B9A\u3002\u56DB\u4E94\u4E2A\u8863\u88F3\u4E0D\u6574\u7684\u5BB6\u4F19\u5708\u817F\u5750\u5728\u7262\u95E8\u5916\u4E00\u4FA7\u5C31\u7740\u70DB\u5149\u73A9\u724C\u4E5D\uFF0C\u5406\u4E94\u559D\u516D\u7684\u6B63\u8D77\u52B2\u3002</p><p>\u5730\u4E0A\u6CA1\u4E00\u4E2A\u94DC\u677F\uFF0C\u51E0\u4E2A\u5BB6\u4F19\u5374\u73A9\u5F97\u975E\u5E38\u8BA4\u771F\u3002</p><p>\u4E24\u4E2A\u9189\u9B3C\u563B\u7B11\u7740\u52A0\u5165\u8FDB\u53BB\uFF0C\u95EE\u9053\uFF0C\u300C\u4ECA\u5929\u7684\u89C4\uFF0C\u89C4\u77E9\u662F\u4EC0\u4E48\uFF1F\u300D</p><p>\u300C\u8F93\u5BB6\u559D\u90A3\u6BCD\u72D7\u7684\u903C\u6C34\uFF0C\u6BCD\u72D7\u559D\u8D62\u5BB6\u7684\u5C3F\u3002\u300D</p><p>\u9189\u9B3C\u4EEC\u5F80\u7262\u623F\u770B\u53BB\uFF0C\u65B9\u660E\u767D\u4E3A\u4EC0\u4E48\u5C41\u80A1\u4F1A\u98A4\u52A8\u4E2A\u6CA1\u5B8C\uFF0C\u539F\u6765\u662F\u6D77\u68E0\u7684\u4E00\u53EA\u624B\u5728\u4F38\u5411\u81EA\u5DF1\u7684\u9634\u6237\u4E2D\u62DA\u547D\u5730\u6405\u52A8\uFF0C\u523A\u6FC0\u51FA\u6DEB\u6C41\u6765\uFF0C\u4E00\u6EF4\u6EF4\u5730\u6EF4\u843D\u5230\u80EF\u4E0B\u4E00\u53EA\u74F7\u7897\u4E2D\uFF0C\u7897\u5185\u5DF2\u6709\u5C0F\u534A\u7897\u7C73\u6C64\u6C34\u4E00\u822C\u7684\u6C41\u6C34\u4E86\u3002</p><p>\u9189\u9B3C\u7532\u563B\u563B\u7B11\u9053\uFF0C\u300C\u6362\u4E86\u6211\uFF0C\u5B81\u613F\uFF0C\u8F93\u3002\u300D</p><p>\u6B63\u8BF4\u8BDD\u95F4\uFF0C\u7A81\u7136\u5916\u9762\u542C\u5230\u67AA\u58F0\uFF0C\u597D\u50CF\u6765\u81EA\u4E09\u4E2D\u961F\u7684\u9A7B\u9632\u5904\uFF0C\u8FD8\u633A\u6FC0\u70C8\uFF0C\u5C4B\u91CC\u6240\u6709\u4EBA\u90FD\u6761\u4EF6\u53CD\u5C04\u5730\u6293\u8D77\u67AA\u5F80\u95E8\u5916\u51B2\u53BB\u3002</p><p>\u5F88\u5FEB\u674E\u8D35\u4E5F\u8FC7\u6765\u4E86\uFF0C\u5306\u5306\u53EB\u9053\uFF0C\u300C\u5F1F\u5144\u4EEC\u5FEB\u968F\u6211\u8D70\uFF0C\u6709\u53DB\u4E71\u3002\u300D\u4ED6\u770B\u770B\u4E24\u4E2A\u9189\u9B3C\uFF0C\u76B1\u7709\u9053\uFF0C\u300C\u4F60\u4EEC\u4E24\u4E2A\u7559\u4E0B\u5B88\u7262\uFF0C\u95E8\u7A97\u7D27\u95ED\uFF0C\u5C0F\u5FC3\u9632\u7740\u3002\u300D</p><p>\u4E00\u961F\u4EBA\u9A6C\u5728\u5927\u96E8\u4E2D\u6025\u5306\u5306\u79BB\u53BB\u3002</p><p>\u9189\u9B3C\u4E59\u9053\uFF0C\u300C\u51FA\uFF0C\u51FA\u5927\u4E8B\u4E86\u3002\u300D</p><p>\u9189\u9B3C\u7532\u70B9\u5934\u9053\uFF0C\u300C\u4E00\u70B9\u4E0D\u9519\u3002\u300D</p><p>\u4E24\u4EBA\u7AD9\u5728\u95E8\u53E3\u5927\u53D1\u611F\u53F9\uFF0C\u65E9\u628A\u674E\u8D35\u7684\u5429\u5490\u4E22\u5230\u4E86\u4E5D\u9704\u4E91\u5916\uFF0C\u53EA\u542C\u5F97\u556A\u556A\u4E24\u58F0\u67AA\u54CD\uFF0C\u4E00\u9F50\u505A\u4E86\u80E1\u6D82\u4E4B\u9B3C\u3002</p><p>\u4E00\u4E2A\u5934\u7F20\u767D\u5E03\u5DFE\u7684\u6C49\u5B50\u4ECE\u9ED1\u6697\u4E2D\u8DF3\u4E86\u51FA\u6765\uFF0C\u5728\u6B7B\u4EBA\u8EAB\u4E0A\u6478\u5230\u94A5\u5319\uFF0C\u62A2\u8FDB\u7262\u623F\u4E2D\u3002</p><p>\u6D77\u68E0\u5E76\u4E0D\u5173\u5FC3\u5916\u9762\u53D1\u751F\u4E86\u4EC0\u4E48\u4E8B\uFF0C\u8FD8\u5728\u4E00\u4E2A\u52B2\u5730\u81EA\u6E0E\u7740\u3002</p><p>\u300C\u68E0\u59D0\uFF01\u300D\u770B\u5230\u8FD9\u526F\u5149\u666F\uFF0C\u6C49\u5B50\u5927\u558A\u4E00\u58F0\uFF0C\u809D\u80C6\u7686\u788E\uFF0C\u864E\u76EE\u542B\u6CEA\u3002</p><p>\u6D77\u68E0\u505C\u4E86\u4E0B\u6765\uFF0C\u6CA1\u6709\u8F6C\u8EAB\u3002</p><p>\u6C49\u5B50\u51B2\u5230\u7262\u95E8\u524D\uFF0C\u4E00\u628A\u5C06\u7EA2\u70DB\u62BD\u6389\uFF0C\u8FDC\u8FDC\u6254\u5728\u89D2\u843D\uFF0C\u300C\u68E0\u59D0\uFF0C\u6211\u662F\u5510\u725B\uFF0C\u963F\u725B\u5440\uFF0C\u6211\u6765\u6551\u4F60\u6765\u4E86\uFF0C\u770B\u770B\u6211\u5440\uFF01\u300D</p><p>\u6D77\u68E0\u5C06\u8EAB\u5B50\u5377\u7F29\u8D77\u6765\uFF0C\u597D\u50CF\u975E\u5E38\u5BD2\u51B7\uFF0C\u8138\u6DF1\u6DF1\u5730\u57CB\u5728\u9634\u6697\u5904\u3002</p><p>\u5510\u725B\u6025\u4E86\uFF0C\u5C06\u7262\u95E8\u7684\u94C1\u94FE\u54D7\u62C9\u62C9\u6253\u5F00\uFF0C\u4E5F\u987E\u4E0D\u5F97\u7F9E\u803B\uFF0C\u8FDB\u53BB\u626F\u6D77\u68E0\u5149\u88F8\u7684\u7389\u81C2\uFF0C\u300C\u6CA1\u65F6\u95F4\u4E86\uFF0C\u5FEB\u968F\u6211\u8D70\u5427\u3002\u300D</p><p>\u6447\u64BC\u4E86\u8BB8\u4E45\uFF0C\u6D77\u68E0\u65B9\u624D\u62AC\u8D77\u5934\u6765\uFF0C\u5979\u8FD8\u662F\u90A3\u4E48\u7F8E\u4E3D\uFF0C\u4F46\u6194\u60B4\u4E86\u8BB8\u591A\uFF0C\u6574\u4E2A\u8138\u90FD\u5C16\u4E86\uFF0C\u773C\u795E\u6697\u6DE1\u65E0\u5149\u3002</p><p>\u5979\u770B\u4E86\u963F\u725B\u4E00\u773C\uFF0C\u53C8\u4F4E\u4E0B\u5934\u53BB\uFF0C\u300C\u4F60\u8D70\u5427\uFF0C\u6211\u4E0D\u4F1A\u8D70\u7684\u3002\u300D</p><p>\u5510\u725B\u96BE\u4EE5\u7F6E\u4FE1\uFF0C\u300C\u4F60\u5728\u8BF4\u4EC0\u4E48\uFF1F\u6211\u9003\u51FA\u767D\u5929\u5FB7\u7684\u56F4\u527F\uFF0C\u53C8\u5BFB\u627E\u4F60\u7684\u4E0B\u843D\uFF0C\u5728\u8FD9\u91CC\u5B88\u4E86\u597D\u51E0\u5929\u627E\u673A\u4F1A\uFF0C\u54EA\u4E00\u4EF6\u4E8B\u4E0D\u662F\u5728\u63D0\u7740\u8111\u888B\uFF0C\u73B0\u5728\u673A\u4F1A\u6765\u4E86\uFF0C\u4F60\u4E0D\u80AF\u8D70\uFF0C\u662F\u4E0D\u662F\u8111\u5B50\u8BA9\u8FD9\u5E2E\u755C\u751F\u6253\u574F\u4E86\uFF1F\u300D</p><p>\u6D77\u68E0\u4F4E\u58F0\u8BF4\uFF0C\u300C\u4F60\u5C31\u5F53\u6211\u6B7B\u4E86\u5427\u3002\u300D</p><p>\u5510\u725B\u86EE\u52B2\u4E0A\u6765\u4E86\uFF0C\u9053\uFF0C\u300C\u4E0D\u884C\uFF0C\u4ECA\u5929\u6211\u600E\u4E48\u7740\u90FD\u8981\u628A\u4F60\u5F04\u51FA\u53BB\u3002\u300D</p><p>\u4ED6\u5C06\u6D77\u68E0\u7684\u4E00\u53EA\u624B\u81C2\u5F80\u81EA\u5DF1\u80A9\u4E0A\u4E00\u642D\uFF0C\u5F3A\u884C\u5C06\u6D77\u68E0\u534A\u6402\u534A\u80CC\u4ECE\u5730\u4E0A\u62D6\u8D77\u6765\u5F80\u95E8\u5916\u8D70\u3002</p><p>\u6D77\u68E0\u5E76\u4E0D\u5F88\u575A\u6301\uFF0C\u4E5F\u4E0D\u5F88\u60C5\u613F\uFF0C\u5C31\u8FD9\u6837\u522B\u522B\u626D\u626D\u5730\u51FA\u4E86\u95E8\u3002</p><p>\u5927\u96E8\u54D7\u5730\u6DCB\u4E86\u4E0B\u6765\uFF0C\u6D77\u68E0\u8D64\u88F8\u7684\u8EAB\u5B50\u8FDE\u6253\u51E0\u4E2A\u5BD2\u5664\uFF0C\u5728\u6CE5\u6CDE\u4E4B\u4E2D\uFF0C\u6B65\u4F10\u66F4\u6162\u4E86\u3002</p><p>\u521A\u624D\u5B89\u9759\u4E86\u4E00\u4F1A\u7684\u67AA\u58F0\u53C8\u54CD\u8D77\u6765\u4E86\uFF0C\u8FD9\u56DE\u662F\u5F80\u8FD9\u8FB9\u79FB\u8FD1\uFF0C\u4EBA\u58F0\u4E5F\u4ECE\u51E0\u4E2A\u65B9\u5411\u9F0E\u6CB8\u8D77\u6765\u3002</p><p>\u300C\u7AD9\u4F4F\uFF01\u300D\u300C\u6293\u4F4F\u4ED6\u4EEC\uFF01\u300D</p><p>\u5510\u725B\u505C\u4E0B\u6765\uFF0C\u8F6C\u8FC7\u8EAB\uFF0C\u53CC\u624B\u6367\u8D77\u6D77\u68E0\u7684\u8138\uFF0C\u6D41\u4E0B\u6CEA\u6765\uFF0C\u300C\u68E0\u59D0\uFF0C\u7B97\u6211\u5510\u725B\u6C42\u4F60\u4E86\uFF0C\u6D3B\u4E0B\u53BB\uFF0C\u4E3A\u6211\u548C\u9752\u7EA2\uFF0C\u4E3A\u6B7B\u53BB\u7684\u5144\u5F1F\u59D0\u59B9\u4EEC\u62A5\u4EC7\u3002\u300D</p><p>\u8BF4\u5427\uFF0C\u4E5F\u4E0D\u5F85\u56DE\u7B54\uFF0C\u62FF\u7740\u957F\u67AA\u8DD1\u5F00\u4E86\uFF0C\u8FC7\u4E86\u4E00\u4F1A\u51B2\u4EBA\u7FA4\u6253\u4E86\u4E00\u67AA\uFF0C\u7ACB\u523B\u6240\u6709\u7684\u4EBA\u67AA\u90FD\u671D\u7740\u4ED6\u7684\u65B9\u5411\u5C04\u51FB\u3002</p><p>\u5510\u725B\u8D8A\u8DD1\u8D8A\u8FDC\uFF0C\u4F46\u5305\u56F4\u5708\u4E5F\u8DDD\u79BB\u4ED6\u8D8A\u6765\u8D8A\u91CD\uFF0C\u53EA\u89C1\u4ED6\u8EAB\u4F53\u7A81\u7136\u4E00\u987F\uFF0C\u7D27\u63A5\u7740\u53C8\u662F\u4E00\u987F\uFF0C\u8EAB\u4E0A\u7EFD\u5F00\u7740\u4E00\u6735\u63A5\u4E00\u6735\u7684\u8840\u82B1\uFF0C\u53C8\u65CB\u5373\u88AB\u96E8\u6C34\u51B2\u5237\u4E2A\u5E72\u51C0\u3002</p><p>\u4E2D\u5F39\u5F77\u4F5B\u4E0E\u4ED6\u6CA1\u6709\u5173\u7CFB\uFF0C\u4ED6\u8FD8\u5728\u8DD1\uFF0C\u8DD1\u4E0D\u52A8\u4E86\u5C31\u8D70\uFF0C\u8D70\u4E0D\u52A8\u4E86\u5C31\u722C\uFF0C\u8A93\u6B7B\u4E0D\u505C\u2026\u2026</p><p>\u6D77\u68E0\u8FDC\u8FDC\u5730\u770B\u7740\uFF0C\u67AF\u7AED\u7684\u773C\u7736\u4E2D\u6D41\u51FA\u6CEA\u6765\u3002</p><p>\u5979\u8DD1\u8D77\u6765\uFF0C\u51B2\u7740\u5927\u5C71\u7684\u65B9\u5411\uFF0C\u8FC8\u5F00\u957F\u817F\u62DA\u547D\u8DD1\u8D77\u6765\u3002</p><p>\u66B4\u96E8\u65E0\u60C5\u5730\u8E42\u8E8F\u7740\u5927\u5730\uFF0C\u5C3D\u60C5\u5BA3\u6CC4\u4E0A\u5929\u7684\u6DEB\u5A01\uFF0C\u4F34\u968F\u7740\u6495\u5929\u88C2\u5730\u7684\u6012\u543C\uFF0C\u4E00\u9053\u9053\u95EA\u7535\u5982\u5229\u7BAD\u5288\u5F00\u4E86\u539A\u539A\u7684\u9634\u4E91\uFF0C\u534A\u8FB9\u5929\u7A7A\u5237\u5730\u53D8\u6210\u4E86\u60E8\u767D\u3002</p><p>\u5927\u7247\u5927\u7247\u7684\u77EE\u6811\u6797\u5728\u98CE\u96E8\u4E2D\u75AF\u72C2\u5730\u6643\u52A8\u7740\u679D\u53F6\u7E41\u8302\u7684\u8111\u888B\uFF0C\u6CE2\u6D6A\u822C\u4E00\u5708\u5708\u8361\u6F3E\u5F00\u6765\u3002</p><p>\u82CD\u7A79\u4E4B\u6012\uFF01</p><p>\u5929\u9645\u6700\u9ED1\u6697\u5904\u51FA\u73B0\u4E86\u4E00\u4E2A\u767D\u751F\u751F\u7684\u4EBA\u513F\uFF0C\u5728\u66B4\u96E8\u7684\u51B2\u5237\u4E0B\u52AA\u529B\u5411\u8FDC\u5904\u7684\u5C71\u6797\u5954\u53BB\u3002</p><p>\u8FDC\u65B9\u51E0\u58F0\u67AA\u54CD\uFF0C\u8FD8\u6709\u72D7\u5420\uFF0C\u8FFD\u6355\u7684\u4EBA\u4EEC\u8D8A\u903C\u8D8A\u8FD1\u3002</p><p>\u90A3\u4E2A\u4FEE\u957F\u800C\u524A\u7626\u7684\u8EAB\u5B50\u6709\u4E9B\u8E09\u8DC4\uFF0C\u4F46\u6CA1\u6709\u4E1D\u6BEB\u8FDF\u7591\u3002</p><p>\u6C85\u6C34\u6A2A\u4E98\u5728\u773C\u524D\uFF0C\u5973\u4EBA\u7AD9\u4F4F\u4E86\uFF0C\u56DE\u5934\u51B2\u7740\u654C\u4EBA\u53D1\u51FA\u6700\u6076\u6BD2\u7684\u8BC5\u5492\uFF0C\u8DC3\u5165\u6025\u6D41\u4E4B\u4E2D\u3002</p><p>\u6D77\u68E0\u8DD1\u4E86\u3002</p>',110);function t(i,c){return r}var o=a(e,[["render",t]]);export{o as default};
