import{e as p}from"./app.630af5f2.js";import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";const e={},r=p('<h1 id="\u7B2C\u5341\u7AE0-\u8C0B\u593A" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u5341\u7AE0-\u8C0B\u593A" aria-hidden="true">#</a> \u7B2C\u5341\u7AE0 \u8C0B\u593A</h1><p>\u4E00\u6643\u53C8\u662F\u6570\u6708\uFF0C\u5929\u6C14\u6E10\u70ED\u3002</p><p>\u5929\u4E0A\u6709\u6708\uFF0C\u591C\u8272\u6E05\u660E\u3002</p><p>\u57CE\u90CA\u5EB7\u5BB6\u82B1\u56ED\u662F\u5EB7\u8001\u7237\u5B50\u7684\u4E00\u5904\u522B\u9662\uFF0C\u6691\u70ED\u96BE\u5F53\u7684\u65F6\u8282\uFF0C\u4ED6\u4E00\u822C\u4F1A\u5E26\u7740\u6700\u5BA0\u7231\u7684\u59E8\u592A\u592A\u5230\u8FD9\u91CC\u6765\u907F\u6691\u3002</p><p>\u4ED6\u5012\u662F\u8001\u5F53\u76CA\u58EE\uFF0C\u521A\u51FA\u4E86\u8D9F\u8FDC\u95E8\uFF0C\u5E26\u56DE\u6765\u4E00\u4E2A\u5973\u5B66\u751F\uFF0C\u53EB\u963F\u6708\uFF0C\u521A\u5341\u56DB\u5C81\uFF0C\u6A21\u6837\u633A\u6E05\u7EAF\u7684\uFF0C\u6253\u7B97\u4ECA\u665A\u5C31\u5728\u5EB7\u5BB6\u82B1\u56ED\u5F00\u82DE\uFF0C\u4EE5\u540E\u6536\u4F5C\u516B\u59E8\u592A\u3002</p><p>\u5BF9\u4E03\u59E8\u592A\u51DD\u5170\u51FA\u8F68\u4E4B\u4E8B\u4ED6\u5176\u5B9E\u6709\u6240\u8033\u95FB\uFF0C\u4F46\u4E00\u5219\u6293\u4E0D\u5230\u771F\u51ED\u5B9E\u636E\uFF0C\u4E8C\u5219\u4E0D\u6562\u6B63\u9762\u5F97\u7F6A\u6C14\u7130\u6B63\u70BD\u7684\u767D\u5929\u5FB7\uFF0C\u9690\u5FCD\u4E86\u4E0B\u6765\uFF0C\u53EA\u662F\u52A0\u5F3A\u4E86\u5BF9\u4E03\u59E8\u592A\u7684\u9650\u5236\uFF0C\u4E0D\u518D\u5141\u8BB8\u5979\u4E0A\u70DF\u9986\uFF0C\u51FA\u95E8\u90FD\u6709\u4EBA\u76F8\u968F\u3002</p><p>\u5728\u5BB6\u4E2D\uFF0C\u4E03\u59E8\u592A\u7684\u5730\u4F4D\u4E5F\u660E\u663E\u4E0D\u5982\u4EE5\u524D\uFF0C\u5EB7\u8001\u7237\u5B50\u5BF9\u5979\u5931\u53BB\u4E86\u5BA0\u7231\uFF0C\u51C9\u5728\u4E00\u8FB9\uFF0C\u5F62\u540C\u6253\u5165\u51B7\u5BAB\u3002</p><p>\u963F\u6708\u7684\u51FA\u73B0\uFF0C\u660E\u663E\u662F\u4E00\u4E2A\u4FE1\u53F7\u3002</p><p>\u5F80\u5E74\u90FD\u662F\u4E03\u59E8\u592A\u5728\u5EB7\u5BB6\u82B1\u56ED\u4F3A\u5019\u5EB7\u8001\u7237\u5B50\uFF0C\u4ECA\u5E74\u5374\u662F\u4E00\u4EE3\u65B0\u4EBA\u6362\u65E7\u4EBA\uFF0C\u53EA\u843D\u5F97\u4E03\u59E8\u592A\u7A7A\u5B88\u5BB6\u4E2D\u5927\u53D1\u813E\u6C14\uFF0C\u4EC0\u7269\u90FD\u7838\u4E86\u4E2A\u7A00\u70C2\u3002\u5BB6\u4EBA\u4EEC\u65E9\u5C31\u770B\u4E0D\u60EF\u5979\u7684\u72D0\u5A9A\u4F5C\u98CE\uFF0C\u6697\u5730\u91CC\u90FD\u5E78\u707E\u4E50\u7978\uFF0C\u8FD9\u4E2A\u72D0\u72F8\u7CBE\u7EC8\u906D\u62A5\u5E94\u4E86\u3002</p><p>\u6B63\u503C\u4E8C\u66F4\uFF0C\u5EB7\u5BB6\u82B1\u56ED\u7684\u6B63\u623F\u5347\u8D77\u4E24\u76CF\u5927\u7EA2\u706F\u7B3C\u3002</p><p>\u4E00\u4E2A\u8001\u5988\u5B50\u62E5\u7740\u4E00\u4E2A\u8BA9\u7EC7\u9526\u4E1D\u7EF8\u88F9\u8D77\u6765\u7684\u5C11\u5973\u6CBF\u7740\u957F\u957F\u7684\u56DE\u5ECA\u5C0F\u788E\u6B65\u5F80\u524D\u8D70\u7740\u3002</p><p>\u5C11\u5973\u7684\u5934\u53D1\u633D\u4E86\u8D77\u6765\uFF0C\u9AD8\u9AD8\u5730\u76D8\u5728\u5934\u9876\uFF0C\u4E00\u53CC\u5C0F\u5C0F\u7684\u80A9\u8180\u88F8\u5728\u5916\u9762\uFF0C\u76AE\u80A4\u975E\u5E38\u7EC6\u5AE9\u5149\u6ED1\u3002</p><p>\u4E24\u4E2A\u5BB6\u4E01\u8FDC\u8FDC\u5730\u5077\u7AA5\uFF0C\u770B\u4E0D\u771F\u5207\uFF0C\u8FD8\u662F\u548B\u820C\u4E0D\u5DF2\uFF0C\u300C\u8001\u7237\u8FD8\u771F\u662F\u8273\u798F\u4E0D\u6D45\uFF0C\u53C8\u5230\u54EA\u91CC\u627E\u6765\u8FD9\u4E48\u5E74\u8F7B\u6F02\u4EAE\u7684\u599E\u513F\u3002\u300D</p><p>\u300C\u4F60\u6CE8\u610F\u4E86\u6CA1\u6709\uFF0C\u5979\u957F\u5F97\u6709\u70B9\u50CF\u5218\u53BF\u957F\u7684\u592A\u592A\u3002\u300D</p><p>\u300C\u54A6\uFF0C\u8FD8\u771F\u662F\uFF0C\u4E4D\u4E00\u770B\uFF0C\u8FD8\u771F\u6709\u70B9\u51E0\u5206\u795E\u4F3C\uFF0C\u53EA\u662F\u5E74\u8F7B\u4E86\u8BB8\u591A\u3002\u83AB\u4E0D\u662F\u54B1\u8001\u7237\u5B50\u5BF9\u5218\u592A\u592A\u4E5F\u6709\u610F\u601D\uFF1F\u300D</p><p>\u4E24\u4EBA\u7325\u4EB5\u5730\u76F8\u89C6\u800C\u7B11\uFF0C\u4E00\u4E2A\u53C8\u8BF4\uFF0C\u300C\u5176\u5B9E\u4E03\u59E8\u592A\u4E5F\u86EE\u6F02\u4EAE\u7684\u3002\u300D</p><p>\u300C\u4E03\u59E8\u592A\u554A\uFF0C\u5C31\u662F\u7A91\u59D0\u5473\u592A\u91CD\uFF0C\u4E0A\u6B21\u7ED9\u6211\u629B\u4E00\u4E2A\u5A9A\u773C\uFF0C\u54CE\u5440\uFF0C\u8001\u5B50\u5DEE\u70B9\u5C3F\u88E4\u5B50\u3002\u300D</p><p>\u300C\u522B\u8BF4\u4E86\uFF0C\u8001\u5B50\u53D7\u4E0D\u4E86\u5566\uFF0C\u5230\u540E\u9762\u53BB\u89E3\u51B3\u4E00\u4E0B\u3002\u300D</p><p>\u5BB6\u4E01\u7532\u8F6C\u5230\u5047\u5C71\u540E\u9762\uFF0C\u534A\u5929\u6CA1\u4E86\u52A8\u9759\u3002</p><p>\u5BB6\u4E01\u4E59\u53EB\u4E86\u53EB\uFF0C\u6CA1\u4EBA\u56DE\u7B54\uFF0C\u6B63\u60CA\u7591\u95F4\uFF0C\u80A9\u5934\u88AB\u8F7B\u62CD\u4E86\u62CD\uFF0C\u626D\u5934\u4E00\u770B\uFF0C\u7ADF\u662F\u4E00\u4E2A\u534A\u88F8\u7684\u9AD8\u5927\u7F8E\u5973\uFF0C\u5168\u8EAB\u5C31\u662F\u8170\u95F4\u56F4\u4E86\u4E00\u5757\u7EB1\u5DFE\uFF0C\u79C1\u5BC6\u5904\u82E5\u9690\u82E5\u73B0\uFF0C\u4E00\u5BF9\u4E30\u6EE1\u633A\u62D4\u7684\u5976\u5B50\u5219\u50B2\u7136\u8892\u7ACB\u3002\u66F4\u8BE1\u5F02\u7684\u662F\u8FD9\u4E2A\u5973\u4EBA\u7684\u9F3B\u5B50\u4E2D\u592E\u8FD8\u6302\u7740\u4E00\u4E2A\u9EC4\u6F84\u6F84\u7684\u5C0F\u94DC\u73AF\u3002</p><p>\u5BB6\u4E01\u4E59\u5446\u5446\u5730\u770B\u7740\uFF0C\u9519\u4EE5\u4E3A\u662F\u89C1\u4E86\u9B3C\uFF0C\u6216\u8005\u72D0\u5973\u4E0B\u51E1\u3002</p><p>\u5973\u5B50\u7684\u4FCF\u8138\u4E0A\u6CA1\u6709\u4E00\u4E1D\u8868\u60C5\uFF0C\u51B7\u51B7\u8F7B\u8A00\u9053\uFF0C\u300C\u623F\u5B50\u91CC\u8FD8\u6709\u6CA1\u6709\u5B88\u536B\uFF1F\u300D</p><p>\u5BB6\u4E01\u4E59\u76EE\u5149\u5446\u6EDE\uFF0C\u76EF\u7740\u5973\u4EBA\u7684\u5976\u5B50\uFF0C\u4E00\u53E5\u8BDD\u4E5F\u8BF4\u4E0D\u4E0A\u6765\u3002</p><p>\u88F8\u5973\u53C8\u6C14\u53C8\u6025\uFF0C\u4E00\u638C\u780D\u5728\u4ED6\u7684\u9888\u52A8\u8109\u5904\uFF0C\u5BB6\u4E01\u4E59\u4E0D\u58F0\u4E0D\u54CD\u5730\u762B\u5012\u5728\u5730\uFF0C\u4E0D\u6B7B\u4E5F\u6B8B\u4E86\u3002</p><p>\u88F8\u5973\u5C06\u5927\u95E8\u7684\u95E8\u6760\u62AC\u8D77\uFF0C\u653E\u5230\u4E00\u8FB9\uFF0C\u5927\u95E8\u4FBF\u5F62\u540C\u865A\u63A9\u3002\u7136\u540E\u8EAB\u5B50\u4E00\u626D\uFF0C\u62D4\u5730\u800C\u8D77\uFF0C\u501F\u52A9\u77EE\u6A90\u96D5\u680F\u7684\u7A81\u51FA\u7269\uFF0C\u51E0\u4E0B\u6500\u722C\u5C31\u8D8A\u8FC7\u4E86\u969C\u788D\u7269\uFF0C\u767D\u751F\u751F\u7684\u8EAB\u5B50\u6D88\u5931\u5728\u591C\u8272\u4E4B\u4E2D\uFF0C\u8EAB\u624B\u8FC5\u6377\u4E4B\u6781\u3002</p><p>\u8001\u5988\u5B50\u8D70\u5230\u6B63\u5802\uFF0C\u8F7B\u8F7B\u53E9\u95E8\uFF0C\u300C\u8001\u7237\uFF0C\u65B0\u4EBA\u6765\u4E86\u3002\u300D</p><p>\u300C\u8FDB\u6765\u5427\u3002\u300D</p><p>\u95E8\u5431\u5440\u5F00\u4E86\uFF0C\u5802\u5C4B\u5BF9\u9762\u6B63\u4E2D\u662F\u4E2A\u795E\u9F9B\uFF0C\u5E76\u6392\u4F9B\u7740\u5B54\u592B\u5B50\u548C\u8D22\u795E\u7237\uFF0C\u6B63\u53EF\u8BF4\u660E\u5EB7\u8001\u7237\u5B50\u7684\u4EA6\u5546\u4EA6\u5B66\u7684\u53CC\u91CD\u8EAB\u4EFD\u3002\u4E24\u5F20\u592A\u5E08\u6905\u5E76\u6392\u653E\u7740\uFF0C\u5EB7\u8001\u7237\u5B50\u5FAE\u95ED\u7740\u773C\uFF0C\u7626\u957F\u7684\u6307\u5C16\u4E0D\u505C\u5730\u637B\u7740\u51E0\u6839\u5C71\u7F8A\u80E1\u5B50\uFF0C\u6B63\u895F\u5371\u5750\u5728\u53F3\u8FB9\u7684\u6905\u5B50\u4E0A\u517B\u795E\u3002</p><p>\u8001\u5988\u5B50\u6276\u7740\u5C11\u5973\u5C0F\u5FC3\u5730\u8DE8\u8FC7\u9AD8\u95E8\u574E\uFF0C\u8D70\u5230\u5EB7\u8001\u7237\u524D\u9762\u3002</p><p>\u963F\u6708\u6CA1\u591A\u5C11\u65B0\u4EBA\u7684\u559C\u8272\uFF0C\u767D\u51C0\u7684\u8138\u4E0A\u5012\u4F3C\u6709\u51E0\u5206\u5FE7\u6101\uFF0C\u8EAB\u5B50\u5FAE\u5FAE\u6709\u70B9\u6296\u52A8\uFF0C\u5782\u7740\u773C\u7751\u4E0D\u6562\u770B\u4EBA\u3002</p><p>\u5EB7\u8001\u7237\u770B\u7740\u5C11\u5973\u82B1\u4E00\u822C\u7684\u8EAB\u4F53\uFF0C\u6DF7\u6D4A\u7684\u773C\u775B\u4E5F\u4EAE\u4E86\u8D77\u6765\uFF0C\u5BF9\u8001\u5988\u5B50\u6325\u6325\u624B\u8981\u5979\u9000\u4E0B\u3002</p><p>\u8001\u5988\u5B50\u63A9\u4E0A\u95E8\u8D70\u4E86\u3002</p><p>\u5EB7\u8001\u7237\u5B50\u5E72\u54B3\u4E86\u4E00\u4E0B\uFF0C\u67D4\u58F0\u9053\uFF0C\u300C\u4E0D\u8981\u6015\uFF0C\u6211\u5EB7\u5FC5\u8FBE\u5411\u6765\u96EA\u4E2D\u9001\u70AD\uFF0C\u4E0D\u4F1A\u4E58\u4EBA\u4E4B\u5371\uFF0C\u7ED9\u4F60\u5BB6\u6E21\u96BE\u7684\u90A3\u70B9\u94B1\u561B\u2026\u2026\u300D</p><p>\u50CF\u4E00\u9635\u98CE\u8D77\uFF0C\u5927\u95E8\u7A81\u7136\u6D1E\u5F00\u6765\u3002</p><p>\u5EB7\u8001\u7237\u5FAE\u5403\u4E00\u60CA\uFF0C\u53EB\u9053\uFF0C\u300C\u738B\u4E94\uFF0C\u674E\u56DB\uFF1F\u6D2A\u5988\uFF1F\u300D</p><p>\u65E0\u4EBA\u5E94\u7B54\u3002</p><p>\u5EB7\u8001\u7237\u53EA\u5F97\u81EA\u5DF1\u8D70\u5230\u95E8\u5916\u770B\u770B\uFF0C\u6708\u5149\u4E0B\uFF0C\u679D\u53F6\u6276\u758F\uFF0C\u54EA\u6709\u4E00\u70B9\u4EBA\u8FF9\uFF1F</p><p>\u4ED6\u6447\u6447\u5934\uFF0C\u521A\u628A\u95E8\u5408\u4E0A\uFF0C\u5374\u542C\u5230\u8EAB\u540E\u963F\u6708\u60CA\u547C\u4E00\u58F0\u3002\u5FD9\u626D\u5934\u770B\u65F6\uFF0C\u5374\u89C1\u5C4B\u91CC\u591A\u4E86\u4E00\u4F4D\u4E0D\u901F\u4E4B\u5BA2\uFF0C\u662F\u4E00\u4F4D\u7F8E\u8273\u7EDD\u4F26\u51E0\u4E4E\u5168\u88F8\u7684\u5973\u5B50\uFF0C\u624B\u4E0A\u867D\u65E0\u4EFB\u4F55\u6B66\u5668\uFF0C\u8EAB\u4E0A\u7684\u6740\u6C14\u8DB3\u4EE5\u4F7F\u4EFB\u4F55\u4EBA\u51B7\u6C57\u6D78\u51FA\u3002</p><p>\u300C\u4F60\u2026\u2026\u4F60\u2026\u2026\u4F60\u662F\u4F55\u4EBA\uFF1F\u300D</p><p>\u300C\u54C8\u54C8\u54C8\uFF0C\u5979\u5440\uFF0C\u53EF\u662F\u4F60\u662F\u8001\u76F8\u8BC6\u554A\u3002\u300D\u56DE\u7B54\u58F0\u5374\u6765\u81EA\u95E8\u5916\uFF0C\u5802\u5C4B\u6B63\u95E8\u5E94\u58F0\u800C\u5F00\uFF0C\u4E00\u4E2A\u767D\u886B\u767D\u88E4\uFF0C\u4E00\u8138\u75DE\u6C14\u7684\u58EE\u5E74\u7537\u4EBA\u6447\u7740\u7EB8\u6247\u8E31\u4E86\u8FDB\u6765\u3002</p><p>\u5EB7\u8001\u7237\u5B50\u60CA\u6012\u9053\uFF0C\u300C\u767D\u5929\u5FB7\uFF0C\u4F60\u5728\u641E\u4E48\u5B50\u9B3C\u3002\u300D</p><p>\u767D\u5929\u5FB7\u65BD\u65BD\u7136\u8D70\u5230\u592A\u5E08\u6905\u4E0A\u5750\u4E0B\uFF0C\u7FD8\u8D77\u4E00\u6761\u4E8C\u90CE\u817F\uFF0C\u4ECE\u8170\u540E\u6478\u51FA\u4E00\u628A\u9A73\u58F3\u67AA\u6446\u5728\u5C0F\u8336\u684C\u4E0A\u3002</p><p>\u88F8\u5973\u4E5F\u8FC5\u901F\u8F6C\u79FB\u5230\u95E8\u53E3\uFF0C\u5173\u4E0A\u95E8\uFF0C\u5C01\u4F4F\u4E86\u51FA\u53E3\u3002</p><p>\u767D\u5929\u5FB7\u7B11\u9053\uFF0C\u300C\u767D\u677F\u513F\uFF0C\u544A\u8BC9\u4ED6\uFF0C\u4F60\u4EE5\u524D\u662F\u4F55\u4EBA\u3002\u300D</p><p>\u88F8\u5973\u6F20\u7136\u5730\u4E00\u5B57\u4E00\u987F\u5730\u56DE\u7B54\uFF0C\u300C\u5974\u5148\u524D\u662F\u7FE0\u7AF9\u6D77\u5973\u532A\uFF0C\u4EBA\u79F0\u9ED1\u51E4\u51F0\u3002\u300D</p><p>\u5EB7\u8001\u7237\u5B50\u8840\u8272\u987F\u5931\uFF0C\u300C\u4F60\u5C31\u662F\u9ED1\u51E4\u51F0\uFF1F\u300D</p><p>\u6D77\u68E0\u88AB\u6355\u4E4B\u4E8B\u4E0D\u7B97\u592A\u79D8\u5BC6\uFF0C\u4F46\u767D\u5929\u5FB7\u4EE5\u8FFD\u95EE\u70DF\u571F\u4E3A\u7531\u5C06\u5979\u5BC6\u85CF\u8D77\u6765\uFF0C\u5C31\u7B97\u5EB7\u8001\u7237\u8FD9\u6837\u7684\u58EB\u7EC5\u4E5F\u672A\u7779\u5176\u771F\u4EBA\u3002\u4ED6\u5FC3\u4E2D\u6709\u9B3C\uFF0C\u4E00\u5FC3\u53EA\u6015\u9ED1\u51E4\u5C06\u81EA\u5DF1\u4F9B\u51FA\uFF0C\u5F53\u7136\u4E5F\u4E0D\u6562\u8C0B\u6C42\u4E0E\u672C\u4EBA\u6664\u9762\uFF0C\u53EA\u6697\u4E2D\u6253\u542C\u95EE\u8BAF\u7684\u7ED3\u679C\uFF0C\u5E76\u6084\u6084\u8F6C\u79FB\u4E86\u5927\u91CF\u7684\u6D6E\u8D22\uFF0C\u4ED6\u5C31\u662F\u5728\u8F6C\u79FB\u8D22\u4EA7\u7684\u65F6\u5019\u9047\u5230\u963F\u6708\u5E76\u4E58\u5979\u5BB6\u6709\u96BE\u4E4B\u9645\u4E70\u4E0B\u5979\u4F5C\u5C0F\u59BE\u7684\u3002\u540E\u6765\u6CA1\u6709\u542C\u5F97\u65B0\u7684\u6D88\u606F\uFF0C\u4E00\u9897\u5FC3\u65B9\u624D\u7A0D\u653E\u56DE\u4E86\u809A\u91CC\u3002</p><p>\u4E0D\u6599\u5728\u4ED6\u81EA\u5DF1\u5BB6\u4E2D\uFF0C\u7ADF\u5192\u51FA\u6765\u4E00\u4E2A\u81EA\u79F0\u9ED1\u51E4\u51F0\u7684\u5996\u8273\u5973\u5B50\u3002</p><p>\u5EB7\u8001\u7237\u5F3A\u7B11\u9053\uFF0C\u300C\u767D\u56E2\u957F\u771F\u4F1A\u5F00\u73A9\u7B11\uFF0C\u628A\u8FD9\u7B49\u4E0D\u77E5\u5EC9\u803B\u7684\u98CE\u5C18\u5973\u5B50\u4E5F\u53EB\u9ED1\u51E4\u51F0\uFF0C\u5C82\u4E0D\u803B\u7B11\u4E8E\u4EBA\u4E86\u3002\u300D</p><p>\u767D\u5929\u5FB7\u773C\u775B\u779F\u5230\u4E86\u7F29\u5728\u4E00\u65C1\u745F\u745F\u53D1\u6296\u7684\u963F\u6708\u8EAB\u4E0A\uFF0C\u4E00\u9762\u4F7F\u52B2\u62FF\u8272\u773C\u7785\u5979\u88F8\u9732\u7684\u90E8\u5206\uFF0C\u4E00\u9762\u8BF4\u9053\uFF0C\u300C\u4F60\u53EF\u4E0D\u8981\u4E0D\u4FE1\uFF0C\u5F53\u65E5\u7684\u9ED1\u51E4\u73B0\u5728\u53EA\u662F\u6211\u767D\u67D0\u4EBA\u7684\u4E00\u6761\u72D7\u800C\u5DF2\u3002\u8001\u5B50\u4E0D\u60F3\u626F\u8FD9\u4E9B\u70C2\u4E8B\uFF0C\u53EA\u6765\u8BF7\u6559\u4E00\u4E2A\u95EE\u9898\uFF0C\u6709\u4EBA\u4E0E\u571F\u532A\u52FE\u901A\uFF0C\u7A9D\u9500\u532A\u8D43\uFF0C\u8BE5\u5F53\u4F55\u7F6A\u554A\u3002\u300D</p><p>\u5176\u5B9E\u5EB7\u8001\u7237\u5B50\u65E9\u5C31\u76F8\u4FE1\u8EAB\u540E\u90A3\u4E2A\u51B7\u51BD\u7684\u7F8E\u5973\u5C31\u662F\u4F20\u8BF4\u4E2D\u7684\u6D77\u68E0\uFF0C\u8FD9\u9662\u91CC\u4E0A\u4E0A\u4E0B\u4E0B\u4E03\u516B\u4E2A\u4EBA\uFF0C\u65E0\u58F0\u65E0\u606F\u5C31\u90FD\u6446\u5E73\u4E86\uFF0C\u9664\u4E86\u9ED1\u51E4\u51F0\uFF0C\u8C01\u8FD8\u6709\u8FD9\u7B49\u672C\u4E8B\uFF1F</p><p>\u4ED6\u4E0D\u77E5\u9053\u4EC0\u4E48\u65F6\u5019\u6D77\u68E0\u8BA9\u767D\u5929\u5FB7\u6536\u670D\u4E3A\u5974\u4E86\uFF0C\u53CD\u6B63\u8FD9\u72D7\u65E5\u7684\u6765\u8005\u4E0D\u5584\uFF0C\u770B\u662F\u51B2\u7740\u4ED6\u7684\u5BB6\u8D22\u6765\u6572\u4E00\u7B14\u6765\u4E86\u3002</p><p>\u5EB7\u8001\u7237\u5B50\u53CD\u800C\u9547\u5B9A\u4E0B\u6765\uFF0C\u5FAE\u7B11\u9053\uFF0C\u300C\u767D\u56E2\u957F\u8BF4\u7B11\u4E86\uFF0C\u7F09\u62FF\u6848\u72AF\u672C\u5C31\u662F\u4F60\u4EEC\u4FDD\u5B89\u56E2\u7684\u672C\u804C\u3002\u5982\u679C\u6211\u6C85\u9547\u6709\u6B64\u7B49\u4EBA\uFF0C\u5EB7\u67D0\u7B2C\u4E00\u4E2A\u4E0D\u9976\u4ED6\u3002\u300D</p><p>\u300C\u5982\u679C\u6B64\u4EBA\u5C31\u662F\u5EB7\u8001\u7237\u60A8\u5462\uFF1F\u300D</p><p>\u300C\u767D\u56E2\u957F\u8BF7\u614E\u8A00\u3002\u300D</p><p>\u767D\u5929\u5FB7\u51B7\u7B11\u4E00\u58F0\uFF0C\u4ECE\u515C\u91CC\u638F\u51FA\u4E00\u518C\u8D26\u672C\uFF0C\u300C\u8FD9\u5C31\u662F\u4F60\u4E0E\u9ED1\u51E4\u51F0\u52FE\u7ED3\u7684\u660E\u7EC6\uFF0C\u60F3\u770B\u770B\u5575\uFF1F\u300D</p><p>\u5EB7\u8001\u7237\u5B50\u4E00\u89C1\u5C01\u9762\u5C31\u4E94\u96F7\u8F70\u9876\uFF0C\u8C46\u5927\u7684\u6C57\u73E0\u5728\u989D\u5934\u6D94\u6D94\u6E17\u51FA\uFF0C\u7B2C\u4E00\u65F6\u95F4\u5C31\u60F3\u5230\u4E86\u4E03\u59E8\u592A\uFF0C\u300C\u51C6\u662F\u90A3\u4E2A\u8D31\u4EBA\u300D\u3002</p><p>\u6D77\u68E0\u8868\u9762\u4E0A\u53E4\u4E95\u4E0D\u6CE2\uFF0C\u9ED1\u51E4\u51F0\u7684\u540D\u5B57\u4E00\u518D\u63D0\u8D77\u4F3C\u4E0E\u5979\u65E0\u751A\u5E72\u7CFB\uFF0C\u8EAB\u4F53\u5185\u5374\u662F\u60CA\u6D9B\u9A87\u6D6A\uFF0C\u82E6\u82E6\u652F\u6491\uFF0C\u8EAB\u4F53\u5448\u73B0\u51FA\u4E0D\u6B63\u5E38\u7684\u7EEF\u7EA2\u3002</p><p>\u8FD9\u4E9B\u65F6\u65E5\u6765\uFF0C\u767D\u5929\u5FB7\u5C06\u5979\u7684\u8EAB\u4F53\u5F53\u6210\u4E86\u70DF\u571F\u7684\u8BD5\u9A8C\u5730\uFF0C\u8BF7\u6559\u4E86\u897F\u6D0B\u5927\u592B\u4F7F\u7528\u6CE8\u5C04\u5668\u4F7F\u5979\u6BD2\u6839\u6DF1\u690D\uFF0C\u518D\u4E5F\u65E0\u6CD5\u6446\u8131\uFF0C\u6700\u53EF\u6076\u7684\u662F\u4ED6\u4F7F\u5979\u7684\u9634\u6237\u548C\u83CA\u809B\u957F\u671F\u4E0E\u70DF\u571F\u63A5\u89E6\uFF0C\u4E5F\u5982\u4E0A\u763E\u4E00\u822C\u5BF9\u70DF\u571F\u4EA7\u751F\u4E86\u8F7B\u5EA6\u7684\u4F9D\u8D56\uFF0C\u6CA1\u6709\u70DF\u571F\u7684\u6ECB\u6DA6\u4E0B\u8EAB\u5C31\u9EBB\u75D2\u4E0D\u582A\uFF0C\u4E00\u523B\u4E5F\u4E0D\u81EA\u5728\u3002</p><p>\u8FD9\u79CD\u9EBB\u75D2\u4E0D\u540C\u4E8E\u6625\u836F\uFF0C\u53D1\u4F5C\u8D77\u6765\u4E07\u8681\u566C\u8EAB\uFF0C\u82E6\u75DB\u4E0D\u582A\uFF0C\u5C31\u7B97\u662F\u6709\u65E0\u6570\u6839\u8089\u68D2\u5728\u91CC\u9762\u62BD\u63D2\u4E5F\u65E0\u6D4E\u4E8E\u4E8B\uFF0C\u552F\u6709\u9ED1\u8272\u7684\u70DF\u571F\u4E00\u6765\uFF0C\u5C31\u6D51\u8EAB\u8212\u6CF0\uFF0C\u98D8\u5165\u4E91\u7AEF\uFF0C\u4ED9\u4EBA\u822C\u7684\u4EAB\u53D7\u3002</p><p>\u6BD2\u54C1\u6210\u4E86\u5979\u73B0\u5728\u552F\u4E00\u9AD8\u4E8E\u751F\u547D\u7684\u4E1C\u897F\u3002</p><p>\u4ECE\u5C0F\u4E3A\u5974\u7684\u6076\u68A6\uFF0C\u5168\u519B\u8986\u706D\u7684\u6253\u51FB\uFF0C\u8FD8\u6709\u65B9\u65B9\u9762\u9762\u4EBA\u751F\u4E0D\u5982\u610F\u4E8B\u5728\u5979\u8EAB\u4E0A\u4F53\u73B0\u5F97\u6DCB\u6F13\u5C3D\u81F4\uFF0C\u5979\u4E00\u76F4\u5728\u5F3A\u6491\uFF0C\u5F3A\u989C\u6B22\u7B11\uFF0C\u8FDE\u6700\u597D\u7684\u5144\u5F1F\u59D0\u59B9\u4E5F\u4E0D\u80FD\u8BC9\u8BF4\u3002</p><p>\u5979\u597D\u7D2F\uFF0C\u597D\u60F3\u627E\u4E2A\u5B89\u9759\u7684\u5730\u65B9\u4F11\u606F\u4E00\u4E0B\uFF0C\u4E8E\u5979\u800C\u8A00\uFF0C\u8FD9\u4E48\u7B80\u5355\u7684\u4E8B\u60C5\u5374\u6210\u4E86\u5962\u671B\u3002</p><p>\u5982\u4ECA\uFF0C\u7EC8\u4E8E\u4E00\u5207\u90FD\u5E7B\u706D\u4E86\uFF0C\u5979\u4E0D\u7528\u518D\u80CC\u8D1F\u90A3\u4E48\u591A\u7684\u4E1C\u897F\uFF0C\u53EA\u6709\u5728\u70DF\u571F\u7684\u9EBB\u9189\u4E2D\uFF0C\u624D\u80FD\u6682\u65F6\u6446\u8131\u5C18\u4E16\u95F4\u7684\u82E6\u75DB\u548C\u5C48\u8FB1\uFF0C\u624D\u80FD\u6709\u90A3\u4E48\u4E00\u70B9\u70B9\u7684\u52C7\u6C14\u76F4\u9762\u6B8B\u9177\u7684\u751F\u6D3B\u3002</p><p>\u6216\u8BB8\uFF0C\u8FD9\u624D\u662F\u5979\u4E0D\u518D\u6297\u62D2\u6BD2\u54C1\u7684\u4E3B\u8981\u539F\u56E0\u7F62\u3002</p><p>\u767D\u5929\u5FB7\u770B\u51C6\u4E86\u5979\u7684\u5F31\u70B9\uFF0C\u8650\u5F85\u5979\uFF0C\u8C03\u6559\u5979\uFF0C\u5524\u9192\u5979\u7684\u5974\u6027\uFF0C\u4E00\u6B65\u6B65\u6CA6\u5165\u4E0D\u53EF\u56DE\u5934\u7684\u70BC\u72F1\u3002</p><p>\u4ED6\u4E0D\u4F46\u60F3\u628A\u6D77\u68E0\u8BAD\u7EC3\u6210\u5FE0\u5FC3\u4E0D\u4E8C\u7684\u6027\u5974\uFF0C\u8FD8\u770B\u4E2D\u4E86\u5979\u7684\u5353\u8D8A\u7684\u6B66\u827A\uFF0C\u5982\u679C\u80FD\u6210\u4E3A\u4ED6\u8D34\u8EAB\u7684\u4FDD\u9556\u517C\u6740\u624B\u5C82\u4E0D\u5999\u54C9\uFF1F</p><p>\u8FD9\u4E00\u6B21\uFF0C\u767D\u5929\u5FB7\u6D3E\u5979\u6765\u5BF9\u4ED8\u5EB7\u8001\u7237\u5B50\u5C31\u662F\u4E3A\u4E86\u8FD9\u4E2A\u76EE\u6807\u8D70\u51FA\u7684\u7B2C\u4E00\u6B65\u3002\u4E4B\u524D\u5DF2\u660E\u91CC\u6697\u91CC\u8003\u9A8C\u8FC7\u591A\u6B21\uFF0C\u53D1\u73B0\u6D77\u68E0\u7684\u786E\u4E27\u5931\u4E86\u81EA\u5DF1\u7684\u610F\u8BC6\uFF0C\u6210\u4E86\u4F9D\u9644\u5728\u9E26\u7247\u8EAB\u4E0A\u7684\u5974\u96B6\uFF0C\u8FD9\u624D\u653E\u5FC3\u628A\u5979\u5728\u6CA1\u6709\u675F\u7F1A\u7684\u60C5\u51B5\u4E0B\u5E26\u51FA\u6765\u3002</p><p>\u5F53\u7136\uFF0C\u9E26\u7247\u5C31\u662F\u5979\u6700\u597D\u7684\u675F\u7F1A\u3002</p><p>\u767D\u5929\u5FB7\u7B97\u4E86\u65F6\u8FB0\uFF0C\u6D77\u68E0\u5C31\u7B97\u8DD1\u4E5F\u71AC\u4E0D\u4E86\u591A\u4E45\uFF0C\u4F55\u51B5\u4ED6\u81EA\u5DF1\u5E26\u4E86\u67AA\uFF0C\u56DB\u4E0B\u91CC\u4E5F\u5E03\u4E86\u54E8\uFF0C\u53D1\u73B0\u6D77\u68E0\u6709\u5F02\u52A8\u5C31\u4E0D\u7559\u60C5\u3002</p><p>\u4E3A\u614E\u91CD\u8D77\u89C1\uFF0C\u4ED6\u6CA1\u6709\u53D1\u7ED9\u4EFB\u4F55\u6B66\u5668\u7ED9\u5979\uFF0C\u8FD8\u53EA\u5141\u8BB8\u5728\u8170\u95F4\u7CFB\u4E00\u6761\u7EB1\u5DFE\u52C9\u5F3A\u906E\u4F4F\u79C1\u5BC6\u82B1\u56ED\uFF0C\u4EFB\u51ED\u5979\u8D64\u624B\u7A7A\u62F3\u53BB\u5BF9\u4ED8\u90A3\u4E48\u591A\u7C97\u6C49\u3002</p><p>\u6D77\u68E0\u8EAB\u624B\u679C\u7136\u4E0D\u51E1\uFF0C\u6218\u6597\u7ECF\u9A8C\u66F4\u662F\u4E30\u5BCC\u65E0\u6BD4\u3002\u867D\u7136\u53D7\u6BD2\u54C1\u6240\u7D2F\uFF0C\u4F53\u80FD\u4E0B\u964D\u4E86\u4E0D\u5C11\uFF0C\u4E00\u8DEF\u4E0A\u5077\u88AD\u52A0\u8272\u8BF1\uFF0C\u4F7F\u5C3D\u5404\u79CD\u624B\u6BB5\uFF0C\u65F6\u95F4\u957F\u4E86\u4E00\u70B9\uFF0C\u7ADF\u80FD\u5728\u65E0\u58F0\u65E0\u606F\u95F4\u5404\u4E2A\u51FB\u7834\uFF0C\u6D88\u706D\u4E86\u6240\u6709\u7684\u62A4\u536B\u3002\u81EA\u5DF1\u9664\u4E86\u4E00\u8EAB\u9999\u6C57\u6DCB\u6F13\uFF0C\u6BEB\u53D1\u65E0\u635F\u3002</p><p>\u5982\u6B64\u9A84\u4EBA\u7684\u6210\u7EE9\u4E0D\u7531\u5F97\u8BA9\u767D\u5929\u5FB7\u5BF9\u5979\u53E6\u773C\u76F8\u770B\uFF0C\u91CD\u65B0\u8BC4\u4EF7\u4E86\u3002</p><p>\u800C\u5BF9\u4E8E\u6D77\u68E0\u6765\u8BF4\uFF0C\u5BF9\u4ED8\u5EB7\u8001\u7237\u5B50\u5E76\u4E0D\u89C9\u5F97\u5982\u4F55\u7F6A\u8FC7\u3002\u867D\u7136\u4ED6\u4EEC\u4EE5\u524D\u662F\u5408\u4F5C\u4F19\u4F34\uFF0C\u4F46\u5EB7\u8001\u7237\u5B50\u4ED7\u7740\u6E20\u9053\u7545\u901A\u548C\u4E0E\u591A\u652F\u532A\u5E2E\u6709\u5173\u7CFB\uFF0C\u9ED1\u767D\u4E24\u9053\u8DEF\u8DEF\u987A\uFF0C\u9ED1\u4E86\u4ED6\u4EEC\u5927\u91CF\u7684\u94F6\u6D0B\uFF0C\u9ED1\u51E4\u51F0\u8BB2\u4E49\u9053\uFF0C\u53EA\u8981\u8FC7\u5F97\u53BB\uFF0C\u6CA1\u6709\u592A\u8BA1\u8F83\u5F97\u5931\uFF0C\u4F46\u4E5F\u5BF9\u6B64\u4EBA\u7684\u4EBA\u54C1\u4E0D\u803B\u3002</p><p>\u4F55\u51B5\u4ED6\u4EEC\u6839\u672C\u6CA1\u89C1\u8FC7\u9762\uFF0C\u90FD\u662F\u7B2C\u4E09\u65B9\u5728\u8054\u7CFB\uFF0C\u611F\u60C5\u4E0A\u4E5F\u758F\u79BB\u5F97\u5F88\u3002</p><p>\u8C08\u5224\u8FD8\u5728\u7EE7\u7EED\uFF0C\u5EB7\u8001\u7237\u5B50\u5904\u5728\u7EDD\u5BF9\u7684\u4E0B\u98CE\u3002</p><p>\u4ED6\u4E00\u54AC\u7259\uFF0C\u300C\u767D\u5929\u5FB7\uFF0C\u7B97\u4F60\u72E0\uFF0C\u4F60\u5F00\u4E2A\u4EF7\u3002\u300D</p><p>\u767D\u5929\u5FB7\u9634\u9634\u5730\u8BF4\uFF0C\u300C\u6CA1\u4EC0\u4E48\u597D\u8BF4\u7684\uFF0C\u62FF\u94B1\u6362\u547D\uFF0C\u8D22\u4EA7\u7559\u4E0B\uFF0C\u4F60\u5C31\u5377\u51E0\u4EF6\u6362\u6D17\u8863\u88F3\u8FDC\u8D70\u9AD8\u98DE\u5427\u3002\u300D</p><p>\u5EB7\u8001\u7237\u5B50\u672C\u4EE5\u4E3A\u4ED6\u4F1A\u8981\u51E0\u5EA7\u5B85\u5B50\u6216\u7530\u571F\u4E4B\u7C7B\u7684\uFF0C\u4E0D\u6599\u60F3\u4ED6\u7684\u80C3\u53E3\u8FD9\u4E48\u5927\uFF0C\u8138\u6C14\u6210\u4E86\u732A\u809D\u8272\uFF0C\u300C\u4F60\u5728\u2026\u2026\u653E\u2026\u2026\u653E\u5C41\uFF01\u300D</p><p>\u767D\u5929\u5FB7\u61D2\u61D2\u5730\u8BF4\uFF0C\u300C\u5929\u6C14\u592A\u70ED\uFF0C\u5EB7\u8001\u7237\u90FD\u70E7\u80E1\u6D82\u4E86\uFF0C\u4EBA\u8BDD\u4E5F\u4E0D\u4F1A\u8BB2\u4E86\u3002\u767D\u677F\u513F\uFF0C\u7ED9\u4ED6\u5582\u70B9\u8425\u517B\u7684\u6E05\u51C9\u4E00\u4E0B\u3002\u300D</p><p>\u6D77\u68E0\u606D\u987A\u5730\u8BF4\uFF0C\u300C\u9075\u547D\uFF0C\u5C11\u7237\u3002\u300D\u8BF4\u7F62\u8D70\u8FC7\u53BB\u62FF\u4E86\u53EA\u7D2B\u7802\u58F6\uFF0C\u5C06\u8336\u6C34\u5012\u6389\u3002</p><p>\u5EB7\u8001\u7237\u5B50\u76EE\u4E0D\u8F6C\u775B\u5730\u76EF\u7740\u5979\uFF0C\u4E0D\u77E5\u5979\u8981\u5E72\u4EC0\u4E48\u3002\u4E00\u4E2A\u8D64\u88F8\u7684\u7F8E\u5973\u5728\u773C\u524D\u6643\u554A\u6643\u603B\u662F\u8D4F\u5FC3\u60A6\u76EE\u7684\u4E8B\u60C5\uFF0C\u5EB7\u8001\u7237\u5B50\u6709\u5BE1\u4EBA\u4E4B\u75BE\uFF0C\u5C31\u7B97\u9A6C\u4E0A\u6709\u6027\u547D\u4E4B\u5FE7\uFF0C\u7F8E\u5973\u4E5F\u662F\u8981\u591A\u770B\u51E0\u773C\u7684\u3002</p><p>\u4ED6\u8D8A\u770B\u53D1\u73B0\u8FD9\u9ED1\u51E4\u51F0\u8D8A\u8010\u770B\uFF0C\u80A4\u8272\u4E94\u5B98\u867D\u7136\u4E0D\u767D\uFF0C\u800C\u4E14\u9887\u6709\u6194\u60B4\u4E4B\u8272\uFF0C\u5374\u7CBE\u81F4\u5927\u6C14\uFF0C\u80F8\u812F\u9971\u6EE1\uFF0C\u8170\u8179\u6709\u529B\uFF0C\u8FDC\u8FDC\u80DC\u8FC7\u4E00\u822C\u7684\u5C18\u4E16\u5973\u5B50\uFF0C\u53EF\u80FD\u8FD8\u53EA\u6709\u51B7\u5982\u971C\u80FD\u5404\u64C5\u80DC\u573A\u3002</p><p>\u6700\u5947\u7279\u7684\u662F\u9F3B\u5B54\u4E0A\u7A7F\u4E86\u4E2A\u9F3B\u73AF\uFF0C\u8D70\u52A8\u8D77\u6765\u4E00\u6643\u4E00\u6643\u7684\uFF0C\u5E73\u6DFB\u51E0\u5206\u8BF1\u8272\u3002</p><p>\u5F3A\u5927\u4E0E\u5351\u987A\uFF0C\u8D1E\u6D01\u4E0E\u653E\u8361\uFF0C\u9AD8\u50B2\u4E0E\u5C48\u8FB1\uFF0C\u7ADF\u540C\u65F6\u5B8C\u7F8E\u5730\u96C6\u4E8E\u8FD9\u5973\u4EBA\u4E00\u8EAB\uFF0C\u6DF7\u5408\u6210\u4E86\u4E00\u79CD\u5947\u7279\u7684\u6C14\u8D28\uFF0C\u8BF4\u4E0D\u6E05\u9053\u4E0D\u660E\u5374\u662F\u5982\u6B64\u8BF1\u4EBA\uFF0C\u4F7F\u4EFB\u4F55\u7537\u4EBA\u5728\u5979\u9762\u524D\u90FD\u6309\u637A\u4E0D\u4F4F\u5F81\u670D\u548C\u88AB\u5F81\u670D\u7684\u6B32\u671B\u3002</p><p>\u5EB7\u8001\u7237\u5B50\u9605\u5973\u65E0\u6570\uFF0C\u54C1\u8BC4\u4E4B\u529F\u4E0D\u7B97\u7B2C\u4E00\u4E5F\u65E0\u4EBA\u6562\u8A00\u5728\u5148\u3002\u8FD9\u4E00\u756A\u611F\u6168\u53EF\u60DC\u53EA\u80FD\u653E\u5728\u5FC3\u5E95\u4E86\u3002</p><p>\u80E1\u601D\u4E71\u60F3\u95F4\uFF0C\u6D77\u68E0\u7A81\u7136\u4F5C\u4E86\u4E2A\u9A87\u4EBA\u7684\u4E3E\u52A8\uFF0C\u5979\u64A9\u8D77\u7EB1\u5DFE\u7684\u4E0B\u6446\uFF0C\u5C06\u4E00\u6761\u957F\u817F\u62AC\u8D77\u6765\u6401\u5230\u51F3\u5B50\u4E0A\uFF0C\u5C06\u63ED\u5F00\u76D6\u7684\u8336\u58F6\u51D1\u5230\u5149\u5149\u7684\u7389\u6237\u4E0B\u9762\u3002\u5F53\u7740\u4F17\u4EBA\u7684\u9762\u4ECE\uFF0C\u7247\u523B\uFF0C\u5C3F\u6C34\u6D60\u6D60\u6F13\u6F13\u6492\u7740\u6B22\u513F\u5730\u51FA\u6765\u4E86\uFF0C\u5927\u90E8\u5206\u6D12\u5230\u4E86\u58F6\u91CC\uFF0C\u8FD8\u6709\u4E00\u5C0F\u90E8\u5206\u6DCB\u5230\u4E86\u5916\u9762\uFF0C\u628A\u5979\u81EA\u5DF1\u7684\u624B\u548C\u58F6\u4F53\u6E85\u4E86\u4E2A\u900F\u6E7F\u3002</p><p>\u5EB7\u8001\u7237\u5B50\u8D77\u5148\u60CA\u8BE7\uFF0C\u65CB\u5373\u609F\u5230\u767D\u5929\u5FB7\u548C\u6D77\u68E0\u8981\u5BF9\u4ED6\u5E72\u4EC0\u4E48\u4E86\uFF0C\u614C\u4E71\u6B32\u9003\u3002</p><p>\u95E8\u5DF2\u6B7B\u9501\uFF0C\u4ED6\u65E0\u8DEF\u53EF\u8D70\uFF0C\u6D77\u68E0\u8F7B\u8F7B\u677E\u677E\u5730\u5C31\u628A\u4ED6\u63D0\u62CE\u56DE\u6765\uFF0C\u4E00\u624B\u7AEF\u7740\u8336\u58F6\u6BD4\u5212\u4E86\u4E00\u4E2A\u559D\u7684\u59FF\u5F0F\u3002</p><p>\u5EB7\u8001\u7237\u5B50\u8001\u6CEA\u7EB5\u6A2A\uFF0C\u568E\u9053\uFF0C\u300C\u79BD\u517D\u4E0D\u5982\uFF0C\u6709\u8FB1\u65AF\u2026\u2026\u5495\u561F\u5495\u561F\u2026\u2026\u300D</p><p>\u540E\u9762\u7684\u58F0\u97F3\u81EA\u7136\u662F\u6D77\u68E0\u628A\u5C3F\u704C\u8FDB\u4ED6\u7684\u5634\u91CC\u7684\u58F0\u97F3\uFF0C\u5EB7\u8001\u7237\u5B50\u731D\u4E0D\u53CA\u9632\uFF0C\u4E0D\u7531\u5F97\u8FDE\u559D\u4E86\u51E0\u53E3\uFF0C\u5F85\u5F97\u610F\u8BC6\u8FC7\u6765\uFF0C\u4E24\u773C\u7FFB\u767D\uFF0C\u6C14\u8840\u4E0D\u7545\uFF0C\u8EAB\u5B50\u5C31\u5F80\u5730\u4E0A\u6ED1\u3002</p><p>\u5C31\u5728\u6D77\u68E0\u8650\u5F04\u5EB7\u8001\u7237\u5B50\u671F\u95F4\uFF0C\u767D\u5929\u5FB7\u5BF9\u59CB\u7EC8\u88F9\u5728\u9526\u888D\u4E2D\u7684\u8C61\u732B\u4E00\u6837\u7684\u5973\u5B69\u5B50\u53D1\u751F\u4E86\u5174\u8DA3\uFF0C\u5C3D\u91CF\u88C5\u5F97\u548C\u989C\u60A6\u8272\u3002</p><p>\u300C\u5C0F\u59D1\u5A18\u591A\u5927\u5566\uFF1F\u300D</p><p>\u300C\u2026\u2026\u300D</p><p>\u300C\u8001\u5BB6\u54EA\u91CC\u7684\u5440\uFF1F\u300D</p><p>\u5C11\u5973\u7684\u773C\u775B\u5FFD\u95EA\u5FFD\u95EA\u7684\uFF0C\u60CA\u6050\u5730\u770B\u7740\u4ED6\uFF0C\u5C31\u662F\u4E0D\u4F5C\u58F0\u3002</p><p>\u767D\u5929\u5FB7\u6709\u4E9B\u6C14\u607C\uFF0C\u5988\u7684\u90FD\u662F\u4E00\u8DEF\u8D27\u8272\uFF0C\u4ED6\u7684\u8138\u53C8\u9634\u4E86\u4E0B\u6765\uFF0C\u5C06\u624B\u67AA\u62CD\u4E86\u62CD\uFF0C\u300C\u8001\u5B50\u505A\u4E0D\u5F97\u597D\u4EBA\uFF0C\u656C\u9152\u4E0D\u5403\u5403\u7F5A\u9152\uFF0C\u628A\u5916\u9762\u888D\u5B50\u8131\u4E86\uFF0C\u8FC7\u6765\uFF0C\u5426\u5219\u5D29\u4E86\u4F60\u3002\u300D</p><p>\u5C11\u5973\u542C\u61C2\u4E86\uFF0C\u6643\u52A8\u7740\u8EAB\u5B50\uFF0C\u8584\u8584\u7684\u9526\u888D\u6389\u843D\u5728\u5730\uFF0C\u9732\u51FA\u82B1\u4E00\u822C\u7684\u5C0F\u8EAB\u5B50\u3002</p><p>\u5979\u867D\u7136\u662F\u4E2A\u7F8E\u4EBA\u80DA\u5B50\uFF0C\u7EC6\u76AE\u5AE9\u8089\uFF0C\u5230\u5E95\u5E74\u7EAA\u5C0F\uFF0C\u8FD8\u6CA1\u53D1\u80B2\u5B8C\u5168\uFF0C\u80F8\u812F\u5FAE\u5FAE\u9686\u8D77\uFF0C\u803B\u90E8\u53EA\u6709\u51E0\u6839\u7ED2\u6BDB\uFF0C\u989C\u8272\u548C\u80A4\u8272\u4E00\u6837\u767D\uFF0C\u4E5F\u662F\u5FAE\u5FAE\u575F\u8D77\uFF0C\u5939\u7740\u4E00\u6761\u7D27\u7EC6\u7684\u5C0F\u7F1D\u3002</p><p>\u5979\u7AD9\u5728\u767D\u5929\u5FB7\u8DDF\u524D\uFF0C\u77E5\u9053\u4ED6\u662F\u4E2A\u5927\u6076\u4EBA\uFF0C\u660E\u663E\u975E\u5E38\u5BB3\u6015\uFF0C\u98A4\u6296\u4E2A\u4E0D\u505C\u3002</p><p>\u767D\u5929\u5FB7\u7684\u5927\u624B\u5728\u5979\u5149\u6D01\u7684\u4E0B\u4F53\u4E00\u8DEF\u6478\u8FC7\u53BB\uFF0C\u80AF\u5B9A\u5979\u8FD8\u662F\u4E2A\u6CA1\u6709\u5F00\u82DE\u7684\u5904\u5973\uFF0C\u4E0D\u8FC7\uFF0C\u4ED6\u4E0D\u50CF\u5EB7\u8001\u7237\u5B50\u5174\u8DA3\u5E7F\u6CDB\uFF0C\u5BF9\u5E7C\u5973\u6CA1\u6709\u592A\u591A\u611F\u89C9\uFF0C\u53C8\u4E0D\u60F3\u7559\u4E0B\u767D\u74A7\u4FBF\u5B9C\u522B\u4EBA\uFF0C\u4FBF\u4E3E\u8D77\u9A73\u58F3\u67AA\uFF0C\u51B2\u7740\u67AA\u53E3\u5439\u4E86\u5439\u3002</p><p>\u300C\u5C0F\u59B9\u5B50\uFF0C\u8FD9\u628A\u67AA\u8DDF\u4E86\u6211\u597D\u591A\u5E74\uFF0C\u6551\u8FC7\u6211\u7684\u547D\uFF0C\u8DDF\u5144\u5F1F\u4E00\u6837\uFF0C\u8FD9\u6837\uFF0C\u4F60\u8BA9\u6211\u5144\u5F1F\u4E5F\u5F00\u5F00\u8364\uFF0C\u89C1\u89C1\u8840\uFF0C\u597D\u4E0D\u597D\uFF1F\u300D</p><p>\u963F\u6708\u4F3C\u61C2\u975E\u61C2\uFF0C\u5446\u5446\u5730\u770B\u7740\u4ED6\u3002</p><p>\u767D\u5929\u5FB7\u634F\u4F4F\u5979\u7684\u4E00\u53EA\u5C0F\u624B\u81C2\uFF0C\u5F15\u5BFC\u5979\u5F20\u5F00\u817F\uFF0C\u5C06\u67AA\u53E3\u51B2\u4E0A\u5BF9\u51C6\u5979\u7684\u5C0F\u7A74\u53E3\uFF0C\u8981\u5979\u81EA\u5DF1\u5750\u4E0B\u53BB\u3002</p><p>\u5C11\u5973\u7A81\u7136\u54ED\u4E86\u8D77\u6765\u3002</p><p>\u5C31\u5728\u5EB7\u8001\u7237\u5B50\u8FF7\u75F0\u5835\u5589\u751F\u6B7B\u83AB\u6D4B\u4E4B\u9645\uFF0C\u963F\u6708\u7684\u5904\u5973\u819C\u4E5F\u88AB\u4E00\u652F\u51B0\u51B7\u7684\u67AA\u7BA1\u6345\u7834\u4E86\u3002</p><p>\u4E00\u7F15\u9C9C\u8840\u6CBF\u7740\u67AA\u8EAB\u873F\u8712\u800C\u4E0B\u3002</p><p>\u767D\u5929\u5FB7\u6CA1\u6709\u8FC7\u591A\u8E42\u8E8F\u8FD9\u5C0F\u59D1\u5A18\uFF0C\u89C1\u4E86\u8840\u5C31\u628A\u67AA\u62BD\u4E86\u51FA\u6765\uFF0C\u628A\u5979\u50CF\u5783\u573E\u4E22\u5F00\u5230\u4E00\u8FB9\u3002\u8D77\u8EAB\u8D70\u5230\u6D77\u68E0\u7684\u8DDF\u524D\uFF0C\u624B\u638C\u629A\u5728\u6D77\u68E0\u633A\u7FD8\u7D27\u5B9E\u7684\u5C41\u80A1\u4E0A\u634F\u5F04\u3002</p><p>\u300C\u8FD9\u8001\u5BB6\u4F19\u600E\u4E48\u6837\uFF0C\u4E0D\u4F1A\u6B7B\u5427\u3002\u300D</p><p>\u300C\u6CA1\u6709\u5927\u788D\uFF0C\u5C11\u7237\u3002\u300D\u6D77\u68E0\u5782\u773C\u9053\u3002</p><p>\u4E0D\u77E5\u9053\u4E3A\u4EC0\u4E48\uFF0C\u53EA\u8981\u767D\u5929\u5FB7\u4E00\u62E2\u8EAB\uFF0C\u6D77\u68E0\u5C31\u4EA7\u751F\u6781\u5F3A\u7684\u6050\u60E7\u611F\uFF0C\u4E0D\u8981\u8BF4\u53CD\u6297\u4E86\uFF0C\u5C31\u662F\u5BF9\u89C6\u7684\u52C7\u6C14\u4E5F\u5728\u5931\u53BB\u3002</p><p>\u4E5F\u8BB8\uFF0C\u767D\u5929\u5FB7\u771F\u662F\u5979\u547D\u4E2D\u7684\u514B\u661F\u3002</p><p>\u767D\u5929\u5FB7\u8E22\u4E86\u8E22\u5EB7\u8001\u7237\u5B50\uFF0C\u300C\u522B\u88C5\u6B7B\u4E86\uFF0C\u4ECE\u4E0D\u4ECE\u4E00\u53E5\u8BDD\u513F\uFF0C\u8001\u5B50\u53EF\u4E0D\u8010\u70E6\u4E45\u5019\u4E86\u3002\u300D</p><p>\u5EB7\u8001\u7237\u5B50\u8EAB\u5B50\u52A8\u4E86\u4E00\u4E0B\uFF0C\u957F\u957F\u5730\u53F9\u606F\u4E00\u58F0\uFF0C\u300C\u7F62\u7F62\u7F62\uFF0C\u547D\u8BE5\u5982\u6B64\u554A\u3002\u597D\uFF0C\u6211\u8D70\uFF0C\u6211\u8D70\u3002\u300D</p><p>\u767D\u5929\u5FB7\u8138\u4E0A\u6D6E\u51FA\u7B11\u5BB9\uFF0C\u4ECE\u53E3\u888B\u4E2D\u6478\u51FA\u5F20\u7EB8\uFF0C\u4E0D\u65E0\u8BA5\u8BBD\u5730\u8BF4\uFF0C\u300C\u8FD9\u662F\u6211\u8349\u62DF\u7684\u4E00\u4EFD\u534F\u8BAE\uFF0C\u60A8\u81EA\u613F\u5C06\u8D22\u4EA7\u65E0\u6761\u4EF6\u8D60\u9001\u4E8E\u6211\uFF0C\u8FD9\u7B49\u5927\u6069\u5927\u5FB7\uFF0C\u9119\u4EBA\u65E0\u4EE5\u4E3A\u62A5\u554A\u3002\u300D</p><p>\u5EB7\u8001\u7237\u5B50\u65E0\u8A00\uFF0C\u770B\u4E5F\u4E0D\u770B\u5C31\u753B\u4E86\u62BC\uFF0C\u5370\u4E86\u6307\u6A21\u3002</p><p>\u767D\u5929\u5FB7\u65AF\u4E95\u6162\u6761\u5730\u6536\u597D\u534F\u8BAE\uFF0C\u53C8\u6478\u51FA\u4E00\u6839\u957F\u957F\u7684\u94F6\u94FE\uFF0C\u8FD9\u6B21\u5374\u662F\u6302\u5230\u4E86\u6D77\u68E0\u7684\u9F3B\u73AF\u4E0A\uFF0C\u6D77\u68E0\u4E56\u5DE7\u7684\u8C61\u72D7\u4E00\u822C\u8DB4\u4E0B\u6765\uFF0C\u56DB\u80A2\u7740\u5730\u3002</p><p>\u767D\u5929\u5FB7\u7275\u7740\u6D77\u68E0\u5F80\u95E8\u5916\u8D70\uFF0C\u8D70\u5F97\u51E0\u6B65\u53C8\u56DE\u5934\u5BF9\u5377\u7F29\u5728\u5899\u89D2\u7F29\u6210\u4E00\u56E2\u7684\u963F\u6708\u8BF4\u9053\uFF1A\u300C\u5C0F\u59B9\u5B50\uFF0C\u4F60\u968F\u6211\u8D70\u4E0D\u7F57\uFF1F\u300D</p><p>\u963F\u6708\u4E00\u52A8\u4E0D\u52A8\uFF0C\u72B6\u82E5\u75F4\u5446\u3002</p><p>\u767D\u5929\u5FB7\u6447\u6447\u5934\uFF0C\u300C\u7B97\u4E86\uFF0C\u4E0D\u52C9\u5F3A\uFF0C\u4EC0\u4E48\u65F6\u5019\u60F3\u901A\u4E86\u4EC0\u4E48\u65F6\u5019\u8FC7\u6765\uFF0C\u8DDF\u7740\u8FD9\u8001\u72D7\u6CA1\u4EC0\u4E48\u597D\u5904\u3002\u300D\u4ED6\u987F\u4E86\u987F\uFF0C\u300C\u5BF9\u4E86\uFF0C\u5EB7\u8001\u7237\uFF0C\u8FD8\u5F97\u544A\u8BC9\u60A8\u4E00\u4EF6\u4E8B\uFF0C\u4E03\u59E8\u592A\u548C\u60A8\u8F6C\u79FB\u5230\u5916\u5730\u7684\u8D22\u4EA7\u6211\u4E5F\u7167\u5355\u5168\u6536\u4E86\uFF0C\u8FD9\u534F\u8BAE\u4E0A\u90FD\u5199\u5F97\u6709\u3002\u300D</p><p>\u5EB7\u8001\u7237\u5B50\u55B7\u51FA\u4E00\u53E3\u9C9C\u8840\u3002</p><p>\u767D\u5929\u5FB7\u8FD9\u624D\u54FC\u8D77\u5C0F\u8C03\uFF0C\u4E00\u6447\u4E09\u6446\u5730\u8D70\u4E86\u51FA\u53BB\uFF0C\u5728\u4ED6\u8EAB\u540E\uFF0C\u7D27\u8DDF\u7740\u4E00\u6761\u7F8E\u4E3D\u7684\u4EBA\u5F62\u72AC\uFF0C\u56DB\u80A2\u4FEE\u957F\uFF0C\u79C0\u7F8E\u7684\u81C0\u90E8\u4E5F\u662F\u4E00\u6447\u4E09\u6446\uFF0C\u6E10\u6E10\u6CA1\u5165\u9ED1\u6697\u4E4B\u4E2D\u3002</p><p>\u7EA2\u706F\u7B3C\u5728\u98CE\u4E2D\u5FAE\u5FAE\u6643\u52A8\u7740\uFF0C\u5431\u5431\u5440\u5440\u7684\uFF0C\u4F3C\u5728\u53D1\u51FA\u8BA5\u8BBD\u7684\u7B11\u58F0\u3002</p>',123);function t(i,o){return r}var _=a(e,[["render",t]]);export{_ as default};
