import{e as p}from"./app.630af5f2.js";import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";const e={},r=p('<h1 id="\u7B2C\u56DB\u7AE0-\u5F80\u4E8B" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u56DB\u7AE0-\u5F80\u4E8B" aria-hidden="true">#</a> \u7B2C\u56DB\u7AE0 \u5F80\u4E8B</h1><p>\u6D77\u68E0\u7684\u672C\u540D\u5176\u5B9E\u53EB\u5B89\u51E4\uFF0C\u7956\u7C4D\u662F\u56DB\u5DDD\u6210\u90FD\uFF0C\u5E74\u5E7C\u65F6\uFF0C\u6B63\u503C\u6EE1\u6E05\u8986\u706D\uFF0C\u519B\u9600\u6DF7\u6218\uFF0C\u7236\u6BCD\u5E26\u7740\u5979\u4E00\u8DEF\u9003\u96BE\u8F97\u8F6C\u5230\u4E86\u6E58\u897F\uFF0C\u6295\u5954\u4E00\u623F\u8FDC\u4EB2\uFF0C\u4E0D\u6599\u4ED6\u4EEC\u65E9\u5DF2\u8FC1\u8D70\uFF0C\u4E0D\u5F97\u5DF2\u5728\u6C85\u9547\u7684\u767D\u5BB6\u5821\u503E\u5C3D\u79EF\u84C4\u7F6E\u4E86\u51E0\u4EA9\u8584\u7530\uFF0C\u5C31\u6B64\u5B89\u8EAB\u7ACB\u547D\u4E0B\u6765\u3002</p><p>\u5B89\u51E4\u6253\u5C0F\u5C31\u548C\u522B\u7684\u5973\u5B69\u5B50\u4E0D\u4E00\u6837\uFF0C\u751F\u6027\u597D\u5F3A\uFF0C\u4F53\u8D28\u5F3A\u5065\uFF0C\u5BF9\u4E00\u4E9B\u821E\u5200\u5F04\u67AA\u3001\u987D\u76AE\u6253\u67B6\u7684\u4E8B\u513F\u6BD4\u7537\u5B69\u5B50\u8FD8\u6765\u52B2\uFF0C\u4E0D\u8FC7\u7236\u4EB2\u5BF9\u806A\u6167\u7684\u5973\u513F\u671F\u8BB8\u5F88\u9AD8\uFF0C\u4E00\u5FC3\u8BA9\u5979\u8BFB\u4E66\uFF0C\u65E5\u540E\u597D\u51FA\u4EBA\u5934\u5730\u6216\u5AC1\u4E2A\u597D\u4EBA\u5BB6\uFF0C\u597D\u65E9\u65E9\u6446\u8131\u751F\u6D3B\u5728\u4E0B\u5C42\u7684\u547D\u8FD0\u3002\u5728\u522B\u4EBA\u5BB6\u7684\u5B69\u5B50\u90FD\u5728\u7530\u95F4\u5730\u5934\u73A9\u800D\u7684\u65F6\u5019\uFF0C\u5979\u5C31\u80CC\u7740\u5C0F\u4E66\u5305\uFF0C\u8D70\u5341\u51E0\u91CC\u5730\u5230\u4E61\u91CC\u4E0A\u79C1\u587E\u3002</p><p>\u6BCF\u5929\u832B\u832B\u7684\u7FE0\u7AF9\u6D77\u91CC\u9762\uFF0C\u603B\u80FD\u89C1\u5230\u4E00\u4E2A\u5C0F\u5C0F\u7684\u8EAB\u5F71\uFF0C\u788E\u82B1\u886B\u5B50\uFF0C\u659C\u630E\u7740\u7C97\u5E03\u7F1D\u5236\u7684\u4E66\u888B\uFF0C\u8F7B\u8F7B\u5DE7\u5DE7\u5730\u8DE8\u8FC7\u4E00\u9053\u9053\u5C71\u5784\uFF0C\u7A7F\u8FC7\u9AD8\u8038\u6797\u7ACB\u7684\u7AF9\u6797\uFF0C\u4FCA\u4FCF\u5C0F\u8138\u4E0A\u603B\u662F\u8361\u6F3E\u7740\u5FAE\u7B11\uFF0C\u6D45\u6D45\u9732\u51FA\u4E00\u53CC\u8FF7\u4EBA\u7684\u68A8\u6DA1\u3002</p><p>\u5B89\u5BB6\u6709\u5973\u521D\u957F\u6210\uFF0C\u51FA\u843D\u5F97\u7709\u6E05\u76EE\u79C0\uFF0C\u7F8E\u4EBA\u80DA\u5B50\u3002\u8FDC\u8FD1\u4E61\u91CC\u63D0\u4EB2\u53EF\u4E0D\u5C11\uFF0C\u5176\u4E2D\u8FD8\u6709\u767D\u6C0F\u5B97\u65CF\u65CF\u957F\u767D\u656C\u8F69\u7684\u5B9D\u8D1D\u5C0F\u513F\u5B50\u767D\u5BCC\u8D35\u3002\u767D\u798F\u8D35\u5E74\u7EAA\u4E0E\u5B89\u51E4\u76F8\u4EFF\uFF0C\u5374\u6210\u65E5\u597D\u5403\u61D2\u505A\uFF0C\u4ED7\u7740\u8001\u5B50\u7684\u52BF\uFF0C\u5E26\u7740\u4E00\u5E2E\u574F\u5C0F\u5B50\u5C3D\u5E72\u4E9B\u9E21\u96F6\u72D7\u788E\u7684\u52FE\u5F53\uFF0C\u6B3A\u538B\u826F\u5584\uFF0C\u6A2A\u884C\u4E61\u91CC\uFF0C\u4E61\u4E0B\u767E\u59D3\u4E5F\u552F\u6709\u5FCD\u6C14\u541E\u58F0\uFF0C\u907F\u800C\u8FDC\u4E4B\u3002\u5B89\u51E4\u7684\u7236\u6BCD\u53EF\u4E0D\u613F\u628A\u597D\u7AEF\u7AEF\u7684\u9C9C\u82B1\u63D2\u5230\u725B\u7CAA\u4E0A\uFF0C\u53C8\u4E0D\u6562\u5F00\u7F6A\u767D\u5BB6\uFF0C\u53EA\u662F\u544A\u8BEB\u5973\u513F\u79BB\u4ED6\u8FDC\u8FDC\u7684\u3002</p><p>\u4E0D\u6015\u8D3C\u5077\uFF0C\u5C31\u6015\u8D3C\u60E6\u8BB0\uFF0C\u5B89\u51E4\u7684\u7F8E\u8C8C\u5728\u8FD9\u4E00\u5E26\u53EF\u7B97\u51FA\u4E86\u5927\u540D\uFF0C\u767D\u5BCC\u8D35\u54EA\u80FD\u4E0D\u77E5\uFF0C\u53EA\u662F\u5728\u4E61\u91CC\u90FD\u662F\u719F\u95E8\u719F\u8138\u7684\u4E0D\u6562\u592A\u653E\u8086\u3002</p><p>\u90A3\u4E00\u65E5\u6B63\u662F\u5341\u6708\u91D1\u79CB\u65F6\u8282\uFF0C\u5B89\u51E4\u7EC8\u751F\u96BE\u5FD8\u3002\u5979\u5077\u5077\u8FDB\u5C71\u91C7\u5C71\u836F\uFF0C\u60F3\u5356\u70B9\u94B1\u4F5C\u8001\u5E08\u7684\u8282\u4FF8\uFF0C\u4E0D\u6599\u8BA9\u90A3\u5E2E\u574F\u5C0F\u5B50\u902E\u4E2A\u6B63\u7740\u3002\u4E00\u4F19\u4EBA\u56F4\u7740\u5B89\u51E4\u4E0D\u8BA9\u5979\u8D70\uFF0C\u8D77\u54C4\u8981\u767D\u5BCC\u8D35\u62B1\u7740\u5B89\u51E4\u4EB2\u5634\uFF0C\u5B89\u51E4\u5F53\u7136\u5B81\u6B7B\u4E0D\u4ECE\uFF0C\u4E24\u4EBA\u626D\u6210\u4E86\u4E00\u56E2\u3002</p><p>\u767D\u5BCC\u8D35\u5A07\u751F\u60EF\u517B\uFF0C\u5E74\u7EAA\u867D\u6BD4\u5B89\u51E4\u5927\u4E00\u4E24\u5C81\uFF0C\u529B\u6C14\u4E0D\u89C1\u5F97\u6BD4\u81EA\u5C0F\u5728\u5C71\u8DEF\u4E0A\u953B\u70BC\u7684\u5979\u5927\u4E86\u591A\u5C11\uFF0C\u5077\u9E21\u4E0D\u4ECE\u8FD8\u8BA9\u5B89\u51E4\u6247\u4E86\u4E2A\u5634\u5DF4\uFF0C\u4F17\u4EBA\u4E00\u9635\u54C4\u7B11\uFF0C\u8FD9\u4E0B\u6302\u4E0D\u4F4F\u4E86\uFF0C\u6DA8\u4E86\u4E2A\u9E21\u51A0\u7EA2\uFF0C\u6307\u6325\u5144\u5F1F\u4EEC\u4E00\u6D8C\u800C\u4E0A\u6309\u4F4F\u5979\u7684\u624B\u811A\u52A8\u5F39\u4E0D\u5F97\uFF0C\u4ED6\u6446\u51FA\u4E00\u5E45\u5F81\u670D\u8005\u7684\u59FF\u6001\uFF0C\u5927\u6447\u5927\u6446\u9A91\u5750\u5728\u5B89\u51E4\u67D4\u8F6F\u7684\u5C0F\u809A\u5B50\u4E0A\uFF0C\u6A21\u4EFF\u5077\u770B\u5230\u7684\u963F\u7238\u7684\u884C\u4E3A\uFF0C\u6380\u5F00\u5979\u8863\u88F3\u4E0B\u6446\uFF0C\u5C06\u624B\u63D2\u8FDB\u4E86\u5B89\u51E4\u7684\u88E4\u88C6\u91CC\uFF0C\u6478\u5230\u4E86\u5C1A\u5728\u53D1\u80B2\u4E2D\u7684\u5C11\u5973\u6E29\u7389\u822C\u5149\u79C3\u6ED1\u5AE9\u7684\u9634\u6237\u3002</p><p>\u300C\u5149\u677F\u5B50\uFF0C\u5149\u677F\u5B50\uFF01\u300D\u767D\u5BCC\u8D35\u602A\u58F0\u602A\u6C14\u5730\u53EB\u8D77\u6765\uFF0C\u4F17\u4EBA\u4E0B\u6D41\u5730\u54C4\u7B11\u3002</p><p>\u5B89\u51E4\u72C2\u6012\u4E86\uFF0C\u5A18\u8BF4\u8FC7\uFF0C\u5973\u4EBA\u7684\u8EAB\u4F53\u662F\u91D1\uFF0C\u522B\u8BF4\u6478\uFF0C\u5C31\u7B97\u8BA9\u7537\u5B50\u770B\u4E86\u4E00\u6B21\u5C31\u53D8\u4E86\u94C1\uFF0C\u53D8\u5F97\u8FDE\u6728\u77F3\u90FD\u4E0D\u5982\u3002\u867D\u7136\u8FD8\u4E0D\u61C2\u5F97\u7537\u5973\u4E4B\u4E8B\uFF0C\u4E5F\u6DF1\u77E5\u8BA9\u7537\u4EBA\u6478\u5230\u4E0B\u4F53\u662F\u6781\u803B\u4E4B\u4E8B\uFF0C\u76DB\u6012\u4E4B\u4E0B\uFF0C\u5979\u6FC0\u53D1\u51FA\u795E\u529B\uFF0C\u6323\u5F00\u4E86\u538B\u5236\u5979\u7684\u4F17\u4EBA\uFF0C\u767D\u5BCC\u8D35\u731D\u4E0D\u53CA\u9632\uFF0C\u5728\u6DF7\u4E71\u4E2D\u649E\u4E0B\u4E86\u5C71\u5D16\uFF0C\u4E0B\u8EAB\u91CD\u91CD\u5730\u649E\u5728\u534A\u622A\u8001\u7AF9\u58A9\u4E4B\u4E0A\u3002\u7ECF\u6551\u6CBB\u6027\u547D\u65E0\u5927\u865E\uFF0C\u547D\u6839\u5904\u5374\u88AB\u521B\u751A\u91CD\uFF0C\u8BF7\u6765\u7684\u4E0D\u5C11\u540D\u533B\u90FD\u6447\u5934\u8868\u793A\u5931\u53BB\u4E86\u751F\u80B2\u80FD\u529B\u3002</p><p>\u8981\u767D\u5BB6\u65AD\u5B50\u7EDD\u5B59\uFF01</p><p>\u8FD9\u4E00\u7F6A\u540D\u53EF\u5927\u4E86\uFF0C\u8BA9\u5B89\u51E4\u4E00\u5BB6\u5927\u7978\u4E34\u5934\u3002\u767D\u656C\u8F69\u5C06\u4ED6\u4EEC\u9501\u62FF\u5728\u5B97\u7960\uFF0C\u76F4\u56B7\u56B7\u8981\u6740\u4EBA\u3002</p><p>\u6570\u65E5\u540E\uFF0C\u4E61\u957F\u5F53\u7740\u4F17\u4E61\u4EB2\u7684\u9762\u5BA3\u5E03\u4E86\u5BB6\u6CD5\u5224\u51B3\u7ED3\u679C\uFF0C\u5C06\u5B89\u51E4\u6C38\u8FDC\u53D1\u914D\u7ED9\u767D\u5BCC\u8D35\u4E3A\u5974\uFF0C\u5B89\u5BB6\u7684\u571F\u5730\u8D22\u4EA7\u5C3D\u5F52\u767D\u5BB6\u6240\u6709\uFF0C\u5B89\u5BB6\u4E24\u8001\u4E3A\u767D\u5BB6\u5145\u5F53\u96C7\u5DE5\u8C0B\u751F\u3002</p><p>\u5B89\u51E4\u7684\u5A18\u5F53\u573A\u5C31\u6655\u5012\u5728\u5730\uFF0C\u5728\u7236\u4EB2\u6CEA\u773C\u6EC2\u6CB1\u5636\u54D1\u7684\u547C\u558A\u58F0\u4E2D\uFF0C\u5C0F\u5B89\u51E4\u88AB\u51E0\u4E2A\u5927\u4EBA\u6293\u7740\uFF0C\u6252\u5149\u4E86\u88E4\u5B50\uFF0C\u8138\u51B2\u4E0B\u8170\u80A2\u5F2F\u6298\u5728\u4E00\u6761\u957F\u51F3\u4E0A\uFF0C\u5C0F\u5C0F\u7684\u81C0\u90E8\u9AD8\u9AD8\u7FD8\u5728\u7A7A\u4E2D\u3002</p><p>\u300C\u5179\u5179\u2026\u2026\u300D\u4E00\u7F15\u9752\u70DF\u5347\u8D77\uFF0C\u70E7\u7EA2\u7684\u70D9\u94C1\u6BEB\u4E0D\u7559\u60C5\u5730\u5370\u5728\u767D\u5AE9\u7684\u81C0\u808C\u4E0A\u3002</p><p>\u63A5\u4E0B\u6765\u7684\u4E00\u4E2A\u6708\uFF0C\u5B89\u51E4\u8EBA\u5728\u767D\u5BB6\u7684\u67F4\u623F\u91CC\uFF0C\u9AD8\u70E7\u4E0D\u9000\uFF0C\u75DB\u9192\u53C8\u660F\u8FF7\uFF0C\u53CD\u590D\u51E0\u6B21\uFF0C\u5728\u751F\u6B7B\u8FB9\u7F18\u6765\u56DE\u8D70\u4E86\u51E0\u906D\uFF0C\u7ADF\u7136\u547D\u5927\u633A\u4E86\u8FC7\u6765\u3002</p><p>\u4ECE\u6B64\uFF0C\u5728\u90A3\u672C\u662F\u5973\u4EBA\u6700\u53EF\u9A84\u50B2\u7684\u5730\u65B9\uFF0C\u7559\u4E0B\u4E86\u4E00\u4E2A\u4E00\u751F\u4E5F\u78E8\u706D\u4E0D\u6389\u7684\u3001\u5982\u540C\u70D9\u8FDB\u5FC3\u5E95\u7684\u6DF1\u6DF1\u5C48\u8FB1\u4E00\u822C\u70D9\u8FDB\u4E86\u808C\u4F53\u6DF1\u5904\u7684\u300C\u767D\u300D\u5B57\uFF0C\u90A3\u4E00\u5757\u4E24\u5BF8\u89C1\u65B9\u3001\u7FFB\u51FA\u4E86\u9C9C\u7EA2\u7684\u8089\u5757\u7684\u75A4\u75D5\uFF0C\u5E26\u7ED9\u5979\u7684\u662F\u5E78\u798F\u7684\u6BC1\u706D\uFF0C\u662F\u5C48\u8FB1\u7684\u89C1\u8BC1\uFF0C\u66F4\u662F\u4E00\u751F\u60B2\u5267\u7684\u5F00\u7AEF\u3002</p><p>\u4ECE\u6B64\uFF0C\u767D\u5BB6\u5821\u5C11\u4E86\u4E00\u4E2A\u6D3B\u6CFC\u7075\u52A8\u7684\u5B89\u51E4\uFF0C\u6362\u4E4B\u4EE5\u4E00\u4E2A\u6EE1\u9762\u60B2\u8272\u5F62\u5BB9\u6194\u60B4\u7684\u5C0F\u51E4\u5974\uFF0C\u5979\u5F31\u5C0F\u7684\u8EAB\u5B50\u627F\u62C5\u8D77\u4E86\u4F3A\u5019\u767D\u5BB6\u8001\u5C0F\u751F\u6D3B\u8D77\u5C45\u7684\u91CD\u62C5\uFF0C\u6311\u6C34\u3001\u5E72\u6D3B\u3001\u5288\u67F4\u6837\u6837\u8981\u5E72\uFF0C\u65E0\u5C3D\u7684\u8D23\u9A82\u548C\u6BB4\u6253\uFF0C\u5979\u90FD\u9ED8\u9ED8\u627F\u53D7\u4E86\u4E0B\u6765\uFF0C\u771F\u6B63\u4E0D\u80FD\u627F\u53D7\u7684\u5374\u662F\u4ECE\u8089\u4F53\u5230\u7075\u9B42\u7684\u53D8\u6001\u6467\u6B8B\u3002</p><p>\u767D\u5BCC\u8D35\u5C31\u4E0D\u7528\u8BF4\u4E86\uFF0C\u4F3A\u5019\u7956\u5B97\u4E00\u822C\uFF0C\u5403\u559D\u62C9\u6492\u90FD\u8981\u53EB\u5979\u670D\u4F3A\uFF0C\u590F\u5929\u6253\u6247\uFF0C\u51AC\u5929\u6696\u88AB\u3002\u6240\u8C13\u6696\u88AB\u5C31\u662F\u6BCF\u5929\u591C\u91CC\uFF0C\u5979\u90FD\u8981\u5149\u7740\u8EAB\u5B50\u5148\u94BB\u5230\u88AB\u5B50\u91CC\uFF0C\u628A\u51B0\u51B7\u7684\u88AB\u7A9D\u7761\u6696\u548C\uFF0C\u624D\u8BA9\u5C0F\u5C11\u7237\u7761\u8FDB\u53BB\u3002\u5FC3\u60C5\u597D\u65F6\u5C31\u4F1A\u653E\u5979\u5230\u67F4\u623F\u53BB\uFF0C\u5FC3\u60C5\u4E0D\u597D\u6216\u662F\u90AA\u6027\u4E0A\u6765\u4E86\u5C31\u4F1A\u7559\u4E0B\u5979\uFF0C\u5728\u5979\u7684\u8EAB\u5B50\u4E0A\u4E71\u8E22\u4E71\u6293\uFF0C\u5F04\u5F97\u8EAB\u4E0A\u9752\u4E00\u5757\u7D2B\u4E00\u5757\uFF0C\u6478\u5AE9\u4E73\u62A0\u4E0B\u8EAB\u66F4\u662F\u5BB6\u5E38\u4FBF\u996D\u3002\u66F4\u90AA\u6027\u7684\u662F\uFF0C\u5750\u5B8C\u9A6C\u6876\u8FD8\u8981\u5B89\u51E4\u7ED9\u4ED6\u64E6\u5C41\u80A1\u3002</p><p>\u4E00\u4E2A\u51AC\u591C\uFF0C\u767D\u5BCC\u8D35\u8BA9\u5C3F\u80C0\u9192\u4E86\uFF0C\u5916\u9762\u51B7\u5F97\u7ED3\u51B0\uFF0C\u4E0D\u613F\u94BB\u51FA\u70ED\u54C4\u54C4\u7684\u88AB\u7A9D\uFF0C\u4E8E\u662F\u8E22\u9192\u4E86\u5377\u7F29\u5728\u4E00\u5934\u7684\u5B89\u51E4\uFF0C\u53EB\u5979\u76F4\u633A\u633A\u5730\u8DEA\u5230\u5E8A\u69BB\u524D\u3002\u5B89\u51E4\u8FF7\u8FF7\u7CCA\u7CCA\u7684\uFF0C\u4E0D\u77E5\u9053\u4ED6\u53C8\u60F3\u8D77\u4EC0\u4E48\u6298\u78E8\u4EBA\u7684\u9B3C\u4E3B\u610F\u4E86\uFF0C\u76F4\u89C9\u5F97\u5149\u8EAB\u5B50\u88AB\u51B7\u7A7A\u6C14\u5305\u88F9\u7740\uFF0C\u51B7\u5F97\u76F4\u6253\u54C6\u55E6\u3002\u767D\u5BCC\u8D35\u53EB\u5B89\u51E4\u5F20\u5F00\u53E3\uFF0C\u4ECE\u88AB\u5B50\u91CC\u628A\u5C0F\u9E21\u5DF4\u62D6\u51FA\u6765\u585E\u5230\u5979\u5634\u8FB9\u3002</p><p>\u4E00\u6CE1\u70ED\u817E\u817E\u7684\u9EC4\u5C3F\u51B2\u4E86\u51FA\u6765\uFF0C\u6D12\u5F97\u5B89\u51E4\u6EE1\u9762\u90FD\u662F\u3002</p><p>\u5B89\u51E4\u60CA\u60F6\u5931\u63AA\u5730\u9003\u5F00\u6765\uFF0C\u65E0\u8BBA\u8FD9\u5C0F\u5B50\u600E\u4E48\u9A82\u90FD\u4E0D\u80AF\u518D\u8FC7\u6765\uFF0C\u62B1\u7740\u80A9\u8EB2\u5728\u89D2\u843D\u5624\u5624\u5730\u54ED\u3002</p><p>\u7B2C\u4E8C\u5929\uFF0C\u767D\u656C\u8F69\u4EE5\u5B89\u51E4\u6297\u547D\u4E3A\u7531\uFF0C\u628A\u5B89\u51E4\u7684\u5A18\u6293\u6765\u6BD2\u6253\u4E86\u4E00\u987F\uFF0C\u5F53\u665A\uFF0C\u5B89\u51E4\u4E00\u52A8\u4E0D\u52A8\u5730\u8DEA\u7740\u5C06\u767D\u5BCC\u8D35\u7684\u5C3F\u6DB2\u559D\u4E86\u4E2A\u4E00\u5E72\u4E8C\u51C0\u3002</p><p>\u4EE5\u540E\u591A\u5E74\uFF0C\u559D\u5C3F\u6210\u4E86\u60EF\u4F8B\u3002</p><p>\u300C\u6211\u513F\u8FD8\u771F\u662F\u4E2A\u5929\u624D\u3002\u300D\u767D\u656C\u8F69\u9AD8\u5174\u5F97\u8FD9\u4E48\u5938\u513F\u5B50\u3002</p><p>\u767D\u656C\u8F69\u5F53\u7136\u66F4\u4E0D\u662F\u4E2A\u597D\u9E1F\uFF0C\u8868\u9762\u4E0A\u7684\u9053\u5FB7\u6587\u7AE0\uFF0C\u4E00\u809A\u5B50\u7684\u7537\u76D7\u5973\u5A3C\u3002\u65E0\u65F6\u65E0\u523B\u4E0D\u5728\u60E6\u5FF5\u5B89\u51E4\u767D\u751F\u751F\u7684\u8EAB\u5B50\uFF0C\u4E00\u770B\u5230\u5979\u5C31\u4E24\u773C\u53D1\u5149\uFF0C\u8D81\u5979\u4E00\u4E2A\u4EBA\u505A\u4E8B\u7684\u65F6\u5019\u7325\u4EB5\u5979\uFF0C\u90A3\u53CC\u9AA8\u8282\u7C97\u7CD9\u7684\u5927\u624B\u5728\u5979\u67D4\u5AE9\u7684\u8EAB\u5B50\u4E0A\u5012\u817E\u7684\u611F\u89C9\u771F\u53EB\u5B89\u51E4\u4F5C\u5455\u3002\u53EA\u662F\u754F\u60E7\u5BB6\u4E2D\u51F6\u608D\u7684\u6BCD\u8001\u864E\u4ED6\u8FD8\u4E0D\u6562\u8FC7\u4E8E\u653E\u8086\u3002</p><p>\u8FD9\u79CD\u4E0D\u662F\u4EBA\u8FC7\u7684\u65E5\u5B50\u4EE4\u5B89\u51E4\u5907\u611F\u714E\u71AC\u3002\u5979\u5B66\u4F1A\u4E86\u4E00\u4EF6\u4E8B\uFF0C\u4EC0\u4E48\u4E5F\u4E0D\u8BF4\uFF0C\u518D\u75DB\u4E5F\u4E0D\u53EB\uFF0C\u54AC\u7740\u7259\u5FCD\u53D7\u7740\u4E0A\u5929\u7684\u4E0D\u516C\u3002</p><p>\u53C8\u662F\u4E00\u65E5\uFF0C\u767D\u5BCC\u8D35\u5E26\u7740\u5979\uFF0C\u8FD8\u6709\u90A3\u5E2E\u574F\u5C0F\u5B50\u6765\u5230\u5F53\u65E5\u7684\u90A3\u5904\u5C71\u5D16\uFF0C\u4EBA\u76F8\u4F3C\uFF0C\u82B1\u76F8\u540C\uFF0C\u5883\u9047\u5374\u5DF2\u662F\u5929\u5DEE\u5730\u8FDC\u3002</p><p>\u767D\u5BCC\u8D35\u4FE8\u7136\u50CF\u4E2A\u7687\u5E1D\uFF0C\u559D\u4EE4\u5B89\u51E4\u81EA\u5DF1\u8131\u5149\u4E0B\u8EAB\uFF0C\u8DEA\u5728\u5730\u4E0A\uFF0C\u5C41\u80A1\u671D\u5929\uFF0C\u8BA9\u90A3\u5E2E\u5C0F\u5B50\u770B\u90A3\u4E2A\u5370\u5728\u5C41\u80A1\u4E0A\u4EE3\u8868\u7740\u6743\u5C5E\u7684\u300C\u767D\u300D\u5B57\uFF0C\u8FD8\u5141\u8BB8\u5C0F\u5B50\u4EEC\u4E00\u4E2A\u4E2A\u8F6E\u6D41\u6765\u6478\u5979\u7684\u300C\u5149\u677F\u5B50\u300D\u3002</p><p>\u6BCF\u4E00\u4E2A\u6478\u5B8C\uFF0C\u5B89\u51E4\u90FD\u8981\u98A4\u6296\u7740\u58F0\u97F3\u5927\u58F0\u5730\u8BF4\uFF0C\u300C\u8C22\u8C22\uFF38\uFF38\u54E5\u73A9\u4E86\u5B89\u51E4\u7684\u5149\u677F\u5B50\u3002\u300D</p><p>\u90A3\u4E00\u523B\uFF0C\u66FE\u7ECF\u5FC3\u9AD8\u6C14\u50B2\u7684\u5B89\u51E4\u5F7B\u5E95\u9A6F\u670D\u4E86\uFF0C\u7167\u505A\u4E86\u767D\u5BCC\u8D35\u4E0B\u7684\u6BCF\u4E00\u9053\u6307\u4EE4\u3002</p><p>\u5F53\u4E00\u53CC\u53CC\u80AE\u810F\u7684\u624B\u8086\u65E0\u5FCC\u60EE\u5730\u63D2\u5165\u5979\u5723\u6D01\u7684\u7981\u5730\u65F6\uFF0C\u521A\u5F3A\u5982\u5979\u518D\u4E5F\u63A7\u5236\u4E0D\u4F4F\u6CEA\u6C34\uFF0C\u5927\u54ED\u4E86\u8D77\u6765\uFF0C</p><p>\u90A3\u65E0\u6CD5\u5FD8\u5374\u7684\u4E00\u5E55\u6210\u4E86\u5979\u6C38\u751F\u7684\u5669\u68A6\u3002</p><p>\u5728\u5979\u7684\u5FC3\u7075\u6DF1\u5904\uFF0C\u4E5F\u690D\u4E0B\u4E86\u5BF9\u767D\u5BCC\u8D35\u65E0\u6CD5\u514B\u670D\u7684\u6050\u60E7\u3002</p><p>\u51E0\u5E74\u540E\uFF0C\u5B89\u51E4\u957F\u5927\u4E86\uFF0C\u9971\u53D7\u6467\u6B8B\u7684\u5979\u5E76\u6CA1\u6709\u5728\u66B4\u98CE\u96E8\u4E2D\u67AF\u840E\uFF0C\u53CD\u51FA\u843D\u5F97\u6108\u53D1\u695A\u695A\u52A8\u4EBA\uFF0C\u4E30\u6EE1\u5982\u7389\uFF0C\u50CF\u4E00\u9897\u8273\u5149\u593A\u4EBA\u7684\u300C\u9ED1\u73CD\u73E0\u300D\uFF0C\u76F4\u53EB\u4EBA\u611F\u53F9\u5929\u751F\u4E3D\u8D28\u4E0D\u81EA\u5F03\uFF0C\u6885\u82B1\u9999\u81EA\u82E6\u5BD2\u6765\u3002</p><p>\u7136\u800C\u78E8\u96BE\u4E5F\u63A5\u8E35\u800C\u81F3\uFF0C\u767D\u5BB6\u5821\u91CC\u65E0\u597D\u4EBA\uFF0C\u4E00\u53CC\u53CC\u6DEB\u90AA\u7684\u8272\u773C\u5F00\u59CB\u76EF\u4F4F\u5979\u65E5\u76CA\u9971\u6EE1\u7684\u80F8\u812F\uFF0C\u90FD\u5728\u4F01\u56FE\u5360\u5979\u7684\u4FBF\u5B9C\uFF0C\u6CA1\u6709\u8C01\u628A\u5979\u5F53\u4EBA\u770B\uFF0C\u53EA\u5F53\u4F5C\u767D\u5BB6\u7684\u4E00\u6761\u72D7\u3002</p><p>\u5B89\u51E4\u7684\u7239\u5988\u53D7\u4E0D\u4F4F\u8FD9\u78E8\u96BE\uFF0C\u629B\u4E0B\u4E86\u5B64\u82E6\u7684\u5973\u513F\u65E9\u65E9\u8C22\u4E16\u3002\u5B89\u51E4\u5931\u53BB\u4E86\u552F\u4E00\u7684\u6170\u85C9\uFF0C\u65E5\u5B50\u66F4\u52A0\u96BE\u8FC7\u4E86\uFF0C\u5728\u6CA1\u6709\u5C0A\u4E25\uFF0C\u6CA1\u6709\u7F9E\u803B\u7684\u5730\u72F1\u4E2D\u82DF\u6D3B\u7740\u3002</p><p>\u767D\u5BCC\u8D35\u81EA\u5C0F\u843D\u4E0B\u7684\u75C5\u6839\u4E00\u76F4\u90FD\u6CA1\u597D\uFF0C\u65E0\u8BBA\u600E\u4E48\u5174\u594B\u4E5F\u52C3\u4E0D\u8D77\uFF0C\u6210\u4E86\u65E0\u7528\u7684\u592A\u76D1\uFF0C\u8D8A\u662F\u61C2\u5F97\u4E86\u7537\u5973\u4E4B\u4E8B\uFF0C\u4ED6\u8D8A\u662F\u75DB\u6068\u5B89\u51E4\uFF0C\u53D8\u7740\u6CD5\u513F\u8650\u5F85\u5979\uFF0C\u62FF\u978B\u62BD\u6253\u5979\u7684\u4E0B\u8EAB\uFF0C\u9488\u523A\u7EA2\u8C46\u5927\u7684\u4E73\u5934\uFF0C\u600E\u4E48\u8BA9\u5979\u75BC\u75DB\u96BE\u5FCD\u600E\u4E48\u6298\u78E8\u5979\u3002</p><p>\u767D\u5BB6\u5821\u5F7B\u591C\u56DE\u8361\u7740\u5B89\u51E4\u51C4\u5389\u7684\u5C16\u53EB\u3002</p><p>\u6B21\u65E5\uFF0C\u603B\u6709\u4E9B\u65E0\u804A\u7684\u4EBA\u62FF\u5B89\u51E4\u6765\u6253\u8D4C\uFF0C\u7B49\u5B89\u51E4\u6B65\u5C65\u8E52\u8DDA\u5730\u51FA\u6765\u5E72\u6D3B\uFF0C\u4FBF\u5728\u8DEF\u4E0A\u5835\u4F4F\u5979\uFF0C\u975E\u8981\u5979\u5C55\u793A\u6628\u665A\u54EA\u4E2A\u90E8\u4F4D\u53D7\u4E86\u6298\u78E8\u6765\u51B3\u5B9A\u80DC\u8D1F\u3002\u5B89\u51E4\u5F80\u5F80\u4E00\u8A00\u4E0D\u53D1\uFF0C\u57CB\u7740\u5934\u60F3\u51B2\u51FA\u53BB\uFF0C\u53C8\u88AB\u4EBA\u7FA4\u6321\u56DE\uFF0C\u4E00\u6B21\u53C8\u4E00\u6B21\uFF0C\u4F60\u4E00\u634F\u4ED6\u4E00\u6478\u8D81\u673A\u63E9\u6CB9\uFF0C\u4F17\u4EBA\u563B\u563B\u54C8\u54C8\u6DEB\u7B11\u4E0D\u65AD\uFF0C\u5F53\u6210\u4E86\u8FD9\u4E00\u5929\u6700\u597D\u73A9\u6700\u9999\u8273\u523A\u6FC0\u7684\u6E38\u620F\u3002</p><p>\u767D\u5BCC\u8D35\u7684\u8001\u5A18\u5730\u4E3B\u5A46\u77E5\u9053\u4E86\uFF0C\u9887\u4E0D\u4EE5\u4E3A\u7136\uFF0C\u4E00\u65B9\u53C8\u9762\u5FC3\u75BC\u513F\u5B50\u7684\u8EAB\u4F53\u4E0D\u80FD\u71AC\u591C\uFF0C\u518D\u4E5F\u89C9\u5F97\u6B64\u4E8B\u6709\u635F\u767D\u5BB6\u7684\u989C\u9762\uFF0C\u5F3A\u884C\u547D\u4EE4\u5B89\u51E4\u665A\u4E0A\u56DE\u67F4\u623F\u7761\u3002</p><p>\u8FD9\u4E0B\u53EF\u7ED9\u4E86\u767D\u656C\u8F69\u8FD9\u8001\u72D7\u673A\u4F1A\uFF0C\u8D81\u8001\u5A46\u5B50\u6413\u9EBB\u5C06\u7684\u673A\u4F1A\uFF0C\u6478\u8FDB\u4E86\u67F4\u623F\uFF0C\u786C\u662F\u5C06\u719F\u7761\u7684\u5B89\u51E4\u751F\u751F\u5978\u6C61\uFF0C\u5723\u6D01\u7684\u5904\u5973\u8840\u6563\u5F00\u5728\u9ED1\u6697\u7684\u5730\u72F1\uFF0C\u4ECE\u59CB\u81F3\u7EC8\uFF0C\u518D\u75DB\u82E6\u5979\u4E5F\u6CA1\u6709\u53D1\u51FA\u4E00\u70B9\u58F0\u97F3\uFF0C\u54AC\u7740\u7259\u5173\uFF0C\u53CC\u76EE\u5706\u77AA\u7740\u5929\u68DA\uFF0C\u6CA1\u6709\u6CEA\uFF0C\u53EA\u6709\u6068\uFF0C\u65E0\u7A77\u65E0\u5C3D\u7684\u6068\u3002</p><p>\u7834\u5904\u4E4B\u540E\uFF0C\u767D\u656C\u8F69\u98DF\u9AD3\u77E5\u5473\uFF0C\u51E0\u6B21\u5077\u98DF\u90FD\u5F97\u4E86\u901E\uFF0C\u7EC8\u4E8E\u5728\u9664\u5915\u4E4B\u591C\u8BA9\u5730\u4E3B\u5A46\u6293\u4E2A\u6B63\u7740\u3002\u8001\u592A\u5A46\u53C8\u6C14\u53C8\u6068\uFF0C\u4E0D\u602A\u8272\u5FC3\u4E0D\u6B7B\u7684\u8001\u5934\u5B50\uFF0C\u8FC1\u6012\u5230\u65E0\u8F9C\u7684\u5B89\u51E4\u5934\u4E0A\uFF0C\u9A82\u5979\u72D0\u72F8\u7CBE\uFF0C\u9A9A\u8D27\uFF0C\u4E0B\u6D41\u79CD\uFF0C\u628A\u5979\u540A\u5230\u95E8\u524D\u7684\u8001\u69D0\u6811\u4E0A\u5265\u5149\u8863\u670D\u62FF\u5927\u76AE\u97AD\u62BD\uFF0C\u4E0A\u4E0A\u4E0B\u4E0B\u6CA1\u6709\u4E00\u5757\u597D\u8089\uFF0C\u6253\u5F97\u5979\u5944\u5944\u4E00\u606F\uFF0C\u5728\u5BD2\u98CE\u4E2D\u745F\u745F\u53D1\u6296\uFF0C\u773C\u770B\u4E0D\u662F\u6253\u6B7B\u5C31\u662F\u51BB\u6B7B\u3002</p><p>\u8FF7\u7CCA\u4E2D\u5979\u8BA9\u4EBA\u62B1\u4E86\u4E0B\u6765\uFF0C\u5306\u5FD9\u62AB\u4E86\u4E00\u4EF6\u5355\u8863\uFF0C\u5728\u5979\u7684\u8033\u8FB9\u53EB\u4E86\u58F0\u300C\u5FEB\u8D70\u300D\u3002</p><p>\u5979\u8FD8\u6765\u4E0D\u53CA\u770B\u6E05\u6069\u4EBA\u662F\u8C01\uFF0C\u5C31\u8863\u4E0D\u906E\u4F53\u5730\u9003\u51FA\u4E86\u767D\u5BB6\u5821\uFF0C\u614C\u4E0D\u62E9\u8DEF\u4E4B\u4E0B\u9003\u5230\u4E86\u65AD\u5934\u5D16\u8FB9\uFF0C\u8EAB\u540E\u661F\u661F\u70B9\u70B9\u7684\u706B\u628A\u5411\u5979\u5728\u56F4\u62E2\uFF0C\u518D\u65E0\u53BB\u8DEF\u53EF\u8A00\uFF0C\u5979\u5FC3\u4E0B\u4E00\u6A2A\uFF0C\u8DF3\u4E0B\u4E86\u60AC\u5D16\u3002</p><p>\u4E5F\u8BB8\u662F\u8001\u5929\u89C1\u601C\uFF0C\u547D\u4E0D\u8BE5\u7EDD\uFF0C\u5B89\u51E4\u8BA9\u5F53\u65F6\u7684\u4E00\u4E2A\u571F\u532A\u5934\u5B50\u9ED1\u864E\u6551\u8D70\uFF0C\u5165\u4E86\u532A\u5E2E\uFF0C\u6539\u540D\u6D77\u68E0\uFF0C\u624D\u7B97\u627E\u5230\u65B0\u751F\u4E4B\u8DEF\u2026\u2026</p><p>\u540E\u9762\u4E00\u622A\u6D77\u68E0\u8BED\u7109\u4E0D\u8BE6\uFF0C\u66F4\u6CA1\u6709\u8BF4\u8D77\u5979\u5728\u82E5\u5E72\u5E74\u540E\uFF0C\u662F\u600E\u6837\u638C\u5230\u532A\u5E2E\u5927\u6743\uFF0C\u7387\u90E8\u8840\u6D17\u767D\u5BB6\u5821\u7684\u4E8B\u60C5\uFF0C\u4F46\u51B7\u5982\u971C\u5DF2\u662F\u542C\u5F97\u6CEA\u6D41\u6EE1\u9762\uFF0C\u5979\u505A\u68A6\u4E5F\u60F3\u4E0D\u5230\u8FD9\u4E16\u4E0A\u4F1A\u6709\u5982\u6B64\u51C4\u60E8\u7684\u751F\u6D3B\uFF0C\u5982\u6B64\u4E11\u964B\u7684\u73B0\u5B9E\uFF0C\u5982\u6B64\u60B2\u82E6\u7684\u547D\u8FD0\u3002</p><p>\u4E0D\u77E5\u4E0D\u89C9\uFF0C\u4E24\u4E2A\u8EAB\u4EFD\u60AC\u6B8A\uFF0C\u5374\u540C\u6C14\u76F8\u601C\u7684\u5973\u4EBA\u7684\u5FC3\u5DF2\u6E10\u6E10\u8D34\u5230\u4E86\u4E00\u8D77\u3002</p><p>\u7FE0\u7AF9\u6D77\u5C71\u4E0B\u7684\u6850\u6EAA\u8FB9\uFF0C\u5728\u5EB7\u8001\u7237\u5B50\u7684\u4E3B\u6301\u4E0B\uFF0C\u5F00\u59CB\u4EA4\u6362\u4EBA\u8D28\u3002\u73B0\u573A\u53CC\u65B9\u7684\u4EE3\u8868\u662F\u6885\u5B50\u548C\u674E\u8D35\uFF0C\u6D77\u68E0\u4E0E\u767D\u5929\u5FB7\u672C\u4EBA\u90FD\u6CA1\u6709\u9732\u9762\u3002</p><p>\u767D\u5929\u5FB7\u65E9\u5DF2\u7ECF\u5E03\u7F6E\u4E86\u5927\u6279\u4EBA\u624B\uFF0C\u53EA\u8981\u51B7\u5982\u971C\u4E00\u8131\u9669\u5C31\u5411\u532A\u5E2E\u53D1\u52A8\u65E0\u60C5\u653B\u51FB\uFF0C\u5F53\u7136\uFF0C\u6D77\u68E0\u4E00\u65B9\u4E5F\u662F\u9AD8\u5EA6\u6212\u5907\u3002</p><p>\u51B7\u5982\u971C\u5411\u8DEF\u90FD\u8D70\u4E0D\u7A33\u7684\u9752\u7EA2\u8D70\u53BB\uFF0C\u6400\u7740\u5979\u9001\u4E86\u56DE\u53BB\u4EA4\u7ED9\u6885\u5B50\uFF0C\u770B\u7740\u5979\u4EEC\u6D88\u5931\u5728\u83BD\u83BD\u7AF9\u6D77\u4E4B\u4E2D\u3002</p><p>\u6789\u8D39\u5FC3\u673A\u7684\u767D\u5929\u5FB7\u4E0D\u660E\u767D\u51B7\u5982\u971C\u4E3A\u4F55\u8981\u7EF4\u62A4\u532A\u5E2E\uFF0C\u4E0D\u6562\u5F53\u9762\u6307\u8D23\u51B7\u5982\u971C\uFF0C\u53CD\u800C\u6BB7\u5207\u4F5C\u52BF\u8981\u6276\u5979\u4E0A\u8F7F\uFF0C\u8FB9\u6253\u542C\u6D77\u68E0\u7684\u957F\u76F8\u548C\u532A\u7A9D\u7684\u60C5\u51B5\u3002</p><p>\u51B7\u5982\u971C\u51B7\u51B7\u5730\u62D2\u7EDD\u4E86\u4ED6\uFF0C\u53EA\u8BF4\u4E86\u4E00\u53E5\uFF0C\u300C\u7D2F\u4E86\uFF0C\u56DE\u5427\u3002\u300D</p><p>\u5C0F\u8F7F\u8FDC\u53BB\uFF0C\u53D7\u5230\u4E86\u7F9E\u8FB1\u7684\u767D\u5929\u5FB7\u773C\u5192\u51F6\u5149\uFF0C\u7AD9\u7ACB\u4E86\u534A\u664C\uFF0C\u65B9\u4E00\u8DFA\u811A\uFF0C\u5FC3\u91CC\u53D1\u72E0\uFF0C\u5988\u4E2A\u5DF4\u5B50\u7684\uFF0C\u9752\u5C71\u4E0D\u6539\uFF0C\u7EFF\u6C34\u957F\u6D41\uFF0C\u6D77\u68E0\u3001\u51B7\u5982\u971C\uFF0C\u5C0F\u5A18\u76AE\u4EEC\u5C31\u6162\u6162\u7B49\u7740\u5927\u7237\u6765\u6536\u62FE\u5427\u3002</p><p>\u532A\u5E2E\u91CC\u9762\uFF0C\u5927\u4F19\u5BF9\u82DF\u6D3B\u4E0B\u6765\u904D\u4F53\u9CDE\u4F24\u7684\u9752\u7EA2\u5883\u9047\u4E4B\u60E8\u65E0\u4E0D\u75DB\u5165\u5FC3\u80BA\uFF0C\u5927\u9A82\u51FA\u58F0\uFF0C\u7EB7\u7EB7\u63D0\u62A2\u8981\u6740\u4E0B\u5C71\u53BB\uFF0C\u8A93\u6740\u65E0\u4EBA\u6027\u7684\u767D\u5929\u5FB7\u3002</p><p>\u6D77\u68E0\u4E00\u9762\u6D3E\u91D1\u82B1\u63A5\u4E00\u4E2A\u8001\u4E2D\u533B\u4E0A\u5C71\u6551\u6CBB\u9752\u7EA2\uFF0C\u4E00\u9762\u963B\u6B62\u4E86\u90E8\u4E0B\u7684\u76F2\u76EE\u51B2\u52A8\uFF0C\u8981\u4ED6\u4EEC\u7B49\u5F85\u65F6\u673A\uFF0C\u518D\u51B3\u6B7B\u6218\u3002</p><p>\u5979\u4E5F\u95EE\u8D77\u9752\u7EA2\u540C\u6837\u4E00\u4E2A\u95EE\u9898\uFF0C\u767D\u5929\u5FB7\u5230\u5E95\u662F\u4EC0\u4E48\u6837\u4E00\u4E2A\u4EBA\u3002</p><p>\u300C\u4ED6\u4E0D\u662F\u4EBA\uFF0C\u662F\u755C\u751F\u3002\u300D\u9752\u7EA2\u4E00\u63D0\u8D77\u90A3\u4E2A\u6076\u68CD\u5C31\u73E0\u6CEA\u6D9F\u6D9F\uFF0C\u65AD\u65AD\u7EED\u7EED\u5730\u628A\u5979\u7684\u906D\u9047\u8BB2\u4E86\u4E2A\u5927\u6982\u3002</p><p>\u6D77\u68E0\u7A81\u7136\u83AB\u540D\u5176\u5999\u5730\u81EA\u8A00\u9053\uFF0C\u300C\u83AB\u975E\u4E0D\u662F\u4ED6\uFF1F\u300D</p><p>\u4E0D\u4E45\uFF0C\u53EF\u601C\u9752\u7EA2\u75C5\u60C5\u53CD\u590D\uFF0C\u4E00\u76F4\u9AD8\u70E7\u4E0D\u9000\uFF0C\u7EC8\u56E0\u4F24\u52BF\u8FC7\u91CD\uFF0C\u56DE\u5929\u4E4F\u672F\uFF0C\u5982\u5B63\u672A\u7684\u9752\u7EA2\u822C\u51CB\u96F6\uFF0C\u968F\u98CE\u901D\u53BB\u4E86\u3002\u4F17\u5144\u5F1F\u59D0\u59B9\u56F4\u5728\u5979\u7684\u8EAB\u8FB9\u4E3E\u67AA\u60B2\u9E23\u3002</p><p>\u67AA\u58F0\u5982\u540C\u51C4\u5389\u7684\u54ED\u568E\uFF0C\u5212\u5F00\u5C71\u8C37\u7684\u6C89\u5BC2\uFF0C\u4E45\u4E45\u56DE\u8361\u3002</p><p>\u6B64\u540E\u4E24\u4E2A\u591A\u6708\u98CE\u5E73\u6D6A\u9759\uFF0C\u5218\u6EA2\u4E4B\u4E00\u542C\u5230\u592A\u592A\u88AB\u7ED1\u7968\u7684\u6D88\u606F\uFF0C\u6B21\u65E5\u5C31\u4ECE\u7701\u5E9C\u8FDE\u591C\u8D76\u4E86\u56DE\u6765\uFF0C\u6B63\u5DE7\u63A5\u7740\u4E86\u5E73\u5B89\u83B7\u6551\u7684\u51B7\u5982\u971C\uFF0C\u5FC3\u6709\u4F59\u60B8\uFF0C\u628A\u6CA1\u6709\u5C3D\u5230\u4FDD\u62A4\u4E4B\u804C\u7684\u767D\u5929\u5FB7\u75DB\u9A82\u4E86\u4E00\u987F\u3002</p><p>\u767D\u5929\u5FB7\u4E0D\u514D\u53C8\u751F\u4E86\u4E00\u573A\u95F7\u6C14\u3002\u4E0D\u8FC7\u6B64\u4E8B\u6D89\u53CA\u9762\u4E0D\u5927\uFF0C\u5305\u7792\u5F97\u7D27\uFF0C\u9664\u4E86\u5F53\u4E8B\u4EBA\u5FC3\u77E5\u809A\u660E\u4E4B\u5916\uFF0C\u6CA1\u6709\u591A\u5C11\u4EBA\u77E5\u9053\uFF0C\u4E5F\u5C31\u8FC7\u53BB\u4E86\u3002</p><p>\u5510\u8001\u50A9\u5728\u4E00\u4E2A\u5348\u591C\u91CC\u8DF3\u8FDB\u6C85\u6C34\u6CB3\u81EA\u6740\uFF0C\u6B7B\u5F97\u9759\u6084\u6084\u7684\uFF0C\u9664\u4E86\u503A\u4E3B\u51E0\u4E4E\u6CA1\u8C01\u4F1A\u60E6\u8BB0\u3002\u5C38\u4F53\u6CE1\u4E86\u51E0\u5929\uFF0C\u9762\u76EE\u5168\u975E\uFF0C\u8BA9\u4EBA\u62FF\u7834\u8349\u5E2D\u5377\u4E86\u6254\u8FDB\u4E86\u4E71\u846C\u5C97\u3002</p><p>\u6570\u65E5\u540E\uFF0C\u83B7\u91CA\u51FA\u72F1\u7684\u5510\u725B\u62FF\u8001\u7236\u7684\u8863\u88F3\u57CB\u4E86\u4E2A\u5C0F\u575F\uFF0C\u53E9\u4E86\u51E0\u4E2A\u54CD\u5934\uFF0C\u5B64\u8EAB\u4E00\u4EBA\u5934\u4E5F\u4E0D\u56DE\u5730\u8FDB\u4E86\u5C71\u3002</p><p>\u590D\u4EC7\u7684\u79CD\u5B50\u4E8E\u65E0\u58F0\u4E4B\u4E2D\u5728\u75AF\u72C2\u6ECB\u957F\uFF0C\u8BE5\u53D1\u751F\u7684\u603B\u4F1A\u53D1\u751F\u7684\u3002</p>',66);function t(i,n){return r}var _=a(e,[["render",t]]);export{_ as default};
