import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_07-\u53D7\u8FB1" tabindex="-1"><a class="header-anchor" href="#_07-\u53D7\u8FB1" aria-hidden="true">#</a> 07 \u53D7\u8FB1</h1><p>\u6628\u665A\u534A\u591C\u65F6\u5206\uFF0C\u767D\u5B5D\u5112\u88AB\u62C9\u5230\u5802\u4E0A\u4E25\u5211\u903C\u4F9B\uFF0C\u76F4\u5230\u5929\u4EAE\u624D\u88AB\u6295\u56DE\u72F1\u4E2D\u3002\u5B59\u5929\u7FBD\u77E5\u9053\u5F97\u6E05\u6E05\u695A\u695A\uFF0C\u52A8\u624B\u5939\u788E\u767D\u5B5D\u5112\u817F\u9AA8\u7684\u5C31\u6709\u4ED6\u3002\u6B63\u662F\u77E5\u9053\u767D\u5B5D\u5112\u5211\u4F24\u6781\u91CD\uFF0C\u660F\u8FF7\u4E0D\u9192\uFF0C\u4ED6\u624D\u9886\u4E39\u5A18\u524D\u6765\u63A2\u89C6\u3002</p><p>\u4E39\u5A18\u6251\u5230\u7262\u95E8\u4E0A\uFF0C\u4E00\u624B\u4F38\u8FDB\u6728\u6805\uFF0C\u62DA\u547D\u53BB\u62C9\u4E08\u592B\uFF0C\u54ED\u9053\uFF1A\u300C\u76F8\u516C\uFF01\u76F8\u516C\uFF01\u300D</p><p>\u95EE\u8D77\u4E08\u592B\u5728\u72F1\u4E2D\u7684\u60C5\u5F62\uFF0C\u5B59\u5929\u7FBD\u603B\u662F\u541E\u541E\u5410\u5410\u8BF4\uFF1A\u300C\u8FD8\u597D\u8FD8\u597D\u3002\u300D\u53C8\u8BF4\u8FD9\u6848\u5B50\u7684\u5185\u60C5\u590D\u6742\uFF0C\u4E3B\u5B98\u50AC\u903C\u7684\u7D27\uFF0C\u8BF4\u7F62\u5509\u58F0\u53F9\u6C14\u3002\u4E39\u5A18\u5BDF\u989C\u89C2\u8272\uFF0C\u5FC3\u91CC\u4E00\u76F4\u7D27\u7D27\u6525\u7740\u3002</p><p>\u5979\u77E5\u9053\u4E08\u592B\u751F\u6027\u56FA\u6267\uFF0C\u514D\u4E0D\u4E86\u5403\u82E6\uFF0C\u591A\u534A\u8FD8\u4F1A\u7528\u5211\u3002\u4F46\u7528\u5211\u9876\u591A\u4E5F\u5C31\u662F\u6253\u4E0A\u51E0\u677F\uFF0C\u4E07\u6CA1\u60F3\u5230\u7ADF\u4F1A\u7528\u4E86\u8FD9\u822C\u91CD\u5211\uFF0C\u76F4\u628A\u4E00\u4E2A\u624B\u65E0\u7F1A\u9E21\u4E4B\u529B\u7684\u8001\u7AE5\u751F\u5F53\u6210\u6C5F\u6D0B\u5927\u76D7\u3002</p><p>\u300C\u76F8\u516C\uFF0C\u4F60\u9192\u9192\u554A\uFF01\u300D\u4E39\u5A18\u4E0D\u987E\u6728\u6869\u4E0A\u6C61\u8FF9\u6591\u6591\uFF0C\u6574\u4E2A\u4EBA\u90FD\u6251\u5728\u4E0A\u9762\uFF0C\u5927\u7EA2\u7F57\u88D9\u6CBE\u4E0A\u6CE5\u571F\u3002</p><p>\u4EFB\u5979\u5982\u4F55\u54ED\u53EB\uFF0C\u4E08\u592B\u4F9D\u7136\u660F\u8FF7\u4E0D\u9192\u3002</p><p>\u65C1\u8FB9\u7684\u56DA\u72AF\u6728\u7136\u770B\u7740\u8FD9\u4E00\u5E55\uFF0C\u5F77\u4F5B\u4E00\u7FA4\u884C\u5C38\u8D70\u8089\uFF0C\u5BF9\u4E39\u5A18\u7684\u60B2\u6078\u65E0\u52A8\u4E8E\u8877\u3002</p><p>\u5B59\u5929\u7FBD\u6276\u8D77\u4E39\u5A18\uFF0C\u5343\u54C4\u4E07\u529D\u5730\u62C9\u5979\u79BB\u5F00\u7262\u623F\u3002</p><p>\u4E39\u5A18\u6765\u65F6\u6EE1\u5FC3\u5E0C\u5180\uFF0C\u56DE\u53BB\u65F6\u5374\u54ED\u4E86\u4E00\u8DEF\uFF0C\u5230\u4E86\u5BA2\u6808\uFF0C\u5979\u5954\u8FDB\u623F\u5185\u5FCD\u4E0D\u4F4F\u5927\u653E\u60B2\u58F0\uFF0C\u4F0F\u5728\u5E8A\u4E0A\u568E\u5555\u75DB\u54ED\u8D77\u6765\u3002</p><p>\u300C\u5A18\uFF01\u300D\u7389\u83B2\u542C\u89C1\u54ED\u58F0\uFF0C\u8FDE\u5FD9\u8FDB\u6765\uFF0C\u300C\u89C1\u7740\u7239\u7239\u4E86\u5417\uFF1F\u4ED6\u600E\u4E48\u4E86\uFF1F\u300D</p><p>\u5B59\u5929\u7FBD\u5411\u5979\u6447\u4E86\u6447\u624B\uFF0C\u4E00\u9762\u51FA\u6765\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u4F60\u7239\u7239\u5728\u72F1\u4E2D\u53D7\u4E86\u5211\u2014\u2014\u300D\u89C1\u7389\u83B2\u60CA\u6050\u5730\u77AA\u5927\u773C\u775B\uFF0C\u5B59\u5929\u7FBD\u5FD9\u9053\uFF1A\u300C\u83AB\u614C\uFF0C\u4F60\u5A18\u5FC3\u91CC\u96BE\u53D7\uFF0C\u4F60\u82E5\u518D\u54ED\u6211\u53EF\u6CA1\u6CD5\u5B50\u4E86\u3002\u300D</p><p>\u300C\u53EF\u6211\u7239\u7239\u2026\u2026\u300D</p><p>\u300C\u53EA\u662F\u53D7\u4E86\u70B9\u4F24\uFF0C\u4E0D\u59A8\u4E8B\u7684\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u8FD8\u662F\u7B2C\u4E00\u6B21\u79BB\u7389\u83B2\u8FD9\u4E48\u8FD1\uFF0C\u4EE5\u5F80\u8FDC\u8FDC\u770B\u53BB\uFF0C\u53EA\u89C9\u5979\u9762\u76EE\u4E0E\u4E39\u5A18\u3001\u767D\u96EA\u83B2\u76F8\u4EFF\uFF0C\u8273\u4E0D\u53CA\u4E39\u5A18\uFF0C\u7709\u5B87\u95F4\u7684\u82F1\u6C14\u7F8E\u8272\u53C8\u4E0D\u53CA\u767D\u96EA\u83B2\uFF0C\u6B64\u65F6\u8D34\u8FD1\u4E86\u770B\uFF0C\u624D\u53D1\u73B0\u7389\u83B2\u7684\u5A07\u67D4\u522B\u6709\u4E00\u756A\u7F8E\u6001\uFF0C\u53C8\u7EAF\u53C8\u51C0\uFF0C\u808C\u80A4\u9C9C\u5AE9\u5F97\u5B9B\u82E5\u900F\u660E\u3002</p><p>\u4ED6\u706B\u8FA3\u8FA3\u7684\u76EE\u5149\u4F7F\u5F97\u7389\u83B2\u5782\u4E0B\u5934\u53BB\uFF0C\u53C8\u7F9E\u53C8\u6025\uFF0C\u4E0D\u77E5\u5982\u4F55\u662F\u597D\u3002</p><p>\u8BF4\u8BDD\u95F4\uFF0C\u82F1\u83B2\u4E5F\u51FA\u6765\u4E86\uFF0C\u63A2\u5934\u63A2\u8111\u5411\u8FD9\u8FB9\u5F20\u671B\uFF0C\u5C0F\u58F0\u5524\u9053\uFF1A\u300C\u5A18\u2026\u2026\u300D</p><p>\u5B59\u5929\u7FBD\u610F\u8BC6\u5230\u81EA\u5DF1\u7684\u5931\u6001\uFF0C\u5FD9\u9053\uFF1A\u300C\u4F60\u53BB\u7167\u5E94\u5F1F\u5F1F\uFF0C\u8FD9\u8FB9\u6709\u6211\u5462\u3002\u300D</p><p>\u4E39\u5A18\u6D95\u6CEA\u4EA4\u6D41\uFF0C\u54ED\u5F97\u5A07\u8EAF\u53D1\u8F6F\u3002\u5B59\u5929\u7FBD\u8BA9\u5979\u5C3D\u60C5\u54ED\u4E86\u534A\u664C\uFF0C\u7136\u540E\u62B1\u7740\u5979\u7684\u80A9\u8180\uFF0C\u8F7B\u8F7B\u6276\u5979\u8D77\u6765\uFF0C\u6EE1\u5FC3\u60F3\u7740\u4E39\u5A18\u4F1A\u4E00\u5934\u624E\u5728\u4ED6\u6000\u91CC\u54ED\u4E2A\u75DB\u5FEB\uFF0C\u4E0D\u6599\u4E39\u5A18\u9999\u80A9\u4E00\u6323\uFF0C\u79BB\u5F00\u4E86\u4ED6\u7684\u624B\u638C\u3002\u52A8\u4F5C\u867D\u8F6F\uFF0C\u5374\u6709\u79CD\u51B3\u7EDD\u7684\u610F\u5473\uFF0C\u5206\u660E\u662F\u6709\u4E86\u6212\u5FC3\u3002</p><p>\u4E39\u5A18\u54ED\u58F0\u6E10\u6B47\uFF0C\u54FD\u54BD\u7740\u95EE\u9053\uFF1A\u300C\u6211\u4E08\u592B\u7A76\u7ADF\u72AF\u4E86\u4EC0\u4E48\u7F6A\uFF1F\u300D</p><p>\u5B59\u5929\u7FBD\u6C89\u9ED8\u7247\u523B\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u5BF9\u4E0D\u8D77\uFF0C\u6211\u9A97\u4E86\u4F60\u3002\u300D</p><p>\u4E39\u5A18\u5782\u5934\u4E0D\u54CD\uFF0C\u6CEA\u73E0\u4ECE\u7389\u988A\u4E0A\u4E32\u4E32\u6EDA\u843D\u3002</p><p>\u300C\u6500\u54AC\u5C0A\u592B\u7684\u5E76\u975E\u76D7\u8D3C\uFF0C\u5C0A\u592B\u53D7\u5211\u4E5F\u4E0D\u662F\u56E0\u4E3A\u9500\u8D43\uFF0C\u800C\u662F\u56E0\u4E3A\u2026\u2026\u300D\u5B59\u5929\u7FBD\u505C\u987F\u4E86\u4E00\u4F1A\u513F\uFF0C\u5728\u4E39\u5A18\u6CEA\u76C8\u76C8\u7684\u6CE8\u89C6\u4E0B\uFF0C\u8F7B\u8F7B\u5410\u51FA\u4E24\u4E2A\u5B57\uFF1A\u300C\u8C0B\u53CD\u3002\u300D</p><p>\u4E39\u5A18\u7684\u773C\u524D\u4E00\u9ED1\uFF0C\u51E0\u4E4E\u6655\u53A5\u3002\u8C0B\u53CD\u662F\u706D\u4E5D\u65CF\u7684\u5927\u7F6A\uFF0C\u4E00\u65E6\u5750\u5B9E\uFF0C\u83AB\u8BF4\u5979\u4EEC\u4E00\u5BB6\uFF0C\u5C31\u662F\u4E0E\u5979\u4EEC\u6CBE\u4EB2\u5E26\u6545\u7684\u4EB2\u53CB\u4E5F\u5728\u52AB\u96BE\u9003\u3002</p><p>\u300C\u6211\u662F\u6015\u4F60\u62C5\u5FC3\uFF0C\u624D\u7792\u4E86\u4F60\u3002\u8FD9\u4E48\u91CD\u7684\u7F6A\u540D\uFF0C\u6211\u6015\u4F60\u6491\u4E0D\u4F4F\u3002\u5C0A\u592B\u82E5\u5750\u5B9E\u662F\u8C0B\u53CD\uFF0C\u53EA\u6015\u2026\u2026\u300D</p><p>\u300C\u546F\u300D\u7684\u4E00\u58F0\uFF0C\u4E00\u53EA\u74F7\u7897\u8DCC\u5728\u5730\u4E0A\uFF0C\u6454\u5F97\u7C89\u788E\u3002</p><p>\u7389\u83B2\u6D17\u624B\u71AC\u4E86\u7FB9\u6C64\uFF0C\u521A\u8D70\u5230\u95E8\u524D\uFF0C\u5C31\u542C\u5230\u300C\u8C0B\u53CD\u300D\uFF0C\u60CA\u60E7\u4E4B\u4E0B\uFF0C\u5931\u624B\u6454\u788E\u4E86\u6C64\u7897\u3002</p><p>\u4E39\u5A18\u811A\u6B65\u53D1\u8F6F\u5730\u8D70\u5230\u95E8\u53E3\uFF0C\u300C\u7389\u83B2\uFF0C\u4F60\u5148\u56DE\u623F\u3002\u300D\u7B49\u5973\u513F\u8D70\u8FDC\uFF0C\u5979\u63A9\u4E0A\u95E8\uFF0C\u8F7B\u8F7B\u8BF4\u9053\uFF1A\u300C\u8FD9\u600E\u4E48\u53EF\u80FD\u2026\u2026\u300D\u8BF4\u7740\u8F6F\u8F6F\u5750\u5728\u5730\u4E0A\u3002</p><p>\u300C\u4E39\u5A18\uFF01\u300D\u5B59\u5929\u7FBD\u8FDE\u5FD9\u6276\u4F4F\u5979\u7684\u8EAB\u5B50\u3002</p><p>\u8FD9\u6B21\u4E39\u5A18\u6CA1\u6709\u6323\u624E\uFF0C\u5979\u9999\u80A9\u4E0D\u4F4F\u8F7B\u98A4\uFF0C\u826F\u4E45\u624D\u9053\uFF1A\u300C\u96EA\u83B2\u5462\uFF1F\u5979\u4E5F\u662F\u8C0B\u2026\u2026\u5417\uFF1F\u300D</p><p>\u5B59\u5929\u7FBD\u70B9\u4E86\u70B9\u5934\uFF0C\u300C\u4F60\u5148\u5750\u8D77\u6765\uFF0C\u6211\u6162\u6162\u544A\u8BC9\u4F60\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u643A\u6276\u7740\u4E39\u5A18\u5750\u5728\u6905\u4E2D\uFF0C\u5C06\u859B\u971C\u7075\u5982\u4F55\u6500\u54AC\u767D\u5B5D\u5112\u4ED4\u7EC6\u8BF4\u4E86\u3002\u4E39\u5A18\u75F4\u75F4\u542C\u7740\uFF0C\u5583\u5583\u9053\uFF1A\u300C\u5979\u4E3A\u4EC0\u4E48\u8981\u6500\u54AC\u6211\u5BB6\u76F8\u516C\uFF1F\u5979\u4E3A\u4EC0\u4E48\u8981\u6500\u54AC\u96EA\u83B2\uFF1F\u6211\u4EEC\u8DDF\u5979\u7D20\u4E0D\u76F8\u8BC6\uFF0C\u5979\u4E3A\u4EC0\u4E48\u8981\u8FD9\u6837\u5BB3\u6211\u4EEC\u2026\u2026\u300D</p><p>\u5B59\u5929\u7FBD\u6B32\u8A00\u53C8\u6B62\u3002\u4E39\u5A18\u62C9\u4F4F\u4ED6\u7684\u624B\uFF0C\u51C4\u58F0\u9053\uFF1A\u300C\u6C42\u4F60\u544A\u8BC9\u6211\uFF0C\u5979\u4E3A\u4EC0\u4E48\u8981\u6500\u54AC\u6211\u4EEC\u4E00\u5BB6\uFF1F\u300D</p><p>\u5B59\u5929\u7FBD\u659F\u914C\u7740\u8BF4\u9053\uFF1A\u300C\u5176\u5B9E\u2026\u2026\u5C0A\u592B\u7684\u8BC1\u636E\u5DF2\u7ECF\u6709\u4E86\u3002\u300D</p><p>\u300C\u662F\u4EC0\u4E48\uFF1F\uFF01\u300D</p><p>\u5B59\u5929\u7FBD\u6447\u4E86\u6447\u5934\u3002</p><p>\u4E39\u5A18\u80FD\u770B\u51FA\u6765\uFF0C\u4ED6\u5E76\u4E0D\u662F\u4E0D\u77E5\u9053\uFF0C\u800C\u662F\u4E0D\u80FD\u8BF4\u3002\u671D\u5EF7\u5904\u7F6E\u8C0B\u53CD\u5411\u6765\u523B\u6BD2\uFF0C\u7565\u6709\u7275\u8FDE\u4FBF\u6740\u4F10\u65E0\u7B97\u3002\u5BF9\u8C0B\u53CD\u7684\u6848\u5B50\u4EBA\u4EBA\u907F\u4E4B\u552F\u6050\u4E0D\u53CA\uFF0C\u4ED6\u4E00\u4E2A\u5B98\u5DEE\uFF0C\u8BF4\u5230\u4E86\u8FD9\u4E00\u6B65\uFF0C\u5DF2\u7ECF\u662F\u5192\u4E86\u5929\u5927\u7684\u5E72\u7CFB\u3002</p><p>\u5B59\u5929\u7FBD\u52A8\u7684\u5374\u662F\u53E6\u4E00\u756A\u5FC3\u601D\u3002</p><p>\u960E\u7F57\u671B\u66FE\u8BF4\u674F\u82B1\u6751\u8FD9\u4E9B\u5973\u5B50\u7531\u7740\u4F17\u4EBA\u53BB\u62D4\u5934\u7B79\u3002\u73B0\u5728\u6848\u5B50\u5DF2\u7ECF\u5448\u62A5\u4E0A\u53BB\uFF0C\u628A\u4E39\u5A18\u4E00\u5BB6\u5C3D\u884C\u4E0B\u72F1\u4E5F\u65E0\u4E0D\u53EF\uFF0C\u4F46\u8FD9\u6837\u4E00\u4E2A\u5A07\u6EF4\u6EF4\u7684\u7F8E\u8273\u5987\u4EBA\uFF0C\u653E\u5728\u7262\u4E2D\u8981\u4E0D\u4E86\u51E0\u65E5\uFF0C\u5C31\u88AB\u72F1\u5352\u4EEC\u73A9\u6210\u6B8B\u82B1\u8D25\u67F3\u3002</p><p>\u4F55\u51B5\u4E39\u5A18\u98CE\u60C5\u5165\u9AA8\uFF0C\u5F3A\u5978\u672A\u514D\u5174\u5473\u7D22\u7136\uFF0C\u600E\u751F\u60F3\u4E2A\u6CD5\u5B50\uFF0C\u9A97\u5F97\u5979\u81EA\u5DF1\u732E\u51FA\u8EAB\u5B50\u4EFB\u81EA\u5DF1\u800D\u5F04\uFF0C\u90A3\u4E00\u756A\u65D6\u65CE\u98CE\u5149\u4E0E\u5A01\u903C\u5F3A\u66B4\u53C8\u662F\u4E0D\u540C\u3002\u603B\u4E4B\uFF0C\u4ED6\u8981\u7684\u662F\u901A\u5978\uFF0C\u800C\u4E0D\u662F\u5F3A\u5978\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5E26\u4E0A\u94C1\u67B7\u3001\u8DB3\u68B0\u7684\u7B2C\u4E8C\u5929\uFF0C\u767D\u96EA\u83B2\u5C31\u5C1D\u5230\u4E86\u8FD9\u4E9B\u5211\u5177\u7684\u9634\u9669\u6BD2\u8FA3\u3002\u8FD9\u9762\u94C1\u67B7\u51E0\u4E4E\u4E0E\u5979\u8EAB\u9AD8\u4E00\u6837\u957F\uFF0C\u5BBD\u5EA6\u8D85\u8FC7\u4E24\u80A9\uFF0C\u56DB\u56DB\u65B9\u65B9\u5957\u5728\u9888\u4E0A\uFF0C\u4EF0\u8EBA\u65F6\u67B7\u9762\u6709\u4E00\u534A\u9876\u5728\u9888\u540E\uFF0C\u6574\u4E2A\u80CC\u90E8\u90FD\u662F\u60AC\u7A7A\u3002\u7FFB\u8FC7\u6765\uFF0C\u4E24\u8098\u591F\u4E0D\u5230\u5730\u9762\uFF0C\u53EA\u80FD\u534A\u8DB4\u534A\u8DEA\u3002\u4FA7\u8EBA\u4E0A\u8EAB\u8FD8\u597D\u53D7\u4E00\u4E9B\uFF0C\u4F46\u5979\u53CC\u811A\u53C8\u88AB\u8DB3\u677B\u9501\u4F4F\uFF0C\u59CB\u7EC8\u5206\u5F00\u4E09\u5C3A\uFF0C\u653E\u5E73\u4E0B\u6765\uFF0C\u8170\u90E8\u5C31\u50CF\u62E7\u65AD\u4E00\u822C\u96BE\u53D7\u3002</p><p>\u800C\u90A3\u5E2E\u72F1\u5352\u6253\u5236\u94C1\u67B7\u65F6\uFF0C\u6545\u610F\u628A\u9888\u5708\u8BBE\u5728\u79BB\u67B7\u9762\u4E24\u5C3A\u7684\u5730\u65B9\uFF0C\u907F\u5F00\u4E86\u91CD\u5FC3\u3002</p><p>\u5355\u7528\u4E24\u80A9\u65E0\u6CD5\u7A33\u4F4F\u94C1\u67B7\uFF0C\u8FD8\u9700\u8981\u4E24\u624B\u4F7F\u529B\u3002\u767D\u96EA\u83B2\u53EA\u597D\u9760\u5728\u5BA4\u89D2\uFF0C\u5C06\u94C1\u67B7\u4E00\u7AEF\u653E\u5728\u5899\u4E0A\uFF0C\u4E00\u8FB9\u66F2\u8D77\u53CC\u819D\uFF0C\u52C9\u5F3A\u5408\u6210\u4E00\u4E2A\u4E09\u89D2\u5F62\uFF0C\u9876\u7740\u94C1\u67B7\u3002</p><p>\u859B\u971C\u7075\u5728\u7262\u5916\u7684\u65F6\u5019\u6BD4\u7262\u5185\u66F4\u591A\uFF0C\u65E0\u8BBA\u54EA\u4E2A\u72F1\u5352\uFF0C\u53EA\u8981\u5174\u81F4\u4E00\u6765\uFF0C\u5C31\u628A\u5979\u62D6\u51FA\u53BB\uFF0C\u63B0\u5F00\u53CC\u817F\u4E00\u901A\u72E0\u808F. \u960E\u7F57\u671B\u5BF9\u4E8E\u8FD9\u4E2A\u8D27\u771F\u4EF7\u5B9E\u7684\u767D\u83B2\u9006\u532A\u5475\u62A4\u5907\u81F3\uFF0C\u6BCF\u5929\u90FD\u8981\u63D0\u5BA1\u4E09\u4E24\u6B21\u3002\u65E0\u8BBA\u5728\u7262\u4E2D\u8FD8\u662F\u5728\u516C\u5802\u4E0A\uFF0C\u859B\u971C\u7075\u90FD\u518D\u672A\u7A7F\u8FC7\u8863\u670D\uFF0C\u90A3\u4E9B\u72F1\u5352\u5C31\u50CF\u4E00\u7FA4\u62AB\u7740\u516C\u670D\u7684\u91CE\u517D\uFF0C\u53D8\u7740\u82B1\u6837\u73A9\u5F04\u5979\u7684\u8089\u4F53\uFF0C\u4E00\u8FB9\u6345\u5F04\uFF0C\u4E00\u8FB9\u903C\u95EE\u767D\u83B2\u6559\u7684\u60C5\u5F62\u3002</p><p>\u859B\u971C\u7075\u4E00\u53E3\u54AC\u5B9A\u90A3\u8001\u4EBA\u662F\u5979\u7239\u7239\uFF0C\u4E24\u4EBA\u5728\u674F\u82B1\u6751\u62FF\u4E86\u4E66\u4FE1\uFF0C\u51C6\u5907\u524D\u5F80\u5E7F\u4E1C\uFF0C\u5176\u5B83\u4E00\u65E0\u6240\u77E5\u3002\u90A3\u4E9B\u72F1\u5352\u4F3C\u4E4E\u5E76\u4E0D\u6025\u4E8E\u6487\u6E05\u767D\u96EA\u83B2\uFF0C\u53CD\u800C\u7EDE\u5C3D\u8111\u6C41\u5F25\u8865\u5176\u4E2D\u7684\u6F0F\u6D1E\uFF0C\u5C31\u50CF\u662F\u4E24\u8FB9\u5408\u8C0B\uFF0C\u8981\u7F6E\u767D\u96EA\u83B2\u4E8E\u6B7B\u5730\u3002</p><p>\u5F80\u5F80\u5728\u573A\u7684\u7537\u4EBA\u90FD\u5E72\u8FC7\u5979\u4E00\u904D\uFF0C\u5BA1\u8BAF\u624D\u544A\u4E00\u6BB5\u843D\u3002\u859B\u971C\u7075\u4ECD\u548C\u6765\u65F6\u4E00\u6837\uFF0C\u88AB\u4EBA\u7275\u7740\u9888\u4E2D\u7684\u94C1\u94FE\uFF0C\u8D64\u8EAB\u88F8\u4F53\u5730\u79BB\u5F00\u5927\u5802\uFF0C\u53EA\u662F\u4F53\u5185\u591A\u4E86\u4E00\u7FA4\u7537\u4EBA\u7684\u7CBE\u6DB2\u3002</p><p>\u8FD9\u65E5\u4E0A\u7F62\u5802\uFF0C\u5DF2\u5230\u4E86\u5348\u996D\u65F6\u5019\u3002\u72F1\u5352\u63D0\u4E86\u6876\u8FA8\u4E0D\u51FA\u989C\u8272\u7684\u7C73\u7CA5\uFF0C\u62FF\u4E86\u51E0\u4E2A\u7A9D\u5934\u4E0B\u5230\u5730\u7262\uFF0C\u7528\u996D\u52FA\u6572\u4E86\u6572\u94C1\u6805\uFF0C\u56B7\u9053\uFF1A\u300C\u6328\u808F\u7684\u8D27\uFF0C\u8FD8\u4E0D\u8D77\u6765\uFF1F\u300D</p><p>\u4ECE\u5802\u4E0A\u4E0B\u6765\uFF0C\u859B\u971C\u7075\u51E0\u4E4E\u53EA\u5269\u4E86\u4E00\u53E3\u6C14\uFF0C\u5979\u52C9\u5F3A\u4F38\u51FA\u624B\uFF0C\u628A\u7A3B\u8349\u4E0B\u4E00\u53EA\u7834\u7897\u63A8\u5230\u6805\u680F\u8FB9\u3002</p><p>\u72F1\u5352\u6405\u4E86\u52FA\u996D\u5012\u5728\u91CC\u9762\uFF0C\u6254\u4E86\u4E24\u4E2A\u7A9D\u5934\uFF0C\u626C\u957F\u800C\u53BB\u3002</p><p>\u859B\u971C\u7075\u6162\u6162\u5730\u8D77\u8EAB\uFF0C\u62FE\u4E86\u53EA\u7A9D\u5934\uFF0C\u6162\u6162\u5543\u7740\u3002\u7A9D\u5934\u662F\u7528\u7389\u7C73\u9762\u505A\u7684\uFF0C\u53C8\u5E72\u53C8\u786C\uFF0C\u8FD8\u6709\u4E00\u80A1\u9709\u5473\u3002\u5582\u732A\u732A\u4E5F\u4E0D\u4F1A\u559C\u6B22\uFF0C\u4F46\u5979\u4EEC\u53EA\u6709\u9760\u5B83\uFF0C\u624D\u80FD\u6D3B\u4E0B\u53BB\u3002</p><p>\u5C06\u624B\u9888\u9501\u5728\u4E00\u8D77\u7684\u94C1\u67B7\uFF0C\u4F7F\u5403\u996D\u8FD9\u6837\u7B80\u5355\u7684\u4E8B\u4E5F\u53D8\u5F97\u8270\u96BE\uFF0C\u767D\u96EA\u83B2\u65E0\u6CD5\u62E3\u8D77\u5730\u4E0A\u7684\u7A9D\u5934\uFF0C\u9760\u7740\u5899\u4E00\u52A8\u4E0D\u52A8\u3002</p><p>\u859B\u971C\u7075\u6CA1\u6709\u7406\u5979\uFF0C\u4F46\u662F\u4E5F\u6CA1\u6709\u78B0\u90A3\u4E2A\u5C5E\u4E8E\u767D\u96EA\u83B2\u7684\u7A9D\u5934\u3002\u52C9\u5F3A\u54BD\u4E0B\u7C97\u783A\u7684\u7A9D\u5934\uFF0C\u859B\u971C\u7075\u6572\u4E86\u6572\u6805\u680F\u3002</p><p>\u80E1\u4E25\u4E0D\u8010\u70E6\u5730\u8FC7\u6765\uFF0C\u300C\u548B\u4E2A\u4E86\uFF1F\u300D</p><p>\u300C\u6C34\u2026\u2026\u300D</p><p>\u80E1\u4E25\u9A82\u9A82\u54A7\u54A7\u53D6\u4E86\u74E2\u6C34\uFF0C\u9694\u7740\u6805\u680F\u6CFC\u8FDB\u7897\u91CC\uFF0C\u4E00\u591A\u534A\u90FD\u6D12\u5728\u4E86\u5916\u9762\u3002</p><p>\u300C\u54A6\uFF1F\u300D\u80E1\u4E25\u8E72\u4E0B\u6765\uFF0C\u62FF\u8D77\u7A9D\u5934\u5728\u624B\u91CC\u634F\u7740\uFF0C\u300C\u767D\u6355\u5934\uFF0C\u60A8\u600E\u4E48\u4E0D\u5403\u554A\uFF1F\u662F\u4E0D\u662F\u5ACC\u54B1\u4EEC\u7684\u7A9D\u5934\u4E0D\u597D\u5403\uFF1F\u300D</p><p>\u767D\u96EA\u83B2\u95ED\u76EE\u51DD\u606F\uFF0C\u5165\u5B9A\u4E00\u822C\u6D51\u4E0D\u7406\u4F1A\u3002\u80E1\u4E25\u4E5F\u89C9\u65E0\u8DA3\uFF0C\u628A\u7A9D\u5934\u6254\u5728\u67B7\u4E0A\uFF0C\u62CD\u62CD\u5C41\u80A1\u8D70\u5F00\u4E86\u3002</p><p>\u90A3\u53EA\u7A9D\u5934\u6EDA\u5230\u767D\u96EA\u83B2\u624B\u8FB9\uFF0C\u5979\u62FF\u8D77\u6765\u54AC\u4E86\u4E00\u53E3\uFF0C\u4F3C\u4E4E\u60F3\u8D77\u4E86\u4EC0\u4E48\uFF0C\u677E\u624B\u6254\u5230\u4E00\u8FB9\u3002\u859B\u971C\u7075\u5FC3\u4E0B\u51B7\u7B11\uFF0C\u8FD9\u4E09\u56DB\u5929\u767D\u96EA\u83B2\u996D\u4E5F\u4E0D\u5403\uFF0C\u6C34\u4E5F\u4E0D\u559D\uFF0C\u5012\u50CF\u662F\u7EDD\u98DF\u81EA\u5C3D\u7684\u6837\u5B50\u3002\u96BE\u9053\u5979\u8FD8\u628A\u81EA\u5DF1\u5F53\u6210\u6355\u5FEB\uFF1F</p><p>\u859B\u971C\u7075\u559D\u5B8C\u4E86\u6C34\uFF0C\u5C0F\u5FC3\u6536\u597D\u7897\uFF0C\u9760\u5728\u6805\u680F\u4E0A\uFF0C\u4ED4\u7EC6\u6253\u91CF\u767D\u96EA\u83B2\u3002</p><p>\u5373\u4F7F\u505A\u4E3A\u5973\u4EBA\uFF0C\u859B\u971C\u7075\u4E5F\u4E0D\u5F97\u4E0D\u627F\u8BA4\u5979\u957F\u5F97\u5F88\u7F8E\u3002\u5979\u9759\u9759\u5730\u5750\u5728\u9ED1\u6697\u7684\u89D2\u843D\u91CC\uFF0C\u9888\u4E2D\u7684\u94C1\u67B7\u548C\u8DB3\u4E0A\u7684\u91CD\u68B0\uFF0C\u4F7F\u5979\u770B\u8D77\u6765\u6108\u53D1\u52A8\u4EBA\u3002\u5728\u72F1\u4E2D\u56DA\u4E86\u8FD9\u4E48\u4E45\uFF0C\u5979\u8FD8\u548C\u5F53\u521D\u4E00\u6837\u5E72\u5E72\u51C0\u51C0\uFF0C\u4E00\u88AD\u767D\u8863\u770B\u4E0D\u5230\u4E1D\u6BEB\u6C61\u6E0D\u3002</p><p>\u859B\u971C\u7075\u54AC\u7D27\u5634\u5507\u3002\u540C\u6837\u662F\u56DA\u5728\u72F1\u4E2D\uFF0C\u5979\u7684\u8EAB\u5B50\u91CC\u91CC\u5916\u5916\u5DF2\u7ECF\u810F\u900F\u4E86\u3002\u5979\u66FE\u7ECF\u548C\u5979\u4E00\u6837\u5E72\u51C0\uFF0C\u53EF\u73B0\u5728\uFF0C\u5979\u8EAB\u4E0A\u6BCF\u4E00\u5904\u90FD\u88AB\u7537\u4EBA\u6700\u80AE\u810F\u7684\u7269\u4F53\u73B7\u6C61\u8FC7\u3002\u5979\u7684\u9634\u9053\u91CC\u8FD8\u6B8B\u7559\u7740\u7537\u4EBA\u7684\u7CBE\u6DB2\uFF0C\u800C\u5979\u5374\u597D\u7AEF\u7AEF\u5750\u5728\u90A3\u91CC\u3002</p><p>\u859B\u971C\u7075\u6000\u7591\u767D\u96EA\u83B2\u53EA\u662F\u88C5\u88C5\u6837\u5B50\uFF0C\u81EA\u5DF1\u88AB\u5E26\u5230\u5802\u4E0A\u906D\u53D7\u8E42\u8E8F\u65F6\uFF0C\u5C31\u6709\u4EBA\u66FF\u5979\u6253\u5F00\u94C1\u67B7\uFF0C\u7ED9\u5979\u4E30\u76DB\u7684\u98DF\u7269\u3002</p><p>\u300C\u770B\u4F60\u8FD8\u80FD\u88C5\u591A\u4E45\u3002\u300D\u859B\u971C\u7075\u8EBA\u5728\u8349\u5806\u4E0A\uFF0C\u88AB\u4EBA\u634F\u80BF\u7684\u4E73\u623F\u4F20\u6765\u9635\u9635\u80C0\u75DB\uFF0C\u5979\u7528\u624B\u62A4\u4F4F\u53CC\u4E73\uFF0C\u95ED\u4E0A\u773C\u775B\u3002</p><p>\u4E00\u80A1\u81ED\u5473\u98D8\u6563\u8FC7\u6765\u3002\u859B\u971C\u7075\u5DF2\u7ECF\u4E60\u60EF\u4E86\u7A7A\u6C14\u4E2D\u5F25\u6F2B\u7684\u81ED\u6C14\uFF0C\u4F46\u8FD9\u80A1\u5473\u9053\u2026\u2026</p><p>\u5979\u7741\u5F00\u773C\u3002\u6765\u81EA\u4E8E\u65C1\u8FB9\u7684\u5C11\u5973\u8EAB\u4E0A\u3002</p><p>\u859B\u971C\u7075\u773C\u4E2D\u7684\u7591\u60D1\u6E10\u6E10\u53D8\u6210\u4E86\u7B11\u610F\uFF0C\u5979\u5FFD\u7136\u5750\u4E86\u8D77\u6765\uFF0C\u6572\u52A8\u7740\u6805\u680F\uFF0C\u300C\u6765\u4EBA\u554A\u2026\u2026\u300D</p><p>\u767D\u96EA\u83B2\u7389\u8138\u6DA8\u5F97\u901A\u7EA2\uFF0C\u4E24\u624B\u5728\u67B7\u5185\u7D27\u7D27\u63E1\u7740\uFF0C\u6068\u4E0D\u5F97\u5373\u53EF\u6B7B\u53BB\u3002</p><p>\u300C\u8BA9\u54B1\u77A7\u77A7\uFF0C\u6C34\u7075\u7075\u7684\u5927\u59D1\u5A18\uFF0C\u548B\u4E2A\u8FD8\u62C9\u88E4\u5B50\u2026\u2026\u300D\u80E1\u4E25\u89D1\u7740\u773C\uFF0C\u7528\u4E00\u6839\u7AF9\u7AFF\u4F38\u8FDB\u6805\u680F\uFF0C\u671D\u767D\u96EA\u83B2\u4E24\u817F\u4E4B\u95F4\u4F38\u53BB\u3002</p><p>\u767D\u96EA\u83B2\u66F2\u819D\u9876\u7740\u94C1\u67B7\uFF0C\u4E24\u817F\u5206\u5F00\uFF0C\u88AB\u960E\u7F57\u671B\u626F\u70C2\u7684\u88C6\u90E8\u88AB\u5979\u6495\u4E0B\u5916\u88D9\u5BC6\u5BC6\u906E\u63A9\u3002\u859B\u971C\u7075\u770B\u5F97\u4E0D\u591F\u4ED4\u7EC6\uFF0C\u5979\u8EAB\u4E0A\u5E76\u975E\u6BEB\u65E0\u6C61\u6E0D\uFF0C\u5728\u5979\u80A1\u95F4\uFF0C\u6B64\u65F6\u6B63\u6709\u4E00\u7247\u9EC4\u9EC4\u7684\u6C61\u75D5\uFF0C\u6B63\u8D8A\u6765\u8D8A\u5927\u3002</p><p>\u80E1\u4E25\u563F\u563F\u7B11\u9053\uFF1A\u300C\u8138\u86CB\u6041\u767D\u51C0\uFF0C\u4E0B\u8FB9\u513F\u4E00\u5C41\u80A1\u5C4E\u2026\u2026\u300D</p><p>\u767D\u96EA\u83B2\u53C8\u7F9E\u53C8\u6068\uFF0C\u94C1\u67B7\u731B\u7136\u4E00\u6C89\uFF0C\u5C06\u7AF9\u7AFF\u78D5\u6210\u4E24\u6BB5\u3002</p><p>\u5E26\u4E0A\u5211\u5177\u6700\u5927\u7684\u4E0D\u65B9\u4FBF\u5E76\u975E\u7761\u89C9\u3001\u5403\u996D\uFF0C\u800C\u662F\u4FBF\u6EBA\u3002\u767D\u96EA\u83B2\u5F3A\u5FCD\u4F4F\u4E86\u4E0D\u5403\u4E0D\u559D\uFF0C\u5C31\u662F\u56E0\u4E3A\u624B\u811A\u88AB\u56F0\uFF0C\u65E0\u6CD5\u5904\u7406\u4FBF\u6EBA\u3002\u4F46\u610F\u5FD7\u7EC8\u654C\u4E0D\u8FC7\u751F\u7406\u673A\u80FD\uFF0C\u82E6\u5FCD\u56DB\u5929\u4E4B\u540E\uFF0C\u8FD8\u662F\u5F04\u810F\u4E86\u88E4\u5B50\u3002</p><p>\u767D\u96EA\u83B2\u5E26\u7740\u5211\u5177\u8FD8\u6562\u53CD\u6297\uFF0C\u80E1\u4E25\u4E0D\u7531\u5927\u6012\uFF0C\u4E3E\u8D77\u7AF9\u7AFF\uFF0C\u5C31\u671D\u5979\u8138\u4E0A\u6233\u53BB\u3002</p><p>\u767D\u96EA\u83B2\u8138\u4E00\u4FA7\uFF0C\u987A\u52BF\u62E7\u4F4F\u7AF9\u7AFF\uFF0C\u5411\u524D\u4E00\u9001\u3002\u5979\u4E24\u624B\u90FD\u9501\u5728\u67B7\u4E2D\uFF0C\u8FD9\u4E00\u9001\u53EA\u9012\u51FA\u5BF8\u8BB8\uFF0C\u80E1\u4E25\u5374\u8FDE\u9000\u51E0\u6B65\uFF0C\u4E00\u8DE4\u5750\u5012\u3002</p><p>\u859B\u971C\u7075\u6697\u81EA\u60CA\u6115\uFF0C\u767D\u96EA\u83B2\u7684\u529F\u592B\u6BD4\u81EA\u5DF1\u9AD8\u51FA\u8FD9\u4E48\u591A\uFF0C\u600E\u4E48\u4F1A\u8BA9\u4EBA\u9501\u4F4F\u624B\u811A\uFF1F</p><p>\u300C\u600E\u4E48\u4E86\uFF1F\u300D</p><p>\u5B59\u5929\u7FBD\u8DEF\u8FC7\u5730\u7262\uFF0C\u89C1\u72B6\u7ACB\u523B\u62A2\u8FC7\u6765\u6293\u4F4F\u7AF9\u7AFF\u3002\u4ED6\u6B66\u529F\u6BD4\u80E1\u4E25\u9AD8\u4E86\u8BB8\u591A\uFF0C\u63E1\u4F4F\u7AF9\u7AFF\u4E00\u626D\uFF0C\u556A\u7684\u4E00\u58F0\uFF0C\u5C06\u7AF9\u7AFF\u62E7\u6210\u4E24\u6BB5\uFF0C\u767D\u96EA\u83B2\u624B\u4E2D\u53EA\u5269\u4E0B\u4E24\u5BF8\u957F\u4E00\u622A\u3002\u5B59\u5929\u7FBD\u4EE5\u7AF9\u4EE3\u67AA\uFF0C\u51FA\u62DB\u53C8\u72E0\u53C8\u5FEB\u3002\u767D\u96EA\u83B2\u5E26\u7740\u91CD\u67B7\uFF0C\u52C9\u5F3A\u6321\u683C\u7247\u523B\uFF0C\u88AB\u4ED6\u63A5\u8FDE\u70B9\u4F4F\u51E0\u5904\u5927\u7A74\uFF0C\u518D\u65E0\u529B\u53CD\u6297\u3002</p><p>\u80E1\u4E25\u8FD9\u4F1A\u624D\u56DE\u8FC7\u8138\u8272\uFF0C\u6076\u72E0\u72E0\u9053\uFF1A\u300C\u8FD9\u8D31\u8D27\u5C4E\u90FD\u62C9\u8EAB\u4E0A\u4E86\uFF0C\u8FD8\u4E0D\u8BA9\u54B1\u770B\uFF01\u300D</p><p>\u5B59\u5929\u7FBD\u77A5\u4E86\u859B\u971C\u7075\u4E00\u773C\uFF0C\u89C1\u5979\u9888\u4E2D\u7684\u94C1\u94FE\u597D\u7AEF\u7AEF\u9501\u5728\u4E86\u94C1\u6805\u4E0A\uFF0C\u4E8E\u662F\u6253\u5F00\u94C1\u95E8\uFF0C\u628A\u767D\u96EA\u83B2\u62C9\u4E86\u51FA\u6765\u3002</p><p>\u300C\u5927\u59D1\u5A18\u62C9\u88E4\u5B50\u4E5F\u662F\u6869\u7A00\u7F55\u4E8B\uFF0C\u5927\u5BB6\u60F3\u770B\u770B\uFF0C\u767D\u6355\u5934\u8FD8\u63A8\u4E09\u963B\u56DB\u7684\u3002\u300D\u5B59\u5929\u7FBD\u62CD\u4E86\u62CD\u767D\u96EA\u83B2\u7684\u5C41\u80A1\uFF0C\u5FAE\u7B11\u9053\uFF1A\u300C\u8FDE\u5C44\u90FD\u8BA9\u770B\u4E86\uFF0C\u770B\u770B\u5C41\u80A1\u53C8\u6709\u4EC0\u4E48\u6253\u7D27\uFF1F\u300D</p><p>\u300C\u65E0\u803B\uFF01\u300D\u767D\u96EA\u83B2\u9A82\u9053\uFF0C\u58F0\u97F3\u91CC\u5DF2\u5E26\u4E86\u54ED\u8154\u3002</p><p>\u56DB\u56DB\u65B9\u65B9\u7684\u94C1\u67B7\u652F\u5728\u5730\u4E0A\uFF0C\u8DB3\u68B0\u5411\u524D\u4E00\u63A8\uFF0C\u767D\u96EA\u83B2\u5C31\u53D8\u6210\u4E86\u8DEA\u4F0F\u7684\u59FF\u52BF\u3002\u5979\u5934\u8138\u88AB\u95E8\u677F\u4E00\u6837\u7684\u94C1\u67B7\u6321\u4F4F\uFF0C\u540E\u9762\u9732\u51FA\u5A40\u5A1C\u7684\u8EAF\u5E72\u3002 \u957F\u8FD1\u56DB\u5C3A\u7684\u8DB3\u68B0\u4F7F\u5979\u53CC\u817F\u4EE5\u4E00\u79CD\u4E0D\u96C5\u7684\u59FF\u52BF\u5927\u5F20\u7740\uFF0C\u5706\u81C0\u9AD8\u7FD8\uFF0C\u7389\u80A1\u88AB\u8FEB\u5411\u5916\u7A81\u8D77\u3002\u6495\u7834\u7684\u88E4\u88C6\u95F4\u9732\u51FA\u4E00\u7247\u767D\u5E03\u3002</p><p>\u8FD9\u79CD\u59FF\u6001\u5DF2\u7ECF\u8DB3\u591F\u7F9E\u803B\uFF0C\u66F4\u4EE4\u5979\u7F9E\u803B\u7684\uFF0C\u5219\u662F\u5706\u81C0\u6B63\u4E2D\u90A3\u7247\u9EC4\u8272\u7684\u6C61\u6E0D\u3002\u800C\u5B59\u5929\u7FBD\u8FD8\u6446\u5F04\u7740\u5979\u50F5\u786C\u7684\u8EAB\u4F53\uFF0C\u628A\u5979\u8170\u80A2\u538B\u4F4E\uFF0C\u5927\u817F\u8FED\u5728\u8EAB\u4E0B\uFF0C\u76F4\u5230\u81C0\u90E8\u7FD8\u5230\u6700\u9AD8\u3002</p><p>\u767D\u96EA\u83B2\u7F9E\u4E0D\u6B32\u751F\uFF0C\u5979\u5E76\u4E0D\u662F\u4E00\u4E2A\u8F6F\u5F31\u7684\u5973\u5B50\uFF0C\u6B64\u65F6\u5374\u5FCD\u4E0D\u4F4F\u54FD\u54BD\u8D77\u6765\u3002</p><p>\u80E1\u4E25\u521A\u624D\u624D\u4E22\u4E86\u9762\u5B50\uFF0C\u8FD9\u4F1A\u513F\u51D1\u4E0A\u6765\uFF0C\u6293\u4F4F\u767D\u96EA\u83B2\u7684\u7389\u4E73\u72E0\u72E0\u626D\u4E86\u4E00\u628A\uFF0C\u300C\u8FD8\u4EE5\u4E3A\u81EA\u5DF1\u662F\u6355\u5934\u5462\uFF1F\u4ED6\u5A18\u7684\u4E00\u4E2A\u9006\u532A\uFF0C\u5728\u8FD9\u513F\u5C31\u662F\u6761\u6BCD\u72D7\uFF01\u300D</p><p>\u300C\u755C\u7272\uFF01\u300D\u767D\u96EA\u83B2\u54ED\u9A82\u9053\u3002</p><p>\u300C\u55E8\uFF01\u8FD8\u5634\u786C\u5462\uFF1F\u300D\u80E1\u4E25\u4E24\u624B\u9F50\u4E0A\uFF0C\u62FF\u4F4F\u767D\u96EA\u83B2\u7684\u4E73\u8BBF\u53C8\u6293\u53C8\u62E7\u3002</p><p>\u767D\u96EA\u83B2\u4E24\u8098\u60AC\u7A7A\uFF0C\u4E00\u5BF9\u575A\u633A\u7684\u9999\u4E73\u65E0\u906E\u65E0\u63A9\u60AC\u5730\u80F8\u524D\uFF0C\u5979\u7684\u4E73\u623F\u8FD8\u6709\u7740\u5C11\u5973\u7684\u9752\u6DA9\uFF0C\u88AB\u80E1\u4E25\u4E0D\u5206\u8F7B\u91CD\u4E00\u901A\u4E71\u62E7\uFF0C\u76F4\u75BC\u5F97\u5A07\u8EAF\u53D1\u98A4\u3002</p><p>\u8FD9\u8FB9\u5B59\u5929\u7FBD\u6446\u5F04\u597D\u5979\u7684\u8EAB\u4F53\uFF0C\u6478\u7740\u5979\u7684\u5C41\u80A1\u7B11\u9053\uFF1A\u300C\u767D\u6355\u5934\u4E00\u4E2A\u5341\u4E03\u516B\u5C81\u7684\u5927\u59D1\u5A18\uFF0C\u600E\u4E48\u8FD8\u628A\u5C4E\u62C9\u8EAB\u4E0A\u4E86\uFF1F\u960E\u5927\u4EBA\u6709\u5148\u89C1\u4E4B\u660E\uFF0C\u77E5\u9053\u4F60\u5E26\u4E86\u5211\u5177\u4E0D\u65B9\u4FBF\uFF0C\u624D\u5E2E\u767D\u6355\u5934\u5F00\u4E86\u88E4\u88C6\u3002\u83AB\u975E\u8FD9\u88E4\u88C6\u5F00\u5F97\u8FD8\u4E0D\u591F\uFF1F\u300D</p><p>\u300C\u55E4\u300D\u7684\u4E00\u58F0\uFF0C\u767D\u96EA\u83B2\u957F\u88E4\u88AB\u4ED6\u5F7B\u5E95\u6495\u5F00\uFF0C\u539F\u672C\u53EA\u5728\u81C0\u4E0B\u7684\u88C2\u7F1D\u4E00\u76F4\u5EF6\u4F38\u5230\u8170\u90E8\uFF0C\u6574\u4E2A\u5C41\u80A1\u90FD\u66B4\u9732\u51FA\u6765\u3002\u63A9\u5728\u817F\u95F4\u7684\u88D9\u7247\u6389\u843D\u51FA\u6765\uFF0C\u9732\u51FA\u4E00\u53EA\u96EA\u767D\u7684\u7F8E\u81C0\u3002</p><p>\u4E24\u540D\u72F1\u5352\u773C\u775B\u90FD\u4EAE\u4E86\u8D77\u6765\uFF0C\u767D\u96EA\u83B2\u7684\u5C41\u80A1\u6D51\u5706\u5149\u6ED1\uFF0C\u808C\u80A4\u767D\u5AE9\uFF0C\u6B64\u523B\u5979\u8863\u886B\u5B8C\u6574\uFF0C\u53EA\u6709\u5C41\u80A1\u50CF\u53EA\u5265\u4E86\u58F3\u7684\u9E21\u86CB\uFF0C\u5149\u6E9C\u6E9C\u9AD8\u7FD8\u8D77\u6765\uFF0C\u8BA9\u4EBA\u4E00\u89C1\u5C31\u9E21\u5DF4\u53D1\u786C\u3002\u7531\u4E8E\u4E24\u819D\u5927\u5F20\uFF0C\u7D27\u51D1\u7684\u7684\u81C0\u7F1D\u4E5F\u968F\u4E4B\u7EFD\u5F00\uFF0C\u4E24\u534A\u767D\u751F\u751F\u7684\u96EA\u81C0\u95F4\uFF0C\u6CBE\u6EE1\u4E86\u7CCA\u72B6\u7684\u9EC4\u8272\u6C61\u7269\u3002</p><p>\u300C\u767D\u6355\u5934\uFF0C\u6CBE\u4E86\u4E00\u5C41\u80A1\u81ED\u54C4\u54C4\u7684\u5C4E\uFF0C\u4F60\u4E5F\u4E0D\u5ACC\u96BE\u53D7\uFF1F\u6211\u4EEC\u5144\u5F1F\u60F3\u5E2E\u4F60\u64E6\u64E6\uFF0C\u4F60\u8FD8\u4E0D\u4E50\u610F\u3002\u4F60\u770B\u600E\u4E48\u529E\uFF1F\u8981\u4E0D\u5C31\u8FD9\u6837\uFF0C\u6211\u4EEC\u8FD8\u628A\u4F60\u9001\u56DE\u53BB\uFF1F\u300D</p><p>\u628A\u5C4E\u62C9\u5728\u8EAB\u4E0A\uFF0C\u767D\u96EA\u83B2\u5DF2\u7ECF\u96BE\u582A\u5F97\u65E0\u5730\u81EA\u5BB9\uFF0C\u518D\u88AB\u4EBA\u6273\u7740\u6CBE\u6EE1\u5927\u4FBF\u7684\u5C41\u80A1\u5982\u6B64\u595A\u843D\uFF0C\u5979\u7F9E\u5FFF\u5F97\u53EA\u60F3\u5C31\u6B64\u6B7B\u53BB\u3002</p><p>\u859B\u971C\u7075\u770B\u7740\u8FD9\u4E00\u5E55\uFF0C\u5FC3\u91CC\u53C8\u662F\u5FEB\u610F\u53C8\u8FF7\u832B\u3002\u8FD9\u4E48\u591A\u5929\u6765\uFF0C\u90FD\u662F\u5979\u5149\u7740\u8EAB\u5B50\u88AB\u72F1\u5352\u4EEC\u5F53\u6210\u4E0D\u8981\u94B1\u7684\u5A4A\u5B50\u4EFB\u610F\u51CC\u8FB1\uFF0C\u767D\u96EA\u83B2\u597D\u7AEF\u7AEF\u5750\u5728\u4E00\u8FB9\u770B\u3002\u8FD9\u6B21\u7EC8\u4E8E\u53CD\u4E86\u8FC7\u6765\uFF0C\u53D8\u6210\u767D\u96EA\u83B2\u5149\u7740\u5C41\u80A1\u88AB\u72F1\u5352\u4EEC\u8C03\u620F\uFF0C\u5979\u5728\u4E00\u65C1\u89C2\u770B\u3002\u4F46\u5979\u771F\u662F\u5367\u5E95\u5417\uFF1F</p><p>\u5B59\u5929\u7FBD\u7B11\u9053\uFF1A\u300C\u8FD8\u4E0D\u597D\u610F\u601D\u5F00\u53E3\u3002\u7B97\u4E86\uFF0C\u8859\u95E8\u91CC\u5934\u597D\u4FEE\u884C\uFF0C\u4E00\u4E2A\u5973\u513F\u5BB6\uFF0C\u6EE1\u5C41\u80A1\u7684\u5C61\u6210\u4EC0\u4E48\u6837\u5B50\uFF1F\u5E2E\u4F60\u6D17\u6D17\u5427\u3002\u300D</p><p>\u767D\u96EA\u83B2\u54AC\u4F4F\u5634\u5507\uFF0C\u7F9E\u5F97\u76F4\u6DCC\u773C\u6CEA\u3002\u5FFD\u7136\u81C0\u540E\u4E00\u70ED\uFF0C\u4E00\u80A1\u6C34\u67F1\u6D47\u5728\u5C41\u80A1\u4E0A\uFF0C\u7ADF\u7136\u662F\u70ED\u7684\u3002\u7247\u523B\u540E\uFF0C\u767D\u96EA\u83B2\u624D\u660E\u767D\u8FC7\u6765\uFF0C\u77E5\u9053\u81EA\u5DF1\u53D7\u5230\u4EC0\u4E48\u6837\u7684\u6C61\u8FB1\uFF0C\u4E0D\u7531\u75DB\u54ED\u5931\u58F0\u3002</p><p>\u660F\u6697\u7684\u7262\u623F\u5185\uFF0C\u5E26\u7740\u91CD\u67B7\u8DB3\u68B0\u7684\u5973\u56DA\u8DEA\u5728\u5730\u4E0A\uFF0C\u4E00\u540D\u72F1\u5352\u6B63\u638F\u51FA\u9633\u5177\uFF0C\u5BF9\u7740\u5979\u767D\u4EAE\u7684\u5C41\u80A1\u6492\u5C3F\u3002</p><p>\u5B59\u5929\u7FBD\u7784\u7740\u767D\u96EA\u83B2\u7EFD\u5F00\u7684\u81C0\u7F1D\uFF0C\u7B11\u563B\u563B\u6296\u52A8\u9633\u5177\u3002\u5C3F\u67F1\u51B2\u5F00\u4E86\u6C61\u7269\uFF0C\u81C0\u6C9F\u9732\u51FA\u672C\u6765\u7684\u767D\u51C0\uFF0C\u4E2D\u95F4\u4E00\u53EA\u7EA2\u5AE9\u7684\u5C41\u773C\u513F\u6E10\u6E10\u53D8\u5F97\u6E05\u6670\u3002\u5C3F\u67F1\u6D47\u5728\u4E0A\u9762\uFF0C\u5939\u7740\u6C61\u7269\u7684\u8089\u5B54\u5C48\u8FB1\u5730\u6E85\u8D77\u6C34\u82B1\uFF0C\u7EC6\u5BC6\u7684\u83CA\u7EB9\u4E00\u4E00\u663E\u9732\u51FA\u6765\u3002</p><p>\u767D\u96EA\u83B2\u88AB\u8FEB\u6485\u8D77\u5C41\u80A1\uFF0C\u5728\u8FD9\u65E0\u6CD5\u60F3\u8C61\u7684\u7F9E\u8FB1\u4E2D\u54ED\u5F97\u51E0\u4E4E\u660F\u53A5\u3002\u5F53\u5C3F\u67F1\u5BF9\u51C6\u809B\u6D1E\uFF0C\u53D1\u51FA\u54D7\u54D7\u7684\u58F0\u54CD\uFF0C\u83CA\u857E\u672C\u80FD\u5730\u6536\u7F29\u8D77\u6765\uFF0C\u5F77\u4F5B\u88AB\u5C3F\u6DB2\u6D47\u5F97\u7741\u4E0D\u5F00\u773C\u3002</p><p>\u4E00\u6CE1\u5C3F\u6492\u5B8C\uFF0C\u5B59\u5929\u7FBD\u7B11\u7740\u6296\u52A8\u9634\u830E\uFF0C\u628A\u6B8B\u6DB2\u6EF4\u5728\u767D\u96EA\u83B2\u96EA\u5AE9\u7684\u81C0\u8089\u4E0A\u3002\u5973\u6355\u81C0\u7F1D\u5185\u6E7F\u6DCB\u6DCB\u6CDB\u7740\u6C34\u5149\uFF0C\u90A3\u53EA\u5C0F\u5DE7\u7684\u83CA\u809B\u88AB\u51B2\u5237\u5F97\u7EA4\u6BEB\u6BD5\u9732\uFF0C\u72B9\u5982\u7EA2\u4EAE\u7684\u73AB\u7470\u82B1\u82DE\u4E00\u6837\u9192\u76EE\u3002</p><p>\u5B59\u5929\u7FBD\u5FC3\u91CC\u4E00\u52A8\uFF0C\u6E29\u8A00\u9053\uFF1A\u300C\u767D\u6355\u5934\uFF0C\u522B\u54ED\u574F\u4E86\u8EAB\u5B50\u3002\u300D\u8BF4\u7740\u8F7B\u8F7B\u6309\u4F4F\u5979\u7684\u7A74\u9053\u3002</p>',102);function t(_,c){return a}var o=e(r,[["render",t]]);export{o as default};
