import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const a={},r=p('<h1 id="_08-\u9047\u9669" tabindex="-1"><a class="header-anchor" href="#_08-\u9047\u9669" aria-hidden="true">#</a> 08 \u9047\u9669</h1><p>\u767D\u96EA\u83B2\u6C89\u6C89\u7761\u53BB\uFF0C\u88AB\u5C3F\u6DB2\u6253\u6E7F\u7684\u5C41\u80A1\u53C8\u767D\u53C8\u4EAE\uFF0C\u81C0\u7F1D\u95F4\u8FD8\u4E0D\u65F6\u6EF4\u4E0B\u5C3F\u6DB2\u3002</p><p>\u80E1\u4E25\u7684\u9E21\u5DF4\u6DA8\u5F97\u96BE\u53D7\uFF0C\u6B63\u5F85\u628A\u859B\u971C\u7075\u62C9\u51FA\u6765\u6CC4\u706B\uFF0C\u89C1\u5B59\u5929\u7FBD\u70B9\u4E86\u767D\u96EA\u83B2\u7684\u7761\u7A74\uFF0C\u4E0D\u7531\u4E00\u6115\u3002</p><p>\u5B59\u5929\u7FBD\u7B11\u9053\uFF1A\u300C\u8FD9\u4E48\u4E2A\u5999\u7269\uFF0C\u80E1\u54E5\u4E0D\u60F3\u73A9\u73A9\u5417\uFF1F\u300D</p><p>\u80E1\u4E25\u65E9\u5C31\u60F3\u8FC7\u6570\u904D\u4E86\uFF0C\u300C\u53EF\u960E\u5927\u4EBA\u2026\u2026\u300D\u960E\u7F57\u671B\u90FD\u6CA1\u6562\u78B0\u5979\uFF0C\u4ED6\u4EEC\u600E\u6562\u5148\u7834\u4E86\u8FD9\u5211\u90E8\u5973\u6355\u7684\u8EAB\u5B50\u3002</p><p>\u300C\u524D\u9762\u4E0D\u884C\uFF0C\u54B1\u4EEC\u5C31\u8D70\u540E\u9762\u2026\u2026\u300D\u5B59\u5929\u7FBD\u624B\u638C\u5728\u767D\u96EA\u83B2\u81C0\u7F1D\u91CC\u629A\u6478\u7740\uFF0C\u6307\u5C16\u5FAE\u4E00\u7528\u529B\uFF0C\u5D4C\u5165\u67D4\u8F6F\u7684\u809B\u6D1E\uFF0C\u4F4E\u7B11\u9053\uFF1A\u300C\u53EA\u8981\u8F7B\u7740\u70B9\u513F\uFF0C\u8C05\u5979\u4E5F\u4E0D\u4F1A\u77E5\u9053\u3002\u300D</p><p>\u80E1\u4E25\u88AB\u4ED6\u8BF4\u5F97\u5FC3\u52A8\uFF0C\u671B\u7740\u767D\u96EA\u83B2\u767D\u751F\u751F\u7684\u5C41\u80A1\u4E5F\u81EA\u5FC3\u75D2\uFF0C\u70B9\u5934\u7B54\u5E94\u4E86\u3002</p><p>\u4E24\u4EBA\u63D0\u6765\u6E05\u6C34\uFF0C\u63B0\u5F00\u767D\u96EA\u83B2\u5C41\u80A1\uFF0C\u91CC\u91CC\u5916\u5916\u6D17\u5F97\u5E72\u51C0\u3002\u5B59\u5929\u7FBD\u6015\u786C\u63D2\u5F04\u4F24\u4E86\u5979\uFF0C\u9732\u4E86\u9A6C\u811A\uFF0C\u5148\u7528\u624B\u6307\u63A2\u8FDB\u53BB\uFF0C\u5C06\u5BC6\u95ED\u7684\u83CA\u857E\u8F7B\u8F7B\u6491\u5F00\u3002</p><p>\u90A3\u53EA\u5C41\u773C\u513F\u53C8\u8F6F\u53C8\u7D27\uFF0C\u6ED1\u5AE9\u7684\u809B\u857E\u88F9\u5728\u6307\u7AEF\uFF0C\u5145\u6EE1\u8FF7\u4EBA\u7684\u5F39\u6027\u3002\u63D2\u5165\u7B2C\u4E00\u4E2A\u6307\u8282\u4E4B\u540E\uFF0C\u6307\u5C16\u89E6\u5230\u4E00\u5708\u67D4\u97E7\uFF0C\u5F77\u4F5B\u4E00\u53EA\u8089\u7B8D\uFF0C\u5728\u68A6\u4E2D\u4E5F\u672C\u80FD\u5730\u6536\u7D27\uFF0C\u963B\u6321\u5F02\u7269\u7EE7\u7EED\u4FB5\u5165\u3002</p><p>\u5B59\u5929\u7FBD\u7F13\u7F13\u7528\u529B\uFF0C\u7A7F\u900F\u4E86\u62EC\u7EA6\u808C\u7684\u963B\u6321\uFF0C\u6574\u6839\u624B\u6307\u90FD\u94BB\u5165\u809B\u6D1E\u3002\u767D\u96EA\u83B2\u5728\u7761\u68A6\u4E2D\u76B1\u8D77\u7709\u5934\uFF0C\u9F3B\u95F4\u8F7B\u8F7B\u54FC\u4E86\u4E00\u58F0\u3002\u5B59\u5929\u7FBD\u6307\u809A\u88AB\u809B\u857E\u5939\u5F97\u5BC6\u4E0D\u900F\u98CE\uFF0C\u6307\u5C16\u5374\u94BB\u8FDB\u4E00\u7247\u6DF1\u4E0D\u89C1\u5E95\u7684\u67D4\u6ED1\u4E4B\u4E2D\u3002\u7531\u4E8E\u5C41\u773C\u513F\u88AB\u5F02\u7269\u63D2\u5165\uFF0C\u80A0\u9053\u6709\u8282\u594F\u5730\u5F8B\u52A8\u8D77\u6765\uFF0C\u90A3\u79CD\u9500\u9B42\u7684\u6ED1\u817B\u611F\uFF0C\u8BA9\u5B59\u5929\u7FBD\u80EF\u4E0B\u4E00\u70ED\uFF0C\u51E0\u4E4E\u5C04\u4E86\u51FA\u6765\u3002</p><p>\u4ED6\u7A33\u4F4F\u5FC3\u795E\uFF0C\u5148\u7528\u4E00\u6839\u624B\u6307\u5728\u53D1\u7D27\u7684\u809B\u6D1E\u91CC\u8F7B\u8F7B\u6345\u5F04\uFF0C\u6E10\u6E10\u5AE9\u809B\u653E\u677E\u4E0B\u6765\uFF0C\u62BD\u9001\u53D8\u5F97\u987A\u7545\u3002\u5B59\u5929\u7FBD\u4E2D\u6307\u4ECD\u63D2\u5728\u767D\u96EA\u83B2\u809B\u4E2D\uFF0C\u98DF\u6307\u6491\u7D27\u7684\u809B\u6D1E\u4E0A\u6309\u4E86\u6309\uFF0C\u7F13\u7F13\u6345\u5165\u3002</p><p>\u767D\u96EA\u83B2\u7684\u5C41\u80A1\u52A8\u4E86\u4E00\u4E0B\uFF0C\u4F3C\u4E4E\u8981\u4ECE\u68A6\u4E2D\u9192\u6765\u3002\u5B59\u5929\u7FBD\u505C\u4F4F\u624B\uFF0C\u65CB\u5373\u60F3\u5230\u5979\u662F\u81EA\u5DF1\u5C01\u4E86\u7A74\u9053\uFF0C\u54EA\u513F\u6709\u8FD9\u4E48\u5BB9\u6613\u9192\uFF0C\u4E8E\u662F\u4E24\u6307\u4E00\u5E76\uFF0C\u7528\u529B\u6345\u8FDB\u767D\u96EA\u83B2\u5A07\u5AE9\u7684\u809B\u6D1E\u4E2D\u3002</p><p>\u7EA2\u5AE9\u7684\u5C41\u773C\u513F\u88AB\u4E24\u6839\u624B\u6307\u6954\u5165\uFF0C\u53D8\u6210\u4E86\u6241\u957F\u5F62\u72B6\u3002\u968F\u7740\u624B\u6307\u7684\u6345\u5165\uFF0C\u7EC6\u5BC6\u7684\u83CA\u7EB9\u65F6\u677E\u65F6\u7D27\uFF0C\u5C55\u793A\u51FA\u67D4\u7F8E\u7684\u5F39\u6027\u3002\u6307\u7F1D\u95F4\u9732\u51FA\u809B\u5185\u7EA2\u6DA6\u7684\u8936\u76B1\uFF0C\u5B59\u5929\u7FBD\u4E24\u6307\u4E00\u5206\uFF0C\u5C41\u773C\u513F\u67D4\u67D4\u7EFD\u5F00\uFF0C\u53EF\u4EE5\u6E05\u695A\u770B\u51FA\u83CA\u857E\u5728\u6307\u4E0B\u8815\u52A8\u7684\u8273\u6001\u3002</p><p>\u5B59\u5929\u7FBD\u9633\u5177\u5DF2\u786C\u4E86\u534A\u664C\uFF0C\u4ED6\u63E1\u4F4F\u8089\u68D2\uFF0C\u9F9F\u5934\u5BF9\u51C6\u96EA\u81C0\u95F4\u5FAE\u7EFD\u7684\u7EA2\u5AE9\uFF0C\u7F13\u7F13\u8FDB\u5165\u3002\u4ED6\u7684\u52A8\u4F5C\u6781\u6709\u8010\u6027\uFF0C\u7B49\u809B\u6D1E\u9002\u5E94\u4E86\u9F9F\u5934\u7684\u7C97\u5706\uFF0C\u624D\u6162\u6162\u8FDB\u5165\u4E00\u5206\u3002</p><p>\u767D\u96EA\u83B2\u53D1\u51FA\u5FAE\u75DB\u7684\u95F7\u54FC\uFF0C\u5C41\u80A1\u4E0B\u610F\u8BC6\u5730\u8EB2\u95EA\u7740\u3002</p><p>\u300C\u771F\u4ED6\u5988\u6D6A\uFF0C\u8FD9\u4F1A\u513F\u5C31\u4F1A\u6447\u5C41\u80A1\u4E86\u3002\u300D\u5B59\u5929\u7FBD\u4F4E\u58F0\u7B11\u7740\uFF0C\u62B1\u4F4F\u767D\u96EA\u83B2\u767D\u5AE9\u7684\u5C41\u80A1\uFF0C\u9633\u5177\u5BF8\u5BF8\u6DF1\u5165\u3002</p><p>\u7EA2\u5AE9\u7684\u5C41\u773C\u513F\u5728\u9F9F\u5934\u7684\u91CD\u538B\u4E4B\u4E0B\uFF0C\u65E0\u5948\u5730\u4E00\u70B9\u70B9\u5F20\u5F00\u3002\u5F53\u5C41\u773C\u513F\u5F20\u5230\u6781\u9650\uFF0C\u7EC6\u5BC6\u7684\u83CA\u7EB9\u88AB\u5168\u90E8\u62C9\u5E73\uFF0C\u7A81\u7136\u4E00\u6536\uFF0C\u9F9F\u5934\u5DF2\u7ECF\u5168\u90E8\u9677\u5165\u5973\u6355\u5FEB\u67D4\u5AE9\u7684\u809B\u4E2D\u3002</p><p>\u7B2C\u4E00\u6B21\u88AB\u5F02\u7269\u4FB5\u5165\uFF0C\u5C41\u773C\u513F\u663E\u5F97\u5341\u5206\u751F\u6DA9\uFF0C\u6BCF\u4E00\u6761\u5AE9\u8089\u90FD\u7D27\u7D27\u7EF7\u7740\uFF0C\u5C06\u9F9F\u5934\u5305\u88F9\u5730\u5BC6\u4E0D\u900F\u98CE\u3002\u5B59\u5929\u7FBD\u5728\u767D\u96EA\u83B2\u6700\u7D27\u7684\u62EC\u7EA6\u808C\u4E0A\u7814\u78E8\u7247\u523B\uFF0C\u4EAB\u53D7\u4E86\u5C11\u5973\u809B\u6D1E\u7684\u7D27\u7A84\u548C\u5F39\u6027\uFF0C\u624D\u7EE7\u7EED\u633A\u8EAB\u5411\u4E0A\u3002</p><p>\u767D\u96EA\u83B2\u5C41\u80A1\u88AB\u9876\u5F97\u5FAE\u5FAE\u62AC\u8D77\uFF0C\u5979\u4E24\u817F\u5206\u5F00\uFF0C\u655E\u9732\u7684\u81C0\u7F1D\u95F4\uFF0C\u4E00\u6839\u9633\u5177\u8D8A\u8FDB\u8D8A\u6DF1\uFF0C\u72B9\u5982\u4E00\u6746\u957F\u67AA\u6345\u5165\u96EA\u56E2\u4F3C\u7684\u7C89\u81C0\u3002\u6CBE\u8FC7\u6C34\u7684\u5AE9\u809B\u53D1\u51FA\u817B\u817B\u7684\u58F0\u54CD\uFF0C\u7EA2\u6DA6\u7684\u809B\u6D1E\u5706\u5706\u5F20\u5F00\uFF0C\u8BA9\u9633\u5177\u987A\u7545\u81EA\u5982\u5730\u94BB\u5165\u81EA\u5DF1\u7684\u6392\u6CC4\u5668\u5B98\u3002</p><p>\u5B59\u5929\u7FBD\u62B1\u7740\u767D\u96EA\u83B2\u7684\u5C41\u80A1\uFF0C\u76F4\u5230\u8EAB\u4F53\u628A\u6D51\u5706\u7684\u96EA\u81C0\u538B\u6241\uFF0C\u624D\u505C\u4E86\u4E0B\u6765\u3002</p><p>\u300C\u600E\u4E48\u6837\uFF1F\u600E\u4E48\u6837\uFF1F\u300D\u80E1\u4E25\u7126\u6025\u5730\u95EE\u3002</p><p>\u5B59\u5929\u7FBD\u5C4F\u4F4F\u4E86\u547C\u5438\uFF0C\u534A\u664C\u624D\u5410\u4E86\u53E3\u6C14\uFF0C\u300C\u8FD9\u5A4A\u5B50\u7684\u5C41\u773C\u513F\u53C8\u7D27\u53C8\u97E7\uFF0C\u771F\u4ED6\u5988\u723D\uFF01\u300D</p><p>\u767D\u96EA\u83B2\u6D51\u7136\u4E0D\u77E5\u9053\u81EA\u5DF1\u540E\u5EAD\u6B63\u88AB\u4EBA\u4FB5\u72AF\uFF0C\u94C1\u67B7\u53E6\u4E00\u4FA7\uFF0C\u5979\u8138\u4E0A\u7684\u6CEA\u75D5\u6E10\u6E10\u5E72\u6DB8\uFF0C\u795E\u60C5\u5C31\u50CF\u4E00\u4E2A\u53D7\u4F24\u7684\u5973\u5B69\u4E00\u6837\u59D4\u5C48\u3002</p><p>\u5B59\u5929\u7FBD\u62B1\u4F4F\u767D\u96EA\u83B2\u7684\u5C41\u80A1\u7F13\u7F13\u62BD\u9001\u8D77\u6765\uFF0C\u7D27\u5BC6\u7684\u809B\u857E\u88AB\u5E26\u5F97\u7FFB\u51FA\uFF0C\u63A5\u7740\u53C8\u5377\u5165\u4F53\u5185\u3002</p><p>\u5979\u7FD8\u7740\u767D\u751F\u751F\u7684\u7F8E\u81C0\uFF0C\u5C41\u773C\u513F\u72B9\u5982\u4E00\u53EA\u67D4\u8273\u7684\u5C0F\u5634\uFF0C\u5A07\u5A9A\u5730\u541E\u5410\u7740\u8089\u68D2\u3002\u5979\u7684\u809B\u857E\u6781\u7D27\uFF0C\u80A0\u9053\u5374\u53C8\u6DF1\u53C8\u957F\uFF0C\u4E00\u5708\u4E00\u5708\u7684\u80A0\u58C1\u5F77\u4F5B\u67D4\u6ED1\u7684\u817B\u8102\uFF0C\u5728\u9F9F\u5934\u7684\u63A8\u6324\u4E0B\uFF0C\u4F20\u6765\u6F6E\u6C34\u822C\u7684\u5F8B\u52A8\u3002\u5B59\u5929\u7FBD\u4E5F\u8D70\u8FC7\u4E0D\u5C11\u540E\u95E8\uFF0C\u6CA1\u6709\u4E00\u53EA\u50CF\u767D\u96EA\u83B2\u751F\u5F97\u8FD9\u6837\u5DE7\u5999\u3002\u8FD9\u6837\u7684\u7EDD\u5999\u540E\u5EAD\u82B1\uFF0C\u5374\u8BA9\u4ED6\u62D4\u4E86\u5934\u7B79\uFF0C\u771F\u662F\u96BE\u5F97\u7684\u8273\u798F\u3002</p><p>\u4ED6\u5FFD\u7136\u60F3\u5230\uFF0C\u4E39\u5A18\u7684\u540E\u5EAD\u53C8\u8BE5\u662F\u600E\u6837\u7684\u5999\u6CD5\uFF1F\u767D\u5B5D\u5112\u8FC2\u592B\u5B50\u4E00\u4E2A\uFF0C\u653E\u7740\u4E39\u5A18\u8FD9\u6837\u7684\u7F8E\u8273\u5A18\u5B50\uFF0C\u884C\u8D77\u5468\u516C\u4E4B\u793C\u591A\u534A\u4E5F\u662F\u90D1\u91CD\u5176\u4E8B\uFF0C\u5012\u63D2\u82B1\u8FD9\u79CD\u4F0E\u4FE9\uFF0C\u4E5D\u6210\u662F\u4E0D\u4F1A\u505A\u7684\u3002\u8FD9\u4E48\u8BF4\u6765\uFF0C\u4E39\u5A18\u7684\u540E\u5EAD\u4E5F\u662F\u6735\u672A\u7ECF\u4EBA\u4E8B\u7684\u9C9C\u82B1\u5462\u3002</p><p>\u4E00\u60F3\u5230\u4E39\u5A18\uFF0C\u5B59\u5929\u7FBD\u5C31\u6EE1\u5FC3\u71E5\u70ED\uFF0C\u62BD\u9001\u4E5F\u5FEB\u4E86\u51E0\u5206\u3002\u767D\u96EA\u83B2\u5C41\u773C\u513F\u88AB\u63D2\u5F97\u53FD\u53FD\u549B\u549B\u4F5C\u54CD\uFF0C\u5FFD\u7136\u8089\u68D2\u4E00\u7D27\uFF0C\u5728\u5979\u809B\u5185\u55B7\u5C04\u8D77\u6765\u3002\u767D\u96EA\u83B2\u62AC\u7740\u5C41\u80A1\uFF0C\u4F53\u5185\u7B2C\u4E00\u6B21\u7559\u4E0B\u4E86\u7537\u4EBA\u7684\u7CBE\u6DB2\u3002</p><p>\u5B59\u5929\u7FBD\u62D4\u51FA\u9633\u5177\uFF0C\u80E1\u4E25\u7ACB\u523B\u51D1\u4E86\u4E0A\u53BB\uFF0C\u7334\u6025\u5730\u633A\u5165\u767D\u96EA\u83B2\u809B\u5185\u3002\u767D\u96EA\u83B2\u5C41\u773C\u513F\u5DF2\u88AB\u63D2\u5F97\u677E\u8F6F\uFF0C\u4F46\u5999\u5904\u5374\u6709\u589E\u65E0\u51CF\u3002\u80E1\u4E25\u4E2A\u5B50\u7626\u5B50\uFF0C\u5C31\u50CF\u4E00\u53EA\u7334\u5B50\u8DB4\u5728\u767D\u96EA\u83B2\u8EAB\u4E0A\uFF0C\u5728\u5E26\u67B7\u7F8E\u5973\u7684\u5C41\u80A1\u91CC\u62DA\u547D\u633A\u5F04\u3002</p><p>\u7B49\u4E24\u4EBA\u5E72\u5B8C\uFF0C\u767D\u96EA\u83B2\u5C41\u773C\u513F\u5DF2\u88AB\u63D2\u6210\u4E00\u4E2A\u5706\u5706\u7684\u7EA2\u5B54\uFF0C\u5AE9\u809B\u5FAE\u5FAE\u80BF\u4E86\u8D77\u6765\u3002</p><p>\u5B59\u5929\u7FBD\u6311\u4E86\u4E9B\u4F24\u836F\uFF0C\u6D82\u5728\u5979\u809B\u4E0A\uFF0C\u8F7B\u8F7B\u63C9\u4E86\u7247\u523B\uFF0C\u5F85\u809B\u6D1E\u5408\u62E2\uFF0C\u628A\u767D\u96EA\u83B2\u9001\u56DE\u7262\u5185\u3002</p><p>\u767D\u96EA\u83B2\u9192\u6765\u5DF2\u7ECF\u662F\u6B21\u65E5\uFF0C\u859B\u971C\u7075\u53C8\u88AB\u5E26\u53BB\u5BA1\u8BAF\uFF0C\u7262\u5185\u53EA\u5269\u4E0B\u5979\u4E00\u4E2A\u4EBA\u3002\u60F3\u8D77\u6628\u5929\u6240\u53D7\u7684\u5C48\u8FB1\uFF0C\u767D\u96EA\u83B2\u53C8\u7F9E\u53C8\u6068\uFF0C\u5979\u597D\u7AEF\u7AEF\u4E00\u4E2A\u65B0\u664B\u7684\u5211\u90E8\u6355\u5FEB\uFF0C\u672C\u6765\u524D\u9014\u65E0\u91CF\uFF0C\u5374\u88AB\u4E00\u7FA4\u65E0\u803B\u7684\u72F1\u5352\u8BE1\u8BA1\u9A97\u5165\u72F1\u4E2D\uFF0C\u5B89\u4E86\u6869\u8C0B\u53CD\u7684\u7F6A\u540D\uFF0C\u4E0D\u4EC5\u62AB\u67B7\u5E26\u9501\uFF0C\u8FDE\u4FBF\u6EBA\u90FD\u65E0\u6CD5\u81EA\u7406\uFF0C\u8FD8\u88AB\u8FEB\u8DB4\u5728\u5730\u4E0A\uFF0C\u8BA9\u4EBA\u5F80\u5C41\u80A1\u4E0A\u6492\u5C3F\u3002</p><p>\u767D\u96EA\u83B2\u7684\u5FC3\u91CC\u7A81\u7136\u4E00\u7D27\uFF0C\u5979\u6700\u6015\u7684\u662F\u5728\u68A6\u4E2D\u88AB\u593A\u8D70\u8D1E\u6D01\uFF0C\u5F85\u89C9\u51FA\u79D8\u5904\u5E76\u65E0\u5F02\u6837\uFF0C\u624D\u677E\u4E86\u53E3\u6C14\u3002\u81F3\u4E8E\u540E\u5EAD\u9690\u9690\u7684\u75DB\u695A\uFF0C\u5979\u5374\u61F5\u61F5\u61C2\u61C2\uFF0C\u6D51\u4E0D\u77E5\u90A3\u4E2A\u90E8\u4F4D\u4E5F\u4F1A\u6210\u4E3A\u7537\u4EBA\u6D88\u9063\u7684\u5730\u65B9\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5BB6\u4E2D\u4E8B\u52A1\u5411\u6765\u662F\u767D\u5B5D\u5112\u8BF4\u4E86\u7B97\uFF0C\u4E00\u5BB6\u4EBA\u4F4F\u5728\u5C71\u91CC\u6CA1\u591A\u5C11\u4EB2\u53CB\uFF0C\u5982\u4ECA\u51FA\u4E86\u8FD9\u6837\u7684\u5927\u4E8B\uFF0C\u4E39\u5A18\u5FE7\u5FC3\u5982\u711A\uFF0C\u5374\u53C8\u4E0D\u77E5\u5982\u4F55\u662F\u597D\u3002\u773C\u4E0B\u6700\u8981\u7D27\u7684\u662F\u6D17\u8131\u4E08\u592B\u7684\u7F6A\u540D\u3002</p><p>\u5979\u4E0D\u76F8\u4FE1\u4E08\u592B\u4F1A\u662F\u8C0B\u53CD\uFF0C\u65E2\u7136\u662F\u6500\u8BEC\uFF0C\u603B\u80FD\u8BF4\u4E2A\u660E\u767D\u3002\u4E39\u5A18\u60F3\u6765\u60F3\u53BB\uFF0C\u8D77\u8EAB\u6362\u4E86\u8863\u7269\uFF0C\u51C6\u5907\u53BB\u72F1\u4E2D\u63A2\u89C6\u3002</p><p>\u300C\u5A18\u3002\u300D\u7389\u83B2\u65E9\u65E9\u5C31\u8D77\u6765\uFF0C\u5728\u5916\u9762\u7B49\u5019\uFF0C\u5979\u60F3\u95EE\u53C8\u4E0D\u6562\u95EE\uFF0C\u53EA\u5C0F\u58F0\u8BF4\uFF1A\u300C\u7239\u7239\u2026\u2026\u7239\u7239\u2026\u2026\u300D</p><p>\u300C\u82F1\u83B2\u5462\uFF1F\u300D</p><p>\u300C\u5728\u623F\u91CC\u5FF5\u4E66\u3002\u300D</p><p>\u4E39\u5A18\u8F7B\u62C2\u7740\u5973\u513F\u7684\u5934\u53D1\uFF0C\u534A\u664C\u624D\u52C9\u5F3A\u7B11\u4E86\u7B11\uFF0C\u300C\u662F\u4ED6\u4EEC\u51A4\u6789\u4E86\u4F60\u7239\u7239\u3002\u4E0D\u8981\u544A\u8BC9\u82F1\u83B2\uFF0C\u83AB\u9A87\u7740\u4ED6\u4E86\u3002\u300D</p><p>\u300C\u5973\u513F\u77E5\u9053\u4E86\u3002\u300D</p><p>\u300C\u4E0D\u8981\u591A\u60F3\u4E86\u3002\u6211\u53BB\u72F1\u4E2D\u770B\u770B\u4F60\u7239\u7239\u3002\u300D</p><p>\u300C\u5A18\uFF0C\u6211\u8DDF\u4F60\u4E00\u8D77\u53BB\uFF01\u300D</p><p>\u7389\u83B2\u4E00\u5411\u6015\u89C1\u751F\u4EBA\uFF0C\u5728\u5BB6\u4E2D\u95E8\u4E5F\u4E0D\u51FA\u3002\u5979\u7F20\u8FC7\u8DB3\uFF0C\u8D70\u8DEF\u4E0D\u4FBF\uFF0C\u800C\u4E14\u2026\u2026\u90A3\u4E9B\u72F1\u5352\u8272\u7787\u7787\u7684\u773C\u795E\uFF0C\u8BA9\u4E39\u5A18\u60F3\u8D77\u6765\u5C31\u5BB3\u6015\u3002\u5982\u679C\u6CA1\u6709\u5929\u7FBD\u966A\u7740\uFF0C\u771F\u4E0D\u77E5\u9053\u4F1A\u53D1\u751F\u4EC0\u4E48\u4E8B\u3002</p><p>\u4E39\u5A18\u770B\u770B\u5929\u8272\uFF0C\u7B97\u6765\u5230\u76D1\u72F1\u8BE5\u662F\u4E2D\u5348\u65F6\u5206\uFF0C\u8BF4\u9053\uFF1A\u300C\u5A18\u4E00\u4E2A\u4EBA\u53BB\u5C31\u884C\u4E86\u3002\u4F60\u5728\u5BB6\u5B88\u7740\u5F1F\u5F1F\u3002\u300D\u5979\u60F3\u4E86\u60F3\uFF0C\u53C8\u9053\uFF1A\u300C\u5982\u679C\u5B59\u53D4\u53D4\u6765\u4E86\uFF0C\u8BF7\u4ED6\u9EBB\u70E6\u4E5F\u53BB\u4E00\u8D9F\u3002\u300D</p><p>\u674F\u82B1\u6751\u5230\u8C7A\u72FC\u5761\u4E8C\u5341\u4F59\u91CC\u5C71\u8DEF\uFF0C\u5E74\u8F7B\u6C49\u5B50\u8981\u4E0D\u4E86\u4E00\u4E2A\u65F6\u8FB0\u5C31\u80FD\u8D70\u5230\uFF0C\u4E39\u5A18\u7528\u4E86\u4E00\u4E2A\u65F6\u8FB0\u624D\u8D70\u5230\u534A\u8DEF\u3002\u524D\u9762\u4E00\u6BB5\u5C71\u5761\u6EE1\u662F\u4E71\u77F3\uFF0C\u4E39\u5A18\u4E24\u811A\u53C8\u9178\u53C8\u75BC\uFF0C\u53EA\u597D\u505C\u4E0B\u6765\u6B47\u606F\u3002</p><p>\u5929\u6C14\u6E10\u6709\u4E9B\u70ED\u4E86\uFF0C\u4E39\u5A18\u627E\u4E86\u5757\u5E72\u51C0\u7684\u77F3\u5934\u4FA7\u8EAB\u5750\u4E86\uFF0C\u4ECE\u8896\u4E2D\u53D6\u51FA\u4E1D\u5DFE\uFF0C\u8F7B\u62B9\u7740\u9888\u4E2D\u7684\u9999\u6C57\u3002\u5979\u672C\u6765\u80A4\u8272\u768E\u7136\uFF0C\u6B64\u65F6\u8D70\u5F97\u5FC3\u6D6E\u6C14\u4FC3\uFF0C\u53CC\u988A\u5FAE\u7EA2\uFF0C\u6108\u53D1\u8273\u4E3D\u3002</p><p>\u4E00\u4E2A\u8FC7\u8DEF\u7684\u6C49\u5B50\u4ECE\u65C1\u7ECF\u8FC7\uFF0C\u4E0D\u7531\u671D\u4E39\u5A18\u770B\u4E86\u51E0\u773C\uFF0C\u6697\u81EA\u60CA\u8273\uFF0C\u8D70\u51FA\u5341\u51E0\u4E08\u7A81\u7136\u53C8\u6298\u4E86\u56DE\u6765\uFF0C\u56B7\u9053\uFF1A\u300C\u8FD9\u4E0D\u662F\u674F\u82B1\u6751\u7684\u8001\u677F\u5A18\u5417\uFF1F\u600E\u4E48\u4E00\u4E2A\u4EBA\u5728\u8FD9\u513F\u5750\u5462\uFF1F\u300D</p><p>\u4E39\u5A18\u4EE5\u4E3A\u662F\u5E97\u91CC\u7684\u5BA2\u4EBA\uFF0C\u867D\u7136\u6709\u5FC3\u4E8B\uFF0C\u8FD8\u662F\u542B\u7B11\u70B9\u4E86\u70B9\u5934\u3002</p><p>\u8FC7\u8DEF\u4EBA\u8D70\u8FC7\u6765\u7B11\u9053\uFF1A\u300C\u6211\u8BF4\u5E97\u91CC\u600E\u4E48\u6CA1\u5F00\u95E8\u5462\u3002\u8001\u677F\u5A18\uFF0C\u8FD9\u662F\u8981\u53BB\u54EA\u554A\uFF1F\u300D</p><p>\u4E39\u5A18\u65E0\u5FC3\u642D\u8BAA\uFF0C\u53EA\u52C9\u5F3A\u7B11\u7740\uFF0C\u5782\u9996\u6396\u597D\u624B\u7EE2\u3002</p><p>\u8FD9\u5730\u65B9\u504F\u50FB\u5F97\u7D27\uFF0C\u90A3\u6C49\u5B50\u8D70\u4E86\u5341\u51E0\u91CC\u8DEF\u4E5F\u6CA1\u89C1\u5230\u4E00\u4E2A\u4EBA\u5F71\uFF0C\u770B\u5230\u8FD9\u4E2A\u7F8E\u8C8C\u5987\u4EBA\u4E00\u4E2A\u4EBA\u5B64\u96F6\u96F6\u5750\u5728\u8DEF\u8FB9\uFF0C\u4E0D\u7531\u52A8\u4E86\u90AA\u5FF5\u3002\u4ED6\u501A\u8FC7\u6765\uFF0C\u7B11\u563B\u563B\u9053\uFF1A\u300C\u8001\u677F\u5A18\uFF0C\u4E00\u4E2A\u4EBA\u8D70\u4E86\u8FD9\u4E48\u8FDC\u7684\u5C71\u8DEF\u554A\u3002\u54DF\uFF0C\u8FD9\u53CC\u5C0F\u811A\uFF0C\u600E\u4E48\u6491\u5F97\u4F4F\u5462\u2026\u2026\u300D</p><p>\u4E39\u5A18\u62C9\u4E86\u62C9\u88D9\u5B50\u906E\u4F4F\u53CC\u811A\uFF0C\u8138\u4E0A\u9732\u51FA\u4E00\u4E1D\u4E0D\u5FEB\u3002\u5973\u5B50\u7684\u811A\u6700\u662F\u7981\u5FCC\uFF0C\u9664\u4E86\u4E08\u592B\u4E0D\u80FD\u8BA9\u7B2C\u4E8C\u4E2A\u4EBA\u770B\u7684\u3002\u8FD9\u6C49\u5B50\u5982\u6B64\u98CE\u8A00\u98CE\u8BED\uFF0C\u591A\u534A\u4E0D\u662F\u4E2A\u597D\u4EBA\u3002</p><p>\u90A3\u6C49\u5B50\u89C1\u5979\u4E0D\u4F5C\u58F0\uFF0C\u6108\u53D1\u5F97\u5BF8\u8FDB\u5C3A\uFF0C\u7ADF\u4E00\u624B\u6765\u62C9\u5979\u7684\u88D9\u5B50\uFF0C\u5634\u91CC\u8BF4\u9053\uFF1A\u300C\u8BA9\u6211\u77A7\u77A7\uFF0C\u8001\u677F\u5A18\u8FD9\u53CC\u5C0F\u811A\u7F20\u5F97\u5468\u6B63\u4E0D\u5468\u6B63\u2026\u2026\u300D</p><p>\u4E39\u5A18\u6C14\u6025\uFF0C\u556A\u7684\u6253\u5F00\u4ED6\u7684\u624B\uFF0C\u63D0\u8D77\u7BEE\u5B50\u626D\u8EAB\u5C31\u8D70\u3002</p><p>\u90A3\u6C49\u5B50\u671D\u56DB\u5468\u671B\u4E86\u671B\uFF0C\u8FD9\u4F1A\u513F\u5C71\u8DEF\u4E0A\u9664\u4E86\u4ED6\u4EEC\u4E24\u4E2A\uFF0C\u518D\u65E0\u65C1\u4EBA\u3002\u90A3\u6C49\u5B50\u4E5F\u66FE\u5728\u5E97\u91CC\u5750\u8FC7\uFF0C\u5BF9\u4E39\u5A18\u65E9\u5DF2\u60F3\u5165\u975E\u975E\uFF0C\u6B64\u523B\u5927\u597D\u673A\u4F1A\uFF0C\u8352\u90CA\u91CE\u5CAD\uFF0C\u5C31\u662F\u5978\u5360\u4E86\u5979\u4E5F\u65E0\u4EBA\u77E5\u6628\uFF0C\u7AEF\u5730\u662F\u98DE\u6765\u7684\u8273\u798F\u3002\u60F3\u7740\u90A3\u6C49\u5B50\u731B\u7136\u8FFD\u4E86\u4E0A\u53BB\uFF0C\u4ECE\u540E\u4E00\u624B\u63A9\u4E86\u4E39\u5A18\u7684\u53E3\uFF0C\u4E00\u624B\u6402\u4E86\u5979\u7684\u8170\uFF0C\u671D\u65C1\u8FB9\u7684\u4E71\u77F3\u5806\u62D6\u8FC7\u53BB\u3002</p><p>\u4E39\u5A18\u6CA1\u60F3\u5230\u8FD9\u6C49\u5B50\u5982\u6B64\u5927\u80C6\uFF0C\u5149\u5929\u5316\u65E5\u4E4B\u4E0B\u7ADF\u6562\u62E6\u8DEF\u884C\u5978\uFF0C\u5979\u53C8\u60CA\u53C8\u6015\uFF0C\u4E00\u9897\u5FC3\u76F4\u8DF3\u5230\u5589\u5499\u91CC\uFF0C\u6B7B\u547D\u6323\u624E\u3002\u4F46\u5979\u529B\u6C14\u7EC8\u7A76\u6BD4\u4E0D\u4E0A\u7537\u4EBA\uFF0C\u6323\u624E\u95F4\uFF0C\u4E00\u53EA\u7EE3\u978B\u6389\u843D\u4E0B\u6765\uFF0C\u7F57\u889C\u4E5F\u88AB\u62FD\u8131\uFF0C\u9732\u51FA\u91CC\u9762\u767D\u7EEB\u811A\u5E26\u3002</p><p>\u90A3\u6C49\u5B50\u4E00\u76F4\u628A\u4E39\u5A18\u62D6\u79BB\u5C71\u8DEF\uFF0C\u62FD\u5230\u4E00\u5757\u5927\u77F3\u540E\u9762\uFF0C\u7136\u540E\u5408\u8EAB\u538B\u5728\u4E39\u5A18\u8EAB\u4E0A\uFF0C\u5C31\u53BB\u626F\u5979\u7684\u8863\u670D\u3002</p><p>\u7BEE\u5B50\u6389\u5728\u4E00\u65C1\uFF0C\u4E39\u5A18\u7CBE\u5FC3\u51C6\u5907\u7684\u83DC\u996D\u6D12\u4E86\u4E00\u5730\uFF0C\u90A3\u53EA\u624B\u6342\u5728\u5634\u4E0A\uFF0C\u6D53\u70C8\u7684\u6C57\u5473\u8BA9\u4EBA\u51E0\u4E4E\u8981\u5410\u51FA\u6765\u3002\u7B49\u6C49\u5B50\u677E\u5F00\u624B\uFF0C\u4E39\u5A18\u7ACB\u523B\u5C16\u53EB\u9053\uFF1A\u300C\u6551\u547D\u554A\u2026\u2026\u300D</p><p>\u90A3\u6C49\u5B50\u5598\u7740\u6C14\u9053\uFF1A\u300C\u8FD9\u5929\u4E0D\u7BA1\uFF0C\u5730\u4E0D\u7BA1\uFF0C\u7687\u4E0A\u4E5F\u4E0D\u7BA1\u7684\u5730\u754C\uFF0C\u5468\u56F4\u5341\u51E0\u91CC\u6CA1\u534A\u4E2A\u4EBA\u5F71\uFF0C\u8001\u677F\u5A18\uFF0C\u80FD\u5728\u8FD9\u513F\u78B0\u4E0A\uFF0C\u4E5F\u662F\u4F60\u6211\u7684\u7F18\u5206\u3002\u53EA\u8981\u4F60\u4ECE\u4E86\u6211\uFF0C\u6211\u4E0D\u4F1A\u4E3A\u96BE\u4F60\u7684\u3002\u300D</p><p>\u4E39\u5A18\u867D\u662F\u5F53\u5786\u5356\u9152\uFF0C\u4F46\u662F\u751F\u5E73\u6E05\u767D\uFF0C\u63A5\u4EBA\u5F85\u5BA2\u7AEF\u5E84\u8D1E\u8C28\uFF0C\u5E73\u5E38\u9152\u8086\u5987\u4EBA\u5356\u5F04\u98CE\u60C5\uFF0C\u6D6E\u6D6A\u8F7B\u8584\uFF0C\u5728\u5979\u4E00\u4E1D\u4E5F\u6CA1\u6709\u7684\u3002\u6B64\u65F6\u820D\u4E86\u547D\u5730\u6323\u624E\uFF0C\u63A8\u6421\u7740\u4E0D\u5BB9\u90A3\u6C49\u5B50\u8FD1\u8EAB\u3002</p><p>\u90A3\u6C49\u5B50\u4E34\u65F6\u8D77\u4E86\u6B79\u610F\uFF0C\u4E5F\u81EA\u60CA\u614C\uFF0C\u6298\u817E\u534A\u664C\uFF0C\u4E5F\u6CA1\u6709\u628A\u4E39\u5A18\u5236\u4F4F\uFF0C\u4E0D\u7531\u6025\u71E5\u8D77\u6765\u3002\u4ED6\u628A\u4E39\u5A18\u538B\u5728\u8EAB\u4E0B\uFF0C\u626F\u4E0B\u5979\u7684\u811A\u5E26\uFF0C\u5C06\u5979\u53CC\u624B\u80E1\u4E71\u6346\u4F4F\uFF0C\u7136\u540E\u9A91\u5728\u4E39\u5A18\u8EAB\u4E0A\uFF0C\u4E00\u624B\u5361\u7740\u5979\u7684\u5589\u5499\uFF0C\u4E00\u624B\u53BB\u626F\u5979\u7684\u8863\u895F\u3002</p><p>\u4E39\u5A18\u6781\u529B\u633A\u52A8\u8EAB\u4F53\uFF0C\u5FFD\u7136\u80F8\u524D\u4E00\u51C9\uFF0C\u8863\u895F\u5DF2\u88AB\u6495\u5F00\uFF0C\u9732\u51FA\u91CC\u9762\u9C9C\u7EA2\u7684\u4EB5\u8863\u3002</p><p>\u90A3\u6C49\u5B50\u773C\u4E2D\u5C04\u51FA\u51F6\u6076\u7684\u6DEB\u5149\uFF0C\u624B\u638C\u4F38\u8FDB\u4E39\u5A18\u7684\u8863\u9886\uFF0C\u671D\u5979\u4E73\u4E0A\u6478\u53BB\u3002</p><p>\u5F53\u90A3\u56E2\u6E29\u8F6F\u88AB\u4E00\u53EA\u7C97\u7CD9\u7684\u5927\u624B\u6293\u4F4F\uFF0C\u4E39\u5A18\u6D51\u8EAB\u90FD\u50F5\u786C\u4E86\u3002\u7247\u523B\u540E\uFF0C\u5979\u60B2\u9E23\u4E00\u58F0\uFF0C\u4FA7\u8EAB\u671D\u5CA9\u77F3\u4E0A\u649E\u53BB\u3002\u4F46\u90A3\u6C49\u5B50\u6309\u7740\u5979\u7684\u8116\u9888\uFF0C\u60F3\u81EA\u5C3D\u4E5F\u4F7F\u4E0D\u4E0A\u529B\u6C14\u3002</p><p>\u773C\u770B\u5C31\u8981\u8D1E\u8282\u4E0D\u4FDD\uFF0C\u5FFD\u7136\u8EAB\u4E0A\u4E00\u8F7B\uFF0C\u90A3\u6C49\u5B50\u88AB\u4EBA\u4E00\u628A\u63D0\u4E86\u8D77\u6765\u3002</p><p>\u5B59\u5929\u7FBD\u51B7\u7740\u8138\u628A\u90A3\u6C49\u5B50\u62FD\u5230\u4E00\u8FB9\uFF0C\u5288\u5934\u76D6\u8138\u4E00\u901A\u731B\u6253\u3002\u4ED6\u7684\u529F\u5E95\u76F8\u5F53\u624E\u5B9E\uFF0C\u8FD9\u4F1A\u513F\u4EC0\u4E48\u62DB\u6570\u90FD\u4E0D\u7528\uFF0C\u4E00\u62F3\u4E00\u638C\u90FD\u662F\u76F4\u6765\u76F4\u53BB\uFF0C\u62F3\u62F3\u5230\u8089\uFF0C\u76F4\u6253\u5F97\u90A3\u6C49\u5B50\u6740\u732A\u4F3C\u7684\u60E8\u568E\u3002</p><p>\u4E39\u5A18\u4E24\u624B\u88AB\u6346\uFF0C\u9760\u5728\u5CA9\u77F3\u4E0A\u6014\u6014\u770B\u7740\u5B59\u5929\u7FBD\u3002\u5B59\u5929\u7FBD\u5E74\u7EAA\u4E0D\u8FC7\u4E8C\u5341\u4E94\u516D\uFF0C\u5E73\u65F6\u8C08\u5410\u6E29\u548C\uFF0C\u751A\u81F3\u8FD8\u6709\u51E0\u5206\u65AF\u6587\uFF0C\u4F46\u6B64\u65F6\u4ED6\u767D\u51C0\u7684\u8138\u4E0A\u6EE1\u662F\u715E\u6C14\uFF0C\u62F3\u8D77\u638C\u843D\uFF0C\u72B9\u5982\u731B\u864E\u4E00\u822C\u3002</p><p>\u4E39\u5A18\u662F\u4E2A\u5F31\u8D28\u5973\u6D41\uFF0C\u767D\u5B5D\u5112\u53C8\u662F\u4E2A\u53E4\u8463\u4E66\u751F\uFF0C\u51E0\u66FE\u89C1\u8FC7\u8FD9\u6837\u751F\u9F99\u6D3B\u864E\u7684\u7CBE\u58EE\u6C49\u5B50\u3002\u90A3\u5927\u6C49\u4F53\u683C\u6BD4\u5B59\u5929\u7FBD\u8FD8\u58EE\u4E86\u4E9B\uFF0C\u4F46\u5728\u8FD9\u4E2A\u5E74\u8F7B\u4EBA\u9762\u524D\u5C31\u5982\u906D\u4E86\u761F\u7684\u83DC\u9E21\uFF0C\u6BEB\u65E0\u8FD8\u624B\u4E4B\u529B\u3002\u5BF9\u66B4\u529B\u7684\u656C\u4EF0\u662F\u4EBA\u7684\u4E00\u79CD\u672C\u80FD\uFF0C\u4E39\u5A18\u76F4\u770B\u5F97\u76EE\u7729\u795E\u9A70\uFF0C\u5973\u513F\u5F53\u65E5\u4E0E\u4EBA\u52A8\u624B\u56FA\u7136\u7CBE\u5F69\uFF0C\u5374\u6CA1\u6709\u8FD9\u79CD\u51CC\u5389\u7684\u51F6\u608D\u4E4B\u6C14\u3002</p><p>\u521A\u624D\u8FD8\u51F6\u795E\u6076\u715E\u7684\u6C49\u5B50\u6B64\u65F6\u53EA\u5269\u4E0B\u62B1\u5934\u54C0\u568E\uFF0C\u54ED\u7239\u558A\u5A18\u7684\u6C42\u4ED6\u4F4F\u624B\u3002\u5B59\u5929\u7FBD\u4E0B\u624B\u53C8\u91CD\u53C8\u72E0\uFF0C\u90A3\u6C49\u5B50\u4E0D\u591A\u65F6\u5C31\u6EE1\u8138\u662F\u8840\uFF0C\u8FDE\u7259\u9F7F\u4E5F\u88AB\u6253\u6389\u4E86\u51E0\u9897\u3002</p><p>\u5B59\u5929\u7FBD\u6559\u8BAD\u591F\u4E86\uFF0C\u6273\u4F4F\u4E86\u90A3\u4EBA\u7684\u624B\u8155\uFF0C\u628A\u4ED6\u521A\u624D\u63A2\u5165\u4E39\u5A18\u895F\u4E2D\u7684\u624B\u638C\u6309\u5728\u77F3\u4E0A\uFF0C\u7136\u540E\u4ECE\u65C1\u8FB9\u62E3\u4E86\u5757\u5C16\u77F3\uFF0C\u7167\u4ED6\u638C\u5FC3\u546F\u7684\u7838\u4E86\u4E2A\u5BF9\u7A7F\u3002</p><p>\u300C\u6211\u7684\u5A18\u54CE\u2014\u2014\u300D\u90A3\u6C49\u5B50\u62B1\u7740\u624B\u75DB\u5F7B\u5FC3\u80BA\u3002</p><p>\u300C\u6EDA\uFF01\u300D\u5B59\u5929\u7FBD\u5BD2\u7740\u8138\u4E22\u5F00\u77F3\u5934\uFF0C\u628A\u4ED6\u8E22\u5230\u4E00\u8FB9\u3002</p><p>\u90A3\u4EBA\u8FDE\u6EDA\u5E26\u722C\u9003\u51FA\u4E71\u77F3\u5806\uFF0C\u5B59\u5929\u7FBD\u8138\u4E0A\u6C14\u8272\u6E10\u6E10\u5E73\u590D\uFF0C\u56DE\u8FC7\u5934\u770B\u7740\u4E39\u5A18\u3002</p><p>\u4E39\u5A18\u4E91\u9B13\u6563\u4E71\uFF0C\u4E00\u53CC\u674F\u773C\u6CEA\u6C6A\u6C6A\uFF0C\u53EB\u4E86\u58F0\u300C\u5929\u7FBD\u2026\u2026\u300D\u5C31\u59D4\u5C48\u5730\u54ED\u4E86\u8D77\u6765\u3002</p><p>\u5979\u8863\u895F\u88AB\u626F\u5230\u814B\u4E0B\uFF0C\u8D34\u8EAB\u7684\u5927\u7EA2\u4EB5\u8863\u6B6A\u5230\u4E00\u8FB9\uFF0C\u9732\u51FA\u96EA\u767D\u7684\u9999\u80A9\uFF0C\u8FD8\u6709\u80F8\u524D\u4E00\u62B9\u8BF1\u4EBA\u7684\u5706\u6DA6\u3002\u5B59\u5929\u7FBD\u5F88\u60F3\u5C31\u6B64\u628A\u5979\u6309\u5012\uFF0C\u5C1D\u5C1D\u8FD9\u5177\u5782\u6D8E\u5DF2\u4E45\u7684\u8089\u4F53\uFF0C\u4F46\u4E39\u5A18\u90A3\u58F0\u300C\u5929\u7FBD\u300D\uFF0C\u8BA9\u4ED6\u628A\u4F38\u51FA\u7684\u624B\u53C8\u7F29\u4E86\u56DE\u6765\u3002</p><p>\u5B59\u5929\u7FBD\u6014\u4E86\u4E00\u4F1A\u513F\uFF0C\u4E0D\u4F5C\u58F0\u5730\u8E72\u4E0B\u8EAB\u5B50\u66FF\u4E39\u5A18\u89E3\u5F00\u53CC\u624B\uFF0C\u6BD4\u8D77\u521A\u624D\u7684\u51F6\u731B\uFF0C\u4ED6\u6B64\u65F6\u7684\u52A8\u4F5C\u663E\u5F97\u683C\u5916\u6E29\u67D4\uFF0C\u8FDE\u4E39\u5A18\u8155\u4E0A\u7684\u7EA2\u80BF\uFF0C\u4E5F\u5C0F\u5FC3\u907F\u5F00\u3002</p><p>\u7B49\u89E3\u5F00\u624B\u4E0A\u7684\u811A\u5E26\uFF0C\u4E39\u5A18\u5DF2\u7ECF\u54ED\u5F97\u6D51\u8EAB\u53D1\u8F6F\u3002\u8131\u79BB\u9669\u5883\u4E4B\u540E\uFF0C\u79CD\u79CD\u9A87\u6015\u3001\u59D4\u5C48\u4E00\u5E76\u6D8C\u4E0A\u5FC3\u5934\uFF0C\u5728\u65B9\u5BF8\u95F4\u6EDA\u6765\u6EDA\u53BB\uFF0C\u53CD\u800C\u6BD4\u8D77\u521D\u66F4\u96BE\u4EE5\u652F\u6491\u3002</p><p>\u5B59\u5929\u7FBD\u5E2E\u5979\u62C9\u597D\u8863\u895F\uFF0C\u7136\u540E\u5C06\u5730\u4E0A\u6389\u843D\u7684\u7269\u54C1\u4E00\u4E00\u62FE\u8D77\uFF0C\u6E29\u8A00\u9053\uFF1A\u300C\u4F24\u7740\u4E86\u5417\uFF1F\u8981\u4E0D\u8981\u6211\u6276\u4F60\u8D77\u6765\uFF1F\u300D</p><p>\u4E39\u5A18\u6447\u4E86\u6447\u5934\uFF0C\u5979\u634F\u7740\u8863\u895F\u60F3\u6491\u8D77\u8EAB\u5B50\uFF0C\u811A\u4E00\u52A8\uFF0C\u987F\u65F6\u75DB\u5F97\u54CE\u5440\u4E00\u58F0\uFF0C\u8E59\u8D77\u7709\u5934\u3002</p><p>\u5979\u90A3\u53EA\u811A\u978B\u889C\u90FD\u6389\u843D\u4E86\uFF0C\u811A\u5E26\u4E5F\u88AB\u626F\u53BB\uFF0C\u8D64\u88F8\u88F8\u7389\u7B0B\u4E00\u822C\u7C89\u5AE9\uFF0C\u521A\u624D\u6323\u624E\u4E2D\u88AB\u5C71\u77F3\u78E8\u7834\uFF0C\u6CBE\u7740\u8840\u8FF9\uFF0C\u53EA\u4E0D\u77E5\u4F24\u7684\u8F7B\u91CD\u3002</p><p>\u5B59\u5929\u7FBD\u6276\u4F4F\u5979\u7684\u811A\u8E1D\uFF0C\u8F7B\u8F7B\u6258\u8D77\uFF0C\u4E39\u5A18\u867D\u7136\u7A98\u5F97\u53D1\u6296\uFF0C\u5374\u987A\u4ECE\u5730\u4EFB\u7531\u4ED6\u62C9\u8D77\u81EA\u5DF1\u8D64\u88F8\u7684\u7EA4\u8DB3\u3002\u5B59\u5929\u7FBD\u4F4E\u5934\u770B\u53BB\uFF0C\u4E0D\u7531\u5C4F\u4F4F\u547C\u5438\u3002</p><p>\u90A3\u662F\u4E00\u53EA\u5B8C\u7F8E\u65E0\u7455\u7684\u7389\u8DB3\uFF0C\u957F\u77ED\u8FD8\u4E0D\u53CA\u4ED6\u7684\u624B\u638C\uFF0C\u80A4\u8272\u6676\u83B9\u767D\u5AE9\uFF0C\u5A07\u7F8E\u5F97\u72B9\u5982\u767D\u7389\u96D5\u6210\u3002\u56DB\u8DBE\u5F2F\u66F2\u8D34\u5728\u8DB3\u5FC3\uFF0C\u4E00\u8DBE\u7EA4\u7EA4\u6311\u8D77\uFF0C\u5B9B\u5982\u4E00\u5F2F\u65B0\u6708\u3002\u4E0E\u65C1\u4EBA\u60F3\u8C61\u4E2D\u4E0D\u540C\uFF0C\u7F20\u8FC7\u7684\u8DB3\u975E\u4F46\u6CA1\u6709\u4E00\u4E1D\u4E11\u964B\uFF0C\u53CD\u800C\u72B9\u5982\u827A\u672F\u54C1\u822C\u7CBE\u81F4\uFF0C\u4EE4\u4EBA\u546F\u7136\u5FC3\u52A8\u3002</p><p>\u4E39\u5A18\u7684\u811A\u6781\u767D\uFF0C\u6781\u8F6F\uFF0C\u5C0F\u5DE7\u73B2\u73D1\uFF0C\u811A\u5E26\u548C\u7F57\u889C\u90FD\u662F\u9999\u718F\u8FC7\u7684\uFF0C\u6E29\u6DA6\u7684\u7389\u8DB3\u63E1\u5728\u624B\u4E2D\uFF0C\u67D4\u82E5\u65E0\u9AA8\uFF0C\u6ED1\u817B\u5F97\u8BA9\u4EBA\u820D\u4E0D\u5F97\u653E\u5F00\u3002</p><p>\u5B59\u5929\u7FBD\u5931\u795E\u7684\u6837\u5B50\uFF0C\u8BA9\u4E39\u5A18\u7F9E\u4E0D\u53EF\u652F\u3002\u5979\u8FD8\u662F\u7B2C\u4E00\u6B21\u8BA9\u4E08\u592B\u4EE5\u5916\u7684\u7537\u4EBA\u63E1\u4F4F\u81EA\u5DF1\u7684\u811A\uFF0C\u5C24\u5176\u662F\u8FD9\u6837\u4E00\u4E2A\u5E74\u8F7B\u6C49\u5B50\u3002\u4ED6\u624B\u4E0A\u4F20\u6765\u7684\u70ED\u5EA6\uFF0C\u8BA9\u5979\u6574\u9897\u5FC3\u90FD\u4E71\u4E86\u3002</p><p>\u4E39\u5A18\u5A07\u9765\u8D8A\u6765\u8D8A\u7EA2\uFF0C\u534A\u664C\uFF0C\u5979\u7F9E\u6DA9\u5730\u8F7B\u8F7B\u4E00\u6323\uFF0C\u5B59\u5929\u7FBD\u56DE\u8FC7\u795E\u6765\uFF0C\u8FDE\u5FD9\u9053\uFF1A\u300C\u6211\u8FD9\u91CC\u6709\u4F24\u836F\u3002\u300D</p><p>\u767D\u5AE9\u7684\u7389\u8DB3\u88AB\u5CA9\u77F3\u78E8\u51FA\u4E86\u4E00\u6761\u8840\u75D5\uFF0C\u6BB7\u7EA2\u5F97\u8BA9\u4EBA\u5FC3\u75BC\u3002\u5B59\u5929\u7FBD\u4ECE\u6000\u91CC\u6478\u51FA\u4F24\u836F\uFF0C\u7EC6\u7EC6\u6D82\u5728\u4F24\u5904\uFF0C\u300C\u75BC\u4E0D\u75BC\uFF1F\u300D</p><p>\u4E39\u5A18\u6447\u4E86\u6447\u5934\uFF0C\u5A07\u8EAF\u7981\u4E0D\u4F4F\u8F7B\u98A4\u3002\u5B59\u5929\u7FBD\u62B9\u5B8C\u836F\uFF0C\u6068\u5FC3\u677E\u5F00\u5979\u7684\u811A\u638C\uFF0C\u628A\u836F\u74F6\u653E\u5728\u7BEE\u5B50\u91CC\uFF0C\u8BF4\u9053\uFF1A\u300C\u6BCF\u5929\u62B9\u4E24\u6B21\uFF0C\u4E09\u4E94\u5929\u5C31\u597D\u4E86\u3002\u300D</p><p>\u4E39\u5A18\u626D\u8FC7\u8EAB\u5B50\uFF0C\u4F4E\u7740\u5934\uFF0C\u9ED8\u9ED8\u7F20\u597D\u811A\u5E26\u3002</p><p>\u5979\u77E5\u9053\u5B59\u5929\u7FBD\u5728\u80CC\u540E\u5077\u770B\uFF0C\u53EF\u662F\u2026\u2026\u5DF2\u7ECF\u90FD\u8BA9\u4ED6\u770B\u4E86\u3002\u5982\u679C\u4E0D\u662F\u4ED6\uFF0C\u81EA\u5DF1\u6B64\u65F6\u5DF2\u7ECF\u8D1E\u6D01\u96BE\u4FDD\uFF0C\u65E0\u8BBA\u5982\u4F55\uFF0C\u4ED6\u90FD\u6551\u4E86\u81EA\u5DF1\u4E00\u6761\u6027\u547D\u3002</p><p>\u5B59\u5929\u7FBD\u7075\u52A8\u7684\u5FC3\u601D\u6B64\u523B\u4E5F\u505C\u6EDE\u4E86\uFF0C\u4ED6\u9ED8\u9ED8\u6CE8\u89C6\u7740\u9762\u524D\u5A07\u7F9E\u7684\u7F8E\u5987\uFF0C\u5FC3\u91CC\u6EE1\u6EE1\u7684\uFF0C\u518D\u5BB9\u4E0D\u4E0B\u5176\u5B83\u5FF5\u5934\u3002</p><p>\u5C71\u98CE\u63A0\u8FC7\uFF0C\u4E39\u5A18\u4E1D\u4E00\u822C\u7684\u79C0\u53D1\u98D8\u626C\u8D77\u6765\uFF0C\u4E71\u7EB7\u7EB7\u6ED1\u8FDB\u96EA\u767D\u7684\u9888\u5B50\u91CC\uFF0C\u4ED6\u7684\u5FC3\u4E5F\u4E71\u4E86\u3002</p>',91);function t(i,n){return r}var o=e(a,[["render",t]]);export{o as default};
