import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_09-\u56DA\u5C45" tabindex="-1"><a class="header-anchor" href="#_09-\u56DA\u5C45" aria-hidden="true">#</a> 09 \u56DA\u5C45</h1><p>\u8C7A\u72FC\u5761\u5730\u5904\u6DF1\u5C71\uFF0C\u6F6E\u5DDE\u5E9C\u7684\u56DE\u6587\u5341\u65E5\u540E\u624D\u9012\u5230\u72F1\u4E2D\u3002</p><p>\u4E0E\u5E73\u8FDC\u77E5\u53BF\u4E00\u6837\uFF0C\u6F6E\u5DDE\u77E5\u5E9C\u4E5F\u53BB\u4E86\u6CB3\u6E90\u53C2\u89C1\u5C01\u516C\u516C\uFF0C\u8859\u95E8\u91CC\u5211\u540D\u5E08\u7237\u63A5\u4E86\u6587\u4E66\uFF0C\u89C1\u662F\u767D\u83B2\u6559\u8C0B\u53CD\u7684\u5927\u6848\uFF0C\u4E0D\u6562\u6020\u6162\uFF0C\u7ACB\u5373\u4FEE\u4E66\u7980\u77E5\u4E3B\u5B98\uFF0C\u72F1\u4E2D\u7684\u6587\u4E66\u5218\u8FA8\u673A\u4E5F\u968F\u4E4B\u53BB\u4E86\u6CB3\u6E90\u4EB2\u7980\u6848\u60C5\u3002</p><p>\u770B\u7F62\u56DE\u6587\uFF0C\u960E\u7F57\u671B\u6478\u7740\u988F\u4E0B\u7684\u77ED\u9AED\uFF0C\u6C89\u541F\u534A\u664C\uFF0C\u8BA9\u4EBA\u628A\u5B59\u5929\u7FBD\u53EB\u6765\u3002</p><p>\u72F1\u5185\u4EE5\u960E\u7F57\u671B\u4E3A\u4E3B\uFF0C\u4E0B\u9762\u662F\u7262\u5934\u9C8D\u6A2A\u548C\u6587\u4E66\u5218\u8FA8\u673A\uFF0C\u5353\u5929\u96C4\u6B66\u529F\u7CBE\u5F3A\uFF0C\u4F17\u4EBA\u501A\u4ED7\u4ED6\u7684\u5730\u65B9\u4E0D\u5C11\uFF0C\u8FD9\u56DB\u4EBA\u7B97\u662F\u72F1\u4E2D\u5934\u9886\u3002\u9C8D\u6A2A\u4E00\u4ECB\u8349\u5305\uFF0C\u4E0D\u8FC7\u662F\u53BF\u5185\u6709\u4E9B\u5173\u7CFB\u624D\u4F5C\u4E86\u7262\u5934\uFF0C\u8FD9\u6869\u6848\u5B50\u5173\u7CFB\u751A\u5927\uFF0C\u960E\u7F57\u671B\u4E5F\u81EA\u5FC3\u614C\uFF0C\u5218\u8FA8\u673A\u4E0D\u5728\uFF0C\u5353\u5929\u96C4\u53BB\u4E86\u4EAC\u5E08\uFF0C\u4F59\u4E0B\u7684\u4E0D\u662F\u8349\u5305\u5C31\u662F\u996D\u6876\uFF0C\u4E5F\u5C31\u5B59\u5929\u7FBD\u8FD8\u80FD\u62FF\u4E9B\u4E3B\u610F\u3002</p><p>\u5B59\u5929\u7FBD\u770B\u7F62\u6587\u4E66\uFF0C\u7B11\u9053\uFF1A\u300C\u300C\u5179\u4F53\u4E8B\u5927\uFF0C\u72F1\u65B9\u8BF8\u540F\u5F53\u6DF1\u5FF5\u7687\u6069\uFF0C\u4E25\u67E5\u9006\u60C5\u3002</p><p>\u8C7A\u72FC\u73BB\u72F1\u8DDD\u5DDE\u5E9C\u751A\u8FDC\uFF0C\u5141\u5176\u4FBF\u5B9C\u884C\u4E8B\uFF0C\u6240\u9700\u7531\u53BF\u5E9C\u503E\u529B\u534F\u52A9\u3002\u51E1\u6848\u60C5\u6240\u7CFB\uFF0C\u65E0\u5DE8\u7EC6\u8BE6\u62A5\u5E9C\u5185\u8BBA\u5904\u3002\u300D\u8FD9\u51E0\u53E5\u8BDD\u6EF4\u6C34\u4E0D\u6F0F\uFF0C\u771F\u662F\u8001\u540F\u624B\u7B14\uFF0C\u6709\u529F\u662F\u5DDE\u5E9C\u6307\u793A\u8BBA\u5904\u7684\u529F\uFF0C\u6709\u8FC7\u662F\u54B1\u4EEC\u4FBF\u5B9C\u884C\u4E8B\u7684\u8FC7\u3002\u65E0\u8BBA\u529F\u8FC7\uFF0C\u90FD\u5C11\u4E0D\u4E86\u53BF\u5E9C\u503E\u529B\u534F\u52A9\u8FD9\u4EFD\u82E6\u52B3\u3002\u300D</p><p>\u960E\u7F57\u671B\u68EE\u7136\u9053\uFF1A\u300C\u6211\u7B49\u4F46\u77E5\u62A5\u6548\u7687\u4E0A\uFF0C\u529F\u8FC7\u8363\u8FB1\u5728\u6240\u4E0D\u8BA1\u3002\u53EA\u662F\u6B64\u6848\u7275\u6D89\u751A\u591A\uFF0C\u53EA\u6015\u671D\u91CE\u5185\u5916\u6709\u5C0F\u4EBA\u6076\u610F\u963B\u6320\uFF0C\u574F\u4E86\u7687\u4E0A\u5927\u4E8B\uFF0C\u6211\u7B49\u5C31\u767E\u6B7B\u83AB\u8D4E\u4E86\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u771F\u662F\u5F88\u4F69\u670D\u8FD9\u4F4D\u960E\u5927\u4EBA\uFF0C\u4E0D\u8FC7\u505A\u4E86\u4E09\u4E94\u5E74\u5C0F\u5B98\uFF0C\u65E0\u8BBA\u4EC0\u4E48\u8BDD\uFF0C\u8BF4\u51FA\u6765\u90FD\u662F\u51A0\u5195\u5802\u7687\uFF0C\u5176\u5B9E\u8BF4\u6765\u8BF4\u53BB\uFF0C\u8FD8\u662F\u788D\u7740\u767D\u96EA\u83B2\u5211\u90E8\u6355\u5FEB\u7684\u8EAB\u4EFD\uFF0C\u6015\u4E0D\u597D\u6536\u573A\u3002</p><p>\u5B59\u5929\u7FBD\u7B11\u9053\uFF1A\u300C\u5927\u4EBA\u4E0D\u5FC5\u5FE7\u5FC3\u3002\u767D\u5B5D\u5112\u8C0B\u53CD\u4E43\u662F\u9006\u532A\u4EB2\u53E3\u6240\u8A00\uFF0C\u53EA\u8981\u4ED6\u62DB\u4E86\u4F9B\uFF0C\u8FD8\u6015\u522B\u4EBA\u767E\u822C\u62B5\u8D56\uFF1F\u300D</p><p>\u960E\u7F57\u671B\u8FDE\u8FDE\u70B9\u5934\uFF0C\u82E5\u80FD\u62FF\u5230\u767D\u5B5D\u5112\u7684\u4F9B\u8F9E\uFF0C\u767D\u96EA\u83B2\u5C31\u662F\u6709\u5341\u6761\u6027\u547D\u4E5F\u53BB\u4E86\u4E5D\u6761\u3002\u4ED6\u628A\u624B\u6307\u634F\u5F97\u8F9F\u556A\u4F5C\u54CD\uFF0C\u5FFD\u7136\u9053\uFF1A\u300C\u65E2\u7136\u5982\u6B64\uFF0C\u5C31\u5C06\u674F\u82B1\u6751\u8BF8\u4EBA\u4E00\u5E76\u6536\u5165\u72F1\u4E2D\uFF0C\u7EC6\u52A0\u5BA1\u8BAF\uFF01\u300D</p><p>\u5B59\u5929\u7FBD\u5FC3\u5934\u4E00\u8DF3\uFF0C\u300C\u4E07\u4E07\u4E0D\u53EF\uFF01\u300D\u4ED6\u6DF1\u77E5\u90A3\u5E2E\u72F1\u5352\uFF0C\u82E5\u662F\u6295\u5165\u72F1\u4E2D\uFF0C\u83AB\u8BF4\u4E39\u5A18\u7ACB\u65F6\u8D1E\u6D01\u4E0D\u4FDD\uFF0C\u5C31\u662F\u7389\u83B2\u514D\u4E0D\u4E86\u6A2A\u906D\u6467\u6B8B\uFF0C\u4ED6\u8D39\u4E86\u504C\u5927\u529B\u6C14\uFF0C\u5982\u6B64\u4E00\u6765\uFF0C\u8FD9\u756A\u5FC3\u8840\u4FBF\u662F\u4ED8\u4E4B\u4E1C\u6D41\u4E86\u3002</p><p>\u960E\u7F57\u671B\u4E00\u6014\uFF0C\u300C\u6709\u4F55\u4E0D\u59A5\uFF1F\u300D\u8FD9\u51E0\u65E5\u4ED6\u6EE1\u5FC3\u60F3\u7740\u5347\u5B98\u53D1\u8D22\uFF0C\u8FDE\u553E\u624B\u53EF\u5F97\u7684\u7F8E\u8272\u4E5F\u4E0D\u514D\u653E\u5230\u4E86\u7B2C\u4E8C\u4F4D\uFF0C\u8FD9\u4F1A\u513F\u8BF4\u62FF\u4E39\u5A18\u7B49\u4EBA\u5165\u72F1\u5012\u975E\u662F\u4E3A\u4E86\u6DEB\u6B32\u3002</p><p>\u5B59\u5929\u7FBD\u5FC3\u5FF5\u7535\u8F6C\uFF0C\u4E00\u9762\u653E\u6162\u4E86\u8BED\u8C03\uFF0C\u7F13\u7F13\u8BF4\u9053\uFF1A\u300C\u767D\u5B5D\u5112\u8FD8\u672A\u670D\u7F6A\uFF0C\u6B64\u65F6\u628A\u5BB6\u5C5E\u62FF\u5230\u72F1\u4E2D\uFF0C\u591A\u6709\u4E0D\u59A5\u2014\u2014\u767D\u5B5D\u5112\u8FC2\u592B\u5B50\u4E00\u4E2A\uFF0C\u5014\u5F97\u5F88\u3002\u300D</p><p>\u960E\u7F57\u671B\u6C89\u541F\u4E0D\u8BED\uFF0C\u5B59\u5929\u7FBD\u6700\u540E\u52A0\u7684\u8FD9\u53E5\u4ED6\u542C\u660E\u767D\u4E86\uFF0C\u4EE5\u767D\u5B5D\u5112\u7684\u6027\u5B50\uFF0C\u5982\u679C\u59BB\u5973\u5728\u72F1\u4E2D\u53D7\u8FB1\u5931\u8EAB\uFF0C\u4ED6\u62B5\u6B7B\u4E5F\u4E0D\u4F1A\u62DB\u4F9B\u3002</p><p>\u5B59\u5929\u7FBD\u53C8\u9053\uFF1A\u300C\u6A2A\u7AD6\u674F\u82B1\u6751\u53EA\u5269\u4E86\u4E24\u4E2A\u5F31\u5973\u4E00\u4E2A\u7A1A\u5B50\uFF0C\u8FD9\u795E\u4ED9\u5CAD\u5C31\u662F\u5929\u7136\u7684\u7262\u7B3C\uFF0C\u5979\u4EEC\u60F3\u9003\u4E5F\u9003\u4E0D\u4E86\u3002\u300D</p><p>\u960E\u7F57\u671B\u70B9\u4E86\u70B9\u5934\uFF0C\u5429\u5490\u5B59\u5929\u7FBD\u7D27\u76EF\u7740\u674F\u82B1\u6751\uFF0C\u4E0D\u8981\u51FA\u4E86\u4E71\u5B50\uFF0C\u4E00\u9762\u4F20\u767D\u5B5D\u5112\u4E0A\u5802\u3002\u4ED6\u8981\u518D\u7ED9\u8FD9\u4E2A\u8001\u5BB6\u4F19\u4E0A\u4E0A\u5211\uFF0C\u6398\u5F00\u4ED6\u7684\u5634\u3002</p><p>\u767D\u5B5D\u5112\u4E24\u817F\u53D7\u4F24\u6781\u91CD\uFF0C\u53EA\u80FD\u52C9\u5F3A\u632A\u52A8\u811A\u6B65\uFF0C\u6B64\u65F6\u88AB\u4E24\u540D\u72F1\u5352\u62D6\u5230\u5802\u4E0A\uFF0C\u4ED6\u5374\u6602\u7740\u5934\u9888\uFF0C\u5927\u58F0\u9053\uFF1A\u300C\u8349\u6C11\u65E0\u7F6A\uFF01\u300D</p><p>\u960E\u7F57\u671B\u61D2\u5F97\u518D\u8DDF\u4ED6\u5E9F\u8BDD\uFF0C\u53EA\u51B7\u7B11\u4E00\u58F0\uFF0C\u4FBF\u5429\u5490\u884C\u5211\u3002</p><p>\u4F55\u6C42\u56FD\u62FF\u7740\u5757\u4E0D\u8D77\u773C\u7684\u6728\u677F\uFF0C\u62CD\u4E86\u62CD\uFF0C\u72DE\u7B11\u9053\uFF1A\u300C\u767D\u8001\u592B\u5B50\uFF0C\u54B1\u7ED9\u4F60\u6362\u53CC\u65B0\u978B\u3002\u300D</p><p>\u72F1\u5352\u4EEC\u5939\u4F4F\u767D\u5B5D\u5112\u7600\u9ED1\u7684\u5C0F\u817F\uFF0C\u6309\u5728\u6728\u677F\u4E0A\uFF0C\u90A3\u5757\u6728\u677F\u56DB\u56DB\u65B9\u65B9\uFF0C\u6BD4\u4ED6\u7684\u811A\u5927\u4E86\u4E00\u5708\uFF0C\u8868\u9762\u7684\u6BDB\u523A\u8FD8\u672A\u78E8\u5E73\u3002\u4F55\u6C42\u56FD\u53C8\u53D6\u4E86\u5757\u8F83\u5C0F\u7684\u653E\u5728\u4ED6\u811A\u80CC\u4E0A\uFF0C\u7136\u540E\u7528\u706B\u94B3\u5728\u7089\u91CC\u62E8\u4E86\u62E8\uFF0C\u5939\u51FA\u4E00\u679A\u4E09\u89D2\u5F62\u7684\u94C1\u9489\u3002</p><p>\u90A3\u94C1\u9489\u5728\u7089\u91CC\u70E7\u5F97\u901A\u7EA2\uFF0C\u706B\u4E00\u822C\u7684\u70AD\u5C51\u6389\u5728\u6728\u677F\u4E0A\uFF0C\u7ACB\u65F6\u5192\u51FA\u4E00\u80A1\u9752\u70DF\u3002\u767D\u5B5D\u5112\u4E24\u773C\u7D27\u7D27\u76EF\u7740\u4ED6\u7684\u4E3E\u52A8\uFF0C\u547C\u5438\u8D8A\u6765\u8D8A\u6025\uFF0C\u4F55\u6C42\u56FD\u4E00\u811A\u8E29\u4F4F\u6728\u677F\u8FB9\u7F18\uFF0C\u7528\u706B\u94B3\u5939\u4F4F\u94C1\u9489\u6811\u5728\u4E0A\u9762\uFF0C\u7136\u540E\u62A1\u94C1\u9524\uFF0C\u546F\u7684\u7838\u4E86\u4E0B\u53BB\u3002</p><p>\u767D\u5B5D\u5112\u5413\u7684\u4E00\u58F0\uFF0C\u4E24\u773C\u9F13\u8D77\uFF0C\u773C\u73E0\u4E0A\u66B4\u8D77\u8840\u4E1D\u3002\u70E7\u7EA2\u7684\u94C1\u9489\u7A7F\u8FC7\u6728\u677F\uFF0C\u6DF1\u6DF1\u523A\u5165\u811A\u80CC\uFF0C\u5728\u8840\u8089\u4E2D\u6ECB\u6ECB\u4F5C\u54CD\uFF0C\u9C9C\u8840\u672A\u53CA\u6D41\u51FA\uFF0C\u5C31\u51DD\u4F4F\u4E86\u3002\u7247\u523B\u540E\uFF0C\u767D\u5B5D\u5112\u624D\u53D1\u51FA\u975E\u4EBA\u7684\u60E8\u53EB\u3002</p><p>\u4F55\u6C42\u56FD\u7B11\u563B\u563B\u62A1\u7740\u94C1\u9524\uFF0C\u4E09\u4E24\u4E0B\u5DE5\u592B\uFF0C\u94C1\u9489\u5C31\u7A7F\u8FC7\u767D\u5B5D\u5112\u7684\u811A\u80CC\uFF0C\u4ECE\u811A\u5E95\u7684\u6728\u677F\u5904\u9732\u51FA\u4E00\u622A\u3002\u94C1\u9489\u7684\u989C\u8272\u9EEF\u6DE1\u4E0B\u6765\uFF0C\u72B9\u5982\u4E00\u53EA\u4E4C\u7EA2\u7684\u5229\u9F7F\uFF0C\u4F55\u6C42\u56FD\u5C06\u9489\u5C16\u6572\u5F2F\uFF0C\u7136\u540E\u518D\u5BF9\u53E6\u4E00\u53EA\u811A\u5982\u6CD5\u70AE\u5236\u3002\u7B49\u4ED6\u677E\u5F00\u624B\uFF0C\u767D\u5B5D\u5112\u811A\u4E0A\u5DF2\u7ECF\u591A\u4E86\u4E00\u5BF9\u539A\u539A\u6728\u677F\uFF0C\u4E24\u5757\u6728\u677F\u7D27\u8D34\u7740\u811A\u638C\u811A\u80CC\uFF0C\u5F77\u4F5B\u4E00\u53CC\u4E09\u89D2\u72B6\u7684\u6728\u978B\u3002</p><p>\u72F1\u5352\u4EEC\u628A\u767D\u5B5D\u5112\u62FD\u8D77\u6765\uFF0C\u90A3\u4E24\u679A\u94C1\u9489\u5DF2\u7ECF\u4E0E\u8840\u8089\u7C98\u8FDE\uFF0C\u7565\u4E00\u7528\u529B\uFF0C\u4F24\u53E3\u5C31\u8F6C\u6765\u6495\u88C2\u822C\u7684\u75DB\u610F\uFF0C\u4E09\u89D2\u72B6\u7684\u9489\u8EAB\u66F4\u4F3C\u8981\u628A\u811A\u638C\u7AD6\u7740\u5288\u5F00\u3002\u767D\u5B5D\u5112\u6643\u4E86\u4E00\u4E0B\uFF0C\u91CD\u91CD\u6454\u5012\u5728\u5730\uFF0C\u4E24\u817F\u62BD\u6410\u8D77\u6765\uFF0C\u6728\u677F\u6572\u5728\u5730\u4E0A\uFF0C\u53D1\u51FA\u6E05\u8106\u7684\u55D2\u55D2\u58F0\u3002</p><p>\u300C\u767D\u5B5D\u5112\uFF0C\u300D\u960E\u7F57\u671B\u6E29\u8A00\u9053\uFF1A\u300C\u4F60\u5BC6\u8C0B\u4F5C\u53CD\uFF0C\u514D\u4E0D\u4E86\u8981\u660E\u5178\u6B63\u5211\uFF0C\u51CC\u8FDF\u5904\u6B7B\u7684\u3002\u4E0D\u5982\u65E9\u4E9B\u62DB\u4F9B\uFF0C\u4E5F\u80FD\u5C11\u5403\u4E9B\u96F6\u788E\u82E6\u5934\u3002\u300D</p><p>\u767D\u5B5D\u5112\u4E45\u4E45\u6CA1\u6709\u4F5C\u58F0\uFF0C\u7ADF\u662F\u53C8\u660F\u4E86\u8FC7\u53BB\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5730\u7262\u95E8\u53E3\u6446\u7740\u4E00\u5F20\u5E8A\uFF0C\u72F1\u5352\u503C\u591C\u65F6\u7761\u7684\u5730\u65B9\uFF0C\u4F46\u6B64\u65F6\u5E8A\u4E0A\u5374\u7761\u4E86\u4E24\u4E2A\u4EBA\u3002\u80E1\u4E25\u4ECE\u80CC\u540E\u6402\u7740\u859B\u971C\u7075\u7684\u8EAB\u5B50\uFF0C\u4E00\u624B\u634F\u7740\u5979\u7684\u4E73\u623F\uFF0C\u4E00\u624B\u653E\u5728\u5979\u80A1\u95F4\u3002\u859B\u971C\u7075\u4FA7\u8EBA\u5728\u5E8A\u4E0A\uFF0C\u5F20\u5F00\u817F\u4EFB\u7531\u90A3\u53EA\u624B\u5728\u81EA\u5DF1\u79D8\u5904\u638F\u6478\uFF0C\u53EA\u95ED\u7740\u773C\u9ED8\u4E0D\u4F5C\u58F0\u3002</p><p>\u8986\u5728\u5165\u53E3\u7684\u94C1\u7F69\u4F20\u6765\u51E0\u58F0\u8F7B\u54CD\uFF0C\u80E1\u4E25\u62D4\u51FA\u6E7F\u6DCB\u6DCB\u7684\u624B\u6307\uFF0C\u5728\u859B\u971C\u7075\u5C41\u80A1\u4E0A\u62B9\u4E86\u62B9\uFF0C\u628A\u5979\u4E00\u811A\u8E22\u5F00\uFF0C\u7136\u540E\u6253\u5F00\u94C1\u7F69\uFF0C\u94BB\u5230\u5916\u9762\u4E0E\u6765\u4EBA\u53FD\u53FD\u5495\u5495\u8BF4\u4E86\u534A\u664C\u3002</p><p>\u859B\u971C\u7075\u629A\u7740\u808B\u4E0B\uFF0C\u6162\u6162\u56DE\u5230\u7262\u7B3C\uFF0C\u50CF\u53D7\u4F24\u7684\u5C0F\u732B\u4E00\u6837\uFF0C\u8737\u7F29\u5728\u7A3B\u8349\u5806\u4E0A\u3002\u8FC7\u4E86\u7247\u523B\uFF0C\u94C1\u7F69\u4F20\u6765\u9501\u94FE\u7684\u54CD\u52A8\uFF0C\u80E1\u4E25\u4ECE\u5916\u9762\u9501\u4F4F\u5730\u7262\uFF0C\u663E\u7136\u8DDF\u90A3\u4EBA\u79BB\u5F00\u4E86\u3002</p><p>\u8737\u7F29\u5728\u5899\u89D2\u7684\u767D\u96EA\u83B2\u6162\u6162\u7AD9\u8D77\u8EAB\u5B50\uFF0C\u6276\u7740\u4E03\u5341\u65A4\u7684\u94C1\u67B7\uFF0C\u5750\u5728\u65C1\u8FB9\u4E00\u53EA\u51C0\u6876\u4E0A\uFF0C\u5C0F\u5FC3\u7FFC\u7FFC\u4E0D\u53D1\u51FA\u4E00\u70B9\u58F0\u54CD\u3002\u5979\u7684\u88D9\u88E4\u540E\u9762\u88AB\u5B8C\u5168\u6495\u5F00\uFF0C\u7565\u4E00\u5F2F\u8170\uFF0C\u767D\u751F\u751F\u7684\u5C41\u80A1\u5C31\u6574\u4E2A\u66B4\u9732\u51FA\u6765\uFF0C\u56E0\u6B64\u4E00\u76F4\u8737\u7740\u817F\u8EB2\u5728\u89D2\u843D\u91CC\u3002\u4F46\u597D\u5904\u5C31\u662F\u65B9\u4FBF\u65F6\u4E0D\u9700\u8981\u892A\u4E0B\u8863\u88E4\uFF0C\u8FD9\u5728\u5979\u662F\u65E0\u6CD5\u505A\u5230\u7684\u3002</p><p>\u867D\u7136\u5F88\u5C0F\u5FC3\u5730\u4E0D\u53D1\u51FA\u4E00\u70B9\u58F0\u97F3\uFF0C\u4F46\u4E45\u84C4\u7684\u5C3F\u6DB2\u6E85\u5728\u51C0\u6876\u4E0A\uFF0C\u8FD8\u662F\u4F20\u6765\u4EE4\u4EBA\u96BE\u582A\u7684\u54CD\u52A8\u3002\u859B\u971C\u7075\u7741\u5F00\u773C\u775B\uFF0C\u4E24\u4EBA\u5BF9\u89C6\u4E00\u773C\uFF0C\u5404\u81EA\u9519\u5F00\u3002\u767D\u96EA\u83B2\u8138\u4E0A\u4E00\u65E0\u6240\u52A8\uFF0C\u7259\u5173\u5374\u4E0D\u7981\u54AC\u7D27\u3002\u82E5\u8BBA\u7F9E\u803B\uFF0C\u859B\u971C\u7075\u6574\u5929\u5149\u7740\u8EAB\u5B50\u88AB\u72F1\u5352\u4EEC\u8F6E\u6D41\u5978\u6DEB\uFF0C\u81EA\u7136\u6BD4\u5979\u803B\u8FB1\u767E\u500D\u3002\u53EF\u5979\u5802\u5802\u4E00\u4E2A\u5211\u90E8\u6355\u5FEB\uFF0C\u7ADF\u4E5F\u843D\u5165\u72F1\u4E2D\uFF0C\u4FBF\u6EBA\u90FD\u65E0\u6CD5\u907F\u4EBA\uFF0C\u60F3\u8D77\u6765\u767D\u96EA\u83B2\u5C31\u7F9E\u5F97\u62AC\u4E0D\u8D77\u5934\u3002</p><p>\u5979\u9ED8\u9ED8\u7B97\u7740\u65E5\u5B50\uFF0C\u6B64\u65F6\u7701\u5E9C\u5DF2\u7ECF\u63A5\u5230\u6848\u60C5\uFF0C\u5E7F\u4E1C\u603B\u6355\u5434\u5927\u5F6A\u662F\u5979\u5E08\u53D4\uFF0C\u867D\u7136\u53EA\u968F\u4F17\u89C1\u8FC7\u4E00\u9762\uFF0C\u4F46\u8FD9\u6B21\u80FD\u6210\u4E3A\u5211\u90E8\u6355\u5FEB\uFF0C\u8FD8\u662F\u4ED6\u4E00\u529B\u4FDD\u4E3E\uFF0C\u5F97\u4FE1\u540E\u52BF\u5FC5\u4F1A\u8D76\u5230\u72F1\u4E2D\u67E5\u95EE\u6848\u7531\u3002\u6700\u591A\u518D\u6709\u516D\u5929\uFF0C\u5979\u5C31\u80FD\u5411\u5E08\u53D4\u7980\u660E\u51A4\u5C48\u3002</p><p>\u9A6C\u6876\u8FB9\u7684\u5899\u58C1\u4E0A\u6A2A\u7740\u6839\u6728\u693D\uFF0C\u7528\u6765\u628A\u56DA\u72AF\u9501\u5728\u4E0A\u9762\uFF0C\u859B\u971C\u7075\u8981\u4F3A\u5019\u4F17\u4EBA\uFF0C\u767D\u96EA\u83B2\u5E26\u7740\u91CD\u67B7\uFF0C\u6728\u693D\u4FBF\u7A7A\u4E86\u51FA\u6765\u3002\u5B83\u79BB\u5730\u9762\u4E24\u5C3A\u9AD8\u4F4E\uFF0C\u957F\u8FD1\u4E00\u5C3A\uFF0C\u62F3\u5934\u7C97\u7EC6\uFF0C\u6B64\u523B\u693D\u5934\u5305\u7740\u51E0\u5C42\u8349\u7EB8\u3002\u767D\u96EA\u83B2\u79BB\u5F00\u9A6C\u6876\uFF0C\u4EE5\u4E00\u4E2A\u5F88\u522B\u626D\u5730\u59FF\u52BF\u5927\u5F20\u7740\u817F\uFF0C\u7FD8\u8D77\u5C41\u80A1\uFF0C\u5728\u4E0A\u9762\u6765\u56DE\u78E8\u52A8\u3002</p><p>\u867D\u7136\u7F9E\u803B\uFF0C\u4F46\u767D\u96EA\u83B2\u53EA\u80FD\u505A\u5230\u8FD9\u4E00\u6B65\u3002\u5979\u5C31\u7528\u8FD9\u79CD\u65B9\u5F0F\u63E9\u51C0\u5C41\u80A1\uFF0C\u7136\u540E\u8E72\u4E0B\u6765\uFF0C\u4ECE\u67B7\u4E2D\u4F38\u76F4\u6307\u5C16\uFF0C\u53D6\u4E0B\u8349\u7EB8\uFF0C\u6254\u8FDB\u9A6C\u6876\uFF0C\u53C8\u65B0\u6362\u4E86\u8349\u7EB8\uFF0C\u518D\u628A\u9A6C\u6876\u76D6\u4F4F\u3002\u859B\u971C\u7075\u770B\u7740\u5979\u8270\u96BE\u5730\u4E3E\u52A8\uFF0C\u8138\u4E0A\u59CB\u7EC8\u5E26\u7740\u4E00\u4E1D\u51B7\u7B11\u3002</p><p>\u80E1\u4E25\u63D0\u7740\u7CA5\u996D\u56DE\u5230\u5730\u7262\uFF0C\u540E\u9762\u8DDF\u7740\u521A\u5BA1\u8BAF\u5B8C\u7684\u4F55\u6C42\u56FD\u3002\u5DF2\u7ECF\u5230\u4E86\u665A\u996D\u65F6\u5019\u3002</p><p>\u8FD1\u51E0\u65E5\u7684\u665A\u996D\u4E0E\u4EE5\u524D\u6709\u4E86\u5929\u58E4\u4E4B\u522B\uFF0C\u71AC\u7CA5\u7684\u7ADF\u662F\u51C0\u7C73\uFF0C\u4EFD\u91CF\u4E5F\u6BD4\u4EE5\u524D\u591A\u4E86\u4E0D\u5C11\uFF0C\u4F46\u8FD9\u4E00\u4EFD\u552F\u72EC\u662F\u767D\u96EA\u83B2\u7684\uFF0C\u859B\u971C\u7075\u5403\u7684\u4ECD\u662F\u998A\u7CA5\u3002\u5979\u6CA1\u6709\u4F5C\u58F0\uFF0C\u53EA\u5B89\u5B89\u5206\u5206\u5403\u7740\u81EA\u5DF1\u7684\u4E00\u4EFD\uFF0C\u5BF9\u767D\u96EA\u83B2\u7684\u4F18\u5F85\u89C6\u82E5\u65E0\u7779\u3002</p><p>\u80E1\u4E25\u76DB\u4E86\u7CA5\uFF0C\u4EB2\u624B\u628A\u7897\u653E\u5728\u67B7\u9762\u4E0A\uFF0C\u53C8\u653E\u4E86\u4E24\u53EA\u7A9D\u5934\uFF0C\u7B11\u7787\u7787\u5730\u9053\uFF1A\u300C\u767D\u59D1\u5A18\uFF0C\u8D76\u7D27\u5403\u5427\u3002\u300D</p><p>\u767D\u96EA\u83B2\u6162\u6162\u5730\u559D\u7740\u7CA5\uFF0C\u7A81\u7136\u60F3\u8D77\u4E86\u4EC0\u4E48\uFF0C\u67B7\u9762\u4E00\u6C89\uFF0C\u4E0A\u9762\u7684\u74F7\u7897\u6389\u5728\u5730\u4E0A\u6454\u5F97\u7C89\u788E\u3002\u521A\u624D\u4E3A\u4E86\u653E\u597D\u7897\uFF0C\u5979\u628A\u94C1\u67B7\u5E73\u6258\u8D77\u6765\uFF0C\u4F55\u6C42\u56FD\u7684\u773C\u775B\u4E00\u76F4\u76EF\u7740\u5979\u4E24\u817F\u4E4B\u95F4\uFF0C\u663E\u7136\u662F\u4ECE\u5979\u80A1\u95F4\u7684\u7834\u5904\u5F80\u91CC\u5F20\u671B\u3002</p><p>\u6562\u5728\u72F1\u4E2D\u6253\u788E\u996D\u7897\uFF0C\u56DA\u72AF\u8F7B\u5219\u6328\u4E00\u987F\u72E0\u6253\uFF0C\u91CD\u5219\u5C31\u6B64\u4EC0\u4E48\u4E5F\u6CA1\u5F97\u5403\uFF0C\u80E1\u4E25\u8FD9\u6B21\u7684\u6001\u5EA6\u5374\u662F\u51FA\u5947\u7684\u597D\uFF0C\u4E00\u53E5\u8BDD\u90FD\u6CA1\u8BF4\u3002</p><p>\u4F55\u6C42\u56FD\u628A\u80E1\u4E25\u62C9\u5230\u4E00\u8FB9\uFF0C\u4F4E\u58F0\u95EE\uFF1A\u300C\u4F60\u8BF4\u7684\u771F\u7684\u5047\u7684\uFF1F\u300D</p><p>\u80E1\u4E25\u779F\u4E86\u767D\u96EA\u83B2\u4E00\u773C\uFF0C\u538B\u4F4E\u4E86\u58F0\u97F3\u8BF4\uFF1A\u300C\u54B1\u4EEC\u4EC0\u4E48\u4EA4\u60C5\uFF1F\u4F60\u4E0D\u77E5\u9053\uFF0C\u90A3\u5C0F\u599E\u5C41\u773C\u513F\u53C8\u7D27\u53C8\u6ED1\uFF0C\u54B1\u5929\u5929\u90FD\u73A9\uFF0C\u522B\u63D0\u591A\u8212\u670D\u4E86\u3002\u300D</p><p>\u4F55\u6C42\u56FD\u54BD\u4E86\u53E3\u5410\u6CAB\uFF0C\u300C\u65F1\u8DEF\u8D70\u8D77\u6765\u6BD4\u524D\u9762\u8FD8\u723D\uFF1F\u300D</p><p>\u80E1\u4E25\u563F\u563F\u7B11\u9053\uFF1A\u300C\u4E00\u4F1A\u513F\u5F04\u8FC7\u4F60\u5C31\u77E5\u9053\u4E86\u3002\u8001\u4F55\uFF0C\u8FD9\u72F1\u91CC\u6211\u5C31\u544A\u8BC9\u4F60\u4E00\u4E2A\uFF0C\u5343\u4E07\u522B\u5BF9\u65C1\u4EBA\u8BF4\u3002\u300D</p><p>\u300C\u653E\u5FC3\u5427\u3002\u300D\u4F55\u6C42\u56FD\u6EE1\u53E3\u7B54\u5E94\uFF0C\u60F3\u8D77\u767D\u96EA\u83B2\u7684\u6B66\u529F\uFF0C\u53C8\u6709\u4E9B\u4E0D\u653E\u5FC3\uFF0C\u300C\u90A3\u836F\u600E\u4E48\u6837\uFF1F\u300D</p><p>\u300C\u90A3\u662F\u5C0F\u5B59\u5F04\u7684\u836F\uFF0C\u597D\u7740\u5462\uFF0C\u600E\u4E48\u73A9\u90FD\u4E0D\u9192\u3002\u4F60\u4E5F\u7559\u70B9\u513F\u795E\uFF0C\u522B\u63D2\u5F97\u592A\u72E0\uFF0C\u514D\u5F97\u5F04\u88C2\u4E86\u9732\u51FA\u9A6C\u811A\u2026\u2026\u300D</p><p>\u8BF4\u8BDD\u95F4\u767D\u96EA\u83B2\u5DF2\u7ECF\u6C89\u6C89\u7761\u53BB\u3002\u5373\u4F7F\u5979\u542C\u5230\u4E24\u4EBA\u7684\u5BF9\u8BDD\uFF0C\u4E5F\u731C\u4E0D\u5230\u662F\u4E0E\u81EA\u5DF1\u6709\u5173\u3002\u81EA\u4ECE\u90A3\u5929\u5B59\u5929\u7FBD\u7ED9\u5979\u7834\u809B\u4E4B\u540E\uFF0C\u80E1\u4E25\u6BCF\u5929\u665A\u996D\u90FD\u7ED9\u767D\u96EA\u83B2\u4E0B\u4E86\u8499\u6C57\u836F\uFF0C\u7136\u540E\u8D81\u7740\u5979\u660F\u7761\u4E0D\u9192\uFF0C\u6063\u610F\u5978\u5F04\u5979\u7684\u540E\u5EAD\u3002\u8FDE\u65E5\u6765\u73A9\u5F97\u9AD8\u5174\uFF0C\u5FCD\u4E0D\u4F4F\u5BF9\u4F55\u6C42\u56FD\u8BF4\u4E86\u3002</p><p>\u767D\u96EA\u83B2\u4E00\u660F\u5012\uFF0C\u4F55\u6C42\u56FD\u5C31\u8FEB\u4E0D\u53CA\u5F85\u5730\u6253\u5F00\u7262\u95E8\uFF0C\u628A\u5979\u7FFB\u8F6C\u8FC7\u6765\uFF0C\u62B1\u7740\u5C41\u80A1\u5C31\u8981\u5F80\u91CC\u9762\u6345\u3002</p><p>\u300C\u6025\u4EC0\u4E48\u5462\uFF1F\u300D\u80E1\u4E25\u626F\u4E86\u4ED6\u4E00\u628A\uFF0C\u300C\u5C31\u54B1\u4EEC\u4FE9\u4EBA\u513F\uFF0C\u795E\u4E0D\u77E5\u9B3C\u4E0D\u89C9\uFF0C\u5F04\u51FA\u6765\u6162\u6162\u5F04\u3002\u300D</p><p>\u8FD9\u94C1\u7B3C\u53EA\u6709\u516D\u5C3A\u89C1\u65B9\uFF0C\u767D\u96EA\u83B2\u9888\u4E0A\u7684\u94C1\u67B7\u5E73\u4E3E\u5DEE\u4E0D\u591A\u9876\u5230\u4E24\u7AEF\uFF0C\u5E72\u8D77\u6765\u9887\u4E3A\u4E0D\u4FBF\u3002\u4F55\u6C42\u56FD\u4F9D\u8A00\u6258\u8D77\u767D\u96EA\u83B2\uFF0C\u53EA\u89C9\u90A3\u9762\u94C1\u67B7\u6C89\u7538\u7538\u4EFD\u91CF\u7740\u5B9E\u4E0D\u8F7B\uFF0C\u771F\u4E0D\u77E5\u9053\u8FD9\u5973\u5B50\u662F\u600E\u4E48\u6491\u4E0B\u6765\u7684\u3002</p><p>\u859B\u971C\u7075\u5F80\u540E\u9000\u4E86\u9000\uFF0C\u4E00\u8A00\u4E0D\u53D1\u5730\u7ED9\u4E24\u4EBA\u7559\u51FA\u901A\u8DEF\u3002\u767D\u96EA\u83B2\u9F3B\u606F\u60A0\u957F\uFF0C\u88AB\u4E24\u4EBA\u4E00\u756A\u6446\u5F04\u4E5F\u4E1D\u6BEB\u6CA1\u6709\u9192\u6765\u7684\u8FF9\u8C61\u3002\u5730\u9762\u9752\u77F3\u95F4\u7684\u7F1D\u9699\uFF0C\u7528\u6765\u5361\u4F4F\u94C1\u67B7\u6700\u662F\u65B9\u4FBF\u4E0D\u8FC7\u3002\u4E24\u4EBA\u534F\u529B\uFF0C\u628A\u94C1\u67B7\u6811\u5728\u77F3\u9699\u4E2D\uFF0C\u7136\u540E\u6258\u7740\u767D\u96EA\u83B2\u7684\u8170\u81C0\uFF0C\u5C06\u5979\u53CC\u817F\u66F2\u8D77\uFF0C\u6446\u6210\u4F0F\u5730\u633A\u81C0\u7684\u59FF\u52BF\u3002</p><p>\u300C\u600E\u4E48\u6837\uFF1F\u300D\u80E1\u4E25\u62CD\u4E86\u62CD\u90A3\u53EA\u8D64\u88F8\u7684\u96EA\u81C0\uFF0C\u7B11\u9053\uFF1A\u300C\u6BD4\u8001\u4F55\u4F60\u7684\u8138\u8FD8\u6F02\u4EAE\u51E0\u5206\u5462\u3002\u300D</p><p>\u8001\u4F55\u770B\u7740\u767D\u96EA\u83B2\u5149\u6D01\u5982\u7389\u7684\u7F8E\u81C0\u76EE\u7729\u795E\u9A70\uFF0C\u5F53\u65E5\u960E\u7F57\u671B\u867D\u7136\u6258\u8D77\u5979\u7684\u9634\u6237\u8BA9\u4F17\u4EBA\u90FD\u6B23\u8D4F\u8FC7\uFF0C\u4F46\u600E\u53CA\u8FD9\u6574\u53EA\u5C41\u80A1\u9C9C\u4EAE\u4EAE\u7FD8\u5728\u773C\u524D\u7684\u6D3B\u8272\u751F\u9999\uFF1F\u4ED6\u4E24\u773C\u53D1\u76F4\uFF0C\u4E00\u8138\u7684\u9EBB\u5B50\u90FD\u653E\u51FA\u5149\u6765\uFF0C\u534A\u664C\u624D\u5E94\u9053\uFF1A\u300C\u90A3\u662F\u90A3\u662F\u2026\u2026\u300D</p><p>\u63B0\u5F00\u5C41\u80A1\uFF0C\u80E1\u4E25\u4E0D\u7531\u300C\u55E8\u300D\u4E86\u4E00\u58F0\uFF0C\u300C\u8FD9\u5C0F\u599E\u8FD8\u771F\u80FD\u5FCD\uFF0C\u6211\u8FD8\u4EE5\u4E3A\u5979\u90FD\u4E0D\u7528\u62C9\u4E86\u5462\u3002\u300D</p><p>\u767D\u96EA\u83B2\u6BCF\u65E5\u5403\u5F97\u6781\u5C11\uFF0C\u4F46\u7EC8\u7A76\u662F\u4EBA\uFF0C\u514D\u4E0D\u4E86\u6709\u751F\u7406\u672C\u80FD\u3002\u4F55\u6C42\u56FD\u6025\u5306\u5306\u89E3\u5F00\u8863\u670D\uFF0C\u53C8\u88AB\u80E1\u4E25\u62C9\u4F4F\uFF0C\u300C\u522B\u6025\uFF0C\u5148\u6D17\u6D17\uFF0C\u73A9\u8D77\u6765\u4E5F\u75DB\u5FEB\u3002\u300D</p><p>\u6876\u91CC\u662F\u521A\u6253\u6765\u7684\u6DA7\u6C34\uFF0C\u6C34\u9762\u8F7B\u8F7B\u4E00\u6643\uFF0C\u5C31\u4F20\u6765\u788E\u51B0\u649E\u51FB\u7684\u54CD\u52A8\u3002\u51B0\u5BD2\u7684\u6DA7\u6C34\u6CFC\u5728\u81C0\u4E0A\uFF0C\u767D\u96EA\u83B2\u5728\u7761\u68A6\u4E2D\u8EAB\u5B50\u98A4\u6296\u4E86\u7247\u523B\uFF0C\u7EC8\u4E45\u8FD8\u662F\u6CA1\u6709\u9192\u6765\u3002</p><p>\u5F85\u6D17\u51C0\u4E0B\u4F53\uFF0C\u7EA2\u5AE9\u7684\u83CA\u809B\u88AB\u51B0\u6C34\u6FC0\u5F97\u7D27\u7D27\u5730\u7F29\u6210\u4E00\u70B9\uFF0C\u72B9\u5982\u96EA\u56E2\u4E2D\u4E00\u6735\u7F9E\u6DA9\u7684\u82B1\u82DE\u3002\u4F55\u6C42\u56FD\u7231\u4E0D\u91CA\u624B\u5730\u629A\u5F04\u7740\u5973\u6355\u5FEB\u7EC6\u6ED1\u7684\u81C0\u6C9F\uFF0C\u6307\u5C16\u5728\u6536\u7D27\u7684\u809B\u857E\u4E0A\u6345\u4E86\u6345\uFF0C\u773C\u73E0\u4E00\u8F6C\uFF0C\u4ECE\u6876\u91CC\u635E\u4E86\u7C92\u788E\u51B0\uFF0C\u585E\u5230\u767D\u96EA\u83B2\u809B\u4E2D\u3002</p><p>\u67D4\u7EA2\u7684\u5AE9\u809B\u4E0D\u60C5\u613F\u5730\u6491\u5F00\u5C11\u8BB8\uFF0C\u63A5\u7740\u5FAE\u5FAE\u4E00\u7D27\uFF0C\u5DF2\u5C06\u51B0\u7C92\u541E\u5165\u4F53\u5185\u3002\u7247\u523B\u4E4B\u540E\uFF0C\u51B0\u7C92\u88AB\u80A0\u58C1\u878D\u5316\uFF0C\u4E00\u80A1\u6E05\u4EAE\u7684\u6DB2\u4F53\u4ECE\u83CA\u5FC3\u7F13\u7F13\u6ED1\u51FA\u3002</p><p>\u4F55\u6C42\u56FD\u8089\u68D2\u6DA8\u5F97\u51E0\u4E4E\u70B8\u5F00\uFF0C\u5F53\u5373\u638F\u51FA\u9633\u5177\uFF0C\u72E0\u72E0\u6345\u4E86\u8FDB\u53BB\u3002\u767D\u96EA\u83B2\u76F4\u80A0\u5185\u8FD8\u6B8B\u7559\u7740\u51B0\u7C92\u7684\u6E29\u5EA6\uFF0C\u7D27\u7D27\u7684\uFF0C\u53C8\u6ED1\u53C8\u51C9\u3002\u4F46\u8FD9\u5C42\u8584\u8584\u7684\u51C9\u610F\u4E4B\u540E\uFF0C\u5374\u662F\u5C11\u5973\u4F53\u5185\u7684\u6E29\u70ED\u3002\u4F55\u6C42\u56FD\u53EA\u89C9\u6574\u6839\u8089\u68D2\u88AB\u6ED1\u817B\u7684\u80A0\u58C1\u7D27\u7D27\u88F9\u4F4F\uFF0C\u5FAE\u4E00\u62BD\u52A8\uFF0C\u4E00\u80A1\u566C\u9B42\u7684\u5FEB\u611F\u4FBF\u76F4\u51B2\u8111\u9645\uFF0C\u5FCD\u4E0D\u4F4F\u602A\u53EB\u8D77\u6765\u3002</p><p>\u4F55\u6C42\u56FD\u7684\u8089\u68D2\u6BD4\u4ED6\u8138\u4E0A\u5149\u6ED1\u8BB8\u591A\uFF0C\u81F3\u5C11\u6CA1\u90A3\u4E48\u591A\u9EBB\u5B50\uFF0C\u4F46\u6C14\u52BF\u5374\u56A3\u5F20\u5F97\u7D27\u3002</p><p>\u8BBA\u7C97\u7EC6\u6BD4\u80E1\u4E25\u7C97\u4E86\u4E00\u500D\uFF0C\u62BD\u9001\u95F4\u72B9\u5982\u4E00\u6761\u4E4C\u9ED1\u7684\u602A\u87D2\uFF0C\u5728\u5C11\u5973\u767D\u51C0\u7684\u81C0\u7F1D\u7FFB\u6EDA\u6345\u5F04\uFF0C\u5C06\u90A3\u53EA\u5AE9\u809B\u6405\u5F04\u5F97\u6CA1\u6709\u7247\u523B\u5B89\u5B81\uFF0C\u809B\u857E\u62C9\u6210\u4E00\u6761\u7EC6\u7EC6\u7684\u7EA2\u7EBF\u5957\u4F4F\u8089\u68D2\uFF0C\u4F3C\u4E4E\u518D\u7C97\u4E0A\u4E00\u4E1D\u5C31\u4F1A\u7EFD\u88C2\u3002</p><p>\u767D\u96EA\u83B2\u8138\u4E0A\u9732\u51FA\u5403\u75DB\u7684\u8868\u60C5\uFF0C\u7709\u5CF0\u7D27\u8E59\uFF0C\u88AB\u8DB3\u68B0\u5361\u4F4F\u7684\u7389\u8DB3\u4E0D\u65F6\u7EF7\u7D27\u3002\u82E5\u975E\u8FD9\u51E0\u65E5\u88AB\u80E1\u4E25\u5C61\u6B21\u809B\u4EA4\uFF0C\u5979\u7684\u5C41\u773C\u513F\u6B64\u65F6\u65E9\u5DF2\u88C2\u5F00\uFF0C\u540E\u5EAD\u8840\u6D41\u5982\u6CE8\u4E86\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u960E\u7F57\u671B\u4ECE\u672A\u89C1\u8FC7\u8FD9\u6837\u5014\u5F3A\u7684\u4EBA\uFF0C\u72F1\u4E2D\u6240\u7528\u7684\u79CD\u79CD\u9177\u5211\u4ED6\u5FC3\u91CC\u6709\u6570\uFF0C\u83AB\u8BF4\u4E00\u4E2A\u8001\u673D\u4E66\u751F\uFF0C\u5C31\u662F\u6740\u4EBA\u5982\u9EBB\u7684\u6D77\u8D3C\uFF0C\u94A2\u6253\u94C1\u94F8\u7684\u6C49\u5B50\u4E5F\u65E9\u670D\u4E86\u8F6F\uFF0C\u53EF\u767D\u5B5D\u5112\u5C31\u80FD\u6491\u7740\u4E0D\u62DB\u3002</p><p>\u97AD\u5211\u3001\u810A\u6756\u90FD\u5DF2\u7528\u904D\uFF0C\u767D\u5B5D\u5112\u6D51\u8EAB\u4E0A\u4E0B\u5DF2\u7ECF\u6CA1\u4E00\u5757\u597D\u8089\u3002\u6BCF\u6B21\u7528\u5211\u66F4\u5C11\u4E0D\u4E86\u5E38\u5211\u4E2D\u6700\u91CD\u7684\u5939\u68CD\uFF0C\u534A\u4E2A\u6708\u4E0B\u6765\uFF0C\u767D\u5B5D\u5112\u4E24\u817F\u90FD\u88AB\u5939\u5F97\u788E\u4E86\uFF0C\u53EA\u80FD\u88AB\u4EBA\u62D6\u7740\u8D70\u3002</p><p>\u9489\u5728\u811A\u4E0A\u90A3\u53CC\u300C\u6728\u978B\u300D\u4ECD\u662F\u65B0\u7684\uFF0C\u5374\u5DF2\u88AB\u8840\u8FF9\u67D3\u5F97\u4E4C\u9ED1\u3002</p><p>\u5E38\u5211\u4E0D\u8D77\u4F5C\u7528\uFF0C\u4F17\u4EBA\u5C31\u4E0B\u4E86\u72E0\u624B\u3002\u72F1\u5352\u4EEC\u628A\u767D\u5B5D\u5112\u624B\u638C\u644A\u5F00\uFF0C\u4ECE\u5C0F\u6307\u5F00\u59CB\uFF0C\u7528\u6728\u9524\u4E00\u70B9\u4E00\u70B9\u628A\u6307\u9AA8\u7838\u9165\uFF0C\u518D\u7528\u94C1\u94B3\u5939\u4F4F\u7528\u529B\u62D4\u6389\u3002\u767D\u8272\u7684\u7B4B\u8171\u5411\u5916\u4E00\u5F39\uFF0C\u503E\u523B\u53D8\u5F97\u8840\u7EA2\uFF0C\u767D\u5B5D\u5112\u9F7F\u95F4\u6E17\u51FA\u4E86\u8840\u6765\uFF0C\u6307\u6839\u5904\u53EA\u5269\u4E0B\u4E00\u7F15\u788E\u8089\uFF0C\u96F6\u4E71\u6302\u5728\u624B\u638C\u8FB9\u7F18\uFF0C\u51C4\u60E8\u7684\u60C5\u5F62\u8FDE\u884C\u5211\u7684\u72F1\u5352\u90FD\u4E0D\u7531\u6697\u81EA\u5FC3\u60CA\u3002</p><p>\u960E\u7F57\u671B\u9053\uFF1A\u300C\u767D\u5B5D\u5112\uFF0C\u4F60\u8FD8\u662F\u4E0D\u62DB\u5417\uFF1F\u300D</p><p>\u767D\u5B5D\u5112\u7684\u51B7\u6C57\u987A\u7740\u767D\u53D1\u76F4\u6DCC\uFF0C\u8138\u4E0A\u6BEB\u65E0\u8840\u8272\u3002\u960E\u7F57\u671B\u5FC3\u91CC\u76F4\u72AF\u5600\u5495\uFF0C\u8FD9\u822C\u91CD\u5211\uFF0C\u5E73\u5E38\u4EBA\u65E9\u6B7B\u4E86\u4E24\u6B21\uFF0C\u8FD9\u624B\u65E0\u7F1A\u9E21\u4E4B\u529B\u7684\u72D7\u5C41\u7AE5\u751F\u8FD8\u5728\u786C\u6491\uFF0C\u771F\u4E0D\u77E5\u9053\u4ED6\u8FD9\u628A\u8001\u9AA8\u5934\u91CC\u6709\u591A\u5C11\u786C\u6C14\u3002</p><p>\u767D\u5B5D\u5112\u5634\u5507\u6296\u4E86\u534A\u664C\uFF0C\u4E5F\u672A\u8BF4\u51FA\u8BDD\u6765\u3002\u4F55\u6C42\u56FD\u62E7\u4F4F\u4ED6\u7684\u62C7\u6307\uFF0C\u76F4\u63A5\u7528\u94C1\u94B3\u5939\u4F4F\uFF0C\u4FBF\u6B32\u4F7F\u529B\u3002</p><p>\u300C\u6162\u7740\u3002\u300D\u960E\u7F57\u671B\u9053\uFF1A\u300C\u7ED9\u4ED6\u7559\u51E0\u6839\u624B\u6307\u753B\u62BC\u3002\u300D</p><p>\u4ED6\u6574\u4E86\u6574\u5B98\u888D\uFF0C\u8FC8\u7740\u65B9\u6B65\u8D70\u5230\u767D\u5B5D\u5112\u9762\u524D\uFF0C\u300C\u767D\u592B\u5B50\u3002\u300D</p><p>\u8FD9\u6B21\u4ED6\u6CA1\u6709\u6253\u5B98\u8154\uFF0C\u800C\u662F\u7528\u4E00\u79CD\u524D\u6240\u672A\u6709\u7684\u53E3\u6C14\u8BF4\u9053\uFF1A\u300C\u8BF4\u5B9E\u8BDD\uFF0C\u4F60\u62DB\u4E0E\u4E0D\u62DB\u2014\u2014\u90FD\u662F\u4E00\u4E2A\u6B7B\u3002\u4F60\u82E5\u62DB\u4E86\uFF0C\u5144\u5F1F\u4EEC\u5C31\u6B64\u7F62\u624B\uFF0C\u597D\u5403\u597D\u559D\u5F85\u4F60\uFF0C\u5F85\u79CB\u540E\u95EE\u65A9\uFF0C\u4E00\u4E86\u767E\u4E86\u3002\u82E5\u662F\u4E0D\u62DB\u2026\u2026\u300D</p><p>\u770B\u7740\u767D\u5B5D\u5112\u55B7\u706B\u7684\u773C\u775B\uFF0C\u960E\u7F57\u671B\u4F4E\u7B11\u4E86\u4E00\u4E0B\uFF0C\u300C\u4F60\u82E5\u4E0D\u62DB\uFF0C\u53EA\u6015\u8FDE\u79CB\u540E\u4E5F\u6371\u4E0D\u8FC7\u3002\u53CD\u6B63\u90FD\u662F\u4E00\u4E2A\u6B7B\uFF0C\u4F55\u4E0D\u62DB\u4E86\uFF1F\u8FD8\u80FD\u5F97\u56DB\u4E2A\u6708\u7684\u5FEB\u6D3B\u2026\u2026\u300D</p><p>\u767D\u5B5D\u5112\u6602\u8D77\u5934\uFF0C\u6C99\u54D1\u7740\u5589\u5499\u8BF4\uFF1A\u300C\u8349\u6C11\u65E0\u7F6A\uFF01\u300D</p><p>\u960E\u7F57\u671B\u6014\u4E86\u4E00\u4E0B\uFF0C\u534A\u664C\u624D\u9053\uFF1A\u300C\u597D\u6C49\u5B50\uFF01\u300D</p><p>\u300C\u771F\u662F\u597D\u6C49\u5B50\uFF01\u960E\u67D0\u4F69\u670D\u3002\u300D\u4ED6\u76EF\u7740\u767D\u5B5D\u5112\uFF0C\u4ECE\u7259\u7F1D\u91CC\u6324\u51FA\u4E86\u4E24\u4E2A\u5B57\uFF0C\u300C\u7528\u5211\uFF01\u300D</p><p>\u94C1\u94B3\u5939\u4F4F\u767D\u5B5D\u5112\u5DE6\u624B\u62C7\u6307\uFF0C\u4F55\u6C42\u56FD\u62B9\u4E86\u628A\u6C57\uFF0C\u4E24\u624B\u7F13\u7F13\u7528\u529B\u3002\u767D\u5B5D\u5112\u671D\u7740\u960E\u7F57\u671B\u6012\u76EE\u800C\u89C6\uFF0C\u988F\u4E0B\u7684\u80E1\u987B\u98A4\u6296\u8D77\u6765\uFF0C\u5FFD\u7136\u5636\u58F0\u53EB\u9053\uFF1A\u300C\u72D7\u5B98\uFF01\u6211\u5C31\u662F\u5316\u4F5C\u5389\u9B3C\u4E5F\u7EDD\u9976\u4E0D\u4E86\u4F60\uFF01\uFF01\u300D</p><p>\u683C\u7684\u4E00\u58F0\uFF0C\u94C1\u94B3\u5939\u788E\u4E86\u6307\u9AA8\uFF0C\u767D\u5B5D\u5112\u773C\u4E2D\u7684\u6012\u706B\u731B\u7136\u4E00\u98A4\uFF0C\u4E00\u5934\u683D\u5012\u5728\u5730\u3002</p><p>\u5468\u56F4\u7684\u72F1\u5352\u90FD\u6CA1\u4F5C\u58F0\uFF0C\u51C4\u5389\u7684\u547C\u558A\u72B9\u81EA\u5728\u9ED1\u72F1\u5185\u56DE\u54CD\uFF0C\u4ED6\u4EEC\u88AB\u51B7\u6C57\u6D78\u6E7F\u7684\u540E\u80CC\u9635\u9635\u53D1\u51B7\u3002</p>',81);function t(i,_){return a}var o=e(r,[["render",t]]);export{o as default};
