import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_12-\u5E08\u53D4" tabindex="-1"><a class="header-anchor" href="#_12-\u5E08\u53D4" aria-hidden="true">#</a> 12 \u5E08\u53D4</h1><p>\u5434\u5927\u5F6A\u524D\u811A\u521A\u8D70\uFF0C\u960E\u7F57\u671B\u8FD9\u8FB9\u5C31\u9A82\u4E86\u8D77\u6765\u3002\u4ED6\u5728\u72F1\u4E2D\u79F0\u738B\u79F0\u9738\uFF0C\u518D\u6CA1\u6709\u5927\u8FC7\u4ED6\u7684\uFF0C\u5434\u5927\u5F6A\u5BF9\u4ED6\u8FD9\u4E00\u72F1\u4E4B\u957F\u5E26\u7406\u4E0D\u7406\uFF0C\u65E9\u8BA9\u4ED6\u7A9D\u4E86\u4E00\u809A\u5B50\u7684\u706B\u3002\u960E\u7F57\u671B\u9A82\u4E86\u4E00\u9635\uFF0C\u89C1\u5B59\u5929\u7FBD\u53EA\u7B11\u4E0D\u8BED\uFF0C\u95EE\u9053\uFF1A\u300C\u4F60\u770B\u8FD9\u4EBA\u600E\u6837\uFF1F\u300D</p><p>\u300C\u5434\u5927\u5F6A\u8C8C\u4F3C\u96C4\u58EE\uFF0C\u5185\u91CC\u751A\u662F\u504F\u72ED\u3002\u300D</p><p>\u960E\u7F57\u671B\u6C14\u54FC\u54FC\u9053\uFF1A\u300C\u6211\u770B\u4E5F\u662F\uFF01\u4F60\u600E\u4E48\u770B\u51FA\u6765\u7684\uFF1F\u300D</p><p>\u300C\u8FD9\u6848\u5B50\u672C\u8F6E\u4E0D\u5230\u5434\u603B\u6355\u5934\u6765\u7BA1\uFF0C\u4ED6\u5374\u62A2\u7740\u6765\u4E86\uFF0C\u5206\u660E\u662F\u6765\u62A2\u593A\u529F\u52B3\u3002\u4ED6\u660E\u77E5\u9053\u672C\u95E8\u5F1F\u5B50\u5728\u72F1\u4E2D\uFF0C\u5374\u4E0D\u8BF4\u7834\uFF0C\u663E\u89C1\u4ED6\u5904\u5904\u6709\u79C1\u5FC3\uFF0C\u8FD9\u7B49\u8981\u6848\u516C\u4E8B\u516C\u529E\u624D\u662F\u6B63\u7406\uFF0C\u4ED6\u773C\u4E0B\u8FD8\u6000\u6709\u79C1\u610F\uFF0C\u53EA\u80FD\u662F\u81EA\u79C1\u6210\u6027\u3002\u300D</p><p>\u960E\u7F57\u671B\u6C89\u541F\u4E0D\u8BED\uFF0C\u5B98\u573A\u4E2D\u4EBA\u90FD\u662F\u5982\u6B64\uFF0C\u4E0D\u7136\u5434\u5927\u5F6A\u4E5F\u6DF7\u4E0D\u5230\u603B\u6355\u5934\u7684\u4F4D\u7F6E\u3002</p><p>\u4ED6\u82E5\u4E3A\u5E08\u95E8\u5F87\u79C1\uFF0C\u600E\u751F\u5E94\u4ED8\uFF1F</p><p>\u5B59\u5929\u7FBD\u7B11\u9053\uFF1A\u300C\u5176\u4E8C\uFF0C\u8FD9\u4F4D\u5434\u603B\u6355\u5934\uFF0C\u662F\u4E2A\u80C6\u5C0F\u9B3C\u3002\u300D</p><p>\u300C\u5514\uFF1F\u300D\u960E\u7F57\u671B\u4E00\u6014\uFF0C\u600E\u4E48\u4E5F\u4E0D\u4F1A\u60F3\u5230\u5B59\u5929\u7FBD\u7ADF\u8BF4\u5434\u5927\u5F6A\u80C6\u5C0F\uFF0C\u4EBA\u5BB6\u5802\u5802\u4E00\u7701\u603B\u6355\u5934\uFF0C\u96BE\u9053\u662F\u6D6A\u5F97\u865A\u540D\uFF1F</p><p>\u300C\u767D\u96EA\u83B2\u5C31\u5728\u6B64\u5730\uFF0C\u4ED6\u5374\u65C1\u6572\u4FA7\u51FB\uFF0C\u4E0D\u6562\u76F4\u627F\u4E0E\u767D\u96EA\u83B2\u7CFB\u51FA\u4E8E\u540C\u95E8\u3002\u754F\u9996\u754F\u5C3E\uFF0C\u5206\u660E\u662F\u88AB\u300C\u8C0B\u53CD\u300D\u8FD9\u4E24\u4E2A\u5B57\u5413\u4F4F\u4E86\u3002\u300D</p><p>\u960E\u7F57\u671B\u6478\u7740\u988C\u4E0B\u7684\u77ED\u9AED\uFF0C\u5FC3\u91CC\u534A\u4FE1\u534A\u7591\uFF0C\u300C\u7F57\u9704\u6D3E\u95E8\u4E0B\u5F1F\u5B50\u751A\u591A\uFF0C\u4E5F\u8BB8\u5434\u5927\u5F6A\u6839\u672C\u5C31\u4E0D\u77E5\u9053\u8FD9\u4E2A\u767D\u96EA\u83B2\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u7B11\u7740\u8BF4\u4E86\u5434\u5927\u5F6A\u5F53\u65F6\u7684\u53CD\u5E94\uFF0C\u53C8\u9053\uFF1A\u300C\u4ED6\u6765\u5F97\u5982\u6B64\u4ED3\u4FC3\uFF0C\u6211\u539F\u4EE5\u4E3A\u4ED6\u662F\u4E3A\u7740\u767D\u96EA\u83B2\uFF0C\u5148\u6765\u63A2\u63A2\u53E3\u98CE\uFF0C\u53EF\u4ED6\u8FDE\u591C\u5F00\u5BA1\uFF0C\u89C1\u4E86\u767D\u96EA\u83B2\u7684\u540D\u5B57\u5374\u8349\u8349\u6536\u573A\uFF0C\u663E\u7136\u662F\u4E71\u4E86\u9635\u811A\u3002\u300D</p><p>\u960E\u7F57\u671B\u7ED5\u5BA4\u8E31\u7740\u6B65\u5B50\uFF0C\u300C\u82E5\u4ED6\u660E\u65E5\u8981\u63D0\u5BA1\u767D\u96EA\u83B2\uFF0C\u53C8\u8BE5\u5982\u4F55\u662F\u597D\uFF1F\u300D</p><p>\u5377\u5B97\u4E0A\u672C\u6765\u90FD\u6CA1\u6709\u767D\u96EA\u83B2\u7684\u540D\u5B57\uFF0C\u662F\u5B59\u5929\u7FBD\u529B\u4E3B\u628A\u90A3\u9875\u7EB8\u9644\u5728\u5377\u540E\uFF0C\u8BD5\u63A2\u5434\u5927\u5F6A\u7684\u53CD\u5E94\uFF0C\u5982\u6B64\u4E00\u6765\uFF0C\u72F1\u4E2D\u62FF\u4E86\u4E2A\u5211\u90E8\u6355\u5FEB\u7684\u4E8B\u518D\u65E0\u6CD5\u9690\u7792\u3002</p><p>\u5B59\u5929\u7FBD\u9053\uFF1A\u300C\u5927\u4EBA\u624B\u4E2D\u8BC1\u636E\u5145\u8DB3\uFF0C\u53EA\u8981\u767D\u5B5D\u5112\u4E00\u6848\u80FD\u5B9A\u4E0B\u8C0B\u53CD\uFF0C\u767D\u96EA\u83B2\u5C31\u662F\u53CD\u8D3C\u5BB6\u5C5E\uFF0C\u5230\u65F6\u65E0\u8BBA\u5211\u90E8\u8FD8\u662F\u5434\u603B\u6355\u5934\u90FD\u4E0D\u4F1A\u63FD\u706B\u4E0A\u8EAB\u3002\u7EB5\u7136\u63D0\u5BA1\u767D\u96EA\u83B2\uFF0C\u5434\u603B\u6355\u5934\u591A\u534A\u4E5F\u662F\u660E\u54F2\u4FDD\u8EAB\uFF0C\u6025\u4E8E\u6487\u6E05\u5173\u7CFB\u3002\u300D</p><p>\u960E\u7F57\u671B\u9762\u8272\u9634\u6C89\uFF0C\u6B64\u4E2D\u9053\u7406\u4ED6\u4E5F\u77E5\u9053\uFF0C\u4F46\u767D\u96EA\u83B2\u4F1A\u5982\u4F55\u5E94\u5BF9\uFF0C\u4ED6\u6B8A\u65E0\u628A\u63E1\u3002</p><p>\u5434\u5927\u5F6A\u8FD9\u4E00\u8D9F\u6765\u5F97\u4E5F\u597D\uFF0C\u6B63\u4F7F\u4E0A\u6295\u77F3\u95EE\u8DEF\uFF0C\u770B\u7F57\u9704\u6D3E\u5728\u5B98\u5E9C\u4E2D\u7684\u52BF\u529B\u662F\u5426\u6562\u56DE\u62A4\u300C\u8C0B\u53CD\u300D\u7684\u5F1F\u5B50\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u8D70\u8FD1\u5730\u7262\uFF0C\u5C31\u542C\u5230\u4E00\u7247\u55A7\u95F9\u58F0\uFF0C\u81F3\u5C11\u6709\u5341\u540D\u72F1\u5352\u805A\u5728\u72ED\u5C0F\u7684\u7A7A\u95F4\u91CC\uFF0C\u56F4\u7740\u5931\u53BB\u77E5\u89C9\u7684\u5973\u6355\u5FEB\uFF0C\u6392\u961F\u5978\u6DEB\u5979\u7684\u540E\u5EAD\u3002</p><p>\u767D\u96EA\u83B2\u540E\u5EAD\u82B1\u5F00\uFF0C\u672C\u6765\u53EA\u6709\u5B59\u5929\u7FBD\u3001\u80E1\u4E25\u4E24\u4EBA\u77E5\u9053\uFF0C\u80E1\u4E25\u4E0E\u4F55\u6C42\u56FD\u4EA4\u597D\uFF0C\u6697\u5730\u91CC\u8BF4\u4E86\u51FA\u53BB\uFF0C\u7ED3\u679C\u4E00\u4F20\u5341\u5341\u4F20\u767E\uFF0C\u72F1\u4E2D\u6CF0\u534A\u90FD\u77E5\u9053\u4E86\u8FD9\u6863\u5B50\u4E8B\u3002\u8FD9\u5730\u7262\u4E00\u5230\u591C\u95F4\uFF0C\u5C31\u95E8\u5EAD\u82E5\u5E02\uFF0C\u62A2\u7740\u53BB\u8FDB\u767D\u96EA\u83B2\u7684\u540E\u5EAD\u3002</p><p>\u960E\u7F57\u671B\u5343\u53EE\u549B\u4E07\u5631\u5490\uFF0C\u8BA9\u624B\u4E0B\u6536\u655B\u4E00\u4E9B\uFF0C\u4F46\u8272\u5B57\u5F53\u5934\uFF0C\u8FD9\u4E9B\u72F1\u5352\u54EA\u513F\u542C\u5F97\u8FDB\u53BB\uFF0C\u53CD\u628A\u4ED6\u7792\u5F97\u6B7B\u6B7B\u7684\u3002\u8FD8\u6709\u4E00\u4E0D\u77E5\u60C5\u7684\uFF0C\u5C31\u662F\u8D75\u9738\u3002\u859B\u971C\u7075\u90A3\u5929\u88AB\u4ED6\u5F3A\u884C\u7ED9\u7834\u809B\uFF0C\u81F3\u4ECA\u8FD8\u672A\u75CA\u6108\u3002\u4ED6\u4E00\u6B21\u5FEB\u6D3B\uFF0C\u8BA9\u4F17\u72F1\u5352\u5C11\u5E72\u4E86\u591A\u5C11\u56DE\u859B\u9006\u7684\u540E\u5EAD\uFF0C\u4F17\u4EBA\u4E0D\u7EA6\u800C\u540C\u90FD\u5BF9\u8D75\u9738\u7792\u4E86\u6D88\u606F\u3002</p><p>\u9976\u662F\u5982\u6B64\uFF0C\u8FD9\u4E00\u591C\u4E5F\u6709\u5341\u51E0\u4E2A\u4EBA\u5149\u987E\u767D\u96EA\u83B2\u7684\u540E\u5EAD\u3002\u90A3\u9762\u94C1\u67B7\u6210\u4E86\u6700\u597D\u7684\u9053\u5177\uFF0C\u53EA\u8981\u5361\u8FDB\u5730\u4E0A\u7684\u77F3\u7F1D\uFF0C\u767D\u96EA\u83B2\u5C31\u88AB\u7262\u7262\u56FA\u5B9A\u6210\u8DB4\u8DEA\u7684\u59FF\u52BF\uFF0C\u600E\u4E48\u5E72\u4E5F\u4E0D\u6015\u5979\u65E0\u77E5\u65E0\u89C9\u7684\u8EAB\u5B50\u6ED1\u52A8\u5206\u6BEB\u3002</p><p>\u859B\u971C\u7075\u8FDB\u6765\u65F6\uFF0C\u770B\u5230\u7684\u5C31\u662F\u8FD9\u6837\u4E00\u5E55\uFF1A\u9ED1\u9EDD\u9EDD\u7684\u94C1\u67B7\u7AD6\u5728\u5730\u4E0A\uFF0C\u9732\u51FA\u5973\u6355\u5FEB\u5982\u82B1\u4F3C\u7389\u7684\u4FCF\u8138\u548C\u53CC\u624B\u3002\u94C1\u67B7\u540E\u9762\uFF0C\u767D\u96EA\u83B2\u6485\u8D77\u81C0\uFF0C\u655E\u5F00\u817F\uFF0C\u5728\u7761\u68A6\u4E2D\u88AB\u4E00\u7FA4\u72F1\u5352\u62B1\u7740\u5149\u6E9C\u6E9C\u7684\u5C41\u80A1\u731B\u5E72\u5C41\u773C\u513F\u3002</p><p>\u300C\u859B\u5A4A\u5B50\uFF01\u5FEB\u8DB4\u8FC7\u6765\uFF01\u300D\u4E00\u7FA4\u72F1\u5352\u56B7\u9053\uFF0C\u4ED6\u4EEC\u7B49\u4E86\u534A\u664C\u8FD8\u6CA1\u8F6E\u5230\uFF0C\u5148\u62FF\u859B\u971C\u7075\u715E\u715E\u706B\u4E5F\u597D\u3002</p><p>\u859B\u971C\u7075\u65E0\u8A00\u8131\u53BB\u8863\u886B\uFF0C\u4E0E\u767D\u96EA\u83B2\u5E76\u80A9\u8DB4\u5728\u4E00\u8D77\u3002\u5979\u808B\u4E0B\u7684\u7EF7\u5E26\u4ECE\u672A\u6362\u8FC7\uFF0C\u5F53\u72F1\u5352\u6478\u4E0A\u65F6\uFF0C\u4E0D\u7531\u75DB\u53EB\u4E00\u58F0\u3002</p><p>\u300C\u9B3C\u53EB\u4E2A\u5C41\u554A\uFF01\u5C41\u80A1\u518D\u62AC\u9AD8\u4E9B\uFF01\u300D</p><p>\u859B\u971C\u7075\u540E\u5EAD\u4F24\u52BF\u672A\u6108\uFF0C\u9633\u5177\u8FDB\u5165\u65F6\u89E6\u5230\u4F24\u53E3\uFF0C\u4E00\u9635\u75DB\u695A\uFF0C\u5979\u5374\u54AC\u7740\u7259\u4E0D\u80AF\u4F5C\u58F0\u3002</p><p>\u6B64\u523B\u4E24\u4EBA\u8DB4\u5728\u4E00\u8D77\uFF0C\u767D\u96EA\u83B2\u4E0E\u5979\u8FD1\u5728\u54AB\u5C3A\uFF0C\u836F\u529B\u4F5C\u7528\u4E0B\uFF0C\u767D\u96EA\u83B2\u6BEB\u65E0\u77E5\u89C9\uFF0C\u4F46\u5F2F\u7EC6\u7684\u7709\u5CF0\u4E0D\u65F6\u62E7\u7D27\uFF0C\u53E3\u9F3B\u95F4\u53D1\u51FA\u7EC6\u7EC6\u7684\u547B\u541F\uFF0C\u4E0B\u610F\u8BC6\u4E2D\u9732\u51FA\u8089\u4F53\u88AB\u4FB5\u72AF\u7684\u53CD\u5E94\u3002</p><p>\u5979\u8FD8\u4E0D\u77E5\u9053\uFF0C\u5979\u7239\u7239\u5DF2\u7ECF\u8FC7\u4E16\u4E86\u3002\u859B\u971C\u7075\u4E0D\u7531\u60F3\u8D77\u81EA\u5DF1\u7684\u7239\u7239\uFF0C\u4ED6\u8001\u4EBA\u5BB6\u8FD8\u4E0D\u77E5\u9053\u81EA\u5DF1\u7684\u5973\u513F\u6B64\u65F6\u9677\u8EAB\u76D1\u7262\uFF0C\u6B63\u88AB\u72F1\u5352\u8086\u610F\u5978\u6DEB\u3002\u5F85\u81EA\u5DF1\u8131\u8EAB\uFF0C\u5FC5\u8BF7\u51FA\u6559\u4E2D\u957F\u8005\uFF0C\u527F\u706D\u8FD9\u6240\u80AE\u810F\u65E0\u803B\u7684\u9ED1\u72F1\uFF01</p><p>\u859B\u971C\u7075\u95ED\u4E0A\u773C\uFF0C\u5C06\u6068\u610F\u57CB\u5728\u5FC3\u5E95\u3002\u65C1\u8FB9\u7684\u767D\u96EA\u83B2\u4E5F\u95ED\u7740\u773C\uFF0C\u4E0D\u77E5\u9053\u5979\u5728\u7761\u68A6\u4E2D\uFF0C\u662F\u5426\u6709\u540C\u6837\u7684\u6068\u610F\u3002</p><p>\u5978\u6DEB\u6301\u7EED\u5230\u9ECE\u660E\u624D\u7ED3\u675F\uFF0C\u5F53\u6700\u540E\u4E00\u540D\u72F1\u5352\u6253\u7740\u5475\u6B20\u79BB\u5F00\uFF0C\u80E1\u4E25\u628A\u4E24\u5973\u9501\u8FDB\u7B3C\u4E2D\uFF0C\u4E0D\u591A\u65F6\u5C31\u9F3E\u58F0\u5927\u4F5C\u3002</p><p>\u859B\u971C\u7075\u52C9\u5F3A\u6491\u8D77\u8EAB\u4F53\uFF0C\u62FF\u8D77\u4E00\u6761\u7834\u5E03\uFF0C\u62B9\u53BB\u4E0B\u4F53\u7684\u6C61\u8FF9\u3002\u4E3A\u9632\u767D\u96EA\u83B2\u5BDF\u89C9\u5F02\u5E38\uFF0C\u6BCF\u665A\u809B\u5978\u4E4B\u540E\uFF0C\u90FD\u662F\u859B\u971C\u7075\u7ED9\u5979\u6574\u7406\u8863\u7269\uFF0C\u9664\u53BB\u6EE5\u4EA4\u7684\u75D5\u8FF9\u3002\u767D\u96EA\u83B2\u91CD\u67B7\u5728\u8EAB\uFF0C\u624B\u811A\u4E0D\u5F97\u81EA\u7531\uFF0C\u5C31\u662F\u6709\u6240\u611F\u89C9\u4E5F\u65E0\u6CD5\u6C42\u8BC1\u3002</p><p>\u8FD9\u4E00\u665A\u5341\u51E0\u540D\u72F1\u5352\u5148\u540E\u7528\u8FC7\u767D\u96EA\u83B2\u7684\u540E\u5EAD\uFF0C\u539F\u6765\u96EA\u767D\u7C89\u5AE9\u7684\u7389\u81C0\uFF0C\u6B64\u65F6\u4EE5\u83CA\u809B\u4E3A\u5706\u5FC3\uFF0C\u81C0\u6C9F\u88AB\u649E\u51FA\u5706\u5706\u4E00\u7247\u7EA2\u75D5\u3002\u5979\u7684\u5C41\u773C\u513F\u6B64\u65F6\u5DF2\u6781\u4E3A\u67D4\u8F6F\uFF0C\u6D51\u4E0D\u4F3C\u5F53\u521D\u7684\u9752\u6DA9\uFF0C\u624B\u6307\u8F7B\u8F7B\u4E00\u6309\uFF0C\u83CA\u7EB9\u5C31\u5411\u5916\u6563\u5F00\uFF0C\u5C06\u6307\u5C16\u541E\u5165\u809B\u4E2D\uFF0C\u67D4\u6ED1\u4E4B\u6781\u3002</p><p>\u90A3\u4E9B\u72F1\u5352\u90FD\u8086\u65E0\u5FCC\u60EE\u5730\u628A\u7CBE\u6DB2\u5C04\u8FDB\u4E86\u767D\u96EA\u83B2\u809B\u5185\uFF0C\u5979\u4E00\u76F4\u4F0F\u7740\u8EAB\u5B50\uFF0C\u6485\u8D77\u5C41\u80A1\uFF0C\u7CBE\u6DB2\u90FD\u6D41\u5165\u80A0\u9053\u6DF1\u5904\u3002\u859B\u971C\u7075\u53EA\u80FD\u62B9\u53BB\u81C0\u6C9F\u548C\u83CA\u857E\u4E0A\u7684\u6C61\u6E0D\uFF0C\u91CC\u9762\u7684\u7CBE\u6DB2\u8981\u7B49\u767D\u96EA\u83B2\u9192\u6765\u540E\uFF0C\u518D\u50CF\u6392\u4FBF\u4E00\u6837\u6392\u51FA\u4E86\u3002</p><p>\u8FB0\u65F6\u4E00\u523B\uFF0C\u767D\u96EA\u83B2\u51C6\u65F6\u9192\u6765\uFF0C\u867D\u7136\u7761\u4E86\u4E00\u591C\uFF0C\u5979\u5374\u89C9\u5F97\u8170\u9178\u80CC\u75BC\uFF0C\u53CC\u819D\u50CF\u88AB\u786C\u7269\u538B\u4F4F\u50F5\u75DB\uFF0C\u5C24\u5176\u662F\u8FD9\u6BB5\u65E5\u5B50\u6BCF\u65E9\u90FD\u6709\u7684\u4FBF\u610F\uFF0C\u6BD4\u4EE5\u5F80\u66F4\u52A0\u5F3A\u70C8\u3002</p><p>\u5979\u89C1\u859B\u971C\u7075\u548C\u80E1\u4E25\u90FD\u5728\u719F\u7761\uFF0C\u4E8E\u662F\u5403\u529B\u5730\u6367\u7740\u94C1\u67B7\uFF0C\u671D\u4FBF\u6876\u632A\u53BB\uFF0C\u5FC3\u91CC\u6697\u81EA\u5947\u602A\uFF0C\u6628\u5929\u5979\u4EC0\u4E48\u90FD\u6CA1\u5403\uFF0C\u600E\u4E48\u8FD8\u6709\u4FBF\u610F\uFF1F\u96BE\u9053\u662F\u72F1\u4E2D\u6F6E\u6E7F\uFF0C\u8179\u90E8\u7740\u4E86\u51C9\uFF1F\u60F3\u8D77\u4EE5\u524D\u6392\u51FA\u7684\u90A3\u4E9B\u6E7F\u6ED1\u7684\u4E1C\u897F\uFF0C\u4F3C\u4E4E\u771F\u662F\u62C9\u809A\u5B50\u4E86\u3002</p><p>\u521A\u632A\u5230\u4E86\u4FBF\u6876\u65C1\uFF0C\u5730\u7262\u7684\u94C1\u7F69\u4F20\u6765\u4E00\u9635\u6572\u51FB\u58F0\u3002\u7B49\u80E1\u4E25\u5F00\u95E8\uFF0C\u5B59\u5929\u7FBD\u5306\u5306\u4E0B\u6765\uFF0C\u6253\u5F00\u56DA\u7B3C\uFF0C\u9664\u53BB\u767D\u96EA\u83B2\u7684\u8DB3\u68B0\u3002</p><p>\u80E1\u4E25\u63C9\u7740\u773C\u9053\uFF1A\u300C\u600E\u4E48\u4E86\uFF1F\u600E\u4E48\u4E86\uFF1F\u300D</p><p>\u300C\u6628\u5929\u5230\u7684\u5927\u4EBA\u8981\u63D0\u5BA1\u5979\uFF0C\u5FEB\u7740\u4E9B\u3002\u300D</p><p>\u80E1\u4E25\u4E00\u60CA\uFF0C\u8FDE\u5FD9\u8FC7\u6765\u5E2E\u5FD9\uFF0C\u4E24\u817F\u7981\u4E0D\u4F4F\u6709\u4E9B\u6253\u98A4\u3002</p><p>\u5B59\u5929\u7FBD\u5FC3\u4E0B\u8D77\u7591\uFF0C\u6697\u4E2D\u62C9\u4E86\u80E1\u4E25\u4E00\u628A\uFF0C\u300C\u600E\u4E48\u4E86\uFF1F\u300D\u83AB\u975E\u8FD9\u5BB6\u4F19\u6628\u665A\u53C8\u5FCD\u4E0D\u4F4F\u5E72\u4E86\u767D\u96EA\u83B2\u7684\u5C41\u773C\u513F\uFF1F</p><p>\u80E1\u4E25\u4E0D\u6562\u8BF4\u51FA\u771F\u76F8\uFF0C\u53EA\u9053\uFF1A\u300C\u6CA1\u4E8B\u513F\u6CA1\u4E8B\u513F\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u6253\u91CF\u4E86\u767D\u96EA\u83B2\u51E0\u773C\uFF0C\u4F3C\u4E4E\u6CA1\u4EC0\u4E48\u5F02\u6837\uFF0C\u5374\u4E0D\u77E5\u4E0D\u4F46\u80E1\u4E25\u5E72\u4E86\uFF0C\u800C\u4E14\u662F\u5341\u51E0\u4E2A\u72F1\u5352\u5E72\u4E86\u5979\u4E00\u591C\u5C41\u773C\u513F\uFF0C\u767D\u96EA\u83B2\u53C8\u60CA\u53C8\u559C\uFF0C\u7701\u91CC\u6765\u4EBA\uFF0C\u603B\u4E0D\u4F1A\u50CF\u4ED6\u4EEC\u8FD9\u6837\u65E0\u6CD5\u65E0\u5929\uFF0C\u81EA\u5DF1\u548C\u7239\u7239\u7684\u51A4\u5C48\u603B\u7B97\u6709\u4E86\u6D17\u8131\u7684\u673A\u4F1A\u3002</p><p>\u867D\u662F\u767D\u663C\uFF0C\u516C\u5802\u4E0A\u4F9D\u7136\u4E00\u6D3E\u9634\u68EE\u3002\u8FD9\u6B21\u5750\u5728\u4E2D\u95F4\u5E76\u975E\u960E\u7F57\u671B\uFF0C\u800C\u662F\u4E00\u4E2A\u56DB\u65B9\u5927\u8138\uFF0C\u795E\u60C5\u521A\u6B63\u7684\u4E2D\u5E74\u4EBA\u3002</p><p>\u5434\u5927\u5F6A\u7AEF\u8BE6\u767D\u96EA\u83B2\u7247\u523B\uFF0C\u89C1\u5979\u8863\u886B\u5B8C\u6574\uFF0C\u8D70\u8DEF\u867D\u7136\u6709\u4E9B\u8FDF\u91CD\uFF0C\u4F46\u663E\u7136\u6CA1\u6709\u5403\u592A\u591A\u82E6\u5934\uFF0C\u53EA\u662F\u5979\u4E0A\u5802\u6765\u975E\u4F46\u4E0D\u8DEA\uFF0C\u8FD8\u6602\u8D77\u5934\uFF0C\u5434\u5927\u5F6A\u4E0D\u7531\u5FC3\u4E2D\u6709\u6C14\uFF0C\u559D\u9053\uFF1A\u300C\u8DEA\u4E0B\uFF01\u300D</p><p>\u767D\u96EA\u83B2\u6014\u4E86\u4E00\u4E0B\uFF0C\u5C48\u819D\u8DEA\u5728\u5802\u4E2D\u3002\u53BB\u5E74\u7F57\u9704\u6BD4\u5251\u65F6\uFF0C\u5434\u5927\u5F6A\u66FE\u56DE\u5E08\u95E8\u89C2\u793C\uFF0C\u4F46\u4ED6\u81EA\u91CD\u5B98\u8EAB\uFF0C\u6CA1\u600E\u4E48\u4E0E\u540E\u8F88\u5F1F\u5B50\u6765\u5F80\u3002\u767D\u96EA\u83B2\u76F8\u8C8C\u51FA\u4F17\uFF0C\u6BD4\u5251\u4E2D\u53C8\u63A5\u8FDE\u83B7\u80DC\uFF0C\u4ED6\u8FD8\u6709\u5370\u50CF\uFF0C\u767D\u96EA\u83B2\u53EA\u8FDC\u8FDC\u89C1\u8FC7\u4ED6\u4E00\u773C\uFF0C\u5374\u4E0D\u77E5\u5802\u4E0A\u5750\u7684\u5C31\u662F\u672C\u95E8\u5E08\u53D4\u3002</p><p>\u5434\u5927\u5F6A\u5BD2\u58F0\u9053\uFF1A\u300C\u4F60\u5C31\u662F\u767D\u96EA\u83B2\u4E48\uFF1F\u300D</p><p>\u767D\u96EA\u83B2\u8EAB\u4E0A\u975E\u5211\u7684\u91CD\u67B7\u8DB3\u68B0\u90FD\u5DF2\u9664\u53BB\uFF0C\u624B\u811A\u4E0A\u6362\u6210\u4E86\u94C1\u9563\uFF0C\u867D\u7136\u4E5F\u662F\u91CD\u5211\uFF0C\u4F46\u6BD4\u4E4B\u4EE5\u5F80\u8F7B\u4FBF\u4E86\u8BB8\u591A\u3002\u53EA\u662F\u8FD9\u4E00\u8DEF\u8D70\u6765\uFF0C\u5979\u7684\u4FBF\u610F\u5374\u8D8A\u6765\u8D8A\u6025\u3002\u5979\u6781\u529B\u6536\u7D27\u4E0B\u4F53\uFF0C\u53EA\u89C9\u81C0\u6C9F\u5185\u4E00\u7247\u7099\u70ED\uFF0C\u809B\u6D1E\u5904\u53C8\u6E7F\u53C8\u70ED\uFF0C\u4E0D\u65F6\u4F20\u6765\u9488\u624E\u822C\u7684\u523A\u75D2\uFF0C\u8BF4\u4E0D\u51FA\u7684\u96BE\u53D7\u3002\u5979\u4E0D\u77E5\u9053\u8FD9\u662F\u56E0\u4E3A\u6628\u665A\u809B\u4EA4\u8FC7\u4E45\uFF0C\u6709\u4E9B\u810F\u4E1C\u897F\u6CBE\u5728\u80A0\u9053\u7684\u9ECF\u819C\u4E0A\uFF0C\u5BFC\u81F4\u540E\u5EAD\u4E0D\u6D01\u5F15\u8D77\u8F7B\u5FAE\u7684\u708E\u75C7\u3002</p><p>\u767D\u96EA\u83B2\u5F3A\u5FCD\u4FBF\u610F\uFF0C\u7B54\u9053\uFF1A\u300C\u5F1F\u5B50\u767D\u96EA\u83B2\uFF0C\u89C1\u8FC7\u5927\u4EBA\u3002\u300D</p><p>\u89C1\u5979\u6CA1\u8BA4\u51FA\u81EA\u5DF1\uFF0C\u5434\u5927\u5F6A\u6697\u4E2D\u677E\u4E86\u53E3\u6C14\uFF0C\u4ED6\u51B7\u7B11\u4E00\u58F0\uFF0C\u300C\u4F60\u8EAB\u4E0A\u7684\u5211\u90E8\u8170\u724C\u662F\u4ECE\u4F55\u5904\u5F97\u6765\u7684\uFF1F\u300D</p><p>\u767D\u96EA\u83B2\u8DEA\u4E0B\u65F6\uFF0C\u8EAB\u5B50\u633A\u76F4\uFF0C\u80A0\u9053\u4E45\u84C4\u7684\u9ECF\u6DB2\u7F13\u7F13\u6ED1\u4E0B\uFF0C\u4ECE\u809B\u4E2D\u5FAE\u5FAE\u6E17\u51FA\uFF0C\u809B\u6D1E\u5904\u6108\u53D1\u70BD\u70ED\u6E7F\u7C98\u3002\u767D\u96EA\u83B2\u62DA\u547D\u63D0\u809B\uFF0C\u6536\u7F29\u809B\u6D1E\uFF0C\u4E00\u9762\u6717\u58F0\u7B54\u9053\uFF1A\u300C\u6211\u662F\u6355\u76D7\u53F8\u65B0\u664B\u6355\u5FEB\uFF0C\u8FD9\u8170\u724C\u662F\u5211\u90E8\u4EB2\u624B\u9881\u53D1\uFF0C\u5211\u90E8\u6587\u6863\u6709\u6848\u53EF\u67E5\uFF01\u300D</p><p>\u5434\u5927\u5F6A\u4E00\u62CD\u60CA\u5802\u6728\uFF0C\u559D\u9053\uFF1A\u300C\u4E00\u6D3E\u80E1\u8A00\uFF01\u5211\u90E8\u6355\u5FEB\u5C82\u662F\u90A3\u4E48\u5BB9\u6613\u5F53\u7684\uFF01\u300D</p><p>\u767D\u96EA\u83B2\u6297\u58F0\u9053\uFF1A\u300C\u6211\u662F\u7F57\u9704\u6D3E\u5F1F\u5B50\uFF01\u5E7F\u4E1C\u603B\u6355\u5434\u5927\u5F6A\u6B63\u662F\u5F1F\u5B50\u5E08\u53D4\uFF0C\u5F1F\u5B50\u80FD\u8FDB\u5165\u5211\u90E8\uFF0C\u5C31\u662F\u7531\u5434\u5E08\u53D4\u4EB2\u81EA\u63A8\u8350\uFF0C\u8BF7\u5927\u4EBA\u660E\u67E5\u3002\u300D</p><p>\u5434\u5927\u5F6A\u5FC3\u91CC\u4E00\u9707\uFF0C\u6697\u9053\u6765\u4E86\u6765\u4E86\u3002\u5F53\u521D\u6302\u540D\u63A8\u8350\u53EA\u662F\u8D70\u8D70\u8FC7\u573A\uFF0C\u73B0\u5728\u88AB\u5979\u516C\u7136\u8BF4\u51FA\uFF0C\u8FD9\u60C5\u4E3E\u5931\u5BDF\uFF0C\u63A8\u8350\u9006\u532A\u4E00\u6761\uFF0C\u5C31\u8DB3\u4EE5\u574F\u4E86\u4ED6\u7684\u524D\u7A0B\u3002</p><p>\u5434\u5927\u5F6A\u54C8\u54C8\u4E00\u7B11\uFF0C\u58F0\u9707\u5C4B\u5B87\uFF0C\u8EAB\u540E\u7684\u6CE5\u50CF\u6251\u6251\u64DE\u64DE\u843D\u4E0B\u4E86\u4E00\u7247\u7070\u571F\uFF0C\u300C\u767D\u96EA\u83B2\uFF01\u4F60\u53EF\u8BA4\u8BC6\u672C\u4EBA\uFF1F\u300D</p><p>\u767D\u96EA\u83B2\u8FDF\u7591\u5730\u6447\u4E86\u6447\u5934\uFF0C\u8FD9\u4EBA\u6B66\u529F\u4E0D\u51E1\uFF0C\u76F8\u8C8C\u4F3C\u4E4E\u5728\u54EA\u91CC\u89C1\u8FC7\u2026\u2026</p><p>\u5434\u5927\u5F6A\u4E00\u62CD\u516C\u6848\uFF0C\u5927\u559D\u9053\uFF1A\u300C\u672C\u4EBA\u6B63\u662F\u5E7F\u4E1C\u4E00\u7701\u603B\u6355\u5934\u5434\u5927\u5F6A\uFF01\u4F60\u8FDE\u6211\u90FD\u4E0D\u8BA4\u8BC6\uFF0C\u8FD8\u6562\u5984\u79F0\u7F57\u9704\u5F1F\u5B50\uFF0C\u5211\u90E8\u6355\u5FEB\uFF0C\u6765\u4EBA\u554A\uFF0C\u7ED9\u6211\u91CD\u8D23\u4E8C\u5341\u5927\u677F\uFF01\u300D</p><p>\u767D\u96EA\u83B2\u77A0\u76EE\u7ED3\u820C\uFF0C\u5434\u5927\u5F6A\u76F8\u8C8C\u5979\u867D\u7136\u8BB0\u4E0D\u6E05\u695A\uFF0C\u4F46\u662F\u5F53\u65E5\u8350\u8BED\u5979\u662F\u4EB2\u773C\u89C1\u8FC7\u7684\u3002\u5434\u5E08\u53D4\u5F53\u65F6\u4E3A\u672C\u95E8\u51FA\u4E86\u8FD9\u6837\u7684\u5F1F\u5B50\u6DF1\u4E3A\u5F97\u610F\uFF0C\u529B\u4E3B\u76F4\u63A5\u8FDB\u5165\u5211\u90E8\u6355\u76D7\u53F8\uFF0C\u4E3A\u671D\u5EF7\u6548\u529B\u3002</p><p>\u4E24\u540D\u72F1\u5352\u4E0A\u6765\u628A\u767D\u96EA\u83B2\u6309\u5728\u5730\u4E0A\uFF0C\u4E3E\u8D77\u6C34\u706B\u68CD\u4FBF\u671D\u5979\u81C0\u4E0A\u6253\u53BB\u3002\u4E8C\u5341\u5927\u677F\u5E76\u4E0D\u7B97\u91CD\uFF0C\u4E5F\u662F\u5434\u5927\u5F6A\u6697\u5730\u91CC\u51E0\u5206\u56DE\u62A4\u4E4B\u610F\u3002\u4F46\u662F\u767D\u96EA\u83B2\u80A0\u9053\u91CC\u704C\u6EE1\u5341\u51E0\u540D\u72F1\u5352\u7684\u7CBE\u6DB2\uFF0C\u4E00\u76F4\u6536\u7D27\u809B\u95E8\u3002\u53EA\u6253\u4E86\u4E24\u677F\uFF0C\u5979\u52C9\u5F3A\u63A9\u597D\u7684\u88D9\u88E4\u5C31\u88AB\u6253\u6563\uFF0C\u9732\u51FA\u96EA\u767D\u7684\u81C0\u8089\u3002</p><p>\u52A8\u5211\u7684\u4E24\u540D\u72F1\u5352\u6628\u665A\u90FD\u662F\u5978\u8FC7\u5979\u7684\uFF0C\u773C\u89C1\u5979\u81C0\u6C9F\u53D1\u7EA2\uFF0C\u90FD\u8D76\u5FD9\u671D\u90A3\u5904\u62DB\u547C\uFF0C\u8BD5\u56FE\u7528\u68CD\u4F24\u63A9\u4F4F\u809B\u5978\u7684\u75D5\u8FF9\u3002</p><p>\u6D82\u7740\u9ED1\u7EA2\u6CB9\u6F06\u7684\u6C34\u706B\u68CD\u6B64\u8D77\u5F7C\u843D\uFF0C\u4E0B\u5F97\u53C8\u5FEB\u53C8\u72E0\u53C8\u51C6\uFF0C\u53D1\u51FA\u8F9F\u8F9F\u556A\u556A\u6E05\u8106\u7684\u8089\u54CD\u3002\u4E0D\u591A\u65F6\uFF0C\u767D\u96EA\u83B2\u96EA\u767D\u7684\u5C41\u80A1\u5C31\u88AB\u6253\u5F97\u7FFB\u5F00\uFF0C\u81C0\u6C9F\u4E00\u7247\u7EA2\u80BF\u3002\u80FD\u6E05\u695A\u5730\u770B\u5230\u90A3\u53EA\u67D4\u5AE9\u6E7F\u817B\u7684\u5C41\u773C\u513F\u4E0D\u65F6\u9F13\u8D77\uFF0C\u53C8\u6781\u529B\u6536\u7F29\u3002</p><p>\u5434\u5927\u5F6A\u76EE\u5149\u4F55\u7B49\u7A0E\u5229\uFF0C\u4E00\u773C\u770B\u51FA\u767D\u96EA\u83B2\u88D9\u88E4\u672C\u662F\u6495\u5F00\u7684\uFF0C\u81C0\u95F4\u66F4\u6709\u53D7\u8FB1\u7684\u75D5\u8FF9\u3002\u4F46\u4ED6\u6628\u665A\u60F3\u4E86\u4E00\u591C\uFF0C\u6253\u5B9A\u4E86\u4E3B\u610F\u5148\u660E\u54F2\u4FDD\u8EAB\u3002\u4ED6\u770B\u4E86\u4F9B\u8BCD\uFF0C\u91CC\u9762\u867D\u6709\u4E9B\u5173\u8282\u8FD8\u5F85\u63A8\u6572\uFF0C\u4F46\u53EA\u8981\u859B\u971C\u7075\u4E00\u53E3\u54AC\u5B9A\uFF0C\u767D\u5B5D\u5112\u5C31\u662F\u6D3B\u7740\u4E5F\u5206\u8FA9\u4E0D\u5F97\u3002</p><p>\u767D\u5B5D\u5112\u65E2\u7136\u6709\u7F6A\uFF0C\u767D\u96EA\u83B2\u5373\u4F7F\u6CA1\u6709\u9644\u9006\u7684\u4E3E\u52A8\uFF0C\u4E5F\u662F\u9006\u532A\u5BB6\u5C5E\uFF0C\u660E\u5F8B\u4E00\u4EBA\u8C0B\u53CD\uFF0C\u5BB6\u5C5E\u95EE\u7F6A\uFF0C\u8FDE\u682A\u4E5D\u65CF\u5341\u65CF\u4E5F\u4E0D\u4E4F\u5176\u4F8B\u3002\u767D\u96EA\u83B2\u65E2\u7136\u8131\u4E0D\u4E86\u5E72\u7CFB\uFF0C\u5F53\u52A1\u4E4B\u6025\uFF0C\u5C31\u662F\u628A\u5979\u4E0E\u7F57\u9704\u6D3E\u6495\u63B3\u5F00\u6765\uFF0C\u514D\u5F97\u6B83\u53CA\u6C60\u9C7C\u3002</p><p>\u767D\u96EA\u83B2\u7F9E\u75DB\u4EA4\u52A0\uFF0C\u6B64\u65F6\u5802\u4E0A\u5750\u7684\u4E0D\u4EC5\u662F\u72F1\u4E2D\u8BF8\u4EBA\uFF0C\u8FD8\u6709\u540C\u95E8\u5E08\u53D4\uFF0C\u81EA\u5DF1\u5374\u5149\u7740\u5C41\u80A1\u88AB\u5927\u68CD\u62F7\u6253\u3002\u5979\u6765\u65F6\u6EE1\u5FC3\u5E0C\u5180\u60F3\u6D17\u8131\u51A4\u5C48\uFF0C\u8C01\u77E5\u5374\u53D7\u5230\u4E86\u66F4\u5927\u51CC\u8FB1\uFF0C\u5FC3\u4E2D\u4E00\u75BC\uFF0C\u7981\u4E0D\u4F4F\u73E0\u6CEA\u76C8\u7736\u3002</p><p>\u72F1\u5352\u4E00\u68CD\u6253\u4E0B\uFF0C\u68CD\u5C16\u6B63\u843D\u5728\u83CA\u809B\u4E0A\uFF0C\u767D\u96EA\u83B2\u540E\u5EAD\u88AB\u63D2\u4E86\u591A\u65E5\uFF0C\u672C\u4E0D\u53CA\u4EE5\u5F80\u7D27\u51D1\uFF0C\u8FD9\u4E00\u68CD\u6B63\u6253\u4E2D\u809B\u6D1E\uFF0C\u5979\u6B7B\u6B7B\u6536\u7D27\u7684\u5C41\u773C\u513F\u731B\u7136\u4E00\u677E\uFF0C\u4E00\u80A1\u9ECF\u7A20\u7684\u6DB2\u4F53\u76F4\u55B7\u51FA\u6765\uFF0C\u5728\u7A7A\u4E2D\u6E85\u51FA\u4E00\u6761\u5F27\u7EBF\uFF0C\u6DCB\u6DCB\u6F13\u6F13\u6D12\u5F97\u5979\u4E24\u817F\u90FD\u662F\u3002</p><p>\u5802\u4E0A\u4F17\u4EBA\u90FD\u662F\u4E00\u6123\uFF0C\u90A3\u4E9B\u7CBE\u6DB2\u5728\u80A0\u9053\u91CC\u79EF\u5F97\u4E45\u4E86\uFF0C\u4E0D\u4F46\u989C\u8272\u3001\u6D53\u5EA6\u6709\u5F02\uFF0C\u8FD8\u5939\u6742\u7740\u80A0\u9053\u4E2D\u7684\u6C61\u7269\uFF0C\u6839\u672C\u8FA8\u4E0D\u51FA\u6765\u662F\u522B\u4EBA\u5C04\u8FDB\u5979\u809A\u5B50\u91CC\u7684\u7CBE\u6DB2\u3002\u5F53\u4E0B\u5C31\u6709\u72F1\u5352\u4F4E\u58F0\u7B11\u9053\uFF1A\u300C\u8FD8\u5211\u90E8\u6355\u5FEB\u5462\uFF0C\u7ADF\u7136\u6253\u51FA\u5C4E\u4E86\u2026\u2026\u300D</p><p>\u767D\u96EA\u83B2\u57CB\u7740\u8138\uFF0C\u9999\u80A9\u6296\u52A8\u7247\u523B\uFF0C\u731B\u7136\u7EB5\u8D77\u8EAB\u6765\uFF0C\u4E00\u5934\u671D\u5802\u67F1\u4E0A\u649E\u53BB\u3002\u5B59\u5929\u7FBD\u773C\u75BE\u624B\u5FEB\uFF0C\u4E00\u628A\u626F\u4F4F\u94C1\u94FE\uFF0C\u559D\u9053\uFF1A\u300C\u767D\u96EA\u83B2\uFF01\u4F60\u6562\u754F\u7F6A\u81EA\u6740\u5417\uFF01\u300D</p><p>\u767D\u96EA\u83B2\u98A4\u58F0\u9053\uFF1A\u300C\u4F60\u4EEC\u8FD9\u822C\u8FB1\u6211\uFF0C\u6211\u8FD8\u6709\u4F55\u9762\u76EE\u6D3B\u5728\u4E16\u4E0A\uFF01\u5927\u4E0D\u4E86\u4E00\u6B7B\uFF0C\u6211\u5230\u9634\u95F4\u53BB\u8BA8\u4E2A\u516C\u6B63\uFF01\u300D</p><p>\u5434\u5927\u5F6A\u8138\u8272\u94C1\u9752\uFF0C\u559D\u9053\uFF1A\u300C\u672C\u6848\u8FD8\u672A\u67E5\u6E05\uFF0C\u6211\u7B49\u79C9\u516C\u6267\u6CD5\uFF0C\u65AD\u65AD\u4E0D\u4F1A\u51A4\u5C48\u65E0\u8F9C\uFF01\u4F60\u8BD5\u56FE\u81EA\u5C3D\uFF0C\u53CD\u5750\u5B9E\u4E86\u6709\u7F6A\uFF01\u300D</p><p>\u767D\u96EA\u83B2\u50F5\u7ACB\u5F53\u573A\uFF0C\u5434\u5927\u5F6A\u8BDD\u4E2D\u63D0\u70B9\u5979\u662F\u542C\u61C2\u4E86\uFF0C\u53EF\u5979\u8BE5\u600E\u4E48\u505A\uFF1F\u8FD8\u8981\u7EE7\u7EED\u53D7\u8FB1\u5417\uFF1F</p><p>\u5434\u5927\u5F6A\u6C89\u58F0\u9053\uFF1A\u300C\u4F60\u4E00\u6B7B\u4E86\u4E4B\uFF0C\u4F46\u5C11\u4E0D\u5F97\u8981\u8FDE\u7D2F\u4EB2\u670B\u597D\u53CB\u3002\u767D\u96EA\u83B2\uFF0C\u4F60\u53EF\u60F3\u6E05\u695A\u4E86\u3002\u300D</p><p>\u960E\u7F57\u671B\u534A\u5929\u6CA1\u6709\u4F5C\u58F0\uFF0C\u6B64\u65F6\u4E5F\u63A5\u53E3\u559D\u9053\uFF1A\u300C\u767D\u96EA\u83B2\uFF01\u8FD8\u4E0D\u5FEB\u5FEB\u4F0F\u8EAB\u53D7\u5211\uFF01\u300D</p><p>\u767D\u96EA\u83B2\u7F13\u7F13\u4F0F\u5728\u5730\u4E0A\uFF0C\u95ED\u4E0A\u773C\uFF0C\u63E1\u7D27\u53CC\u62F3\u3002</p><p>\u72F1\u5352\u64CD\u8D77\u6C34\u706B\u68CD\uFF0C\u7EE7\u7EED\u62F7\u6253\uFF0C\u8FD9\u6B21\u767D\u96EA\u83B2\u64A4\u53BB\u4E86\u62A4\u4F53\u771F\u6C14\uFF0C\u767D\u5AE9\u7684\u81C0\u90E8\u5728\u68CD\u4E0B\u5FFD\u5706\u5FFD\u6241\uFF0C\u4E0D\u4F4F\u8DF3\u52A8\uFF0C\u6CBE\u5728\u81C0\u8089\u7684\u6C61\u8FF9\u53D1\u51FA\u6E7F\u9ECF\u7684\u6C34\u58F0\u3002\u4E8C\u5341\u5927\u677F\u6253\u5B8C\uFF0C\u767D\u96EA\u83B2\u81C0\u95F4\u5DF2\u662F\u4F24\u75D5\u7D2F\u7D2F\uFF0C\u518D\u591A\u51E0\u68CD\uFF0C\u514D\u4E0D\u5F97\u8981\u76AE\u5F00\u8089\u7EFD\u3002</p><p>\u5434\u5927\u5F6A\u7F13\u7F13\u9053\uFF1A\u300C\u767D\u96EA\u83B2\uFF0C\u4F60\u662F\u5426\u7F57\u9704\u5F1F\u5B50\uFF0C\u6211\u81EA\u4F1A\u6D3E\u4EBA\u67E5\u5B9E\uFF0C\u4F60\u82E5\u679C\u771F\u4E0E\u767D\u83B2\u9006\u532A\u6709\u6D89\uFF0C\u5373\u4F7F\u771F\u662F\u7F57\u9704\u5F1F\u5B50\uFF0C\u95E8\u4E2D\u4E5F\u4F1A\u5C06\u4F60\u5F00\u9769\u51FA\u6D3E\u3002\u6211\u5434\u5927\u5F6A\u5FDD\u5C45\u7F57\u9704\u6D3E\u957F\u8F88\uFF0C\u5728\u6B64\u544A\u77E5\u5929\u4E0B\uFF0C\u672C\u6848\u672A\u67E5\u6E05\u4E4B\u524D\uFF0C\u4F60\u4E0D\u5F97\u5984\u79F0\u6211\u7F57\u9704\u6D3E\u5F1F\u5B50\u3002\u4F60\u53EF\u6E05\u695A\u5417\uFF1F\u300D</p><p>\u5434\u5927\u5F6A\u770B\u7740\u5979\u7684\u795E\u60C5\uFF0C\u53C8\u6162\u6162\u52A0\u4E86\u4E00\u53E5\uFF0C\u300C\u514D\u5F97\u4F60\u501A\u4ED7\u5E08\u95E8\uFF0C\u62D2\u4E0D\u8BA4\u7F6A\u3002\u300D</p><p>\u300C\u2026\u2026\u5F1F\u5B50\u77E5\u9053\u4E86\u3002\u300D</p><p>\u5434\u5927\u5F6A\u518D\u4E0D\u591A\u8A00\uFF0C\u8D77\u8EAB\u9053\uFF1A\u300C\u9000\u5802\uFF01\u300D</p><p>\u5802\u4E0A\u4F17\u4EBA\u9877\u523B\u6563\u53BB\uFF0C\u90FD\u8D76\u7740\u53BB\u5DF4\u7ED3\u5434\u603B\u6355\u5934\u3002\u5B59\u5929\u7FBD\u633D\u4F4F\u94C1\u94FE\uFF0C\u9053\uFF1A\u300C\u767D\u59D1\u5A18\uFF0C\u5148\u56DE\u53BB\u5427\u3002\u300D</p><p>\u767D\u96EA\u83B2\u6728\u7136\u8FC8\u7740\u6B65\u5B50\u3002\u8FDE\u8892\u9732\u7684\u81C0\u90E8\u4E5F\u672A\u66FE\u7406\u4F1A\u3002\u9664\u4E86\u6700\u521D\u7684\u51E0\u677F\uFF0C\u90A3\u4E8C\u5341\u5927\u677F\u90FD\u843D\u4E86\u5979\u81C0\u6C9F\u5185\uFF0C\u81C0\u8089\u80BF\u5F97\u65E0\u6CD5\u5408\u62E2\uFF0C\u8EAB\u5B50\u5F77\u4F5B\u5288\u5F00\u75BC\u75DB\u3002\u90A3\u53EA\u5C0F\u5DE7\u7684\u83CA\u809B\u4E5F\u91CD\u91CD\u6328\u4E86\u51E0\u4E0B\uFF0C\u88AB\u6253\u5F97\u5931\u53BB\u77E5\u89C9\uFF0C\u8D70\u52A8\u95F4\uFF0C\u80A0\u9053\u91CC\u6B8B\u7559\u7684\u6C61\u7269\u4E0D\u4F4F\u6D41\u51FA\uFF0C\u957F\u957F\u62D6\u5728\u81C0\u4E0B\u3002</p><p>\u5E08\u53D4\u6700\u540E\u7684\u90A3\u53E5\u8BDD\u5979\u542C\u61C2\u4E86\uFF0C\u662F\u8BA9\u5979\u300C\u62D2\u4E0D\u8BA4\u7F6A\u300D\uFF0C\u300C\u501A\u4ED7\u5E08\u95E8\u300D\u6765\u6551\u5979\u8131\u8EAB\u3002</p><p>\u4F46\u767D\u96EA\u83B2\u6B64\u523B\u5DF2\u7ECF\u4E0D\u76F8\u4FE1\u5434\u5E08\u53D4\u4F1A\u6551\u81EA\u5DF1\u51FA\u72F1\u3002\u5979\u5E76\u4E0D\u50BB\u3002\u5434\u5927\u5F6A\u8FDE\u6848\u60C5\u90FD\u4E0D\u591A\u95EE\uFF0C\u53EA\u53CD\u590D\u6487\u6E05\u5979\u4E0E\u7F57\u9704\u6D3E\u7684\u5173\u7CFB\uFF0C\u76F4\u5230\u628A\u5979\u5F00\u9769\u51FA\u95E8\uFF0C\u663E\u7136\u662F\u8BA4\u4E3A\u81EA\u5DF1\u786E\u7CFB\u9006\u532A\uFF0C\u62A2\u5148\u544A\u77E5\u5929\u4E0B\uFF0C\u5979\u767D\u96EA\u83B2\u5DF2\u7ECF\u4E0E\u7F57\u9704\u6D3E\u6CA1\u6709\u4E86\u4EFB\u4F55\u5173\u7CFB\u3002\u5E08\u95E8\u5C0A\u957F\u7ADF\u662F\u5982\u6B64\u65E0\u60C5\uFF0C\u8BA9\u767D\u96EA\u83B2\u5BD2\u900F\u4E86\u5FC3\u3002</p><p>\u56DE\u5230\u72F1\u4E2D\uFF0C\u5B59\u5929\u7FBD\u9664\u4E0B\u5979\u624B\u811A\u7684\u94C1\u9563\uFF0C\u767D\u96EA\u83B2\u5C06\u624B\u4E3E\u5230\u80F8\u53E3\uFF0C\u7B49\u7740\u4ED6\u7ED9\u81EA\u5DF1\u5E26\u4E0A\u94C1\u67B7\u3002</p><p>\u5B59\u5929\u7FBD\u5374\u6CA1\u6709\u52A8\u4F5C\uFF0C\u53CD\u800C\u9012\u6765\u6E05\u6C34\u6BDB\u5DFE\uFF0C\u300C\u767D\u59D1\u5A18\uFF0C\u4F60\u5148\u6D17\u6D17\u5427\u3002\u300D</p><p>\u767D\u96EA\u83B2\u867D\u7136\u840C\u751F\u6B7B\u610F\uFF0C\u4F46\u7EC8\u7A76\u662F\u5973\u513F\u5BB6\uFF0C\u5C31\u662F\u6B7B\u4E5F\u8981\u6B7B\u4E2A\u5E72\u51C0\u3002\u5979\u6CA1\u6709\u907F\u5F00\u5B59\u5929\u7FBD\uFF0C\u5C31\u5728\u4ED6\u9762\u524D\u8E72\u4E0B\u8EAB\u5B50\uFF0C\u6D17\u53BB\u4E0B\u4F53\u7684\u6C61\u8FF9\u3002</p><p>\u90A3\u4E00\u523B\u5979\u624D\u53D1\u73B0\uFF0C\u5E76\u4E0D\u662F\u81EA\u5DF1\u7684\u9519\u89C9\uFF0C\u5979\u7684\u83CA\u809B\u771F\u7684\u5F88\u677E\uFF0C\u5F88\u8F6F\u3002\u4E5F\u8BB8\u662F\u957F\u4E45\u7684\u8179\u6CFB\u2026\u2026\u4F46\u5979\u4E0D\u5FC5\u5728\u4E4E\u4E86\u3002</p><p>\u5B59\u5929\u7FBD\u4F3C\u4E4E\u8BF4\u4E86\u53E5\u4EC0\u4E48\u3002\u767D\u96EA\u83B2\u626C\u8D77\u8138\uFF0C\u300C\u4EC0\u4E48\uFF1F\u300D</p><p>\u300C\u767D\u8001\u5148\u751F\uFF0C\u5DF2\u7ECF\u8FC7\u4E16\u4E86\u3002\u300D</p>',88);function t(i,_){return a}var o=e(r,[["render",t]]);export{o as default};
