import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_23-\u5931\u8D1E" tabindex="-1"><a class="header-anchor" href="#_23-\u5931\u8D1E" aria-hidden="true">#</a> 23 \u5931\u8D1E</h1><p>\u4E39\u5A18\u5B5D\u671F\u8FD8\u5DEE\u4E86\u4E24\u65E5\uFF0C\u4ECD\u7A7F\u7740\u7D20\u670D\u3002\u867D\u7136\u8FED\u9022\u4E27\u4E71\uFF0C\u4F46\u5979\u65B0\u8FD1\u4E0E\u5B59\u5929\u7FBD\u60C5\u6D53\u5982\u9152\uFF0C\u975E\u4F46\u672A\u89C1\u6194\u635F\uFF0C\u53CD\u800C\u6108\u53D1\u719F\u8273\uFF0C\u767D\u5AE9\u7684\u808C\u80A4\u900F\u51FA\u6DE1\u6DE1\u80A4\u5149\uFF0C\u5728\u9634\u6697\u7684\u7262\u72F1\u4E2D\u9690\u9690\u751F\u8F89\u3002\u4E00\u6761\u767D\u7EEB\u6C57\u5DFE\u675F\u5728\u8170\u95F4\uFF0C\u66F4\u663E\u5F97\u8170\u5982\u7EA6\u7D20\uFF0C\u5A40\u5A1C\u591A\u59FF\u3002</p><p>\u5353\u5929\u96C4\u5FC3\u5934\u706B\u8D77\uFF0C\u6068\u4E0D\u5F97\u628A\u8FD9\u5987\u4EBA\u4E00\u53E3\u541E\u5230\u809A\u91CC\u3002\u4ED6\u4F38\u51FA\u624B\u6765\uFF0C\u4E39\u5A18\u4E00\u8EB2\uFF0C\u672A\u80FD\u8EB2\u5F00\uFF0C\u88AB\u4ED6\u634F\u4F4F\u4E0B\u5DF4\uFF0C\u300C\u597D\u4E2A\u6807\u81F4\u7684\u5987\u4EBA\uFF0C\u6211\u4E0D\u5728\u72F1\u91CC\uFF0C\u5012\u4FBF\u5B9C\u4E86\u5B59\u5929\u7FBD\u8FD9\u5C0F\u5B50\u3002\u300D</p><p>\u4E39\u5A18\u7EA2\u5507\u8840\u8272\u892A\u5C3D\uFF0C\u50F5\u4E86\u7247\u523B\u540E\uFF0C\u5979\u9192\u609F\u8FC7\u6765\uFF0C\u62DA\u547D\u6323\u5F00\u5353\u5929\u96C4\u7684\u624B\u638C\uFF0C\u671D\u7262\u95E8\u8DD1\u53BB\u3002\u521A\u8DD1\u4E86\u4E24\u6B65\uFF0C\u9888\u540E\u4E00\u7D27\uFF0C\u88AB\u4EBA\u626F\u4F4F\u8863\u895F\uFF0C\u63A5\u7740\u4E24\u819D\u9178\u8F6F\uFF0C\u4E0D\u7531\u81EA\u4E3B\u5730\u8DEA\u5728\u5730\u4E0A\u3002</p><p>\u4E39\u5A18\u63D0\u7684\u7BEE\u5B50\u8131\u624B\u6389\u843D\uFF0C\u70B9\u5FC3\u3001\u8863\u7269\u6389\u4E86\u4E00\u5730\u3002\u5353\u5929\u96C4\u8E72\u4E0B\u8EAB\u5B50\uFF0C\u5C06\u4E39\u5A18\u7684\u8793\u9996\u653E\u5728\u819D\u4E0A\uFF0C\u7B11\u9053\uFF1A\u300C\u5230\u4E86\u8FD9\u91CC\uFF0C\u4E0D\u966A\u6211\u8001\u5353\u4E50\u4E50\uFF0C\u5C31\u60F3\u8D70\uFF1F\u300D</p><p>\u4E39\u5A18\u98A4\u58F0\u9053\uFF1A\u300C\u5974\u5BB6\u662F\u6765\u63A2\u76D1\u7684\u2026\u2026\u672A\u66FE\u2026\u2026\u672A\u66FE\u2026\u2026\u300D</p><p>\u5353\u5929\u96C4\u563B\u7B11\u9053\uFF1A\u300C\u63A2\u76D1\u597D\u8BF4\uFF0C\u53EA\u8981\u8BA9\u6211\u8001\u5353\u5148\u63A2\u63A2\u4F60\u7684\u2026\u2026\u300D\u8BF4\u7740\u5728\u4E39\u5A18\u8179\u4E0B\u637B\u4E86\u4E00\u628A\u3002</p><p>\u4E39\u5A18\u60CA\u9A87\u5DF2\u6781\uFF0C\u8111\u4E2D\u4E00\u7247\u7A7A\u767D\uFF0C\u5979\u53CC\u819D\u8DEA\u5730\uFF0C\u4E0A\u8EAB\u540E\u4EF0\uFF0C\u67D4\u9888\u6795\u5728\u5353\u5929\u96C4\u819D\u4E0A\uFF0C\u4E00\u5BF9\u9999\u4E73\u9AD8\u9AD8\u9F13\u8D77\uFF0C\u51E0\u4E4E\u5C06\u8863\u886B\u6491\u88C2\u3002\u5353\u5929\u96C4\u529B\u6C14\u5927\u5F97\u60CA\u4EBA\uFF0C\u4E00\u53EA\u624B\u653E\u5728\u5589\u524D\uFF0C\u5C31\u628A\u4E39\u5A18\u7262\u7262\u6309\u4F4F\uFF0C\u52A8\u5F39\u4E0D\u5F97\u3002</p><p>\u90A3\u5F20\u5A07\u8273\u7684\u4FCF\u8138\u8840\u8272\u5168\u65E0\uFF0C\u72B9\u5982\u767D\u7389\u96D5\u6210\u822C\u7CBE\u81F4\u3002\u5353\u5929\u96C4\u6309\u637A\u4E0D\u4F4F\uFF0C\u4FEF\u8EAB\u543B\u4F4F\u4E39\u5A18\u9999\u8F6F\u7684\u5C0F\u5634\uFF0C\u4E00\u624B\u4F38\u5230\u80F8\u524D\uFF0C\u9694\u7740\u8863\u886B\u6293\u4F4F\u4E39\u5A18\u9971\u6EE1\u7684\u53CC\u4E73\uFF0C\u7528\u529B\u63C9\u634F\u8D77\u6765\u3002</p><p>\u4E39\u5A18\u7AED\u529B\u6323\u624E\uFF0C\u4F46\u9762\u5BF9\u5353\u5929\u96C4\u94C1\u94F8\u822C\u7684\u624B\u81C2\uFF0C\u5979\u7684\u529B\u6C14\u4E0D\u6BD4\u4E00\u53EA\u873B\u8713\u5F3A\u4E0A\u591A\u5C11\u3002\u6323\u624E\u4E2D\uFF0C\u4E00\u6EF4\u6CEA\u6C34\u4ECE\u5979\u773C\u89D2\u6EDA\u843D\uFF0C\u6389\u5728\u6563\u4E71\u7684\u9B13\u53D1\u4E0A\u3002</p><p>\u826F\u4E45\uFF0C\u5353\u5929\u96C4\u677E\u5F00\u5634\uFF0C\u6469\u6332\u7740\u4E39\u5A18\u6ED1\u5AE9\u7684\u7C89\u988A\uFF0C\u563F\u563F\u7B11\u9053\uFF1A\u300C\u8FD9\u9999\u55B7\u55B7\u7684\u5C0F\u5634\uFF0C\u4EB2\u8D77\u6765\u53EF\u771F\u8212\u670D\u3002\u300D\u8BF4\u7740\u624B\u638C\u987A\u7740\u5979\u7684\u9999\u816E\u6ED1\u5230\u9888\u4E0B\uFF0C\u6307\u5C16\u4E00\u6311\uFF0C\u89E3\u5F00\u4E86\u895F\u53E3\u7684\u94AE\u6263\u3002</p><p>\u4E39\u5A18\u7D27\u7D27\u634F\u7740\u8863\u895F\uFF0C\u54ED\u9053\uFF1A\u300C\u6C42\u4F60\u653E\u8FC7\u6211\u5427\uFF0C\u5974\u5BB6\u5DF2\u7ECF\u662F\u5929\u7FBD\u54E5\u7684\u4EBA\u4E86\u3002\u300D</p><p>\u5353\u5929\u96C4\u72DE\u7B11\u9053\uFF1A\u300C\u600E\u4E48\uFF1F\u5B59\u5929\u7FBD\u90A3\u5C0F\u5B50\u80FD\u5E72\u4F60\uFF0C\u6211\u5C31\u4E0D\u80FD\u4E86\uFF1F\u300D</p><p>\u4E39\u5A18\u73E0\u6CEA\u7EB7\u7136\u6EDA\u843D\uFF0C\u5BE1\u5987\u518D\u5AC1\u672C\u4E0D\u5149\u5F69\uFF0C\u4F46\u6B64\u65F6\u5979\u987E\u4E0D\u4E0A\u7F9E\u803B\uFF0C\u6025\u9053\uFF1A\u300C\u5974\u5BB6\u662F\u8DDF\u5929\u7FBD\u54E5\u5B9A\u4E86\u4EB2\uFF0C\u518D\u8FC7\u4E24\u65E5\u5C31\u8981\u5AC1\u7ED9\u4ED6\u4E3A\u59BB\u3002\u300D</p><p>\u5353\u5929\u96C4\u5012\u4E0D\u77E5\u4E24\u4EBA\u8FD8\u6709\u8FD9\u5C42\u5173\u7CFB\uFF0C\u4F46\u8FD9\u4F1A\u513F\u5F13\u5728\u5F26\u4E0A\uFF0C\u83AB\u8BF4\u4E39\u5A18\u8FD8\u6CA1\u518D\u5AC1\uFF0C\u5C31\u662F\u5DF2\u7ECF\u6210\u4E86\u4EB2\uFF0C\u4E5F\u51B3\u8BA1\u4E0D\u4F1A\u7F62\u624B\u3002\u4ED6\u63B0\u5F00\u4E39\u5A18\u7684\u624B\u6307\uFF0C\u6293\u4F4F\u8863\u895F\u5411\u4E0B\u4E00\u62C9\uFF0C\u9732\u51FA\u4E39\u5A18\u96EA\u6ED1\u7684\u80A9\u5934\u3002</p><p>\u4E39\u5A18\u4E00\u8FB9\u906E\u63A9\u8EAB\u4F53\uFF0C\u4E00\u8FB9\u54ED\u53EB\u9053\uFF1A\u300C\u6551\u547D\u554A\uFF01\u6551\u547D\u554A\uFF01\u300D</p><p>\u5353\u5929\u96C4\u4E5F\u4E0D\u7406\u4F1A\uFF0C\u52A8\u624B\u5265\u4E0B\u4E39\u5A18\u7684\u8863\u886B\uFF0C\u5FC3\u9053\uFF1A\u5B59\u5929\u7FBD\uFF0C\u8FD9\u53EF\u6028\u4E0D\u5F97\u6211\uFF0C\u662F\u5979\u81EA\u5DF1\u8981\u558A\u7684\u3002\u5ECA\u4E2D\u4F20\u6765\u4E00\u9635\u811A\u6B65\u58F0\uFF0C\u6709\u4EBA\u56B7\u9053\uFF1A\u300C\u54EA\u4E2A\u6B7B\u8D3C\u56DA\u5728\u558A\u6551\u547D\uFF1F\u662F\u4E0D\u662F\u76AE\u75D2\u4E86\uFF1F\u300D</p><p>\u4E39\u5A18\u614C\u4E71\u4E2D\u53EA\u4E00\u8FED\u58F0\u53EB\u7740\uFF0C\u5FFD\u7136\u95E8\u53E3\u5149\u5577\u4E00\u58F0\uFF0C\u51E0\u540D\u72F1\u5352\u670D\u8272\u7684\u6C49\u5B50\u4E00\u6D8C\u800C\u5165\u3002</p><p>\u660F\u6697\u7684\u7262\u623F\u5185\uFF0C\u4E00\u4E2A\u904D\u8EAB\u7F1F\u7D20\u7684\u7F8E\u8273\u5987\u4EBA\u6B63\u8DEA\u5728\u5730\u4E0A\uFF0C\u5979\u7389\u4F53\u6A2A\u9648\uFF0C\u4E0A\u8EAB\u7684\u8863\u886B\u88AB\u892A\u5230\u8170\u95F4\uFF0C\u4E00\u5BF9\u96EA\u5AE9\u6D51\u5706\u7684\u7389\u4E73\u88F8\u9732\u51FA\u6765\uFF0C\u9876\u7AEF\u4E24\u53EA\u7EA2\u6DA6\u7684\u4E73\u5934\u968F\u7740\u4E73\u7403\u7684\u6447\u52A8\uFF0C\u4E00\u8361\u4E00\u8361\u5728\u7A7A\u4E2D\u5212\u7740\u5708\u5B50\u3002\u5979\u7389\u9888\u88AB\u4EBA\u6309\u4F4F\uFF0C\u9497\u7C2A\u6ED1\u8131\uFF0C\u4E4C\u4EAE\u7684\u957F\u53D1\u59D4\u5728\u5730\u4E0A\uFF0C\u4E00\u5F20\u767D\u51C0\u7684\u7389\u8138\u6CBE\u6EE1\u6CEA\u5149\uFF0C\u795E\u60C5\u51C4\u7136\u3002</p><p>\u8FDB\u6765\u7684\u72F1\u5352\u90FD\u5F20\u5927\u4E86\u5634\u5DF4\uFF0C\u773C\u775B\u77AA\u5F97\u6EDA\u5706\uFF0C\u76F4\u52FE\u52FE\u76EF\u7740\u4E39\u5A18\u8D64\u88F8\u7684\u80F8\u4E73\uFF0C\u8C01\u90FD\u6CA1\u6709\u4F5C\u58F0\u3002\u5353\u5929\u96C4\u677E\u5F00\u624B\uFF0C\u4E39\u5A18\u7ACB\u5373\u62B1\u4F4F\u80F8\u4E73\uFF0C\u8EB2\u5728\u89D2\u843D\u91CC\uFF0C\u80A9\u5934\u4E0D\u4F4F\u8038\u52A8\u3002</p><p>\u4E00\u4E2A\u94C1\u5854\u4F3C\u7684\u5927\u6C49\u54A7\u5F00\u5927\u5634\uFF0C\u9732\u51FA\u6B8B\u7F3A\u7684\u7259\u9F7F\uFF0C\u300C\u8FD9\u4E0D\u662F\u4E39\u5A18\u4E48\uFF1F\u300D</p><p>\u65C1\u8FB9\u4E00\u4E2A\u9752\u767D\u9762\u76AE\u7684\u6C49\u5B50\u662F\u7262\u5934\u9C8D\u6A2A\uFF0C\u4ED6\u5F20\u7740\u5634\uFF0C\u53E3\u6C34\u6D41\u4E86\u51FA\u6765\u4E5F\u4E0D\u77E5\u9053\u62B9\u62ED\u3002</p><p>\u5353\u5929\u96C4\u8D77\u8EAB\u89E3\u5F00\u725B\u76AE\u8170\u5E26\uFF0C\u7C97\u58F0\u8BF4\u9053\uFF1A\u300C\u72F1\u91CC\u7684\u89C4\u77E9\uFF0C\u89C1\u8005\u6709\u4EFD\u513F\uFF0C\u65E2\u7136\u6765\u4E86\uFF0C\u5927\u4F19\u513F\u90FD\u6765\u5FEB\u6D3B\u5FEB\u6D3B\u3002\u300D</p><p>\u8D75\u9738\u7B49\u4EBA\u4E00\u4E2A\u4E2A\u7B11\u9010\u989C\u5F00\uFF0C\u4F17\u4EBA\u5782\u6D8E\u4E39\u5A18\u7684\u7F8E\u8272\u4E0D\u662F\u4E00\u5929\u4E24\u5929\u4E86\uFF0C\u524D\u51E0\u6B21\u4E39\u5A18\u63A2\u76D1\uFF0C\u788D\u7740\u5B59\u5929\u7FBD\u7684\u9762\u5B50\u4E0D\u597D\u7528\u5F3A\uFF0C\u8FD9\u6B21\u5979\u81EA\u6295\u7F57\u7F51\uFF0C\u5353\u5929\u96C4\u53C8\u5982\u6B64\u4ED7\u4E49\uFF0C\u8FD9\u5230\u53E3\u7684\u7F8E\u8089\u600E\u4E48\u4E5F\u4E0D\u80FD\u8BA9\u5979\u98DE\u4E86\u3002</p><p>\u4E39\u5A18\u672C\u4EE5\u4E3A\u6765\u7684\u662F\u6551\u661F\uFF0C\u5F85\u770B\u5230\u4ED6\u4EEC\u6DEB\u90AA\u7684\u76EE\u5149\uFF0C\u542C\u5230\u5353\u5929\u96C4\u90A3\u756A\u8BDD\uFF0C\u4E0D\u7531\u4ECE\u5934\u51C9\u5230\u811A\uFF0C\u4E00\u65F6\u5413\u5F97\u5446\u4E86\u3002</p><p>\u5353\u5929\u96C4\u62E7\u4F4F\u4E39\u5A18\u7684\u624B\u8155\uFF0C\u5C06\u5979\u6254\u5230\u5899\u89D2\u7684\u5E8A\u4E0A\uFF0C\u7136\u540E\u817E\u8EAB\u9A91\u5728\u4E39\u5A18\u8170\u4E0A\uFF0C\u5265\u4E0B\u5979\u7684\u7F57\u886B\uFF0C\u626F\u6389\u80F8\u515C\uFF0C\u4E00\u4E00\u6254\u5728\u5730\u4E0A\u3002</p><p>\u4E39\u5A18\u4E24\u624B\u62B1\u5728\u80F8\u524D\uFF0C\u67D4\u5507\u8F7B\u98A4\uFF0C\u5A07\u9765\u5F77\u4F5B\u767D\u7EB8\u4E00\u822C\u3002\u72F1\u5352\u4EEC\u56F4\u8FC7\u6765\uFF0C\u62C9\u624B\u6309\u811A\u5C06\u4E39\u5A18\u56DB\u80A2\u5206\u5F00\uFF0C\u8D75\u9738\u5288\u624B\u62E7\u4F4F\u4E39\u5A18\u7684\u4E73\u623F\u7528\u529B\u4E00\u634F\uFF0C\u767D\u817B\u7684\u4E73\u8089\u4ECE\u6307\u7F1D\u95F4\u6EA2\u51FA\uFF0C\u6ED1\u8F6F\u5F97\u72B9\u5982\u4E00\u56E2\u817B\u8102\uFF0C\u5FAE\u786C\u7684\u4E73\u5934\u9876\u5728\u638C\u5FC3\uFF0C\u968F\u7740\u7EB7\u4E71\u7684\u5FC3\u8DF3\u4E00\u98A4\u4E00\u98A4\u3002</p><p>\u9C8D\u6A2A\u62A2\u4F4F\u53E6\u4E00\u53EA\u4E73\u623F\uFF0C\u4ED6\u4E3E\u52A8\u66F4\u4E3A\u4E0D\u582A\uFF0C\u62B1\u4F4F\u90A3\u53EA\u5C31\u8DB4\u4E0A\u53BB\uFF0C\u997F\u72D7\u822C\u53C8\u8214\u53C8\u5543\uFF0C\u8FDE\u8BDD\u90FD\u987E\u4E0D\u5F97\u8BF4\u3002</p><p>\u5353\u5929\u96C4\u8131\u4E0B\u8863\u670D\uFF0C\u9732\u51FA\u5065\u58EE\u7684\u4F53\u9B44\uFF0C\u8FD8\u6709\u80EF\u4E0B\u4E00\u6839\u4E03\u5BF8\u591A\u957F\uFF0C\u53CC\u7C97\u53C8\u9ED1\u7684\u8089\u68D2\u3002\u4ED6\u8D77\u8EAB\u62FD\u4E0B\u4E39\u5A18\u7684\u767D\u7EEB\u6C57\u5DFE\uFF0C\u624B\u638C\u987A\u52BF\u63A2\u5165\u4EB5\u88E4\uFF0C\u6263\u5728\u90A3\u7247\u9500\u9B42\u7684\u6ED1\u817B\u95F4\u3002</p><p>\u4E39\u5A18\u5A07\u8EAF\u5267\u9707\uFF0C\u5E26\u7740\u54ED\u8154\u53EB\u4E86\u58F0\uFF0C\u300C\u5929\u7FBD\u54E5\u2014\u2014\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u51FA\u4E86\u5927\u72F1\uFF0C\u5B59\u5929\u7FBD\u6EE1\u5FC3\u70E6\u95F7\uFF0C\u5728\u9662\u91CC\u8F6C\u4E86\u7247\u523B\uFF0C\u5FC3\u7EEA\u53CD\u800C\u8D8A\u6765\u8D8A\u4E71\uFF0C\u4ED6\u63C9\u4E86\u63C9\u773C\u7736\uFF0C\u60F3\u5230\u5916\u9762\u900F\u900F\u6C14\u3002</p><p>\u4E00\u540D\u72F1\u5352\u5306\u5306\u8FC7\u6765\uFF0C\u89C1\u7740\u5B59\u5929\u7FBD\u4E0D\u7531\u9053\uFF1A\u300C\u5218\u6587\u4E66\u4E0D\u662F\u627E\u4F60\u5417\uFF1F\u600E\u4E48\u8FD8\u4E0D\u8FC7\u53BB\uFF1F\u300D</p><p>\u5B59\u5929\u7FBD\u6014\u4E86\u4E00\u4E0B\u3002</p><p>\u5218\u8FA9\u673A\u7684\u623F\u95E8\u865A\u63A9\uFF0C\u5C4B\u91CC\u6446\u7740\u4E00\u5F20\u5C4F\u98CE\uFF0C\u540E\u9762\u9690\u9690\u4F20\u6765\u7B11\u8BED\u3002\u7ECD\u5174\u5E08\u7237\u5BF9\u4F4F\u5904\u4E00\u5411\u8BB2\u7A76\u6C60\u9C7C\u9648\u8BBE\uFF0C\u5218\u8FA9\u673A\u4E0D\u751A\u5F97\u5FD7\uFF0C\u843D\u5728\u76D1\u72F1\u91CC\u5F53\u4E86\u6587\u540F\uFF0C\u6C60\u9C7C\u7AF9\u67B6\u53EA\u597D\u7701\u4E86\uFF0C\u53EA\u7559\u4E86\u67B6\u5C4F\u98CE\u7565\u5C3D\u4EBA\u4E8B\u3002</p><p>\u300C\u518D\u6DF1\u70B9\u513F\u2026\u2026\u5BF9\u2026\u2026\u7528\u70B9\u513F\u529B\uFF0C\u771F\u4E56\u2026\u2026\u300D</p><p>\u5475\u5475\u7B11\u58F0\u4E2D\uFF0C\u5939\u6742\u7740\u53FD\u53FD\u557E\u557E\u7684\u5438\u542E\u58F0\u3002\u5B59\u5929\u7FBD\u5FC3\u4E0B\u5927\u5947\uFF0C\u4ED6\u6CA1\u6709\u4F5C\u58F0\uFF0C\u6084\u6084\u8D8B\u8FD1\u4E86\u4E9B\uFF0C\u4ECE\u5C4F\u98CE\u7684\u7F1D\u9699\u4E2D\u671D\u5185\u7AA5\u53BB\u3002</p><p>\u5C4F\u540E\u662F\u4E00\u5F20\u5927\u5E8A\uFF0C\u5218\u8FA9\u673A\u9760\u5728\u5E8A\u5934\uFF0C\u4E24\u817F\u7B95\u5F20\uFF0C\u4E00\u4E2A\u7A1A\u5AE9\u7684\u8EAB\u5B50\u50CF\u53EA\u5C0F\u767D\u5154\u822C\u8DB4\u5728\u4ED6\u817F\u95F4\uFF0C\u4E24\u624B\u62B1\u7740\u4ED6\u7684\u8089\u68D2\uFF0C\u5AE3\u7EA2\u7684\u5C0F\u5634\u542B\u4F4F\u9F9F\u5934\uFF0C\u4E0D\u4F4F\u8214\u542E\u3002</p><p>\u5B59\u5929\u7FBD\u5FC3\u5934\u7EB3\u7F55\uFF0C\u5218\u8FA9\u673A\u597D\u7537\u98CE\u4ED6\u4E5F\u6709\u6240\u542C\u95FB\uFF0C\u4E0D\u77E5\u5218\u8FA9\u673A\u7528\u4E86\u4EC0\u4E48\u624B\u6BB5\uFF0C\u8BA9\u8FD9\u5C0F\u5154\u5D3D\u5B50\u5982\u6B64\u542C\u8BDD\u3002\u770B\u4ED6\u8214\u9E21\u5DF4\u7684\u4E56\u5DE7\u6837\u5B50\uFF0C\u8DDF\u4ED6\u5A18\u4EB2\u5012\u6709\u4E00\u6BD4\u2026\u2026</p><p>\u767D\u82F1\u83B2\u8EAB\u65E0\u5BF8\u7F15\uFF0C\u6485\u7740\u767D\u767D\u7684\u5C0F\u5C41\u80A1\u8DB4\u5728\u5218\u8FA9\u673A\u5E72\u7626\u7684\u53CC\u817F\u95F4\uFF0C\u5C0F\u5634\u5F20\u5F97\u6D51\u5706\uFF0C\u4F9D\u7167\u5218\u8FA9\u673A\u7684\u6307\u70B9\u52AA\u529B\u542E\u5438\u7740\u90A3\u6839\u9633\u5177\u3002\u826F\u4E45\uFF0C\u4ED6\u5410\u51FA\u8089\u68D2\uFF0C\u4E00\u8FB9\u7528\u624B\u80CC\u62B9\u53BB\u53E3\u6C34\uFF0C\u4E00\u8FB9\u63C9\u7740\u816E\u6839\u3002</p><p>\u300C\u5634\u5DF4\u9178\u4E86\uFF1F\u300D</p><p>\u82F1\u83B2\u70B9\u4E86\u70B9\u5934\u3002</p><p>\u5218\u8FA9\u673A\u6478\u7740\u4ED6\u7684\u5C0F\u8138\u9053\uFF1A\u300C\u83B2\u513F\u771F\u4E56\uFF0C\u6B47\u4E00\u4F1A\u513F\u5427\u3002\u6765\uFF0C\u8BA9\u53D4\u53D4\u770B\u770B\u4F60\u7684\u4F24\u53E3\u3002\u300D</p><p>\u82F1\u83B2\u722C\u7740\u8F6C\u8FC7\u8EAB\u5B50\uFF0C\u6485\u8D77\u7C89\u5AE9\u7684\u5C0F\u5C41\u80A1\uFF0C\u5C06\u517B\u8FD9\u51E0\u65E5\uFF0C\u7EF7\u5E26\u65E9\u5DF2\u53BB\u6389\u3002\u5218\u8FA9\u673A\u5265\u5F00\u4ED6\u7684\u81C0\u8089\uFF0C\u4ED4\u7EC6\u770B\u4E86\u770B\uFF0C\u300C\u662F\u4E0D\u662F\u8FD8\u6709\u70B9\u513F\u75BC\u554A\uFF1F\u300D</p><p>\u300C\u55EF\u3002\u300D</p><p>\u300C\u6CA1\u4E8B\u513F\u7684\uFF0C\u5C31\u5FEB\u597D\u4E86\u3002\u300D\u8BF4\u7740\u4ED6\u62FF\u51FA\u4E00\u53EA\u5C0F\u74F6\u5B50\uFF0C\u4F38\u51FA\u4E2D\u6307\u8638\u4E86\u8638\uFF0C\u6309\u5728\u82F1\u83B2\u7EA2\u5AE9\u7684\u5C41\u773C\u513F\u4E0A\u3002</p><p>\u82F1\u83B2\u5403\u75DB\u5730\u4E00\u98A4\uFF0C\u5C41\u80A1\u671D\u524D\u632A\u53BB\u3002</p><p>\u5218\u8FA9\u673A\u5475\u54C4\u9053\uFF1A\u300C\u83B2\u513F\u522B\u6015\uFF0C\u53D4\u53D4\u518D\u7ED9\u4F60\u4E0A\u51E0\u6B21\u836F\uFF0C\u5C31\u4E0D\u4F1A\u75BC\u4E86\u3002\u300D</p><p>\u82F1\u83B2\u9053\uFF1A\u300C\u53D4\u53D4\u8F7B\u4E00\u70B9\u513F\u3002\u300D</p><p>\u5218\u8FA9\u673A\u5728\u82F1\u83B2\u5C41\u773C\u513F\u5468\u56F4\u6309\u4E86\u7247\u523B\uFF0C\u5F85\u4ED6\u653E\u677E\u4E0B\u6765\u624D\u7F13\u7F13\u6345\u5165\u3002\u82F1\u83B2\u6CA1\u6709\u95EA\u907F\uFF0C\u4F46\u7709\u5934\u5374\u62E7\u4E86\u8D77\u6765\uFF0C\u5C0F\u5634\u6241\u6241\u7684\uFF0C\u4F3C\u4E4E\u8981\u54ED\u51FA\u6765\u3002</p><p>\u5B59\u5929\u7FBD\u5728\u5C4F\u98CE\u5916\u770B\u5F97\u6E05\u695A\uFF0C\u4E0D\u7531\u60F3\u8D77\u4E39\u5A18\u8DDF\u81EA\u5DF1\u809B\u4EA4\u65F6\u4E00\u8FB9\u5403\u75DB\uFF0C\u4E00\u8FB9\u5A49\u8F6C\u627F\u6B22\u7684\u7F8E\u6001\u3002\u82F1\u83B2\u7709\u773C\u4E0E\u4E39\u5A18\u6709\u516D\u4E03\u76F8\u4F3C\uFF0C\u53EA\u662F\u5E74\u7EAA\u5C1A\u5C0F\uFF0C\u72B9\u5982\u4E00\u4E2A\u79C0\u7F8E\u53EF\u7231\u7684\u5973\u5B69\u513F\u3002\u90A3\u53EA\u5C0F\u5C41\u80A1\u6676\u83B9\u7C89\u5AE9\uFF0C\u4E2D\u95F4\u4E00\u53EA\u7EA2\u7EA2\u7684\u5C0F\u8089\u5B54\u88AB\u624B\u6307\u6345\u7A7F\uFF0C\u5FAE\u5FAE\u9F13\u8D77\u3002</p><p>\u6D82\u5B8C\u836F\uFF0C\u5218\u8FA9\u673A\u5E76\u6CA1\u6709\u62D4\u51FA\u624B\u6307\uFF0C\u800C\u662F\u5728\u82F1\u83B2\u5C0F\u5C41\u80A1\u91CC\u62BD\u9001\u8D77\u6765\u3002\u82F1\u83B2\u5C0F\u8138\u6E10\u6E10\u53D1\u7EA2\uFF0C\u9F3B\u606F\u4E5F\u53D8\u5F97\u7C97\u91CD\u3002</p><p>\u5218\u8FA9\u673A\u54C4\u9053\uFF1A\u300C\u83B2\u513F\uFF0C\u5C41\u773C\u513F\u5939\u7D27\u4E00\u70B9\u513F\u3002\u300D</p><p>\u82F1\u83B2\u54FC\u54FC\u549B\u549B\u8BF4\uFF1A\u300C\u75BC\u5462\u2026\u2026\u300D</p><p>\u300C\u5939\u7D27\u4E00\u70B9\u513F\u836F\u624D\u80FD\u62B9\u5230\u4E0A\u9762\u554A\uFF0C\u542C\u8BDD\u2026\u2026\u5BF9\u4E86\u2026\u2026\u300D</p><p>\u82F1\u83B2\u542C\u8BDD\u5730\u6536\u7D27\u4E86\u5C41\u773C\u513F\uFF0C\u5218\u8FA9\u673A\u4E00\u8FB9\u6233\u5F04\uFF0C\u4E00\u8FB9\u5728\u4ED6\u7684\u4F1A\u9634\u5904\u63C9\u6413\u3002\u4E0D\u591A\u65F6\uFF0C\u82F1\u83B2\u90A3\u6839\u7389\u8695\u4F3C\u7684\u5C0F\u8089\u68D2\u7ADF\u7136\u4E00\u70B9\u4E00\u70B9\u786C\u4E86\u8D77\u6765\uFF0C\u4ED6\u8DB4\u5728\u5E8A\u4E0A\uFF0C\u52FE\u5934\u770B\u5230\u80EF\u4E0B\u7684\u5F02\u72B6\uFF0C\u4E0D\u7531\u5145\u6EE1\u60CA\u5947\u5730\u54A6\u4E86\u4E00\u58F0\u3002</p><p>\u5218\u8FA9\u673A\u6276\u7740\u82F1\u83B2\u7684\u624B\u653E\u5728\u5C0F\u8089\u68D2\u4E0A\uFF0C\u8BA9\u4ED6\u63E1\u4F4F\u6162\u6162\u634B\u52A8\uFF0C\u95EE\u9053\uFF1A\u300C\u597D\u4E0D\u597D\u73A9\u554A\uFF1F\u300D</p><p>\u300C\u55EF\u3002\u300D\u82F1\u83B2\u5C0F\u8138\u6DA8\u5F97\u901A\u7EA2\u3002\u5218\u8FA9\u673A\u624B\u6307\u4E0D\u7C97\u4E0D\u7EC6\uFF0C\u6EE1\u6EE1\u63D2\u5728\u809B\u4E2D\uFF0C\u53C8\u6CBE\u4E86\u836F\u6C41\uFF0C\u6ED1\u6E9C\u5F97\u7D27\uFF0C\u4ED6\u4E00\u8FB9\u88AB\u4EBA\u63D2\u7740\u5C41\u773C\u513F\uFF0C\u4E00\u8FB9\u6478\u7740\u81EA\u5DF1\u8FD8\u672A\u957F\u6210\u7684\u5C0F\u9633\u5177\uFF0C\u7ADF\u7136\u6709\u4E86\u79CD\u5947\u7279\u7684\u5FEB\u611F\u3002</p><p>\u5218\u8FA9\u673A\u7528\u624B\u6307\u63D2\u5F04\u591A\u65F6\uFF0C\u53EA\u89C9\u82F1\u83B2\u7684\u5C0F\u5C41\u773C\u513F\u5149\u6ED1\u7D27\u51D1\uFF0C\u6709\u8DA3\u4E4B\u6781\uFF0C\u6709\u5FC3\u8D81\u52BF\u4EA4\u6B22\uFF0C\u53C8\u6015\u4ED6\u4F24\u52BF\u672A\u6108\uFF0C\u53EA\u597D\u5F3A\u538B\u6B32\u706B\uFF0C\u7528\u4ED6\u5C0F\u5634\u715E\u706B\u3002</p><p>\u82F1\u83B2\u5E74\u5E7C\uFF0C\u5C1A\u4E0D\u77E5\u7537\u5973\u4E4B\u4E8B\uFF0C\u5218\u8FA9\u673A\u5BF9\u4ED6\u5173\u6000\u5907\u81F3\uFF0C\u53C8\u5141\u8BFA\u5E2E\u4ED6\u662D\u96EA\u7236\u4EB2\u7684\u51A4\u60C5\uFF0C\u82F1\u83B2\u611F\u6FC0\u4E4B\u4F59\uFF0C\u5BF9\u8FD9\u4E2A\u7559\u7740\u9F20\u987B\u7684\u53D4\u53D4\u4FE1\u4EFB\u5F97\u65E0\u4EE5\u590D\u52A0\u3002\u4ED6\u5FCD\u7740\u5634\u5DF4\u7684\u9178\u75DB\u5E2E\u5218\u8FA9\u673A\u542E\u51FA\u7CBE\u6DB2\uFF0C\u553E\u5728\u58F6\u91CC\uFF0C\u81EA\u53BB\u53D6\u4E86\u6C34\u6F31\u53E3\u3002</p><p>\u5B59\u5929\u7FBD\u811A\u5C16\u8F7B\u70B9\uFF0C\u4EBA\u5DF2\u9000\u5728\u95E8\u5916\uFF0C\u8BF4\u9053\uFF1A\u300C\u5218\u592B\u5B50\u5728\u4E48\uFF1F\u300D</p><p>\u5C4F\u98CE\u540E\u54CD\u8D77\u6089\u6089\u7D22\u7D22\u7684\u7A7F\u8863\u58F0\uFF0C\u5218\u8FA9\u673A\u9053\uFF1A\u300C\u5929\u7FBD\u5417\uFF1F\u8FDB\u6765\u5427\u3002\u300D\u65C1\u8FB9\u82F1\u83B2\u5413\u5F97\u7F29\u6210\u4E00\u56E2\uFF0C\u663E\u7136\u5BF9\u5B59\u5929\u7FBD\u5FC3\u6709\u4F59\u60B8\u3002</p><p>\u5218\u8FA9\u673A\u8E31\u51FA\u6765\u9053\uFF1A\u300C\u767D\u5B5D\u5112\u8FD9\u6848\u5B50\u8FD8\u6709\u7591\u5904\u3002\u5176\u5B50\u767D\u82F1\u83B2\u529B\u8BC1\u5176\u7236\u51A4\u6789\uFF0C\u6211\u5DF2\u5F55\u4E0B\u53E3\u4F9B\uFF0C\u4F60\u4E14\u770B\u770B\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u63A5\u8FC7\u6765\u4E00\u7FFB\uFF0C\u53EA\u89C1\u4E0A\u9762\u5370\u6EE1\u82F1\u83B2\u7684\u6307\u5370\uFF0C\u5C06\u5F53\u665A\u5E97\u4E2D\u51B2\u7A81\u8BB2\u5F97\u5DE8\u7EC6\u65E0\u9057\u3002\u867D\u7136\u767D\u82F1\u83B2\u529B\u79F0\u5BB6\u4EBA\u4E0E\u51FA\u624B\u7684\u8FC7\u8DEF\u5973\u5B50\u7D20\u4E0D\u76F8\u8BC6\uFF0C\u4F46\u5218\u8FA9\u673A\u8001\u540F\u624B\u7B14\uFF0C\u63CF\u6479\u4E0B\u76F4\u5982\u767D\u96EA\u83B2\u4E0E\u859B\u971C\u7075\u5E76\u80A9\u6253\u4F24\u524D\u6765\u6253\u63A2\u6D88\u606F\u7684\u4FBF\u8863\u72F1\u5352\uFF0C\u6587\u672B\u5199\u5230\u767D\u96EA\u83B2\u5165\u72F1\u540E\u767D\u5B5D\u5112\u56DB\u5904\u5954\u8D70\uFF0C\u66F4\u662F\u6B32\u76D6\u5F25\u5F70\uFF0C\u8C7A\u72FC\u5761\u8FD1\u5728\u54AB\u5C3A\uFF0C\u4F55\u5FC5\u8FDC\u8D74\u957F\u5B81\u6B66\u5E73\uFF1F</p><p>\u5B59\u5929\u7FBD\u5FC3\u4E0B\u6697\u8D5E\uFF0C\u8FD9\u4EFD\u4F9B\u8F9E\u9012\u4E0A\u53BB\uFF0C\u767D\u5B5D\u5112\u5C31\u662F\u65E0\u7F6A\uFF0C\u4E5F\u6709\u4E86\u4E09\u5206\u7F6A\uFF0C\u53EF\u7B11\u82F1\u83B2\u8FD8\u8499\u5728\u9F13\u91CC\uFF0C\u5FC3\u7518\u60C5\u613F\u7ED9\u5218\u8FA9\u673A\u8214\u9E21\u5DF4\u73A9\u5C41\u80A1\u3002</p><p>\u5B59\u5929\u7FBD\u6536\u4E86\u4F9B\u8F9E\uFF0C\u5218\u8FA9\u673A\u53C8\u9053\uFF1A\u300C\u960E\u5927\u4EBA\u4F20\u56DE\u6D88\u606F\uFF0C\u8FD8\u672A\u89C1\u7740\u5C01\u516C\u516C\uFF0C\u8981\u8FDF\u51E0\u65E5\u624D\u80FD\u56DE\u72F1\uFF0C\u8BA9\u6211\u4EEC\u5C0F\u5FC3\u7167\u770B\uFF0C\u522B\u51FA\u4E86\u4E71\u5B50\u3002\u300D</p><p>\u859B\u971C\u7075\u6740\u4EBA\u8D8A\u72F1\uFF0C\u5E78\u5F97\u5434\u5927\u5F6A\u64D2\u56DE\uFF0C\u72F1\u4E2D\u5DF2\u7ECF\u52A0\u5F3A\u6212\u5907\uFF0C\u95E8\u7A97\u9501\u94A5\u90FD\u6362\u4E86\u65B0\u7684\uFF0C\u552F\u6050\u9003\u7684\u662F\u767D\u96EA\u83B2\u3002\u5B59\u5929\u7FBD\u9053\uFF1A\u300C\u960E\u5927\u4EBA\u5DF2\u7ECF\u53BB\u4E86\u8FD9\u4E48\u591A\u65E5\uFF0C\u8FD8\u672A\u89C1\u7740\u5C01\u516C\u516C\uFF1F\u300D</p><p>\u5218\u8FA9\u673A\u9053\uFF1A\u300C\u5C01\u516C\u516C\u662F\u4E1C\u5382\u638C\u6743\u7684\u5927\u603B\u7BA1\uFF0C\u83AB\u8BF4\u5404\u5E9C\u4E3B\u5B98\uFF0C\u5C31\u662F\u5468\u56F4\u51E0\u7701\u7684\u5DE1\u629A\u603B\u7763\u90FD\u8D76\u6765\u62DC\u89C1\u3002\u82E5\u4E0D\u662F\u960E\u5927\u4EBA\u4E0E\u5C01\u516C\u516C\u6709\u4E9B\u6545\u4EA4\uFF0C\u600E\u4E48\u4E5F\u8F6E\u4E0D\u5230\u4ED6\u3002\u300D</p><p>\u8BF4\u7740\u5218\u8FA9\u673A\u538B\u4F4E\u58F0\u97F3\uFF0C\u300C\u82F1\u83B2\u7559\u5728\u6211\u623F\u91CC\uFF0C\u8FD9\u4E9B\u6587\u4E66\u4F60\u6536\u597D\uFF0C\u8BB0\u4F4F\u770B\u7D27\u4E39\u5A18\u548C\u767D\u7389\u83B2\uFF0C\u5979\u4EEC\u4E0D\u5728\u72F1\u91CC\u4E5F\u597D\uFF0C\u4F46\u5343\u4E07\u4E0D\u80FD\u8BA9\u5979\u4EEC\u79BB\u5F00\u795E\u4ED9\u5CAD\u3002\u8FD8\u6709\u767D\u96EA\u83B2\uFF0C\u52A1\u5FC5\u770B\u7D27\u4E86\uFF01\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6B63\u5728\u95ED\u76EE\u8C03\u606F\u7684\u767D\u96EA\u83B2\u7A81\u7136\u7741\u5F00\u773C\u775B\u3002\u859B\u971C\u7075\u4ECD\u88AB\u540A\u5728\u6805\u680F\u4E0A\uFF0C\u6655\u5012\u822C\u5782\u7740\u5934\u4E00\u58F0\u4E0D\u54CD\u3002\u6574\u95F4\u5730\u7262\u5B89\u9759\u5F97\u5982\u540C\u575F\u5893\u3002</p><p>\u767D\u96EA\u83B2\u6162\u6162\u5730\u5410\u4E86\u53E3\u6C14\uFF0C\u5FC3\u5934\u5140\u81EA\u60B8\u52A8\u3002\u521A\u624D\u5979\u4F3C\u4E4E\u542C\u5230\u5A18\u4EB2\u7684\u58F0\u97F3\uFF0C\u90A3\u58F0\u97F3\u50CF\u662F\u5728\u54ED\u53EB\u3002\u4E5F\u8BB8\u662F\u56E0\u4E3A\u60F3\u5A18\u4E86\uFF0C\u624D\u4F1A\u542C\u9519\u3002\u73B0\u5728\u7236\u4EB2\u4E0D\u5728\u4E86\uFF0C\u4E0D\u77E5\u9053\u5A18\u548C\u59B9\u59B9\u3001\u5F1F\u5F1F\u4EEC\u8FD8\u597D\u5417\uFF1F</p><p>\u5C31\u5728\u767D\u96EA\u83B2\u5934\u9876\uFF0C\u4E00\u95F4\u72ED\u5C0F\u7684\u7262\u623F\u5185\uFF0C\u5435\u56B7\u58F0\u3001\u563B\u7B11\u58F0\u54CD\u6210\u4E00\u7247\uFF0C\u4E2D\u95F4\u8FD8\u5939\u6742\u7740\u5973\u5B50\u7684\u54ED\u53EB\u54C0\u6C42\u3002</p><p>\u4E94\u540D\u72F1\u5352\u90FD\u6324\u5728\u5899\u89D2\uFF0C\u8F70\u7B11\u7740\u6309\u4F4F\u4E00\u4E2A\u5987\u4EBA\u7684\u624B\u811A\uFF0C\u5728\u5979\u8EAB\u4E0A\u8086\u610F\u6478\u5F04\uFF0C\u4E39\u5A18\u4E00\u4E2A\u67D4\u5F31\u5987\u4EBA\uFF0C\u600E\u654C\u5F97\u8FC7\u8FD9\u4E9B\u4EBA\u9762\u517D\u5FC3\u7684\u5927\u6C49\uFF0C\u5979\u8863\u886B\u6389\u4E86\u4E00\u5730\uFF0C\u8D64\u88F8\u7684\u80F4\u4F53\u5728\u4F17\u4EBA\u624B\u4E2D\u65E0\u52A9\u7684\u626D\u52A8\u7740\uFF0C\u54ED\u6CE3\u5F97\u6CEA\u6D41\u6EE1\u9762\u3002</p><p>\u5353\u5929\u96C4\u63E1\u4F4F\u4E39\u5A18\u7684\u811A\u8E1D\uFF0C\u5C06\u5979\u6700\u540E\u4E00\u6761\u853D\u4F53\u7684\u4EB5\u88E4\u62FD\u843D\u4E0B\u6765\u3002\u770B\u7740\u4E39\u5A18\u8D64\u6761\u6761\u7684\u7389\u4F53\uFF0C\u5353\u5929\u96C4\u4E0D\u7531\u5439\u4E86\u4E00\u58F0\u53E3\u54E8\uFF0C\u300C\u8FD9\u5A46\u5A18\u7684\u8EAB\u5B50\uFF0C\u6BD4\u6625\u9999\u697C\u7684\u5C0F\u7389\u8FD8\u767D\u7740\u4E9B\u3002\u8001\u8D75\uFF0C\u5148\u522B\u5543\u4E86\uFF0C\u8BA9\u5927\u4F19\u513F\u597D\u597D\u770B\u770B\u3002\u300D</p><p>\u8D75\u9738\u6C14\u5598\u5401\u5401\u7684\u677E\u5F00\u5634\uFF0C\u4E39\u5A18\u4E73\u5934\u88AB\u53E3\u6C34\u6253\u6E7F\uFF0C\u53C8\u7EA2\u53C8\u4EAE\uFF0C\u4E73\u5C16\u7559\u4E0B\u4E86\u534A\u5708\u7259\u5370\u3002\u8BB8\u591A\u5973\u4EBA\u76F8\u8C8C\u8FD8\u7B97\u6807\u81F4\uFF0C\u8EAB\u6750\u5374\u662F\u5E73\u5E73\uFF0C\u4E0D\u662F\u592A\u80D6\u5C31\u662F\u592A\u7626\u3002\u4E39\u5A18\u5BB9\u8C8C\u5A07\u8273\uFF0C\u4E00\u8EAB\u7F8E\u8089\u4E5F\u6BEB\u4E0D\u900A\u8272\u3002\u5979\u5468\u8EAB\u808C\u80A4\u767D\u5AE9\u5982\u8102\uFF0C\u9999\u6ED1\u52A8\u4EBA\uFF0C\u4E73\u623F\u4E30\u6EE1\u5706\u6DA6\uFF0C\u8170\u80A2\u7EC6\u8F6F\uFF0C\u6D51\u4E0D\u4F3C\u751F\u513F\u80B2\u5973\u7684\u534A\u8001\u5F90\u5A18\u3002</p><p>\u4E39\u5A18\u6CEA\u773C\u6A21\u7CCA\uFF0C\u54FD\u54BD\u9053\uFF1A\u300C\u6C42\u6C42\u4F60\u4EEC\u653E\u8FC7\u6211\u5427\u3002\u5974\u5BB6\u5B5D\u671F\u8FD8\u672A\u6EE1\u2026\u2026\u300D</p><p>\u9C8D\u6A2A\u563F\u563F\u7B11\u9053\uFF1A\u300C\u767D\u5B5D\u5112\u90A3\u4E2A\u8001\u4E1C\u897F\uFF0C\u8981\u77E5\u9053\u5979\u5A46\u5A18\u88AB\u54B1\u4EEC\u5265\u5149\u4E86\u8FD9\u4E48\u770B\uFF0C\u505A\u9B3C\u4E5F\u4E0D\u5B89\u5B81\u3002\u300D</p><p>\u8D75\u9738\u62AC\u8D77\u4E86\u624B\uFF0C\u4E00\u8FB9\u5438\u6C14\u4E00\u8FB9\u56B7\u9053\uFF1A\u300C\u771F\u4ED6\u5988\u7684\u9999\uFF01\u4E39\u5A18\uFF0C\u4F60\u8EAB\u4E0A\u643D\u5F97\u4EC0\u4E48\u7C89\uFF1F\u300D\u8BF4\u7740\u5728\u5979\u817F\u6839\u637B\u4E86\u4E00\u628A\u3002</p><p>\u4E39\u5A18\u62DA\u547D\u6323\u624E\uFF0C\u5979\u7EA4\u8DB3\u6781\u5C0F\uFF0C\u53C8\u6781\u4E3A\u5149\u6ED1\uFF0C\u8FD9\u4E00\u6323\u5C45\u7136\u4ECE\u4E00\u540D\u72F1\u5352\u624B\u91CC\u6ED1\u4E86\u51FA\u6765\uFF0C\u5408\u4F4F\u53CC\u817F\u3002</p><p>\u5353\u5929\u96C4\u773C\u75BE\u624B\u5FEB\uFF0C\u4E00\u628A\u6349\u4F4F\u4E39\u5A18\u7684\u819D\u5F2F\uFF0C\u671D\u4E0A\u62AC\u8D77\uFF0C\u7B11\u9053\uFF1A\u300C\u8FD8\u5BB3\u7F9E\u5462\uFF0C\u53C8\u4E0D\u662F\u9EC4\u82B1\u95FA\u5973\uFF0C\u8BA9\u4EBA\u770B\u770B\u6709\u4EC0\u4E48\u6253\u7D27\uFF1F\u300D</p><p>\u65C1\u8FB9\u4E00\u540D\u72F1\u5352\u9053\uFF1A\u300C\u5929\u5927\u5730\u5927\uFF0C\u5230\u4E86\u8FD9\u7262\u91CC\u5C31\u662F\u54B1\u4EEC\u6700\u5927\uFF0C\u5C31\u662F\u7687\u540E\u5A18\u5A18\u8FDB\u4E86\u7262\u91CC\uFF0C\u54B1\u4EEC\u4E5F\u662F\u60F3\u5E72\u5C31\u5E72\uFF0C\u60F3\u73A9\u5C31\u73A9\u3002\u4F60\u95EE\u95EE\u8FD9\u7262\u91CC\u7684\u5973\u72AF\uFF0C\u54EA\u4E2A\u4E0D\u662F\u5148\u8131\u5149\u4E86\u8BA9\u54B1\u4EEC\u770B\uFF1F\u300D</p><p>\u4E39\u5A18\u545C\u54BD\u9053\uFF1A\u300C\u6211\u4E0D\u662F\u5973\u72AF\u2026\u2026\u300D</p><p>\u72F1\u5352\u7B11\u9053\uFF1A\u300C\u767D\u5B5D\u5112\u8C0B\u53CD\uFF0C\u4F60\u5C31\u662F\u53CD\u8D3C\u5BB6\u5C5E\uFF0C\u5927\u660E\u56FD\u6CD5\uFF0C\u53CD\u8D3C\u5973\u7737\u4E00\u5F8B\u53D1\u5356\u4E3A\u5993\u3002\u4F60\u4E00\u4E2A\u5A4A\u5B50\u8FD8\u88C5\u4EC0\u4E48\u8D1E\u6D01\uFF1F\u300D</p><p>\u9C8D\u6A2A\u9053\uFF1A\u300C\u5C11\u8DDF\u5979\u5E9F\u8BDD\uFF0C\u628A\u8FD9\u5A4A\u5B50\u7684\u817F\u63B0\u5F00\uFF0C\u8BA9\u54B1\u770B\u770B\u751F\u8FC7\u5B69\u5B50\u8FD9\u5A4A\u5B50\u7684\u5C44\u53D8\u5F62\u6CA1\u6709\uFF1F\u300D</p><p>\u54ED\u53EB\u58F0\u4E2D\uFF0C\u5353\u5929\u96C4\u63E1\u4F4F\u4E39\u5A18\u7684\u819D\u5F2F\uFF0C\u5C06\u5979\u4E24\u6761\u767D\u751F\u751F\u7684\u5927\u817F\u4E00\u5B57\u5206\u5F00\u3002</p><p>\u53EA\u89C1\u4E39\u5A18\u8179\u4E0B\u4E00\u56E2\u80A5\u8F6F\u7684\u7389\u961C\u5706\u5706\u9F13\u8D77\uFF0C\u8986\u7740\u4E00\u5C42\u4E4C\u4EAE\u7684\u9634\u6BDB\uFF0C\u4E0B\u9762\u96EA\u767D\u5927\u817F\u95F4\u655E\u9732\u51FA\u4E00\u53EA\u7CBE\u7F8E\u7EDD\u4F26\u7684\u7389\u6237\u3002\u4E24\u7247\u8273\u7EA2\u7684\u82B1\u74E3\u5F27\u72B6\u7FFB\u5F00\uFF0C\u6563\u53D1\u51FA\u5B9D\u77F3\u822C\u7684\u5149\u6CFD\u3002\u4E2D\u95F4\u4E00\u7247\u6843\u53F6\u72B6\u7684\u817B\u8089\u6DA6\u5982\u7EA2\u7389\uFF0C\u4E24\u7247\u67D4\u5AE9\u7684\u79D8\u8089\u534A\u906E\u534A\u63A9\uFF0C\u5C42\u6B21\u5206\u660E\uFF0C\u9690\u9690\u80FD\u770B\u5230\u4E0B\u65B9\u4E00\u53EA\u5A07\u5AE9\u7684\u7389\u7A74\u3002</p><p>\u72F1\u5352\u4EEC\u90FD\u628A\u5934\u51D1\u4E86\u8FC7\u6765\uFF0C\u4F60\u4E00\u8A00\u6211\u4E00\u8BED\u5730\u8BF4\u9053\uFF1A\u300C\u4E39\u5A18\u8FD9\u5C44\u751F\u5F97\uFF0C\u6BD4\u9EC4\u82B1\u95FA\u5973\u8FD8\u6807\u81F4\u3002\u300D</p><p>\u300C\u7EA2\u662F\u7EA2\u767D\u662F\u767D\uFF0C\u9C9C\u7075\u7075\u5AE9\u5F97\u50CF\u4E00\u6CE1\u6C34\u513F\uFF0C\u8001\u5B50\u90FD\u4E0D\u820D\u5F97\u63D2\u4E86\u3002\u300D</p><p>\u300C\u8001\u8D75\u77A7\u4F60\u8BF4\u7684\uFF0C\u518D\u6F02\u4EAE\u7684\u5C44\u8FD8\u4E0D\u662F\u8BA9\u4EBA\u63D2\u7684\u5417\uFF1F\u4E39\u5A18\uFF0C\u4F60\u7684\u5C44\u8BA9\u591A\u5C11\u4EBA\u63D2\u8FC7\uFF1F\u300D</p><p>\u4E39\u5A18\u54ED\u5F97\u8BF4\u4E0D\u51FA\u8BDD\u6765\uFF0C\u53EA\u62DA\u547D\u6447\u5934\u3002</p><p>\u5353\u5929\u96C4\u8BA9\u4EBA\u6309\u4F4F\u4E39\u5A18\u7684\u53CC\u817F\uFF0C\u817E\u51FA\u624B\u6252\u4F4F\u5979\u7684\u7389\u6237\uFF0C\u671D\u4E24\u8FB9\u5265\u5F00\uFF0C\u9732\u51FA\u5BC6\u85CF\u7684\u7F8E\u7A74\u3002\u5353\u5929\u96C4\u5439\u4E86\u53E3\u6C14\uFF0C\u7EA2\u5AE9\u8089\u7A74\u987F\u65F6\u4E00\u9635\u7FD5\u52A8\uFF0C\u6CDB\u8D77\u6EDF\u6EDF\u5149\u6CFD\uFF0C\u5353\u5929\u96C4\u54C8\u54C8\u5927\u7B11\uFF0C\u624B\u6307\u8D34\u7740\u8089\u7F1D\u4E00\u8FB9\u6478\u5F04\uFF0C\u4E00\u8FB9\u9053\uFF1A\u300C\u4E39\u5A18\uFF0C\u4F60\u8FD9\u5C44\u6709\u51E0\u4E2A\u4EBA\u63D2\u8FC7\uFF1F\u300D</p><p>\u4E39\u5A18\u53C8\u7F9E\u53C8\u75DB\uFF0C\u53EA\u662F\u54ED\u6CE3\u3002</p><p>\u5353\u5929\u96C4\u6276\u8D77\u4E86\u6012\u6DA8\u7684\u8089\u68D2\uFF0C\u9876\u4F4F\u7A74\u53E3\uFF0C\u6DEB\u7B11\u9053\uFF1A\u300C\u4F60\u8981\u4E0D\u8BF4\uFF0C\u6211\u53EF\u8981\u63D2\u8FDB\u53BB\u4E86\u3002\u300D</p><p>\u4E39\u5A18\u8EAB\u5B50\u50F5\u4E86\u7247\u523B\uFF0C\u7528\u8033\u8BED\u822C\u7684\u58F0\u97F3\u54FD\u54BD\u9053\uFF1A\u300C\u5974\u5BB6\u7684\u4E08\u592B\u2026\u2026\u300D</p><p>\u300C\u8FD8\u6709\u5417\uFF1F\u300D</p><p>\u5353\u5929\u96C4\u620F\u8C11\u5730\u633A\u4E86\u633A\u8170\u8EAB\uFF0C\u4F5C\u52BF\u6B32\u5165\u3002</p><p>\u300C\u5B59\u5929\u7FBD\uFF01\u300D\u4E39\u5A18\u5E26\u7740\u54ED\u8154\u9053\uFF1A\u300C\u8FD8\u6709\u5B59\u5929\u7FBD\u63D2\u8FC7\u2026\u2026\u300D</p><p>\u5353\u5929\u96C4\u7B11\u9053\uFF1A\u300C\u8FD8\u8BF4\u4EC0\u4E48\u4E08\u592B\u5C38\u9AA8\u672A\u5BD2\uFF0C\u539F\u6765\u521A\u5F53\u4E86\u5BE1\u5987\u5C31\u8DDF\u5B59\u5929\u7FBD\u52FE\u642D\u4E0A\u4E86\u3002\u90A3\u5C0F\u5B50\u5E72\u4E86\u4F60\u591A\u5C11\u6B21\uFF1F\u300D</p><p>\u4E39\u5A18\u54ED\u9053\uFF1A\u300C\u4E0D\u8BB0\u5F97\u4E86\u2026\u2026\u300D</p><p>\u300C\u8FD8\u6709\u522B\u4EBA\u63D2\u8FC7\u4F60\u7684\u5C44\u5417\uFF1F\u300D</p><p>\u4E39\u5A18\u62DA\u547D\u6447\u5934\uFF0C\u54C0\u6C42\u9053\uFF1A\u300C\u5974\u5BB6\u5DF2\u7ECF\u8DDF\u5929\u7FBD\u54E5\u8BF4\u597D\u4E86\u6210\u4EB2\uFF0C\u6C42\u4F60\u4EEC\u770B\u5728\u4ED6\u7684\u9762\u5B50\u653E\u8FC7\u5974\u5BB6\u5427\u3002\u4E0D\u7136\u2026\u2026\u300D</p><p>\u2026.\u6587.\u2026;</p><p>\u2026.\u4EBA.\u2026;</p><p>\u2026.\u4E66.\u2026;</p><p>\u2026.\u5C4B.\u2026;</p><p>\u2026.\u5C0F.\u2026;</p><p>\u2026.\u8BF4.\u2026;</p><p>\u2026.\u4E0B.\u2026;</p><p>\u2026.\u8F7D.\u2026;</p><p>\u2026.\u7F51.\u2026;</p>',111);function t(i,n){return a}var o=e(r,[["render",t]]);export{o as default};
