import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_25-\u51FA\u8EAB" tabindex="-1"><a class="header-anchor" href="#_25-\u51FA\u8EAB" aria-hidden="true">#</a> 25 \u51FA\u8EAB</h1><p>\u76F4\u5230\u7EA2\u65E5\u504F\u897F\uFF0C\u5B59\u5929\u7FBD\u624D\u56DE\u5927\u72F1\u3002\u521A\u8E0F\u5165\u72F1\u95E8\uFF0C\u4ED6\u7A81\u7136\u4E00\u9635\u5FC3\u60CA\u8089\u8DF3\u3002\u4ECD\u7136\u662F\u4ED6\u719F\u6089\u5F97\u4E0D\u80FD\u518D\u719F\u6089\u7684\u76D1\u72F1\uFF0C\u9634\u6697\u800C\u6F6E\u6E7F\uFF0C\u7A7A\u6C14\u4E2D\u5145\u6EE1\u4E86\u9709\u70C2\u7684\u6C14\u606F\u3002\u53EA\u662F\u73B0\u5728\u53C8\u591A\u4E86\u4E00\u80A1\u6D53\u91CD\u7CBE\u6DB2\u7684\u8165\u6C14\u2026\u2026\u4E00\u79CD\u4E0D\u7965\u7684\u9884\u611F\u4ECE\u5FC3\u5E95\u5347\uFF0C\u5B59\u5929\u7FBD\u63E1\u7D27\u62F3\u5934\uFF0C\u6162\u6162\u8E0F\u5165\u9ED1\u72F1\u3002</p><p>\u7089\u706B\u95EA\u52A8\u7740\u5E7D\u6697\u7684\u7EA2\u5149\uFF0C\u9EDD\u9ED1\u7684\u94C1\u5236\u67B7\u5E8A\u4E0A\uFF0C\u6254\u7740\u4E00\u5177\u767D\u82B1\u82B1\u7684\u8089\u4F53\u3002\u4E39\u5A18\u8EAB\u65E0\u5BF8\u7F15\uFF0C\u4E00\u817F\u88AB\u94C1\u73AF\u5361\u4F4F\u5411\u4E0A\u7FD8\u8D77\uFF0C\u4E00\u817F\u5782\u5728\u5E8A\u4FA7\uFF0C\u6D53\u6D4A\u7684\u7CBE\u6DB2\u987A\u7740\u5C0F\u817F\u6DCC\u5230\u811A\u5C16\uFF0C\u60AC\u7A7A\u62C9\u51FA\u4E00\u6761\u660E\u6643\u6643\u7684\u9ECF\u4E1D\uFF0C\u5728\u5730\u4E0A\u6C47\u6210\u4E00\u6EE9\u3002</p><p>\u5979\u4E24\u624B\u538B\u5728\u8170\u540E\uFF0C\u8179\u90E8\u5FAE\u5FAE\u62AC\u8D77\uFF0C\u79D8\u5904\u655E\u9732\uFF0C\u5F77\u4F5B\u88AB\u4EBA\u7528\u5668\u5177\u6345\u8FC7\u822C\u51CC\u4E71\u4E0D\u582A\uFF0C\u91CC\u9762\u8FD8\u6076\u4F5C\u5267\u5730\u585E\u7740\u4E00\u622A\u6CB9\u9ED1\u7684\u7269\u4F53\u3002\u7CBE\u6DB2\u4E0D\u4F46\u4ECE\u585E\u7740\u5F02\u7269\u7684\u7A74\u5185\u6EA2\u51FA\uFF0C\u4E39\u5A18\u53D1\u4E0A\u3001\u8138\u4E0A\u3001\u5507\u4E0A\u3001\u4E73\u4E0A\u3001\u8170\u80A2\u3001\u8179\u4E0A\u2026\u2026\u6DCB\u6DCB\u6F13\u6F13\u6CBE\u6EE1\u6D53\u7CBE\uFF0C\u6574\u5177\u8EAB\u4F53\u5F77\u4F5B\u88AB\u7CBE\u6DB2\u6CE1\u8FC7\u822C\u6563\u53D1\u7740\u6D53\u6D53\u7684\u8165\u6C14\u3002</p><p>\u5979\u95ED\u7740\u773C\uFF0C\u9690\u85CF\u5728\u9ED1\u6697\u4E0B\u7684\u7389\u8138\u8F6F\u8F6F\u4FA7\u5728\u4E00\u65C1\uFF0C\u65E0\u7455\u7684\u7389\u8138\u6CBE\u6EE1\u6D46\u6C41\u72B6\u7684\u9ECF\u6DB2\uFF0C\u5374\u4F9D\u7136\u63A9\u4E0D\u4F4F\u5979\u6EE1\u8138\u7684\u54C0\u5A49\u548C\u88AB\u8E42\u8E8F\u540E\u7684\u51C4\u8273\u3002</p><p>\u7A7A\u8361\u8361\u7684\u5927\u72F1\u5F77\u4F5B\u4ECD\u56DE\u54CD\u7740\u7537\u4EBA\u4EEC\u7684\u6DEB\u7B11\u58F0\uFF0C\u4ED6\u4EEC\u4ECE\u4E39\u5A18\u6E7F\u6F09\u6F09\u7684\u4E0B\u4F53\u62D4\u51FA\u9633\u5177\uFF0C\u671D\u5979\u8EAB\u4E0A\u6063\u610F\u55B7\u6D12\u51CC\u8FB1\u3002\u5B59\u5929\u7FBD\u4E0B\u5DF4\u54AC\u808C\u9F13\u8D77\uFF0C\u5B64\u72FC\u822C\u7684\u76EE\u5149\u4E0D\u4F4F\u95EA\u70C1\uFF0C\u4ECE\u4E39\u5A18\u8138\u5E9E\u3001\u67D4\u9888\u3001\u6EE1\u662F\u54AC\u75D5\u7684\u4E73\u623F\u3001\u8170\u8179\u2026\u2026\u4E00\u8DEF\u5411\u4E0B\uFF0C\u5F53\u770B\u5230\u7389\u961C\u4E0A\u90A3\u4E2A\u6241\u5706\u7684\u5370\u8FF9\uFF0C\u5B59\u5929\u7FBD\u76EE\u5149\u970D\u7136\u4E00\u8DF3\u3002</p><p>\u539F\u672C\u80A5\u5706\u9F13\u80C0\u7684\u7389\u961C\u50CF\u662F\u88AB\u65E0\u5F62\u7684\u9510\u5668\u6309\u5F97\u51F9\u4E0B\uFF0C\u4E00\u53EA\u9ED1\u7EA2\u76F8\u95F4\u7684\u75A4\u75D5\u6DF1\u6DF1\u70D9\u5165\u808C\u80A4\uFF0C\u886C\u7740\u660E\u51C0\u5149\u6ED1\u7684\u7F8E\u8089\uFF0C\u4E24\u4E2A\u8840\u8089\u4EA4\u7EC7\u800C\u6210\u7684\u300C\u6DEB\u5987\u300D\uFF0C\u89E6\u76EE\u60CA\u5FC3\u3002</p><p>\u5B59\u5929\u7FBD\u7A81\u7136\u540E\u8DC3\uFF0C\u62AC\u8098\u671D\u540E\u51FB\u53BB\u3002\u4E00\u53EA\u5927\u624B\u84E6\u5730\u4F38\u6765\uFF0C\u5728\u4ED6\u8098\u4E0B\u4E00\u6258\uFF0C\u5316\u53BB\u529B\u9053\uFF0C\u65CB\u5373\u5411\u4E0B\u5C01\u683C\uFF0C\u6321\u4F4F\u5B59\u5929\u7FBD\u65E0\u5F71\u65E0\u8E2A\u7684\u4E00\u811A\u3002</p><p>\u9877\u523B\u95F4\u4E24\u4EBA\u4FBF\u4EA4\u4E86\u4E09\u62DB\uFF0C\u90A3\u4EBA\u6A2A\u81C2\u67B6\u4F4F\u5B59\u5929\u7FBD\u7684\u62F3\u950B\uFF0C\u501F\u52BF\u8DC3\u5F00\uFF0C\u60CA\u7591\u4E0D\u5B9A\u5730\u671B\u7740\u4ED6\u3002</p><p>\u300C\u5353\u4E8C\u54E5\uFF0C\u662F\u4F60\u3002\u300D\u5B59\u5929\u7FBD\u6536\u56DE\u624B\uFF0C\u6DE1\u6DE1\u8BF4\u9053\u3002</p><p>\u5353\u5929\u96C4\u770B\u7740\u4ED6\uFF0C\u6CA1\u6709\u7B54\u8BDD\u3002\u5B59\u5929\u7FBD\u7684\u529F\u592B\u867D\u8FC7\u5F97\u53BB\uFF0C\u4F46\u4E5F\u7B97\u4E0D\u5F97\u4EC0\u4E48\u597D\u624B\uFF0C\u4F46\u521A\u624D\u8FD9\u51E0\u62DB\u52BF\u5927\u529B\u6C89\uFF0C\u8F83\u4E4B\u81EA\u5DF1\u4E5F\u4E0D\u8D8B\u591A\u8BA9\uFF0C\u96BE\u9053\u4ED6\u4E00\u76F4\u9690\u85CF\u4E86\u5B9E\u529B\uFF1F\u8FD8\u662F\u8FD9\u4E00\u4E24\u4E2A\u6708\u95F4\u7A81\u98DE\u731B\u8FDB\uFF1F</p><p>\u300C\u8FD9\u2026\u2026\u662F\u600E\u4E48\u56DE\u4E8B\uFF1F\u300D</p><p>\u5353\u5929\u96C4\u770B\u7740\u4ED6\u7684\u795E\u60C5\u6162\u6162\u9053\uFF1A\u300C\u8FD9\u53EF\u6028\u4E0D\u5F97\u6211\u3002\u662F\u5979\u81EA\u5DF1\u4E71\u558A\uFF0C\u8BA9\u9C8D\u6A2A\u4ED6\u4EEC\u649E\u4E0A\u4E86\u3002\u8FD9\u4E8B\u513F\uFF0C\u6211\u600E\u4E48\u597D\u72EC\u541E\u5462\uFF1F\u300D</p><p>\u300C\u53EA\u6709\u9C8D\u6A2A\u4ED6\u4EEC\uFF1F\u300D\u770B\u7740\u4E39\u5A18\u8EAB\u4E0A\u7684\u7CBE\u6DB2\uFF0C\u5B59\u5929\u7FBD\u663E\u7136\u4E0D\u4FE1\u3002</p><p>\u5353\u5929\u96C4\u7768\u4E86\u4E39\u5A18\u4E00\u773C\uFF0C\u300C\u9C8D\u6A2A\u88AB\u5979\u54AC\u4E86\u4E00\u53E3\uFF0C\u5FC3\u91CC\u6709\u6C14\uFF0C\u53D1\u72E0\u5E72\u4E86\u4E24\u8F6E\uFF0C\u53C8\u6311\u4E86\u51E0\u4E2A\u56DA\u72AF\u6765\u5978\u5979\u3002\u90A3\u4E24\u4E2A\u5B57\uFF0C\u4E5F\u662F\u4ED6\u4EB2\u624B\u70D9\u4E0A\u53BB\u7684\u3002\u300D</p><p>\u4E39\u5A18\u901A\u4F53\u51B0\u51B7\uFF0C\u6C14\u606F\u5FAE\u5F31\u5F97\u5F77\u4F5B\u968F\u65F6\u90FD\u4F1A\u65AD\u7EDD\u3002\u5B59\u5929\u7FBD\u6273\u5F00\u673A\u62EC\uFF0C\u89E3\u5F00\u5979\u7684\u624B\u811A\uFF0C\u7136\u540E\u8131\u4E0B\u5916\u8863\uFF0C\u76D6\u4F4F\u4E39\u5A18\u6E7F\u9ECF\u7684\u8EAB\u5B50\uFF0C\u4E00\u624B\u4F38\u5230\u5979\u8179\u4E0B\uFF0C\u8F7B\u8F7B\u4E00\u62D4\u3002\u4E00\u80A1\u9ECF\u7CBE\u987A\u52BF\u6D41\u51FA\uFF0C\u6253\u6E7F\u4E86\u4ED6\u7684\u624B\u8155\u3002</p><p>\u90A3\u662F\u4E2A\u5947\u602A\u7684\u7269\u4F53\uFF0C\u8272\u6CFD\u6CB9\u9ED1\u53D1\u7EA2\uFF0C\u72B9\u5982\u4E00\u622A\u814A\u8089\uFF0C\u6B64\u65F6\u5438\u9971\u4E86\u6C34\uFF0C\u4F53\u79EF\u66F4\u5927\u4E86\u8BB8\u591A\u3002\u770B\u5F62\u72B6\uFF0C\u7ADF\u4F3C\u2026\u2026</p><p>\u5B59\u5929\u7FBD\u731B\u7136\u60F3\u4E86\u8D77\u6765\uFF0C\u9C8D\u6A2A\u542C\u4E86\u4E00\u4E2A\u58EE\u9633\u7684\u504F\u65B9\uFF0C\u8D81\u767D\u5B5D\u5112\u4E0B\u846C\u7684\u65F6\u5019\u5272\u4E86\u4ED6\u7684\u9633\u5177\uFF0C\u814A\u8FC7\u4E86\u51C6\u5907\u4E0B\u9152\uFF0C\u6CA1\u60F3\u5230\u5374\u7528\u5728\u4E86\u8FD9\u91CC\u3002\u4ED6\u770B\u4E86\u4E39\u5A18\u4E00\u773C\uFF0C\u5047\u5982\u5979\u5F53\u65F6\u8FD8\u9192\u7740\uFF0C\u88AB\u4E08\u592B\u4E00\u622A\u6B8B\u7559\u7684\u8089\u5757\u63D2\u5165\uFF0C\u4E5F\u8BB8\u4F1A\u75AF\u6389\u3002</p><p>\u5353\u5929\u96C4\u9053\uFF1A\u300C\u5929\u7FBD\uFF0C\u8FD9\u6848\u5B50\u82E5\u662F\u63A8\u5012\uFF0C\u54B1\u4EEC\u8C01\u90FD\u8131\u4E0D\u5E72\u7CFB\uFF0C\u82E5\u662F\u5750\u5B9E\uFF0C\u5979\u80AF\u5B9A\u662F\u8981\u5B98\u5356\u4E3A\u5A3C\uFF0C\u4F60\u53EF\u8981\u60F3\u6E05\u695A\u4E86\uFF0C\u4E3A\u4E86\u4E00\u4E2A\u5A4A\u5B50\uFF0C\u7A76\u7ADF\u503C\u4E0D\u503C\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u62E5\u7740\u4E39\u5A18\u8F6F\u7EF5\u7EF5\u7684\u8EAB\u5B50\uFF0C\u5FFD\u7136\u4E00\u7B11\uFF0C\u300C\u5353\u4E8C\u54E5\u591A\u8651\u4E86\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6E29\u6C34\u704C\u5165\u53E3\u4E2D\uFF0C\u7247\u523B\u540E\u6D41\u51FA\u7684\u5374\u662F\u6D53\u7CBE\u3002\u5B59\u5929\u7FBD\u62B9\u53BB\u4E39\u5A18\u5507\u89D2\u7684\u9ECF\u6DB2\uFF0C\u7814\u4E86\u4E00\u7C92\u5B89\u795E\u7684\u4E39\u836F\uFF0C\u5316\u5F00\u5582\u5979\u670D\u4E0B\u3002</p><p>\u56DE\u5230\u674F\u82B1\u6751\u5DF2\u7ECF\u662F\u5165\u591C\u65F6\u5206\uFF0C\u5B59\u5929\u7FBD\u53EA\u8BF4\u4E39\u5A18\u8DEF\u4E0A\u4E0D\u5C0F\u5FC3\u8DCC\u4E86\u4E00\u8DE4\uFF0C\u660F\u4E86\u8FC7\u53BB\uFF0C\u8EAB\u5B50\u5E76\u65E0\u5927\u788D\u3002</p><p>\u6253\u53D1\u4E86\u7389\u83B2\uFF0C\u4ED6\u95E9\u4E0A\u95E8\uFF0C\u5E2E\u4E39\u5A18\u6D17\u4E86\u8EAB\u5B50\uFF0C\u7136\u540E\u7528\u88AB\u5B50\u76D6\u4F4F\uFF0C\u81EA\u5DF1\u548C\u8863\u8EBA\u5728\u65C1\u8FB9\uFF0C\u4E45\u4E45\u51DD\u89C6\u7740\u660F\u8FF7\u4E2D\u7684\u7389\u4EBA\uFF0C\u76F4\u5230\u7761\u53BB\u3002</p><p>\u534A\u591C\uFF0C\u5B59\u5929\u7FBD\u4F38\u4E86\u4F38\u624B\u81C2\uFF0C\u4E60\u60EF\u6027\u5730\u60F3\u6402\u4E39\u5A18\uFF0C\u5374\u6402\u4E86\u4E2A\u7A7A\u3002\u4ED6\u60CA\u9192\u8FC7\u6765\uFF0C\u53EA\u89C9\u6795\u4E0A\u6E7F\u6E7F\u7684\uFF0C\u5C3D\u662F\u6CEA\u75D5\u3002\u5E8A\u540E\u9690\u9690\u4F20\u6765\u6C34\u58F0\uFF0C\u5B59\u5929\u7FBD\u8DBF\u4E86\u978B\uFF0C\u8D77\u8EAB\u53BB\u770B\u3002</p><p>\u4E39\u5A18\u8DEA\u5728\u5730\u4E0A\uFF0C\u6DE1\u6DE1\u7684\u6708\u5149\u6D12\u843D\uFF0C\u8D64\u88F8\u7684\u80F4\u4F53\u7B3C\u7F69\u7740\u6726\u80E7\u7684\u94F6\u8F89\u3002\u5979\u62FF\u7740\u624B\u5DFE\u5728\u8179\u4E0B\u64E6\u62ED\u7740\uFF0C\u542C\u5230\u58F0\u97F3\uFF0C\u5979\u8F6C\u8FC7\u8138\uFF0C\u5BF9\u5B59\u5929\u7FBD\u51C4\u51C9\u5730\u7B11\u4E86\u7B11\uFF0C\u6709\u4E9B\u60CA\u8BB6\uFF0C\u6709\u4E9B\u4E0D\u89E3\uFF0C\u53C8\u6709\u4E9B\u5931\u671B\u5730\u8BF4\uFF1A\u300C\u6D17\u4E0D\u6389\u4E86\u2026\u2026\u300D\u7136\u540E\u65E0\u529B\u5730\u5012\u5728\u5730\u4E0A\u3002</p><p>\u4E39\u5A18\u5728\u660F\u8FF7\u4E2D\u4E0D\u505C\u5730\u54ED\u6CE3\u3001\u4E5E\u6C42\uFF0C\u4E0D\u65F6\u53D1\u51FA\u4E86\u557C\u8840\u822C\u60B2\u9E23\uFF0C\u54C0\u54C0\u5730\u547C\u5524\u7740\u5929\u7FBD\u3002\u88AB\u4EBA\u8F6E\u5978\u7684\u906D\u9047\uFF0C\u72B9\u5982\u4E00\u573A\u6325\u4E4B\u4E0D\u53BB\u7684\u68A6\u5669\uFF0C\u4F7F\u5979\u65E0\u6CD5\u9762\u5BF9\u3002\u5B59\u5929\u7FBD\u72B9\u8C6B\u4E86\u4E00\u4F1A\u513F\uFF0C\u5C55\u81C2\u628A\u5979\u6402\u5728\u6000\u4E2D\uFF0C\u4E39\u5A18\u7D27\u7D27\u6293\u4F4F\u4ED6\u7684\u8863\u895F\uFF0C\u5C0F\u5973\u5B69\u513F\u822C\u6CE3\u9053\uFF1A\u300C\u5929\u7FBD\u54E5\uFF0C\u4ED6\u4EEC\u6B3A\u8D1F\u6211\u2026\u2026\u300D</p><p>\u5979\u54ED\u9053\uFF1A\u300C\u6211\u5AC1\u4E0D\u5F97\u4E86\u2026\u2026\u300D</p><p>\u5B59\u5929\u7FBD\u60F3\u7B11\uFF0C\u7B11\u5BB9\u5374\u50F5\u5728\u8138\u4E0A\u3002\u4ED6\u4EE5\u4E3A\u81EA\u5DF1\u4F1A\u5F97\u610F\u3002\u4F46\u4ED6\u6CA1\u6709\u3002</p><p>\u54C0\u5A49\u7684\u4E00\u591C\u7EC8\u4E8E\u8FC7\u53BB\u3002\u518D\u9192\u6765\u65F6\uFF0C\u5929\u8272\u5DF2\u7ECF\u5927\u4EAE\u3002\u5B59\u5929\u7FBD\u7741\u5F00\u773C\uFF0C\u53EA\u89C1\u4E39\u5A18\u6728\u7136\u671B\u7740\u5E10\u9876\uFF0C\u539F\u672C\u660E\u51C0\u7684\u7738\u5B50\u4E00\u7247\u82E6\u6DA9\uFF0C\u8138\u8272\u82CD\u767D\u5F97\u8BA9\u4EBA\u5FC3\u75BC\u3002</p><p>\u300C\u4F60\u9192\u4E86\u3002\u300D</p><p>\u4E39\u5A18\u5F2F\u957F\u776B\u6BDB\u6162\u6162\u5408\u4E0A\uFF0C\u773C\u89D2\u6ED1\u4E0B\u4E00\u6EF4\u6CEA\u73E0\u3002\u90A3\u79CD\u51C4\u695A\u7684\u795E\u60C5\uFF0C\u522B\u6709\u4E00\u756A\u52A8\u4EBA\u8273\u6001\u3002</p><p>\u5B59\u5929\u7FBD\u7981\u4E0D\u4F4F\u4F38\u624B\u6ED1\u5165\u88AB\u4E2D\uFF0C\u671D\u5979\u8EAB\u4E0A\u6478\u53BB\u3002\u82E5\u5728\u5E73\u65F6\uFF0C\u4E39\u5A18\u603B\u4F1A\u67D4\u987A\u5730\u644A\u5F00\u8EAB\u4F53\uFF0C\u4EFB\u4ED6\u629A\u5F04\u3002\u6B64\u523B\u5979\u5374\u50CF\u53D7\u60CA\u7684\u5C0F\u9E1F\uFF0C\u8EAB\u5B50\u8737\u6210\u4E00\u56E2\uFF0C\u7D27\u7D27\u62C9\u4F4F\u88AB\u5B50\uFF0C\u4E0D\u8BA9\u4ED6\u78B0\u89E6\u3002</p><p>\u300C\u600E\u4E48\u4E86\uFF1F\u300D\u5B59\u5929\u7FBD\u5FAE\u7B11\u9053\uFF1A\u300C\u4E0B\u9762\u8FD8\u75DB\u5417\uFF1F\u300D</p><p>\u4E39\u5A18\u54AC\u4F4F\u5634\u5507\uFF0C\u8EAB\u5B50\u745F\u745F\u53D1\u6296\uFF0C\u5979\u4F3C\u4E4E\u4E0D\u8BB0\u5F97\u6628\u665A\u81EA\u5DF1\u66FE\u8D77\u6765\uFF0C\u534A\u664C\u624D\u4F4E\u58F0\u9053\uFF1A\u300C\u8EAB\u5B50\u810F\u2026\u2026\u8981\u6D17\u6D17\u2026\u2026\u300D</p><p>\u300C\u597D\u554A\uFF0C\u6211\u6765\u5E2E\u4F60\u3002\u300D</p><p>\u300C\u4E0D\uFF01\u300D\u4E39\u5A18\u58F0\u97F3\u5927\u5F97\u5413\u4E86\u81EA\u5DF1\u4E00\u8DF3\uFF0C\u5979\u52C9\u5F3A\u52A8\u4E86\u52A8\u5507\u89D2\uFF0C\u60F3\u9732\u51FA\u4E2A\u7B11\u5BB9\uFF0C\u5374\u6CA1\u6709\u6210\u529F\uFF0C\u6700\u540E\u98A4\u58F0\u8BF4\uFF1A\u300C\u5929\u7FBD\u54E5\uFF0C\u4F60\u5728\u5916\u9762\u7B49\u4E00\u4F1A\u513F\u2026\u2026\u6211\u81EA\u5DF1\u6D17\u2026\u2026\u300D</p><p>\u300C\u597D\u3002\u300D\u5B59\u5929\u7FBD\u7B11\u541F\u541F\u8BF4\u3002</p><p>\u51FA\u4E86\u95E8\uFF0C\u8138\u4E0A\u7684\u7B11\u5BB9\u987F\u65F6\u57AE\u4E86\u4E0B\u6765\u3002\u4ED6\u8D70\u5230\u540E\u9662\uFF0C\u4ECE\u4E95\u91CC\u6C72\u4E86\u6876\u6C34\uFF0C\u4E00\u5934\u624E\u8FDB\u91CC\u9762\u3002\u51B0\u51B7\u7684\u4E95\u6C34\u4F7F\u53D1\u6DA8\u7684\u8111\u5B50\u6E05\u9192\u4E86\u4E00\u4E9B\u3002</p><p>\u505A\u90FD\u505A\u4E86\uFF0C\u8FD8\u6709\u4EC0\u4E48\u597D\u540E\u6094\u7684\u5462\uFF1F\u96BE\u4E0D\u6210\u81EA\u5DF1\u771F\u8981\u5A36\u4E00\u4E2A\u9006\u532A\u7684\u5B40\u5987\uFF1F\u5979\u8FDF\u65E9\u662F\u8981\u5F53\u5A4A\u5B50\uFF0C\u65E9\u4E00\u65E5\u665A\u4E00\u65E5\u6709\u4EC0\u4E48\u5173\u7CFB\uFF1F</p><p>\u5B59\u5929\u7FBD\u62AC\u8D77\u5934\uFF0C\u6210\u4E32\u7684\u6C34\u73E0\u6389\u5728\u6C34\u91CC\uFF0C\u6E85\u8D77\u9053\u9053\u6D9F\u6F2A\uFF0C\u770B\u7740\u6C34\u4E0A\u6643\u52A8\u53D8\u5F62\u7684\u5F71\u5B50\uFF0C\u4ED6\u54AC\u7259\u4E00\u7B11\uFF0C\u5FC3\u9053\uFF1A\u300C\u5927\u4E08\u592B\u5FC3\u72E0\u624B\u8FA3\uFF0C\u5B59\u5929\u7FBD\u554A\u5B59\u5929\u7FBD\uFF0C\u4E00\u4E2A\u5A4A\u5B50\u4F60\u90FD\u7529\u4E0D\u8131\uFF0C\u8FD8\u60F3\u505A\u4EC0\u4E48\u5927\u4E8B\uFF1F\u300D</p><p>\u5B59\u5929\u7FBD\u7CBE\u8D64\u7740\u4E0A\u8EAB\u7528\u51C9\u6C34\u51B2\u4E86\u4E00\u906D\uFF0C\u6700\u540E\u72E0\u72E0\u7529\u4E86\u7529\u5934\uFF0C\u4E00\u8FB9\u64E6\u8138\u4E00\u8FB9\u56DE\u5230\u5BA2\u6808\u3002\u9694\u7740\u95E8\u542C\u53BB\uFF0C\u5374\u6CA1\u6709\u6C34\u58F0\uFF0C\u4ED6\u62CD\u4E86\u62CD\u95E8\uFF0C\u5524\u9053\uFF1A\u300C\u4E39\u5A18\uFF0C\u6D17\u597D\u4E86\u5417\uFF1F\u300D</p><p>\u91CC\u9762\u6CA1\u6709\u56DE\u7B54\uFF0C\u7A7A\u6D1E\u6D1E\u7684\u9759\u8C27\u3002\u5B59\u5929\u7FBD\u5FC3\u5934\u4E00\u7D27\uFF0C\u7ACB\u5373\u659C\u80A9\u649E\u53BB\u3002\u95E8\u95E9\u683C\u7684\u4E00\u58F0\u65AD\u5F00\uFF0C\u5B59\u5929\u7FBD\u987F\u65F6\u8138\u8272\u5927\u53D8\u3002</p><p>\u5C4B\u6881\u4E0A\u5782\u7740\u4E00\u6761\u767D\u7EEB\uFF0C\u4E39\u5A18\u521A\u6D17\u4E86\u8EAB\u5B50\uFF0C\u53D1\u68A2\u8FD8\u6E7F\u6F09\u6F09\u6EF4\u7740\u6C34\uFF0C\u8EAB\u4E0A\u62AB\u7740\u4EF6\u888D\u5B50\uFF0C\u53C8\u5BBD\u53C8\u5927\uFF0C\u5374\u662F\u5B59\u5929\u7FBD\u7684\u5916\u8863\u3002\u4E24\u6761\u767D\u751F\u751F\u7684\u5C0F\u817F\u4ECE\u957F\u888D\u5E95\u7F18\u9732\u51FA\uFF0C\u5BC2\u7136\u60AC\u5728\u534A\u7A7A\u3002</p><p>\u5B59\u5929\u7FBD\u7EB5\u8EAB\u62C9\u65AD\u767D\u7EEB\uFF0C\u5C55\u81C2\u63FD\u4F4F\u4E39\u5A18\u7684\u8170\u8EAB\uFF0C\u5C06\u5979\u653E\u5728\u5E8A\u4E0A\uFF0C\u4E00\u624B\u6309\u4F4F\u5979\u7684\u80CC\u5FC3\uFF0C\u4E00\u624B\u5728\u5979\u80F8\u53E3\u8BF8\u5904\u8981\u7A74\u8FC5\u901F\u70B9\u8FC7\u3002</p><p>\u5B59\u5929\u7FBD\u529F\u529B\u4E0D\u8DB3\uFF0C\u52B2\u6C14\u5165\u4F53\u53EA\u6CBF\u7ECF\u8109\u8D70\u4E86\u6570\u5BF8\u4FBF\u5316\u4E3A\u4E4C\u6709\u3002\u7247\u523B\u540E\u4ED6\u989D\u5934\u5DF2\u7136\u89C1\u6C57\uFF0C\u72B9\u8C6B\u7740\u8981\u4E0D\u8981\u4F7F\u51FA\u6E21\u6C14\u7684\u6CD5\u5B50\u3002\u4F46\u8FD9\u6837\u4E00\u6765\uFF0C\u4ED6\u90A3\u70B9\u597D\u4E0D\u5BB9\u6613\u7EC3\u6210\u7684\u771F\u6C14\u4E0D\u514D\u8981\u4ED8\u8BF8\u4E1C\u6D41\uFF0C\u800C\u4E14\u2026\u2026\u4E5F\u4E0D\u89C1\u5F97\u5C31\u80FD\u6551\u4E0B\u4E39\u5A18\u2026\u2026</p><p>\u5FFD\u7136\u4E39\u5A18\u6E29\u51C9\u7684\u808C\u80A4\u5FAE\u5FAE\u4E00\u9707\uFF0C\u8155\u4E0A\u6709\u4E86\u8109\u52A8\u3002\u5B59\u5929\u7FBD\u5927\u559C\u8FC7\u671B\uFF0C\u8FDE\u5FD9\u5E2E\u5979\u884C\u6C14\u5BFC\u5F15\uFF0C\u63A8\u8840\u8FC7\u5BAB\u3002</p><p>\u4E39\u5A18\u60A0\u60A0\u9192\u8F6C\uFF0C\u770B\u5230\u5B59\u5929\u7FBD\u6EE1\u5934\u662F\u6C57\uFF0C\u4E24\u773C\u53D1\u7EA2\uFF0C\u5979\u5FAE\u5FAE\u4E00\u6014\uFF0C\u7136\u540E\u4F0F\u5E8A\u568E\u5555\u75DB\u54ED\u8D77\u6765\u3002\u5B59\u5929\u7FBD\u5410\u6C14\u6536\u529F\uFF0C\u63A5\u7740\u865A\u8131\u822C\u5927\u53E3\u5927\u53E3\u5598\u8D77\u6C14\u6765\uFF0C\u51B7\u6C57\u6EDA\u6EDA\u800C\u4E0B\u3002</p><p>\u516D\u6247\u95E8\u5C3D\u6709\u9AD8\u624B\uFF0C\u4EAC\u5E08\u5929\u7262\u51E0\u540D\u523D\u5B50\u624B\u5404\u6000\u7EDD\u6280\uFF0C\u884C\u5211\u65F6\u4E00\u80A1\u771F\u6C14\u62A4\u4F4F\u72AF\u4EBA\u7684\u5FC3\u8109\uFF0C\u76F4\u5230\u56DB\u80A2\u808C\u8089\u5254\u5C3D\u72AF\u4EBA\u8FD8\u5728\u6D3B\u7740\u3002\u53EF\u4ED6\u53EA\u4E0D\u8FC7\u7528\u4E86\u4E00\u67F1\u9999\u7684\u65F6\u95F4\uFF0C\u5C31\u9669\u4E9B\u8017\u5C3D\u771F\u5143\uFF0C\u5B59\u5929\u7FBD\u5598\u7740\u6C14\u60F3\uFF0C\u82E5\u662F\u6709\u767D\u96EA\u83B2\u7684\u529F\u592B\uFF0C\u4E5F\u4E0D\u4F1A\u8FD9\u822C\u5403\u529B\u4E86\u3002</p><p>\u300C\u597D\u4E86\u597D\u4E86\uFF0C\u674F\u513F\uFF0C\u4E0D\u8981\u54ED\u4E86\u3002\u300D\u5B59\u5929\u7FBD\u529D\u6170\u9053\u3002</p><p>\u4E39\u5A18\u54ED\u9053\uFF1A\u300C\u6211\u88AB\u4EBA\u6C61\u4E86\u8EAB\u5B50\uFF0C\u4E22\u4E86\u4F60\u7684\u8138\uFF0C\u6211\u4E0D\u8981\u6D3B\u4E86\u2026\u2026\u300D</p><p>\u5B59\u5929\u7FBD\u4ECE\u540E\u62E5\u7740\u5979\u80A9\u8180\uFF0C\u7B11\u7740\u8BF4\u9053\uFF1A\u300C\u8EAB\u4E0A\u597D\u7AEF\u7AEF\u7684\uFF0C\u53C8\u6CA1\u5C11\u5757\u8089\uFF0C\u6709\u4EC0\u4E48\u6253\u7D27\u7684\uFF1F\u300D</p><p>\u4E39\u5A18\u54ED\u9053\uFF1A\u300C\u674F\u513F\u8EAB\u5B50\u810F\u4E86\u2026\u2026\u300D</p><p>\u300C\u810F\u4E86\u5417\uFF1F\u300D\u5B59\u5929\u7FBD\u628A\u8138\u57CB\u5728\u4E39\u5A18\u53D1\u95F4\uFF0C\u6DF1\u6DF1\u5438\u4E86\u4E00\u53E3\uFF0C\u7B11\u9053\uFF1A\u300C\u521A\u6D17\u8FC7\u5C31\u9999\u55B7\u55B7\u7684\uFF0C\u54EA\u91CC\u4E0D\u5E72\u51C0\u4E86\uFF1F\u300D</p><p>\u300C\u674F\u513F\u88AB\u2026\u2026\u4ED6\u4EEC\u597D\u591A\u4EBA\uFF0C\u674F\u513F\u8EAB\u5B50\u90FD\u810F\u900F\u4E86\u2026\u2026\u300D</p><p>\u300C\u4F60\u8BF4\u8FD9\u91CC\uFF1F\u300D</p><p>\u5B59\u5929\u7FBD\u7684\u624B\u638C\u6ED1\u5230\u888D\u4E0B\uFF0C\u4E0D\u987E\u4E39\u5A18\u7684\u6323\u624E\uFF0C\u5F3A\u884C\u63A2\u5165\u81C0\u7F1D\uFF0C\u6345\u8FDB\u90A3\u7247\u67D4\u817B\u4E4B\u4E2D\u3002\u4E39\u5A18\u54ED\u7740\u5E76\u7D27\u53CC\u817F\uFF0C\u5C41\u80A1\u626D\u52A8\u7740\u4E0D\u613F\u4ED6\u78B0\u89E6\u81EA\u5DF1\u88AB\u73B7\u6C61\u7684\u8089\u4F53\u3002</p><p>\u7B97\u8D77\u6765\u5DEE\u4E0D\u591A\u4E00\u6574\u5929\u6CA1\u6CBE\u8FC7\u4E39\u5A18\u7684\u8EAB\u5B50\uFF0C\u6478\u7740\u5979\u6ED1\u5AE9\u7684\u871C\u7A74\uFF0C\u5B59\u5929\u7FBD\u4E0D\u7531\u98DF\u6307\u5927\u52A8\uFF0C\u62AC\u624B\u5C06\u957F\u888D\u64A9\u5230\u8170\u95F4\uFF0C\u7136\u540E\u62B1\u4F4F\u4E39\u5A18\u7684\u96EA\u81C0\u5411\u5916\u4E00\u5206\uFF0C\u4F38\u51FA\u4E2D\u6307\u52FE\u4F4F\u871C\u7A74\u8FB9\u7F18\uFF0C\u7528\u529B\u62C9\u5F00\uFF0C\u4E00\u8FB9\u89C2\u8D4F\u4E39\u5A18\u7F8E\u7A74\u7EA2\u5AE9\u8FF7\u4EBA\u7684\u8273\u666F\uFF0C\u4E00\u8FB9\u8C03\u7B11\u9053\uFF1A\u300C\u810F\u4E1C\u897F\u5728\u54EA\u513F\u5462\uFF1F\u300D</p><p>\u4E39\u5A18\u6B63\u6EE1\u5FC3\u60B2\u82E6\uFF0C\u75DB\u4E0D\u6B32\u751F\uFF0C\u60C5\u90CE\u975E\u4F46\u4E0D\u52A0\u4F53\u8C05\u5B89\u6170\uFF0C\u8FD8\u5BF9\u5979\u5982\u6B64\u72CE\u73A9\uFF0C\u7981\u4E0D\u4F4F\u653E\u58F0\u5927\u54ED\u3002</p><p>\u5B59\u5929\u7FBD\u8D39\u4E86\u4E5D\u725B\u4E8C\u864E\u4E4B\u529B\u624D\u6551\u4E0B\u4E39\u5A18\u7684\u6027\u547D\uFF0C\u5979\u82E5\u6653\u4E8B\uFF0C\u77E5\u9053\u81EA\u5DF1\u8FD8\u6CA1\u73A9\u591F\u5979\u7684\u8EAB\u5B50\uFF0C\u5C31\u8BE5\u4E56\u4E56\u62AC\u8D77\u5C41\u80A1\uFF0C\u8BA9\u81EA\u5DF1\u5148\u63D2\u4E2A\u9AD8\u5174\uFF0C\u518D\u54C4\u5F97\u5979\u5F00\u5FC3\u3002\u53EF\u4E39\u5A18\u2026\u2026</p><p>\u300C\u54ED\uFF01\u5C31\u77E5\u9053\u54ED\uFF01\u300D</p><p>\u5B59\u5929\u7FBD\u607C\u5C06\u4E0A\u6765\uFF0C\u4E00\u5DF4\u638C\u6325\u5728\u4E39\u5A18\u4E30\u7FD8\u7684\u5706\u81C0\u4E0A\u3002\u4E39\u5A18\u7684\u5C41\u80A1\u987A\u4E0D\u6E9C\u624B\uFF0C\u624B\u638C\u6325\u4E0B\uFF0C\u300C\u556A\u300D\u7684\u4E00\u58F0\u8106\u54CD\uFF0C\u767D\u4EAE\u7684\u81C0\u8089\u4E00\u9635\u4E71\u98A4\uFF0C\u72B9\u5982\u4E00\u56E2\u5F39\u6027\u5341\u8DB3\u7684\u51DD\u8102\u3002</p><p>\u5B59\u5929\u7FBD\u4E00\u8FB9\u6253\u4E00\u8FB9\u9053\uFF1A\u300C\u4E0D\u5C31\u662F\u88AB\u5E72\u4E86\uFF1F\u6709\u4EC0\u4E48\u597D\u54ED\u7684\uFF1F\u5973\u4EBA\u751F\u4E0B\u6765\u5C31\u662F\u8BA9\u7537\u4EBA\u5E72\u7684\uFF0C\u4E00\u4E2A\u4EBA\u5E72\u8DDF\u5341\u4E2A\u4EBA\u5E72\u6709\u4EC0\u4E48\u533A\u522B\uFF1F\u8FD8\u5BFB\u6B7B\u89C5\u6D3B\u7684\u2026\u2026\u5C41\u80A1\u6485\u8D77\u6765\uFF01\u300D</p><p>\u4E39\u5A18\u6014\u6014\u62AC\u8D77\u81C0\u90E8\uFF0C\u81EA\u5DF1\u88AB\u4EBA\u5F3A\u66B4\uFF0C\u6700\u8BE5\u751F\u6C14\u7684\u96BE\u9053\u4E0D\u662F\u4ED6\u5417\uFF1F\u5973\u4EBA\u7684\u8D1E\u6D01\u591A\u534A\u662F\u4E3A\u81EA\u5DF1\u7684\u7537\u4EBA\u5B88\u7684\uFF0C\u81EA\u5DF1\u906D\u4EBA\u5F3A\u66B4\uFF0C\u4E22\u5C3D\u4E86\u5B59\u5929\u7FBD\u7684\u8138\u9762\uFF0C\u53EA\u6709\u4E00\u6B7B\u624D\u5BF9\u5F97\u8D77\u4ED6\u3002\u53EF\u4ED6\u7ADF\u6D51\u7136\u4E0D\u628A\u8FD9\u5F53\u56DE\u4E8B\u2026\u2026</p><p>\u5B59\u5929\u7FBD\u6402\u4F4F\u4E39\u5A18\u7684\u7EA4\u8170\uFF0C\u4E0D\u7BA1\u4E09\u4E03\u4E8C\u5341\u4E00\uFF0C\u786C\u751F\u751F\u63D2\u4E86\u8FDB\u53BB\u3002\u4ED6\u7684\u52A8\u4F5C\u731B\u70C8\u5F02\u5E38\uFF0C\u4E39\u5A18\u4E0B\u4F53\u4F24\u52BF\u672A\u6108\uFF0C\u9634\u961C\u5728\u8925\u4E0A\u78E8\u64E6\uFF0C\u70D9\u5904\u75DB\u695A\u96BE\u5F53\uFF0C\u4E0D\u5F97\u4E0D\u52C9\u529B\u5F13\u8D77\u8EAB\u5B50\u3002\u8FD9\u6837\u5B59\u5929\u7FBD\u7684\u9633\u5177\u8F7B\u6613\u4FBF\u6DF1\u5165\u8089\u7A74\u3002</p><p>\u5947\u602A\u7684\u662F\uFF0C\u6628\u65E5\u88AB\u4EBA\u8F6E\u66B4\u591A\u65F6\uFF0C\u5979\u6CA1\u6709\u4F53\u4F1A\u5230\u4E00\u4E1D\u5FEB\u611F\uFF0C\u6B64\u65F6\u5B59\u5929\u7FBD\u7684\u63D2\u5F04\u4E0E\u6628\u5929\u7684\u5F3A\u66B4\u6BEB\u65E0\u4E8C\u81F4\uFF0C\u8FDE\u5C48\u8FB1\u4E5F\u662F\u4E00\u822C\uFF0C\u5979\u4E0B\u4F53\u5374\u6E10\u6E10\u6E7F\u6DA6\uFF0C\u751A\u81F3\u53D1\u51FA\u53FD\u53FD\u7684\u6C34\u58F0\u3002</p><p>\u770B\u7740\u4E39\u5A18\u8138\u4E0A\u7684\u6CEA\u5149\uFF0C\u5B59\u5929\u7FBD\u4E0D\u7531\u5FC3\u8F6F\u4E0B\u6765\u3002\u4ED6\u4FEF\u8EAB\u543B\u4E86\u543B\u4E39\u5A18\u7684\u8033\u5782\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u674F\u513F\uFF0C\u522B\u591A\u60F3\u4E86\u3002\u65E0\u8BBA\u4F60\u600E\u4E48\u6837\uFF0C\u6211\u90FD\u4E00\u6837\u559C\u6B22\u4F60\u7684\u2026\u2026\u300D</p><p>\u4E00\u76F4\u6697\u6697\u996E\u6CE3\u7684\u4E39\u5A18\u545C\u7684\u4E00\u58F0\u54ED\u4E86\u51FA\u6765\uFF0C\u5979\u629B\u5F00\u4E86\u6240\u6709\u7684\u77DC\u6301\uFF0C\u4E00\u8FB9\u54ED\u4E00\u8FB9\u62DA\u547D\u633A\u52A8\u5706\u81C0\u3002\u5979\u54ED\u6CE3\u7740\u6CC4\u4E86\u8EAB\u5B50\uFF0C\u53C8\u54ED\u6CE3\u7740\u62AC\u8D77\u56E0\u6CC4\u8EAB\u800C\u54C6\u55E6\u7684\u7F8E\u7A74\uFF0C\u4E3B\u52A8\u5957\u5F04\u60C5\u90CE\u7684\u8089\u68D2\uFF0C\u76F4\u5230\u60C5\u90CE\u5728\u5979\u4F53\u5185\u55B7\u5C04\u3002</p><p>\u9C9C\u8840\u67D3\u7EA2\u4E86\u88AB\u8925\uFF0C\u4E39\u5A18\u4F0F\u5728\u69BB\u4E0A\uFF0C\u5FAE\u5FAE\u98A4\u6296\uFF0C\u826F\u4E45\uFF0C\u5979\u4F4E\u58F0\u9053\uFF1A\u300C\u5929\u7FBD\u54E5\uFF0C\u4F60\u4E0D\u602A\u6211\u4E48\uFF1F\u300D</p><p>\u300C\u600E\u4E48\u4F1A\u5462\uFF1F\u300D</p><p>\u4E39\u5A18\u95ED\u4E0A\u773C\uFF0C\u6CEA\u6C34\u5374\u65E0\u6CD5\u963B\u6321\u5730\u6EDA\u843D\u51FA\u6765\uFF0C\u300C\u662F\u56E0\u4E3A\u6211\u662F\u4E2A\u5A4A\u5B50\u5417\uFF1F\u300D</p><p>\u5B59\u5929\u7FBD\u773C\u89D2\u4E00\u8DF3\uFF0C\u300C\u8C01\u8BF4\u7684\uFF1F\u300D</p><p>\u300C\u5929\u7FBD\u54E5\uFF0C\u9006\u532A\u7684\u5973\u7737\u90FD\u8981\u5B98\u5356\u4E3A\u5A3C\uFF0C\u4E0D\u662F\u4E48\u2026\u2026\u2026\u300D\u4E39\u5A18\u4F4E\u6CE3\u9053\uFF1A\u300C\u5929\u7FBD\u54E5\uFF0C\u4F60\u8BA9\u6211\u6B7B\u4E86\u5427\u2026\u2026\u300D</p><p>\u5B59\u5929\u7FBD\u6C89\u9ED8\u826F\u4E45\uFF0C\u6162\u6162\u9053\uFF1A\u300C\u6211\u4E0D\u59D3\u5B59\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u6CA1\u6709\u7406\u4F1A\u4E39\u5A18\u60CA\u6115\u7684\u773C\u795E\uFF0C\u300C\u6211\u4E5F\u4E0D\u77E5\u6211\u4EE5\u524D\u59D3\u4EC0\u4E48\uFF0C\u4ECE\u6211\u61C2\u4E8B\u8D77\uFF0C\u6211\u5C31\u59D3\u9ED1\u3002\u300D</p><p>\u300C\u554A\uFF01\u300D</p><p>\u5B59\u5929\u7FBD\u7B11\u4E86\u7B11\uFF0C\u300C\u4F60\u77E5\u9053\u4E86\u3002\u662F\u7684\uFF0C\u6211\u662F\u8D31\u6237\u3002\u300D</p><p>\u300C\u6211\u5A18\u4ECE\u6765\u4E0D\u8BF4\u6211\u7239\u662F\u8C01\uFF0C\u4EE5\u524D\u662F\u505A\u4EC0\u4E48\u7684\u3002\u4F46\u662F\u6211\u8BB0\u5F97\uFF0C\u5C0F\u65F6\u5019\u6211\u4EEC\u5BB6\u5F88\u5927\uFF0C\u6709\u5F88\u591A\u4EC6\u4EBA\u3002\u4E0D\u8FC7\u90A3\u65F6\u5019\u6211\u53EA\u6709\u4E09\u5C81\uFF0C\u53EA\u6709\u4E00\u70B9\u6A21\u7CCA\u7684\u5370\u50CF\uFF0C\u8FD8\u4E0D\u77E5\u9053\u662F\u4E0D\u662F\u505A\u68A6\u3002\u300D</p><p>\u300C\u597D\u50CF\u662F\u6211\u7239\u7239\u529D\u7687\u4E0A\u4EC0\u4E48\u4E8B\uFF0C\u7ED3\u679C\u60F9\u4E86\u7687\u4E0A\u4E0D\u9AD8\u5174\uFF0C\u88AB\u7687\u4E0A\u6740\u4E86\u5934\u3002\u4E0D\u4F46\u662F\u4ED6\uFF0C\u6211\u4EEC\u6240\u6709\u4EB2\u621A\u5BB6\u7684\u7537\u4EBA\u90FD\u88AB\u6740\u4E86\u5934\u3002\u5269\u4E86\u51E0\u5341\u5BB6\u7684\u5B64\u513F\u5BE1\u6BCD\uFF0C\u88AB\u6D41\u653E\u5230\u5404\u5730\uFF0C\u6210\u4E86\u8D31\u6237\u3002\u300D</p><p>\u300C\u8DDF\u6211\u4EEC\u5BB6\u4E00\u8D77\u6D41\u653E\u7684\uFF0C\u8FD8\u6709\u6211\u4E00\u4E2A\u4F2F\u6BCD\uFF0C\u4E00\u4E2A\u59D1\u59D1\u3002\u5979\u4EEC\u90FD\u662F\u56E0\u4E3A\u6709\u4E86\u5B69\u5B50\uFF0C\u671D\u5EF7\u5F00\u6069\uFF0C\u53EA\u8D2C\u4E3A\u8D31\u6237\u3002\u6211\u90A3\u4E9B\u672A\u5AC1\u7684\u59D1\u59D1\u3001\u5802\u59D0\u3001\u5C0F\u59E8\uFF0C\u90FD\u88AB\u5356\u5230\u5993\u9662\u63A5\u5BA2\u3002\u300D</p><p>\u300C\u8D31\u6237\u5230\u54EA\u513F\u90FD\u6BD4\u4EBA\u4F4E\u4E00\u7B49\uFF0C\u4E0D\u8BB8\u8BFB\u4E66\uFF0C\u4E0D\u8BB8\u5F53\u5B98\uFF0C\u53EA\u80FD\u5F53\u5439\u9F13\u624B\uFF0C\u6CBF\u8857\u5356\u5531\uFF0C\u505A\u4E9B\u4E0B\u4E09\u6EE5\u7684\u6D3B\u8BA1\uFF0C\u8C01\u90FD\u80FD\u6B3A\u8D1F\u3002\u6709\u4E9B\u5730\u65B9\u4EBA\u597D\uFF0C\u8D31\u6237\u8FD8\u8FC7\u5F97\u53BB\uFF0C\u6709\u4E9B\u5730\u65B9\u2026\u2026\u6211\u4EEC\u53BB\u7684\u662F\u4E2A\u5C71\u6751\uFF0C\u521A\u8FC1\u8FC7\u6765\uFF0C\u5C31\u6709\u4E00\u4F19\u5730\u75DE\u6D41\u6C13\u95EF\u8FDB\u6211\u4EEC\u5BB6\uFF0C\u628A\u6211\u4F2F\u6BCD\u548C\u59D1\u59D1\u5F3A\u5978\u4E86\u3002\u300D</p><p>\u300C\u90A3\u662F\u5927\u767D\u5929\uFF0C\u4ED6\u4EEC\u5C31\u5728\u9662\u5B50\u91CC\uFF0C\u628A\u6211\u4F2F\u6BCD\u548C\u59D1\u59D1\u6252\u5149\u4E86\u8F6E\u6D41\u53BB\u5E72\u3002\u5F53\u65F6\u6211\u5A18\u8FD8\u6000\u7740\u6211\u59B9\u59B9\uFF0C\u4ED6\u4EEC\u5ACC\u4E0D\u5409\u5229\uFF0C\u624D\u6CA1\u6709\u78B0\u5979\u3002\u4F2F\u6BCD\u5BB6\u7684\u5802\u59D0\u5F53\u65F6\u4E03\u5C81\uFF0C\u59D1\u59D1\u5BB6\u7684\u59D0\u59D0\u662F\u56DB\u5C81\uFF0C\u6211\u4EEC\u8DDF\u5A18\u8EB2\u5728\u5C4B\u91CC\u5413\u5F97\u53D1\u6296\u3002\u300D</p><p>\u300C\u542C\u6211\u5A18\u8BF4\uFF0C\u6211\u59D1\u59D1\u4EE5\u524D\u662F\u4EAC\u57CE\u6709\u540D\u7684\u624D\u5973\uFF0C\u90A3\u5929\u665A\u4E0A\u5979\u54ED\u4E86\u4E00\u591C\uFF0C\u7B2C\u4E8C\u5929\u53BB\u53BF\u91CC\u62A5\u4E86\u5B98\u3002\u6309\u7167\u5927\u660E\u5F8B\uFF0C\u4E09\u4EBA\u4EE5\u4E0A\u8F6E\u5978\uFF0C\u90FD\u662F\u6740\u5934\u7684\u7F6A\uFF0C\u53BF\u8001\u7237\u63A5\u4E86\u6848\u5B50\uFF0C\u7ACB\u523B\u6D3E\u4EBA\u9501\u62FF\u3002\u7B49\u5230\u5802\u4E0A\u4E00\u95EE\uFF0C\u539F\u6765\u6211\u4EEC\u5BB6\u662F\u8D31\u6237\uFF0C\u7ED3\u679C\u5F53\u5802\u64A4\u4E86\u6848\u3002\u300D</p><p>\u300C\u90A3\u4E9B\u4EBA\u89C9\u5F97\u4E22\u4E86\u9762\u5B50\uFF0C\u8DEF\u4E0A\u628A\u6211\u59D1\u59D1\u6321\u4F4F\uFF0C\u5265\u5149\u4E86\u62D6\u56DE\u6751\u91CC\uFF0C\u53C8\u7EA0\u96C6\u4E86\u5468\u56F4\u51E0\u4E2A\u6751\u7684\u65E0\u8D56\uFF0C\u90FD\u6765\u5E72\u5979\uFF0C\u4E00\u8FB9\u5E72\u8FD8\u4E00\u8FB9\u8BA9\u6211\u59D1\u59D1\u5199\u8BD7\u5531\u66F2\u3002\u6211\u59D1\u59D1\u662F\u88AB\u4ED6\u4EEC\u6D3B\u6D3B\u5978\u6B7B\u7684\uFF0C\u6B7B\u7684\u65F6\u5019\u5979\u4E0B\u8EAB\u90FD\u662F\u8840\uFF0C\u809A\u5B50\u5374\u9F13\u5F97\u5F88\u5927\u3002\u300D</p><p>\u300C\u6211\u4F2F\u6BCD\u4E5F\u662F\u4E16\u5BB6\u51FA\u8EAB\uFF0C\u751F\u5F97\u5F88\u6F02\u4EAE\u3002\u6211\u59D1\u59D1\u6B7B\u7684\u65F6\u5019\u5979\u5C31\u5728\u65C1\u8FB9\u3002\u540E\u6765\u6BCF\u5929\u90FD\u6709\u4EBA\u6765\u627E\u5979\u3002\u6211\u5370\u50CF\u6700\u6DF1\u7684\uFF0C\u5C31\u662F\u5979\u6BCF\u5929\u4E0D\u505C\u5730\u8131\u8863\u670D\u3002\u518D\u540E\u6765\u5979\u6BCF\u5929\u665A\u4E0A\u90FD\u8981\u51FA\u53BB\u3002\u56E0\u4E3A\u6211\u4EEC\u5BB6\u592A\u7834\uFF0C\u6709\u4E9B\u4EBA\u4E0D\u613F\u610F\u6765\uFF0C\u5C31\u628A\u5979\u5524\u53BB\u4F3A\u5019\u3002\u90A3\u65F6\u6211\u4EEC\u90FD\u5F88\u9AD8\u5174\uFF0C\u56E0\u4E3A\u5979\u6BCF\u6B21\u56DE\u6765\u90FD\u80FD\u5E26\u4E00\u4E9B\u597D\u5403\u7684\u3002\u540E\u6765\u5979\u8DF3\u4E95\u6B7B\u4E86\u3002\u300D</p><p>\u300C\u6751\u91CC\u51E0\u4E2A\u65E0\u8D56\u559D\u9189\u4E86\uFF0C\u62FF\u6211\u4F2F\u6BCD\u53D6\u4E50\uFF0C\u62C9\u4E86\u6761\u91CE\u72D7\u8981\u7ED9\u5979\u914D\u79CD\u3002\u90A3\u5929\u662F\u5728\u4E0B\u96EA\u3002\u508D\u665A\u7684\u65F6\u5019\u4E00\u7FA4\u4EBA\u51B2\u8FDB\u6765\uFF0C\u8BF4\u6211\u4F2F\u6BCD\u5F04\u810F\u4E86\u4ED6\u4EEC\u7684\u4E95\uFF0C\u8BA9\u6211\u5A18\u8D54\u3002\u6211\u5A18\u90A3\u65F6\u521A\u751F\u4E86\u6211\u59B9\u59B9\uFF0C\u7ED9\u4ED6\u4EEC\u78D5\u4E86\u65E0\u6570\u7684\u5934\u3002\u4ED6\u4EEC\u8FD8\u662F\u4E0D\u4F9D\u3002\u300D</p><p>\u300C\u6700\u540E\u6211\u5A18\u8131\u4E86\u8863\u670D\uFF0C\u8BA9\u4ED6\u4EEC\u4E00\u4E2A\u4E2A\u6765\u808F. \u4ED6\u4EEC\u5ACC\u6211\u5A18\u524D\u9762\u592A\u677E\uFF0C\u90FD\u8D70\u7684\u65F1\u8DEF\u3002\u6211\u5A18\u88AB\u4ED6\u4EEC\u5E72\u5F97\u4E00\u5C41\u80A1\u8840\uFF0C\u7B49\u4ED6\u4EEC\u5E72\u5B8C\uFF0C\u4EBA\u4E5F\u660F\u8FC7\u53BB\u4E86\u3002\u300D</p><p>\u300C\u8D31\u6237\u662F\u4E0D\u80FD\u8FC1\u5F99\u7684\uFF0C\u6211\u4EEC\u5C31\u8FD9\u6837\u5728\u6751\u91CC\u4F4F\u4E0B\u6765\u3002\u6211\u5A18\u4EC0\u4E48\u6D3B\u8BA1\u90FD\u4E0D\u4F1A\uFF0C\u4E5F\u6CA1\u94B1\u4E70\u5BB6\u4EC0\u3002\u5979\u4E00\u4EBA\u517B\u6211\u4EEC\u56DB\u4E2A\u5B69\u5B50\uFF0C\u53EA\u80FD\u8DDF\u6211\u4F2F\u6BCD\u4E00\u6837\uFF0C\u62FF\u8EAB\u5B50\u6362\u4E9B\u5403\u559D\u3002\u300D</p><p>\u4E39\u5A18\u5DF2\u7ECF\u542C\u5F97\u5446\u4E86\uFF0C\u5B59\u5929\u7FBD\u53CC\u624B\u6795\u5728\u8111\u540E\uFF0C\u7EE7\u7EED\u8BF4\u9053\uFF1A\u300C\u6211\u5802\u59D0\u5341\u5C81\u5C31\u88AB\u4EBA\u5F00\u4E86\u82DE\u3002\u5979\u4ECE\u5C0F\u957F\u5F97\u6C34\u7075\uFF0C\u9644\u8FD1\u6709\u4EBA\u5BB6\u7ED3\u4EB2\uFF0C\u90FD\u5148\u628A\u5979\u53EB\u53BB\uFF0C\u8BA9\u65B0\u90CE\u500C\u5B66\u7740\u600E\u4E48\u8FDB\u6D1E\u623F\u3002\u300D</p><p>\u300C\u6751\u91CC\u7537\u4EBA\u90FD\u628A\u6211\u5A18\u5F53\u5A4A\u5B50\uFF0C\u5973\u4EBA\u90FD\u6068\u6211\u5A18\uFF0C\u8BF4\u5979\u662F\u72D0\u72F8\u7CBE\u3002\u6211\u5A18\u4ECE\u6765\u4E0D\u6562\u9886\u6211\u51FA\u95E8\uFF0C\u56E0\u4E3A\u8DEF\u4E0A\u649E\u5230\u6709\u4EBA\uFF0C\u7537\u4EBA\u5C31\u4F1A\u62FF\u6211\u5A18\u5F00\u5FC3\uFF0C\u5973\u4EBA\u4F1A\u6765\u6253\u5979\uFF0C\u6495\u5979\u7684\u8863\u670D\uFF0C\u9A82\u5979\u662F\u5A3C\u5987\uFF0C\u88AB\u4EBA\u73A9\u70C2\u7684\u8D31\u8D27\u3002\u300D</p><p>\u300C\u6211\u5A18\u6BCF\u6B21\u770B\u5230\u6211\u59B9\u59B9\u90FD\u4F1A\u6D41\u6CEA\u3002\u6211\u59B9\u59B9\u662F\u4E00\u4E2A\u7F8E\u4EBA\u80DA\u5B50\uFF0C\u957F\u5F97\u50CF\u74F7\u5A03\u5A03\u4E00\u6837\uFF0C\u96EA\u767D\u96EA\u767D\u3002\u5979\u53EB\u54E5\u54E5\u7684\u58F0\u97F3\u7279\u522B\u597D\u542C\u3002\u5230\u5979\u56DB\u5C81\u90A3\u5E74\uFF0C\u6211\u5A18\u4E00\u72E0\u5FC3\uFF0C\u628A\u5979\u5356\u5230\u5993\u9662\u91CC\u4E86\u3002\u300D</p><p>\u300C\u554A\uFF01\u300D\u4E39\u5A18\u4E00\u58F0\u60CA\u547C\u3002</p><p>\u5B59\u5929\u7FBD\u6DE1\u6DE1\u9053\uFF1A\u300C\u4E0D\u7136\u8FD8\u80FD\u600E\u4E48\u6837\uFF1F\u6211\u59D1\u59D1\u5BB6\u7684\u5973\u513F\u88AB\u4E00\u7FA4\u5927\u5B69\u5B50\u5E26\u5230\u5C71\u91CC\u53BB\u73A9\uFF0C\u5C31\u518D\u4E5F\u6CA1\u56DE\u6765\u3002\u300D</p><p>\u300C\u90A3\u4F60\u2026\u2026\u300D</p><p>\u300C\u6211\u4E03\u5C81\u751F\u65E5\u90A3\u5929\uFF0C\u5A18\u7ED9\u6211\u51C6\u5907\u4E86\u4E00\u987F\u597D\u5403\u7684\u3002\u521A\u521A\u6446\u4E0A\u684C\uFF0C\u5C31\u6765\u4E86\u51E0\u4E2A\u65E0\u8D56\u3002\u5A18\u8BA9\u6211\u5728\u95E8\u53E3\u7B49\u7740\uFF0C\u7136\u540E\u5173\u4E0A\u95E8\u3002\u6211\u997F\u7740\u809A\u5B50\u7B49\u4E86\u5F88\u4E45\uFF0C\u8FD8\u4E0D\u89C1\u4ED6\u4EEC\u51FA\u6765\uFF0C\u5C31\u722C\u5230\u7A97\u6237\u4E0A\u53BB\u770B\u3002\u6211\u5A18\u5149\u7740\u8EAB\u5B50\u8DB4\u5728\u684C\u4E0A\uFF0C\u6211\u5FD8\u4E86\u6709\u51E0\u4E2A\u4EBA\u5728\u5E72\u5979\uFF0C\u53EA\u8BB0\u5F97\u5979\u8EAB\u8FB9\u7684\u76D8\u5B50\u90FD\u7A7A\u4E86\u3002\u300D</p><p>\u300C\u6211\u5927\u54ED\u7740\u51FA\u4E86\u95E8\uFF0C\u5728\u6751\u5916\u78B0\u4E0A\u4E86\u4E00\u4E2A\u8001\u9053\uFF0C\u5C31\u8DDF\u7740\u4ED6\u8D70\u3002\u90A3\u9053\u58EB\u5C31\u662F\u6211\u5E08\u7236\u3002\u4ED6\u662F\u4E09\u6E05\u6D3E\u7684\uFF0C\u53EA\u4F1A\u4E00\u70B9\u7C97\u6D45\u7684\u6B66\u529F\uFF0C\u5E73\u65F6\u5C31\u9760\u5356\u836F\u4E3A\u751F\u3002\u8FC7\u4E86\u51E0\u5E74\uFF0C\u4ED6\u6B7B\u4E86\u3002\u6211\u4ECE\u5C71\u4E1C\u4E00\u8DEF\u5357\u4E0B\uFF0C\u5728\u5E73\u8FDC\u5F85\u4E86\u4E00\u5E74\uFF0C\u6B63\u597D\u9047\u5230\u8859\u95E8\u62DB\u4EBA\uFF0C\u6211\u4F1A\u4E24\u624B\u529F\u592B\uFF0C\u5C31\u9009\u4E86\u8FDB\u6765\u3002\u540E\u6765\uFF0C\u5C31\u5230\u4E86\u8FD9\u91CC\u3002\u300D</p><p>\u4E39\u5A18\u8F7B\u58F0\u9053\uFF1A\u300C\u4F60\u5A18\u5979\u4EEC\u5462\uFF1F\u300D</p><p>\u5B59\u5929\u7FBD\u6C89\u9ED8\u4E86\u4E00\u4F1A\u513F\uFF0C\u300C\u4E0D\u77E5\u9053\uFF0C\u4E5F\u8BB8\u6B7B\u4E86\u5427\u3002\u300D</p>',98);function t(n,_){return a}var o=e(r,[["render",t]]);export{o as default};
