import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const a={},r=p('<h1 id="_27-\u9500\u804C" tabindex="-1"><a class="header-anchor" href="#_27-\u9500\u804C" aria-hidden="true">#</a> 27 \u9500\u804C</h1><p>\u300C\u6211\u5E08\u7236\u61C2\u4E00\u4E9B\u6B67\u9EC4\u4E4B\u672F\uFF0C\u4F46\u4E5F\u4E0D\u600E\u4E48\u7CBE\u901A\u3002\u4E61\u91CC\u4EBA\u6027\u5B50\u76F4\uFF0C\u533B\u597D\u4E86\u5F53\u83E9\u8428\u656C\u7740\u4F9B\u7740\uFF0C\u8981\u662F\u51FA\u4E86\u5C94\u5B50\u5C31\u662F\u4E00\u901A\u72E0\u6253\u3002\u6240\u4EE5\u6211\u5E08\u7236\u6700\u64C5\u957F\u7684\u662F\u6CBB\u5916\u4F24\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u7B11\u4E86\u7B11\uFF0C\u300C\u6211\u5E08\u7236\u4E00\u8F88\u5B50\u90FD\u5728\u627E\u4ED9\u836F\uFF0C\u60F3\u505A\u5730\u4ED9\uFF0C\u7ED3\u679C\u4EC0\u4E48\u90FD\u6CA1\u6709\uFF0C\u4E34\u6B7B\u7684\u65F6\u5019\u4ED6\u53F9\u4E86\u53E3\u6C14\u8BF4\uFF0C\u65E9\u77E5\u9053\u8FD9\u6837\uFF0C\u4E0D\u5982\u505A\u4E2A\u5996\u9053\uFF0C\u4E5F\u4E0D\u767D\u6765\u4E16\u4E0A\u4E00\u906D\u3002\u300D</p><p>\u300C\u6211\u5E08\u7236\u6027\u5B50\u597D\u4E50\u3002\u6709\u4E00\u6B21\u6211\u4EEC\u4E09\u5929\u6CA1\u6709\u5403\u996D\uFF0C\u4ED6\u8FD8\u7ED9\u6211\u8BB2\u7B11\u8BDD\u2026\u2026\u300D\u5B59\u5929\u7FBD\u8F6C\u5934\u9053\uFF1A\u300C\u5026\u4E86\u5417\uFF1F\u300D</p><p>\u4E39\u5A18\u95ED\u7740\u773C\uFF0C\u8F7B\u8F7B\u6447\u4E86\u6447\u5934\uFF0C\u300C\u4F60\u8BF4\u5427\u3002\u6211\u7231\u542C\u3002\u4ED6\u8BF4\u7684\u4EC0\u4E48\uFF1F\u300D</p><p>\u5B59\u5929\u7FBD\u60F3\u4E86\u4E00\u4F1A\u513F\uFF0C\u300C\u6211\u5FD8\u4E86\u3002\u53EA\u8BB0\u5F97\u7B11\u8FC7\u540E\u809A\u5B50\u66F4\u997F\u4E86\u3002\u300D</p><p>\u300C\u4ED6\u77E5\u9053\u4F60\u2026\u2026\u300D\u2192\u6587\xB7\u5187\xB7\u4EBA\xB7\u5187\xB7\u4E66\xB7\u5187\xB7\u5C4B\u2190</p><p>\u300C\u6211\u6CA1\u6709\u544A\u8BC9\u8FC7\u4EFB\u4F55\u4EBA\u3002\u9664\u4E86\u4F60\u3002\u300D\u5B59\u5929\u7FBD\u9053\uFF1A\u300C\u597D\u591A\u5E74\u90FD\u6CA1\u60F3\u8FC7\uFF0C\u6211\u539F\u4EE5\u4E3A\u90FD\u5FD8\u4E86\uFF0C\u8C01\u77E5\u9053\u4F1A\u8BB0\u8FD9\u4E48\u6E05\u3002\u300D</p><p>\u300C\u90A3\u65F6\u5019\uFF0C\u6211\u6700\u7FA1\u6155\u53BF\u91CC\u7684\u8859\u5F79\uFF0C\u6751\u91CC\u4E0D\u7BA1\u662F\u5927\u6237\u8FD8\u662F\u65E0\u8D56\uFF0C\u6240\u6709\u4EBA\u90FD\u6015\u4ED6\u4EEC\u3002\u6BCF\u6B21\u6765\u6536\u7CAE\uFF0C\u6751\u91CC\u90FD\u8981\u4E71\u4E0A\u5F88\u4E45\u3002\u6211\u5E38\u60F3\uFF0C\u80FD\u7A7F\u4E0A\u4ED6\u4EEC\u7684\u8863\u670D\u5C31\u597D\u4E86\u3002\u6709\u4E86\u90A3\u8EAB\u8863\u670D\uFF0C\u5C31\u6CA1\u4EBA\u6562\u6B3A\u8D1F\u6211\u5A18\u4E86\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u56DE\u8FC7\u5934\uFF0C\u53EA\u89C1\u4E39\u5A18\u4E0D\u77E5\u4F55\u65F6\u5DF2\u7ECF\u62C9\u8D77\u88AB\u5B50\u906E\u4F4F\u9762\u5B54\u3002\u4ECE\u76D1\u72F1\u56DE\u6765\uFF0C\u4E39\u5A18\u5C31\u8138\u8272\u60E8\u767D\uFF0C\u770B\u4E0D\u5230\u4E00\u4E1D\u6B22\u5BB9\uFF0C\u6BD4\u5979\u4E0A\u6B21\u5931\u8EAB\u65F6\u663E\u5F97\u66F4\u6C89\u9ED8\u3002</p><p>\u5B59\u5929\u7FBD\u6CA1\u4F5C\u58F0\uFF0C\u6084\u6084\u4F38\u624B\uFF0C\u7ED9\u4E39\u5A18\u6396\u4E86\u6396\u88AB\u89D2\u3002\u7A7F\u4E0A\u7682\u5E95\u6731\u8FB9\u7684\u8859\u5F79\u670D\u8272\uFF0C\u4F3C\u4E4E\u662F\u6362\u4E86\u4E00\u4E2A\u4EBA\u3002\u80E5\u540F\u662F\u65E0\u6CD5\u4F5C\u5B98\u7684\uFF0C\u5C11\u4E86\u4E0A\u8FDB\u7684\u4ED5\u9014\uFF0C\u5BF9\u529F\u540D\u5C31\u4E0D\u5728\u4E4E\u4E86\uFF0C\u4F59\u4E0B\u7684\u53EA\u662F\u8D64\u88F8\u88F8\u7684\u5229\u6B32\u4E4B\u5FC3\u3002</p><p>\u72F1\u4E2D\u5341\u51E0\u540D\u72F1\u5352\uFF0C\u9664\u4E86\u960E\u7F57\u671B\u8FD8\u6709\u51E0\u5206\u91CE\u5FC3\uFF0C\u5176\u5B83\u4E0D\u662F\u5F85\u7F6A\u4E4B\u8EAB\uFF0C\u5C31\u662F\u4E00\u4E8B\u65E0\u6210\uFF0C\u90FD\u5728\u660F\u660F\u5669\u5669\u8FC7\u65E5\u5B50\uFF0C\u8FC7\u4E00\u5929\u56FE\u4E24\u664C\u5FEB\u6D3B\u3002\u5B59\u5929\u7FBD\u4E0E\u4ED6\u4EEC\u4E0D\u540C\u3002\u4ED6\u4E0D\u613F\u4E00\u8F88\u5B50\u90FD\u5F85\u5728\u8C7A\u72FC\u5761\uFF0C\u8FD9\u6837\u57CB\u5728\u5C71\u91CC\u4E0E\u72F1\u4E2D\u7684\u56DA\u72AF\u6709\u4EC0\u4E48\u533A\u522B\uFF1F</p><p>\u4ED6\u95ED\u4E0A\u773C\uFF0C\u7F13\u7F13\u5165\u5B9A\u3002\u4E39\u7530\u90A3\u56E2\u6D51\u6C8C\u7684\u6696\u610F\u6E10\u6E10\u53D8\u5F97\u6E05\u6670\uFF0C\u65CB\u8F6C\u7247\u523B\u540E\u4E00\u4E1D\u4E1D\u6563\u5165\u56DB\u80A2\u767E\u9AB8\u3002\u5F88\u5FEB\u4ED6\u5C31\u80FD\u7EC3\u6210\u6DF7\u5143\u6C14\u7684\u7B2C\u4E00\u5C42\u3002</p><p>\u7F57\u9704\u6DF7\u5143\u6C14\u5165\u624B\u5FEB\u6377\uFF0C\u7B2C\u4E00\u5C42\u53EA\u9700\u4E09\u4E2A\u6708\uFF0C\u7B2C\u4E8C\u5C42\u516D\u4E2A\u6708\uFF0C\u7B97\u6765\u4E00\u5E74\u534A\u540E\u5C31\u80FD\u7EC3\u81F3\u7B2C\u4E09\u5C42\uFF0C\u53EA\u8981\u5C0F\u5FC3\u4E00\u4E9B\uFF0C\u5728\u6C5F\u6E56\u4E2D\u8DB3\u4EE5\u4FDD\u547D\u3002\u90A3\u65F6\uFF0C\u4E5F\u8BE5\u662F\u4ED6\u79BB\u5F00\u7684\u65F6\u5019\u4E86\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u2026\u2026\u539F\u7CFB\u7F57\u9704\u6D3E\u5F1F\u5B50\uFF0C\u7ECF\u5BDF\uFF0C\u6240\u62A5\u7C4D\u8D2F\u6709\u8BEF\u3002\u73B0\u5C01\u6863\u9664\u540D\uFF0C\u5F85\u67E5\u5B9E\u540E\u518D\u884C\u53D9\u5F55\u3002\u8BF8\u53F8\u6709\u95FB\uFF0C\u6536\u5176\u300C\u5341\u516B\u7701\u901A\u884C\u300D\u8170\u724C\uFF0C\u7F34\u5F52\u5211\u90E8\u3002\u5927\u660E\u4E07\u5386\u56DB\u5341\u4E8C\u5E74\u4E8C\u6708\u5341\u4E94\u3002\u300D</p><p>\u960E\u7F57\u671B\u5408\u4E0A\u6298\u9875\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u672C\u72F1\u5730\u65B9\u504F\u8FDC\uFF0C\u4ECA\u65E5\u624D\u63A5\u5230\u5211\u90E8\u6587\u4E66\uFF0C\u9669\u4E9B\u8BA9\u4F60\u7792\u8FC7\u4E86\u3002\u6765\u4EBA\u554A\uFF0C\u6536\u4E86\u5979\u7684\u8170\u724C\uFF0C\u5C01\u597D\u7F34\u4E88\u5211\u90E8\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u63A5\u8FC7\u8170\u724C\uFF0C\u653E\u5728\u76D2\u4E2D\uFF0C\u7528\u706B\u6F06\u5C01\u597D\uFF0C\u76D6\u4E0A\u76D1\u72F1\u7684\u94DC\u5370\uFF0C\u5C0F\u5FC3\u6536\u8D77\u3002</p><p>\u660E\u660E\u662F\u6015\u60F9\u4E0A\u9006\u6848\uFF0C\u5374\u627E\u4E86\u4E2A\u7C4D\u8D2F\u4E0D\u6E05\u7684\u501F\u53E3\u585E\u642A\uFF0C\u53C8\u628A\u65E5\u671F\u632A\u5230\u6848\u53D1\u524D\uFF0C\u663E\u5F97\u4E0E\u767D\u5B5D\u5112\u4E00\u6848\u65E0\u5E72\u3002\u5211\u90E8\u8FD9\u756A\u624B\u811A\u4E5F\u7B97\u715E\u8D39\u82E6\u5FC3\u3002\u767D\u96EA\u83B2\u542C\u8BF4\u7687\u4E0A\u8EB2\u5728\u5BAB\u91CC\u4E8C\u5341\u4F59\u5E74\u4E0D\u89C1\u5EF7\u81E3\uFF0C\u671D\u653F\u5927\u4E71\uFF0C\u5374\u6CA1\u60F3\u5230\u4F1A\u4E71\u5230\u8FD9\u4E2A\u5730\u6B65\u3002</p><p>\u300C\u4E3E\u5934\u4E09\u5C3A\u6709\u795E\u660E\uFF01\u767D\u96EA\u83B2\uFF0C\u672C\u5B98\u529D\u4F60\u8FD8\u662F\u65E9\u4E9B\u62DB\u4F9B\uFF0C\u7B49\u5019\u671D\u5EF7\u53D1\u843D\u3002\u83AB\u5F85\u65E5\u540E\u8FFD\u6094\u83AB\u53CA\u3002\u300D</p><p>\u960E\u7F57\u671B\u80CC\u540E\u90A3\u5C0A\u6CE5\u50CF\u7F3A\u4E86\u534A\u8FB9\u8138\uFF0C\u770B\u4E0D\u51FA\u4EFB\u54EA\u8DEF\u795E\u660E\uFF0C\u4EC5\u5269\u7684\u4E00\u53EA\u72EC\u76EE\u5706\u7741\u7740\uFF0C\u843D\u6EE1\u4E86\u7070\u5C18\u3002\u960E\u7F57\u671B\u8BF4\u7F62\u6B63\u4E86\u6B63\u5B98\u5E3D\uFF0C\u6602\u7136\u8D77\u8EAB\uFF0C\u5429\u5490\u9053\uFF1A\u300C\u628A\u767D\u72AF\u5E26\u56DE\u72F1\u4E2D\uFF0C\u8BA9\u5979\u597D\u751F\u6D17\u6D17\uFF0C\u4ECA\u665A\u672C\u5B98\u8981\u6311\u706F\u591C\u5BA1\u3002\u300D</p><p>\u767D\u96EA\u83B2\u62AB\u67B7\u5E26\u9501\u56DE\u5230\u5730\u7262\uFF0C\u5148\u770B\u5230\u4E00\u4E2A\u4E0D\u8BE5\u5728\u6B64\u7684\u8EAB\u5F71\u3002</p><p>\u300C\u82F1\u83B2\uFF01\u4F60\u600E\u4E48\u5728\u8FD9\u91CC\uFF01\u300D</p><p>\u5979\u4E0E\u859B\u971C\u7075\u56DA\u5728\u4E86\u4E00\u5904\uFF0C\u5BF9\u9762\u7684\u94C1\u7B3C\u4E00\u76F4\u7A7A\u7740\uFF0C\u6B64\u65F6\u91CC\u9762\u653E\u4E86\u88AB\u8925\u3001\u6795\u5934\uFF0C\u8FD8\u6709\u4E00\u5F20\u9F50\u819D\u9AD8\u7684\u56DB\u65B9\u5C0F\u684C\uFF0C\u6BD4\u5979\u4EEC\u4FE9\u7684\u8870\u8349\u8D25\u7D6E\u5F3A\u4E86\u5341\u500D\u3002\u82F1\u83B2\u602F\u751F\u751F\u5750\u5728\u91CC\u9762\uFF0C\u4F3C\u4E4E\u6CA1\u6709\u8BA4\u51FA\u5979\u6765\uFF0C\u534A\u664C\u624D\u53EB\u4E86\u58F0\uFF1A\u300C\u59D0\u3002\u300D</p><p>\u767D\u96EA\u83B2\u4E00\u6025\uFF0C\u6323\u5F00\u9501\u94FE\u671D\u5F1F\u5F1F\u5954\u53BB\u3002\u5979\u5E26\u7740\u8DB3\u68B0\uFF0C\u8FC8\u6B65\u672C\u5C31\u8270\u96BE\uFF0C\u53EA\u8D70\u51FA\u4E24\u6B65\uFF0C\u4F55\u6C42\u56FD\u4ECE\u540E\u8FFD\u6765\uFF0C\u6C34\u706B\u68CD\u72E0\u72E0\u6253\u5728\u767D\u96EA\u83B2\u819D\u5F2F\u3002</p><p>\u767D\u96EA\u83B2\u546F\u7684\u8DEA\u5012\uFF0C\u94C1\u67B7\u7838\u5728\u9752\u77F3\u4E0A\uFF0C\u6E85\u8D77\u4E00\u7F15\u706B\u82B1\u3002\u4F55\u6C42\u56FD\u4E3E\u68CD\u671D\u5979\u81C0\u4E0A\u817F\u95F4\u4E00\u901A\u72E0\u6253\uFF0C\u9A82\u9053\uFF1A\u300C\u6740\u5343\u5200\u7684\u6B7B\u8D3C\u56DA\uFF01\u8FD8\u6562\u8DD1\uFF01\u300D</p><p>\u767D\u96EA\u83B2\u62AC\u8D77\u5934\uFF0C\u6025\u9053\uFF1A\u300C\u4F60\u600E\u4E48\u4F1A\u5728\u8FD9\u91CC\uFF1F\u5A18\u5462\uFF1F\u300D</p><p>\u677F\u5B50\u91CD\u91CD\u843D\u5728\u8EAB\u4E0A\uFF0C\u54CD\u8D77\u7684\u76AE\u8089\u58F0\u8BA9\u82F1\u83B2\u5FC3\u60CA\u8089\u8DF3\uFF0C\u4ED6\u767D\u7740\u8138\u8BF4\uFF1A\u300C\u5A18\u5728\u5BB6\u91CC\u2026\u2026\u6211\u2026\u2026\u6211\u662F\u6765\u544A\u72B6\u7684\u3002\u300D</p><p>\u542C\u5230\u5A18\u4EB2\u65E0\u6059\uFF0C\u767D\u96EA\u83B2\u677E\u4E86\u53E3\u6C14\uFF0C\u5F85\u542C\u5230\u5F1F\u5F1F\u8BF4\u6765\u544A\u72B6\uFF0C\u5979\u4E0D\u7531\u5931\u58F0\u9053\uFF1A\u300C\u4EC0\u4E48\uFF1F\u300D</p><p>\u82F1\u83B2\u5C0F\u58F0\u8BF4\uFF1A\u300C\u6211\u8981\u7ED9\u7239\u7239\u7533\u51A4\uFF0C\u5A18\u5C31\u8BA9\u6211\u8DDF\u5B59\u53D4\u53D4\u5230\u8FD9\u91CC\u6765\u4E86\u3002\u300D</p><p>\u300C\u5A18\u8BA9\u4F60\u6765\u7684\uFF1F\u300D\u767D\u96EA\u83B2\u60CA\u9053\uFF0C\u5A18\u600E\u4E48\u8FD9\u4E48\u4E0D\u6653\u4E8B\uFF0C\u8FD9\u4E0D\u662F\u628A\u5F1F\u5F1F\u5F80\u864E\u53E3\u9001\u5417\uFF1F\u53C8\u662F\u5B59\u5929\u7FBD\uFF0C\u96BE\u9053\u5A18\u771F\u8BA9\u4ED6\u7ED9\u9A97\u4E86\uFF0C\u8FDE\u5F1F\u5F1F\u8FD9\u4E2A\u547D\u6839\u5B50\u90FD\u6258\u4ED8\u51FA\u6765\uFF1F</p><p>\u82F1\u83B2\u70B9\u70B9\u5934\uFF0C\u300C\u6211\u5728\u5916\u9762\u4F4F\u4E86\u4E00\u665A\uFF0C\u5218\u5927\u53D4\u5C31\u5E26\u6211\u56DE\u53BB\u4E86\u3002\u4ED6\u5F85\u6211\u5F88\u2026\u2026\u300D</p><p>\u300C\u54EA\u4E2A\u5927\u53D4\uFF1F\u300D</p><p>\u300C\u5C31\u662F\u90A3\u4E2A\u957F\u80E1\u5B50\u7684\u3002\u300D\u82F1\u83B2\u6BD4\u5212\u7740\u4ED6\u7684\u6A21\u6837\uFF0C\u300C\u5218\u5927\u53D4\u8BF4\uFF0C\u53EA\u8981\u6211\u542C\u8BDD\uFF0C\u4ED6\u5C31\u5E2E\u6211\u5199\u72B6\u5B50\uFF0C\u4E00\u5B9A\u80FD\u544A\u8D62\u3002\u300D</p><p>\u767D\u96EA\u83B2\u6B63\u5F85\u8BF4\u8BDD\uFF0C\u81C0\u540E\u5FFD\u7136\u4E00\u51C9\uFF0C\u5374\u662F\u4F55\u6C42\u56FD\u89C1\u5979\u8BF4\u5F97\u51FA\u795E\uFF0C\u7ADF\u7136\u5927\u7740\u80C6\u5B50\u4F38\u624B\u5728\u5979\u81C0\u95F4\u6478\u5F04\u3002\u767D\u96EA\u83B2\u624B\u8DB3\u88AB\u56F0\uFF0C\u4E00\u65F6\u8D77\u4E0D\u4E86\u8EAB\uFF0C\u88AB\u4ED6\u72E0\u6478\u51E0\u628A\uFF0C\u624D\u6323\u624E\u7740\u8EB2\u5F00\u3002</p><p>\u767D\u96EA\u83B2\u8138\u4E0A\u65F6\u7EA2\u65F6\u767D\uFF0C\u5979\u4E00\u65E9\u5C31\u88AB\u5E26\u53BB\u4E0A\u5802\uFF0C\u6B64\u65F6\u8179\u4E2D\u4E00\u9635\u7D27\u5F20\u53C8\u6709\u4E9B\u9690\u9690\u7684\u4FBF\u610F\u3002\u4F55\u6C42\u56FD\u65E9\u5C31\u8FDC\u8FDC\u8EB2\u5F00\uFF0C\u4E00\u8FB9\u628A\u624B\u653E\u5728\u9F3B\u4E0A\u7528\u529B\u55C5\u7740\uFF0C\u4E00\u8FB9\u659C\u773C\u8BAA\u7B11\u3002\u8FD9\u4E9B\u72F1\u6C49\u5C31\u50CF\u9644\u9AA8\u7684\u86C6\u866B\u65E0\u5B54\u4E0D\u5165\uFF0C\u767D\u96EA\u83B2\u867D\u7136\u6068\u5F97\u54AC\u7259\u5207\u9F7F\uFF0C\u5374\u4E5F\u62FF\u4ED6\u4EEC\u7684\u65E0\u8D56\u884C\u5F84\u65E0\u8BA1\u53EF\u65BD\uFF0C\u53EA\u80FD\u76EF\u7740\u4F55\u6C42\u56FD\uFF0C\u6162\u6162\u9000\u5165\u94C1\u7B3C\u3002</p><p>\u7B49\u767D\u96EA\u83B2\u5750\u597D\uFF0C\u4F55\u6C42\u56FD\u624D\u5C0F\u5FC3\u5730\u9760\u8FC7\u6765\uFF0C\u9501\u4F4F\u7B3C\u95E8\uFF0C\u53C8\u98DE\u5FEB\u5730\u8EB2\u5230\u4E00\u8FB9\u3002\u63A5\u7740\u4ED6\u60F3\u8D77\u4E86\u4EC0\u4E48\uFF0C\u62CD\u4E86\u62CD\u8111\u95E8\uFF0C\u5306\u5FD9\u51FA\u4E86\u5730\u7262\u3002</p><p>\u82F1\u83B2\u7D27\u7EF7\u7684\u5FC3\u4E8B\u8FD9\u4F1A\u513F\u624D\u677E\u61C8\u4E0B\u6765\uFF0C\u4ED6\u4E2A\u5B50\u5C0F\uFF0C\u5750\u5728\u5730\u4E0A\u6B63\u80FD\u770B\u5230\u59D0\u59D0\u4E0B\u8EAB\u7834\u5F00\u7684\u8863\u9699\u91CC\u767D\u767D\u7684\u817F\u6839\u3002\u4ED6\u5FD9\u8F6C\u8FC7\u773C\uFF0C\u4E0D\u6562\u518D\u770B\u3002\u5FC3\u91CC\u5947\u602A\uFF0C\u59D0\u59D0\u90A3\u91CC\u600E\u4E48\u957F\u4E86\u6BDB\u6BDB\uFF0C\u5374\u6CA1\u6709\u5C0F\u9E21\u9E21\uFF1F</p><p>\u767D\u96EA\u83B2\u6CA1\u6709\u7559\u610F\u5F1F\u5F1F\u7684\u773C\u795E\uFF0C\u95EE\u9053\uFF1A\u300C\u6765\u4E86\u591A\u4E45\u4E86\uFF1F\u300D</p><p>\u300C\u6709\u516B\u4E5D\u5929\u4E86\u3002\u4E00\u76F4\u8DDF\u5218\u5927\u53D4\uFF0C\u540E\u6765\u6709\u4E2A\u8138\u9ED1\u9ED1\u7684\u5927\u5B98\uFF0C\u8BF4\u6211\u5728\u5916\u9762\u4E0D\u597D\uFF0C\u5218\u4F2F\u4F2F\u5C31\u628A\u6211\u9001\u5230\u8FD9\u513F\u6765\u4E86\u3002\u8FD9\u4E9B\u4E1C\u897F\u90FD\u662F\u4ED6\u7ED9\u6211\u7684\u3002\u300D</p><p>\u767D\u96EA\u83B2\u5BF9\u5218\u8FA9\u673A\u5370\u50CF\u4E0D\u6DF1\uFF0C\u518D\u60F3\u4E0D\u51FA\u4ED6\u4E3A\u4F55\u4F1A\u5982\u6B64\u597D\u5FC3\u3002\u4E5F\u8BB8\u72F1\u91CC\u4E5F\u662F\u6709\u597D\u4EBA\u7684\u5427\u3002\u82F1\u83B2\u5DF2\u7ECF\u5728\u8FD9\u91CC\uFF0C\u7740\u6025\u4E5F\u6CA1\u6709\u6CD5\u5B50\uFF0C\u5979\u6C89\u9ED8\u4E86\u4E00\u4F1A\u513F\uFF0C\u53C8\u95EE\u9053\uFF1A\u300C\u5A18\u8FD8\u597D\u5417\uFF1F\u300D</p><p>\u82F1\u83B2\u95ED\u4E86\u5634\uFF0C\u5C0F\u5634\u6162\u6162\u6485\u4E86\u8D77\u6765\u3002</p><p>\u300C\u600E\u4E48\u4E86\uFF1F\u51FA\u4E86\u4EC0\u4E48\u4E8B\u5417\uFF1F\u82F1\u83B2\uFF0C\u4F60\u8BF4\u8BDD\u554A\uFF1F\u300D</p><p>\u82F1\u83B2\u56D4\u7740\u9F3B\u5B50\u8BF4\uFF1A\u300C\u5A18\u6574\u5929\u90FD\u5F88\u9AD8\u5174\u3002\u300D</p><p>\u767D\u96EA\u83B2\u53C8\u597D\u6C14\u53C8\u597D\u7B11\uFF0C\u300C\u5A18\u9AD8\u5174\u8FD8\u4E0D\u597D\uFF1F\u300D</p><p>\u300C\u7239\u6B7B\u4E86\uFF0C\u5A18\u5F00\u59CB\u54ED\u4E86\u4E24\u5929\uFF0C\u7136\u540E\u5C31\u5F88\u5F00\u5FC3\u7684\u6837\u5B50\uFF0C\u4E00\u70B9\u513F\u90FD\u4E0D\u60F3\u7239\u7239\u3002\u300D</p><p>\u767D\u96EA\u83B2\u9053\uFF1A\u300C\u53EF\u80FD\u5A18\u662F\u6015\u60F9\u4F60\u54ED\uFF0C\u80CC\u7740\u4F60\u6D41\u6CEA\u5462\u3002\u300D</p><p>\u300C\u5A18\u8EAB\u4E0A\u6BD4\u4EE5\u524D\u8FD8\u9999\uFF0C\u5473\u9053\u5F88\u597D\u95FB\u2026\u2026\u300D</p><p>\u767D\u96EA\u83B2\u5931\u7B11\u9053\uFF1A\u300C\u5A18\u8EAB\u4E0A\u9999\u9999\u7684\u4E0D\u597D\u5417\uFF1F\u300D</p><p>\u300C\u597D\u554A\uFF0C\u53EF\u6211\u60F3\u8DDF\u5A18\u4E00\u8D77\u7761\uFF0C\u5A18\u4E0D\u8BA9\u3002\u300D</p><p>\u300C\u82F1\u83B2\u8FD9\u4E48\u5927\u4E86\uFF0C\u4E0D\u7528\u8DDF\u5A18\u4E00\u8D77\u7761\u554A\u3002\u300D</p><p>\u300C\u90A3\u4E3A\u4EC0\u4E48\u5B59\u53D4\u53D4\u80FD\u8DDF\u5A18\u4E00\u8D77\u7761\uFF1F\u300D</p><p>\u300C\u4EC0\u4E48\uFF01\u300D</p><p>\u82F1\u83B2\u561F\u7740\u5634\u8BF4\uFF1A\u300C\u5B59\u5929\u7FBD\u6BCF\u5929\u90FD\u6765\uFF0C\u5A18\u4E00\u770B\u89C1\u4ED6\uFF0C\u773C\u775B\u90FD\u5728\u7B11\u3002\u6709\u5929\u65E9\u4E0A\u6211\u8D77\u6765\u8BFB\u4E66\uFF0C\u770B\u5230\u4ED6\u4ECE\u5A18\u623F\u91CC\u51FA\u6765\uFF0C\u8863\u670D\u90FD\u6CA1\u7CFB\u3002\u4ED6\u6BD4\u6211\u8FD8\u5927\uFF0C\u53C8\u4E0D\u662F\u7239\u7239\uFF0C\u4E3A\u4EC0\u4E48\u8981\u8DDF\u5A18\u4E00\u8D77\u7761\uFF1F\u300D</p><p>\u767D\u96EA\u83B2\u60CA\u5F97\u76EE\u77AA\u53E3\u5446\uFF0C\u65C1\u8FB9\u859B\u971C\u7075\u54AF\u54AF\u7B11\u4E86\u8D77\u6765\uFF0C\u300C\u90A3\u662F\u4F60\u5A18\u8F67\u59D8\u5934\u5462\u3002\u5C0F\u5F1F\u5F1F\uFF0C\u4F60\u4EE5\u4E3A\u4ED6\u4EEC\u53EA\u662F\u7761\u89C9\u5417\uFF1F\u300D</p><p>\u767D\u96EA\u83B2\u5389\u58F0\u9053\uFF1A\u300C\u4F60\u7ED9\u6211\u4F4F\u53E3\uFF01\u300D</p><p>\u859B\u971C\u7075\u6311\u8D77\u7709\u68A2\uFF0C\u300C\u4F60\u5A18\u505A\u5F97\uFF0C\u6211\u96BE\u9053\u8BF4\u4E0D\u5F97\uFF1F\u90A3\u8001\u677F\u5A18\u5C31\u662F\u4F60\u5A18\u5427\uFF0C\u679C\u7136\u662F\u98CE\u9A9A\u5165\u9AA8\u3002\u4E08\u592B\u521A\u6B7B\uFF0C\u5C31\u8DDF\u72F1\u91CC\u7684\u8859\u5F79\u9B3C\u6DF7\u4E0A\u4E86\u3002\u83AB\u975E\u5F00\u7684\u662F\u8089\u5E97\uFF1F\u300D</p><p>\u300C\u556A\uFF01\u300D\u859B\u971C\u7075\u8138\u4E0A\u91CD\u91CD\u6328\u4E86\u4E00\u8BB0\u3002\u5979\u60CA\u6115\u5730\u6342\u7740\u8138\uFF0C\u77AA\u5927\u773C\u775B\uFF0C\u534A\u664C\u9053\uFF1A\u300C\u4F60\u4F1A\u7F29\u9AA8\u529F\uFF1F\u300D</p><p>\u67B7\u6D1E\u53EA\u6709\u624B\u8155\u7C97\u7EC6\uFF0C\u767D\u96EA\u83B2\u7684\u624B\u81C2\u5374\u4ECE\u4E2D\u4F38\u51FA\u534A\u622A\uFF0C\u8FDE\u5173\u8282\u90FD\u8F6F\u5F97\u5F77\u4F5B\u6CA1\u6709\u9AA8\u5934\u3002\u859B\u971C\u7075\u604D\u7136\u9053\uFF1A\u300C\u6211\u8BF4\u4F60\u4E24\u624B\u6263\u7740\uFF0C\u600E\u4E48\u80FD\u628A\u8863\u670D\u7684\u7834\u5904\u63A9\u597D\uFF0C\u8FD8\u80FD\u628A\u5C41\u80A1\u64E6\u90A3\u4E48\u51C0\u2026\u2026\u771F\u662F\u597D\u5FC3\u8BA1\u2026\u2026\u300D</p><p>\u300C\u95ED\u5634\uFF01\u300D\u767D\u96EA\u83B2\u6536\u56DE\u624B\u3002\u5176\u5B9E\u5979\u662F\u5230\u72F1\u91CC\u624D\u5F00\u59CB\u7EC3\u8FD9\u95E8\u7F29\u9AA8\u529F\uFF0C\u859B\u971C\u7075\u8D8A\u72F1\u65F6\uFF0C\u5979\u7B2C\u4E00\u6B21\u8131\u5F00\u53CC\u624B\uFF0C\u4F46\u9888\u90E8\u603B\u4E0D\u53EF\u80FD\u7F29\u8FC7\u53BB\uFF0C\u53EA\u80FD\u60F3\u529E\u6CD5\u62D4\u6389\u9500\u5B50\uFF0C\u624D\u80FD\u8131\u6389\u94C1\u67B7\u3002</p><p>\u859B\u971C\u7075\u6709\u4E9B\u601C\u60AF\u5730\u770B\u7740\u5979\uFF0C\u300C\u771F\u4E0D\u77E5\u9053\u8BE5\u8BF4\u4F60\u806A\u660E\uFF0C\u8FD8\u662F\u7B28\u5F97\u8981\u6B7B\uFF0C\u8FD9\u4E48\u597D\u7684\u529F\u592B\uFF0C\u8FD8\u5F85\u5728\u72F1\u91CC\uFF0C\u662F\u7B49\u4ED6\u4EEC\u7ED9\u4F60\u5F00\u82DE\u5462\uFF1F\u8FD8\u662F\u89C9\u5F97\u5728\u8FD9\u91CC\u5F53\u4E2A\u4E0D\u8981\u94B1\u7684\u5A4A\u5B50\u4E5F\u4E0D\u9519\uFF1F\u96BE\u9053\u4F60\u8FD8\u8DDF\u4F60\u8FD9\u5C0F\u5F1F\u5F1F\u4E00\u6837\uFF0C\u7B49\u5B98\u5E9C\u7ED9\u4F60\u4EEC\u7533\u51A4\u4E48\uFF1F\u300D</p><p>\u300C\u6211\u5E74\u7EAA\u53EA\u6BD4\u4F60\u5927\u4E24\u5C81\uFF0C\u89C1\u5F97\u5374\u6BD4\u4F60\u591A\u5F97\u591A\u3002\u5F53\u4ECA\u7687\u5E1D\u767B\u57FA\u56DB\u5341\u591A\u5E74\uFF0C\u5012\u6709\u4E8C\u5341\u591A\u5E74\u8EB2\u5728\u5BAB\u91CC\u4E0D\u89C1\u81E3\u6C11\u3002\u5929\u4E0B\u6C34\u707E\u3001\u65F1\u707E\u3001\u8757\u707E\uFF0C\u8FD8\u6709\u502D\u5BC7\u3001\u9791\u977C\u3001\u82D7\u4EBA\uFF0C\u773C\u770B\u767E\u59D3\u90FD\u6CA1\u4E86\u751F\u8DEF\uFF0C\u671D\u5EF7\u7684\u5927\u81E3\u4EEC\u8FD8\u53EA\u987E\u7740\u515A\u4E89\u3002\u8FD9\u4E2A\u4E0D\u5165\u6D41\u7684\u53BF\u72F1\u90FD\u5982\u6B64\u80C6\u5927\u5984\u4E3A\uFF0C\u4F55\u51B5\u4E0A\u9762\u7684\u5978\u5B98\u6ED1\u540F\uFF1F\u666E\u5929\u4E0B\u60F3\u627E\u4E00\u4E2A\u6E05\u5B98\uFF0C\u6BD4\u627E\u4E00\u53EA\u51E4\u51F0\u8FD8\u96BE\u2026\u2026\u300D</p><p>\u300C\u4F55\u5927\u4EBA\u3002\u300D</p><p>\u859B\u971C\u7075\u6014\u4E86\u4E00\u4E0B\uFF0C\u300C\u5927\u7406\u5BFA\u53F3\u4E1E\u4F55\u6E05\u6CB3\uFF1F\u300D</p><p>\u5979\u4F4E\u5934\u60F3\u4E86\u60F3\uFF0C\u300C\u4E0D\u9519\uFF0C\u4ED6\u662F\u4E00\u4E2A\u6E05\u5B98\u3002\u8BF4\u6765\u8FD8\u662F\u6258\u4E86\u5F53\u4ECA\u5929\u5B50\u4E0D\u7406\u671D\u653F\u7684\u798F\uFF0C\u5404\u90E8\u804C\u5B98\u4E0D\u5347\u4E0D\u9EDC\uFF0C\u9022\u7F3A\u4E0D\u8865\u3002\u4F55\u5927\u4EBA\u5F53\u4E86\u4E8C\u5341\u591A\u5E74\u4E94\u54C1\u53F8\u4E1E\uFF0C\u53C2\u4ED6\u7684\u594F\u6298\u5C01\u4E86\uFF0C\u53D9\u529F\u7684\u594F\u6298\u4E5F\u5C01\u4E86\uFF0C\u5C31\u8FDE\u544A\u8001\u6C42\u8F9E\u7684\u594F\u6298\u4E5F\u4E00\u6837\u5C01\u4E86\uFF0C\u53EA\u6015\u8981\u8001\u6B7B\u5728\u4EFB\u4E0A\u4E86\u3002\u300D</p><p>\u859B\u971C\u7075\u5FAE\u5FAE\u4E00\u7B11\uFF0C\u300C\u539F\u6765\u4F60\u662F\u5728\u7B49\u4ED6\u3002\u4ED6\u7BA1\u7740\u5929\u4E0B\u5341\u4E09\u5E03\u653F\u53F8\u7684\u6848\u5B50\uFF0C\u4E0D\u89C1\u5F97\u5C31\u80FD\u770B\u5230\u8FD9\u4E00\u6869\u3002\u5C31\u662F\u770B\u5230\u4E86\uFF0C\u4ED6\u4E5F\u672A\u5FC5\u4F1A\u4EB2\u6765\u590D\u6838\u3002\u5012\u662F\u4F60\uFF0C\u6CA1\u6709\u591A\u5C11\u65F6\u95F4\u7B49\u4E86\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u5C0F\u83B2\u83B2\u2026\u2026\u300D</p><p>\u542C\u5230\u90A3\u4EE4\u4EBA\u5934\u76AE\u53D1\u9EBB\u7684\u58F0\u97F3\uFF0C\u767D\u96EA\u83B2\u5FC3\u5934\u4E00\u9635\u6076\u5BD2\u3002\u82F1\u83B2\u5374\u9AD8\u5174\u5730\u6251\u5230\u6805\u680F\u4E0A\uFF0C\u53EB\u4E86\u58F0\uFF0C\u300C\u4F2F\u4F2F\uFF01\u300D</p><p>\u5218\u8FA9\u673A\u559C\u4E0D\u81EA\u80DC\uFF0C\u9694\u7740\u94C1\u6805\u6478\u4E86\u6478\u82F1\u83B2\u7684\u5C0F\u8138\uFF0C\u4E00\u8FB9\u54B3\u55FD\u4E00\u58F0\uFF0C\u300C\u8001\u4F55\u3002\u300D</p><p>\u300C\u54CE\uFF0C\u5218\u7237\u3002\u300D\u4F55\u6C42\u56FD\u98A0\u98A0\u5730\u8DD1\u8FC7\u6765\u5F00\u4E86\u9501\uFF0C\u7B11\u9053\uFF1A\u300C\u4E0A\u9762\u6B63\u70E7\u7740\u6C34\uFF0C\u5C0F\u7684\u53BB\u770B\u770B\uFF0C\u60A8\u5148\u5FD9\u3002\u300D</p><p>\u5218\u8FA9\u673A\u4E5F\u4E0D\u7406\u776C\u65C1\u4EBA\uFF0C\u8FDB\u4E86\u94C1\u7B3C\u5C31\u4E00\u628A\u6402\u4F4F\u82F1\u83B2\uFF0C\u8D2A\u5A6A\u5F97\u50CF\u8981\u628A\u4ED6\u4E00\u53E3\u541E\u4E0B\u4E00\u822C\u3002</p><p>\u767D\u96EA\u83B2\u548C\u859B\u971C\u7075\u6115\u7136\u671B\u7740\u4E24\u4EBA\uFF0C\u5218\u8FA9\u673A\u5BF9\u82F1\u83B2\u7684\u559C\u7231\u50CF\u662F\u4E0D\u5047\uFF0C\u53EF\u60C5\u6001\u5374\u5341\u4E8C\u5206\u7684\u7578\u5F02\uFF0C\u8BA9\u4EBA\u5BD2\u6BDB\u76F4\u7AD6\u2026\u2026</p><p>\u8BF4\u8BDD\u95F4\u5218\u8FA9\u673A\u5DF2\u7ECF\u653E\u5F00\u82F1\u83B2\uFF0C\u4ED6\u4ECE\u8896\u4E2D\u638F\u51FA\u4E00\u9875\u7EB8\u6643\u4E86\u6643\uFF0C\u300C\u72B6\u5B50\u6211\u53C8\u5199\u4E86\u4E00\u5F20\uFF0C\u518D\u6709\u534A\u6708\u5C31\u80FD\u5199\u5B8C\u3002\u300D\u4ED6\u63C9\u4E86\u63C9\u8155\u5B50\uFF0C\u300C\u5199\u5F97\u6211\u624B\u90FD\u9EBB\u4E86\u2026\u2026\u5C0F\u83B2\u83B2\uFF0C\u4F60\u8BE5\u600E\u4E48\u8C22\u6211\u554A\uFF1F\u300D</p><p>\u82F1\u83B2\u4E0D\u89E3\u4E16\u4E8B\uFF0C\u8DDF\u4ED6\u4F4F\u4E86\u6570\u65E5\uFF0C\u65E9\u628A\u8FD9\u4E2A\u628A\u4ED6\u4ECE\u5371\u96BE\u4E2D\u89E3\u6551\u51FA\u6765\u7684\u7537\u4EBA\u5F53\u6210\u5929\u4E0B\u7B2C\u4E00\u7B49\u7684\u597D\u4EBA\uFF0C\u5E73\u65E5\u91CC\u808C\u80A4\u76F8\u63A5\uFF0C\u65E0\u5F62\u4E2D\u53C8\u4EB2\u8FD1\u4E86\u51E0\u5206\u3002\u4ED6\u7B11\u563B\u563B\u62B1\u7740\u5927\u53D4\u7684\u8116\u5B50\uFF0C\u7528\u529B\u4EB2\u4E86\u4E0A\u53BB\u3002\u4ED6\u8FD9\u4EB2\u543B\u4E0E\u5E73\u5E38\u4E0D\u540C\uFF0C\u5C0F\u5634\u8D34\u5728\u4E0A\u9762\u53C8\u8214\u53C8\u5438\uFF0C\u9017\u5F97\u5218\u8FA9\u673A\u7709\u5F00\u773C\u7B11\uFF0C\u534A\u664C\u5408\u4E0D\u62E2\u5634\u3002</p><p>\u859B\u971C\u7075\u5550\u4E86\u4E00\u53E3\uFF0C\u63F6\u63C4\u9053\uFF1A\u300C\u4EBA\u5BB6\u90FD\u8BF4\u6709\u5176\u7236\u5FC5\u6709\u5176\u5B50\uFF0C\u4F60\u8FD9\u5F1F\u5F1F\u4E0E\u4EE4\u5C0A\u53EF\u5927\u4E0D\u4E00\u6837\uFF0C\u5012\u6709\u51E0\u5206\u4EE4\u5802\u7684\u98CE\u81F4\u2026\u2026\u300D</p><p>\u767D\u96EA\u83B2\u6068\u6068\u77AA\u7740\u5979\u3002\u859B\u971C\u7075\u82E5\u65E0\u5176\u4E8B\u5730\u8212\u4E86\u8212\u8EAB\u5B50\uFF0C\u300C\u6211\u77E5\u9053\u4F60\u60F3\u8BF4\u4EC0\u4E48\u3002</p><p>\u4F60\u4E0D\u5C31\u662F\u5ACC\u6211\u88AB\u4EBA\u5E72\u8FC7\uFF0C\u770B\u4E0D\u8D77\u6211\u2014\u2014\u767D\u6355\u5934\uFF0C\u4F60\u4EE5\u4E3A\u81EA\u5DF1\u8FD8\u80FD\u5E72\u51C0\u591A\u4E45\uFF1F\u300D\u5979\u77A5\u4E86\u767D\u96EA\u83B2\u4E00\u773C\uFF0C\u8BED\u5E26\u5632\u8BBD\u5730\u8BF4\uFF1A\u300C\u4F60\u4EE5\u4E3A\u81EA\u5DF1\u8FD8\u662F\u5E72\u51C0\u7684\u5417\uFF1F\u300D</p><p>\u767D\u96EA\u83B2\u83AB\u540D\u7684\u7126\u71E5\u8D77\u6765\uFF0C\u300C\u4F60\u4EC0\u4E48\u610F\u601D\uFF1F\u300D</p><p>\u859B\u971C\u7075\u51B7\u7B11\u7740\u8F6C\u8FC7\u8138\uFF0C\u773C\u775B\u7A81\u7136\u7741\u5F97\u6D51\u5706\uFF0C\u50CF\u76EE\u7779\u4E86\u8352\u8C2C\u5F97\u4E0D\u53EF\u601D\u8BAE\u7684\u4E00\u5E55\u822C\uFF0C\u300C\u54C8\u300D\u4E86\u4E00\u58F0\u3002</p><p>\u4E0D\u77E5\u4F55\u65F6\uFF0C\u5218\u8FA9\u673A\u892A\u6389\u4E86\u82F1\u83B2\u7684\u88E4\u5B50\uFF0C\u4E24\u6307\u5939\u7740\u90A3\u6839\u767D\u767D\u7684\u5C0F\u9634\u830E\uFF0C\u50CF\u628A\u73A9\u4E00\u6761\u7389\u8695\u822C\u5728\u6307\u95F4\u6311\u9017\u7740\u3002\u4E0D\u65F6\u8FD8\u62FF\u7740\u4E24\u53EA\u7EC6\u5C0F\u7684\u776A\u4E38\u8F7B\u8F7B\u63C9\u634F\u3002\u82F1\u83B2\u5750\u5728\u5218\u8FA9\u673A\u6000\u91CC\uFF0C\u4F4E\u5934\u770B\u7740\u81EA\u5DF1\u7684\u5C0F\u9E21\u9E21\uFF0C\u60F3\u7B11\u53C8\u4E0D\u6562\u7B11\uFF0C\u597D\u50CF\u5F88\u6709\u8DA3\u7684\u6837\u5B50\u3002</p><p>\u5218\u8FA9\u673A\u4E00\u9762\u628A\u73A9\u82F1\u83B2\u7684\u9634\u830E\uFF0C\u4E00\u9762\u4E0D\u65F6\u4F38\u51FA\u624B\u6307\uFF0C\u671D\u82F1\u83B2\u817F\u7F1D\u91CC\u63A2\u53BB\uFF0C\u90A3\u526F\u8272\u8FF7\u8FF7\u7684\u6837\u5B50\uFF0C\u51E0\u4E4E\u8981\u6D41\u51FA\u53E3\u6C34\u3002</p><p>\u767D\u96EA\u83B2\u6123\u4E86\u4E00\u4F1A\u513F\uFF0C\u7A81\u7136\u5389\u559D\u9053\uFF1A\u300C\u4F60\u5728\u505A\u4EC0\u4E48\uFF01\u300D</p><p>\u82F1\u83B2\u5168\u8EAB\u4E00\u98A4\uFF0C\u672C\u6765\u5C31\u4E0D\u5927\u7684\u9634\u830E\u8FC5\u901F\u7F29\u5C0F\uFF0C\u830E\u5C16\u5192\u51FA\u4E00\u6EF4\u4EAE\u6676\u6676\u7684\u6DB2\u4F53\u3002</p><p>\u767D\u96EA\u83B2\u6C14\u5F97\u6D51\u8EAB\u53D1\u6296\uFF0C\u4E00\u53E3\u6C14\u54FD\u5728\u5589\u5934\uFF0C\u534A\u664C\u624D\u559D\u9053\uFF1A\u300C\u59D3\u5218\u7684\uFF01\u4F60\u8FD8\u662F\u4EBA\u5417\uFF1F\u300D</p><p>\u5218\u8FA9\u673A\u5148\u628A\u82F1\u83B2\u6402\u5728\u6000\u91CC\uFF0C\u7528\u8863\u8896\u63A9\u597D\uFF0C\u6C14\u9053\uFF1A\u300C\u4F60\u4EEC\u8FD9\u4E9B\u51E1\u592B\u4FD7\u5B50\u77E5\u9053\u4E9B\u4EC0\u4E48\uFF1F\u56B7\u8FD9\u4E48\u5927\u58F0\uFF0C\u83AB\u5413\u7740\u6211\u7684\u5C0F\u83B2\u83B2\u2026\u2026\u300D</p><p>\u767D\u96EA\u83B2\u817E\u7684\u8D77\u8EAB\uFF0C\u4E3E\u67B7\u671D\u6805\u680F\u4E0A\u7838\u53BB\uFF0C\u5321\u7684\u4E00\u58F0\u5DE8\u54CD\uFF0C\u62C7\u6307\u7C97\u7684\u94C1\u6805\u88AB\u7838\u5F97\u5F2F\u66F2\uFF0C\u77F3\u5C51\u7EB7\u7EB7\u800C\u843D\u3002\u5218\u8FA9\u673A\u3001\u859B\u971C\u7075\u53EA\u89C9\u8033\u4E2D\u55E1\u55E1\u4F5C\u54CD\uFF0C\u88AB\u5979\u8FD9\u96F7\u9706\u4E00\u51FB\u9707\u5F97\u8138\u4E0A\u53D8\u8272\u3002</p><p>\u767D\u96EA\u83B2\u66F4\u52A0\u4E0D\u597D\u53D7\uFF0C\u5979\u624B\u9888\u90FD\u9501\u5728\u67B7\u4E2D\uFF0C\u53CD\u9707\u7684\u529B\u9053\u78E8\u7834\u4E86\u5979\u5DE6\u624B\u7684\u808C\u80A4\uFF0C\u8155\u4E0A\u9C9C\u8840\u6DCB\u6F13\u3002\u859B\u971C\u7075\u6697\u81EA\u8BE7\u5F02\uFF0C\u767D\u96EA\u83B2\u5165\u72F1\u4EE5\u6765\u4E00\u76F4\u6781\u4E3A\u514B\u5236\uFF0C\u5C31\u7B97\u662F\u9732\u4F53\u53D7\u8FB1\uFF0C\u4E5F\u672A\u66FE\u5982\u6B64\u6124\u6012\uFF0C\u5979\u770B\u8D77\u6765\u5C31\u50CF\u4E00\u53EA\u53D1\u6012\u7684\u96CC\u8C79\u3002</p><p>\u82F1\u83B2\u7D27\u7D27\u6293\u4F4F\u5218\u8FA9\u673A\u7684\u8863\u8896\uFF0C\u62DA\u547D\u628A\u5934\u57CB\u5728\u4ED6\u6000\u91CC\uFF0C\u8EAB\u5B50\u4E0D\u505C\u54C6\u55E6\u3002\u5218\u8FA9\u673A\u53EA\u6015\u5979\u7838\u5F00\u94C1\u7B3C\uFF0C\u987E\u4E0D\u5F97\u8BF4\u8BDD\uFF0C\u614C\u5FD9\u62B1\u4F4F\u82F1\u83B2\u62D4\u817F\u5C31\u8DD1\u3002</p><p>\u300C\u82F1\u83B2\uFF01\u82F1\u83B2\uFF01\u300D\u65E0\u4EE5\u540D\u72B6\u7684\u6050\u60E7\u75AF\u72C2\u8513\u5EF6\uFF0C\u767D\u96EA\u83B2\u7528\u529B\u7838\u7740\u94C1\u6805\uFF0C\u76F4\u5230\u4E24\u8155\u90FD\u78D5\u51FA\u9C9C\u8840\uFF0C\u6700\u540E\u65E0\u529B\u5730\u8DEA\u5728\u5730\u4E0A\u3002</p><p>\u51FA\u4E86\u5730\u7262\uFF0C\u8033\u8FB9\u987F\u65F6\u4E00\u9759\u3002\u5218\u8FA9\u673A\u62B9\u4E86\u628A\u51B7\u6C57\uFF0C\u7A33\u4F4F\u5FC3\u795E\uFF0C\u53C8\u5B89\u6170\u534A\u664C\uFF0C\u82F1\u83B2\u715E\u767D\u7684\u5C0F\u8138\u624D\u6E10\u6E10\u8F6C\u8FC7\u989C\u8272\u3002</p><p>\u4ED6\u4E3A\u7740\u82F1\u83B2\u8D39\u4E86\u4E0D\u5C11\u5FC3\u601D\uFF0C\u6700\u8981\u7D27\u7684\u8FD8\u672A\u5F97\u624B\uFF0C\u5C31\u88AB\u960E\u7F57\u671B\u52D2\u4EE4\u628A\u4EBA\u9001\u5230\u5730\u7262\uFF0C\u5FC3\u4E2D\u5927\u662F\u4E0D\u7518\u3002\u8D81\u7740\u6B64\u523B\u7262\u5185\u65E0\u4EBA\uFF0C\u4ED6\u5343\u54C4\u4E07\u529D\uFF0C\u8BA9\u82F1\u83B2\u8DB4\u5728\u5899\u89D2\u7684\u5E8A\u4E0A\uFF0C\u6485\u8D77\u5C41\u80A1\u3002</p><p>\u88AB\u59D0\u59D0\u4E00\u559D\uFF0C\u82F1\u83B2\u5FC3\u91CC\u8FD8\u6709\u4E9B\u5FD0\u5FD1\uFF0C\u6B64\u65F6\u8DB4\u5728\u5E8A\u4E0A\uFF0C\u8EAB\u5B50\u5374\u7D27\u7EF7\u7740\u3002\u4ED6\u7684\u5C41\u80A1\u8FD8\u672A\u53D1\u80B2\u6210\u719F\uFF0C\u7389\u7403\u822C\u5C0F\u5DE7\uFF0C\u5706\u6DA6\u53EF\u7231\uFF0C\u7EC6\u770B\u6765\u8FD8\u6709\u4E00\u5C42\u6781\u7EC6\u7684\u6C57\u6BDB\u3002\u4ED6\u540E\u5EAD\u7684\u4F24\u52BF\u5DF2\u7ECF\u6108\u5408\uFF0C\u4E0D\u540C\u7684\u662F\uFF0C\u4E0E\u5E74\u9F84\u76F8\u6BD4\uFF0C\u4ED6\u7684\u5C41\u773C\u513F\u8981\u5927\u4E86\u4E00\u5708\u3002</p><p>\u7537\u4EBA\u7684\u9633\u5177\u5728\u7A1A\u5AE9\u7684\u81C0\u95F4\u78E8\u64E6\u4E86\u7247\u523B\uFF0C\u6162\u6162\u8FDB\u5165\u7537\u5B69\u4F53\u5185\u3002\u53EA\u6709\u6210\u4EBA\u4E00\u534A\u5927\u7684\u5C0F\u5C41\u80A1\u4F7F\u9633\u5177\u663E\u5F97\u5206\u5916\u7C97\u58EE\uFF0C\u540C\u6837\u7A1A\u5AE9\u7684\u5C41\u773C\u513F\u548C\u80A0\u9053\u4F7F\u9633\u5177\u5F97\u5230\u4E86\u83AB\u5927\u7684\u5FEB\u611F\u3002\u5218\u8FA9\u673A\u8089\u68D2\u88AB\u90A3\u53EA\u5C0F\u5C41\u80A1\u7D27\u7D27\u7B8D\u7740\uFF0C\u8EAB\u4F53\u5374\u5982\u5728\u4E91\u7AEF\u3002</p><p>\u6BD4\u8D77\u7D22\u7136\u65E0\u5473\u7684\u5973\u4EBA\uFF0C\u4E00\u4E2A\u5996\u5A9A\u7684\u5A08\u7AE5\u66F4\u53EF\u610F\uFF0C\u5C31\u50CF\u4ED6\u80EF\u4E0B\u7684\u8FD9\u4E2A\u7AE5\u5B50\uFF0C\u65E0\u8BBA\u662F\u5C0F\u5634\u8FD8\u662F\u5AE9\u5AE9\u7684\u540E\u5EAD\u82B1\uFF0C\u90FD\u4EE4\u4EBA\u9500\u9B42\u3002</p><p>\u82F1\u83B2\u53EA\u89C9\u5C41\u80A1\u88AB\u4E00\u6485\u786C\u7269\u6491\u5F97\u7D27\u7D27\u7684\uFF0C\u4ED6\u54AC\u4F4F\u5507\u8FB9\u7684\u5C0F\u75E3\uFF0C\u8FDE\u6C14\u4E5F\u4E0D\u6562\u5598\uFF0C\u751F\u6015\u4E00\u7528\u529B\u5C41\u773C\u513F\u5C31\u4F1A\u88C2\u5F00\u3002\u8089\u68D2\u6162\u6162\u8FDB\u5165\u76F4\u80A0\uFF0C\u7565\u4E00\u505C\u987F\uFF0C\u4FBF\u5F00\u59CB\u62BD\u9001\u8D77\u6765\u3002</p><p>\u5C41\u773C\u513F\u6E10\u6E10\u53D1\u70ED\uFF0C\u72B9\u5982\u4E00\u4E2A\u8F6F\u4E2D\u5E26\u97E7\u7684\u8089\u7B8D\u5957\u5728\u9633\u5177\u4E0A\u6765\u56DE\u62D6\u52A8\u3002\u8FD9\u4E00\u6B21\u82F1\u83B2\u5E76\u6CA1\u6709\u591A\u5C11\u4E0D\u9002\uFF0C\u53CD\u800C\u8FD8\u6709\u79CD\u5F02\u6837\u7684\u538B\u8FEB\u611F\uFF0C\u4F7F\u4ED6\u4E0B\u9762\u7684\u5C0F\u8089\u68D2\u4E0D\u81EA\u89C9\u5730\u81A8\u80C0\u8D77\u6765\u3002</p><p>\u300C\u5927\u53D4\u2026\u2026\u597D\u602A\u2026\u2026\u300D</p><p>\u5218\u8FA9\u673A\u7684\u5598\u606F\u58F0\u8D8A\u6765\u8D8A\u6025\u4FC3\uFF0C\u4F3C\u4E4E\u6CA1\u6709\u542C\u5230\u3002</p><p>\u82F1\u83B2\u4E0D\u518D\u542D\u58F0\uFF0C\u4ED6\u600E\u4E48\u4E5F\u4E0D\u77E5\u9053\uFF0C\u6570\u65E5\u524D\uFF0C\u5A18\u4EB2\u540C\u6837\u662F\u5728\u8FD9\u91CC\u5931\u8EAB\u3002</p><p>\u767D\u96EA\u83B2\u8DEA\u5750\u5728\u5730\u4E0A\uFF0C\u9762\u524D\u94C1\u6805\u88AB\u7838\u5F97\u5F2F\u66F2\uFF0C\u5374\u6CA1\u6709\u4E00\u6839\u8131\u843D\uFF0C\u663E\u7136\u4E24\u7AEF\u5728\u77F3\u4E2D\u57CB\u5F97\u6781\u6DF1\u3002\u5979\u5E76\u4E0D\u6E05\u695A\u90A3\u4E2A\u7537\u4EBA\u8981\u5BF9\u5F1F\u5F1F\u505A\u4EC0\u4E48\uFF0C\u4F46\u5FC3\u5E95\u9690\u7EA6\u7684\u6050\u60E7\u5374\u8D8A\u6765\u8D8A\u6D53\u91CD\u3002\u82F1\u83B2\u662F\u767D\u5BB6\u552F\u4E00\u7684\u7537\u5B69\uFF0C\u4ED6\u82E5\u6709\u4E2A\u4EC0\u4E48\u957F\u77ED\uFF0C\u767D\u5BB6\u5C31\u7EDD\u540E\u4E86\u3002</p><p>\u5979\u60F3\u4E0D\u660E\u767D\uFF0C\u628A\u82F1\u83B2\u7231\u903E\u6027\u547D\u7684\u5A18\u4EB2\u4E3A\u4F55\u4F1A\u8BA9\u4ED6\u6765\u544A\u72B6\u3002\u96BE\u9053\u5A18\u771F\u7684\u88AB\u5B59\u5929\u7FBD\u9A97\u4E86\uFF1F</p><p>\u767D\u96EA\u83B2\u95ED\u4E0A\u773C\uFF0C\u80A9\u5934\u5FAE\u5FAE\u6218\u6296\uFF0C\u300C\u90A3\u662F\u4F60\u5A18\u8F67\u59D8\u5934\u5462\u3002\u300D\u2026\u2026\u5A18\u4EB2\u771F\u7684\u4F1A\u662F\u90A3\u4E48\u2026\u2026\u4E0B\u8D31\u5417\uFF1F</p><p>\u6CA1\u591A\u4E45\uFF0C\u82F1\u83B2\u5C31\u56DE\u6765\u4E86\u3002\u4ED6\u88AB\u4EBA\u62B1\u7740\u9001\u56DE\u5730\u7262\uFF0C\u4F3C\u4E4E\u6709\u4E9B\u75B2\u7D2F\uFF0C\u7787\u4E86\u773C\u60F3\u7761\u89C9\uFF0C\u8138\u4E0A\u5012\u6CA1\u6709\u75DB\u82E6\u7684\u8868\u60C5\u3002\u767D\u96EA\u83B2\u7565\u7565\u653E\u4E0B\u5FC3\u4E8B\uFF0C\u60F3\u95EE\u5F1F\u5F1F\u5728\u5916\u9762\u53D1\u751F\u4E86\u4EC0\u4E48\u4E8B\uFF0C\u7EC8\u7A76\u6CA1\u6709\u5F20\u53E3\u3002</p><p>\u770B\u5230\u5F2F\u66F2\u7684\u6805\u680F\uFF0C\u4F55\u6C42\u56FD\u4E5F\u53D8\u4E86\u8138\u8272\u3002\u4E8F\u5F97\u767D\u96EA\u83B2\u5E26\u7740\u5927\u53F7\u7684\u94C1\u67B7\uFF0C\u5426\u5219\u8FD9\u94C1\u7B3C\u4E5F\u56F0\u5979\u4E0D\u4F4F\u3002\u8FD9\u5973\u5B50\u624B\u4E0A\u7684\u529B\u9053\uFF0C\u8DB3\u4EE5\u62C9\u5F2F\u94C1\u6746\uFF0C\u5979\u82E5\u9003\u51FA\u751F\u5929\uFF0C\u90A3\u5C31\u4E0D\u662F\u6B7B\u4E00\u4E2A\u4EBA\u90A3\u4E48\u7B80\u5355\u4E86\u3002</p><p>\u72F1\u5352\u5306\u5FD9\u79BB\u5F00\uFF0C\u5730\u7262\u5B89\u9759\u4E0B\u6765\u3002\u8FD9\u5730\u7262\u5730\u65B9\u72ED\u5C0F\uFF0C\u4E24\u53EA\u94C1\u7B3C\u76F8\u8DDD\u4E0D\u8FC7\u4E09\u5C3A\uFF0C\u4F38\u624B\u53EF\u53CA\uFF0C\u5F7C\u6B64\u770B\u5F97\u6E05\u6E05\u695A\u695A\u3002\u767D\u96EA\u83B2\u72B9\u8C6B\u4E00\u4F1A\u513F\uFF0C\u4F4E\u58F0\u95EE\u9053\uFF1A\u300C\u82F1\u83B2\uFF0C\u4F60\u8D70\u7684\u65F6\u5019\u5A18\u662F\u600E\u4E48\u8BF4\u7684\uFF1F\u300D</p><p>\u82F1\u83B2\u7741\u5F00\u773C\u60F3\u7740\u8BF4\u9053\uFF1A\u300C\u5A18\u8BF4\u5B59\u5929\u7FBD\u5E26\u6211\u53BB\u544A\u72B6\uFF0C\u8BA9\u6211\u8DEF\u4E0A\u542C\u8BDD\uFF0C\u8FD8\u7ED9\u4E86\u4ED6\u597D\u591A\u94F6\u94B1\u3002\u300D</p><p>\u300C\u7389\u83B2\u5462\uFF1F\u5979\u8BF4\u4E86\u4EC0\u4E48\uFF1F\u300D</p><p>\u300C\u7389\u83B2\u59D0\u7ED9\u6211\u62FF\u4E86\u5403\u7684\uFF0C\u8BF4\u8DEF\u4E0A\u8FDC\uFF0C\u5230\u4E86\u90A3\u8FB9\u597D\u597D\u4F4F\u7740\uFF0C\u4E0D\u8981\u7740\u6025\uFF0C\u8FC7\u4E9B\u65E5\u5B50\u5979\u4F1A\u6765\u770B\u6211\u3002\u59D0\uFF0C\u4E8C\u59D0\u662F\u4E0D\u662F\u4E5F\u8981\u6765\uFF1F\u300D</p><p>\u674F\u82B1\u6751\u4E0E\u8C7A\u72FC\u5761\u53EA\u6709\u4E8C\u5341\u4F59\u91CC\u5C71\u8DEF\uFF0C\u4F55\u5FC5\u8981\u5E26\u94F6\u94B1\u98DF\u7269\uFF1F\u767D\u96EA\u83B2\u9690\u7EA6\u5BDF\u89C9\u51FA\u91CC\u9762\u7684\u5F02\u72B6\uFF0C\u542C\u5230\u82F1\u83B2\u8FD9\u6837\u95EE\uFF0C\u5FD9\u9053\uFF1A\u300C\u522B\u80E1\u8BF4\u3002\u7389\u83B2\u600E\u4E48\u4F1A\u6765\u8FD9\u91CC\u3002\u300D</p><p>\u518D\u95EE\u65F6\uFF0C\u5F1F\u5F1F\u5374\u662F\u61F5\u61C2\u3002\u4ED6\u53EA\u542C\u8BF4\u53BB\u4EAC\u5E08\u6572\u767B\u95FB\u9F13\u5C31\u80FD\u544A\u5FA1\u72B6\uFF0C\u5374\u4E0D\u77E5\u9053\u4EAC\u5E08\u8DDF\u8FD9\u91CC\u6709\u4EC0\u4E48\u4E0D\u540C\u3002</p><p>\u767D\u96EA\u83B2\u95EE\u4E86\u4E00\u4F1A\u513F\uFF0C\u53EA\u597D\u7F62\u4F11\uFF0C\u8BF4\u9053\uFF1A\u300C\u7A7F\u4E86\u88E4\u5B50\u5427\uFF0C\u5C0F\u5FC3\u522B\u7740\u4E86\u51C9\u3002\u300D</p><p>\u82F1\u83B2\u6307\u4E86\u6307\u5979\u4E0B\u9762\uFF0C\u5C0F\u58F0\u8BF4\uFF1A\u300C\u59D0\uFF0C\u4F60\u88E4\u5B50\u7834\u4E86\u3002\u300D</p><p>\u767D\u96EA\u83B2\u8138\u4E0A\u4E00\u7EA2\uFF0C\u5FD9\u53BB\u5408\u817F\uFF0C\u4F46\u8DB3\u68B0\u672A\u9664\uFF0C\u65E0\u6CD5\u5E76\u7D27\u3002\u5979\u5438\u4E86\u53E3\u6C14\uFF0C\u53F3\u624B\u9AA8\u9ABC\u53D1\u51FA\u4E00\u4E32\u6E05\u8106\u7684\u4F4E\u54CD\uFF0C\u5C45\u7136\u4ECE\u67B7\u6D1E\u4E2D\u4E00\u70B9\u70B9\u8131\u51FA\u3002</p><p>\u859B\u971C\u7075\u773C\u4E2D\u900F\u51FA\u4E00\u4E1D\u70ED\u5207\uFF0C\u53EA\u8981\u5979\u5E2E\u767D\u96EA\u83B2\u9664\u6389\u67B7\u5C3E\u7684\u9500\u5B50\uFF0C\u767D\u96EA\u83B2\u5C31\u80FD\u8131\u5F00\u91CD\u67B7\uFF0C\u62E7\u5F2F\u6805\u680F\u9003\u51FA\u751F\u5929\u3002\u5979\u4E0A\u6B21\u53EA\u662F\u8FD0\u6C14\u4E0D\u597D\uFF0C\u83AB\u540D\u5176\u5999\u5730\u649E\u4E0A\u5434\u5927\u5F6A\uFF0C\u624D\u4F1A\u5931\u624B\u88AB\u64D2\u3002</p><p>\u53EF\u60DC\u8FD9\u4E2A\u5973\u6355\u5FEB\u6789\u81EA\u751F\u5F97\u7F8E\u8C8C\uFF0C\u8111\u5B50\u5374\u662F\u673D\u6728\uFF0C\u7ADF\u7136\u8FD8\u6307\u671B\u5B98\u5E9C\u7ED9\u5979\u6D17\u8131\u7F6A\u540D\u3002\u8FD9\u6BB5\u65E5\u5B50\u859B\u971C\u7075\u51B7\u773C\u65C1\u89C2\uFF0C\u65E9\u5DF2\u770B\u5F97\u660E\u767D\uFF0C\u8FD9\u822C\u72F1\u5352\u662F\u8D2A\u56FE\u767D\u96EA\u83B2\u7684\u7F8E\u8272\uFF0C\u624D\u5047\u81EA\u5DF1\u4E4B\u624B\u7ED9\u5979\u5B89\u4E0A\u7F6A\u540D\uFF0C\u5982\u4ECA\u5979\u7684\u8EAB\u4EFD\u88AB\u4E00\u4E00\u5265\u53BB\uFF0C\u4ECE\u5211\u90E8\u6355\u5FEB\u3001\u7F57\u9704\u5F1F\u5B50\u6CA6\u4E3A\u4E00\u4E2A\u65E0\u540D\u5973\u72AF\uFF0C\u53EA\u5269\u4E0B\u8FD9\u5177\u8EAB\u5B50\uFF0C\u8FDF\u65E9\u90FD\u662F\u72F1\u5352\u4EEC\u53E3\u4E2D\u7F8E\u98DF\u3002</p><p>\u5373\u4F7F\u77E5\u9053\u767D\u96EA\u83B2\u7684\u65E0\u8F9C\uFF0C\u859B\u971C\u7075\u4E5F\u672A\u66FE\u540E\u6094\uFF0C\u5047\u5982\u767D\u96EA\u83B2\u4ECD\u662F\u6355\u5FEB\uFF0C\u6559\u91CC\u5144\u5F1F\u7684\u6027\u547D\u4F1A\u66F4\u5371\u9669\u3002\u53CD\u6B63\u5979\u662F\u671D\u5EF7\u7684\u8D70\u72D7\u9E70\u72AC\uFF0C\u51A4\u6B7B\u4E5F\u7B97\u4E0D\u5F97\u51A4\u6789\u3002</p>',117);function t(i,_){return r}var o=e(a,[["render",t]]);export{o as default};
