import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_28-\u88F8\u5BA1" tabindex="-1"><a class="header-anchor" href="#_28-\u88F8\u5BA1" aria-hidden="true">#</a> 28 \u88F8\u5BA1</h1><p>\u767D\u96EA\u83B2\u53F3\u624B\u8131\u51FA\u94C1\u67B7\uFF0C\u987E\u4E0D\u5F97\u8155\u4E0A\u9C9C\u8840\u6DCB\u6F13\uFF0C\u4FBF\u6025\u5FD9\u63A9\u4F4F\u4E0B\u4F53\u3002\u624B\u6307\u653E\u5165\u81C0\u7F1D\uFF0C\u6307\u5C16\u5FFD\u7136\u4E00\u6ED1\uFF0C\u81C0\u6C9F\u5185\u4E0D\u77E5\u4F55\u65F6\u7ADF\u7136\u6DCC\u6EE1\u4E86\u9ECF\u6DB2\u3002\u5979\u6014\u6014\u62AC\u8D77\u624B\u6307\uFF0C\u8179\u5185\u7684\u538B\u529B\u6E10\u6E10\u660E\u6670\u8D77\u6765\u3002</p><p>\u65E9\u95F4\u5979\u4E0D\u53CA\u6392\u4FBF\u4FBF\u88AB\u5E26\u5230\u5802\u4E0A\uFF0C\u56DE\u6765\u89C1\u7740\u82F1\u83B2\u4E00\u65F6\u5FC3\u795E\u6FC0\u8361\uFF0C\u7ADF\u672A\u53D1\u89C9\u809B\u80A0\u7684\u5F02\u5E38\uFF0C\u6B64\u65F6\u4E00\u52A0\u7559\u610F\uFF0C\u80A0\u5185\u7684\u5145\u80C0\u611F\u987F\u65F6\u5F3A\u70C8\u8D77\u6765\u3002\u767D\u96EA\u83B2\u8DEA\u5728\u51B0\u51B7\u7684\u9752\u77F3\u677F\u4E0A\uFF0C\u53CC\u817F\u5206\u5F00\uFF0C\u767D\u51C0\u7684\u7F8E\u81C0\u4ECE\u8863\u670D\u7684\u7834\u53E3\u5904\u5706\u5706\u7FD8\u8D77\uFF0C\u6291\u5236\u4E0D\u4F4F\u7684\u8F7B\u8F7B\u98A4\u6296\uFF0C\u663E\u7136\u5DF2\u7ECF\u5230\u4E86\u5D29\u6E83\u7684\u8FB9\u7F18\u3002</p><p>\u5979\u7C89\u9762\u6DA8\u7EA2\uFF0C\u7A7A\u51FA\u7684\u53F3\u624B\u50F5\u5728\u534A\u7A7A\uFF0C\u7247\u523B\u540E\u5979\u7A81\u7136\u60F3\u8D77\u4E86\u4EC0\u4E48\uFF0C\u6025\u5FD9\u53BB\u62FF\u8349\u7EB8\uFF0C\u5374\u5DF2\u7ECF\u8FDF\u4E86\u3002\u5979\u5F2F\u7709\u731B\u7136\u6324\u7D27\uFF0C\u75DB\u695A\u5730\u95F7\u54FC\u4E00\u58F0\u3002\u4E00\u80A1\u767D\u8272\u7684\u9ECF\u7A20\u6D53\u6C41\u4ECE\u81C0\u95F4\u8FF8\u51FA\uFF0C\u6E85\u5728\u5730\u4E0A\u3002\u767D\u96EA\u83B2\u62DA\u547D\u6536\u7D27\u809B\u6D1E\uFF0C\u8179\u4E2D\u7684\u80C0\u75DB\u5374\u6108\u53D1\u5F3A\u70C8\uFF0C\u90A3\u53EA\u767D\u5AE9\u7684\u7389\u81C0\u4E0D\u65F6\u6536\u7D27\uFF0C\u53C8\u4E0D\u65F6\u677E\u5F00\uFF0C\u72B9\u5982\u6EF4\u6C34\u7684\u767D\u6843\u822C\uFF0C\u6D46\u6C41\u56DB\u6EA2\u3002</p><p>\u767D\u96EA\u83B2\u6276\u7740\u6805\u680F\uFF0C\u81C0\u4E0B\u6DCB\u6DCB\u6F13\u6F13\u5C3D\u662F\u5E26\u7740\u4F53\u6E29\u7684\u9ECF\u6DB2\uFF0C\u5979\u7741\u5F00\u773C\uFF0C\u770B\u5230\u5F1F\u5F1F\u6B63\u8DB4\u5728\u680F\u8FB9\u597D\u5947\u5730\u76EF\u7740\u81EA\u5DF1\uFF0C\u4E0D\u7531\u53C8\u7F9E\u53C8\u6025\uFF0C\u98A4\u58F0\u9053\uFF1A\u300C\u82F1\u83B2\uFF0C\u4E0D\u8981\u770B\u4E86\uFF01\u59D0\u59D0\u5403\u574F\u4E86\u809A\u5B50\u2026\u2026\u300D\u5979\u5FC3\u91CC\u5374\u5728\u72B9\u7591\uFF0C\u8FD9\u51E0\u65E5\u5979\u6B63\u5728\u8F9F\u8C37\uFF0C\u6BCF\u65E5\u53EA\u559D\u4E86\u5C11\u8BB8\u6E05\u6C34\uFF0C\u5982\u4F55\u4E3A\u5403\u574F\u809A\u5B50\uFF1F\u8F9F\u8C37\u4E4B\u540E\u5979\u4F53\u5185\u6D01\u51C0\u4E4B\u6781\uFF0C\u53C8\u4E3A\u4F55\u4F1A\u6392\u51FA\u8FD9\u6837\u7684\u9ECF\u6DB2\uFF1F</p><p>\u82F1\u83B2\u7728\u7728\u773C\u775B\uFF0C\u5FFD\u7136\u7B11\u4E86\u8D77\u6765\uFF0C\u300C\u59D0\uFF0C\u4F60\u4E0D\u662F\u5403\u574F\u809A\u5B50\u4E86\uFF0C\u662F\u6709\u4EBA\u63D2\u4E86\u4F60\u7684\u5C41\u80A1\uFF0C\u628A\u4E1C\u897F\u5C04\u5728\u91CC\u9762\u4E86\u3002\u4F60\u770B\u2026\u2026\u300D</p><p>\u767D\u96EA\u83B2\u5982\u4E94\u96F7\u8F70\u9876\uFF0C\u60CA\u5F97\u8BF4\u4E0D\u51FA\u8BDD\u6765\u3002\u5979\u5446\u5446\u770B\u7740\u82F1\u83B2\u722C\u4E86\u8D77\u6765\uFF0C\u6485\u8D77\u5C0F\u5C41\u80A1\uFF0C\u5C41\u773C\u513F\u4E00\u9F13\u4E00\u9F13\uFF0C\u50CF\u6392\u4FBF\u4E00\u6837\u6392\u51FA\u4E00\u4E32\u9ECF\u6DB2\uFF0C\u8272\u6CFD\u4E0E\u90A3\u4E9B\u7537\u4EBA\u5C04\u5728\u859B\u971C\u7075\u4F53\u5185\u7684\u4E00\u6837\u3002</p><p>\u300C\u8FD9\u662F\u5218\u5927\u53D4\u521A\u624D\u5C04\u5728\u6211\u5C41\u80A1\u91CC\u7684\uFF0C\u59D0\uFF0C\u4F60\u597D\u5389\u5BB3\uFF0C\u80FD\u62C9\u51FA\u6765\u90A3\u4E48\u591A\uFF0C\u80AF\u5B9A\u6709\u597D\u591A\u7537\u4EBA\u5728\u63D2\u4F60\u7684\u5C41\u80A1\u2026\u2026\u300D</p><p>\u767D\u96EA\u83B2\u5468\u8EAB\u53D1\u51B7\uFF0C\u8FD9\u6837\u7684\u60C5\u5F62\u6709\u591A\u4E45\u4E86\uFF1F\u4E00\u4E2A\u6708\uFF1F\u56DB\u5341\u5929\uFF1F\u5979\u7684\u540E\u5EAD\u88AB\u7537\u4EBA\u7528\u8FC7\u591A\u5C11\u6B21\uFF1F\u4E00\u767E\u8FD8\u662F\u4E8C\u767E\uFF1F\u53EF\u7B11\u5979\u6BCF\u5929\u90FD\u8981\u62C9\u51FA\u4E00\u6EE9\u7537\u4EBA\u7684\u7CBE\u6DB2\uFF0C\u8FD8\u4E00\u76F4\u4EE5\u4E3A\u81EA\u5DF1\u662F\u5E72\u51C0\u7684\u3002</p><p>\u300C\u4F60\u90FD\u77E5\u9053\u5417\uFF1F\u300D\u767D\u96EA\u83B2\u98A4\u58F0\u95EE\u9053\u3002</p><p>\u300C\u96BE\u9053\u4F60\u4E00\u76F4\u4E0D\u77E5\u9053\uFF1F\u300D\u859B\u971C\u7075\u6545\u4F5C\u60CA\u5947\u5730\u8BF4\u9053\uFF1A\u300C\u6BCF\u5929\u665A\u4E0A\u7537\u4EBA\u4EEC\u90FD\u6392\u961F\u6765\u63D2\u4F60\u7684\u5C41\u773C\u513F\uFF0C\u4F60\u7684\u5C41\u773C\u513F\u4EE5\u524D\u53EA\u6709\u8FD9\u4E48\u5927\uFF0C\u73B0\u5728\u5DF2\u7ECF\u8FD9\u4E48\u5927\u4E86\uFF0C\u677E\u5F97\u80FD\u585E\u8FDB\u4E00\u53EA\u62F3\u5934\uFF0C\u5C41\u773C\u513F\u957F\u5728\u81EA\u5DF1\u8EAB\u4E0A\uFF0C\u88AB\u4EBA\u73A9\u6210\u8FD9\u4E2A\u6837\u5B50\uFF0C\u4F60\u8FD8\u4E0D\u77E5\u9053\uFF1F\u300D</p><p>\u300C\u662F\u8C01\uFF1F\u300D</p><p>\u300C\u8FD9\u5C31\u592A\u591A\u4E86\uFF0C\u300D\u859B\u971C\u7075\u82E5\u65E0\u5176\u4E8B\u5730\u8BF4\u9053\uFF1A\u300C\u4F60\u89C1\u8FC7\u7684\u7537\u4EBA\u5DEE\u4E0D\u591A\u90FD\u5E72\u8FC7\u4F60\u7684\u5C41\u773C\u513F\uFF0C\u8FDE\u90A3\u4E2A\u6B7B\u4E86\u7684\u80E1\u4E25\u4E5F\u6CA1\u5C11\u5E72\u3002\u300D\u5979\u779F\u4E86\u767D\u96EA\u83B2\u4E00\u773C\uFF0C\u300C\u4F60\u77E5\u9053\u5417\uFF1F\u90A3\u4E9B\u7537\u4EBA\u5E72\u4F60\u7684\u65F6\u5019\uFF0C\u4F60\u8FD8\u8212\u670D\u5F97\u54FC\u54FC\u5462\u3002\u300D</p><p>\u767D\u96EA\u83B2\u7A81\u7136\u60F3\u8D77\uFF0C\u8F9F\u8C37\u4E4B\u524D\uFF0C\u81EA\u5DF1\u6BCF\u665A\u90FD\u7761\u5F97\u5F88\u719F\uFF0C\u65E9\u4E0A\u9192\u6765\u4FBF\u610F\u4E5F\u5341\u5206\u5F3A\u70C8\u3002\u8F9F\u8C37\u4E4B\u540E\u4E00\u5207\u90FD\u53D8\u5F97\u6B63\u5E38\uFF0C\u76F4\u5230\u6628\u5929\uFF0C\u559D\u4E86\u5B59\u5929\u7FBD\u5012\u7684\u90A3\u7897\u6E05\u6C34\uFF0C\u53C8\u4E00\u6B21\u7761\u5F97\u4E0D\u7701\u4EBA\u4E8B\u3002</p><p>\u767D\u96EA\u83B2\u6CEA\u6D41\u6EE1\u9762\uFF0C\u53CD\u53CD\u590D\u8986\u5730\u95EE\uFF1A\u300C\u662F\u8C01\uFF1F\u662F\u8C01\uFF1F\u300D</p><p>\u859B\u971C\u7075\u5374\u6CA1\u6709\u7ED9\u5979\u60F3\u8981\u7684\u7B54\u6848\uFF0C\u300C\u600E\u4E48\uFF1F\u4F60\u60F3\u77E5\u9053\u8C01\u7B2C\u4E00\u4E2A\u5F00\u4E86\u7684\u5C41\u773C\u513F\uFF0C\u597D\u5AC1\u7ED9\u4ED6\u5417\uFF1F\u767D\u6355\u5934\uFF0C\u60F3\u5F00\u4E9B\uFF0C\u7B2C\u4E00\u4E2A\u8DDF\u7B2C\u4E00\u767E\u4E2A\u53C8\u6709\u4EC0\u4E48\u5173\u7CFB\uFF1F\u6A2A\u7AD6\u4F60\u90FD\u6485\u7740\u5C41\u80A1\u8BA9\u7537\u4EBA\u63D2\u597D\u4E86\u3002\u300D</p><p>\u82F1\u83B2\u4E0D\u660E\u767D\u59D0\u59D0\u4E3A\u4EC0\u4E48\u8981\u54ED\uFF0C\u4ED6\u5C0F\u5FC3\u5730\u8BF4\uFF1A\u300C\u59D0\uFF0C\u521A\u5F00\u59CB\u6709\u70B9\u513F\u75DB\uFF0C\u4EE5\u540E\u5C31\u4E0D\u75DB\u6068\u3002\u5218\u5927\u53D4\u8BF4\uFF0C\u5F80\u540E\u4F1A\u8D8A\u63D2\u8D8A\u8212\u670D\uFF0C\u4E00\u5929\u4E0D\u63D2\u5C31\u4F1A\u60F3\u5462\u2026\u2026\u300D</p><p>\u767D\u96EA\u83B2\u7D27\u7D27\u6293\u7740\u90A3\u5F20\u8349\u7EB8\uFF0C\u773C\u6CEA\u4E00\u6EF4\u6EF4\u6389\u5728\u94C1\u67B7\u4E0A\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u94C1\u7F69\u6253\u5F00\uFF0C\u900F\u8FDB\u6765\u7684\u4E0D\u662F\u5149\u7EBF\uFF0C\u800C\u662F\u6DF1\u6DF1\u7684\u9ED1\u6697\uFF0C\u767D\u96EA\u83B2\u8FD9\u624D\u77E5\u9053\uFF0C\u591C\u5DF2\u7ECF\u6DF1\u4E86\u3002</p><p>\u4E24\u540D\u72F1\u5352\u8D39\u529B\u5730\u62AC\u4E00\u53EA\u6728\u76C6\uFF0C\u6446\u5728\u5730\u7262\u4E2D\u95F4\uFF0C\u7136\u540E\u63D0\u51FA\u70ED\u6C34\uFF0C\u4E00\u6876\u6876\u503E\u5728\u76C6\u4E2D\u3002\u90A3\u53EA\u6728\u76C6\u6709\u4E00\u4EBA\u957F\u77ED\uFF0C\u5F62\u5982\u9A6C\u978D\u3002\u6C34\u6C7D\u84B8\u817E\u800C\u8D77\uFF0C\u767D\u96FE\u822C\u5F25\u6F2B\u5F00\u6765\u3002\u60AC\u5728\u7262\u9876\u7684\u94C1\u94FE\u6E7F\u6DCB\u6DCB\u5782\u5728\u534A\u7A7A\uFF0C\u8F7B\u8F7B\u6447\u6643\u7740\u3002</p><p>\u7247\u523B\u540E\u4E00\u6761\u5927\u6C49\u8D70\u8FDB\u4E86\u5730\u7262\uFF0C\u4ED6\u770B\u4E86\u4E00\u773C\u5F2F\u66F2\u7684\u6805\u680F\uFF0C\u51B7\u7B11\u9053\uFF1A\u300C\u597D\u5927\u7684\u529B\u6C14\u3002\u300D\u8BF4\u7740\u5C06\u94C1\u6805\u4E00\u4E00\u6273\u56DE\u539F\u4F4D\u3002</p><p>\u5C11\u9877\uFF0C\u960E\u7F57\u671B\u8FC8\u7740\u65B9\u6B65\u8E31\u4E86\u8FDB\u6765\uFF0C\u540E\u9762\u8DDF\u7740\u5B59\u5929\u7FBD\u3002\u4ED6\u62AC\u4E86\u62AC\u4E0B\u5DF4\uFF0C\u300C\u628A\u767D\u9006\u7684\u91CD\u67B7\u53BB\u6389\u3002\u300D</p><p>\u5353\u5929\u96C4\u6253\u5F00\u7B3C\u95E8\uFF0C\u5148\u5C01\u4E86\u767D\u96EA\u83B2\u808B\u4E0B\u7684\u7A74\u9053\uFF0C\u767D\u96EA\u83B2\u624B\u4E00\u7D27\uFF0C\u4FBF\u6B32\u7528\u7F29\u9AA8\u529F\u8131\u51FA\u67B7\u9501\uFF0C\u4E0D\u6599\u5B59\u5929\u7FBD\u4ECE\u5353\u5929\u96C4\u80CC\u540E\u4F38\u51FA\u624B\u6765\uFF0C\u5728\u5979\u8179\u95F4\u8F7B\u8F7B\u4E00\u6309\u3002</p><p>\u4E00\u80A1\u719F\u6089\u7684\u529B\u9053\u900F\u4F53\u800C\u5165\uFF0C\u4E39\u7530\u8FD0\u8F6C\u7684\u771F\u6C14\u5F77\u4F5B\u88AB\u871C\u80F6\u9ECF\u4F4F\u4E86\u4E00\u822C\uFF0C\u8D8A\u6765\u8D8A\u6162\uFF0C\u6700\u540E\u4E00\u4E00\u6536\u5165\u6C14\u6D77\u3002\u767D\u96EA\u83B2\u771F\u6C14\u4E00\u6563\uFF0C\u9888\u4E2D\u7684\u94C1\u67B7\u7ACB\u523B\u91CD\u4E86\u6570\u500D\uFF0C\u5979\u52C9\u5F3A\u652F\u6491\u7247\u523B\uFF0C\u8EAB\u5B50\u6447\u6447\u6B32\u5760\u3002</p><p>\u5B59\u5929\u7FBD\u4F38\u624B\u6276\u4F4F\u94C1\u67B7\uFF0C\u7B11\u9053\uFF1A\u300C\u767D\u59D1\u5A18\uFF0C\u5C0F\u5FC3\u4E86\u3002\u300D</p><p>\u767D\u96EA\u83B2\u5FC3\u4E2D\u60CA\u9A87\u83AB\u540D\uFF0C\u5B59\u5929\u7FBD\u9012\u6765\u7684\u771F\u6C14\u5E76\u4E0D\u5F3A\u52B2\uFF0C\u8FD8\u4E0D\u53CA\u81EA\u5DF1\u4E09\u6210\uFF0C\u5374\u50CF\u662F\u81EA\u5DF1\u6240\u7EC3\u529F\u592B\u7684\u514B\u661F\uFF0C\u8F7B\u6613\u5C31\u9501\u4F4F\u81EA\u5DF1\u7684\u771F\u5143\u3002\u518D\u5BDF\u4ED6\u884C\u529F\u8FD0\u6C14\u7684\u8DEF\u5F84\uFF0C\u7ADF\u4F3C\u4E0E\u5979\u540C\u51FA\u4E00\u95E8\uFF0C\u90FD\u662F\u7F57\u9704\u6D3E\u4E0D\u4F20\u4E4B\u79D8\uFF0C\u7CBE\u5FAE\u5904\u72B9\u5728\u81EA\u5DF1\u6240\u5B66\u4E4B\u4E0A\u3002</p><p>\u5353\u5929\u96C4\u5F92\u624B\u63A8\u6389\u9500\u5B50\uFF0C\u6273\u5F00\u673A\u62EC\uFF0C\u5B59\u5929\u7FBD\u4E0D\u77E5\u662F\u529F\u529B\u4E0D\u53CA\uFF0C\u8FD8\u662F\u6709\u610F\u9690\u85CF\uFF0C\u5374\u62FF\u4E86\u51FF\u5B50\u6162\u6162\u6572\u6253\u3002\u7B49\u62D4\u4E0B\u4E24\u7AEF\u7684\u94C1\u9500\uFF0C\u524D\u540E\u4E00\u63A8\uFF0C\u5431\u560E\u4E00\u58F0\uFF0C\u94C1\u67B7\u5206\u5F00\u3002</p><p>\u767D\u96EA\u83B2\u5DF2\u7ECF\u5E26\u4E86\u4E09\u4E2A\u6708\u7684\u91CD\u67B7\uFF0C\u6B64\u65F6\u80A9\u5934\u4E00\u8F7B\uFF0C\u8EAB\u5B50\u5931\u53BB\u5E73\u8861\uFF0C\u9669\u4E9B\u683D\u5012\u3002</p><p>\u4E24\u4EBA\u62BD\u53BB\u67B7\u6D1E\u5185\u4FA7\u7684\u94C1\u6813\uFF0C\u677E\u5F00\u5979\u7684\u624B\u8155\u3002\u767D\u96EA\u83B2\u6276\u7740\u6805\u680F\uFF0C\u51B7\u51B7\u770B\u7740\u8FD9\u51E0\u4E2A\u79BD\u517D\u3002\u5982\u679C\u859B\u971C\u7075\u6CA1\u6709\u8BF4\u8C0E\uFF0C\u4ED6\u4EEC\u90FD\u66FE\u73A9\u8FC7\u5979\u7684\u540E\u5EAD\u3002</p><p>\u767D\u96EA\u83B2\u65E0\u6CD5\u7406\u89E3\u8FD9\u7B97\u4E0D\u7B97\u5978\u6C61\uFF0C\u4F46\u5BF9\u5979\u6765\u8BF4\uFF0C\u8FD9\u90FD\u610F\u5473\u7740\u540C\u6837\u7684\u7ED3\u5C40\uFF1A\u4ED6\u4EEC\u628A\u90A3\u822C\u4E11\u964B\u7684\u7269\u4F53\uFF0C\u653E\u5728\u4E86\u81EA\u5DF1\u8EAB\u4F53\u91CC\u9762\u3002</p><p>\u859B\u971C\u7075\u9760\u5728\u5899\u89D2\u7D27\u5F20\u5730\u770B\u7740\u5353\u5929\u96C4\u7684\u53CC\u811A\uFF0C\u5728\u8FD9\u6240\u76D1\u72F1\u91CC\uFF0C\u5979\u6700\u6015\u7684\u662F\u5353\u5929\u96C4\uFF0C\u5176\u6B21\u624D\u662F\u960E\u7F57\u671B\u3002\u960E\u7F57\u671B\u6311\u65AD\u4E86\u5979\u7684\u811A\u7B4B\u662F\u6015\u5979\u518D\u8D8A\u72F1\uFF0C\u800C\u5047\u5982\u4E0D\u662F\u6848\u5B50\u672A\u7ED3\uFF0C\u90A3\u4E2A\u7ED9\u5979\u5F00\u82DE\u7684\u7537\u4EBA\u4F1A\u6BEB\u4E0D\u72B9\u8C6B\u5730\u628A\u5979\u6D3B\u6D3B\u73A9\u6B7B\u3002</p><p>\u82F1\u83B2\u8EAB\u5B50\u7F29\u5F97\u66F4\u7D27\uFF0C\u4ED6\u5BB3\u6015\u7684\u662F\u5B59\u5929\u7FBD\u3002\u5B59\u5929\u7FBD\u6574\u5929\u5E26\u7740\u7B11\u5BB9\uFF0C\u751A\u81F3\u6CA1\u6709\u5927\u58F0\u5BF9\u4ED6\u8BF4\u8FC7\u8BDD\uFF0C\u82F1\u83B2\u5374\u770B\u5230\u4ED6\u7684\u5F71\u5B50\u5C31\u4F1A\u53D1\u6296\u3002</p><p>\u960E\u7F57\u671B\u4E00\u64A9\u888D\u89D2\uFF0C\u5750\u5728\u7262\u5185\u552F\u4E00\u4E00\u5F20\u6905\u5B50\u4E0A\uFF0C\u5F00\u53E3\u9053\uFF1A\u300C\u767D\u9006\uFF0C\u4F60\u8BF8\u822C\u60C5\u72B6\u672C\u5B98\u65E9\u5DF2\u77E5\u6653\uFF0C\u4ECA\u665A\u4EB2\u6765\u5BA1\u8BAF\uFF0C\u5012\u8981\u770B\u770B\u4F60\u8FD8\u6709\u4F55\u624B\u6BB5\u906E\u63A9\u9690\u7792\uFF01\u300D</p><p>\u767D\u96EA\u83B2\u51B7\u51B7\u9053\uFF1A\u300C\u4F60\u65E2\u7136\u77E5\u6653\uFF0C\u4F55\u5FC5\u518D\u5BA1\uFF1F\u5F85\u628A\u6211\u62BC\u89E3\u4EAC\u5E08\uFF0C\u4E09\u5802\u4F1A\u5BA1\u65F6\uFF0C\u6211\u81EA\u7136\u4F1A\u62DB\u4F9B\u3002\u300D</p><p>\u300C\u8FD8\u6562\u5634\u786C\uFF01\u300D\u960E\u7F57\u671B\u559D\u9053\uFF0C\u53EF\u60DC\u624B\u8FB9\u6CA1\u6709\u60CA\u5802\u6728\uFF0C\u53EA\u597D\u5728\u5927\u817F\u4E0A\u62CD\u4E86\u4E00\u8BB0\u52A9\u5174\uFF0C\u300C\u6765\u4EBA\u554A\uFF01\u5265\u4E86\u5979\u7684\u8863\u886B\uFF01\u770B\u5979\u8FD8\u5982\u4F55\u9690\u7792\uFF01\u300D</p><p>\u767D\u96EA\u83B2\u771F\u6C14\u88AB\u5C01\uFF0C\u529B\u6C14\u53EA\u6BD4\u5BFB\u5E38\u5973\u5B50\u5927\u4E86\u5C11\u8BB8\uFF0C\u7565\u4E00\u6323\u624E\uFF0C\u5C31\u88AB\u5353\u5929\u96C4\u62E7\u4F4F\u624B\u81C2\u8DEA\u5012\u5728\u5730\u3002\u5353\u5929\u96C4\u62E7\u4F4F\u5979\u7684\u624B\u8155\uFF0C\u8FEB\u4F7F\u5979\u633A\u8D77\u80F8\u3002\u960E\u7F57\u671B\u4FEF\u8EAB\u89E3\u5F00\u5979\u9886\u53E3\u7684\u94AE\u6263\uFF0C\u7B11\u9053\uFF1A\u300C\u8FD9\u767D\u9006\u8DDF\u5979\u5A18\u7684\u8D31\u5C44\u4E00\u6837\uFF0C\u90FD\u662F\u5403\u786C\u4E0D\u5403\u8F6F\u3002\u4E0D\u8FC7\u8FD9\u5973\u4EBA\u518D\u5014\uFF0C\u62FF\u9E21\u5DF4\u6233\u51E0\u56DE\u4E5F\u5C31\u5B89\u5206\u4E86\u3002\u300D</p><p>\u767D\u96EA\u83B2\u895F\u53E3\u6563\u5F00\uFF0C\u9732\u51FA\u767D\u5AE9\u7684\u80F8\u812F\uFF0C\u8FD8\u6709\u9888\u4E2D\u4E00\u5708\u7EA2\u75D5\uFF0C\u300C\u72D7\u5B98\uFF0C\u4F60\u8981\u505A\u4EC0\u4E48\uFF01\u300D</p><p>\u960E\u7F57\u671B\u7B11\u9053\uFF1A\u300C\u5F53\u7136\u662F\u7ED9\u4F60\u7684\u5C0F\u5AE9\u5C44\u5F00\u82DE\u4E86\u3002\u5728\u6211\u72F1\u4E2D\u767D\u5403\u4E86\u8FD9\u4E48\u4E45\uFF0C\u603B\u8BE5\u62FF\u4E9B\u4E1C\u897F\u5B5D\u656C\u672C\u5B98\u4E86\u5427\u3002\u8BA9\u672C\u5B98\u5C1D\u5C1D\uFF0C\u662F\u4F60\u7684\u5C44\u5AE9\u5462\uFF0C\u8FD8\u662F\u4F60\u5A18\u7684\u5C44\u5AE9\u3002\u300D</p><p>\u521A\u624D\u90A3\u53E5\u767D\u96EA\u83B2\u53EA\u4EE5\u4E3A\u662F\u9A82\u4EBA\uFF0C\u6B64\u65F6\u624D\u542C\u51FA\u4E0D\u59A5\uFF0C\u300C\u4F60\u6562\u78B0\u6211\u5A18\uFF01\u300D</p><p>\u960E\u7F57\u671B\u6DEB\u7B11\u9053\uFF1A\u300C\u4E0D\u5149\u662F\u78B0\u4E86\uFF0C\u8FD8\u4ECE\u5934\u5230\u811A\u6478\u4E86\u4E2A\u904D\u3002\u4F60\u5A18\u8EAB\u5B50\u53C8\u6ED1\u53C8\u5AE9\uFF0C\u53C8\u542C\u8BDD\u5F97\u7D27\uFF0C\u53EF\u60DC\u5A07\u5F31\u4E86\u70B9\u513F\uFF0C\u672C\u5B98\u53EA\u73A9\u4E86\u4E00\u4E2A\u65F6\u8FB0\uFF0C\u90A3\u5A4A\u5B50\u5C31\u6655\u4E86\u8FC7\u53BB\u3002\u300D</p><p>\u767D\u96EA\u83B2\u98A4\u58F0\u9053\uFF1A\u300C\u4F60\u8EAB\u4E3A\u671D\u5EF7\u547D\u5B98\uFF0C\u79C1\u5978\u826F\u6C11\uFF0C\u4E0D\u6015\u738B\u6CD5\u5417\uFF1F\u300D</p><p>\u300C\u738B\u6CD5\uFF01\u300D\u960E\u7F57\u671B\u54C8\u54C8\u7B11\u9053\uFF1A\u300C\u79C1\u5978\u826F\u6C11\uFF0C\u5F8B\u6CD5\u4E0D\u5BB9\uFF0C\u672C\u5B98\u81EA\u7136\u77E5\u9053\uFF01\u6211\u960E\u7F57\u671B\u514B\u5DF1\u5949\u516C\uFF0C\u600E\u4F1A\u505A\u6789\u6CD5\u52FE\u5F53\uFF1F\u5927\u660E\u5F8B\uFF0C\u8C0B\u5927\u9006\u8005\uFF0C\u7537\u5B50\u82E5\u975E\u51CC\u8FDF\u5904\u6B7B\u4FBF\u662F\u65A9\u9996\u5F03\u5E02\uFF01\u5973\u5B50\u5373\u4F7F\u4E0D\u65A9\u4E5F\u662F\u5145\u519B\u3001\u5B98\u5356\uFF01\u672C\u5B98\u4E0D\u8FC7\u662F\u5978\u4E86\u4E00\u4E2A\u5A4A\u5B50\uFF0C\u72AF\u4E86\u54EA\u5BB6\u738B\u6CD5\uFF1F\u300D</p><p>\u960E\u7F57\u671B\u6478\u7740\u5979\u7684\u8138\u988A\uFF0C\u72DE\u58F0\u9053\uFF1A\u300C\u767D\u5B5D\u5112\u8C0B\u9006\u72AF\u4E0A\uFF0C\u4F60\u767D\u5BB6\u4E5D\u65CF\u90FD\u8131\u4E0D\u4E86\u5E72\u7CFB\uFF0C\u83AB\u8BF4\u4F60\u5A18\uFF0C\u5C31\u662F\u4F60\uFF0C\u8FDF\u65E9\u4E5F\u8981\u8FDB\u4E86\u52FE\u680F\u63A5\u5BA2\u5230\u6B7B\uFF01\u672C\u5B98\u5B98\u79E9\u867D\u7136\u53EA\u6709\u4E5D\u54C1\uFF0C\u4E5F\u662F\u671D\u5EF7\u547D\u5B98\uFF0C\u62D4\u4E86\u4F60\u7684\u7EA2\u7B79\uFF0C\u662F\u4F60\u7684\u798F\u5206\uFF0C\u8FD8\u4E0D\u5FEB\u4E9B\u8C22\u8FC7\u672C\u5B98\u3002\u300D</p><p>\u767D\u96EA\u83B2\u6C14\u6068\u4EA4\u52A0\uFF0C\u5F20\u53E3\u671D\u4ED6\u624B\u4E0A\u54AC\u53BB\uFF0C\u960E\u7F57\u671B\u6B63\u5728\u5F97\u610F\uFF0C\u8EB2\u95EA\u4E0D\u53CA\uFF0C\u987F\u65F6\u75DB\u5F97\u602A\u53EB\u4E00\u58F0\u3002\u5B59\u5929\u7FBD\u5FD9\u6309\u4F4F\u767D\u96EA\u83B2\u988A\u4E0A\u7684\u7A74\u9053\uFF0C\u8FEB\u4F7F\u5979\u677E\u5F00\u7259\u5173\u3002</p><p>\u960E\u7F57\u671B\u597D\u4E0D\u5BB9\u6613\u62D4\u51FA\u624B\u6307\uFF0C\u6307\u6839\u5DF2\u7ECF\u54AC\u51FA\u8840\u6765\uFF0C\u4F24\u53E3\u6DF1\u53EF\u89C1\u9AA8\u3002\u4ED6\u988C\u4E0B\u7684\u77ED\u9AED\u4E00\u6839\u6839\u7AD6\u4E86\u8D77\u6765\uFF0C\u6293\u4F4F\u767D\u96EA\u83B2\u7684\u5934\u53D1\uFF0C\u72E0\u72E0\u62BD\u4E86\u51E0\u4E2A\u8033\u5149\u3002</p><p>\u767D\u96EA\u83B2\u5550\u4E86\u53E3\u5E26\u8840\u7684\u5410\u6CAB\uFF0C\u8F7B\u8511\u5730\u8F6C\u8FC7\u773C\u775B\u3002\u960E\u7F57\u671B\u5FC3\u4E0B\u6012\u6781\uFF0C\u6293\u4F4F\u767D\u96EA\u83B2\u7684\u8863\u895F\u671D\u4E24\u8FB9\u6495\u5F00\u3002\u767D\u96EA\u83B2\u5165\u72F1\u65F6\u8FD8\u662F\u521D\u6625\uFF0C\u91CC\u9762\u7A7F\u4E86\u4EF6\u6DE1\u7EFF\u7684\u5939\u8863\u3002\u960E\u7F57\u671B\u5C06\u5979\u8863\u670D\u91CC\u5916\u6495\u5F00\uFF0C\u9732\u51FA\u91CC\u9762\u6843\u7EA2\u7684\u4E1D\u7EF8\u4EB5\u8863\u3002</p><p>\u5145\u6EE1\u5F39\u6027\u7684\u53CC\u4E73\u9AD8\u9AD8\u8038\u8D77\uFF0C\u5728\u4EB5\u8863\u4E0B\u5FAE\u5FAE\u98A4\u52A8\u3002\u960E\u7F57\u671B\u4E00\u624B\u4E00\u4E2A\u62FF\u5728\u624B\u91CC\uFF0C\u7528\u529B\u4E00\u634F\uFF0C\u767D\u96EA\u83B2\u987F\u65F6\u75DB\u5F97\u53D8\u4E86\u8138\u8272\u3002</p><p>\u300C\u5C0F\u8D31\u4EBA\uFF01\u54AC\u5F97\u8001\u5B50\u597D\u72E0\uFF01\u300D\u960E\u7F57\u671B\u626F\u6389\u5979\u7684\u4EB5\u8863\uFF0C\u556A\u7684\u4E00\u638C\uFF0C\u5728\u5979\u5976\u5B50\u4E0A\u7528\u529B\u62BD\u4E86\u4E00\u8BB0\u3002</p><p>\u5706\u6DA6\u7684\u7389\u4E73\u50CF\u88AB\u629B\u5F00\u822C\u731B\u7136\u4E00\u7529\uFF0C\u649E\u5728\u53E6\u4E00\u53EA\u4E73\u4E0A\uFF0C\u4E73\u4FA7\u6E10\u6E10\u6D6E\u73B0\u4E00\u4E2A\u9C9C\u7EA2\u7684\u638C\u5370\u3002\u867D\u7136\u8FD8\u662F\u5904\u5B50\u4E4B\u8EAB\uFF0C\u767D\u96EA\u83B2\u6BCF\u665A\u809B\u4EA4\uFF0C\u90FD\u5C11\u4E0D\u4E86\u88AB\u4EBA\u6469\u4E73\u629A\u9634\uFF0C\u4E73\u623F\u8F83\u4E4B\u5165\u72F1\u65F6\u8DB3\u8DB3\u5927\u4E86\u4E00\u5708\uFF0C\u4E5F\u4E0D\u4F3C\u5F53\u521D\u7684\u9752\u6DA9\u4E86\u3002</p><p>\u5979\u7684\u4E73\u8089\u53C8\u6ED1\u53C8\u4EAE\uFF0C\u9887\u6709\u51E0\u5206\u6210\u719F\u5987\u4EBA\u7684\u8273\u6001\uFF0C\u4F46\u575A\u633A\u7684\u5F62\u72B6\u5374\u662F\u6210\u719F\u5987\u4EBA\u6240\u6CA1\u6709\u7684\u3002\u4E24\u53EA\u4E73\u5934\u4ECD\u662F\u7C89\u5AE9\u7684\u7EA2\u8272\uFF0C\u6CA1\u6709\u6CBE\u67D3\u4E1D\u6BEB\u6DEB\u8273\u7684\u8272\u6CFD\u3002</p><p>\u960E\u7F57\u671B\u4E00\u624B\u63A2\u5230\u5979\u80EF\u4E0B\uFF0C\u5728\u5BC6\u95ED\u7684\u9634\u6C9F\u5185\u6311\u5F04\uFF0C\u51B7\u7B11\u9053\uFF1A\u300C\u5973\u8D3C\u5973\u532A\u672C\u5B98\u5E72\u5F97\u591A\u4E86\uFF0C\u5012\u8FD8\u6CA1\u73A9\u8FC7\u6355\u5FEB\u3002\u767D\u6355\u5934\uFF0C\u53EF\u83AB\u8BA9\u672C\u5B98\u5931\u671B\u3002\u300D</p><p>\u767D\u96EA\u83B2\u7AED\u529B\u6323\u624E\uFF0C\u90A3\u53EA\u5927\u624B\u5374\u7262\u7262\u8D34\u5728\u79D8\u5904\uFF0C\u4EFB\u5979\u600E\u4E48\u626D\u52A8\u4E5F\u65E0\u6CD5\u6323\u8131\u3002</p><p>\u960E\u7F57\u671B\u624B\u4E0A\u6709\u4F24\uFF0C\u4E0D\u6562\u6CBE\u6C34\uFF0C\u672C\u6765\u6253\u7B97\u540C\u6C60\u5171\u6D74\uFF0C\u4E5F\u53EA\u597D\u8BA9\u5B59\u5929\u7FBD\u3001\u5353\u5929\u96C4\u4EE3\u52B3\u3002\u4E24\u4EBA\u626F\u6389\u767D\u96EA\u83B2\u6495\u70C2\u7684\u8863\u88E4\uFF0C\u628A\u5979\u8D64\u6761\u6761\u62D6\u8FDB\u76C6\u91CC\uFF0C\u64E6\u6D17\u8D77\u6765\u3002</p><p>\u6C34\u82B1\u4E0D\u4F4F\u6E85\u8D77\uFF0C\u5939\u6742\u7740\u7537\u4EBA\u7325\u4EB5\u7684\u7B11\u58F0\u3002\u6726\u80E7\u7684\u6C34\u96FE\u4E2D\uFF0C\u4E00\u5177\u96EA\u767D\u7684\u5A07\u8EAF\u6E10\u6E10\u6E05\u6670\u3002\u767D\u96EA\u83B2\u8DEA\u5728\u76C6\u91CC\uFF0C\u53CC\u624B\u626D\u5230\u80CC\u540E\uFF0C\u5C0F\u81C2\u5E76\u5728\u4E00\u8D77\uFF0C\u88AB\u5353\u5929\u96C4\u4E00\u628A\u63E1\u4F4F\uFF0C\u4F7F\u5979\u4E0A\u8EAB\u540E\u4EF0\uFF0C\u663E\u9732\u51FA\u80F8\u4E73\u4F18\u7F8E\u7684\u66F2\u7EBF\u3002\u5353\u5929\u96C4\u53E6\u4E00\u53EA\u624B\u5219\u5728\u90A3\u4E24\u56E2\u9971\u6EE1\u7684\u4E73\u8089\u95F4\u6765\u56DE\u63C9\u6413\u3002</p><p>\u5B59\u5929\u7FBD\u633D\u8D77\u8896\u5B50\uFF0C\u7B11\u9053\uFF1A\u300C\u767D\u59D1\u5A18\u597D\u798F\u6C14\uFF0C\u6D17\u4E2A\u6FA1\u8FD8\u6709\u6211\u4EEC\u5144\u5F1F\u670D\u4F8D\u3002\u5F85\u4F1A\u513F\u6D17\u5F97\u5E72\u5E72\u51C0\u51C0\uFF0C\u4E56\u4E56\u8BA9\u960E\u5927\u4EBA\u6536\u7528\u4E86\uFF0C\u4EE5\u540E\u6709\u4F60\u7684\u597D\u65E5\u5B50\u3002\u300D</p><p>\u767D\u96EA\u83B2\u6700\u6068\u7684\u662F\u5B59\u5929\u7FBD\uFF0C\u5728\u81EA\u5DF1\u53E3\u91CC\u5C04\u7CBE\u7684\u662F\u4ED6\uFF0C\u5978\u4E86\u81EA\u5DF1\u540E\u5EAD\u7684\u6709\u4ED6\uFF0C\u9A97\u5360\u4E86\u5A18\u4EB2\u8EAB\u5B50\u7684\u66F4\u662F\u4ED6\u3002\u7B49\u5B59\u5929\u7FBD\u5F13\u4E0B\u8170\uFF0C\u767D\u96EA\u83B2\u7A81\u7136\u62AC\u817F\uFF0C\u7528\u529B\u8E39\u5728\u4ED6\u80A9\u5934\u3002</p><p>\u5B59\u5929\u7FBD\u8EAB\u4F53\u4E00\u6643\uFF0C\u4FBF\u5373\u7A33\u4F4F\uFF0C\u968F\u5373\u53CD\u624B\u62FF\u4F4F\u5979\u7684\u811A\u8E1D\uFF0C\u5FC3\u4E0B\u6697\u81EA\u8BB6\u5F02\u3002\u767D\u96EA\u83B2\u8DEA\u5728\u76C6\u91CC\uFF0C\u53C8\u88AB\u7D27\u7D27\u6309\u4F4F\uFF0C\u83AB\u8BF4\u62AC\u817F\uFF0C\u5C31\u662F\u60F3\u52A8\u4E00\u4E0B\u4E5F\u4E0D\u5BB9\u6613\u3002\u53EF\u5979\u53EA\u819D\u90E8\u5FAE\u5206\uFF0C\u63A5\u7740\u4E00\u817F\u4FBF\u7B14\u76F4\u8E22\u51FA\uFF0C\u8FD1\u5728\u54AB\u5C3A\uFF0C\u7ADF\u770B\u4E0D\u51FA\u5979\u662F\u5982\u4F55\u884C\u529F\u8FD0\u529B\u3002</p><p>\u767D\u96EA\u83B2\u4E00\u6761\u6E7F\u6DCB\u6DCB\u7684\u7389\u817F\u67B6\u5728\u534A\u7A7A\uFF0C\u51FA\u6C34\u96EA\u85D5\u822C\u767D\u7F8E\u52A8\u4EBA\u3002\u5B59\u5929\u7FBD\u6525\u7740\u5979\u7684\u7EA4\u8DB3\u7B11\u9053\uFF1A\u300C\u767D\u59D1\u5A18\u6789\u81EA\u6A21\u6837\u4FCA\u4FCF\uFF0C\u8FD9\u53CC\u811A\u6BD4\u4F60\u5A18\u53EF\u5DEE\u5F97\u8FDC\u4E86\u3002\u4F60\u5A18\u90A3\u53CC\u5C0F\u811A\u5C0F\u5DE7\u73B2\u73D1\uFF0C\u53C8\u767D\u53C8\u8F6F\uFF0C\u4E00\u624B\u5C31\u80FD\u63E1\u4F4F\u3002\u53EF\u60DC\u767D\u59D1\u5A18\u662F\u53CC\u534A\u5927\u7684\u811A\uFF0C\u5BA2\u4EBA\u89C1\u4E86\u591A\u534A\u4E0D\u559C\u3002\u4E0D\u8FC7\u8FD9\u6761\u5927\u817F\u2026\u2026\u300D</p><p>\u5B59\u5929\u7FBD\u987A\u7740\u7389\u817F\u4F18\u7F8E\u7684\u66F2\u7EBF\uFF0C\u671D\u5979\u5927\u817F\u5185\u4FA7\u6478\u53BB\uFF0C\u4E00\u8FB9\u7B11\u9053\uFF1A\u300C\u679C\u771F\u6ED1\u5F97\u8DDF\u7F0E\u5B50\u4E00\u6837\uFF0C\u8FD9\u53CC\u817F\u8981\u76D8\u5230\u8170\u4E0A\u2026\u2026\u300D\u8BF4\u7740\u4ED6\u58F0\u97F3\u4E00\u6EDE\uFF0C\u5374\u662F\u60F3\u8D77\u4E86\u4E39\u5A18\u5750\u5728\u960E\u7F57\u671B\u8170\u4E0A\u7684\u4E00\u5E55\u3002</p><p>\u867D\u7136\u5149\u7740\u8EAB\u5B50\u88AB\u4EBA\u620F\u5F04\uFF0C\u767D\u96EA\u83B2\u8138\u4E0A\u5374\u6CA1\u6709\u5BFB\u5E38\u5973\u5B50\u7684\u7F9E\u602F\u3002\u5979\u54AC\u7259\u6323\u52A8\u7389\u817F\uFF0C\u6D51\u7136\u4E0D\u987E\u81EA\u5DF1\u79D8\u5883\u6BD5\u9732\u7684\u7F9E\u6001\u3002\u859B\u971C\u7075\u8FDC\u8FDC\u770B\u7740\uFF0C\u773C\u4E2D\u9732\u51FA\u4E00\u4E1D\u4E0D\u6613\u5BDF\u89C9\u7684\u6CE2\u52A8\u3002\u8FD9\u4E00\u5E55\u5979\u5DF2\u7ECF\u7B49\u4E86\u8BB8\u4E45\uFF0C\u5979\u539F\u672C\u4EE5\u4E3A\u767D\u96EA\u83B2\u4F1A\u6323\u624E\u54ED\u53EB\u2014\u2014\u4E16\u4E0A\u6709\u54EA\u4E2A\u5973\u5B50\u88AB\u4EBA\u593A\u8D70\u7B2C\u4E00\u6B21\u65F6\u4F1A\u65E0\u52A8\u4E8E\u8877\u5462\uFF1F\u53EF\u767D\u96EA\u83B2\u8FDE\u4E00\u6EF4\u6CEA\u90FD\u6CA1\u6709\u6D41\u3002\u4E5F\u8BB8\u77E5\u9053\u81EA\u5DF1\u88AB\u4EBA\u73A9\u8FC7\u53E6\u4E00\u5904\u8089\u6D1E\u65F6\uFF0C\u5979\u7684\u6CEA\u5DF2\u7ECF\u6D41\u5E72\u4E86\u3002</p><p>\u960E\u7F57\u671B\u5305\u597D\u624B\u6307\uFF0C\u9634\u7740\u8138\u62FF\u6765\u4E00\u679D\u957F\u67C4\u9B03\u5237\uFF0C\u300C\u556A\u300D\u7684\u5728\u767D\u96EA\u83B2\u4E73\u4E0A\u62BD\u4E86\u4E00\u8BB0\u3002\u7389\u4E73\u7ACB\u523B\u6D6E\u73B0\u51FA\u4E00\u6761\u5BF8\u8BB8\u5BBD\u7684\u80BF\u75D5\uFF0C\u6A2A\u7740\u7A7F\u8FC7\u638C\u5370\u3002</p><p>\u960E\u7F57\u671B\u7528\u9B03\u5237\u538B\u4F4F\u90A3\u7C92\u7C89\u7EA2\u7684\u4E73\u5934\uFF0C\u72E0\u72E0\u62E7\u52A8\u3002\u5C16\u5229\u7684\u9B03\u6BDB\u5F77\u4F5B\u65E0\u6570\u7EC6\u9488\u5728\u654F\u611F\u7684\u4E73\u6912\u4E0A\u523A\u8FC7\uFF0C\u767D\u96EA\u83B2\u7709\u5934\u8F7B\u98A4\uFF0C\u5F3A\u5FCD\u7740\u4E00\u58F0\u4E0D\u542D\u3002\u7B49\u9B03\u5237\u79BB\u5F00\uFF0C\u53D8\u786C\u7684\u4E73\u5934\u7ACB\u523B\u5F39\u8D77\uFF0C\u8868\u9762\u5F77\u4F5B\u6E17\u8840\u822C\u53D8\u5F97\u9C9C\u7EA2\u3002</p><p>\u960E\u7F57\u671B\u62FF\u5237\u5B50\u8638\u4E86\u6C34\uFF0C\u50CF\u5237\u6D17\u4E00\u4EF6\u5668\u5177\u822C\uFF0C\u64E6\u6D17\u7740\u5C11\u5973\u5A07\u5AE9\u7684\u7389\u4F53\u3002\u767D\u96EA\u83B2\u8EAB\u4F53\u7531\u767D\u800C\u7EA2\uFF0C\u5F77\u4F5B\u6D82\u4E86\u5C42\u6DE1\u6DE1\u7684\u80ED\u8102\uFF0C\u5728\u6E29\u6C34\u91CC\u4E00\u6D78\uFF0C\u53C8\u8FC5\u901F\u53D8\u5F97\u767D\u7699\u3002\u6574\u5177\u8EAB\u4F53\u72B9\u5982\u6E05\u6C34\u6D17\u8FC7\u7684\u8102\u7389\uFF0C\u6676\u83B9\u5254\u900F\uFF0C\u500D\u52A0\u660E\u8273\u3002</p><p>\u300C\u517B\u719F\u7684\u6BCD\u732A\u603B\u8981\u5148\u6D17\u51C0\u4E86\uFF0C\u624D\u597D\u6740\u6765\u5403\u3002\u300D\u4E3A\u4E86\u64E6\u6D17\u65B9\u4FBF\uFF0C\u767D\u96EA\u83B2\u88AB\u6309\u5F97\u4F0F\u5728\u76C6\u8FB9\uFF0C\u960E\u7F57\u671B\u628A\u7AF9\u67C4\u585E\u5728\u5979\u817F\u7F1D\u91CC\u5939\u597D\uFF0C\u8D77\u8EAB\u8131\u53BB\u5B98\u670D\u3002</p><p>\u4E8B\u5230\u4E34\u5934\uFF0C\u767D\u96EA\u83B2\u53CD\u800C\u6CA1\u6709\u4E86\u5BB3\u6015\u3002\u5979\u7528\u6E05\u6670\u7684\u58F0\u97F3\u8BF4\u9053\uFF1A\u300C\u72D7\u5B98\uFF0C\u4F60\u6562\u8FB1\u6211\uFF0C\u6211\u767D\u96EA\u83B2\u82E5\u662F\u4E00\u65E5\u4E0D\u6B7B\uFF0C\u5FC5\u8981\u5728\u4E09\u5802\u516C\u5BA1\u65F6\u8BA8\u4E2A\u516C\u9053\uFF01\u300D</p><p>\u8C0B\u53CD\u5927\u6848\u9700\u5211\u90E8\u3001\u5927\u7406\u5BFA\u3001\u90FD\u5BDF\u9662\u4E09\u5802\u4F1A\u5BA1\uFF0C\u960E\u7F57\u671B\u4E00\u4ECB\u5FAE\u672B\u5C0F\u540F\uFF0C\u95F9\u5927\u4E86\u4E5F\u4E0D\u597D\u6536\u573A\u3002\u4ED6\u51B7\u7B11\u4E00\u58F0\uFF0C\u300C\u767D\u6355\u5934\u65E0\u987B\u591A\u8651\uFF0C\u672C\u5B98\u81EA\u6709\u624B\u6BB5\u8BA9\u4F60\u4F5C\u58F0\u4E0D\u5F97\u3002\u300D</p><p>\u767D\u96EA\u83B2\u904D\u4F53\u6C34\u8FF9\uFF0C\u79C0\u53D1\u6E7F\u6DCB\u6DCB\u5782\u5728\u4E86\u80A9\u4FA7\uFF0C\u7C89\u80CC\u5149\u6D01\u5982\u7389\uFF0C\u7EA4\u8170\u7F8E\u81C0\u66F2\u7EBF\u73B2\u73D1\uFF0C\u51F8\u51F9\u6709\u81F4\u3002\u5979\u8FD9\u4E2A\u59FF\u52BF\u5B59\u5929\u7FBD\u518D\u719F\u6089\u4E0D\u8FC7\uFF0C\u4EE5\u5F80\u809B\u4EA4\u65F6\uFF0C\u5979\u90FD\u662F\u5982\u6B64\u9732\u51FA\u540E\u5EAD\uFF0C\u4EFB\u4EBA\u5728\u5979\u5C41\u773C\u513F\u91CC\u8FDB\u51FA\u53D6\u4E50\u3002\u53EA\u662F\u6B64\u523B\u767D\u96EA\u83B2\u8EAB\u65E0\u5BF8\u7F15\uFF0C\u4F53\u5982\u6DA6\u7389\uFF0C\u808C\u80A4\u80DC\u96EA\uFF0C\u8D64\u88F8\u7684\u80F4\u4F53\u66F4\u4E3A\u8BF1\u4EBA\u3002</p><p>\u767D\u96EA\u83B2\u7684\u53CC\u817F\u6781\u4E3A\u4FEE\u957F\uFF0C\u867D\u7136\u8DEA\u7740\uFF0C\u96EA\u81C0\u4ECD\u7FD8\u51FA\u76C6\u6CBF\uFF0C\u7389\u7403\u822C\u6563\u53D1\u7740\u6E7F\u6DCB\u6DCB\u7684\u6C34\u5149\u3002\u6BD4\u8D77\u4E39\u5A18\u7684\u80A5\u7F8E\u591A\u59FF\uFF0C\u5979\u7684\u81C0\u90E8\u66F4\u52A0\u7D27\u51D1\uFF0C\u4E5F\u66F4\u4E3A\u5706\u6DA6\uFF0C\u7EC6\u5AE9\u7684\u81C0\u8089\u5145\u6EE1\u5F39\u6027\uFF0C\u4E00\u638C\u62CD\u4E0B\u53EA\u5FAE\u5FAE\u4E00\u98A4\uFF0C\u58F0\u97F3\u6E05\u8106\u53EF\u95FB\u3002</p><p>\u300C\u6574\u5929\u770B\u7740\u859B\u5A4A\u5B50\u6328\u808F\uFF0C\u8FD9\u91CC\u65E9\u6025\u51FA\u706B\u4E86\u5427\uFF1F\u300D\u960E\u7F57\u671B\u6DEB\u7B11\u9053\uFF1A\u300C\u4ECA\u65E5\u6211\u5C31\u9042\u4E86\u4F60\u7684\u5FC3\u613F\u2026\u2026\u300D</p><p>\u767D\u96EA\u83B2\u53CC\u624B\u88AB\u5236\uFF0C\u8170\u8EAB\u53C8\u88AB\u5B59\u5929\u7FBD\u6309\u4F4F\uFF0C\u53CC\u817F\u65E0\u6CD5\u52A8\u4F5C\uFF0C\u53EA\u80FD\u633A\u8D77\u81C0\u90E8\uFF0C\u5C06\u5973\u513F\u5BB6\u6700\u9690\u79D8\u7684\u7F9E\u5904\u66B4\u9732\u5728\u4F17\u4EBA\u9762\u524D\u3002\u90A3\u53CC\u5927\u624B\u5728\u81C0\u4E0A\u8086\u65E0\u5FCC\u60EE\u5730\u62CD\u6253\u6478\u5F04\uFF0C\u7A81\u7136\u63D2\u8FDB\u81C0\u7F1D\uFF0C\u5C06\u5BC6\u95ED\u7684\u81C0\u8089\u63B0\u5F97\u655E\u5F00\u3002</p><p>\u767D\u96EA\u83B2\u795E\u60C5\u51B7\u6DE1\uFF0C\u8EAB\u4F53\u5374\u4E00\u7D27\u3002</p><p>\u960E\u7F57\u671B\u60CA\u5947\u7684\u300C\u54A6\u300D\u4E86\u4E00\u58F0\uFF0C\u8131\u53E3\u9053\uFF1A\u300C\u8FD9\u5A4A\u5B50\u7684\u5C41\u773C\u513F\u600E\u4E48\u8FD9\u4E48\u5927\uFF1F\u300D</p><p>\u767D\u96EA\u83B2\u8138\u4E0A\u987F\u65F6\u4E00\u7247\u8840\u7EA2\u3002\u4E00\u4E2A\u5904\u5B50\uFF0C\u4E34\u5F00\u82DE\u65F6\u624D\u77E5\u9053\u81EA\u5DF1\u7684\u540E\u5EAD\u65E9\u5DF2\u88AB\u4EBA\u73A9\u5927\uFF0C\u4E0D\u7531\u5F97\u5979\u4E0D\u7F9E\u6127\u3002\u859B\u971C\u7075\u53D7\u8FB1\u7684\u573A\u9762\u5728\u5979\u773C\u524D\u4E0D\u65AD\u6D6E\u73B0\uFF0C\u5979\u8DB4\u5728\u5730\u4E0A\uFF0C\u7537\u4EBA\u4EEC\u63B0\u5F00\u5979\u7684\u5C41\u80A1\uFF0C\u628A\u90A3\u6839\u4E11\u964B\u7684\u5668\u5B98\u63D2\u8FDB\u5979\u8EAB\u4F53\u91CC\u9762\u2026\u2026</p><p>\u300C\u554A\u2014\u2014\u300D</p><p>\u4E00\u4E2A\u575A\u786C\u7684\u7269\u4F53\u72E0\u72E0\u5730\u6363\u5165\u4F53\u5185\u3002\u5979\u8FD8\u662F\u7B2C\u4E00\u6B21\u771F\u5207\u4F53\u4F1A\u5230\u88AB\u5F02\u7269\u4FB5\u5165\u7684\u611F\u89C9\uFF0C\u539F\u6765\u5973\u4EBA\u7684\u8EAB\u4F53\u7ADF\u662F\u5982\u6B64\u5BB9\u6613\u88AB\u4FB5\u72AF\uFF0C\u51E0\u4E4E\u6CA1\u6709\u4EFB\u4F55\u62B5\u6297\uFF0C\u7AF9\u67C4\u4FBF\u8F7B\u6613\u6345\u5165\u5C41\u773C\u513F\uFF0C\u649E\u5728\u80A0\u9053\u6DF1\u5904\u3002</p><p>\u960E\u7F57\u671B\u60BB\u60BB\u5730\u9053\uFF1A\u300C\u677E\u5F97\u8DDF\u4F60\u5A18\u7684\u88E4\u5E26\u4E00\u6837\uFF0C\u8FD8\u88C5\u70C8\u5973\u3002\u300D\u4ED6\u624B\u4E0B\u6CA1\u4E00\u53EA\u597D\u9E1F\uFF0C\u767D\u96EA\u83B2\u62AB\u67B7\u5E26\u9501\u5728\u7262\u91CC\u5173\u4E86\u8FD1\u4E09\u4E2A\u6708\uFF0C\u82E5\u4E0D\u60F3\u65B9\u8BBE\u6CD5\u5730\u5077\u8165\u624D\u662F\u90AA\u4E8B\uFF0C\u53EA\u662F\u5077\u8FD9\u4E48\u72E0\uFF0C\u5927\u5927\u51FA\u4E4E\u4ED6\u7684\u610F\u6599\uFF0C\u677E\u6210\u5C31\u6837\uFF0C\u8FD8\u4E0D\u5982\u4E39\u5A18\u7684\u540E\u5EAD\u6709\u8DA3\u5462\u3002</p><p>\u960E\u7F57\u671B\u62FF\u4F4F\u9B03\u5237\u4E00\u62E7\uFF0C\u767D\u96EA\u83B2\u7C89\u81C0\u7ACB\u523B\u5F20\u5F00\uFF0C\u9732\u51FA\u91CC\u9762\u7EA2\u5AE9\u7684\u83CA\u809B\u3002\u9ED1\u9EC4\u7684\u7AF9\u67C4\u6DF1\u6DF1\u63D2\u5728\u96EA\u767D\u7684\u5C41\u80A1\u91CC\uFF0C\u5C06\u5C41\u773C\u513F\u6491\u6210\u6241\u957F\u5F62\u72B6\u3002\u767D\u96EA\u83B2\u53EA\u89C9\u80A0\u58C1\u5F77\u4F5B\u7C98\u5728\u786C\u7269\u4E0A\uFF0C\u968F\u7740\u7AF9\u67C4\u7684\u8F6C\u52A8\u88AB\u5E26\u5F97\u79FB\u4F4D\uFF0C\u76F4\u5230\u7D27\u7D27\u7F20\u4F4F\uFF0C\u65E0\u6CD5\u677E\u8131\u3002</p><p>\u960E\u7F57\u671B\u53C8\u8F6C\u4E86\u534A\u5708\uFF0C\u7136\u540E\u5411\u5916\u4E00\u62D4\uFF0C\u53EA\u89C1\u7AF9\u67C4\u5E26\u7740\u4E00\u5708\u7EA2\u8089\u4ECE\u51F9\u9677\u7684\u5C41\u773C\u513F\u91CC\u731B\u7136\u7FFB\u51FA\u3002\u767D\u96EA\u83B2\u7AED\u529B\u6536\u7D27\u809B\u6D1E\uFF0C\u4F46\u7AF9\u67C4\u4E0E\u80A0\u58C1\u7D27\u7D27\u7F20\u5728\u4E00\u8D77\uFF0C\u5DE8\u5927\u7684\u529B\u9053\uFF0C\u5F77\u4F5B\u8981\u5C06\u5979\u7684\u809B\u857E\u8FDE\u540C\u80A0\u9053\u4E00\u5E76\u62FD\u51FA\u4F53\u5916\u3002</p><p>\u96EA\u767D\u7684\u81C0\u8089\u95F4\uFF0C\u4E00\u6735\u9C9C\u7EA2\u7684\u8089\u82B1\u7531\u5C0F\u5230\u5927\uFF0C\u8D8A\u7EFD\u8D8A\u5F00\uFF0C\u7A81\u7136\u95F4\u500F\u5FFD\u4E00\u6536\uFF0C\u6574\u4E2A\u94BB\u5165\u81C0\u5185\u3002\u6C89\u95F7\u7684\u75DB\u695A\u4ECE\u8179\u8154\u6DF1\u5904\u8FC5\u901F\u8513\u5EF6\uFF0C\u767D\u96EA\u83B2\u8EAB\u5B50\u5411\u524D\u4E00\u503E\uFF0C\u8138\u8272\u53D8\u5F97\u53D1\u767D\u3002\u960E\u7F57\u671B\u8FD9\u4E00\u6345\u4E1D\u6BEB\u6CA1\u6709\u987E\u53CA\u5979\u540E\u5EAD\u7684\u5A07\u5AE9\uFF0C\u529B\u9053\u4E4B\u5927\uFF0C\u51E0\u4E4E\u6345\u7A7F\u4E86\u80A0\u58C1\u3002</p><p>\u960E\u7F57\u671B\u60CA\u96F7\u822C\u7684\u58F0\u97F3\u5728\u8033\u8FB9\u54CD\u8D77\uFF0C\u300C\u767D\u96EA\u83B2\uFF01\u4F60\u52FE\u7ED3\u9006\u532A\uFF0C\u5984\u56FE\u8C0B\u53CD\uFF01\u7A76\u7ADF\u62DB\u8FD8\u662F\u4E0D\u62DB\uFF01\u300D</p>',81);function t(n,_){return a}var o=e(r,[["render",t]]);export{o as default};
