import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_34-\u52AB\u6301" tabindex="-1"><a class="header-anchor" href="#_34-\u52AB\u6301" aria-hidden="true">#</a> 34 \u52AB\u6301</h1><p>\u300C\u8D8A\u53D1\u7684\u6C34\u7075\u4E86\u2026\u2026\u300D</p><p>\u4E00\u4E32\u70DB\u6CEA\u6EDA\u843D\u4E0B\u6765\uFF0C\u6389\u5728\u5C11\u5973\u7EA2\u5AE9\u7684\u4E73\u5C16\u4E0A\u3002\u767D\u96EA\u83B2\u8EAB\u5B50\u4E00\u98A4\uFF0C\u4E73\u5934\u4E0D\u7531\u81EA\u4E3B\u5730\u6162\u6162\u9F13\u8D77\uFF0C\u8721\u6DB2\u5728\u4E0A\u9762\u51DD\u6210\u4E00\u5C42\u7EA2\u4EAE\u7684\u786C\u58F3\uFF0C\u5F77\u4F5B\u767D\u7389\u4E0A\u5D4C\u7740\u7684\u4E00\u7C92\u739B\u7459\u3002</p><p>\u5979\u8EAB\u5B50\u6A2A\u5728\u5E8A\u4E0A\uFF0C\u53CC\u811A\u88AB\u5206\u5F00\u540A\u5728\u5E8A\u89D2\uFF0C\u960E\u7F57\u671B\u4E00\u624B\u629A\u5F04\u7740\u5979\u7CBE\u81F4\u7684\u7389\u6237\uFF0C\u5FC3\u91CC\u6697\u81EA\u8D5E\u53F9\u3002\u5982\u6B64\u5C24\u7269\u53EF\u60DC\u662F\u4E2A\u5973\u56DA\uFF0C\u5982\u679C\u662F\u4E2A\u620F\u5B50\u79FE\u5993\uFF0C\u4E70\u6765\u505A\u623F\u5C0F\u59BE\uFF0C\u6BCF\u65E5\u6469\u6332\u72CE\u73A9\uFF0C\u4EE5\u6D88\u6C38\u591C\uFF0C\u5C82\u4E0D\u5FEB\u54C9\u3002</p><p>\u4E00\u77AC\u95F4\uFF0C\u960E\u7F57\u671B\u771F\u6709\u79CD\u51B2\u52A8\uFF0C\u62FC\u7740\u524D\u7A0B\u4E0D\u8981\uFF0C\u62A5\u4E2A\u56E0\u75C5\u8EAB\u6545\uFF0C\u628A\u767D\u96EA\u83B2\u6536\u5165\u623F\u4E2D\u79C1\u7528\uFF0C\u65E5\u65E5\u5FEB\u6D3B\u3002\u4E0D\u8FC7\u60F3\u5230\u5979\u4E00\u8EAB\u529F\u592B\uFF0C\u960E\u7F57\u671B\u7ACB\u523B\u6253\u6D88\u4E86\u5FF5\u5934\u3002</p><p>\u960E\u7F57\u671B\u628A\u8721\u70DB\u79FB\u5230\u5979\u8179\u4E0B\uFF0C\u6620\u7740\u5979\u5149\u6DA6\u5706\u8038\u7684\u7389\u961C\uFF0C\u6162\u6162\u9053\uFF1A\u300C\u597D\u8BDD\u90FD\u5DF2\u8BF4\u5C3D\uFF0C\u8FD9\u4E9B\u65E5\u4F60\u4E5F\u4EAB\u53D7\u5F97\u591F\u4E86\u3002\u767D\u59D1\u5A18\uFF0C\u4F60\u53EF\u60F3\u597D\uFF0C\u62DB\u8FD8\u662F\u4E0D\u62DB\uFF1F\u300D</p><p>\u767D\u96EA\u83B2\u95ED\u4E0A\u4E86\u773C\uFF0C\u5BF9\u4ED6\u4E0D\u7406\u4E0D\u776C\u3002\u960E\u7F57\u671B\u624B\u4E00\u503E\uFF0C\u70DB\u6CEA\u6E85\u5728\u767D\u5AE9\u7684\u7389\u961C\u4E0A\uFF0C\u5FAE\u5FAE\u4E00\u6643\uFF0C\u4FBF\u51DD\u4E0A\u9762\u3002\u6EDA\u70EB\u7684\u8721\u6DB2\u4F7F\u767D\u96EA\u83B2\u4E0B\u8179\u9690\u9690\u62BD\u52A8\uFF0C\u7EA4\u7EC6\u7684\u9634\u6BDB\u88AB\u8721\u6DB2\u7C98\u4F4F\uFF0C\u67D4\u987A\u5730\u8D34\u5728\u7389\u961C\u4E0A\u3002</p><p>\u6BB7\u7EA2\u7684\u70DB\u6CEA\u4ECE\u706B\u7130\u4E0B\u4E0D\u4F4F\u6EDA\u843D\uFF0C\u4E0D\u591A\u65F6\u5C31\u5C06\u5C11\u5973\u7684\u9634\u961C\u6574\u4E2A\u8986\u4F4F\u3002\u51E0\u9053\u8721\u6DB2\u4ECE\u7389\u6237\u8FB9\u7F18\u6DCC\u4E0B\uFF0C\u72B9\u5982\u672A\u5E72\u7684\u8840\u6CEA\u3002</p><p>\u300C\u597D\u5014\u7684\u8D31\u4EBA\uFF01\u300D\u960E\u7F57\u671B\u5265\u5F00\u5C11\u5973\u67D4\u5AE9\u7684\u7389\u6237\uFF0C\u5C06\u70DB\u6CEA\u6EF4\u5728\u90A3\u7C92\u5C0F\u5C0F\u7684\u82B1\u8482\u4E0A\u3002</p><p>\u300C\u5440\u2026\u2026\u300D\u767D\u96EA\u83B2\u75DB\u53EB\u4E00\u58F0\uFF0C\u8EAB\u5B50\u5267\u70C8\u5730\u98A4\u6296\u8D77\u6765\u3002\u5973\u5B50\u79D8\u5904\u6BD4\u4F53\u8868\u654F\u611F\u5341\u500D\uFF0C\u82B1\u8482\u53C8\u662F\u6700\u654F\u611F\u7684\u6240\u5728\uFF0C\u88AB\u8721\u6DB2\u4E00\u70EB\uFF0C\u6574\u4E2A\u4E0B\u4F53\u90FD\u9707\u98A4\u8D77\u6765\u3002</p><p>\u767D\u96EA\u83B2\u62DA\u547D\u5408\u7D27\u53CC\u817F\uFF0C\u4F46\u70DB\u6CEA\u8FD8\u662F\u6BEB\u65E0\u963B\u788D\u5730\u6EF4\u8FDB\u9634\u6237\u3002\u4E0D\u591A\u65F6\uFF0C\u9634\u6237\u4E2D\u4E00\u53EA\u4E0D\u8D77\u773C\u7684\u5C0F\u5B54\u7A81\u7136\u4E00\u677E\uFF0C\u4E00\u80A1\u5C3F\u6DB2\u76F4\u55B7\u51FA\u6765\u3002</p><p>\u300C\u679C\u7136\u662F\u9A9A\u8D27\u2026\u2026\u300D\u960E\u7F57\u671B\u5C0F\u6307\u6311\u8D77\uFF0C\u6309\u4F4F\u5C3F\u53E3\u3002\u55B7\u6D8C\u7684\u5C3F\u6DB2\u5835\u5728\u8089\u5B54\u4E2D\uFF0C\u5728\u6307\u4E0B\u4E00\u9F13\u4E00\u9F13\uFF0C\u4F20\u6765\u67D4\u817B\u7684\u9707\u98A4\u3002\u960E\u7F57\u671B\u5FC3\u4E0B\u4E00\u52A8\uFF0C\u6307\u5C16\u7528\u529B\uFF0C\u671D\u90A3\u53EA\u7EC6\u5C0F\u7684\u8089\u5B54\u5185\u6345\u53BB\u3002</p><p>\u767D\u96EA\u83B2\u53CC\u817F\u7EF7\u7D27\uFF0C\u67D4\u9888\u6602\u8D77\uFF0C\u5589\u4E2D\u53D1\u51FA\u75DB\u82E6\u5730\u5410\u6C14\u58F0\u3002\u4E0B\u4F53\u4F20\u6765\u6495\u88C2\u822C\u914D\u75DB\u610F\uFF0C\u5DF2\u7ECF\u6D41\u51FA\u7684\u5C3F\u6DB2\uFF0C\u91CD\u53C8\u88AB\u6324\u5165\u8180\u80F1\uFF0C\u968F\u4E4B\u800C\u6765\u7684\uFF0C\u8FD8\u6709\u4E00\u6839\u8D85\u8FC7\u8089\u5B54\u5BB9\u7EB3\u6781\u9650\u7684\u5F02\u7269\u3002</p><p>\u960E\u7F57\u671B\u5C06\u6574\u6839\u624B\u6307\u90FD\u6363\u5165\u8089\u5B54\uFF0C\u7136\u540E\u5728\u5145\u6EE1\u5F39\u6027\u7684\u8154\u9053\u5185\u62BD\u9001\u8D77\u6765\u3002\u7EC6\u5C0F\u7684\u8089\u5B54\u88AB\u7D27\u7D27\u6491\u5F00\uFF0C\u7C89\u8272\u7684\u5AE9\u8089\u5305\u88F9\u7740\u7C97\u9ED1\u8272\u624B\u6307\uFF0C\u751F\u7740\u9ED1\u6BDB\u7684\u6307\u8282\u65F6\u8FDB\u65F6\u51FA\uFF0C\u5C3F\u6DB2\u5728\u8089\u6D1E\u91CC\u6D41\u52A8\u7FFB\u6EDA\uFF0C\u80C0\u75DB\u4E00\u6CE2\u6CE2\u6269\u6563\u5F00\u6765\u3002</p><p>\u960E\u7F57\u671B\u62D4\u51FA\u624B\u6307\uFF0C\u5C3F\u6DB2\u7ACB\u523B\u4ECE\u6491\u5F00\u6570\u500D\u7684\u6CC4\u51FA\uFF0C\u63A5\u7740\u53C8\u7528\u529B\u6363\u5165\u3002\u767D\u96EA\u83B2\u4E00\u6CE1\u5C3F\u65AD\u65AD\u7EED\u7EED\uFF0C\u76F4\u6D41\u4E86\u4E00\u67F1\u9999\u7684\u5DE5\u592B\u624D\u6CC4\u5B8C\u3002\u7EC6\u5C0F\u7684\u8089\u5B54\u88AB\u624B\u6307\u6345\u5F97\u5F20\u5F00\uFF0C\u9732\u51FA\u7EA2\u7EA2\u7684\u8089\u58C1\uFF0C\u72B9\u5982\u4E0B\u4F53\u65B0\u5F00\u4E86\u4E00\u4E2A\u8089\u7A74\u3002</p><p>\u960E\u7F57\u671B\u7529\u4E86\u7529\u624B\u6307\uFF0C\u5265\u5F00\u767D\u96EA\u83B2\u7684\u7389\u6237\uFF0C\u628A\u6EE1\u84C4\u7684\u8721\u6DB2\u5168\u4E2D\u5012\u5165\u5176\u4E2D\u3002\u767D\u96EA\u83B2\u53D1\u51FA\u4E00\u58F0\u60CA\u75DB\u4EA4\u52A0\u7684\u5C16\u53EB\uFF0C\u5A07\u8EAF\u5267\u98A4\uFF0C\u7C89\u767D\u7684\u53CC\u817F\u5728\u7A7A\u4E2D\u4E0D\u4F4F\u626D\u52A8\u3002</p><p>\u960E\u7F57\u671B\u677E\u5F00\u624B\uFF0C\u8721\u6DB2\u5DF2\u7ECF\u51DD\u7ED3\u6210\u5757\uFF0C\u786C\u786C\u6491\u5F00\u4E86\u7389\u6237\uFF0C\u5F77\u4F5B\u4E00\u53EA\u83F1\u5F62\u7684\u7EA2\u5B9D\u77F3\uFF0C\u5D4C\u5728\u767D\u817B\u7684\u7389\u80A1\u95F4\u3002\u9694\u7740\u534A\u900F\u660E\u7684\u8721\u5757\uFF0C\u9690\u9690\u80FD\u770B\u5230\u5C11\u5973\u67D4\u5AE9\u7684\u82B1\u74E3\uFF0C\u5C0F\u5DE7\u7684\u82B1\u8482\uFF0C\u9634\u6237\u5F20\u5F00\u7684\u4F18\u7F8E\u8F6E\u5ED3\uFF0C\u8FD8\u6709\u5706\u5F20\u7684\u5C3F\u5B54\u548C\u5E95\u90E8\u51F9\u9677\u7684\u871C\u7A74\u3002</p><p>\u960E\u7F57\u671B\u4F38\u6307\u5728\u5979\u9634\u6237\u4E2D\u5F39\u4E86\u5F39\uFF0C\u51B7\u7B11\u9053\uFF1A\u300C\u82E5\u4E0D\u662F\u4F60\u751F\u4E86\u4E2A\u597D\u5C44\uFF0C\u960E\u67D0\u5C82\u4F1A\u5927\u8D39\u5468\u6298\u3002\u82E5\u6CA1\u4E86\u8FD9\u4E1C\u897F\uFF0C\u4F60\u73B0\u5728\u591A\u534A\u5C38\u4F53\u4E5F\u81ED\u4E86\uFF01\u300D</p><p>\u960E\u7F57\u671B\u628A\u9633\u5177\u7C97\u7684\u8721\u70DB\u6345\u8FDB\u4E86\u767D\u96EA\u83B2\u809B\u4E2D\uFF0C\u5750\u4E0B\u6765\u72E0\u72E0\u704C\u4E86\u676F\u9152\uFF0C\u53EB\u6765\u859B\u971C\u7075\uFF0C\u628A\u5979\u7684\u5934\u6309\u5728\u80EF\u95F4\uFF0C\u773C\u775B\u76EF\u7740\u767D\u96EA\u83B2\u3002</p><p>\u76D8\u7740\u9F99\u7EB9\u7684\u7EA2\u70DB\u4ECE\u767D\u96EA\u83B2\u81C0\u4E0B\u4F38\u51FA\u4E00\u622A\uFF0C\u706B\u7130\u5728\u5979\u80A1\u95F4\u6447\u66F3\uFF0C\u5C06\u5C11\u5973\u79D8\u5904\u6620\u5F97\u4E00\u7247\u5149\u660E\u3002\u4E00\u53CC\u96EA\u767D\u7684\u7389\u817F\u5927\u5F20\u7740\uFF0C\u4E0B\u4F53\u655E\u9732\uFF0C\u9C9C\u7EA2\u7684\u70DB\u6CEA\u4ECE\u7389\u961C\u4E00\u76F4\u51DD\u5230\u7389\u6237\u5E95\u90E8\uFF0C\u4E0E\u871C\u8089\u7EA0\u7F20\u7740\u7ED3\u6210\u4E00\u5C42\u786C\u58F3\uFF0C\u5728\u70DB\u5149\u4E0B\u9690\u9690\u95EA\u4EAE\u3002</p><p>\u960E\u7F57\u671B\u8089\u68D2\u5728\u859B\u971C\u7075\u53E3\u4E2D\u8D8A\u6DA8\u8D8A\u5927\uFF0C\u4ED6\u8E22\u5F00\u859B\u971C\u7075\uFF0C\u8D70\u5230\u767D\u96EA\u83B2\u817F\u95F4\uFF0C\u6293\u4F4F\u5979\u7684\u7389\u961C\u4E00\u62FD\u3002\u90A3\u56E2\u767D\u8F6F\u7684\u96EA\u8089\u731B\u7136\u5F39\u8D77\uFF0C\u4F20\u6765\u4E86\u4E00\u9635\u5267\u75DB\u3002\u539A\u539A\u7684\u8721\u5757\u5E94\u624B\u63ED\u4E0B\uFF0C\u9634\u961C\u4E0A\u90A3\u5C42\u7EA4\u8F6F\u7684\u9634\u6BDB\u4E5F\u88AB\u5C3D\u6570\u626F\u843D\u3002\u88AB\u8721\u6DB2\u70EB\u5F97\u5FAE\u7EA2\u7684\u9634\u961C\u6E17\u51FA\u4E00\u5C42\u7EC6\u5BC6\u7684\u8840\u73E0\uFF0C\u63A5\u7740\u8D8A\u6765\u8D8A\u5927\u3002</p><p>\u5361\u5728\u81C0\u7F1D\u91CC\u7684\u8721\u70DB\u8D8A\u70E7\u8D8A\u77ED\uFF0C\u706B\u82D7\u51E0\u4E4E\u89E6\u5230\u4E86\u767D\u767D\u7684\u81C0\u8089\uFF0C\u960E\u7F57\u671B\u5F39\u706D\u4E86\u70DB\u706B\uFF0C\u987A\u52BF\u5C06\u8721\u70DB\u6574\u4E2A\u63A8\u5165\u80A0\u9053\u3002\u809B\u6D1E\u54C6\u55E6\u7740\u6536\u7D27\uFF0C\u6EA2\u51FA\u4E00\u4E32\u70DB\u6CEA\u3002</p><p>\u960E\u7F57\u671B\u62B9\u53BB\u767D\u96EA\u83B2\u9634\u961C\u4E0A\u7684\u8840\u8FF9\uFF0C\u63A5\u7740\u5206\u5F00\u7389\u6237\uFF0C\u5C06\u7262\u7262\u7C98\u5728\u91CC\u9762\u7684\u8721\u5757\u6574\u4E2A\u63ED\u4E0B\u3002\u8721\u5757\u4E00\u9762\u5149\u4EAE\uFF0C\u4E00\u9762\u5374\u51F8\u51F9\u8D77\u4F0F\uFF0C\u52FE\u52D2\u51FA\u9634\u6237\u7684\u5F62\u72B6\uFF0C\u8FDE\u82B1\u74E3\u4E0A\u7684\u7EC6\u5FAE\u8936\u76B1\uFF0C\u4E5F\u6E05\u6670\u53EF\u8FA8\u3002</p><p>\u767D\u96EA\u83B2\u4E0B\u4F53\u88AB\u70EB\u5F97\u53D1\u70ED\uFF0C\u871C\u7A74\u5FAE\u5FAE\u5145\u8840\u80BF\u80C0\uFF0C\u63D2\u5F04\u65F6\u53C8\u70ED\u53C8\u7D27\uFF0C\u500D\u89C9\u9165\u723D\u3002</p><p>\u960E\u7F57\u671B\u4E00\u8FB9\u72E0\u5E72\uFF0C\u4E00\u8FB9\u5FC3\u91CC\u8D70\u9A6C\u706F\u4F3C\u7684\u6253\u7740\u4E3B\u610F\u3002</p><p>\u767D\u96EA\u83B2\u8F6F\u786C\u4E0D\u5403\uFF0C\u6B7B\u9876\u7740\u4E0D\u613F\u62DB\u4F9B\uFF0C\u773C\u89C1\u65F6\u671F\u65E5\u8FD1\uFF0C\u82E5\u662F\u5C06\u5979\u63D0\u89E3\u5165\u4EAC\uFF0C\u88AB\u4F55\u6E05\u6CB3\u5BDF\u51FA\u5185\u60C5\uFF0C\u4E0D\u4F46\u524D\u529F\u5C3D\u5F03\uFF0C\u800C\u4E14\u6027\u547D\u6709\u5371\u3002\u960E\u7F57\u671B\u72E0\u72E0\u76EF\u7740\u767D\u96EA\u83B2\uFF0C\u771F\u628A\u8001\u5B50\u903C\u6025\uFF0C\u5E72\u8106\u5F04\u6B7B\u4F60\u8FD9\u4E2A\u8D31\u4EBA\uFF0C\u4EE5\u7EDD\u540E\u60A3\uFF01</p><p>\u960E\u7F57\u671B\u6B63\u5E72\u5F97\u8D77\u52B2\uFF0C\u767D\u96EA\u83B2\u7A81\u7136\u6A31\u5507\u4E00\u5F20\uFF0C\u5410\u51FA\u4E00\u53E3\u9C9C\u8840\uFF0C\u63A5\u7740\u300C\u7BF7\u300D\u7684\u4E00\u58F0\uFF0C\u6323\u65AD\u4E86\u811A\u8E1D\u4E0A\u7684\u767D\u7EEB\u3002</p><p>\u960E\u7F57\u671B\u9B42\u98DE\u9B44\u6563\uFF0C\u4E00\u8BB0\u9ED1\u864E\u638F\u5FC3\uFF0C\u671D\u767D\u96EA\u83B2\u7684\u80F8\u53E3\u51FB\u53BB\u3002\u767D\u96EA\u83B2\u4E0A\u8EAB\u5FAE\u5FAE\u4E00\u6643\uFF0C\u907F\u5F00\u62F3\u950B\uFF0C\u63A5\u7740\u4E24\u624B\u4E00\u7FFB\uFF0C\u8155\u4E0A\u7684\u767D\u7EEB\u5BF8\u5BF8\u65AD\u88C2\u3002</p><p>\u960E\u7F57\u671B\u614C\u5FD9\u62D4\u8EAB\u5411\u540E\u9000\u53BB\uFF0C\u767D\u96EA\u83B2\u5DF2\u6323\u5F00\u53E6\u4E00\u6761\u7389\u817F\uFF0C\u66F2\u819D\u76D8\u4F4F\u4ED6\u7684\u8170\u80CC\uFF0C\u63A5\u7740\u7D20\u624B\u4E00\u626C\uFF0C\u5361\u4F4F\u4ED6\u7684\u5589\u5499\u3002</p><p>\u8FD9\u51E0\u4E0B\u5154\u8D77\u9E5C\u843D\uFF0C\u524E\u90A3\u95F4\u960E\u7F57\u671B\u5C31\u88AB\u5236\u4F4F\u3002\u960E\u7F57\u671B\u9633\u5177\u8FD8\u63D2\u5728\u5979\u4F53\u5185\uFF0C\u5979\u4E00\u817F\u76D8\u7740\u960E\u7F57\u671B\u7684\u8170\u80CC\uFF0C\u5012\u50CF\u662F\u820D\u4E0D\u5F97\u8BA9\u4ED6\u62D4\u4E0D\u51FA\u6765\u3002\u767D\u96EA\u83B2\u9762\u4E0A\u4E00\u7EA2\uFF0C\u5E76\u6307\u70B9\u5728\u960E\u7F57\u671B\u8170\u95F4\u3002\u960E\u7F57\u671B\u95F7\u54FC\u4E00\u58F0\uFF0C\u8138\u6DA8\u6210\u732A\u809D\u8272\uFF0C\u66F2\u819D\u6B6A\u5728\u4E00\u65C1\u3002</p><p>\u300C\u4F60\u51B2\u5F00\u4E86\u7A74\u9053\uFF1F\u600E\u4E48\u529E\uFF1F\u300D\u859B\u971C\u7075\u53C8\u60CA\u53C8\u559C\u3002</p><p>\u767D\u96EA\u83B2\u53C8\u5410\u4E86\u53E3\u9C9C\u8840\uFF0C\u95ED\u76EE\u8C03\u606F\u8D77\u6765\u3002\u859B\u971C\u7075\u77E5\u9053\u5979\u62DA\u6B7B\u51B2\u7A74\uFF0C\u6025\u9700\u8C03\u606F\uFF0C\u4FBF\u4E0D\u518D\u5F00\u53E3\u3002\u5979\u628A\u660F\u53A5\u7684\u960E\u7F57\u671B\u62D6\u5230\u4E00\u65C1\uFF0C\u4FA7\u8033\u5C0F\u5FC3\u542C\u7740\u5916\u9762\u7684\u52A8\u9759\u3002</p><p>\u7B49\u767D\u96EA\u83B2\u7741\u5F00\u773C\u775B\uFF0C\u859B\u971C\u7075\u6084\u58F0\u9053\uFF1A\u300C\u73B0\u5728\u6B63\u662F\u65F6\u5019\uFF0C\u5916\u9762\u770B\u5B88\u90FD\u5728\u7761\u7740\uFF0C\u6211\u8D70\u8FC7\u4E00\u6B21\uFF0C\u77E5\u9053\u8DEF\u5F84\uFF0C\u51FA\u4E86\u76D1\u72F1\u79BB\u674F\u82B1\u6751\u53EA\u6709\u4E00\u4E2A\u65F6\u8FB0\u7684\u5C71\u8DEF\uFF0C\u5982\u679C\u987A\u5229\uFF0C\u5929\u4EAE\u65F6\u6211\u4EEC\u5C31\u80FD\u51FA\u5C71\u3002\u300D</p><p>\u300C\u8981\u8D70\u4F60\u81EA\u5DF1\u8D70\u3002\u300D\u767D\u96EA\u83B2\u9053\uFF1A\u300C\u6211\u4E0D\u8D70\u3002\u300D</p><p>\u300C\u4F60\u75AF\u4E86\uFF01\u300D\u859B\u971C\u7075\u60CA\u53EB\u9053\uFF0C\u300C\u7B49\u5929\u4EAE\uFF0C\u72F1\u91CC\u7684\u4EBA\u90FD\u77E5\u9053\u4E86\uFF0C\u4F60\u5C31\u662F\u60F3\u8D70\u4E5F\u8D70\u4E0D\u4E86\uFF01\u300D</p><p>\u300C\u6211\u4E0D\u4F1A\u8D70\u7684\u3002\u300D</p><p>\u859B\u971C\u7075\u77AA\u4E86\u5979\u8DB3\u8DB3\u534A\u664C\uFF0C\u6447\u5934\u9053\uFF1A\u300C\u6211\u8FD9\u8F88\u5B50\u90FD\u6CA1\u89C1\u8FC7\u4F60\u8FD9\u4E48\u8822\u7684\u5973\u4EBA\u3002\u4F60\u4E0D\u8D70\uFF0C\u96BE\u9053\u8FD8\u771F\u8981\u7B49\u5B98\u5E9C\u7ED9\u4F60\u7FFB\u6848\u5417\uFF1F\u300D</p><p>\u300C\u4E0D\u9519\u3002\u6211\u4E00\u4E2A\u4EBA\u8981\u8D70\uFF0C\u65E9\u5C31\u8D70\u4E86\u3002\u4F46\u82F1\u83B2\u8FD8\u5728\u72F1\u91CC\uFF0C\u8FD8\u6709\u6211\u5A18\uFF0C\u6211\u59B9\u59B9\u7389\u83B2\u3002\u5C31\u7B97\u6211\u6709\u672C\u4E8B\u628A\u5979\u4EEC\u90FD\u5E26\u8D70\uFF0C\u6210\u4E86\u8D8A\u72F1\u7684\u94A6\u72AF\u8FD8\u80FD\u5F80\u54EA\u91CC\u9003\u5462\uFF1F\u300D</p><p>\u767D\u96EA\u83B2\u770B\u4E86\u859B\u971C\u7075\u4E00\u773C\uFF0C\u300C\u5176\u5B9E\u6211\u4E5F\u4E0D\u8BE5\u8BA9\u4F60\u8D70\u7684\u3002\u4F60\u82E5\u8D70\u4E86\uFF0C\u5C31\u6CA1\u4EBA\u80FD\u8BC1\u660E\u6211\u7684\u6E05\u767D\u3002\u4E0D\u8FC7\u6211\u4E0D\u4F1A\u7BA1\u4F60\uFF0C\u4F60\u8981\u8D70\u5C31\u8D76\u7D27\u8D70\u5427\u3002\u300D</p><p>\u859B\u971C\u7075\u55E4\u7B11\u4E86\u4E00\u58F0\uFF0C\u300C\u6211\u7684\u811A\u8DDB\u4E86\uFF0C\u6CA1\u6709\u4F60\u5E26\u7740\uFF0C\u6211\u8FDE\u76D1\u72F1\u7684\u5927\u5899\u4E5F\u722C\u4E0D\u8FC7\u53BB\u3002\u5929\u554A\uFF0C\u4F60\u600E\u4E48\u8FD8\u4E0D\u660E\u767D\u5462\uFF1F\u5929\u4E0B\u4E4C\u9E26\u4E00\u822C\u9ED1\uFF0C\u60F3\u8BA9\u5B98\u5E9C\u7ED9\u4F60\u4E2A\u516C\u9053\uFF0C\u6BD4\u767B\u5929\u8FD8\u96BE\uFF01\u300D</p><p>\u859B\u971C\u7075\u7D22\u6027\u8BF4\u9053\uFF1A\u300C\u6211\u7239\u7239\u5C31\u662F\u767D\u83B2\u6559\u7684\u7EA2\u9633\u771F\u4EBA\uFF0C\u4F60\u82E5\u628A\u6211\u9001\u56DE\u53BB\uFF0C\u83AB\u8BF4\u4F60\u5A18\uFF0C\u4F60\u5F1F\u5F1F\u59B9\u59B9\uFF0C\u5C31\u662F\u518D\u591A\u5341\u500D\u4EB2\u4EBA\uFF0C\u4E5F\u80FD\u5B89\u7F6E\uFF01\u300D</p><p>\u300C\u5B89\u7F6E\u4E86\u505A\u4EC0\u4E48\uFF1F\u8DDF\u4F60\u4EEC\u4E00\u8D77\u505A\u9006\u532A\u4E48\uFF1F\u300D</p><p>\u859B\u971C\u7075\u6C14\u7ED3\uFF0C\u300C\u9006\u532A\u53C8\u600E\u4E48\u4E86\uFF01\u8FD9\u5929\u4E0B\u53C8\u4E0D\u662F\u59D3\u4E86\u6731\u7684\uFF0C\u82E5\u662F\u6211\u7239\u7239\u6210\u4E8B\uFF0C\u521B\u4E0B\u5F25\u52D2\u4E16\u754C\uFF0C\u5C82\u4E0D\u6BD4\u73B0\u5728\u597D\u4E0A\u767E\u500D\u3002\u300D\u770B\u5230\u767D\u96EA\u83B2\u4E0D\u4EE5\u4E3A\u7136\u7684\u773C\u795E\uFF0C\u859B\u971C\u7075\u626C\u624B\u8BF4\u9053\uFF1A\u300C\u597D\u597D\u597D\uFF0C\u6211\u4E0D\u8DDF\u4F60\u4E89\u3002\u4F60\u77A7\uFF0C\u6211\u73B0\u5728\u8D70\u4E5F\u8D70\u4E0D\u5F97\uFF0C\u8981\u4E0D\u4F60\u628A\u6211\u9001\u51FA\u5927\u72F1\uFF0C\u5269\u4E0B\u7684\u4E8B\u5C31\u4E0D\u7528\u4F60\u7BA1\u4E86\u3002\u300D</p><p>\u300C\u4E0D\u3002\u300D</p><p>\u300C\u53C8\u600E\u4E48\u4E86\uFF01\u300D</p><p>\u300C\u6211\u8BF4\u8FC7\u4E0D\u4F1A\u7BA1\u4F60\u3002\u4F60\u8981\u8D8A\u72F1\u6211\u4E0D\u7BA1\uFF0C\u4F46\u6211\u4E0D\u4F1A\u5E2E\u4F60\u3002\u300D</p><p>\u859B\u971C\u7075\u5446\u4E86\u534A\u664C\uFF0C\u300C\u771F\u662F\u88AB\u4F60\u5BB3\u6B7B\u4E86\u2026\u2026\u300D\u5979\u98A4\u58F0\u9053\uFF1A\u300C\u767D\u96EA\u83B2\uFF01\u6211\u8981\u6B7B\u5230\u8FD9\u5E2E\u79BD\u517D\u624B\u91CC\uFF0C\u975E\u8981\u627E\u4F60\u507F\u547D\uFF01\u300D\u8BF4\u7740\u6276\u5899\u671D\u5916\u8D70\u53BB\u3002</p><p>\u767D\u96EA\u83B2\u6162\u6162\u8D70\u5230\u5E8A\u540E\uFF0C\u8E72\u4E0B\u8EAB\u5B50\u3002\u90A3\u6839\u70E7\u6B8B\u7684\u8721\u70DB\u5361\u5728\u76F4\u80A0\u91CC\uFF0C\u5979\u7528\u5C3D\u529B\u6C14\u624D\u5C06\u5B83\u4E00\u70B9\u70B9\u6392\u51FA\u4F53\u5916\u3002\u5FFD\u7136\u5E37\u5E55\u4E00\u6643\uFF0C\u9732\u51FA\u4E00\u5F20\u4FCF\u8138\u3002</p><p>\u300C\u4F60\u600E\u4E48\u8FD8\u4E0D\u8D70\uFF1F\u300D\u767D\u96EA\u83B2\u95EE\u9053\u3002</p><p>\u300C\u6211\u8D70\u4E0A\u697C\u68AF\u5DF2\u7ECF\u6CA1\u4E86\u529B\u6C14\u3002\u5916\u9762\u7A97\u6237\u4E5F\u6362\u4E86\u94C1\u7684\uFF0C\u53EA\u597D\u722C\u56DE\u6765\uFF0C\u8DDF\u4F60\u4E00\u9053\u7B49\u6B7B\u597D\u4E86\u3002\u300D</p><p>\u300C\u672A\u5FC5\u5C31\u662F\u6B7B\u3002\u300D\u767D\u96EA\u83B2\u62B9\u51C0\u8EAB\u4F53\uFF0C\u7A7F\u4E0A\u8863\u88D9\u3002</p><p>\u300C\u4F60\u662F\u8D4C\u4E5D\u6B7B\u4E00\u751F\u91CC\u7684\u4E00\u751F\uFF0C\u6211\u5462\uFF0C\u6A2A\u7AD6\u90FD\u662F\u4E2A\u6B7B\u3002\u300D\u859B\u971C\u7075\u62B1\u819D\u9760\u5728\u6805\u680F\u4E0A\uFF0C\u626C\u8138\u9053\uFF1A\u300C\u5176\u5B9E\u5C31\u662F\u9003\u51FA\u53BB\u53C8\u80FD\u600E\u4E48\u6837\u5462\uFF1F\u8EAB\u5B50\u810F\u4E86\uFF0C\u817F\u4E5F\u8DDB\u4E86\u2026\u2026\u6211\u53EA\u662F\u60F3\u89C1\u7239\u7239\uFF0C\u4ED6\u8FD8\u4E0D\u77E5\u9053\u6211\u5728\u8FD9\u91CC\u5462\u2026\u2026\u300D\u8BF4\u7740\u859B\u971C\u7075\u5C0F\u58F0\u54ED\u6CE3\u8D77\u6765\u3002</p><p>\u767D\u96EA\u83B2\u6C89\u9ED8\u4E00\u4F1A\u513F\uFF0C\u300C\u6211\u9001\u4F60\u51FA\u53BB\u3002\u300D</p><p>\u300C\u4E0D\u7528\u4E86\u3002\u300D\u859B\u971C\u7075\u56D4\u7740\u9F3B\u5B50\u8BF4\uFF1A\u300C\u9664\u975E\u4F60\u80FD\u628A\u6211\u9001\u5230\u5C71\u4E0B\uFF0C\u518D\u627E\u4E00\u8F86\u8F66\u3002</p><p>\u4E0D\u7136\u6211\u6CA1\u722C\u5230\u674F\u82B1\u6751\uFF0C\u5C31\u4F1A\u88AB\u72FC\u5403\u4E86\u3002\u300D</p><p>\u9001\u5230\u5C71\u4E0B\u7EDD\u65E0\u53EF\u80FD\u3002\u5979\u73B0\u5728\u6B66\u529F\u5269\u4E0D\u5230\u4E00\u6210\uFF0C\u9632\u8EAB\u5C1A\u4E14\u4E0D\u6613\uFF0C\u4F55\u51B5\u8981\u722C\u51FA\u8FD9\u767E\u91CC\u5927\u5C71\u3002</p><p>\u859B\u971C\u7075\u62B9\u5E72\u773C\u6CEA\uFF0C\u300C\u4F60\u51C6\u5907\u600E\u4E48\u529E\u5462\uFF1F\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u960E\u7F57\u671B\u6C89\u7740\u8138\uFF0C\u4E00\u8A00\u4E0D\u53D1\u3002\u4E24\u5973\u5012\u4E5F\u6CA1\u6346\u4ED6\uFF0C\u53EA\u5C01\u4E86\u4ED6\u7684\u7A74\u9053\uFF0C\u8BA9\u4ED6\u52A8\u5F39\u4E0D\u5F97\u3002\u697C\u68AF\u9876\u4E0A\u7684\u94C1\u7F69\u6253\u5F00\u4E00\u534A\uFF0C\u900F\u51FA\u6668\u66E6\u7684\u5FAE\u5149\u3002\u5916\u9762\u6709\u4EBA\u558A\u9053\uFF1A\u300C\u5927\u80C6\u9006\u532A\uFF01</p><p>\u5FEB\u653E\u4E86\u960E\u5927\u4EBA\uFF0C\u9976\u4F60\u4EEC\u4E0D\u6B7B\uFF01\u300D</p><p>\u300C\u808F\u4F60\u5988\u7684\u9C8D\u8001\u4E8C\uFF01\u7ED9\u6211\u95ED\u5634\uFF01\u300D\u960E\u7F57\u671B\u543C\u9053\u3002\u5916\u9762\u987F\u65F6\u5B89\u9759\u4E0B\u6765\u3002</p><p>\u960E\u7F57\u671B\u91CD\u91CD\u5598\u4E86\u53E3\u7C97\u6C14\uFF0C\u9053\uFF1A\u300C\u4E00\u8F88\u5B50\u73A9\u9E1F\uFF0C\u5012\u88AB\u9E1F\u5544\u4E86\u773C\u3002\u59D3\u767D\u7684\u5A4A\u5B50\uFF0C\u4F60\u5212\u4E0B\u9053\u6765\uFF01\u300D</p><p>\u300C\u7B2C\u4E00\uFF0C\u5211\u90E8\u6765\u4EBA\u4E4B\u524D\uFF0C\u4F60\u4E0D\u80FD\u79BB\u5F00\u5730\u7262\u3002\u300D</p><p>\u300C\u597D\uFF01\u6C42\u7740\u8DDF\u8001\u5B50\u540C\u623F\uFF0C\u6709\u4F55\u4E0D\u53EF\uFF01\u8981\u662F\u518D\u80FD\u540C\u5E8A\u5171\u6795\uFF0C\u5929\u5929\u5E72\u4F60\u7684\u5C0F\u5C44\uFF0C\u8001\u5B50\u5728\u8FD9\u7262\u91CC\u5F85\u4E00\u8F88\u5B50\uFF0C\u4E5F\u53EA\u5F53\u662F\u4E0A\u4E86\u8D9F\u5993\u9662\uFF01\u300D</p><p>\u767D\u96EA\u83B2\u6CA1\u6709\u7406\u4F1A\u4ED6\u7684\u6C61\u8A00\u79FD\u8BED\uFF0C\u300C\u7B2C\u4E8C\uFF0C\u5403\u7684\u7528\u7684\u8BA9\u4ED6\u4EEC\u653E\u5728\u697C\u68AF\u4E0A\uFF0C\u4E0D\u51C6\u5728\u91CC\u9762\u505A\u624B\u811A\u3002\u300D</p><p>\u300C\u4E0D\u5C31\u662F\u6709\u4E86\u4E1C\u897F\u8001\u5B50\u5148\u5403\uFF0C\u4F60\u4EEC\u5403\u5269\u7684\uFF1F\u5916\u9762\u7684\u542C\u5230\u4E86\u5417\uFF1F\u6709\u836F\u6709\u6BD2\u90FD\u7ED9\u6211\u6536\u8D77\u6765\uFF01\u836F\u6B7B\u8001\u5B50\u4E8B\u5C0F\uFF0C\u8FD9\u4FE9\u5A4A\u5B50\u8981\u51B2\u51FA\u53BB\uFF0C\u4F60\u4EEC\u4E00\u4E2A\u90FD\u6D3B\u4E0D\u4E86\u3002\u300D</p><p>\u300C\u7B2C\u4E09\uFF0C\u4E0D\u8BB8\u518D\u78B0\u6211\u5A18\u3002\u300D</p><p>\u960E\u7F57\u671B\u51B7\u54FC\u4E00\u58F0\uFF0C\u300C\u8FD9\u8BDD\u542C\u7740\u90AA\u6027\uFF01\u7BA1\u5929\u7BA1\u5730\uFF0C\u6211\u80FD\u7BA1\u4F4F\u522B\u4EBA\u7684\u9E21\u5DF4\u4F60\u5A18\u7684\u5C44\uFF1F\u4EBA\u5BB6\u613F\u6328\u613F\u808F\u5E72\u4F60\u5C41\u4E8B\uFF01\u300D</p><p>\u767D\u96EA\u83B2\u6325\u624B\u7ED9\u4E86\u4ED6\u4E00\u4E2A\u8033\u5149\u3002</p><p>\u960E\u7F57\u671B\u8214\u4E86\u8214\u5634\u89D2\u7684\u8840\u8FF9\uFF0C\u72DE\u7B11\u9053\uFF1A\u300C\u53C8\u6CA1\u6709\u4EBA\u903C\u5979\uFF0C\u662F\u4F60\u5A18\u81EA\u5DF1\u9001\u4E0A\u95E8\u6765\u8BA9\u4EBA\u73A9\u7684\u3002\u683D\u5728\u4F60\u624B\u91CC\uFF0C\u960E\u67D0\u8BA4\u4E86\uFF0C\u4F46\u5916\u9762\u7684\u4EBA\u960E\u67D0\u4E5F\u7BA1\u4E0D\u7740\uFF0C\u7B54\u5E94\u4E86\u4F60\u6709\u4E2A\u5C41\u7528\uFF01\u300D</p><p>\u767D\u96EA\u83B2\u54AC\u4E86\u54AC\u7259\uFF0C\u671D\u5730\u7262\u5916\u558A\u9053\uFF1A\u300C\u5916\u9762\u7684\u542C\u7740\uFF0C\u628A\u82F1\u83B2\u9001\u8FDB\u6765\uFF01\u300D</p><p>\u300C\u4E0D\u884C\uFF01\u300D\u960E\u7F57\u671B\u559D\u9053\u3002</p><p>\u300C\u4F60\u8BF4\u4EC0\u4E48\uFF1F\u300D</p><p>\u300C\u4F60\u4EEC\u628A\u767D\u82F1\u83B2\u770B\u7D27\u4E86\uFF01\u5979\u4EEC\u8981\u6562\u78B0\u6211\uFF0C\u4F60\u4EEC\u5C31\u5241\u6389\u4ED6\u4E00\u6839\u624B\u6307\uFF01\u8001\u5B50\u8981\u662F\u6B7B\u5728\u8FD9\u91CC\uFF0C\u4F60\u4EEC\u5C31\u628A\u90A3\u5C0F\u5154\u5D3D\u5B50\u5241\u788E\u4E86\u5582\u72D7\uFF01\u300D</p><p>\u767D\u96EA\u83B2\u7F8E\u76EE\u55B7\u706B\uFF0C\u5389\u58F0\u9053\uFF1A\u300C\u4F60\u518D\u8BF4\u4E00\u904D\uFF01\u300D</p><p>\u960E\u7F57\u671B\u51B7\u7B11\u9053\uFF1A\u300C\u8981\u8BA9\u4F60\u626F\u8DB3\u4E86\u987A\u98CE\u5E06\uFF0C\u960E\u67D0\u8FD8\u6709\u4E2A\u5C41\u6DF7\u7684\u3002\u6B63\u597D\u767D\u82F1\u83B2\u90A3\u5C0F\u5154\u5D3D\u5B50\u5728\u5916\u9762\uFF0C\u54B1\u4EEC\u4E00\u8FB9\u4E00\u4E2A\uFF0C\u8C01\u4E5F\u4E0D\u8981\u505A\u7EDD\u4E86\u3002\u300D</p><p>\u5730\u7262\u91CC\u4E00\u65F6\u5B89\u9759\u4E0B\u6765\uFF0C\u5916\u9762\u53FD\u53FD\u55B3\u55B3\u8BAE\u4E86\u534A\u5929\uFF0C\u4F20\u6765\u5218\u8FA9\u673A\u7684\u58F0\u97F3\uFF0C\u300C\u960E\u5927\u4EBA\uFF0C\u4F60\u6709\u4F55\u5429\u5490\uFF1F\u300D</p><p>\u960E\u7F57\u671B\u51B7\u51B7\u5730\u9053\uFF1A\u300C\u8BA9\u5F1F\u5144\u4EEC\u5B89\u5206\u4E9B\uFF0C\u4E8B\u60C5\u4E86\u7ED3\u4E4B\u524D\uFF0C\u8C01\u4E5F\u4E0D\u8BB8\u79BB\u5C71\uFF01\u6BCF\u65E5\u7684\u516C\u6587\u8FDE\u7740\u996D\u83DC\u4E00\u8D77\u9001\u8FDB\u6765\uFF0C\u8BA9\u8001\u5353\u8DDF\u5929\u7FBD\u8F6E\u6D41\u5E26\u4EBA\uFF0C\u5728\u5916\u9762\u5B88\u7740\uFF0C\u672C\u5B98\u65E0\u8BBA\u751F\u6B7B\uFF0C\u90FD\u662F\u4E3A\u671D\u5EF7\u5C3D\u5FE0\u6548\u529B\uFF0C\u5207\u83AB\u8BA9\u8FD9\u4E24\u4E2A\u9006\u532A\u8DD1\u4E86\uFF01\u300D</p><p>\u5916\u9762\u53C8\u8BAE\u8BBA\u534A\u5929\uFF0C\u5218\u8FA9\u673A\u9053\uFF1A\u300C\u5927\u4EBA\u653E\u5FC3\u3002\u5F1F\u5144\u4EEC\u4E00\u5207\u4F9D\u5927\u4EBA\u5429\u5490\u3002\u300D</p><p>\u767D\u96EA\u83B2\u548C\u859B\u971C\u7075\u4EA4\u6362\u4E86\u4E00\u4E2A\u773C\u8272\uFF0C\u7B49\u996D\u83DC\u9001\u6765\uFF0C\u4FBF\u5408\u4E0A\u94C1\u7F69\uFF0C\u4ECE\u91CC\u9762\u9501\u4E0A\u3002</p><p>\u960E\u7F57\u671B\u5750\u5728\u5BF9\u9762\u7684\u94C1\u7B3C\u91CC\uFF0C\u63A5\u8FC7\u996D\u83DC\u4FBF\u653E\u6000\u5403\u559D\uFF0C\u6D51\u4E0D\u628A\u4E24\u4EBA\u653E\u5728\u773C\u5185\u3002</p><p>\u859B\u971C\u7075\u5FCD\u4E0D\u4F4F\u63F6\u63C4\u9053\uFF1A\u300C\u960E\u5927\u4EBA\u597D\u5BBD\u7684\u5FC3\u80F8\uFF0C\u5802\u5802\u671D\u5EF7\u547D\u5B98\uFF0C\u88AB\u4E24\u4E2A\u56DA\u72AF\u62FF\u4F4F\uFF0C\u5750\u5728\u7262\u91CC\uFF0C\u5C45\u7136\u8FD8\u80FD\u5403\u5F97\u8FDB\u53BB\uFF1F\u300D</p><p>\u960E\u7F57\u671B\u51B7\u7B11\u9053\uFF1A\u300C\u8FD9\u53C8\u5982\u4F55\uFF1F\u4F60\u4EEC\u4E24\u4E2A\u5A4A\u5B50\u8FDE\u5C44\u5E26\u5C41\u773C\u513F\u8001\u5B50\u54EA\u4E00\u4E2A\u6D1E\u6CA1\u73A9\u8FC7\uFF1F\u91CC\u91CC\u5916\u5916\u90FD\u5E72\u4E86\u4E2A\u904D\uFF0C\u8FD8\u4F1A\u6015\u4E86\u4F60\u4EEC\uFF01\u300D</p><p>\u773C\u89C1\u996D\u83DC\u90FD\u8981\u88AB\u4ED6\u5403\u5B8C\uFF0C\u859B\u971C\u7075\u624D\u60F3\u8D77\u6765\u9053\uFF1A\u300C\u5582\uFF0C\u59D3\u960E\u7684\uFF0C\u8FD8\u6709\u6211\u4EEC\u4E00\u4EFD\u5462\u3002\u300D</p><p>\u960E\u7F57\u671B\u653E\u4E0B\u7B77\u5B50\uFF0C\u671D\u4E0A\u9762\u5478\u7684\u5410\u4E86\u4E00\u53E3\uFF0C\u7136\u540E\u5F80\u5916\u4E00\u63A8\u3002\u859B\u971C\u7075\u6C14\u6781\u53CD\u7B11\uFF0C\u300C\u6211\u73B0\u5728\u662F\u4FE1\u4E86\uFF0C\u960E\u5927\u4EBA\u771F\u662F\u505A\u8FC7\u6D77\u8D3C\u3002\u5F53\u4E86\u9636\u4E0B\u56DA\u8FD8\u8FD9\u4E48\u6A2A\uFF0C\u4EE5\u524D\u5750\u5B98\u5E9C\u7684\u5927\u72F1\u65F6\u4E5F\u662F\u5982\u6B64\u4E48\uFF1F\u300D</p><p>\u960E\u7F57\u671B\u773C\u4E00\u7FFB\uFF0C\u300C\u60F3\u5F53\u5B98\uFF0C\u6740\u4EBA\u653E\u706B\u53D7\u62DB\u5B89\uFF0C\u960E\u67D0\u5C31\u662F\u53D7\u7684\u62DB\u5B89\uFF0C\u53EF\u6CA1\u5750\u8FC7\u4EC0\u4E48\u5927\u7262\uFF01\u300D</p><p>\u767D\u96EA\u83B2\u9053\uFF1A\u300C\u4E0D\u5FC5\u7406\u4ED6\u3002\u7B49\u5211\u90E8\u6765\u4EBA\uFF0C\u5BDF\u6E05\u6848\u5B50\uFF0C\u4ED6\u4E5F\u4E0D\u7528\u51FA\u6765\u4E86\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u72F1\u7F72\u5185\u4E00\u7FA4\u4EBA\u8138\u8272\u94C1\u9752\uFF0C\u88AB\u8FD9\u7A81\u5982\u5176\u6765\u7684\u53D8\u6545\u6253\u4E71\u4E86\u624B\u811A\u3002\u5728\u62BC\u7684\u56DA\u72AF\u6263\u4F4F\u4E86\u76D1\u72F1\u7684\u4E3B\u5B98\uFF0C\u5C45\u7136\u4E0D\u9003\uFF0C\u5206\u660E\u662F\u94C1\u4E86\u5FC3\u8981\u7B49\u7FFB\u6848\u3002\u8FD9\u6869\u6848\u5B50\u5728\u5EA7\u7684\u4EBA\u4EBA\u6709\u4EFD\uFF0C\u8017\u4E0B\u53BB\u5C82\u975E\u7B49\u6B7B\uFF1F\u4F46\u5C31\u7B97\u4ED6\u4EEC\u4E0D\u987E\u960E\u7F57\u671B\u7684\u751F\u6B7B\uFF0C\u5F3A\u884C\u653B\u5165\u5730\u7262\uFF0C\u4E5F\u672A\u5FC5\u662F\u767D\u96EA\u83B2\u7684\u5BF9\u624B\u3002</p><p>\u300C\u600E\u4E48\u5C31\u4F1A\u8BA9\u5979\u89E3\u5F00\u4E86\u7A74\u9053\uFF1F\u300D\u4F17\u4EBA\u90FD\u5728\u7EB3\u95F7\u3002</p><p>\u5B59\u5929\u7FBD\u9053\uFF1A\u300C\u6211\u6307\u529B\u672C\u6765\u5C31\u4E0D\u5982\u5353\u4E8C\u54E5\uFF0C\u53EF\u80FD\u662F\u960E\u5927\u4EBA\u6298\u817E\u5F97\u4E45\u4E86\uFF0C\u52A8\u4E86\u6C14\u8840\u3002\u300D\u5728\u5EA7\u7684\u5927\u90FD\u662F\u7C97\u901A\u62F3\u811A\uFF0C\u5BF9\u70B9\u7A74\u4E00\u77E5\u534A\u89E3\uFF0C\u4F46\u5353\u5929\u96C4\u5BF9\u5B59\u5929\u7FBD\u7684\u8BDD\u4E00\u4E07\u4E2A\u4E0D\u4FE1\uFF0C\u96BE\u9053\u767D\u96EA\u83B2\u7684\u529F\u592B\u7ADF\u5230\u4E86\u80FD\u51B2\u5F00\u7A74\u9053\u7684\u5730\u6B65\uFF1F</p><p>\u300C\u6211\u5C31\u8BF4\u4E0D\u8BE5\u89E3\u4E86\u67B7\u68B0\uFF0C\u53EF\u960E\u5927\u4EBA\u5C31\u662F\u4E0D\u542C\u3002\u300D\u9C8D\u6A2A\u7684\u53E3\u6C14\u900F\u51FA\u4E00\u5206\u5174\u707E\u4E50\u7978\u3002</p><p>\u300C\u4E8B\u5DF2\u81F3\u6B64\uFF0C\u8FD8\u8BF4\u8FD9\u4E2A\uFF01\u300D\u5218\u8FA8\u673A\u9053\uFF1A\u300C\u8FD9\u4E8B\u513F\u5927\u4F19\u770B\u600E\u4E48\u529E\uFF1F\u300D</p><p>\u4F17\u4EBA\u9646\u9646\u7EED\u7EED\u51FA\u4E86\u51E0\u4E2A\u4E3B\u610F\uFF0C\u6CA1\u4E00\u4E2A\u884C\u5F97\u901A\u3002\u542C\u5230\u9C8D\u6A2A\u8BA9\u4ED6\u8DDF\u5353\u5929\u96C4\u51B2\u8FDB\u53BB\u6551\u4EBA\uFF0C\u5B59\u5929\u7FBD\u677F\u7740\u8138\u9053\uFF1A\u300C\u9C8D\u7262\u5934\u8981\u662F\u613F\u610F\u6253\u5934\u9635\uFF0C\u6211\u5B59\u5929\u7FBD\u7EDD\u65E0\u4E8C\u8BDD\u3002\u300D</p><p>\u5C4B\u91CC\u9759\u9ED8\u4E86\u4E00\u4F1A\u513F\uFF0C\u5B59\u5929\u7FBD\u9053\uFF1A\u300C\u73B0\u5728\u5979\u4EEC\u770B\u5F97\u6B63\u7D27\uFF0C\u4E0D\u5982\u8017\u4E0A\u51E0\u65E5\uFF0C\u7B49\u5979\u4EEC\u61C8\u4E0B\u6765\u518D\u505A\u8BA1\u8F83\u3002\u300D</p><p>\u4F17\u4EBA\u4E5F\u53EA\u597D\u5982\u6B64\u3002\u4E34\u6563\u65F6\uFF0C\u5218\u8FA9\u673A\u9053\uFF1A\u300C\u5929\u7FBD\uFF0C\u7167\u960E\u5927\u4EBA\u7684\u5429\u5490\uFF0C\u4ECA\u65E5\u4F60\u5148\u5E26\u51E0\u4E2A\u5F1F\u5144\u770B\u7740\u4E9B\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u7B11\u4E86\u7B11\uFF0C\u300C\u5979\u4EEC\u60F3\u9003\u65E9\u5C31\u9003\u4E86\uFF0C\u767D\u96EA\u83B2\u8981\u51B2\u51FA\u6765\u8C01\u80FD\u6321\u5F97\u4F4F\uFF1F\u5C31\u662F\u628A\u6211\u4EEC\u5168\u6740\u4E86\uFF0C\u4E5F\u8D39\u4E0D\u4E86\u5979\u591A\u5C11\u529B\u6C14\u3002\u5979\u73B0\u5728\u662F\u62FF\u7740\u960E\u5927\u4EBA\uFF0C\u628A\u6211\u4EEC\u90FD\u5173\u7262\u91CC\uFF0C\u5979\u4E0D\u52A8\u624B\uFF0C\u5C31\u7B49\u5B98\u5E9C\u6765\u780D\u54B1\u4EEC\u7684\u5934\u3002\u60F3\u660E\u767D\u70B9\u513F\uFF0C\u4E0D\u662F\u6211\u4EEC\u770B\u5979\uFF0C\u662F\u5979\u770B\u7740\u6211\u4EEC\u3002</p><p>\u8981\u770B\u4F4F\u5979\uFF0C\u5916\u9762\u7684\u94C1\u6805\u5C3D\u591F\u4E86\uFF0C\u6709\u4EC0\u4E48\u597D\u5B88\u7684\uFF1F\u300D\u8BF4\u7740\u626C\u957F\u800C\u53BB\u3002</p>',98);function t(i,_){return a}var o=e(r,[["render",t]]);export{o as default};
