import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_39-\u6BCD\u8FB1" tabindex="-1"><a class="header-anchor" href="#_39-\u6BCD\u8FB1" aria-hidden="true">#</a> 39 \u6BCD\u8FB1</h1><p>\u4ED6\u80FD\u542C\u5230\u6BD2\u6DB2\u5728\u4F53\u5185\u6D41\u52A8\u7684\u58F0\u97F3\u3002\u9ED1\u6697\u4E2D\uFF0C\u4ED6\u8BE7\u5F02\u5730\u7AD6\u8D77\u8033\u6735\u3002\u7ADF\u7136\u4ECD\u8FD8\u6709\u5FC3\u8DF3\u7684\u8F7B\u54CD\u3002\u4ED6\u8C28\u614E\u5730\u8EB2\u85CF\u5728\u9634\u5F71\u4E2D\uFF0C\u7B49\u5F85\u7740\u3002</p><p>\u90A3\u58F0\u97F3\u603B\u4F1A\u7ED3\u675F\u3002\u7136\u540E\u4ED6\u53EF\u4EE5\u7741\u5F00\u773C\u775B\u3002</p><p>\u70C8\u65E5\u4E0B\uFF0C\u5973\u5B50\u51C4\u5A49\u7684\u75DB\u53EB\u5728\u5C71\u6797\u4E2D\u56DE\u8361\u3002\u4E00\u4E2A\u7CBE\u58EE\u7684\u6C49\u5B50\u7ACB\u5728\u6811\u4E0B\uFF0C\u91CE\u517D\u822C\u75AF\u72C2\u8E42\u8E8F\u7740\u9762\u524D\u65E0\u529B\u53CD\u6297\u7684\u7F8E\u5987\u3002\u90A3\u5973\u5B50\u53D1\u6563\u9B13\u4E71\uFF0C\u4E00\u6839\u73E0\u9497\u659C\u659C\u6E9C\u5230\u80A9\u5934\uFF0C\u51E0\u4E4E\u5815\u4E0B\u3002\u5979\u8D64\u6761\u6761\u8DB4\u5728\u6811\u4E0B\uFF0C\u50CF\u6BCD\u72D7\u4E00\u6837\u6485\u7740\u5C41\u80A1\uFF0C\u9634\u6237\u88AB\u5E72\u5F97\u7FFB\u5F00\uFF0C\u80FD\u6E05\u695A\u770B\u5230\u67D4\u8273\u7684\u6027\u5668\u5185\u4E00\u6839\u7C97\u786C\u7684\u9633\u5177\u75BE\u8FDB\u75BE\u51FA\u3002</p><p>\u5B59\u5929\u7FBD\u62E7\u4F4F\u5979\u7684\u5934\u53D1\uFF0C\u5C06\u5979\u8138\u90E8\u62FD\u5F97\u626C\u8D77\u3002</p><p>\u5B59\u5929\u7FBD\u6B23\u8D4F\u7740\u5979\u8138\u4E0A\u7684\u75DB\u695A\u548C\u6050\u60E7\uFF0C\u5FAE\u7B11\u7740\u4F38\u51FA\u624B\uFF0C\u6293\u4F4F\u5979\u7684\u4E73\u623F\uFF0C\u7528\u529B\u62E7\u4E0B\u3002\u90A3\u7C92\u7EA2\u7EA2\u7684\u4E73\u5934\u5728\u4ED6\u6307\u95F4\u6EDA\u52A8\u7740\uFF0C\u5F77\u4F5B\u4E00\u7C92\u6613\u788E\u7684\u6A31\u6843\u3002\u7389\u5A18\u6EE1\u9762\u75DB\u695A\uFF0C\u4E24\u624B\u7D27\u7D27\u62B1\u7740\u6811\u5E72\uFF0C\u624B\u6307\u51E0\u4E4E\u6263\u8FDB\u6811\u8EAB\u3002</p><p>\u4E0D\u77E5\u8FC7\u4E86\u591A\u4E45\uFF0C\u90A3\u4E2A\u75AF\u72C2\u7684\u7537\u4EBA\u7EC8\u4E8E\u5728\u5979\u4F53\u5185\u55B7\u5C04\u8D77\u6765\u3002\u5F53\u8089\u68D2\u79BB\u5F00\u8089\u7A74\uFF0C\u7389\u5A18\u5931\u53BB\u652F\u6491\u822C\u762B\u8F6F\u5728\u5730\u3002\u5979\u5C41\u80A1\u88AB\u649E\u5F97\u53D1\u7EA2\uFF0C\u79D8\u5904\u4E00\u7247\u51CC\u4E71\uFF0C\u8089\u7A74\u5706\u5F20\u7740\uFF0C\u4ECD\u5728\u4E0D\u65F6\u62BD\u52A8\uFF0C\u91CC\u9762\u767D\u6D4A\u7684\u7CBE\u6DB2\u9ECF\u9ECF\u7684\u6ED1\u843D\u51FA\u6765\uFF0C\u6CBE\u5728\u817F\u95F4\u9752\u7FE0\u7684\u8349\u53F6\u4E0A\u3002</p><p>\u5B59\u5929\u7FBD\u7528\u811A\u628A\u5979\u7FFB\u8F6C\u8FC7\u6765\uFF0C\u53EA\u89C1\u5979\u80A9\u5934\u5DF2\u7ECF\u88AB\u6811\u76AE\u78E8\u7834\uFF0C\u4E24\u4E73\u88AB\u62E7\u5F97\u9752\u80BF\uFF0C\u5C0F\u817F\u67D3\u4E0A\u9752\u8349\u7684\u6C41\u6DB2\uFF0C\u65E0\u529B\u5730\u6B6A\u5728\u4E00\u65C1\u3002\u90A3\u53CC\u7EA2\u7EE3\u978B\u6CBE\u4E86\u6CE5\u571F\uFF0C\u5DF2\u6CA1\u6709\u521D\u65F6\u90A3\u4E48\u9C9C\u8273\u3002</p><p>\u5B59\u5929\u7FBD\u770B\u4E86\u770B\u5929\u8272\uFF0C\u7136\u540E\u6258\u8D77\u5979\u7684\u811A\u8E1D\uFF0C\u8131\u6389\u7EE3\u978B\uFF0C\u626F\u4E0B\u5979\u7684\u811A\u5E26\u3002\u5973\u5B50\u7684\u811A\u6700\u662F\u7981\u5FCC\uFF0C\u7389\u83B2\u4E0E\u4ED6\u6210\u4EB2\u591A\u65E5\uFF0C\u5468\u8EAB\u90FD\u73A9\u904D\u4E86\uFF0C\u5374\u600E\u4E48\u4E5F\u4E0D\u613F\u5728\u4ED6\u9762\u524D\u9732\u51FA\u88F8\u8DB3\uFF0C\u6BCF\u65E5\u88F9\u811A\u7F20\u8DB3\uFF0C\u90FD\u662F\u80CC\u7740\u5B59\u5929\u7FBD\u505A\u7684\u3002</p><p>\u7389\u5A18\u5FC3\u91CC\u53EA\u6709\u6050\u60E7\uFF0C\u5979\u5C31\u50CF\u9759\u5BA4\u91CC\u4F9B\u7684\u6843\u679D\uFF0C\u4E00\u573A\u9AA4\u96E8\u5C31\u8DB3\u4EE5\u5C06\u5979\u5F81\u670D\u3002\u5979\u7684\u811A\u53C8\u767D\u53C8\u8F6F\uFF0C\u770B\u4E0D\u5230\u4E00\u4E1D\u98CE\u971C\u7684\u75D5\u8FF9\uFF0C\u63E1\u5728\u638C\u4E2D\uFF0C\u67D4\u82E5\u65E0\u9AA8\u3002</p><p>\u5B59\u5929\u7FBD\u5C06\u5979\u7684\u8863\u88D9\u3001\u7EE3\u978B\u62E2\u6210\u4E00\u5305\uFF0C\u4E00\u5E76\u6254\u8FDB\u5C71\u6DA7\uFF0C\u7B11\u9053\uFF1A\u300C\u8981\u59D4\u5C48\u4F60\u5728\u8FD9\u91CC\u5F85\u4E00\u591C\u4E86\u3002\u300D</p><p>\u56DE\u5230\u5E97\u91CC\uFF0C\u7389\u83B2\u521A\u9192\uFF0C\u5BF9\u5348\u95F4\u53D1\u751F\u7684\u4E00\u5207\u6D51\u7136\u4E0D\u89C9\u3002\u5B59\u5929\u7FBD\u8DDF\u5979\u8C08\u7B11\u4E24\u53E5\uFF0C\u81EA\u53BB\u95ED\u95E8\u7EC3\u529F\u3002\u4ED6\u5FC3\u91CC\u5F88\u5E73\u9759\uFF0C\u6CA1\u6709\u4EFB\u4F55\u62C5\u5FC3\u3002</p><p>\u7389\u5A18\u6240\u5728\u7684\u5730\u65B9\u65E9\u5DF2\u504F\u79BB\u4E86\u5927\u8DEF\uFF0C\u65E0\u8BBA\u600E\u4E48\u547C\u6551\u5C31\u4E0D\u865E\u6709\u4EBA\u80FD\u542C\u89C1\u3002\u5979\u5149\u7740\u811A\uFF0C\u6CA1\u4EBA\u6276\u7740\uFF0C\u5728\u5C71\u91CC\u5BF8\u6B65\u96BE\u884C\uFF0C\u60F3\u9003\u4E5F\u9003\u4E0D\u6389\u3002</p><p>\u6740\u4E86\u5979\uFF0C\u5F53\u7136\u662F\u6700\u5B89\u5168\u7684\u4F5C\u6CD5\u3002\u4F46\u662F\u5B59\u5929\u7FBD\u8FD8\u4E0D\u60F3\u8F7B\u6613\u6254\u53EA\u4EAB\u7528\u8FC7\u4E00\u6B21\u7684\u730E\u7269\u3002\u4E5F\u8BB8\u4ED6\u53EF\u4EE5\u5C31\u8FD9\u6837\u628A\u5979\u7559\u5728\u5C71\u91CC\uFF0C\u9010\u65E5\u73A9\u5F04\uFF0C\u76F4\u5230\u5979\u5BB9\u989C\u51CB\u96F6\uFF0C\u65E0\u590D\u5982\u4ECA\u7684\u7F8E\u6001\u3002</p><p>\u76F4\u5230\u638C\u706F\u65F6\u5206\uFF0C\u8FD8\u4E0D\u89C1\u4E39\u5A18\u56DE\u6765\u3002\u5B59\u5929\u7FBD\u6CBF\u8DEF\u53BB\u5BFB\uFF0C\u534A\u8DEF\u4E0A\u9047\u89C1\u5979\u6B63\u5728\u8DEF\u8FB9\u6B47\u606F\uFF0C\u4FBF\u8D1F\u7740\u5979\u56DE\u9152\u5E97\u3002</p><p>\u5B59\u5929\u7FBD\u4E00\u53E5\u4E0D\u95EE\uFF0C\u4E39\u5A18\u4E5F\u4E00\u5B57\u4E0D\u8BF4\u3002\u5979\u53C8\u7D2F\u53C8\u5026\uFF0C\u8138\u8272\u82CD\u767D\u5F97\u5413\u4EBA\uFF0C\u5230\u5E97\u91CC\u996D\u4E5F\u4E0D\u5403\uFF0C\u4FBF\u56DE\u623F\u6C89\u6C89\u7761\u53BB\u3002</p><p>\u5F53\u665A\u5C06\u8FD1\u4E09\u66F4\uFF0C\u7389\u83B2\u88AB\u4E00\u9635\u62CD\u95E8\u58F0\u60CA\u9192\u3002\u5B59\u5929\u7FBD\u62AB\u8863\u8D77\u8EAB\uFF0C\u4E0B\u697C\u5F00\u4E86\u95E8\uFF0C\u5728\u95E8\u5916\u8BF4\u4E86\u51E0\u53E5\u8BDD\uFF0C\u4FBF\u5373\u5E26\u4E0A\u95E8\uFF0C\u8DDF\u6765\u4EBA\u5306\u5306\u79BB\u5F00\u3002</p><p>\u7389\u83B2\u518D\u65E0\u6CD5\u5165\u7761\uFF0C\u5979\u7A7F\u4E0A\u5C0F\u8863\uFF0C\u79C9\u4E86\u70DB\uFF0C\u8D70\u8FDB\u6BCD\u4EB2\u7684\u5367\u623F\u3002</p><p>\u4E39\u5A18\u4FA7\u7740\u8EAB\uFF0C\u9762\u671D\u91CC\u7761\u7740\u3002\u5929\u6C14\u708E\u70ED\uFF0C\u5979\u6CA1\u76D6\u88AB\u887E\uFF0C\u53EA\u7A7F\u7740\u8D34\u8EAB\u7684\u5C0F\u8863\uFF0C\u811A\u4E0A\u7684\u978B\u5B50\u4E5F\u672A\u8131\uFF0C\u663E\u7136\u662F\u7D2F\u5F97\u7D27\u4E86\u3002</p><p>\u7389\u83B2\u653E\u4E0B\u706F\u70DB\uFF0C\u5750\u5728\u5E8A\u8FB9\uFF0C\u8F7B\u8F7B\u5E2E\u5A18\u9664\u4E0B\u978B\u5B50\uFF0C\u677E\u5F00\u811A\u5E26\u3002\u4E39\u5A18\u8EAB\u4E0A\u6709\u80A1\u6C57\u9999\u4E0E\u8165\u817B\u6C14\u606F\u6DF7\u548C\u7684\u5473\u9053\uFF0C\u7389\u83B2\u60F3\uFF0C\u591A\u534A\u662F\u4E00\u8DEF\u8D70\u5F97\u7D2F\u4E86\u3002</p><p>\u4E00\u8F6C\u773C\uFF0C\u53EA\u89C1\u4E39\u5A18\u80A1\u95F4\u6E7F\u4E86\u4E00\u7247\uFF0C\u5E26\u7740\u51E0\u4E1D\u8840\u7EA2\uFF0C\u5370\u5728\u6708\u8272\u7684\u4EB5\u88E4\u4E0A\u3002\u7389\u83B2\u4EE5\u4E3A\u662F\u5A18\u7684\u6708\u4E8B\u6765\u4E86\uFF0C\u8BAA\u8BAA\u5730\u6536\u4E86\u624B\u3002\u60F3\u53EB\u9192\u5A18\uFF0C\u53C8\u89C1\u5979\u7761\u5F97\u6B63\u719F\u3002\u7389\u83B2\u72B9\u8C6B\u826F\u4E45\uFF0C\u7EC8\u662F\u6BCD\u5973\u4FE9\u5DF2\u7ECF\u540C\u5E8A\u5171\u4F8D\u4E00\u592B\uFF0C\u8FD8\u6709\u4EC0\u4E48\u6015\u7F9E\u7684\u3002</p><p>\u5979\u8F7B\u8F7B\u53EB\u4E86\u58F0\u300C\u5A18\u300D\uFF0C\u89C1\u5A18\u4ECD\u5728\u719F\u7761\uFF0C\u4FBF\u5C0F\u5FC3\u5730\u89E3\u5F00\u4EB5\u88E4\uFF0C\u8F7B\u8F7B\u62C9\u5230\u81C0\u4E0B\u3002</p><p>\u5165\u76EE\u7684\u60C5\u5F62\u4F7F\u7389\u83B2\u60CA\u53EB\u4E00\u58F0\uFF0C\u51E0\u4E4E\u6253\u7FFB\u4E86\u706F\u70DB\u3002</p><p>\u4E39\u5A18\u767D\u6ED1\u7684\u96EA\u81C0\u50CF\u88AB\u4E00\u7FA4\u91CE\u517D\u6293\u5F04\u8FC7\u822C\uFF0C\u5E03\u6EE1\u4E86\u5404\u79CD\u5404\u6837\u9752\u7D2B\u7EA2\u80BF\u7684\u4F24\u75D5\uFF0C\u6709\u6293\u7684\u3001\u6390\u7684\u3001\u62E7\u7684\u3001\u6253\u7684\uFF0C\u751A\u81F3\u8FD8\u6709\u54AC\u51FA\u7684\u75D5\u8FF9\uFF0C\u4E24\u7247\u5927\u5C41\u80A1\u51E0\u4E4E\u6CA1\u6709\u4E00\u5BF8\u5B8C\u597D\u3002</p><p>\u66F4\u4E3A\u9A87\u4EBA\u7684\u662F\u4E39\u5A18\u4E0B\u4F53\u7684\u4E24\u53EA\u8089\u7A74\u3002\u5979\u4FA7\u8EAB\u7761\u7740\uFF0C\u81C0\u6C9F\u4E0D\u81EA\u7136\u5730\u5411\u5916\u5F20\u5F00\uFF0C\u90A3\u53EA\u5C0F\u5DE7\u7684\u7F8E\u809B\u50CF\u88AB\u5DE8\u7269\u6345\u8FC7\u822C\uFF0C\u9732\u51FA\u4E00\u4E2A\u9C9C\u7EA2\u7684\u5165\u53E3\u3002\u7EA2\u5AE9\u7684\u809B\u857E\u6574\u4E2A\u7FFB\u51FA\u4F53\u5916\uFF0C\u4E0A\u9762\u88AB\u786C\u7269\u78E8\u7834\uFF0C\u5370\u7740\u51CC\u4E71\u7684\u8840\u75D5\uFF0C\u5140\u81EA\u6E17\u51FA\u9C9C\u8840\u3002</p><p>\u7389\u83B2\u624B\u6307\u8F7B\u98A4\uFF0C\u6BCD\u4EB2\u53EA\u8BF4\u662F\u53BB\u63A2\u76D1\uFF0C\u6CA1\u60F3\u5230\u7ADF\u662F\u8FD9\u6837\u7684\u63A2\u6CD5\u3002\u5979\u65E0\u6CD5\u60F3\u8C61\u4E16\u95F4\u4F1A\u6709\u8FD9\u6837\u6DEB\u79FD\u6B8B\u5FCD\u7684\u4E3E\u52A8\uFF0C\u66F4\u65E0\u6CD5\u60F3\u8C61\u662F\u4EC0\u4E48\u5728\u6BCD\u4EB2\u4F53\u5185\u7559\u4E0B\u8FD9\u6837\u7684\u4F24\u75D5\u3002</p><p>\u76F8\u6BD4\u4E4B\u4E0B\uFF0C\u4E39\u5A18\u7684\u79D8\u5904\u66F4\u4E3A\u51C4\u60E8\u3002\u5979\u5E76\u7740\u817F\uFF0C\u9634\u6237\u5374\u50CF\u63C9\u788E\u7684\u828D\u836F\u82B1\u4E00\u6837\u4ECE\u817F\u7F1D\u4E2D\u7FFB\u5F00\uFF0C\u9732\u51FA\u5185\u90E8\u4E00\u7247\u72FC\u7C4D\u3002\u5979\u9634\u6BDB\u51CC\u4E71\uFF0C\u9634\u5507\u7EA2\u80BF\u5F97\u5F77\u4F5B\u6EF4\u8840\uFF0C\u8089\u7A74\u5411\u5916\u9F13\u8D77\uFF0C\u91CC\u9762\u5939\u7740\u4E00\u7247\u5947\u602A\u7684\u767D\u8272\u3002</p><p>\u7389\u83B2\u5FC3\u91CC\u72B9\u8C6B\u826F\u4E45\uFF0C\u634F\u4F4F\u90A3\u89D2\u7269\u4F53\u8F7B\u8F7B\u4E00\u626F\u3002\u4E00\u6761\u767D\u8272\u7684\u4E1D\u5DFE\u4ECE\u7A74\u53E3\u6ED1\u51FA\uFF0C\u5374\u662F\u4E39\u5A18\u968F\u8EAB\u5E26\u7684\u5E15\u5B50\u3002\u90A3\u4E1D\u5E15\u5728\u4E39\u5A18\u4F53\u5185\u585E\u5F97\u6781\u6DF1\uFF0C\u91CC\u9762\u7D27\u7D27\u5361\u5728\u9634\u5185\u3002</p><p>\u7389\u83B2\u54AC\u4E86\u54AC\u7259\uFF0C\u7528\u529B\u4E00\u626F\u3002\u633D\u6210\u4E00\u56E2\u7684\u4E1D\u5DFE\u8131\u51FA\u7A74\u53E3\uFF0C\u5374\u662F\u6253\u4E86\u4E2A\u7ED3\uFF0C\u4E0A\u9762\u53C8\u6E7F\u53C8\u9ECF\uFF0C\u6CBE\u6EE1\u4EE4\u4EBA\u4F5C\u5455\u7684\u6ED1\u7A20\u6DB2\u4F53\u3002\u4E39\u5A18\u4E0B\u4F53\u5F77\u4F5B\u62D4\u6389\u4E00\u4E2A\u585E\u5B50\uFF0C\u7A74\u53E3\u5F20\u5F00\uFF0C\u8815\u52A8\u7247\u523B\u540E\uFF0C\u731B\u7136\u6D8C\u51FA\u4E00\u80A1\u9ECF\u6DB2\uFF0C\u4E00\u76F4\u6D41\u5230\u5927\u817F\u4E0A\u3002</p><p>\u4E39\u5A18\u53D1\u51FA\u4E00\u58F0\u8F7B\u67D4\u7684\u547B\u541F\uFF0C\u817F\u95F4\u6E7F\u6ED1\u7684\u871C\u7A74\u5F77\u4F5B\u4E00\u5F20\u5C0F\u5634\uFF0C\u5C06\u7A74\u5185\u6EE1\u84C4\u7684\u7CBE\u6DB2\u4E00\u80A1\u80A1\u5410\u51FA\u3002\u7389\u83B2\u5FC3\u5934\u9707\u98A4\uFF0C\u8DB3\u8DB3\u6D41\u4E86\u4E00\u76CF\u70ED\u8336\u7684\u65F6\u95F4\uFF0C\u4E39\u5A18\u7A74\u5185\u624D\u6D41\u7A7A\u3002\u9EC4\u767D\u4E0D\u4E00\u7684\u7CBE\u6DB2\u987A\u7740\u5927\u817F\u6ED1\u843D\u4E0B\u6765\uFF0C\u5728\u5E2D\u4E0A\u6D41\u51FA\u534A\u4E2A\u6795\u5934\u5927\u4E00\u7247\u6E7F\u75D5\u3002</p><p>\u4E39\u5A18\u8EAB\u5B50\u52A8\u4E86\u4E00\u4E0B\uFF0C\u53EA\u89C9\u4E0B\u4F53\u4E00\u7247\u6E05\u51C9\uFF0C\u5979\u7741\u5F00\u773C\uFF0C\u8033\u8FB9\u4F20\u6765\u5973\u513F\u7684\u62BD\u6CE3\u3002</p><p>\u300C\u5A18\uFF0C\u600E\u4E48\u4F1A\u8FD9\u6837\u2026\u2026\u300D</p><p>\u7389\u83B2\u7EDE\u4E86\u6761\u6BDB\u5DFE\uFF0C\u4E00\u8FB9\u6389\u6CEA\uFF0C\u4E00\u8FB9\u62B9\u62ED\u4E39\u5A18\u4E0B\u4F53\u7684\u6C61\u6E0D\u3002\u4E39\u5A18\u52C9\u5F3A\u8BF4\u4E86\u53E5\uFF0C\u300C\u4E0D\u59A8\u7684\u3002\u300D\u4E5F\u4E0D\u7981\u843D\u4E0B\u6CEA\u6765\u3002</p><p>\u6BCD\u5973\u4FE9\u76F8\u62E5\u6CE3\u6D95\uFF0C\u826F\u4E45\u624D\u6B62\u4F4F\u60B2\u58F0\u3002\u4E39\u5A18\u62ED\u53BB\u6CEA\u75D5\uFF0C\u53CD\u8FC7\u6765\u5B89\u6170\u5973\u513F\u9053\uFF1A\u300C\u83AB\u54ED\u4E86\u3002\u603B\u662F\u5A18\u547D\u4E0D\u597D\u2026\u2026\u624D\u843D\u5F97\u5982\u6B64\u3002\u300D</p><p>\u300C\u662F\u90A3\u73ED\u72F1\u5352\u5417\uFF1F\u300D</p><p>\u4E39\u5A18\u6CA1\u6709\u56DE\u7B54\uFF0C\u5374\u95EE\u9053\uFF1A\u300C\u76F8\u516C\u5462\uFF1F\u300D</p><p>\u7389\u83B2\u7D22\u6027\u8BF4\u9053\uFF1A\u300C\u5A18\uFF0C\u4F60\u600E\u4E48\u8FD8\u8BB0\u6302\u7740\u4ED6\uFF1F\u76F8\u516C\u4ED6\u2026\u2026\u5DE6\u53F3\u662F\u4E2A\u6CA1\u826F\u5FC3\u7684\uFF0C\u7531\u7740\u5A18\u53D7\u8FD9\u6837\u7684\u59D4\u5C48\u3002\u300D</p><p>\u300C\u8FD9\u90FD\u662F\u5A18\u4E0D\u597D\uFF0C\u6028\u4E0D\u5F97\u5929\u7FBD\u54E5\u3002\u300D</p><p>\u300C\u4F60\u8FD8\u66FF\u4ED6\u8BF4\u8BDD\u3002\u4ED6\u8DDF\u90A3\u73ED\u4EBA\u6709\u4EC0\u4E48\u4E0D\u4E00\u6837\uFF1F\u8FD8\u4E0D\u662F\u8D2A\u56FE\u5A18\u7684\u8EAB\u5B50\u3002\u82E5\u4E0D\u662F\u5A18\u529D\u6211\uFF0C\u6211\u5B81\u613F\u6B7B\u4E86\u5E72\u51C0\u3002\u300D</p><p>\u300C\u4F60\u4E0D\u77E5\u9053\u7684\u3002\u54B1\u4EEC\u5BB6\u9047\u4E86\u8FD9\u6837\u7684\u7978\u4E8B\uFF0C\u603B\u8981\u6709\u4E00\u4E2A\u7537\u4EBA\u7167\u5E94\u3002\u5929\u7FBD\u54E5\u5A36\u4E86\u4F60\uFF0C\u5F80\u540E\u4F60\u4E5F\u6709\u4E2A\u4F9D\u9760\u3002\u300D</p><p>\u300C\u4ED6\u5A36\u4E86\u6211\uFF0C\u5A18\u5C31\u662F\u4ED6\u4E08\u6BCD\uFF0C\u4ED6\u4E3A\u4F55\u8FD8\u8981\u4E0D\u987E\u5EC9\u803B\uFF0C\u903C\u7740\u5A18\u540C\u5E8A\uFF1F\u300D</p><p>\u4E39\u5A18\u54ED\u9053\uFF1A\u300C\u4F60\u65E2\u8FD9\u6837\u8BF4\uFF0C\u5A18\u4E5F\u4E0D\u6015\u7F9E\u4E86\u3002\u662F\u5A18\u4E0D\u8981\u8138\uFF0C\u4F60\u7239\u521A\u6B7B\uFF0C\u5A18\u5C31\u8DDF\u4ED6\u597D\u4E0A\u4E86\u3002\u76F8\u516C\u539F\u8BF4\u8FC7\u8981\u5A36\u6211\u7684\uFF0C\u53EF\u5A18\u4E0D\u8BE5\u4E00\u4E2A\u4EBA\u53BB\u63A2\u76D1\uFF0C\u88AB\u4EBA\u5F04\u810F\u4E86\u8EAB\u5B50\u3002\u300D</p><p>\u4E39\u5A18\u7D22\u6027\u7FFB\u8FC7\u8EAB\u5B50\uFF0C\u5F20\u5F00\u817F\u9053\uFF1A\u300C\u4F60\u770B\u2026\u2026\u300D</p><p>\u7389\u83B2\u6440\u4F4F\u53E3\uFF0C\u5C06\u90A3\u58F0\u60CA\u547C\u6B7B\u6B7B\u538B\u4F4F\u3002\u4E39\u5A18\u9634\u961C\u5FAE\u5FAE\u9F13\u8D77\uFF0C\u50CF\u5979\u8EAB\u4E0A\u6BCF\u5BF8\u808C\u80A4\u4E00\u6837\u767D\u5AE9\uFF0C\u4E0A\u9762\u4E00\u6839\u6BDB\u53D1\u4E5F\u65E0\u3002\u4F46\u5C31\u5728\u5979\u9634\u961C\u6B63\u4E2D\uFF0C\u50CF\u56FE\u7AE0\u4E00\u6837\u70D9\u7740\u4E24\u4E2A\u6241\u6241\u7684\u5B57\u4F53\uFF0C\u300C\u6DEB\u5987\u300D\u3002\u5B57\u8FF9\u8272\u6CFD\u9C9C\u7EA2\uFF0C\u6DF1\u6DF1\u51F9\u5165\u808C\u80A4\uFF0C\u663E\u7136\u662F\u7528\u70D9\u94C1\u751F\u751F\u70D9\u4E0A\u7684\u3002\u7389\u83B2\u8FD9\u624D\u60F3\u8D77\uFF0C\u5A18\u5728\u5979\u9762\u524D\u8DDF\u76F8\u516C\u4EA4\u6B22\uFF0C\u603B\u6709\u610F\u65E0\u610F\u63A9\u7740\u9634\u961C\uFF0C\u539F\u6765\u662F\u56E0\u4E3A\u8FD9\u4E2A\u3002</p><p>\u4E39\u5A18\u629A\u5F04\u7740\u90A3\u4E24\u4E2A\u70D9\u5B57\uFF0C\u4E0D\u77E5\u662F\u60F3\u628A\u5B83\u4EEC\u62B9\u6389\uFF0C\u8FD8\u662F\u628A\u5B83\u4EEC\u64E6\u5F97\u66F4\u52A0\u9C9C\u660E\u3002</p><p>\u5979\u8138\u4E0A\u795E\u60C5\u4F3C\u54ED\u4F3C\u7B11\uFF0C\u300C\u4ED6\u4EEC\u73A9\u8FC7\u6211\uFF0C\u53C8\u7ED9\u5A18\u8EAB\u4E0A\u70D9\u4E0B\u8FD9\u5B57\uFF0C\u597D\u53EB\u5A18\u4E00\u8F88\u5B50\u90FD\u62AC\u4E0D\u8D77\u5934\u6765\uFF0C\u505A\u4E2A\u5343\u4EBA\u7761\u4E07\u4EBA\u538B\u7684\u5A4A\u5B50\u3002\u300D</p><p>\u300C\u76F8\u516C\u5728\u72F1\u91CC\u4F5C\u4E8B\uFF0C\u8FD9\u4E0A\u4E0B\u7262\u91CC\u54EA\u4E2A\u4EBA\u6CA1\u5978\u8FC7\u5A18\u7684\u8EAB\u5B50\uFF0C\u5A18\u8FD8\u6709\u4EC0\u4E48\u8138\u518D\u5AC1\u7ED9\u5929\u7FBD\u54E5\u3002\u5C31\u662F\u5AC1\u4E86\u4ED6\uFF0C\u5F80\u540E\u53EB\u76F8\u516C\u8FD8\u600E\u4E48\u505A\u4EBA\uFF1F\u300D</p><p>\u300C\u76F8\u516C\u60F3\u5A36\u4F60\uFF0C\u5A18\u4E5F\u613F\u610F\u3002\u7EC8\u7A76\u662F\u5A18\u8D1F\u4E86\u4ED6\uFF0C\u6CA1\u80FD\u4E3A\u5B59\u5BB6\u4FDD\u4F4F\u8EAB\u5B50\u3002\u90A3\u65E5\u76F8\u516C\u8BF4\u8FDE\u5A18\u4E00\u5E76\u5A36\u4E86\uFF0C\u5A18\u771F\u662F\u5F88\u5F00\u5FC3\u3002\u4F60\u9A82\u5A18\u8D31\u4E5F\u597D\uFF0C\u4E0D\u8981\u8138\u4E5F\u597D\uFF0C\u4F46\u5A18\u7EC8\u662F\u79BB\u4E0D\u5F00\u4ED6\u3002\u300D</p><p>\u300C\u5A18\u4E5F\u4E0D\u8981\u540D\u5206\uFF0C\u53EA\u8981\u4ED6\u8FD8\u60F3\u7740\u6211\uFF0C\u5FF5\u7740\u6211\uFF0C\u5A18\u5C31\u662F\u4E3A\u5974\u4E3A\u5A62\u4E5F\u613F\u610F\u3002\u5A18\u4E5F\u4E0D\u8981\u5EC9\u803B\u4E86\uFF0C\u5C31\u7B97\u662F\u4ED6\u8D2A\u56FE\u5A18\u7684\u59FF\u8272\uFF0C\u5A18\u4E5F\u613F\u610F\u628A\u8EAB\u5B50\u7ED9\u4ED6\u3002\u53EA\u8981\u5929\u7FBD\u54E5\u5E72\u5A18\u7684\u65F6\u5019\uFF0C\u5728\u5A18\u8EAB\u5B50\u91CC\u8FDB\u51FA\u7684\u65F6\u5019\u89C9\u5F97\u5F00\u5FC3\uFF0C\u5A18\u5C31\u5F00\u5FC3\u5F97\u8981\u6B7B\u3002\u300D</p><p>\u7389\u83B2\u77A0\u76EE\u7ED3\u820C\uFF0C\u6014\u6014\u770B\u7740\u6BCD\u4EB2\u3002</p><p>\u4E39\u5A18\u53CC\u988A\u6F6E\u7EA2\uFF0C\u773C\u775B\u5206\u5916\u660E\u4EAE\uFF0C\u98A4\u58F0\u9053\uFF1A\u300C\u5A18\u4E00\u8F88\u5B50\u5C31\u559C\u6B22\u8FC7\u8FD9\u4E00\u4E2A\u7537\u4EBA\uFF0C\u8FDE\u5FC3\u90FD\u6316\u4E86\u7ED9\u4ED6\u3002\u76F8\u516C\u65E0\u4EB2\u65E0\u65E7\uFF0C\u5728\u72F1\u91CC\u53C8\u662F\u4E00\u4E2A\u5C0F\u540F\uFF0C\u4E0A\u6709\u4E3B\u5B98\uFF0C\u4E0B\u6709\u540C\u50DA\uFF0C\u80FD\u62A4\u5F97\u4F60\u4E00\u4E2A\u5C31\u597D\uFF0C\u54EA\u80FD\u62A4\u4F4F\u6211\u4EEC\u6BCD\u5973\u5468\u5168\u3002\u5A18\u7684\u8EAB\u5B50\u5DE6\u53F3\u662F\u810F\u4E86\uFF0C\u591A\u4E00\u4E2A\u5C11\u4E00\u4E2A\uFF0C\u591A\u51E0\u6B21\u5C11\u51E0\u6B21\u53C8\u6709\u4EC0\u4E48\u3002\u8FD9\u4E8B\u6211\u4E0D\u602A\u76F8\u516C\uFF0C\u4F60\u4E5F\u83AB\u602A\u4ED6\uFF0C\u5DE6\u53F3\u662F\u5A18\u547D\u4E0D\u597D\uFF0C\u4E0A\u8F88\u5B50\u6B20\u4E86\u4ED6\u4EEC\u7684\u3002\u300D</p><p>\u7389\u83B2\u5446\u5446\u5750\u5728\u5E8A\u8FB9\uFF0C\u5FC3\u91CC\u7FFB\u7FFB\u6EDA\u6EDA\uFF0C\u6CA1\u6709\u7247\u523B\u5B89\u5B81\u3002\u534A\u664C\uFF0C\u5979\u8F6F\u5F31\u5730\u8BF4\u9053\uFF1A\u300C\u5A18\uFF0C\u6211\u4E0A\u8F88\u5B50\u6B20\u4E86\u8C01\u7684\u2026\u2026\u300D</p><p>\u4E39\u5A18\u633D\u7740\u5979\u7684\u624B\u9053\uFF1A\u300C\u4F60\u8C01\u4E5F\u4E0D\u6B20\uFF0C\u4F46\u6211\u4EEC\u90FD\u6B20\u4E86\u76F8\u516C\u7684\u3002\u8981\u597D\u597D\u7684\u670D\u4F8D\u76F8\u516C\u3002\u300D</p><p>\u7389\u83B2\u65E0\u8A00\u4EE5\u5BF9\u3002\u4E39\u5A18\u62E2\u4E86\u62E2\u5979\u7684\u79C0\u53D1\uFF0C\u8F7B\u7B11\u9053\uFF1A\u300C\u600E\u4E48\u4E0D\u966A\u76F8\u516C\u7761\uFF0C\u8DD1\u5230\u8FD9\u91CC\u4E86\u3002\u300D</p><p>\u300C\u76F8\u516C\u51FA\u53BB\u4E86\u3002\u300D</p><p>\u300C\u54E6\uFF1F\u300D\u4E39\u5A18\u6697\u9053\uFF0C\u8FD9\u4E48\u665A\u6709\u4EC0\u4E48\u4E8B\u5462\uFF1F\u5979\u6709\u79CD\u611F\u89C9\uFF0C\u8FD9\u4EF6\u4E8B\u4E0E\u5979\u4EEC\u7684\u6848\u5B50\u6709\u5173\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u80E1\u4E25\u3001\u960E\u7F57\u671B\u5148\u540E\u8EAB\u6B7B\uFF0C\u518D\u6CA1\u6709\u72F1\u5352\u613F\u6765\u5730\u7262\u770B\u5B88\uFF0C\u9664\u4E86\u91CD\u65B0\u6234\u4E0A\u94C1\u67B7\uFF0C\u8FD9\u4E9B\u65E5\u5B50\u767D\u96EA\u83B2\u7ADF\u662F\u96BE\u5F97\u7684\u8F7B\u95F2\u3002\u5373\u4F7F\u5728\u5730\u7262\u5185\uFF0C\u5979\u4E5F\u80FD\u611F\u5230\u72F1\u4E2D\u6C14\u6C1B\u660E\u663E\u4E0D\u540C\u3002</p><p>\u960E\u7F57\u671B\u88AB\u6740\u8FD9\u6837\u7684\u5927\u4E8B\uFF0C\u7ADF\u7136\u8349\u8349\u6536\u6B93\u4E86\u4E8B\uFF0C\u663E\u7136\u6709\u66F4\u5927\u7684\u4E8B\u60C5\u53D1\u751F\u3002</p><p>\u300C\u4F55\u6E05\u6CB3\u8981\u6765\u4E86\u3002\u300D\u859B\u971C\u7075\u8BF4\u3002</p><p>\u867D\u7136\u662F\u7B2C\u4E8C\u6B21\u542C\u5230\uFF0C\u767D\u96EA\u83B2\u8FD8\u662F\u5FC3\u4E0B\u9707\u52A8\u3002\u5979\u4E0D\u76F8\u4FE1\u5B59\u5929\u7FBD\u4F1A\u300C\u597D\u5FC3\u300D\u5730\u544A\u8BC9\u5979\u5B9E\u60C5\u3002</p><p>\u300C\u4ED6\u4EEC\u5E72\u6211\u7684\u65F6\u5019\u8BF4\u7684\u3002\u300D\u859B\u971C\u7075\u9760\u5728\u5899\u4E0A\uFF0C\u5F77\u4F5B\u5728\u53D9\u8BF4\u522B\u4EBA\u7684\u906D\u9047\u3002</p><p>\u300C\u542C\u8BF4\u5929\u7262\u6709\u5973\u76D1\u3002\u300D\u859B\u971C\u7075\u5FFD\u7136\u8BF4\u3002\u300C\u53CD\u6B63\u4E0D\u4F1A\u6BD4\u8FD9\u66F4\u574F\u4E86\u3002\u300D</p><p>\u767D\u96EA\u83B2\u4E0D\u77E5\u600E\u4E48\u5B89\u6170\u5979\u3002\u4E5F\u65E0\u4ECE\u5B89\u6170\u3002</p><p>\u300C\u4F60\u5462\uFF1F\u300D\u859B\u971C\u7075\u95EE\uFF0C\u300C\u542C\u5230\u8FD9\u6D88\u606F\u662F\u4E0D\u662F\u5F88\u5F00\u5FC3\u3002\u300D</p><p>\u300C\u662F\u3002\u300D\u767D\u96EA\u83B2\u6CA1\u6709\u9690\u7792\u3002</p><p>\u859B\u971C\u7075\u5E7D\u5E7D\u53F9\u4E86\u53E3\u6C14\uFF0C\u300C\u4F60\u7684\u6848\u5B50\u4E5F\u8BB8\u4F1A\u7FFB\u8FC7\u6765\u5427\u3002\u773C\u4E0B\u59D3\u960E\u7684\u4E5F\u6B7B\u4E86\u3002\u300D</p><p>\u767D\u96EA\u83B2\u6C89\u9ED8\u4EE5\u5BF9\u3002\u8FD9\u6848\u5B50\u6700\u8981\u7D27\u7684\u662F\u859B\u971C\u7075\u7684\u53E3\u4F9B\u3002\u82E5\u975E\u5979\u6500\u54AC\uFF0C\u4E8B\u60C5\u600E\u4E48\u4F1A\u843D\u5230\u5982\u6B64\u5730\u6B65\u3002</p><p>\u859B\u971C\u7075\u5374\u50CF\u662F\u6CA1\u610F\u8BC6\u5230\u8FD9\u4E00\u70B9\u3002\u5979\u6014\u6014\u671B\u7740\u7262\u9876\u7684\u94C1\u94FE\uFF0C\u4E0D\u77E5\u5728\u60F3\u7740\u4EC0\u4E48\u3002</p><p>\u300C\u4EE5\u540E\u5462\uFF1F\u300D\u859B\u971C\u7075\u6CA1\u5934\u6CA1\u8111\u5730\u8BF4\u3002</p><p>\u300C\u55EF\uFF1F\u300D</p><p>\u300C\u51FA\u72F1\u4E86\u4F60\u4F1A\u505A\u4EC0\u4E48\uFF1F\u300D</p><p>\u300C\u6211\u4E48\uFF1F\u300D\u767D\u96EA\u83B2\u4ECE\u672A\u60F3\u8FC7\u3002</p><p>\u859B\u971C\u7075\u7B11\u4E86\u7B11\uFF0C\u300C\u8FD8\u505A\u6355\u5FEB\u5417\uFF1F\u300D</p><p>\u767D\u96EA\u83B2\u54AC\u4E86\u54AC\u5634\u5507\uFF0C\u300C\u4E0D\u3002\u4E0D\u4F1A\u3002\u300D</p><p>\u300C\u90A3\u4F60\u505A\u4EC0\u4E48\uFF1F\u300D</p><p>\u505A\u4EC0\u4E48\uFF1F\u4EC5\u4EC5\u4E09\u56DB\u4E2A\u6708\u524D\uFF0C\u5979\u8FD8\u662F\u65B0\u664B\u7684\u5211\u90E8\u6355\u5FEB\u3002\u6709\u7236\u6BCD\u4EB2\u4EBA\uFF0C\u6709\u663E\u8D6B\u7684\u5E08\u95E8\u3002\u73B0\u5728\u7239\u7239\u6B7B\u4E86\uFF0C\u6BCD\u4EB2\u88AB\u72F1\u5352\u4EEC\u6C61\u8FB1\uFF0C\u5E08\u95E8\u4E5F\u653E\u5F03\u4E86\u5979\u3002\u5373\u4F7F\u80FD\u591F\u51FA\u72F1\uFF0C\u5979\u4E5F\u5931\u53BB\u4E86\u592A\u591A\u592A\u591A\u3002</p><p>\u826F\u4E45\uFF0C\u767D\u96EA\u83B2\u6447\u4E86\u6447\u5934\uFF0C\u300C\u6211\u4E0D\u77E5\u9053\u3002\u300D</p><p>\u4E5F\u8BB8\u5979\u4F1A\u79BB\u5F00\u8FD9\u91CC\u3002\u5230\u4E00\u4E2A\u6CA1\u6709\u4EBA\u7684\u5730\u65B9\uFF0C\u5243\u5EA6\u4E3A\u5C3C\u3002\u4E5F\u8BB8\u5979\u4F1A\u9690\u540D\u57CB\u59D3\uFF0C\u5728\u4E61\u6751\u91CC\u4E86\u6B64\u6B8B\u751F\u3002\u603B\u4E4B\u90A3\u4E2A\u6614\u65E5\u7684\u767D\u96EA\u83B2\u5DF2\u7ECF\u6B7B\u4E86\u3002</p><p>\u300C\u4E5F\u8BB8\u4F60\u4F1A\u5AC1\u4EBA\uFF0C\u7136\u540E\u751F\u51E0\u4E2A\u5B69\u5B50\u3002\u300D</p><p>\u767D\u96EA\u83B2\u5FC3\u5E95\u62BD\u75BC\u4E86\u4E00\u4E0B\u3002\u5979\u8FD8\u80FD\u5AC1\u4EBA\u5417\uFF1F\u5979\u600E\u4E48\u80FD\u5FD8\u4E86\u90A3\u4E9B\u79BD\u517D\u600E\u6837\u5BF9\u5F85\u5979\u7684\u3002</p><p>\u859B\u971C\u7075\u55DF\u53F9\u9053\uFF1A\u300C\u53EF\u60DC\u4E86\u4F60\u4E00\u8EAB\u529F\u592B\u2026\u2026\u300D</p><p>\u767D\u96EA\u83B2\u622A\u65AD\u5979\uFF0C\u300C\u6211\u5E0C\u671B\u6211\u4ECE\u6765\u5C31\u6CA1\u5B66\u8FC7\u3002\u300D</p><p>\u859B\u971C\u7075\u8F7B\u63C9\u7740\u811A\u8E1D\uFF0C\u6539\u53D8\u4E86\u8BDD\u9898\uFF0C\u300C\u4E0D\u77E5\u9053\u4F55\u6E05\u6CB3\u4EC0\u4E48\u65F6\u5019\u6765\u3002\u300D</p><p>\u5979\u82E5\u65E0\u5176\u4E8B\u5730\u8BF4\uFF1A\u300C\u65E9\u4E9B\u6765\uFF0C\u65E9\u4E9B\u5224\u4E86\uFF0C\u628A\u6211\u4E00\u5200\u6740\u4E86\u3002\u591A\u4E48\u5E72\u51C0\u3002\u300D</p><p>\u767D\u96EA\u83B2\u5374\u4E0D\u80FD\u6B7B\u3002\u5979\u8FD8\u6709\u592A\u591A\u7275\u6302\u3002\u6BCD\u4EB2\u3001\u59B9\u59B9\u3001\u5F1F\u5F1F\u3002</p><p>\u859B\u971C\u7075\u5FFD\u7136\u60F3\u8D77\u6765\uFF0C\u300C\u542C\u8BF4\u8C0B\u9006\u662F\u8981\u6740\u5343\u5200\u7684\u3002\u62FF\u5F20\u6E14\u7F51\u7F69\u5728\u8EAB\u4E0A\uFF0C\u4E00\u5757\u4E00\u5757\u96F6\u788E\u5730\u628A\u8089\u5272\u4E0B\u6765\u3002\u300D\u859B\u971C\u7075\u7B11\u9053\uFF1A\u300C\u90A3\u8BE5\u591A\u75DB\u5462\u3002\u300D</p><p>\u300C\u5230\u65F6\u5019\u8BF4\u4E0D\u5B9A\u4F60\u5DF2\u7ECF\u51FA\u72F1\u4E86\u3002\u300D\u859B\u971C\u7075\u671B\u7740\u767D\u96EA\u83B2\uFF0C\u300C\u4F60\u4F1A\u6765\u770B\u5417\uFF1F\u300D</p><p>\u767D\u96EA\u83B2\u51DD\u89C6\u5979\u7684\u773C\u775B\uFF0C\u7F13\u7F13\u9053\uFF1A\u300C\u5982\u679C\u4E0D\u8D85\u8FC7\u5341\u4E94\u4E08\uFF0C\u6211\u4F1A\u7528\u9556\u6253\u6B7B\u4F60\u3002\u300D</p><p>\u859B\u971C\u7075\u7B11\u9053\uFF1A\u300C\u8FD9\u53EF\u662F\u4F60\u7B54\u5E94\u7684\uFF0C\u5207\u83AB\u5FD8\u8BB0\u4E86\u3002\u90A3\u8981\u7B49\u4F60\u5148\u51FA\u72F1\u4E86\u3002\u300D</p><p>\u767D\u96EA\u83B2\u5FCD\u4E0D\u4F4F\u95EE\u9053\uFF1A\u300C\u4E3A\u4EC0\u4E48\u4E0D\u8BA9\u6211\u52AB\u4F60\u51FA\u53BB\uFF1F\u300D\u65E2\u7136\u662F\u4EA4\u6613\uFF0C\u5979\u8981\u5F97\u672A\u514D\u592A\u5C11\u4E86\u3002</p><p>\u859B\u971C\u7075\u8BB6\u7136\u770B\u4E86\u5979\u4E00\u773C\uFF0C\u300C\u4F60\u4F1A\u5417\uFF1F\u300D</p><p>\u4E00\u4E2A\u6311\u65AD\u4E86\u811A\u7B4B\u7684\u5973\u5B50\u7F62\u4E86\uFF0C\u5373\u4F7F\u5979\u6709\u4EC0\u4E48\u7F6A\u8FC7\uFF0C\u8FD9\u4E9B\u65E5\u5B50\u53D7\u7684\u6298\u78E8\u4E5F\u8DB3\u591F\u4E86\u3002</p><p>\u767D\u96EA\u83B2\u7B11\u4E86\u7B11\uFF0C\u300C\u4E0D\u4F1A\u3002\u300D</p><p>\u767D\u96EA\u83B2\u5728\u7761\u68A6\u4E2D\uFF0C\u7A81\u7136\u88AB\u4E00\u9635\u94C1\u5668\u7684\u78E8\u64E6\u58F0\u60CA\u9192\u3002\u4E00\u5730\u7262\u94C1\u7F69\u6253\u5F00\uFF0C\u51E0\u540D\u72F1\u5352\u63D0\u7740\u706F\u7B3C\u9C7C\u8D2F\u800C\u5165\uFF3B\uFF37\uFF37\uFF37\u3002\uFF3Aei8\u3002\uFF23\uFF2F\uFF2D\u8D3C\u5427\u7535\u5B50\u4E66\uFF3D\u3002\u6DF1\u66F4\u534A\u591C\uFF0C\u4ED6\u4EEC\u7A7F\u7684\u5374\u51FA\u5947\u5F97\u6574\u9F50\uFF0C\u7682\u8863\u7682\u9774\uFF0C\u8FDE\u5E3D\u5B50\u4E5F\u6234\u7740\u3002</p><p>\u6700\u524D\u9762\u7684\u662F\u5B59\u5929\u7FBD\uFF0C\u4ED6\u4E3E\u7740\u706F\u7B3C\u628A\u767D\u96EA\u83B2\u4E0A\u4E0B\u7167\u4E86\u4E00\u904D\uFF0C\u4F3C\u4E4E\u5728\u770B\u6709\u4EC0\u4E48\u7834\u7EFD\u3002\u7136\u540E\u4E00\u6446\u5934\uFF0C\u300C\u5E26\u8D70\u3002\u300D</p><p>\u4E00\u540D\u72F1\u5352\u6296\u5F00\u94C1\u7D22\uFF0C\u5957\u5728\u767D\u96EA\u83B2\u9888\u4E2D\u3002\u767D\u96EA\u83B2\u5FAE\u5FAE\u4E00\u6323\uFF0C\u90A3\u72F1\u5352\u9669\u4E9B\u8DCC\u5012\u3002</p><p>\u5B59\u5929\u7FBD\u4E00\u628A\u633D\u4F4F\u94C1\u7D22\uFF0C\u6C89\u7740\u8138\u9053\uFF1A\u300C\u4F55\u5927\u4EBA\u5DF2\u7ECF\u6765\u4E86\u3002\u8981\u8FDE\u591C\u63D0\u5BA1\u3002\u300D</p>',98);function t(i,_){return a}var o=e(r,[["render",t]]);export{o as default};
