import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const a={},r=p('<h1 id="_41-\u7834\u809B" tabindex="-1"><a class="header-anchor" href="#_41-\u7834\u809B" aria-hidden="true">#</a> 41 \u7834\u809B</h1><p>\u7389\u5A18\u54AC\u4E86\u54AC\u5634\u5507\u9053\uFF1A\u300C\u5927\u54E5\uFF0C\u6C42\u4F60\u4E5F\u591A\u7167\u5E94\u6211\u5427\u3002\u6211\u8DDF\u59D0\u59D0\u4E00\u6837\u90FD\u4F9D\u4F60\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u7B11\u9053\uFF1A\u300C\u597D\u8BF4\u3002\u6211\u5148\u7167\u5E94\u7167\u5E94\u4F60\u7684\u5C41\u773C\u5427\u3002\u300D</p><p>\u7389\u5A18\u770B\u4E86\u770B\u4ED6\u7684\u9633\u5177\uFF0C\u8138\u4E0A\u9732\u51FA\u4E00\u4E1D\u4E3A\u96BE\u7684\u7F9E\u8272\u3002</p><p>\u5B59\u5929\u7FBD\u9053\uFF1A\u300C\u5DF2\u7ECF\u5AC1\u8FC7\u7684\u5987\u4EBA\u4E86\uFF0C\u8FD8\u6709\u4EC0\u4E48\u6015\u7F9E\u7684\uFF1F\u300D</p><p>\u7389\u5A18\u77E5\u9053\u5FC5\u53EB\u4ED6\u9042\u4E86\u5FC3\u610F\uFF0C\u53EA\u597D\u5C0F\u58F0\u9053\uFF1A\u300C\u4F46\u542C\u5927\u54E5\u5429\u5490\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u4FEF\u5728\u5979\u8033\u8FB9\u8BF4\u4E86\u51E0\u53E5\uFF0C\u7389\u5A18\u7F9E\u5F97\u8033\u6839\u5B50\u90FD\u7EA2\u900F\u4E86\uFF0C\u534A\u664C\u4F4E\u7740\u5934\u5C0F\u58F0\u5E94\u4E86\u3002</p><p>\u5858\u91CC\u9762\u7684\u77F3\u5757\u9AD8\u4F4E\u4E0D\u4E00\uFF0C\u6709\u7684\u5927\u5982\u684C\u9762\uFF0C\u6709\u7684\u72B6\u5982\u9C7C\u80CC\uFF0C\u6216\u6F5C\u6216\u9732\uFF0C\u5F62\u6001\u5404\u5F02\u3002</p><p>\u7389\u5A18\u62E3\u4E86\u5757\u6D78\u5728\u6C34\u4E2D\u7684\u5706\u77F3\uFF0C\u4FEF\u8EAB\u8DB4\u5728\u4E0A\u9762\u3002\u90A3\u77F3\u6709\u534A\u4EBA\u5927\u5C0F\uFF0C\u8272\u767D\u5982\u7389\uFF0C\u9876\u90E8\u51B2\u5237\u5F97\u5149\u6ED1\u5982\u955C\uFF0C\u79BB\u6C34\u9762\u5BF8\u8BB8\u9AD8\u4F4E\u3002\u7389\u5A18\u8DB4\u5728\u4E0A\u9762\uFF0C\u534A\u8EAB\u90FD\u6D78\u5728\u6C34\u4E2D\uFF0C\u53EA\u6709\u4E00\u53EA\u767D\u81C0\u513F\u7FD8\u5728\u5916\u9762\uFF0C\u5F77\u4F5B\u6D6E\u5728\u6C34\u4E0A\u4E00\u822C\u3002</p><p>\u7389\u5A18\u4E24\u6761\u7389\u817F\u5206\u5F00\uFF0C\u5F2F\u66F2\u7740\u8E6C\u5728\u6C34\u5E95\uFF0C\u5C06\u5C41\u80A1\u8038\u5F97\u66F4\u9AD8\uFF0C\u5979\u4E24\u53EA\u4E73\u623F\u8FDE\u540C\u9999\u80A9\u90FD\u6D78\u5728\u6C34\u91CC\uFF0C\u626C\u8D77\u5934\u90E8\uFF0C\u7136\u540E\u4E24\u624B\u7ED5\u5230\u81C0\u540E\uFF0C\u63B0\u5F00\u5C41\u80A1\uFF0C\u5C06\u5BC6\u85CF\u7684\u5C41\u773C\u513F\u66B4\u9732\u5728\u5149\u5929\u5316\u65E5\u4E4B\u4E0B\u3002</p><p>\u9633\u5149\u76F4\u5C04\u800C\u4E0B\uFF0C\u7389\u5A18\u534A\u6D78\u5728\u6C34\u4E2D\u7684\u8089\u4F53\u5448\u73B0\u51FA\u4E24\u79CD\u622A\u7136\u4E0D\u540C\u7684\u5149\u6CFD\uFF0C\u6C34\u4E0B\u7684\u72B9\u5982\u878D\u5728\u6C34\u4E2D\u7684\u6708\u5149\u4E00\u6837\u83B9\u767D\uFF0C\u6C34\u4E0A\u7684\u4E00\u7247\u96EA\u5AE9\u3002\u96EA\u6ED1\u7684\u81C0\u6C9F\u6D12\u6EE1\u9633\u5149\uFF0C\u4E2D\u95F4\u4E00\u53EA\u5C0F\u5DE7\u7684\u5C41\u773C\u513F\u53C8\u7EA2\u53C8\u5AE9\uFF0C\u5F77\u4F5B\u4E00\u53EA\u6A31\u6843\u5D4C\u5728\u7C89\u56E2\u822C\u7684\u96EA\u81C0\u4E2D\uFF0C\u8273\u5149\u52A8\u4EBA\u3002</p><p>\u7389\u5A18\u957F\u53D1\u843D\u5728\u6C34\u4E2D\uFF0C\u63A9\u4F4F\u4E86\u9762\u5B54\uFF0C\u5979\u7F9E\u602F\u5730\u6446\u597D\u59FF\u52BF\uFF0C\u5C0F\u58F0\u9053\uFF1A\u300C\u6709\u52B3\u5B98\u5DEE\u5927\u54E5\u8D39\u5FC3\u2026\u2026\u7ED9\u59BE\u8EAB\u7684\u540E\u5EAD\u5F00\u82DE\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u7B11\u9053\uFF1A\u300C\u600E\u4E48\u5F00\u554A\uFF1F\u300D</p><p>\u7389\u5A18\u7F9E\u4E0D\u53EF\u652F\uFF0C\u55EB\u5685\u534A\u664C\uFF0C\u624D\u7167\u5B59\u5929\u7FBD\u6559\u5979\u7684\u9053\uFF1A\u300C\u7528\u5B98\u5DEE\u5927\u54E5\u7684\u5927\u9E21\u5DF4\uFF0C\u63D2\u5230\u59B9\u59B9\u7684\u5C0F\u5C41\u773C\u513F\u91CC\u3002\u300D</p><p>\u300C\u53EA\u662F\u63D2\u5417\uFF1F\u300D</p><p>\u7389\u5A18\u88AB\u4ED6\u903C\u5F97\u7A98\u8FEB\uFF0C\u7F9E\u7B54\u7B54\u9053\uFF1A\u300C\u8FD8\u8981\u52B3\u70E6\u5B98\u5DEE\u5927\u54E5\u7528\u529B\u5E72\u59B9\u59B9\u7684\u5C41\u773C\u513F\u3002</p><p>\u7528\u5B98\u5DEE\u54E5\u54E5\u7684\u5927\u9E21\u5DF4\uFF0C\u628A\u59B9\u59B9\u7684\u5C0F\u5C41\u773C\u513F\u6491\u5927\u4E86\uFF0C\u5F80\u540E\u597D\u7528\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u7B11\u9053\uFF1A\u300C\u597D\u4E56\u7684\u5C0F\u59B9\u59B9\u3002\u628A\u5C41\u80A1\u518D\u63B0\u5F00\u4E9B\uFF0C\u5B98\u5DEE\u5927\u54E5\u8981\u7ED9\u4F60\u5C41\u773C\u513F\u5F00\u82DE\u4E86\u3002\u300D</p><p>\u7389\u5A18\u9053\uFF1A\u300C\u591A\u8C22\u5B98\u5DEE\u5927\u54E5\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u64A9\u4E86\u6367\u6C34\u6D47\u5728\u7389\u5A18\u81C0\u95F4\uFF0C\u7136\u540E\u62B1\u4F4F\u5979\u7684\u96EA\u81C0\uFF0C\u9F9F\u5934\u9876\u4F4F\u5C41\u773C\u513F\uFF0C\u7528\u529B\u538B\u4E0B\u3002</p><p>\u7389\u5A18\u53EA\u89C9\u4E00\u4E2A\u7C97\u5706\u7684\u7269\u4F53\u786C\u786C\u9876\u4F4F\u809B\u6D1E\uFF0C\u5E26\u7740\u4E00\u80A1\u5F3A\u5927\u7684\u538B\u529B\uFF0C\u5C06\u5C41\u773C\u513F\u6324\u5F97\u5706\u5706\u5F20\u5F00\u3002\u5C41\u773C\u513F\u5F88\u5FEB\u6491\u5230\u6781\u9650\uFF0C\u4F20\u6765\u4E00\u80A1\u96BE\u5FCD\u7684\u80C0\u75DB\u3002\u5979\u4E00\u53E3\u6C14\u54FD\u5728\u5589\u5934\uFF0C\u5F20\u7740\u5C0F\u5634\uFF0C\u624B\u6307\u7981\u4E0D\u4F4F\u8F7B\u98A4\u8D77\u6765\u3002</p><p>\u5B59\u5929\u7FBD\u8DB4\u5728\u7389\u5A18\u80CC\u540E\uFF0C\u8089\u68D2\u7B14\u76F4\u63D2\u5728\u90A3\u53EA\u96EA\u81C0\u6B63\u4E2D\uFF0C\u9F9F\u5934\u88AB\u4E00\u5708\u67D4\u97E7\u7684\u8089\u7B8D\u7B8D\u7740\uFF0C\u4F20\u6765\u9635\u9635\u5FEB\u611F\u3002\u7389\u5A18\u809B\u6D1E\u6CBE\u4E86\u6C34\uFF0C\u6ED1\u987A\u8BB8\u591A\uFF0C\u5C06\u9F9F\u5934\u5305\u88F9\u5F97\u5BC6\u4E0D\u900F\u98CE\u3002\u4ED6\u8038\u8EAB\u4E00\u633A\uFF0C\u9F9F\u5934\u53FD\u7684\u4E00\u58F0\u786C\u94BB\u8FDB\u53BB\u3002</p><p>\u81C0\u95F4\u4F20\u6765\u4E00\u9635\u6495\u88C2\u7684\u75DB\u695A\uFF0C\u7389\u5A18\u75DB\u53EB\u7740\u6602\u8D77\u67D4\u9888\uFF0C\u4E24\u6761\u7389\u817F\u633A\u76F4\uFF0C\u5C41\u773C\u513F\u5939\u5F97\u6108\u53D1\u7D27\u4E86\u3002\u65F6\u5019\u6B63\u957F\uFF0C\u5B59\u5929\u7FBD\u4E5F\u4E0D\u7740\u6025\uFF0C\u8089\u68D2\u63D2\u5230\u4E00\u534A\uFF0C\u505C\u4E0B\u6765\u5206\u5F00\u7389\u5A18\u96EA\u6ED1\u7684\u81C0\u8089\uFF0C\u6B23\u8D4F\u5979\u5AE9\u809B\u65B0\u7834\u7684\u8273\u6001\u3002</p><p>\u7389\u5A18\u5C41\u80A1\u672C\u751F\u5F97\u7F8E\uFF0C\u6B64\u65F6\u90A3\u53EA\u5C0F\u5DE7\u7684\u5C41\u773C\u513F\u88AB\u8089\u68D2\u6574\u4E2A\u9876\u5165\u809B\u5185\uFF0C\u53EA\u6709\u4E00\u5708\u767D\u767D\u7684\u81C0\u8089\u5305\u88F9\u7740\u8089\u68D2\u3002\u4E00\u80A1\u6BB7\u7EA2\u7684\u9C9C\u8840\u4ECE\u8089\u68D2\u9876\u5165\u7684\u51F9\u5904\u6D8C\u51FA\uFF0C\u5728\u8089\u68D2\u4E0A\u6CBE\u4E86\u51E0\u8BB8\u8165\u7EA2\uFF0C\u987A\u7740\u81C0\u6C9F\u873F\u8712\u800C\u4E0B\uFF0C\u8BA9\u4EBA\u53C8\u601C\u53C8\u601C\u3002</p><p>\u5B59\u5929\u7FBD\u7B11\u9053\uFF1A\u300C\u5F00\u4E86\u53EA\u597D\u82DE\uFF0C\u8FD8\u4E0D\u535A\u4E2A\u53E3\u5F69\uFF1F\u300D</p><p>\u7389\u5A18\u75DB\u5F97\u53D1\u660F\uFF0C\u4F46\u5979\u6015\u5B59\u5929\u7FBD\u6015\u5F97\u7D27\u4E86\uFF0C\u88AB\u4ED6\u5F3A\u5F00\u4E86\u540E\u5EAD\uFF0C\u8FD8\u8D3A\u9053\uFF1A\u300C\u606D\u559C\u5B98\u5DEE\u5927\u54E5\uFF0C\u91C7\u4E86\u59BE\u8EAB\u540E\u5EAD\u7684\u9C9C\u82B1\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u7B11\u9053\uFF1A\u300C\u679C\u7136\u662F\u559C\u4E8B\u3002\u600E\u4E48\u6CA1\u534A\u70B9\u559C\u610F\uFF0C\u4F60\u4E14\u7B11\u7740\u8BF4\u3002\u300D</p><p>\u7389\u5A18\u5FC3\u5E95\u6D41\u6CEA\uFF0C\u8138\u4E0A\u52C9\u5F3A\u5E26\u51FA\u6B22\u5BB9\uFF0C\u5AE3\u7136\u7B11\u9053\uFF1A\u300C\u606D\u559C\u5B98\u5DEE\u5927\u54E5\u5F97\u4E86\u59BE\u8EAB\u540E\u5EAD\u7684\u5F69\u5934\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u7B11\u9053\uFF1A\u300C\u540C\u559C\u540C\u559C\u3002\u300D\u8BF4\u7740\u9633\u5177\u91CD\u91CD\u6363\u5165\uFF0C\u5728\u7389\u5A18\u7D27\u5BC6\u7684\u809B\u6D1E\u91CC\u7528\u529B\u633A\u52A8\u3002</p><p>\u4E24\u4EBA\u90FD\u534A\u8EAB\u6D78\u5728\u6C34\u4E2D\uFF0C\u8FDC\u5904\u770B\u53BB\uFF0C\u72B9\u5982\u6C34\u9762\u4E0A\u7FD8\u7740\u4E00\u53EA\u96EA\u81C0\uFF0C\u88AB\u540E\u9762\u7684\u6C49\u5B50\u7740\u529B\u6345\u5165\u3002\u7389\u5A18\u81C0\u95F4\u9C9C\u8840\u8D8A\u6D8C\u8D8A\u591A\uFF0C\u4E00\u4E32\u4E32\u65AD\u7EBF\u7684\u739B\u7459\u73E0\u5B50\u822C\u6389\u8FDB\u6C34\u4E2D\u3002</p><p>\u5B59\u5929\u7FBD\u62FF\u5979\u53EA\u662F\u53D6\u4E50\uFF0C\u6CA1\u6709\u534A\u5206\u601C\u60DC\u4E4B\u5FC3\uFF0C\u5728\u5979\u65B0\u5F00\u7684\u5AE9\u809B\u4E2D\u4E00\u5473\u6345\u5F04\u3002\u7389\u5A18\u8DB4\u7684\u77F3\u5934\u672C\u5728\u6C34\u4E0B\uFF0C\u6781\u529B\u7FD8\u8D77\u81C0\u513F\u6765\u8FCE\u5408\u3002\u5B59\u5929\u7FBD\u4E00\u62BD\u4E00\u9001\u90FD\u4F7F\u5C3D\u529B\u6C14\uFF0C\u5C06\u90A3\u53EA\u96EA\u5AE9\u7684\u767D\u81C0\u538B\u5F97\u4E0D\u4F4F\u53D8\u5F62\uFF0C\u6E10\u6E10\u6D78\u5165\u6C34\u4E2D\u3002</p><p>\u5929\u6C14\u9177\u6691\uFF0C\u7389\u5A18\u624D\u6D74\u8FC7\u7684\u8EAB\u5B50\u53C8\u6ED1\u53C8\u51C9\uFF0C\u9165\u723D\u52A8\u4EBA\u3002\u5B59\u5929\u7FBD\u4E00\u53E3\u6C14\u628A\u9633\u5177\u9001\u8FDB\u7389\u5A18\u809B\u5185\uFF0C\u5C0F\u8179\u538B\u7740\u5979\u5145\u6EE1\u5F39\u6027\u7684\u5706\u81C0\u6765\u56DE\u63C9\u5F04\u3002\u7389\u5A18\u65E9\u5DF2\u652F\u6491\u4E0D\u4F4F\uFF0C\u4E00\u8FB9\u54ED\u4E00\u8FB9\u8BA8\u9976\uFF0C\u5B59\u5929\u7FBD\u53EA\u662F\u7B11\u8C11\u3002\u7389\u5A18\u5659\u7740\u6CEA\u82B1\uFF0C\u767D\u751F\u751F\u7684\u96EA\u81C0\u88AB\u8089\u68D2\u63D2\u7740\uFF0C\u7C89\u56E2\u822C\u5728\u77F3\u4E0A\u6EDA\u6765\u6EDA\u53BB\uFF0C\u4E0D\u65AD\u6D12\u4E0B\u4E32\u4E32\u8840\u73E0\u3002</p><p>\u7389\u5A18\u7684\u5C41\u80A1\u7FD8\u5728\u575A\u786C\u7684\u77F3\u9762\u4E0A\uFF0C\u6ED1\u52A8\u95F4\u66F4\u663E\u5F97\u67D4\u8F6F\u4E30\u817B\u3002\u5979\u5C41\u773C\u513F\u4E5F\u6D78\u5165\u6C34\u4E2D\uFF0C\u8089\u68D2\u8FDB\u51FA\u95F4\u53FD\u53FD\u4F5C\u54CD\uFF0C\u66F4\u589E\u8DA3\u5473\u3002\u5B59\u5929\u7FBD\u4E00\u624B\u4E00\u4E2A\uFF0C\u635E\u4F4F\u4E86\u5979\u6D78\u5728\u6C34\u91CC\u7684\u4E73\u623F\uFF0C\u5728\u624B\u4E2D\u63C9\u634F\u6293\u62E7\u3002</p><p>\u7389\u5A18\u6252\u7740\u77F3\u5934\uFF0C\u88AB\u4ED6\u5E72\u5F97\u6B7B\u53BB\u6D3B\u6765\uFF0C\u4E00\u8FB9\u8FD8\u88AB\u903C\u7740\u5A07\u6EF4\u6EF4\u8BF4\u4E9B\u6DEB\u8BCD\u6D6A\u8BED\uFF0C\u7ED9\u809B\u4E2D\u7684\u8089\u68D2\u52A9\u5174\u3002\u5B59\u5929\u7FBD\u5174\u81F4\u52C3\u53D1\uFF0C\u76F4\u5E72\u4E86\u5927\u534A\u4E2A\u65F6\u8FB0\uFF0C\u624D\u4E00\u80A1\u6D53\u7CBE\u5C04\u8FDB\u7389\u5A18\u80A0\u9053\u6DF1\u5904\u3002</p><p>\u5B59\u5929\u7FBD\u63D2\u7740\u7389\u5A18\u7684\u5C41\u773C\u513F\uFF0C\u628A\u5979\u62B1\u5230\u5CB8\u8FB9\uFF0C\u8BA9\u5979\u5939\u7D27\u4E86\uFF0C\u624D\u62D4\u51FA\u8089\u68D2\u3002</p><p>\u7389\u5A18\u53C8\u75DB\u53C8\u51B7\uFF0C\u8138\u8272\u96EA\u767D\u8DB4\u5728\u5730\u4E0A\uFF0C\u9AD8\u4E3E\u7740\u767D\u767D\u7684\u5927\u5C41\u80A1\uFF0C\u8BA9\u5B59\u5929\u7FBD\u89C2\u8D4F\u5979\u65B0\u5F00\u7684\u5C41\u773C\u513F\u3002\u7389\u5A18\u5AE9\u809B\u7D27\u7D27\u6536\u7740\uFF0C\u4E0D\u4F4F\u6DCC\u51FA\u9C9C\u8840\u3002</p><p>\u5F3A\u5FCD\u7247\u523B\uFF0C\u5C41\u773C\u513F\u5FFD\u7136\u4E00\u677E\uFF0C\u50CF\u6492\u5C3F\u822C\u55B7\u51FA\u4E00\u80A1\u6E05\u6C34\u3002</p><p>\u5B59\u5929\u7FBD\u6309\u7740\u5979\u5728\u6C34\u4E0B\u809B\u5978\u8BB8\u4E45\uFF0C\u62BD\u9001\u95F4\u6324\u8FDB\u53BB\u6EE1\u809B\u7684\u6C34\uFF0C\u6B64\u65F6\u90FD\u6DCC\u4E86\u51FA\u6765\u3002</p><p>\u6E05\u6C34\u6DCC\u5B8C\uFF0C\u7389\u5A18\u5C41\u773C\u513F\u4E5F\u88AB\u51B2\u5F97\u7FFB\u5F00\uFF0C\u518D\u65E0\u6CD5\u5408\u62E2\u3002\u6700\u540E\u6D41\u51FA\u7684\u662F\u4E00\u80A1\u767D\u767D\u7684\u7CBE\u6DB2\uFF0C\u6302\u5728\u6495\u88C2\u7684\u5C41\u773C\u513F\u4E0A\uFF0C\u5728\u817F\u95F4\u4E0D\u4F4F\u6447\u6643\u3002</p><p>\u7389\u5A18\u5F00\u8FC7\u82DE\u7684\u5C41\u773C\u513F\u6BD4\u8D77\u521D\u65F6\u7684\u7F9E\u6DA9\u5DF2\u7ECF\u662F\u9762\u76EE\u5168\u975E\u3002\u809B\u857E\u5916\u7FFB\uFF0C\u4E0A\u4E0B\u88C2\u5F00\u51E0\u9053\u51C4\u60E8\u7684\u4F24\u53E3\uFF0C\u4E2D\u95F4\u5F20\u5F00\u4E00\u4E2A\u5706\u6D1E\u3002\u6BD4\u539F\u6765\u5927\u4E86\u6570\u500D\uFF0C\u7EA2\u901A\u901A\u9F13\u5728\u81C0\u7F1D\u91CC\u3002\u886C\u7740\u767D\u6ED1\u7684\u81C0\u8089\uFF0C\u5F77\u4F5B\u4E00\u4E2A\u88AB\u4EBA\u5F53\u5A3C\u5993\u5E72\u8FC7\u7684\u8D35\u5987\uFF0C\u65E0\u590D\u5F80\u65E5\u7684\u5A07\u6001\u3002</p><p>\u5B59\u5929\u7FBD\u62FF\u8D77\u5979\u7684\u624B\uFF0C\u5728\u81C0\u95F4\u6469\u6332\u7740\u7B11\u9053\uFF1A\u300C\u592B\u4EBA\u6478\u6478\uFF0C\u662F\u4E0D\u662F\u5927\u4E86\u4E9B\u3002\u300D</p><p>\u7389\u5A18\u88AB\u4ED6\u5E72\u5F97\u6015\u4E86\uFF0C\u72B9\u5982\u767D\u5154\u89C1\u4E86\u8001\u864E\uFF0C\u6B64\u65F6\u809B\u4E2D\u53D7\u521B\u75DB\u751A\uFF0C\u6478\u53BB\u65F6\u7FFB\u88C2\u7684\u809B\u857E\u53C8\u5927\u53C8\u9F13\uFF0C\u7ADF\u628A\u4E00\u53EA\u5C0F\u5C0F\u7684\u8089\u5B54\u94BB\u6210\u4E86\u4E2A\u8C41\u8FB9\u7684\u5927\u6D1E\uFF0C\u5FC3\u91CC\u867D\u7136\u53C8\u6015\u53C8\u75DB\uFF0C\u8138\u4E0A\u5374\u4E0D\u6562\u5E26\u51FA\u5206\u6BEB\uFF0C\u53EA\u5659\u7740\u6CEA\u9053\uFF1A\u300C\u591A\u8C22\u5927\u54E5\u4E86\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u7B11\u9053\uFF1A\u300C\u4E0D\u5FC5\u5BA2\u6C14\u4E86\uFF0C\u78D5\u4E2A\u5934\u8C22\u6211\u597D\u4E86\u3002\u8BF4\u4E0A\u51E0\u53E5\u5409\u7965\u8BDD\u513F\uFF0C\u5927\u5BB6\u5E86\u8D3A\u3002\u300D</p><p>\u7389\u5A18\u5FCD\u75DB\u8F6C\u8FC7\u8EAB\uFF0C\u8DB4\u5728\u5B59\u5929\u7FBD\u811A\u4E0B\uFF0C\u4E56\u4E56\u5730\u78D5\u4E86\u51E0\u4E2A\u5934\uFF0C\u300C\u591A\u627F\u5B98\u5DEE\u5927\u54E5\u6069\u5178\uFF0C\u8D39\u5FC3\u5E72\u5927\u4E86\u8D31\u59BE\u7684\u5C41\u773C\u513F\uFF0C\u8D31\u59BE\u611F\u6FC0\u4E0D\u5C3D\u3002\u8499\u5B98\u5DEE\u5927\u54E5\u4E0D\u5ACC\uFF0C\u53D7\u7528\u4E86\u59BE\u8EAB\u5C41\u773C\u513F\u7684\u7B2C\u4E00\u6B21\uFF0C\u8D31\u59BE\u7ED9\u60A8\u9053\u559C\u4E86\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u6258\u8D77\u5979\u7684\u4E0B\u5DF4\uFF0C\u5C06\u8089\u68D2\u9001\u5230\u5979\u5507\u8FB9\uFF0C\u300C\u5B83\u5728\u5C41\u773C\u513F\u91CC\u8F9B\u82E6\u90A3\u4E48\u4E45\uFF0C\u52B3\u70E6\u4F60\u7684\u5C0F\u5634\u8214\u8214\u3002\u300D</p><p>\u7389\u5A18\u89C1\u4ED6\u9633\u5177\u8FDE\u6839\u90E8\u90FD\u88AB\u9C9C\u8840\u67D3\u7EA2\u4E86\uFF0C\u53EF\u4EE5\u60F3\u8C61\u81EA\u5DF1\u540E\u5EAD\u7684\u60E8\u72B6\u3002\u5979\u4F38\u51FA\u9999\u820C\uFF0C\u5728\u8089\u68D2\u4E0A\u4ED4\u7EC6\u8214\u542E\u8D77\u6765\u3002</p><p>\u4F38\u4E86\u4E2A\u61D2\u8170\uFF0C\u300C\u771F\u662F\u7D2F\u4E86\u3002\u6211\u5148\u7761\u4E00\u4F1A\u513F\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u5047\u7684\uFF01\u300D\u767D\u96EA\u83B2\u5FFD\u7136\u7741\u5F00\u773C\u775B\u3002</p><p>\u859B\u971C\u7075\u88AB\u5979\u60CA\u9192\uFF0C\u300C\u4EC0\u4E48\u662F\u5047\u7684\uFF1F\u300D</p><p>\u300C\u90A3\u4E2A\u4F55\u6E05\u6CB3\u662F\u5047\u7684\uFF01\u300D\u767D\u96EA\u83B2\u521D\u65F6\u6EE1\u5FC3\u5E0C\u5180\uFF0C\u76FC\u671B\u7740\u9047\u4E0A\u4E2A\u6E05\u5B98\uFF0C\u6D17\u8131\u51A4\u6848\uFF0C\u4E2D\u4E86\u8BA1\u8FD8\u4E0D\u77E5\u6653\u3002\u6B64\u65F6\u56DE\u60F3\u8D77\u6765\uFF0C\u90A3\u4E2A\u6240\u8C13\u7684\u300C\u4F55\u6E05\u6CB3\u300D\u6839\u672C\u5C31\u662F\u5C31\u72F1\u65B9\u4E32\u901A\u597D\uFF0C\u6F14\u4E86\u4E00\u51FA\u620F\u3002\u60F3\u5230\u81EA\u5DF1\u5728\u90A3\u4F19\u732A\u72D7\u4E0D\u5982\u7684\u72F1\u5352\u9762\u524D\u8131\u8863\u9A8C\u9634\uFF0C\u4EE4\u7B7E\u63D2\u7A74\uFF0C\u767D\u96EA\u83B2\u53C8\u662F\u6094\u6068\u53C8\u662F\u7F9E\u8FB1\uFF0C\u51E0\u4E4E\u843D\u4E0B\u6CEA\u6765\u3002</p><p>\u859B\u971C\u7075\u867D\u4E0D\u77E5\u8BE6\u60C5\uFF0C\u5FC3\u91CC\u4E5F\u660E\u767D\u4E86\u4E00\u4E9B\uFF0C\u53EA\u662F\u4E0D\u77E5\u90A3\u72F1\u5352\u4EEC\u4F55\u5FC5\u8D39\u6B64\u5468\u7AE0\uFF0C\u626E\u4E86\u4F55\u6E05\u6CB3\u6765\u9A97\u5979\u3002</p><p>\u767D\u96EA\u83B2\u524D\u540E\u4E00\u60F3\uFF0C\u731C\u51FA\u4E86\u516B\u4E5D\u5206\uFF0C\u300C\u5FC5\u662F\u4F55\u6E05\u6CB3\u771F\u8981\u6765\u4E86\u3002\u90A3\u73ED\u72F1\u5352\u9A97\u4E0D\u5F97\u4ED6\uFF0C\u5C31\u6DF7\u5145\u4E86\u9A97\u6211\u3002\u300D</p><p>\u8FD9\u6B63\u662F\u5B59\u5929\u7FBD\u60F3\u51FA\u7684\u9C7C\u76EE\u6DF7\u73E0\u4E4B\u8BA1\u3002\u4F55\u6E05\u6CB3\u5B98\u58F0\u663E\u8D6B\uFF0C\u4ED6\u4EEC\u4E0D\u8FC7\u4E00\u7FA4\u4E0D\u5165\u6D41\u7684\u5C0F\u540F\uFF0C\u600E\u6562\u6253\u4ED6\u7684\u4E3B\u610F\u3002\u4E8E\u662F\u60F3\u51FA\u8FD9\u6CD5\u5B50\uFF0C\u627E\u4EBA\u5192\u5145\u4F55\u6E05\u6CB3\uFF0C\u5148\u5957\u51FA\u767D\u96EA\u83B2\u7684\u8BF4\u8F9E\uFF0C\u8BBE\u6CD5\u5F25\u8865\u6F0F\u6D1E\uFF0C\u7136\u540E\u5BF9\u5979\u5927\u8086\u7528\u5211\uFF0C\u7B49\u767D\u96EA\u83B2\u5403\u8FC7\u82E6\u5934\uFF0C\u5FC3\u91CC\u5B58\u4E86\u6212\u5907\uFF0C\u4E0D\u6562\u8F7B\u6613\u5410\u9732\u5B9E\u60C5\uFF0C\u5230\u4F55\u6E05\u6CB3\u6765\u65F6\uFF0C\u597D\u7CCA\u5F04\u4E86\u4ED6\u3002</p><p>\u859B\u971C\u7075\u6B64\u65F6\u4E5F\u60F3\u901A\u4E86\uFF0C\u5FD9\u95EE\u9053\uFF1A\u300C\u4F60\u89C1\u8FC7\u4F55\u6E05\u6CB3\u5417\uFF1F\u77E5\u9053\u4ED6\u7684\u76F8\u8C8C\u5417\uFF1F\u300D</p><p>\u767D\u96EA\u83B2\u6447\u4E86\u6447\u5934\uFF0C\u300C\u6211\u82E5\u77E5\u9053\uFF0C\u4E5F\u4E0D\u4F1A\u4E2D\u4E86\u4ED6\u4EEC\u7684\u5978\u8BA1\u3002\u300D</p><p>\u300C\u90A3\u53EF\u7CDF\u4E86\uFF01\u300D</p><p>\u767D\u96EA\u83B2\u4E0D\u8BA4\u8BC6\u4F55\u6E05\u6CB3\uFF0C\u8FA8\u4E0D\u51FA\u771F\u5047\uFF0C\u8FC7\u5802\u65F6\u82E5\u662F\u4F9D\u65E7\u558A\u51A4\uFF0C\u5C11\u4E0D\u5F97\u500D\u53D7\u7B1E\u695A\uFF1B\u82E5\u662F\u4E00\u8A00\u4E0D\u53D1\uFF0C\u6B63\u5408\u4E86\u72F1\u5352\u4EEC\u7684\u5FC3\u610F\uFF0C\u82E5\u662F\u76F8\u673A\u884C\u4E8B\uFF0C\u4E07\u4E00\u9519\u771F\u4E3A\u5047\uFF0C\u5C31\u518D\u6CA1\u6709\u6D17\u51A4\u7684\u673A\u4F1A\u3002</p><p>\u859B\u971C\u7075\u60F3\u6765\u60F3\u53BB\uFF0C\u4E5F\u6CA1\u6709\u4E2A\u5BF9\u7B56\uFF0C\u82E6\u7B11\u9053\uFF1A\u300C\u8FD9\u5012\u50CF\u662F\u76D6\u7740\u76CF\u8D4C\u5927\u5C0F\uFF0C\u649E\u7740\u4E00\u8BB0\u5927\u7684\u5C31\u7B97\u8D62\u3002\u6015\u5C31\u6015\u8FDE\u7740\u53EB\u5927\uFF0C\u5F00\u51FA\u6765\u90FD\u662F\u5C0F\uFF0C\u4E00\u8BB0\u8BB0\u628A\u672C\u94B1\u90FD\u8D54\u5149\u4E86\u3002\u82E5\u662F\u53EB\u5C0F\u5462\uFF0C\u53C8\u6015\u90A3\u4E00\u76CF\u679C\u771F\u662F\u5927\uFF0C\u4E00\u8BB0\u5C31\u8F93\u5149\u4E86\u672C\u94B1\uFF0C\u518D\u6CA1\u6709\u7FFB\u8EAB\u7684\u673A\u4F1A\u3002\u300D</p><p>\u767D\u96EA\u83B2\u6C89\u9ED8\u4E86\u4E00\u4F1A\u513F\uFF0C\u8BF4\u9053\uFF1A\u300C\u4F55\u6E05\u6CB3\u5FC5\u5B9A\u662F\u8981\u6765\u7684\u3002\u300D</p><p>\u859B\u971C\u7075\u77E5\u9053\u5979\u662F\u8981\u62DA\u6B7B\u71AC\u5211\u4E86\uFF0C\u5FC3\u4E0B\u6697\u53F9\uFF0C\u5C94\u5F00\u9053\uFF1A\u300C\u4F60\u4FA7\u7740\u8EAB\u5B50\u5750\u4E00\u4F1A\u513F\u5427\uFF0C\u6211\u5E2E\u4F60\u770B\u770B\u4F24\u53E3\u3002\u300D</p><p>\u767D\u96EA\u83B2\u90A3\u65E5\u9732\u51FA\u529F\u592B\uFF0C\u4E5F\u4E0D\u518D\u7792\u5979\uFF0C\u5438\u4E86\u53E3\u6C14\uFF0C\u4E24\u624B\u4ECE\u67B7\u6D1E\u4E2D\u8131\u51FA\uFF0C\u53CD\u8FC7\u624B\u8BD5\u7740\u53BB\u591F\u67B7\u5C3E\u7684\u9500\u5B50\u3002</p><p>\u859B\u971C\u7075\u89C1\u5979\u7684\u4E3E\u52A8\uFF0C\u7ADF\u4F3C\u6709\u4E2A\u8D8A\u72F1\u7684\u60F3\u5934\uFF0C\u5FC3\u91CC\u53EB\u4E86\u58F0\uFF1A\u59D1\u5976\u5976\uFF0C\u4F60\u7EC8\u4E8E\u662F\u60F3\u901A\u4E86\uFF01\u53E3\u4E2D\u6307\u70B9\u9053\uFF1A\u300C\u518D\u5F80\u540E\u4E00\u70B9\u2026\u2026\u4E0A\u9762\u8FD8\u6709\u4E2A\u9501\u5462\u2026\u2026\u300D</p><p>\u90A3\u67B7\u957F\u8FD1\u4E03\u5C3A\uFF0C\u767D\u96EA\u83B2\u4F38\u76F4\u4E86\u624B\u81C2\u4E5F\u591F\u4E0D\u5230\u67B7\u5C3E\uFF0C\u53EA\u597D\u4F5C\u7F62\u3002\u4E0D\u8FC7\u4E24\u624B\u65E2\u7136\u677E\u5F00\uFF0C\u90A3\u67B7\u4E5F\u7565\u53EF\u8F6C\u52A8\uFF0C\u6BD4\u8D77\u521D\u65F6\u53EA\u80FD\u625B\u67B7\u5750\u7740\u7684\u82E6\u51B5\uFF0C\u4E0D\u557B\u4E8E\u5929\u58E4\u4E4B\u522B\u3002</p><p>\u5979\u6697\u6697\u9053\uFF0C\u4E0D\u7BA1\u4F55\u6E05\u6CB3\u7A76\u7ADF\u662F\u6E05\u662F\u6D4A\uFF0C\u5979\u7EC8\u7A76\u662F\u8981\u6551\u5168\u5BB6\u4EBA\u7684\u6027\u547D\u3002\u65E0\u8BBA\u5982\u4F55\uFF0C\u518D\u4E0D\u80FD\u8BA9\u6BCD\u4EB2\u3001\u59B9\u59B9\uFF0C\u8FD8\u6709\u5F1F\u5F1F\u82F1\u83B2\u53D7\u4EBA\u51CC\u8FB1\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u4E39\u5A18\u5026\u5026\u56F0\u4E86\u4E00\u65E5\uFF0C\u5230\u4E86\u4E0B\u5348\u65B9\u9192\u3002\u697C\u91CC\u9759\u6084\u6084\u4E00\u4E2A\u5BA2\u4EBA\u4E5F\u65E0\uFF0C\u5979\u8D77\u8EAB\u68B3\u6D17\u4E86\uFF0C\u63A8\u5F00\u7A97\uFF0C\u61D2\u61D2\u4F9D\u7740\uFF0C\u671B\u7740\u5F80\u7262\u72F1\u53BB\u7684\u90A3\u6761\u8DEF\uFF0C\u9759\u9759\u60F3\u7740\u5FC3\u4E8B\u3002</p><p>\u6B63\u662F\u708E\u590F\u6D53\u7EFF\u65F6\u8282\uFF0C\u6F2B\u5C71\u8349\u6728\u8471\u830F\uFF0C\u8FDE\u5C71\u52BF\u4E5F\u663E\u5F97\u4E30\u8174\u8D77\u6765\u3002\u8FD9\u795E\u4ED9\u5CAD\u867D\u7136\u4E0D\u662F\u5927\u5C71\u6781\u6DF1\u5904\uFF0C\u4F46\u5C71\u52BF\u8FDE\u7EF5\uFF0C\u4EBA\u70DF\u7A00\u5C11\u3002\u5F53\u521D\u767D\u5B5D\u5112\u643A\u7737\u5728\u6B64\u5B89\u5BB6\uFF0C\u5C31\u662F\u56E0\u4E3A\u6B64\u5730\u8FDC\u79BB\u5E02\u4E95\u55A7\u56A3\uFF0C\u65E0\u4EBA\u4E89\u6267\u3002</p><p>\u7531\u4E8E\u5730\u65B9\u504F\u50FB\uFF0C\u5F80\u6765\u7684\u5BA2\u5546\u4E0D\u591A\uFF0C\u867D\u7136\u5F00\u7740\u4E2A\u9152\u5E97\uFF0C\u7EC8\u5C81\u4E5F\u6323\u4E0D\u4E0B\u51E0\u4E2A\u94B1\u3002</p><p>\u597D\u5728\u767D\u5B5D\u5112\u548C\u4E39\u5A18\u4E5F\u65E0\u5FC3\u7ECF\u8425\uFF0C\u53EA\u662F\u5B88\u7740\u8FD9\u5E97\u4E00\u5BB6\u4EBA\u5E73\u5E73\u5B89\u5B89\u8FC7\u65E5\u5B50\u3002</p><p>\u51E0\u5E74\u4E0B\u6765\uFF0C\u8FD9\u795E\u4ED9\u5CAD\u5982\u540C\u68A6\u91CC\u6843\u6E90\uFF0C\u867D\u7136\u5C11\u4E86\u5E02\u4E95\u7E41\u534E\uFF0C\u4F46\u4E00\u65E0\u90BB\u91CC\u7EB7\u4E89\uFF0C\u4E8C\u65E0\u80E5\u540F\u6405\u6270\uFF0C\u5012\u6BD4\u5C71\u4E0B\u66F4\u4E3A\u60EC\u610F\u3002</p><p>\u4E0D\u6210\u60F3\u4E00\u68A6\u672A\u5706\uFF0C\u8FD9\u5BB6\u5DF2\u7136\u6B8B\u7834\u3002\u5FFD\u7136\u95F4\u591A\u4E86\u5EA7\u76D1\u72F1\uFF0C\u6765\u4E86\u73ED\u72F1\u5352\uFF0C\u5F77\u4F5B\u51A5\u51A5\u4E2D\u6709\u4EBA\u8F7B\u8F7B\u4E00\u70B9\u3002\u5929\u5730\u9661\u7136\u53D8\u8272\u3002</p><p>\u4E39\u5A18\u745F\u7F29\u4E86\u4E00\u4E0B\uFF0C\u8FD9\u624D\u6CE8\u610F\u5230\u6EE1\u5C71\u679D\u53F6\u6447\u66F3\uFF0C\u7A97\u6247\u5431\u5431\u8F7B\u54CD\uFF0C\u5374\u662F\u8D77\u98CE\u4E86\u3002</p><p>\u8FD9\u98CE\u6765\u52BF\u6781\u731B\uFF0C\u521A\u624D\u8FD8\u8273\u9633\u9AD8\u7167\uFF0C\u8F6C\u773C\u5C31\u98CE\u58F0\u6EE1\u8033\uFF0C\u5FFD\u5587\u5587\u704C\u5F97\u6EE1\u697C\u90FD\u662F\u3002\u7D27\u63A5\u7740\u5C71\u540E\u6D8C\u8D77\u4E00\u7247\u58A8\u9ED1\u7684\u4E4C\u4E91\uFF0C\u5F77\u4F5B\u6E14\u7FC1\u624B\u4E2D\u7684\u5927\u7F51\uFF0C\u4E00\u626C\u4FBF\u6492\u4E86\u534A\u7A7A\uFF0C\u53C8\u5982\u94C1\u9A6C\u7ADE\u6E21\uFF0C\u7FFB\u6EDA\u7740\u76F4\u6D8C\u8FC7\u6765\u3002\u65E5\u8272\u6084\u7136\u9000\u53BB\uFF0C\u98CE\u91CC\u5E26\u6765\u4E1D\u4E1D\u51C9\u610F\u3002</p><p>\u4E39\u5A18\u53C8\u671D\u90A3\u8DEF\u770B\u4E86\u4E00\u773C\uFF0C\u5E7D\u5E7D\u53F9\u4E86\u53E3\u6C14\uFF0C\u6162\u6162\u5173\u4E86\u7A97\u6237\u3002</p><p>\u5B59\u5929\u7FBD\u4E5F\u88AB\u90A3\u98CE\u5439\u9192\u3002\u4ED6\u6628\u665A\u5FD9\u4E86\u534A\u5BBF\u672A\u66FE\u5408\u773C\uFF0C\u5348\u95F4\u4E58\u5174\u5E72\u8FC7\u7389\u5A18\uFF0C\u5026\u610F\u6D8C\u6765\uFF0C\u5C31\u8EBA\u5728\u6811\u836B\u4E0B\u7761\u4E86\u3002\u8FD9\u98CE\u5439\u5F97\u6797\u6728\u76F4\u6447\uFF0C\u8349\u6728\u5043\u4F0F\uFF0C\u4ED6\u7EC3\u8FC7\u529F\u7684\uFF0C\u8033\u76EE\u7075\u4FBF\uFF0C\u5F53\u5373\u4FBF\u9192\u4E86\u3002</p><p>\u4E00\u7741\u773C\uFF0C\u4FBF\u770B\u5230\u4E00\u6839\u5E26\u7740\u53F6\u7247\u7684\u6768\u6811\u679D\u3002\u7389\u5A18\u80CC\u5BF9\u7740\u5B59\u5929\u7FBD\u8DEA\u5728\u5730\u4E0A\uFF0C\u5979\u8863\u886B\u978B\u889C\u90FD\u88AB\u5B59\u5929\u7FBD\u6254\u4E86\u4E2A\u5E72\u51C0\uFF0C\u4ECD\u8D64\u7740\u8EAB\u5B50\u3002\u90A3\u53EA\u767D\u5AE9\u5AE9\u7684\u7C89\u81C0\u4E3E\u5F97\u9AD8\u9AD8\u7684\uFF0C\u4E00\u6446\u4E00\u6446\u8F7B\u8F7B\u626D\u7740\u3002\u90A3\u6839\u624B\u6307\u7684\u6811\u679D\u5C31\u63D2\u5728\u5979\u5C41\u773C\u513F\u91CC\uFF0C\u968F\u7740\u5979\u5C41\u80A1\u7684\u6446\u52A8\uFF0C\u5728\u5B59\u5929\u7FBD\u8EAB\u524D\u6447\u6765\u6447\u53BB\u3002</p><p>\u5C71\u91CC\u6BCF\u591A\u868A\u866B\uFF0C\u6270\u4EBA\u7761\u7720\u3002\u5B59\u5929\u7FBD\u7761\u524D\u4FBF\u6298\u4E86\u6839\u5E7C\u679D\uFF0C\u8BA9\u7389\u5A18\u63D2\u5728\u5C41\u773C\u513F\u91CC\u7ED9\u4ED6\u9A71\u8D76\u868A\u866B\u3002\u7389\u5A18\u600E\u6562\u4E0D\u4ECE\uFF0C\u81EA\u7136\u4E56\u4E56\u63D2\u4E86\uFF0C\u6447\u7740\u5C41\u80A1\u5C3D\u5FC3\u670D\u4F8D\u3002\u5B59\u5929\u7FBD\u4E00\u89C9\u7761\u5F97\u719F\u751C\uFF0C\u7389\u5A18\u65B0\u5F00\u82DE\u7684\u5C41\u773C\u513F\u5374\u6CA1\u6709\u7247\u523B\u5B89\u6B47\uFF0C\u8FD9\u4F1A\u513F\u809B\u6D1E\u4E0A\u7EA2\u7EA2\u7684\uFF0C\u5C3D\u662F\u8840\u8FF9\u3002</p><p>\u5B59\u5929\u7FBD\u597D\u6574\u4EE5\u6687\u5730\u770B\u4E86\u770B\u5929\u9645\u3002\u90A3\u4E4C\u4E91\u5DF2\u7ECF\u6D8C\u5230\u5934\u9876\uFF0C\u906E\u4F4F\u4E86\u9633\u5149\uFF0C\u4E4C\u4E91\u8FB9\u7F18\u7ED2\u6BDB\u822C\u7BF7\u677E\uFF0C\u88AB\u9633\u5149\u4E00\u6620\uFF0C\u5F77\u4F5B\u9576\u4E86\u4E00\u6761\u91D1\u707F\u707F\u7684\u8FB9\u9970\u3002</p><p>\u4E4C\u4E91\u8D8A\u6765\u8D8A\u539A\uFF0C\u6797\u4E2D\u5149\u7EBF\u8FC5\u901F\u9EEF\u6DE1\u4E0B\u6765\u3002\u5B59\u5929\u7FBD\u6253\u91CF\u7740\u5583\u5583\u9053\uFF1A\u300C\u597D\u5927\u7684\u4E00\u573A\u96E8\u3002\u300D</p><p>\u4ED6\u624B\u4E00\u6491\uFF0C\u8DF3\u8D77\u8EAB\u6765\uFF0C\u7A7F\u4E86\u8863\u670D\uFF0C\u89C1\u7389\u5A18\u4ECD\u7FD8\u7740\u4E00\u53EA\u5149\u6E9C\u6E9C\u7684\u5927\u5C41\u80A1\u4E0D\u6562\u4E71\u52A8\uFF0C\u7B11\u9053\uFF1A\u300C\u8BF4\u6765\u4F60\u662F\u56DA\u72AF\uFF0C\u79C1\u7EB5\u4E0D\u5F97\uFF0C\u8FD8\u628A\u4F60\u7ED1\u5728\u6811\u4E0A\u7F62\u4E86\u3002\u300D</p><p>\u7389\u5A18\u770B\u4E86\u773C\u5929\u8272\uFF0C\u4E5E\u6C42\u9053\uFF1A\u300C\u8D31\u59BE\u6DCB\u4E0A\u4E00\u591C\uFF0C\u5FC5\u8981\u6B7B\u7684\u3002\u5927\u54E5\uFF0C\u6C42\u4F60\u4E0D\u62D8\u54EA\u91CC\uFF0C\u7ED9\u8D31\u59BE\u627E\u4E2A\u907F\u96E8\u7684\u5730\u65B9\uFF0C\u5C31\u662F\u5927\u54E5\u7684\u6148\u60B2\u4E86\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u9053\uFF1A\u300C\u8DDF\u6211\u6765\u5427\u3002\u300D\u8BF4\u7740\u5F53\u5148\u5C31\u8D70\u3002</p><p>\u7389\u5A18\u5728\u540E\u9762\u5524\u9053\uFF1A\u300C\u5B98\u5DEE\u5927\u54E5\u4F53\u8C05\uFF0C\u8D31\u59BE\u8D70\u4E0D\u5F97\u8DEF\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u8FDC\u8FDC\u9053\uFF1A\u300C\u54EA\u4E2A\u8BA9\u4F60\u8D70\u4E86\uFF1F\u722C\u8FC7\u6765\u5427\u3002\u300D</p><p>\u7389\u5A18\u53EA\u597D\u624B\u811A\u5E76\u7528\u722C\u4E86\u8FC7\u53BB\u3002\u5979\u6811\u679D\u4E5F\u4E0D\u6562\u62D4\uFF0C\u4ECD\u65E7\u6485\u7740\u5C41\u80A1\uFF0C\u5C41\u773C\u513F\u91CC\u63D2\u7740\u6811\u679D\uFF0C\u4E00\u6447\u4E00\u6447\u722C\u5728\u5B59\u5929\u7FBD\u8EAB\u540E\u3002</p><p>\u7389\u5A18\u6765\u65F6\u4E58\u7684\u9AA1\u8F66\u4ECD\u6254\u5728\u539F\u5730\uFF0C\u90A3\u5339\u513F\u9AA1\u6813\u5728\u6811\u65C1\u3002\u5B59\u5929\u7FBD\u6380\u5F00\u8F66\u5E18\uFF0C\u8BA9\u7389\u5A18\u722C\u8FDB\u53BB\uFF0C\u62FF\u51FA\u7EF3\u5B50\u8981\u7ED1\u3002\u7389\u5A18\u5A49\u8F6C\u54C0\u6C42\u9053\uFF1A\u300C\u6C42\u5927\u54E5\u514D\u4E86\u8D31\u59BE\u7684\u7ED1\u5427\u3002\u8D31\u59BE\u6CA1\u4E86\u978B\u5B50\uFF0C\u5BF8\u6B65\u4E5F\u8D70\u4E0D\u5F97\u3002\u51B5\u4E14\u8D31\u59BE\u4E00\u6574\u65E5\u6CA1\u5403\u4E1C\u897F\u4E86\uFF0C\u5B98\u5DEE\u5927\u54E5\u514D\u4E86\u59BE\u8EAB\u7684\u7ED1\uFF0C\u8D31\u59BE\u628A\u8EAB\u5B50\u4ED4\u7EC6\u6574\u7406\u4E00\u756A\uFF0C\u8BA9\u5927\u54E5\u80FD\u73A9\u5F97\u9AD8\u5174\uFF0C\u597D\u4E48\uFF1F\u300D</p><p>\u8BF4\u7740\uFF0C\u534A\u7A7A\u4E2D\u4E00\u4E2A\u70B8\u96F7\uFF0C\u5927\u96E8\u74E2\u6CFC\u822C\u4E0B\u4E86\u8D77\u6765\u3002\u90A3\u8F66\u867D\u5C0F\uFF0C\u91CC\u9762\u88AB\u8925\u7AF9\u5E2D\u5C3D\u6709\uFF0C\u7389\u5A18\u88AB\u96F7\u5413\u5F97\u8737\u6210\u4E00\u56E2\uFF0C\u62B1\u7740\u88AB\u8925\u745F\u745F\u53D1\u6296\u3002</p><p>\u5B59\u5929\u7FBD\u89C1\u96E8\u4E0B\u5F97\u5927\u4E86\uFF0C\u4FBF\u6536\u4E86\u7EF3\u5B50\uFF0C\u7B11\u9053\uFF1A\u300C\u8FD9\u4E48\u542C\u8BDD\u7684\u4FCF\u4EBA\u513F\uFF0C\u6211\u600E\u4E48\u4F1A\u7ED1\u5462\uFF1F\u8F66\u4E0A\u6709\u5E72\u7CAE\u4E48\uFF1F\u300D</p><p>\u7389\u5A18\u8FDE\u5FD9\u70B9\u5934\u3002</p><p>\u96E8\u8D8A\u4E0B\u8D8A\u6025\uFF0C\u5B59\u5929\u7FBD\u4E0D\u6562\u591A\u5F85\uFF0C\u300C\u90A3\u6211\u53BB\u4E86\u3002\u300D</p><p>\u300C\u5927\u54E5\u2026\u2026\u300D\u7389\u5A18\u5C0F\u58F0\u9053\uFF1A\u300C\u6211\u6015\u2026\u2026\u300D</p><p>\u5927\u96E8\u503E\u76C6\uFF0C\u8F66\u91CC\u8F66\u5916\u4E00\u7247\u6F06\u9ED1\uFF0C\u5C0F\u5C0F\u7684\u9AA1\u8F66\u5F77\u4F5B\u5DE8\u6D6A\u4E2D\u4E00\u53F6\u5C0F\u821F\uFF0C\u8981\u98CE\u96E8\u4E2D\u98D8\u6447\uFF0C\u96BE\u602A\u5979\u4F1A\u5BB3\u6015\u3002\u5B59\u5929\u7FBD\u62D4\u4E86\u5979\u809B\u4E2D\u7684\u6811\u679D\uFF0C\u6296\u5F00\u88AB\u5B50\uFF0C\u5C06\u5979\u88F9\u4F4F\uFF0C\u8BF4\u9053\uFF1A\u300C\u4F60\u4E14\u7761\u4E00\u89C9\u3002\u82E5\u6015\u4E86\uFF0C\u5C31\u5FF5\u83E9\u8428\u5427\u3002\u300D</p><p>\u96F7\u58F0\u4E00\u4E2A\u63A5\u4E00\u4E2A\u54CD\u8D77\uFF0C\u95EA\u7535\u6620\u5F97\u5C71\u6797\u72B9\u5982\u9B3C\u57DF\u3002\u4E16\u4EBA\u5E38\u8BF4\u8FD9\u96F7\u662F\u8001\u5929\u7237\u7528\u6765\u51FB\u6740\u8D1F\u5FC3\u4EBA\u7684\uFF0C\u5B59\u5929\u7FBD\u505A\u4E86\u65E0\u6570\u4E8F\u5FC3\u4E8B\uFF0C\u5374\u6CA1\u6709\u4E1D\u6BEB\u602F\u610F\u3002\u4ED6\u672C\u6765\u60F3\u53BB\u674F\u82B1\u6751\uFF0C\u89C1\u89C1\u4E39\u5A18\u548C\u7389\u83B2\uFF0C\u665A\u4E0A\u5C31\u5728\u5E97\u91CC\u5BBF\u4E86\u3002</p><p>\u773C\u89C1\u8FD9\u96F7\u6253\u5F97\u5389\u5BB3\uFF0C\u4E00\u4E2A\u4E2A\u5F77\u4F5B\u5C31\u5728\u811A\u524D\u70B8\u5F00\uFF0C\u5C31\u50CF\u662F\u4E00\u8DEF\u8DDF\u7740\u4ED6\u4E00\u822C\uFF0C\u5B59\u5929\u7FBD\u72B9\u7591\u4E86\u4E00\u4E0B\uFF0C\u6389\u5934\u671D\u8FDC\u5904\u7684\u8C7A\u72FC\u5761\u5954\u53BB\u3002</p>',95);function t(n,_){return r}var o=e(a,[["render",t]]);export{o as default};
