import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_43-\u9001\u72F1" tabindex="-1"><a class="header-anchor" href="#_43-\u9001\u72F1" aria-hidden="true">#</a> 43 \u9001\u72F1</h1><p>\u5353\u5929\u96C4\u5E76\u6CA1\u6709\u8BF4\u611F\u6069\u7684\u8BDD\uFF0C\u751A\u81F3\u7528\u51FA\u624B\u76F8\u8BD5\u8BF4\u660E\u4ED6\u4E0D\u662F\u4E00\u4E2A\u611F\u6069\u7684\u4EBA\u3002\u8FD9\u53CD\u800C\u8BA9\u5B59\u5929\u7FBD\u653E\u4E86\u5FC3\u3002\u4ED6\u867D\u7136\u4E0D\u5230\u800C\u7ACB\u4E4B\u5E74\uFF0C\u89C1\u8FC7\u7684\u4E8B\u5374\u6BD4\u5BFB\u5E38\u4EBA\u4E00\u751F\u90FD\u591A\uFF0C\u4E16\u4E0A\u6700\u9760\u4E0D\u4F4F\u7684\u83AB\u8FC7\u4E8E\u6069\u60C5\uFF0C\u8FD8\u6709\u628A\u6069\u60C5\u6302\u5728\u53E3\u8FB9\u7684\u4EBA\u3002</p><p>\u5353\u5929\u96C4\u6311\u660E\u4E86\u5148\u8BD5\u4ED6\u7684\u65A4\u4E24\uFF0C\u5982\u679C\u5B59\u5929\u7FBD\u65A4\u4E24\u4E0D\u591F\uFF0C\u5C31\u662F\u6709\u5929\u5927\u7684\u6069\u60C5\u90A3\u4E5F\u4E00\u7B14\u52FE\u9500\u3002</p><p>\u5728\u8FD9\u8C7A\u72FC\u5761\uFF0C\u5353\u5929\u96C4\u7B97\u662F\u6761\u91CE\u5FC3\u52C3\u52C3\u7684\u6C49\u5B50\uFF0C\u53EA\u56E0\u72AF\u4E86\u519B\u7EAA\u6CA6\u4E3A\u72F1\u5352\uFF0C\u6CA1\u6709\u673A\u7F18\u4E5F\u5C31\u7F62\u4E86\uFF0C\u4E00\u65E6\u5BFB\u5230\u65F6\u673A\uFF0C\u5FC5\u4E0D\u4F1A\u7518\u4E8E\u8001\u6B7B\u72F1\u4E2D\u3002\u73B0\u5728\uFF0C\u4ED6\u4F1A\u662F\u5B59\u5929\u7FBD\u6700\u9760\u5F97\u4F4F\u7684\u5E2E\u624B\u3002</p><p>\u4E0D\u8FC7\u8BF4\u5230\u56FE\u8C0B\uFF0C\u5353\u5929\u96C4\u672A\u514D\u592A\u9AD8\u770B\u4E86\u4ED6\u3002\u5B59\u5929\u7FBD\u8BF4\u7684\u4FDD\u547D\u4E5F\u5E76\u975E\u63A8\u6258\u3002\u5982\u4ECA\u6700\u8981\u7D27\u7684\u83AB\u8FC7\u4E8E\u767D\u5B5D\u5112\u7684\u9006\u6848\uFF0C\u767D\u96EA\u83B2\u6027\u5B50\u575A\u6BC5\uFF0C\u90A3\u4E2A\u9C7C\u76EE\u6DF7\u73E0\u7684\u4F0E\u4FE9\u5BF9\u4ED8\u522B\u4EBA\u72B9\u53EF\uFF0C\u5BF9\u4ED8\u767D\u96EA\u83B2\u6BEB\u65E0\u7528\u5904\u3002\u4E00\u65E6\u7FFB\u6848\uFF0C\u4ED6\u4EEC\u8EAB\u4E3A\u72F1\u5352\uFF0C\u7F6A\u52A0\u4E00\u7B49\uFF0C\u4E00\u4E2A\u4E2A\u5C11\u4E0D\u4E86\u8981\u4EBA\u5934\u843D\u5730\u3002</p><p>\u82E5\u60F3\u5750\u5B9E\u6B64\u6848\uFF0C\u4F55\u6E05\u6CB3\u8FD9\u4E00\u5173\u5FC5\u5B9A\u8981\u8FC7\u3002\u9C7C\u76EE\u6DF7\u73E0\u4E0D\u6210\uFF0C\u53EA\u6709\u501F\u5200\u6740\u4EBA\u3002\u6B64\u8BA1\u4ED6\u8DDF\u5218\u8FA8\u673A\u5546\u8BAE\u591A\u65F6\uFF0C\u5982\u679C\u8BF4\u5F53\u521D\u8FD8\u6709\u534A\u5206\u628A\u63E1\uFF0C\u73B0\u5728\u8FDE\u534A\u5206\u4E5F\u6CA1\u6709\u4E86\u3002\u4F46\u75C5\u6025\u4E71\u6295\u533B\uFF0C\u62FC\u4E0A\u4E00\u8BD5\u603B\u597D\u8FC7\u675F\u624B\u5C31\u64D2\u3002</p><p>\u518D\u6709\u4E8C\u5341\u5929\uFF0C\u4F55\u6E05\u6CB3\u4FBF\u5230\u72F1\u4E2D\uFF0C\u4ED6\u73B0\u5728\u5C31\u5E94\u8BE5\u79BB\u5C71\u4E00\u884C\u3002\u4F46\u8FD8\u6709\u6869\u4E8B\u8981\u5148\u7ED3\u4E86\uFF0C\u624D\u80FD\u653E\u4E0B\u5FC3\u6765\u3002</p><p>\u5B59\u5929\u7FBD\u6EE1\u5FC3\u6B32\u706B\u6B64\u65F6\u90FD\u6D88\u892A\u5F97\u4E00\u4E7E\u4E8C\u51C0\uFF0C\u4ED6\u653E\u5F00\u674F\u82B1\u6751\uFF0C\u534A\u8DEF\u8F6C\u5165\u5C71\u6797\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u767D\u96EA\u83B2\u88AB\u9001\u56DE\u6765\u65F6\u5DF2\u7ECF\u660F\u8FF7\u591A\u65F6\uFF0C\u4F46\u8EAB\u4F53\u4ECD\u4E0D\u65F6\u62BD\u52A8\u3002\u5979\u81C0\u4E0A\u539F\u672C\u5E26\u4F24\uFF0C\u6B64\u523B\u8DB4\u5728\u5730\u4E0A\uFF0C\u4E24\u534A\u5C41\u80A1\u65E0\u6CD5\u5408\u62E2\u5730\u5411\u5916\u5F20\u5F00\uFF0C\u9732\u51FA\u4E2D\u95F4\u4E00\u4E2A\u8840\u6DCB\u6DCB\u7684\u5706\u5B54\u3002\u90A3\u53EA\u67D4\u5AE9\u7684\u5C41\u773C\u513F\u88AB\u63D2\u5F97\u770B\u4E0D\u51FA\u4E1D\u6BEB\u75D5\u8FF9\uFF0C\u5931\u53BB\u9ECF\u819C\u7684\u809B\u6D1E\u50CF\u662F\u88AB\u4EBA\u525C\u8FC7\uFF0C\u88F8\u9732\u51FA\u5185\u90E8\u7684\u7EA2\u8089\uFF0C\u4E0A\u9762\u8840\u8FF9\u5DF2\u7ECF\u5E72\u6DB8\u91CC\u9762\u4F9D\u7A00\u80FD\u770B\u5230\u4E00\u4E9B\u51DD\u56FA\u7684\u9897\u7C92\u3002</p><p>\u4F55\u6C42\u56FD\u9053\uFF1A\u300C\u767D\u5A4A\u5B50\u597D\u4E00\u6761\u80A5\u80A0\uFF0C\u88AB\u54B1\u4EEC\u62FF\u9E21\u5DF4\u63CE\u5F97\u6EE1\u6EE1\u7684\uFF0C\u8FD8\u7528\u76D0\u814C\u8FC7\u2026\u2026\u300D</p><p>\u859B\u971C\u7075\u5A9A\u7B11\u9053\uFF1A\u300C\u5927\u7237\u539F\u6765\u662F\u505A\u8089\u80A0\u7684\u3002\u4E0D\u8FC7\u4EBA\u5BB6\u90FD\u662F\u5148\u53D6\u4E86\u80A0\u5B50\u518D\u505A\uFF0C\u5927\u7237\u662F\u5C31\u7740\u767D\u5A4A\u5B50\u7684\u5C41\u773C\u513F\u505A\u4E86\u3002\u300D</p><p>\u4F55\u6C42\u56FD\u54C8\u54C8\u5927\u7B11\u3002\u859B\u971C\u7075\u9119\u5937\u5730\u8E22\u4E86\u767D\u96EA\u83B2\u4E00\u811A\uFF0C\u300C\u4F60\u4E5F\u6709\u4ECA\u65E5\u554A\uFF0C\u767D\u5927\u6355\u5FEB\u3002\u300D</p><p>\u4F55\u6C42\u56FD\u9053\uFF1A\u300C\u8981\u4E0D\u662F\u8FD9\u5A4A\u5B50\uFF0C\u4F60\u600E\u4E48\u4F1A\u843D\u72F1\uFF1F\u518D\u8FC7\u51E0\u5929\u4F55\u5927\u4EBA\u6765\u72F1\u91CC\uFF0C\u4F60\u53EA\u8981\u54AC\u5F97\u6B7B\u6B7B\u7684\uFF0C\u5C31\u591F\u4F60\u51FA\u6C14\u4E86\u3002\u300D</p><p>\u300C\u53EF\u4E0D\u662F\u561B\u3002\u300D</p><p>\u4F55\u6C42\u56FD\u4FEF\u8FC7\u8EAB\u6765\uFF0C\u6084\u58F0\u9053\uFF1A\u300C\u53EA\u8981\u4F60\u54AC\u5B9A\u8FD9\u5A4A\u5B50\uFF0C\u6211\u4EEC\u5144\u5F1F\u8054\u540D\u7ED9\u4F60\u4F5C\u4FDD\uFF0C\u8BA9\u4E0A\u5CF0\u9976\u4E86\u4F60\u6027\u547D\u3002\u8FDE\u4E0A\u6B21\u8D8A\u72F1\u7684\u4E8B\u4E5F\u90FD\u66FF\u4F60\u7792\u8FC7\u4E86\uFF0C\u5230\u65F6\u5224\u4E0B\u6765\uFF0C\u5728\u72F1\u91CC\u5750\u4E0A\u534A\u5E74\uFF0C\u4E8B\u60C5\u4E00\u51B7\uFF0C\u5C31\u653E\u4F60\u51FA\u53BB\u2014\u2014\u77E5\u9053\u4E86\u5417\uFF1F\u300D</p><p>\u859B\u971C\u7075\u63C9\u7740\u811A\u8E1D\u7B11\u9053\uFF1A\u300C\u90A3\u53EF\u591A\u8C22\u60A8\u4E86\u3002\u300D</p><p>\u4F55\u6C42\u56FD\u6EE1\u8138\u9EBB\u5B50\u90FD\u7B11\u6210\u5F2F\u7684\u4E86\uFF0C\u5FC3\u6EE1\u610F\u8DB3\u5730\u53BB\u4E86\u3002\u859B\u971C\u7075\u8138\u4E0A\u7684\u5A9A\u7B11\u6E10\u6E10\u51B7\u5374\uFF0C\u5979\u6478\u4F4F\u767D\u96EA\u83B2\u7684\u540E\u5EAD\u8F7B\u8F7B\u4E00\u6309\uFF0C\u987F\u65F6\u5413\u4E86\u4E00\u8DF3\u3002</p><p>\u90A3\u809B\u4E2D\u8840\u8089\u90FD\u5DF2\u7ECF\u5E72\u4E86\uFF0C\u786C\u786C\u7684\u72B9\u5982\u7ED3\u4E86\u5C42\u76D0\u58F3\u3002\u5979\u60F3\u4E86\u60F3\uFF0C\u53EA\u597D\u5C06\u6BDB\u5DFE\u6D78\u5F97\u6E7F\u900F\uFF0C\u8986\u5728\u767D\u96EA\u83B2\u81C0\u95F4\uFF0C\u8BA9\u4F24\u53E3\u8F6F\u5316\u3002\u90A3\u5E2E\u72F1\u5352\u597D\u6BD2\u8FA3\u7684\u624B\u6BB5\uFF0C\u8FD9\u4E00\u756A\u6298\u78E8\uFF0C\u767D\u96EA\u83B2\u7684\u540E\u5EAD\u591A\u534A\u8981\u88AB\u6BC1\u4E86\u3002\u5C31\u662F\u52C9\u5F3A\u6108\u5408\uFF0C\u8BF4\u4E0D\u5B9A\u4E5F\u8981\u6210\u4E86\u809B\u7618\u3002</p><p>\u767D\u96EA\u83B2\u7684\u8EAB\u5B50\u4E00\u52A8\uFF0C\u773C\u775B\u7741\u5F00\u4E00\u7EBF\u3002\u859B\u971C\u7075\u62E8\u5F00\u5979\u8138\u4E0A\u7684\u53D1\u4E1D\uFF0C\u770B\u7740\u5979\u60E8\u767D\u7684\u9762\u5B54\u53F9\u9053\uFF1A\u300C\u6211\u82E5\u662F\u7537\u4EBA\uFF0C\u5C31\u628A\u4F60\u62FF\u56DE\u5BB6\u53BB\uFF0C\u5F53\u5B9D\u8D1D\u4F9B\u7740\u3002\u600E\u4E48\u80FD\u8BA9\u4F60\u53D7\u8FD9\u79CD\u82E6\u3002\u300D</p><p>\u767D\u96EA\u83B2\u54B3\u55FD\u7247\u523B\uFF0C\u5FFD\u7136\u4F38\u76F4\u5589\u5499\uFF0C\u5410\u51FA\u4E00\u6EE9\u6D53\u7CBE\u3002</p><p>\u859B\u971C\u7075\u5FCD\u4E0D\u4F4F\u9053\uFF1A\u300C\u90A3\u4E9B\u72F1\u5352\u90FD\u4E0D\u662F\u4EBA\uFF01\u4F60\u518D\u71AC\u4E0B\u53BB\uFF0C\u89C1\u4E0D\u7740\u4F55\u6E05\u6CB3\uFF0C\u5148\u5C31\u8BA9\u4ED6\u4EEC\u5F04\u6B7B\u4E86\uFF01\u300D</p><p>\u767D\u96EA\u83B2\u54AC\u7740\u7259\u5438\u4E86\u53E3\u6C14\uFF0C\u7136\u540E\u6162\u6162\u9053\uFF1A\u300C\u4F60\u8FD9\u4E9B\u5929\u5728\u5916\u9762\u89C1\u7740\u82F1\u83B2\u4E86\u5417\uFF1F\u300D</p><p>\u859B\u971C\u7075\u607C\u9053\uFF1A\u300C\u4F60\u957F\u5F97\u597D\uFF0C\u6027\u5B50\u53C8\u5014\uFF0C\u4ED6\u4EEC\u90FD\u5728\u5E72\u4F60\u5462\uFF0C\u7528\u4E0D\u7740\u6211\u53BB\u4F3A\u5019\uFF01</p><p>\u6211\u600E\u4E48\u77E5\u9053\uFF1F\u300D\u5979\u8D4C\u6C14\u8BF4\u5B8C\uFF0C\u53C8\u53F9\u4E86\u53E3\u6C14\uFF0C\u300C\u4F60\u653E\u5FC3\u5427\uFF0C\u7537\u4EBA\u4E5F\u4E0D\u662F\u94C1\u6253\u7684\uFF0C\u8FD9\u6837\u5F04\u8FC7\u4E5F\u5C3D\u591F\u4E86\uFF0C\u518D\u4E0D\u4F1A\u627E\u82F1\u83B2\u3002\u6709\u8FD9\u5FC3\u601D\uFF0C\u8FD8\u662F\u591A\u60F3\u60F3\u4F60\u81EA\u5DF1\u5427\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5927\u96E8\u4E0B\u4E86\u4E00\u591C\uFF0C\u6EE1\u5730\u9752\u8349\u66F4\u663E\u6D53\u7EFF\uFF0C\u4E00\u7247\u7247\u78A7\u8272\u53C2\u5DEE\uFF0C\u6668\u66E6\u4E0B\u6EE1\u76EE\u7686\u65B0\uFF0C\u8D70\u5728\u5176\u4E2D\u4EE4\u4EBA\u795E\u6E05\u6C14\u723D\u3002\u5B59\u5929\u7FBD\u5FC3\u8840\u6765\u6F6E\uFF0C\u628A\u624B\u6307\u653E\u5728\u53E3\u4E2D\uFF0C\u6253\u4E86\u4E2A\u5FFD\u54E8\uFF0C\u58F0\u97F3\u8FDC\u8FDC\u4F20\u51FA\uFF0C\u5C71\u8C37\u7686\u5E94\u3002</p><p>\u5230\u4E86\u6797\u95F4\uFF0C\u5927\u8F66\u4ECD\u505C\u5728\u539F\u5904\uFF0C\u90A3\u5934\u513F\u9AA1\u542C\u5230\u4EBA\u58F0\uFF0C\u6253\u4E86\u4E2A\u54CD\u9F3B\uFF0C\u8179\u4E0B\u4E00\u6839\u9ED1\u9EDD\u9EDD\u7684\u9AA1\u97AD\u76F4\u633A\u633A\u4F38\u7740\uFF0C\u50CF\u68D2\u69CC\u4E00\u6837\u6572\u7740\u809A\u76AE\uFF0C\u556A\u556A\u4F5C\u54CD\u3002\u8BA9\u5B59\u5929\u7FBD\u7981\u4E0D\u4F4F\u7B11\u4E86\u8D77\u6765\u3002</p><p>\u4ED6\u62AC\u624B\u6380\u5F00\u8F66\u5E18\uFF0C\u53EA\u89C1\u8F66\u91CC\u6574\u9F50\u94FA\u7740\u88AB\u8925\uFF0C\u7389\u5A18\u5E76\u819D\u8DEA\u7740\u53E9\u4E86\u5934\uFF0C\u8BF4\u9053\uFF1A\u300C\u5B98\u5DEE\u5927\u54E5\uFF0C\u597D\u65E9\u3002\u300D</p><p>\u8F66\u91CC\u653E\u7740\u4E00\u53EA\u534A\u5F00\u7684\u68B3\u5986\u5323\u5B50\uFF0C\u7389\u5A18\u68B3\u4E86\u5934\uFF0C\u633D\u4E86\u9AFB\uFF0C\u9B13\u89D2\u4ED4\u7EC6\u52FE\u8FC7\uFF0C\u62BF\u5F97\u5200\u88C1\u822C\u9F50\u6574\u3002\u5979\u8138\u4E0A\u5300\u4E86\u7C89\uFF0C\u5F2F\u7709\u7F8E\u76EE\u4FEE\u9970\u4E00\u65B0\uFF0C\u5507\u4E0A\u7EC6\u7EC6\u6D82\u4E86\u80ED\u8102\uFF0C\u886C\u7740\u96EA\u767D\u7684\u8EAB\u5B50\u66F4\u663E\u5F97\u53E3\u8102\u751F\u9999\uFF0C\u5A07\u8273\u5982\u82B1\uFF0C\u4E4D\u770B\u6765\u7ADF\u6BD4\u4E39\u5A18\u8FD8\u8981\u4FCF\u4E0A\u51E0\u5206\u3002</p><p>\u5B59\u5929\u7FBD\u5FC3\u5934\u4E00\u9635\u604D\u60DA\uFF0C\u4ED6\u539F\u4EE5\u4E3A\u7389\u5A18\u649E\u4E0A\u8FD9\u6837\u7684\u6848\u5B50\uFF0C\u62C5\u60CA\u53D7\u6015\u4E4B\u4F59\uFF0C\u53C8\u88AB\u4ED6\u5F04\u4F24\u540E\u5EAD\uFF0C\u5F88\u5403\u4E86\u4E9B\u82E6\u695A\uFF0C\u514D\u4E0D\u4E86\u5F62\u5BB9\u6194\u60B4\uFF0C\u989C\u8272\u51CF\u635F\uFF0C\u6CA1\u60F3\u5230\u7ADF\u626E\u5F97\u8FD9\u822C\u9999\u8273\u3002\u8BBA\u5802\u4E0A\u7684\u7AEF\u5E84\uFF0C\u5E8A\u4E0A\u7684\u67D4\u5A9A\u5979\u4E5F\u8BB8\u53CA\u4E0D\u4E0A\u4E39\u5A18\uFF0C\u4F46\u90A3\u79CD\u98CE\u6D41\u5A49\u8F6C\u7684\u8273\u6001\u5374\u80DC\u8FC7\u4E86\u4E39\u5A18\u3002</p><p>\u7389\u5A18\u5FC3\u5934\u5FD0\u5FD1\uFF0C\u5979\u8F66\u91CC\u672C\u6765\u8FD8\u5907\u7740\u51E0\u5957\u6362\u6D17\u7684\u8863\u670D\u3001\u978B\u5B50\uFF0C\u91CC\u5916\u7686\u6709\uFF0C\u4F46\u4E0D\u77E5\u9053\u60E7\u4E8E\u5B98\u5DEE\u7684\u6DEB\u5A01\uFF0C\u8FD8\u662F\u6015\u4E86\u5C71\u4E2D\u65E0\u8DEF\uFF0C\u5979\u7ADF\u6CA1\u6709\u5174\u8D77\u534A\u70B9\u9003\u8D70\u7684\u5FF5\u5934\u3002</p><p>\u6628\u665A\u6253\u96F7\uFF0C\u5979\u85CF\u5728\u88AB\u4E0B\u54C6\u55E6\u4E86\u4E00\u591C\uFF0C\u4E0D\u77E5\u4F55\u65F6\u624D\u7761\u7740\u3002</p><p>\u6E05\u6668\u96E8\u4F4F\uFF0C\u5979\u4E5F\u9192\u4E86\u3002\u60F3\u5230\u81EA\u5DF1\u5149\u7740\u8EAB\u4F53\uFF0C\u7389\u5A18\u4E5F\u89C9\u7F9E\u803B\uFF0C\u4F46\u62FF\u51FA\u8863\u670D\u53C8\u72B9\u8C6B\u7740\u4E0D\u6562\u6253\u5F00\u3002\u714E\u71AC\u826F\u4E45\uFF0C\u6700\u540E\u53EA\u62FF\u51FA\u68B3\u5986\u5323\uFF0C\u7528\u5FC3\u68B3\u5986\u6253\u626E\uFF0C\u8FDE\u4EF6\u5C0F\u8863\u4E5F\u6CA1\u6562\u7A7F\uFF0C\u5C31\u90A3\u4E48\u4E00\u4E1D\u4E0D\u6302\u5730\u5728\u8F66\u91CC\u5019\u7740\uFF0C\u7B49\u90A3\u5B98\u5DEE\u5230\u6765\u3002</p><p>\u89C1\u5B59\u5929\u7FBD\u53D1\u6014\uFF0C\u7389\u5A18\u5AE3\u7136\u4E00\u7B11\uFF0C\u500D\u663E\u59A9\u5A9A\u3002\u5979\u5A07\u58F0\u7EC6\u6C14\u5730\u8BF4\uFF1A\u300C\u5927\u54E5\uFF0C\u8981\u4E0D\u8981\u770B\u59BE\u8EAB\u88AB\u5F00\u82DE\u7684\u540E\u5EAD\u82B1\uFF1F\u300D</p><p>\u5B59\u5929\u7FBD\u6162\u6162\u5730\u5B9A\u4E0B\u5FC3\u795E\u3002\u4E39\u5A18\u5DF2\u7ECF\u662F\u96BE\u5F97\u7684\u5C24\u7269\uFF0C\u5979\u8FD9\u5AE1\u4EB2\u59B9\u5B50\u5374\u662F\u5929\u751F\u5A9A\u9AA8\uFF0C\u5929\u751F\u5C31\u77E5\u9053\u600E\u6837\u53D6\u60A6\u7537\u4EBA\u3002\u8FD9\u5012\u7701\u4E86\u4ED6\u4E0D\u5C11\u5DE5\u592B\u3002</p><p>\u5B59\u5929\u7FBD\u53D6\u51FA\u8170\u95F4\u62FF\u4EBA\u7684\u94C1\u7D22\uFF0C\u6296\u624B\u6254\u5728\u5979\u5149\u6D01\u7684\u8089\u4F53\u4E0A\u3002\u7389\u5A18\u8EAB\u5B50\u4E00\u98A4\uFF0C\u9732\u51FA\u4E00\u4E1D\u60E7\u610F\u3002\u5B59\u5929\u7FBD\u9053\uFF1A\u300C\u4ECA\u540E\u5B83\u5C31\u662F\u4F60\u8EAB\u4E0A\u7684\u8863\u670D\u4E86\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u5728\u5979\u4E73\u4E0A\u62E7\u4E86\u4E00\u628A\uFF0C\u300C\u8FD8\u4E0D\u8DEA\u597D\uFF1F\u4F60\u8981\u5B66\u7684\u4E1C\u897F\u8FD8\u591A\u7740\u5462\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u4E00\u8FDE\u6570\u65E5\uFF0C\u5B59\u5929\u7FBD\u65E9\u51FA\u665A\u5F52\uFF0C\u8FDE\u674F\u82B1\u6751\u4E5F\u53BB\u5F97\u5C11\u4E86\u3002\u4E39\u5A18\u6BCF\u65E5\u7B49\u5019\uFF0C\u4E5F\u4E0D\u77E5\u4ED6\u5FD9\u4E9B\u4EC0\u4E48\u3002</p><p>\u8FD9\u5929\u76F4\u5230\u6DF1\u591C\uFF0C\u5B59\u5929\u7FBD\u624D\u6765\u5230\u5E97\u91CC\u3002\u4ED6\u4F3C\u4E4E\u662F\u7D2F\u5F97\u7D27\u4E86\uFF0C\u968F\u53E3\u8BF4\u4E86\u51E0\u53E5\uFF0C\u8349\u8349\u5403\u8FC7\u996D\u4FBF\u4E0A\u5E8A\u53BB\u7761\u3002\u4E39\u5A18\u60F3\u95EE\u53C8\u4E0D\u6562\u95EE\uFF0C\u5E2E\u4ED6\u9664\u4E86\u9774\u889C\uFF0C\u64E6\u4E86\u811A\u3002\u7136\u540E\u81EA\u5DF1\u8131\u4E86\u8863\u670D\uFF0C\u6253\u6C34\u6D17\u6D74\u5E72\u51C0\uFF0C\u7528\u8309\u8389\u7C89\u5C06\u8EAB\u5B50\u62B9\u5F97\u9999\u55B7\u55B7\u7684\uFF0C\u4E0A\u4E86\u5E8A\u6328\u7740\u4ED6\u7761\u4E0B\u3002</p><p>\u7389\u83B2\u5728\u5E8A\u5C3E\u7684\u5C4F\u98CE\u540E\u9762\u6D17\u4E86\u8EAB\u4F53\uFF0C\u5439\u4E86\u706F\u624D\u62B1\u7740\u8863\u670D\u51FA\u6765\uFF0C\u4ECD\u7A7F\u7740\u8D34\u8EAB\u7684\u5C0F\u8863\uFF0C\u4E0A\u5E8A\u5728\u53E6\u4E00\u4FA7\u7761\u4E0B\u3002</p><p>\u7761\u5230\u534A\u591C\uFF0C\u5B59\u5929\u7FBD\u7A81\u7136\u9192\u6765\uFF0C\u53EA\u89C9\u80F8\u4FA7\u6E7F\u4E86\u4E00\u7247\uFF0C\u4E39\u5A18\u9999\u8F6F\u7684\u8EAB\u5B50\u504E\u5728\u4ED6\u8EAB\u65C1\uFF0C\u80A9\u5934\u5FAE\u5FAE\u62BD\u52A8\u3002</p><p>\u300C\u600E\u4E48\u54ED\u4E86\uFF1F\u300D</p><p>\u4E39\u5A18\u6CA1\u4F5C\u58F0\u3002\u5B59\u5929\u7FBD\u6258\u8D77\u5979\u7684\u4E0B\u5DF4\uFF0C\u501F\u7740\u6708\u8272\u53EA\u89C1\u5979\u6EE1\u8138\u6E7F\u6E7F\u7684\u90FD\u662F\u6CEA\u75D5\u3002</p><p>\u300C\u4E00\u591C\u90FD\u6CA1\u7761\u4E48\uFF1F\u300D</p><p>\u4E39\u5A18\u70B9\u4E86\u70B9\u5934\u3002\u5B59\u5929\u7FBD\u5C55\u81C2\u6402\u4F4F\u5979\u5149\u6ED1\u7684\u7389\u4F53\uFF0C\u4E39\u5A18\u4F0F\u5728\u4ED6\u6000\u4E2D\uFF0C\u70ED\u6CEA\u8D8A\u6D8C\u8D8A\u591A\u3002</p><p>\u5B59\u5929\u7FBD\u4F4E\u58F0\u9053\uFF1A\u300C\u5230\u5E95\u600E\u4E48\u4E86\uFF1F\u54ED\u6210\u8FD9\u6837\uFF1F\u300D</p><p>\u4E39\u5A18\u62BD\u6CE3\u826F\u4E45\uFF0C\u624D\u9053\uFF1A\u300C\u76F8\u516C\uFF0C\u4F60\u662F\u4E0D\u662F\u4E0D\u8981\u6211\u4EEC\u4E86\uFF1F\u300D</p><p>\u5B59\u5929\u7FBD\u9053\uFF1A\u300C\u600E\u4E48\u4F1A\u5462\uFF1F\u300D</p><p>\u4E39\u5A18\u6D41\u6CEA\u4E0D\u8BED\u3002\u8FD9\u51E0\u65E5\u5B59\u5929\u7FBD\u65F6\u6765\u65F6\u4E0D\u6765\uFF0C\u5C31\u662F\u6765\u4E86\u4E5F\u6CA1\u6709\u51E0\u53E5\u8BDD\u8BF4\uFF0C\u66F4\u4E0D\u7528\u63D0\u5F80\u65E5\u7684\u6E29\u5B58\u4E86\u3002\u81EA\u4ECE\u4ED6\u5A36\u4E86\u7389\u83B2\uFF0C\u6BCD\u5973\u4FE9\u4E3A\u6C42\u4ED6\u6B22\u5FC3\uFF0C\u4E0D\u987E\u7F9E\u803B\u540C\u5E8A\u966A\u4ED6\u53D6\u4E50\uFF0C\u6B63\u5982\u80F6\u4F3C\u6F06\u60C5\u6D53\u4E07\u5206\u7684\u65F6\u5019\uFF0C\u7A81\u7136\u51B7\u6DE1\u4E0B\u6765\uFF0C\u4E39\u5A18\u4E0D\u514D\u53C8\u662F\u7591\u60D1\u53C8\u662F\u4F24\u5FC3\u3002</p><p>\u5B59\u5929\u7FBD\u6709\u70B9\u660E\u767D\u8FC7\u6765\u4E86\uFF0C\u4ED6\u7B97\u4E86\u7B97\uFF0C\u4F4E\u7B11\u9053\uFF1A\u300C\u6211\u6709\u51E0\u5929\u6CA1\u8DDF\u4F60\u4EEC\u5A18\u513F\u4FE9\u5F04\u4E86\uFF1F\u300D</p><p>\u4E39\u5A18\u504E\u4F9D\u5728\u4ED6\u81C2\u95F4\uFF0C\u624B\u6307\u5728\u4ED6\u80F8\u53E3\u8F7B\u8F7B\u5212\u4E86\u4E2A\u300C\u56DB\u300D\u5B57\u3002</p><p>\u5B59\u5929\u7FBD\u7B11\u9053\uFF1A\u300C\u90FD\u56DB\u5929\u4E86\uFF0C\u96BE\u602A\u4F60\u7740\u6025\u3002\u8BA9\u76F8\u516C\u6478\u6478\uFF0C\u4E0B\u8FB9\u662F\u4E0D\u662F\u6E7F\u4E86\u3002\u300D</p><p>\u5F80\u5E38\u5B59\u5929\u7FBD\u624B\u6307\u4F38\u6765\uFF0C\u4E39\u5A18\u90FD\u4E56\u4E56\u655E\u5F00\u8EAB\u5B50\uFF0C\u60F3\u6478\u54EA\u91CC\u90FD\u7531\u4ED6\u4EB5\u73A9\u3002\u8FD9\u56DE\u4E39\u5A18\u5374\u5E76\u7D27\u4E86\u817F\uFF0C\u8BA9\u4ED6\u78B0\u89E6\uFF0C\u63A8\u5F04\u7247\u523B\uFF0C\u4E39\u5A18\u7A81\u7136\u75DB\u54ED\u8D77\u6765\uFF0C\u6CE3\u58F0\u9053\uFF1A\u300C\u90FD\u662F\u674F\u513F\u4E0D\u597D\uFF0C\u88AB\u4EBA\u5F04\u810F\u4E86\u8EAB\u5B50\u3002\u6028\u4E0D\u5F97\u76F8\u516C\u5ACC\u5F03\u2026\u2026\u300D</p><p>\u5B59\u5929\u7FBD\u624B\u6307\u505C\u4F4F\uFF0C\u300C\u6211\u4E0D\u662F\u8BF4\u8FC7\u5417\uFF1F\u522B\u6574\u5929\u6302\u5728\u5FC3\u4E0A\uFF01\u300D</p><p>\u4E39\u5A18\u54FD\u54BD\u9053\uFF1A\u300C\u4ED6\u4EEC\u628A\u674F\u513F\u5F53\u5A3C\u5987\u4E00\u6837\u5F04\u2026\u2026\u5929\u7FBD\u54E5\u2014\u2014\u300D</p><p>\u5B59\u5929\u7FBD\u63A9\u4F4F\u5979\u7684\u5634\uFF0C\u4F4E\u7B11\u9053\uFF1A\u300C\u518D\u54ED\u5C31\u4E0D\u597D\u770B\u4E86\u3002\u6765\uFF0C\u76F8\u516C\u8DDF\u4F60\u6625\u98CE\u4E00\u5EA6\uFF0C\u597D\u751F\u6170\u85C9\u6170\u85C9\u674F\u513F\u3002\u300D</p><p>\u4E39\u5A18\u4ECE\u4ED6\u624B\u91CC\u6323\u5F00\uFF0C\u6447\u7740\u5934\u54ED\u7740\u8BF4\uFF1A\u300C\u4E0D\u662F\u7684\u2026\u2026\u5929\u7FBD\u54E5\uFF0C\u4F60\u8D8A\u5BF9\u6211\u597D\uFF0C\u6211\u8D8A\u89C9\u5F97\u5BF9\u4E0D\u8D77\u4F60\u2026\u2026\u674F\u513F\u8FD9\u6837\u8D31\u7684\u4E00\u4E2A\u5973\u4EBA\uFF0C\u534A\u70B9\u90FD\u914D\u4E0D\u4E0A\u4F60\u3002\u300D</p><p>\u4E39\u5A18\u6015\u5435\u9192\u7389\u83B2\uFF0C\u4E00\u76F4\u538B\u7740\u54ED\u58F0\uFF0C\u5149\u6ED1\u7684\u8EAB\u5B50\u5728\u5B59\u5929\u7FBD\u6000\u91CC\u62BD\u52A8\u7740\uFF0C\u4E00\u5F20\u7389\u8138\u54ED\u5F97\u68A8\u82B1\u5E26\u96E8\u3002\u7B49\u5979\u54ED\u58F0\u6E10\u6B62\uFF0C\u5B59\u5929\u7FBD\u82E6\u7B11\u9053\uFF1A\u300C\u8BA9\u4F60\u54ED\u5F97\u4E00\u70B9\u513F\u7761\u610F\u90FD\u6CA1\u6709\u4E86\u3002\u300D</p><p>\u4E39\u5A18\u54ED\u4E86\u4F1A\u513F\uFF0C\u5FC3\u91CC\u7684\u90C1\u82E6\u8F7B\u677E\u4E86\u4E9B\uFF0C\u5979\u7528\u6307\u5C16\u62ED\u4E86\u6CEA\uFF0C\u5C0F\u58F0\u9053\uFF1A\u300C\u662F\u6211\u4E0D\u597D\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u6258\u8D77\u5979\u7684\u4E0B\u5DF4\uFF0C\u624B\u6307\u653E\u5728\u5979\u6E29\u70ED\u7684\u5507\u74E3\u4E0A\u8F7B\u8F7B\u6309\u7740\uFF0C\u7B11\u9053\uFF1A\u300C\u90A3\u5C31\u7F5A\u4F60\u8FD9\u5F20\u5C0F\u5634\u7ED9\u6211\u542B\u4E00\u4F1A\u513F\uFF0C\u7B49\u5B83\u786C\u4E86\uFF0C\u518D\u7528\u4F60\u4E0B\u9762\u90A3\u5F20\u5C0F\u5634\u628A\u5B83\u4F3A\u5019\u8F6F\u4E86\u3002\u300D</p><p>\u4E39\u5A18\u626C\u8138\u5728\u4ED6\u9888\u4E2D\u4E00\u543B\uFF0C\u300C\u4E0D\u3002\u300D</p><p>\u300C\u54E6\uFF1F\u300D</p><p>\u4E39\u5A18\u67D4\u58F0\u9053\uFF1A\u300C\u5974\u5BB6\u77E5\u9053\u76F8\u516C\u662F\u6015\u674F\u513F\u59D4\u5C48\u3002\u4F46\u8FD9\u51E0\u65E5\u4F60\u7D2F\u5F97\u5F88\u4E86\uFF0C\u4E0D\u7528\u518D\u8D39\u529B\u6765\u54C4\u674F\u513F\u3002\u8FD9\u4F1A\u513F\u5929\u665A\u4E86\uFF0C\u8D77\u6765\u4F1A\u4F24\u8EAB\u5B50\u3002\u518D\u8BF4\uFF0C\u4EBA\u5BB6\u53C8\u4E0D\u662F\u8D2A\u5403\u7684\u3002\u300D</p><p>\u4E39\u5A18\u5E2E\u4ED6\u63A8\u597D\u6795\u5934\uFF0C\u8BF4\u9053\uFF1A\u300C\u76F8\u516C\uFF0C\u4F60\u518D\u7761\u4E00\u513F\uFF0C\u8BA9\u5974\u5BB6\u7ED9\u4F60\u6309\u6309\u80CC\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u95ED\u4E0A\u773C\u3002\u4E39\u5A18\u8DEA\u5750\u8D77\u6765\uFF0C\u624B\u638C\u5728\u4ED6\u8EAB\u4E0A\u8F7B\u8F7B\u6309\u7740\u3002</p><p>\u5B59\u5929\u7FBD\u672C\u6765\u4E86\u65E0\u56F0\u610F\uFF0C\u4F46\u90A3\u53CC\u67D4\u82E5\u65E0\u9AA8\u7684\u624B\u638C\u5728\u8EAB\u4E0A\u6309\u7740\uFF0C\u6E10\u6E10\u6726\u80E7\u8FC7\u53BB\u3002</p><p>\u4E00\u89C9\u9192\u6765\uFF0C\u5DF2\u7ECF\u662F\u7EA2\u65E5\u521D\u5347\u3002\u5B59\u5929\u7FBD\u4F38\u624B\u4E00\u6402\uFF0C\u5374\u6402\u4E86\u4E2A\u7A7A\uFF0C\u56DE\u5934\u53EA\u89C1\u7389\u83B2\u5728\u65C1\u8FB9\u7761\u7740\uFF0C\u776B\u6BDB\u5FAE\u52A8\uFF0C\u663E\u7136\u5DF2\u7ECF\u9192\u4E86\u3002</p><p>\u5B59\u5929\u7FBD\u7FFB\u8EAB\u652F\u7740\u5934\uFF0C\u7EC6\u7EC6\u5730\u770B\u7740\u7389\u83B2\u3002\u6BD4\u8D77\u96EA\u83B2\u548C\u4E39\u5A18\uFF0C\u7389\u83B2\u7684\u5BB9\u8C8C\u66F4\u663E\u79C0\u6C14\uFF0C\u6709\u79CD\u5C0F\u5BB6\u78A7\u7389\u7684\u6E29\u5A49\u52A8\u4EBA\u3002\u5979\u521A\u5230\u7834\u74DC\u5E74\u7EAA\uFF0C\u8138\u988A\u6E29\u6DA6\u5982\u7389\uFF0C\u627E\u4E0D\u5230\u4E00\u4E1D\u76B1\u7EB9\u3002\u67D4\u7EC6\u7684\u808C\u80A4\u6C34\u7075\u7075\u53C8\u767D\u53C8\u5AE9\uFF0C\u6563\u53D1\u7740\u751C\u7F8E\u7684\u5973\u513F\u9999\uFF0C\u9C9C\u5AE9\u5F97\u8BA9\u4EBA\u6068\u4E0D\u5F97\u4E00\u53E3\u541E\u4E0B\u3002</p><p>\u5B59\u5929\u7FBD\u62E5\u7740\u5979\u9053\uFF1A\u300C\u4F60\u5A18\u5462\uFF1F\u300D</p><p>\u300C\u5A18\u5DF2\u7ECF\u8D77\u6765\u4E86\u3002\u300D</p><p>\u300C\u4F60\u600E\u4E48\u8FD8\u4E0D\u8D77\uFF1F\u300D</p><p>\u7389\u83B2\u4FCF\u8138\u5FAE\u5FAE\u53D1\u7EA2\uFF0C\u300C\u5A18\u8BA9\u6211\uFF0C\u966A\u76F8\u516C\u518D\u7761\u4E00\u4F1A\u513F\u2026\u2026\u300D</p><p>\u5B59\u5929\u7FBD\u7B11\u7740\u521A\u8981\u8BF4\u8BDD\uFF0C\u4E39\u5A18\u63A8\u95E8\u51FA\u6765\u3002\u4ED6\u62AC\u773C\u770B\u53BB\uFF0C\u4E0D\u7531\u4E00\u6014\u3002\u4E39\u5A18\u6536\u62FE\u5F97\u6574\u6574\u9F50\u9F50\uFF0C\u9B13\u4FA7\u7C2A\u4E86\u6735\u706B\u7EA2\u77F3\u69B4\u82B1\uFF0C\u8EAB\u4E0A\u7A7F\u4E86\u6D17\u5F97\u5E72\u51C0\u7684\u8863\u670D\uFF0C\u5012\u4F3C\u8981\u51FA\u95E8\u7684\u6837\u5B50\u3002</p><p>\u5B59\u5929\u7FBD\u8BB6\u9053\uFF1A\u300C\u4F60\u53BB\u54EA\u513F\uFF1F\u300D</p><p>\u4E39\u5A18\u5C06\u4E00\u6761\u6C57\u5DFE\u6396\u5728\u4E86\u8170\u95F4\uFF0C\u4F4E\u7740\u5934\u6DE1\u6DE1\u7B11\u9053\uFF1A\u300C\u4ECA\u5929\u8BE5\u662F\u6211\u53BB\u72F1\u91CC\u7684\u65E5\u5B50\u4E86\u3002\u300D\u8BF4\u7740\u867D\u7136\u5E26\u7B11\uFF0C\u773C\u775B\u5374\u6E10\u6E10\u6E7F\u4E86\u3002</p><p>\u5B59\u5929\u7FBD\u8D77\u8EAB\u62FF\u8D77\u5E8A\u5934\u6C8F\u597D\u7684\u8336\u4E00\u996E\u800C\u5C3D\uFF0C\u8212\u4E86\u53E3\u6C14\uFF0C\u300C\u4E0D\u7528\u53BB\u4E86\u3002\u300D</p><p>\u4E39\u5A18\u6115\u7136\u62AC\u9996\uFF0C\u5B59\u5929\u7FBD\u5374\u6CA1\u518D\u89E3\u91CA\uFF0C\u4ED6\u7A7F\u597D\u8863\u670D\uFF0C\u5E26\u4E0A\u9ED1\u5E95\u7EA2\u8FB9\u7684\u5E3D\u5B50\uFF0C\u996D\u4E5F\u6CA1\u5403\u5C31\u79BB\u5F00\u4E86\u674F\u82B1\u6751\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u72F1\u5352\u4EEC\u4E3A\u4E86\u767D\u96EA\u83B2\u4F7F\u5C3D\u4E86\u624B\u6BB5\uFF0C\u8FD9\u51E0\u65E5\u6709\u65F6\u4E00\u5929\u5BA1\u4E0A\u4E24\u4E09\u6B21\uFF0C\u6709\u65F6\u4E00\u6574\u5929\u4E5F\u4E0D\u89C1\u52A8\u9759\uFF1B\u4E0D\u4EC5\u72F1\u5352\u4EEC\u5047\u795E\u5F04\u9B3C\uFF0C\u8FDE\u72F1\u91CC\u7684\u56DA\u72AF\u4E5F\u6311\u4E86\u51E0\u4E2A\uFF0C\u6765\u626E\u4EAC\u5E08\u7684\u5927\u5B98\u3002</p><p>\u4F46\u4E0D\u7BA1\u72F1\u5352\u4EEC\u600E\u4E48\u82B1\u6837\u767E\u51FA\uFF0C\u767D\u96EA\u83B2\u53EA\u51ED\u7740\u4E00\u53E3\u6C14\uFF0C\u62B5\u6B7B\u4E0D\u62DB\uFF0C\u534A\u70B9\u4E5F\u4E0D\u9000\u8BA9\u3002</p><p>\u72F1\u5352\u4EEC\u53C8\u6C14\u53C8\u6068\uFF0C\u53C8\u4E0D\u6562\u771F\u5E9F\u4E86\u5979\uFF0C\u53CC\u65B9\u5C31\u8FD9\u4E48\u6B7B\u7F20\u591A\u65E5\uFF0C\u4E5F\u6CA1\u4E2A\u5934\u7EEA\u3002</p><p>\u8FD9\u5929\u4E00\u76F4\u5BA1\u5230\u5929\u4EAE\uFF0C\u4E00\u9000\u5802\u4F17\u4EBA\u5C31\u4F5C\u4E86\u9E1F\u517D\u6563\u3002\u9C8D\u6A2A\u53D8\u7740\u82B1\u6837\u5728\u767D\u96EA\u83B2\u8EAB\u4E0A\u821E\u5F04\uFF0C\u4E5F\u71AC\u5F97\u7CBE\u75B2\u529B\u5C3D\uFF0C\u56DE\u53BB\u5C31\u5012\u5934\u5927\u7761\uFF0C\u76F4\u5230\u5348\u65F6\u8FD8\u6CA1\u8D77\u6765\u3002</p><p>\u6B63\u7761\u5F97\u719F\uFF0C\u5916\u5802\u4F20\u6765\u51E0\u58F0\u54CD\u52A8\uFF0C\u6709\u4EBA\u9053\uFF1A\u300C\u5351\u804C\u5B59\u5929\u7FBD\uFF0C\u53C2\u89C1\u9C8D\u5927\u4EBA\u3002\u300D</p><p>\u4EFB\u547D\u72F1\u6B63\u7684\u6587\u4E66\u8FD8\u672A\u4E0B\u6765\uFF0C\u4F46\u8FD9\u8BDD\u9C8D\u6A2A\u542C\u5F97\u5341\u4E8C\u5206\u53D7\u7528\uFF0C\u4E5F\u4E0D\u518D\u8BA1\u8F83\u5B59\u5929\u7FBD\u65E0\u793C\u6253\u6405\u81EA\u5DF1\u597D\u7761\u3002\u4ED6\u6253\u7740\u5475\u6B20\u9053\uFF1A\u300C\u662F\u5C0F\u5B59\u554A\uFF0C\u4EC0\u4E48\u4E8B\u554A\uFF1F\u300D</p><p>\u5B59\u5929\u7FBD\u8FDB\u4E86\u5185\u5BA4\uFF0C\u7B11\u9053\uFF1A\u300C\u5012\u662F\u4E00\u6869\u597D\u4E8B\u3002\u6258\u5927\u4EBA\u798F\uFF0C\u5351\u804C\u62FF\u4E86\u767D\u9006\u5BB6\u5C5E\u4E00\u540D\u3002\u300D</p><p>\u542C\u5230\u662F\u8FD9\u8981\u7D27\u6848\u5B50\uFF0C\u9C8D\u6A2A\u987F\u65F6\u9192\u4E86\u4E00\u534A\uFF0C\u300C\u8C01\uFF1F\u300D</p><p>\u5B59\u5929\u7FBD\u8D34\u5728\u4ED6\u8033\u8FB9\u4F4E\u58F0\u9053\uFF1A\u300C\u662F\u767D\u5B5D\u5112\u7684\u59BB\u59B9\uFF0C\u767D\u96EA\u83B2\u7684\u5AE1\u4EB2\u5A18\u59E8\u3002\u6765\u674F\u82B1\u6751\u63A2\u4EB2\uFF0C\u6B63\u597D\u8BA9\u6211\u649E\u4E0A\u3002\u300D</p><p>\u767D\u5B5D\u5112\u7684\u9006\u6848\u6B63\u5728\u52D8\u67E5\uFF0C\u867D\u7136\u5B98\u5E9C\u8FD8\u672A\u4E0B\u6355\u62FF\u7684\u6587\u4E66\uFF0C\u4F46\u767D\u5B5D\u5112\u59BB\u59B9\u4E0D\u7528\u8BF4\u5728\u4E5D\u65CF\u4E4B\u5185\uFF0C\u62FF\u4E86\u4E5F\u6CA1\u4EBA\u8BF4\u4E2A\u4E0D\u5B57\uFF0C\u505A\u597D\u4E86\u53C8\u662F\u6869\u529F\u52B3\u3002\u9C8D\u6A2A\u9053\uFF1A\u300C\u770B\u4E0D\u51FA\u554A\uFF0C\u4F60\u5C0F\u5B50\u8FD8\u633A\u6709\u4E9B\u798F\u6C14\u2026\u2026\u300D</p><p>\u5B59\u5929\u7FBD\u62FF\u51FA\u6536\u62BC\u6587\u4E66\uFF0C\u300C\u5351\u804C\u5DF2\u7ECF\u5148\u5BA1\u8FC7\uFF0C\u8BE5\u72AF\u59D3\u88F4\uFF0C\u540D\u9752\u7389\uFF0C\u4E09\u5341\u4E8C\u5C81\u3002</p><p>\u4E08\u592B\u5DF2\u6B7B\uFF0C\u5E76\u65E0\u5B50\u606F\u3002\u662F\u4E2A\u5B88\u671B\u95E8\u5BE1\u7684\u5C0F\u5BE1\u5987\u3002\u300D</p><p>\u9C8D\u6A2A\u4E00\u542C\uFF0C\u5FC3\u91CC\u987F\u65F6\u75D2\u75D2\u4E86\u8D77\u6765\uFF0C\u7FFB\u8EAB\u8DBF\u4E86\u978B\u9053\uFF1A\u300C\u6536\u76D1\u4E86\u6CA1\uFF1F\u5728\u54EA\u513F\u5173\u7740\u5462\uFF1F\u300D</p><p>\u5B59\u5929\u7FBD\u7B11\u7740\u62C9\u4F4F\u4ED6\uFF0C\u300C\u5927\u4EBA\u8FD8\u4E0D\u77E5\u9053\uFF0C\u8FD9\u88F4\u9752\u7389\u6BD4\u4E39\u5A18\u8FD8\u4FCF\u7740\u51E0\u5206\uFF0C\u6C34\u5AE9\u5AE9\u4E00\u4E2A\u7F8E\u4EBA\u3002\u300D\u4ED6\u6DEB\u7B11\u51E0\u58F0\uFF0C\u8F7B\u58F0\u8033\u8BED\u9053\uFF1A\u300C\u66F4\u96BE\u5F97\u7684\u662F\u53C8\u9A9A\u53C8\u6D6A\u53C8\u542C\u8BDD\uFF0C\u5929\u751F\u7684\u4E00\u4E2A\u5A4A\u5B50\uFF0C\u6BD4\u4E39\u5A18\u66F4\u77E5\u60C5\u8BC6\u8DA3\u3002\u5351\u804C\u62FF\u5979\u7684\u65F6\u5019\uFF0C\u628A\u5979\u5413\u7834\u4E86\u80C6\uFF0C\u8981\u600E\u4E48\u6837\u5C31\u600E\u4E48\u6837\u2014\u2014\u5927\u4EBA\u4E00\u8BD5\u4FBF\u77E5\uFF0C\u6BD4\u7A91\u5B50\u91CC\u7684\u59D0\u513F\u8FD8\u4E56\u7740\u4E9B\u3002\u300D</p><p>\u9C8D\u6A2A\u88AB\u4ED6\u4E00\u756A\u8BDD\u52FE\u5F97\u5FC3\u75D2\u96BE\u6414\uFF0C\u7C97\u58F0\u5927\u6C14\u5730\u9053\uFF1A\u300C\u5728\u54EA\u513F\u5728\u54EA\u513F\uFF1F\u6211\u5148\u53BB\u5BA1\u5BA1\uFF01\u300D</p><p>\u5B59\u5929\u7FBD\u89C1\u706B\u5019\u5DF2\u5230\u4E5D\u6210\uFF0C\u9042\u7B11\u9053\uFF1A\u300C\u6536\u76D1\u7EAA\u6863\u8981\u7531\u5927\u4EBA\u70B9\u5934\uFF0C\u5351\u804C\u6597\u80C6\uFF0C\u5148\u628A\u56DA\u72AF\u7ED9\u5927\u4EBA\u5E26\u6765\u4E86\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u653E\u4E0B\u4E86\u6587\u4E66\uFF0C\u51FA\u4E86\u95E8\u53BB\uFF0C\u63A5\u7740\u5C31\u542C\u5230\u94C1\u9563\u58F0\u54CD\u3002\u518D\u8FDB\u6765\u65F6\uFF0C\u624B\u91CC\u591A\u4E86\u6761\u94C1\u7D22\u3002\u9C8D\u6A2A\u77AA\u5927\u773C\u775B\uFF0C\u53EA\u89C1\u94C1\u7D22\u672B\u7AEF\u5957\u5728\u4E00\u622A\u96EA\u767D\u7684\u9888\u5B50\u4E0A\uFF0C\u63A5\u7740\u662F\u5149\u6ED1\u7684\u80A9\u5934\u3002</p><p>\u90A3\u5973\u5B50\u7ADF\u7136\u4E00\u4E1D\u4E0D\u6302\uFF0C\u50CF\u72D7\u4E00\u6837\u88AB\u94C1\u7D22\u62F4\u7740\uFF0C\u722C\u5230\u5BA4\u5185\u3002\u5979\u7709\u773C\u5982\u753B\uFF0C\u79C0\u53D1\u68B3\u7406\u5F97\u6574\u6574\u9F50\u9F50\uFF0C\u7528\u4E00\u6839\u5149\u4EAE\u7684\u94F6\u9497\u522B\u5728\u8111\u540E\uFF0C\u4FEE\u9970\u5F97\u5982\u65B0\u5AC1\u5A18\u822C\u7CBE\u81F4\u3002\u5979\u56DB\u80A2\u7740\u5730\uFF0C\u90A3\u5BF9\u4E30\u817B\u7684\u4E73\u623F\u60AC\u5728\u80F8\u524D\uFF0C\u968F\u7740\u5979\u7684\u722C\u52A8\u5728\u81C2\u95F4\u4E00\u6447\u4E00\u6446\uFF0C\u6643\u4E2A\u4E0D\u505C\u3002</p><p>\u7389\u5A18\u722C\u5230\u7262\u5934\u811A\u524D\uFF0C\u78D5\u5934\u9053\uFF1A\u300C\u8D31\u56DA\u88F4\u9752\u7389\u53E9\u89C1\u5927\u4EBA\u3002\u300D</p><p>\u9C8D\u6A2A\u76F4\u52FE\u52FE\u76EF\u7740\u5979\u767D\u6ED1\u7684\u80F4\u4F53\uFF0C\u5F20\u5927\u4E86\u5634\uFF0C\u9B42\u98DE\u5929\u5916\u3002</p><p>\u5B59\u5929\u7FBD\u5C06\u94C1\u94FE\u9501\u5728\u5E8A\u811A\uFF0C\u94A5\u5319\u6254\u5728\u5E8A\u4E0A\uFF0C\u7136\u540E\u628A\u5173\u62BC\u7684\u6587\u4E66\u9012\u5230\u7389\u5A18\u9762\u524D\uFF0C\u300C\u6309\u4E86\u624B\u5370\uFF0C\u4F60\u5C31\u7B97\u8FDB\u4E86\u72F1\u91CC\uFF0C\u5F80\u540E\u5C31\u7531\u9C8D\u5927\u4EBA\u5173\u7167\u4F60\u4E86\u3002\u300D</p><p>\u7389\u5A18\u624B\u6307\u8F7B\u98A4\u7740\u91AE\u4E86\u5370\u6CE5\uFF0C\u5728\u90A3\u9875\u8F7B\u98D8\u98D8\u7684\u7EB8\u4E0A\u6309\u4E0B\u6307\u5370\u3002</p><p>\u8FD9\u51E0\u65E5\u5979\u88AB\u5B59\u5929\u7FBD\u53CD\u590D\u8C03\u6559\uFF0C\u65E9\u5DF2\u6CA1\u6709\u534A\u70B9\u53CD\u6297\u7684\u5FF5\u5934\u3002\u65E9\u6668\u5B59\u5929\u7FBD\u8BA9\u5979\u5986\u626E\u4E86\uFF0C\u5957\u4E86\u8F66\u8D76\u5230\u8FD9\u91CC\uFF0C\u5979\u8FDE\u8EAB\u5728\u4F55\u5904\u90FD\u4E0D\u77E5\u6653\u3002\u6B64\u65F6\u542C\u5230\u662F\u76D1\u72F1\uFF0C\u7389\u5A18\u53C8\u60CA\u53C8\u6015\uFF0C\u4E00\u5B57\u4E5F\u4E0D\u6562\u591A\u8BF4\u3002</p><p>\u56DB\u4E2A\u6307\u5370\u4E00\u4E00\u6309\u5B8C\uFF0C\u5B59\u5929\u7FBD\u6536\u4E86\u6587\u4E66\uFF0C\u7B11\u9053\uFF1A\u300C\u8FD8\u4E0D\u597D\u597D\u4F3A\u5019\u9C8D\u5927\u4EBA\u3002\u82E5\u4F3A\u5019\u5F97\u597D\u4E86\uFF0C\u8BF4\u4E0D\u5B9A\u9C8D\u5927\u4EBA\u4F1A\u628A\u8FD9\u513F\u5F53\u505A\u4F60\u7684\u56DA\u7262\uFF0C\u514D\u4E86\u4F60\u5230\u72F1\u91CC\u5403\u7A9D\u5934\u3002\u300D</p><p>\u7389\u5A18\u8FD8\u672A\u7B54\u8BDD\uFF0C\u9C8D\u6A2A\u4E00\u4E2A\u997F\u72D7\u6251\u98DF\u628A\u5979\u538B\u5012\uFF0C\u4E24\u624B\u5728\u5979\u767D\u5AE9\u7684\u8089\u4F53\u4E0A\u53C8\u6478\u53C8\u62E7\u3002</p><p>\u79BB\u5F00\u72F1\u6B63\u5385\uFF0C\u5353\u5929\u96C4\u4E0E\u5218\u8FA8\u673A\u90FD\u5728\u623F\u5185\u7B49\u5019\u3002\u81EA\u4ECE\u4E24\u4EBA\u900F\u9732\u51FA\u6500\u9644\u7684\u5FC3\u601D\uFF0C\u4E0E\u5B59\u5929\u7FBD\u53C8\u4EB2\u8FD1\u4E86\u51E0\u5206\u3002\u5B59\u5929\u7FBD\u4E5F\u4E0D\u518D\u9690\u7792\uFF0C\u5C06\u8C0B\u5212\u5408\u76D8\u6258\u51FA\uFF0C\u4E09\u4EBA\u5546\u91CF\u591A\u65E5\uFF0C\u867D\u7136\u5747\u89C9\u6307\u671B\u4E0D\u5927\uFF0C\u4F46\u603B\u597D\u8FC7\u5750\u4EE5\u5F85\u6BD9\uFF0C\u6210\u4E0E\u4E0D\u6210\uFF0C\u5C31\u770B\u8001\u5929\u7237\u7684\u5FC3\u610F\u4E86\u3002</p><p>\u5B59\u5929\u7FBD\u8BF4\u4E86\u628A\u88F4\u9752\u7389\u9001\u7ED9\u9C8D\u6A2A\u7684\u4E8B\u60C5\uFF0C\u7B11\u9053\uFF1A\u300C\u62B1\u6B49\u504F\u4E86\u4E24\u4F4D\uFF0C\u6CA1\u80FD\u5C1D\u5230\u9C9C\u6C64\u3002\u300D</p><p>\u5218\u8FA8\u673A\u55B7\u4E86\u53E3\u70DF\uFF0C\u300C\u9C8D\u6A2A\u611A\u6C13\u4E00\u4E2A\uFF0C\u597D\u5403\u8D2A\u5360\uFF0C\u5B59\u5144\u8FD9\u7740\u68CB\u5C11\u8BF4\u8BA9\u4ED6\u4E09\u4E94\u5929\u4E0D\u80FD\u51FA\u95E8\u3002\u770B\u6765\u5B59\u5144\u662F\u51C6\u5907\u8FDC\u884C\u4E86\u3002\u300D</p><p>\u5353\u5929\u96C4\u9053\uFF1A\u300C\u7528\u4E0D\u7528\u6211\u8DDF\u4F60\u540C\u53BB\uFF1F\u4E5F\u597D\u6709\u4E2A\u7167\u5E94\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u9053\uFF1A\u300C\u4EBA\u591A\u4E86\u53CD\u800C\u4E0D\u597D\uFF0C\u8FD8\u662F\u6211\u81EA\u5DF1\u53BB\u5427\u3002\u4E94\u4E03\u5929\u5FC5\u7136\u56DE\u6765\uFF0C\u5230\u65F6\u518D\u4F5C\u8BA1\u8F83\u3002\u81F3\u4E8E\u8FD9\u8FB9\uFF0C\u5C31\u6709\u52B3\u4E24\u4F4D\uFF0C\u5207\u4E0D\u53EF\u8BA9\u4E39\u5A18\u649E\u89C1\u82F1\u83B2\u548C\u7389\u5A18\uFF0C\u518D\u54C4\u5979\u51E0\u65E5\uFF0C\u514D\u5F97\u95F9\u817E\u8D77\u6765\uFF0C\u7B49\u6253\u53D1\u4E86\u4F55\u6E05\u6CB3\u518D\u8BF4\u3002\u300D</p><p>\u4E09\u4EBA\u5546\u8BAE\u5DF2\u5B9A\uFF0C\u5218\u8FA8\u673A\u62FF\u51FA\u960E\u7F57\u671B\u9057\u4E0B\u7684\u6D12\u91D1\u6298\u6247\uFF0C\u653E\u5728\u5305\u88F9\u4E2D\uFF0C\u4E00\u5E76\u9012\u7ED9\u5B59\u5929\u7FBD\u3002</p>',111);function t(n,_){return a}var o=e(r,[["render",t]]);export{o as default};
