import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_45-\u6BCD\u5973" tabindex="-1"><a class="header-anchor" href="#_45-\u6BCD\u5973" aria-hidden="true">#</a> 45 \u6BCD\u5973</h1><p>\u5348\u540E\u7684\u5C71\u6797\u4E00\u7247\u5BC2\u9759\uFF0C\u5C71\u4E2D\u4E00\u95F4\u5C0F\u5C0F\u7684\u9152\u5E97\uFF0C\u674F\u9EC4\u9152\u5E4C\u4E0A\u300C\u674F\u82B1\u6751\u300D\u4E09\u4E2A\u58A8\u5B57\u5DF2\u7ECF\u892A\u8272\u3002\u4E00\u5EA7\u4E24\u5C42\u5C0F\u697C\u9694\u65AD\u4E86\u89C6\u7EBF\uFF0C\u5374\u6321\u4E0D\u4F4F\u592A\u9633\u76F4\u5C04\u7684\u5149\u7EBF\u3002</p><p>\u697C\u540E\u7684\u7A7A\u9662\u91CC\uFF0C\u4E00\u4E2A\u7F8E\u8C8C\u5987\u4EBA\u8D64\u6761\u6761\u8EBA\u5728\u5E2D\u4E0A\uFF0C\u5C55\u9732\u51FA\u767D\u82B1\u82B1\u7684\u8089\u4F53\u3002\u5979\u5A07\u8273\u7684\u9762\u5B54\u4E00\u7247\u7EEF\u7EA2\uFF0C\u767D\u5AE9\u7684\u53CC\u817F\u5927\u5F20\u7740\u4E0D\u4F4F\u6218\u6817\u3002\u4E00\u4E2A\u82B1\u74E3\u822C\u7684\u5C11\u5973\u8DEA\u4F0F\u5728\u5979\u817F\u95F4\uFF0C\u957F\u53D1\u4F4E\u5782\uFF0C\u8214\u8210\u7F8E\u5987\u9634\u4E2D\u9732\u51FA\u7684\u4E00\u622A\u74DC\u8482\u3002\u5706\u6DA6\u7684\u7389\u81C0\u5FAE\u5FAE\u7FD8\u8D77\uFF0C\u88AB\u540E\u9762\u4E00\u4E2A\u7CBE\u58EE\u6C49\u5B50\u62B1\u4F4F\uFF0C\u7C97\u786C\u7684\u9633\u5177\u5C11\u5973\u767D\u5AE9\u7684\u81C0\u7F1D\u4E2D\u7528\u529B\u6233\u5F04\u3002</p><p>\u90A3\u5987\u4EBA\u9634\u4E2D\u7EA2\u7B4B\u5410\u9732\uFF0C\u867D\u7136\u88AB\u8214\u5F04\u7684\u53EA\u662F\u74DC\u8482\uFF0C\u6DEB\u6DB2\u5374\u6D41\u4E2A\u4E0D\u505C\u3002\u90A3\u5C11\u5973\u88AB\u4E24\u4EBA\u5939\u5728\u4E2D\u95F4\uFF0C\u4E00\u8FB9\u8214\u7740\u74DC\u8482\uFF0C\u4E00\u8FB9\u6328\u808F. \u4E0D\u77E5\u4E0D\u89C9\u95F4\uFF0C\u6811\u836B\u5DF2\u7ECF\u79FB\u5F00\uFF0C\u4E24\u5973\u5927\u534A\u8EAB\u5B50\u90FD\u88AB\u9633\u5149\u76F4\u5C04\u3002\u8FD9\u6837\u5149\u5929\u5316\u65E5\u4E0B\u7684\u767D\u663C\u6DEB\u620F\uFF0C\u4F7F\u6BCD\u5973\u4FE9\u518D\u6CA1\u6709\u4E1D\u6BEB\u9690\u79C1\u548C\u906E\u63A9\uFF0C\u8EAB\u4F53\u6BCF\u4E2A\u90E8\u4F4D\u90FD\u6E05\u6670\u5730\u5C55\u9732\u51FA\u6765\uFF0C\u4F9B\u4EBA\u628A\u73A9\u3002</p><p>\u90A3\u7537\u5B50\u62D4\u51FA\u9633\u5177\uFF0C\u8BF4\u9053\uFF1A\u300C\u5927\u5C0F\u6BCD\u72D7\u90FD\u8EBA\u597D\uFF0C\u8BA9\u6211\u6328\u7740\u5E72\u3002\u300D</p><p>\u5C11\u5973\u6328\u7740\u5987\u4EBA\u8EBA\u4E0B\uFF0C\u5206\u5F00\u817F\uFF0C\u5979\u7389\u6237\u5DF2\u88AB\u63D2\u5F97\u5F20\u5F00\uFF0C\u9732\u51FA\u91CC\u9762\u7EA2\u817B\u7684\u871C\u8089\u3002</p><p>\u5B59\u5929\u7FBD\u7528\u624B\u6307\u91CF\u4E86\u91CF\uFF0C\u7B11\u9053\uFF1A\u300C\u6BD4\u4F60\u5A18\u8FD8\u5C0F\u4E86\u70B9\u513F\u3002\u7741\u5F00\u773C\uFF0C\u770B\u770B\u6211\u600E\u4E48\u5E72\u4F60\u5A18\u8FD9\u6761\u5927\u6BCD\u72D7\u7684\u3002\u300D</p><p>\u4E39\u5A18\u67D4\u5A9A\u5730\u633A\u8D77\u4E86\u4E0B\u8179\uFF0C\u5B59\u5929\u7FBD\u63E1\u4F4F\u74DC\u8482\u4E00\u6405\uFF0C\u7136\u540E\u7528\u529B\u4E00\u62D4\uFF0C\u300C\u5575\u53FD\u300D\u4E00\u58F0\uFF0C\u7C97\u5927\u7684\u74DC\u4F53\u4ECE\u4E39\u5A18\u9634\u4E2D\u731B\u7136\u8131\u51FA\uFF0C\u5E26\u51FA\u4E00\u80A1\u6E05\u4EAE\u7684\u6DB2\u4F53\u3002</p><p>\u5B59\u5929\u7FBD\u6258\u8D77\u5987\u4EBA\u7684\u53CC\u811A\u67B6\u5728\u80A9\u4E0A\uFF0C\u9633\u5177\u5BF9\u7740\u8FD8\u672A\u5408\u62E2\u7684\u871C\u7A74\u6775\u4E86\u8FDB\u53BB\u3002\u8FD9\u4E00\u4E0B\u5C3D\u6839\u800C\u5165\uFF0C\u9876\u5F97\u4E39\u5A18\u300C\u5440\u300D\u7684\u4E00\u58F0\u53EB\u4E86\u51FA\u6765\u3002\u8DDF\u6BCD\u5973\u4FE9\u5728\u5BA4\u5916\u4EA4\u5ABE\uFF0C\u4F7F\u5B59\u5929\u7FBD\u60C5\u7EEA\u9AD8\u6DA8\uFF0C\u52A8\u4F5C\u4E5F\u7C97\u66B4\u4E86\u8BB8\u591A\u3002\u9633\u5177\u5728\u5987\u4EBA\u6E7F\u6ED1\u7684\u871C\u7A74\u91CC\u76F4\u5165\u76F4\u51FA\uFF0C\u5F77\u4F5B\u8981\u628A\u5979\u7684\u8089\u7A74\u649E\u788E\u3002</p><p>\u4E39\u5A18\u767D\u7F8E\u7684\u8EAB\u5B50\u5728\u7AF9\u5E2D\u4E0A\u626D\u52A8\uFF0C\u62DA\u547D\u8FCE\u5408\u7740\u9633\u5177\u7684\u62BD\u9001\uFF0C\u53E3\u4E2D\u6D41\u6C34\u822C\u53EB\u7740\uFF0C\u300C\u54E5\u54E5\uFF0C\u597D\u54E5\u54E5\uFF0C\u5E72\u6B7B\u5974\u5BB6\u4E86\u2026\u2026\u300D</p><p>\u5B59\u5929\u7FBD\u6293\u4F4F\u5979\u7684\u4E73\u7403\uFF0C\u7528\u529B\u4E00\u62E7\uFF0C\u300C\u4F60\u662F\u5927\u6BCD\u72D7\u3002\u300D</p><p>\u5987\u4EBA\u4E00\u53CC\u7EA4\u8DB3\u5728\u5B59\u5929\u7FBD\u80A9\u5934\u7D27\u52FE\u7740\uFF0C\u4E0D\u4F4F\u98A4\u6296\uFF0C\u6D6A\u58F0\u9053\uFF1A\u300C\u6211\u662F\u5927\u6BCD\u72D7\uFF0C\u5927\u6BCD\u72D7\u7684\u5C44\u90FD\u88AB\u54E5\u54E5\u63D2\u6EE1\u4E86\u2026\u2026\u300D</p><p>\u7389\u83B2\u5728\u65C1\u770B\u5F97\u9762\u7EA2\u8033\u8D64\uFF0C\u5B59\u5929\u7FBD\u9053\uFF1A\u300C\u5C0F\u6BCD\u72D7\uFF0C\u5148\u73A9\u81EA\u5DF1\u7684\u5C44\uFF0C\u7B49\u6211\u5E72\u5B8C\u8FD9\u6761\u5927\u6BCD\u72D7\u518D\u6765\u5E72\u4F60\u3002\u300D</p><p>\u7389\u83B2\u7F9E\u7B54\u7B54\u628A\u624B\u4F38\u5230\u8179\u4E0B\uFF0C\u5728\u4ED6\u9762\u524D\u73A9\u5F04\u8D77\u81EA\u5DF1\u7684\u6027\u5668\u3002</p><p>\u4E39\u5A18\u4E0B\u4F53\u65E9\u88AB\u62B9\u4E86\u6DEB\u836F\uFF0C\u7A74\u5185\u7684\u871C\u8089\u70BD\u70ED\u5982\u706B\uFF0C\u5728\u9633\u7269\u6345\u5F04\u4E0B\uFF0C\u4E0D\u591A\u65F6\u4FBF\u6CC4\u4E86\u8EAB\u5B50\u3002\u5B59\u5929\u7FBD\u62C9\u8FC7\u7389\u83B2\uFF0C\u5C06\u5E26\u7740\u6BCD\u4EB2\u4F53\u6DB2\u7684\u8089\u68D2\u6345\u8FDB\u5C11\u5973\u4F53\u5185\u3002</p><p>\u7389\u83B2\u871C\u7A74\u7D27\u5BC6\u6D45\u7A84\uFF0C\u6C34\u7075\u7075\u9C9C\u5AE9\u65E0\u6BD4\uFF0C\u9633\u5177\u63D2\u5728\u91CC\u9762\uFF0C\u88AB\u871C\u8089\u7D27\u5939\u7740\uFF0C\u5F77\u4F5B\u8981\u88AB\u6324\u51FA\u4F53\u5916\u3002\u5979\u82B1\u5FC3\u751F\u5F97\u6D45\uFF0C\u6BD4\u5E73\u5E38\u5973\u5B50\u66F4\u5BB9\u6613\u88AB\u9876\u4F4F\uFF0C\u6CA1\u51E0\u4E0B\u5C31\u88AB\u9F9F\u5934\u649E\u5F97\u53C8\u9178\u53C8\u6DA9\uFF0C\u8EAB\u5B50\u60C5\u4E0D\u81EA\u7981\u5730\u54C6\u55E6\u8D77\u6765\u3002</p><p>\u4E39\u5A18\u4E24\u817F\u90FD\u88AB\u6DEB\u6C34\u6E85\u6E7F\uFF0C\u5979\u5A07\u5598\u7740\u6B47\u606F\u7247\u523B\uFF0C\u624D\u8D77\u8EAB\u62FF\u6C57\u5DFE\u51C6\u5907\u62B9\u62ED\u3002\u8FD9\u8FB9\u7389\u83B2\u51E0\u4E4E\u8981\u54ED\u4E86\u51FA\u6765\uFF0C\u5F80\u5E38\u5B59\u5929\u7FBD\u4E0E\u5979\u4EA4\u5408\u90FD\u662F\u6E29\u5B58\u5C45\u591A\uFF0C\u6B64\u65F6\u62FF\u5979\u4E0E\u4E39\u5A18\u4E00\u6837\u5BF9\u5F85\uFF0C\u7389\u83B2\u67D4\u5F31\u7684\u8EAB\u5B50\u5C31\u6709\u4E9B\u627F\u53D7\u4E0D\u8D77\u4E86\u3002</p><p>\u4E39\u5A18\u89C1\u72B6\u987E\u4E0D\u5F97\u62B9\u62ED\uFF0C\u8BF4\u9053\uFF1A\u300C\u7389\u83B2\u91CC\u9762\u751F\u5F97\u6D45\uFF0C\u6211\u6765\u7ED9\u76F8\u516C\u675F\u4E00\u4E0B\u5427\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u7B11\u9053\uFF1A\u300C\u597D\u4E2A\u5FC3\u8F6F\u7684\u5A18\u4EB2\u3002\u300D</p><p>\u89C1\u5B59\u5929\u7FBD\u6CA1\u6709\u53CD\u5BF9\uFF0C\u4E39\u5A18\u62FF\u8D77\u767D\u7EEB\u6C57\u5DFE\uFF0C\u675F\u5728\u4ED6\u9633\u5177\u6839\u90E8\u3002\u8FD9\u6837\u4E00\u6765\uFF0C\u7389\u83B2\u627F\u53D7\u7684\u649E\u51FB\u5C31\u8F7B\u591A\u4E86\u3002\u8FDB\u51FA\u95F4\u725D\u4E2D\u4E50\u8DA3\u6E10\u751F\uFF0C\u7389\u83B2\u7709\u5934\u6E10\u6E10\u5730\u6563\u5F00\uFF0C\u5589\u4E2D\u4E5F\u6162\u6162\u9038\u51FA\u5A9A\u58F0\u3002\u5979\u7709\u773C\u95F4\u9752\u6DA9\u672A\u892A\uFF0C\u6B64\u65F6\u542B\u7F9E\u5E26\u5A9A\uFF0C\u534A\u662F\u5C11\u5973\u7684\u5A07\u7F9E\uFF0C\u534A\u662F\u65B0\u5987\u7684\u598D\u6001\uFF0C\u5AE9\u7A74\u67D4\u817B\u751F\u59FF\uFF0C\u5B59\u5929\u7FBD\u6B32\u706B\u6108\u53D1\u9AD8\u3002</p><p>\u6CA1\u591A\u4E45\uFF0C\u7389\u83B2\u4E5F\u6CC4\u4E86\u8EAB\u5B50\u3002\u5B59\u5929\u7FBD\u677E\u5F00\u5979\uFF0C\u773C\u89C1\u6BCD\u5973\u4FE9\u5728\u7389\u4F53\u6A2A\u9648\uFF0C\u4E0B\u8EAB\u90FD\u88AB\u63D2\u5F97\u7FFB\u5F00\uFF0C\u80A1\u95F4\u6DEB\u6C34\u9634\u7CBE\u6DCB\u6DCB\u6F13\u6F13\uFF0C\u4E5F\u672A\u66FE\u62B9\u62ED\uFF0C\u6027\u5668\u6E7F\u6DCB\u6DCB\u655E\u9732\u5728\u9633\u5149\u4E0B\uFF0C\u500D\u89C9\u6DEB\u8273\u3002\u7B11\u9053\uFF1A\u300C\u4F60\u4EEC\u5A18\u513F\u4FE9\u90FD\u6CC4\u4E86\u8EAB\u5B50\uFF0C\u62FF\u4EC0\u4E48\u6765\u4F3A\u5019\u6211\u5462\uFF1F\u300D</p><p>\u4E39\u5A18\u7B11\u7740\u63A8\u4E86\u7389\u83B2\u4E00\u628A\uFF0C\u7389\u83B2\u8EAB\u5B50\u90FD\u8F6F\u4E86\uFF0C\u52C9\u5F3A\u5E76\u4F4F\u817F\uFF0C\u7528\u624B\u63A9\u4E86\uFF0C\u55EB\u5685\u9053\uFF1A\u300C\u540E\u5EAD\u2026\u2026\u300D</p><p>\u4E39\u5A18\u7B11\u9053\uFF1A\u300C\u7389\u83B2\u5728\u697C\u4E0A\u5DF2\u7ECF\u6D17\u4E86\u5C41\u80A1\uFF0C\u5C31\u7B49\u4F60\u7ED9\u5979\u540E\u9762\u5F00\u82DE\u5462\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u5BF9\u7389\u83B2\u9053\uFF1A\u300C\u4F60\u6015\u4E0D\u6015\uFF1F\u300D</p><p>\u7389\u83B2\u8BF4\u9053\uFF1A\u300C\u5A18\u8BF4\u8FD9\u662F\u8BE5\u5F53\u7684\u2026\u2026\u53EB\u5974\u5BB6\u542C\u8BDD\uFF0C\u5FCD\u7740\u70B9\u513F\u75DB\u2026\u2026\u8BA9\u76F8\u516C\u597D\u597D\u63D2\u2026\u2026\u300D</p><p>\u5B59\u5929\u7FBD\u7B11\u9053\uFF1A\u300C\u90A3\u8FD8\u4E0D\u8D77\u6765\uFF1F\u300D</p><p>\u7389\u83B2\u722C\u8D77\u8EAB\uFF0C\u8DB4\u5728\u5E2D\u4E0A\uFF0C\u9888\u80A9\u7740\u5730\uFF0C\u7FD8\u8D77\u767D\u5AE9\u7684\u81C0\u513F\uFF0C\u817E\u51FA\u624B\u6252\u4F4F\u81C0\u8089\uFF0C\u8F7B\u8F7B\u5265\u5F00\uFF0C\u9732\u51FA\u81C0\u6C9F\u91CC\u4E00\u53EA\u7EA2\u5AE9\u5C0F\u5DE7\u7684\u8089\u5B54\u3002\u597D\u8089\u5B54\u4E0D\u8FC7\u6307\u5C16\u5927\u5C0F\uFF0C\u7EC6\u7EC6\u7684\u83CA\u7EB9\u7D27\u5F20\u5730\u7F29\u7740\uFF0C\u886C\u7740\u96EA\u767D\u7684\u81C0\u8089\uFF0C\u7EA4\u79C0\u53EF\u7231\uFF0C\u4EE4\u4EBA\u7981\u4E0D\u4F4F\u5FC3\u751F\u601C\u60DC\u3002</p><p>\u5B59\u5929\u7FBD\u629A\u5F04\u7247\u523B\u3002\u7389\u83B2\u7684\u81C0\u8089\u53C8\u7EC6\u53C8\u5AE9\uFF0C\u6ED1\u4E0D\u6E9C\u624B\uFF0C\u6478\u5728\u81C0\u6C9F\u91CC\u6EE1\u624B\u90FD\u662F\u67D4\u6ED1\uFF0C\u552F\u6709\u5C41\u773C\u513F\u7D27\u7F29\u7740\uFF0C\u6307\u5C16\u6309\u53BB\u7D27\u7EF7\u7EF7\u6CA1\u6709\u4E1D\u6BEB\u7F1D\u9699\u3002\u4ED6\u53EB\u8FC7\u4E39\u5A18\uFF0C\u8BA9\u5979\u4E5F\u4E00\u822C\u5730\u8DB4\u597D\u3002</p><p>\u4E39\u5A18\u7684\u5C41\u80A1\u4E30\u6EE1\u80A5\u7FD8\uFF0C\u6BD4\u7389\u83B2\u66F4\u5927\u4E5F\u66F4\u52A0\u5706\u7855\uFF0C\u81C0\u8089\u6ED1\u5AE9\u4E2D\u6709\u79CD\u6CB9\u8102\u822C\u7684\u817B\u611F\uFF0C\u7531\u4E8E\u88AB\u4EBA\u73A9\u5F04\u5F97\u4E45\u4E86\uFF0C\u808C\u80A4\u4E2D\u900F\u51FA\u767D\u4EAE\u7684\u6DEB\u8273\u5149\u6CFD\u3002</p><p>\u6BCD\u5973\u4FE9\u7528\u540C\u6837\u7684\u59FF\u52BF\u5934\u9888\u8D34\u4FEF\u5728\u5E2D\u4E0A\uFF0C\u62B1\u7740\u5C41\u80A1\u671D\u4E24\u8FB9\u6253\u5F00\uFF0C\u8BA9\u4EBA\u89C2\u8D4F\u5176\u4E2D\u7684\u8273\u666F\u3002</p><p>\u540C\u6837\u662F\u6392\u6CC4\u7684\u5668\u5B98\uFF0C\u4E39\u5A18\u7684\u5C41\u773C\u513F\u660E\u663E\u6BD4\u5973\u513F\u5927\u4E86\u4E00\u5708\uFF0C\u8DB3\u6709\u94DC\u94B1\u5927\u5C0F\u3002</p><p>\u5B59\u5929\u7FBD\u5E76\u8D77\u4E24\u6839\u624B\u6307\uFF0C\u8F7B\u8F7B\u4E00\u6345\u5C31\u63D2\u4E86\u8FDB\u53BB\uFF0C\u63A5\u7740\u4E00\u5206\uFF0C\u90A3\u53EA\u5C41\u773C\u513F\u8F7B\u6613\u5C31\u88AB\u6491\u5F00\u53D8\u5F62\uFF0C\u663E\u5F97\u67D4\u8F6F\u4E4B\u6781\u3002</p><p>\u5B59\u5929\u7FBD\u7B11\u9053\uFF1A\u300C\u5927\u6BCD\u72D7\u7684\u5C41\u773C\u513F\u88AB\u808F\u5F97\u8FD9\u4E48\u8F6F\u4E86\uFF1F\u8FDE\u62F3\u5934\u90FD\u80FD\u585E\u8FDB\u53BB\u5462\u3002\u300D</p><p>\u4E39\u5A18\u6BCF\u6B21\u88AB\u4ED6\u629A\u4F4F\u8EAB\u4F53\u90FD\u53D8\u5F97\u7279\u522B\u654F\u611F\uFF0C\u90A3\u4E24\u6839\u7075\u6D3B\u7684\u624B\u6307\u5728\u809B\u4E2D\u6405\u52A8\uFF0C\u4F7F\u5979\u6574\u4E2A\u5C41\u80A1\u90FD\u7981\u4E0D\u4F4F\u54C6\u55E6\u8D77\u6765\u3002\u5FFD\u7136\u624B\u6307\u4ECE\u809B\u4E2D\u62D4\u51FA\uFF0C\u5B59\u5929\u7FBD\u9053\uFF1A\u300C\u4F60\u53BB\u6252\u4F4F\u5C0F\u6BCD\u72D7\u7684\u5C41\u80A1\uFF0C\u6211\u6765\u7ED9\u5979\u5F00\u809B\u3002\u300D</p><p>\u4E39\u5A18\u6252\u5F00\u7389\u83B2\u7684\u5C41\u80A1\uFF0C\u5C06\u90A3\u53EA\u7D27\u63EA\u63EA\u7684\u5AE9\u809B\u66B4\u9732\u5728\u7C97\u5927\u7684\u9633\u5177\u4E0B\u3002\u5B59\u5929\u7FBD\u8089\u68D2\u5148\u540E\u5728\u6BCD\u5973\u4FE9\u9634\u5185\u63D2\u8FC7\uFF0C\u68D2\u8EAB\u6E7F\u6DCB\u6DCB\u4E5F\u5206\u4E0D\u6E05\u6CBE\u7684\u662F\u6BCD\u4EB2\u7684\u6DEB\u6C34\u8FD8\u662F\u5973\u513F\u7684\u4F53\u6DB2\u3002\u4ED6\u9F9F\u5934\u53C8\u9ED1\u53C8\u7EA2\uFF0C\u786C\u6886\u6886\u72B9\u5982\u77F3\u5B50\uFF0C\u76F4\u5F84\u6BD4\u7389\u83B2\u7684\u5C41\u773C\u513F\u5927\u4E86\u6570\u500D\uFF0C\u90A3\u53EA\u7C89\u7EA2\u7684\u5AE9\u809B\u6108\u53D1\u7684\u7EA4\u5F31\u53EF\u601C\u3002</p><p>\u4E39\u5A18\u5FFD\u7136\u4FEF\u4E0B\u8EAB\uFF0C\u5C06\u7389\u8138\u57CB\u5165\u5973\u513F\u81C0\u95F4\uFF0C\u7528\u820C\u5934\u55A5\u4E86\u9999\u553E\u8210\u5728\u5979\u5C41\u773C\u513F\u91CC\u3002</p><p>\u7389\u83B2\u4E0D\u77E5\u53D1\u751F\u4E86\u4EC0\u4E48\uFF0C\u53EA\u89C9\u5C41\u773C\u513F\u4E00\u9635\u9165\u75D2\uFF0C\u8EAB\u5B50\u987F\u65F6\u8F7B\u98A4\u8D77\u6765\u3002</p><p>\u5B59\u5929\u7FBD\u8170\u8EAB\u524D\u633A\uFF0C\u9633\u5177\u786C\u6485\u6485\u4F38\u8FDB\u767D\u5AE9\u7684\u7389\u81C0\u5185\uFF0C\u9F9F\u5934\u9876\u4F4F\u4E86\u83CA\u809B\uFF0C\u7528\u529B\u9876\u5165\u3002</p><p>\u7389\u83B2\u300C\u554A\u5440\u300D\u53EB\u51FA\u58F0\u6765\uFF0C\u4F46\u5979\u8170\u80A2\u88AB\u5B59\u5929\u7FBD\u62B1\u4F4F\uFF0C\u81C0\u8089\u53C8\u88AB\u5A18\u4EB2\u6252\u5F00\uFF0C\u6CA1\u6709\u4E1D\u6BEB\u8EB2\u907F\u7684\u4F59\u5730\u3002</p><p>\u5F80\u65E5\u5B59\u5929\u7FBD\u626F\u7740\u5A18\u4EB2\u809B\u4EA4\u5979\u4E5F\u89C1\u8FC7\uFF0C\u6BCF\u6B21\u90A3\u4E48\u5927\u7684\u9633\u5177\u6775\u8FDB\u53BB\uFF0C\u5A18\u4EB2\u90FD\u662F\u7709\u82B1\u773C\u7B11\uFF0C\u4E50\u5728\u5176\u4E2D\u7684\u6837\u5B50\u3002\u6CA1\u60F3\u5230\u5230\u4E86\u81EA\u5DF1\u8EAB\u4E0A\uFF0C\u7ADF\u4F1A\u662F\u5982\u6B64\u75DB\u695A\u3002</p><p>\u7389\u83B2\u75DB\u53EB\u7740\u557C\u54ED\u8D77\u6765\uFF0C\u9F9F\u5934\u521A\u5D4C\u5165\u81C0\u7F1D\uFF0C\u5C41\u773C\u513F\u5C31\u50CF\u88C2\u5F00\u822C\u5267\u75DB\u3002</p><p>\u4E39\u5A18\u5728\u65C1\u770B\u5F97\u6E05\u695A\uFF0C\u90A3\u53EA\u9ED1\u7EA2\u53D1\u4EAE\u7684\u9F9F\u5934\u4E00\u6324\uFF0C\u83CA\u809B\u5468\u56F4\u7EC6\u5BC6\u7684\u83CA\u7EB9\u7ACB\u5373\u6563\u5F00\uFF0C\u5F62\u6210\u4E00\u5708\u7EC6\u7EC6\u7684\u7EA2\u7EBF\u3002\u7389\u83B2\u8EAB\u5B50\u53D1\u6296\uFF0C\u5C41\u773C\u513F\u62DA\u547D\u6536\u7D27\uFF0C\u4F46\u90A3\u6839\u9633\u5177\u5374\u6CA1\u6709\u4E1D\u6BEB\u72B9\u7591\uFF0C\u7F13\u6162\u800C\u6BEB\u4E0D\u505C\u7559\u5730\u7B14\u76F4\u633A\u5165\u3002</p><p>\u83CA\u809B\u7684\u7EA2\u8089\u88AB\u5B8C\u5168\u6324\u5165\u4F53\u5185\uFF0C\u9633\u5177\u4E0E\u767D\u5AE9\u7684\u81C0\u8089\u76F8\u63A5\uFF0C\u7B14\u76F4\u63D2\u5728\u96EA\u6ED1\u7684\u81C0\u6C9F\u91CC\u3002\u5FFD\u7136\u4E00\u6EF4\u6BB7\u7EA2\u7684\u8840\u73E0\u51FA\u73B0\u5728\u9633\u5177\u4E0E\u81C0\u8089\u7ED3\u5408\u5904\uFF0C\u8D8A\u6765\u8D8A\u5927\uFF0C\u63A5\u7740\u4E00\u6643\uFF0C\u4ECE\u68D2\u8EAB\u4E0A\u90E8\u6EDA\u843D\uFF0C\u5212\u4E86\u4E2A\u5F27\u5F62\uFF0C\u6389\u843D\u5728\u81C0\u7F1D\u4E2D\u3002</p><p>\u4E39\u5A18\u54AC\u4F4F\u4E0B\u5507\uFF0C\u5FC3\u91CC\u63EA\u6210\u4E00\u56E2\u3002\u767D\u5B5D\u5112\u7BA1\u6559\u867D\u7136\u4E25\u5389\uFF0C\u4F46\u5BF9\u5973\u513F\u6781\u4E3A\u7231\u62A4\uFF0C\u4ECE\u5C0F\u5230\u5927\uFF0C\u7389\u83B2\u90FD\u672A\u53D7\u8FC7\u534A\u70B9\u5730\u59D4\u5C48\u3002\u4E3A\u4E86\u670D\u4F8D\u5B59\u5929\u7FBD\uFF0C\u5148\u662F\u88AB\u4ED6\u7834\u4F53\uFF0C\u6210\u4E86\u5987\u4EBA\uFF0C\u5982\u4ECA\u53C8\u5F00\u4E86\u540E\u5EAD\uFF0C\u4E24\u6B21\u89C1\u7EA2\uFF0C\u628A\u5973\u513F\u5A07\u602F\u602F\u7684\u8EAB\u5B50\u90FD\u7ED9\u4E86\u4ED6\u3002</p><p>\u5B59\u5929\u7FBD\u7C97\u786C\u7684\u9633\u5177\uFF0C\u76F4\u633A\u633A\u4ECE\u67D4\u5AE9\u7684\u5C41\u773C\u513F\u4E2D\u8D2F\u5165\uFF0C\u5F77\u4F5B\u4E00\u622A\u94C1\u68CD\u6345\u5165\u5C11\u5973\u767D\u5AE9\u7684\u5C41\u80A1\u91CC\u9762\u3002\u7389\u83B2\u540E\u5EAD\u6BD5\u7ADF\u662F\u7B2C\u4E00\u6B21\u5BB9\u7EB3\u8FD9\u6837\u7C97\u5927\u7684\u7269\u4F53\uFF0C\u867D\u7136\u4E39\u5A18\u5FC3\u7EC6\u5148\u8214\u7684\u6E7F\u4E86\uFF0C\u4F46\u83CA\u809B\u5DF2\u7ECF\u7EFD\u88C2\u3002\u9C9C\u8840\u8D70\u73E0\u822C\u6EDA\u51FA\uFF0C\u7389\u83B2\u75DB\u5F97\u82B1\u5BB9\u5931\u8272\uFF0C\u8FDE\u58F0\u5A07\u557C\u3002</p><p>\u5B59\u5929\u7FBD\u7B11\u7740\u5BF9\u4E39\u5A18\u8BF4\uFF1A\u300C\u77A7\u89C1\u4E86\u5417\uFF1F\u4F60\u5C41\u773C\u513F\u88AB\u6211\u5F00\u82DE\u65F6\uFF0C\u4E5F\u662F\u8FD9\u822C\u3002\u300D</p><p>\u4E39\u5A18\u53EA\u9053\uFF1A\u300C\u6162\u7740\u4E9B\uFF0C\u7389\u83B2\u75BC\u5F97\u7D27\u4E86\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u4E00\u76F4\u63D2\u5230\u6839\u90E8\uFF0C\u5C06\u6574\u6839\u9633\u5177\u90FD\u63D2\u8FDB\u7389\u83B2\u5C41\u773C\u513F\u91CC\uFF0C\u624D\u505C\u4E0B\u6765\uFF0C\u611F\u53D7\u5C11\u5973\u76F4\u80A0\u5185\u7684\u7D27\u5BC6\u548C\u6E29\u6696\u3002\u7389\u83B2\u989D\u5934\u5192\u51FA\u51B7\u6C57\uFF0C\u6EE1\u9762\u75DB\u695A\u3002</p><p>\u5B59\u5929\u7FBD\u7B11\u9053\uFF1A\u300C\u6211\u7ED9\u4F60\u5A18\u5C41\u773C\u513F\u5F00\u82DE\u7684\u65F6\u5019\uFF0C\u4F60\u5A18\u6D41\u4E86\u4E00\u5C41\u80A1\u8840\uFF0C\u8FD8\u6EE1\u8138\u5E26\u7B11\uFF0C\u8FD8\u6709\u4F60\u5A18\u59E8\u2026\u2026\u300D\u5B59\u5929\u7FBD\u8BF4\u5F97\u987A\u53E3\uFF0C\u672C\u60F3\u8BF4\uFF1A\u4F60\u5A18\u59E8\u5C41\u773C\u513F\u88AB\u6211\u5F00\u82DE\uFF0C\u8FD8\u5343\u6069\u4E07\u8C22\uFF0C\u5411\u6211\u9053\u559C\uFF0C\u8FDE\u5FD9\u6253\u4F4F\u4E86\u3002\u5E78\u597D\u4E39\u5A18\u6B63\u5FC3\u75BC\u5973\u513F\u5403\u82E6\uFF0C\u6CA1\u6709\u7559\u610F\u3002</p><p>\u5B59\u5929\u7FBD\u9633\u5177\u4E00\u62D4\uFF0C\u9F9F\u5934\u5C06\u6324\u8FDB\u4F53\u5185\u7684\u809B\u857E\u5E26\u4E86\u51FA\u6765\uFF0C\u53EA\u89C1\u4E00\u5708\u7EA2\u8089\u4ECE\u81C0\u6C9F\u5185\u731B\u7136\u7EFD\u5F00\uFF0C\u6E85\u51FA\u4E00\u4E32\u9C9C\u8840\u3002\u7389\u83B2\u75DB\u53EB\u4E00\u58F0\uFF0C\u6D51\u8EAB\u5267\u70C8\u5730\u98A4\u6296\u8D77\u6765\u3002\u5B59\u5929\u7FBD\u6309\u4F4F\u5979\u7684\u8170\uFF0C\u633A\u8EAB\u5728\u5979\u5C0F\u5DE7\u7684\u5C41\u773C\u513F\u91CC\u6233\u5F04\u8D77\u6765\u3002</p><p>\u7389\u83B2\u81C0\u95F4\u9C9C\u8840\u8D8A\u6D41\u8D8A\u591A\uFF0C\u4E0D\u591A\u65F6\u81C0\u6C9F\u5185\u4FBF\u88AB\u67D3\u5F97\u9C9C\u7EA2\u3002\u4E39\u5A18\u54AC\u7740\u5507\uFF0C\u6CEA\u6C34\u5728\u773C\u7736\u91CC\u6253\u8F6C\uFF0C\u53EA\u6015\u5973\u513F\u627F\u53D7\u4E0D\u4E86\u3002\u5B59\u5929\u7FBD\u7B11\u9053\uFF1A\u300C\u522B\u6015\uFF0C\u5973\u4EBA\u7684\u8EAB\u5B50\u4F60\u53C8\u4E0D\u662F\u4E0D\u77E5\uFF0C\u7B2C\u4E00\u6B21\u514D\u4E0D\u4E86\u8981\u5403\u4E9B\u82E6\u5934\uFF0C\u4F60\u540E\u5EAD\u4E0D\u8FD8\u89C1\u8FC7\u4E24\u6B21\u7EA2\u4E48\uFF1F\u73B0\u5728\u8FD8\u4E0D\u662F\u63D2\u8D77\u6765\u53C8\u8F6F\u53C8\u5F97\u8DA3\u3002\u300D</p><p>\u4E39\u5A18\u8BAA\u8BAA\u5730\u8BF4\u4E0D\u51FA\u8BDD\u6765\uFF0C\u4F46\u5FC3\u91CC\u4ECD\u63EA\u7740\u3002\u8FD9\u8FB9\u7389\u83B2\u51FA\u4E86\u4E00\u8EAB\u7684\u51B7\u6C57\uFF0C\u5979\u8EAB\u5B50\u672C\u6765\u5C31\u5F31\uFF0C\u6B64\u65F6\u53D7\u521B\u5931\u8840\uFF0C\u53C8\u5728\u592A\u9633\u4E0B\u6652\u5F97\u4E45\u4E86\uFF0C\u5FFD\u7136\u8EAB\u5B50\u4E00\u8F6F\uFF0C\u6655\u4E86\u8FC7\u53BB\u3002</p><p>\u4E39\u5A18\u60CA\u5F97\u9669\u4E9B\u6655\u5012\uFF0C\u5B59\u5929\u7FBD\u4E5F\u662F\u4E00\u6014\uFF0C\u5FD9\u6390\u7740\u7389\u83B2\u7684\u4EBA\u4E2D\uFF0C\u628A\u5979\u5F04\u9192\u3002\u7389\u83B2\u4E00\u624B\u63A9\u7740\u81C0\uFF0C\u8737\u7740\u8EAB\u5B50\u5624\u5624\u54ED\u6CE3\u3002\u4E39\u5A18\u5FE7\u5FC3\u5973\u513F\uFF0C\u53C8\u6015\u5B59\u5929\u7FBD\u626B\u5174\uFF0C\u5FD9\u9053\uFF1A\u300C\u76F8\u516C\uFF0C\u8BA9\u7389\u83B2\u6B47\u6B47\uFF0C\u6211\u6765\u966A\u4F60\u9AD8\u5174\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u9760\u5728\u6811\u6839\u4E0A\u9053\uFF1A\u300C\u4E5F\u6210\u3002\u4F60\u4EEC\u5A18\u513F\u4FE9\u8EAB\u4E0A\u516D\u4E2A\u6D1E\u6211\u73A9\u4E86\u4E94\u4E2A\uFF0C\u8FD8\u5269\u4E00\u4E2A\uFF0C\u4F60\u81EA\u5DF1\u4E0A\u6765\u5427\u3002\u300D</p><p>\u4E39\u5A18\u4E0D\u987E\u7F9E\u803B\u5730\u722C\u8D77\u8EAB\u6765\uFF0C\u5F20\u5F00\u817F\u8DEA\u5728\u5B59\u5929\u7FBD\u8170\u95F4\uFF0C\u4E00\u624B\u6491\u5F00\u81C0\u8089\uFF0C\u4E00\u624B\u6276\u7740\u90A3\u6839\u6EF4\u8840\u7684\u9633\u5177\uFF0C\u6485\u7740\u80A5\u767D\u7684\u5927\u5C41\u80A1\uFF0C\u628A\u9F9F\u5934\u9876\u8FDB\u809B\u6D1E\uFF0C\u626D\u7740\u5C41\u80A1\u5750\u4E86\u4E0B\u53BB\u3002</p><p>\u5728\u4E0A\u9762\u7528\u5C41\u773C\u513F\u5957\u5F04\uFF0C\u5E76\u4E0D\u5BB9\u6613\uFF0C\u4E39\u5A18\u4E24\u624B\u63B0\u7740\u5C41\u80A1\uFF0C\u8EAB\u5B50\u5FAE\u5FAE\u540E\u4EF0\uFF0C\u96EA\u81C0\u4E00\u4E0A\u4E00\u4E0B\uFF0C\u987A\u7740\u7B14\u76F4\u7684\u9633\u5177\u4E0A\u4E0B\u6ED1\u52A8\u3002</p><p>\u8FD9\u6837\u4E00\u6765\u4E0D\u4EC5\u5979\u4E00\u53CC\u80A5\u4E73\u5728\u80F8\u524D\u5F39\u8DF3\u4E0D\u5DF2\uFF0C\u9634\u6237\u4E5F\u6574\u4E2A\u66B4\u9732\u51FA\u6765\u3002\u5B59\u5929\u7FBD\u4F38\u51FA\u624B\uFF0C\u968F\u610F\u73A9\u5F04\u7740\u5979\u7684\u6027\u5668\uFF0C\u4E0D\u65F6\u4F38\u8FDB\u5979\u7A74\u5185\u6316\u5F04\u3002</p><p>\u4E39\u5A18\u53CC\u817F\u90FD\u8DEA\u5F97\u9EBB\u4E86\uFF0C\u5C41\u773C\u513F\u70ED\u8FA3\u8FA3\u88AB\u78E8\u5F97\u53D1\u80C0\uFF0C\u624D\u7EC8\u4E8E\u611F\u89C9\u5230\u8EAB\u4E0B\u7684\u7537\u5B50\u8EAB\u4F53\u6E10\u6E10\u7D27\u7EF7\u3002\u5979\u6B63\u5F85\u52A0\u529B\u5957\u5F04\uFF0C\u8BA9\u60C5\u90CE\u5728\u81EA\u5DF1\u4F53\u5185\u5C04\u7CBE\uFF0C\u5374\u5B59\u5929\u7FBD\u62B1\u5230\u4E00\u8FB9\u3002</p><p>\u5B59\u5929\u7FBD\u63B0\u5F00\u7389\u83B2\u8F6F\u7EF5\u7EF5\u7684\u7389\u817F\uFF0C\u4FEF\u8EAB\u63D2\u8FDB\u5979\u6E7F\u6ED1\u7684\u871C\u7A74\u91CC\uFF0C\u7D27\u7D27\u9876\u4F4F\u5979\u7684\u82B1\u5FC3\uFF0C\u5C06\u7CBE\u6DB2\u5C3D\u6570\u5C04\u5165\u5C11\u5973\u7A74\u5185\u3002\u4E39\u5A18\u521A\u62FF\u4E86\u6C57\u5DFE\u51C6\u5907\u62B9\u62ED\uFF0C\u5374\u89C1\u5B59\u5929\u7FBD\u9633\u5177\u53C8\u786C\u4E86\u8D77\u6765\u3002</p><p>\u5B59\u5929\u7FBD\u5C06\u5979\u6309\u5728\u5E2D\u4E0A\uFF0C\u987A\u52BF\u9876\u5165\u540E\u5EAD\uFF0C\u5728\u5979\u80A5\u767D\u7684\u5927\u5C41\u80A1\u91CC\u5927\u529B\u62BD\u9001\u8D77\u6765\u3002</p><p>\u8FD9\u4E00\u6B21\u4ED6\u4E0D\u518D\u505C\u6B47\uFF0C\u4E00\u53E3\u6C14\u5E72\u4E86\u534A\u67F1\u9999\u65F6\u95F4\uFF0C\u5728\u4E39\u5A18\u540E\u5EAD\u51FA\u4E86\u56DE\u3002</p><p>\u8FD9\u756A\u6DEB\u620F\u76F4\u5F04\u4E86\u4E00\u4E2A\u65F6\u8FB0\u6709\u4F59\uFF0C\u6BCD\u5973\u4FE9\u90FD\u88AB\u5E72\u7CBE\u75B2\u529B\u5C3D\uFF0C\u4E00\u4FEF\u4E00\u4EF0\uFF0C\u9634\u95F4\u809B\u5185\u767D\u82B1\u82B1\u7684\u90FD\u662F\u7CBE\u6DB2\u3002\u7389\u83B2\u7834\u4E86\u809B\uFF0C\u4EF0\u8EBA\u7740\u53CC\u817F\u95F4\u6D41\u4E39\u6EE1\u5E2D\uFF0C\u4E39\u5A18\u4FEF\u5728\u5E2D\u4E0A\uFF0C\u767D\u5AE9\u7684\u5C41\u80A1\u88AB\u63D2\u5F97\u5206\u5F00\uFF0C\u5C41\u773C\u513F\u5F20\u7740\u4E00\u4E2A\u5706\u5706\u7684\u7EA2\u6D1E\uFF0C\u767D\u6D4A\u7684\u7CBE\u6DB2\u6B63\u7F13\u7F13\u6D41\u51FA\u3002</p><p>\u5B59\u5929\u7FBD\u8FDE\u5C04\u4E24\u6B21\uFF0C\u4E5F\u6709\u4E86\u4E9B\u5026\u610F\uFF0C\u4ED6\u770B\u4E86\u770B\u708E\u70ED\u7684\u9633\u5149\uFF0C\u79BB\u884C\u8DEF\u5C1A\u65E9\uFF0C\u4FBF\u6402\u8FC7\u4E24\u5973\uFF0C\u6C89\u6C89\u7761\u53BB\u3002</p><p>\u8FDC\u5904\u4F20\u6765\u94C1\u94FE\u5728\u5730\u4E0A\u62D6\u62FD\u7684\u58F0\u97F3\u3002\u8FD8\u6709\u5728\u5899\u58C1\u95F4\u56DE\u8361\u7684\u559D\u9A82\u58F0\u3002\u7070\u8499\u8499\u7684\u5899\u4E0A\u6709\u9752\u8367\u7684\u706F\u5149\u6447\u66F3\uFF0C\u5F77\u4F5B\u4E00\u9635\u5FAE\u98CE\u5C31\u80FD\u8BA9\u5B83\u7184\u706D\u3002</p><p>\u7A7A\u6C14\u4E2D\u6709\u8840\u8165\u548C\u8150\u81ED\u7684\u6C14\u606F\uFF0C\u4ED6\u5F88\u5FEB\u8BA4\u51FA\uFF0C\u8FD9\u540C\u6837\u662F\u95F4\u76D1\u72F1\u3002\u94C1\u9501\u6728\u6805\u7684\u7262\u623F\u4E00\u95F4\u95F4\u6392\u5217\u7740\uFF0C\u4F38\u5411\u7070\u8499\u8499\u7684\u96FE\u4E2D\uFF0C\u4F3C\u4E4E\u6CA1\u6709\u5C3D\u5934\u3002</p><p>\u77F3\u4E0A\u6709\u9ED1\u8272\u7684\u8840\u8FF9\u3002\u5899\u6302\u7740\u4E00\u6392\u6392\u95EA\u7740\u5BD2\u5149\u7684\u5211\u5177\u3002\u604D\u60DA\u4E2D\uFF0C\u4E00\u4E32\u9762\u5B54\u4ECE\u773C\u524D\u95EA\u8FC7\uFF0C\u6709\u7684\u9AD8\u8D35\uFF0C\u6709\u7684\u534E\u7F8E\uFF0C\u6709\u7684\u79C0\u4E3D\uFF0C\u6709\u7684\u96C5\u81F4\uFF0C\u6709\u7684\u5996\u8273\uFF0C\u6709\u7684\u7CBE\u81F4\uFF0C\u4F46\u5979\u4EEC\u90FD\u6709\u7740\u540C\u6837\u4E00\u53CC\u5145\u6EE1\u6050\u60E7\u548C\u75DB\u695A\u7684\u773C\u775B\u3002</p><p>\u4ED6\u6B63\u5728\u5947\u602A\u8FD9\u91CC\u662F\u4E0D\u662F\u8C7A\u72FC\u5761\uFF0C\u5FFD\u7136\u542C\u5230\u949F\u58F0\uFF0C\u4E00\u62AC\u5934\uFF0C\u4E00\u5EA7\u9AD8\u5854\u51FA\u73B0\u5728\u89C6\u91CE\u4E2D\u3002\u90A3\u662F\u5EA7\u4E03\u5C42\u5B9D\u5854\uFF0C\u6BCF\u4E00\u5C42\u90FD\u6709\u516B\u4E2A\u6A90\u89D2\uFF0C\u6BCF\u4E2A\u6A90\u89D2\u90FD\u6311\u7740\u4E00\u4E32\u94DC\u94C3\uFF0C\u6BCF\u4E32\u94DC\u94C3\u90FD\u5728\u6DE1\u8840\u8272\u7684\u98CE\u4E2D\u6447\u52A8\uFF0C\u6EF4\u4E0B\u5E26\u8840\u7684\u94C3\u58F0\u3002</p><p>\u9192\u6765\u65F6\uFF0C\u65E5\u8272\u5DF2\u7ECF\u504F\u897F\uFF0C\u68A6\u5883\u4E5F\u5FD8\u4E86\u5927\u534A\u3002\u5B59\u5929\u7FBD\u770B\u4E86\u770B\u4ECD\u5728\u719F\u7761\u7684\u4E24\u5973\uFF0C\u51B7\u51B7\u7A7F\u4E86\u8863\u670D\uFF0C\u62FF\u4E0A\u5305\u88F9\uFF0C\u79BB\u5F00\u4E86\u674F\u82B1\u6751\u3002</p>',68);function t(_,c){return a}var o=e(r,[["render",t]]);export{o as default};
