import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_46-\u901A\u4E73" tabindex="-1"><a class="header-anchor" href="#_46-\u901A\u4E73" aria-hidden="true">#</a> 46 \u901A\u4E73</h1><p>\u5B59\u5929\u7FBD\u4E00\u53BB\u6570\u65E5\u6773\u65E0\u97F3\u4FE1\uFF0C\u76D1\u72F1\u8FD9\u8FB9\u7684\u98CE\u58F0\u5374\u662F\u8D8A\u6765\u8D8A\u6025\u3002\u4ED6\u8D70\u540E\u4E0D\u4E45\uFF0C\u53BF\u91CC\u5C31\u63A5\u4E86\u5E9C\u6587\uFF0C\u8BF4\u4F55\u6E05\u6CB3\u5DF2\u62B5\u8FBE\u6F6E\u5DDE\u5E9C\uFF0C\u6B63\u5728\u8C03\u9605\u767D\u5B5D\u5112\u901A\u532A\u8C0B\u9006\u4E00\u6848\u7684\u6240\u6709\u6587\u4E66\uFF0C\u547D\u53BF\u91CC\u5C01\u6863\u5F85\u52D8\u3002</p><p>\u53BF\u91CC\u63A5\u6587\u53C8\u63A8\u5230\u72F1\u91CC\uFF0C\u5218\u8FA8\u673A\u6253\u5F00\u4E00\u770B\uFF0C\u624B\u6307\u987F\u65F6\u50CF\u88AB\u706B\u70EB\u4E86\u4E00\u4E0B\u3002\u6CA1\u60F3\u5230\u4F55\u6E05\u6CB3\u4F1A\u6765\u8FD9\u4E48\u5FEB\uFF0C\u6F6E\u5DDE\u5230\u795E\u4ED9\u5CAD\u4E0D\u8FC7\u4E94\u516D\u65E5\u8DEF\u7A0B\uFF0C\u5C31\u7B97\u5728\u5E9C\u53BF\u6709\u6240\u803D\u6401\uFF0C\u516B\u4E5D\u65E5\u4E5F\u5C31\u5230\u4E86\u72F1\u4E2D\u3002\u5B59\u5929\u7FBD\u81F3\u4ECA\u8FD8\u5728\u5916\u94BB\u8425\u95E8\u8DEF\uFF0C\u82E5\u518D\u803D\u8BEF\uFF0C\u53EA\u6015\u662F\u6765\u4E0D\u53CA\u4E86\u3002</p><p>\u6700\u4EE4\u5218\u8FA8\u673A\u5934\u75BC\u7684\u8FD8\u662F\u767D\u96EA\u83B2\u3002\u767D\u96EA\u83B2\u62B5\u6B7B\u4E0D\u62DB\uFF0C\u72F1\u65B9\u53C8\u4E0D\u6562\u4E0B\u6BD2\u624B\u65BD\u5211\uFF0C\u6B63\u6CA1\u5904\u7406\u4F1A\u3002\u53EF\u7B11\u9C8D\u6A2A\u90A3\u53AE\u6574\u65E5\u95ED\u95E8\u4E0D\u51FA\uFF0C\u6240\u6709\u5FC3\u601D\u90FD\u82B1\u5728\u300C\u5BA1\u95EE\u300D\u65B0\u6765\u7684\u5973\u72AF\u4E0A\uFF0C\u5927\u7978\u4E34\u5934\u72B9\u4E0D\u81EA\u77E5\uFF0C\u8FD8\u4EE5\u4E3A\u81EA\u5DF1\u6709\u9760\u5C71\u5927\u53EF\u501A\u4ED7\u3002</p><p>\u5218\u8FA8\u673A\u7EDE\u5C3D\u8111\u6C41\u4E5F\u6CA1\u60F3\u51FA\u4E3B\u610F\uFF0C\u7D22\u6027\u4E0D\u518D\u53BB\u60F3\u3002\u4ED6\u62E3\u51FA\u5370\u8FC7\u767D\u5B5D\u5112\u6307\u6A21\u7684\u539F\u4F9B\uFF0C\u859B\u971C\u7075\u753B\u62BC\u7684\u8BC1\u8BCD\uFF0C\u8FDE\u540C\u72F1\u65B9\u67E5\u529E\u6848\u6863\u4E00\u5E76\u5305\u597D\uFF0C\u7B49\u4F55\u6E05\u6CB3\u6765\u65F6\u5F80\u4E0A\u4E00\u5448\uFF0C\u8981\u6740\u8981\u5250\u5C31\u542C\u5929\u7531\u547D\u4E86\u3002</p><p>\u8F6C\u773C\u5DF2\u7ECF\u662F\u5B59\u5929\u7FBD\u8D70\u540E\u7684\u7B2C\u5341\u65E5\uFF0C\u53BF\u91CC\u4F20\u6765\u6D88\u606F\uFF0C\u8BF4\u4F55\u6E05\u6CB3\u5DF2\u7ECF\u8D77\u8EAB\uFF0C\u660E\u540E\u65E5\u4FBF\u62B5\u8FBE\u5B81\u8FDC\u53BF\u3002\u8FD9\u4E00\u4E0B\u6240\u6709\u72F1\u5352\u518D\u90FD\u5750\u4E0D\u4F4F\u4E86\uFF0C\u7EA6\u597D\u4E86\u4E00\u8D77\u6765\u5BFB\u9C8D\u6A2A\uFF0C\u591A\u5C11\u5546\u91CF\u51FA\u4E2A\u4E3B\u610F\u3002</p><p>\u4F17\u4EBA\u90FD\u6653\u5F97\u9C8D\u6A2A\u5F04\u4E86\u4E2A\u5973\u72AF\u5728\u623F\u91CC\u80E1\u95F9\uFF0C\u4F46\u9664\u4E86\u8DDF\u4ED6\u6700\u4EB2\u8FD1\u7684\u9648\u6CF0\uFF0C\u8C01\u4E5F\u672A\u66FE\u89C1\u8FC7\u3002\u9648\u6CF0\u8BB2\u5F97\u5929\u82B1\u4E71\u5760\uFF0C\u8BF4\u90A3\u5973\u5B50\u4E0D\u4F46\u751F\u5F97\u6807\u81F4\uFF0C\u800C\u4E14\u517C\u6709\u5185\u5A9A\u3002\u4E0D\u7BA1\u9E21\u5DF4\u591A\u8F6F\uFF0C\u8BA9\u5979\u5C0F\u5634\u4E00\u5439\uFF0C\u7ACB\u9A6C\u53C8\u786C\u4E86\u8D77\u6765\u3002\u4E0B\u8FB9\u4E24\u4E2A\u8089\u6D1E\u66F4\u662F\u6CA1\u5F97\u8BF4\u3002</p><p>\u9648\u6CF0\u624B\u6BD4\u53E3\u8BB2\uFF0C\u9F13\u7740\u773C\u8BF4\u5F97\u53E3\u6CAB\u6A2A\u98DE\u3002\u90A3\u5973\u72AF\u7EC6\u76AE\u5AE9\u8089\uFF0C\u770B\u4E0A\u53BB\u50CF\u662F\u5927\u6237\u4EBA\u5BB6\u51FA\u6765\u7684\u5C11\u5976\u5976\uFF0C\u6BD4\u7A91\u5B50\u91CC\u6700\u5EC9\u4EF7\u7684\u5A4A\u5B50\u8FD8\u8D31\u3002\u8BA9\u9C8D\u6A2A\u5F04\u8D77\u6765\u5C31\u8DDF\u6D17\u811A\u76C6\u4F3C\u7684\uFF0C\u60F3\u600E\u4E48\u7CDF\u8DF5\u5C31\u600E\u4E48\u7CDF\u8DF5\u3002\u8BF4\u5F97\u4F17\u4EBA\u5FC3\u91CC\u90FD\u76F4\u75D2\u75D2\uFF0C\u4E03\u53E3\u516B\u820C\u9A82\u8FD9\u9C8D\u6A2A\u4E0D\u4ED7\u4E49\uFF0C\u88AB\u7A9D\u91CC\u653E\u5C41\uFF0C\u72EC\u541E\u3002</p><p>\u4F17\u4EBA\u62C9\u4E0A\u5218\u8FA8\u673A\uFF0C\u6572\u677F\u6253\u95E8\u628A\u9C8D\u6A2A\u53EB\u8D77\u6765\u3002\u95E8\u4E00\u5F00\uFF0C\u53EA\u89C1\u9C8D\u6A2A\u90A3\u5F20\u9752\u767D\u9762\u76AE\u9EC4\u8721\u8721\uFF0C\u8EAB\u4F53\u865A\u5F97\u8D70\u8DEF\u90FD\u98D8\u5FFD\u3002\u4F17\u4EBA\u5FC3\u91CC\u6709\u706B\uFF0C\u56B7\u9053\uFF1A\u300C\u7ED9\u9C8D\u5927\u4EBA\u9053\u559C\u554A\uFF0C\u6C14\u8272\u8FD9\u4E48\u597D\uFF0C\u662F\u4E0D\u662F\u7968\u62DF\u5FEB\u4E0B\u6765\u4E86\uFF1F\u300D\u8BF4\u7740\u4E00\u7A9D\u8702\u6324\u8FDB\u540E\u5802\u3002</p><p>\u540E\u5802\u95E8\u7A97\u4E5F\u4E0D\u77E5\u51E0\u5929\u6CA1\u5F00\u4E86\uFF0C\u9664\u4E86\u9648\u6CF0\u5DF4\u7ED3\u7740\u6765\u9001\u996D\uFF0C\u5E73\u65F6\u90FD\u7D27\u5173\u7740\uFF0C\u4E00\u8FDB\u5C4B\u5C31\u95FB\u5230\u7A7A\u6C14\u4E2D\u6D53\u91CD\u7684\u7CBE\u6DB2\u5473\u9053\uFF0C\u53C8\u8165\u53C8\u81ED\u3002\u4F17\u4EBA\u4ED4\u7EC6\u770B\u65F6\uFF0C\u53EA\u89C1\u5E8A\u811A\u62F4\u7740\u4E00\u6839\u94C1\u94FE\uFF0C\u94C1\u94FE\u672B\u7AEF\u4E00\u76F4\u5EF6\u4F38\u5230\u5E8A\u4E0A\u7684\u88AB\u5B50\u91CC\u9762\uFF0C\u88AB\u5B50\u91CC\u9F13\u56CA\u56CA\u4E0D\u77E5\u85CF\u7740\u4EC0\u4E48\u3002</p><p>\u300C\u505A\u4EC0\u4E48\u5462\uFF1F\u505A\u4EC0\u4E48\u5462\uFF1F\u6709\u8BDD\u90FD\u51FA\u6765\u8BF4\uFF01\u300D\u9C8D\u6A2A\u6446\u51FA\u67B6\u5B50\uFF0C\u8981\u5F80\u5C4B\u91CC\u6324\u3002</p><p>\u4F17\u4EBA\u7406\u90FD\u4E0D\u7406\uFF0C\u5F84\u76F4\u6324\u8FC7\u53BB\u6380\u5F00\u88AB\u5B50\u3002\u53EA\u89C1\u773C\u524D\u4E00\u4EAE\uFF0C\u9732\u51FA\u4E00\u5177\u767D\u82B1\u82B1\u7684\u8089\u4F53\u3002\u90A3\u5973\u5B50\u9888\u4E2D\u62F4\u7740\u94C1\u94FE\uFF0C\u773C\u775B\u8499\u7740\uFF0C\u5634\u5DF4\u585E\u7740\uFF0C\u4E24\u624B\u6346\u5728\u80CC\u540E\uFF0C\u5C41\u80A1\u91CC\u63D2\u7740\u534A\u622A\u6728\u68CD\uFF0C\u4E5F\u4E0D\u77E5\u9C8D\u6A2A\u5728\u5F04\u4EC0\u4E48\u6DEB\u620F\u3002</p><p>\u4F55\u6C42\u56FD\u602A\u58F0\u602A\u6C14\u5730\u8BF4\u9053\uFF1A\u300C\u54DF\uFF0C\u9C8D\u5927\u4EBA\uFF0C\u60A8\u8FD9\u662F\u5531\u7684\u54EA\u51FA\u554A\uFF1F\u300D</p><p>\u8D75\u9738\u773C\u91CC\u5FEB\u55B7\u51FA\u706B\u6765\uFF0C\u56B7\u9053\uFF1A\u300C\u4F60\u5E72\u4E0D\u52A8\u8FD8\u6709\u8FD9\u4E48\u591A\u5144\u5F1F\uFF0C\u62FF\u6839\u6728\u68CD\u513F\u778E\u6BD4\u5212\u5565\u5462\uFF1F\u300D</p><p>\u9C8D\u6A2A\u8138\u4E0A\u4E00\u9635\u9752\u4E00\u9635\u767D\uFF0C\u633A\u7740\u8170\u8BF4\u9053\uFF1A\u300C\u600E\u4E48\u7740\u600E\u4E48\u7740\uFF1F\u6709\u4F60\u4EEC\u8FD9\u53F7\u4E71\u95EF\u7684\u5417\uFF1F\u60F3\u4F5C\u53CD\uFF1F\u300D</p><p>\u4ED6\u8FD9\u4E00\u4E0B\u89E6\u4E86\u4F17\u6012\uFF0C\u5F53\u65F6\u5C31\u6709\u4EBA\u56B7\u9053\uFF1A\u300C\u7968\u62DF\u8FD8\u6CA1\u4E0B\u6765\uFF0C\u8FD8\u771F\u4EE5\u4E3A\u81EA\u5DF1\u5F53\u4E86\u72F1\u6B63\uFF1F\u7B97\u4E2A\u4EC0\u4E48\u4E1C\u897F\uFF01\u300D</p><p>\u9648\u6CF0\u89C1\u9C8D\u6A2A\u8FD8\u8981\u518D\u56B7\uFF0C\u5FD9\u8FC7\u6765\u6253\u4E86\u5706\u573A\uFF0C\u300C\u8FD9\u4E0D\u51E0\u5929\u6CA1\u89C1\u4F60\u4E86\uFF0C\u5927\u4F19\u513F\u6765\u770B\u770B\uFF0C\u6CA1\u522B\u7684\u610F\u601D\u3002\u300D</p><p>\u7389\u5A18\u773C\u4E0D\u80FD\u89C1\uFF0C\u53E3\u4E0D\u80FD\u8BF4\uFF0C\u542C\u5230\u5FFD\u7136\u8FDB\u6765\u4E00\u5927\u7FA4\u4EBA\uFF0C\u5413\u5F97\u745F\u745F\u53D1\u6296\u3002\u65C1\u8FB9\u6709\u4EBA\u56B7\u9053\uFF1A\u300C\u5927\u767D\u5929\u5C4B\u91CC\u85CF\u4E2A\u5A4A\u5B50\uFF0C\u5144\u5F1F\u4EEC\u770B\u770B\u90FD\u4E0D\u884C\uFF1F\u300D\u63A5\u7740\u51E0\u53EA\u624B\u4F38\u4E86\u8FC7\u6765\uFF0C\u5728\u5979\u4E73\u4E0A\u81C0\u4E0A\u80E1\u4E71\u6293\u634F\u3002</p><p>\u5218\u8FA8\u673A\u5FD9\u9053\uFF1A\u300C\u5927\u4F19\u513F\u90FD\u4F4F\u624B\uFF01\u542C\u6211\u8BF4\uFF01\u300D</p><p>\u4F17\u4EBA\u60BB\u60BB\u505C\u4E0B\u624B\uFF0C\u659C\u773C\u770B\u7740\u9C8D\u6A2A\u3002\u5218\u8FA8\u673A\u9053\uFF1A\u300C\u72AF\u4E0D\u7740\u4E3A\u4E2A\u5A4A\u5B50\u4F24\u4E86\u548C\u6C14\uFF0C\u773C\u4E0B\u6709\u6869\u5927\u4E8B\uFF0C\u5144\u5F1F\u4EEC\u6765\u627E\u4F60\u5546\u91CF\u3002\u5148\u51FA\u53BB\u518D\u8BF4\u3002\u300D</p><p>\u5230\u4E86\u5916\u9762\uFF0C\u5218\u8FA8\u673A\u8BB2\u4E86\u4F55\u6E05\u6CB3\u9A6C\u4E0A\u5C31\u8981\u5230\u72F1\u91CC\uFF0C\u300C\u8FD9\u6848\u5B50\u6211\u4E5F\u4E0D\u7528\u591A\u8BF4\uFF0C\u5927\u4F19\u513F\u90FD\u6709\u4EFD\uFF0C\u8C01\u4E5F\u522B\u60F3\u6495\u63B3\u6E05\u767D\uFF0C\u8FD8\u662F\u60F3\u4E2A\u4E3B\u610F\uFF0C\u7CCA\u5F04\u8FC7\u53BB\u3002\u300D</p><p>\u300C\u767D\u96EA\u83B2\u8FD8\u4E0D\u62DB\uFF1F\u300D</p><p>\u300C\u6CD5\u5B50\u90FD\u4F7F\u5C3D\u4E86\uFF0C\u90A3\u5A4A\u5B50\u54AC\u7740\u7259\u6B7B\u633A\u3002\u8FD9\u4E9B\u5929\u5144\u5F1F\u4EEC\u90FD\u6CA1\u95F2\u7740\uFF0C\u4E00\u76F4\u8F6E\u6D41\u5986\u626E\u4E86\uFF0C\u5957\u5979\u7684\u8BDD\uFF0C\u4E5F\u6CA1\u4EC0\u4E48\u6536\u6548\u3002\u300D</p><p>\u9C8D\u6A2A\u95EE\u4E86\u4E00\u53E5\uFF0C\u5472\u7740\u7259\u4E0D\u518D\u4F5C\u58F0\u3002\u4F55\u6C42\u56FD\u9053\uFF1A\u300C\u771F\u4E0D\u884C\u5C31\u628A\u5979\u505A\u4E86\uFF01\u62A5\u4E2A\u66B4\u75C5\u8EAB\u4EA1\u3002\u5904\u5206\u54B1\u4EEC\u5927\u4F19\u625B\u7740\uFF0C\u7EDD\u4E0D\u8BA9\u8C01\u5403\u4E8F\u3002\u300D</p><p>\u300C\u6211\u770B\u884C\u3002\u300D\u8D75\u9738\u4E5F\u9053\uFF0C\u300C\u5F04\u6B7B\u5979\uFF01\u4E00\u4E86\u767E\u4E86\u3002\u300D</p><p>\u4F17\u4EBA\u8BAE\u8BBA\u7EB7\u7EB7\uFF0C\u540C\u610F\u7684\u5012\u662F\u5360\u4E86\u516B\u6210\u3002\u5218\u8FA8\u673A\u7B49\u4E0D\u5230\u5B59\u5929\u7FBD\u7684\u6D88\u606F\uFF0C\u5FC3\u91CC\u53D1\u6025\uFF0C\u601D\u91CF\u7740\u771F\u8981\u4E0D\u884C\uFF0C\u4E5F\u53EA\u597D\u5982\u6B64\u4E86\u3002</p><p>\u9648\u6CF0\u8D34\u5728\u9C8D\u6A2A\u7684\u8033\u8FB9\u8BF4\u4E86\u534A\u5929\uFF0C\u7136\u540E\u9053\uFF1A\u300C\u6211\u5012\u6709\u4E2A\u4E3B\u610F\uFF0C\u767D\u96EA\u83B2\u4E0D\u662F\u4E0D\u62DB\u5417\uFF1F\u54B1\u4EEC\u5E72\u8106\u628A\u5979\u5A18\u3001\u59B9\u5B50\u90FD\u5F04\u5230\u72F1\u91CC\u6765\uFF0C\u5728\u5979\u4EEC\u8EAB\u4E0A\u7528\u5211\uFF0C\u770B\u5979\u62DB\u662F\u4E0D\u62DB\uFF01\u300D</p><p>\u5353\u5929\u96C4\u51B7\u51B7\u9053\uFF1A\u300C\u522B\u5FD8\u4E86\u767D\u82F1\u83B2\u5C31\u5728\u72F1\u91CC\uFF0C\u4F60\u770B\u5979\u6709\u6CA1\u6709\u5403\u8F6F\u3002\u300D</p><p>\u300C\u90A3\u662F\u6CA1\u6709\u52A8\u5211\uFF01\u5C31\u7B97\u767D\u82F1\u83B2\u4E0D\u884C\uFF0C\u4E39\u5A18\u662F\u5979\u4EB2\u5A18\uFF0C\u54B1\u4EEC\u628A\u5979\u5F04\u8FC7\u6765\uFF0C\u5F53\u7740\u767D\u96EA\u83B2\u7684\u9762\u524D\u4F7F\u51FA\u624B\u6BB5\u5F04\u4E0A\u4E00\u906D\uFF0C\u6211\u5C31\u4E0D\u4FE1\u5979\u8FD8\u80FD\u633A\u4F4F\u3002\u300D</p><p>\u5218\u8FA8\u673A\u62BD\u7740\u6C34\u70DF\u60F3\u4E86\u534A\u664C\uFF0C\u300C\u542C\u8BF4\u9C8D\u5927\u4EBA\u5C4B\u91CC\u7684\u662F\u767D\u96EA\u83B2\u7684\u5AE1\u4EB2\u5A18\u59E8\uFF0C\u4E0D\u5982\u62FF\u5979\u8BD5\u8BD5\u3002\u300D</p><p>\u9C8D\u6A2A\u73A9\u5F97\u6B63\u5728\u5174\u5934\u4E0A\uFF0C\u95FB\u8A00\u987F\u65F6\u8001\u5927\u7684\u4E0D\u60C5\u613F\uFF0C\u6897\u7740\u8116\u5B50\u9053\uFF1A\u300C\u4E0D\u53EA\u662F\u8FD9\u5C4B\u91CC\u6709\u4EBA\u5427\uFF1F\u5218\u592B\u5B50\u5C4B\u91CC\u90A3\u5C0F\u5154\u5D3D\u5B50\uFF0C\u662F\u4E0D\u662F\u4E0D\u820D\u5F97\uFF1F\u300D</p><p>\u8FD9\u8BDD\u4E00\u51FA\uFF0C\u4F17\u4EBA\u53C8\u5435\u56B7\u8D77\u6765\uFF0C\u6B63\u95F9\u5F97\u4E0D\u53EF\u5F00\u4EA4\uFF0C\u6709\u4EBA\u8BF4\u9053\uFF1A\u300C\u90FD\u5728\u8FD9\u513F\u5462\uFF0C\u8BF4\u4EC0\u4E48\u5462\uFF1F\u8FD9\u4E48\u70ED\u95F9\uFF1F\u300D</p><p>\u5802\u4E2D\u5012\u6709\u4E00\u591A\u534A\u7684\u4EBA\u559C\u5F62\u4E8E\u8272\uFF0C\u300C\u5929\u7FBD\uFF1F\u8FD9\u51E0\u5929\u8DD1\u54EA\u513F\u53BB\u4E86\uFF1F\u300D</p><p>\u5B59\u5929\u7FBD\u8138\u8272\u5982\u5E38\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u51FA\u95E8\u901B\u4E86\u901B\u3002\u5218\u592B\u5B50\u5462\uFF1F\u300D</p><p>\u9C8D\u6A2A\u5FC3\u80F8\u72ED\u7A84\uFF0C\u89C1\u7740\u4F17\u4EBA\u7EB7\u7EB7\u8D77\u8EAB\u53BB\u8FCE\u5B59\u5929\u7FBD\uFF0C\u4E0D\u7531\u6CDB\u4E86\u918B\u5473\uFF0C\u518D\u89C1\u5230\u5B59\u5929\u7FBD\u4E5F\u4E0D\u6765\u62DC\u89C1\u4ED6\u8FD9\u300C\u4E3B\u5B98\u300D\uFF0C\u5FC3\u91CC\u66F4\u4E0D\u4E50\u610F\u3002\u4ED6\u672C\u6765\u8111\u5B50\u5C31\u7F3A\u6839\u5F26\uFF0C\u8FD9\u4F1A\u513F\u9178\u706B\u4E0A\u6765\uFF0C\u4E00\u62CD\u684C\u5B50\uFF0C\u559D\u9053\uFF1A\u300C\u5B59\u5929\u7FBD\uFF01\u4F60\u64C5\u79BB\u804C\u5B88\uFF0C\u773C\u91CC\u8FD8\u6709\u6CA1\u6709\u738B\u6CD5\uFF01\u300D</p><p>\u5B59\u5929\u7FBD\u60C5\u7EEA\u672C\u6765\u5C31\u6B20\u4F73\uFF0C\u8FD9\u4F1A\u513F\u7A9D\u7740\u5FC3\u4E8B\uFF0C\u4E5F\u4E0D\u8010\u70E6\u88C5\u8154\u4F5C\u52BF\uFF0C\u89C1\u4ED6\u4E0A\u6765\u5C31\u6311\u523A\uFF0C\u5F53\u4E0B\u6A2A\u7709\u9053\uFF1A\u300C\u95ED\u5634\uFF01\u54EA\u513F\u6709\u4F60\u8FD9\u8349\u5305\u8BF4\u8BDD\u7684\u4EFD\u513F\uFF01\u300D</p><p>\u9C8D\u6A2A\u4E0D\u77E5\u8FDB\u9000\u60EF\u4E86\uFF0C\u4E00\u5411\u628A\u5B59\u5929\u7FBD\u5F53\u4E86\u8F6F\u67FF\u5B50\uFF0C\u88AB\u4ED6\u559D\u4E86\u4E00\u58F0\uFF0C\u987F\u65F6\u7D2B\u6DA8\u4E86\u9762\u76AE\uFF0C\u6284\u8D77\u8336\u76CF\u5C31\u671D\u4ED6\u6254\u6765\u3002\u5353\u5929\u96C4\u4E00\u638C\u62CD\u6389\u8336\u676F\uFF0C\u987A\u624B\u7ED9\u4E86\u9C8D\u6A2A\u4E00\u4E2A\u8033\u5149\uFF0C\u9A82\u9053\uFF1A\u300C\u5E9F\u4EC0\u4E48\u8BDD\u5462\uFF1F\u300D</p><p>\u9C8D\u6A2A\u5288\u9762\u6328\u4E86\u4E00\u638C\uFF0C\u987F\u65F6\u6655\u4E86\u8FC7\u53BB\uFF0C\u65C1\u8FB9\u8DDF\u4ED6\u4EA4\u597D\u7684\u51E0\u4E2A\u90FD\u5664\u4E86\u53E3\uFF0C\u4E0D\u6562\u4F5C\u58F0\u3002</p><p>\u5B59\u5929\u7FBD\u5F90\u5F90\u9053\uFF1A\u300C\u5218\u592B\u5B50\uFF0C\u5353\u4E8C\u54E5\uFF0C\u6211\u4EEC\u51FA\u6765\u8BF4\u8BDD\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5B59\u5929\u7FBD\u4E00\u8EAB\u7684\u98CE\u5C18\u4EC6\u4EC6\uFF0C\u8138\u8272\u9634\u6C89\u3002\u5218\u8FA8\u673A\u547C\u565C\u547C\u565C\u5438\u7740\u6C34\u70DF\u888B\uFF0C\u6D51\u6CA1\u6CE8\u610F\u70DF\u5DF2\u7ECF\u7184\u4E86\u3002\u5353\u5929\u96C4\u62B1\u7740\u80A9\u8180\uFF0C\u76EE\u5149\u95EA\u95EA\u5730\u770B\u7740\u5B59\u5929\u7FBD\uFF0C\u4E5F\u4E0D\u4F5C\u58F0\u3002</p><p>\u5218\u8FA8\u673A\u54B3\u55FD\u4E00\u58F0\uFF0C\u300C\u5B59\u5144\uFF0C\u53EF\u662F\u4E8B\u4F53\u4E0D\u534F\uFF1F\u300D</p><p>\u5B59\u5929\u7FBD\u82E6\u7B11\u4E00\u58F0\uFF0C\u8BF4\u9053\uFF1A\u300C\u6211\u5728\u9F99\u6E90\u5446\u4E86\u516D\u5929\uFF0C\u90A3\u70B9\u513F\u8D22\u7269\u6296\u843D\u4E86\u4E2A\u5E72\u51C0\uFF0C\u8FDE\u4EBA\u90FD\u6CA1\u89C1\u5230\u3002\u300D</p><p>\u5218\u5353\u4E8C\u4EBA\u867D\u7136\u65E9\u77E5\u5982\u6B64\uFF0C\u8FD8\u662F\u5B58\u7740\u4E00\u4E1D\u4FA5\u5E78\uFF0C\u95EE\u9053\uFF1A\u300C\u960E\u5927\u4EBA\u4E0D\u662F\u4E0E\u4ED6\u66FE\u6709\u6545\u4EA4\uFF0C\u90A3\u6298\u6247\u2026\u2026\u300D</p><p>\u300C\u4EC0\u4E48\u6545\u4EA4\uFF0C\u6211\u4EEC\u90FD\u4E0A\u4E86\u960E\u7F57\u671B\u7684\u5F53\u4E86\uFF0C\u6211\u53BB\u95E8\u4E0B\u4E00\u95EE\uFF0C\u4EBA\u5BB6\u8FDE\u542C\u90FD\u6CA1\u542C\u8BF4\u8FC7\uFF0C\u90A3\u6298\u6247\u66F4\u662F\u72D7\u5C41\u4E0D\u503C\uFF0C\u4E5F\u4E0D\u77E5\u4ED6\u662F\u54EA\u513F\u5BFB\u6765\u7684\uFF0C\u9012\u8FDB\u53BB\u968F\u624B\u90FD\u6254\u4E86\u51FA\u6765\u3002\u300D</p><p>\u4E24\u4EBA\u54D1\u53E3\u65E0\u8A00\u3002\u826F\u4E45\uFF0C\u5218\u8FA8\u673A\u53F9\u9053\uFF1A\u300C\u5BD2\u5BB6\u6570\u4EE3\u90FD\u662F\u5E08\u7237\uFF0C\u5F80\u6765\u8859\u95E8\uFF0C\u5BB6\u7236\u79C1\u4E0B\u8BF4\u8FC7\uFF0C\u8C0B\u9006\u6848\u4E0D\u80FD\u5403\u9ED1\uFF0C\u56E0\u5176\u5584\u540E\u4E0D\u6613\uFF0C\u4E07\u4E00\u8D25\u9732\u5C31\u96BE\u4EE5\u6536\u573A\u3002\u767D\u5B5D\u5112\u8FD9\u6848\u5B50\u6211\u5F53\u521D\u4E5F\u529D\u8FC7\u960E\u5927\u4EBA\uFF0C\u4F46\u60F3\u7740\u767D\u5BB6\u72EC\u5C45\u5C71\u4E2D\uFF0C\u53C8\u6709\u4E66\u4FE1\u8BC1\u7269\uFF0C\u767D\u5B5D\u5112\u4E00\u6B7B\uFF0C\u5269\u4E0B\u51E0\u4E2A\u5987\u4EBA\u5B7A\u5B50\uFF0C\u4E0D\u4F1A\u917F\u6210\u5927\u60A3\u3002\u8C01\u77E5\u2026\u2026\u300D</p><p>\u5353\u5929\u96C4\u9053\uFF1A\u300C\u73B0\u5728\u4E5F\u672A\u5FC5\u5C31\u662F\u7EDD\u8DEF\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u63C9\u4E86\u63C9\u8138\uFF0C\u4ED6\u8D76\u4E86\u4E00\u65E5\u4E00\u591C\u7684\u8DEF\uFF0C\u8DEF\u8FC7\u674F\u82B1\u6751\u4E5F\u672A\u505C\u7559\uFF0C\u76F4\u63A5\u8D76\u56DE\u72F1\u4E2D\uFF0C\u627E\u4E24\u4EBA\u5546\u91CF\uFF0C\u6B64\u523B\u5DF2\u7ECF\u56F0\u5F97\u7D27\u4E86\u3002\u4ED6\u6253\u70B9\u7CBE\u795E\u9053\uFF1A\u300C\u6211\u8FD9\u6B21\u53BB\u9F99\u6E90\uFF0C\u6B63\u8D76\u4E0A\u519B\u524D\u6377\u62A5\uFF0C\u767D\u83B2\u6559\u9006\u532A\u5DF2\u88AB\u8BA8\u5E73\uFF0C\u751F\u64D2\u9006\u532A\u5343\u4F59\u3002\u770B\u6765\u8FD9\u6848\u5B50\u5DF2\u7ECF\u62D6\u4E0D\u4E0B\u53BB\uFF0C\u5C31\u8981\u7ED3\u6848\u4E86\u3002\u300D</p><p>\u5218\u8FA8\u673A\u9053\uFF1A\u300C\u8FD9\u4E48\u8BF4\u6765\uFF0C\u5C01\u516C\u516C\u4E5F\u8981\u56DE\u4EAC\u5E08\u4E86\uFF1F\u300D</p><p>\u5F53\u521D\u4E09\u4EBA\u53CD\u590D\u5546\u8BAE\uFF0C\u767D\u96EA\u83B2\u6B7B\u4E0D\u62DB\u4F9B\uFF0C\u8FD9\u6848\u5B50\u60F3\u7792\u8FC7\u4F55\u6E05\u6CB3\u5343\u96BE\u4E07\u96BE\u3002\u65E2\u7136\u7792\u5929\u8FC7\u6D77\u4E0D\u6210\uFF0C\u5B59\u5929\u7FBD\u53E6\u8F9F\u6B67\u5F84\uFF0C\u60F3\u51FA\u4E00\u62DB\u501F\u5200\u6740\u4EBA\u3002\u5982\u4ECA\u4E24\u5E7F\u6700\u6709\u6743\u52BF\u7684\u4E0D\u662F\u5E03\u653F\u4F7F\u6309\u5BDF\u4F7F\uFF0C\u800C\u662F\u5750\u9547\u9F99\u6E90\u7684\u4E1C\u5382\u4E8C\u54C1\u526F\u90FD\u592A\u76D1\u5C01\u516C\u516C\u3002</p><p>\u960E\u7F57\u671B\u5439\u5618\u8BF4\u4E0E\u5C01\u516C\u516C\u662F\u6545\u4EA4\uFF0C\u4ED6\u53BB\u9F99\u6E90\u767B\u95E8\u62DC\u8BBF\uFF0C\u5C01\u516C\u516C\u8D60\u91D1\u9001\u6247\uFF0C\u7740\u5B9E\u770B\u5F97\u8D77\u4ED6\u3002\u8C01\u77E5\u90FD\u662F\u865A\u8A00\u3002</p><p>\u6B64\u523B\u60F3\u60F3\uFF0C\u82E5\u960E\u7F57\u671B\u771F\u4E0E\u5C01\u516C\u516C\u6545\u4EA4\uFF0C\u8FD8\u7528\u5F97\u7740\u4E3A\u8FD9\u6848\u5B50\u7EDE\u5C3D\u8111\u6C41\uFF1F\u6709\u8FD9\u4F4D\u526F\u90FD\u592A\u76D1\u51FA\u9762\uFF0C\u4EC0\u4E48\u6CE2\u5929\u5927\u6848\u4E5F\u90FD\u6309\u4E0B\u4E86\u3002\u4E09\u4EBA\u54EA\u77E5\u9053\u8FD9\u662F\u960E\u7F57\u671B\u5439\u725B\uFF0C\u8BBE\u8BA1\u628A\u960E\u7F57\u671B\u9057\u7559\u7684\u8D22\u7269\u90FD\u7A83\u4E86\u6765\uFF0C\u7531\u5B59\u5929\u7FBD\u62FF\u4E86\uFF0C\u53BB\u9F99\u6E90\u8D70\u5C01\u516C\u516C\u7684\u95E8\u8DEF\u3002\u6CA1\u60F3\u5230\u8FDE\u4EBA\u5F71\u90FD\u6CA1\u89C1\u7740\u3002</p><p>\u5B59\u5929\u7FBD\u8BF4\u9053\uFF1A\u300C\u6211\u628A\u8D22\u7269\u90FD\u62FF\u4E86\u51FA\u6765\uFF0C\u624D\u89C1\u7740\u5C01\u516C\u516C\u8EAB\u8FB9\u4E00\u4E2A\u5C0F\u592A\u76D1\uFF0C\u8BF7\u4ED6\u51FA\u6765\u5403\u4E86\u987F\u9152\u5E2D\u3002\u90A3\u5C0F\u592A\u76D1\u8BF4\uFF0C\u5C01\u516C\u516C\u6392\u573A\u6781\u5927\uFF0C\u8FD9\u6B21\u53C8\u63E1\u7740\u5175\u6743\uFF0C\u5E73\u5B9A\u767D\u83B2\u6559\u6848\uFF0C\u7B49\u95F2\u77E5\u5E9C\u4E5F\u96BE\u89C1\u4ED6\u4E00\u9762\u3002\u5C31\u662F\u89C1\u5230\u4E86\uFF0C\u4E5F\u672A\u5FC5\u4F1A\u8FD9\u7B49\u5C0F\u4E8B\u51FA\u5934\u3002\u300D</p><p>\u8FD9\u4E00\u4E0B\u95E8\u8DEF\u65AD\u4E86\u5E72\u5E72\u51C0\u51C0\uFF0C\u4E09\u4EBA\u90FD\u662F\u9ED4\u9A74\u6280\u7A77\uFF0C\u518D\u60F3\u4E0D\u51FA\u4E3B\u610F\u3002\u5B59\u5929\u7FBD\u5954\u5FD9\u5341\u4F59\u65E5\uFF0C\u81EA\u53BB\u6B47\u606F\u3002\u5218\u8FA8\u673A\u4E0E\u5353\u5929\u96C4\u4E5F\u65E0\u5FC3\u8BF4\u8BDD\uFF0C\u5404\u81EA\u56DE\u623F\u3002</p><p>\u5929\u8272\u5C06\u665A\uFF0C\u5B59\u5929\u7FBD\u6765\u5230\u72F1\u4E2D\u3002\u4F17\u4EBA\u4ECE\u7262\u91CC\u62C9\u4E86\u4E2A\u56DA\u72AF\uFF0C\u6559\u4ED6\u8BF4\u4E86\u51E0\u53E5\uFF0C\u5F04\u4E86\u8EAB\u8863\u51A0\u7A7F\u6234\u4E0A\uFF0C\u5145\u4F5C\u4EAC\u5E08\u6765\u7684\u5927\u5B98\u3002\u7136\u540E\u5C06\u767D\u96EA\u83B2\u63D0\u5230\u516C\u5802\u2014\u2014\u8FD9\u4E9B\u5929\u6765\u4F17\u4EBA\u4E5F\u77E5\u9053\u7792\u4E0D\u4F4F\u767D\u96EA\u83B2\uFF0C\u4E0D\u8FC7\u662F\u7565\u5C3D\u4EBA\u4E8B\u7F62\u4E86\u3002</p><p>\u767D\u96EA\u83B2\u4ECD\u5E26\u7740\u94C1\u67B7\u8DB3\u9563\uFF0C\u90A3\u65E5\u88AB\u72F1\u5352\u4EEC\u91CD\u521B\u4E86\u540E\u5EAD\uFF0C\u539F\u672C\u6781\u9669\uFF0C\u7565\u6709\u6C61\u7269\u6CBE\u67D3\u5C31\u6210\u4E86\u809B\u7618\uFF0C\u7EC8\u8EAB\u96BE\u6108\u3002\u5E78\u800C\u767D\u96EA\u83B2\u6B63\u503C\u8F9F\u8C37\uFF0C\u5E73\u65E5\u53EA\u7528\u4E00\u4E9B\u6E05\u6C34\uFF0C\u80A0\u9053\u5185\u5E72\u51C0\u5982\u65B0\uFF0C\u624D\u5C06\u517B\u5F97\u75CA\u6108\u3002</p><p>\u5802\u4E0A\u5BA1\u8BAF\u51E0\u53E5\uFF0C\u767D\u96EA\u83B2\u5DF2\u7ECF\u770B\u51FA\u4E0A\u9762\u5750\u7684\u662F\u5047\u8D27\uFF0C\u4FBF\u95ED\u4E86\u53E3\u4E00\u8A00\u4E0D\u53D1\u3002\u5979\u6015\u4E2D\u4E86\u72F1\u5352\u4EEC\u7684\u5708\u5957\uFF0C\u7EB5\u7136\u660E\u77E5\u662F\u5047\uFF0C\u4E5F\u4E0D\u6562\u66F2\u610F\u8BA4\u7F6A\u3002\u5BA1\u8BAF\u6B21\u6570\u8D8A\u591A\uFF0C\u5979\u8D8A\u662F\u5C0F\u5FC3\uFF0C\u4E07\u4E00\u8BF4\u9519\u4E00\u53E5\uFF0C\u6216\u662F\u6709\u4EBA\u5728\u5802\u540E\u65C1\u542C\uFF0C\u90A3\u6BC1\u7684\u5C31\u662F\u5979\u4EEC\u4E00\u5BB6\u4EBA\u3002</p><p>\u5B59\u5929\u7FBD\u8E31\u8FC7\u6765\u9053\uFF1A\u300C\u767D\u96EA\u83B2\uFF0C\u4F60\u8FD8\u662F\u4E0D\u62DB\u5417\uFF1F\u300D</p><p>\u767D\u96EA\u83B2\u95ED\u773C\u4E0D\u8BED\u3002</p><p>\u300C\u628A\u65B0\u6765\u7684\u4EBA\u72AF\u5E26\u4E0A\u6765\u3002\u300D</p><p>\u4E00\u9635\u94C1\u94FE\u58F0\u4ECE\u5802\u540E\u54CD\u8D77\uFF0C\u5728\u767D\u96EA\u83B2\u8EAB\u524D\u4E0D\u8FDC\u505C\u4E86\u4E0B\u6765\u3002</p><p>\u300C\u7ED9\u6211\u6253\uFF01\u300D</p><p>\u76AE\u97AD\u300C\u556A\u300D\u7684\u4E00\u58F0\u843D\u4E0B\uFF0C\u63A5\u7740\u54CD\u8D77\u4E00\u58F0\u5973\u5B50\u7684\u5C16\u53EB\u3002</p><p>\u767D\u96EA\u83B2\u5FC3\u5934\u5267\u9707\uFF0C\u970D\u7136\u7741\u5F00\u773C\u775B\uFF0C\u5931\u58F0\u53EB\u9053\uFF1A\u300C\u5A18\u59E8\uFF01\u300D</p><p>\u90A3\u58F0\u97F3\u867D\u7136\u75DB\u5F97\u53D8\u4E86\u8154\u8C03\uFF0C\u4F46\u5979\u672C\u80FD\u5730\u542C\u51FA\u90A3\u662F\u5A18\u59E8\u7684\u58F0\u97F3\u3002\u81EA\u4ECE\u5341\u4E8C\u5C81\u5230\u7F57\u9704\u5C71\u5B66\u827A\uFF0C\u5979\u5C31\u4E00\u76F4\u8DDF\u7740\u5A18\u59E8\uFF0C\u5A18\u59E8\u6027\u5B50\u867D\u7136\u67D4\u5F31\uFF0C\u4F46\u5F85\u5979\u72B9\u5982\u4EB2\u751F\u5973\u513F\uFF0C\u8F83\u4E4B\u5A18\u4EB2\u4E5F\u5DEE\u4E0D\u4E86\u534A\u5206\u3002</p><p>\u9762\u524D\u662F\u4E00\u5177\u767D\u8272\u7684\u8089\u4F53\uFF0C\u7EA4\u8170\u5706\u81C0\uFF0C\u66F2\u7EBF\u67D4\u6DA6\u52A8\u4EBA\uFF0C\u72B9\u5982\u6C34\u4E2D\u7389\u74A7\u3002\u5728\u5979\u5149\u6D01\u7684\u7389\u80CC\u4E0A\uFF0C\u4E00\u6761\u9C9C\u7EA2\u7684\u97AD\u75D5\u4ECE\u80A9\u5934\u659C\u5212\u5230\u8170\u4FA7\uFF0C\u5F77\u4F5B\u4E00\u6761\u706B\u86C7\u5728\u96EA\u80A4\u4E0A\u8DF3\u52A8\u3002</p><p>\u300C\u88F4\u9752\u7389\uFF01\u767D\u5B5D\u5112\u52FE\u7ED3\u9006\u532A\u4E00\u6848\u662F\u5426\u5C5E\u5B9E\uFF0C\u4F60\u53EF\u62DB\u4F9B\uFF1F\u300D</p><p>\u7389\u5A18\u8DB4\u5728\u5730\u4E0A\uFF0C\u6CE3\u58F0\u9053\uFF1A\u300C\u5974\u5BB6\u90FD\u62DB\u4E86\u7684\uFF0C\u6C42\u4F60\u4EEC\u522B\u518D\u6253\u4E86\u3002\u300D</p><p>\u9C8D\u6A2A\u88AB\u5353\u5929\u96C4\u63B4\u4E86\u4E00\u638C\uFF0C\u5C31\u8DDF\u7EB8\u624E\u7684\u4F3C\u7684\u584C\u4E86\u67B6\u5B50\uFF0C\u81F3\u4ECA\u8FD8\u672A\u9192\u3002\u90A3\u73ED\u72F1\u5352\u4E45\u5DF2\u607C\u4ED6\uFF0C\u4E5F\u4E0D\u5BA2\u6C14\uFF0C\u62FD\u4E86\u7389\u5A18\u51FA\u6765\u6063\u610F\u6E32\u6DEB\u3002</p><p>\u5979\u88AB\u4F17\u4EBA\u8F6E\u5978\u4E86\u534A\u65E5\uFF0C\u53C8\u6015\u53C8\u75DB\uFF0C\u8FD8\u672A\u4E0A\u5802\u8EAB\u5B50\u5C31\u5DF2\u7ECF\u9165\u4E86\u3002</p><p>\u6B64\u65F6\u5DF2\u662F\u591C\u534A\uFF0C\u8FD9\u516C\u5802\u672C\u662F\u5E99\u5B87\u6539\u6210\uFF0C\u4E24\u8FB9\u70B9\u4E86\u706F\u706B\uFF0C\u66F4\u663E\u5F97\u9634\u68EE\u53EF\u6016\uFF0C\u72B9\u5982\u960E\u7F57\u6BBF\u3002\u770B\u5230\u5802\u4E0A\u9648\u5217\u7684\u5211\u5177\uFF0C\u7389\u5A18\u66F4\u662F\u9A87\u5F97\u9762\u65E0\u4EBA\u8272\uFF0C\u9669\u4E9B\u6655\u4E86\u8FC7\u53BB\u3002</p><p>\u5B59\u5929\u7FBD\u9053\uFF1A\u300C\u767D\u96EA\u83B2\uFF0C\u88F4\u72AF\u5DF2\u7ECF\u62DB\u8BA4\uFF0C\u5C14\u7236\u767D\u5B5D\u5112\u4E0E\u767D\u83B2\u6559\u9006\u532A\u52FE\u7ED3\uFF0C\u4F60\u8FD8\u6709\u4F55\u8BDD\u8BF4\u3002\u300D</p><p>\u767D\u96EA\u83B2\u6012\u9053\uFF1A\u300C\u653E\u5F00\u5979\uFF01\u6709\u4EC0\u4E48\u4E8B\u5C31\u671D\u6211\u6765\uFF01\u300D</p><p>\u300C\u6211\u53EA\u95EE\u4F60\u62DB\u8FD8\u662F\u4E0D\u62DB\u3002\u300D</p><p>\u300C\u4F60\u4EEC\u98A0\u5012\u9ED1\u767D\uFF0C\u52FE\u9677\u4E8E\u524D\uFF0C\u683D\u8D43\u4E8E\u540E\uFF0C\u6211\u767D\u96EA\u83B2\u5C31\u662F\u6B7B\u4E5F\u7EDD\u4E0D\u62DB\u4F9B\uFF01\u300D</p><p>\u5B59\u5929\u7FBD\u4E5F\u4E0D\u5E9F\u8BDD\uFF0C\u6446\u4E86\u6446\u624B\u9053\uFF1A\u300C\u7ED9\u5979\u901A\u5976\u3002\u300D</p><p>\u5353\u5929\u96C4\u62FF\u51FA\u4E86\u4E00\u6839\u4E03\u5BF8\u94A2\u9488\uFF0C\u72DE\u7B11\u7740\u62FD\u51FA\u767D\u96EA\u83B2\u4E00\u53EA\u4E73\u623F\uFF0C\u637B\u4E86\u637B\u4E73\u5934\uFF0C\u7136\u540E\u5BF9\u7740\u4E73\u773C\u523A\u4E86\u8FDB\u53BB\u3002\u5973\u4EBA\u7684\u4E73\u5934\u6781\u662F\u575A\u97E7\uFF0C\u90A3\u94A2\u9488\u867D\u7136\u950B\u5229\uFF0C\u63D2\u8D77\u6765\u4E5F\u9887\u4E3A\u4E0D\u6613\u3002\u767D\u96EA\u83B2\u53EA\u89C9\u4E73\u5934\u50CF\u88AB\u706B\u70E7\u4E86\u4E00\u4E0B\uFF0C\u7136\u540E\u90A3\u7C92\u706B\u661F\u968F\u7740\u4E73\u773C\u4E00\u70B9\u70B9\u7099\u5165\u4E73\u5934\u3002</p><p>\u767D\u96EA\u83B2\u4E00\u53EA\u4E73\u623F\u88AB\u634F\u5F97\u53D8\u4E86\u5F62\uFF0C\u4E73\u5934\u5FAE\u7FD8\u7740\uFF0C\u88AB\u94A2\u9488\u624E\u5F97\u6B6A\u5411\u4E00\u8FB9\u3002\u5353\u5929\u96C4\u4E0D\u5F97\u4E0D\u634F\u4F4F\u5979\u7684\u4E73\u5934\uFF0C\u5728\u9488\u5C16\u4E0A\u62E7\u4E86\u62E7\uFF0C\u6276\u6B63\u4E86\u518D\u5F80\u91CC\u63A5\u7740\u624E\u3002\u4E73\u623F\u662F\u795E\u7ECF\u5BC6\u5E03\u5904\uFF0C\u611F\u89C9\u6700\u4E3A\u654F\u611F\uFF0C\u75DB\u695A\u4E5F\u5206\u5916\u5F3A\u70C8\u3002\u767D\u96EA\u83B2\u75DB\u5F97\u6D51\u8EAB\u98A4\u6296\uFF0C\u5374\u7D27\u54AC\u7740\u7259\uFF0C\u4E00\u58F0\u4E0D\u542D\u3002</p><p>\u7389\u5A18\u662F\u88AB\u5F53\u4F5C\u4E00\u4EF6\u73A9\u7269\u5F04\u5230\u72F1\u91CC\uFF0C\u53EA\u7EA6\u7565\u77E5\u9053\u662F\u56E0\u4E3A\u59D0\u592B\u901A\u532A\uFF0C\u62DB\u4F9B\u4E0D\u4E45\u5C31\u4E00\u547D\u545C\u547C\uFF0C\u53EA\u662F\u96EA\u83B2\u6B7B\u4E0D\u62DB\u4F9B\uFF0C\u5728\u72F1\u91CC\u8FD8\u5C61\u6B21\u4F24\u4EBA\uFF0C\u60F9\u5F97\u4F17\u4EBA\u6C14\u607C\u3002\u96EA\u83B2\u7684\u6027\u5B50\u5979\u6700\u6E05\u695A\u4E0D\u8FC7\uFF0C\u8DDF\u5979\u7239\u7239\u4E00\u6837\uFF0C\u90FD\u662F\u5B81\u6298\u4E0D\u5F2F\u7684\u5014\u5F3A\u6027\u683C\u3002\u4F46\u5979\u6CA1\u60F3\u5230\u4F1A\u96EA\u83B2\u5014\u5F3A\u5230\u8FD9\u79CD\u5730\u6B65\u2026\u2026</p><p>\u94A2\u9488\u4E00\u70B9\u4E00\u70B9\u523A\u5165\u4E73\u623F\uFF0C\u4E73\u5934\u88AB\u9488\u8EAB\u8F83\u7C97\u7684\u90E8\u5206\u6491\u5F97\u80C0\u8D77\uFF0C\u767D\u7389\u822C\u7684\u4E73\u8089\u98A4\u6296\u7740\uFF0C\u6E17\u51FA\u51B7\u6C57\uFF0C\u50CF\u6D17\u8FC7\u822C\u6C34\u6DCB\u6DCB\u7F29\u6210\u4E00\u56E2\u3002\u7389\u5A18\u5149\u662F\u5728\u65C1\u8FB9\u770B\u7740\uFF0C\u5FC3\u5934\u5C31\u62BD\u7D27\u4E86\u3002\u94A2\u9488\u8D8A\u8FDB\u8D8A\u6DF1\uFF0C\u4E03\u5BF8\u957F\u7684\u9488\u8EAB\u51E0\u4E4E\u7A7F\u900F\u4E86\u4E73\u7403\uFF0C\u6DF1\u6DF1\u5D4C\u5728\u4E73\u5185\uFF0C\u6BCF\u4E00\u4E1D\u75DB\u695A\u90FD\u6E05\u6670\u5730\u4F20\u5165\u4F53\u5185\u3002</p><p>\u5B59\u5929\u7FBD\u6DE1\u6DE1\u7B11\u9053\uFF1A\u300C\u767D\u96EA\u83B2\uFF0C\u4F60\u62DB\u4F9B\u4E48\uFF1F\u300D</p><p>\u767D\u96EA\u83B2\u4ECE\u7259\u7F1D\u4E2D\u5410\u51FA\u4E00\u4E2A\u5B57\uFF0C\u300C\u4E0D\uFF01\u300D</p><p>\u5353\u5929\u96C4\u634F\u7740\u94A2\u9488\uFF0C\u5728\u5979\u4E73\u5185\u4E00\u6405\u3002</p><p>\u767D\u96EA\u83B2\u987F\u65F6\u5C16\u53EB\u4E00\u58F0\uFF0C\u5FFD\u7136\u8EAB\u5B50\u5F80\u524D\u4E00\u633A\uFF0C\u671D\u9488\u5C16\u649E\u53BB\u3002\u90A3\u94A2\u9488\u5DF2\u7ECF\u7A7F\u900F\u4E73\u623F\uFF0C\u79BB\u5FC3\u810F\u53EA\u6709\u5BF8\u8BB8\u3002\u5802\u4E0A\u8C01\u90FD\u6CA1\u6709\u60F3\u5230\u5979\u4F1A\u5BFB\u6B7B\uFF0C\u5353\u5929\u96C4\u8FDE\u5FD9\u677E\u624B\uFF0C\u5374\u665A\u4E86\u4E00\u6B65\u3002\u53EA\u542C\u683C\u7684\u4E00\u58F0\u8F7B\u54CD\uFF0C\u9488\u5C16\u649E\u5728\u4E86\u80F8\u9AA8\u4E0A\u3002</p><p>\u5353\u5929\u96C4\u62D4\u51FA\u94A2\u9488\uFF0C\u9C9C\u8840\u4E00\u4E0B\u6E85\u4E86\u51FA\u6765\u3002\u5B59\u5929\u7FBD\u4E5F\u6015\u767D\u96EA\u83B2\u6B7B\u4E86\u65E0\u6CD5\u6536\u573A\uFF0C\u6CE8\u89C6\u7740\u4F24\u53E3\uFF0C\u7B49\u9C9C\u8840\u6E10\u6B62\u624D\u677E\u4E86\u53E3\u6C14\u3002\u300C\u597D\u4E2A\u70C8\u6027\u5973\u5B50\u3002\u4F60\u82E5\u6B7B\u4E86\uFF0C\u5927\u4E0D\u4E86\u62A5\u4E2A\u754F\u7F6A\u81EA\u6740\uFF0C\u5728\u573A\u7684\u90FD\u662F\u4EBA\u8BC1\u3002\u5230\u65F6\u8C01\u6765\u7ED9\u4F60\u5BB6\u4EBA\u6D17\u51A4\u5462\uFF1F\u300D</p><p>\u767D\u96EA\u83B2\u75DB\u6781\u96BE\u5F53\u624D\u840C\u751F\u6B7B\u610F\uFF0C\u6B64\u65F6\u5DF2\u5E73\u590D\u4E0B\u6765\uFF0C\u5979\u5598\u4E86\u53E3\u6C14\uFF0C\u54AC\u7259\u9053\uFF1A\u300C\u6211\u4F1A\u6D3B\u4E0B\u53BB\u3002\u6211\u8981\u770B\u7740\u4F60\u4EEC\u8FD9\u7FA4\u72D7\u8D3C\u4E00\u4E2A\u4E2A\u6B7B\u65E0\u846C\u8EAB\u4E4B\u5730\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u62FF\u4F4F\u767D\u96EA\u83B2\u53D7\u4F24\u7684\u5DE6\u4E73\uFF0C\u6162\u6162\u62E7\u52A8\uFF0C\u9C9C\u8840\u50CF\u4E73\u6C41\u822C\u4ECE\u4E73\u5934\u6D8C\u51FA\uFF0C\u67D3\u7EA2\u4E86\u96EA\u7389\u822C\u7684\u623F\u3002\u300C\u90A3\u597D\uFF0C\u6211\u5C31\u8DDF\u4F60\u8D4C\u4E0A\u4E00\u628A\uFF0C\u770B\u8C01\u6B7B\u5728\u524D\u9762\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u4E0D\u6562\u518D\u5BF9\u767D\u96EA\u83B2\u7528\u5211\uFF0C\u4ED6\u671D\u5353\u5929\u96C4\u4F7F\u4E86\u773C\u8272\uFF0C\u95EE\u9053\uFF1A\u300C\u767D\u96EA\u83B2\uFF0C\u4F60\u62DB\u4E0D\u62DB\u4F9B\uFF1F\u300D</p><p>\u300C\u4E0D\u62DB\u3002\u6B7B\u4E5F\u4E0D\u62DB\uFF01\u300D</p><p>\u5353\u5929\u96C4\u62FF\u7740\u6EF4\u8840\u7684\u94A2\u9488\uFF0C\u9053\uFF1A\u300C\u88F4\u5A4A\u5B50\uFF0C\u628A\u5976\u5B50\u633A\u8D77\u6765\u3002\u8BA9\u5353\u4E8C\u7237\u7ED9\u4F60\u901A\u901A\u5976\u3002\u300D</p><p>\u5353\u5929\u96C4\u4E00\u8D70\u8FC7\u6765\uFF0C\u7389\u5A18\u5FC3\u5934\u5C31\u5DF2\u63EA\u7D27\uFF0C\u542C\u5230\u8FD9\u53E5\u8BDD\uFF0C\u987F\u65F6\u5413\u5F97\u6D51\u8EAB\u4E71\u98A4\uFF0C\u5E26\u7740\u54ED\u8154\u9053\uFF1A\u300C\u5927\u7237\uFF0C\u9976\u4E86\u8D31\u5974\u5427\u2026\u2026\u300D</p><p>\u300C\u8FD9\u4E8B\u6211\u4EEC\u8BF4\u4E86\u4E0D\u7B97\uFF0C\u300D\u5B59\u5929\u7FBD\u6CB9\u7136\u9053\uFF1A\u300C\u53BB\u6C42\u4F60\u7684\u597D\u7525\u5973\u5427\u3002\u770B\u5979\u5F00\u4E0D\u5F00\u6069\u3002\u300D</p><p>\u7389\u5A18\u62C9\u4F4F\u767D\u96EA\u83B2\uFF0C\u6CE3\u58F0\u9053\uFF1A\u300C\u96EA\u83B2\uFF0C\u96EA\u83B2\u2026\u2026\u300D</p><p>\u767D\u96EA\u83B2\u6B7B\u76EF\u7740\u5B59\u5929\u7FBD\uFF0C\u54AC\u7259\u9053\uFF1A\u300C\u5351\u9119\uFF01\u300D</p><p>\u5B59\u5929\u7FBD\u8E72\u4E0B\u6765\uFF0C\u5E73\u89C6\u7740\u5979\u7684\u773C\u775B\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u6211\u8DDF\u4F60\u4E00\u6837\uFF0C\u90FD\u60F3\u6D3B\u4E0B\u53BB\u3002\u53EF\u80FD\u4F60\u8FD8\u4E0D\u77E5\u9053\u6211\u4E0E\u4EE4\u59B9\u5DF2\u7ECF\u6210\u4EB2\u2014\u2014\u300D\u4ED6\u6B62\u4F4F\u767D\u96EA\u83B2\uFF0C\u300C\u522B\u6025\u3002\u6211\u662F\u660E\u5A92\u6B63\u5A36\uFF0C\u4F60\u5A18\u4EB2\u53E3\u628A\u7389\u83B2\u8BB8\u7ED9\u6211\u7684\u3002\u6211\u5B59\u5929\u7FBD\u867D\u7136\u4F4D\u5351\u804C\u5C0F\uFF0C\u4F46\u4FDD\u4F4F\u4EE4\u59B9\u8FD8\u662F\u6709\u529E\u6CD5\u7684\u3002</p><p>\u4F55\u51B5\uFF0C\u300D\u4ED6\u987F\u4E86\u987F\uFF0C\u300C\u4EE4\u59B9\u8EAB\u4E0A\u5DF2\u7ECF\u6709\u4E86\u3002\u300D</p><p>\u300C\u5982\u679C\u4F60\u62DB\u4F9B\uFF0C\u4E39\u5A18\u3001\u7389\u83B2\uFF0C\u8FDE\u4F60\u4EEC\u4E24\u4E2A\u5728\u5185\uFF0C\u90FD\u662F\u7F6A\u5C5E\uFF0C\u5E76\u975E\u4E0D\u4F1A\u6B7B\u7F6A\u3002</p><p>\u6211\u65E2\u7136\u5A36\u4E86\u7389\u83B2\uFF0C\u80AF\u5B9A\u4F1A\u62A4\u5979\u5468\u5168\u3002\u5982\u679C\u4F60\u4E0D\u62DB\uFF0C\u5C31\u662F\u7FFB\u8FC7\u6848\u6765\uFF0C\u4F60\u4EEC\u767D\u5BB6\u4E5F\u5DF2\u7ECF\u5BB6\u7834\u4EBA\u4EA1\uFF0C\u552F\u4E00\u5E72\u51C0\u7684\u7389\u83B2\u4E5F\u6210\u4E86\u5BE1\u5987\u2014\u2014\u8BE5\u8BF4\u7684\u6211\u90FD\u8BF4\u4E86\uFF0C\u597D\u574F\u4F60\u81EA\u5DF1\u6382\u91CF\u5427\u3002\u300D</p><p>\u300C\u96EA\u83B2\uFF0C\u5B59\u5927\u54E5\u90FD\u662F\u4E3A\u6211\u4EEC\u7740\u60F3\uFF0C\u4F60\u5C31\u62DB\u4E86\u5427\u3002\u300D\u7389\u5A18\u8FDE\u5FD9\u592E\u9053\u3002</p><p>\u96EA\u83B2\u542C\u8BF4\u4ED6\u8FDE\u7389\u83B2\u4E5F\u73B7\u6C61\u4E86\uFF0C\u8138\u8272\u987F\u65F6\u53D8\u5F97\u60E8\u767D\u3002\u826F\u4E45\u5979\u522B\u8FC7\u8138\u53BB\uFF0C\u8BF4\u9053\uFF1A\u300C\u6211\u53EA\u6068\u6CA1\u6709\u65E9\u70B9\u6740\u4E86\u4F60\uFF01\u300D</p><p>\u5B59\u5929\u7FBD\u62AC\u8D77\u624B\uFF0C\u4E00\u637B\u624B\u6307\u3002\u5353\u5929\u96C4\u8FC7\u6765\u6293\u4F4F\u7389\u5A18\u4E00\u53EA\u4E73\u623F\uFF0C\u5BF9\u7740\u4E73\u773C\u5C31\u523A\u3002</p><p>\u7389\u5A18\u62DA\u547D\u6323\u624E\uFF0C\u4F46\u600E\u4E48\u80FD\u6323\u8FC7\u5353\u5929\u96C4\uFF0C\u90A3\u53EA\u4E73\u623F\u88AB\u63EA\u5F97\u62C9\u957F\uFF0C\u5374\u600E\u4E48\u4E5F\u6323\u4E0D\u5F00\u3002</p><p>\u300C\u554A\u2026\u2026\u300D\u6DF1\u591C\u7684\u516C\u5802\u4E0A\u4F20\u6765\u4E00\u58F0\u51C4\u75DB\u7684\u5C16\u53EB\u3002\u6811\u4E0A\u6816\u606F\u7684\u7FA4\u9E26\u88AB\u8FD9\u75DB\u53EB\u58F0\u60CA\u9192\uFF0C\u62CD\u6253\u7740\u7FC5\u8180\u53EB\u7740\u98DE\u53BB\u3002</p><p>\u94A2\u9488\u7A7F\u900F\u4E73\u5934\uFF0C\u9488\u5C16\u523A\u5728\u4E73\u5185\u5AE9\u8089\u9876\u7AEF\uFF0C\u7389\u5A18\u54ED\u53EB\u9053\uFF1A\u300C\u96EA\u83B2\uFF0C\u6C42\u6C42\u4F60\u770B\u5728\u5A18\u59E8\u5F80\u65E5\u7167\u987E\u4F60\u7684\u4EFD\u4E0A\uFF0C\u522B\u8BA9\u5A18\u59E8\u518D\u53D7\u82E6\u4E86\u3002\u5A18\u59E8\u53D7\u4E0D\u4F4F\u2026\u2026\u300D</p><p>\u767D\u96EA\u83B2\u77AA\u7740\u5B59\u5929\u7FBD\uFF0C\u7F8E\u76EE\u4E2D\u51E0\u4E4E\u55B7\u51FA\u706B\u6765\u3002</p><p>\u5353\u5929\u96C4\u624B\u6307\u4E00\u9001\uFF0C\u94A2\u9488\u72E0\u72E0\u6233\u8FDB\u4E73\u8089\u3002\u7389\u5A18\u53EA\u53EB\u4E86\u534A\u58F0\uFF0C\u4E24\u670B\u4E00\u8E6C\uFF0C\u80A1\u95F4\u55B7\u51FA\u4E00\u80A1\u5C3F\u6DB2\uFF0C\u6DCB\u5F97\u6EE1\u817F\u90FD\u662F\u3002</p><p>\u5353\u5929\u96C4\u9053\uFF1A\u300C\u4E0D\u662F\u6B7B\u4E86\u5427\uFF1F\u300D\u8BF4\u7740\u63A2\u4E86\u63A2\u5979\u7684\u9F3B\u606F\u3002</p><p>\u300C\u6CA1\u4E8B\u513F\uFF0C\u53EA\u662F\u75DB\u660F\u4E86\u3002\u300D\u5B59\u5929\u7FBD\u8F7B\u63CF\u6DE1\u5199\u5730\u8BF4\u9053\u3002\u300C\u767D\u6355\u5934\uFF0C\u4F60\u5FC3\u80A0\u771F\u591F\u786C\u7684\uFF0C\u4E3A\u4E86\u81EA\u5DF1\uFF0C\u8FDE\u5AE1\u4EB2\u5A18\u59E8\u90FD\u4E0D\u8981\u4E86\u3002\u300D</p><p>\u300C\u5C31\u662F\u4E3A\u4E86\u5979\u4EEC\uFF0C\u6211\u624D\u4E0D\u80FD\u62DB\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u8D34\u8FD1\u8FC7\u6765\uFF0C\u8BF4\u9053\uFF1A\u300C\u5FD8\u4E86\u544A\u8BC9\u4F60\u2014\u2014\u4F60\u5A18\u8EAB\u4E0A\u4E5F\u6709\u4E86\uFF0C\u4E0D\u8FC7\u4E0D\u77E5\u9053\u662F\u8C01\u7684\u79CD\u3002\u4F60\u518D\u4E0D\u62DB\uFF0C\u6211\u53EA\u597D\u8BA9\u4F60\u5A18\u6765\u966A\u4F60\u3002\u5979\u7684\u8EAB\u5B50\u53EF\u662F\u8FD9\u72F1\u91CC\u7684\u7231\u7269\u3002\u6211\u77E5\u9053\u4F60\u60F3\u6740\u4E86\u6211\uFF0C\u4F46\u4F60\u73B0\u5728\u6CA1\u529E\u6CD5\u3002\u4EE5\u540E\u4E5F\u6CA1\u6709\u3002\u300D</p>',110);function t(n,_){return a}var o=e(r,[["render",t]]);export{o as default};
