import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const a={},r=p('<h1 id="_49-\u52D8\u6848" tabindex="-1"><a class="header-anchor" href="#_49-\u52D8\u6848" aria-hidden="true">#</a> 49 \u52D8\u6848</h1><p>\u534A\u4E2A\u65F6\u8FB0\u4E4B\u524D\uFF0C\u5B59\u5929\u7FBD\u98CE\u5C18\u4EC6\u4EC6\u5954\u56DE\u795E\u4ED9\u5CAD\u3002\u8FDC\u8FDC\u770B\u5230\u674F\u82B1\u6751\u7684\u9152\u5E4C\uFF0C\u4ED6\u624D\u677E\u4E86\u53E3\u6C14\u3002\u5929\u4E0D\u4EAE\u5C31\u52A8\u8EAB\uFF0C\u5230\u73B0\u5728\u4ED6\u8FDE\u6C34\u90FD\u6CA1\u559D\u4E00\u53E3\uFF0C\u4E5F\u4E4F\u5F97\u7D27\u4E86\uFF0C\u597D\u5728\u7EC8\u4E8E\u5230\u4E86\u5BB6\uFF0C\u5148\u5728\u5E97\u91CC\u6B47\u606F\u7247\u523B\uFF0C\u518D\u8D76\u56DE\u72F1\u4E2D\u4E0D\u8FDF\u3002</p><p>\u5E97\u95E8\u5F00\u7740\uFF0C\u5374\u672A\u89C1\u4EBA\uFF0C\u5B59\u5929\u7FBD\u4E00\u9762\u53EB\u7740\uFF0C\u300C\u4E39\u5A18\u300D\uFF0C\u4E00\u9762\u4E0A\u697C\u3002\u5367\u5BA4\u623F\u95E8\u534A\u5F00\uFF0C\u4E0A\u9762\u8FD8\u5D2D\u65B0\u7684\u559C\u5B57\u88AB\u626F\u6389\u5728\u5730\uFF0C\u8E29\u4E86\u51E0\u4E2A\u811A\u5370\u3002\u6574\u4E2A\u9152\u5E97\u5F77\u4F5B\u7A7A\u65E0\u4E00\u4EBA\uFF0C\u6CA1\u6709\u534A\u70B9\u58F0\u97F3\u3002</p><p>\u5B59\u5929\u7FBD\u5FC3\u53EB\u4E0D\u5999\uFF0C\u8FDE\u5FD9\u51B2\u8FDB\u623F\u5185\u3002\u5165\u76EE\u7684\u60C5\u666F\u4F7F\u4ED6\u5927\u53EB\u4E00\u58F0\uFF0C\u76EE\u7726\u6B32\u88C2\u3002</p><p>\u5E8A\u4E0A\u7EB1\u5E10\u6380\u5F00\uFF0C\u767D\u7389\u83B2\u88AB\u7ED1\u4F4F\u53CC\u624B\uFF0C\u4EF0\u9762\u8EBA\u5728\u5E8A\u4E0A\u3002\u5979\u53CC\u817F\u5F20\u5F00\uFF0C\u767D\u7F8E\u7684\u7EA4\u8DB3\u88AB\u811A\u5E26\u6346\u7740\uFF0C\u540A\u5728\u7EB1\u5E10\u4E24\u89D2\u3002\u817F\u95F4\u67D4\u7F8E\u7684\u7389\u6237\u4E00\u7247\u72FC\u85C9\uFF0C\u4E5F\u4E0D\u77E5\u88AB\u591A\u5C11\u7537\u4EBA\u8E42\u8E8F\u8FC7\uFF0C\u63D2\u5F04\u5F97\u53C8\u7EA2\u53C8\u80BF\uFF0C\u9762\u76EE\u5168\u975E\uFF0C\u91CC\u5916\u6CBE\u6EE1\u4E86\u6D53\u75F0\u4E00\u6837\u7684\u7CBE\u6DB2\u3002</p><p>\u90A3\u4E9B\u4EBA\u8FDE\u5979\u7684\u540E\u5EAD\u4E5F\u672A\u653E\u8FC7\uFF0C\u7389\u83B2\u5C0F\u5DE7\u7684\u5C41\u773C\u513F\u88AB\u786C\u7269\u6491\u88C2\uFF0C\u5E72\u5F97\u6EE1\u81C0\u90FD\u662F\u8840\uFF0C\u53C8\u88AB\u4EBA\u63D2\u8FDB\u53BB\u4E00\u6839\u652F\u7A97\u6237\u7684\u7AF9\u7AFF\uFF0C\u534A\u622A\u7AFF\u8EAB\u88AB\u9C9C\u8840\u67D3\u5F97\u901A\u7EA2\u3002\u5979\u7389\u988A\u6B6A\u5728\u4E00\u8FB9\uFF0C\u53CC\u76EE\u7D27\u95ED\uFF0C\u776B\u6BDB\u3001\u9F3B\u7FFC\u3001\u7EA2\u5507\u2026\u2026\u540C\u6837\u6CBE\u7740\u5E26\u8840\u7684\u7CBE\u6DB2\uFF0C\u5F77\u4F5B\u662F\u809B\u4EA4\u540E\u53C8\u88AB\u5F3A\u8FEB\u53E3\u4EA4\uFF0C\u7136\u540E\u88AB\u4EBA\u62E7\u7740\u5934\u53D1\u5C04\u5728\u8138\u4E0A\u3002</p><p>\u5B59\u5929\u7FBD\u63A2\u4E86\u63A2\u5979\u7684\u9F3B\u606F\uFF0C\u53D1\u73B0\u5979\u53EA\u662F\u660F\u8FF7\u8FC7\u53BB\uFF0C\u6B64\u65F6\u628A\u5979\u53EB\u9192\uFF0C\u53CD\u800C\u4E0D\u597D\uFF0C\u4E8E\u662F\u5C0F\u5FC3\u5730\u62D4\u51FA\u7AF9\u7AFF\uFF0C\u89E3\u5F00\u811A\u5E26\uFF0C\u653E\u4E0B\u5979\u50F5\u76F4\u7684\u53CC\u817F\u3002\u5B59\u5929\u7FBD\u53CC\u624B\u5FAE\u5FAE\u53D1\u6296\uFF0C\u5E76\u4E0D\u662F\u56E0\u4E3A\u7389\u83B2\u8EAB\u4E0A\u7684\u4F24\u75D5\uFF0C\u800C\u662F\u56E0\u4E3A\u6CA1\u6709\u770B\u5230\u4E39\u5A18\u3002</p><p>\u5C06\u7389\u83B2\u8FD9\u8FB9\u5904\u7406\u597D\uFF0C\u5B59\u5929\u7FBD\u7ACB\u5373\u5728\u697C\u5185\u75AF\u72C2\u5730\u5BFB\u627E\u8D77\u6765\u3002\u4ED6\u5DF2\u7ECF\u9690\u7EA6\u731C\u5230\u662F\u8C01\u5F3A\u66B4\u4E86\u7389\u83B2\u3002\u4ED6\u4E0D\u6562\u53BB\u60F3\u4E39\u5A18\u53D7\u5230\u4EC0\u4E48\u6837\u7684\u6298\u78E8\u3002\u53EA\u6C42\u5979\u4E0D\u8981\u6B7B\u3002</p><p>\u697C\u4E0A\u697C\u4E0B\u90FD\u627E\u8FC7\uFF0C\u4ECD\u4E0D\u89C1\u4E39\u5A18\u8E2A\u5F71\uFF0C\u5B59\u5929\u7FBD\u53C8\u627E\u4E86\u53A8\u623F\u3001\u67F4\u623F\uFF0C\u4F46\u9664\u4E86\u4E95\u65C1\u4E00\u53EA\u7EE3\u978B\uFF0C\u518D\u6CA1\u6709\u4E1D\u6BEB\u7EBF\u7D22\u3002\u4EE5\u5B59\u5929\u7FBD\u7684\u9634\u72E0\uFF0C\u6B64\u65F6\u4E5F\u65B9\u5BF8\u5927\u4E71\uFF0C\u4FEF\u5728\u4E95\u8FB9\u543C\u9053\uFF1A\u300C\u4E39\u5A18\uFF01\u300D</p><p>\u55E1\u55E1\u7684\u56DE\u58F0\u4F7F\u5B59\u5929\u7FBD\u51B7\u9759\u4E86\u4E00\u4E9B\uFF0C\u4ED6\u5FFD\u7136\u5F39\u8D77\u4E86\u8EAB\uFF0C\u63A0\u5230\u9152\u7A91\uFF0C\u4E00\u811A\u8E22\u5F00\u623F\u95E8\u3002</p><p>\u7A91\u5185\u539F\u672C\u6446\u653E\u6574\u9F50\u7684\u9152\u575B\u63A8\u5F97\u4E1C\u5012\u897F\u6B6A\uFF0C\u4E2D\u95F4\u4E00\u53EA\u534A\u4EBA\u9AD8\u7684\u5927\u74EE\u63ED\u4E86\u5C01\uFF0C\u9152\u6C34\u503E\u4E86\u4E00\u5730\uFF0C\u4E00\u53EA\u767D\u82B1\u82B1\u7684\u5C41\u80A1\u5361\u5728\u74EE\u53E3\uFF0C\u91CC\u9762\u63D2\u7740\u4E00\u6839\u4E4C\u9ED1\u7684\u6728\u68CD\u3002\u5979\u8EAB\u5B50\u50CF\u88AB\u9152\u6C34\u6CE1\u8FC7\uFF0C\u96EA\u767D\u7684\u53CC\u817F\u8F6F\u7EF5\u7EF5\u5782\u5728\u74EE\u4FA7\uFF0C\u4ECE\u96EA\u81C0\u5230\u811A\u5C16\uFF0C\u6DCB\u6EE1\u4E86\u9152\u6C61\u3002</p><p>\u5B59\u5929\u7FBD\u62B1\u4F4F\u4E39\u5A18\u7684\u8170\u81C0\uFF0C\u5C06\u5979\u4ECE\u74EE\u91CC\u6162\u6162\u5730\u62D6\u51FA\u3002\u4ED6\u8FD9\u624D\u770B\u51FA\uFF0C\u74EE\u91CC\u5269\u7684\u4E0D\u662F\u9152\uFF0C\u800C\u662F\u5C3F\u3002\u4E39\u5A18\u4E0A\u534A\u8EAB\u88AB\u585E\u8FDB\u74EE\u91CC\uFF0C\u8EAB\u4E0A\u6E7F\u6F09\u6F09\u90FD\u662F\u5C3F\u6DB2\u3002\u5979\u53CC\u624B\u88AB\u6346\u5728\u8EAB\u540E\uFF0C\u5634\u4E2D\u585E\u4E86\u4E00\u56E2\u7834\u5E03\uFF0C\u96EA\u767D\u7684\u9762\u5B54\u8840\u8272\u5168\u65E0\uFF0C\u4EBA\u5012\u8FD8\u9192\u7740\u3002</p><p>\u5B59\u5929\u7FBD\u638F\u51FA\u585E\u53E3\u7684\u7834\u5E03\uFF0C\u4E39\u5A18\u53EB\u4E86\u58F0\uFF0C\u300C\u76F8\u516C\uFF01\u300D\u4FBF\u75DB\u54ED\u8D77\u6765\u3002</p><p>\u5B59\u5929\u7FBD\u5FC3\u91CC\u7D27\u7EF7\u7684\u5F26\u7EC8\u4E8E\u677E\u5F00\uFF0C\u5B89\u6170\u9053\uFF1A\u300C\u4E0D\u7528\u6015\u3002\u6211\u56DE\u6765\u4E86\uFF0C\u4F60\u4EC0\u4E48\u90FD\u4E0D\u7528\u6015\u3002\u300D</p><p>\u4E39\u5A18\u75DB\u54ED\u9053\uFF1A\u300C\u90FD\u6028\u6211\u2026\u2026\u4E0A\u5348\u4ED6\u4EEC\u6765\uFF0C\u8BF4\u8981\u5728\u67DC\u53F0\u4E0A\u5978\u6211\u2026\u2026\u6211\u4E0D\u8BE5\u8BF4\u6362\u4E2A\u5730\u65B9\uFF0C\u8BA9\u4ED6\u4EEC\u649E\u89C1\u7389\u83B2\u2026\u2026\u300D</p><p>\u300C\u4ED6\u4EEC\u597D\u72E0\u7684\u5FC3\u2026\u2026\u6211\u600E\u4E48\u6C42\u4ED6\u4EEC\u90FD\u6CA1\u9976\u8FC7\u7389\u83B2\u2026\u2026\u8FD8\u662F\u628A\u5979\u6346\u8D77\u6765\uFF0C\u7CDF\u8E4B\u4E86\u2026\u2026\u6211\u8BF4\u7389\u83B2\u5DF2\u7ECF\u5AC1\u7ED9\u4E86\u76F8\u516C\uFF0C\u4ED6\u4EEC\u4E5F\u4E0D\u542C\u2026\u2026\u300D</p><p>\u9C8D\u6A2A\u4ED6\u4EEC\u975E\u4F46\u4E0D\u542C\uFF0C\u53CD\u800C\u53D8\u672C\u52A0\u5229\uFF0C\u50CF\u91CE\u517D\u4E00\u6837\u8F6E\u5978\u4E86\u7389\u83B2\u3002\u7389\u83B2\u8EAB\u5B50\u672C\u5C31\u67D4\u5F31\uFF0C\u53C8\u53D7\u4E86\u60CA\uFF0C\u88AB\u4ED6\u4EEC\u5978\u5F97\u751F\u751F\u660F\u6B7B\u8FC7\u53BB\u3002\u4E39\u5A18\u6BCD\u5973\u90FD\u88AB\u5B59\u5929\u7FBD\u5360\u4E86\u5148\uFF0C\u9C8D\u6A2A\u53C8\u5AC9\u53C8\u6068\u3002</p><p>\u5E72\u5B8C\u7389\u83B2\uFF0C\u53C8\u628A\u4E39\u5A18\u62FD\u5230\u9152\u7A91\uFF0C\u51E0\u4E2A\u4EBA\u4E00\u8FB9\u996E\u9152\uFF0C\u4E00\u8FB9\u62FF\u5979\u53D6\u4E50\u3002\u6700\u540E\u53C8\u628A\u5979\u585E\u5230\u74EE\u91CC\uFF0C\u5F80\u91CC\u9762\u6492\u5C3F\u3002\u4E39\u5A18\u88AB\u585E\u5230\u74EE\u91CC\uFF0C\u811A\u5C16\u79BB\u5730\u9762\u8FD8\u6709\u534A\u5C3A\u591A\u9AD8\uFF0C\u7389\u83B2\u53C8\u88AB\u6346\u4E86\u624B\u811A\uFF0C\u82E5\u65E0\u4EBA\u89E3\u6551\uFF0C\u6BCD\u5973\u4FE9\u5355\u9760\u81EA\u5DF1\u53EA\u80FD\u5750\u4EE5\u5F85\u6BD9\u3002</p><p>\u5B59\u5929\u7FBD\u5E2E\u5979\u89E3\u5F00\u4E86\u624B\u4E0A\u7684\u7EF3\u7D22\uFF0C\u518D\u770B\u5979\u9634\u5185\u63D2\u7684\u6728\u68CD\uFF0C\u5374\u662F\u6761\u56DB\u9762\u89C1\u68F1\u7684\u95E8\u95E9\uFF0C\u53C8\u7C97\u53C8\u786C\u3002</p><p>\u300C\u5FCD\u7740\u4E9B\uFF0C\u6211\u5E2E\u4F60\u62D4\u51FA\u6765\u3002\u300D</p><p>\u95E8\u95E9\u7D27\u7D27\u5361\u4F4F\u8089\u7A74\uFF0C\u6B64\u65F6\u4E39\u5A18\u9634\u5185\u5DF2\u7ECF\u53D8\u5F97\u5E72\u6DA9\uFF0C\u62D4\u51FA\u65F6\u7684\u75DB\u695A\uFF0C\u4E0D\u4E9A\u4E8E\u5F53\u521D\u88AB\u5F3A\u884C\u585E\u5165\u3002\u4F46\u5F53\u6728\u68CD\u79BB\u5F00\u8EAB\u4F53\uFF0C\u4E39\u5A18\u7709\u5934\u4ECD\u672A\u6563\u5F00\uFF0C\u5979\u629A\u7740\u5C0F\u8179\uFF0C\u54AC\u4F4F\u53D1\u767D\u7684\u5634\u5507\uFF0C\u8EAB\u4F53\u5FAE\u5FAE\u98A4\u6296\u3002</p><p>\u5B59\u5929\u7FBD\u8FD9\u624D\u6CE8\u610F\u5230\u4E39\u5A18\u5C0F\u8179\u9686\u8D77\uFF0C\u6BD4\u51E0\u5929\u524D\u5927\u4E86\u8BB8\u591A\u3002\u4ED6\u5FC3\u91CC\u4E00\u6C89\uFF0C\u96BE\u9053\u662F\u4E39\u5A18\u4E0B\u4F53\u53D7\u521B\u592A\u91CD\uFF0C\u4F24\u4E86\u8179\u4E2D\u7684\u80CE\u513F\uFF1F</p><p>\u4E39\u5A18\u54ED\u6CE3\u9053\uFF1A\u300C\u540E\u9762\u8FD8\u6709\u2026\u2026\u300D</p><p>\u5B59\u5929\u7FBD\u5FD9\u6258\u8D77\u5979\u7684\u5C41\u80A1\uFF0C\u671D\u81C0\u7F1D\u91CC\u6478\u53BB\u3002\u624B\u6307\u89E6\u5230\u4E00\u4E2A\u7269\u4F53\uFF0C\u97E7\u4E2D\u5E26\u786C\uFF0C\u50CF\u585E\u5B50\u4E00\u6837\u9876\u5728\u809B\u4E2D\u3002</p><p>\u300C\u522B\u62D4\uFF01\u300D</p><p>\u4E39\u5A18\u5E26\u7740\u54ED\u8154\u7684\u58F0\u97F3\u521A\u8BF4\u51FA\u53E3\uFF0C\u5B59\u5929\u7FBD\u5DF2\u5C06\u90A3\u4E2A\u7269\u4F53\u62D4\u4E86\u51FA\u6765\u3002\u4E39\u5A18\u8138\u8272\u715E\u767D\uFF0C\u9686\u8D77\u7684\u5C0F\u8179\u4E00\u9635\u8815\u52A8\u3002\u5979\u4E24\u624B\u6346\u5F97\u9EBB\u4E86\uFF0C\u6323\u624E\u7740\u63A2\u5230\u81C0\u95F4\u53BB\u63A9\uFF0C\u5DF2\u7ECF\u665A\u4E86\u3002</p><p>\u4E00\u80A1\u5E26\u7740\u4F53\u6E29\u7684\u6DB2\u4F53\u4ECE\u5979\u809B\u4E2D\u55B7\u51FA\uFF0C\u6E85\u5F97\u6EE1\u5730\u90FD\u662F\u3002</p><p>\u300C\u4ED6\u4EEC\u62FF\u4E86\u6253\u9152\u7684\u6F0F\u6597\uFF0C\u628A\u2026\u2026\u704C\u5230\u91CC\u9762\u2026\u2026\u300D\u4E39\u5A18\u6CE3\u58F0\u9053\uFF1A\u300C\u8FD8\u62FF\u4E86\u90A3\u4E1C\u897F\u585E\u4F4F\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u8FD9\u624D\u8BA4\u51FA\u90A3\u809B\u585E\u662F\u4ECE\u767D\u5B5D\u5112\u8EAB\u4E0A\u5272\u4E0B\u7684\u9633\u5177\u3002\u90A3\u7269\u4EF6\u7528\u836F\u7269\u6CE1\u8FC7\uFF0C\u53C8\u63CE\u4E86\u786C\u7269\uFF0C\u76F4\u6485\u6485\u633A\u7740\uFF0C\u6BD4\u751F\u524D\u8FD8\u8981\u7C97\u957F\u51E0\u5206\u3002\u4ED6\u6402\u4F4F\u5979\uFF0C\u52C9\u5F3A\u6324\u51FA\u4E00\u4E1D\u7B11\u5BB9\uFF0C\u54AC\u7259\u9053\uFF1A\u300C\u7B49\u62FF\u5230\u90A3\u5BB6\u4F19\uFF0C\u770B\u6211\u600E\u4E48\u6536\u62FE\u4ED6\uFF01\u300D</p><p>\u300C\u522B\u3002\u300D\u4E39\u5A18\u751F\u6015\u5B59\u5929\u7FBD\u5403\u4E8F\uFF0C\u54ED\u9053\uFF1A\u300C\u6211\u8EAB\u5B50\u5DF2\u7ECF\u810F\u4E86\u7684\u2026\u2026\u300D\u5979\u54ED\u5F97\u8BF4\u4E0D\u51FA\u8BDD\u6765\u3002</p><p>\u5B59\u5929\u7FBD\u6276\u5979\u8D77\u6765\uFF0C\u300C\u4F60\u53BB\u7167\u770B\u7389\u83B2\uFF0C\u522B\u8BA9\u5979\u8F7B\u751F\u3002\u5BF9\u5979\u8BF4\uFF0C\u6211\u4E0D\u602A\u5979\u3002\u4ED6\u4EEC\u4EC0\u4E48\u65F6\u5019\u8D70\u7684\uFF1F\u300D</p><p>\u300C\u4E2D\u5348\u7684\u65F6\u5019\uFF0C\u8BF4\u662F\u53BB\u63A5\u4EAC\u57CE\u6765\u7684\u5927\u96C4\u5B9D\u6BBF\u3002\u300D</p><p>\u300C\u4F55\u6E05\u6CB3\uFF01\u4ED6\u5DF2\u7ECF\u6765\u4E86\uFF1F\uFF01\u300D\u5B59\u5929\u7FBD\u970D\u7136\u8D77\u8EAB\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u4F60\u5C31\u662F\u767D\u96EA\u83B2\u4E48\uFF1F\u300D</p><p>\u5802\u4E0B\u4E00\u7247\u6C89\u5BC2\u3002</p><p>\u90A3\u8001\u8005\u7FFB\u7740\u5377\u5B97\uFF0C\u7F13\u7F13\u9053\uFF1A\u300C\u5C14\u7236\u767D\u5B5D\u5112\u662F\u600E\u4E48\u6B7B\u7684\uFF1F\u300D</p><p>\u767D\u96EA\u83B2\u672C\u6765\u6253\u5B9A\u4E3B\u610F\uFF0C\u50CF\u4EE5\u5F80\u4E00\u6837\u4E00\u8A00\u4E0D\u53D1\uFF0C\u5230\u670D\u8FA8\u65F6\u62D2\u7EDD\u8BA4\u7F6A\uFF0C\u542C\u5230\u6B64\u8A00\u5979\u970D\u7136\u62AC\u5934\uFF0C\u671B\u5411\u5802\u4E0A\u9AD8\u5750\u7684\u8001\u4EBA\u3002\u9664\u4E86\u72F1\u5352\u4EEC\u89C1\u60EF\u4E86\u4EE5\u5916\uFF0C\u5176\u5B83\u4EBA\u5FC3\u91CC\u90FD\u662F\u4E00\u52A8\u3002</p><p>\u957F\u65F6\u95F4\u7684\u5730\u7262\u56DA\u7981\uFF0C\u4F7F\u5979\u7684\u76AE\u80A4\u6709\u79CD\u75C5\u6001\u7684\u82CD\u767D\u3002\u4F46\u4E94\u4E2A\u591A\u6708\u7684\u6298\u78E8\uFF0C\u5E76\u672A\u4F7F\u5979\u7684\u7F8E\u8C8C\u6194\u60B4\u51CB\u96F6\u3002\u53CD\u800C\u7709\u773C\u6E10\u5F00\uFF0C\u591A\u4E86\u51E0\u5206\u4F7F\u5979\u7F9E\u803B\u7684\u5C11\u5987\u98CE\u60C5\uFF0C\u518D\u975E\u5C11\u5973\u65F6\u8282\u7684\u7EAF\u51C0\u4E4B\u6001\u3002</p><p>\u968F\u884C\u7684\u51E0\u4EBA\u90FD\u662F\u8001\u4E8E\u5211\u540D\u7684\u719F\u540F\uFF0C\u6700\u957F\u4E8E\u5BDF\u989C\u89C2\u8272\uFF0C\u4E00\u773C\u4FBF\u770B\u51FA\u767D\u96EA\u83B2\u5DF2\u975E\u5B8C\u74A7\uFF0C\u60F3\u5230\u5377\u4E2D\u8BF4\u5176\u4E91\u82F1\u672A\u5AC1\uFF0C\u5F85\u5B57\u95FA\u4E2D\uFF0C\u5FC3\u4E0B\u4FBF\u77E5\u4E86\u51E0\u5206\u3002</p><p>\u6B63\u597D\u8001\u8005\u4E5F\u62AC\u8D77\u773C\u775B\u671D\u767D\u96EA\u83B2\u770B\u6765\uFF0C\u4ED6\u8EAB\u6750\u7626\u5C0F\u4F5D\u507B\uFF0C\u90A3\u8EAB\u5B98\u670D\u663E\u5F97\u53C8\u5BBD\u53C8\u5927\uFF0C\u4E4C\u7EB1\u5E3D\u4E5F\u4F3C\u4E4E\u5927\u4E86\u4E00\u5708\uFF0C\u770B\u4E0A\u53BB\u5C31\u50CF\u501F\u4E86\u8EAB\u5B98\u670D\u80E1\u4E71\u62AB\u5728\u8EAB\u4E0A\uFF0C\u4E0E\u524D\u4E9B\u5929\u88C5\u626E\u7684\u5B98\u5458\u76F8\u5DEE\u65E0\u51E0\u3002\u53EA\u662F\u4ED6\u53CC\u76EE\u867D\u7136\u4E0D\u505C\u6D41\u6CEA\uFF0C\u773C\u795E\u5374\u6CA1\u6709\u4E1D\u6BEB\u542B\u7CCA\u3002</p><p>\u767D\u96EA\u83B2\u5634\u5507\u8815\u52A8\u51E0\u4E0B\uFF0C\u8BF4\u9053\uFF1A\u300C\u51A4\u6789\u554A\uFF01\u5927\u4EBA\uFF01\u300D</p><p>\u65C1\u8FB9\u7684\u5218\u8FA8\u673A\u80CC\u540E\u60CA\u51FA\u4E00\u5C42\u51B7\u6C57\uFF0C\u8FDE\u5353\u5929\u96C4\u4E5F\u811A\u4E0B\u5FAE\u52A8\uFF0C\u4F3C\u4E4E\u60F3\u62D4\u8DB3\u98DE\u5954\u3002</p><p>\u4F55\u6E05\u6CB3\u82CD\u8001\u7684\u9762\u5B54\u6CA1\u6709\u4E1D\u6BEB\u5F02\u6837\uFF0C\u4F9D\u7136\u7528\u6DE1\u6DE1\u7684\u8BED\u6C14\u9053\uFF1A\u300C\u4F60\u6709\u4F55\u51A4\u6789\uFF1F\u300D</p><p>\u767D\u96EA\u83B2\u51C4\u58F0\u9053\uFF1A\u300C\u6C11\u5973\u767D\u96EA\u83B2\uFF0C\u8981\u544A\u8FD9\u8C7A\u72FC\u5761\u76D1\u72F1\u4E0A\u4E0B\u52FE\u7ED3\uFF0C\u65E0\u6CD5\u65E0\u5929\uFF0C\u8BEC\u9677\u826F\u6C11\uFF0C\u51A4\u6740\u6211\u7236\uFF0C\u903C\u5978\u6211\u6BCD\uFF0C\u975E\u5211\u903C\u4F9B\uFF0C\u79C1\u5978\u5973\u72AF\uFF01\u300D</p><p>\u6B64\u8A00\u4E00\u51FA\uFF0C\u4E00\u65C1\u7684\u5B81\u8FDC\u77E5\u53BF\u4E5F\u5750\u4E0D\u4F4F\u4E86\uFF0C\u5FD9\u8D77\u8EAB\u559D\u9053\uFF1A\u300C\u5927\u80C6\u9006\u532A\uFF0C\u4F11\u5F97\u65E0\u793C\uFF01\u300D</p><p>\u4F55\u6E05\u6CB3\u62FF\u5E15\u5B50\u62B9\u4E86\u62B9\u6CEA\u6C34\uFF0C\u6162\u541E\u541E\u9053\uFF1A\u300C\u8D35\u53BF\u4E0D\u5FC5\u52A8\u6012\uFF0C\u662F\u975E\u66F2\u76F4\uFF0C\u7EC8\u6709\u516C\u65AD\u3002\u767D\u96EA\u83B2\uFF0C\u672C\u5B98\u4E14\u95EE\u4F60\uFF0C\u4F60\u8BF4\u7684\u8BEC\u9677\u7A76\u7ADF\u662F\u4F55\u610F\u554A\uFF1F\u300D</p><p>\u767D\u96EA\u83B2\u5438\u4E86\u53E3\u6C14\uFF0C\u7A33\u4F4F\u5FC3\u795E\uFF0C\u300C\u6C11\u5973\u767D\u96EA\u83B2\uFF0C\u672C\u662F\u7F57\u9704\u6D3E\u5F1F\u5B50\u3002\u5E74\u521D\u88AB\u5E08\u95E8\u8350\u4E3A\u6355\u5FEB\uFF0C\u4E8C\u6708\u5949\u547D\u4E0B\u5C71\uFF0C\u62DC\u89C1\u5E08\u53D4\u5434\u5927\u5F6A\u2026\u2026\u300D</p><p>\u516C\u5802\u5185\u4E00\u58F0\u54B3\u55FD\u4E5F\u65E0\uFF0C\u53EA\u6709\u767D\u96EA\u83B2\u51C4\u7136\u7684\u58F0\u97F3\u5728\u5385\u5185\u56DE\u8361\u3002\u5979\u4ECE\u4E0B\u5C71\u8BF4\u8D77\uFF0C\u539F\u539F\u672C\u672C\u8BB2\u4E86\u72F1\u5352\u4EEC\u5982\u4F55\u8BBE\u8BA1\u8BEC\u9677\uFF0C\u6740\u4EBA\u706D\u53E3\uFF0C\u903C\u5978\u6B3A\u8BC8\u7B49\u7B49\u6076\u884C\u3002</p><p>\u7B49\u5979\u8BF4\u5B8C\uFF0C\u4F55\u6E05\u6CB3\u5E26\u6765\u7684\u4E00\u540D\u4E66\u540F\u4E5F\u5C06\u5979\u7684\u53E3\u8FF0\u5F55\u4E0B\uFF0C\u5448\u5230\u5802\u4E0A\u3002\u5B81\u8FDC\u77E5\u53BF\u6C57\u6D41\u6D43\u80CC\uFF0C\u514D\u51A0\u8DEA\u5230\u5802\u4E0B\uFF0C\u300C\u4F55\u5927\u4EBA\u660E\u9274\uFF0C\u6B64\u4E8B\u4E0B\u5B98\u5B9E\u4E0D\u77E5\u60C5\u3002\u300D</p><p>\u4F55\u6E05\u6CB3\u7FFB\u7740\u767D\u96EA\u83B2\u7684\u53E3\u5F55\uFF0C\u53F9\u9053\uFF1A\u300C\u8D77\u6765\u5427\u3002\u8FD9\u4E5F\u6028\u4E0D\u5F97\u4F60\u3002\u300D</p><p>\u4F9D\u7167\u5F8B\u6CD5\uFF0C\u65E0\u8BBA\u5927\u6848\u5C0F\u6848\uFF0C\u90FD\u8BE5\u7531\u5E9C\u53BF\u5BA1\u660E\u4E0A\u62A5\u3002\u4F46\u5982\u4ECA\u4E1C\u5382\u6743\u503E\u671D\u91CE\uFF0C\u526F\u90FD\u592A\u76D1\u5C01\u5FB7\u660E\u5750\u9547\u9F99\u6E90\uFF0C\u8282\u5236\u516D\u7701\u519B\u653F\uFF0C\u4E3A\u9632\u4ED6\u4EBA\u62A2\u529F\uFF0C\u660E\u4EE4\u8C0B\u9006\u5927\u6848\u5E9C\u53BF\u65E0\u6743\u8FC7\u95EE\uFF0C\u4E00\u5F8B\u62A5\u7701\u6309\u5BDF\u53F8\uFF0C\u4EAC\u5E08\u5211\u90E8\u3001\u90FD\u5BDF\u9662\u3001\u5927\u7406\u5BFA\u5904\u7F6E\u3002</p><p>\u4F55\u6E05\u6CB3\u6DF1\u77E5\u5176\u4E2D\u60C5\u5F0A\uFF0C\u4F46\u5929\u5B50\u6570\u5341\u5E74\u4E0D\u7406\u671D\u653F\uFF0C\u5185\u5916\u4EA4\u901A\u88AB\u6743\u76D1\u4E00\u624B\u638C\u63E1\uFF0C\u4ED6\u4E5F\u65E0\u529B\u56DE\u5929\uFF0C\u53EA\u80FD\u53F9\u606F\u800C\u5DF2\u3002</p><p>\u53C8\u770B\u4E86\u904D\u4F9B\u8FF0\uFF0C\u4F55\u6E05\u6CB3\u95EE\u9053\uFF1A\u300C\u4F60\u6240\u8BC9\u51A4\u60C5\u7EC8\u662F\u4E00\u9762\u4E4B\u8F9E\uFF0C\u72F1\u65B9\u65E2\u6709\u67E5\u83B7\u5BC6\u4FE1\uFF0C\u53C8\u6709\u4EBA\u8BC1\uFF0C\u7109\u77E5\u4F60\u4E0D\u662F\u53CD\u54AC\u72F1\u65B9\uFF0C\u4EE5\u56FE\u8131\u7F6A\uFF1F\u6765\u4EBA\u554A\uFF0C\u5E26\u859B\u971C\u7075\u3002\u300D</p><p>\u300C\u7F6A\u56DA\u859B\u971C\u7075\u53E9\u89C1\u5927\u4EBA\u3002\u300D</p><p>\u859B\u971C\u7075\u8BDD\u8BF4\u5F97\u6CA1\u534A\u5206\u5DEE\u9519\uFF0C\u53E3\u6C14\u5374\u6EE1\u4E0D\u662F\u90A3\u4E48\u56DE\u4E8B\u3002\u5979\u8BF4\u7740\u53E9\u9996\uFF0C\u5374\u8FDE\u8170\u90FD\u672A\u5F2F\u4E00\u4E0B\uFF0C\u53EA\u6602\u7740\u5934\uFF0C\u8086\u65E0\u5FCC\u60EE\u5730\u770B\u7740\u4F55\u6E05\u6CB3\u3002</p><p>\u5B81\u8FDC\u77E5\u53BF\u8EAB\u4E3A\u6B64\u5730\u7236\u6BCD\u5B98\uFF0C\u5FD9\u559D\u9053\uFF1A\u300C\u8BA4\u771F\u56DE\u590D\u5927\u4EBA\u95EE\u8BDD\uFF0C\u4E0D\u5F97\u65E0\u793C\uFF01\u300D</p><p>\u859B\u971C\u7075\u300C\u6251\u55E4\u300D\u4E00\u7B11\uFF0C\u300C\u4F60\u6025\u4EC0\u4E48\uFF1F\u7F6A\u56DA\u53EA\u662F\u6CA1\u89C1\u8FC7\u4F55\u5927\u4EBA\uFF0C\u60F3\u4ED4\u7EC6\u770B\u770B\u7F62\u4E86\u3002\u300D</p><p>\u5B81\u8FDC\u77E5\u53BF\u5FC3\u4E0B\u6697\u6697\u53EB\u82E6\uFF0C\u8FD9\u6848\u5B50\u4ED6\u5E76\u672A\u63D2\u624B\uFF0C\u53EA\u63A5\u5230\u72F1\u65B9\u7684\u6587\u4E66\uFF0C\u4E0A\u9762\u5199\u5F97\u5929\u82B1\u4E71\u5760\uFF0C\u5982\u4F55\u6697\u67E5\u532A\u5E97\uFF0C\u6355\u62FF\u9006\u532A\uFF0C\u7F34\u83B7\u5BC6\u4FE1\u4E00\u5C01\uFF0C\u8FDE\u591C\u5BA1\u8BAF\u4E0B\uFF0C\u9006\u532A\u5DF2\u7ECF\u4F9B\u8BA4\u4E0D\u8BB3\u4E91\u4E91\uFF0C\u8C01\u77E5\u9053\u4F55\u6E05\u6CB3\u8FD8\u672A\u4E0B\u8F7F\uFF0C\u5C31\u5148\u62FF\u4E86\u76D1\u72F1\u72F1\u6B63\uFF0C\u63A5\u7740\u662F\u767D\u96EA\u83B2\u5F53\u5802\u558A\u51A4\uFF0C\u7136\u540E\u53C8\u6765\u4E2A\u859B\u971C\u7075\uFF0C\u5728\u516C\u5802\u4E0A\u5982\u6B64\u653E\u8086\u3002</p><p>\u4F55\u5927\u4EBA\u82E5\u662F\u602A\u7F6A\u4E0B\u6765\uFF0C\u4E00\u4E2A\u300C\u660F\u6126\u300D\u7684\u8003\u8BED\uFF0C\u5C31\u65AD\u9001\u4E86\u4ED6\u7684\u524D\u7A0B\u3002\u60F3\u7740\u4ED6\u624B\u91CC\u634F\u4E86\u4E00\u628A\u51B7\u6C57\u3002\u5077\u773C\u53BB\u770B\u4F55\u6E05\u6CB3\u3002</p><p>\u4F55\u6E05\u6CB3\u5374\u4E0D\u52A8\u58F0\u8272\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u6211\u5C31\u662F\u4F55\u6E05\u6CB3\uFF0C\u770B\u4ED4\u7EC6\u4E86\u5427\u3002\u859B\u971C\u7075\uFF0C\u4F60\u53E3\u4F9B\u4E2D\u81EA\u8FF0\u4E3A\u767D\u83B2\u6559\u532A\uFF0C\u53EF\u662F\u5B9E\u8A00\uFF1F\u300D</p><p>\u859B\u971C\u7075\u7FFB\u4E86\u7FFB\u773C\u775B\uFF0C\u300C\u662F\u767D\u83B2\u6559\uFF0C\u5374\u4E0D\u662F\u4EC0\u4E48\u532A\u3002\u6211\u6559\u7EA2\u9633\u771F\u4EBA\u4E43\u5F25\u52D2\u4F5B\u8F6C\u4E16\uFF0C\u8981\u5C06\u5929\u4E0B\u5EFA\u6210\u5F25\u52D2\u4E16\u754C\uFF0C\u53D1\u5927\u5584\u5FC3\uFF0C\u7ACB\u5927\u5B8F\u613F\uFF0C\u600E\u4E48\u662F\u532A\uFF1F\u300D</p><p>\u4F55\u6E05\u6CB3\u6BEB\u4E0D\u52A8\u5BB9\uFF0C\u7528\u523B\u677F\u7684\u58F0\u97F3\u7EE7\u7EED\u95EE\u9053\uFF1A\u300C\u672C\u5E74\u4E8C\u6708\u5341\u4E8C\u65E5\uFF0C\u4F60\u5230\u795E\u4ED9\u5CAD\u674F\u82B1\u6751\u9152\u5E97\uFF0C\u62FF\u53D6\u5BC6\u4FE1\uFF0C\u53EF\u662F\u6709\u7684\uFF1F\u300D</p><p>\u859B\u971C\u7075\u60F3\u4E5F\u4E0D\u60F3\uFF0C\u5E94\u58F0\u9053\uFF1A\u300C\u6709\uFF01\u300D</p><p>\u300C\u662F\u767D\u5B5D\u5112\u4EA4\u4E88\u4F60\u7684\u5417\uFF1F\u300D</p><p>\u300C\u662F\uFF01\u300D</p><p>\u6B64\u8A00\u4E00\u51FA\uFF0C\u767D\u96EA\u83B2\u5FFF\u7136\u62AC\u5934\uFF0C\u9762\u65E0\u4EBA\u8272\u7684\u5218\u8FA8\u673A\u548C\u4E00\u73ED\u72F1\u5352\u90FD\u677E\u4E86\u53E3\u6C14\u3002</p><p>\u859B\u971C\u7075\u7B11\u4E86\u7B11\uFF0C\u6717\u58F0\u9053\uFF1A\u300C\u767D\u5B5D\u5112\u662F\u5C01\u795E\u7684\u59DC\u5B50\u7259\u8F6C\u4E16\uFF0C\u4ECE\u5929\u5EAD\u53D6\u4E86\u7389\u5E1D\u8BCF\u4E66\uFF0C\u6B7B\u540E\u711A\u7ED9\u6C11\u5973\uFF0C\u6B64\u4E8B\u6709\u5341\u6BBF\u960E\u7F57\u4F5C\u8BC1\uFF0C\u7EDD\u65E0\u865A\u5047\uFF01\u300D</p><p>\u5218\u8FA8\u673A\u77A0\u76EE\u7ED3\u820C\u3002</p><p>\u8859\u5F79\u73ED\u91CC\u4F20\u6765\u51E0\u58F0\u5077\u7B11\uFF0C\u6709\u4EBA\u5600\u5495\u9053\uFF1A\u300C\u83AB\u4E0D\u662F\u5931\u5FC3\u75AF\u4E86\uFF1F\u300D\u5B81\u8FDC\u77E5\u53BF\u9762\u9732\u5C34\u5C2C\uFF0C\u8DEA\u4E0B\u53C8\u8981\u8C22\u7F6A\u3002\u4F55\u6E05\u6CB3\u5374\u542C\u5F97\u5341\u5206\u4E13\u6CE8\uFF0C\u6446\u4E86\u6446\u624B\u8BA9\u77E5\u53BF\u8D77\u8EAB\uFF0C\u95EE\u9053\uFF1A\u300C\u672C\u5B98\u95EE\u7684\u662F\u90A3\u5C01\u5BC6\u4FE1\u2014\u2014\u5F55\u6709\u8BF8\u7701\u6559\u532A\u540D\u59D3\u7684\u4FE1\u4EF6\u3002\u300D</p><p>\u859B\u971C\u7075\u8BA4\u771F\u8BF4\u9053\uFF1A\u300C\u662F\u4E00\u9053\u8BCF\u4E66\uFF0C\u767D\u5B5D\u5112\u6B7B\u540E\u4E0A\u5F97\u5929\u5EAD\uFF0C\u7389\u5E1D\u9881\u7ED9\u4E86\u4ED6\uFF0C\u8BCF\u4E66\u5C01\u6211\u4E3A\u4F0F\u9B54\u5927\u5E1D\u3002\u300D</p><p>\u4F55\u6E05\u6CB3\u70B9\u4E86\u70B9\u5934\uFF0C\u4E0D\u518D\u8FFD\u95EE\uFF0C\u53C8\u9053\uFF1A\u300C\u4F60\u4F9B\u8FF0\u4E2D\u79F0\u767D\u96EA\u83B2\u662F\u767D\u83B2\u6559\u540C\u515A\uFF0C\u767D\u96EA\u83B2\u5374\u79F0\u51A4\u6789\uFF0C\u5982\u4ECA\u4F60\u4EEC\u4FE9\u5C31\u5728\u6B64\u5904\uFF0C\u53EF\u4EE5\u5F53\u5802\u5BF9\u8D28\u3002\u300D</p><p>\u767D\u96EA\u83B2\u8FD8\u672A\u5F00\u53E3\uFF0C\u859B\u971C\u7075\u5C31\u4E0D\u5C51\u5730\u51B7\u7B11\u4E00\u58F0\uFF0C\u300C\u767D\u96EA\u83B2\u662F\u4E5D\u5C3E\u6D6A\u72D0\u8F6C\u4E16\uFF0C\u5929\u751F\u7684\u6DEB\u6750\u513F\uFF0C\u89C1\u4E2A\u7537\u4EBA\u5C31\u52FE\u5F15\u3002\u4E00\u8EAB\u7684\u597D\u76AE\u8089\u8D31\u5F97\u8981\u6B7B\uFF0C\u6CA1\u4E8B\u5C31\u559C\u6B22\u60F9\u4EBA\u52A8\u5FC3\uFF0C\u4F55\u5927\u4EBA\uFF0C\u4F60\u6252\u4E86\u5979\u7684\u8863\u670D\uFF0C\u72E0\u72E0\u6253\u5979\u4E00\u987F\u677F\u5B50\uFF0C\u518D\u62FF\u6839\u94A2\u9488\u5728\u5979\u5976\u5B50\u91CC\u6345\u51E0\u4E0B\uFF0C\u5979\u5C31\u5B89\u5206\u4E86\u3002\u300D</p><p>\u767D\u96EA\u83B2\u6DA8\u7EA2\u4E86\u8138\uFF0C\u54AC\u7740\u7259\u6CA1\u6709\u4F5C\u58F0\uFF0C\u4F55\u6E05\u6CB3\u62FF\u5E15\u5B50\u62B9\u7740\u773C\u6CEA\u9053\uFF1A\u300C\u6211\u95EE\u4F60\u4EEC\u662F\u4E0D\u662F\u540C\u515A\u3002\u300D</p><p>\u300C\u5F53\u7136\u662F\u3002\u6211\u662F\u8718\u86DB\u7CBE\u8F6C\u4E16\uFF0C\u8DDF\u59D3\u767D\u7684\u72D0\u72F8\u7CBE\u4E00\u6837\uFF0C\u90FD\u662F\u6B7B\u8D31\u7684\u6D6A\u5A4A\u5B50\u3002\u524D\u751F\u6211\u4EEC\u4FE9\u88AB\u5929\u5175\u5929\u5C06\u62FF\u4F4F\uFF0C\u4E00\u5757\u513F\u7834\u4E86\u8EAB\u5B50\uFF0C\u7136\u540E\u53C8\u88AB\u62BC\u7ED9\u5730\u5E9C\uFF0C\u7ED9\u725B\u5934\u9A6C\u9762\u5F53\u4E86\u5356\u5C44\u9012\u5C41\u773C\u513F\u7684\u8D31\u8D27\u2014\u2014\u4E0A\u8F88\u5B50\u5C31\u8BA4\u8BC6\u7684\uFF0C\u600E\u4E48\u4E0D\u662F\u540C\u515A\uFF1F\u300D</p><p>\u5218\u8FA8\u673A\u62A2\u4E0A\u6765\u9053\uFF1A\u300C\u542F\u7980\u5927\u4EBA\uFF0C\u859B\u72AF\u5165\u72F1\u4EE5\u6765\u795E\u667A\u6E10\u5931\uFF0C\u6EE1\u53E3\u80E1\u8A00\u4E71\u8BED\uFF01\u4F46\u4E0E\u767D\u5B5D\u5112\u52FE\u7ED3\uFF0C\u4E0E\u767D\u96EA\u83B2\u540C\u4E3A\u9006\u532A\u7B49\u4E8B\uFF0C\u4F3C\u5C5E\u65E0\u7591\u3002\u300D</p><p>\u859B\u971C\u7075\u7B11\u541F\u541F\u9053\uFF1A\u300C\u5218\u5927\u7237\u8BF4\u7684\u662F\u3002\u8BF7\u592A\u4E0A\u8001\u541B\u660E\u9274\u3002\u300D</p><p>\u5218\u8FA8\u673A\u5FC3\u91CC\u6068\u5F97\u8981\u6B7B\uFF0C\u859B\u971C\u7075\u8FD9\u8D31\u5A4A\u5B50\uFF0C\u79C1\u4E0B\u91CC\u7B54\u5E94\u5F97\u597D\u597D\u7684\uFF0C\u5230\u4E86\u5802\u4E0A\u5374\u88C5\u75AF\u5356\u50BB\uFF0C\u6446\u660E\u4E86\u8981\u4ED6\u4EEC\u597D\u770B\u3002</p><p>\u5218\u8FA8\u673A\u7684\u5FC3\u4E00\u6A2A\uFF0C\u8BE5\u6B7B\u7403\u671D\u4E0A\uFF0C\u62DA\u4E2A\u9C7C\u6B7B\u7F51\u7834\u518D\u8BF4\uFF0C\u300C\u7980\u5927\u4EBA\uFF0C\u859B\u72AF\u795E\u667A\u5DF2\u5931\uFF0C\u65E0\u6CD5\u5BF9\u8D28\uFF0C\u4F46\u672C\u6848\u8FD8\u6709\u4E00\u4F4D\u4EBA\u8BC1\u3002\u300D</p><p>\u300C\u5514\uFF1F\u4E14\u5E26\u4E0A\u6765\u3002\u300D</p><p>\u5218\u8FA8\u673A\u53EB\u9053\uFF1A\u300C\u5E26\u88F4\u72AF\u9752\u7389\uFF01\u300D</p><p>\u672C\u6765\u662F\u4E00\u6869\u8C0B\u9006\u5927\u6848\uFF0C\u4E3B\u72AF\u62B5\u6B7B\u4E0D\u8BA4\uFF0C\u53CD\u9053\u72F1\u65B9\u8BEC\u9677\uFF0C\u8BC1\u4EBA\u53C8\u662F\u6EE1\u53E3\u75AF\u8A00\u75AF\u8BED\uFF0C\u5F04\u6210\u4E86\u4E00\u573A\u8352\u5510\u95F9\u5267\u3002\u968F\u6765\u7684\u8859\u5F79\u4EEC\u867D\u7136\u7EF7\u7740\u8138\uFF0C\u5FC3\u91CC\u90FD\u6697\u6697\u597D\u7B11\u3002</p><p>\u76D1\u72F1\u7684\u72F1\u5352\u53C8\u662F\u4E00\u79CD\u60C5\u5F62\u3002\u9C8D\u6A2A\u7B49\u4EBA\u88AB\u6346\u7740\u8DEA\u5728\u9636\u4E0B\uFF0C\u5982\u540C\u56DA\u72AF\uFF0C\u5176\u5B83\u4EBA\u8138\u4E0A\u65F6\u9752\u65F6\u767D\uFF0C\u90FD\u77E5\u9053\u5927\u4E8B\u4E0D\u5999\u3002\u5218\u8FA8\u673A\u6296\u7740\u9F20\u987B\u6C14\u6025\u8D25\u574F\uFF0C\u5353\u5929\u96C4\u5219\u7528\u773C\u89D2\u4F59\u5149\u56DB\u5904\u770B\u7740\uFF0C\u4E07\u4E00\u4F55\u6E05\u6CB3\u7FFB\u8138\uFF0C\u5F53\u5802\u62FF\u4EBA\uFF0C\u4ED6\u5C31\u8981\u6740\u51FA\u91CD\u56F4\uFF0C\u8FDC\u8D70\u9AD8\u98DE\u4E86\u3002</p><p>\u859B\u971C\u7075\u5DE6\u53F3\u90FD\u662F\u6B7B\uFF0C\u5012\u628A\u751F\u6B7B\u7F6E\u4E4B\u5EA6\u5916\uFF0C\u4EF0\u7740\u8138\uFF0C\u4E00\u8138\u6EE1\u4E0D\u5728\u4E4E\u7684\u795E\u60C5\u3002\u767D\u96EA\u83B2\u624B\u6307\u5FAE\u5FAE\u53D1\u98A4\uFF0C\u5979\u6CA1\u60F3\u5230\u5A18\u59E8\u4E5F\u4F1A\u6210\u4E86\u72F1\u65B9\u7684\u4EBA\u8BC1\uFF0C\u5A18\u59E8\u7684\u4F9B\u8BCD\u7A76\u7ADF\u4F1A\u662F\u4EC0\u4E48\u5462\uFF1F</p><p>\u5B81\u8FDC\u77E5\u53BF\u88AB\u5F04\u5F97\u624B\u8DB3\u65E0\u63AA\uFF0C\u54C1\u5473\u7740\u859B\u971C\u7075\u7684\u4F9B\u8BCD\uFF0C\u60F3\u7B11\u53C8\u7B11\u4E0D\u51FA\u6765\u3002\u826F\u4E45\u642D\u8BAA\u9053\uFF1A\u300C\u8001\u5927\u4EBA\u7684\u773C\u775B\u75C5\u4E86\u6709\u4E9B\u65E5\u5B50\u4E86\u3002\u300D</p><p>\u4F55\u6E05\u6CB3\u4F3C\u4E4E\u5BF9\u516C\u5802\u4E0A\u8FD9\u4E9B\u8352\u5510\u53F8\u7A7A\u89C1\u60EF\uFF0C\u795E\u8272\u5168\u65E0\u5F02\u6837\u3002\u77E5\u53BF\u7684\u8BDD\u867D\u7136\u4E0D\u5927\u59A5\u5F53\uFF0C\u4F55\u6E05\u6CB3\u4E5F\u4E0D\u6B32\u7ED9\u4ED6\u96BE\u582A\uFF0C\u4E00\u8FB9\u64E6\u7740\u773C\u775B\uFF0C\u4E00\u8FB9\u5E94\u9053\uFF1A\u300C\u5F80\u5E74\u71AC\u591C\u770B\u5377\u5B50\uFF0C\u88AB\u70DF\u718F\u7684\u3002\u6CBB\u4E0D\u597D\u4E5F\u5C31\u4E0D\u518D\u6CBB\u4E86\u3002\u300D</p><p>\u5B81\u8FDC\u77E5\u53BF\u5FD9\u9053\uFF1A\u300C\u54CE\u5440\uFF0C\u8001\u5927\u4EBA\u662F\u56FD\u4E4B\u5E72\u57CE\uFF0C\u600E\u80FD\u5982\u6B64\u64CD\u52B3\u3002\u4E0B\u5B98\u8BA4\u8BC6\u51E0\u4E2A\u5927\u592B\uFF0C\u8BF7\u6765\u7ED9\u5927\u4EBA\u770B\u770B\u5982\u4F55\uFF1F\u300D</p><p>\u4F55\u6E05\u6CB3\u70B9\u5934\u6577\u884D\u9053\uFF1A\u300C\u591A\u8C22\u591A\u8C22\u3002\u300D</p><p>\u8BF4\u8BDD\u95F4\u7389\u5A18\u5DF2\u88AB\u5E26\u5230\u5802\u4E0A\uFF0C\u5979\u7A7F\u4E86\u8EAB\u9752\u8863\uFF0C\u8E59\u7740\u7709\u5934\uFF0C\u6EE1\u9762\u60CA\u6050\uFF0C\u4E00\u53CC\u5C0F\u811A\u9A87\u5F97\u51E0\u4E4E\u8F6F\u4E86\uFF0C\u88AB\u4EBA\u6276\u7740\u52C9\u5F3A\u8D70\u5230\u5802\u4E0A\uFF0C\u4FBF\u4F4E\u7740\u5934\u8DEA\u5728\u5730\u4E0A\uFF0C\u8EAB\u5B50\u4E0D\u4F4F\u6218\u6817\u3002</p><p>\u5218\u8FA8\u673A\u68EE\u7136\u9053\uFF1A\u300C\u88F4\u72AF\uFF0C\u5C06\u4F60\u7684\u4F9B\u8BCD\u7ED9\u4E24\u4F4D\u5927\u4EBA\u8BF4\u8BF4\u3002\u300D</p><p>\u300C\u7F6A\u5987\u88F4\u9752\u7389\uFF0C\u7F57\u9704\u5C71\u4EBA\u6C0F\u2026\u2026\u300D\u7389\u5A18\u8FDE\u5934\u4E5F\u4E0D\u6562\u62AC\uFF0C\u98A4\u58F0\u9053\uFF1A\u300C\u4E43\u9006\u532A\u767D\u5B5D\u5112\u59BB\u59B9\u3002\u767D\u5B5D\u5112\u6240\u5F00\u9152\u5E97\u5B9E\u4E3A\u767D\u83B2\u6559\u7A9D\u70B9\uFF0C\u7F6A\u5987\u2026\u2026\u662F\u77E5\u9053\u7684\u3002\u5BB6\u59CA\u88F4\u4E39\u674F\u5AC1\u4E88\u767D\u5B5D\u5112\uFF0C\u751F\u6709\u4E24\u5973\u4E00\u5B50\uFF0C\u6848\u53D1\u540E\u5176\u5B50\u66FE\u5230\u6211\u5904\u2026\u2026\u540E\u4E0D\u77E5\u6240\u7EC8\u2026\u2026\u300D</p><p>\u300C\u4F60\u80E1\u8BF4\uFF01\u300D</p><p>\u767D\u96EA\u83B2\u51C4\u58F0\u9053\uFF1A\u300C\u5A18\u59E8\uFF01\u4F60\u8BF4\u5B9E\u8BDD\u554A\uFF0C\u82F1\u83B2\u88AB\u4ED6\u4EEC\u56DA\u5230\u72F1\u91CC\u2026\u2026\u4F55\u5927\u4EBA\u662F\u6E05\u5B98\uFF0C\u5FC5\u80FD\u7ED9\u6211\u4EEC\u4E2A\u516C\u9053\u7684\uFF01\u300D</p><p>\u859B\u971C\u7075\u5374\u55E4\u7B11\u4E00\u58F0\uFF0C\u81EA\u8A00\u81EA\u8BED\u9053\uFF1A\u300C\u8BA4\u51FA\u6765\u4E86\uFF0C\u662F\u725B\u9B54\u738B\u517B\u7684\u90A3\u53EA\u9E66\u9E49\u7CBE\uFF0C\u8F6C\u4E16\u6210\u4E86\u8FD9\u4E2A\u9A9A\u8D27\u3002\u4E0D\u77E5\u9053\u5403\u4E86\u591A\u5C11\u7537\u4EBA\u7684\u9A9A\u4E1C\u897F\uFF0C\u5B66\u5F97\u4E00\u6761\u597D\u820C\u3002\u300D</p><p>\u7389\u5A18\u8138\u4E0A\u9752\u7EA2\u4E0D\u5B9A\uFF0C\u5979\u7D27\u5F20\u4E4B\u4E0B\uFF0C\u6559\u597D\u7684\u8BDD\u8BF4\u5F97\u98A0\u4E09\u5012\u56DB\uFF0C\u754F\u60E7\u5730\u770B\u4E86\u4F55\u6E05\u6CB3\u4E00\u773C\uFF0C\u4E0D\u6562\u5F00\u53E3\u3002</p><p>\u5218\u8FA8\u673A\u671D\u767D\u96EA\u83B2\u559D\u9053\uFF1A\u300C\u5927\u80C6\u5201\u72AF\uFF01\u4F60\u6562\u5486\u54EE\u516C\u5802\uFF01\u300D</p><p>\u4F55\u6E05\u6CB3\u9053\uFF1A\u300C\u5514\u3002\u767D\u96EA\u83B2\uFF0C\u4F60\u4E14\u4E0D\u8BB8\u63D2\u8BDD\uFF0C\u5F85\u88F4\u72AF\u8BB2\u5B8C\u518D\u8BF4\u3002\u88F4\u72AF\uFF0C\u4F60\u7EE7\u7EED\u8BF4\u3002\u300D</p><p>\u767D\u96EA\u83B2\u54AC\u7259\u5FCD\u4F4F\uFF0C\u53EA\u542C\u7389\u5A18\u8BF4\u9053\uFF1A\u300C\u767D\u5B5D\u5112\u5C61\u8BD5\u4E0D\u7B2C\uFF0C\u5BF9\u671D\u5EF7\u5FC3\u751F\u6028\u6068\uFF0C\u6570\u5E74\u524D\u4FBF\u4E0E\u767D\u83B2\u6559\u52FE\u7ED3\uFF0C\u629B\u5F03\u7956\u4E1A\uFF0C\u4E3E\u5BB6\u8FC1\u5165\u6DF1\u5C71\uFF0C\u6258\u540D\u9152\u5E97\uFF0C\u5B9E\u5219\u6697\u4E2D\u4E3A\u767D\u83B2\u6559\u4F20\u9012\u6D88\u606F\u3002</p><p>\u300C\u540E\u53C8\u6307\u4F7F\u957F\u5973\u767D\u96EA\u83B2\u6295\u5165\u7F57\u9704\u6D3E\u95E8\u4E0B\uFF0C\u4E60\u6B66\u7EC3\u827A\uFF0C\u56FE\u8C0B\u4E0D\u8F68\u3002\u597D\u5728\u2026\u2026\u597D\u5728\u5929\u7F51\u6062\u6062\uFF0C\u88AB\u960E\u72F1\u6B63\u6D1E\u70DB\u5176\u5978\uFF0C\u6D3E\u4EBA\u5C06\u767D\u5B5D\u5112\u7236\u5973\u4E0E\u524D\u6765\u53D6\u4FE1\u7684\u859B\u72AF\u7B49\u4E00\u540C\u6349\u62FF\u5F52\u6848\u3002\u767D\u5B5D\u5112\u8BA4\u7F6A\u540E\u75C5\u6B7B\u72F1\u4E2D\uFF0C\u767D\u96EA\u83B2\u62D2\u4E0D\u8BA4\u7F6A\uFF0C\u88AB\u5173\u62BC\u81F3\u4ECA\uFF0C\u7F6A\u5987\u2026\u2026\u53EF\u4EE5\u4F5C\u8BC1\u3002\u300D</p><p>\u767D\u96EA\u83B2\u521A\u8981\u5F00\u53E3\uFF0C\u4F55\u6E05\u6CB3\u8BF4\u9053\uFF1A\u300C\u6B64\u6848\u7684\u5934\u7EEA\u751A\u591A\uFF0C\u773C\u4E0B\u5929\u8272\u5DF2\u665A\uFF0C\u6682\u4E14\u9000\u5802\uFF0C\u5148\u5C06\u5404\u72AF\u5E26\u56DE\u72F1\u4E2D\uFF0C\u665A\u95F4\u518D\u884C\u5BA1\u7406\u3002\u300D</p><p>\u8859\u5F79\u7980\u9053\uFF1A\u300C\u8BF7\u95EE\u5927\u4EBA\uFF0C\u9C8D\u72F1\u6B63\u7B49\u4EBA\u5982\u4F55\u5904\u7F6E\uFF1F\u300D</p><p>\u300C\u4E00\u5E76\u5E26\u5165\u7262\u5185\u5173\u62BC\u3002\u5176\u5B83\u72F1\u5352\u4E0D\u62D8\uFF0C\u4F46\u4E25\u7981\u51FA\u72F1\u3002\u300D\u4F55\u6E05\u6CB3\u671D\u5B81\u8FDC\u77E5\u53BF\u62F1\u4E86\u62F1\u624B\uFF0C\u300C\u8FD8\u8BF7\u8D35\u53BF\u8D39\u5FC3\uFF0C\u9063\u4EBA\u63A5\u7BA1\u5B88\u67E5\u7B49\u4E8B\u3002\u4ECE\u73B0\u5728\u5230\u6848\u60C5\u67E5\u660E\uFF0C\u6B64\u72F1\u8BB8\u8FDB\u4E0D\u8BB8\u51FA\u3002\u300D</p><p>\u5B81\u8FDC\u77E5\u53BF\u5FD9\u8D77\u8EAB\u5E94\u9053\uFF1A\u300C\u9075\u547D\u3002\u8BF7\u4F55\u5927\u4EBA\u653E\u5FC3\uFF0C\u5351\u804C\u7ACB\u5373\u6D3E\u4EBA\u770B\u7BA1\u3002\u300D</p><p>\u4F55\u6E05\u6CB3\u62F1\u624B\u79F0\u8C22\uFF0C\u7136\u540E\u9053\uFF1A\u300C\u767D\u96EA\u83B2\uFF0C\u4F60\u968F\u672C\u5B98\u5230\u540E\u5802\u6765\u3002\u300D</p>',104);function t(n,_){return r}var o=e(a,[["render",t]]);export{o as default};
