import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const a={},r=p('<h1 id="_50-\u771F\u76F8" tabindex="-1"><a class="header-anchor" href="#_50-\u771F\u76F8" aria-hidden="true">#</a> 50 \u771F\u76F8</h1><p>\u5218\u8FA8\u673A\u5173\u4E0A\u95E8\uFF0C\u7ED5\u5BA4\u75BE\u8D70\u3002\u5353\u5929\u96C4\u62B1\u7740\u80A9\u8180\u5728\u65C1\u770B\u7740\uFF0C\u8FC7\u4E86\u534A\u664C\u9053\uFF1A\u300C\u5218\u592B\u5B50\uFF0C\u73B0\u5728\u8FD8\u6709\u4EC0\u4E48\u8BA1\u7B56\uFF1F\u300D</p><p>\u5218\u8FA8\u673A\u9893\u7136\u5750\u5728\u6905\u4E2D\uFF0C\u5583\u5583\u9053\uFF1A\u300C\u542C\u5929\u7531\u547D\uFF0C\u542C\u5929\u7531\u547D\u2026\u2026\u300D</p><p>\u5353\u5929\u96C4\u72E0\u72E0\u7B11\u9053\uFF1A\u300C\u4F60\u4E0D\u8D70\uFF0C\u6211\u53EF\u8981\u8D70\u4E86\u3002\u300D</p><p>\u5218\u8FA8\u673A\u6D51\u8EAB\u4E00\u9707\uFF0C\u4E0B\u6B7B\u773C\u76EF\u7740\u4ED6\u3002</p><p>\u300C\u8FD8\u7528\u76D8\u7B97\uFF1F\uFF01\u767D\u5A4A\u5B50\u6B7B\u4E0D\u8BA4\u7F6A\uFF0C\u628A\u6848\u5B50\u63ED\u4E86\u4E2A\u5E95\u513F\u6389\uFF1B\u59D3\u859B\u7684\u5A4A\u5B50\u88C5\u75AF\u5356\u50BB\uFF0C\u628A\u4F9B\u8BCD\u6405\u6210\u4E00\u76C6\u70C2\u7CE8\u7CCA\uFF1B\u59D3\u88F4\u7684\u867D\u7136\u542C\u8BDD\uFF0C\u4F46\u4F55\u6E05\u6CB3\u5C82\u662F\u597D\u9A97\u7684\uFF1F\u8981\u4E0D\u4E86\u4E24\u4E0B\uFF0C\u5C31\u6F0F\u4E86\u9985\u3002\u8FD8\u7559\u5728\u8FD9\u91CC\uFF0C\u975E\u7B49\u59D3\u4F55\u7684\u628A\u54B1\u4EEC\u4E00\u9505\u70E9\u4E86\u5417\uFF1F\u300D</p><p>\u5218\u8FA8\u673A\u6014\u6014\u9053\uFF1A\u300C\u8FD9\u4E00\u56DE\u771F\u662F\u5C71\u7A77\u6C34\u5C3D\u4E86\u2026\u2026\u300D</p><p>\u300C\u4E0D\u89C1\u5F97\u3002\u300D\u8BF4\u7740\u5802\u540E\u8D70\u51FA\u4E00\u4E2A\u4EBA\u3002</p><p>\u300C\u5929\u7FBD\uFF01\u300D\u4E24\u4EBA\u5F77\u4F5B\u635E\u5230\u6551\u547D\u7684\u7A3B\u8349\uFF0C\u8FDE\u5FD9\u8D77\u8EAB\u8FCE\u4E0A\u3002</p><p>\u5B59\u5929\u7FBD\u795E\u60C5\u51DD\u91CD\uFF0C\u300C\u6211\u56DE\u6765\u5DF2\u7ECF\u4E00\u4E2A\u65F6\u8FB0\uFF0C\u91CC\u9762\u7684\u52A8\u9759\u6211\u4E5F\u7EA6\u7565\u542C\u89C1\u4E86\u3002\u5148\u8BF4\u4E09\u6869\u4E8B\u2014\u2014\u4E00\u4E2A\u662F\u767D\u83B2\u6559\u5DF2\u88AB\u527F\u5E73\uFF0C\u9664\u7EA2\u9633\u771F\u4EBA\u859B\u7389\u82F1\u6F5C\u9003\u5F85\u6355\u4EE5\u5916\uFF0C\u5176\u5B83\u9006\u532A\u90FD\u88AB\u4E00\u7F51\u64D2\u5C3D\u3002\u300D</p><p>\u8FD9\u662F\u6869\u5927\u4E8B\u3002\u53CD\u4E71\u65E2\u5E73\uFF0C\u9A6C\u4E0A\u5C31\u8BE5\u7ED3\u6848\u4E86\u3002</p><p>\u300C\u7B2C\u4E8C\u6869\uFF0C\u4E24\u5E7F\u7B49\u516D\u7701\u5E9C\u53BF\u8054\u540D\u4E0A\u4E66\uFF0C\u8BF7\u65E8\u7ED9\u7763\u629A\u5927\u4EBA\uFF0C\u5C01\u5FB7\u660E\u5C01\u516C\u516C\u7ACB\u751F\u7960\u3002\u300D</p><p>\u300C\u7B2C\u4E09\u6869\uFF0C\u8C7A\u72FC\u5761\u76D1\u72F1\u7F34\u83B7\u767D\u83B2\u6559\u9006\u532A\u5BC6\u4EF6\uFF0C\u5404\u5E9C\u6309\u4EF6\u6355\u62FF\u9006\u532A\u4E00\u767E\u4F59\u540D\uFF0C\u7ECF\u67E5\u786E\u5B9E\u65E0\u8BEF\u3002\u6B64\u6848\u5217\u4E3A\u519B\u529F\u4E4B\u540E\uFF0C\u4E3A\u5211\u540D\u7B2C\u4E00\u5927\u529F\uFF0C\u5DF2\u4E0A\u62A5\u671D\u5EF7\u3002\u300D</p><p>\u5218\u8FA8\u673A\u5408\u4EC0\u9053\uFF1A\u300C\u4F5B\u7956\u4FDD\u4F51\uFF01\u300D</p><p>\u5B59\u5929\u7FBD\u7B11\u9053\uFF1A\u300C\u5353\u4E8C\u54E5\uFF0C\u4E0D\u7528\u6025\u7740\u8D70\u4E86\u5427\u3002\u300D</p><p>\u5353\u5929\u96C4\u7B11\u9053\uFF1A\u300C\u679C\u7136\u662F\u559C\u4E8B\u3002\u4E0D\u8FC7\u773C\u4E0B\u5C31\u6709\u4E2A\u5927\u7406\u5BFA\u53F3\u4E1E\u5728\u72F1\u91CC\u67E5\u6848\uFF0C\u8FDE\u9C8D\u6A2A\u4E5F\u88AB\u62FF\u4E86\uFF0C\u8BF4\u4E0D\u5B9A\u7B49\u6069\u8D4F\u4E0B\u6765\uFF0C\u54B1\u4EEC\u5C31\u90FD\u5728\u56DA\u7262\u91CC\u8C22\u6069\u4E86\u3002\u300D</p><p>\u300C\u62FF\u4E86\u9C8D\u6A2A\u6B63\u597D\u3002\u300D\u5B59\u5929\u7FBD\u5FC3\u91CC\u5BF9\u9C8D\u6A2A\u6068\u6781\uFF0C\u8138\u4E0A\u5374\u4E0D\u52A8\u58F0\u8272\uFF0C\u300C\u79C1\u5978\u5973\u72AF\u7EC8\u7A76\u906E\u63A9\u4E0D\u4F4F\uFF0C\u5C31\u8BA9\u4ED6\u6765\u9876\u7F38\u3002\u300D</p><p>\u5353\u5929\u96C4\u9053\uFF1A\u300C\u90A3\u767D\u96EA\u83B2\u8981\u6500\u54AC\u8D77\u6765\u5462\uFF1F\u522B\u5FD8\u4E86\uFF0C\u90A3\u5A4A\u5B50\u54B1\u4EEC\u90FD\u6CA1\u5C11\u5F04\u3002\u60F3\u8DDF\u9C8D\u6A2A\u6495\u63B3\u5F00\u6765\uFF0C\u53EA\u6015\u4E0D\u5BB9\u6613\u5427\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u5FC3\u77E5\u809A\u660E\uFF0C\u767D\u96EA\u83B2\u6700\u6068\u7684\u9664\u4E86\u960E\u7F57\u671B\u591A\u534A\u5C31\u662F\u81EA\u5DF1\u3002\u80FD\u53EB\u4ED6\u6B7B\uFF0C\u7EDD\u4E0D\u4F1A\u8BA9\u4ED6\u6D3B\u3002\u4ED6\u53CD\u590D\u6382\u91CF\uFF0C\u6700\u540E\u9053\uFF1A\u300C\u5218\u592B\u5B50\uFF0C\u5353\u4E8C\u54E5\uFF0C\u54B1\u4EEC\u6765\u5408\u8BA1\u4E00\u4E0B\u3002\u8FD9\u6848\u5B50\u6B63\u7ECF\u6765\u8BF4\uFF0C\u62FF\u5230\u7684\u9006\u532A\u662F\u859B\u971C\u7075\uFF0C\u4E66\u4FE1\u4E5F\u662F\u5728\u5979\u8EAB\u4E0A\u641C\u51FA\u6765\u7684\u3002\u53EA\u662F\u56E0\u4E3A\u859B\u971C\u7075\u68C0\u4E3E\u767D\u5B5D\u5112\u7236\u5973\u901A\u532A\uFF0C\u6211\u4EEC\u624D\u6355\u62FF\u4E86\u767D\u6C0F\u7236\u5973\u3002</p><p>\u300C\u5BA1\u8BAF\u4E4B\u4E0B\uFF0C\u767D\u5B5D\u5112\u5DF2\u7ECF\u8BA4\u7F6A\uFF0C\u6211\u4EEC\u4E5F\u4F9D\u6B64\u5448\u6587\u3002\u5982\u4ECA\u67E5\u51FA\u767D\u6C0F\u7236\u5973\u6709\u51A4\u6789\u4E4B\u7591\uFF0C\u4E5F\u662F\u56E0\u4E3A\u859B\u971C\u7075\u8BEC\u9677\u3002\u6211\u4EEC\u5C06\u529F\u6298\u7F6A\uFF0C\u8BB0\u4E2A\u8D2A\u529F\u6025\u4E8B\uFF0C\u5931\u5BDF\u6709\u8BEF\u7684\u8FC7\u9519\u4E5F\u5C31\u5B8C\u4E86\u2014\u2014\u8FD9\u6837\u53EF\u6210\uFF1F\u300D</p><p>\u5218\u5353\u4E24\u4EBA\u90FD\u4E0D\u4EE5\u4E3A\u7136\uFF0C\u300C\u8FD9\u6B21\u6211\u4EEC\u5DF2\u5C06\u767D\u96EA\u83B2\u5F97\u7F6A\u5230\u6B7B\u5904\uFF0C\u5979\u82E5\u65E0\u7F6A\u8131\u8EAB\uFF0C\u54B1\u4EEC\u540E\u534A\u8F88\u5B50\u752D\u60F3\u7761\u4E00\u4E2A\u597D\u89C9\u3002\u300D</p><p>\u8FD9\u91CC\u9762\u7684\u5229\u5BB3\uFF0C\u5B59\u5929\u7FBD\u4E5F\u662F\u77E5\u9053\u3002\u4F46\u56DE\u6765\u65F6\u4ED6\u60F3\u4E86\u4E00\u8DEF\uFF0C\u52A8\u4E86\u7ED9\u767D\u5BB6\u8131\u7F6A\u7684\u5FC3\u601D\uFF0C\u5230\u65F6\u5927\u5927\u65B9\u65B9\u628A\u7389\u83B2\u5A36\u8FC7\u95E8\uFF0C\u8FDE\u4E08\u6BCD\u5A18\u4E00\u5E76\u517B\u7740\uFF0C\u6709\u8C01\u8BF4\u4E2A\u4E0D\u5B57\uFF1F\u4F55\u51B5\u4E39\u5A18\u7389\u83B2\u90FD\u6000\u4E86\u4ED6\u7684\u5B69\u5B50\uFF0C\u7ECF\u4E0D\u8D77\u7262\u72F1\u4E4B\u707E\u3002\u767D\u96EA\u83B2\u5C31\u662F\u518D\u6068\u4ED6\uFF0C\u77A7\u5728\u6BCD\u59B9\u52A0\u4E0A\u5979\u4EEC\u809A\u91CC\u5B69\u5B50\u7684\u9762\u5B50\u4E0A\uFF0C\u8BF4\u4E0D\u5B9A\u4E5F\u4F1A\u653E\u4ED6\u4E00\u9A6C\u3002\u4F46\u6B64\u7740\u592A\u9669\uFF0C\u8C01\u4E5F\u4E0D\u77E5\u9053\u767D\u96EA\u83B2\u5FC3\u610F\u5982\u4F55\u3002</p><p>\u5B59\u5929\u7FBD\u7B11\u9053\uFF1A\u300C\u6211\u53EA\u662F\u968F\u53E3\u4E00\u8BF4\u3002\u4E24\u4F4D\u8BF4\u7684\u662F\u3002\u5C31\u4F9D\u4F60\u4EEC\uFF0C\u8DDF\u767D\u96EA\u83B2\u6B7B\u6297\u5230\u5E95\u3002\u300D</p><p>\u4ED6\u60F3\u8D77\u5C11\u5E74\u65F6\u968F\u5E08\u5085\u6E38\u7ECF\u5FBD\u5DDE\uFF0C\u770B\u5230\u90A3\u53EA\u60AC\u5728\u65D7\u6746\u4E0A\u7684\u56DB\u5C3A\u6E9C\u91D1\u7B97\u76D8\uFF0C\u4E24\u65C1\u7F00\u7740\u767D\u5E4C\uFF0C\u5199\u7740\u300C\u4EBA\u6709\u5343\u7B97\uFF0C\u5929\u53EA\u4E00\u7B97\u300D\u3002\u5B59\u5929\u7FBD\u867D\u7B97\u5F97\u4ED4\u7EC6\uFF0C\u4F46\u674F\u82B1\u6751\u4E00\u4E8B\uFF0C\u5374\u8BA9\u4ED6\u68A6\u60F3\u5168\u6D88\u3002\u5973\u4EBA\u8FD9\u4E1C\u897F\u5C31\u5982\u8349\u7EB8\uFF0C\u88AB\u522B\u4EBA\u7528\u8FC7\u5C31\u4E0D\u503C\u94B1\u4E86\u3002\u7389\u83B2\u548C\u4E39\u5A18\uFF0C\u4ED6\u4E00\u4E2A\u90FD\u4E0D\u5A36\u3002</p><p>\u5B59\u5929\u7FBD\u867D\u7136\u8BF4\u5F97\u7B03\u5B9A\uFF0C\u5218\u8FA8\u673A\u8FD8\u662F\u4E0D\u653E\u5FC3\uFF0C\u6BD5\u7ADF\u8FD9\u91CC\u73B0\u5750\u7740\u4E00\u4E2A\u4F55\u6E05\u6CB3\uFF0C\u4E8E\u662F\u95EE\u9053\uFF1A\u300C\u4E0D\u77E5\u5B59\u5144\u6B64\u884C\u2026\u2026\u300D</p><p>\u5B59\u5929\u7FBD\u5FAE\u7B11\u7740\u62AC\u8D77\u624B\uFF0C\u300C\u4E0D\u5FC5\u62C5\u5FC3\uFF0C\u665A\u4E9B\u4FBF\u77E5\u9053\u4E86\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u72F1\u6B63\u5385\u540E\u5802\u5185\uFF0C\u968F\u4ECE\u9012\u4E0A\u8336\u6C34\uFF0C\u4F55\u6E05\u6CB3\u559D\u4E86\u4E00\u53E3\uFF0C\u6E29\u8A00\u9053\uFF1A\u300C\u767D\u96EA\u83B2\uFF0C\u4F60\u8BF4\u81EA\u5DF1\u4E0B\u5C71\u662F\u4E3A\u5E7F\u4E1C\u603B\u6355\u5434\u5927\u5F6A\u9001\u4FE1\uFF0C\u90A3\u4E48\u4E66\u4FE1\u4F55\u5728\uFF1F\u300D</p><p>\u767D\u96EA\u83B2\u54AC\u4E86\u54AC\u7259\uFF0C\u300C\u90A3\u662F\u672C\u95E8\u5BC6\u5377\uFF0C\u6C11\u5973\u88AB\u9A97\u5165\u72F1\u4E2D\uFF0C\u5BC6\u5377\u4E5F\u4E22\u5931\u4E86\u3002\u300D</p><p>\u300C\u55EF\uFF0C\u4F60\u8BF4\u4F60\u4EEC\u7236\u5973\u4E0E\u859B\u971C\u7075\u7D20\u4E0D\u76F8\u8BC6\uFF0C\u4E3A\u4F55\u4F1A\u8FDE\u624B\u4E0E\u72F1\u5352\u51B2\u7A81\uFF1F\u300D</p><p>\u300C\u90A3\u73ED\u72F1\u5352\u6B3A\u4EBA\u592A\u751A\uFF0C\u8C03\u620F\u6211\u5A18\uFF0C\u5373\u4F7F\u522B\u5904\u649E\u89C1\uFF0C\u6C11\u5973\u4E5F\u540C\u6837\u4E0D\u4F1A\u5750\u89C6\u3002\u300D</p><p>\u300C\u5982\u6B64\u8BF4\u6765\uFF0C\u859B\u971C\u7075\u4E5F\u662F\u6FC0\u4E8E\u4E49\u6124\u3002\u90A3\u5979\u4E3A\u4F55\u6307\u8BA4\u4F60\u4F1A\u540C\u515A\u5462\uFF1F\u300D</p><p>\u300C\u5927\u4EBA\u660E\u9274\uFF0C\u5F53\u65F6\u6C11\u5973\u8EAB\u4E3A\u6355\u5FEB\uFF0C\u859B\u971C\u7075\u88AB\u64D2\u65F6\u88AB\u72F1\u5352\u6311\u62E8\uFF0C\u4EE5\u4E3A\u662F\u6C11\u5973\u8BBE\u8BA1\u5C06\u5979\u6349\u4F4F\uFF0C\u56E0\u6B64\u624D\u6500\u54AC\u6211\u7236\u5973\u4E8C\u4EBA\u4E3A\u767D\u83B2\u6559\u9006\u532A\u3002\u300D</p><p>\u4F55\u6E05\u6CB3\u5FFD\u7136\u9053\uFF1A\u300C\u82E5\u4F60\u77E5\u9053\u859B\u971C\u7075\u5B9E\u4E3A\u767D\u83B2\u6559\u9006\u532A\uFF0C\u4F1A\u6349\u62FF\u5979\u5F52\u6848\u5417\uFF1F\u300D</p><p>\u767D\u96EA\u83B2\u6C89\u9ED8\u4E86\u4E00\u4F1A\u513F\uFF0C\u300C\u82E5\u5728\u5F53\u65F6\uFF0C\u6211\u4F1A\u7684\u3002\u300D</p><p>\u300C\u5982\u4ECA\u5462\uFF1F\u300D</p><p>\u767D\u96EA\u83B2\u6DE1\u6DE1\u9053\uFF1A\u300C\u6C11\u5973\u5982\u4ECA\u5DF2\u7ECF\u4E0D\u662F\u6355\u5FEB\u3002\u300D</p><p>\u4F55\u6E05\u6CB3\u70B9\u4E86\u70B9\u5934\uFF0C\u300C\u960E\u7F57\u671B\u4F55\u4EE5\u4F1A\u4E3A\u4F60\u6240\u64D2\uFF1F\u52AB\u6301\u4E3B\u5B98\u540E\u53C8\u4E3A\u4F55\u4E0D\u8D70\uFF1F\u300D</p><p>\u300C\u6C11\u5973\u4E0D\u582A\u53D7\u8FB1\u624D\u52AB\u6301\u960E\u67D0\uFF0C\u53EA\u4E3A\u81EA\u4FDD\uFF0C\u5E76\u6CA1\u6709\u6253\u7B97\u8D8A\u72F1\uFF0C\u5426\u5219\u2014\u2014\u300D\u767D\u96EA\u83B2\u4E24\u624B\u4E00\u6491\uFF0C\u9576\u94C1\u7684\u6728\u5236\u624B\u67B7\uFF0C\u683C\u7684\u4E00\u58F0\u88C2\u5F00\u3002</p><p>\u540E\u9762\u7684\u5E74\u8F7B\u4EBA\u7ACB\u523B\u8E0F\u524D\uFF0C\u6321\u5728\u4F55\u6E05\u6CB3\u8EAB\u524D\u3002</p><p>\u300C\u4E0D\u7528\u60CA\u614C\u3002\u300D\u4F55\u6E05\u6CB3\u5C4F\u9000\u968F\u4ECE\uFF0C\u53F9\u9053\uFF1A\u300C\u4F60\u5982\u6B64\u529F\u592B\uFF0C\u5374\u5728\u72F1\u91CC\u2026\u2026\u300D\u4ED6\u6253\u91CF\u7740\u5979\uFF0C\u505C\u53E3\u6CA1\u6709\u518D\u5F80\u4E0B\u8BF4\u3002</p><p>\u767D\u96EA\u83B2\u7684\u6CEA\u6C34\u6253\u6E7F\u4E86\u776B\u6BDB\uFF0C\u300C\u767D\u96EA\u83B2\u6B7B\u4E0D\u8DB3\u60DC\uFF0C\u53EA\u662F\u6211\u82E5\u8131\u8EAB\uFF0C\u6211\u5A18\u3001\u6211\u59B9\u59B9\u3001\u5F1F\u5F1F\uFF0C\u8FD8\u6709\u6211\u6B7B\u53BB\u7684\u7239\u7239\u90FD\u4E0D\u514D\u542B\u51A4\u3002\u53EF\u6068\u90A3\u73ED\u72F1\u5352\u65E0\u803B\uFF0C\u501F\u7740\u63A2\u72F1\uFF0C\u5C06\u6211\u5A18\u903C\u5978\u4E86\u2026\u2026\u300D</p><p>\u4F55\u6E05\u6CB3\u6162\u6162\u9053\uFF1A\u300C\u5987\u4EBA\u5931\u8EAB\uFF0C\u539F\u6709\u4E0D\u5F97\u5DF2\u5904\u3002\u65E2\u7136\u5FCD\u8FB1\u5931\u8D1E\uFF0C\u5F80\u540E\u5728\u4F5B\u524D\u5FCF\u6094\u7EC8\u8EAB\u4E5F\u5C31\u662F\u4E86\u3002\u300D</p><p>\u767D\u96EA\u83B2\u51C4\u7136\u7B11\u9053\uFF1A\u300C\u82E5\u80FD\u62A5\u5F97\u5927\u4EC7\uFF0C\u6211\u8FD8\u6709\u4F55\u9762\u76EE\u82DF\u6D3B\u4E8E\u4E16\u3002\u300D</p><p>\u4F55\u6E05\u6CB3\u867D\u7136\u5E73\u548C\uFF0C\u4F46\u662F\u5BF9\u5FE0\u5B5D\u8282\u70C8\u770B\u5F97\u6781\u91CD\uFF0C\u529D\u767D\u96EA\u83B2\u6BCD\u5973\u51FA\u5BB6\u5DF2\u7ECF\u662F\u5BBD\u7EB5\u4E86\uFF0C\u89C1\u767D\u96EA\u83B2\u5FC3\u6709\u6B7B\u5FD7\uFF0C\u5F53\u4E0B\u4E5F\u4E0D\u529D\u963B\u3002\u8D77\u8EAB\u9053\uFF1A\u300C\u7A0D\u540E\u672C\u5B98\u518D\u5F00\u5802\u5BA1\u7406\u3002\u4F60\u597D\u81EA\u4E3A\u4E4B\u5427\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u518D\u6B21\u5347\u5802\u5DF2\u7ECF\u662F\u9149\u672B\u65F6\u5206\uFF0C\u72F1\u6B63\u5385\u5185\u6302\u8D77\u706F\u7B3C\uFF0C\u6848\u4E0A\u4E5F\u638C\u4E86\u706F\u3002\u4F55\u6E05\u6CB3\u773C\u775B\u672C\u88AB\u718F\u574F\u4E86\uFF0C\u773C\u6CEA\u8D8A\u6D41\u8D8A\u591A\uFF0C\u53EA\u597D\u95ED\u4E0A\u4E00\u53EA\u773C\uFF0C\u7528\u5E15\u5B50\u6342\u7740\u9053\uFF1A\u300C\u88F4\u9752\u7389\uFF0C\u4F60\u7684\u4F9B\u8BCD\u8FD8\u6709\u4F55\u8981\u8BF4\u7684\u5417\uFF1F\u300D</p><p>\u7389\u5A18\u6218\u6218\u5162\u5162\u9053\uFF1A\u300C\u6CA1\u2026\u2026\u6CA1\u6709\u4E86\u3002\u300D</p><p>\u4F55\u6E05\u6CB3\u54B3\u4E86\u4E00\u58F0\uFF0C\u300C\u672C\u5B98\u4E14\u95EE\u4F60\uFF0C\u4F60\u4F55\u4EE5\u77E5\u9053\u767D\u5B5D\u5112\u4E0E\u767D\u83B2\u6559\u52FE\u7ED3\uFF1F\u300D</p><p>\u300C\u767D\u5B5D\u5112\u8DDF\u767D\u83B2\u6559\u2026\u2026\u771F\u4EBA\u539F\u662F\u8BA4\u8BC6\u7684\u2026\u2026\u300D\u7389\u5A18\u5077\u5077\u53BB\u770B\u5218\u8FA8\u673A\uFF0C\u5374\u770B\u89C1\u5B59\u5929\u7FBD\u542B\u7B11\u671B\u7740\u5979\uFF0C\u4E0D\u7531\u8EAB\u5B50\u4E00\u98A4\uFF0C\u300C\u56E0\u6B64\u7ED9\u5B50\u5973\u8D77\u540D\u65F6\uFF0C\u4EE5\u767D\u83B2\u4E3A\u540D\uFF0C\u5C06\u2026\u2026\u90A3\u4E2A\u771F\u4EBA\u540D\u5B57\u7684\u4E09\u4E2A\u5B57\u5D4C\u5728\u5176\u4E2D\u3002\u300D</p><p>\u300C\u8FD9\u662F\u4F60\u731C\u5EA6\u7684\uFF0C\u8FD8\u662F\u6709\u4EBA\u4E3A\u8BC1\uFF1F\u300D</p><p>\u7389\u5A18\u72B9\u8C6B\u7247\u523B\uFF0C\u300C\u662F\u59D0\u592B\u544A\u8BC9\u6211\u7684\u3002\u300D</p><p>\u4F55\u6E05\u6CB3\u62FF\u8D77\u5377\u5B97\uFF0C\u300C\u636E\u4F60\u6240\u4F9B\u751F\u8FB0\uFF0C\u767D\u5B5D\u5112\u957F\u5973\u51FA\u751F\u65F6\uFF0C\u4F60\u5E74\u4EC5\u5341\u4E09\uFF0C\u767D\u5B5D\u5112\u4E3A\u4F55\u4F1A\u544A\u8BC9\u4F60\u8FD9\u4E9B\uFF1F\u300D</p><p>\u300C\u662F\u2026\u2026\u540E\u6765\u544A\u8BC9\u6211\u7684\u3002\u300D</p><p>\u300C\u90A3\u767D\u5B5D\u5112\u4E3A\u4F55\u4F1A\u544A\u8BC9\u4F60\uFF1F\u300D</p><p>\u7389\u5A18\u55EB\u5685\u7740\u7B54\u4E0D\u4E0A\u6765\u3002\u5B59\u5929\u7FBD\u8E0F\u524D\u4E00\u6B65\uFF0C\u5355\u819D\u8DEA\u5012\uFF0C\u300C\u7980\u5927\u4EBA\uFF0C\u88F4\u9752\u7389\u4E0E\u767D\u5B5D\u5112\u539F\u6709\u79C1\u60C5\u3002\u6B64\u662F\u4EA4\u6B22\u4E4B\u4F59\u7684\u95F2\u8BDD\uFF0C\u88F4\u72AF\u7F9E\u4E8E\u542F\u9F7F\u3002\u300D</p><p>\u7389\u5A18\u6DA8\u7EA2\u4E86\u8138\uFF0C\u4E0D\u6562\u8BF4\u662F\uFF0C\u4E5F\u4E0D\u6562\u8BF4\u4E0D\u662F\u3002</p><p>\u4F55\u6E05\u6CB3\u6CEA\u773C\u6A21\u7CCA\u5730\u770B\u7740\u5B59\u5929\u7FBD\uFF0C\u300C\u4F60\u662F\u4F55\u4EBA\u554A\uFF1F\u300D</p><p>\u300C\u5351\u804C\u5B59\u5929\u7FBD\uFF0C\u4E43\u8C7A\u72FC\u5761\u5927\u7262\u72F1\u5352\u3002\u6B64\u6848\u7531\u5351\u804C\u7ECF\u624B\uFF0C\u6DF1\u6089\u5185\u60C5\u3002\u672A\u7ECF\u5927\u4EBA\u5141\u8BB8\u64C5\u81EA\u5F00\u53E3\uFF0C\u8BF7\u5927\u4EBA\u6CBB\u7F6A\u3002\u300D</p><p>\u300C\u5514\u3002\u5012\u662F\u4E2A\u6709\u62C5\u5F53\u7684\u6C49\u5B50\u3002\u7AD9\u8D77\u6765\u8BF4\u8BDD\u5427\u3002\u300D\u4F55\u6E05\u6CB3\u8427\u7D22\u7684\u767D\u53D1\u5728\u706F\u5149\u91CC\u5FAE\u5FAE\u6643\u52A8\uFF0C\u4F3C\u4E4E\u5DF2\u7ECF\u5026\u5F97\u5F88\u4E86\u3002\u4ED6\u52C9\u5F3A\u6253\u8D77\u7CBE\u795E\uFF0C\u300C\u8FD9\u4E5F\u662F\u88F4\u72AF\u7684\u4F9B\u8FF0\u5417\uFF1F\u300D</p><p>\u300C\u6B63\u662F\u3002\u56E0\u4E8B\u5173\u5987\u4EBA\u540D\u8282\uFF0C\u53C8\u4E0E\u6848\u60C5\u65E0\u5173\uFF0C\u5351\u804C\u5E76\u672A\u5F55\u5165\u88F4\u72AF\u4F9B\u8FF0\u4E4B\u4E2D\u3002\u300D</p><p>\u300C\u88F4\u9752\u7389\uFF0C\u4ED6\u8BF4\u7684\u53EF\u662F\u5B9E\u60C5\uFF1F\u300D</p><p>\u7389\u5A18\u4F4E\u58F0\u5E94\u9053\uFF1A\u300C\u662F\u3002\u300D</p><p>\u300C\u672C\u5B98\u518D\u95EE\u4F60\uFF0C\u6848\u53D1\u65F6\u4F60\u5728\u4F55\u5904\uFF1F\u300D</p><p>\u300C\u7F57\u9704\u5C71\u3002\u300D</p><p>\u300C\u90A3\u4F60\u4F55\u65F6\uFF0C\u56E0\u4F55\u5165\u72F1\uFF1F\u300D</p><p>\u300C\u4E0A\u4E2A\u6708\uFF0C\u7F6A\u5987\u6765\u770B\u671B\u5BB6\u59CA\u2026\u2026\u88AB\u6355\u5165\u72F1\u3002\u300D</p><p>\u300C\u8C01\u6355\u7684\u4F60\uFF1F\u300D</p><p>\u7389\u5A18\u671D\u5B59\u5929\u7FBD\u770B\u53BB\uFF0C\u5B59\u5929\u7FBD\u62B1\u62F3\u9053\uFF1A\u300C\u662F\u5351\u804C\u5728\u8DEF\u4E0A\u9047\u89C1\uFF0C\u56E0\u5979\u662F\u767D\u5B5D\u5112\u59BB\u65CF\uFF0C\u5351\u804C\u6015\u5979\u4E0E\u767D\u5B5D\u5112\u59BB\u5973\u52FE\u7ED3\u4E32\u4F9B\uFF0C\u4FBF\u5C06\u5979\u62FF\u5165\u72F1\u4E2D\u5173\u62BC\u3002\u300D</p><p>\u4F55\u6E05\u6CB3\u70B9\u5934\u9053\uFF1A\u300C\u4F60\u6015\u7684\u6709\u7406\u3002\u6211\u95EE\u4F60\uFF0C\u88F4\u9752\u7389\u53EF\u662F\u72EC\u81EA\u800C\u6765\u5417\uFF1F\u300D</p><p>\u5B59\u5929\u7FBD\u5FC3\u4E2D\u53EB\u7CDF\uFF0C\u786C\u7740\u5934\u76AE\u9053\uFF1A\u300C\u88F4\u72AF\u5F53\u65F6\u72EC\u81EA\u4E00\u4EBA\u3002\u300D</p><p>\u300C\u6709\u4EBA\u968F\u884C\u5417\uFF1F\u300D</p><p>\u300C\u5351\u804C\u5E76\u672A\u89C1\u5230\u3002\u300D</p><p>\u300C\u88F4\u9752\u7389\uFF0C\u4F60\u662F\u81EA\u5DF1\u6765\u7684\u5417\uFF1F\u300D</p><p>\u88F4\u9752\u7389\u4E0D\u77E5\u5982\u4F55\u56DE\u7B54\uFF0C\u826F\u4E45\u624D\u5E94\u4E86\u58F0\uFF0C\u300C\u662F\u3002\u300D</p><p>\u4F55\u6E05\u6CB3\u53F9\u4E86\u53E3\u6C14\uFF0C\u300C\u7F57\u9704\u5C71\u79BB\u6B64\u6570\u767E\u91CC\uFF0C\u4F60\u4E00\u4ECB\u5973\u6D41\uFF0C\u53C8\u662F\u7F20\u8FC7\u8DB3\u7684\uFF0C\u4E00\u4E2A\u4EBA\u600E\u80FD\u6765\u6B64\uFF1F\u300D</p><p>\u5B59\u5929\u7FBD\u9053\uFF1A\u300C\u56DE\u5927\u4EBA\uFF0C\u767D\u96EA\u83B2\u5F53\u65F6\u4E5F\u662F\u4E00\u4EBA\u8FD4\u5BB6\u3002\u300D</p><p>\u300C\u5594\uFF1F\u88F4\u9752\u7389\u4E5F\u7EC3\u8FC7\u6B66\u529F\u4E48\uFF1F\u300D\u4F55\u6E05\u6CB3\u7528\u5E15\u5B50\u6342\u7740\u773C\uFF0C\u53C8\u9053\uFF1A\u300C\u88F4\u9752\u7389\uFF0C\u4F60\u6765\u65F6\u53EF\u77E5\u6B64\u6848\uFF1F\u300D</p><p>\u300C\u77E5\uFF0C\u77E5\u9053\u3002\u300D</p><p>\u300C\u90A3\u4E48\u4F60\u4E3A\u4F55\u6562\u6765\uFF1F\u300D</p><p>\u300C\u7F6A\u5987\u53EA\u60F3\u770B\u4E00\u773C\uFF0C\u5C31\u8D70\u7684\u3002\u300D</p><p>\u4F55\u6E05\u6CB3\u53C8\u8F6C\u5F00\u8BDD\u9898\uFF0C\u300C\u4F60\u8FD9\u6B21\u89C1\u7740\u767D\u82F1\u83B2\u65F6\uFF0C\u4ED6\u6709\u591A\u9AD8\u4E86\uFF1F\u300D</p><p>\u7389\u5A18\u4E0A\u6B21\u89C1\u7740\u82F1\u83B2\uFF0C\u4ED6\u521A\u6EE1\u5468\u5C81\uFF0C\u53EA\u597D\u5927\u81F4\u6BD4\u4E86\u4E2A\u516D\u4E03\u5C81\u5B69\u5B50\u7684\u9AD8\u5EA6\u3002</p><p>\u767D\u96EA\u83B2\u5FCD\u4E0D\u4F4F\u9053\uFF1A\u300C\u80E1\u626F\uFF01\u82F1\u83B2\u6BD4\u4E00\u822C\u5B69\u5B50\u751F\u5F97\u8981\u9AD8\u3002\u300D</p><p>\u88F4\u9752\u7389\u4E0D\u77E5\u6240\u63AA\u5730\u6536\u4E86\u624B\u3002</p><p>\u4F55\u6E05\u6CB3\u9053\uFF1A\u300C\u672C\u5B98\u518D\u6765\u95EE\u4F60\u3002\u767D\u96EA\u83B2\u4E0E\u767D\u83B2\u6559\u52FE\u7ED3\uFF0C\u4F60\u53EF\u77E5\u60C5\uFF1F\u300D</p><p>\u7389\u5A18\u72B9\u8C6B\u7740\u70B9\u4E86\u70B9\u5934\u3002</p><p>\u300C\u767D\u96EA\u83B2\u4E0E\u54EA\u4E2A\u9006\u532A\u52FE\u7ED3\u52FE\u7ED3\uFF1F\u300D</p><p>\u300C\u662F\u5979\u3002\u300D\u88F4\u9752\u7389\u6307\u5411\u4E00\u65C1\u7684\u859B\u971C\u7075\u3002</p><p>\u300C\u4EC0\u4E48\u65F6\u5019\uFF1F\u300D</p><p>\u300C\u8FC7\u5E74\u7684\u65F6\u5019\u3002\u300D</p><p>\u4F55\u6E05\u6CB3\u95EE\u7684\u968F\u610F\uFF0C\u4F3C\u4E4E\u5BF9\u5979\u7684\u7B54\u590D\u4E5F\u4E0D\u751A\u5728\u610F\uFF0C\u968F\u53E3\u9053\uFF1A\u300C\u51E0\u4E2A\u4EBA\uFF1F\u300D</p><p>\u300C\u5979\u4E00\u4E2A\u3002\u300D</p><p>\u300C\u4F4F\u4E86\u591A\u4E45\uFF1F\u300D</p><p>\u300C\u4E00\u4E24\u5929\u3002\u300D</p><p>\u300C\u6B64\u524D\u89C1\u8FC7\u4E48\uFF1F\u300D</p><p>\u300C\u6CA1\u6709\u3002\u300D</p><p>\u300C\u662F\u8C01\u8BA9\u4F60\u8FD9\u4E48\u8BF4\u7684\uFF1F\u300D</p><p>\u7389\u5A18\u4E0B\u610F\u8BC6\u5730\u671D\u5218\u8FA8\u673A\u770B\u53BB\u3002</p><p>\u4F55\u6E05\u6CB3\u53CC\u76EE\u4E00\u7741\uFF0C\u51B7\u559D\u9053\uFF1A\u300C\u62FF\u4E0B\uFF01\u300D</p><p>\u4E24\u540D\u8859\u5F79\u5E94\u58F0\u628A\u5218\u8FA8\u673A\u62D6\u51FA\u6765\uFF0C\u7528\u7EF3\u5B50\u6346\u4E0A\u3002\u5218\u8FA8\u673A\u731D\u4E0D\u53CA\u9632\u4E0B\uFF0C\u987F\u65F6\u9762\u65E0\u8840\u8272\uFF0C\u98A4\u58F0\u53EB\u9053\uFF1A\u300C\u51A4\uFF0C\u51A4\u6789\u554A\u2026\u2026\u300D</p><p>\u5B59\u5929\u7FBD\u672C\u60F3\u51FA\u9762\u628A\u6C34\u6405\u6DF7\uFF0C\u62D6\u5EF6\u65F6\u95F4\uFF0C\u8FD9\u4F1A\u513F\u624D\u77E5\u9053\u8FD9\u7CDF\u8001\u5934\u5B50\u7740\u5B9E\u4E0D\u597D\u5BF9\u4ED8\u3002\u601D\u7D22\u95F4\uFF0C\u53EA\u542C\u4F55\u6E05\u6CB3\u6DE1\u6DE1\u9053\uFF1A\u300C\u88F4\u9752\u7389\uFF0C\u672C\u5B98\u518D\u95EE\u4F60\uFF0C\u767D\u5B5D\u5112\u3001\u767D\u96EA\u83B2\u7236\u5973\u4E0E\u767D\u83B2\u6559\u52FE\u7ED3\u4E4B\u60C5\uFF0C\u4F60\u53EF\u77E5\u60C5\uFF1F\u300D</p><p>\u7389\u5A18\u6014\u4E86\u4E00\u4F1A\u513F\uFF0C\u7A81\u7136\u4E00\u4E0B\u5B50\u762B\u8F6F\u5728\u5730\uFF0C\u54ED\u6CE3\u9053\uFF1A\u300C\u5927\u4EBA\u9976\u547D\uFF0C\u59BE\u8EAB\u4EC0\u4E48\u90FD\u4E0D\u77E5\u9053\u2026\u2026\u300D</p><p>\u300C\u90A3\u4F60\u4E3A\u4F55\u4F5C\u51FA\u4F2A\u4F9B\uFF1F\u300D</p><p>\u300C\u662F\u4ED6\u4EEC\u903C\u6211\u8BF4\u7684\u2026\u2026\u300D</p><p>\u300C\u53EF\u662F\u7528\u5211\u62F7\u6253\u4E86\u4E48\uFF1F\u300D</p><p>\u7389\u5A18\u568E\u5555\u75DB\u54ED\u9053\uFF1A\u300C\u4ED6\u4EEC\u8BF4\uFF0C\u82E5\u59BE\u8EAB\u4E0D\u8BF4\uFF0C\u5C31\u8BA9\u59BE\u8EAB\u8DDF\u2026\u2026\u8DDF\u90A3\u513F\u9AA1\u2026\u2026\u300D</p><p>\u4F55\u6E05\u6CB3\u8138\u8272\u4E00\u53D8\uFF0C\u72E0\u72E0\u76EF\u4E86\u5218\u8FA8\u673A\u4E00\u773C\uFF0C\u300C\u518D\u8BF4\u4F60\u662F\u5982\u4F55\u5165\u72F1\u7684\uFF1F\u300D</p><p>\u300C\u662F\u4ED6\u2026\u2026\u300D\u7389\u5A18\u6307\u7740\u5B59\u5929\u7FBD\u54ED\u9053\uFF1A\u300C\u4ED6\u6740\u4E86\u59BE\u8EAB\u968F\u884C\u7684\u4EBA\uFF0C\u628A\u59BE\u8EAB\u62D8\u5728\u5C71\u91CC\u5978\u4E86\u56DB\u65E5\uFF0C\u624D\u9001\u5230\u72F1\u4E2D\uFF0C\u8BA9\u59BE\u8EAB\u670D\u4F8D\u72F1\u91CC\u7684\u7537\u4EBA\u3002\u300D</p><p>\u767D\u96EA\u83B2\u6012\u9053\uFF1A\u300C\u5B59\u5929\u7FBD\uFF01\u4F60\u4E0D\u8981\u8138\uFF01\u300D</p><p>\u4F55\u6E05\u6CB3\u624B\u4E00\u6325\uFF0C\u300C\u62FF\u4E0B\uFF01\u300D</p><p>\u4E24\u540D\u8859\u5F79\u62FD\u4F4F\u5B59\u5929\u7FBD\u7684\u624B\u81C2\uFF0C\u5374\u88AB\u4ED6\u300C\u556A\u300D\u7684\u7529\u5F00\u3002</p><p>\u300C\u4F55\u5927\u4EBA\uFF01\u4F60\u5982\u6B64\u65AD\u6848\uFF0C\u96BE\u4EE5\u670D\u4EBA\uFF01\u300D</p><p>\u300C\u54E6\uFF1F\u4F60\u6709\u4F55\u8BDD\u8BF4\uFF1F\u300D</p><p>\u300C\u672C\u72F1\u622A\u83B7\u767D\u83B2\u6559\u5BC6\u4FE1\u662F\u771F\uFF0C\u62FF\u4F4F\u4E86\u767D\u83B2\u6559\u9006\u532A\u662F\u771F\uFF0C\u859B\u72AF\u4F9B\u8BCD\uFF0C\u767D\u5B5D\u5112\u53E3\u4F9B\uFF0C\u90FD\u6709\u6307\u5370\u4E3A\u8BC1\uFF0C\u4EF6\u4EF6\u662F\u771F\uFF01\u5927\u4EBA\u4E3A\u4F55\u542C\u4FE1\u4E00\u9762\u4E4B\u8F9E\uFF0C\u5C31\u8981\u6355\u62FF\u6211\u7B49\uFF1F\u300D</p><p>\u4F55\u6E05\u6CB3\u653E\u4E0B\u624B\u5E15\uFF0C\u5E26\u7740\u51E0\u5206\u4E0D\u5C51\u51B7\u51B7\u770B\u7740\u5B59\u5929\u7FBD\uFF0C\u534A\u664C\u9053\uFF1A\u300C\u597D\u4E00\u4E2A\u4E0D\u89C1\u68FA\u6750\u4E0D\u843D\u6CEA\u7684\u5201\u540F\u3002\u62FF\u8BC1\u7269\u6765\uFF01\u300D</p><p>\u968F\u4ECE\u53D6\u51FA\u4E00\u6482\u5377\u5B97\uFF0C\u644A\u5F00\u6765\u9010\u4E00\u6446\u5728\u6848\u4E0A\u3002</p><p>\u300C\u8FD9\u662F\u4F60\u4EEC\u5448\u7ED9\u5404\u90E8\u53F8\u7684\u767D\u5B5D\u5112\u4F9B\u8BCD\u539F\u6863\uFF0C\u4E00\u5171\u516D\u4EFD\u3002\u4E0A\u9762\u90FD\u6709\u767D\u5B5D\u5112\u7684\u6307\u5370\u3002\u82E5\u662F\u4E00\u4EFD\uFF0C\u4E5F\u8BB8\u80FD\u7792\u8FC7\u6211\u53BB\u3002\u53EF\u60DC\u8FD9\u516D\u4EFD\u6863\u6848\uFF0C\u5E26\u4E0A\u4F60\u72F1\u4E2D\u7684\u4E00\u4EFD\uFF0C\u5C31\u63ED\u51FA\u4F60\u8C7A\u72FC\u5761\u76D1\u72F1\u4E0A\u4E0B\u52FE\u8FDE\uFF0C\u8BEC\u9677\u826F\u6C11\uFF0C\u8349\u83C5\u4EBA\u547D\u7684\u4E00\u6869\u5927\u6848\uFF01\u300D</p><p>\u4F55\u6E05\u6CB3\u5C06\u624B\u4E2D\u4E00\u76F4\u7FFB\u9605\u7684\u90A3\u4EFD\u5377\u5B97\u63B7\u5230\u6848\u4E0A\uFF0C\u300C\u770B\u5230\u4E86\u5417\uFF1F\u8FD9\u4E03\u4EFD\u5377\u5B97\u5171\u6709\u4E00\u767E\u4E8C\u5341\u516D\u4E2A\u6307\u5370\uFF0C\u5168\u4E3A\u53F3\u624B\u98DF\u6307\uFF0C\u540C\u4E00\u5377\u5B97\u4E2D\u6307\u5370\u53C2\u5DEE\u4E0D\u9F50\uFF0C\u6A2A\u7AD6\u4E0D\u4E00\uFF0C\u751A\u81F3\u6709\u51E0\u679A\u6307\u5370\u4E0A\u4E0B\u98A0\u5012\uFF01\u5C82\u662F\u4E00\u4E2A\u8BA4\u7F6A\u4E4B\u4EBA\u4EB2\u624B\u6240\u6309\uFF1F\u518D\u770B\u8FD9\u5370\u75D5\u5F62\u72B6\uFF0C\u7EB9\u8DEF\u6DF1\u6D45\u5BBD\u7EC6\u2014\u2014\u82E5\u4F60\u4EEC\u5148\u5907\u597D\u5377\u5B97\uFF0C\u5728\u767D\u5B5D\u5112\u6B7B\u65F6\u7ACB\u523B\u53D6\u4E0B\u6307\u5370\uFF0C\u8BF4\u4E0D\u5B9A\u80FD\u7792\u8FC7\u6211\u53BB\u3002</p><p>\u300C\u53EF\u60DC\u4F60\u4EEC\u624B\u6BB5\u867D\u7136\u72E0\u8FA3\uFF0C\u884C\u4E8B\u5374\u8349\u7387\u53EF\u7B11\uFF0C\u8FD9\u4E03\u4EFD\u5377\u5B97\u8FB9\u6284\u8FB9\u5370\uFF0C\u8017\u8D39\u4E86\u81F3\u5C11\u4E00\u4E2A\u65F6\u8FB0\u3002\u5BFB\u5E38\u5C38\u4F53\u534A\u4E2A\u65F6\u8FB0\u4FBF\u51FA\u73B0\u5C38\u50F5\uFF0C\u8FD9\u4E00\u767E\u4E8C\u5341\u516D\u4E2A\u6307\u5370\u6B63\u6E05\u695A\u663E\u51FA\u767D\u5B5D\u5112\u6307\u75D5\u7531\u8F6F\u800C\u786C\uFF0C\u5206\u660E\u662F\u6B7B\u540E\u76D7\u53D6\u6307\u5370\uFF01\u300D</p><p>\u300C\u518D\u770B\u8FD9\u4E00\u4EFD\u4F9B\u8BCD\uFF0C\u300D\u4F55\u6E05\u6CB3\u4E0D\u5C51\u5730\u6446\u4E86\u6446\u624B\uFF0C\u300C\u4EE5\u59D3\u540D\u5165\u7F6A\uFF0C\u672C\u53F8\u95FB\u6240\u672A\u95FB\u3002\u4F55\u51B5\u859B\u7389\u82F1\u539F\u540D\u859B\u957F\u5CF0\uFF0C\u8D77\u4E8B\u4E4B\u524D\u65B9\u624D\u6539\u540D\uFF0C\u4F55\u4EE5\u5341\u4F59\u5E74\u524D\u767D\u5B5D\u5112\u5C31\u5C06\u5176\u59D3\u540D\u5D4C\u5165\u5B50\u5973\u540D\u5185\uFF1F\u5982\u6B64\u8352\u5510\u53EF\u7B11\uFF0C\u8FD8\u6562\u62FF\u6765\u732E\u4E11\uFF1F\u300D</p><p>\u300C\u859B\u957F\u5CF0\u6539\u540D\u4E00\u4E8B\uFF0C\u672C\u662F\u767D\u83B2\u6559\u673A\u5BC6\uFF0C\u5C14\u7B49\u4E0D\u77E5\u4E5F\u60C5\u6709\u53EF\u539F\u3002\u4F46\u8FD9\u4F9B\u8BCD\u60C5\u8282\u9519\u6F0F\u767E\u51FA\u2014\u2014\u767D\u5B5D\u5112\u82E5\u662F\u4EE5\u5F00\u5E97\u4E3A\u540D\u4E3A\u767D\u83B2\u6559\u4F20\u9012\u6D88\u606F\uFF0C\u9009\u6B64\u50FB\u5904\uFF0C\u5C82\u975E\u6B32\u76D6\u5F25\u5F70\uFF1F\u5C14\u7B49\u533A\u533A\u72F1\u5352\uFF0C\u4F55\u6765\u6355\u76D7\u4E4B\u6743\uFF1F\u82E5\u662F\u5148\u63A2\u51FA\u674F\u82B1\u6751\u6709\u8C0B\u9006\u884C\u4E3A\uFF0C\u4F55\u4E0D\u7980\u77E5\u53BF\u8859\uFF1F\u51B5\u4E14\u82E5\u662F\u6B64\u524D\u67E5\u6709\u60C5\u5F0A\uFF0C\u4E3A\u4F55\u6587\u4E2D\u53EA\u5B57\u4E0D\u63D0\uFF1F\u518D\u5219\u767D\u96EA\u83B2\u8EAB\u4E3A\u5211\u90E8\u6355\u76D7\u53F8\u6355\u5FEB\uFF0C\u6B66\u529F\u9AD8\u5C14\u7B49\u5341\u500D\uFF0C\u5982\u786E\u4E3A\u9006\u532A\uFF0C\u4E3A\u4F55\u675F\u624B\u5C31\u64D2\uFF1F\u300D</p><p>\u300C\u66F4\u6709\u4E00\u6869\u6F5C\u6F0F\u4E86\u9A6C\u811A\uFF0C\u82E5\u767D\u5BB6\u679C\u771F\u4E3A\u532A\uFF0C\u56E0\u4F55\u4E0D\u5C06\u767D\u5B5D\u5112\u4E4B\u59BB\u88F4\u4E39\u674F\uFF0C\u6B21\u5973\u767D\u7389\u83B2\u7CFB\u72F1\uFF1F\u5373\u4F7F\u5979\u4EEC\u6BCD\u5973\u4E43\u5F31\u8D28\u5973\u6D41\uFF0C\u96BE\u884C\u8FDC\u8DEF\uFF0C\u4E0D\u6015\u5176\u9003\u4EA1\uFF0C\u4E3A\u4F55\u4E0D\u6015\u767D\u83B2\u6559\u9006\u532A\u524D\u6765\u63A2\u95EE\u7AEF\u502A\uFF1F\u51B5\u4E14\u8FD9\u5C71\u4E2D\u8FC7\u5F80\u5BA2\u5546\u5C3D\u591A\uFF0C\u7A76\u7ADF\u662F\u4E0D\u6015\u5979\u4EEC\u4F20\u9012\u6D88\u606F\uFF0C\u8FD8\u662F\u77E5\u9053\u5979\u4EEC\u6839\u672C\u5C31\u65E0\u4ECE\u52FE\u7ED3\u532A\u4EBA\uFF0C\u53EA\u80FD\u7531\u5C14\u7B49\u8086\u901E\u6DEB\u6B32\uFF1F\u300D</p><p>\u65C1\u8FB9\u7684\u5B81\u8FDC\u77E5\u53BF\u3001\u4E09\u73ED\u8859\u5F79\uFF0C\u8FDE\u540C\u88AB\u62FF\u7684\u72F1\u5352\u3001\u767D\u96EA\u83B2\u3001\u859B\u971C\u7075\u3001\u7389\u5A18\u90FD\u542C\u5F97\u76EE\u7729\u795E\u9A70\uFF0C\u8FDE\u8EAB\u5728\u5176\u4E2D\u7684\u767D\u96EA\u83B2\u4E5F\u542C\u5F97\u5982\u540C\u4F5C\u68A6\u4E00\u6837\uFF0C\u5934\u4E00\u6B21\u77E5\u9053\u8FD9\u91CC\u9762\u8FD8\u6709\u5982\u6B64\u591A\u7684\u5185\u5E55\u3002</p><p>\u5B59\u5929\u7FBD\u539F\u4EE5\u4E3A\u6B64\u6848\u5DF2\u7ECF\u505A\u5F97\u8DB3\u591F\u5468\u5BC6\uFF0C\u6CA1\u60F3\u5230\u88AB\u4F55\u6E05\u6CB3\u4E00\u4E00\u6233\u7A7F\uFF0C\u7ADF\u662F\u6EE1\u7EB8\u758F\u6F0F\u3002\u5728\u4F55\u6E05\u6CB3\u7684\u8F9E\u950B\u4E0B\uFF0C\u4EFB\u4ED6\u81EA\u4EE5\u4E3A\u5DE7\u820C\u5982\u7C27\uFF0C\u6B64\u65F6\u4E5F\u65E0\u53EA\u8A00\u7247\u8F9E\u4EE5\u5BF9\u3002\u4F55\u6E05\u6CB3\u8BF4\u5230\u4E00\u534A\u4ED6\u5DF2\u7ECF\u6C57\u6D41\u6D43\u80CC\uFF0C\u7B49\u4F55\u6E05\u6CB3\u8BF4\u5B8C\uFF0C\u5B59\u5929\u7FBD\u4EC5\u6709\u7684\u52C7\u6C14\u4E5F\u8361\u7136\u65E0\u5B58\uFF0C\u53EA\u89C9\u81EA\u5DF1\u50CF\u5728\u5927\u5EAD\u5E7F\u4F17\u4E4B\u4E0B\u88AB\u6252\u5149\u4E86\u8863\u88F3\uFF0C\u7F9E\u6127\u5F97\u65E0\u5730\u81EA\u5BB9\u3002</p><p>\u4F55\u6E05\u6CB3\u4E00\u62CD\u516C\u6848\uFF0C\u5486\u54EE\u9053\uFF1A\u300C\u5B59\u5929\u7FBD\uFF01\u4F60\u8D2A\u56FE\u767D\u5B5D\u5112\u59BB\u5973\u7F8E\u8272\uFF0C\u52FE\u7ED3\u540C\u50DA\uFF0C\u9677\u5BB3\u826F\u5584\uFF0C\u903C\u5978\u88F4\u6C0F\uFF0C\u9A97\u5978\u767D\u5973\u2026\u2026\u5982\u6B64\u8863\u51A0\u79BD\u517D\uFF0C\u4F60\u8FD8\u6709\u4F55\u8BDD\u8BF4\uFF01\u8FD8\u4E0D\u7ED9\u6211\u8DEA\u4E0B\uFF01\u300D</p><p>\u5B59\u5929\u7FBD\u8EAB\u5B50\u4E00\u6643\uFF0C\u53C8\u6B7B\u6B7B\u5730\u5FCD\u4F4F\u4E86\u3002\u4ED6\u53E3\u4E2D\u6D8C\u51FA\u4E00\u80A1\u82E6\u6C34\uFF0C\u5F77\u4F5B\u662F\u80C6\u6C41\u7684\u5473\u9053\u3002\u4ED6\u54AC\u7D27\u7259\u5173\uFF0C\u5C06\u82E6\u6C34\u54BD\u4E86\u4E0B\u53BB\uFF0C\u50CF\u6728\u5934\u822C\u50F5\u786C\u5730\u7ACB\u5728\u5802\u4E2D\u3002</p><p>\u4F55\u6E05\u6CB3\u6C14\u6781\u53CD\u7B11\uFF0C\u300C\u597D\u4E2A\u4E0D\u89C1\u68FA\u6750\u4E0D\u843D\u6CEA\u7684\u786C\u6C49\u3002\u300D\u4ED6\u4E3E\u8D77\u60CA\u5802\u6728\uFF0C\u91CD\u91CD\u62CD\u5728\u6848\u4E0A\uFF0C\u5389\u58F0\u9053\uFF1A\u300C\u6765\u4EBA\u554A\uFF01\u5E26\u88F4\u6C0F\u51FA\u6765\uFF01\u300D</p><p>\u5B59\u5929\u7FBD\u5FC3\u91CC\u50CF\u88AB\u4EBA\u6345\u4E86\u4E00\u5200\uFF0C\u75BC\u5F97\u626D\u66F2\u8D77\u6765\u3002</p><p>\u540E\u5802\u4E00\u4E2A\u5973\u5B50\u5982\u5728\u68A6\u91CC\uFF0C\u6014\u6014\u8D70\u4E86\u51FA\u6765\u3002\u4E00\u76F4\u8D70\u5230\u5B59\u5929\u7FBD\u9762\u524D\uFF0C\u5F77\u4F5B\u4E0D\u8BA4\u8BC6\u822C\uFF0C\u7EC6\u7EC6\u770B\u7740\u4ED6\uFF0C\u773C\u795E\u4E2D\u6709\u4E00\u4E1D\u60CA\u8BB6\uFF0C\u4E00\u4E9B\u4E0D\u4FE1\uFF0C\u4E00\u4E9B\u9119\u5937\uFF0C\u4E00\u70B9\u72B9\u8C6B\uFF0C\u4E00\u7F15\u67D4\u60C5\uFF0C\u4E00\u5206\u6E29\u5B58\u7684\u7231\u610F\uFF0C\u66F4\u591A\u7684\u5219\u662F\u832B\u7136\u3002\u63A5\u7740\u5979\u8EAB\u5B50\u4E00\u8F6F\uFF0C\u50CF\u6B92\u843D\u7684\u82B1\u74E3\u822C\u5012\u5728\u5730\u4E0A\u3002</p><p>\u300C\u4E39\u5A18\uFF01\u300D\u5B59\u5929\u7FBD\u8DEA\u5230\u5730\u4E0A\uFF0C\u7528\u529B\u62B1\u7D27\u5979\u9999\u8F6F\u7684\u8EAB\u4F53\u3002</p><p>\u300C\u522B\u78B0\u6211\u5A18\uFF01\u300D\u767D\u96EA\u83B2\u62C9\u5F00\u5B59\u5929\u7FBD\u7684\u624B\u81C2\uFF0C\u91CD\u91CD\u7ED9\u4E86\u5979\u4E00\u4E2A\u8033\u5149\u3002</p><p>\u5B59\u5929\u7FBD\u534A\u8FB9\u8138\u987F\u65F6\u80BF\u4E86\u8D77\u6765\uFF0C\u5374\u4E0D\u95EA\u907F\uFF0C\u629A\u7740\u4E39\u5A18\u51B0\u51C9\u7684\u9762\u5B54\uFF0C\u5636\u58F0\u53EB\u9053\uFF1A\u300C\u4E39\u5A18\uFF01\u674F\u513F\uFF01\u300D\u4ED6\u53E3\u4E2D\u8840\u6CAB\u98DE\u6E85\u51FA\u6765\uFF0C\u6CBE\u5728\u4E39\u5A18\u6D01\u767D\u7684\u7C89\u988A\u4E0A\uFF0C\u72B9\u5982\u672A\u5316\u5F00\u7684\u80ED\u8102\u3002</p><p>\u300C\u4F4F\u624B\uFF01\u300D</p><p>\u4F55\u6E05\u6CB3\u559D\u6B62\u767D\u96EA\u83B2\uFF0C\u4E0D\u5C51\u5730\u770B\u4E86\u5B59\u5929\u7FBD\u4E00\u773C\uFF0C\u51B7\u51B7\u9053\uFF1A\u300C\u5C0F\u4EBA\uFF01\u300D</p><p>\u65C1\u8FB9\u7684\u77E5\u53BF\u65E9\u5DF2\u662F\u76EE\u77AA\u53E3\u5446\uFF0C\u534A\u664C\u624D\u53E3\u5403\u5730\u9053\uFF1A\u300C\u8FD8\u4E0D\uFF0C\u8FD8\u4E0D\u2014\u2014\u62FF\u4E0B\uFF01\u300D</p><p>\u300C\u4E0D\u5FD9\u3002\u4ED6\u5DF2\u7ECF\u662F\u5F85\u6B7B\u7684\u56DA\u72AF\uFF0C\u4F55\u5FC5\u7740\u6025\u3002\u300D\u4F55\u6E05\u6CB3\u51B7\u51B7\u9053\uFF1A\u300C\u5B59\u5929\u7FBD\uFF0C\u672C\u5B98\u4E14\u95EE\u4F60\uFF0C\u4F60\u53EF\u77E5\u7F6A\u5417\uFF1F\u300D</p><p>\u5B59\u5929\u7FBD\u5F20\u4E86\u5F20\u53E3\uFF0C\u6162\u6162\u4F4E\u4E0B\u5934\u9885\u3002</p><p>\u300C\u7B49\u7B49\u2026\u2026\u300D\u4E00\u4E2A\u5973\u5B50\u8F7B\u58F0\u8BF4\u9053\u3002</p>',139);function t(n,i){return r}var o=e(a,[["render",t]]);export{o as default};
