import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const a={},r=p('<h1 id="_52-\u8C0B\u5212" tabindex="-1"><a class="header-anchor" href="#_52-\u8C0B\u5212" aria-hidden="true">#</a> 52 \u8C0B\u5212</h1><p>\u4F55\u6E05\u6CB3\u4E00\u884C\u8D70\u8FDC\uFF0C\u5B81\u8FDC\u77E5\u53BF\u4E00\u64A9\u888D\u89D2\uFF0C\u8DEA\u5728\u5C01\u603B\u7BA1\u8EAB\u524D\uFF0C\u300C\u8BF7\u5343\u5C81\u793A\u4E0B\u3002\u300D</p><p>\u5C01\u603B\u7BA1\u5FAE\u7B11\u9053\uFF1A\u300C\u662F\u8BB8\u77E5\u53BF\u5427\u3002\u4F60\u8F96\u5185\u7834\u83B7\u8FD9\u6869\u5927\u6848\uFF0C\u8D35\u53BF\u4E5F\u6709\u8BAD\u5BFC\u4E4B\u529F\uFF0C\u672C\u9547\u8BBA\u529F\u884C\u8D4F\uFF0C\u81EA\u7136\u5C11\u4E0D\u4E86\u4F60\u7684\u3002\u300D</p><p>\u77E5\u53BF\u542C\u7684\u660E\u767D\uFF0C\u7834\u83B7\u5927\u6848\u662F\u4ED6\u8BAD\u5BFC\u6709\u529F\uFF0C\u90A3\u72F1\u5352\u4EEC\u72AF\u4E0B\u7684\u5927\u7F6A\uFF0C\u4E5F\u5C11\u4E0D\u4E86\u4ED6\u300C\u8BAD\u5BFC\u300D\u7684\u5E72\u7CFB\u3002\u6382\u91CF\u7740\u8FD9\u91CC\u9762\u7684\u4EFD\u91CF\uFF0C\u77E5\u53BF\u53E9\u5934\u9053\uFF1A\u300C\u591A\u8C22\u5343\u5C81\u3002\u300D</p><p>\u5C01\u603B\u7BA1\u9053\uFF1A\u300C\u8FD9\u6848\u5B50\u672C\u9547\u4E5F\u770B\u4E86\uFF0C\u4F55\u5927\u4EBA\u5BA1\u7684\u4E0D\u9519\uFF0C\u5176\u4E2D\u786E\u6709\u60C5\u5F0A\uFF0C\u82E5\u4E0D\u60E9\u5904\uFF0C\u7F6E\u6211\u5927\u660E\u5F8B\u6CD5\u4E8E\u4F55\u5730\uFF1F\u300D\u77E5\u53BF\u53C8\u8981\u8C22\u7F6A\uFF0C\u5C01\u603B\u7BA1\u6446\u624B\u7B11\u9053\uFF1A\u300C\u8D77\u6765\u5427\u3002\u8D35\u53BF\u4E0D\u5FC5\u7D27\u5F20\uFF0C\u6B64\u4E8B\u4E0E\u4F60\u65E0\u5173\u3002\u300D</p><p>\u77E5\u53BF\u677E\u4E86\u53E3\u6C14\u3002\u5C01\u603B\u7BA1\u53C8\u9053\uFF1A\u300C\u672C\u9547\u8282\u5236\u516D\u7701\uFF0C\u8FD9\u6848\u5B50\u4E5F\u5728\u672C\u9547\u5206\u5185\u3002\u65E2\u7136\u5230\u4E86\u6B64\u5904\uFF0C\u672C\u9547\u5B9A\u8981\u5C06\u672C\u6848\u5BA1\u7406\u660E\u767D\u3002\u55EF\uFF0C\u8D35\u53BF\u82E5\u662F\u65E0\u4E8B\uFF0C\u53EF\u4EE5\u5148\u8D70\u4E86\u3002\u300D</p><p>\u77E5\u53BF\u5DF4\u4E0D\u5F97\u4E22\u5F00\u8FD9\u70EB\u624B\u7684\u70ED\u70AD\u56E2\uFF0C\u4F46\u662F\u4F55\u6E05\u6CB3\u8D70\u65F6\u4EA4\u5F85\u8FC7\uFF0C\u5982\u6709\u5DEE\u9519\u552F\u4ED6\u662F\u95EE\uFF0C\u4E24\u8FB9\u4ED6\u8C01\u4E5F\u5F97\u7F6A\u4E0D\u8D77\uFF0C\u53EA\u597D\u54BD\u4E86\u53E3\u5410\u6CAB\uFF0C\u9053\uFF1A\u300C\u4E0B\u5B98\u9075\u547D\u3002\u4F46\u4F55\u5927\u4EBA\u66FE\u6709\u5429\u5490\u2026\u2026\u300D</p><p>\u5C01\u603B\u7BA1\u7AD9\u8D77\u8EAB\u6765\uFF0C\u8D1F\u7740\u624B\u8E31\u4E86\u51E0\u6B65\uFF0C\u8BF4\u9053\uFF1A\u300C\u4F60\u5E26\u6765\u7684\u8859\u5F79\uFF0C\u672C\u9547\u4FE1\u4ED6\u4EEC\u4E0D\u8FC7\u3002\u672C\u9547\u73B0\u547D\u4F60\uFF0C\u6B64\u6848\u6240\u6709\u6863\u6848\u6587\u4E66\uFF0C\u8FDE\u8FD9\u76D1\u72F1\u5373\u523B\u90FD\u7531\u672C\u9547\u7740\u4EBA\u63A5\u7BA1\u3002\u5C31\u4E0D\u52B3\u8D35\u53BF\u8D39\u5FC3\u4E86\u3002\u300D</p><p>\u4E00\u542C\u80FD\u6446\u8131\u5E72\u7CFB\uFF0C\u5B81\u8FDC\u77E5\u53BF\u5343\u60C5\u4E07\u613F\uFF0C\u5FD9\u65BD\u793C\u544A\u9000\uFF0C\u63A5\u7740\u4F20\u4E0B\u4EE4\u53BB\uFF0C\u5E26\u4E0A\u4E09\u73ED\u8859\u5F79\uFF0C\u4E00\u65F6\u95F4\u8D70\u5F97\u5E72\u5E72\u51C0\u51C0\u3002</p><p>\u767D\u96EA\u83B2\u4E00\u573A\u6B22\u559C\u4E00\u573A\u7A7A\uFF0C\u5FC3\u91CC\u51E0\u4E4E\u6EF4\u51FA\u8840\u6765\uFF0C\u773C\u89C1\u7740\u90A3\u4E9B\u5C0F\u592A\u76D1\u4F17\u661F\u6367\u6708\u822C\u56F4\u7740\u90A3\u8138\u8272\u96EA\u767D\u7684\u5C01\u603B\u7BA1\u4E71\u8F6C\uFF0C\u6CA1\u4EBA\u6765\u7406\u776C\u5979\u4EEC\uFF0C\u7981\u4E0D\u4F4F\u95EE\u9053\uFF1A\u300C\u6562\u95EE\u5927\u4EBA\uFF0C\u8FD9\u6848\u5B50\u8FD8\u8981\u5BA1\u4E48\uFF1F\u300D</p><p>\u5C01\u603B\u7BA1\u770B\u4E86\u5979\u4E00\u773C\uFF0C\u7528\u5C16\u7EC6\u7684\u58F0\u97F3\u8BF4\u9053\uFF1A\u300C\u81EA\u7136\u662F\u8981\u5BA1\u7684\u2026\u2026\u5929\u7FBD\uFF0C\u4F60\u4E14\u8FC7\u6765\u3002\u300D</p><p>\u767D\u96EA\u83B2\u8C41\u51FA\u53BB\u4E86\uFF0C\u9053\uFF1A\u300C\u5B59\u5929\u7FBD\u6740\u4EBA\u884C\u5978\uFF0C\u4E43\u662F\u6B64\u6848\u51F6\u72AF\uFF0C\u5927\u4EBA\u662F\u8981\u56DE\u62A4\u4E8E\u4ED6\u5417\uFF1F\u300D</p><p>\u5C01\u603B\u7BA1\u8138\u8272\u4E00\u6C89\uFF0C\u300C\u638C\u5634\uFF01\u300D</p><p>\u4E00\u540D\u5C0F\u592A\u76D1\u8FC7\u6765\u626C\u8D77\u624B\uFF0C\u4E39\u5A18\u5FD9\u906E\u5728\u767D\u96EA\u83B2\u8EAB\u524D\uFF0C\u300C\u522B\u6253\uFF01\u300D</p><p>\u5C0F\u592A\u76D1\u677F\u7740\u8138\u4E00\u5DF4\u638C\u6325\u4E86\u4E0B\u53BB\uFF0C\u300C\u556A\u300D\u7684\u5728\u4E39\u5A18\u8138\u4E0A\u7559\u4E0B\u4E94\u4E2A\u6307\u5370\u3002\u767D\u96EA\u83B2\u987E\u4E0D\u5F97\u591A\u60F3\uFF0C\u53CC\u624B\u4E00\u9519\uFF0C\u683C\u7684\u62E7\u788E\u6728\u677B\uFF0C\u4E0E\u90A3\u5C0F\u592A\u76D1\u4EA4\u4E86\u4E00\u638C\u3002</p><p>\u90A3\u5C0F\u592A\u76D1\u5728\u4E3B\u5B50\u7684\u9762\u524D\u4E22\u4E86\u8138\uFF0C\u987F\u65F6\u9752\u4E86\u8138\uFF0C\u4E24\u624B\u5F20\u6210\u864E\u5F62\uFF0C\u6307\u4E0A\u5DF2\u5E26\u4E86\u5185\u52B2\u3002\u4ED6\u5E74\u7EAA\u4E0D\u8FC7\u5341\u4E94\u516D\u5C81\uFF0C\u529F\u592B\u5374\u662F\u4E0D\u5F31\uFF0C\u4E13\u95E8\u517B\u8D77\u7684\u6307\u7532\u95EA\u7740\u767D\u5BD2\u7684\u5149\u6CFD\uFF0C\u62DB\u672F\u602A\u5F02\u9634\u6BD2\u3002</p><p>\u767D\u96EA\u83B2\u62F3\u811A\u529F\u592B\u8FDC\u4E0D\u53CA\u5251\u6CD5\u7CBE\u6E5B\uFF0C\u4F46\u5185\u529F\u4FEE\u4E3A\u9AD8\u4E86\u90A3\u5C0F\u592A\u76D1\u8BB8\u591A\uFF0C\u51E0\u62DB\u8FC7\u540E\u5C31\u5360\u4E86\u4E0A\u98CE\u3002\u90A3\u5C0F\u592A\u76D1\u96BE\u4EE5\u53D6\u80DC\uFF0C\u53C8\u6362\u4E86\u4E00\u5957\u62F3\u8DEF\uFF0C\u4E24\u624B\u4E94\u6307\u5E76\u62E2\uFF0C\u638C\u5FC3\u865A\u63E1\uFF0C\u72B9\u5982\u86C7\u5F62\u3002</p><p>\u767D\u96EA\u83B2\u5355\u638C\u659C\u5288\uFF0C\u780D\u5728\u5C0F\u592A\u76D1\u53F3\u8155\u4E0A\u3002\u90A3\u5C0F\u592A\u76D1\u5403\u75DB\u4E4B\u4E0B\uFF0C\u5C31\u5730\u4E00\u6EDA\uFF0C\u5FFD\u7136\u5E76\u6307\u671D\u767D\u96EA\u83B2\u8179\u4E0B\u63D2\u53BB\u3002\u767D\u96EA\u83B2\u607C\u4ED6\u4E0B\u624B\u9634\u6BD2\uFF0C\u5DE6\u811A\u4E00\u52FE\uFF0C\u8E29\u4F4F\u4ED6\u7684\u624B\u8155\u3002\u90A3\u5C0F\u592A\u76D1\u75DB\u53EB\u7740\u8737\u8D77\u8EAB\u4F53\uFF0C\u4ED6\u5E74\u7EAA\u4E0D\u5927\uFF0C\u53EB\u58F0\u53C8\u4F3C\u7537\u4F3C\u5973\uFF0C\u5E7C\u67AD\u822C\u5C16\u4EA2\uFF0C\u8BF4\u4E0D\u51FA\u7684\u51C4\u607B\u8BE1\u5F02\u3002\u767D\u96EA\u83B2\u5FC3\u4E0B\u4E0D\u5FCD\uFF0C\u6162\u6162\u677E\u5F00\u811A\uFF0C\u8F6C\u8EAB\u6276\u4F4F\u4E39\u5A18\u3002</p><p>\u4E39\u5A18\u81EA\u4ECE\u5F53\u5802\u4F9B\u8BA4\u5978\u60C5\uFF0C\u613F\u4E0E\u5B59\u5929\u7FBD\u540C\u6B7B\u540E\uFF0C\u81EA\u89C9\u65E0\u989C\u9762\u5BF9\u5973\u513F\uFF0C\u4E00\u76F4\u56DE\u907F\u7740\u5979\u7684\u76EE\u5149\u3002\u5230\u4E86\u5371\u96BE\u5173\u5934\uFF0C\u5973\u513F\u4ECD\u62A4\u7740\u5979\uFF0C\u5FC3\u91CC\u53C8\u662F\u9178\u82E6\u53C8\u662F\u611F\u52A8\u3002\u5979\u6276\u7740\u767D\u96EA\u83B2\u7684\u624B\uFF0C\u521A\u8981\u8BF4\u8BDD\uFF0C\u5FFD\u7136\u60CA\u53EB\u9053\uFF1A\u300C\u5C0F\u5FC3\uFF01\u300D</p><p>\u5012\u5728\u5730\u4E0A\u7684\u5C0F\u592A\u76D1\u5FFD\u7136\u4E00\u8DC3\u800C\u8D77\uFF0C\u4ECE\u8EAB\u540E\u6478\u51FA\u4E00\u6761\u77ED\u5251\uFF0C\u671D\u767D\u96EA\u83B2\u8170\u95F4\u731B\u523A\u8FC7\u53BB\u3002</p><p>\u767D\u96EA\u83B2\u5E94\u58F0\u800C\u52A8\uFF0C\u5148\u65CB\u8EAB\u8E22\u98DE\u77ED\u5251\uFF0C\u63A5\u7740\u671D\u4ED6\u80F8\u53E3\u6293\u53BB\u3002\u624B\u6307\u8FD8\u79BB\u7740\u6570\u5BF8\uFF0C\u90A3\u5C0F\u592A\u76D1\u7A81\u7136\u6A2A\u98DE\u8D77\u6765\uFF0C\u50CF\u88AB\u4E00\u80A1\u5927\u529B\u62FD\u8D77\u822C\uFF0C\u51CC\u7A7A\u98DE\u51FA\u6570\u4E08\uFF0C\u4E00\u5934\u649E\u5728\u5927\u5802\u7684\u795E\u50CF\u4E0A\uFF0C\u987F\u65F6\u8111\u6D46\u8FF8\u88C2\uFF0C\u6B7B\u4E8E\u975E\u547D\u3002</p><p>\u5C01\u603B\u7BA1\u8896\u5185\u4F38\u51FA\u4E00\u6761\u9ED1\u8272\u7684\u7EC6\u97AD\uFF0C\u6BD2\u86C7\u822C\u7F20\u5728\u5C0F\u592A\u76D1\u9888\u4E2D\u3002\u97AD\u8EAB\u8272\u6CFD\u9EEF\u6DE1\uFF0C\u4E0D\u77E5\u662F\u5426\u56E0\u4E3A\u6D78\u900F\u4E86\u4EBA\u8840\uFF0C\u9690\u9690\u663E\u51FA\u8840\u8272\u3002\u4ED6\u9634\u51B7\u7684\u58F0\u97F3\u6DE1\u6DE1\u9053\uFF1A\u300C\u65E0\u80FD\u3002\u4E22\u4E86\u6211\u4E1C\u5382\u7684\u8138\u9762\u3002\u300D</p><p>\u5C01\u603B\u7BA1\u624B\u4ECD\u85CF\u5728\u8896\u4E2D\uFF0C\u4E5F\u4E0D\u89C1\u4ED6\u5982\u4F55\u52A8\u4F5C\uFF0C\u90A3\u6761\u957F\u97AD\u84E6\u5730\u659C\u63A0\u8FC7\u6765\uFF0C\u767D\u96EA\u83B2\u626C\u624B\u5C01\u6321\uFF0C\u5374\u6321\u4E86\u4E2A\u7A7A\u3002\u957F\u97AD\u5982\u540C\u865A\u5F71\u822C\u4ECE\u5979\u638C\u95F4\u7A7F\u8FC7\uFF0C\u5728\u5979\u80F8\u53E3\u8F7B\u8F7B\u4E00\u89E6\uFF0C\u7136\u540E\u7075\u86C7\u822C\u9000\u4E86\u56DE\u6765\uFF0C\u7F29\u5165\u8896\u4E2D\u3002</p><p>\u767D\u96EA\u83B2\u53EA\u89C9\u5F97\u5468\u8EAB\u7684\u7A74\u9053\u540C\u65F6\u4E00\u9EBB\uFF0C\u771F\u6C14\u50CF\u88AB\u622A\u65AD\u822C\u6D88\u6563\u6B86\u5C3D\uFF0C\u65E0\u529B\u5730\u8DEA\u4E86\u4E0B\u6765\u3002\u5979\u671B\u7740\u8138\u8272\u82CD\u767D\u7684\u592A\u76D1\u5934\u76EE\uFF0C\u773C\u4E2D\u5145\u6EE1\u4E86\u60CA\u9A87\u3002\u53E6\u4E00\u4E2A\u5C0F\u592A\u76D1\u633D\u7740\u8896\u5B50\u8FC7\u6765\uFF0C\u6728\u7740\u8138\u300C\u556A\u300D\u7684\u7ED9\u4E86\u5979\u4E00\u4E2A\u8033\u5149\u3002</p><p>\u8E0F\u8FDB\u540E\u5802\uFF0C\u5B59\u5929\u7FBD\u9669\u4E9B\u4EE5\u4E3A\u8D70\u9519\u4E86\u5C4B\u5B50\u3002\u5C31\u5728\u5802\u4E0A\u4EA4\u624B\u7684\u7247\u523B\u5DE5\u592B\uFF0C\u8FD9\u91CC\u5DF2\u7ECF\u6574\u9970\u4E00\u65B0\u3002\u5730\u4E0A\u94FA\u4E86\u5C42\u7329\u7EA2\u7684\u5730\u6BEF\uFF0C\u58C1\u4E0A\u5F20\u7740\u5E37\u5E55\uFF0C\u6881\u67F1\u7528\u5F69\u7EE2\u5305\u88F9\uFF0C\u60AC\u4E86\u56DB\u9876\u7CBE\u5DE7\u7684\u5BAB\u706F\uFF0C\u684C\u6905\u90FD\u5DF2\u6362\u8FC7\uFF0C\u4E0A\u9762\u6446\u4E86\u8336\u70B9\u3002</p><p>\u5B59\u5929\u7FBD\u5B9A\u4E86\u5B9A\u795E\uFF0C\u8FDE\u5FD9\u8DEA\u4E0B\u53E9\u9996\u9053\uFF1A\u300C\u591A\u8C22\u7239\u7239\u6551\u5B69\u513F\u4E00\u547D\u3002\u300D</p><p>\u5C01\u603B\u7BA1\u5750\u5728\u6905\u4E0A\uFF0C\u5477\u4E86\u53E3\u8336\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u4E0D\u7528\u8C22\u6211\u3002\u662F\u4F55\u6E05\u6CB3\u6551\u4E86\u4F60\u4E00\u6761\u6027\u547D\u3002\u82E5\u975E\u4F55\u6E05\u6CB3\u5728\u6B64\uFF0C\u672C\u9547\u600E\u4F1A\u4EB2\u6765\u6B64\u5730\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u62AC\u8D77\u5934\uFF0C\u5C0F\u5FC3\u770B\u4E86\u5C01\u603B\u7BA1\u4E00\u773C\u3002\u5F53\u65E5\u5728\u9F99\u6E90\uFF0C\u4ED6\u5E76\u672A\u89C1\u5230\u8FD9\u4E2A\u6743\u503E\u516D\u7701\u7684\u9547\u629A\u5927\u592A\u76D1\uFF0C\u6B64\u65F6\u5750\u5F97\u8FD1\u4E86\uFF0C\u53EA\u89C1\u4ED6\u96EA\u767D\u7684\u9762\u5B54\u5C31\u50CF\u74F7\u5668\u4E00\u822C\uFF0C\u6CA1\u6709\u4E1D\u6BEB\u8840\u8272\u3002\u7709\u773C\u7AEF\u6B63\uFF0C\u6CA1\u6709\u4E1D\u6BEB\u4E0D\u59A5\uFF0C\u4F46\u706F\u4E0B\u770B\u6765\uFF0C\u5374\u5982\u540C\u6CA1\u6709\u751F\u6C14\u7684\u6BAD\u5C38\u822C\uFF0C\u6709\u79CD\u8BF4\u4E0D\u51FA\u7684\u8BE1\u5F02\u3002</p><p>\u5B59\u5929\u7FBD\u966A\u7B11\u9053\uFF1A\u300C\u4F55\u6E05\u6CB3\u90A3\u8001\u5339\u592B\uFF0C\u600E\u662F\u7239\u7239\u7684\u5BF9\u624B\uFF1F\u770B\u4ED6\u4E00\u8EAB\u662F\u75C5\uFF0C\u516B\u6210\u6D3B\u4E0D\u5230\u4EAC\u57CE\u3002\u300D</p><p>\u5C01\u603B\u7BA1\u770B\u4E86\u4ED6\u534A\u664C\uFF0C\u6162\u6162\u9053\uFF1A\u300C\u4F60\u9519\u4E86\u3002\u7B2C\u4E00\uFF0C\u4F60\u4E0D\u8BE5\u53EB\u4ED6\u8001\u5339\u592B\u3002\u4F55\u6E05\u6CB3\u867D\u7136\u5B98\u804C\u4E0D\u9AD8\uFF0C\u5374\u662F\u6211\u671D\u91CD\u81E3\u3002\u82E5\u975E\u4E07\u5C81\u5026\u653F\uFF0C\u4E0D\u613F\u7406\u4E8B\uFF0C\u4F55\u6E05\u6CB3\u65E9\u5C31\u8BE5\u5347\u4EFB\u5927\u7406\u5BFA\u6B63\u537F\u3002\u5BF9\u4ED6\u7684\u4E3A\u4EBA\u624D\u5E72\uFF0C\u6211\u5C01\u5FB7\u660E\u503E\u5FC3\u656C\u670D\u3002\u300D</p><p>\u300C\u7B2C\u4E8C\uFF0C\u4F60\u4E0D\u8BE5\u5492\u4ED6\u6B7B\u3002\u4F55\u6E05\u6CB3\u4E0E\u6211\u867D\u653F\u89C1\u4E0D\u540C\uFF0C\u4F46\u4E00\u671D\u4E3A\u81E3\uFF0C\u90FD\u662F\u4E3A\u4E07\u5C81\u6548\u547D\u3002\u4ED6\u770B\u4E0D\u8D77\u6211\u8FD9\u9609\u4EBA\u81EA\u7136\u6709\u4ED6\u7684\u9053\u7406\uFF0C\u6211\u4E5F\u4E0D\u53BB\u602A\u4ED6\u3002\u6211\u671D\u73B0\u6709\u592A\u76D1\u5341\u4E07\uFF0C\u4F55\u6E05\u6CB3\u5374\u53EA\u6709\u4E00\u4E2A\u3002\u5982\u4ECA\u80FD\u5E72\u4E8B\u7684\u5B98\u540F\u8D8A\u6765\u8D8A\u5C11\uFF0C\u4ED6\u662F\u4E07\u4E07\u6B7B\u4E0D\u5F97\u7684\u3002\u300D</p><p>\u300C\u7B2C\u4E09\uFF0C\u4E0D\u59A8\u544A\u8BC9\u4F60\uFF0C\u672C\u9547\u7740\u5B9E\u770B\u4E0D\u8D77\u4F60\u7684\u4E3A\u4EBA\u3002\u6B32\u6210\u5927\u4E8B\uFF0C\u4E0D\u62D8\u5C0F\u8282\uFF0C\u81EA\u7136\u4E0D\u9519\u3002\u4F46\u5927\u8282\u6709\u4E8F\uFF0C\u5C31\u6210\u4E0D\u4E86\u4EC0\u4E48\u5927\u4E8B\u3002\u6709\u4E86\u8FD9\u4E00\u6761\uFF0C\u5B59\u5929\u7FBD\uFF0C\u4F60\u8FD9\u8F88\u5B50\u90FD\u6210\u4E0D\u4E86\u6C14\u5019\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u6EE1\u8EAB\u51B7\u6C57\uFF0C\u5F3A\u5FCD\u7740\u5FC3\u5E95\u7684\u60CA\u614C\uFF0C\u4E0D\u6562\u4F5C\u58F0\u3002</p><p>\u5C01\u5FB7\u660E\u4E0D\u52A8\u58F0\u8272\uFF0C\u300C\u7B2C\u56DB\uFF0C\u6211\u8FD9\u4E2A\u5E72\u7239\u662F\u4F60\u5F3A\u8BA4\u7684\uFF0C\u6211\u5E76\u4E0D\u60C5\u613F\u3002\u4F46\u4F60\u4E0D\u7528\u62C5\u5FC3\u3002\u4F60\u80FD\u903C\u6211\u8BA4\u4E86\u4F60\u8FD9\u5E72\u513F\u5B50\uFF0C\u6211\u5C31\u6562\u5E94\u627F\u4E0B\u6765\u3002\u4E5F\u56E0\u7740\u4F60\u6709\u8FD9\u4EFD\u6025\u667A\uFF0C\u672C\u9547\u7740\u5B9E\u53C8\u9AD8\u770B\u4E86\u4F60\u4E00\u773C\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u6084\u6084\u900F\u4E86\u53E3\u6C14\uFF0C\u300C\u591A\u8C22\u7239\u7239\u6307\u70B9\u3002\u300D</p><p>\u300C\u5F88\u597D\uFF0C\u4F60\u6CA1\u6709\u518D\u7F16\u7740\u4E9B\u94ED\u611F\u4E94\u5185\u7684\u865A\u8BCD\u6765\u7CCA\u5F04\u6211\u3002\u73B0\u5728\u4F60\u6765\u8BF4\u8BF4\uFF0C\u8FD9\u6848\u5B50\u8BE5\u5982\u4F55\u5904\u7F6E\uFF1F\u300D</p><p>\u5B59\u5929\u7FBD\u60F3\u4E86\u4E00\u4F1A\u513F\uFF0C\u9053\uFF1A\u300C\u513F\u5B50\u81EA\u7136\u662F\u4E0D\u60F3\u6B7B\u7684\u3002\u53EA\u80FD\u4F9D\u90B8\u62A5\u4E3A\u51C6\uFF0C\u9876\u4F4F\u5927\u7406\u5BFA\uFF0C\u4E0D\u8BB8\u4ED6\u4EEC\u7FFB\u6848\u3002\u300D</p><p>\u300C\u55EF\u3002\u4F55\u6E05\u6CB3\u7684\u9762\u5B50\u4E0D\u80FD\u4E0D\u5356\u3002\u4ED6\u6E05\u540D\u5728\u5916\uFF0C\u671D\u91CE\u4FF1\u77E5\uFF0C\u6211\u4EEC\u6B7B\u9876\u7740\u626B\u4ED6\u9762\u5B50\uFF0C\u83AB\u8BF4\u671D\u5EF7\u6E05\u8BAE\u6709\u788D\uFF0C\u672C\u9547\u81EA\u5DF1\u4E5F\u6709\u4E9B\u8FC7\u610F\u4E0D\u53BB\u3002\u300D</p><p>\u53C8\u8981\u987E\u53CA\u4F55\u6E05\u6CB3\u7684\u9762\u5B50\uFF0C\u53C8\u8981\u4FDD\u4F4F\u81EA\u5DF1\u7684\u6027\u547D\uFF0C\u5B59\u5929\u7FBD\u518D\u60F3\u4E0D\u51FA\u8F99\u6765\uFF0C\u53EA\u597D\u9053\uFF1A\u300C\u8BF7\u7239\u7239\u6307\u70B9\u3002\u300D</p><p>\u5C01\u603B\u7BA1\u6C89\u541F\u4E00\u4F1A\u513F\uFF0C\u300C\u4E0D\u59A8\u907F\u91CD\u5C31\u8F7B\uFF0C\u627F\u8BA4\u8FD9\u91CC\u9762\u6709\u903C\u5978\u903C\u4F9B\u7684\u60C5\u5F0A\uFF0C\u4F46\u662F\u6848\u5B50\u5927\u4F53\u65E0\u8BEF\u3002\u6211\u8DDF\u4F55\u6E05\u6CB3\u79C1\u4E0B\u5546\u91CF\u4E00\u4E0B\uFF0C\u6211\u4EEC\u4E24\u4EBA\u8138\u9762\u8981\u7D27\uFF0C\u671D\u5EF7\u7684\u8138\u9762\u66F4\u8981\u7D27\u3002\u5DF2\u7ECF\u90B8\u62A5\u660E\u53D1\u7684\u6848\u5B50\u53C8\u7FFB\u8FC7\u6765\uFF0C\u671D\u5EF7\u989C\u9762\u4F55\u5B58\uFF1F\u53CD\u6B63\u767D\u5B5D\u5112\u5DF2\u6B7B\uFF0C\u6D17\u8131\u7F6A\u540D\u4E5F\u4E0D\u80FD\u6D3B\u8FC7\u6765\u3002</p><p>\u300C\u4E0D\u59A8\u5C06\u9519\u5C31\u9519\uFF0C\u5BF9\u5176\u5BB6\u5C5E\u4ECE\u8F7B\u5224\u5904\uFF0C\u4FDD\u5168\u6027\u547D\uFF1B\u53E6\u4E00\u8FB9\u5BF9\u6D89\u5ACC\u903C\u5978\u7684\u72F1\u5352\u4ECE\u91CD\u60E9\u5904\uFF0C\u6740\u4E0A\u51E0\u4E2A\u3002\u8FD9\u6837\u4E0D\u9700\u7FFB\u6848\uFF0C\u671D\u5EF7\u7684\u8138\u9762\u4E5F\u4FDD\u4F4F\u4E86\uFF0C\u53D7\u51A4\u7684\u5BB6\u5C5E\u7565\u52A0\u62C2\u62ED\uFF0C\u6D89\u6848\u7684\u72F1\u5352\u8BE5\u6740\u5C31\u6740\u2014\u2014\u5C31\u662F\u7FFB\u8FC7\u6848\u6765\uFF0C\u7ED3\u679C\u4E5F\u4E0D\u5916\u5982\u6B64\u3002\u4F60\u770B\u5982\u4F55\uFF1F\u300D</p><p>\u5B59\u5929\u7FBD\u542C\u5F97\u5FC3\u60A6\u8BDA\u670D\uFF0C\u300C\u5E72\u7239\u8BF4\u7684\u662F\u3002\u767D\u5BB6\u867D\u7136\u53D7\u4E86\u51A4\u5C48\uFF0C\u4F46\u54EA\u4E2A\u5E99\u91CC\u6CA1\u6709\u51A4\u6B7B\u7684\u9B3C\u5462\uFF1F\u4E3A\u4E86\u671D\u5EF7\u8138\u9762\uFF0C\u8F7B\u5224\u5BBD\u7EB5\u4E5F\u5C31\u662F\u4E86\u3002\u300D</p><p>\u5C01\u603B\u7BA1\u9053\uFF1A\u300C\u65E2\u7136\u5982\u6B64\uFF0C\u8FD9\u6848\u5B50\u4E0D\u59A8\u7531\u4F60\u5904\u7F6E\u3002\u8BE5\u6293\u7684\u8BE5\u653E\u7684\uFF0C\u90FD\u7531\u4F60\u62FF\u51FA\u7AE0\u7A0B\uFF0C\u62DF\u51FA\u6765\u62A5\u7ED9\u5211\u90E8\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u8FDE\u5FD9\u53E9\u9996\uFF0C\u300C\u5B69\u513F\u9075\u547D\u3002\u300D</p><p>\u5C01\u603B\u7BA1\u53C8\u9053\uFF1A\u300C\u867D\u7136\u7531\u4F60\u6765\u62DF\uFF0C\u4F46\u6587\u4E66\u4E0A\u4E0D\u80FD\u6709\u4F60\u7684\u540D\u5B57\uFF0C\u514D\u5F97\u62DB\u4F55\u6E05\u6CB3\u4E4B\u5FCC\u3002\u8FD9\u6837\u5427\uFF0C\u521A\u624D\u6211\u4E5F\u8DDF\u5B81\u8FDC\u77E5\u53BF\u8BF4\u4E86\uFF0C\u6B64\u72F1\u7531\u672C\u9547\u7740\u4EBA\u63A5\u7BA1\uFF0C\u5C31\u7531\u4E1C\u5382\u63A5\u7BA1\uFF0C\u4F5C\u4E3A\u4E1C\u5382\u5CAD\u5357\u9053\u67E5\u9006\u4F7F\u72F1\u3002\u4F60\u6765\u4F5C\u72F1\u6B63\uFF0C\u7559\u4E00\u540D\u592A\u76D1\u4F5C\u4F60\u526F\u624B\uFF0C\u53E6\u5916\u518D\u7ED9\u4F60\u7559\u4E9B\u795E\u673A\u8425\u519B\u58EB\u4F5C\u72F1\u5352\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u5927\u559C\u8FC7\u671B\uFF0C\u300C\u591A\u8C22\u7239\u7239\u6069\u5178\uFF01\u5B69\u513F\u7C89\u8EAB\u788E\u9AA8\u4E5F\u96BE\u62A5\u7239\u7239\u5927\u6069\u3002\u300D\u4ED6\u4ECE\u504F\u8FDC\u53BF\u72F1\u4E00\u4E2A\u5C0F\u5C0F\u7684\u72F1\u5352\uFF0C\u4E00\u8DC3\u6210\u4E3A\u4E1C\u5382\u79D8\u5BC6\u76D1\u72F1\u7684\u5934\u5B50\uFF0C\u53EF\u8C13\u662F\u4E00\u6B65\u767B\u5929\uFF0C\u4E0D\u7531\u4ED6\u4E0D\u611F\u6FC0\u6D95\u96F6\u3002</p><p>\u5C01\u603B\u7BA1\u7B11\u9053\uFF1A\u300C\u6211\u7684\u51E0\u4E2A\u5E72\u513F\u5B50\uFF0C\u6700\u5C0F\u4E5F\u662F\u4E09\u54C1\u5B98\u3002\u4F60\u8FD8\u5E74\u8F7B\uFF0C\u5148\u5728\u8FD9\u91CC\u5386\u7EC3\u5386\u7EC3\u3002\u5DEE\u4E8B\u513F\u5E72\u5F97\u597D\uFF0C\u5C06\u6765\u8FD8\u6709\u6069\u8D4F\u3002\u300D</p><p>\u8BF4\u7740\uFF0C\u5C01\u603B\u7BA1\u5524\u6765\u4E00\u4E2A\u592A\u76D1\uFF0C\u6307\u7740\u5B59\u5929\u7FBD\u9053\uFF1A\u300C\u8FD9\u662F\u6211\u65B0\u8BA4\u7684\u5E72\u513F\u5B50\uFF0C\u5728\u8FD9\u513F\u66FF\u6211\u4EEC\u7BA1\u7740\u76D1\u72F1\u3002\u97E9\u5168\uFF0C\u4F60\u7559\u8FD9\u513F\u5E2E\u6211\u513F\u5B50\u6253\u7406\u51E0\u65E5\uFF0C\u5F97\u7A7A\u513F\u8DDF\u4ED6\u8BB2\u8BB2\u91CC\u9762\u7684\u4E8B\u3002\u7B49\u6848\u5B50\u529E\u5B8C\u518D\u56DE\u4EAC\u3002\u300D</p><p>\u90A3\u592A\u76D1\u7709\u6E05\u76EE\u79C0\uFF0C\u5507\u7EA2\u9F7F\u767D\uFF0C\u524A\u80A9\u7EC6\u8170\uFF0C\u5B9B\u5982\u5973\u5B50\uFF0C\u4ED6\u8EAC\u8EAB\u7EC6\u58F0\u7EC6\u6C14\u5730\u7B54\u5E94\u9053\uFF1A\u300C\u9075\u547D\u3002\u300D</p><p>\u5C01\u603B\u7BA1\u6EE1\u610F\u5730\u76F4\u8D77\u8EAB\uFF0C\u300C\u6848\u5B50\u5C31\u7531\u4F60\u4EEC\u53BB\u5BA1\u3002\u672C\u9547\u5148\u53BB\u6B47\u6B47\u3002\u544A\u9000\u5427\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u90A3\u592A\u76D1\u4ECD\u4E00\u4E94\u4E00\u5341\u638C\u5634\uFF0C\u767D\u96EA\u83B2\u76F4\u633A\u633A\u8DEA\u5728\u5730\u4E0A\uFF0C\u5934\u53D1\u6563\u5F00\uFF0C\u53CC\u988A\u88AB\u6253\u5F97\u901A\u7EA2\u3002\u4E39\u5A18\u6D41\u7740\u6CEA\u4E0D\u4F4F\u6D95\u54ED\uFF1B\u859B\u971C\u7075\u677F\u7740\u8138\u9762\u65E0\u8868\u60C5\uFF1B\u7389\u5A18\u521A\u4E3E\u53D1\u8FC7\u5B59\u5929\u7FBD\uFF0C\u6CA1\u60F3\u5230\u98CE\u4E91\u7A81\u53D8\uFF0C\u6765\u4E86\u4E2A\u5927\u592A\u76D1\u903C\u8D70\u4F55\u6E05\u6CB3\uFF0C\u53C8\u8BA4\u4E86\u5B59\u5929\u7FBD\u4F5C\u4E49\u5B50\uFF0C\u6B64\u65F6\u89C1\u5B59\u5929\u7FBD\u51FA\u6765\uFF0C\u987F\u65F6\u50CF\u89C1\u4E86\u732B\u7684\u8001\u9F20\uFF0C\u5413\u5F97\u745F\u745F\u53D1\u6296\u3002</p><p>\u8FD9\u8FB9\u5218\u8FA8\u673A\u7B49\u4EBA\u6B7B\u91CC\u9003\u751F\uFF0C\u90FD\u773C\u5DF4\u5DF4\u671B\u7740\u5B59\u5929\u7FBD\uFF0C\u76FC\u4ED6\u9AD8\u62AC\u8D35\u624B\u3002\u5B59\u5929\u7FBD\u4E00\u7B11\uFF0C\u8D70\u5230\u5927\u5802\u516C\u6848\u540E\uFF0C\u671D\u5802\u4E0B\u8DEA\u7684\u4F17\u4EBA\u770B\u53BB\uFF0C\u7B2C\u4E00\u6B21\u611F\u89C9\u5230\u624B\u63E1\u751F\u6740\u5927\u6743\u90A3\u79CD\u98D8\u98D8\u7136\u7684\u6ECB\u5473\u3002</p><p>\u5B59\u5929\u7FBD\u6276\u7740\u5EA7\u6905\uFF0C\u8BF4\u9053\uFF1A\u300C\u97E9\u5144\u8BF7\u5750\u3002\u300D</p><p>\u97E9\u5168\u5782\u7740\u624B\uFF0C\u7B11\u54AA\u54AA\u9053\uFF1A\u300C\u5C0F\u7684\u4E0D\u6562\uFF0C\u5B59\u5144\u8BF7\u3002\u300D</p><p>\u4E24\u4EBA\u63A8\u8BA9\u7247\u523B\uFF0C\u5B59\u5929\u7FBD\u624D\u5750\u4E86\u3002\u4ED6\u770B\u4E86\u773C\u6CEA\u5149\u76C8\u7136\u7684\u4E39\u5A18\uFF0C\u5FC3\u91CC\u4E00\u9178\uFF0C\u63A5\u7740\u53C8\u88AB\u5FC3\u91CC\u7684\u559C\u60A6\u81EA\u5F97\u51B2\u6DE1\uFF0C\u6E29\u8A00\u9053\uFF1A\u300C\u8D77\u6765\u5427\u3002\u300D</p><p>\u4E39\u5A18\u6447\u4E86\u6447\u5934\uFF0C\u4F4E\u6CE3\u9053\uFF1A\u300C\u522B\u6253\u4E86\u2026\u2026\u300D</p><p>\u5B59\u5929\u7FBD\u65E2\u4E0D\u8BA4\u5F97\u638C\u5211\u7684\u592A\u76D1\uFF0C\u4E5F\u4E0D\u77E5\u9053\u5E72\u7239\u8BF4\u7684\u638C\u5634\u8981\u6253\u591A\u5C11\uFF0C\u72B9\u8C6B\u95F4\uFF0C\u65C1\u8FB9\u7684\u97E9\u5168\u5DF2\u7ECF\u559D\u9053\uFF1A\u300C\u4F4F\u624B\uFF01\u300D</p><p>\u90A3\u592A\u76D1\u7ACB\u523B\u6536\u624B\u9000\u4E0B\u3002</p><p>\u5B59\u5929\u7FBD\u54B3\u4E86\u4E00\u58F0\uFF0C\u9053\uFF1A\u300C\u6B64\u6848\u7EE7\u7EED\u5BA1\u7406\uFF0C\u7531\u672C\u4EBA\u90A3\u4E2A\u2026\u2026\u300D\u5EA7\u6905\u4E0A\u4F3C\u4E4E\u8FD8\u5E26\u7740\u4F55\u6E05\u6CB3\u7684\u4F53\u6E29\uFF0C\u4F46\u7728\u773C\u4E4B\u95F4\uFF0C\u4ED6\u5C31\u7531\u6848\u72AF\u6210\u4E86\u5BA1\u6848\u7684\u4E3B\u5B98\uFF0C\u72B9\u5982\u4F5C\u68A6\u4E00\u822C\uFF0C\u4ED6\u987F\u4E86\u987F\uFF0C\u538B\u4E0B\u5FC3\u5E95\u7684\u614C\u4E71\uFF0C\u53E3\u9F7F\u53D8\u5F97\u6D41\u5229\u8D77\u6765\uFF0C\u300C\u8C01\u6709\u51A4\u60C5\uFF0C\u5C3D\u53EF\u5448\u8BC9\u4E0A\u6765\u3002\u300D</p><p>\u5802\u4E0B\u9759\u6084\u6084\u65E0\u4EBA\u5F00\u53E3\u3002</p><p>\u5B59\u5929\u7FBD\u7F13\u7F13\u770B\u8FC7\u4F17\u4EBA\uFF0C\u8BF4\u9053\uFF1A\u300C\u859B\u971C\u7075\uFF0C\u4F60\u6709\u51A4\u60C5\u4E48\uFF1F\u300D</p><p>\u859B\u971C\u7075\u9053\uFF1A\u300C\u5927\u4EBA\u660E\u9274\u3002\u8D31\u56DA\u5B9E\u5B9E\u5728\u5728\u65E9\u8BE5\u6B7B\u4E86\uFF0C\u88AB\u62FF\u5165\u72F1\u90FD\u662F\u8D31\u56DA\u72AF\u8D31\u81EA\u627E\u7684\uFF0C\u4E00\u70B9\u51A4\u60C5\u90FD\u4E0D\u6562\u6709\u3002\u5927\u4EBA\u5982\u4F55\u5904\u7F6E\uFF0C\u90FD\u662F\u8D31\u56DA\u5E94\u5F97\u4E4B\u7F6A\uFF0C\u8D31\u56DA\u5FC3\u7518\u60C5\u613F\u5F97\u5F88\u5462\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u76EE\u5149\u4ECE\u4E39\u5A18\u8EAB\u4E0A\u63A0\u8FC7\uFF0C\u671B\u7740\u7389\u5A18\u9053\uFF1A\u300C\u88F4\u9752\u7389\uFF0C\u4F60\u6709\u51A4\u60C5\u4E48\uFF1F\u300D</p><p>\u7389\u5A18\u54AC\u4F4F\u7EA2\u5507\uFF0C\u5FCD\u7740\u6CEA\u6447\u4E86\u6447\u5934\u3002\u5979\u672C\u6765\u751F\u5F97\u98CE\u6D41\u4FCF\u4E3D\uFF0C\u6B64\u65F6\u7A7F\u7740\u4E00\u4EF6\u5BBD\u5927\u7684\u9752\u5E03\u7537\u8863\uFF0C\u6108\u53D1\u663E\u5F97\u8EAB\u6750\u7EA4\u67D4\u5A07\u7F8E\uFF0C\u98CE\u59FF\u695A\u695A\u52A8\u4EBA\u3002</p><p>\u8FD9\u8D31\u4EBA\u5F53\u5802\u7FFB\u4F9B\uFF0C\u9669\u4E9B\u5BB3\u5F97\u4ED6\u8EAB\u8D25\u540D\u88C2\uFF0C\u8FD9\u4E00\u6B21\u65E0\u8BBA\u5982\u4F55\u4E5F\u9976\u4E0D\u8FC7\u5979\u3002\u5B59\u5929\u7FBD\u6697\u81EA\u76D8\u7B97\u7740\uFF0C\u770B\u5411\u5802\u4E0A\u6700\u540E\u7684\u5014\u5F3A\u5973\u5B50\uFF0C\u51B7\u7B11\u7740\u95EE\u9053\uFF1A\u300C\u767D\u96EA\u83B2\uFF0C\u4F60\u53EF\u6709\u51A4\u60C5\u5417\uFF1F\u300D</p><p>\u767D\u96EA\u83B2\u626C\u8D77\u8138\uFF0C\u558A\u4E86\u58F0\u300C\u6709\uFF01\u300D\u5FFD\u7136\u53E3\u4E2D\u6EA2\u51FA\u9C9C\u8840\uFF0C\u59D4\u987F\u5728\u5730\u3002\u4E39\u5A18\u8FD9\u4E00\u591C\u5FC3\u529B\u60B4\u635F\uFF0C\u60CA\u75DB\u4E4B\u4E0B\uFF0C\u4E5F\u9669\u4E9B\u6655\u5012\u3002</p><p>\u5218\u8FA8\u673A\u5728\u5E95\u4E0B\u89C1\u5802\u4E0A\u53C8\u8981\u5927\u4E71\uFF0C\u5FD9\u558A\u9053\uFF1A\u300C\u7980\u5927\u4EBA\uFF0C\u5C0F\u7684\u6709\u51A4\uFF01\u300D\u90A3\u4E9B\u72F1\u5352\u95FB\u58F0\u4E5F\u8FDE\u5FD9\u53EB\u56B7\u558A\u51A4\u3002</p><p>\u5B59\u5929\u7FBD\u5FC3\u4E0B\u7740\u6025\uFF0C\u5FD9\u9053\uFF1A\u300C\u677E\u5F00\u5218\u8FA8\u673A\uFF0C\u5353\u5929\u96C4\u4E24\u4EBA\uFF0C\u5176\u5B83\u4EBA\u7B49\u4E00\u5F8B\u62BC\u5165\u5927\u7262\uFF01\u300D</p><p>\u9648\u6CF0\u7B49\u4EBA\u8FD9\u4F1A\u513F\u53EA\u6068\u81EA\u5DF1\u778E\u4E86\u773C\uFF0C\u6295\u9519\u4E86\u5A18\u80CE\uFF0C\u62B1\u9519\u4E86\u5927\u817F\uFF0C\u6500\u9519\u4E86\u9AD8\u679D\uFF0C\u4E00\u7A9D\u8702\u56B7\u9053\uFF1A\u300C\u5B59\u54E5\uFF01\u5B59\u7237\uFF01\u9976\u4E86\u5144\u5F1F\u5427\uFF01\u5F80\u540E\u5C31\u662F\u7ED9\u4F60\u5F53\u725B\u4F5C\u9A6C\u2026\u2026\u300D\u56B7\u7740\u88AB\u519B\u58EB\u4EEC\u62D6\u4E86\u4E0B\u53BB\u3002</p><p>\u51E0\u540D\u5973\u72AF\u4E5F\u88AB\u5E26\u4E86\u4E0B\u53BB\uFF0C\u859B\u971C\u7075\u3001\u767D\u96EA\u83B2\u4ECD\u88AB\u62BC\u56DE\u5927\u7262\u3002\u97E9\u5168\u4E00\u8FB9\u5429\u5490\u5175\u58EB\u62FF\u4EBA\uFF0C\u4E00\u8FB9\u7B11\u7740\u7EC6\u58F0\u5BF9\u5B59\u5929\u7FBD\u9053\uFF1A\u300C\u5C0F\u7684\u521A\u6765\uFF0C\u5BF9\u6848\u5B50\u4E5F\u4E0D\u719F\uFF0C\u8BF7\u5B59\u5927\u4EBA\u544A\u51C6\uFF0C\u8FD9\u4E9B\u5377\u5B97\uFF0C\u8FD8\u6709\u8FD9\u72AF\u4EBA\u7531\u5C0F\u7684\u5E26\u4E0B\u53BB\uFF0C\u5148\u884C\u5BA1\u7406\u3002\u300D</p><p>\u300C\u8FD9\u4E2A\u5F53\u7136\uFF01\u300D\u5B59\u5929\u7FBD\u89C1\u4ED6\u8981\u5E26\u7389\u5A18\u5BA1\u8BAF\uFF0C\u5F53\u5373\u6EE1\u53E3\u7B54\u5E94\uFF0C\u53C8\u53EB\u6765\u5353\u5929\u96C4\uFF0C\u300C\u8FD9\u4F4D\u662F\u97E9\u5185\u4F7F\uFF0C\u5C01\u5343\u5C81\u5429\u5490\u4E86\uFF0C\u97E9\u5185\u4F7F\u5F80\u540E\u5C31\u662F\u54B1\u4EEC\u7684\u4E3B\u5FC3\u9AA8\uFF0C\u8D76\u7D27\u7ED9\u97E9\u7237\u5B89\u6392\u5904\u9662\u5B50\uFF0C\u7528\u5FC3\u7167\u987E\u4F3A\u5019\u3002\u300D</p><p>\u97E9\u5168\u7B11\u541F\u541F\u9053\uFF1A\u300C\u5C82\u6562\u5C82\u6562\uFF0C\u5C0F\u7684\u4E0D\u8FC7\u662F\u53D7\u5B59\u5144\u9A71\u4F7F\u7684\u5C0F\u5352\u7F62\u4E86\u3002\u300D</p><p>\u5353\u5929\u96C4\u7B54\u5E94\u4E86\uFF0C\u9886\u7740\u97E9\u5168\u5230\u540E\u9662\u5B89\u6392\u4F4F\u5904\u3002\u4F59\u4E0B\u7684\u592A\u76D1\u4E0D\u7528\u5429\u5490\uFF0C\u5DF2\u7ECF\u628A\u72F1\u6B63\u5385\u6574\u7406\u59A5\u5F53\uFF0C\u8BF7\u4E86\u5C01\u603B\u7BA1\u524D\u53BB\u5B89\u6B47\u3002\u7B49\u5802\u4E0A\u65E0\u4EBA\uFF0C\u5218\u8FA8\u673A\u63C9\u7740\u8155\u4E0A\u7684\u6346\u75D5\uFF0C\u6447\u5934\u7B11\u53F9\u9053\uFF1A\u300C\u771F\u8DDF\u4F5C\u68A6\u4E00\u6837\u2026\u2026\u521A\u624D\u8FD8\u662F\u9636\u4E0B\u56DA\uFF0C\u8F6C\u773C\u53C8\u9003\u51FA\u751F\u5929\u3002\u4E0D\u7ECF\u6B64\u4E8B\uFF0C\u600E\u77E5\u5C31\u8FD9\u4E48\u597D\u7AEF\u7AEF\u7AD9\u7740\uFF0C\u5C31\u662F\u798F\u5206\u5462\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u7B11\u9053\uFF1A\u300C\u53EF\u4E0D\u662F\u4E48\u3002\u4E0D\u8FC7\u73B0\u5728\u54B1\u4EEC\u53EF\u4E0D\u53EA\u662F\u7AD9\u7740\u7684\u4E8B\u4E86\u3002\u5218\u5144\u53EF\u77E5\uFF0C\u4F60\u6211\u73B0\u5728\u662F\u4EC0\u4E48\u8EAB\u4EFD\uFF1F\u300D</p>',75);function t(_,c){return r}var o=e(a,[["render",t]]);export{o as default};
