import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const a={},r=p('<h1 id="_53-\u65AD\u60C5" tabindex="-1"><a class="header-anchor" href="#_53-\u65AD\u60C5" aria-hidden="true">#</a> 53 \u65AD\u60C5</h1><p>\u300C\u6211\u4E94\u65E5\u524D\u2014\u2014\u300D\u5B59\u5929\u7FBD\u770B\u4E86\u770B\u5929\u8272\uFF0C\u300C\u5DF2\u7ECF\u662F\u516D\u65E5\u4E86\u2014\u2014\u8D76\u5230\u9F99\u6E90\uFF0C\u628A\u82F1\u83B2\u9001\u5230\u5343\u5C81\u5E9C\u4E2D\u3002\u6211\u8FD9\u8EAB\u4EFD\u5F53\u7136\u662F\u89C1\u4E0D\u5230\u5343\u5C81\uFF0C\u5E78\u597D\u82F1\u83B2\u8EAB\u8FB9\u5E26\u7740\u5218\u5144\u7684\u72B6\u5B50\uFF0C\u6211\u53C8\u9644\u4E86\u4E2A\u5939\u7247\uFF0C\u8BA9\u90A3\u5C0F\u5B50\u90FD\u5E26\u4E86\u53BB\u3002\u7B2C\u4E8C\u65E5\uFF0C\u90A3\u97E9\u5185\u4F7F\u6765\u89C1\u4E86\u6211\uFF0C\u8BF4\u5343\u5C81\u5DF2\u7ECF\u77E5\u9053\u4E86\uFF0C\u8BA9\u6211\u56DE\u6765\u5F85\u547D\u3002\u6211\u53C8\u7B49\u4E86\u4E24\u65E5\uFF0C\u6253\u542C\u5230\u767D\u83B2\u6559\u5DF2\u88AB\u8BA8\u5E73\uFF0C\u8FF0\u529F\u7684\u6298\u5B50\u4E5F\u9012\u4E86\u4E0A\u53BB\uFF0C\u624D\u6025\u5FD9\u56DE\u6765\u3002\u6CA1\u60F3\u5230\u5C01\u5343\u5C81\u4E5F\u5DF2\u7ECF\u52A8\u8EAB\uFF0C\u4EB2\u81EA\u6765\u4E86\u8FD9\u795E\u4ED9\u5CAD\u3002\u300D</p><p>\u5218\u8FA8\u673A\u7B97\u4E86\u7B97\u65F6\u95F4\uFF0C\u8BB6\u9053\uFF1A\u300C\u8FD9\u4E0D\u5BF9\u554A\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u54AC\u7259\u4E00\u7B11\uFF0C\u300C\u6CA1\u9519\u3002\u90A3\u90B8\u62A5\u5FC5\u662F\u5047\u7684\u3002\u5723\u65E8\u5C01\u5343\u5C81\u672A\u5FC5\u6562\u4F5C\u4F2A\uFF0C\u60F3\u5FC5\u662F\u542C\u8BF4\u4F55\u6E05\u6CB3\u8981\u6765\u4E24\u5E7F\uFF0C\u5C31\u5728\u4EAC\u57CE\u7740\u4EBA\u8FD0\u4F5C\uFF0C\u8BA8\u4E86\u5723\u65E8\uFF0C\u5FEB\u9A6C\u9012\u5230\u9F99\u6E90\u3002\u4F46\u8FF0\u529F\u7684\u6298\u5B50\u9001\u53BB\u4E0D\u8FC7\u6570\u65E5\uFF0C\u90B8\u62A5\u7EDD\u4E0D\u53EF\u80FD\u8FD9\u4E48\u5FEB\u9001\u6765\u3002\u300D</p><p>\u5218\u8FA8\u673A\u62C8\u7740\u9F20\u987B\u9053\uFF1A\u300C\u6CA1\u542C\u8BF4\u5C01\u5343\u5C81\u8DDF\u4F55\u6E05\u6CB3\u6709\u4EC0\u4E48\u8FC7\u8282\u554A\uFF1F\u5C01\u5343\u5C81\u600E\u4E48\u8FD9\u4E48\u504F\u5E2E\u54B1\u4EEC\uFF1F\u300D</p><p>\u5B59\u5929\u7FBD\u79C1\u4E0B\u4E5F\u731C\u5EA6\u8FC7\uFF0C\u8FD9\u91CC\u9762\u81F3\u5C11\u6709\u4E24\u4E2A\u539F\u56E0\uFF0C\u4E00\u662F\u5C01\u5FB7\u660E\u8282\u5236\u516D\u7701\uFF0C\u8BA8\u4F10\u767D\u83B2\u9006\u532A\uFF0C\u8017\u65F6\u5E74\u4F59\uFF0C\u8017\u8D39\u5185\u5E01\u5343\u4F59\u4E07\u4E24\uFF0C\u8FD9\u91CC\u9762\u672A\u5FC5\u5C31\u6CA1\u6709\u60C5\u5F0A\u3002</p><p>\u4F55\u6E05\u6CB3\u5B98\u58F0\u663E\u8457\uFF0C\u6709\u4ED6\u5728\u5E7F\u4E1C\uFF0C\u5C31\u597D\u6BD4\u4E00\u5806\u706B\u836F\u65C1\u653E\u4E86\u4E2A\u706B\u79CD\uFF0C\u56E0\u6B64\u65E0\u8BBA\u5982\u4F55\u4E5F\u8981\u628A\u4ED6\u652F\u8D70\uFF0C\u8FDC\u8FDC\u8C03\u56DE\u4EAC\u57CE\u3002</p><p>\u5176\u4E8C\u662F\u82F1\u83B2\u3002\u90A3\u65E5\u8DDF\u4E39\u5A18\u95F2\u8BDD\uFF0C\u8BF4\u8D77\u6709\u4E9B\u592A\u76D1\u559C\u6B22\u72CE\u7AE5\u3002\u5B59\u5929\u7FBD\u987F\u65F6\u52A8\u4E86\u5FC3\u601D\u3002\u4ED6\u7B2C\u4E00\u6B21\u53BB\u9F99\u6E90\uFF0C\u8FDE\u4EBA\u5F71\u90FD\u6CA1\u89C1\u5230\uFF0C\u5E72\u8106\u4E00\u4E0D\u505A\u4E8C\u4E0D\u4F11\uFF0C\u628A\u82F1\u83B2\u9001\u7ED9\u5C01\u603B\u7BA1\u5F53\u4E86\u89D0\u89C1\u7684\u793C\u7269\u3002\u8FD9\u4E0B\u5BF9\u4E86\u8DEF\u5B50\uFF0C\u7B2C\u4E8C\u65E5\u5C31\u6709\u4E86\u56DE\u97F3\u3002</p><p>\u97E9\u5168\u79C1\u4E0B\u544A\u8BC9\u4ED6\uFF0C\u5C01\u5343\u5C81\u5BF9\u82F1\u83B2\u6781\u4E3A\u4E2D\u610F\u3002\u8FD9\u4E2A\u6848\u5B50\u4E00\u65E6\u7FFB\u8FC7\u6765\uFF0C\u4F5C\u4E3A\u767D\u5B5D\u5112\u552F\u4E00\u7684\u5B50\u55E3\uFF0C\u4F55\u6E05\u6CB3\u5FC5\u5B9A\u8981\u7A77\u7D22\u767D\u82F1\u83B2\u7684\u4E0B\u843D\u3002\u5230\u90A3\u65F6\uFF0C\u5C11\u4E0D\u4E86\u8981\u7275\u6D89\u5230\u5C01\u603B\u7BA1\u8EAB\u4E0A\uFF0C\u4ED6\u62A2\u5148\u4E00\u6B65\u5C06\u6848\u5B50\u538B\u4E0B\u6765\uFF0C\u4E5F\u5C11\u4E86\u4E2A\u628A\u67C4\u3002</p><p>\u5723\u65E8\u4E0D\u6562\u4F5C\u4F2A\uFF0C\u90B8\u62A5\u90FD\u662F\u4E66\u624B\u6284\u5F55\u540E\u660E\u53D1\u5404\u7701\uFF0C\u4F2A\u9020\u4E00\u4EFD\u7EDD\u65E0\u96BE\u5904\u3002\u4F55\u6E05\u6CB3\u63A5\u4E86\u5723\u65E8\u3001\u90B8\u62A5\uFF0C\u5C31\u7B97\u6709\u5343\u822C\u7591\u95EE\uFF0C\u4E5F\u53EA\u597D\u7ACB\u5373\u56DE\u4EAC\u3002\u5C01\u5FB7\u660E\u6765\u6B64\u7B2C\u4E00\u4EF6\u4E8B\u5C31\u662F\u6536\u4E86\u5B81\u8FDC\u53BF\u8FD9\u6240\u76D1\u72F1\uFF0C\u6539\u7531\u4E1C\u5382\u5BC6\u8F96\uFF0C\u7EDD\u975E\u968F\u610F\u5904\u7F6E\u3002</p><p>\u800C\u662F\u8FD9\u6837\u4E00\u6765\uFF0C\u8C7A\u72FC\u5761\u76D1\u72F1\u5C31\u6210\u4E3A\u4E1C\u5382\u5C5E\u4E0B\u3002\u8D81\u7740\u4F55\u6E05\u6CB3\u5949\u65E8\u79BB\u5E7F\u56DE\u4EAC\uFF0C\u5C01\u5FB7\u660E\u5C3D\u53EF\u4EE5\u4ECE\u5BB9\u62A5\u90E8\u8FF0\u529F\uFF0C\u518D\u6B63\u5F0F\u520A\u4E8E\u90B8\u62A5\u3002\u5230\u65F6\u4F55\u6E05\u6CB3\u7EB5\u7136\u660E\u77E5\u6709\u8BC8\uFF0C\u4F46\u6728\u5DF2\u6210\u821F\uFF0C\u4E5F\u65E0\u6CD5\u5BF9\u4E0D\u5C5E\u516D\u90E8\u7BA1\u8F96\u7684\u4E1C\u5382\u4E0B\u624B\u3002</p><p>\u5B59\u5929\u7FBD\u5FC3\u91CC\u60F3\u7684\u660E\u767D\uFF0C\u5374\u4E0D\u80FD\u5BF9\u5218\u8FA8\u673A\u660E\u8BF4\uFF0C\u53EA\u5632\u8BBD\u5730\u7B11\u9053\uFF1A\u300C\u8FD9\u591A\u534A\u5C31\u662F\u7236\u5B50\u60C5\u6DF1\u5427\u3002\u300D</p><p>\u5218\u8FA8\u673A\u95FB\u8A00\u4E5F\u7B11\u4E86\u8D77\u6765\uFF0C\u53F9\u670D\u9053\uFF1A\u300C\u5B59\u5144\u8FD9\u4E00\u7740\u771F\u662F\u9AD8\u660E\uFF01\u8FD9\u6B21\u6211\u8DDF\u5929\u96C4\u90FD\u662F\u6258\u4E86\u5B59\u5144\u7684\u798F\uFF0C\u5F80\u540E\u8FD8\u671B\u5B59\u5144\u83AB\u602A\u6211\u7B49\u611A\u9C81\uFF0C\u591A\u591A\u63D0\u643A\u3002\u300D</p><p>\u300C\u5218\u592B\u5B50\u8FD9\u6837\u8BF4\u5C31\u592A\u5BA2\u6C14\u4E86\u3002\u300D\u5B59\u5929\u7FBD\u900A\u8C22\u51E0\u53E5\uFF0C\u7136\u540E\u6B63\u5BB9\u9053\uFF1A\u300C\u8FD9\u76D1\u72F1\u773C\u4E0B\u5DF2\u7ECF\u662F\u4E1C\u5382\u5BC6\u8F96\u2014\u2014\u6211\u4EEC\u51E0\u4E2A\u90FD\u5DF2\u7ECF\u662F\u4E1C\u5382\u5C5E\u4E0B\u4E86\u3002\u300D</p><p>\u300C\u4EC0\u4E48\uFF1F\u300D\u5218\u8FA8\u673A\u95FB\u8A00\u4E00\u60CA\uFF0C\u5F85\u95EE\u660E\u539F\u59D4\uFF0C\u4ED6\u76B1\u7709\u60F3\u9053\uFF1A\u5CAD\u5357\u9053\u67E5\u9006\u4F7F\u2026\u2026\u96BE\u9053\u4E0D\u662F\u4E8C\u5341\u56DB\u8859\u95E8\uFF1F\u60F3\u7740\u4ED6\u6D51\u8EAB\u4E00\u9707\uFF0C\u300C\u5B59\u5144\uFF0C\u8FD9\u4E00\u4E0B\u54B1\u4EEC\u53EF\u662F\u8FDB\u5230\u5185\u5EF7\u6700\u9690\u79D8\u7684\u91CD\u5730\u4E86\u3002\u300D</p><p>\u660E\u4EE3\u5185\u5EF7\u8BBE\u6709\u5341\u4E8C\u76D1\u3001\u56DB\u53F8\u3001\u516B\u5C40\uFF0C\u5408\u79F0\u4E8C\u5341\u56DB\u8859\u95E8\uFF0C\u5404\u8BBE\u6709\u638C\u5370\u592A\u76D1\uFF0C\u638C\u7BA1\u5927\u5185\u8BF8\u4E8B\u3002\u81F3\u4E8E\u4E1C\u5382\uFF0C\u5219\u662F\u6C38\u4E50\u5341\u516B\u5E74\uFF0C\u660E\u6210\u7956\u5728\u4E1C\u5B89\u95E8\u5317\u8BBE\u7ACB\uFF0C\u6697\u4E2D\u76D1\u5BDF\u767E\u59D3\u3001\u8BF8\u5B98\u8A00\u884C\u3002\u5176\u603B\u7BA1\u7531\u53F8\u793C\u76D1\u7B2C\u4E8C\u79C9\u7B14\u592A\u76D1\u3001\u7B2C\u4E09\u79C9\u7B14\u592A\u76D1\u5145\u4EFB\uFF0C\u5411\u6765\u4E3A\u7687\u5E1D\u79C1\u5C5E\u9E70\u72AC\u3002</p><p>\u6210\u5316\u5E74\u95F4\uFF0C\u5927\u592A\u76D1\u6C6A\u76F4\u6210\u7ACB\u897F\u5382\uFF0C\u4E0E\u4E1C\u5382\u3001\u9526\u8863\u536B\u5408\u79F0\u4E24\u5382\u4E00\u536B\uFF0C\u6743\u52BF\u66F4\u5728\u4E1C\u5382\u4E4B\u4E0A\uFF0C\u4F46\u4E0D\u4E45\u5373\u5E9F\u3002\u6B66\u5B97\u5E74\u95F4\u91CD\u8BBE\u53C8\u5E9F\uFF0C\u552F\u6709\u4E1C\u5382\u59CB\u7EC8\u4E3A\u7687\u5E1D\u64CD\u7EB5\u3002</p><p>\u7ECF\u8FC7\u767E\u4F59\u5E74\u7ECF\u8425\uFF0C\u4E1C\u5382\u52BF\u529B\u65E9\u5DF2\u51CC\u9A7E\u4E8E\u516D\u90E8\u4E5D\u537F\u4E4B\u4E0A\u3002\u867D\u7136\u5BF9\u5916\u53EA\u8BBE\u638C\u5211\u5343\u6237\u3001\u7406\u5211\u767E\u6237\u4E24\u4F4D\u8D34\u5211\u5B98\uFF0C\u7531\u9526\u8863\u536B\u5343\u6237\u3001\u767E\u6237\u5145\u5F53\u3002\u5C5E\u4E0B\u96B6\u5F79\u3001\u7F09\u4E8B\u7B49\u5C5E\u5458\u4E5F\u90FD\u7531\u9526\u8863\u536B\u62E8\u7ED9\uFF0C\u4F46\u662F\u5B9E\u9645\u4E0A\u4E1C\u5382\u52BF\u529B\u8F83\u5BF9\u5916\u5BA3\u79F0\u7684\u8FDC\u8FC7\u767E\u500D\u3002\u53EA\u662F\u4E1C\u5382\u9664\u7687\u5E1D\u5916\uFF0C\u4E0D\u53D7\u4EFB\u4F55\u7BA1\u8F96\uFF0C\u65E0\u4EBA\u77E5\u5176\u5185\u60C5\u3002</p><p>\u5C01\u5FB7\u660E\u672C\u662F\u53F8\u793C\u76D1\u7B2C\u4E09\u79C9\u7B14\u592A\u76D1\uFF0C\u517C\u4EFB\u4E1C\u5382\u526F\u90FD\u603B\u7BA1\u3002\u56E0\u4E3A\u767D\u83B2\u6559\u4F5C\u4E71\u51FA\u4EFB\u9547\u629A\u5C06\u519B\uFF0C\u8282\u5236\u516D\u7701\u519B\u653F\uFF0C\u53EF\u8C13\u4F4D\u9AD8\u6743\u91CD\u3002\u8FD9\u6B21\u4ED6\u5C06\u76D1\u72F1\u5B89\u7F6E\u4E3A\u5CAD\u5357\u9053\u67E5\u9006\u4F7F\u7BA1\u8F96\uFF0C\u624D\u9690\u9690\u9732\u51FA\u4E1C\u5382\u5185\u5E55\u4E00\u89D2\u3002\u4E1C\u5382\u5185\u90E8\u4E0D\u4EC5\u8BBE\u6709\u67E5\u9006\u4F7F\uFF0C\u800C\u4E14\u52BF\u529B\u65E9\u5DF2\u8D85\u51FA\u4EAC\u5E08\uFF0C\u76F4\u81F3\u5CAD\u5357\u3002\u602A\u4E0D\u5F97\u5C01\u5FB7\u660E\u5DF2\u7ECF\u53D7\u5C01\u5343\u5C81\uFF0C\u4ECD\u4E0D\u80AF\u8F9E\u53BB\u540D\u4E49\u4E0A\u4EC5\u56DB\u54C1\u7684\u4E1C\u5382\u526F\u90FD\u603B\u7BA1\u4E00\u804C\u3002</p><p>\u5218\u8FA8\u673A\u9053\uFF1A\u300C\u5B59\u5144\uFF0C\u8FD9\u4E2A\u72F1\u6B63\u7684\u4EFD\u91CF\u7740\u5B9E\u4E0D\u8F7B\u3002\u5728\u4E0B\u6697\u81EA\u63A8\u7B97\uFF0C\u4E1C\u5382\u5728\u5404\u7701\u7EC6\u4F5C\u867D\u591A\uFF0C\u4F46\u76D1\u72F1\u8981\u4EBA\u8981\u5730\uFF0C\u4E0D\u6613\u9690\u853D\uFF0C\u672A\u5FC5\u80FD\u6709\u51E0\u4E2A\u3002\u5C01\u5343\u5C81\u591A\u534A\u662F\u770B\u5230\u8C7A\u72FC\u5761\u5730\u5904\u6DF1\u5C71\uFF0C\u53C8\u662F\u4E09\u7701\u4EA4\u754C\uFF0C\u624D\u6311\u4E2D\u6B64\u5904\u3002\u300D\u4ED6\u53E3\u6C14\u70ED\u5207\u5730\u8BF4\uFF1A\u300C\u5B59\u5927\u4EBA\uFF0C\u60A8\u5F80\u540E\u5FC5\u8981\u53D7\u5927\u7528\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u7B11\u9053\uFF1A\u300C\u80FD\u9003\u8FC7\u8FD9\u4E00\u52AB\u5DF2\u7ECF\u662F\u6C42\u795E\u62DC\u4F5B\u4E86\uFF0C\u8FD8\u8BF4\u4EC0\u4E48\u5927\u7528\u4E0D\u5927\u7528\uFF1F\u5012\u662F\u8FD9\u72F1\u91CC\u65B0\u6DFB\u4E86\u8FD9\u4E48\u4E9B\u751F\u4EBA\uFF0C\u8FD8\u8981\u9760\u5218\u592B\u5B50\u8DDF\u5353\u4E8C\u54E5\u4E24\u4F4D\u591A\u591A\u5E2E\u5FD9\u3002\u300D</p><p>\u591C\u8272\u5DF2\u6DF1\uFF0C\u4E24\u4EBA\u53C8\u8C08\u4E86\u4E00\u4F1A\u513F\uFF0C\u4FBF\u5404\u81EA\u56DE\u4E86\u4F4F\u5904\u3002</p><p>\u5B59\u5929\u7FBD\u672C\u6765\u88AB\u8FD9\u591C\u7684\u53D8\u6545\u64A9\u62E8\u5F97\u6EE1\u5FC3\u5174\u594B\uFF0C\u4E00\u8DEF\u8D70\u6765\uFF0C\u811A\u6B65\u5374\u6E10\u6E10\u6C89\u91CD\u3002\u5230\u4E86\u95E8\u524D\uFF0C\u4ED6\u72B9\u8C6B\u826F\u4E45\uFF0C\u624D\u63A8\u5F00\u4E86\u95E8\u3002</p><p>\u4E39\u5A18\u5728\u5E8A\u5934\u9759\u9759\u5750\u7740\uFF0C\u4E24\u4EBA\u90FD\u56DE\u907F\u7740\u5F7C\u6B64\u7684\u76EE\u5149\uFF0C\u4E0D\u77E5\u5982\u4F55\u5F00\u53E3\u3002</p><p>\u5FFD\u7136\u4E0D\u8FDC\u4F20\u6765\u4E00\u58F0\u51C4\u60E8\u7684\u53EB\u58F0\uFF0C\u63A5\u7740\u53C8\u88AB\u7A81\u7136\u622A\u65AD\u3002\u4E39\u5A18\u8EAB\u5B50\u4E00\u6296\uFF0C\u542C\u51FA\u662F\u7389\u5A18\u7684\u58F0\u97F3\u3002</p><p>\u300C\u6211\u5BF9\u4E0D\u4F4F\u4F60\u3002\u300D\u5B59\u5929\u7FBD\u9053\uFF1A\u300C\u4F46\u4ECE\u73B0\u5728\u8D77\uFF0C\u6211\u4E0D\u4F1A\u8BA9\u4F60\u518D\u53D7\u534A\u70B9\u59D4\u5C48\u3002\u300D</p><p>\u4E39\u5A18\u6C89\u9ED8\u4E86\u4E00\u4F1A\u513F\uFF0C\u4F4E\u58F0\u8BF4\u9053\uFF1A\u300C\u5176\u5B9E\u6211\u4E5F\u6709\u5BF9\u4E0D\u4F4F\u4F60\u7684\u5730\u65B9\u3002\u300D\u5979\u58F0\u97F3\u6781\u8F7B\uFF0C\u7136\u540E\u5C55\u989C\u4E00\u7B11\uFF0C\u300C\u5974\u77E5\u9053\u5929\u7FBD\u54E5\u662F\u4E2A\u6709\u5FD7\u6C14\u7684\u3002\u4E0D\u8981\u4E3A\u5974\u8BEF\u4E86\u4F60\u7684\u4E8B\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u672C\u60F3\u628A\u81EA\u5DF1\u5347\u5B98\u7684\u4E8B\u8BF4\u51FA\u6765\uFF0C\u7A81\u7136\u53C8\u89C9\u5F97\u65E0\u5473\uFF0C\u53EA\u597D\u9ED8\u7136\u3002\u4E39\u5A18\u4E5F\u4E0D\u518D\u8BF4\u8BDD\uFF0C\u50CF\u5F80\u5E38\u90A3\u6837\u670D\u4F8D\u4ED6\u9664\u4E86\u9774\u889C\uFF0C\u94FA\u4E86\u5E8A\u7761\u4E0B\u3002</p><p>\u5B59\u5929\u7FBD\u8D34\u7740\u6795\u5934\u5FFD\u7136\u60F3\u8D77\u6765\uFF0C\u95EE\u9053\uFF1A\u300C\u7389\u83B2\u600E\u4E48\u6837\uFF1F\u300D</p><p>\u4E39\u5A18\u50CF\u88AB\u9488\u624E\u4E86\u4E0B\u822C\u4E00\u98A4\uFF0C\u63A5\u7740\u5F3A\u81EA\u9547\u9759\u4E0B\u6765\uFF0C\u300C\u6CA1\u4E8B\u7684\uFF0C\u5979\u53D7\u4E86\u4E9B\u4F24\uFF0C\u6655\u4E86\u8FC7\u53BB\u3002\u4F60\u8D70\u540E\u5979\u9192\u4E86\u4E00\u4F1A\u513F\uFF0C\u4E5F\u6CA1\u8BF4\u8BDD\uFF0C\u53C8\u7761\u7740\u4E86\u3002\u540E\u6765\u2026\u2026\u300D</p><p>\u4E39\u5A18\u6CA1\u6709\u518D\u8BF4\u4E0B\u53BB\uFF0C\u5B59\u5929\u7FBD\u77E5\u9053\uFF0C\u540E\u6765\u5979\u88AB\u4F55\u6E05\u6CB3\u6D3E\u4EBA\u8BF7\u6765\uFF0C\u4F55\u6E05\u6CB3\u672C\u610F\u662F\u60F3\u63ED\u7A7F\u4ED6\u5B59\u5929\u7FBD\u7684\u5634\u8138\uFF0C\u7136\u540E\u8BA9\u4E39\u5A18\u6765\u4F5C\u8BC1\uFF0C\u6CA1\u60F3\u5230\u52BF\u5F97\u5176\u53CD\u3002\u4E39\u5A18\u5C06\u7F6A\u8FC7\u90FD\u63FD\u5728\u4E86\u81EA\u5DF1\u8EAB\u4E0A\uFF0C\u5B59\u5929\u7FBD\u53C8\u662F\u611F\u6FC0\uFF0C\u53C8\u662F\u60ED\u6127\uFF0C\u53C8\u9690\u7EA6\u6709\u4E9B\u4E0D\u662F\u6ECB\u5473\uFF0C\u8FC7\u4E86\u4F1A\u513F\u67D4\u58F0\u9053\uFF1A\u300C\u660E\u513F\u4E00\u65E9\uFF0C\u6211\u5C31\u8BA9\u4EBA\u9001\u4F60\u56DE\u53BB\u3002\u300D</p><p>\u4E39\u5A18\u70B9\u4E86\u70B9\u5934\u3002</p><p>\u5B59\u5929\u7FBD\u5FC3\u91CC\u7A81\u7136\u4E00\u6025\uFF0C\u4E00\u628A\u62C9\u4F4F\u4E39\u5A18\uFF0C\u300C\u674F\u513F\uFF0C\u4F60\u522B\u6B7B\uFF01\u8FD8\u6709\u7389\u83B2\u2026\u2026\u300D</p><p>\u4E39\u5A18\u80CC\u5BF9\u7740\u4ED6\uFF0C\u5F77\u4F5B\u7761\u7740\u822C\u6CA1\u6709\u4F5C\u58F0\u3002\u5B59\u5929\u7FBD\u7684\u8BDD\u8131\u53E3\u800C\u51FA\uFF0C\u8BF4\u5B8C\u81EA\u5DF1\u53C8\u89C9\u8352\u5510\u3002\u4ED6\u55C5\u7740\u4E39\u5A18\u8EAB\u4E0A\u6696\u6696\u7684\u4F53\u9999\uFF0C\u5FC3\u4E8B\u6E10\u6E10\u61C8\u4E86\uFF0C\u4E0D\u4E45\u660F\u7136\u5165\u7761\u3002</p><p>\u6697\u591C\u91CC\uFF0C\u4E39\u5A18\u4E00\u53CC\u7738\u5B50\u5FAE\u5FAE\u95EA\u52A8\u6CE2\u5149\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6B21\u65E5\u4E00\u65E9\uFF0C\u5B59\u5929\u7FBD\u4FBF\u6765\u62DC\u89C1\u5C01\u603B\u7BA1\uFF0C\u53E3\u79F0\uFF1A\u300C\u513F\u5B50\u7ED9\u7236\u4EB2\u5927\u4EBA\u95EE\u5B89\u3002\u300D</p><p>\u5C01\u603B\u7BA1\u53D7\u4E86\u4ED6\u4E00\u62DC\uFF0C\u95EE\u9053\uFF1A\u300C\u6848\u5B50\u5BA1\u5F97\u5982\u4F55\uFF1F\u300D</p><p>\u300C\u56DE\u7236\u4EB2\u5927\u4EBA\uFF0C\u6848\u60C5\u5DF2\u7ECF\u67E5\u660E\uFF1A\u767D\u5B5D\u5112\u52FE\u7ED3\u767D\u83B2\u6559\u9006\u532A\u4E00\u4E8B\u5C5E\u5B9E\u3002\u767D\u83B2\u6559\u9006\u532A\u859B\u971C\u7075\u3001\u5176\u59BB\u88F4\u4E39\u674F\u3001\u59BB\u59B9\u88F4\u9752\u7389\u90FD\u53EF\u4F5C\u8BC1\u3002\u53E6\u7531\u4F55\u6E05\u6CB3\u5927\u4EBA\u4E3E\u53D1\uFF0C\u8C7A\u72FC\u5761\u76D1\u72F1\u72F1\u5352\u805A\u4F17\u903C\u5978\u9006\u532A\u5BB6\u5C5E\u4E4B\u4E8B\uFF0C\u4E5F\u5DF2\u67E5\u660E\u5C5E\u5B9E\u3002\u6D89\u6848\u72F1\u5352\u5341\u4E00\u540D\uFF0C\u73B0\u5DF2\u5168\u90E8\u6349\u62FF\u5165\u72F1\uFF0C\u542C\u5019\u5904\u7F6E\u3002\u300D</p><p>\u5C01\u603B\u7BA1\u6EE1\u610F\u5730\u70B9\u70B9\u5934\uFF0C\u8BF4\u9053\uFF1A\u300C\u6D89\u9006\u5927\u6848\uFF0C\u4E1C\u5382\u6709\u6743\u81EA\u884C\u5BA1\u7406\uFF0C\u53EA\u9700\u62A5\u90E8\u5373\u53EF\uFF0C\u4F60\u4E14\u8BF4\u8BF4\u5982\u4F55\u5904\u7F6E\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u51B3\u7136\u5730\u9053\uFF1A\u300C\u903C\u5978\u5973\u72AF\uFF0C\u5F8B\u6CD5\u4E0D\u5BB9\u3002\u51B5\u4E14\u53C8\u662F\u805A\u4F17\u903C\u5978\uFF0C\u4F9D\u5F8B\u4E0D\u8BBA\u9996\u4ECE\uFF0C\u4E00\u5F8B\u65A9\u9996\uFF01\u300D\u4ED6\u6B64\u523B\u52A8\u4E86\u6740\u5FC3\uFF0C\u8981\u5C06\u9664\u5218\u8FA8\u673A\u3001\u5353\u5929\u96C4\u4E24\u4EBA\u5916\u4E00\u4F17\u72F1\u5352\uFF0C\u5168\u90E8\u5904\u6B7B\u3002</p><p>\u5B59\u5929\u7FBD\u5982\u6B64\u8FA3\u624B\uFF0C\u8FDE\u5C01\u603B\u7BA1\u90FD\u6709\u4E9B\u610F\u5916\u3002\u6740\u51E0\u4E2A\u72F1\u5352\uFF0C\u4E0D\u8FC7\u662F\u6F14\u620F\u7ED9\u4F55\u6E05\u6CB3\u770B\uFF0C\u6CA1\u60F3\u5230\u4ED6\u5F04\u5047\u6210\u771F\uFF0C\u7ADF\u8981\u628A\u540C\u50DA\u6E05\u9664\u5E72\u51C0\u3002\u4F46\u6B64\u4E3E\u5BF9\u5C01\u603B\u7BA1\u6765\u8BF4\u6709\u5229\u65E0\u5BB3\uFF0C\u51E0\u4E2A\u72F1\u5352\u7684\u6027\u547D\uFF0C\u4E0D\u8FC7\u866B\u8681\u4E00\u822C\u3002</p><p>\u300C\u4E5F\u597D\u3002\u90A3\u6D89\u6848\u9006\u532A\uFF0C\u767D\u5B5D\u5112\u7684\u5BB6\u5C5E\u5462\uFF1F\u300D</p><p>\u5B59\u5929\u7FBD\u8BF4\u9053\uFF1A\u300C\u859B\u971C\u7075\u8EAB\u4E3A\u9006\u532A\uFF0C\u786E\u51FF\u65E0\u7591\uFF0C\u62DF\u51CC\u8FDF\u5904\u6B7B\u3002\u767D\u5B5D\u5112\u4E4B\u59BB\u3001\u59BB\u59B9\u3001\u6B21\u5973\u5E76\u4E0D\u77E5\u60C5\uFF0C\u53C8\u65E0\u8FC7\u9519\uFF0C\u5404\u6756\u51B3\u4E09\u5341\u3002\u5176\u957F\u5973\u767D\u96EA\u83B2\uFF0C\u8EAB\u6000\u6B66\u529F\uFF0C\u56FE\u8C0B\u4E0D\u8F68\uFF0C\u5B9A\u4E3A\u65A9\u9996\u3002\u300D</p><p>\u5C01\u603B\u7BA1\u867D\u7136\u662F\u592A\u76D1\uFF0C\u5374\u65E0\u534A\u70B9\u8F7B\u6D6E\u4E4B\u6001\uFF0C\u4ED6\u5750\u5728\u6905\u4E0A\uFF0C\u8170\u80CC\u633A\u5F97\u7B14\u76F4\uFF0C\u4E3E\u624B\u6295\u8DB3\u95F4\u6C14\u5EA6\u8C28\u4E25\uFF0C\u82E5\u975E\u988C\u4E0B\u65E0\u987B\uFF0C\u80A4\u8272\u602A\u5F02\uFF0C\u4E0E\u5176\u5B83\u671D\u5EF7\u91CD\u81E3\u76F8\u6BD4\uFF0C\u5E76\u65E0\u534A\u70B9\u900A\u8272\u3002</p><p>\u4ED6\u770B\u4E86\u773C\u5B59\u5929\u7FBD\uFF0C\u8BF4\u9053\uFF1A\u300C\u859B\u971C\u7075\u5012\u4E5F\u7F62\u4E86\uFF0C\u62DF\u65A9\u9996\u5373\u53EF\u3002\u767D\u5B5D\u5112\u5BB6\u5C5E\u8F7B\u8005\u592A\u8F7B\uFF0C\u91CD\u8005\u592A\u91CD\u3002\u82E5\u8BF4\u4E0D\u77E5\u60C5\uFF0C\u53C8\u4E3A\u4F55\u80FD\u4F5C\u8BC1\uFF1F\u6756\u51B3\u4E09\u5341\u76F4\u5982\u513F\u620F\uFF0C\u6539\u4E3A\u6756\u51B3\u4E09\u5341\uFF0C\u6D41\u4E09\u5343\u91CC\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u5FC3\u4E0B\u53EB\u82E6\uFF0C\u82E5\u662F\u6D41\u4E09\u5343\u91CC\uFF0C\u4E39\u5A18\u7B49\u4EBA\u90FD\u8981\u5317\u8FC1\u8FBD\u4E1C\uFF0C\u90A3\u624D\u662F\u9E21\u98DE\u86CB\u6253\u4E00\u573A\u7A7A\u3002</p><p>\u5C01\u603B\u7BA1\u89C1\u4ED6\u4E0D\u4F5C\u58F0\uFF0C\u4E5F\u4E0D\u5F00\u53E3\uFF0C\u53EA\u6162\u6162\u559D\u7740\u8336\u3002</p><p>\u5B59\u5929\u7FBD\u786C\u7740\u5934\u76AE\u9053\uFF1A\u300C\u7236\u4EB2\u5927\u4EBA\u2026\u2026\u300D</p><p>\u5C01\u603B\u7BA1\u5C06\u8336\u76CF\u5F80\u684C\u4E0A\u4E00\u653E\uFF0C\u559D\u9053\uFF1A\u300C\u660F\u6126\uFF01\u4E00\u4E2A\u4E0B\u4E09\u6EE5\u7684\u6751\u5987\u5C31\u5C06\u4F60\u5F04\u5F97\u795E\u9B42\u98A0\u5012\uFF01\u6765\u4EBA\uFF01\u4F20\u6211\u7684\u4EE4\uFF0C\u5C06\u7F6A\u5987\u88F4\u4E39\u674F\u62D8\u6765\uFF01\u300D</p><p>\u5B59\u5929\u7FBD\u4E00\u9897\u5FC3\u76F4\u6C89\u4E86\u4E0B\u53BB\u3002</p><p>\u5C01\u603B\u7BA1\u5C16\u7740\u55D3\u5B50\u9053\uFF1A\u300C\u6CA1\u51FA\u606F\u7684\u4E1C\u897F\uFF01\u8981\u4E0D\u4F60\u8DDF\u90A3\u5973\u5B50\u4E00\u5200\u4E24\u65AD\uFF0C\u8981\u4E0D\u4F60\u5C31\u81EA\u5DF1\u4E00\u5200\u4E24\u65AD\uFF0C\u8FDB\u5BAB\u6765\u5F53\u592A\u76D1\uFF0C\u514D\u5F97\u5C06\u6765\u574F\u6211\u5927\u4E8B\uFF01\u300D</p><p>\u4E00\u4E2A\u592A\u76D1\u8FDB\u6765\u9053\uFF1A\u300C\u7980\u603B\u7BA1\uFF0C\u6CA1\u89C1\u7740\u90A3\u5973\u5B50\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u54BD\u4E86\u53E3\u5410\u6CAB\uFF0C\u8270\u96BE\u5730\u8BF4\u9053\uFF1A\u300C\u56DE\u7980\u7239\u7239\u3002\u88F4\u6C0F\u6E05\u6668\u5DF2\u7ECF\u56DE\u5BB6\u53BB\u4E86\u3002\u5B69\u513F\u77E5\u8FC7\u4E86\uFF0C\u5F80\u540E\u7EDD\u4E0D\u4F1A\u4E3A\u90A3\u7F6A\u5987\u52A8\u5FC3\u3002\u300D</p><p>\u5C01\u603B\u7BA1\u9053\uFF1A\u300C\u4E00\u4E2A\u5973\u5B50\uFF0C\u6709\u4F55\u5173\u7D27\u7684\uFF1F\u4F60\u82E5\u5F53\u771F\u820D\u4E0D\u5F97\uFF0C\u5C31\u544A\u8BC9\u7239\u7239\uFF0C\u7239\u7239\u7ACB\u523B\u8BA9\u4EBA\u628A\u5979\u6740\u4E86\uFF0C\u514D\u5F97\u4F60\u5FC3\u795E\u4E0D\u5B9A\uFF0C\u529E\u4E0D\u5F97\u5DEE\u4E8B\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u9053\uFF1A\u300C\u5B69\u513F\u53EA\u662F\u8D2A\u56FE\u90A3\u5A4A\u5B50\u7684\u7F8E\u8272\uFF0C\u54EA\u91CC\u6709\u534A\u5206\u60C5\u4E49\uFF1F\u7239\u7239\u7684\u6559\u8BAD\u5B69\u513F\u90FD\u8BB0\u4F4F\u4E86\u3002\u83AB\u8BF4\u6CA1\u6709\u60C5\u4E49\uFF0C\u5C31\u662F\u6709\uFF0C\u6B64\u65F6\u4E5F\u6069\u65AD\u4E49\u7EDD\uFF0C\u662F\u6B7B\u662F\u6D3B\u5B69\u513F\u4E5F\u4E0D\u653E\u5728\u5FC3\u4E0A\u3002\u300D</p><p>\u8FD9\u65F6\u97E9\u5168\u4E5F\u5DF2\u8FDB\u6765\uFF0C\u5782\u624B\u7ACB\u5728\u4E00\u8FB9\uFF0C\u5C01\u603B\u7BA1\u5BB9\u8272\u7A0D\u9701\uFF0C\u8BF4\u9053\uFF1A\u300C\u4E1C\u5382\u6700\u5FCC\u513F\u5973\u79C1\u60C5\u3002\u5F80\u540E\u4F60\u4FBF\u77E5\u9053\u4E86\u3002\u97E9\u5168\uFF0C\u90A3\u51E0\u4E2A\u5973\u5B50\u7531\u4F60\u5904\u7F6E\u3002\u83AB\u574F\u4E86\u6211\u4E1C\u5382\u89C4\u77E9\u3002\u300D</p><p>\u300C\u5C0F\u7684\u660E\u767D\u3002\u300D</p><p>\u623F\u95F4\u91CC\u5BC2\u9759\u4E0B\u6765\uFF0C\u5C01\u603B\u7BA1\u4F3C\u4E4E\u5728\u60F3\u7740\u4EC0\u4E48\uFF0C\u8FD1\u4E4E\u900F\u660E\u7684\u76AE\u80A4\u4E0B\u4E00\u6839\u8840\u7BA1\u5FAE\u5FAE\u53D8\u7EA2\uFF0C\u53C8\u6D88\u5931\u4E86\u3002\u5B59\u5929\u7FBD\u5FFD\u7136\u610F\u8BC6\u5230\u4ED6\u662F\u4E00\u4E2A\u5E74\u8FD1\u516D\u65EC\u7684\u8001\u4EBA\uFF0C\u5FC3\u91CC\u4E0D\u7531\u5F97\u6CDB\u8D77\u4E00\u9635\u5BD2\u610F\u3002</p><p>\u300C\u5929\u7FBD\u3002\u6709\u6869\u4E8B\u8981\u4F60\u6765\u505A\u3002\u300D</p><p>\u300C\u5B69\u513F\u542C\u547D\u3002\u300D</p><p>\u5C01\u603B\u7BA1\u53C8\u6C89\u541F\u4E86\u4E00\u4F1A\u513F\uFF0C\u8BF4\u9053\uFF1A\u300C\u767D\u5B5D\u5112\u7684\u5BB6\u7737\u5982\u4ECA\u6709\u51E0\u4EBA\u5728\u72F1\u91CC\uFF1F\u300D</p><p>\u300C\u6709\u767D\u5B5D\u5112\u59BB\u59B9\u3001\u957F\u5973\u3002\u300D</p><p>\u300C\u628A\u5979\u4EEC\u90FD\u5E26\u6765\u3002\u300D</p><p>\u4E00\u591C\u4E4B\u95F4\uFF0C\u767D\u96EA\u83B2\u5F77\u4F5B\u5DF2\u7ECF\u7EDD\u671B\u4E86\uFF0C\u7A7A\u6D1E\u7684\u773C\u775B\u6CA1\u6709\u4E00\u4E1D\u795E\u60C5\u3002\u7389\u5A18\u50CF\u662F\u7A81\u7136\u5F97\u4E86\u91CD\u75C5\uFF0C\u8138\u8272\u82CD\u767D\uFF0C\u6B65\u5C65\u8270\u96BE\uFF0C\u7531\u4EBA\u6276\u7740\u624D\u52C9\u5F3A\u8D70\u6765\u3002</p><p>\u5C01\u603B\u7BA1\u4E0D\u7ECF\u610F\u5730\u77A5\u4E86\u4E24\u5973\u4E00\u773C\uFF0C\u8BF4\u9053\uFF1A\u300C\u97E9\u5168\uFF0C\u628A\u83B2\u513F\u5E26\u6765\u3002\u300D</p><p>\u97E9\u5168\u8F7B\u624B\u8F7B\u811A\u8FDB\u4E86\u5185\u5BA4\uFF0C\u63A5\u7740\u5E26\u4E86\u4E00\u4E2A\u9526\u8863\u7389\u670D\u7684\u7AE5\u5B50\u51FA\u6765\u3002</p><p>\u767D\u96EA\u83B2\u5634\u5507\u4E00\u98A4\uFF0C\u9669\u4E9B\u8131\u53E3\u53EB\u51FA\u300C\u82F1\u83B2\u300D\u3002</p><p>\u767D\u82F1\u83B2\u7A7F\u4E86\u4EF6\u767D\u7EEB\u7EE3\u82B1\u7684\u9526\u8863\uFF0C\u5934\u53D1\u68B3\u5230\u8111\u540E\uFF0C\u7528\u4E00\u53EA\u91D1\u73AF\u675F\u4E86\uFF0C\u66F4\u663E\u5F97\u4E00\u5F20\u7CBE\u81F4\u7684\u5C0F\u8138\u7C89\u96D5\u7389\u7422\uFF0C\u5507\u89D2\u90A3\u9897\u5C0F\u75E3\u80ED\u8102\u822C\u9C9C\u7EA2\u3002\u6570\u65E5\u4E0D\u89C1\u4ED6\u795E\u8272\u4E2D\u4F3C\u4E4E\u591A\u4E86\u51E0\u5206\u964C\u751F\uFF0C\u4F46\u89C1\u5230\u59D0\u59D0\u4ED6\u4ECD\u9732\u51FA\u6B22\u6B23\u7684\u8868\u60C5\u3002\u770B\u5F97\u51FA\uFF0C\u8FD9\u4E9B\u5929\u5C01\u603B\u7BA1\u5E76\u6CA1\u6709\u8584\u5F85\u4ED6\u3002</p><p>\u5C01\u603B\u7BA1\u62DB\u4E86\u62DB\u624B\uFF0C\u53EB\u4ED6\u5230\u4E86\u8DDF\u524D\uFF0C\u7EC6\u58F0\u9053\uFF1A\u300C\u83B2\u513F\uFF0C\u6628\u665A\u7761\u5F97\u9999\u4E48\uFF1F\u300D</p><p>\u300C\u55EF\u3002\u300D</p><p>\u5C01\u603B\u7BA1\u9053\uFF1A\u300C\u516C\u516C\u5DF2\u7ECF\u66FF\u4F60\u7FFB\u4E86\u6848\u5B50\u3002\u8FD9\u72F1\u91CC\u6B3A\u8D1F\u8FC7\u4F60\u8DDF\u4F60\u5A18\u7684\u574F\u4EBA\uFF0C\u516C\u516C\u5DF2\u7ECF\u4E0B\u4EE4\u8981\u628A\u4ED6\u4EEC\u5168\u90E8\u65A9\u9996\u3002\u300D</p><p>\u6B64\u8A00\u4E00\u51FA\uFF0C\u767D\u82F1\u83B2\u56FA\u7136\u7B11\u9010\u989C\u5F00\uFF0C\u8FDE\u767D\u96EA\u83B2\u4E5F\u6123\u4F4F\u4E86\u3002</p><p>\u5C01\u603B\u7BA1\u548C\u989C\u60A6\u58F0\u5730\u8BF4\u9053\uFF1A\u300C\u4F60\u5A18\u8DDF\u4F60\u4E24\u4E2A\u59D0\u59D0\uFF0C\u73B0\u5728\u8FD8\u6709\u4F60\u5A18\u59E8\uFF0C\u4E5F\u90FD\u514D\u4E86\u6B7B\u7F6A\u3002\u516C\u516C\u7B54\u5E94\u4F60\u7684\u5DF2\u7ECF\u505A\u4E86\uFF0C\u83B2\u513F\uFF0C\u4F60\u7B54\u5E94\u516C\u516C\u7684\u5462\uFF1F\u300D</p><p>\u767D\u82F1\u83B2\u5F00\u5FC3\u5730\u8BF4\uFF1A\u300C\u516C\u516C\uFF0C\u6211\u5F80\u540E\u5C31\u966A\u7740\u4F60\u3002\u300D</p><p>\u5C01\u603B\u7BA1\u7B11\u5475\u5475\u9053\uFF1A\u300C\u90A3\u597D\uFF0C\u83B2\u513F\u5F80\u540E\u5C31\u8DDF\u7740\u516C\u516C\u5427\u3002\u300D</p><p>\u767D\u96EA\u83B2\u5FC3\u91CC\u731B\u7136\u4E00\u8DF3\uFF0C\u8131\u53E3\u53EB\u9053\uFF1A\u300C\u82F1\u83B2\uFF01\u300D</p><p>\u767D\u82F1\u83B2\u6115\u7136\u56DE\u9996\uFF0C\u53EA\u89C1\u59D0\u59D0\u7D27\u5F20\u5F97\u9762\u65E0\u8840\u8272\uFF0C\u53EB\u9053\uFF1A\u300C\u4ED6\u662F\u4E2A\u592A\u76D1\uFF01\u4ED6\u8981\u4F60\u4E5F\u505A\u592A\u76D1\u7684\uFF01\u300D</p><p>\u5C01\u603B\u7BA1\u8138\u8272\u4E00\u6C89\uFF0C\u97E9\u5168\u559D\u9053\uFF1A\u300C\u4F4F\u53E3\uFF01\u300D</p><p>\u767D\u96EA\u83B2\u6491\u8D77\u8EAB\u6765\uFF0C\u300C\u4F60\u8FD9\u4E2A\u4E0D\u7537\u4E0D\u5973\u7684\u9609\u72D7\uFF01\u4E0D\u8BB8\u78B0\u6211\u5F1F\u5F1F\uFF01\u300D</p><p>\u97E9\u5168\u626C\u6307\u671D\u5979\u7684\u80F8\u524D\u70B9\u53BB\uFF0C\u52A8\u4F5C\u5FEB\u5982\u9B3C\u9B45\u3002\u767D\u96EA\u83B2\u7FFB\u8155\u683C\u5F00\uFF0C\u53C8\u8FDE\u6321\u4E86\u4ED6\u4E09\u6307\uFF0C\u53C8\u8FD8\u4E86\u4E00\u638C\u3002\u767D\u96EA\u83B2\u8D44\u8D28\u51FA\u4F17\uFF0C\u867D\u7136\u4FEE\u4E3A\u5C1A\u6D45\uFF0C\u4F46\u8F83\u97E9\u5168\u4E5F\u5DEE\u4E0D\u4E86\u592A\u591A\u3002\u97E9\u5168\u6697\u81EA\u4F30\u7B97\uFF0C\u5373\u4F7F\u80FD\u53D6\u80DC\u4E5F\u8981\u5230\u767E\u62DB\u4E4B\u540E\u3002</p><p>\u6B63\u7126\u6025\u95F4\uFF0C\u65C1\u8FB9\u7684\u5B59\u5929\u7FBD\u7A81\u7136\u4E00\u638C\u671D\u767D\u82F1\u83B2\u5934\u4E0A\u62CD\u53BB\u3002\u767D\u5BB6\u4EC5\u8FD9\u4E00\u4E2A\u7537\u5B69\uFF0C\u7EDD\u4E0D\u5BB9\u6709\u534A\u70B9\u95EA\u5931\uFF0C\u767D\u96EA\u83B2\u8FDE\u5FD9\u53BB\u6321\uFF0C\u8EAB\u540E\u9732\u51FA\u7834\u7EFD\uFF0C\u88AB\u97E9\u5168\u8D81\u673A\u70B9\u4E2D\u7A74\u9053\uFF0C\u521A\u626C\u8D77\u8EAB\u4FBF\u762B\u5012\u5728\u5730\u3002\u5B59\u5929\u7FBD\u624B\u638C\u843D\u4E0B\uFF0C\u5728\u82F1\u83B2\u989D\u4E0A\u4E00\u62B9\uFF0C\u4FBF\u6536\u4E86\u56DE\u6765\u3002</p><p>\u5C01\u603B\u7BA1\u5BF9\u767D\u96EA\u83B2\u7684\u6323\u624E\u89C6\u82E5\u65E0\u7779\uFF0C\u53EA\u7B11\u54AA\u54AA\u5BF9\u82F1\u83B2\u9053\uFF1A\u300C\u4E56\u5B69\u5B50\uFF0C\u7B49\u4F60\u51C0\u4E86\u8EAB\uFF0C\u5C31\u80FD\u8DDF\u7740\u516C\u516C\u4E86\u3002\u300D</p><p>\u767D\u82F1\u83B2\u867D\u7136\u4E0D\u660E\u767D\u51C0\u8EAB\u662F\u4EC0\u4E48\u610F\u601D\uFF0C\u4F46\u59D0\u59D0\u7684\u53EB\u58F0\u5374\u8BA9\u4ED6\u5BB3\u6015\u4E86\uFF0C\u8BF4\u9053\uFF1A\u300C\u4E0D\uFF0C\u6211\u4E0D\u8981\u5F53\u592A\u76D1\u2026\u2026\u300D</p><p>\u5C01\u603B\u7BA1\u51B7\u54FC\u4E00\u58F0\uFF0C\u300C\u4E0D\u6653\u4E8B\u7684\u5B69\u5B50\u3002\u300D</p><p>\u97E9\u5168\u5728\u65C1\u7EC6\u58F0\u7EC6\u6C14\u8BF4\u9053\uFF1A\u300C\u5C0F\u50BB\u74DC\uFF0C\u505A\u592A\u76D1\u6709\u4EC0\u4E48\u4E0D\u597D\uFF1F\u5F53\u4E86\u592A\u76D1\uFF0C\u51FA\u5165\u5185\u5EF7\uFF0C\u5728\u4E07\u5C81\u7237\u8DDF\u524D\u6548\u529B\u2014\u2014\u90A3\u662F\u51E0\u4E16\u4FEE\u6765\u7684\u798F\u6C14\u3002\u591A\u5C11\u4EBA\u51C0\u4E86\u8EAB\u60F3\u5F53\u592A\u76D1\u8FD8\u5F53\u4E0D\u4E86\u5462\u3002\u5C01\u516C\u516C\u5FC3\u75BC\u4F60\uFF0C\u624D\u7ED9\u4F60\u6307\u4E86\u6761\u660E\u8DEF\u3002\u5F80\u540E\u60F3\u5347\u5B98\u60F3\u53D1\u8D22\uFF0C\u90FD\u7531\u7740\u4F60\u5462\u3002\u300D</p><p>\u82F1\u83B2\u88AB\u4ED6\u8BF4\u5F97\u80E1\u6D82\u8D77\u6765\uFF0C\u5077\u773C\u53BB\u770B\u59D0\u59D0\u3002\u4F46\u767D\u96EA\u83B2\u88AB\u70B9\u4E86\u54D1\u7A74\uFF0C\u773C\u775B\u53D1\u7EA2\u5374\u8BF4\u4E0D\u51FA\u8BDD\u6765\u3002\u7389\u5A18\u4F3C\u4E4E\u53D7\u4E86\u6781\u5927\u7684\u60CA\u5413\uFF0C\u795E\u60C5\u604D\u60DA\u5730\u671B\u7740\u82F1\u83B2\u3002\u8FD9\u5B69\u5B50\uFF0C\u4FCA\u7F8E\u5F97\u592A\u6709\u4E9B\u8FC7\u5206\u4E86\u3002\u5C31\u50CF\u90A3\u4E2A\u97E9\u5168\u2026\u2026\u5979\u5927\u817F\u53C8\u98A4\u6296\u8D77\u6765\u3002</p><p>\u5C01\u603B\u7BA1\u7EC6\u58F0\u9053\uFF1A\u300C\u97E9\u5168\uFF0C\u4F60\u5E2E\u5929\u7FBD\u4E00\u628A\u3002\u300D</p><p>\u97E9\u5168\u62BF\u5634\u4E00\u7B11\uFF0C\u6293\u4F4F\u82F1\u83B2\u7684\u80F8\u53E3\uFF0C\u5C06\u4ED6\u63D0\u5230\u4E00\u53EA\u5706\u51F3\u4E0A\u3002\u767D\u82F1\u83B2\u731B\u7136\u95F4\u6709\u4E86\u79CD\u5927\u96BE\u4E34\u5934\u7684\u9884\u611F\uFF0C\u4E00\u8FB9\u5C16\u53EB\uFF0C\u4E00\u8FB9\u62DA\u547D\u626D\u52A8\u8EAB\u4F53\uFF0C\u4E24\u811A\u4E71\u8E22\u3002\u97E9\u5168\u9A7E\u8F7B\u5C31\u719F\u5730\u62E7\u4F4F\u4ED6\u7684\u53CC\u81C2\uFF0C\u5C06\u4ED6\u62E7\u5F97\u5411\u540E\u5012\u53BB\uFF0C\u987A\u624B\u5C01\u4E86\u4ED6\u4E24\u817F\u7684\u7A74\u9053\u3002</p><p>\u97E9\u5168\u7B11\u541F\u541F\u9053\uFF1A\u300C\u522B\u6015\u554A\uFF0C\u53EA\u8981\u8F7B\u8F7B\u75BC\u4E00\u4E0B\uFF0C\u628A\u4E0B\u9762\u90A3\u4E2A\u591A\u4F59\u7684\u5BF9\u8C61\u4E00\u5207\uFF0C\u628A\u4F60\u9609\u6389\u5C31\u597D\u4E86\u3002\u300D</p><p>\u767D\u82F1\u83B2\u5413\u5F97\u54ED\u4E86\u8D77\u6765\uFF0C\u300C\u516C\u516C\uFF0C\u522B\u9609\u6211\u2026\u2026\u6211\u4EE5\u540E\u4E00\u5B9A\u542C\u4F60\u7684\u8BDD\u2026\u2026\u300D</p><p>\u97E9\u5168\u631F\u4F4F\u4ED6\u7684\u8EAB\u5B50\uFF0C\u300C\u8FD9\u4F1A\u513F\u5C31\u8BE5\u542C\u8BDD\u5462\uFF0C\u522B\u4E71\u52A8\u3002\u300D</p><p>\u300C\u6211\u4E0D\u542C\uFF01\u6211\u4E0D\u542C\uFF01\u4F60\u653E\u5F00\u6211\uFF01\u300D\u767D\u82F1\u83B2\u626D\u52A8\u7740\u8EAB\u4F53\uFF0C\u62DA\u547D\u6323\u624E\u3002</p><p>\u97E9\u5168\u62AC\u8D77\u624B\u6307\uFF0C\u51C6\u5907\u8FDE\u4ED6\u4E0A\u80A2\u7684\u7A74\u9053\u4E5F\u4E00\u5E76\u5C01\u4E86\uFF0C\u5C01\u603B\u7BA1\u6446\u4E86\u6446\u624B\uFF0C\u5C16\u58F0\u9053\uFF1A\u300C\u53D6\u5BB6\u4EC0\u6765\u3002\u8FD8\u6709\uFF0C\u6728\u51FB\u5B50\u4E5F\u62FF\u6765\u3002\u300D</p><p>\u97E9\u5168\u6014\u4E86\u4E00\u4E0B\uFF0C\u5E94\u9053\uFF1A\u300C\u662F\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u8FC7\u6765\u6293\u4F4F\u82F1\u83B2\u7684\u53CC\u624B\u3002\u7247\u523B\u540E\uFF0C\u97E9\u5168\u4ECE\u5185\u5BA4\u51FA\u6765\uFF0C\u4E00\u624B\u62FF\u4E86\u53EA\u6258\u76D8\uFF0C\u4E00\u624B\u62FF\u7740\u53EA\u7C7B\u4F3C\u7AF9\u6795\u7684\u5BF9\u8C61\u3002\u4E24\u4EBA\u62AC\u8D77\u82F1\u83B2\uFF0C\u628A\u7AF9\u6795\u57AB\u5728\u4ED6\u81C0\u4E0B\u3002</p><p>\u5C01\u603B\u7BA1\u5C16\u7EC6\u7684\u58F0\u97F3\u518D\u6B21\u54CD\u8D77\uFF0C\u300C\u5929\u7FBD\uFF0C\u53BB\u505A\u5427\u3002\u300D</p><p>\u767D\u82F1\u83B2\u8EBA\u5728\u5730\u4E0A\uFF0C\u5C41\u80A1\u57AB\u5F97\u62AC\u8D77\u3002\u4ED6\u4E0B\u80A2\u65E0\u6CD5\u52A8\u4F5C\uFF0C\u80A9\u5934\u88AB\u97E9\u5168\u6309\u7740\uFF0C\u6EE1\u9762\u60CA\u6050\uFF0C\u50CF\u4E00\u6761\u6401\u6D45\u7684\u5C0F\u9C7C\uFF0C\u5728\u6697\u7EA2\u7684\u5730\u6BEF\u4E0A\u7D27\u5F20\u5730\u5438\u7740\u6C14\uFF0C\u5C0F\u809A\u5B50\u4E00\u9F13\u4E00\u9F13\u3002</p><p>\u5B59\u5929\u7FBD\u7AD9\u8D77\u8EAB\uFF0C\u6258\u76D8\u91CC\u653E\u7740\u4E00\u5177\u6728\u5323\uFF0C\u65C1\u8FB9\u6446\u7740\u4E00\u65B9\u5D2D\u65B0\u7684\u767D\u5E03\uFF0C\u8FD8\u6709\u4E00\u53EA\u6728\u5236\u64C2\u81FC\u3002\u5C01\u603B\u7BA1\u5C06\u767D\u96EA\u83B2\u548C\u7389\u5A18\u53EB\u6765\uFF0C\u8BA9\u4ED6\u5F53\u7740\u4E24\u4EBA\u7684\u9762\uFF0C\u4EB2\u624B\u9609\u5272\u82F1\u83B2\u3002\u5C31\u662F\u5728\u4ED6\u8DDF\u4E39\u5A18\u4E4B\u95F4\uFF0C\u7ED3\u4E0B\u4E00\u4E2A\u89E3\u4E0D\u5F00\u7684\u6B7B\u7ED3\u3002\u4ED6\u65E0\u6CD5\u60F3\u8C61\uFF0C\u4E39\u5A18\u77E5\u9053\u4ED6\u9609\u5272\u4E86\u81EA\u5DF1\u552F\u4E00\u7684\u513F\u5B50\u4F1A\u662F\u2026\u2026\u5B59\u5929\u7FBD\u4E00\u54AC\u7259\uFF0C\u6253\u5F00\u6728\u5323\u3002</p>',99);function t(n,i){return r}var o=e(a,[["render",t]]);export{o as default};
