import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_56-\u8131\u8EAB" tabindex="-1"><a class="header-anchor" href="#_56-\u8131\u8EAB" aria-hidden="true">#</a> 56 \u8131\u8EAB</h1><p>\u539F\u6765\u7684\u72F1\u5352\u4EEC\u4E09\u4EBA\u4E94\u4EBA\u4E00\u95F4\u88AB\u5173\u5728\u7262\u5185\uFF0C\u8FD9\u4E9B\u4EBA\u4F5C\u5A01\u4F5C\u798F\u60EF\u4E86\uFF0C\u5982\u4ECA\u8FDB\u4E86\u7262\u72F1\uFF0C\u624D\u77E5\u9053\u9636\u4E0B\u56DA\u7684\u6ECB\u5473\u4E0D\u662F\u597D\u53D7\u7684\u3002\u4F17\u4EBA\u6709\u7684\u9762\u5982\u6B7B\u7070\uFF0C\u6709\u7684\u76EE\u5149\u5446\u6EDE\uFF0C\u6709\u7684\u8FDE\u58F0\u53EB\u9A82\uFF0C\u6709\u7684\u53CD\u5507\u76F8\u8BA5\uFF0C\u53EB\u7684\u95F9\u7684\u7838\u4E1C\u897F\u7684\uFF0C\u4E71\u6210\u4E00\u7247\u3002</p><p>\u6B63\u5435\u56B7\u95F4\uFF0C\u4E00\u540D\u58EB\u5352\u8FC7\u6765\u559D\u9053\uFF1A\u300C\u5435\u4EC0\u4E48\u5435\uFF01\u90FD\u7ED9\u6211\u95ED\u5634\uFF01\u300D</p><p>\u8FD9\u7262\u91CC\u7684\u5934\u4E24\u5929\u524D\u8FD8\u90FD\u662F\u72F1\u5352\uFF0C\u5728\u7262\u91CC\u5173\u5F97\u4E00\u809A\u5B50\u7262\u9A9A\uFF0C\u6709\u4EBA\u53EB\u9053\uFF1A\u300C\u51ED\u4EC0\u4E48\u5173\u6211\u4EEC\uFF01\u6B20\u503A\u8FD8\u94B1\uFF0C\u6740\u4EBA\u507F\u547D\uFF0C\u6211\u4EEC\u62DB\u8C01\u60F9\u8C01\u4E86\uFF1F\u300D</p><p>\u90A3\u58EB\u5352\u6C89\u7740\u8138\u9053\uFF1A\u300C\u6709\u51A4\u5230\u5802\u4E0A\u558A\u53BB\uFF0C\u5728\u7262\u91CC\u90FD\u7ED9\u6211\u8001\u5B9E\u4E9B\uFF01\u300D</p><p>\u4F55\u6C42\u56FD\u5C0F\u58F0\u561F\u56D4\u9053\uFF1A\u300C\u800D\u4EC0\u4E48\u5A01\u98CE\u5462\uFF1F\u6211\u7BA1\u8FD9\u5927\u7262\u7684\u65F6\u5019\uFF0C\u8C01\u77E5\u9053\u4F60\u662F\u8001\u51E0\u554A\u3002\u300D</p><p>\u90A3\u58EB\u5352\u558A\u4E86\u58F0\uFF0C\u53EB\u6765\u4E00\u540D\u540C\u4F34\uFF0C\u7136\u540E\u6253\u5F00\u7262\u95E8\uFF0C\u62E7\u4F4F\u4F55\u6C42\u56FD\u7684\u9886\u5B50\uFF0C\u628A\u4ED6\u62D6\u4E86\u51FA\u6765\u3002\u4E0D\u7B49\u4ED6\u8FA9\u89E3\uFF0C\u5C31\u62A1\u8D77\u76AE\u97AD\u5288\u5934\u76D6\u8138\u4E00\u901A\u72E0\u6253\uFF0C\u76F4\u6253\u5F97\u4F55\u6C42\u56FD\u9B3C\u54ED\u72FC\u568E\uFF0C\u54ED\u7239\u558A\u5A18\u3002</p><p>\u90A3\u6C49\u5B50\u529B\u6C14\u65E2\u5927\uFF0C\u4E0B\u624B\u53C8\u72E0\uFF0C\u4F55\u6C42\u56FD\u5F00\u59CB\u8FD8\u6740\u732A\u4F3C\u7684\u60E8\u53EB\uFF0C\u63A5\u7740\u58F0\u97F3\u8D8A\u6765\u8D8A\u5C0F\uFF0C\u6700\u540E\u5C31\u53EA\u5269\u4E0B\u76AE\u97AD\u6253\u5728\u8EAB\u4E0A\u7684\u556A\u556A\u58F0\uFF0C\u7B49\u6254\u56DE\u7262\u91CC\uFF0C\u4F55\u6C42\u56FD\u6EE1\u8138\u9EBB\u5B50\u90FD\u80BF\u4E86\u8D77\u6765\uFF0C\u5C31\u50CF\u6B7B\u4E86\u822C\u4E00\u58F0\u4E0D\u542D\u3002\u90A3\u6C49\u5B50\u6307\u7740\u4F17\u4EBA\u9053\uFF1A\u300C\u5C31\u662F\u6709\u5C41\u4E5F\u7ED9\u6211\u5939\u7D27\u4E86\uFF01\u8C01\u6562\u6492\u51FA\u58F0\uFF0C\u6211\u5C31\u80FD\u53EB\u4F60\u54BD\u56DE\u53BB\uFF01\u300D</p><p>\u8FD9\u73ED\u72F1\u5352\u539F\u672C\u4E5F\u662F\u72E0\u4EBA\uFF0C\u5BF9\u56DA\u72AF\u6BD2\u6253\u9177\u5211\u4E60\u4EE5\u4E3A\u5E38\uFF0C\u8F6E\u5230\u81EA\u5DF1\u5934\u4E0A\uFF0C\u8FD9\u4F1A\u513F\u4E00\u4E2A\u4E2A\u90FD\u5664\u82E5\u5BD2\u8749\u3002</p><p>\u539F\u6709\u7684\u56DA\u72AF\u5DF2\u88AB\u79FB\u8D70\uFF0C\u9664\u4E86\u90A3\u4E9B\u72F1\u5352\uFF0C\u8FD9\u7262\u91CC\u53EA\u5269\u4E0B\u767D\u96EA\u83B2\u3001\u859B\u971C\u7075\u4E24\u4EBA\u3002</p><p>\u859B\u971C\u7075\u662F\u5DF2\u5B9A\u7684\u6B7B\u56DA\uFF0C\u88AB\u5173\u5165\u5730\u7262\uFF0C\u767D\u96EA\u83B2\u8EAB\u4E3A\u91CD\u72AF\uFF0C\u5E26\u4E86\u624B\u677B\u811A\u9563\uFF0C\u72EC\u81EA\u5173\u5728\u4E00\u5904\u3002</p><p>\u5149\u7EBF\u6E10\u6E10\u9EEF\u6DE1\uFF0C\u767D\u96EA\u83B2\u95ED\u7740\u773C\uFF0C\u82CD\u767D\u800C\u6194\u60B4\u7684\u8138\u4E0A\u5E26\u7740\u5FC3\u6B7B\u822C\u7684\u6F20\u7136\u3002</p><p>\u4F55\u6E05\u6CB3\u88AB\u4E00\u9053\u5723\u65E8\u903C\u8D70\uFF0C\u76D1\u72F1\u843D\u5165\u4E1C\u5382\u624B\u4E2D\uFF0C\u767D\u96EA\u83B2\u5C31\u662F\u518D\u50BB\uFF0C\u4E5F\u77E5\u9053\u8FD9\u610F\u5473\u7740\u4EC0\u4E48\u3002\u5C01\u603B\u7BA1\u8BF4\u7684\u7FFB\u6848\uFF0C\u5979\u6839\u672C\u4E0D\u4FE1\uFF0C\u5B59\u5929\u7FBD\u5802\u4E0A\u8BA4\u7236\uFF0C\u8DDF\u8FD9\u592A\u76D1\u5206\u660E\u662F\u4E00\u4E18\u4E4B\u8C89\u3002\u5047\u5982\u767D\u96EA\u83B2\u6B64\u524D\u8FD8\u6709\u5E7B\u60F3\uFF0C\u73B0\u5728\u5DF2\u7ECF\u662F\u68A6\u9192\u4E86\u3002</p><p>\u90A3\u4E9B\u65B0\u6765\u7684\u72F1\u5352\u6CA1\u7ED9\u5979\u5E26\u4E0A\u91CD\u67B7\uFF0C\u4F46\u5728\u5353\u5929\u96C4\u7684\u575A\u6301\u4E0B\uFF0C\u7ED9\u767D\u96EA\u83B2\u8155\u4E0A\u53C8\u52A0\u4E86\u6761\u7CBE\u94A2\u9501\u94FE\uFF0C\u5C06\u5979\u5E26\u4E86\u6728\u677B\u7684\u53CC\u624B\u9501\u5728\u4E00\u5904\u3002\u811A\u9563\u662F\u5E73\u5E38\u7684\u94F8\u94C1\uFF0C\u4E24\u7AEF\u5E26\u5B54\uFF0C\u7528\u9500\u5B50\u62E7\u6B7B\uFF0C\u4E2D\u95F4\u7CFB\u7740\u5C3A\u8BB8\u957F\u7684\u94C1\u94FE\uFF0C\u8D70\u8DEF\u65F6\u53EA\u80FD\u4E00\u6B65\u4E00\u6B65\u632A\u52A8\u3002</p><p>\u5927\u7262\u5185\u5916\u4E00\u5171\u6709\u516D\u540D\u72F1\u5352\uFF0C\u5176\u4E2D\u4E24\u4E2A\u5728\u5730\u7262\u770B\u5B88\u859B\u971C\u7075\uFF0C\u4E24\u4E2A\u5728\u7262\u5916\u5DE1\u89C6\uFF0C\u53E6\u5916\u4E24\u4E2A\u5728\u5927\u7262\u5185\u7684\u8033\u623F\u5B88\u7740\u3002\u6BCF\u4E2A\u65F6\u8FB0\u6362\u4E00\u6B21\u73ED\u3002\u7262\u623F\u9664\u4E86\u5927\u95E8\uFF0C\u53EA\u6709\u9876\u4E0A\u4E00\u6392\u6C14\u7A97\u53EF\u5BB9\u901A\u8FC7\u3002\u5F53\u65E5\u859B\u971C\u7075\u5C31\u662F\u4ECE\u90A3\u91CC\u8D8A\u72F1\uFF0C\u73B0\u5728\u90FD\u6362\u6210\u4E86\u94C1\u6805\u3002</p><p>\u4ECE\u7262\u623F\u51FA\u6765\uFF0C\u79BB\u6700\u8FD1\u7684\u56F4\u5899\u53EA\u6709\u5341\u51E0\u4E08\u8FDC\u8FD1\u3002\u56F4\u5899\u9AD8\u6709\u4E08\u8BB8\uFF0C\u968F\u4FBF\u627E\u4EF6\u4E1C\u897F\u652F\u6491\uFF0C\u5C31\u80FD\u8D8A\u8FC7\u3002</p><p>\u767D\u96EA\u83B2\u62E7\u4F4F\u8155\u4E0A\u7684\u94C1\u94FE\uFF0C\u8BD5\u4E86\u8BD5\u4EFD\u91CF\uFF0C\u7136\u540E\u5782\u76EE\u5165\u5B9A\uFF0C\u9759\u9759\u7B49\u6DF1\u591C\u7684\u6765\u4E34\u3002</p><p>\u591C\u4EA4\u5B50\u65F6\uFF0C\u53E6\u4E00\u7EC4\u516D\u4EBA\u524D\u6765\u6362\u73ED\uFF0C\u5730\u7262\u91CC\u7684\u4E24\u4EBA\u51FA\u6765\uFF0C\u8DDF\u4F17\u4EBA\u54AC\u7740\u8033\u6735\u8BF4\u4E86\u51E0\u53E5\uFF0C\u4F9D\u7A00\u5728\u8BF4\u90A3\u8718\u86DB\u7CBE\u5982\u4F55\u5982\u4F55\uFF0C\u7247\u523B\u4E4B\u540E\u4F17\u4EBA\u53D1\u51FA\u4E00\u9635\u6DEB\u7B11\u58F0\u3002\u7B49\u90A3\u4E00\u7EC4\u79BB\u5F00\uFF0C\u8FD9\u73ED\u4EBA\u628A\u7262\u95E8\u4E00\u9501\uFF0C\u4E00\u7A9D\u8702\u6D8C\u8FDB\u5730\u7262\uFF0C\u987A\u624B\u6263\u4E0A\u94C1\u7F69\u3002</p><p>\u767D\u96EA\u83B2\u7741\u5F00\u773C\u775B\uFF0C\u6478\u4F4F\u811A\u4E0A\u7684\u94C1\u9563\uFF0C\u5438\u4E86\u53E3\u6C14\u6162\u6162\u626D\u52A8\u3002\u5979\u7684\u811A\u539F\u672C\u7F20\u8FC7\uFF0C\u867D\u7136\u653E\u5F00\u5DF2\u6709\u6570\u5E74\uFF0C\u4F46\u6BD4\u6B63\u5E38\u811A\u5F62\u7EA4\u7EC6\u8BB8\u591A\uFF0C\u5E76\u6CA1\u6709\u8D39\u4EC0\u4E48\u529B\u6C14\u5C31\u53D6\u4E86\u4E0B\u6765\u3002\u624B\u4E0A\u7684\u6728\u677B\u5BF9\u767D\u96EA\u83B2\u6765\u8BF4\u5E76\u6CA1\u6709\u4EC0\u4E48\u4F5C\u7528\uFF0C\u552F\u4E00\u7684\u56F0\u96BE\u662F\u90A3\u6761\u7CBE\u94A2\u9501\u94FE\u3002\u767D\u96EA\u83B2\u7684\u6307\u529B\u8FD8\u4E0D\u8DB3\u4EE5\u5C06\u5B83\u626F\u65AD\u3002</p><p>\u7262\u5185\u5DF2\u7ECF\u662F\u9F3E\u58F0\u5927\u4F5C\uFF0C\u9690\u9690\u80FD\u542C\u5230\u5730\u7262\u91CC\u6DEB\u7325\u7684\u7B11\u8C11\u58F0\uFF0C\u6CA1\u6709\u4EBA\u6CE8\u610F\u5230\u89D2\u843D\u91CC\u767D\u96EA\u83B2\u7684\u52A8\u4F5C\u3002\u5979\u7528\u8863\u670D\u5305\u4F4F\u6728\u677B\uFF0C\u5FAE\u4E00\u7528\u529B\uFF0C\u5C06\u6728\u677B\u8F7B\u8F7B\u62E7\u788E\uFF0C\u7136\u540E\u6296\u53BB\u6728\u5C51\uFF0C\u5F20\u5F00\u624B\u8BD5\u4E86\u8BD5\u9501\u94FE\u7684\u957F\u5EA6\u3002</p><p>\u8FD9\u9501\u94FE\u6263\u5F97\u6781\u7D27\uFF0C\u54AC\u7D27\u4E86\u76AE\u8089\uFF0C\u5373\u4F7F\u5979\u80FD\u591F\u7F29\u9AA8\uFF0C\u4E5F\u65E0\u6CD5\u65BD\u5C55\u3002\u767D\u96EA\u83B2\u63E1\u4F4F\u94C1\u94FE\u7528\u529B\u4E00\u6323\uFF0C\u6700\u540E\u8FD8\u662F\u653E\u5F03\u4E86\u3002</p><p>\u76F8\u6BD4\u4E4B\u4E0B\uFF0C\u7262\u95E8\u4E0A\u7684\u94C1\u9501\u5C31\u7C97\u7CD9\u5F97\u591A\uFF0C\u6709\u4E9B\u73AF\u6263\u751A\u81F3\u6CA1\u6709\u710A\u7262\u3002\u767D\u96EA\u83B2\u62E3\u51FA\u4E00\u73AF\uFF0C\u5C06\u5B83\u6162\u6162\u62E7\u5F00\uFF0C\u8F7B\u624B\u8F7B\u811A\u5730\u89E3\u4E0B\u94FE\u9501\uFF0C\u7136\u540E\u5C06\u6240\u6709\u4E1C\u897F\u90FD\u585E\u5728\u88AB\u4E2D\uFF0C\u8F7B\u8F7B\u6253\u5F00\u7262\u95E8\u3002</p><p>\u5F53\u767D\u96EA\u83B2\u7ACB\u5728\u5899\u5934\uFF0C\u6C90\u6D74\u5728\u591C\u98CE\u7684\u6E05\u65B0\u4E0B\uFF0C\u5979\u624D\u6CE8\u610F\u5230\u6F2B\u5929\u661F\u6597\uFF0C\u5C06\u591C\u7A7A\u88C5\u626E\u5F97\u65E0\u6BD4\u7480\u74A8\u3002\u5979\u5446\u5446\u770B\u4E86\u7247\u523B\uFF0C\u7136\u540E\u7528\u6EF4\u8840\u7684\u624B\u638C\u62C9\u7D27\u94C1\u94FE\uFF0C\u98DE\u8EAB\u63A0\u4E0B\uFF0C\u5934\u4E5F\u4E0D\u56DE\u5730\u79BB\u5F00\u4E86\u8FD9\u5EA7\u5C06\u5979\u56DA\u7981\u5C06\u8FD1\u7684\u534A\u5E74\u9ED1\u72F1\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5B59\u5929\u7FBD\u5F13\u7740\u8170\u4F0F\u5728\u5E8A\u8FB9\uFF0C\u4E24\u6761\u6D01\u767D\u7684\u7389\u817F\u76D8\u5728\u4ED6\u8170\u95F4\uFF0C\u5F2F\u7FD8\u7684\u7EA4\u8DB3\u50CF\u4E00\u5BF9\u767D\u7389\u6263\u822C\u4EA4\u5728\u4E00\u8D77\u3002\u4ECE\u5B59\u5929\u7FBD\u5206\u5F00\u7684\u817F\u95F4\uFF0C\u80FD\u770B\u5230\u5C11\u5973\u767D\u5AE9\u7684\u96EA\u81C0\uFF0C\u4E2D\u95F4\u5A07\u67D4\u7EA2\u817B\u7684\u7F8E\u7A74\u6B63\u88AB\u4E00\u6839\u9633\u5177\u8086\u610F\u6233\u5F04\uFF0C\u6C34\u6C6A\u6C6A\u6DCC\u7740\u871C\u6C41\u3002</p><p>\u5B59\u5929\u7FBD\u5598\u7740\u6C14\u9053\uFF1A\u300C\u674F\u513F\uFF0C\u628A\u706F\u62FF\u6765\u3002\u300D</p><p>\u53EA\u7A7F\u4E86\u8D34\u8EAB\u5C0F\u8863\u7684\u4E39\u5A18\u4E3E\u4E86\u706F\u8FC7\u6765\uFF0C\u8EBA\u5728\u5B59\u5929\u7FBD\u8EAB\u4E0B\u7684\u7389\u83B2\u5F77\u4F5B\u6574\u4E2A\u4EBA\u90FD\u5316\u6210\u4E86\u4E00\u6C6A\u6C34\uFF0C\u767D\u6ED1\u7684\u7389\u4F53\u67D4\u817B\u4E4B\u6781\u3002\u5B59\u5929\u7FBD\u63E1\u4F4F\u7389\u83B2\u7684\u819D\u5F2F\uFF0C\u5C06\u5979\u4E24\u6761\u767D\u751F\u751F\u7684\u5927\u817F\u63B0\u5F00\uFF0C\u8BA9\u4E39\u5A18\u4E3E\u7740\u706F\uFF0C\u89C2\u8D4F\u4E24\u4EBA\u4EA4\u5408\u7684\u8273\u6001\u3002</p><p>\u5728\u5B59\u5929\u7FBD\u7684\u50AC\u4FC3\u4E0B\uFF0C\u7389\u83B2\u7F9E\u6DA9\u5730\u628A\u624B\u4F38\u5230\u8179\u4E0B\uFF0C\u5C06\u63D2\u7740\u9633\u5177\u7684\u7389\u6237\u5265\u5F00\uFF0C\u8BA9\u4ED6\u770B\u5F97\u66F4\u4ED4\u7EC6\u3002\u5B59\u5929\u7FBD\u4E00\u8FB9\u633A\u52A8\u8170\u80CC\uFF0C\u4E00\u8FB9\u89C2\u8D4F\u871C\u7A74\u5728\u9633\u5177\u63D2\u5F04\u4E0B\u7684\u53D8\u5316\uFF0C\u7B11\u9053\uFF1A\u300C\u706F\u4E0B\u770B\u7261\u4E39\uFF0C\u679C\u7136\u662F\u522B\u6709\u4E00\u756A\u98CE\u60C5\u3002\u300D</p><p>\u7389\u83B2\u5403\u5403\u8F7B\u7B11\u9053\uFF1A\u300C\u4EBA\u5BB6\u90A3\u91CC\u53C8\u4E0D\u662F\u7261\u4E39\u2026\u2026\u300D</p><p>\u300C\u8FD9\u662F\u8089\u7261\u4E39\uFF0C\u8BA9\u4F60\u5A18\u770B\u770B\uFF0C\u662F\u4E0D\u662F\u6BD4\u771F\u7261\u4E39\u8FD8\u7EA2\u8FD8\u8273\uFF1F\u300D</p><p>\u4E39\u5A18\u7B11\u4E86\u7B11\uFF0C\u300C\u7389\u83B2\u4E0B\u8FB9\u751F\u5F97\u597D\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u7B11\u9053\uFF1A\u300C\u5A18\u513F\u4FE9\u4E00\u822C\u597D\uFF0C\u90FD\u662F\u53C8\u6ED1\u53C8\u8F6F\u7684\u5C0F\u5AE9\u5C44\u3002\u300D</p><p>\u4E39\u5A18\u5782\u4E0B\u773C\u6CA1\u6709\u4F5C\u58F0\u3002\u5B59\u5929\u7FBD\u62FF\u8D77\u7389\u83B2\u7684\u53CC\u817F\uFF0C\u4E00\u8F6E\u731B\u5E72\uFF0C\u5C06\u7389\u83B2\u63D2\u5F04\u5F97\u6CC4\u4E86\u8EAB\u5B50\uFF0C\u53C8\u5728\u5979\u6CC4\u8EAB\u7684\u7F8E\u7A74\u91CC\u62BD\u9001\u591A\u65F6\uFF0C\u76F4\u5230\u5979\u6CC4\u5C3D\u9634\u7CBE\uFF0C\u53CC\u817F\u53D1\u98A4\uFF0C\u53EB\u7684\u58F0\u97F3\u4E5F\u5F31\u4E86\uFF0C\u624D\u505C\u4E0B\u6765\u3002</p><p>\u7389\u83B2\u7CBE\u75B2\u529B\u5C3D\uFF0C\u660F\u660F\u6C89\u6C89\u7761\u4E86\u8FC7\u53BB\u3002\u4E39\u5A18\u4FA7\u8EAB\u5750\u5728\u5E8A\u8FB9\uFF0C\u5782\u624B\u9664\u53BB\u978B\u889C\u3002\u5B59\u5929\u7FBD\u62C9\u4F4F\u5979\uFF0C\u9053\uFF1A\u300C\u8BA9\u7389\u83B2\u7761\u4F1A\u513F\uFF0C\u6211\u4EEC\u5230\u4F60\u623F\u91CC\u53BB\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u559C\u8DDF\u5979\u4EEC\u6BCD\u5973\u540C\u5E8A\u6B22\u597D\uFF0C\u591C\u95F4\u4E39\u5A18\u4E0E\u7389\u83B2\u5F80\u5F80\u5BBF\u5728\u4E00\u5904\uFF0C\u5E73\u5E38\u5404\u6709\u623F\u95F4\u3002\u5230\u4E86\u4E39\u5A18\u623F\u91CC\uFF0C\u5B59\u5929\u7FBD\u6CA1\u6709\u6025\u4E8E\u6551\u6B22\uFF0C\u800C\u662F\u4E0A\u4E86\u5E8A\uFF0C\u5E2E\u4E39\u5A18\u9664\u53BB\u5C0F\u8863\uFF0C\u5C06\u5979\u8D64\u8EAB\u88F8\u4F53\u62E5\u5728\u6000\u91CC\uFF0C\u6162\u6162\u9053\uFF1A\u300C\u8FD9\u51E0\u65E5\uFF0C\u4F60\u5728\u51B7\u843D\u6211\uFF1F\u300D</p><p>\u300C\u4E0D\u3002\u5974\u5728\u60F3\u2026\u2026\u662F\u5974\u5BF9\u4E0D\u4F4F\u4ED6\u4EEC\u3002\u300D\u4E39\u5A18\u6162\u6162\u9053\uFF1A\u300C\u5974\u4E00\u4E2A\u4E0B\u8D31\u7684\u5A3C\u5987\uFF0C\u5BB3\u4E86\u90A3\u4E48\u591A\u4EBA\u3002\u539F\u6765\u7684\u76F8\u516C\u3001\u4E24\u4E2A\u5973\u513F\u3001\u82F1\u83B2\u2026\u2026\u8FD8\u6709\u9752\u7389\u3002\u5974\u662F\u4E0D\u7965\u4E4B\u8EAB\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u629A\u6478\u7740\u5979\u7684\u8138\u988A\uFF0C\u300C\u6211\u89C1\u4F60\u8FD9\u51E0\u5929\u6709\u4E9B\u9B42\u4E0D\u5B88\u820D\u7684\u6837\u5B50\u2026\u2026\u300D\u4ED6\u82E6\u7B11\u4E86\u4E00\u4E0B\uFF0C\u300C\u7D22\u6027\u544A\u8BC9\u4F60\u5427\u3002\u674F\u513F\uFF0C\u6848\u5B50\u5DF2\u7ECF\u5B9A\u4E86\uFF0C\u4F60\u4EEC\u6BCD\u5973\u90FD\u514D\u4E86\u6B7B\u7F6A\uFF0C\u4F46\u8981\u6D41\u653E\u4E09\u5343\u91CC\u3002\u300D</p><p>\u300C\u82F1\u83B2\u5462\uFF1F\u300D</p><p>\u5B59\u5929\u7FBD\u72B9\u8C6B\u4E86\u4E00\u4E0B\uFF0C\u300C\u4ED6\u6CA1\u4E8B\u3002\u6709\u4EBA\u62A4\u7740\u4ED6\u3002\u300D</p><p>\u4E39\u5A18\u4E00\u624B\u629A\u7740\u5C0F\u8179\uFF0C\u6014\u6014\u60F3\u7740\u3002\u5979\u5DF2\u7ECF\u6709\u4E86\u56DB\u4E2A\u591A\u6708\u7684\u8EAB\u5B55\uFF0C\u5E73\u65F6\u7A7F\u4E86\u5BBD\u677E\u7684\u8863\u670D\uFF0C\u770B\u7740\u8FD8\u4E0D\u751A\u660E\u663E\uFF0C\u6B64\u65F6\u88F8\u7740\u8EAB\u5B50\uFF0C\u767D\u7389\u822C\u7684\u5C0F\u8179\u6D51\u5706\u9686\u8D77\uFF0C\u5C06\u4E0B\u8179\u90FD\u906E\u6CA1\u4E86\u3002</p><p>\u300C\u8FD8\u80FD\u7559\u591A\u4E45\uFF1F\u300D</p><p>\u300C\u4E09\u4E94\u4E2A\u6708\u5427\u3002\u6211\u4F1A\u5C3D\u529B\u62D6\u7740\u3002\u300D</p><p>\u4E39\u5A18\u60F3\u4E86\u4E00\u4F1A\u513F\uFF0C\u300C\u8FD8\u662F\u65E9\u4E9B\u5427\u3002\u518D\u7B49\uFF0C\u7389\u83B2\u7684\u8EAB\u5B50\u5C31\u7B28\u4E86\u3002\u300D\u7389\u83B2\u6000\u5B55\u4E5F\u6709\u4E24\u4E2A\u591A\u6708\u4E86\uFF0C\u62D6\u4E0B\u53BB\uFF0C\u53EA\u6015\u6B63\u8D76\u4E0A\u5206\u5A29\u3002</p><p>\u5B59\u5929\u7FBD\u9053\uFF1A\u300C\u674F\u513F\uFF0C\u4F60\u771F\u7684\u4E0D\u6028\u6211\u4E48\uFF1F\u300D</p><p>\u300C\u90FD\u662F\u6211\u7684\u4E0D\u662F\uFF0C\u600E\u4E48\u6028\u5F97\u4E86\u65C1\u4EBA\u3002\u5929\u7FBD\u54E5\uFF0C\u4F60\u9A97\u6211\u7684\u90A3\u4E9B\u65E5\u5B50\u91CC\uFF0C\u6211\u5F88\u5FEB\u6D3B\u3002\u8BF4\u4F60\u559C\u6B22\u6211\u5427\u2026\u2026\u300D</p><p>\u300C\u6211\u4E0D\u9A97\u4F60\u3002\u6211\u771F\u7684\u559C\u6B22\u4F60\uFF0C\u820D\u4E0D\u5F97\u4F60\u3002\u300D\u5B59\u5929\u7FBD\u51B2\u52A8\u5730\u8BF4\u9053\uFF1A\u300C\u674F\u513F\uFF0C\u54B1\u4EEC\u4E00\u8D77\u8D70\u5427\uFF0C\u4EC0\u4E48\u90FD\u4E0D\u7BA1\u4E86\uFF0C\u6211\u4EEC\u4E00\u8D77\u53BB\u6EC7\u5357\uFF0C\u53BB\u6CA1\u6709\u4EBA\u7684\u5927\u5C71\u91CC\u9762\uFF0C\u53EA\u6709\u4F60\u8DDF\u6211\u3002\u300D</p><p>\u4E39\u5A18\u54AC\u5507\u7B11\u7740\uFF0C\u773C\u775B\u5374\u6E7F\u4E86\u3002\u826F\u4E45\u9053\uFF1A\u300C\u4F60\u771F\u4F1A\u54C4\u5F97\u6211\u5F00\u5FC3\u2026\u2026\u591C\u6DF1\u4E86\uFF0C\u5974\u5BB6\u670D\u4F8D\u4E86\u5B83\uFF0C\u518D\u966A\u4F60\u7761\u89C9\u5427\u3002\u300D</p><p>\u4E39\u5A18\u62C9\u7740\u5B59\u5929\u7FBD\uFF0C\u8BA9\u4ED6\u4F0F\u5230\u81EA\u5DF1\u8EAB\u4E0A\u3002</p><p>\u5B59\u5929\u7FBD\u5374\u9053\uFF1A\u300C\u5C0F\u5FC3\uFF0C\u522B\u538B\u7740\u5B69\u5B50\u3002\u300D</p><p>\u4E39\u5A18\u6014\u4E86\u4E00\u4E0B\uFF0C\u8FD9\u662F\u5B59\u5929\u7FBD\u7B2C\u4E00\u6B21\u5173\u5FC3\u5979\u809A\u91CC\u7684\u80CE\u513F\u3002\u77E5\u9053\u81EA\u5DF1\u6000\u5B55\u524D\uFF0C\u4E39\u5A18\u5C31\u88AB\u4EBA\u8F6E\u5978\u8FC7\uFF0C\u867D\u7136\u7B97\u7740\u65E5\u5B50\u5E94\u8BE5\u662F\u5B59\u5929\u7FBD\u7684\uFF0C\u4F46\u770B\u5F97\u51FA\u5B59\u5929\u7FBD\u4E00\u76F4\u90FD\u6709\u4E9B\u5728\u610F\uFF0C\u4E24\u4EBA\u6B22\u597D\u65F6\uFF0C\u5B59\u5929\u7FBD\u4ECE\u6765\u6CA1\u907F\u5FCC\u8FC7\u5979\u8179\u91CC\u7684\u80CE\u513F\u3002</p><p>\u300C\u6765\uFF0C\u5750\u6211\u6000\u91CC\u3002\u300D\u5B59\u5929\u7FBD\u501A\u5728\u5E8A\u9760\u4E0A\uFF0C\u8BF4\u9053\uFF1A\u300C\u5973\u4EBA\u5FC3\uFF0C\u6D77\u5E95\u9488\uFF0C\u6211\u4E5F\u5F04\u4E0D\u61C2\u4F60\u600E\u4E48\u60F3\u3002\u300D</p><p>\u4E39\u5A18\u8F7B\u7B11\u9053\uFF1A\u300C\u4E0D\u7528\u7BA1\u5974\u5BB6\u600E\u4E48\u60F3\u3002\u4F60\u628A\u5974\u5F53\u5A3C\u800D\u5C31\u662F\u4E86\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u671D\u5979\u81C0\u4E0A\u62CD\u4E86\u4E00\u638C\uFF0C\u300C\u5927\u6BCD\u72D7\uFF0C\u8FD8\u4E0D\u5FEB\u4E0A\u6765\u3002\u300D</p><p>\u4E39\u5A18\u5F20\u5F00\u817F\uFF0C\u66F2\u819D\u8DEA\u5728\u5B59\u5929\u7FBD\u8170\u4FA7\uFF0C\u7136\u540E\u6276\u4F4F\u4ED6\u7684\u9633\u5177\uFF0C\u96EA\u6ED1\u7684\u5706\u81C0\u67D4\u5A9A\u5730\u5411\u540E\u7FD8\u8D77\uFF0C\u5BF9\u7740\u8089\u68D2\u7F13\u7F13\u5750\u4E0B\u3002\u5B59\u5929\u7FBD\u6795\u7740\u53CC\u624B\uFF0C\u4E00\u8FB9\u89C2\u8D4F\u4E00\u8FB9\u7B11\u9053\uFF1A\u300C\u6765\u4E2A\u540E\u5EAD\u8BD5\u7BAB\u3002\u300D</p><p>\u4E39\u5A18\u5728\u4ED6\u817F\u4E0A\u626D\u4E86\u4E00\u628A\uFF0C\u4E00\u9762\u5206\u5F00\u81C0\u8089\uFF0C\u6478\u7D22\u7740\u5C06\u9F9F\u5934\u9876\u4F4F\u5C41\u773C\u513F\uFF0C\u7136\u540E\u677E\u5F00\u624B\uFF0C\u67D4\u5AE9\u7684\u5927\u767D\u81C0\u538B\u4F4F\u8089\u68D2\u65CB\u8F6C\u7740\uFF0C\u5355\u9760\u5C41\u80A1\u7684\u626D\u52A8\u7F13\u7F13\u5750\u4E0B\u3002\u9F9F\u5934\u6324\u5165\u5C41\u773C\u513F\uFF0C\u6CBF\u7740\u67D4\u8F6F\u7684\u80A0\u9053\u8D8A\u8FDB\u8D8A\u6DF1\uFF0C\u76F4\u5230\u6574\u6761\u9633\u5177\u9677\u8FDB\u7F8E\u81C0\uFF0C\u88AB\u809B\u8089\u67D4\u817B\u5730\u5305\u88F9\u4F4F\u3002</p><p>\u4E39\u5A18\u7684\u5C41\u80A1\u4E30\u6EE1\u80A5\u7FD8\uFF0C\u767D\u817B\u817B\u4E00\u56E2\u96EA\u8089\u8D34\u5728\u8179\u4E0A\uFF0C\u7EF5\u8F6F\u6ED1\u5AE9\u800C\u53C8\u5145\u6EE1\u5F39\u6027\uFF0C\u611F\u89C9\u9165\u723D\u4E4B\u6781\u3002\u5B59\u5929\u7FBD\u4E00\u624B\u63D2\u5230\u4E39\u5A18\u81C0\u4E0B\uFF0C\u6CBF\u7740\u81C0\u6C9F\u671D\u5185\u6478\u53BB\uFF0C\u4E39\u5A18\u5FAE\u5FAE\u62AC\u8D77\u5C41\u80A1\uFF0C\u5C06\u5C41\u773C\u513F\u4E0E\u8089\u68D2\u7684\u7ED3\u5408\u5904\u66B4\u9732\u51FA\u6765\uFF0C\u8BA9\u4ED6\u72CE\u73A9\u6478\u5F04\u3002</p><p>\u4E39\u5A18\u8DEA\u5750\u5728\u5B59\u5929\u7FBD\u8170\u95F4\uFF0C\u7EA2\u5AE9\u7684\u5C41\u773C\u513F\u5939\u7D27\u8089\u68D2\uFF0C\u96EA\u81C0\u4E0A\u4E0B\u6ED1\u52A8\u3002\u5B59\u5929\u7FBD\u4E00\u624B\u6478\u5230\u4E39\u5A18\u8179\u4E0B\uFF0C\u5728\u5979\u7389\u6237\u5185\u6478\u5F04\u7740\u3002\u4E39\u5A18\u4E24\u53EA\u96EA\u7403\u822C\u7684\u5706\u4E73\u5728\u80F8\u524D\u8DF3\u52A8\u7740\uFF0C\u8361\u51FA\u67D4\u8273\u7684\u8089\u5149\u3002\u5979\u7F8E\u76EE\u534A\u95ED\uFF0C\u8F7B\u58F0\u547B\u541F\u7740\uFF0C\u808C\u80A4\u6E10\u6E10\u6E17\u51FA\u9999\u6C57\u3002\u6848\u4E0A\u7684\u7EA2\u70DB\u71C3\u53BB\u4E00\u534A\uFF0C\u8089\u68D2\u624D\u5728\u5979\u809B\u5185\u5F8B\u52A8\u8D77\u6765\uFF0C\u5C06\u7CBE\u6DB2\u5C04\u8FDB\u5979\u76F4\u80A0\u6DF1\u5904\u3002</p><p>\u4E39\u5A18\u7B49\u9633\u5177\u5C04\u5B8C\u7CBE\uFF0C\u8F6F\u5316\u4E0B\u6765\uFF0C\u624D\u6536\u7D27\u5C41\u773C\u513F\uFF0C\u5C0F\u5FC3\u5730\u62AC\u8D77\u5C41\u80A1\uFF0C\u8F7B\u7B11\u9053\uFF1A\u300C\u5C04\u4E86\u597D\u591A\u2026\u2026\u300D</p><p>\u8BDD\u97F3\u672A\u843D\uFF0C\u697C\u4E0B\u5FFD\u7136\u54CD\u8D77\u6572\u95E8\u58F0\u3002</p><p>\u300C\u5A18\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u6B63\u5728\u56DE\u5473\u7740\u4E39\u5A18\u8089\u4F53\u7684\u5999\u5904\uFF0C\u542C\u5230\u58F0\u97F3\uFF0C\u8138\u8272\u731B\u7136\u4E00\u767D\u3002\u4E39\u5A18\u4E5F\u6123\u4F4F\u4E86\uFF0C\u7247\u523B\u540E\u624D\u624B\u5FD9\u811A\u4E71\u5730\u62AB\u4E0A\u5C0F\u8863\uFF0C\u4E00\u8FB9\u9053\uFF1A\u300C\u662F\u96EA\u83B2\u4E48\uFF1F\u300D</p><p>\u767D\u96EA\u83B2\u58F0\u97F3\u91CC\u5E26\u7740\u4E9B\u8BB8\u75B2\u60EB\uFF0C\u300C\u5A18\uFF0C\u662F\u6211\u3002\u300D</p><p>\u4E39\u5A18\u987E\u4E0D\u4E0A\u591A\u60F3\uFF0C\u5FD9\u8D77\u8EAB\u5F00\u95E8\uFF0C\u5306\u5306\u4E0B\u697C\u3002\u767D\u96EA\u83B2\u7684\u58F0\u97F3\u5E76\u4E0D\u5927\uFF0C\u843D\u5728\u5B59\u5929\u7FBD\u8033\u4E2D\u5374\u5982\u540C\u9739\u96F3\uFF0C\u4ED6\u5446\u5750\u5E8A\u5934\uFF0C\u5377\u5377\u820C\u5934\uFF0C\u60F3\u54BD\u4E0B\u4E9B\u4EC0\u4E48\uFF0C\u53E3\u4E2D\u5374\u5E72\u5F97\u53D1\u82E6\uFF0C\u8111\u4E2D\u4E71\u8F70\u8F70\u53EA\u6709\u4E00\u53E5\u8BDD\uFF1A\u767D\u96EA\u83B2\u8D8A\u72F1\u4E86\uFF01</p><p>\u697C\u4E0B\u4F20\u6765\u5F00\u95E8\u58F0\uFF0C\u63A5\u7740\u662F\u4E00\u58F0\u60CA\u53EB\uFF0C\u300C\u8C01\u7684\u8840\uFF1F\u96EA\u83B2\uFF0C\u662F\u4F60\u53D7\u4F24\u4E86\u5417\uFF1F\u300D</p><p>\u300C\u624B\u4E0A\u5212\u7834\u4E86\uFF0C\u6CA1\u4E8B\u7684\u3002\u300D\u767D\u96EA\u83B2\u7684\u53F3\u624B\u638C\u5FC3\u5212\u4E86\u957F\u957F\u4E00\u9053\u4F24\u53E3\uFF0C\u7528\u8863\u670D\u4E0A\u6495\u4E0B\u7684\u5E03\u6761\u968F\u4FBF\u88F9\u4F4F\uFF0C\u5979\u795E\u60C5\u75B2\u60EB\uFF0C\u773C\u795E\u4E2D\u5374\u5E26\u7740\u4E00\u4E1D\u4EA2\u594B\uFF0C\u300C\u82E5\u4E0D\u662F\u8FD9\u91CC\u70B9\u7740\u706F\uFF0C\u6211\u53EF\u80FD\u5C31\u8D70\u4E86\u53E6\u4E00\u6761\u8DEF\uFF0C\u5A18\uFF0C\u8FD9\u4E48\u665A\u8FD8\u6CA1\u7761\uFF1F\u300D</p><p>\u4E39\u5A18\u81EA\u7136\u65E0\u6CD5\u8BF4\u51FA\u5B9E\u60C5\uFF0C\u4E5F\u987E\u4E0D\u5F97\u8BF4\u4E86\uFF0C\u7D27\u5F20\u5730\u95EE\u9053\uFF1A\u300C\u96EA\u83B2\uFF0C\u4F60\u600E\u4E48\u51FA\u6765\u4E86\uFF1F\u300D</p><p>\u767D\u96EA\u83B2\u9053\uFF1A\u300C\u6211\u6740\u4E86\u4EBA\uFF0C\u8D8A\u72F1\u51FA\u6765\u7684\u3002\u300D</p><p>\u4E39\u5A18\u5999\u76EE\u5706\u7741\uFF0C\u5931\u58F0\u9053\uFF1A\u300C\u4EC0\u4E48\uFF1F\u300D</p><p>\u859B\u971C\u7075\u662F\u677F\u4E0A\u9489\u9489\u7684\u6B7B\u56DA\uFF0C\u53C8\u77E5\u60C5\u8BC6\u8DA3\u4E0D\u52A0\u53CD\u6297\uFF0C\u5B88\u5927\u7262\u7684\u72F1\u5352\u90FD\u6D8C\u53BB\u62FF\u5979\u5978\u6DEB\u53D6\u4E50\u3002\u767D\u96EA\u83B2\u6CA1\u8D39\u4EC0\u4E48\u529B\u6C14\u5C31\u51FA\u4E86\u7262\u623F\uFF0C\u5728\u9662\u4E2D\u5374\u610F\u5916\u649E\u4E0A\u4E86\u4E00\u540D\u72F1\u5352\u8FC7\u6765\u5DE1\u89C6\u3002\u767D\u96EA\u83B2\u6015\u4ED6\u558A\u53EB\u60CA\u52A8\u4F17\u4EBA\uFF0C\u62A2\u5148\u7528\u8155\u4E0A\u7684\u94C1\u94FE\u7F20\u4F4F\u4ED6\u7684\u8116\u9888\u3002</p><p>\u90A3\u72F1\u5352\u6B66\u529F\u9887\u4E3A\u602A\u5F02\uFF0C\u800C\u4E14\u51F6\u608D\u4E4B\u6781\uFF0C\u88AB\u767D\u96EA\u83B2\u9501\u4F4F\u8116\u9888\uFF0C\u8FD8\u62E8\u51FA\u4E00\u67C4\u5C3A\u8BB8\u957F\u7684\u7A84\u80CC\u77ED\u5200\uFF0C\u671D\u80A9\u540E\u5288\u53BB\u3002\u767D\u96EA\u83B2\u62C5\u5FC3\u7528\u94C1\u94FE\u683C\u5F00\u53D1\u51FA\u58F0\u54CD\uFF0C\u4E00\u54AC\u7259\uFF0C\u8D64\u624B\u6293\u4F4F\u5200\u5203\uFF0C\u7528\u529B\u4E00\u62E7\uFF0C\u593A\u4E0B\u77ED\u5200\uFF0C\u987A\u52BF\u53CD\u624B\u523A\u8FDB\u90A3\u4EBA\u9888\u4FA7\u3002</p><p>\u767D\u96EA\u83B2\u5728\u72F1\u91CC\u56DA\u4E86\u591A\u65F6\uFF0C\u5BF9\u81EA\u5DF1\u8EAB\u5728\u4F55\u5904\u4E00\u65E0\u6240\u77E5\u3002\u5979\u5C06\u5C38\u4F53\u4E22\u5230\u5899\u5916\u9690\u85CF\u8D77\u6765\uFF0C\u56DB\u5904\u5F20\u671B\u826F\u4E45\uFF0C\u7EC8\u4E8E\u770B\u5230\u6DF1\u5C71\u91CC\u4E00\u70B9\u9690\u7EA6\u7684\u706F\u706B\uFF0C\u624D\u5BFB\u8FF9\u800C\u6765\u3002</p><p>\u4E39\u5A18\u542C\u5230\u5973\u513F\u662F\u6740\u4EBA\u8D8A\u72F1\uFF0C\u5413\u5F97\u715E\u8272\u96EA\u767D\uFF0C\u8FDE\u5FD9\u63D2\u4E0A\u5927\u95E8\uFF0C\u5E26\u7740\u767D\u96EA\u83B2\u5230\u697C\u4E0A\u623F\u91CC\u3002</p><p>\u5230\u4E86\u95E8\u53E3\uFF0C\u5979\u624D\u60F3\u8D77\u623F\u91CC\u8FD8\u6709\u4E2A\u5B59\u5929\u7FBD\uFF0C\u4E0D\u7531\u63A9\u4F4F\u53E3\uFF0C\u60CA\u53EB\u9053\uFF1A\u300C\u54CE\u5440\uFF01\u300D</p><p>\u767D\u96EA\u83B2\u5728\u524D\u9762\u5DF2\u7ECF\u8FDB\u4E86\u623F\u95F4\uFF0C\u56DE\u5934\u9053\uFF1A\u300C\u600E\u4E48\u4E86\uFF1F\u300D</p><p>\u623F\u91CC\u7A7A\u65E0\u4E00\u4EBA\uFF0C\u4E39\u5A18\u6697\u6697\u677E\u4E86\u53E3\u6C14\uFF0C\u5979\u63A9\u9970\u7740\u7A98\u6001\uFF0C\u4E00\u9762\u653E\u597D\u706F\u70DB\uFF0C\u4E00\u9762\u9053\uFF1A\u300C\u4F60\u600E\u4E48\u9003\u4E86\u51FA\u6765\uFF1F\u8FD8\u6740\u4E86\u4EBA\uFF1F\u300D</p><p>\u4E00\u8F6C\u8EAB\uFF0C\u53EA\u89C1\u5973\u513F\u7ACB\u5728\u5E8A\u8FB9\uFF0C\u795E\u60C5\u602A\u5F02\u5730\u671B\u7740\u5979\u3002\u4E39\u5A18\u987A\u7740\u5979\u7684\u89C6\u7EBF\u770B\u53BB\uFF0C\u624D\u53D1\u73B0\u81EA\u5DF1\u5306\u5FD9\u95F4\u53EA\u7A7F\u4E86\u4EB5\u88E4\u5185\u8863\uFF0C\u809A\u515C\u4E0B\u660E\u663E\u9686\u8D77\u4E00\u4E2A\u5706\u9F13\u9F13\u7684\u5F62\u72B6\u3002\u4E39\u5A18\u8138\u987F\u65F6\u7EA2\u4E86\uFF0C\u5979\u626F\u7740\u809A\u515C\u906E\u63A9\u5C0F\u8179\uFF0C\u5583\u5583\u5730\u5F00\u4E0D\u4E86\u53E3\u3002</p><p>\u5728\u516C\u5802\u4E0A\uFF0C\u767D\u96EA\u83B2\u89C1\u8FC7\u5A18\u4EB2\uFF0C\u90A3\u65F6\u4E39\u5A18\u8863\u886B\u906E\u63A9\u5F97\u597D\uFF0C\u7ADF\u6CA1\u7559\u610F\u5230\u5A18\u8EAB\u4E0A\u7684\u5F02\u72B6\uFF0C\u5446\u4E86\u534A\u664C\uFF0C\u5979\u6709\u4E9B\u5403\u529B\u5730\u8BF4\u9053\uFF1A\u300C\u5A18\uFF0C\u4F60\u6000\u5B55\u4E86\u2026\u2026\u300D</p><p>\u4E39\u5A18\u7F9E\u6127\u5730\u626D\u8FC7\u8138\uFF0C\u6CA1\u6709\u4F5C\u58F0\u3002</p><p>\u300C\u8C01\u7684\uFF1F\u300D\u767D\u96EA\u83B2\u8FDF\u7591\u5730\u8BF4\u9053\uFF1A\u300C\u2026\u2026\u90A3\u4E2A\u59D3\u5B59\u7684\uFF1F\u300D</p><p>\u4E39\u5A18\u7528\u6C89\u9ED8\u627F\u8BA4\u4E86\u5979\u7684\u731C\u6D4B\u3002</p><p>\u767D\u96EA\u83B2\u7389\u8138\u4E00\u65F6\u6DA8\u5F97\u901A\u7EA2\uFF0C\u4E00\u4F1A\u513F\u53C8\u53D8\u5F97\u60E8\u767D\uFF0C\u5E26\u7740\u6068\u610F\u98A4\u58F0\u9053\uFF1A\u300C\u4ED6\u5728\u54EA\u513F\uFF1F\u300D</p><p>\u767D\u96EA\u83B2\u63E1\u7D27\u7EA4\u624B\uFF0C\u9C9C\u8840\u4E00\u6EF4\u6EF4\u6389\u5728\u5730\u4E0A\u3002\u5979\u8D8A\u72F1\u65F6\u5C31\u6709\u5FC3\u53BB\u6740\u6389\u5B59\u5929\u7FBD\uFF0C\u4F46\u4E00\u6765\u4E0D\u77E5\u9053\u5B59\u5929\u7FBD\u8EAB\u5728\u4F55\u5904\uFF0C\u4E8C\u6765\u62C5\u5FC3\u9047\u4E0A\u97E9\u5168\uFF0C\u6700\u540E\u51B3\u5B9A\u8FD8\u662F\u5148\u56DE\u5BB6\uFF0C\u7B49\u5B89\u7F6E\u4E86\u5A18\u4EB2\u548C\u59B9\u59B9\u518D\u6765\u62A5\u4EC7\u3002\u6CA1\u60F3\u5230\u7B2C\u4E00\u773C\u5C31\u770B\u5230\u5A18\u771F\u88AB\u90A3\u65E0\u803B\u4E4B\u5F92\u5F04\u5927\u4E86\u809A\u5B50\u3002</p><p>\u4E39\u5A18\u62C9\u4F4F\u5973\u513F\u7684\u624B\uFF0C\u4E5E\u6C42\u9053\uFF1A\u300C\u96EA\u83B2\uFF0C\u4F60\u522B\u751F\u6C14\u3002\u90FD\u662F\u5A18\u4E0D\u597D\u2026\u2026\u300D</p><p>\u767D\u96EA\u83B2\u773C\u6CEA\u4E00\u4E0B\u5B50\u6D8C\u4E86\u51FA\u6765\uFF0C\u300C\u5A18\uFF0C\u4F60\u75AF\u4E86\u5417\uFF1F\u5230\u8FD9\u65F6\u5019\u4F60\u8FD8\u62A4\u7740\u4ED6\uFF01\u4ED6\u5BB3\u6B7B\u4E86\u7239\u7239\uFF0C\u5BB3\u4E86\u4F60\uFF0C\u5BB3\u4E86\u7389\u83B2\uFF0C\u5BB3\u4E86\u6211\u4EEC\u5168\u5BB6\uFF01\u4F60\u77E5\u9053\u4ED6\u628A\u82F1\u83B2\u600E\u4E48\u4E86\u5417\uFF1F\u300D</p><p>\u4E39\u5A18\u50CF\u88AB\u4EBA\u731B\u7136\u62BD\u5E72\u9C9C\u8840\uFF0C\u300C\u82F1\u83B2\u600E\u4E48\u4E86\uFF01\u300D</p><p>\u767D\u96EA\u83B2\u54AC\u4E86\u54AC\u5634\u5507\uFF0C\u6068\u58F0\u9053\uFF1A\u300C\u4F60\u8FDF\u65E9\u4F1A\u77E5\u9053\u7684\u3002\u300D</p><p>\u7A7A\u6C14\u4E2D\u4ECD\u98D8\u6D6E\u7740\u6DEB\u7CDC\u7684\u6C14\u606F\uFF0C\u767D\u96EA\u83B2\u4ED4\u7EC6\u770B\u7740\u5A18\u4EB2\uFF0C\u4ECE\u5979\u614C\u5F20\u7684\u773C\u795E\uFF0C\u51CC\u4E71\u7684\u9B13\u53D1\uFF0C\u5230\u5979\u9686\u8D77\u7684\u5C0F\u8179\u2014\u2014\u4E39\u5A18\u817F\u95F4\u7684\u4EB5\u88E4\u6E7F\u4E86\u4E00\u7247\uFF0C\u90A3\u79CD\u6E7F\u9ECF\u7684\u75D5\u8FF9\uFF0C\u767D\u96EA\u83B2\u518D\u719F\u6089\u4E0D\u8FC7\u3002</p><p>\u4E39\u5A18\u4E5F\u610F\u8BC6\u5230\u80A1\u95F4\u7684\u9ECF\u610F\uFF0C\u521A\u624D\u6B22\u597D\u540E\u5979\u6765\u4E0D\u53CA\u6E05\u7406\uFF0C\u5C31\u5306\u5FD9\u8D77\u8EAB\uFF0C\u8FD9\u4F1A\u513F\u697C\u4E0A\u697C\u4E0B\u8D70\u4E86\u534A\u664C\uFF0C\u76F4\u80A0\u91CC\u704C\u6EE1\u7684\u7CBE\u6DB2\u6EA2\u4E86\u51FA\u6765\uFF0C\u4ECE\u5C41\u773C\u513F\u5230\u817F\u95F4\u9ECF\u4E4E\u4E4E\u4E00\u7247\uFF0C\u81C0\u6C9F\u91CC\u53C8\u6E7F\u53C8\u6ED1\uFF0C\u5047\u5982\u5973\u513F\u77E5\u9053\u81EA\u5DF1\u4EB5\u88E4\u7684\u5C41\u80A1\u6B63\u5F80\u4E0B\u6EF4\u7740\u7CBE\u6DB2\uFF0C\u4E0D\u77E5\u4F1A\u600E\u6837\u9119\u89C6\u5979\u8FD9\u4E2A\u4E0D\u77E5\u7F9E\u803B\u7684\u6BCD\u4EB2\u3002</p><p>\u767D\u96EA\u83B2\u7EC8\u4E8E\u79FB\u5F00\u76EE\u5149\uFF0C\u50CF\u662F\u4E0D\u518D\u8FFD\u95EE\uFF0C\u63A5\u7740\u5FFD\u7136\u7EB5\u8EAB\u800C\u8D77\uFF0C\u8E22\u5012\u4E86\u5E8A\u540E\u7684\u5C4F\u98CE\u3002</p><p>\u4E39\u5A18\u5931\u58F0\u53EB\u9053\uFF1A\u300C\u4E0D\u8981\uFF01\u300D</p><p>\u5C4F\u98CE\u5012\u5411\u4E00\u65C1\uFF0C\u540E\u9762\u7A7A\u8361\u8361\u6CA1\u6709\u4EBA\u5F71\u3002</p><p>\u767D\u96EA\u83B2\u4E0D\u613F\u63D0\u5B59\u5929\u7FBD\u7684\u540D\u5B57\uFF0C\u5389\u58F0\u9053\uFF1A\u300C\u4ED6\u5728\u54EA\u513F\uFF1F\u300D</p><p>\u4E39\u5A18\u4E5F\u4E0D\u77E5\u4ED6\u8EB2\u5230\u54EA\u91CC\uFF0C\u4F46\u82E5\u8BF4\u4E0D\u77E5\u9053\uFF0C\u5973\u513F\u52BF\u5FC5\u4E0D\u80AF\u7F62\u4F11\uFF0C\u53EA\u597D\u9053\uFF1A\u300C\u4ED6\u542C\u89C1\u58F0\u97F3\u5C31\u8D70\u4E86\u3002\u60F3\u662F\u56DE\u53BB\u4E86\u3002\u300D</p><p>\u767D\u96EA\u83B2\u51B7\u9759\u4E0B\u6765\u3002\u5B59\u5929\u7FBD\u82E5\u662F\u8FD4\u56DE\u76D1\u72F1\uFF0C\u81F3\u591A\u4E00\u4E2A\u65F6\u8FB0\uFF0C\u5C31\u4F1A\u5E26\u4E86\u4EBA\u6765\u3002\u5230\u65F6\u522B\u8BF4\u4E24\u4E2A\u7F20\u8DB3\u5973\u5B50\uFF0C\u8FDE\u5979\u4E5F\u4E0D\u6613\u8131\u8EAB\u3002</p><p>\u300C\u7389\u83B2\u5462\uFF1F\u6211\u5148\u628A\u5979\u9001\u8D70\u3002\u300D</p><p>\u300C\u7389\u83B2\u7761\u4E0B\u4E86\u3002\u4E3A\u4EC0\u4E48\u8981\u8D70\uFF1F\u6211\u542C\u5929\u2026\u2026\u300D\u4E39\u5A18\u6709\u4E9B\u614C\u5F20\u5730\u63A0\u4E86\u63A0\u9B13\u89D2\uFF0C\u300C\u4ED6\u8BF4\uFF0C\u54B1\u4EEC\u90FD\u6CA1\u6709\u6B7B\u7F6A\u3002\u300D</p><p>\u300C\u6D41\u4E09\u5343\u91CC\u4E48\uFF1F\u300D\u767D\u96EA\u83B2\u9053\uFF1A\u300C\u5A18\uFF0C\u4F60\u592A\u50BB\u4E86\uFF0C\u4ED6\u4EEC\u5FC5\u5B9A\u8981\u706D\u53E3\u7684\u3002\u6211\u4EE5\u524D\u5C31\u662F\u592A\u50BB\uFF0C\u4EE5\u4E3A\u5B98\u5E9C\u4F1A\u4E3A\u6C11\u4F5C\u4E3B\u3002\u7ED3\u679C\u600E\u4E48\u6837\uFF1F\u300D</p><p>\u5979\u95ED\u4E0A\u4E86\u773C\uFF0C\u60F3\u8D77\u81EA\u5DF1\u88AB\u8F6E\u5978\u6DEB\u8FB1\u7684\u65E5\u5B50\u3002\u56DE\u5BB6\u65F6\uFF0C\u5979\u8FD8\u662F\u4E2A\u610F\u6C14\u98CE\u53D1\u7684\u65B0\u664B\u6355\u5FEB\uFF0C\u73B0\u5728\u5374\u6210\u4E86\u8D8A\u72F1\u7684\u9006\u56DA\u3002\u5979\u7684\u6E05\u767D\u4E4B\u8EAF\uFF0C\u5C31\u846C\u9001\u5728\u8FD9\u4E0D\u89C1\u5929\u65E5\u7684\u9ED1\u72F1\u6DF1\u5904\u3002</p>',98);function t(n,_){return a}var o=e(r,[["render",t]]);export{o as default};
