import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const a={},r=p('<h1 id="_62-\u7F20\u8DB3" tabindex="-1"><a class="header-anchor" href="#_62-\u7F20\u8DB3" aria-hidden="true">#</a> 62 \u7F20\u8DB3</h1><p>\u5973\u5B69\u5750\u5728\u5E8A\u8FB9\uFF0C\u4E24\u811A\u5728\u70ED\u6C34\u91CC\u6CE1\u7740\u3002\u4E39\u5A18\u5C06\u4E00\u5E45\u767D\u5E03\u644A\u5728\u684C\u4E0A\uFF0C\u88C1\u6210\u4E00\u6761\u6761\u5BF8\u8BB8\u5BBD\u7684\u5E03\u6761\u3002</p><p>\u300C\u672C\u6765\u8BE5\u662F\u6D46\u8FC7\u7684\uFF0C\u7F20\u51FA\u6765\u624D\u597D\u770B\u3002\u300D\u4E39\u5A18\u5C06\u5E03\u6761\u4E00\u6761\u4E00\u6761\u642D\u5728\u684C\u65C1\uFF0C\u7136\u540E\u633D\u8D77\u8896\u5B50\uFF0C\u63C9\u6413\u7740\u5973\u5B69\u7684\u5C0F\u811A\u3002</p><p>\u300C\u5A18\uFF0C\u6211\u7684\u811A\u9EBB\u4E86\u3002\u300D\u5973\u5B69\u819D\u5F2F\u4E0B\u538B\u4E86\u5757\u6728\u677F\uFF0C\u957F\u65F6\u95F4\u538B\u8FEB\u4E0B\uFF0C\u4E24\u811A\u6E10\u6E10\u53D8\u5F97\u9EBB\u6728\u3002</p><p>\u300C\u518D\u591A\u538B\u4F1A\u513F\u3002\u300D</p><p>\u4E39\u5A18\u53C8\u6DFB\u4E86\u4E9B\u70ED\u6C34\uFF0C\u7136\u540E\u62FF\u8D77\u4E00\u5757\u5207\u5F00\u7684\u751F\u59DC\uFF0C\u5728\u5973\u5B69\u811A\u4E0A\u6765\u56DE\u64E6\u62ED\u3002</p><p>\u300C\u64E6\u8FD9\u4E2A\u5E72\u561B\uFF1F\u300D</p><p>\u4E39\u5A18\u67D4\u58F0\u9053\uFF1A\u300C\u64E6\u4E86\u59DC\uFF0C\u811A\u5C31\u8F6F\u5F97\u50CF\u5E74\u7CD5\u4E00\u6837\uFF0C\u60F3\u7F20\u6210\u4EC0\u4E48\u6837\u5C31\u5C31\u7F20\u6210\u4EC0\u4E48\u6837\u3002\u300D</p><p>\u90A3\u5E74\u7ED9\u7389\u83B2\u7F20\u8DB3\u7684\u65F6\u5019\uFF0C\u6BCD\u5973\u4FE9\u4E5F\u662F\u8FD9\u6837\u8BF4\u7740\u8BDD\u3002\u4F46\u90A3\u65F6\u5979\u5FC3\u5934\u662F\u559C\u4E50\u7684\u3002</p><p>\u5979\u4E0D\u4F1A\u60F3\u5230\uFF0C\u6709\u4E00\u5929\u5979\u8FD8\u8981\u7ED9\u81EA\u5DF1\u7684\u9AA8\u8089\u7F20\u8DB3\u3002</p><p>\u300C\u82F1\u83B2\u2026\u2026\u5A18\u5BF9\u4E0D\u8D77\u4F60\u2026\u2026\u300D\u4E39\u5A18\u54ED\u6CE3\u7740\uFF0C\u6CEA\u6C34\u4E00\u6EF4\u6EF4\u6389\u8FDB\u76C6\u91CC\u3002</p><p>\u83BA\u601C\u6B6A\u7740\u5934\u770B\u7740\u5979\u7684\u5927\u809A\u5B50\uFF0C\u300C\u5A18\uFF0C\u4F60\u809A\u5B50\u91CC\u662F\u4E2A\u4EC0\u4E48\u5A03\u5A03\uFF1F\u300D\u89C1\u5A18\u6CA1\u6709\u56DE\u7B54\uFF0C\u53C8\u95EE\uFF1A\u300C\u5B83\u6709\u7239\u7239\u5417\uFF1F\u300D</p><p>\u300C\u6211\u77E5\u9053\u4E86\uFF0C\u5A18\u4E5F\u4E0D\u77E5\u9053\u5B83\u7684\u7239\u7239\u662F\u54EA\u4E2A\u3002\u5BF9\u5417\uFF1F\u300D</p><p>\u300C\u82F1\u83B2\uFF0C\u4F60\u6068\u5A18\u4E48\uFF1F\u300D</p><p>\u83BA\u601C\u7B11\u563B\u563B\u8BF4\uFF1A\u300C\u516C\u516C\u8BF4\uFF0C\u7B49\u6211\u957F\u5927\uFF0C\u5C31\u5B66\u4F1A\u6740\u4EBA\u4E86\u3002\u6211\u60F3\u6740\u7684\u4EBA\u6709\u597D\u591A\u597D\u591A\uFF0C\u540E\u9762\u624D\u8F6E\u5230\u5A18\u5462\u3002\u300D</p><p>\u4E39\u5A18\u8F7B\u67D4\u5730\u64E6\u7740\u5979\u7684\u811A\u8BF4\uFF1A\u300C\u82F1\u83B2\u60F3\u600E\u4E48\u6740\u5A18\u5462\uFF1F\u300D</p><p>\u300C\u5A18\u7684\u8EAB\u5B50\u672C\u6765\u662F\u7239\u7239\u7684\uFF0C\u7239\u7239\u6B7B\u4E86\uFF0C\u5A18\u53C8\u7ED9\u4E86\u522B\u7684\u7537\u4EBA\u3002\u6211\u60F3\u628A\u5A18\u8EAB\u4E0A\u88AB\u522B\u4EBA\u7528\u8FC7\u7684\u5730\u65B9\u90FD\u5207\u4E0B\u6765\uFF0C\u8FD8\u7ED9\u7239\u7239\u3002\u300D</p><p>\u4E39\u5A18\u67D4\u58F0\u9053\uFF1A\u300C\u597D\u554A\u3002\u5A18\u5C31\u597D\u597D\u6D3B\u7740\uFF0C\u7B49\u7740\u82F1\u83B2\u6765\u6740\u3002\u5A18\u77E5\u9053\uFF0C\u4F60\u6253\u5C0F\u5C31\u6027\u786C\uFF0C\u8DDF\u4F60\u7239\u7239\u4E00\u6837\u3002\u53EA\u662F\u4F60\u6CA1\u6709\u4F60\u7239\u7239\u90A3\u6837\u5FC3\u5B9E\u3002\u8FD9\u597D\u8FD8\u662F\u4E0D\u597D\uFF0C\u5A18\u89C1\u8BC6\u6D45\uFF0C\u4E5F\u8BF4\u4E0D\u51C6\u3002\u4F60\u5E74\u7EAA\u5C0F\uFF0C\u5BF9\u4E8B\u60C5\u8FD8\u61F5\u61C2\uFF0C\u53EA\u51ED\u7740\u4E00\u53E3\u6C14\u505A\u4E86\uFF0C\u5F80\u540E\u53EF\u8981\u7559\u610F\uFF0C\u597D\u597D\u6D3B\u7740\u2026\u2026\u628A\u6728\u68CD\u54AC\u4E0A\uFF0C\u5FCD\u7740\u4E9B\u3002\u300D</p><p>\u4E39\u5A18\u4E00\u8FB9\u628A\u83BA\u601C\u7684\u811A\u64E6\u5E72\uFF0C\u4E00\u8FB9\u9053\uFF1A\u300C\u7F20\u4E86\u811A\uFF0C\u5934\u4E24\u4E2A\u6708\u6700\u8981\u7D27\uFF0C\u6BCF\u6B21\u90FD\u8981\u7F20\u7D27\u624D\u4E0D\u4F1A\u8D70\u6837\u3002\u5A18\u8DDF\u4E0D\u4E86\u4F60\u90A3\u4E48\u4E45\uFF0C\u4F60\u8981\u8BB0\u6E05\uFF0C\u5F80\u540E\u5C31\u5F97\u81EA\u5DF1\u7F20\u4E86\u3002\u300D</p><p>\u4E39\u5A18\u5C06\u56DB\u6839\u5C0F\u5DE7\u767D\u5AE9\u7684\u811A\u8DBE\u63E1\u5728\u4E86\u624B\u91CC\uFF0C\u300C\u7389\u83B2\u811A\u8F6F\uFF0C\u516D\u5C81\u624D\u88F9\uFF0C\u82F1\u83B2\u811A\u4E5F\u8F6F\uFF0C\u4F46\u5E74\u7EAA\u53C8\u5927\u4E86\u4E00\u5C81\uFF0C\u514D\u4E0D\u4E86\u8981\u5403\u82E6\u2026\u2026\u300D</p><p>\u4E39\u5A18\u624B\u5F80\u4E0B\u4E00\u62D7\uFF0C\u90A3\u811A\u9AA8\u5934\u679C\u7136\u8FD8\u662F\u8F6F\u7684\uFF0C\u8DBE\u9AA8\u51E0\u58F0\u8106\u54CD\uFF0C\u56DB\u6839\u811A\u8DBE\u4FBF\u9F50\u9F50\u62D7\u4E0B\uFF0C\u8D34\u5728\u811A\u638C\u4E0B\uFF0C\u552F\u6709\u5206\u5F00\u7684\u62C7\u8DBE\u4ECD\u7FD8\u7740\u3002</p><p>\u83BA\u601C\u7684\u811A\u4E5F\u9EBB\u4E86\uFF0C\u4E00\u65F6\u6CA1\u89C9\u51FA\u75DB\u6765\uFF0C\u53EA\u89C9\u5F97\u811A\u4E0A\u9635\u9635\u53D1\u80C0\u3002\u4E39\u5A18\u626F\u4E0B\u4E00\u6839\u5E03\u6761\uFF0C\u8D34\u7740\u62C7\u8DBE\u7ED5\u5230\u811A\u5FC3\uFF0C\u5C06\u5F2F\u6298\u7684\u56DB\u6839\u811A\u8DBE\u7D27\u7D27\u7F20\u4F4F\uFF0C\u4E00\u76F4\u5230\u5E03\u6761\u7F20\u5B8C\uFF0C\u53C8\u53D6\u4E86\u4E00\u6839\uFF0C\u6253\u6A2A\u7F20\u4E86\u4E24\u9053\u3002\u62D7\u65AD\u811A\u8DBE\u8FD8\u4E0D\u7B97\u75DB\uFF0C\u6700\u75DB\u7684\u662F\u5C06\u5C0F\u8DBE\u76F8\u8FDE\u7684\u811A\u9AA8\u62D7\u65AD\uFF0C\u8FD9\u6837\u7F20\u51FA\u7684\u811A\u624D\u5C16\u5C16\u7684\u5C0F\u5DE7\u7EC6\u7FD8\u3002</p><p>\u83BA\u601C\u8FD9\u4F1A\u513F\u4E5F\u89C9\u51FA\u811A\u4E0A\u949D\u949D\u7684\u75DB\u610F\uFF0C\u7B49\u5A18\u4E00\u624B\u6309\u4F4F\u4E86\u811A\u80CC\u5916\u4FA7\uFF0C\u4E00\u624B\u62C9\u7D27\u5E03\u6761\uFF0C\u7528\u529B\u4E00\u7D27\uFF0C\u5979\u6E05\u695A\u542C\u5230\u9AA8\u5934\u6298\u65AD\u7684\u8106\u54CD\u3002\u83BA\u601C\u8EAB\u4E0A\u77AC\u65F6\u5192\u51FA\u4E00\u5C42\u70ED\u6C57\uFF0C\u7259\u9F7F\u4E0D\u7531\u81EA\u4E3B\u5730\u54AC\u4F4F\u6728\u68CD\u3002</p><p>\u4E39\u5A18\u5728\u62D7\u65AD\u811A\u9AA8\u7684\u540C\u65F6\uFF0C\u5E03\u6761\u4E5F\u675F\u7D27\u4E86\u3002\u5979\u7528\u7684\u662F\u83B2\u72B6\u7684\u7F20\u6CD5\uFF0C\u811A\u80CC\u5F2F\u6210\u5F13\u5F62\uFF0C\u811A\u5FC3\u4E2D\u7A7A\uFF0C\u62C7\u8DBE\u4E0A\u7FD8\uFF0C\u672C\u6765\u5C31\u5C0F\u7684\u811A\u638C\u987F\u65F6\u53C8\u5C0F\u4E86\u4E00\u534A\uFF0C\u5F62\u6210\u4E00\u6735\u5C16\u5C16\u7684\u83B2\u74E3\u5F62\u72B6\u3002</p><p>\u4E39\u5A18\u6765\u4E0D\u53CA\u518D\u505A\u65B0\u978B\uFF0C\u7B49\u811A\u5E26\u7F20\u5B8C\u7ED1\u597D\uFF0C\u5C31\u62FF\u4E86\u7389\u83B2\u7559\u5728\u8FD9\u513F\u7684\u4E00\u53EA\u65E7\u978B\uFF0C\u7ED9\u82F1\u83B2\u7A7F\u4E0A\u3002\u8D81\u7740\u75DB\u82E6\u8FD8\u6CA1\u4F20\u5F00\uFF0C\u4E39\u5A18\u62FF\u8D77\u53E6\u4E00\u53EA\u811A\uFF0C\u4F9D\u6837\u7F20\u4F4F\u3002</p><p>\u7B2C\u4E8C\u6839\u811A\u9AA8\u65AD\u6298\u65F6\uFF0C\u83BA\u601C\u5DF2\u7ECF\u75DB\u5F7B\u5FC3\u80BA\uFF0C\u5979\u628A\u6728\u68CD\u54AC\u5F97\u683C\u683C\u4F5C\u54CD\uFF0C\u4E24\u624B\u62DA\u547D\u62FD\u4F4F\u8863\u895F\u3002\u7B49\u4E39\u5A18\u7F20\u5B8C\uFF0C\u5979\u628A\u4E24\u811A\u63D0\u5728\u534A\u7A7A\uFF0C\u4E00\u70B9\u529B\u4E5F\u4E0D\u6562\u4F7F\uFF0C\u54C6\u55E6\u7740\u4E00\u53E3\u4E00\u53E3\u62BD\u7740\u6C14\u3002\u7531\u4E8E\u811A\u80CC\u5F13\u7740\uFF0C\u90A3\u811A\u770B\u4E0A\u53BB\u53EA\u6709\u4E09\u5BF8\u5927\u5C0F\uFF0C\u7EA4\u5DE7\u73B2\u73D1\uFF0C\u767D\u5E03\u88F9\u5F97\u6574\u6574\u9F50\u9F50\uFF0C\u6BD4\u4E39\u5A18\u7684\u811A\u4F3C\u4E4E\u8FD8\u5C0F\u4E9B\u3002</p><p>\u65E2\u7136\u7F20\u4E86\u5C31\u5F97\u7F20\u597D\uFF0C\u4E39\u5A18\u6276\u7740\u5979\u8D77\u6765\uFF0C\u8BA9\u5979\u7AD9\u4F4F\u3002\u83BA\u601C\u53CC\u811A\u4F3C\u4E4E\u5DF2\u7ECF\u4E0D\u662F\u81EA\u5DF1\u7684\u4E86\uFF0C\u5F80\u5730\u4E0A\u4E00\u7AD9\uFF0C\u8EAB\u4F53\u987F\u65F6\u524D\u503E\u540E\u4EF0\uFF0C\u63A5\u7740\u5C31\u4E00\u5C41\u80A1\u5750\u4E86\u4E0B\u53BB\u3002\u811A\u4E0A\u4F20\u6765\u5200\u5207\u822C\u7684\u75DB\u610F\uFF0C\u6298\u65AD\u7684\u9AA8\u5934\u6233\u5728\u8089\u91CC\uFF0C\u75DB\u5F97\u5979\u9762\u5B54\u96EA\u767D\uFF0C\u6C57\u73E0\u4E00\u9897\u4E00\u9897\u76F4\u5F80\u4E0B\u6389\u3002</p><p>\u4E39\u5A18\u80F8\u53E3\u50CF\u585E\u4E86\u56E2\u68C9\u7D6E\u822C\u5835\u5F97\u96BE\u53D7\uFF0C\u5589\u5934\u9635\u9635\u6076\u5FC3\u3002\u52C9\u5F3A\u53C8\u628A\u82F1\u83B2\u62C9\u8D77\u6765\uFF0C\u8BA9\u5979\u518D\u8D70\uFF0C\u5FFD\u7136\u773C\u524D\u4E00\u6655\u3002\u4E24\u624B\u6367\u7740\u809A\u5B50\uFF0C\u95ED\u7740\u773C\u5455\u5410\u8D77\u6765\u3002</p><p>\u4E39\u5A18\u5410\u51FA\u7684\u9664\u4E86\u6E05\u6C34\uFF0C\u5C31\u662F\u4E00\u4E9B\u767D\u767D\u7684\u7CBE\u6DB2\u6CAB\u5B50\u3002\u5B59\u5929\u7FBD\u542C\u89C1\u58F0\u97F3\uFF0C\u8FC7\u6765\u628A\u5979\u62B1\u5230\u81EA\u5DF1\u623F\u91CC\uFF0C\u653E\u5728\u5E8A\u4E0A\uFF0C\u53D6\u8FC7\u88AB\u8925\u76D6\u4E86\uFF0C\u53C8\u5F80\u88AB\u91CC\u585E\u4E86\u4E24\u53EA\u5E8A\u4E0A\u7528\u7684\u6696\u7089\uFF0C\u6C89\u7740\u8138\u5750\u5728\u4E00\u65C1\u3002</p><p>\u9694\u58C1\u90A3\u4E2A\u5973\u88C5\u6253\u626E\u7684\u5B69\u5B50\u534A\u8DB4\u534A\u8DEA\u5730\u4F0F\u5728\u5730\u4E0A\uFF0C\u4E24\u53EA\u811A\u659C\u7740\u62D6\u5728\u8EAB\u540E\uFF0C\u4E0D\u6562\u6CBE\u5730\uFF0C\u8138\u8272\u60E8\u767D\uFF0C\u50CF\u5C0F\u72D7\u4E00\u6837\u545C\u545C\u75DB\u53EB\u7740\uFF0C\u6D51\u8EAB\u4E0D\u505C\u98A4\u6296\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6B21\u65E5\u662F\u4E2A\u5927\u96FE\u5929\u6C14\uFF0C\u6D53\u6D53\u7684\u767D\u96FE\u7F69\u5728\u5C71\u6797\u95F4\uFF0C\u9694\u5F00\u51E0\u6B65\u5C31\u770B\u4E0D\u6E05\u4EBA\u5F71\u3002\u97E9\u5168\u4E00\u53E3\u4E00\u53E3\u559D\u7740\u6D53\u8336\uFF0C\u7136\u540E\u638F\u51FA\u5E15\u5B50\u62B9\u4E86\u62B9\u8138\uFF0C\u8212\u5C55\u4E86\u7B4B\u9AA8\uFF0C\u683C\u683C\u4E00\u7B11\uFF0C\u300C\u54E5\u54E5\uFF0C\u4F60\u5FC3\u80A0\u8FD8\u4E0D\u591F\u786C\u5462\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u6DE1\u6DE1\u9053\uFF1A\u300C\u8BA9\u5144\u5F1F\u89C1\u7B11\u4E86\u3002\u300D</p><p>\u300C\u5C0F\u5F1F\u600E\u4E48\u6562\u7B11\u54E5\u54E5\uFF1F\u54E5\u54E5\u591A\u534A\u60F3\u7740\u5C0F\u5F1F\u662F\u4E2A\u8FDE\u9E21\u5DF4\u90FD\u6CA1\u6709\u7684\u9609\u4EBA\uFF0C\u4E0D\u61C2\u5F97\u7537\u5973\u4E4B\u60C5\u3002\u300D\u97E9\u5168\u9760\u5728\u6905\u4E0A\uFF0C\u60A0\u7136\u9053\uFF1A\u300C\u5B59\u5144\u53EF\u80FD\u4E0D\u77E5\u9053\uFF0C\u5BAB\u91CC\u4E5F\u662F\u6709\u83DC\u6237\u7684\u3002</p><p>\u83AB\u770B\u662F\u592A\u76D1\u5BAB\u5973\uFF0C\u6709\u4E9B\u6BD4\u5E73\u5E38\u592B\u59BB\u8FD8\u4EB2\u5BC6\u7740\u4E9B\u3002\u4EAC\u5E08\u7684\u516B\u5B9D\u5C71\u662F\u592A\u76D1\u4EEC\u51D1\u94B1\u4E70\u4E0B\u7684\u575F\u5730\uFF0C\u90A3\u91CC\u6709\u95F4\u5927\u5C4B\uFF0C\u4F9B\u7684\u90FD\u662F\u83DC\u6237\u8DDF\u5BF9\u98DF\u7684\u724C\u4F4D\u3002\u4E00\u5E74\u5230\u5934\u9999\u706B\u4E0D\u65AD\u3002\u300D</p><p>\u300C\u5144\u5F1F\u8BF4\u8FD9\u4E9B\uFF0C\u662F\u60F3\u8BF4\u592A\u76D1\u4E5F\u6709\u4E03\u60C5\u516D\u6B32\u3002\u6211\u8FD9\u4F1A\u513F\u9760\u7740\uFF0C\u5C31\u6BD4\u76F4\u8170\u5750\u7740\u8212\u670D\u2014\u2014\u300D\u97E9\u5168\u633A\u8EAB\u5750\u4E86\u8D77\u6765\uFF0C\u8170\u80CC\u633A\u5F97\u7B14\u76F4\uFF0C\u7CBE\u6C14\u51DD\u7136\uFF0C\u300C\u54E5\u54E5\u4E5F\u89C1\u8FC7\u5C01\u516C\u516C\uFF0C\u4F60\u89C1\u4ED6\u4EC0\u4E48\u65F6\u5019\u677E\u8FC7\uFF1F\u516C\u516C\u8BB2\u7684\u5C31\u662F\u8FD9\u5FCD\u5B57\u8BC0\uFF0C\u80FD\u5FCD\u4E0B\u5FC3\u6765\u3002\u300D</p><p>\u300C\u516C\u516C\u8981\u56DE\u4EAC\u5E08\uFF0C\u5DF2\u7ECF\u6765\u4E86\u4FE1\uFF0C\u8BA9\u6211\u968F\u884C\u3002\u770B\u516C\u516C\u8BDD\u91CC\u7684\u610F\u601D\uFF0C\u5FEB\u5219\u534A\u5E74\uFF0C\u591A\u5219\u4E00\u5E74\uFF0C\u54E5\u54E5\u5C06\u8FD9\u91CC\u5B89\u987F\u505C\u5F53\uFF0C\u4E5F\u8981\u53BB\u4EAC\u5E08\u4EFB\u804C\u3002\u4EAC\u4E2D\u4EBA\u4E8B\u6700\u662F\u8BE1\u8C32\uFF0C\u54E5\u54E5\u82E5\u4E0D\u80FD\u5FCD\u5FC3\uFF0C\u6B64\u884C\u53EF\u662F\u9669\u4E4B\u53C8\u9669\u3002\u300D</p><p>\u97E9\u5168\u770B\u4E86\u770B\u5929\u8272\uFF0C\u300C\u8FD9\u96FE\u4E00\u6563\uFF0C\u5C0F\u5F1F\u5C31\u8981\u544A\u8F9E\u3002\u4E34\u884C\u524D\u8FD8\u6709\u4E00\u8A00\uFF0C\u54E5\u54E5\u4E0D\u59A8\u542C\u4E86\u2014\u2014\u90A3\u4E24\u6BCD\u5973\u809A\u91CC\u4E0D\u7BA1\u662F\u4E0D\u662F\u54E5\u54E5\u7684\u9AA8\u8089\uFF0C\u6700\u597D\u522B\u7559\u3002\u5C0F\u5F1F\u8A00\u5C3D\u4E8E\u6B64\uFF0C\u8BF7\u54E5\u54E5\u4E09\u601D\u3002\u300D</p><p>\u4E00\u76F4\u9ED8\u4E0D\u4F5C\u58F0\u7684\u5B59\u5929\u7FBD\u62CD\u4E86\u62CD\u624B\uFF0C\u8BA9\u4EBA\u628A\u4E39\u5A18\u6BCD\u5B50\u53EB\u6765\u3002</p><p>\u83BA\u601C\u4E00\u591C\u6CA1\u7761\uFF0C\u5979\u4E24\u811A\u75DB\u5F97\u4E0D\u6562\u78B0\uFF0C\u652F\u7740\u817F\u60AC\u4E86\u4E00\u591C\uFF0C\u65AD\u9AA8\u5904\u80BF\u8D77\u6765\uFF0C\u53C8\u88AB\u811A\u5E26\u7D27\u675F\u7740\uFF0C\u75DB\u5F97\u94BB\u5FC3\u3002\u8FD9\u4F1A\u513F\u6709\u4EBA\u6765\u53EB\uFF0C\u5979\u53EA\u597D\u7528\u624B\u819D\u6491\u7740\u8EAB\u4F53\uFF0C\u50CF\u8FD8\u4E0D\u4F1A\u8D70\u8DEF\u7684\u5A74\u513F\u4E00\u6837\uFF0C\u4E00\u70B9\u4E00\u70B9\u722C\u5230\u5385\u5185\u3002</p><p>\u5979\u722C\u5F97\u5F88\u6162\uFF0C\u56E0\u4E3A\u4E24\u811A\u4E0D\u6562\u7740\u5730\uFF0C\u53EA\u80FD\u5411\u4E0A\u7FD8\u7740\uFF0C\u90A3\u53CC\u7389\u83B2\u7A7F\u8FC7\u7684\u7EE3\u82B1\u978B\uFF0C\u50CF\u7EA4\u8F6F\u7684\u6708\u7259\u4E00\u6837\u5F2F\u5F2F\u52FE\u8D77\uFF0C\u53C8\u50CF\u4E00\u5BF9\u7EA2\u8273\u7684\u83B2\u74E3\uFF0C\u5F2F\u7FD8\u7740\u5728\u8EAB\u540E\u6447\u52A8\u3002</p><p>\u97E9\u5168\u62FF\u8D77\u83BA\u601C\u7684\u811A\uFF0C\u7FFB\u6765\u8986\u53BB\u770B\u7740\uFF0C\u8FDE\u8FDE\u9053\uFF1A\u300C\u88F9\u5F97\u597D\uFF0C\u88F9\u5F97\u597D\u3002\u597D\u4E00\u53CC\u5A07\u4FCF\u7684\u5C0F\u811A\uFF0C\u771F\u8DDF\u83B2\u74E3\u4E00\u6837\u3002\u300D</p><p>\u89C1\u5230\u4E39\u5A18\u51FA\u6765\uFF0C\u83BA\u601C\u5C31\u54ED\u9053\uFF1A\u300C\u5A18\uFF0C\u6211\u7684\u811A\u597D\u75DB\u3002\u300D</p><p>\u300C\u5934\u4E24\u4E2A\u6708\u90FD\u8981\u75DB\u7684\uFF0C\u5F80\u540E\u5C31\u597D\u4E86\u3002\u300D\u4E39\u5A18\u529D\u6170\u7740\u8131\u4E0B\u5979\u7684\u978B\u5B50\uFF0C\u5C06\u7565\u677E\u7684\u811A\u5E26\u91CD\u65B0\u7CFB\u7D27\uFF0C\u7136\u540E\u7528\u9488\u7EBF\u7EC6\u7EC6\u7F1D\u4F4F\uFF0C\u300C\u7B49\u9AA8\u5934\u957F\u4F4F\u4E86\u518D\u89E3\u5F00\u3002\u5E73\u65F6\u8981\u591A\u8D70\u624D\u957F\u5F97\u6B63\uFF0C\u4E0D\u7136\u957F\u597D\u5C31\u8D70\u4E0D\u5F97\u8DEF\u4E86\u3002\u300D</p><p>\u4E39\u5A18\u8BF4\u7740\u6276\u4E86\u83BA\u601C\u8D77\u6765\uFF0C\u83BA\u601C\u6276\u7740\u5899\uFF0C\u7EA4\u5DE7\u7684\u5C0F\u811A\u4E00\u7528\u529B\uFF0C\u7ACB\u65F6\u75DB\u5F97\u6D51\u8EAB\u6253\u98A4\uFF0C\u6B7B\u6B7B\u54AC\u4F4F\u4E0B\u5507\uFF0C\u624D\u6CA1\u53EB\u51FA\u58F0\u6765\u3002</p><p>\u826F\u4E45\uFF0C\u5979\u8BD5\u7740\u8FC8\u51FA\u7B2C\u4E00\u6B65\u3002\u5C0F\u5DE7\u7684\u811A\u638C\u843D\u5728\u5730\u4E0A\uFF0C\u5F77\u4F5B\u4E00\u74E3\u8F7B\u67D4\u7684\u767D\u83B2\uFF0C\u4F46\u56DB\u6839\u62D7\u6298\u7684\u811A\u8DBE\u8E29\u5728\u811A\u5E95\uFF0C\u811A\u5185\u6298\u65AD\u7684\u9AA8\u5934\u5374\u540C\u65F6\u523A\u8FDB\u8089\u91CC\uFF0C\u75DB\u5F97\u94BB\u5FC3\u3002\u83BA\u601C\u54AC\u4F4F\u5507\u89D2\u7684\u5C0F\u75E3\uFF0C\u52C9\u5F3A\u7AD9\u7740\u3002\u8981\u4E0D\u4E86\u592A\u4E45\uFF0C\u8FD9\u53CC\u811A\u5C31\u4F1A\u91CD\u65B0\u957F\u597D\uFF0C\u53D8\u6210\u5D2D\u65B0\u7684\u7F8E\u4E3D\u52A8\u4EBA\u7684\u5F62\u72B6\uFF0C\u7528\u6765\u652F\u6491\u5979\u7684\u8EAB\u4F53\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5929\u6C14\u8D8A\u6765\u8D8A\u51B7\uFF0C\u814A\u6708\u91CC\uFF0C\u795E\u4ED9\u5CAD\u4E0B\u4E86\u7B2C\u4E00\u573A\u96EA\u3002\u96EA\u4E0B\u5F97\u5E76\u4E0D\u5927\uFF0C\u53EA\u662F\u623F\u9876\u6811\u4E0A\u767D\u4E86\u4E00\u5C42\uFF0C\u9662\u91CC\u7684\u4E0D\u4E45\u4FBF\u5316\u4E86\u3002</p><p>\u4E39\u5A18\u5DF2\u7ECF\u4E34\u76C6\u5F85\u4EA7\uFF0C\u5B59\u5929\u7FBD\u547D\u4EBA\u6536\u62FE\u4E86\u4E00\u95F4\u5E72\u51C0\u7684\u7262\u623F\uFF0C\u5C4B\u91CC\u751F\u4E86\u70AD\u706B\uFF0C\u4F46\u7262\u91CC\u4ECD\u7136\u6CA1\u6709\u8BBE\u5E8A\uFF0C\u53EA\u5728\u5899\u89D2\u94FA\u4E86\u5C42\u7A3B\u8349\u3002\u4E39\u5A18\u5C31\u8EBA\u5728\u8349\u5806\u91CC\uFF0C\u6276\u7740\u6447\u6447\u6B32\u5760\u7684\u5927\u809A\u5B50\uFF0C\u7B49\u5F85\u7740\u4EA7\u671F\u7684\u4E34\u8FD1\u3002</p><p>\u5979\u7A7F\u4E86\u4E0A\u8863\uFF0C\u5374\u6CA1\u6709\u7A7F\u88E4\u5B50\uFF0C\u8EAB\u4E0A\u53EA\u76D6\u4E86\u6761\u8584\u8584\u7684\u5E03\u5355\u3002\u4E0D\u65F6\u6709\u72F1\u6C49\u8FDB\u6765\uFF0C\u6380\u5F00\u5E03\u5355\uFF0C\u8BA9\u5979\u5F20\u5F00\u817F\uFF0C\u626A\u5F04\u5979\u7684\u4EA7\u95E8\u3002\u72F1\u91CC\u65E5\u5B50\u65E0\u804A\uFF0C\u72F1\u6C49\u62FF\u5979\u809A\u5B50\u91CC\u7684\u80CE\u513F\u6253\u8D4C\uFF0C\u731C\u662F\u7537\u662F\u5973\uFF0C\u8FDE\u5B59\u72F1\u6B63\u4E5F\u51D1\u8DA3\u8D4C\u4E86\u4E00\u4EFD\u3002\u5012\u6709\u516B\u6210\u4EBA\u8D4C\u5979\u6000\u7684\u662F\u4E2A\u5973\u513F\uFF0C\u6000\u7740\u80CE\u5C31\u88AB\u5978\u4E86\u8FD9\u4E48\u591A\u6B21\uFF0C\u4E00\u751F\u4E0B\u6765\uFF0C\u6307\u5B9A\u5C31\u662F\u4E2A\u6DEB\u6750\u513F\u3002</p><p>\u90A3\u72F1\u6C49\u7B11\u9A82\u9053\uFF1A\u300C\u773C\u770B\u5230\u4E86\u6708\u4EFD\uFF0C\u8FD8\u5939\u8FD9\u4E48\u7D27\u3002\u544A\u8BC9\u4F60\uFF0C\u6211\u53EF\u662F\u62BC\u4E86\u4E94\u4E24\u94F6\u5B50\u7684\u5C0F\u5A4A\u5B50\uFF0C\u4F60\u8981\u6562\u751F\u4E2A\u5C0F\u5154\u5D3D\u5B50\uFF0C\u6211\u975E\u628A\u4ED6\u585E\u56DE\u53BB\uFF0C\u8BA9\u4F60\u518D\u751F\u4E00\u4E2A\uFF01\u300D</p><p>\u4E39\u5A18\u88F8\u7740\u4E0B\u4F53\u4EFB\u4ED6\u62E8\u5F04\uFF0C\u4FA7\u8FC7\u8138\u4E0D\u8A00\u8BED\u3002\u72F1\u6C49\u60BB\u60BB\u7136\u6536\u56DE\u624B\uFF0C\u51FA\u4E86\u7262\u95E8\u3002</p><p>\u8FC7\u4E86\u4F1A\u513F\uFF0C\u7262\u95E8\u54CD\u4E86\u4E00\u58F0\uFF0C\u5B59\u5929\u7FBD\u8FC8\u6B65\u8FDB\u6765\u3002</p><p>\u300C\u6848\u5B50\u5224\u4E86\u4E0B\u6765\u3002\u300D</p><p>\u300C\u65A9\u7F6A\u4E48\uFF1F\u300D\u4E39\u5A18\u4F3C\u4E4E\u6D51\u8EAB\u7684\u7CBE\u8840\u90FD\u7ED9\u4E86\u8179\u4E2D\u7684\u80CE\u513F\uFF0C\u90A3\u5F20\u7C89\u8138\u767D\u5F97\u51E0\u4E4E\u900F\u660E\u3002</p><p>\u300C\u4E0D\u662F\u3002\u300D</p><p>\u300C\u90A3\u662F\u6D41\u653E\uFF1F\u300D</p><p>\u300C\u4E5F\u4E0D\u662F\u3002\u300D</p><p>\u4E39\u5A18\u7591\u60D1\u5730\u62AC\u8D77\u53CC\u76EE\u3002</p><p>\u5B59\u5929\u7FBD\u5401\u4E86\u53E3\u6C14\uFF0C\u7F13\u7F13\u9053\uFF1A\u300C\u4F60\u4EEC\u6309\u9006\u532A\u7737\u5C5E\u5904\u7F6E\uFF0C\u4E00\u5F8B\u8FD9\u5B98\u5356\u4E3A\u5993\uFF0C\u9047\u8D66\u4E0D\u8D66\uFF0C\u4E0D\u8BB8\u8D4E\u4E70\u3002\u300D</p><p>\u5C01\u603B\u7BA1\u8FD4\u4EAC\u540E\uFF0C\u4E0D\u4EC5\u642C\u52A8\u5BAB\u5185\u52BF\u529B\uFF0C\u5750\u5B9A\u4E86\u767D\u5B5D\u5112\u8C0B\u9006\u7684\u7F6A\u540D\uFF0C\u5E76\u4E14\u9762\u89C1\u4F55\u6E05\u6CB3\u5546\u69B7\u6848\u60C5\u3002\u4F9D\u7740\u4ED6\u7684\u610F\u601D\uFF0C\u53CD\u6B63\u767D\u5B5D\u5112\u5DF2\u6B7B\uFF0C\u7FFB\u4E0D\u7FFB\u6848\u4E5F\u6D3B\u4E0D\u8FC7\u6765\u3002\u6BD5\u7ADF\u662F\u90B8\u62A5\u660E\u53D1\u7684\u6848\u5B50\uFF0C\u4E3A\u7740\u671D\u5EF7\u8138\u9762\uFF0C\u7D22\u6027\u51A4\u6789\u4E86\u6B7B\u4EBA\u3002\u6D89\u6848\u7684\u72F1\u5352\u4E00\u53E3\u6C14\u6740\u4E86\u5341\u51E0\u4E2A\uFF0C\u4E5F\u80FD\u4EA4\u5F85\u8FC7\u53BB\u3002</p><p>\u81F3\u4E8E\u4E39\u5A18\u60F9\u5F97\u4F55\u6E05\u6CB3\u6C14\u607C\uFF0C\u4E0D\u59A8\u987A\u6C34\u63A8\u821F\u5224\u4E39\u5A18\u4E2A\u65A9\u7F6A\uFF0C\u5356\u4F55\u6E05\u6CB3\u4E00\u4E2A\u4EBA\u60C5\uFF1B\u859B\u971C\u7075\u662F\u9006\u532A\uFF0C\u5250\u7F6A\u662F\u9003\u4E0D\u4E86\u7684\uFF1B\u767D\u96EA\u83B2\u8EAB\u4E3A\u516C\u95E8\u4E2D\u4EBA\uFF0C\u7406\u5F53\u65A9\u9996\uFF0C\u4E3A\u7740\u5211\u90E8\u7684\u4F53\u9762\uFF0C\u53EF\u79FB\u4EA4\u5211\u90E8\u5904\u7F6E\uFF0C\u4E0D\u8FC7\u5C01\u603B\u7BA1\u7279\u610F\u8BB2\u660E\uFF0C\u767D\u96EA\u83B2\u5728\u7262\u91CC\u53D7\u4E86\u98CE\u5BD2\uFF0C\u53EA\u6015\u5230\u4E0D\u4E86\u4EAC\u5E08\u3002\u5176\u5B83\u767D\u7389\u83B2\u3001\u88F4\u9752\u7389\u7B49\u72AF\uFF0C\u5224\u4E3A\u6D41\u5211\u4ECE\u8F7B\u53D1\u843D\u3002</p><p>\u6CA1\u60F3\u5230\u4F55\u6E05\u6CB3\u4E1D\u6BEB\u4E0D\u627F\u4ED6\u8FD9\u4EFD\u60C5\uFF0C\u677F\u7740\u8138\u9053\uFF1A\u6848\u5B50\u65E2\u7136\u5DF2\u7ECF\u7531\u5185\u5EF7\u5B9A\u4E86\uFF0C\u5750\u5B9E\u4E86\u767D\u5B5D\u5112\u8C0B\u9006\uFF0C\u90A3\u4ED6\u53EA\u80FD\u4F9D\u5F8B\u884C\u4E8B\u3002\u767D\u5B5D\u5112\u8EAB\u4E3A\u4E3B\u72AF\uFF0C\u5E94\u5256\u68FA\u622E\u5C38\uFF0C\u5176\u5BB6\u4E2D\u5973\u7737\u7531\u5B98\u5E9C\u9020\u518C\uFF0C\u4E00\u5F8B\u5356\u5165\u9752\u697C\u4E3A\u5993\u3002\u5947\u602A\u7684\u662F\uFF0C\u4F55\u6E05\u6CB3\u5C45\u7136\u7CCA\u91CC\u80E1\u6D82\u628A\u859B\u971C\u7075\u4E5F\u4E00\u5E76\u5F52\u5165\u53E6\u6863\uFF0C\u4E0D\u4F46\u62DF\u597D\u7684\u51CC\u8FDF\u4F5C\u4E0D\u5F97\u6570\uFF0C\u8FDE\u6B7B\u7F6A\u4E5F\u514D\u4E86\u3002</p><p>\u8FD9\u6848\u5B50\u6765\u56DE\u626F\u4E86\u5C06\u8FD1\u4E00\u5E74\uFF0C\u5C01\u603B\u7BA1\u5DF4\u4E0D\u5F97\u4ED6\u653E\u8FC7\u4E0D\u518D\u8FFD\u7A76\uFF0C\u867D\u7136\u5FC3\u91CC\u7EB3\u7F55\uFF0C\u4E5F\u5F53\u5373\u7B54\u5E94\u4E0B\u6765\u3002\u5374\u4E0D\u77E5\u662F\u5434\u5927\u5F6A\u6697\u4E2D\u5411\u4F55\u6E05\u6CB3\u77E5\u4F1A\u4E86\u859B\u971C\u7075\u7684\u8EAB\u4EFD\u3002</p><p>\u767D\u83B2\u6559\u867D\u706D\uFF0C\u7EA2\u9633\u771F\u4EBA\u5374\u9690\u8E2A\u533F\u8FF9\uFF0C\u4FDD\u6E05\u6CB3\u4E0D\u613F\u8F7B\u6740\u4E86\u859B\u971C\u7075\uFF0C\u53C8\u62C5\u5FC3\u4E1C\u5382\u501F\u673A\u751F\u4E8B\uFF0C\u5E72\u8106\u88C5\u80E1\u6D82\uFF0C\u5148\u7559\u4F4F\u859B\u971C\u7075\u7684\u6027\u547D\uFF0C\u518D\u501F\u673A\u884C\u4E8B\u3002\u8FD9\u6837\u4E00\u6765\uFF0C\u7B97\u662F\u7ECF\u5927\u7406\u5BFA\u70B9\u5934\uFF0C\u660E\u660E\u767D\u767D\u628A\u6848\u5B50\u7ED3\u4E86\u3002\u4E00\u573A\u5927\u6848\u5230\u6B64\u4E91\u6536\u96E8\u6563\u3002</p><p>\u4E39\u5A18\u542C\u7740\u8F7B\u58F0\u7B11\u8D77\u6765\uFF0C\u300C\u5B98\u5356\uFF1F\u4F5C\u4E00\u8F88\u5B50\u5A3C\u5993\u4E48\uFF1F\u90A3\u53EF\u662F\u592A\u4FBF\u5B9C\u5974\u5BB6\u4E86\u3002\u300D</p><p>\u5B59\u5929\u7FBD\u770B\u7740\u8FDC\u5904\uFF0C\u6CA1\u6709\u7B54\u8BDD\u3002</p><p>\u4E39\u5A18\u7528\u624B\u6440\u4F4F\u773C\u775B\uFF0C\u534A\u664C\u4F4E\u58F0\u9053\uFF1A\u300C\u628A\u6211\u4EEC\u5356\u5230\u54EA\u513F\u5462\uFF1F\u53BF\u91CC\u8FD8\u662F\u5E9C\u91CC\uFF1F\u8FD8\u662F\u5916\u7701\u7684\u9752\u697C\uFF1F\u300D</p><p>\u5B59\u5929\u7FBD\u6162\u6162\u8BF4\u9053\uFF1A\u300C\u674F\u82B1\u6751\u662F\u9006\u532A\u4EA7\u4E1A\uFF0C\u4F9D\u5F8B\u6CA1\u5165\u5B98\u5E9C\u3002\u6211\u5DF2\u7ECF\u628A\u5B83\u8D4E\u4E70\u4E0B\u6765\u3002\u300D\u4ED6\u62FF\u51FA\u4E00\u5C01\u6587\u4E66\uFF0C\u300C\u4F60\u53EA\u9700\u753B\u4E2A\u62BC\u3002\u300D</p><p>\u674F\u82B1\u6751\u4E0D\u8FC7\u662F\u5BB6\u5C0F\u5C0F\u7684\u9152\u5E97\uFF0C\u53C8\u5730\u5904\u6DF1\u5C71\uFF0C\u503C\u4E0D\u4E86\u591A\u5C11\u94F6\u5B50\uFF0C\u800C\u8FD9\u4E9B\u94F6\u5B50\uFF0C\u8FD8\u662F\u5F53\u65E5\u4E39\u5A18\u6258\u4ED6\u7167\u987E\u5973\u513F\u800C\u4EA4\u7ED9\u4ED6\u7684\u3002</p><p>\u81EA\u5DF1\u7684\u8EAB\u5B50\u90FD\u6210\u4E86\u5B98\u5356\u7684\u7269\u54C1\uFF0C\u4F55\u51B5\u8FD9\u4E9B\u5DF2\u7ECF\u4E0D\u5C5E\u4E8E\u5979\u7684\u8EAB\u5916\u4E4B\u7269\u3002\u4E39\u5A18\u95EE\u4E5F\u4E0D\u95EE\uFF0C\u63A5\u8FC7\u7B14\uFF0C\u5728\u4E0A\u9762\u5708\u4E86\u3002\u4ECE\u6B64\u523B\u8D77\uFF0C\u674F\u82B1\u6751\u5C31\u662F\u5B59\u5929\u7FBD\u7684\u4EA7\u4E1A\u4E86\u3002</p><p>\u5B59\u5929\u7FBD\u6536\u4E86\u6587\u4E66\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u8FD9\u95F4\u5E97\u5F80\u540E\u5C31\u662F\u5993\u9662\u4E86\u3002\u300D</p><p>\u4E39\u5A18\u624B\u4E00\u6296\uFF0C\u7B14\u6389\u5728\u5730\u4E0A\u3002</p><p>\u300C\u4F60\u4EEC\u6CA1\u5356\u5230\u522B\u5904\uFF0C\u90FD\u8BA9\u6211\u4E70\u4E86\u3002\u88F4\u4E39\u674F\u3001\u88F4\u9752\u7389\u3001\u767D\u96EA\u83B2\u3001\u767D\u7389\u83B2\u3001\u859B\u971C\u7075\u4E94\u4F4D\u5B98\u5993\uFF0C\u4E00\u5171\u516D\u5341\u4E94\u4E24\u3002\u300D</p><p>\u300C\u8FD8\u503C\u8FD9\u4E48\u4E9B\u94F6\u5B50\u5462\u3002\u300D\u4E39\u5A18\u7B11\u7740\u773C\u775B\u6E7F\u4E86\uFF0C\u8FC7\u4E86\u4F1A\u9053\uFF1A\u300C\u4E0D\u662F\u516D\u4E2A\u5417\uFF1F\u300D</p><p>\u5B59\u5929\u7FBD\u54FC\u4E86\u4E00\u58F0\uFF0C\u300C\u90A3\u4E2A\u4E0D\u662F\u3002\u5979\u53EA\u662F\u6761\u72D7\uFF0C\u4F60\u522B\u7BA1\u3002\u300D</p><p>\u4E39\u5A18\u5782\u4E0B\u773C\u542C\u5B59\u5929\u7FBD\u8BF4\u9053\uFF1A\u300C\u5F80\u540E\u5B98\u5E9C\u4F1A\u5B9A\u671F\u6D3E\u4EBA\u67E5\u770B\uFF0C\u4E00\u4E2A\u770B\u662F\u5426\u9003\u9038\uFF0C\u4E00\u4E2A\u770B\u63A5\u5BA2\u7684\u6570\u91CF\uFF0C\u8FD8\u6709\u5C31\u662F\u6536\u53D6\u5356\u8EAB\u7684\u91D1\u82B1\u94B1\u3002\u300D</p><p>\u300C\u91D1\u82B1\u94B1\uFF1F\u300D</p><p>\u300C\u5B98\u5993\u90FD\u8981\u7F34\u7684\u5356\u8EAB\u94B1\uFF0C\u9022\u4E8C\u62BD\u4E00\uFF0C\u9010\u6708\u7F34\u5165\u5185\u5EF7\uFF0C\u5145\u4F5C\u540E\u5BAB\u8102\u7C89\u94B1\u3002\u300D</p><p>\u4E39\u5A18\u7B2C\u4E00\u6B21\u542C\u8BF4\u8FD9\u6837\u8352\u5510\u7684\u7A0E\u94B1\uFF0C\u62FF\u5A4A\u5B50\u4EEC\u7684\u5356\u8EAB\u94B1\u7ED9\u5BAB\u91CC\u7684\u5A18\u5A18\u4E70\u8102\u7C89\u3002</p><p>\u5B59\u5929\u7FBD\u8BF4\u9053\uFF1A\u300C\u8FD9\u662F\u6309\u4EBA\u6536\u7684\uFF0C\u8FC7\u4E9B\u65E5\u5B50\u5B98\u5E9C\u4F1A\u6765\u4EBA\uFF0C\u7ED9\u4F60\u4EEC\u5B9A\u4E0B\u5356\u8EAB\u7684\u4EF7\u94B1\u3002\u8FD9\u91CC\u504F\u50FB\uFF0C\u8FC7\u5F80\u5BA2\u4EBA\u4E5F\u4E0D\u591A\uFF0C\u8EAB\u4EF7\u4E0D\u4F1A\u5B9A\u5F97\u592A\u9AD8\u3002\u300D</p><p>\u4E00\u80A1\u5BD2\u610F\u88AD\u6765\uFF0C\u4E39\u5A18\u98A4\u6296\u7740\u62B1\u4F4F\u8EAB\u4F53\uFF0C\u826F\u4E45\u9053\uFF1A\u300C\u8FD8\u6709\u5417\uFF1F\u300D</p><p>\u5B59\u5929\u7FBD\u9ED8\u7136\u5750\u4E86\u8BB8\u4E45\uFF0C\u6CA1\u5934\u6CA1\u8111\u5730\u8BF4\u4E86\u53E5\uFF0C\u300C\u5FEB\u7ACB\u6625\u4E86\u3002\u300D</p>',83);function t(n,_){return r}var o=e(a,[["render",t]]);export{o as default};
