import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const a={},r=p('<h1 id="j" tabindex="-1"><a class="header-anchor" href="#j" aria-hidden="true">#</a> J\u2026</h1><p>\u60CA\u96F7\u3002</p><p>\u5965\u6258\u5927\u5E1D\u6000\u62B1\u4E2D\u662F\u4ED6\u67D4\u8F6F\u7684\u5973\u513F\u3002\u6EE1\u662F\u76B1\u7EB9\u7684\u5927\u624B\u63E1\u7D27\u5979\u7684\u9165\u624B\u3002\u6413\u63C9\u3002</p><p>\u300C\u5509\u2026\u2026\u600E\u4F1A\u559D\u5230\u591A\u3002\u300D</p><p>\u8D1D\u73B2\u8FBE\u662F\u5A49\u67D4\u5A07\u5A9A\u7684\u5973\u5B50\u3002\u5728\u5979\u9189\u5230\u660F\u8FF7\u7684\u65F6\u5019\uFF0C\u5F25\u6F2B\u7740\u5FAE\u5999\u6C14\u606F\u3002\u96BE\u4EE5\u6297\u62D2\u3002</p><p>\u7F8E\u76EE\u662F\u7981\u95ED\uFF0C\u9762\u4E0A\u7684\u6F6E\u7EA2\u8513\u5EF6\u81F3\u8033\u8DDF\u7C89\u9888\uFF0C\u9999\u82B1\u4F3C\u7389\u3002\u7236\u4EB2\u7528\u6084\u6084\u89E6\u6478\u5979\u7CBE\u7F8E\u7684\u9F3B\u5C16\uFF0C\u6CBE\u4E00\u6EF4\u6C57\u3002\u8F7B\u8584\u7684\u4E24\u7247\u5507\uFF0C\u5FAE\u542F\u5F00\uFF0C\u6C14\u5982\u5E7D\u5170\u3002</p><p>\u96EA\u80A9\u67D4\u6DA6\uFF0C\u8F6F\u4F3C\u65E0\u9AA8\u3002\u4E00\u5C42\u8584\u838E\u8986\u76D6\u5728\u7389\u4F53\uFF0C\u8F6F\u800C\u8273\u3002\u7236\u4EB2\u4EE5\u624B\u63A2\uFF0C\u611F\u89C9\u5979\u5347\u6E29\u3002\u7EA2\u5986\u534E\u670D\uFF0C\u80F8\u7EBF\u7684\u4F4D\u7F6E\u66A7\u6627\uFF0C\u6070\u770B\u5230\u82E5\u9690\u82E5\u73B0\u7684\u4E73\u6C9F\uFF0C\u6574\u4E2A\u4E73\u623F\u7684\u5F62\u65E0\u53EF\u6311\u5254\u3002\u5C0F\u8179\u5E73\u5766\uFF0C\u7EA4\u7EC6\u67D4\u7F8E\u7684\u8170\u90E8\u66F2\u7EBF\u88F9\u5728\u4FEE\u957F\u7684\u88D9\uFF0C\u4E00\u53CC\u6C34\u6676\u978B\u3002</p><p>\u7C89\u8273\u5149\u6CFD\uFF0C\u900F\u660E\u81F3\u7F8E\u3002</p><p>\u5C06\u5979\u957F\u4E45\u7684\u6367\u5165\u6000\u4E2D\uFF0C\u95FB\u904D\u8EAB\u4E0A\u5E7D\u9999\u3002</p><p>\u521D\u751F\u90A3\u65E5\uFF0C\u4ED6\u66FE\u8F7B\u62CD\u5979\u4E09\u5BF8\u8DB3\u5FC3\uFF0C\u5F85\u8FD9\u591C\u5265\u843D\u4E00\u5BF9\u6C34\u6676\u978B\u3002\u6D41\u5E74\u4E0D\u89C9\u6697\u6E21\u3002</p><p>\u5728\u4ED6\u7684\u7B2C\u4E8C\u4E2A\u5973\u513F\u51FA\u4E16\u7684\u65F6\u5019\uFF0C\u59BB\u5B50\u6B7B\u4E8E\u96BE\u4EA7\u3002\u90A3\u65E5\u8D1D\u73B2\u8FBE\u628A\u59B9\u59B9\u62B1\u8FDB\u6000\u4E2D\uFF0C\u4EB2\u543B\u9999\u55B7\u55B7\u7684\u5A74\u3002</p><p>\u4ECA\u6B21\u8FD9\u559C\u60A6\u4FBF\u662F\u7B80\u5355\u91CD\u6F14\u3002</p><p>\u8D1D\u73B2\u8FBE\u67D4\u8F6F\u7684\u8EAF\uFF0C\u627F\u53D7\u7236\u4EB2\u7684\u8D2A\u5A6A\u3002\u5E76\u65E0\u77E5\u89C9\uFF0C\u50CF\u662F\u56DE\u53BB\u521D\u5A74\u3002\u5982\u6B64\u9759\u7F8E\u3002</p><p>\u4ED6\u662F\u4E00\u4E2A\u7236\u4EB2\u3002\u4ED6\u8BB0\u5F97\u4ECE\u524D\u4E3A\u5979\u4EEC\u8131\u8863\u6C90\u6D74\uFF0C\u8BB0\u5F97\u8D1D\u73B2\u8FBE\u5C0F\u65F6\u4FAF\u7684\u6837\u5B50\u3002\u5982\u4ECA\u90A3\u5C0F\u5DE7\u7684\u9999\u6ED1\u7684\u81C2\u8180\u5DF2\u7C89\u5AE9\u9999\u6ED1\uFF0C\u6108\u53D1\u6025\u5267\u7684\u9F3B\u606F\u71CE\u8D77\u66F4\u9AD8\u6B32\u7130\u3002</p><p>\u75BE\u4FC3\u7684\u96F7\u5149\u660E\u706D\u3002</p><p>\u5979\u7709\u5934\u76B1\u4E86\uFF0C\u9762\u4E0A\u662F\u77DC\u6301\u3002\u73E0\u5507\u8F7B\u542F\u4E86\uFF0C\u8BD5\u8FC7\u4E00\u4E2A\u6F6E\u6E7F\u7EF5\u8F6F\u97F3\u8282\u3002</p><p>\u50CF\u662F\u300C\u7687\u5B50\u300D\u7684\u8BCD\u6839\u3002</p><p>\u70DF\u706B\u70EB\uFF0C\u9713\u88F3\u4E71\u3002\u5FFD\u7136\u6625\u5149\u8D25\u9732\uFF0C\u9165\u624B\u63A9\u62A4\u3002</p><p>\u4E0D\u77E5\u5965\u6258\u662F\u5426\u60F3\u8FC7\u60AC\u5D16\u52D2\u9A6C\u3002\u6291\u6216\u5F53\u4E86\u5979\u662F\u82CD\u5170\u3002</p><p>\u90A3\u7EDD\u4E16\u82F1\u59FF\u7684\u5973\u7687\uFF0C\u4E3B\u5BB0\u5929\u7A7A\u9AD8\u5904\u7684\u57CE\u3002</p><p>\u521D\u6B21\u89C1\u5979\u7684\u65F6\u5019\uFF0C\u5965\u6258\u5927\u5E1D\u7684\u5FC3\u4E2D\u5C31\u711A\u70E7\u8D77\u72C2\u70ED\u7684\u60C5\u6B32\u706B\u7130\u3002\u5728\u5FC3\u6E05\u795E\u9759\u7684\u5BF9\u767D\u4E4B\u4E0B\uFF0C\u4ED6\u65E0\u9650\u6B21\u76D8\u7B97\u7740\u5343\u4E07\u9634\u8C0B\u3002\u5979\u7684\u7EDD\u8272\uFF0C\u4EE4\u4ED6\u5982\u6B64\u4E0D\u5B89\u3002\u4EA6\u4EE4\u4ED6\u4FC3\u6210\u5973\u513F\u548C\u686B\u6469\u7684\u4E00\u573A\u5A5A\u4E8B\u3002</p><p>\u8FD9\u523B\uFF0C\u4ED6\u5C06\u660F\u8FF7\u7684\u8D1D\u73B2\u8FBE\u538B\u5728\u8EAB\u4E0B\uFF0C\u6C34\u6676\u5149\u7EBF\u6251\u6714\u6210\u8FF7\u3002</p><p>\u65E5\u5149\u4E4B\u4E0B\uFF0C\u5FC5\u6709\u7981\u5FCC\uFF1B\u81EA\u5F53\u4F9D\u4ECE\uFF0C\u60DF\u6709\u656C\u754F\u3002\u4EBA\u5728\u4E07\u56FD\uFF0C\u5F53\u884C\u4E49\u4E8B\u3002\u6559\u5316\u7684\u9053\u662F\u8981\u4ECE\u7684\uFF0C\u5728\u4E07\u4E8B\u4E0A\u90FD\u8981\u4EE5\u4E3A\u6B63\u76F4\u3002</p><p>\u5F53\u4ED6\u7684\u624B\u6307\u8DDD\u79BB\u5979\u7684\u5185\u8863\u53EA\u6709\u4E00\u5F20\u767D\u7EB8\u7684\u7A7A\u9699\uFF0C\u4ED6\u751A\u81F3\u6E05\u6670\u5730\u770B\u89C1\u5979\u4E73\u5934\u5145\u8840\u7684\u5F62\u72B6\u3002\u8FD9\u7981\u5FCC\u662F\u8981\u88AB\u7834\u9664\u7684\uFF0C\u5F53\u4EE5\u5978\u6076\u7684\u517D\u9053\u3002</p><p>\u53EA\u8981\u6495\u88C2\u8FD9\u8584\u7EB1\uFF0C\u4E00\u5207\u5206\u5D29\u74E6\u89E3\u3002\u50F5\u5728\u90A3\u5F20\u7EB8\u7684\u8DDD\u79BB\uFF0C\u601D\u91CF\u5978\u6076\u7684\u65B9\u5F0F\u3002</p><p>\u8FD9\u65B9\u5F0F\u662F\u8D25\u574F\u7684\u3002</p><p>\u4ED6\u6253\u6765\u4E00\u7897\u6C34\uFF0C\u662F\u7687\u5BAB\u540E\u56ED\u7684\u6CC9\u3002\u4E00\u5982\u704C\u6E89\u9C9C\u82B1\uFF0C\u6162\u6162\u6DCB\u5728\u5973\u513F\u3002</p><p>\u5979\u9661\u7136\u8F6C\u9192\uFF0C\u9189\u773C\u6726\u80E7\u5730\u671B\u89C1\u9762\u76EE\u72F0\u72DE\u7684\u7236\u7687\u3002</p><p>\u300C\u554A\u2014\u2014\u300D</p><p>\u2014\u2014\u5979\u53D1\u73B0\u81EA\u5DF1\u7684\u53CC\u624B\uFF0C\u662F\u5E73\u644A\u5730\u88AB\u6346\u5728\u5E8A\u7684\u4E24\u8FB9\u3002\u77DC\u6301\u7684\u96EA\u767D\u8EAB\u4F53\uFF0C\u7ADF\u662F\u4E00\u4E1D\u4E0D\u6302\u7684\u5C34\u5C2C\u3002\u5728\u9888\u4E0A\u6263\u7740\u4E00\u8BB0\u94C1\u9879\u5708\uFF0C\u9879\u5708\u7684\u4E0B\u7AEF\uFF0C\u8FDE\u7740\u4E00\u6761\u624B\u638C\u5BBD\u5EA6\u7684\u9ED1\u8272\u76AE\u5E26\uFF0C\u7EB5\u5411\u800C\u4E0B\u3002</p><p>\u4EE4\u5979\u7F9E\u6127\u7684\u662F\uFF0C\u76AE\u5E26\u65E0\u6CD5\u906E\u4F4F\u4E00\u5BF9\u4E73\u5CF0\u548C\u4E73\u623F\u8FF7\u4EBA\u7684\u5F62\u72B6\u3002\u5EF6\u4F38\u5230\u5C0F\u8179\u5904\uFF0C\u518D\u7531\u4E00\u8BB0\u94F6\u5708\u5206\u4E3A\u4E24\u8FB9\u73AF\u7ED5\uFF0C\u5728\u80CC\u90E8\u518D\u8FDE\u7ED3\u5408\u4E00\uFF0C\u5982\u8170\u5E26\u4E00\u822C\u3002</p><p>\u8170\u8EAB\u4EE5\u4E0B\u7A7F\u7740\u4E00\u6761\u9ED1\u8272\u76AE\u9769\u8D28\u5730\u7684\u77ED\u88E4\uFF0C\u800C\u5728\u88E4\u88C6\u5904\u5374\u662F\u8086\u610F\u655E\u5F00\u7684\u4E00\u9053\u88C2\u7F1D\u3002</p><p>\u7236\u4EB2\u7AD9\u7740\uFF0C\u62AC\u9AD8\u5979\u4E00\u53EA\u7389\u817F\uFF0C\u5979\u53D1\u73B0\u90A3\u53EA\u6C34\u6676\u978B\u4E3A\u4ED6\u892A\u53BB\u4E86\u3002\u4E00\u53CC\u7389\u817F\u5206\u5F00\u6210\u949D\u89D2\u3002\u56E0\u4E3A\u5E95\u88E4\u4E2D\u592E\u90A3\u6761\u90AA\u6076\u7684\u88C2\u7F1D\uFF0C\u5979\u77E5\u9053\u7236\u4EB2\u6240\u5728\u7684\u89D2\u5EA6\u4E00\u5B9A\u80FD\u770B\u89C1\u5979\u6700\u5927\u7684\u9690\u79C1\u3002</p><p>\u300C\u7236\u7687\u2026\u2026\u4E0D\u2026\u2026\u60A8\u4E3A\u4EC0\u4E48\u2026\u2026\u300D\u5979\u6218\u6218\u5162\u5162\u7684\u8BF4\u8BDD\u3002</p><p>\u4ED6\u672A\u5E94\u5979\uFF0C\u53EA\u7528\u820C\u5C16\u5728\u5979\u811A\u5FC3\u5212\u7740\u5708\u513F\u3002\u5979\u88AB\u64A9\u5F97\u53C8\u70ED\u53C8\u75D2\uFF0C\u626D\u52A8\u7740\u7F9E\u6DA9\u4E0D\u582A\u7684\u8EAB\u4F53\u3002\u6323\u624E\u7740\u53D1\u51FA\u4EE4\u4EBA\u96BE\u8010\u7684\u751C\u7F8E\u58F0\u97F3\u3002</p><p>\u5979\u90A3\u4E48\u67D4\u5F31\uFF0C\u751A\u81F3\u6323\u624E\u90FD\u4F3C\u6C34\u7EA4\u67D4\u3002</p><p>\u4ED6\u4E00\u8DEF\u653B\uFF0C\u5979\u65E0\u6CD5\u5B88\u3002\u4ED6\u5EF6\u7740\u5973\u513F\u96EA\u767D\u5300\u79F0\u7684\u817F\u578B\uFF0C\u5728\u5149\u6ED1\u7684\u808C\u80A4\u7559\u4E0B\u81EA\u5DF1\u7684\u553E\u6DB2\u8F68\u8FF9\u3002\u4ED6\u4E00\u76F4\u662F\u5C0F\u5FC3\u7FFC\u7FFC\uFF0C\u7136\u540E\u63A2\u8BD5\u82B3\u8349\u4E1B\u95F4\u9690\u79D8\u7684\u7A74\u4F4D\u3002</p><p>\u90A3\u4E2A\u88AB\u6346\u53CC\u624B\u7684\u5B71\u5F31\u5973\u5B69\u53C8\u80FD\u505A\u4EC0\u4E48\u5462\uFF1F</p><p>\u5979\u9664\u4E86\u6D41\u6CEA\u7684\u54C0\u6C42\uFF0C\u53EA\u5269\u54C0\u6C42\u7740\u6D41\u6CEA\u3002\u4EFB\u4F55\u4E00\u4E2A\u5973\u4EBA\u5728\u60C5\u6B32\u64A9\u52A8\u7684\u65F6\u5019\u90FD\u4E0D\u53EF\u80FD\u6291\u5236\u6DB2\u4F53\u5206\u6CCC\u3002</p><p>\u300C\u7236\u4EB2\u965B\u4E0B\u2026\u2026\u7236\u7687\u965B\u2026\u2026\u4E0B\uFF01\u300D</p><p>\u4ED6\u8D2A\u5A6A\u542E\u5438\uFF0C\u5E76\u4EE5\u53CC\u624B\u9F13\u52B1\u7740\u5979\u7684\u4E73\u5934\u7EE7\u7EED\u5145\u8840\u81A8\u80C0\u3002</p><p>\u300C\u6C42\u60A8\u4E86\u2026\u2026\u7236\u2026\u2026\u7687\u965B\u4E0B\u2026\u2026\u6C42\u2026\u2026\u6C42\u2026\u2026\u300D</p><p>\u4ED6\u628A\u820C\u95F4\u7A0D\u7A0D\u63A2\u8FDB\u6D1E\u53E3\uFF0C\u8FD8\u672A\u89E6\u5230\u5C4F\u969C\u3002</p><p>\u53EF\u601C\u7684\u8D1D\u73B2\u8FBE\u4FBF\u60E8\u53EB\u8D77\u6765\uFF0C\u5979\u771F\u7684\u4E0D\u660E\u767D\u662F\u4EC0\u4E48\u7406\u7531\u8BA9\u81EA\u5DF1\u7684\u7236\u4EB2\u7740\u4E86\u9B54\u3002</p><p>\u5979\u54ED\u7740\uFF0C\u5636\u53EB\uFF0C\u7528\u5C3D\u6240\u6709\u7684\u529B\u8E6C\u7740\u53CC\u817F\uFF0C\u5374\u542C\u89C1\u6C34\u6676\u978B\u7834\u788E\u7684\u58F0\u97F3\u3002</p><p>\u4ED6\u505C\u4E86\u4E0B\u6765\uFF0C\u4ECE\u623F\u5C4B\u7684\u89D2\u843D\u62FE\u6765\u4E00\u53EA\u70DB\u53F0\u3002</p><p>\u64E6\u4EAE\u706B\u77F3\u7684\u77AC\u95F4\uFF0C\u4ED6\u770B\u89C1\u5973\u513F\u7EDD\u671B\u7684\u6CEA\u773C\uFF0C\u90A3\u662F\u96BE\u4EE5\u7F6E\u4FE1\u7684\u795E\u8272\uFF0C\u5979\u751A\u81F3\u60F3\u770B\u6E05\u8FD9\u66B4\u5F92\u662F\u5426\u81EA\u5DF1\u7684\u7236\u7687\u3002</p><p>\u4ED6\u4E3E\u7740\u8721\u70DB\u8D70\u8FDB\u5979\uFF0C\u5E7D\u5FAE\u5730\u70DB\u5149\u7167\u51FA\u5979\u5206\u5916\u5A07\u5A9A\u3002\u5FAE\u503E\u3002\u6EDA\u70EB\u7684\u8721\u6CB9\u5C31\u6EF4\u843D\u5979\u7684\u96EA\u767D\u4E73\u623F\uFF0C\u5973\u513F\u7EDD\u53EB\u7740\u54ED\u558A\uFF0C\u7236\u7687\u5374\u7B11\u5BB9\u6148\u7965\u3002</p><p>\u4E00\u6EF4\u8721\u6EF4\u5728\u8EAB\u4F53\uFF0C\u7ADF\u53EF\u4EE5\u6362\u6765\u5979\u8BB8\u591A\u6CEA\u3002\u4ED6\u7EC8\u4E8E\u660E\u767D\u4E3A\u4EC0\u4E48\u8721\u70DB\u8FD9\u6837\u7684\u9053\u5177\u4F1A\u5728\u5343\u767E\u5E74\u4FDD\u7559\u4E0B\u6765\u3002</p><p>\u5728\u5979\u4E00\u6B21\u6B21\u7684\u62BD\u6410\u548C\u54ED\u558A\u4E2D\uFF0C\u4ED6\u6B23\u8D4F\u4E86\u5979\u8EAB\u4F53\u4E00\u5207\u7684\u7F8E\u6001\u3002\u76F4\u5230\u5979\u7684\u58F0\u7EBF\u90FD\u6C99\u54D1\uFF0C\u4ED6\u624D\u5F00\u53E3\u8BF4\u8BDD\uFF1A\u300C\u5973\u513F\u554A\uFF0C\u5973\u513F\uFF0C\u6211\u662F\u7231\u4F60\u7684\u3002\u4F60\u5C31\u8981\u51FA\u5AC1\uFF0C\u6211\u90FD\u597D\u820D\u4E0D\u5F97\u3002\u300D</p><p>\u7136\u540E\u8F7B\u8F7B\u62AC\u8D77\u5979\u7684\u4E0B\u989A\u3002</p><p>\u70DB\u5149\u4F9D\u4F9D\uFF0C\u4EBA\u5982\u7389\u6DA6\u3002\u5982\u6B64\u67D4\u5F31\u7684\u5973\u5B50\uFF0C\u4ECE\u5979\u7684\u76EE\u5149\uFF0C\u5965\u6258\u5927\u5E1D\u8BFB\u51FA\u60CA\u60E7\u548C\u75DB\u82E6\u3002\u90A3\u773C\u795E\u4E2D\u751A\u81F3\u4E0D\u5E26\u4E00\u70B9\u7684\u6068\uFF0C\u53EA\u6709\u695A\u695A\u7684\u53EF\u601C\u3002</p><p>\u300C\u4F60\u8981\u5AC1\uFF0C\u7236\u7687\u60F3\u7834\u4F60\u7684\u5904\u5B50\u8EAB\u3002\u300D</p><p>\u770B\u7740\u5979\u7684\u8EAB\u4F53\u5C3D\u662F\u8721\u6CB9\u51DD\u56FA\u7684\u75D5\u8FF9\uFF0C\u51B0\u96EA\u808C\u80A4\uFF0C\u80F8\u90E8\u4F0F\u5F04\uFF0C\u5979\u662F\u4EE4\u4EBA\u6309\u5948\u4E0D\u4F4F\u7684\u5C24\u7269\u3002</p><p>\u300C\u4E0D\u8981\u554A\u2014\u2014\u7236\u7687\uFF0C\u60A8\u2026\u2026\u60A8\u662F\u6211\u7684\u7236\u7687\u554A\uFF0C\u7236\u7687\u2014\u2014\u300D\uFF0C\u53EF\u601C\u7684\u5C24\u7269\u53EB\u558A\u7740\u54C0\u6C42\u3002</p><p>\u300C\u5BF9\u5440\uFF0C\u6211\u662F\u4F60\u7684\u7236\u7687\uFF0C\u6240\u4EE5\u66F4\u5E94\u8BE5\u62E5\u6709\u4F60\u7684\u5168\u90E8\u3002\u300D\u4ED6\u5374\u8BED\u8C03\u8212\u7F13\uFF0C\u7B11\u5BB9\u4EB2\u539A\uFF0C\u5C31\u50CF\u662F\u513F\u65F6\u54C4\u5979\u5403\u7CD6\u3002</p><p>\u300C\u6C42\u60A8\u4E86\uFF0C\u6C42\u60A8\u4E86\uFF0C\u6211\u7684\u7236\u7687\u3002\u300D\u5979\u5DF2\u6B47\u65AF\u5E95\u91CC\uFF0C\u6CE3\u4E0D\u6210\u58F0\u3002</p><p>\u7236\u7687\u9762\u5E26\u4E3A\u96BE\u795E\u8272\uFF0C\u624B\u6307\u5374\u8F7B\u67D4\u63A2\u5165\u7EC6\u8F6F\u7684\u9634\u6BDB\u95F4\u5BF8\u52A8\uFF0C\u604B\u604B\u4E0D\u820D\u5730\u64A9\u5F04\u5979\u52C3\u8D77\u7684\u9634\u8482\u3002\u300C\u5509\u2026\u2026\u300D\u53F9\u4E00\u53E3\u6C14\uFF0C\u7AED\u529B\u9020\u4F5C\u3002</p><p>\u300C\u597D\u5427\uFF0C\u7236\u7687\u53EA\u4E0D\u8FC7\u662F\u56E0\u4E3A\u592A\u6DF1\u7231\u4F60\u3002\u8FD9\u6837\u5427\uFF0C\u4ECA\u5929\u6211\u7559\u4F60\u7684\u5904\u5973\u3002\u4F46\u5728\u4F60\u6210\u5A5A\u4E4B\u540E\uFF0C\u6211\u8981\u4F60\u65F6\u65F6\u6765\u966A\u6211\u4EA4\u6B22\u3002\u300D</p><p>\u7535\u95EA\u3002</p><p>\u60CA\u96F7\u3002</p><p>\u70DB\u5149\u6447\u64BC\u3002</p><p>\u5965\u6258\u5927\u5E1D\u671B\u671B\u7A97\u5916\u7684\u9AD8\u5854\uFF0C\u53C8\u62CD\u62CD\u5979\u8D64\u88F8\u7684\u9634\u6237\u3002\u300C\u662F\u7684\uFF0C\u6211\u662F\u8BF4\u4E71\u4F26\u3002\u300D</p><p>\u94C1\u94FE\u88AB\u5F3A\u98CE\u5439\u5F97\u4F5C\u54CD\uFF0C\u53D1\u51FA\u523A\u8033\u58F0\u97F3\u3002\u5965\u6258\u5927\u5E1D\u60F3\u53BB\u5173\u95ED\u7A97\u6237\uFF0C\u5374\u8E0F\u5728\u65B9\u624D\u7834\u788E\u4E00\u5730\u7684\u6C34\u6676\uFF0C\u5272\u7834\u8DB3\u5FC3\u3002</p><p>\u300C\u6211\u7684\u8D1D\u73B2\u8FBE\uFF0C\u4F60\u6700\u597D\u77E5\u9053\u4E56\u4E00\u70B9\u3002\u5426\u5219\u4ECA\u5929\u665A\u4E0A\u7684\u4E8B\u60C5\u8BF4\u51FA\u53BB\uFF0C\u4F20\u5230\u4F60\u7684\u5982\u610F\u90CE\u541B\u90A3\u91CC\u2026\u2026\u90A3\uFF0C\u53EF\u662F\u5341\u5206\u4E0D\u59A5\u7684\u4E8B\u3002\u300D</p><p>\u4ED6\u72E0\u72E0\u5730\uFF0C\u62C9\u8D77\u5979\u7684\u957F\u53D1\uFF0C\u518D\u4E00\u8DE8\u6B65\uFF0C\u5750\u5728\u5979\u7684\u4E73\u623F\u3002</p><p>\u300C\u554A\u2014\u2014\u300D\u5973\u4EBA\u7684\u4E73\u623F\u662F\u5F88\u654F\u611F\u7684\u3002\u4E73\u623F\u5BB9\u6613\u611F\u89C9\u751C\u871C\u4E5F\u5C24\u5176\u4E0D\u582A\u9644\u75DB\u3002</p><p>\u300C\u53EB\u4EC0\u4E48\u53EB\uFF01\u7ED9\u6211\u5F20\u5F00\u5634\uFF01\u300D\u4ED6\u4E00\u76F4\u662F\u6E29\u548C\u6148\u7231\u7684\u7236\u4EB2\uFF0C\u53EA\u6709\u8981\u6C42\u5973\u513F\u4E3A\u4ED6\u53E3\u4EA4\u7684\u65F6\u5019\u53D8\u5F97\u4E25\u5389\u3002</p><p>\u4ED6\u534A\u8E72\u4E0B\uFF0C\u7A0D\u5FAE\u62AC\u9AD8\u81EA\u5DF1\u7684\u4F53\u4F4D\uFF0C\u518D\u638F\u51FA\u90A3\u8DDF\u4E0D\u5927\u4E0D\u5C0F\u7684\u5206\u8EAB\u3002</p><p>\u5927\u6982\u662F\u56E0\u4E3A\u7236\u7687\u771F\u7684\u6781\u5C11\u5982\u6B64\u4E25\u5389\uFF0C\u8D1D\u94C3\u8FBE\u7ADF\u5413\u5F97\u4E56\u4E56\u5F20\u5F00\u53E3\u578B\u3002</p><p>\u5728\u4ED6\u7684\u9634\u830E\u8FDB\u5165\u53E3\u4E2D\u7684\u7247\u523B\uFF0C\u5979\u5F00\u59CB\u9ED8\u9ED8\u7948\u7977\uFF1A\u300C\u686B\u6469\uFF0C\u4F60\u4F1A\u539F\u8C05\u6211\u7684\u3002\u662F\u4F1A\u7684\u3002\u5A11\u6469\u3002\u300D</p><p>\u5979\u5C48\u8FB1\u5730\uFF0C\u542B\u4F4F\u7236\u4EB2\u7684\u9634\u830E\uFF0C\u7528\u5979\u7518\u751C\u7684\u53E3\u6C34\u5305\u56F4\u4ED6\u9F9F\u5934\u3002\u5979\u5F00\u59CB\u61C2\u5F97\u4E00\u4E2A\u5973\u4EBA\u7684\u75DB\u82E6\u3002</p><p>\u300C\u7528\u820C\u5934\uFF0C\u4E56\u6211\u7684\u5973\u513F\uFF0C\u4E56\u3002\u300D</p><p>\u820C\u5C16\u7684\u5473\u857E\u5FCD\u53D7\u7740\u9634\u830E\u7684\u81CA\u81ED\uFF0C\u5B83\u4E00\u8FDB\u4E00\u51FA\uFF0C\u4E00\u6D45\u4E00\u6DF1\u7684\u5F98\u5F8A\uFF0C\u5979\u5374\u53EA\u6709\u5728\u8FD9\u5DE8\u5927\u7684\u5C48\u8FB1\u4E2D\u7981\u95ED\u773C\u775B\uFF0C\u751A\u81F3\u4E0D\u6562\u518D\u9ED8\u5FF5\u5A11\u6469\u7684\u540D\u5B57\u3002</p><p>\u800C\u5979\u53E3\u8154\u7684\u5185\u58C1\u662F\u90A3\u6837\u7684\u67D4\u8F6F\uFF0C\u90A3\u91CC\u8212\u9002\u5730\uFF0C\u5C31\u50CF\u5929\u5802\u3002</p><p>\u5979\u77E5\u9053\uFF0C\u5979\u5DF2\u4E0D\u7EAF\uFF0C\u5DF2\u65E0\u8D44\u683C\u518D\u61A7\u61AC\u90A3\u4E9B\u7231\u60C5\u7684\u5584\u7F8E\u3002</p><p>\u4ED6\u629A\u6469\u7740\u5979\u7684\u79C0\u53D1\uFF0C\u7528\u5C3E\u6307\u6E29\u67D4\u5730\u7231\u629A\u5979\u7684\u8033\u6839\u3002\u5728\u5979\u53E3\u8154\u7684\u5305\u56F4\uFF0C\u4ED6\u66F4\u591A\u4F53\u4F1A\u7740\u5FC3\u7075\u5FEB\u611F\u3002\u7531\u4E8E\u8D1D\u73B2\u8FBE\u7684\u53E3\u4EA4\u6280\u5E76\u4E0D\u9AD8\u660E\uFF0C\u56E0\u6B64\u5C04\u7CBE\u7684\u65F6\u95F4\u6BD4\u610F\u6599\u8981\u665A\u51FA\u4E9B\u3002</p><p>\u800C\u5728\u8FD9\u4E9B\u7A7A\u95F2\u7684\u65F6\u95F4\uFF0C\u5965\u6258\u5927\u5E1D\u66F4\u613F\u610F\u8BA9\u81EA\u5DF1\u5E7B\u60F3\u3002\u5979\u7684\u5934\u53D1\u5F77\u4F5B\u53D8\u6210\u67D4\u4EAE\u7684\u9ED1\uFF0C\u77B3\u5B54\u662F\u7070\u84DD\u8272\u7684\u6DF1\u9083\u3002\u7136\u540E\u5728\u5973\u5B69\u7684\u80CC\u4E0A\u662F\u4E00\u5BF9\u7F8E\u4E3D\u7684\u767D\u8272\u7FC5\u8180\u3002</p><p>\u90A3\uFF0C\u662F\u5965\u6258\u5927\u5E1D\u6700\u5927\u7684\u7FFC\u671B\u3002</p><p>\u5F53\u4ED6\u628A\u8165\u81CA\u7684\u7CBE\u6DB2\u55B7\u5728\u5979\u9762\u5E9E\u3001\u9F3B\u5C16\u3001\u5634\u89D2\u3001\u776B\u6BDB\u3002\u4ED6\u53D1\u89C9\u81EA\u5DF1\u7684\u7CBE\u6DB2\u4E0E\u82CD\u5170\u7684\u90A3\u5BF9\u7FC5\u8180\u7684\u989C\u8272\u7ADF\u662F\u4E00\u6837\u7684\u3002</p><p>\u4ED6\u4E8E\u662F\u987E\u4E0D\u4E0A\u5973\u513F\u7684\u6781\u5927\u75DB\u695A\uFF0C\u5F00\u59CB\u611F\u6168\u4E0A\u5929\u5BF9\u81EA\u5DF1\u7684\u51A5\u51A5\u7737\u987E\u3002</p><p>\u2026\u2026</p><p>\u8FD9\u591C\u3002</p><p>\u59EC\u5A1C\u62B1\u7740\u5E03\u7ED2\u73A9\u5076\u606C\u9759\u5165\u7720\u3002</p>',84);function t(c,n){return r}var _=e(a,[["render",t]]);export{_ as default};
