import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const a={},r=p('<h1 id="k" tabindex="-1"><a class="header-anchor" href="#k" aria-hidden="true">#</a> K\u2026</h1><p>\u5B8C\u5A5A\u90A3\u65E5\uFF0C\u4E07\u4EBA\u7A7A\u5DF7\u7684\u76DB\u4E16\u3002</p><p>\u2014\u2014\u300C\u8FE6\u697C\u686B\u6469\u7687\u5B50\uFF0C\u4F60\u613F\u610F\u5A36\u4E9A\u5386\u5C71\u5927\u3002\u7D22\u4F26\u3002\u8D1D\u73B2\u8FBE\u516C\u4E3B\u4E3A\u59BB\u5B50\uFF0C\u5E76\u5B88\u62A4\u5979\u4E00\u751F\u5417\uFF1F\u300D</p><p>\u300C\u6211\u613F\u3002\u300D</p><p>\u2014\u2014\u300C\u4E9A\u5386\u5C71\u5927\u3002\u7D22\u4F26\u3002\u8D1D\u73B2\u8FBE\u516C\u4E3B\uFF0C\u4F60\u662F\u5426\u613F\u610F\u5AC1\u4E8E\u8FE6\u697C\u686B\u6469\u4E3A\u4ED6\u7684\u59BB\uFF0C\u5E76\u76F8\u7231\u4E00\u751F\uFF1F\u300D</p><p>\u300C\u662F\u7684\uFF0C\u6211\u613F\u3002\u300D</p><p>\u5728\u4F17\u795E\u4E4B\u5854\u4E0B\uFF0C\u4ED6\u548C\u5979\u5411\u7267\u5E08\u501F\u795E\u4E4B\u540D\u8D77\u8A93\u3002</p><p>\u4ED6\u671B\u7740\u5979\u7684\u65B0\u5A18\uFF0C\u5E76\u5728\u4E3A\u5979\u5957\u4E0A\u5A5A\u6212\uFF0C\u4F18\u96C5\u7684\u543B\u5979\u3002\u4ED6\u628A\u624B\u6307\u57CB\u5728\u5979\u4F18\u67D4\u7684\u53D1\uFF0C\u5374\u7528\u773C\u89D2\u5077\u671B\u4EBA\u7FA4\u4E2D\u5B51\u7136\u800C\u7ACB\u7684\u59D0\u59D0\u3002</p><p>\u8D1D\u73B2\u8FBE\u5728\u686B\u6469\u7684\u6000\u62B1\u4E2D\u53D8\u6210\u65B0\u5A18\u3002\u4ED6\u7684\u81C2\u5F2F\u4EE4\u5979\u767E\u611F\u4EA4\u96C6\u3002\u543B\u7740\u7684\u65F6\u5019\uFF0C\u89E6\u52A8\u53E3\u8154\u548C\u5507\u7EBF\uFF0C\u5979\u7CBE\u5FC3\u68B3\u7406\u7684\u7FD8\u8D77\u7684\u776B\u6BDB\u597D\u50CF\u5F69\u8679\u7684\u5F27\u7EBF\u3002</p><p>\u90A3\u4E9B\u66FE\u7ECF\u6CBE\u4E0A\u7236\u7687\u767D\u6D4A\u7684\u7CBE\u6DB2\u3002</p><p>\u5728\u8FD9\u77ED\u6682\u6E29\u99A8\uFF0C\u7231\u4EBA\u7684\u543B\u5C31\u50CF\u65E0\u9650\u5149\u5E74\u822C\u5EF6\u7EED\u5230\u6C38\u8FDC\u3002\u7136\u540E\u5979\u7D27\u95ED\u4E0A\u773C\uFF0C\u4E0D\u613F\u610F\u8BA9\u7236\u7687\u770B\u89C1\u5979\u7684\u8868\u60C5\u3002</p><p>\u5165\u591C\u3002</p><p>\u5165\u65B0\u623F\u3002</p><p>\u300C\u628A\u6240\u6709\u7684\u8721\u70DB\u90FD\u7184\u706D\uFF0C\u597D\u5417\uFF1F\u300D</p><p>\u300C\u4E3A\u4EC0\u4E48\uFF0C\u5B9D\u8D1D\uFF1F\u300D</p><p>\u300C\u686B\u6469\uFF0C\u6211\u2026\u2026\u6211\u4E0D\u559C\u6B22\uFF0C\u706F\u706B\u3002\u300D</p><p>\u300C\u5514\u2026\u2026\u6211\uFF0C\u4E5F\u4E0D\u559C\u6B22\u3002\u300D</p><p>\u686B\u6469\u8BF4\u7684\u5B9E\u8BDD\u3002\u4E0D\u559C\u6B22\u6F06\u9ED1\u4E2D\u7684\u4EAE\uFF0C\u662F\u56E0\u4E3A\u5341\u5E74\u3002</p><p>\u800C\u8D1D\u73B2\u8FBE\u4EA6\u662F\u4ECE\u5728\u67D0\u4E2A\u591C\u665A\u4EE5\u540E\uFF0C\u7A81\u7136\u5BB3\u6015\u6447\u66F3\u7684\u70DB\u3002</p><p>\u65B0\u5A5A\u7684\u5973\u5B50\uFF0C\u603B\u662F\u4E0D\u5E0C\u671B\u67D0\u4E9B\u753B\u9762\u73B0\u51FA\u6765\uFF0C\u574F\u4E86\u8FD9\u591C\u7684\u9999\u8273\u3002</p><p>\u8FD9\u591C\u9999\u8273\u3002</p><p>\u5965\u6258\u5927\u5E1D\u559D\u4E86\u8BB8\u591A\uFF0C\u5728\u7687\u5EA7\u4E0A\u5531\u7740\u6B4C\u3002\u67B7\u697C\u82CD\u5170\u662F\u4E0D\u559D\u7684\uFF0C\u65E0\u61C8\u53EF\u51FB\u3002</p><p>\u591C\u6DF1\u65F6\uFF0C\u5979\u53C8\u5B64\u7ACB\u5728\u9AD8\u5854\u811A\u4E0B\u7684\u796D\u575B\uFF0C\u4E00\u5BF9\u7FBD\u7FFC\u5728\u591C\u8272\u4E2D\u5206\u5916\u662D\u5F70\u3002\u4EF0\u671B\u7740\u5854\u5C16\u4EE5\u4E0A\u7684\u665A\u7A7A\uFF0C\u6853\u53E4\u7684\u661F\u8FB0\u6392\u5217\u9635\u578B\u3002\u7F2D\u4E71\u800C\u6DF1\u5965\u3002\u5979\u627E\u5230\u6700\u4EAE\u4E00\u8BB0\u6D41\u5149\uFF0C\u7948\u6C42\u5B83\u4E3A\u5F1F\u5F1F\u8D50\u798F\u3002</p><p>\u5979\u90A3\u8654\u8BDA\u4E13\u6CE8\u7684\u795E\u60C5\uFF0C\u5965\u6258\u5927\u5E1D\u662F\u5F88\u559C\u6B22\u7684\u3002</p><p>\u4E0D\u8FDC\u5904\u662F\u5C0F\u6811\u6797\u3002\u84DD\u7684\u6708\u8272\u4E0B\uFF0C\u53EF\u89C1\u6843\u82B1\u76DB\u653E\u6210\u7C89\u8273\u7684\u4E00\u7247\u3002\u8FD8\u6709\u8424\u706B\u866B\u5C31\u50CF\u865A\u6D6E\u7684\u773C\u3002\u90A3\u4E9B\u7C89\u8273\u7684\u82B1\uFF0C\u8150\u574F\u7684\u8349\u603B\u4F1A\u62DB\u60F9\u773C\u795E\u3002</p><p>\u4E0D\u77E5\u540D\u7684\u9E23\u866B\u548C\u86D9\u7C7B\u6B63\u53D1\u51FA\u5FAE\u5F31\u7684\u58F0\u606F\uFF0C\u5F7C\u6B64\u5B89\u7136\u5728\u8FD9\u5B63\u8282\u7684\u547D\u6570\u3002</p><p>\u547D\u6570\u3002</p><p>\u686B\u6469\u628A\u4E00\u5343\u4E2A\u543B\u5370\u5728\u8D1D\u73B2\u8FBE\u7684\u5168\u8EAB\uFF0C\u6253\u5F00\u5979\u77DC\u6301\u7684\u5C01\u5370\u3002\u8D8A\u53D1\u6FC0\u8361\u7684\u547B\u541F\u4E2D\uFF0C\u611F\u53D7\u7740\u8EAB\u4F53\u8FF7\u4EBA\u8282\u594F\u3002\u706D\u4E86\u7EA2\u70DB\uFF0C\u5408\u4E0A\u7F57\u5E10\u662F\u660F\u6697\u7684\u7EA2\uFF0C\u60C5\u6B32\u70E6\u70E7\u3002</p><p>\u76F8\u7231\u7684\u4EBA\u8BE5\u662F\u7528\u5FC3\u53BB\u54C1\u5473\u5BF9\u65B9\uFF0C\u4E00\u70B9\u4E00\u6EF4\u3002</p><p>\u800C\u5E74\u5C11\u7684\u4ED6\u8FD8\u53EA\u5F97\u4F1A\u7528\u624B\u54C1\u5473\u7740\u5979\uFF0C\u5F97\u5BF8\u8FDB\u5C3A\u3002</p><p>\u5979\u7684\u58F0\u7EBF\u5C16\uFF0C\u6BCF\u4E00\u6B21\u5A07\u541F\u90FD\u4EE4\u4ED6\u547C\u5438\u4E0D\u606F\u3002\u4ED6\u60DF\u6709\u7528\u5634\u518D\u5C06\u5979\u5C01\u5370\uFF0C\u8FD9\u6837\u624D\u4E0D\u4F1A\u81F3\u4E8E\u6218\u6B7B\u5728\u5979\u4E73\u95F4\u7684\u67D4\u8F6F\u4E4B\u5730\u3002</p><p>\u5979\u5149\u6ED1\u7684\u8EAB\u578B\u50CF\u662F\u4E00\u5C3E\u9C7C\uFF0C\u8361\u8FDB\u60C5\u6B32\u6D2A\u6C34\u3002\u4ED6\u770B\u4E0D\u89C1\uFF0C\u53EA\u6709\u7528\u624B\u892A\u53BB\u5979\u7684\u5E95\u88E4\u3002\u74E6\u89E3\u5979\u6700\u7EC8\u9632\u7EBF\uFF0C\u800C\u540E\u91D1\u98CE\u7389\u9732\u3002</p><p>\u5979\u662F\u534A\u63A8\u534A\u5C31\uFF0C\u5374\u96BE\u63A9\u60CA\u60F6\u3002</p><p>\u4ED6\u629A\u6469\u7740\u5979\u7684\u803B\u6BDB\u548C\u79C0\u53D1\uFF0C\u8FD9\u4EE4\u5979\u66F4\u9677\u60CA\u60F6\u3002\u4ED6\u4E00\u76F4\u5728\u8F6F\u8F6F\u7684\u6BDB\u53D1\u95F4\u6E29\u67D4\u5BF8\u8FDB\uFF0C\u76F4\u5230\u4ED6\u611F\u89C9\u5230\u5979\u7684\u8EAB\u4F53\u5F7B\u5E95\u5F00\u59CB\u71E5\u70ED\u6E7F\u6DA6\u3002\u5979\u9002\u65F6\u5730\u543B\u4ED6\uFF0C\u4ED6\u611F\u5230\u5979\u7684\u6CEA\u548C\u6DEB\u6C34\u540C\u6837\u5F25\u8DB3\u73CD\u8D35\u3002</p><p>\u9ED1\u6697\u662F\u66A7\u6627\u7684\u3002\u5BFB\u4E86\u5F88\u4E45\u624D\u89C5\u5230\u673A\u8981\u3002\u5A07\u5AE9\u5730\u5C31\u50CF\u5E26\u7740\u6668\u9732\u7684\u8587\uFF0C\u53C8\u50CF\u4E86\u65E0\u4EBA\u8FF9\u7684\u8FF7\u4E4B\u6CBC\u6CFD\u3002\u5206\u5F00\u4E24\u7247\u82B1\u74E3\uFF0C\u4E0D\u77E5\u662F\u5728\u7231\u60DC\u8FD8\u662F\u6467\u6BC1\u8FD9\u5723\u6D01\u8106\u5F31\u7684\u7F8E\u3002</p><p>\u5979\u5728\u4ED6\u80A9\u8180\u523B\u4E0B\u6307\u7532\u7684\u6293\u75D5\uFF0C\u5728\u90A3\u5C42\u8106\u5F31\u7684\u819C\u5373\u5C06\u652F\u79BB\u7684\u5173\u5934\u3002\u539F\u6765\u7ADF\u662F\u5982\u6B64\u7684\u5267\u75DB\u3002\u5B83\u50CF\u4E00\u6746\u67AA\uFF0C\u76F4\u523A\u5979\u7684\u4E2D\u67A2\uFF0C\uFF1B\u4EE4\u5979\u60E7\u6015\uFF0C\u60F3\u9003\uFF0C\u60F3\u8981\u5C16\u53EB\uFF0C\u5374\u53C8\u542B\u7740\u671F\u8BB8\u548C\u67D0\u79CD\u6E34\u671B\u3002</p><p>\u5F53\u5973\u4EBA\u9677\u8FDB\u8FD9\u6837\u7684\u77DB\u76FE\u4E2D\uFF0C\u4F53\u6DB2\u4E8E\u662F\u6210\u4E3A\u6700\u597D\u9053\u5177\u3002\u5979\u5C3D\u53EF\u80FD\u591A\u5F97\u5206\u6CCC\u6CEA\u548C\u6DEB\u6DB2\u3002</p><p>\u90A3\u52BF\u5FC5\u6D17\u5C3D\u4E00\u4E2A\u5E74\u4EE3\u7684\u94C5\u534E\uFF0C\u540C\u65F6\u4EA6\u6709\u6548\u5730\u6DA6\u6ED1\u9634\u830E\u3002</p><p>\u300C\u4E00\uFF0C\u4E8C\uFF0C\u4E09\u2014\u2014\u300D\u5979\u9ED8\u6570\u7740\uFF0C\u731C\u6D4B\u7740\u4ED6\u7684\u8282\u594F\u3002\u5C31\u50CF\u63E3\u6D4B\u547D\u6570\uFF0C\u5979\u50CF\u8774\u8776\u4E00\u6837\u5F20\u5F00\u81EA\u5DF1\u7684\u8EAB\u4F53\uFF0C\u5F77\u4F5B\u98DE\u5760\u3002</p><p>\u76F4\u5230\u90A3\u4E9B\u6E05\u6F88\u7684\u6DB2\u4F53\u88AB\u6BB7\u7EA2\u7684\u8840\u8986\u76D6\uFF0C\u4E00\u9635\u706B\u8FA3\u5730\u94BB\u5FC3\u75DB\u3002\u5979\u53C8\u53D8\u5F97\u50CF\u817E\u8513\u4E00\u6837\u7F20\u5728\u686B\u6469\u7684\u8EAB\u4E0A\uFF0C\u518D\u4E0D\u53EF\u4EE5\u5206\u3002</p><p>\u518D\u4E0D\u53EF\u4EE5\u662F\u5C11\u5973\u3002</p><p>\u686B\u6469\u770B\u4E0D\u89C1\u5979\u7684\u8868\u60C5\uFF0C\u5374\u4F53\u5473\u5F97\u5230\u5979\u7684\u75DB\u3002\u4ED6\u8F7B\u8F7B\u5730\u79FB\uFF0C\u60DF\u6050\u8FC7\u4E8E\u6FC0\u70C8\u7684\u523A\u6FC0\u6467\u6BC1\u5979\u5A07\u5AE9\u7684\u5668\u5B98\u3002\u5E76\u629A\u6170\u5979\u9634\u8482\uFF0C\u5C3D\u529B\u6E29\u99A8\u3002</p><p>\u4ED6\u538B\u5728\u5979\u7684\u8EAB\u4E0A\uFF0C\u5979\u8FF7\u604B\u4ED6\u5BBD\u539A\u7684\u80A9\u8180\u548C\u5766\u8361\u80F8\u8154\u4E0A\u7684\u6676\u83B9\u6C57\u73E0\u3002\u5979\u7528\u7389\u624B\u6478\u8FC7\u4ED6\u6BCF\u4E00\u5BF8\u80CC\u808C\uFF0C\u53D1\u73B0\u4ED6\u88AB\u5979\u6307\u7532\u6293\u7834\u6E17\u8840\u3002\u7136\u540E\u7ADF\u9732\u51FA\u5E78\u798F\u7B11\u610F\u3002\u4ED6\u770B\u4E0D\u5230\uFF0C\u4F9D\u7136\u8F7B\u7F13\u62BD\u9001\uFF0C\u968F\u5373\u989C\u5C04\u3002</p><p>\u90A3\u4E00\u6B21\u7684\u6625\u5BB5\uFF0C\u662F\u4ED6\u548C\u5979\u7684\u521D\u6B21\u3002\u686B\u6469\u5F00\u59CB\u8FF7\u4FE1\u5E78\u798F\u662F\u6C38\u6052\u7684\u540C\u4E49\u3002</p><p>\u6B21\u65E5\u7684\u6668\u5149\u4E2D\uFF0C\u8D1D\u73B2\u8FBE\u671B\u671B\u8EAB\u8FB9\u719F\u7761\u7684\u7537\u5B50\uFF0C\u671B\u671B\u5E8A\u8925\u4E0A\u954C\u8BB0\u7684\u6BB7\u7EA2\u3002\u5979\u5F00\u59CB\u89C9\u5F97\u82E6\u77ED\u3002</p><p>\u51E0\u5206\u8C03\u76AE\u5730\u5728\u4ED6\u8EAB\u4E0A\u62E7\u634F\uFF0C\u7136\u540E\u60F3\u5230\u81EA\u5DF1\u7684\u7236\u7687\u3002</p><p>\u5E8A\u8925\u4E0A\u954C\u8BB0\u7684\u7EA2\u8272\u50CF\u82B1\uFF0C\u5E8A\u8FB9\u62E8\u843D\u4E24\u4EBA\u7684\u8863\u88F3\u6563\u6563\u4E71\u4E71\u3002</p><p>\u4E00\u8BB0\u82CD\u767D\u5E7D\u53F9\uFF0C\u4F4E\u5934\u62FE\u8D77\uFF0C\u628A\u8863\u88F3\u53E0\u597D\uFF0C\u6709\u4ED6\u7684\u5473\u9053\u3002</p><p>\u4E00\u53EA\u6C34\u6676\u978B\u5728\u9633\u5149\u4E0B\u95EA\u51FA\u7EEE\u4E3D\u5149\u6CFD\uFF0C\u800C\u53E6\u4E00\u53EA\u5374\u4E0D\u5728\u4E86\u3002</p>',49);function t(c,n){return r}var _=e(a,[["render",t]]);export{_ as default};
