import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const a={},r=p('<h1 id="o" tabindex="-1"><a class="header-anchor" href="#o" aria-hidden="true">#</a> O\u2026</h1><p>\u5728\u59D0\u5F1F\u5206\u79BB\u7684\u4E5D\u4E2A\u6708\u65F6\u95F4\uFF0C\u686B\u6469\u7ED9\u82CD\u5170\u5199\u4E86\u4FE1\u3002</p><p>\u90A3\u4E9B\u8BF8\u5982\uFF1A\u59D0\u59D0\u5728\u6218\u573A\u53AE\u6740\u800C\u6211\u5374\u4E0D\u80FD\u5E76\u80A9\u800C\u6218\uFF0C\u4F60\u7684\u5DE8\u96C0\u5251\u9971\u996E\u9B54\u65CF\u8840\uFF0C\u6211\u4F53\u5185\u90A3\u4E9B\u6FC0\u6602\u7684\u4FE1\u5FF5\u548C\u6218\u610F\uFF0C\u67D0\u5929\u7EC8\u88AB\u8FD9\u5E78\u798F\u7684\u751F\u6D3B\u8017\u53BB\u6B86\u5C3D\u3002</p><p>\u59BB\u5B50\u662F\u6E29\u67D4\u7684\uFF0C\u53EA\u662F\u5E38\u6709\u6101\u4E91\u9501\u4F4F\u7709\u3002\u5965\u6258\u662F\u4EB2\u548C\u7684\uFF0C\u5F85\u6211\u5982\u5B50\u3002\u800C\u6211\u5374\u4ECE\u4E0D\u613F\u4EE5\u4ED6\u4E3A\u7236\u4EB2\u3002\u56E0\u4E3A\u686B\u6469\u7684\u8EAB\u4F53\u5185\uFF0C\u6D41\u6DCC\u7740\u548C\u4F60\u4E00\u6837\u9AD8\u50B2\u7684\u8840\u3002\u6211\u4EEC\u662F\u4E00\u5F0F\u4E24\u4EFD\u4E0D\u53EF\u5206\u7684\u3002</p><p>\u59D0\u59D0\uFF0C\u6211\u77E5\u9053\u51ED\u85C9\u4F60\u7684\u667A\u6167\u548C\u6B66\u6280\u662F\u65E0\u8BBA\u5982\u4F55\u4E0D\u81F3\u9677\u5165\u5371\u9669\u3002\u4F46\u662F\u5929\u5DF2\u8F6C\u5BD2\u4E86\uFF0C\u53EF\u6709\u8BB0\u5F97\u52A0\u8863\uFF1F</p><p>\u59BB\u4E09\u4E2A\u6708\u524D\u6000\u5B55\u4E86\u3002\u6211\u60F3\u5982\u679C\u662F\u5973\u5B69\uFF0C\u90A3\u4E00\u5B9A\u4F1A\u5F88\u50CF\u5F88\u50CF\u4F60\u3002\u4F60\u66FE\u7ECF\u8BF4\u6B64\u751F\u4E0D\u4F1A\u5B8C\u5A5A\uFF0C\u6211\u548C\u59BB\u7EA6\u5B9A\u628A\u6211\u4EEC\u7684\u7B2C\u4E00\u4E2A\u5B69\u5B50\u8D60\u4F60\uFF0C\u8BA9\u5979\u7EE7\u627F\u5929\u7A7A\u7684\u7687\u4F4D\u3002\u8BA9\u5979\u62EF\u6551\u5929\u7A7A\u3002\u53EA\u662F\u4E0D\u77E5\u9053\uFF0C\u8FD9\u53EF\u7231\u7684\u5B69\u5B50\u662F\u5426\u6709\u8D44\u683C\u7FFC\u671B\u3002</p><p>\u5728\u8FD9\u4E9B\u6377\u62A5\u9891\u4F20\u7684\u65E5\u5B50\uFF0C\u6211\u7684\u8840\u6DB2\u4FF1\u5728\u6CB8\u817E\u3002\u59D0\u59D0\uFF0C\u6211\u4E3A\u4F60\u611F\u5230\u9A84\u50B2\u3002\u59BB\u5B50\u5C06\u5206\u5A29\u4E86\uFF0C\u6211\u671F\u5F85\u7740\u770B\u5979\u90A3\u53CC\u7070\u84DD\u8272\u7684\u773C\uFF0C\u8FE6\u84DD\u65CF\u7684\u7687\u5BA4\u552F\u4E00\u4E0D\u4F1A\u6539\u53D8\u7684\u9057\u4F20\u7279\u8D28\u3002\u90A3\u53CC\u9AD8\u8D35\u6DF1\u9083\u7684\u77B3\u3002</p><p>\u6211\u548C\u59BB\u5546\u91CF\u8D50\u540D\u4E3A\u300C\u77B3\u300D\u3002\u5979\u662F\u597D\u7684\uFF0C\u4F60\u4F1A\u559C\u6B22\u5979\u5417\uFF1F\u6211\u7684\u59D0\u59D0\u3002</p><p>\u8FD9\u4E9B\u5929\uFF0C\u59BB\u5E38\u5E38\u5728\u4ED6\u7236\u4EB2\u90A3\u91CC\u5446\u5230\u5F88\u8FDF\u3002\u6211\u60F3\u5965\u6258\u5927\u5E1D\u4E5F\u662F\u5982\u6B64\u6FC0\u52A8\u5730\u61A7\u61AC\u7740\u5F53\u5916\u7956\u7684\u60C5\u5F62\u3002\u6709\u7684\u65F6\u5019\uFF0C\u6211\u90FD\u4F1A\u770B\u89C1\u4ED6\u8F7B\u8F7B\u62CD\u6253\u59BB\u9686\u8D77\u7684\u8179\uFF0C\u7B11\u5730\u5927\u58F0\u3002\u800C\u5F53\u4E00\u4E2A\u4EBA\u5728\u5C45\u5BA4\u7684\u665A\u4E0A\uFF0C\u4F1A\u542C\u89C1\u5BD2\u98CE\u4E2D\u9AD8\u5854\u4E0A\u9501\u94FE\u98A4\u6296\u7684\u58F0\u97F3\uFF0C\u6211\u672C\u662F\u8BA8\u538C\u90A3\u54CD\u52A8\u7684\uFF0C\u800C\u73B0\u5728\u5B83\u7ADF\u50CF\u4E00\u9996\u6B22\u5FEB\u7684\u6B4C\u9882\uFF0C\u795D\u798F\u6211\u4EEC\u7684\u77B3\uFF0C\u65E9\u65E9\u964D\u751F\u3002</p><p>\u2026\u2026</p><p>\u800C\u7FCE\u7684\u552F\u4E00\u4E00\u5C01\u56DE\u4FE1\u662F\u7B80\u6D01\u7684\u2014\u2014</p><p>\u300C\u80DC\u51FA\u3002\u8FD4\u7A0B\u62DC\u4E9A\u65AF\u3002\u5DF2\u5728\u5F52\u9014\u3002\u300D</p><p>\u62DC\u4E9A\u65AF\u7687\u5BB6\u536B\u519B\u961F\u662F\u9A91\u9A6C\u6216\u4E58\u9A86\u9A7C\u3002\u800C\u5929\u7A7A\u57CE\u7684\u5C06\u58EB\u5219\u4EE5\u5927\u9E1F\u4E3A\u5750\u9A91\u3002\u53EA\u662F\u6218\u4E8B\u7D27\u5BC6\uFF0C\u5927\u6377\u7684\u5F52\u9014\uFF0C\u8FE6\u84DD\u65CF\u7684\u6218\u58EB\u4F1A\u8BA9\u5750\u9A91\u4F11\u606F\uFF0C\u9009\u62E9\u6B65\u884C\u3002\u5927\u9E1F\u4EEC\u5C31\u8DDF\u5728\u5927\u519B\u884C\u8FDB\u7684\u8DEF\u7EBF\uFF0C\u4E8E\u534A\u7A7A\u5A01\u6B66\u7FF1\u7FD4\u3002</p><p>\u539F\u672C\uFF0C\u4ECE\u4F4D\u4E8E\u897F\u7EBF\u8352\u539F\u7684\u6218\u573A\u8FD4\u56DE\u62DC\u4E9A\u65AF\u7684\u8DEF\u9014\u9700\u8981\u4E03\u591C\u516B\u5929\u3002\u53EF\u662F\u90A3\u591C\uFF0C\u6770\u5F17\u900A\u5B50\u7235\u5728\u884C\u8425\u7684\u5E10\u7BF7\u5185\u5B89\u5BDD\uFF0C\u7136\u540E\u4E0D\u53EF\u601D\u8BAE\u5730\u5BFF\u7EC8\u6B63\u5BDD\u3002</p><p>\u82CD\u5170\u53EF\u4EE5\u53D1\u8A93\u5B50\u7235\u7684\u6B7B\u4E0E\u5DF1\u65E0\u5E72\u3002\u5979\u60F3\uFF0C\u6216\u8BB8\u662F\u4E4B\u524D\u4ED6\u50AC\u52A8\u706B\u7CFB\u9B54\u6CD5\u8017\u5C3D\u592A\u591A\u7CBE\u6C14\uFF0C\u53C8\u6216\u8005\u7F18\u4E8E\u6CC4\u9732\u5929\u673A\u3002</p><p>\u5C31\u5730\u4E3E\u884C\u7684\u9686\u91CD\u846C\u793C\uFF0C\u5979\u770B\u7740\u4ED6\u90A3\u6591\u767D\u7684\u987B\u53D1\uFF0C\u7ADF\u53C8\u60F3\u8D77\u90A3\u65E5\u773C\u8272\u53E6\u7C7B\u7684\u5927\u796D\u53F8\u3002\u5979\u5FFD\u7136\u89C9\u5F97\u60E7\u6015\u6B7B\u4EA1\uFF0C\u8870\u8D25\u67AF\u7AED\u7684\u6B7B\u4EA1\u3002</p><p>\u5F88\u9057\u61BE\uFF0C\u4E5F\u6B63\u662F\u56E0\u4E3A\u8FD9\u846C\u793C\u7684\u803D\u8BEF\uFF0C\u5979\u9519\u8FC7\u770B\u89C1\u521D\u751F\u7684\u77B3\u3002</p><p>\u800C\u5979\u6216\u8BB8\u6C38\u8FDC\u4E0D\u4F1A\u77E5\u9053\uFF0C\u8001\u8FC8\u7684\u5B50\u7235\u771F\u6B63\u7684\u6B7B\u56E0\uFF0C\u53EA\u4E0D\u8FC7\u662F\u5965\u6258\u5927\u5E1D\u9065\u9065\u5BC4\u4E0A\u7684\u4E00\u7C92\u9ED1\u8272\u8721\u4E38\u3002</p><p>\u4ED6\u5B8C\u6210\u4E86\u4E00\u573A\u5B8C\u7F8E\u7684\u4F5C\u79C0\uFF0C\u63CF\u7ED8\u51FA\u5982\u6B64\u52A8\u4EBA\u7684\u4E00\u4E2A\u4F20\u8BF4\uFF0C\u7136\u540E\u5C31\u88AB\u5927\u5E1D\u6DD8\u6C70\uFF0C\u88AB\u5927\u5730\u6536\u7EB3\u3002</p><p>\u8FDE\u5965\u6258\u5927\u5E1D\u81EA\u5DF1\u4E5F\u4E0D\u77E5\u9053\uFF0C\u6709\u671D\u4E00\u65E5\uFF0C\u4ED6\u662F\u5426\u4F1A\u8DEA\u5728\u5B50\u7235\u7684\u7075\u724C\u4E4B\u524D\uFF0C\u9762\u65E0\u8868\u60C5\u5374\u5FC3\u5982\u6F6E\u6D8C\u5730\u8BF4\uFF1A\u300C\u6211\u6740\u4F60\uFF0C\u5E76\u4E0D\u662F\u56E0\u4F60\u7684\u7F6A\u3002\u800C\u662F\u4E3A\u4E86\u786E\u4FDD\u4E07\u65E0\u4E00\u5931\u7684\u63D2\u8FDB\u82CD\u5170\u7684\u9634\u9053\uFF0C\u7ED9\u5979\u9AD8\u6F6E\u3002\u300D</p><p>\u2026\u2026</p><p>\u5965\u6258\u5927\u5E1D\u3002</p><p>\u4ED6\u7684\u9634\u830E\u5728\u8D1D\u73B2\u8FBE\u7684\u540E\u5EAD\u5185\u8361\u6C14\u56DE\u80A0\uFF0C\u71C3\u70E7\u7236\u4EB2\u7684\u7231\u706B\u711A\u5316\u5979\u7684\u6BCF\u4E00\u5BF8\u3002\u5979\u80CC\u8FC7\u8EAB\uFF0C\u505A\u6210\u6BCD\u72AC\u7684\u59FF\u6001\u3002</p><p>\u9686\u8D77\u7684\u809A\u5B50\u8D34\u5728\u5730\u9762\uFF0C\u90A3\u91CC\u9762\u85CF\u7740\u7684\u662F\u5C48\u8FB1\u7684\u9AA8\u8089\u3002</p><p>\u5965\u6258\u5927\u5E1D\u628A\u5973\u513F\u7684\u5904\u5973\u9988\u8D60\u7ED9\u686B\u6469\uFF0C\u5374\u5728\u90A3\u4E4B\u540E\u5978\u6DEB\u5979\u767E\u5343\u6B21\u3002\u8D77\u521D\u7684\u65F6\u5019\uFF0C\u8D1D\u73B2\u8FBE\u4F1A\u54C0\u6C42\u4ED6\uFF0C\u4ED6\u8BF4\uFF0C\u7B49\u4F60\u6000\u5B55\uFF0C\u6211\u5C31\u4E0D\u518D\u5978\u4F60\u3002</p><p>\u540E\u6765\uFF0C\u8D1D\u73B2\u8FBE\u6000\u5B55\u4E86\u3002\u4ED6\u4FBF\u8BF4\u7B49\u4F60\u751F\u4E0B\u4ED6\u7684\u5B69\u5B50\uFF0C\u6211\u4FBF\u518D\u4E0D\u5978\u4F60\u3002</p><p>\u7B49\u5230\u8D1D\u73B2\u8FBE\u5373\u5C06\u4E34\u76C6\u4E86\u3002\u4ED6\u8BF4\uFF0C\u6211\u4FBF\u8F7B\u8F7B\u5730\uFF0C\u8F7B\u8F7B\u5730\u5978\u4F60\u3002</p><p>\u5973\u4EBA\u662F\u67D4\u5F31\u7684\uFF0C\u4E0D\u77E5\u9053\u6297\u4E89\u7684\u3002\u5979\u53EA\u6709\u65E0\u9650\u6B21\u4F9B\u4ED6\u6CC4\u6B32\uFF0C\u7528\u81EA\u5DF1\u6BCF\u4E00\u4E2A\u6D1E\u7A74\u5BB9\u7EB3\u4ED6\u767D\u6D4A\u7684\u7CBE\u6DB2\uFF1B\u9690\u7792\u81EA\u5DF1\u7684\u592B\u541B\u3002</p><p>\u6BCF\u4E00\u6B21\uFF0C\u686B\u6469\u5439\u706D\u8721\u70DB\uFF0C\u4E3A\u5979\u76D6\u4E0A\u88AB\u8925\u8F7B\u8F7B\u543B\u5979\u7684\u65F6\u5019\uFF0C\u8D1D\u73B2\u8FBE\u53EA\u6709\u628A\u9762\u988A\u8D34\u8FDB\u4ED6\u7684\u80F8\u819B\uFF0C\u5E0C\u671B\u4ED6\u611F\u89C9\u5230\u5E7D\u6697\u4E2D\u5979\u5728\u4ED6\u5FC3\u53E3\u5782\u6CEA\u3002</p><p>\u6709\u7684\u65F6\u5019\uFF0C\u5965\u6258\u5927\u5E1D\u62BD\u63D2\u5728\u5979\u7684\u9634\u9053\u4E4B\u4E2D\uFF0C\u4F1A\u4E00\u8FB9\u8D5E\u53F9\u5979\u7684\u7EDD\u7F8E\uFF0C\u5E76\u544A\u8BC9\u5979\u5F53\u4E00\u4E2A\u5973\u4EBA\u5728\u53D1\u60C5\u547B\u541F\u7684\u65F6\u5019\uFF0C\u65E0\u8BBA\u9AD8\u8D35\u7684\u5929\u795E\u6216\u8005\u5351\u5FAE\u7684\u5996\u9B45\uFF0C\u65E0\u8BBA\u9762\u5BF9\u60C5\u4EBA\u8FD8\u662F\u517D\u5978\u90FD\u662F\u4E00\u6837\u6700\u7F8E\u7684\u3002</p><p>\u4E0D\u77E5\u9053\u4E3A\u4EC0\u4E48\uFF0C\u8D1D\u73B2\u8FBE\u65E9\u5C31\u4E0D\u518D\u6709\u773C\u6CEA\u3002\u54EA\u6015\u6DEB\u6C34\u6D93\u6D93\u6D41\u6DCC\u6210\u6EAA\u6D41\uFF0C\u4E73\u5934\u5206\u6CCC\u51FA\u767D\u6C41\uFF1B\u54EA\u6015\u5373\u5C06\u5206\u5A29\u7684\u5B50\u5BAB\u5E38\u662F\u4E00\u9635\u7684\u75BC\u75DB\u3002</p><p>\u5979\u4E0D\u66FE\u8BA9\u773C\u6CEA\u6D41\u4E0B\u6765\u3002\u5E76\u975E\u575A\u5F3A\uFF0C\u800C\u662F\u6700\u5815\u843D\u7684\u9EBB\u6728\u3002</p><p>\u518D\u591A\u7684\u6D41\u6CEA\uFF0C\u7EB5\u7136\u6D17\u51C0\u5468\u8EAB\u7CBE\u6591\uFF1B\u5374\u4E0D\u4F1A\u53D8\u6210\u5229\u5251\uFF0C\u65A9\u65AD\u8C01\u7684\u8F93\u7CBE\u7BA1\u3002</p>',33);function t(o,c){return r}var _=e(a,[["render",t]]);export{_ as default};
