import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const a={},r=p('<h1 id="s" tabindex="-1"><a class="header-anchor" href="#s" aria-hidden="true">#</a> S\u2026</h1><p>\u4F17\u795E\u4E4B\u5854\u3002\u9AD8\u4E03\u5341\u4E03\u3002</p><p>\u82CD\u5170\u98DE\u62B5\u7687\u57CE\u7684\u65F6\u5019\uFF0C\u686B\u6469\u5DF2\u4E0A\u5230\u4E94\u5341\u56DB\u5C42\u3002</p><p>\u5965\u6258\u5728\u4E03\u5341\u516D\u5C42\u7EE7\u7EED\u5978\u6DEB\uFF0C\u5E76\u4EE5\u4E3A\u5C38\u4F53\u6D41\u51FA\u7684\u4F53\u6DB2\u662F\u7F8E\u7684\uFF0C\u7EA2\u8272\u767D\u8272\u3002</p><p>\u82CD\u5170\u8D76\u5728\u8FD4\u57CE\u7684\u5927\u519B\u4E4B\u524D\u5C55\u7FFC\u800C\u6765\uFF0C\u5979\u770B\u89C1\u4F17\u795E\u4E4B\u5854\u4E0A\u7A7A\u7B3C\u7F69\u7740\u60E8\u6DE1\u7684\u4E91\u3002</p><p>\u5979\u8FF3\u81EA\u98DE\u5411\u5B64\u9AD8\u7684\u5854\u5C16\uFF0C\u75BE\u98CE\u8427\u745F\u7684\u5929\u6C14\uFF0C\u52AA\u529B\u8BA9\u81EA\u5DF1\u5E73\u9759\u4E0B\u6765\u3002</p><p>\u5854\u9876\u6709\u56DB\u9762\u655E\u5F00\u7684\u7A97\uFF0C\u5374\u53EA\u900F\u8FDB\u5C11\u8BB8\u7684\u5149\u7EBF\uFF0C\u770B\u4E0D\u6E05\u5185\u5728\u7684\u7384\u673A\u3002\u82CD\u5170\u5728\u865A\u7A7A\u60AC\u6D6E\u4E00\u5468\uFF0C\u73AF\u987E\u56DB\u9762\u3002\u9AD8\u7A7A\u7684\u98CE\u603B\u662F\u51DB\u51BD\u7684\uFF0C\u6709\u4E9B\u8D39\u529B\u5730\u6491\u5F00\u80CC\u4E0A\u7684\u53CC\u7FFC\uFF0C\u90A3\u4E00\u5934\u9ED1\u53D1\u4FBF\u9A6D\u98CE\u800C\u821E\u3002</p><p>\u5979\u611F\u5230\u4E00\u70B9\u5BD2\uFF0C\u4FBF\u7531\u5317\u9762\u7684\u7A97\u76F4\u63A5\u6B65\u5165\u5854\u5185\u3002</p><p>\u5979\u8C28\u614E\u5730\uFF0C\u63E1\u7740\u5DE8\u96C0\u5251\uFF0C\u5728\u5E7D\u6697\u7684\u89C6\u91CE\u4E2D\u6B65\u5C65\u8F7B\u76C8\u3002</p><p>\u516B\u6839\u9F50\u8EAB\u9AD8\u7684\u77F3\u67F1\u6392\u6210\u56DB\u4E2A\u9510\u89D2\u7684\u661F\u8F89\u9635\u578B\uFF0C\u5728\u4E2D\u5FC3\u7684\u65B9\u4F4D\u7ADF\u6446\u653E\u4E00\u76CF\u6CB9\u706F\u3002\u706F\u706B\u4ECD\u7136\u6709\u4E00\u4E1D\u7684\u6696\u610F\uFF0C\u4E00\u7EBF\u5149\u534E\u3002</p><p>\u5979\u5360\u7740\u5251\uFF0C\u5C4F\u606F\u51DD\u671B\uFF0C\u800C\u6CA1\u6709\u63A5\u8FD1\u5B83\u3002\u56E0\u4E3A\u5979\u89C9\u5F97\uFF0C\u5B83\u5C31\u50CF\u67D0\u4E2A\u6F5C\u5728\u7684\u5371\u9669\u8BAF\u53F7\u3002\u8033\u8FB9\u4F20\u6765\u9AD8\u7A7A\u9AA4\u9E23\u7684\u98CE\u58F0\uFF0C\u4F20\u6765\u82E5\u6709\u82E5\u65E0\u7684\u78B0\u649E\u58F0\u606F\u3002</p><p>\u5728\u8FD9\u964C\u751F\u800C\u8BE1\u5F02\u7684\u73AF\u5883\uFF0C\u5979\u4E0D\u4F1A\u5141\u8BB8\u81EA\u5DF1\u6709\u54EA\u6015\u4E00\u70B9\u7684\u6020\u6162\u3002\u5979\u662F\u65E0\u61C8\u53EF\u51FB\u7684\u5973\u4EBA\uFF0C\u65E0\u8BBA\u5916\u578B\u6216\u8005\u6C14\u8D28\u98CE\u683C\u3002</p><p>\u51ED\u85C9\u5DE8\u96C0\u5251\u7684\u5149\u5F71\uFF0C\u82CD\u5170\u89C2\u5BDF\u7740\u6BCF\u4E00\u5904\u53EF\u4EE5\u770B\u89C1\u7684\u4E8B\u7269\uFF0C\u60F3\u67E5\u627E\u51FA\u4EFB\u4F55\u4E00\u4E2A\u6F5C\u4F0F\u5371\u9669\u7684\u6240\u5728\u3002</p><p>\u706F\u82AF\u662F\u504F\u5411\u4E00\u8FB9\u7684\uFF0C\u6D78\u5728\u6CB9\u4E2D\u5FAE\u5F31\u7684\u71C3\u70E7\u3002</p><p>\u76F4\u89C9\u544A\u8BC9\u5979\u4E0D\u53EF\u4EE5\u8BA9\u5B83\u7184\u706D\uFF0C\u5979\u5BFB\u5230\u4E00\u7247\u96F6\u843D\u7684\u7EC6\u7FBD\uFF0C\u6390\u5728\u6307\u5C16\u3002\u518D\u4F38\u51FA\u624B\u53BB\u89E6\u52A8\u9762\u524D\u9634\u6697\u7684\u7A7A\u6C14\u3002</p><p>\u7FBD\u6BDB\u6240\u89E6\uFF0C\u7ADF\u73B0\u51FA\u5FAE\u5F31\u7684\u7535\u5F27\uFF0C\u4F34\u968F\u7684\u8F7B\u5FAE\u7684\u300C\u9739\u9739\u300D\u4F5C\u54CD\u3002\u90A3\u4E00\u5239\u90A3\uFF0C\u5979\u53EF\u4EE5\u5728\u5E7D\u6697\u4E2D\u770B\u89C1\u8BB8\u591A\u7EC6\u5982\u866B\u4E1D\u7684\u7EFF\u8272\u7684\u7EBF\u6761\uFF0C\u5F7C\u6B64\u4EA4\u9519\u5728\u4E00\u6B65\u4E4B\u9065\u3002</p><p>\u2014\u2014\u7ED3\u754C\u3002</p><p>\u770B\u6765\u5979\u7684\u8C28\u614E\u5E76\u975E\u5962\u4F88\u3002\u591A\u5E74\u4EE5\u6765\uFF0C\u5979\u4E00\u76F4\u4EE5\u4E3A\uFF0C\u4E00\u4E2A\u65E0\u61C8\u53EF\u51FB\u7684\u5973\u5B50\uFF0C\u9664\u4E86\u62E5\u6709\u7EDD\u8272\u51B7\u8273\u7684\u5916\u8868\uFF0C\u66F4\u5E94\u8BE5\u5728\u4EFB\u4F55\u7684\u73AF\u5883\u62E5\u6709\u4E00\u9897\u614E\u5BC6\u5B81\u9759\u4E4B\u5FC3\u3002\u8FD9\u6837\u624D\u4F1A\u4F7F\u4F60\u663E\u5F97\u6E38\u5203\u6709\u4F59\u3002</p><p>\u5BF9\u5DE8\u96C0\u5251\u65BD\u4EE5\u6C34\u7CFB\u9B54\u6CD5\uFF0C\u5373\u53EF\u4EE5\u51FB\u7834\u96F7\u7CFB\u7ED3\u754C\u3002\u5979\u5FC5\u987B\u8D76\u5728\u706F\u706D\u4E4B\u524D\uFF0C\u56E0\u6B64\u90A3\u4E00\u5251\u53BB\u610F\u5FEB\u7EDD\u3002\u4E00\u9635\u6025\u4FC3\u7684\u4F4E\u97F3\uFF0C\u5979\u770B\u89C1\u90A3\u4E9B\u7EC6\u5BC6\u5982\u866B\u4E1D\u7684\u7EFF\u7EBF\u5D29\u6E83\u6D88\u5931\u3002\u4E8E\u662F\u524D\u884C\u5230\u9635\u578B\u7684\u4E2D\u592E\u3002</p><p>\u5979\u8D70\u5230\u706F\u53F0\u4E4B\u524D\uFF0C\u89E6\u624B\u53EF\u53CA\u7684\u5173\u5934\u3002\u5374\u5FFD\u7136\u542C\u89C1\u94C1\u7D22\u58F0\u97F3\u3002\u53EA\u89C1\u4E00\u8BB0\u9501\u94FE\u8D34\u5730\u98DE\u6765\uFF0C\u88AD\u5411\u5DE6\u8FB9\u811A\u8E1D\u3002\u82CD\u5170\u4F18\u96C5\u4E4B\u81F3\uFF0C\u53EA\u662F\u957F\u5251\u4E00\u6311\uFF0C\u90A3\u94C1\u7D22\u4FBF\u65AD\u4E3A\u4E24\u622A\uFF0C\u597D\u4F3C\u86C7\u5C38\u4E00\u822C\u4E0D\u518D\u52A8\u5F39\u3002</p><p>\u60CA\u9A87\u4E2D\uFF0C\u5979\u751A\u81F3\u8FDE\u559D\u4E00\u58F0\u300C\u8C01\uFF01\u300D\u7684\u65F6\u95F4\u90FD\u672A\u6D6A\u8D39\u3002\u5373\u523B\u56DE\u590D\u5168\u795E\u6212\u5907\u7684\u59FF\u6001\u3002\u90A3\u672C\u662F\u5341\u5206\u7D27\u5F20\u7684\u5173\u5934\uFF0C\u800C\u5979\u5374\u4E0D\u5931\u4F18\u96C5\u81EA\u82E5\u7684\u5360\u5251\u73AF\u89C6\u3002\u7709\u5B87\u95F4\u4F9D\u7136\u662F\u51B7\u82E5\u7384\u971C\u7684\u5B64\u9AD8\u6218\u610F\u3002</p><p>\u8FD9\u4E00\u6B21\uFF0C\u94C1\u94FE\u5E76\u672A\u53D1\u58F0\u3002\u800C\u662F\u540E\u9888\u7684\u98CE\u52A8\u63A8\u8FDF\u4E86\u5979\u5C31\u64D2\u7684\u65F6\u95F4\u3002</p><p>\u5979\u534A\u8F6C\u8FC7\u8EAB\uFF0C\u5251\u5F71\u5982\u6708\u3002</p><p>\u65A9\u65AD\u4E86\u94C1\u94FE\u7684\u6765\u52BF\uFF0C\u5374\u7184\u4E86\u706F\u706B\u3002</p><p>\u5BB9\u4E0D\u5F97\u9877\u523B\u7684\u601D\u7EEA\uFF0C\u53C8\u662F\u4E00\u8BB0\u98DE\u94FE\u76F4\u903C\u8FC7\u6765\uFF0C\u7FCE\u770B\u4E0D\u89C1\uFF0C\u5374\u51C6\u786E\u5224\u5B9A\u5B83\u7684\u65B9\u4F4D\uFF0C\u4E00\u5251\u547D\u4E2D\u3002</p><p>\u5DF2\u987E\u4E0D\u4E0A\u706F\u706D\u7684\u6697\u55BB\uFF0C\u7FCE\u53EA\u6709\u4E14\u6218\u4E14\u9000\uFF0C\u9003\u79BB\u8FD9\u5371\u9669\u7684\u65B9\u4F4D\u3002\u5979\u60F3\uFF0C\u5979\u5FC5\u987B\u6539\u53D8\u8BA1\u5212\u3002</p><p>\u800C\u8FD9\u9003\u79BB\u662F\u56F0\u96BE\u7684\u3002\u5979\u51E0\u4E4E\u5168\u795E\u6212\u5907\uFF0C\u5E76\u50AC\u52A8\u5149\u7CFB\u7684\u5492\u6587\u3002\u5374\u88AB\u9635\u578B\u7684\u673A\u5173\u628A\u63E1\u7740\u65F6\u95F4\u5DEE\u2014\u2014\u90A3\u4E00\u5251\uFF0C\u5288\u5F97\u98DE\u94FE\u652F\u79BB\u7C89\u788E\uFF0C\u672A\u6599\u5230\uFF0C\u7ADF\u88AB\u9501\u4F4F\u6267\u5251\u7684\u53F3\u624B\u3002</p><p>\u5239\u90A3\u95F4\uFF0C\u4E00\u9635\u91D1\u5C5E\u64E6\u97F3\u2014\u2014\u5979\u7684\u4E00\u53CC\u811A\u8E1D\u548C\u624B\u8155\u5DF2\u88AB\u56DB\u6761\u9501\u94FE\u624E\u5B9E\u9501\u4F4F\u3002</p><p>\u5979\u4FDD\u6301\u539F\u6709\u7684\u59FF\u52BF\u7AD9\u5B9A\uFF0C\u4E00\u53CC\u5F92\u52B3\u7684\u7FBD\u7FFC\u987F\u65F6\u6CA6\u4E3A\u6700\u5962\u4F88\u7684\u6446\u8BBE\u3002</p><p>\u9ED1\u6697\u4E2D\uFF0C\u5979\u5E76\u65E0\u60CA\u60F6\uFF0C\u4E5F\u6CA1\u6709\u653E\u4E0B\u624B\u4E2D\u5229\u5203\u3002\u751A\u81F3\u4E0D\u613F\u53D1\u51FA\u4E00\u70B9\u7684\u58F0\u97F3\u3002\u5979\u53EA\u662F\u7AD9\u5B9A\u7740\uFF0C\u7AD9\u5B9A\u7740\uFF0C\u89C2\u671B\u4E0B\u4E00\u6B65\u7684\u547D\u8FD0\u3002</p><p>\u90A3\u4F3C\u4E4E\u662F\u65E0\u4EBA\u638C\u63A7\u7684\u673A\u5173\u3002\u82CD\u5170\u53EA\u662F\u542C\u89C1\u7A97\u5916\u5486\u54EE\u7684\u98CE\u52A8\uFF0C\u542C\u89C1\u81EA\u5DF1\u6E10\u663E\u51DD\u91CD\u7684\u547C\u5438\u3002</p><p>\u4E00\u76F4\u5230\u5965\u6258\u5927\u5E1D\u8D70\u4E0A\u6765\uFF0C\u70B9\u4EAE\u4E86\u4E09\u4E94\u8721\u70DB\u3002</p><p>\u4ED6\u8FD8\u662F\u62B1\u7740\u5973\u513F\u8D64\u88F8\u7684\u5C38\u8EAB\uFF0C\u653E\u5728\u5730\u3002\u7136\u540E\u7B11\u7B11\u3002</p><p>\u300C\u686B\u6469\u2026\u2026\u6211\u7684\u686B\u6469\u5462\uFF1F\u300D</p><p>\u300C\u8FE6\u697C\u82CD\u5170\uFF0C\u6211\u5EFA\u8BAE\u60A8\u5148\u8003\u91CF\u4E00\u4E0B\u81EA\u5DF1\u3002\u300D\u4ED6\u4E00\u8FB9\u8BF4\uFF0C\u4E00\u8FB9\u5F97\u610F\u5730\u6324\u6324\u5C38\u8EAB\u51B0\u51B7\u5730\u6D6E\u80BF\u7684\u5DE6\u4E73\uFF0C\u82CD\u5170\u7ADF\u7136\u770B\u89C1\u4E73\u6C41\u50CF\u6CC9\u4E00\u6837\u55B7\u6CFB\u800C\u51FA\u3002</p><p>\u8EAB\u4E3A\u5973\u4EBA\uFF0C\u5979\u5E76\u672A\u56DE\u907F\u3002\u80C6\u602F\u5E76\u975E\u5973\u5B50\u7684\u7F8E\u5FB7\u3002\u603B\u662F\u4E60\u60EF\u7528\u90A3\u51B7\u51B7\u7684\u76EE\u5149\u6B63\u89C6\u53D1\u751F\u7684\u4E00\u5207\uFF0C\u54EA\u6015\u706D\u7EDD\u4EBA\u5BF0\u7684\u8868\u6F14\uFF0C\u5979\u4E5F\u53EA\u6709\u6DE1\u6DE1\u7684\u8868\u60C5\uFF0C\u6DE1\u6DE1\u7684\u671B\u3002</p><p>\u300C\u686B\u6469\u5462\uFF1F\u300D</p><p>\u5965\u6258\u5927\u5E1D\u6CA1\u6709\u7ED9\u5979\u56DE\u7B54\uFF0C\u800C\u662F\u8D70\u8FD1\u5979\u3002</p><p>\u300C\u54D7\u2026\u2026\u4F60\u88AB\u9501\u4F4F\u7684\u6837\u5B50\uFF0C\u597D\u6F02\u4EAE\u3002\u597D\u6027\u611F\u3002\u300D\u4ED6\u63D0\u7740\u8721\u70DB\uFF0C\u7B11\u5BB9\u4EB2\u539A\u3002\u4ED6\u5FF5\u51FA\u4E00\u58F0\uFF1A\u300C\u74E6\u62C9\u4E4C\u2014\u2014\u4EE5\u6492\u8DEF\uFF01\u300D</p><p>\u56DB\u6839\u9501\u4F4F\u82CD\u5170\u7684\u94C1\u94FE\u7ADF\u9006\u5411\u7684\u6536\u7F29\uFF0C\u82CD\u5170\u5C3D\u529B\u6297\u62D2\uFF0C\u5374\u7EC8\u88AB\u62C9\u6210\u300C\u5927\u300D\u5B57\u9020\u578B\u3002</p><p>\u300C\u5514\u2026\u2026\u4E0D\u5F97\u9760\u8FD1\u3002\u5426\u5219\uFF0C\u683C\u6740\u65E0\u8D66\u3002\u300D</p><p>\u300C\u54C7\u54C8\u54C8\u54C8\u54C8\u2026\u2026\u4F60\u53EF\u4EE5\u8BD5\u7740\u626D\u52A8\u4E00\u4E0B\uFF0C\u6211\u7684\u51B7\u7F8E\u4EBA\u3002\u300D\u4ED6\u7B11\u7684\u6DEB\u90AA\u3002</p><p>\u300C\u5514\u2026\u2026\u4F60\u8FD9\u6761\u8001\u72D7\uFF01\u300D</p><p>\u5148\u524D\uFF0C\u4ED6\u4E00\u76F4\u4EE5\u4E3A\u8D1D\u73B2\u8FBE\u548C\u5979\u5F88\u76F8\u4F3C\u3002\u4ECA\u6B21\u5728\u70DB\u5149\u4E0B\uFF0C\u5982\u6B64\u903C\u8FD1\u7684\u89C2\u8D4F\uFF0C\u4ED6\u5374\u4E0D\u5F97\u4E0D\u627F\u8BA4\uFF0C\u5373\u4FBF\u578B\u4F3C\uFF0C\u5728\u8FD9\u4E4B\u95F4\u8FD8\u662F\u6709\u7740\u7B49\u6B21\u4E4B\u522B\u3002</p><p>\u8FD9\u6216\u8BB8\u672A\u591F\u5929\u6E0A\u4E91\u6CE5\u7684\u60AC\u6B8A\uFF0C\u4F46\u8D1D\u73B2\u8FBE\u7EC8\u5F52\u8FD8\u662F\u51E1\u4FD7\u4E2D\u8273\u3002</p><p>\u90A3\u65E5\uFF0C\u82CD\u5170\u53D1\u578B\u6781\u4E4B\u7CBE\u7F8E\uFF0C\u867D\u662F\u7ECF\u8FC7\u957F\u9014\u7684\u98DE\u884C\uFF0C\u7565\u663E\u51CC\u4E71\uFF0C\u4F46\u53D1\u9B13\u53D1\u9645\u4E4B\u95F4\uFF0C\u4F9D\u7136\u662F\u65E0\u53EF\u6311\u5254\u3002\u90A3\u663E\u7136\u662F\u6709\u8FC7\u8003\u7A76\u7684\u68B3\u7406\uFF0C\u5728\u5927\u9646\u4E0A\u4ED6\u672A\u770B\u8FC7\u4E0E\u4E4B\u8FD1\u4F3C\u7684\u53D1\u578B\u3002\u90A3\u770B\u4F3C\u6563\u4E71\uFF0C\u5782\u843D\u9762\u5E9E\u7684\u51E0\u675F\uFF0C\u66F4\u589E\u6DFB\u6D51\u5982\u5929\u9020\u7684\u51B7\u8273\u6C14\u8D28\u3002</p><p>\u4E00\u8EAB\u6027\u611F\u7684\u84DD\u7FCE\u94E0\uFF0C\u8892\u9732\u51FA\u7626\u524A\u9999\u80A9\uFF0C\u96EA\u9888\u4FEE\u957F\u3002</p><p>\u4ED6\u73AF\u7ED5\u5979\u5468\u56F4\uFF0C\u7EC6\u8D4F\u5979\u6BCF\u4E2A\u89D2\u5EA6\u3002\u82CD\u5170\u662F\u90A3\u6837\u7F8E\uFF0C\u5373\u4FBF\u80CC\u5F71\uFF0C\u90FD\u8DB3\u4EE5\u4EE4\u4EBA\u9189\u3002\u5979\u7684\u94E0\u7532\u5E76\u65E0\u8FC7\u5206\u7684\u82B1\u4FCF\uFF0C\u7B80\u7EA6\u7684\u7EBF\u6761\u5206\u5272\uFF0C\u5E7D\u51B7\u7684\u84DD\u8272\u91D1\u5C5E\u5149\u8292\u886C\u5F97\u96EA\u767D\u7684\u808C\u80A4\u683C\u5916\u660E\u5A9A\u3002</p><p>\u5979\u7684\u8774\u8776\u9AA8\u548C\u80A9\u5E26\u7565\u663E\u7A81\u5140\uFF0C\u5374\u5F62\u6210\u67D0\u79CD\u5174\u594B\u70B9\u3002\u8FD8\u6709\u7EA4\u7EC6\u7684\u8170\u548C\u7CBE\u7F8E\u7EDD\u4F26\u7684\u81C0\u90E8\u7EBF\u6761\u3002\u8FD9\u4E9B\u548C\u9690\u79D8\u7684\u4E73\u623F\u662F\u4E0D\u4E00\u6837\u7684\uFF0C\u5B83\u968F\u65F6\u53EF\u4EE5\u5C55\u73B0\u5728\u4EBA\u9762\u524D\uFF0C\u8BA9\u4EBA\u6B23\u8D4F\u5F97\u5230\uFF0C\u53F9\u4E3A\u89C2\u6B62\u3002</p><p>\u4ED6\u662F\u6709\u827A\u672F\u4FEE\u517B\u7684\u8001\u4EBA\uFF0C\u5374\u627E\u4E0D\u51FA\u5408\u9002\u7684\u8F9E\u85FB\u8D5E\u7F8E\u82CD\u5170\u7684\u4FCF\u81C0\u3002\u4ECE\u6765\u6CA1\u6709\u89C1\u8FC7\u8FD9\u6837\u5B8C\u7F8E\u7684\u9020\u7269\uFF0C\u5B8C\u7F8E\u5730\u627F\u63A5\u7740\u4E0A\u80A2\u548C\u817F\u578B\uFF0C\u628A\u4E00\u5177\u7EDD\u7F8E\u7684\u8EAB\u59FF\u53D8\u5730\u66F4\u52A0\u4E0D\u53EF\u601D\u8BAE\u3002</p><p>\u5C31\u4F3C\u9526\u4E0A\u6DFB\u82B1\u7684\u7F8E\u5999\u3002</p><p>\u4E00\u6B3E\u6D45\u8272\u7684\u76AE\u88D9\u6070\u5230\u597D\u5904\u5730\u8986\u76D6\u5176\u4E0A\uFF0C\u66F4\u50CF\u4E00\u5F0F\u534E\u4E3D\u5305\u88C5\u3002\u4ED6\u51C6\u5907\u7559\u5F85\u6700\u540E\u518D\u63ED\u5F00\u5B83\uFF0C\u72EC\u5360\u795E\u79D8\u7684\u60CA\u559C\u3002</p><p>\u5979\u6BD4\u8D1D\u73B2\u8FBE\u9AD8\u4F7B\u4E9B\uFF0C\u4ED6\u4F38\u624B\u64A9\u64A9\u5979\u7684\u4E73\u6C9F\uFF0C\u7A0D\u7A0D\u6382\u811A\u3002\u84DD\u8272\u80F8\u94E0\u82B1\u74E3\u5F62\uFF0C\u51B7\u800C\u575A\u786C\u3002\u4E4B\u4E0B\u7684\u7384\u673A\u65E9\u5DF2\u4EE4\u4ED6\u60F3\u5165\u975E\u975E\u3002</p><p>\u6E05\u7626\u7684\u5973\u5B50\u3002\u751A\u81F3\u53EF\u4EE5\u9690\u7EA6\u770B\u89C1\u808B\u9AA8\u3002\u5728\u5979\u5E73\u5766\u7684\u5C0F\u8179\uFF0C\u4ED6\u9047\u89C1\u60CA\u559C\u3002\u539F\u6765\u7ADF\u6253\u7740\u4E00\u53EA\u8110\u73AF\uFF0C\u7EC6\u800C\u5FAE\u5C0F\uFF0C\u989C\u8272\u662F\u6BD4\u62A4\u80F8\u7684\u94E0\u7532\u66F4\u663E\u5E7D\u84DD\u3002</p><p>\u8170\u5E26\u662F\u5782\u843D\u4E1D\u5E26\u7684\u82B1\u5F0F\uFF0C\u9576\u4E0A\u6676\u83B9\u51B0\u94BB\u5728\u70DB\u5149\u4E0B\u6E29\u6DA6\u751F\u8F89\u3002\u90A3\u5927\u4E8E\u70DB\u5149\u7684\u66A7\u6627\uFF0C\u98CE\u666F\u8FE4\u4FEA\u3002</p><p>\u6D45\u8272\u7684\u76AE\u9769\u77ED\u88D9\u4E4B\u4E0B\uFF0C\u7ECF\u5178\u7684\u81C0\u5EF6\u4F38\u51FA\u65E0\u61C8\u53EF\u51FB\u7684\u53CC\u817F\uFF0C\u4E00\u53CC\u540C\u4E3A\u94F6\u8272\u7CFB\u7684\u6218\u9774\u7CBE\u5999\u5730\u70B9\u7F00\uFF0C\u4ED6\u4E0D\u5F97\u4E0D\u627F\u8BA4\u8FD9\u662F\u4E00\u4F4D\u5F88\u6709\u7740\u88C5\u8003\u7A76\u7684\u5973\u7687\u3002</p><p>\u5C31\u8FD9\u6837\u4E3E\u7740\u8721\u70DB\uFF0C\u5728\u8DDD\u79BB\u5979\u6700\u8FD1\u7684\u5730\u65B9\u7EC6\u7EC6\u8D4F\u904D\u5979\u7684\u5168\u8EAB\u3002</p><p>\u4ED6\u4E0D\u53BB\u89E6\u78B0\u5979\uFF0C\u53EA\u542C\u7740\u5979\u6E10\u4E71\u7684\u547C\u5438\u3002</p><p>\u70DB\u706B\u592A\u8D34\u8FD1\u7684\u65F6\u5019\uFF0C\u5979\u4F1A\u89C9\u5F97\u70EB\uFF0C\u4F46\u4E0D\u613F\u547C\u53EB\u3002\u56E0\u4E3A\u90A3\u6837\u662F\u803B\u8FB1\u7684\u3002</p><p>\u5979\u52A8\u5F39\u4E0D\u5F97\uFF0C\u4ED6\u4FBF\u4E3E\u7740\u8721\u70DB\u8E72\u4E0B\u53BB\u63A2\u671B\u5979\u53CC\u817F\u4E4B\u95F4\u3002</p><p>\u5979\u540E\u6094\u4ECA\u6B21\u7740\u4E86\u77ED\u88D9\uFF0C\u5374\u4E5F\u53EA\u6709\u51B7\u51BD\u5730\u9A82\u4ED6\uFF1A\u300C\u755C\u751F\u3002\u300D</p><p>\u8001\u755C\u751F\u5374\u51E0\u5206\u5931\u671B\uFF0C\u56E0\u4E3A\u5979\u7684\u5E95\u88E4\u5E76\u975E\u7279\u522B\u6027\u611F\u7684\u6B3E\u5F0F\uFF0C\u800C\u662F\u4E0E\u77ED\u88D9\u8D28\u5730\u76F8\u540C\u7684\u4E1D\u7EC7\uFF0C\u5305\u88F9\u4E25\u5B9E\u3002\u7136\u800C\u4ECE\u8FD9\u6837\u5201\u94BB\u7684\u89D2\u5EA6\u89C2\u8D4F\u5979\u7684\u7F8E\u81C0\uFF0C\u5374\u53C8\u662F\u4E0D\u540C\u7684\u89C6\u89C9\u51B2\u51FB\u3002</p><p>\u5BA1\u7F8E\u4E00\u4F46\u9644\u5E26\u7740\u7981\u5FCC\u7684\u610F\u5473\uFF0C\u4FBF\u5347\u534E\u5230\u65B0\u7684\u9AD8\u5904\u3002\u660F\u6697\u7684\u70DB\u5149\u7167\u5C04\uFF0C\u88D9\u5185\u7684\u89C6\u91CE\u4E00\u89C8\u65E0\u4F59\u3002\u4ED6\u66F2\u817F\u8EAC\u80CC\uFF0C\u62AC\u5934\u4EF0\u671B\uFF0C\u5979\u7F8E\u5999\u7684\u81C0\u90E8\u66F2\u7EBF\u5C31\u50CF\u521D\u6708\u7684\u5F27\u3002</p><p>\u4E3A\u4E86\u4F53\u73B0\u4E00\u56FD\u4E4B\u541B\u7684\u96C5\u91CF\uFF0C\u4ED6\u5927\u529B\u8D5E\u7F8E\u5979\u7684\u7EDD\u8272\uFF1A\u300C\u554A\u2026\u2026\u4F60\u6BD4\u6211\u5973\u513F\u7F8E\u591A\u4E86\uFF0C\u641E\u8D77\u6765\uFF0C\u4E5F\u52BF\u5FC5\u4F1A\u66F4\u723D\u7684\u3002\u300D\u4ED6\u8BF4\u5F97\u662F\u5B9E\u60C5\u3002</p><p>\u4ED6\u9009\u62E9\u4ECE\u4E73\u6C9F\u5F00\u59CB\uFF0C\u89E6\u52A8\u5979\u808C\u80A4\u7684\u4E00\u5239\u90A3\uFF0C\u5979\u7684\u4E00\u5BF9\u7FC5\u8180\u4FBF\u9661\u7136\u94FA\u5F20\u3002</p><p>\u2014\u2014\u300C\u66DD\uFF01\u300D\u5730\u5DE8\u5927\u4E00\u58F0\uFF0C\u5BA4\u5185\u7684\u7A7A\u6C14\u968F\u4E4B\u75BE\u9707\u3002</p><p>\u4ED6\u4E00\u60CA\uFF0C\u624B\u6307\u8D76\u5FD9\u6536\u56DE\u3002</p><p>\u5979\u90A3\u51B7\u51DB\u7684\u8868\u60C5\u4E2D\u5199\u4E0B\u65E0\u671B\u4E0E\u4E0D\u7518\uFF0C\u7EDD\u8272\u503E\u57CE\u3002</p><p>\u300C\u54C8\u54C8\u54C8\u3002\u300D\u4ED6\u7B11\u5F97\u5F20\u72C2\uFF1A\u300C\u63D2\u7FFC\u96BE\u98DE\u3002\u300D</p>',69);function t(c,n){return r}var o=e(a,[["render",t]]);export{o as default};
