import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const a={},r=p('<h1 id="t" tabindex="-1"><a class="header-anchor" href="#t" aria-hidden="true">#</a> T\u2026</h1><p>\u686B\u6469\u8FDB\u6765\u4E4B\u524D\uFF0C\u5C31\u770B\u89C1\u70DB\u5149\u5E7D\u5E7D\u3002\u542C\u5230\u5965\u6258\u90A3\u719F\u6089\u7684\u8BDD\u97F3\uFF0C\u4ED6\u7ADF\u9732\u51FA\u7B11\u3002\u800C\u8FD9\u6837\u7684\u7B11\u5BB9\u53C8\u5F88\u5FEB\u5B9A\u683C\u3002</p><p>\u4ED6\u770B\u5230\u6A2A\u9648\u5728\u5730\u7684\u5973\u5C38\u3002</p><p>\u300C\u686B\u6469\uFF01\u300D\u662F\u59D0\u59D0\u5728\u53EB\u5524\u4ED6\u3002</p><p>\u300C\u4F60\uFF0C\u4F60\u2026\u2026\u686B\u6469\u2026\u2026\u300D\u8FD9\u4E00\u58F0\u662F\u5965\u6258\u3002</p><p>\u4ED6\u672A\u776C\uFF0C\u8FF3\u81EA\u8D70\u5411\u8D1D\u73B2\u8FBE\u7684\u5C38\u8EAB\uFF0C\u53CD\u8F6C\u8FC7\u6765\uFF0C\u5979\u7ADF\u672A\u7791\u76EE\u3002</p><p>\u90A3\u53CC\u5931\u5374\u751F\u547D\u5149\u5F69\u7684\u773C\u775B\uFF0C\u5E26\u7740\u6DF1\u6DF1\u5730\u6050\u6016\u610F\u5473\u3002\u4F3C\u671B\u5B9A\u4ED6\u3002\u90A3\u4E9B\u6D41\u51FA\u7684\u4E73\u6C41\u5DF2\u5E72\u6DB8\u4E86\uFF0C\u9634\u51B7\u7684\u808C\u80A4\u4E0A\u6B8B\u7559\u7740\u767D\u8272\u7684\u6591\u75D5\u3002\u4E0B\u4F53\u7684\u8840\u8FF9\u8FD8\u672A\u5168\u51DD\u7ED3\u3002\u4ED6\u7528\u624B\u6307\u6CBE\u6CBE\uFF0C\u653E\u5165\u53E3\u4E2D\u6D45\u5C1D\u3002</p><p>\u5730\u9762\u4E0A\u6A21\u7CCA\u7684\u4E00\u6EE9\u8840\u6C61\uFF0C\u62B9\u6765\u4E00\u7247\u3002\u518D\u6162\u6162\u7528\u6CBE\u6EE1\u8840\u7684\u624B\u638C\u5728\u8D1D\u73B2\u8FBE\u51B0\u51B7\u7684\u9762\u4E0A\u629A\u6469\uFF0C\u628A\u8840\u6E0D\u7C98\u5728\u5979\u7684\u5634\u89D2\u776B\u6BDB\u3002</p><p>\u4ED6\u7528\u6CBE\u6EE1\u6C61\u8840\u7684\u624B\u638C\u62B9\u8FC7\u5973\u5C38\u7684\u773C\u775B\u3002\u90A3\u4E9B\u7FD8\u8D77\u7684\u776B\u6BDB\u64A9\u5230\u4ED6\u75D2\u3002</p><p>\u5979\u4E0D\u80AF\u95ED\u773C\uFF0C\u4ED6\u4FBF\u653E\u843D\u5979\u4E00\u65C1\uFF0C\u4E0D\u518D\u770B\u5979\u3002</p><p>\u82CD\u5170\u7A81\u7136\u89C9\u5F97\u8FD9\u4E2A\u686B\u6469\u4E0D\u518D\u662F\u4ECE\u524D\u7684\u686B\u6469\u3002\u4E8E\u662F\u5979\u672A\u518D\u5524\u4ED6\u7684\u540D\u5B57\uFF0C\u53EA\u662F\u9759\u89C2\u3002\u624B\u5FC3\u5F00\u59CB\u6CDB\u6F6E\u3002</p><p>\u70DB\u706B\u4E00\u76F4\u98A4\u3002</p><p>\u65B9\u624D\u56A3\u5F20\u7684\u5965\u6258\u611F\u5230\u4E00\u9635\u6DF1\u6DF1\u7684\u5BD2\u3002\u4ED6\u4E0D\u4FBF\u6D41\u9732\u80C6\u602F\uFF0C\u4ED6\u8981\u7A33\u4F4F\u9635\u811A\uFF0C\u8FD9\u624D\u662F\u4E00\u8D2F\u98CE\u683C\u3002</p><p>\u4ED6\u76D8\u7B97\u7740\u3002\u7136\u540E\u8BDD\u97F3\u4EB2\u548C\uFF1A</p><p>\u300C\u6216\u8BB8\u2026\u2026\u686B\u6469\u3002\u6211\u4EEC\u5E94\u8BE5\u8C08\u8C08\u3002\u300D\u4ED6\u5E76\u4E0D\u662F\u4EE5\u6B66\u529B\u89C1\u957F\uFF0C\u4E5F\u4E0D\u4F1A\u5F3A\u5927\u653B\u51FB\u529B\u7684\u9B54\u6CD5\u3002\u4ED6\u53EA\u6709\u5E0C\u671B\u686B\u6469\u6B65\u5165\u9635\u6CD5\uFF0C\u7136\u540E\u50CF\u64D2\u62FF\u82CD\u5170\u90A3\u6837\uFF0C\u628A\u5C40\u52BF\u633D\u56DE\u4E0A\u98CE\u3002</p><p>\u4ED6\u8BF4\uFF1A\u300C\u686B\u6469\uFF0C\u4F60\u613F\u610F\u518D\u548C\u7236\u7687\u8BF4\u8BDD\u5417\uFF1F\u686B\u6469\uFF0C\u6211\u7684\u5B69\u5B50\u3002\u300D</p><p>\u686B\u6469\u6325\u6325\u624B\uFF0C\u53C8\u518D\u7B11\u7B11\u3002\u90A3\u7B11\u5BB9\u5145\u6EE1\u7740\u5996\u5F02\u90AA\u6C14\u3002\u4ED6\u672A\u8BF4\u8BDD\uFF0C\u6162\u6162\u6B65\u8FD1\u5965\u6258\u8EAB\u524D\u3002</p><p>\u5965\u6258\u9000\u4E00\u6B65\uFF0C\u4ED6\u4E5F\u4E0D\u518D\u76F8\u903C\u3002</p><p>\u7B11\u3002</p><p>\u5965\u6258\u4E5F\u7B11\uFF0C\u5374\u662F\u7B11\u5F97\u82CD\u767D\u88AB\u52A8\u3002\u4ED6\u7684\u773C\u795E\u5199\u6EE1\u5DE8\u5927\u7684\u60CA\u60E7\u3002\u70DB\u5149\u7167\u5728\u686B\u6469\u5996\u5F02\u7684\u9762\u76F8\uFF0C\u7259\u9F7F\u68EE\u767D\u3002</p><p>\u300C\u4F60\u4EE5\u4E3A\uFF0C\u4F60\u6740\u5F97\u6B7B\u6211\u5417\uFF1F\u300D</p><p>\u4ED6\u53C8\u9000\u51FA\u4E00\u6B65\uFF0C\u52C9\u529B\u9547\u5B9A\u3002\u5374\u53D1\u73B0\u70DB\u5149\u98A4\u6296\u66F4\u5267\u3002</p><p>\u686B\u6469\u63A5\u8FC7\u4ED6\u7684\u70DB\u53F0\uFF0C\u63A5\u8FC7\u6765\u3002\u7136\u540E\u629B\u5728\u5730\u4E0A\u3002</p><p>\u300C\u4F60\uFF0C\u81EA\u5DF1\u6316\u51FA\u53CC\u773C\u3002\u300D</p><p>\u4ED6\u518D\u4E0D\u662F\u4EE5\u524D\u90A3\u4E2A\u6E29\u826F\u7684\u8FE6\u697C\u686B\u6469\uFF0C\u5929\u7A7A\u7687\u5B50\u3002\u4ED6\u5DF2\u6CA6\u843D\u6210\u9B54\u3002</p><p>\u5965\u6258\u9003\u4E0D\u8FC7\u3002\u800C\u686B\u6469\u4EA6\u4E0D\u7ED9\u4ED6\u81EA\u6740\u7684\u798F\u5229\u3002</p><p>\u7535\u5149\u706B\u77F3\u51FA\u624B\uFF0C\u4FBF\u5C06\u5965\u6258\u7684\u4E00\u53CC\u7738\u5B50\u5939\u5728\u81EA\u5DF1\u7684\u6307\u95F4\u3002\u8001\u4EBA\u53D1\u51FA\u65E0\u6BD4\u51C4\u60E8\u7684\u53EB\u58F0\uFF0C\u686B\u6469\u5374\u8FD8\u662F\u4FDD\u6301\u5996\u5F02\u7B11\u5BB9\u3002</p><p>\u4ED6\u628A\u4ED6\u63D0\u8D77\u6765\uFF0C\u64AC\u5F00\u4ED6\u7684\u53E3\uFF0C\u518D\u5C06\u4E00\u5BF9\u6DCC\u8840\u7684\u7738\u5B50\u653E\u5165\u5176\u4E2D\u3002</p><p>\u300C\u4F60\uFF0C\u54BD\u4E0B\u3002\u300D</p><p>\u4E5F\u8BB8\u662F\u5965\u6258\u5BF9\u8FD9\u5904\u7684\u5730\u5F62\u4E86\u5982\u6307\u638C\uFF0C\u5373\u4FBF\u662F\u5728\u5250\u53BB\u53CC\u76EE\u7684\u5267\u75DB\u4E2D\uFF0C\u4F9D\u7136\u660E\u786E\u7A97\u53E3\u65B9\u4F4D\uFF0C\u53EA\u89C1\u4ED6\u7EB5\u8EAB\u4E00\u8D8A\u2014\u2014</p><p>\u53EF\u60DC\u686B\u6469\u7684\u51FA\u624B\u5374\u66F4\u5FEB\uFF0C\u5DF2\u6293\u7262\u4ED6\u7684\u4E0B\u80A2\uFF0C\u5E76\u594B\u529B\u4E00\u62C9\u3002</p><p>\u9057\u61BE\u7684\u662F\uFF0C\u5965\u6258\u5374\u8FD8\u662F\u6B7B\u4E86\u3002</p><p>\u56E0\u4E3A\u686B\u6469\u90A3\u4E00\u62C9\u7528\u5F97\u592A\u5927\u529B\uFF0C\u5965\u6258\u7684\u8EAB\u4F53\u91CD\u91CD\u7684\u649E\u5411\u5854\u5C16\u7684\u8FB9\u6CBF\u3002\u90A3\u8FB9\u6CBF\u662F\u524A\u5230\u6781\u8584\u7684\u7247\u72B6\uFF0C\u8FD1\u4F3C\u77F3\u65A7\u7684\u8FB9\u5203\u3002</p><p>\u90A3\u4E00\u77AC\u95F4\uFF0C\u7531\u4E8E\u5DE8\u5927\u7684\u51B2\u649E\u529B\u91CF\uFF0C\u4ED6\u7684\u8EAB\u4F53\u7ADF\u62E6\u8170\u88AB\u622A\u4E3A\u4E8C\u3002</p><p>\u2014\u2014\u686B\u6469\u63E1\u7740\u7684\uFF0C\u53EA\u662F\u5965\u6258\u7684\u4E00\u53CC\u817F\u548C\u80EF\u90E8\u3002\u65B0\u9C9C\u7684\u8840\u6DB2\u5C31\u50CF\u7011\u5E03\u90A3\u6837\u6FC0\u5C04\u55B7\u6D8C\uFF0C\u4ED6\u629B\u5F00\u624B\u4E2D\u7684\u534A\u4E2A\u4EBA\u578B\uFF0C\u649E\u5230\u7981\u9522\u82CD\u5170\u7684\u94C1\u94FE\u3002\u843D\u4E0B\u3002\u90A3\u53CC\u817F\u7ADF\u8FD8\u62BD\u6410\u4E00\u9635\u624D\u80AF\u5B89\u8BE6\u6B7B\u53BB\u3002</p><p>\u7B11\u3002</p><p>\u82CD\u5170\u5219\u662F\u5982\u5E38\u7684\u9759\u3002</p><p>\u8FD9\u5E55\u8840\u8089\u6A2A\u98DE\u7684\u573A\u666F\uFF0C\u5979\u9762\u65E0\u8868\u60C5\u7684\u89C2\u671B\u3002</p><p>\u686B\u6469\u751A\u81F3\u672A\u7528\u5251\uFF0C\u56DB\u8BB0\u624B\u5200\u5C31\u9694\u7A7A\u5288\u5F00\u59D0\u59D0\u8EAB\u4E0A\u7684\u94C1\u94FE\u3002</p><p>\u82CD\u5170\u8F7B\u8212\u4E00\u53E3\u6C14\uFF0C\u300C\u686B\u6469\u3002\u300D</p><p>\u686B\u6469\u7B11\uFF0C\u672A\u8BF4\u8BDD\u3002\u76EE\u5149\u843D\u5B9A\u5728\u59D0\u59D0\u624B\u8155\u4E0A\u6346\u6210\u7684\u6DE4\u75D5\u3002</p><p>\u82CD\u5170\u6307\u6307\u686B\u6469\u7684\u8EAB\u540E\uFF0C\u686B\u6469\u8F6C\u8FC7\u8EAB\u3002</p><p>\u53EA\u5728\u8FD9\u4E00\u5239\uFF0C\u5DE8\u96C0\u5251\u7834\u7A7A\u76F4\u523A\u2014\u2014</p><p>\u5979\u4E0D\u662F\u60F3\u6740\u4ED6\uFF0C\u800C\u662F\u5979\u77E5\u9053\u4ED6\u5DF2\u5815\u5165\u9B54\u9053\u3002\u8FD9\u4E00\u5251\u5E76\u6CA1\u6709\u6740\u610F\uFF0C\u800C\u662F\u6551\u8D4E\u3002</p><p>\u800C\u662F\u8D85\u5EA6\u3002</p><p>\u6325\u5251\u7684\u4E00\u77AC\uFF0C\u82CD\u5170\u7ADF\u611F\u5230\u4E00\u4E1D\u7684\u7737\u5FF5\u3002\u4F46\u8FD8\u662F\u51B7\u51BD\u5730\u76F4\u523A\u3002\u5C31\u50CF\u5979\u66FE\u7ECF\u7528\u96F7\u540C\u7684\u4E00\u51FB\u8D2F\u7A7F\u5927\u796D\u53F8\u7684\u5FC3\u810F\u3002</p><p>\u90A3\u4E00\u6B21\u7684\u4EE3\u4EF7\u662F\u57CE\u5E02\u7684\u672A\u6765\u3002</p><p>\u800C\u8FD9\u4E00\u5251\u5374\u503E\u5C3D\u662F\u5979\u5168\u90E8\u7684\u56DE\u5FC6\u548C\u5E0C\u671B\uFF0C\u7231\u548C\u6069\u6148\u3002</p><p>\u5979\u95ED\u4E0A\u773C\uFF0C\u5FC3\u4E2D\u5411\u7740\u5929\u5149\u8654\u8BDA\u7977\u544A\u3002</p><p>\u300C\u686B\u6469\u2026\u2026\u4F60\u4F1A\u539F\u8C05\u59D0\u59D0\u5417\uFF1F\u686B\u6469\u2026\u2026\u300D</p><p>\u8FD9\u4E00\u5251\u5145\u6EE1\u7981\u5FCC\u7684\u6B8B\u9177\u3002</p><p>\u5145\u6EE1\u58EE\u70C8\u7684\u6292\u60C5\u3002</p><p>\u4ED6\u662F\u5979\u7684\u5B6A\u751F\u5F1F\u5F1F\uFF0C\u4F46\u5979\u5374\u8981\u6740\u4ED6\u3002\u56E0\u4E3A\u4ED6\u5DF2\u4E0D\u662F\uFF0C\u6240\u4EE5\u5979\u52BF\u5FC5\u6D88\u706D\u4ED6\u3002</p><p>\u5979\u77E5\u9053\u5F53\u4EBA\u53D8\u4E3A\u9B54\uFF0C\u552F\u4E00\u62EF\u6551\u7684\u65B9\u5F0F\u5373\u662F\u6D88\u706D\u4ED6\u3002\u5979\u7528\u5239\u90A3\u7684\u65F6\u70B9\u8BF4\u670D\u81EA\u5DF1\u6740\u6B7B\u4ED6\uFF0C\u5979\u544A\u6170\u81EA\u5DF1\u8BF4\uFF1A\u300C\u686B\u6469\u5DF2\u4E0D\u5728\u4E86\u3002\u300D</p><p>\u5C31\u50CF\u8FD9\u4E8C\u5341\u591A\u5E74\u6765\u7684\u8BB8\u591A\u4E2A\u5239\u90A3\uFF0C\u5230\u8FD9\u523B\u90FD\u88AB\u6C38\u7559\u5728\u67D0\u6BB5\u66FE\u7ECF\u3002</p><p>\u5979\u548C\u4ED6\u7684\u8DDD\u79BB\u662F\u4E00\u67C4\u5DE8\u96C0\u5251\u7684\u957F\u5EA6\u3002</p><p>\u5979\u63E1\u7740\u4E00\u7AEF\uFF0C\u53E6\u4E00\u7AEF\u5374\u9003\u4E0D\u51FA\u4ED6\u7684\u6307\u95F4\u3002</p><p>\u4ED6\u53EA\u4E0D\u8FC7\u7528\u624B\u6307\u7684\u7F1D\u9699\u8F7B\u8F7B\u5939\u4F4F\u5251\u950B\uFF0C\u5979\u7684\u7528\u5FC3\u826F\u82E6\u4FBF\u544A\u74E6\u89E3\u3002</p><p>\u4ED6\u4E0D\u518D\u662F\u7B11\uFF0C\u773C\u795E\u95F4\u6D41\u9732\u51FA\u96BE\u4EE5\u7F6E\u4FE1\u7684\u6124\u6012\u3002</p><p>\u5979\u66F4\u662F\u60CA\u5F02\u8FD9\u4E00\u5251\u7684\u9519\u5931\u3002\u5979\u5F7B\u5E95\u76F8\u4FE1\uFF0C\u9762\u524D\u8FD9\u6124\u6012\u7684\u7537\u5B50\u5DF2\u4E0D\u518D\u662F\u4ECE\u524D\u7684\u8FE6\u697C\u686B\u6469\u3002</p><p>\u2026\u2026</p><p>\u7A97\u5916\u662F\u9AD8\u7A7A\u547C\u5578\u7684\u98CE\uFF0C\u5BA4\u5185\u4E00\u5177\u5973\u5C38\u9759\u9759\u8EBA\u5367\uFF0C\u8FD8\u6709\u534A\u5177\u4EBA\u578B\u501A\u5728\u5899\u8FB9\u89E6\u76EE\u60CA\u5FC3\u3002</p><p>\u9EEF\u6DE1\u7684\u8840\u8272\u70B9\u7F00\u7740\u70DB\u5149\u6E29\u99A8\u3002</p><p>\u70DB\u5149\u4E0B\uFF0C\u5979\u548C\u4ED6\u7684\u526A\u5F71\u8F6E\u5ED3\u6620\u5728\u660F\u9EC4\u7684\u5899\u3002</p><p>\u300C\u8FDE\u4F60\u4E5F\u2026\u2026\u300D</p><p>\u300C\u4E0D\uFF0C\u4E0D\u662F\u6740\u6211\u7684\u4EB2\u5F1F\u3002\u4E43\u662F\u706D\u9B54\u3002\u300D</p><p>\u5979\u611F\u5230\u5DE8\u96C0\u5251\u5F00\u59CB\u6025\u98A4\uFF0C\u662F\u6765\u81EA\u4ED6\u7684\u8EAB\u8EAF\u7684\u6296\u9707\u3002</p><p>\u4ED6\u628A\u773C\u775B\u77AA\u5230\u6700\u5927\uFF0C\u989D\u5934\u4E0A\u5206\u660E\u66B4\u51FA\u9752\u7B4B\uFF0C\u5507\u5728\u62BD\u6410\uFF0C\u7275\u52A8\u9F3B\u7FFC\u3002\u300C\u55D1\u55D1\u300D</p><p>\u90A3\u662F\u686B\u6469\u54AC\u7259\u65F6\u53D1\u51FA\u662F\u58F0\u97F3\u3002</p><p>\u300C\u554A\u2014\u2014\u300D\u686B\u6469\u53D1\u51FA\u91CE\u517D\u4E00\u6837\u7684\u568E\u53EB\uFF0C\u6325\u52A8\u624B\u81C2\u7ADF\u5C06\u5DE8\u96C0\u5251\u65AD\u4E3A\u4E24\u622A\u2014\u2014</p><p>\u300C\u6211\u2014\u2014\u4E0D\u2014\u2014\u662F\u2014\u2014\u9B54\uFF01\u300D</p><p>\u5DE8\u96C0\u5251\u65AD\u4E3A\u4E24\u622A\u843D\u5728\u5730\u9762\u3002</p><p>\u82CD\u5170\u5C55\u7FC5\u98DE\u9041\u3002\u5374\u8FD8\u662F\u88AB\u6251\u4E0A\u6765\u686B\u6469\u6263\u4F4F\u811A\u8E1D\u3002\u4ED6\u7684\u529B\u9053\u5DE8\u5927\u5730\u8D85\u8D8A\u5979\u7684\u9884\u6599\uFF0C\u7ADF\u662F\u65E0\u6CD5\u6323\u8131\u7684\u6307\u638C\u3002</p><p>\u4E27\u5FC3\u75C5\u72C2\u7684\u602A\u53EB\u4E2D\uFF0C\u4ED6\u628A\u5979\u6574\u4E2A\u8EAB\u8EAF\u91CD\u91CD\u5F97\u6454\u5728\u5730\u9762\u3002\u4E0D\u7B49\u5979\u8D77\u8EAB\u7AD9\u5B9A\uFF0C\u518D\u6325\u51FA\u52BF\u5927\u529B\u6C89\u7684\u4E00\u8BB0\u91CD\u51FB\uFF0C\u76F4\u903C\u82CD\u5170\u7684\u5FC3\u810F\u3002</p><p>\u5979\u5DF2\u4E0D\u53CA\u907F\u95EA\uFF0C\u60DF\u6709\u4EE5\u6700\u5FEB\u7684\u901F\u5EA6\u504F\u8F6C\u8EAB\u4F53\u3002</p><p>\u94C1\u62F3\u4ECE\u5979\u7684\u80F8\u94E0\u64E6\u8FC7\uFF0C\u90A3\u65E0\u6BD4\u575A\u786C\u7684\u94E0\u7532\u7ADF\u88AB\u8F70\u5F97\u788E\u88C2\u5F00\u6765\uFF0C\u53F3\u4E73\u7684\u5F27\u7EBF\u8892\u9732\u51FA\u6765\u3002</p><p>\u686B\u6469\u65E0\u5FC3\u7737\u604B\u8FD9\u6625\u5149\uFF0C\u7D27\u63A5\u7740\u4E00\u8BB0\u98DE\u817F\u8F70\u5728\u59D0\u59D0\u7684\u5C0F\u8179\u3002</p><p>\u5979\u88AB\u8E22\u5F97\u76F4\u98DE\u51FA\u53BB\uFF0C\u649E\u5728\u5899\u58C1\uFF0C\u72FC\u72C8\u7684\u843D\u5728\u5730\u9762\uFF0C\u626C\u8D77\u8BB8\u591A\u5C18\u3002</p><p>\u5979\u60F3\u7AD9\u8D77\u8EAB\uFF0C\u5374\u65E0\u529B\u3002\u53EA\u6709\u501A\u5899\u5750\u5B9A\uFF0C\u4E00\u624B\u6309\u7740\u5267\u75DB\u7684\u5C0F\u8179\uFF0C\u4E00\u624B\u7D27\u63E1\u534A\u622A\u65AD\u5251\uFF0C\u63D2\u8FDB\u5730\u9762\uFF0C\u4ECD\u662F\u652F\u6491\u7740\u60F3\u52C9\u5F3A\u3002\u800C\u4E00\u5BF9\u7FC5\u8180\u5DF2\u644A\u5F00\u3002</p><p>\u6BB7\u7EA2\u7684\u9C9C\u8840\u4ECE\u5979\u5634\u89D2\u6EA2\u51FA\uFF0C\u6CBF\u7740\u4E0B\u989D\u5B8C\u7F8E\u7684\u7EBF\u5F62\u6EF4\u843D\u3002\u6EF4\u5728\u5979\u66B4\u9732\u7684\u534A\u53EA\u4E73\u623F\u548C\u624B\u81C2\uFF0C\u5C31\u50CF\u96EA\u4E2D\u7684\u8840\u6E0D\uFF0C\u5206\u5916\u8273\u3002</p><p>\u686B\u6469\u4F9D\u7136\u50CF\u88AB\u6FC0\u6012\u7684\u731B\u517D\u90A3\u6837\u6251\u4E0A\u6765\uFF0C\u5374\u88AB\u5979\u66B4\u9732\u7684\u80F8\u578B\u5206\u5F00\u4E86\u795E\u3002</p><p>\u5979\u771F\u7684\u592A\u7F8E\uFF0C\u54EA\u6015\u66B4\u6012\u7684\u91CE\u517D\u4E5F\u4F1A\u5206\u5FC3\u9A7B\u8DB3\u3002</p><p>\u7834\u88C2\u7684\u94E0\u7532\uFF0C\u770B\u5F97\u89C1\u4E73\u6655\u7684\u8FB9\u6CBF\uFF0C\u4ED6\u4F38\u624B\u7275\u5F15\uFF0C\u60F3\u628A\u5979\u4E73\u5934\u4E5F\u66DD\u5149\u3002\u5374\u53D1\u73B0\u4ED6\u8FD8\u6709\u767D\u8272\u7684\u7EF7\u5E26\u675F\u80F8\u3002\u94E0\u7532\u7D27\u5BC6\u5730\u8D34\u5408\uFF0C\u7EF7\u5E26\u867D\u88AB\u7834\u788E\u91D1\u5C5E\u7684\u8FB9\u7F18\u5272\u5F00\u5C11\u8BB8\uFF0C\u72ED\u5C0F\u7684\u7A7A\u95F4\uFF0C\u5374\u96BE\u4EE5\u518D\u5BB9\u7EB3\u624B\u6307\u7684\u52FE\u5F15\u3002</p><p>\u59D0\u59D0\u8FD8\u5728\u91CD\u521B\u4E4B\u4E2D\uFF0C\u6C14\u606F\u96BE\u5300\u3002\u8FDE\u8BF4\u8BDD\u7684\u6C14\u529B\u4E5F\u65E0\uFF0C\u53EA\u80FD\u8C61\u5F81\u6027\u5730\u7528\u624B\u963B\u9694\u4ED6\u7684\u653E\u8086\u3002</p><p>\u686B\u6469\u5FFD\u89C6\u5979\u7684\u62B5\u6297\uFF0C\u7531\u4E73\u6C9F\u5904\u63A2\u5165\uFF0C\u56DE\u907F\u4E86\u7A7A\u95F4\u7684\u5C34\u5C2C\u3002</p><p>\u90A3\u4E00\u523B\u82CD\u5170\u7684\u9762\u8272\u4F9D\u7136\u662F\u5B81\u9759\uFF0C\u56E0\u4E3A\u5979\u77E5\u9053\uFF0C\u5373\u4FBF\u53EB\u558A\uFF0C\u4EA6\u53EA\u4F1A\u5F92\u589E\u4ED6\u7684\u90AA\u6B32\u3002\u7136\u800C\u5979\u9A97\u4E0D\u8FC7\u81EA\u5DF1\u7684\u5FC3\u8DF3\uFF0C\u5F53\u4ED6\u7684\u624B\u6307\u94BB\u8FDB\u7EF7\u5E26\u7684\u7F1D\u9699\uFF0C\u6309\u5728\u5979\u4E73\u623F\u7684\u96EA\u767D\uFF0C\u6307\u5C16\u90FD\u89E6\u5230\u6697\u85CF\u7684\u84D3\u857E\uFF0C\u5979\u627F\u8BA4\u4ECE\u672A\u6709\u8FC7\u5982\u6B64\u7684\u72C2\u4E71\u5FC3\u8DF3\u3002</p><p>\u8FD9\u4E27\u5FC3\u75C5\u72C2\u7684\u686B\u6469\u3002\u5373\u4FBF\u653E\u4E86\u5C60\u5200\uFF0C\u4E5F\u53EA\u5269\u7269\u79CD\u672C\u80FD\u3002</p><p>\u53E6\u4E00\u53EA\u624B\u7C97\u66B4\u5730\u4F38\u8FDB\u5979\u77ED\u88D9\uFF0C\u88D9\u8EAB\u7A84\uFF0C\u4E00\u65F6\u4EA6\u6709\u4E9B\u5C34\u5C2C\u3002\u5979\u4F9D\u7136\u4FDD\u6301\u51B7\u51B7\u7684\u9762\u76F8\uFF0C\u800C\u686B\u6469\u5374\u65E0\u5FC3\u53BB\u770B\u3002</p><p>\u4ED6\u53EA\u987E\u8DEA\u4E0B\u8EAB\u53BB\uFF0C\u518D\u62AC\u8D77\u5979\u4E00\u53EA\u7389\u817F\u67B6\u5728\u81EA\u5DF1\u80A9\u4E0A\u3002\u53EF\u60DC\u90A3\u77ED\u88D9\u5B9E\u5728\u592A\u7A84\uFF0C\u65E0\u6CD5\u6446\u6210\u66F4\u7F8E\u5999\u7684\u9020\u578B\uFF0C\u4ED6\u770B\u89C1\u5979\u624B\u4E2D\u7684\u65AD\u5251\uFF0C\u80FD\u7528\u5B83\u5212\u5F00\u5979\u7684\u88D9\u3002</p><p>\u300C\u686B\u6469\u2026\u2026\u686B\u6469\u2026\u2026\u300D</p><p>\u5979\u7EC8\u4E8E\u6709\u6C14\u529B\u53EF\u4EE5\u5F00\u53E3\u52C9\u529B\u7684\u8BF4\u8BDD\uFF0C\u5979\u7528\u6700\u4F4E\u6C89\u51DD\u91CD\u5374\u60A6\u8033\u7684\u58F0\u97F3\uFF0C\u5E0C\u671B\u4F20\u5230\u4ED6\u7684\u5FC3\u7075\uFF0C\u54EA\u6015\u90A3\u5904\u53EA\u4E00\u7EBF\u5149\u3002</p><p>\u5979\u8BF4\uFF1A\u300C\u686B\u6469\u2026\u2026\u686B\u6469\u2026\u2026\u662F\u59D0\u59D0\uFF0C\u686B\u6469\uFF0C\u6211\u662F\u59D0\u59D0\u3002\u7231\u4F60\u7684\u59D0\u59D0\u3002\u300D</p><p>\u686B\u6469\u6296\u52A8\u4E00\u4E0B\u8EAB\u4F53\uFF0C\u7728\u4E86\u7728\u773C\u3002\u5145\u6EE1\u4FB5\u72AF\u6027\u52A8\u4F5C\u4E5F\u505C\u4E0B\u3002</p><p>\u300C\u686B\u6469\u2026\u2026\u686B\u6469\u2026\u2026\u662F\u4F60\u5417\uFF0C\u686B\u6469\uFF1F\u4F60\u653E\u5F00\u6211\uFF0C\u6211\u662F\u59D0\u59D0\u3002\u662F\u59D0\u59D0\u3002\u4F60\u662F\u6C99\u8FE6\uFF0C\u662F\u8FE6\u84DD\u65CF\u7684\u6700\u5584\u826F\u7684\u7687\u5B50\uFF0C\u686B\u6469\u3002\u300D</p><p>\u300C\u5443\u2014\u2014\u300D\u686B\u6469\u7ADF\u771F\u7684\u62BD\u79BB\u76D8\u8E1E\u4E24\u5904\u7684\u53CC\u624B\u3002\u60CA\u5F02\u53C8\u832B\u7136\uFF0C\u5730\u671B\u671B\u70DB\u5149\uFF0C\u671B\u671B\u82CD\u5170\u3002</p><p>\u300C\u5929\u7A7A\u57CE\uFF0C\u686B\u6469\u3002\u4F60\u7684\u5BB6\u56ED\u3002\u686B\u6469\uFF0C\u686B\u6469\u3002\u6162\u6162\u5730\uFF0C\u686B\u6469\u2026\u2026\u300D</p><p>\u4ED6\u6447\u6447\u5934\uFF0C\u4E00\u53CC\u624B\u63D2\u5728\u53D1\u95F4\uFF0C\u75F4\u75F4\u671B\u7740\u82CD\u5170\u7684\u4E00\u5BF9\u7FBD\u7FFC\uFF0C\u5979\u7684\u76EE\u5149\u6E29\u99A8\u3002</p><p>\u5728\u686B\u6469\u7684\u9762\u4E0A\uFF0C\u6E10\u56DE\u590D\u4E00\u4E1D\u5F80\u65E5\u7684\u6C14\u606F\u3002</p><p>\u300C\u686B\u6469\uFF0C\u6276\u6211\u8D77\u6765\uFF0C\u686B\u6469\u3002\u6211\u53D7\u4F24\u4E86\uFF0C\u686B\u6469\u3002\u300D</p><p>\u686B\u6469\u5F2F\u4E0B\u8170\uFF0C\u4F38\u51FA\u624B\uFF0C\u4EA6\u6B63\u4EA6\u90AA\u3002\u63FD\u8D77\u82CD\u5170\u7684\u7EA4\u8170\u3002\u4EE4\u5979\u6B23\u6170\u7684\u662F\uFF0C\u53EF\u4EE5\u91CD\u65B0\u611F\u89C9\u5230\u4ED6\u638C\u5FC3\u6E29\u5EA6\u3002</p><p>\u5979\u7B11\u3002</p><p>\u5C31\u50CF\u90A3\u5929\u4E00\u6837\uFF0C\u5979\u7528\u7FC5\u8180\u56F4\u6210\u6700\u5C0F\u7684\u89D2\u5EA6\uFF0C\u505A\u6210\u5C0F\u5C0F\u5E10\u7BF7\uFF0C\u628A\u4ED6\u5305\u56F4\u3002\u67D4\u548C\u800C\u6E29\u6696\u3002</p><p>\u2014\u2014\u300C\u539F\u8C05\u6211\uFF0C\u686B\u6469\u3002\u6211\u5E76\u4E0D\u6740\u4F60\uFF0C\u800C\u662F\u706D\u9B54\uFF0C\u800C\u662F\u8D85\u5EA6\u3002\u300D</p><p>\u4ED6\u7684\u9C9C\u8840\u987A\u7740\u534A\u652F\u5DE8\u96C0\u7684\u5251\u67C4\u4E00\u76F4\u6D41\u4E0B\u6765\uFF0C\u6D41\u4E0B\u6765\u3002\u6D41\u5728\u5979\u7FC5\u8180\u7684\u767D\u7FBD\uFF0C\u6D41\u8FC7\u5979\u7684\u4E73\u623F\u548C\u5C0F\u8179\uFF0C\u4E0E\u5979\u7684\u9C9C\u8840\u878D\u5316\u4E3A\u4E00\u3002</p><p>\u65AD\u5251\u7EC8\u4E8E\u4E0D\u504F\u4E0D\u501A\u5730\u8D2F\u7A7F\u686B\u6469\u7684\u5FC3\u810F\u3002</p><p>\u5979\u7B11\uFF0C\u5728\u7B11\u5BB9\u7684\u80CC\u540E\u5C3D\u662F\u4EA4\u7EC7\u7740\u4E8C\u5341\u4F59\u5E74\u7684\u56DE\u5FC6\u548C\u6C38\u751F\u5E0C\u7FFC\u3002\u90A3\u4E2A\u65F6\u5019\uFF0C\u4E16\u754C\u662F\u6E29\u99A8\u800C\u5B81\u9759\u7684\u3002</p><p>\u90A3\u4E0D\u518D\u6709\u529F\u7F6A\uFF0C\u4E5F\u65E0\u8BBA\u795E\u9B54\u3002\u53EA\u6709\u5C81\u6708\u5982\u6B4C\u3002</p><p>\u686B\u6469\u7684\u8EAB\u8EAF\u7F13\u6162\u7684\u503E\u5012\u4E0B\u6765\uFF0C\u538B\u5728\u82CD\u5170\u7684\u8EAB\u4E0A\u3002\u5F20\u5F00\u53CC\u81C2\uFF0C\u9759\u5728\u5979\u7684\u62E5\u62B1\u3002</p><p>\u4ED6\u4EEC\u66FE\u5728\u540C\u4E00\u8BB0\u5B50\u5BAB\u7684\u5305\u56F4\u4E2D\uFF0C\u4EE5\u8FD9\u6837\u7684\u59FF\u52BF\u4EB2\u5BC6\u65E0\u95F4\u3002</p><p>\u5979\u4E8E\u662F\u5E7B\u89C9\u8537\u8587\u82B1\u74E3\uFF0C\u8FD8\u6709\u6D77\u9E25\u5728\u4ED6\u4EEC\u8EAB\u8FB9\u98D8\u96F6\u98DE\u821E\uFF0C\u90A3\u5C31\u50CF\u793C\u8D5E\u7684\u97F3\u9636\uFF0C\u56ED\u821E\u7684\u9635\u578B\u3002</p><p>\u5979\u7A81\u7136\u53C8\u56DE\u5FC6\u5927\u796D\u53F8\u7684\u846C\u793C\uFF0C\u60F3\u8D77\u90A3\u558B\u8840\u7684\u5DE8\u5927\u767D\u9E1F\u548C\u98D8\u6447\u7684\u57CE\u5E02\u3002\u800C\u5728\u8FD9\u4E00\u523B\uFF0C\u662F\u5979\u4E0D\u613F\u65C1\u987E\u7684\u3002</p><p>\u8FD9\u523B\uFF0C\u5979\u6DE1\u5FD8\u81EA\u5DF1\u662F\u5973\u7687\u3002</p><p>\u53EA\u662F\u60F3\u50CF\u4E00\u4E2A\u59D0\u59D0\u90A3\u6837\u62B1\u7740\u81EA\u5DF1\u7684\u5F1F\u5F1F\u3002</p><p>\u5979\u6162\u6162\u5730\u5750\u8D77\u8EAB\uFF0C\u4FDD\u6301\u8DEA\u59FF\u3002\u7136\u540E\u8F7B\u8F7B\u5730\uFF0C\u8F7B\u8F7B\u5730\u8BA9\u686B\u6469\u8DB4\u5728\u5979\u5927\u817F\uFF0C\u5C31\u50CF\u7761\u719F\u3002</p><p>\u767D\u8272\u7684\u7FC5\u8180\uFF0C\u5408\u5E76\u6210\u6700\u5C0F\u7684\u89D2\u5EA6\u3002\u50CF\u4E00\u5E8A\u88AB\u8925\u7684\u5305\u56F4\uFF0C\u5E0C\u671B\u4ED6\u53EF\u4EE5\u611F\u5230\u6696\u3002</p><p>\u5728\u8FD9\u79BB\u522B\u60C6\u6005\u7684\u6DF1\u79CB\u5929\u6C14\u3002</p>',116);function t(c,n){return r}var _=e(a,[["render",t]]);export{_ as default};
