import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const a={},r=p('<h1 id="x" tabindex="-1"><a class="header-anchor" href="#x" aria-hidden="true">#</a> X\u2026</h1><p>\u90A3\u65E5\u8BB8\u591A\u4EBA\u76EE\u7779\u4E86\u8FD9\u573A\u68A6\u5E7B\u822C\u7684\u4E71\u4F26\u5267\u76EE\u3002</p><p>\u8FD9\u4F7F\u5F97\u4ED6\u4EEC\u8840\u8109\u94FA\u5F20\u3002</p><p>\u6563\u53BB\u4E4B\u540E\uFF0C\u56DE\u53BB\u5BB6\u4E2D\u3002\u6709\u59BB\u5BA4\u7684\u7537\u5B50\uFF0C\u5927\u591A\u4EE4\u59BB\u5BA4\u6109\u60A6\u3002\u800C\u65E0\u59BB\u5BA4\u6216\u59BB\u5BA4\u5728\u6708\u4E8B\u4E2D\u7684\uFF0C\u7EB7\u7EB7\u76F8\u9047\u5728\u5993\u9986\u3002</p><p>\u5373\u4FBF\u662F\u62DC\u4E9A\u65AF\u7684\u5FE0\u81E3\u4E5F\u4E0D\u518D\u8C08\u8BBA\u5965\u6258\u5927\u5E1D\u7684\u9A7E\u5D29\u3002\u4EE5\u53CA\u90A3\u4F4D\u6028\u6B7B\u7684\u516C\u4E3B\u8D1D\u73B2\u8FBE\uFF0C\u4EA6\u4E0D\u518D\u4E3A\u4EBA\u6302\u5FF5\u3002</p><p>\u7B2C\u4E8C\u5929\u7684\u6668\u5149\u4E2D\uFF0C\u6574\u4E2A\u7687\u57CE\u518D\u6CA1\u6709\u4EBA\u8212\u9192\u3002</p><p>\u7AE5\u989C\u7684\u5996\uFF0C\u5BC4\u6258\u8718\u86DB\u7684\u578B\uFF0C\u722C\u8FC7\u7687\u57CE\u7684\u6BCF\u5904\u89D2\u843D\u3002\u5B83\u5728\u9634\u6697\u5730\uFF0C\u5410\u51FA\u5267\u6BD2\u7684\u4E1D\uFF0C\u5B83\u541E\u566C\u4EBA\u7684\u5FC3\u80BA\uFF0C\u7B11\u996E\u4EBA\u7C7B\u7684\u8840\u3002\u5200\u67AA\u4F24\u4E0D\u4E86\u5B83\uFF0C\u6CD5\u672F\u5728\u5996\u9B54\u7684\u9762\u524D\u7EFD\u653E\uFF0C\u72B9\u5982\u70DF\u82B1\u3002</p><p>\u8FD9\u8EAB\u9644\u6028\u5FF5\u7684\u5996\uFF0C\u772F\u7740\u5B83\u7684\u53CC\u773C\uFF0C\u7B11\u5BB9\u5373\u996E\u8840\u3002\u7834\u5F00\u6CE5\u571F\u7684\u51B0\u51B7\uFF0C\u996E\u98DF\u751F\u547D\u7684\u6C14\u7130\u3002\u5B69\u7AE5\u88AB\u5B83\u6495\u88C2\uFF0C\u5987\u5973\u7684\u5185\u810F\u662F\u7518\u7F8E\u7684\u5BB4\u3002\u4F60\u8EB2\u907F\u4E0D\u4E86\u5B83\uFF0C\u56E0\u4E3A\u5B83\u662F\u9B54\u9B3C\u7684\u4EC6\u4ECE\u3002</p><p>\u5175\u58EB\u96C6\u7ED3\u8D77\u6765\uFF0C\u7528\u91D1\u5C5E\u780D\u523A\u5B83\uFF0C\u7528\u706B\u7130\u711A\u70E7\u3002\u5B83\u4EE5\u516B\u53EA\u811A\uFF0C\u8EB2\u907F\u51E1\u4E16\u7684\u653B\uFF0C\u5207\u5272\u4EBA\u7684\u8EAB\u4F53\uFF0C\u5728\u8FD9\u6B7B\u4EA1\u7684\u57CE\u5E02\uFF0C\u5F00\u59CB\u6B7B\u4EA1\u7684\u5BB4\u3002</p><p>\u5E73\u6C11\u5954\u8D70\u7684\u9003\u547D\uFF0C\u5FF5\u795E\u7684\u540D\uFF0C\u5728\u5B83\u662F\u65E0\u7528\u3002\u4F60\u542B\u7740\u4EBA\u7684\u5FC3\u810F\uFF0C\u9C9C\u8840\u5728\u5634\u89D2\u6D41\u6EA2\uFF0C\u548C\u7740\u7EFF\u8272\u553E\u6DB2\uFF0C\u8165\u7684\u5473\u89C9\u3002</p><p>\u5927\u4E3B\u6559\u8BA4\u51FA\u5B83\u7684\u51E1\u8EAB\uFF0C\u5E94\u4E86\u53E4\u65E9\u7684\u7ECF\u3002\u4F46\u5B83\u662F\u6028\u6076\u7684\u7075\uFF0C\u4E0D\u95FB\u795E\u660E\u7684\u6559\u3002\u5B83\u628A\u5927\u4E3B\u6559\u6EE1\u662F\u76B1\u7EB9\u7684\u8138\u541E\u4E0B\uFF0C\u7C89\u788E\u4ED6\u7684\u5934\u9AA8\u3002\u957F\u957F\u7684\u820C\uFF0C\u4E00\u7AEF\u8214\u98DF\u4ED6\u8116\u5B50\u4E0A\u55B7\u51FA\u7684\u8840\uFF0C\u8111\u6D46\u6162\u6162\u7684\u5E72\u3002</p><p>\u300C\u8FE6\u697C\u686B\u6469\u3002\u4F60\u662F\u6211\u7684\u4E3B\u4EBA\u3002\u4F60\u8981\u6211\u4EE5\u6B7B\u4EA1\u7684\u4EEA\u5F0F\u51C0\u5316\u8FD9\u80AE\u810F\u4EBA\u4E16\u3002\u800C\u6B7B\u53BB\u7684\u90FD\u5C06\u53D8\u6210\u6BAD\u5C38\uFF0C\u4E92\u76F8\u5206\u98DF\uFF0C\u4E92\u76F8\u8865\u7ED9\u3002\u9075\u5FAA\u4F60\u7684\u547D\uFF0C\u6740\u4F60\u7684\u654C\u3002\u300D</p><p>\u2026\u2026</p><p>\u8FD9\u591C\uFF0C\u5B83\u4EE5\u86DB\u86DB\u7684\u8EAF\u4F53\u8840\u6D17\u5927\u5730\u3002</p><p>\u5356\u9C9C\u82B1\u7684\u5C0F\u5973\u5B69\u8737\u7F29\u5728\u5899\u89D2\u54ED\u6CE3\uFF0C\u6B7B\u53BB\u4E5F\u4FBF\u4E0D\u518D\u6709\u6CEA\u3002\u5E03\u7ED2\u73A9\u5177\u6389\u5728\u90A3\u91CC\uFF0C\u6D78\u5728\u8840\u6C34\u7684\u9A9A\u3002</p><p>\u59EC\u5A1C\u4EA6\u662F\u6D6A\u6F2B\u7684\u7AE5\uFF0C\u5374\u6709\u627F\u53D7\u4E0D\u8D77\u7684\u91CD\u3002\u5B83\u90A3\u957F\u7740\u7ED2\u6BDB\u7684\u8DB3\uFF0C\u9510\u5229\u5730\u5207\u5F00\u4EBA\u7C7B\u76AE\u8089\u3002\u73A9\u5076\u6D78\u900F\u8840\u6E0D\uFF0C\u65AD\u4E86\u81C2\u8180\uFF0C\u4EA6\u662F\u6CBE\u8840\u77F3\u68C9\u3002</p><p>\u65E0\u751F\u547D\u3002\u65E0\u75DB\u695A\u3002\u65E0\u6765\u65F6\u3002</p><p>\u2026\u2026</p><p>\u300C\u686B\u6469\uFF0C\u4F60\u4E0D\u53EF\u4EE5\u518D\u6740\u4EBA\u3002\u300D</p><p>\u82CD\u5170\u8DB4\u5728\u4ED6\u7684\u81C2\u5F2F\u3002\u4ED6\u4EEC\u8EBA\u5728\u4F17\u795E\u4E4B\u5854\u7684\u6781\u9876\uFF0C\u5728\u516B\u6839\u77F3\u67F1\u4E4B\u95F4\uFF0C\u7FC5\u5408\u6210\u4E00\u5F20\u6E29\u5E8A\u3002</p><p>\u300C\u8FD9\u4E16\u754C\u6240\u6709\u7684\u4EBA\u4E2D\uFF0C\u59D0\u59D0\uFF0C\u6211\u4F1A\u53EA\u7231\u4F60\u4E00\u4EBA\u3002\u300D</p><p>\u5979\u543B\u4ED6\uFF0C\u4ED6\u4EB2\u6635\u5730\u629A\u6469\u5979\u7EC6\u8F6F\u7684\u9634\u6BDB\u3002</p><p>\u300C\u59D0\u59D0\uFF0C\u300D\u4ED6\u4ECE\u9999\u543B\u4E2D\u9003\u51FA\u6765\uFF0C\u4ED6\u8BF4\uFF1A\u300C\u59D0\u59D0\u7684\u90A3\u91CC\uFF0C\u641E\u5230\u686B\u6469\u5F88\u8212\u670D\u3002\u300D</p><p>\u8FE6\u697C\u82CD\u5170\u987D\u76AE\u5730\u626D\u8FC7\u8170\u8EAB\uFF0C\u7FD8\u81C0\u4F18\u7F8E\u5730\u6643\u8FC7\u4ED6\u773C\u524D\u3002\u4ED6\u8C03\u76AE\u7684\u62BD\u6253\u3002</p><p>\u300C\u54C8\uFF0C\u686B\u6469\uFF0C\u4F60\u597D\u8BA8\u538C\u5462\u3002\u300D</p><p>\u5979\u7684\u773C\u795E\u6D0B\u6EA2\u7740\u4F3C\u6C34\u67D4\u60C5\uFF0C\u53E3\u9F3B\u547C\u51FA\u6696\u6696\u7684\u6C14\u6D41\uFF0C\u4EE4\u4ED6\u611F\u5230\u75D2\u3002</p><p>\u4ED6\u53C8\u5FCD\u4E0D\u4F4F\u60F3\u4E0E\u59D0\u59D0\u9020\u7231\uFF0C\u4E8E\u662F\u5524\u9192\u86C7\u5996\u3002</p><p>\u300C\u59EC\u5A1C\u662F\u7528\u4EE5\u5C60\u6740\u7684\u3002\u800C\u8D1D\u73B2\u8FBE\uFF0C\u4F60\u8981\u79F0\u8C22\u6211\u3002\u56E0\u4E3A\u6211\u5141\u4F60\u540C\u6211\u5171\u4EAB\u8FD9\u7F8E\u4E3D\u7684\u5973\u3002\u300D</p><p>\u5B83\u9075\u547D\u722C\u81F3\u686B\u6469\u7684\u8EAB\u8FB9\uFF0C\u543B\u4ED6\u811A\u9762\u3002</p><p>\u300C\u6211\u8981\u4F60\u7AD9\u8D77\u6765\uFF0C\u548C\u6211\u5171\u4EAB\u8FD9\u7F8E\u4E3D\u7684\u5973\u3002\u6211\u8981\u4F60\u50AC\u751F\u5979\u4F53\u5185\u70ED\u7684\u8BF8\u6C34\uFF0C\u5728\u6211\u6DEB\u5979\u7684\u65F6\u5019\uFF0C\u4F60\u8981\u6DEB\u5979\u5176\u4F59\u7684\u7A74\u3002\u300D</p><p>\u4ED6\u80CC\u8FC7\u59D0\u59D0\u7684\u8EAB\u4F53\uFF0C\u8BA9\u5979\u50CF\u56DB\u811A\u7740\u5730\u7684\u517D\u7C7B\u3002\u4ED6\u628A\u624B\u653E\u843D\u5979\u767D\u7F8E\u7684\u81C0\u90E8\uFF0C\u4ED6\u662F\u5E78\u798F\u7684\u3002</p><p>\u4ED6\u7684\u9634\u830E\u56E0\u5979\u7684\u7F8E\u800C\u66B4\u8038\uFF0C\u4ED6\u8981\u6DEB\u5979\u7684\u83CA\u7A74\u3002</p><p>\u4ED6\u8BF4\uFF1A\u300C\u59D0\u59D0\uFF0C\u6211\u4F1A\u8F7B\uFF0C\u4E0D\u518D\u5F04\u75BC\u4F60\u3002\u300D</p><p>\u82CD\u5170\u8BF4\uFF1A\u300C\u686B\u6469\uFF0C\u90A3\u2026\u2026\u4E0D\u53EF\u4EE5\u3002\u300D</p><p>\u4ED6\u65E0\u89C6\u5979\u7684\u62D2\u7EDD\u3002\u90A3\u4E48\u7F8E\u4E3D\u7684\u81C0\uFF0C\u4ED6\u662F\u5FC5\u987B\u5360\u6709\u7684\u3002</p><p>\u4ED6\u52AA\u529B\u5730\u63D2\u5411\u5185\uFF0C\u5979\u626D\u52A8\u8D77\u6765\u3002\u83CA\u7A74\u5E72\u800C\u6DA9\uFF0C\u686B\u6469\u4E8E\u662F\u8BF4\uFF1A\u300C\u8D1D\u73B2\u8FBE\uFF0C\u6211\u7684\u4EC6\uFF0C\u4F60\u8981\u4EE4\u5979\u6D41\u51FA\u591A\u7684\u6C34\u3002\u300D</p><p>\u5B83\u4E8E\u662F\u722C\u5230\u82CD\u5170\u7684\u8EAB\u540E\uFF0C\u5B83\u7528\u957F\u820C\u4F38\u8FDB\u5979\u4EE5\u5185\u3002\u957F\u820C\u5E26\u7740\u50AC\u60C5\u7684\u6BD2\uFF0C\u5B83\u4F38\u8FDB\u4E24\u5BF8\uFF0C\u5373\u505C\u4E0B\u6765\uFF0C\u8D2A\u5A6A\u7684\u8214\u52A8\u9634\u9053\u5185\u58C1\u7684\u76B1\u8936\u3002</p><p>\u5B83\u7684\u9F3B\u5C16\u5728\u5979\u9634\u8482\u4E0A\u63A5\u89E6\uFF0C\u5E76\u7528\u624B\u6307\u8F7B\u6309\u9634\u5507\u7684\u74E3\u3002</p><p>\u800C\u4ED6\u628A\u9634\u830E\u653E\u8FDB\u59D0\u59D0\u7684\u53E3\u4E2D\uFF0C\u544A\u8BC9\u5979\u8981\u8214\u548C\u542E\u5438\u3002</p><p>\u82CD\u5170\u88AB\u6311\u5F04\u7684\u60F3\u8981\u5C16\u53EB\uFF0C\u5979\u7FFB\u4E86\u767D\u773C\uFF0C\u5374\u4E0D\u80FD\u53EB\u51FA\u58F0\u97F3\uFF0C\u56E0\u4E3A\u686B\u6469\u7684\u9634\u830E\u62B5\u5728\u5589\u5934\uFF0C\u8FD9\u4EE4\u5979\u80C0\u7EA2\u4E86\u8138\u3002</p><p>\u82CD\u5170\u6DEB\u8361\u7684\uFF0C\u5782\u843D\u7684\u53D1\u4E1D\u8FDE\u7740\u5F1F\u5F1F\u7684\u9634\u6BDB\u3002</p><p>\u5979\u628A\u5F1F\u5F1F\u7684\u9634\u830E\u542B\u5728\u53E3\u4E2D\uFF0C\u54C1\u4ED6\u7684\u5473\u9053\u3002\u5979\u77E5\u9053\u81EA\u5DF1\u6240\u505A\u7684\u3002</p><p>\u5996\u5973\u7684\u820C\u5728\u5979\u7684\u9634\u9053\u4E2D\u6E38\u5203\u6709\u4F59\uFF0C\u706B\u70E7\u4E00\u6837\u7684\u8EAF\u4F53\u4FBF\u53C8\u6709\u4E86\u9AD8\u6F6E\u7684\u8822\u52A8\u3002</p><p>\u800C\u686B\u6469\u5374\u5148\u5979\u5230\u9AD8\u6F6E\uFF0C\u4ED6\u628A\u7CBE\u6DB2\u5C04\u8FDB\u59D0\u59D0\u7684\u5589\u5499\u3002\u62BD\u51FA\u7684\u65F6\u5019\uFF0C\u4E00\u6761\u767D\u8272\u7684\u7EC6\u7EBF\u8FDE\u7740\u9F9F\u5934\uFF0C\u53E6\u4E00\u7AEF\u662F\u82CD\u5170\u7684\u820C\u3002</p><p>\u5996\u5973\u968F\u5373\u79BB\u5F00\u5979\u7684\u9634\u9053\uFF0C\u686B\u6469\u62AC\u8D77\u5B83\u7684\u9762\u3002</p><p>\u5B83\u548C\u5979\u5982\u6B64\u76F8\u4F3C\uFF0C\u5373\u4FBF\u6DEB\u7CDC\u65F6\u7684\u795E\u8272\u4E5F\u662F\u4E00\u6837\u7684\u3002</p><p>\u4ED6\u628A\u9634\u830E\u653E\u8FDB\u5B83\u7684\u53E3\u8154\uFF0C\u9AD8\u6F6E\u540E\u7684\u9634\u830E\u662F\u8F6F\u7684\u3002\u800C\u5996\u5973\u7684\u773C\u775B\u95EA\u70C1\u7EFF\u8272\u7684\u5149\uFF0C\u518D\u4EE5\u4E73\u6C41\u62B9\u5728\u686B\u6469\u7684\u5C0F\u8179\uFF0C\u4ED6\u628A\u5B83\u62BD\u51FA\u7684\u65F6\u5019\uFF0C\u53C8\u662F\u94A2\u94C1\u4E00\u6837\u7684\u575A\u786C\u3002</p><p>\u4ED6\u8BF4\uFF1A\u300C\u59D0\u59D0\uFF0C\u6211\u8981\u6DEB\u904D\u4F60\u7684\u6BCF\u4E2A\u7A74\u3002\u300D</p><p>\u5996\u5973\u628A\u4E73\u6C41\u548C\u5979\u7684\u6DEB\u6DB2\u6D82\u5728\u83CA\u7A74\u7684\u5468\u56F4\uFF0C\u4E8E\u662F\u686B\u6469\u90A3\u6E7F\u6ED1\u4E14\u5C16\u9510\u7684\u9634\u830E\u4FBF\u6E10\u523A\u8FDB\u53BB\u3002</p><p>\u5979\u662F\u8DB4\u7740\u7684\uFF0C\u50CF\u6BCD\u72AC\u4E00\u6837\u803B\u8FB1\u3002\u5979\u53D7\u7740\u6495\u5F00\u8EAB\u4F53\u7684\u75DB\uFF0C\u5979\u7684\u5F1F\u5F1F\u8981\u6DEB\u904D\u6BCF\u4E00\u4E2A\u7A74\u3002</p><p>\u809B\u4EA4\u4E8E\u5973\u4EBA\u6765\u8BF4\u672C\u662F\u65E0\u5FEB\u611F\u7684\uFF0C\u4F46\u8D1D\u73B2\u8FBE\u5374\u6DEB\u5DE7\u3002\u5B83\u9075\u4ECE\u686B\u6469\u7684\u547D\uFF0C\u6DEB\u5979\u5176\u4F59\u7684\u7A74\u3002\u5B83\u7528\u624B\u62B1\u5979\u7684\u819D\uFF0C\u56DE\u5230\u521A\u624D\u7684\u59FF\u52BF\uFF0C\u7528\u957F\u957F\u7684\u820C\u8214\u9634\u9053\u7684\u5185\u58C1\u3002</p><p>\u82CD\u5170\u7684\u4F53\u6DB2\u8D8A\u6765\u8D8A\u4E30\u76DB\uFF0C\u5979\u5DF2\u88AB\u5996\u5973\u7684\u53E3\u820C\u9001\u62B5\u534A\u7A7A\u3002</p><p>\u5979\u5F20\u5F00\u7FC5\u8180\uFF0C\u5E7B\u89C9\u5728\u98DE\u3002\u4F46\u686B\u6469\u91CD\u91CD\u5730\u6309\u5728\u5979\u7684\u7FD8\u8D77\u7684\u81C0\uFF0C\u628A\u5979\u7684\u8170\u538B\u4F4E\uFF0C\u8BA9\u9634\u830E\u6B63\u4E2D\u83CA\u7A74\u7684\u4F4D\u7F6E\u3002</p><p>\u800C\u5996\u5973\u7684\u86C7\u8EAB\u4EA6\u7F20\u7ED5\u7740\u5979\uFF0C\u5C16\u9510\u7684\u9CDE\u7247\u5272\u75DB\u4E86\u5979\u7684\u4E73\u623F\u3002</p><p>\u686B\u6469\u5728\u59D0\u59D0\u7684\u83CA\u7A74\u5185\u4F53\u5473\u7740\u53E6\u7C7B\u7684\u523A\u6FC0\uFF0C\u4ED6\u4E00\u52A8\uFF0C\u5979\u5373\u9AD8\u53EB\u3002</p><p>\u5979\u7684\u53E3\u8154\uFF0C\u6B8B\u4F59\u7740\u4ED6\u7684\u7CBE\u6DB2\u3002\u9634\u6237\u5185\u4EA6\u5F25\u6563\u7740\u5996\u5973\u7684\u6BD2\u6DB2\u3002\u4E00\u534A\u662F\u82E6\u6DA9\u548C\u75DB\u89C9\uFF0C\u4E00\u534A\u5374\u662F\u60C5\u6B32\u706B\u7130\u3002</p><p>\u5F53\u5979\u62BD\u6410\u7740\u5C16\u58F0\u558A\u53EB\uFF0C\u53EB\u5230\u54D1\u7136\u5931\u58F0\uFF0C\u5979\u81EA\u5DF1\u4E5F\u5206\u4E0D\u6E05\u662F\u75BC\u75DB\u8FD8\u662F\u6DEB\u8361\u3002</p><p>\u5979\u7684\u5C3F\u9053\u7EC8\u4E8E\u518D\u6B21\u731B\u70C8\u55B7\u5C04\uFF0C\u6709\u4E9B\u5C04\u5728\u5F1F\u5F1F\u7684\u8EAB\u4E0A\uFF0C\u6709\u4E9B\u5219\u843D\u5230\u5996\u5973\u53E3\u4E2D\u3002</p><p>\u4ED6\u548C\u5B83\u662F\u5174\u594B\u7684\uFF0C\u4ED6\u4EEC\u8D5E\u53F9\u5982\u6B64\u5927\u7684\u6C34\u91CF\u3002</p><p>\u82CD\u5170\u7EC8\u4E8E\u762B\u5012\u4E0B\u53BB\u3002</p><p>\u686B\u6469\u4E8E\u662F\u5C06\u5979\u62B1\u8D77\uFF0C\u8EBA\u5728\u4ED6\u7684\u81C2\u5F2F\u3002\u8D64\u8EAB\u88F8\u4F53\u7684\u7F8E\u5999\u59FF\u6001\uFF0C\u4E73\u623F\u7D27\u7D27\u5730\u8D34\u5728\u4ED6\u58EE\u9614\u7684\u80F8\u808C\u3002</p><p>\u5979\u534A\u660F\u534A\u8165\uFF0C\u6B32\u6B7B\u6B32\u751F\u3002\u5979\u5168\u8EAB\u90FD\u662F\u6F6E\u6E7F\uFF0C\u662F\u8F6F\u7684\u3002</p><p>\u83CA\u7A74\u5185\u7CBE\u6DB2\u5728\u5012\u704C\u800C\u51FA\uFF0C\u9634\u6237\u4EA6\u72FC\u85C9\u4E0D\u582A\u3002\u90A3\u4E9B\u55B7\u5C04\u51FA\u6765\u7684\u5927\u91CF\u900F\u660E\u6DB2\u4F53\uFF0C\u6B63\u4E00\u6EF4\u6EF4\u4E0D\u65AD\u987A\u5EF6\u817F\u90E8\u7684\u7EBF\u6761\u6D41\u8D70\u3002</p><p>\u2026\u2026</p><p>\u4ED6\u5429\u5490\u8D1D\u73B2\u8FBE\u5F52\u56DE\u4F11\u7720\uFF0C\u7136\u540E\u628A\u59D0\u59D0\u4E00\u76F4\u62B1\u5728\u6000\u4E2D\u3002\u5E76\u8F7B\u8F7B\u62ED\u64E6\u5979\u5634\u89D2\u7684\u7CBE\u6DB2\u3002\u8D64\u88F8\u7684\u59D0\u59D0\u4F9D\u7136\u8361\u6F3E\u5728\u9AD8\u6F6E\u7684\u4F59\u6CE2\uFF0C\u4E73\u623F\u8D77\u4F0F\uFF0C\u9888\u9AA8\u5FAE\u98A4\u3002</p><p>\u4ED6\u62ED\u5E72\u59D0\u59D0\u5634\u89D2\u7684\u7CBE\u6591\uFF0C\u8F7B\u8F7B\u7F15\u5F00\u542B\u8FDB\u53E3\u4E2D\u7684\u4E00\u7C07\u957F\u53D1\u3002</p><p>\u8FE6\u697C\u82CD\u5170\uFF0C\u5979\u662F\u4ED6\u7684\u59D0\u59D0\u3002\u6DEB\u800C\u7F8E\u3002</p><p>\u4ED6\u5728\u7B49\u5979\u9192\u6765\u3002\u4ED6\u60F3\u5979\u5E26\u4ED6\u98DE\uFF0C\u5C31\u50CF\u4ECE\u524D\u4E00\u6837\u7684\u5347\u817E\u548C\u98DE\u5760\u3002</p><p>\u5979\u56DE\u795E\u7684\u65F6\u5019\u662F\u7B11\u7740\u7684\uFF0C\u686B\u6469\u4E8E\u662F\u8BF4\uFF1A\u300C\u6211\u8981\u4F60\u5E26\u6211\u8D77\u98DE\uFF0C\u59D0\u59D0\u3002\u300D</p><p>\u5979\u66FE\u7ECF\u60F3\u4ECE\u8FD9\u7A97\u53E3\u8D77\u98DE\uFF0C\u5374\u88AB\u4ED6\u6263\u4F4F\u811A\u8E1D\uFF0C\u62C9\u56DE\u5730\u9762\u3002\u800C\u8FD9\u4E00\u6B21\uFF0C\u4ED6\u5374\u60F3\u5728\u9AD8\u7A7A\uFF0C\u61A7\u61AC\u4E00\u6B21\u771F\u6B63\u7684\u9AD8\u6F6E\u3002</p><p>\u5979\u4E8E\u662F\u8D77\u98DE\u3002</p><p>\u300C\u63E1\u7D27\u6211\uFF0C\u686B\u6469\u3002\u300D</p><p>\u300C\u6211\u4F1A\u7684\uFF0C\u59D0\u59D0\u3002\u6211\u63D2\u8FDB\u53BB\u4E86\uFF0C\u59D0\u59D0\u3002\u300D</p><p>\u300C\u5514\u2014\u2014\u8F7B\u70B9\uFF0C\u686B\u6469\u3002\u8F7B\u2014\u2014\u554A\u2026\u2026\u686B\u6469\u2026\u2026\u300D</p><p>\u4ED6\u63D2\u8FDB\u5979\u7684\u9634\u6237\uFF0C\u53EA\u62BD\u52A8\u4E00\u4E0B\uFF0C\u5C31\u611F\u5230\u90A3\u91CC\u6E10\u53D8\u5F97\u6F6E\u6E7F\u6E29\u6DA6\u3002\u5979\u7D27\u7D27\u7684\u62B1\u7740\u4ED6\uFF0C\u53D1\u73B0\u5728\u4ED6\u7684\u80CC\u4E0A\u7ADF\u6709\u4E00\u5904\u6307\u7532\u7684\u6293\u75D5\u3002\u5979\u65E0\u6687\u53BB\u95EE\uFF0C\u5979\u89C9\u5F97\u4ED6\u7684\u9634\u830E\u5C31\u50CF\u4E00\u67C4\u9AD8\u6602\u7684\u7F28\u67AA\u3002\u5979\u627F\u53D7\u7740\uFF0C\u4E00\u8FB9\u5C55\u7FFC\u5929\u7FD4\u3002</p><p>\u9AD8\u7A7A\u7684\u98CE\u75BE\uFF0C\u4ED6\u7684\u62BD\u63D2\u5374\u66F4\u7126\u6025\u3002\u7FCE\u62B1\u8D77\u4ED6\u7684\u8170\uFF0C\u4E00\u5BF9\u7F8E\u6EE1\u7684\u80F8\u90E8\u8D34\u5728\u4ED6\u5BBD\u539A\u7684\u80F8\u808C\uFF0C\u70ED\u6D41\u76F8\u4E92\u4F20\u9012\u3002\u5979\u7684\u53D1\u51CC\u4E71\u7684\u98D8\u821E\uFF0C\u5145\u6EE1\u60C5\u6B32\u7EFD\u653E\u7684\u91CE\u6027\u4E4B\u7F8E\u3002</p><p>\u4ED6\u7B11\uFF0C\u4ED6\u53EB\u5979\u59D0\u59D0\u3002</p><p>\u5979\u751A\u81F3\u95ED\u4E0A\u773C\uFF0C\u5728\u5F1F\u5F1F\u7684\u62BD\u52A8\u4E2D\u4E0D\u65AD\u9AD8\u5347\u3002</p><p>\u5979\u559C\u6B22\u98CE\u773C\u7684\u611F\u89C9\uFF0C\u5728\u5F1F\u5F1F\u7684\u6000\u62B1\u4E2D\u5F7C\u6B64\u4EAB\u53D7\u7740\u8FD9\u6837\u7684\u523A\u6FC0\u3002</p><p>\u300C\u554A\u2026\u2026\u554A\u2026\u2026\u518D\u2026\u2026\u5927\u529B\u70B9\u554A\u2026\u2026\u686B\u6469\u2026\u2026\u5927\u529B\u70B9\u3002\u300D</p><p>\u82CD\u5170\u547B\u541F\u7740\u81EA\u8BED\uFF0C\u5728\u8FD9\u9AD8\u7A7A\u7684\u98CE\u901F\u4E2D\uFF0C\u686B\u6469\u542C\u4E0D\u6E05\u5979\u7684\u8BF4\u8BDD\u3002</p><p>\u9AD8\u7A7A\u7684\u98CE\u662F\u547C\u5578\u7684\uFF0C\u4ED6\u548C\u5979\u7684\u8DDD\u79BB\u4E0D\u5230\u4E00\u5F20\u767D\u7EB8\u7684\u7A7A\u9699\uFF0C\u5374\u542C\u4E0D\u6E05\u6670\u5979\u7684\u8BF4\u8BDD\u3002</p><p>\u300C\u4EC0\u4E48\u2014\u2014\u59D0\u59D0\u2014\u2014\u4F60\u8BF4\u4EC0\u4E48\uFF1F\u300D</p><p>\u300C\u5514\u2014\u2014\u686B\u6469\u2014\u2014\u6211\u8BF4\u2014\u2014\u554A\u2026\u2026\u554A\u2026\u2026\u300D</p><p>\u300C\u4EC0\u4E48\u2014\u2014\u59D0\u59D0\u2014\u2014\u300D</p><p>\u300C\u554A\u2026\u2026\u6211\u8BF4\u2014\u2014\u686B\u6469\u2014\u2014\u6211\u8BF4\uFF0C\u518D\u5927\u529B\u70B9\u2026\u2026\u5927\u529B\u70B9\u2026\u2026\u686B\u6469\u3002\u300D</p><p>\u300C\u4EC0\u4E48\u2014\u2014\u300D</p><p>\u300C\u5443\u2014\u2014\u300D</p><p>\u4E00\u9635\u6781\u81F3\u7684\u5FEB\u611F\u5E2D\u5377\u8FC7\u6765\uFF0C\u82CD\u5170\u53C8\u7FFB\u4E86\u767D\u773C\uFF0C\u5934\u90E8\u7ADF\u50CF\u53D1\u75AF\u4F3C\u7684\u6446\u52A8\u7740\uFF0C\u8EAB\u4F53\u4EA6\u662F\u4E00\u9635\u7684\u75C9\u631B\uFF0C\u4E00\u53CC\u7FC5\u8180\u6025\u5267\u5730\u62CD\u6253\uFF0C\u7FFC\u671B\u5347\u5230\u51CC\u5BB5\u3002</p><p>\u686B\u6469\u6FC0\u70C8\u7684\u543B\u904D\u5979\u7684\u4E73\u6C9F\u3001\u96EA\u9888\u3001\u4E0B\u989A\u548C\u8033\u8DDF\u3002\u4ED6\u751A\u81F3\u5BB3\u6015\u88AB\u5979\u70EB\u4F24\u3002\u4ED6\u7684\u62E5\u62B1\u51E0\u4E4E\u4EE4\u5979\u7A92\u606F\uFF0C\u4E8E\u662F\u5979\u5F20\u5F00\u53E3\uFF0C\u62DA\u547D\u7684\u6D6A\u53EB\u7740\uFF0C\u72C2\u4E71\u7740\u3002</p><p>\u300C\u4EC0\u4E48\u2014\u2014\u59D0\u59D0\u2014\u2014\u300D</p><p>\u300C\u686B\u6469\u2014\u2014\u686B\u6469\u2014\u2014\u5927\u529B\u70B9\uFF0C\u518D\u5927\u529B\u70B9\uFF0C\u5E72\u6211\u2014\u2014\u300D</p><p>\u300C\u5927\u529B\u70B9\u505A\u4EC0\u4E48\uFF1F\u300D</p><p>\u300C\u5E72\u6211\u554A\u2014\u2014\u5514\u2026\u2026\u554A\uFF01\u5E72\u6211\u2014\u2014\u5514\u2026\u2026\u300D</p><p>\u82CD\u5170\u8BF4\u8BDD\u7684\u58F0\u97F3\u90FD\u53D8\u6210\u50CF\u54ED\uFF0C\u539F\u6765\u4ED6\u7684\u9634\u830E\u7ADF\u771F\u7684\u53EF\u4EE5\u4EE4\u59D0\u59D0\u9189\u751F\u68A6\u6B7B\u3002\u81EA\u4ECE\u90A3\u65E5\u5979\u6253\u5F00\u6697\u5BA4\u7684\u95E8\uFF0C\u89E3\u5F00\u4ED6\u7684\u67B7\u9501\u3002\u4ED6\u5C31\u88AB\u59D0\u59D0\u7684\u7F8E\u4E3D\u6298\u670D\u3002</p><p>\u548C\u8D1D\u73B2\u8FBE\u7684\u4E00\u573A\u5B7D\u604B\uFF0C\u4E5F\u6E90\u81EA\u5979\u548C\u82CD\u5170\u76F8\u4F3C\u7684\u5BB9\u989C\u3002</p><p>\u4ED6\u627F\u8BA4\u4ED6\u662F\u7231\u5979\u7684\uFF0C\u4F46\u662F\u62FF\u8FD9\u6837\u7684\u7231\u548C\u5BF9\u59D0\u59D0\u7684\u6B32\u671B\u76F8\u6BD4\uFF0C\u5C31\u5982\u540C\u7528\u8424\u706B\u7C89\u9970\u6708\u5149\u3002</p><p>\u4ED6\u7565\u62AC\u8D77\u59D0\u59D0\u7684\u81C0\u56F4\uFF0C\u611F\u89C9\u4ED6\u6BCF\u4E00\u6B21\u7684\u62BD\u52A8\uFF0C\u5979\u7684\u81C0\u90FD\u4F1A\u4F18\u96C5\u7684\u540E\u7FD8\u3002\u4E00\u7537\u4E00\u5973\uFF0C\u4E24\u5177\u76F8\u62E5\u7FF1\u7FD4\u7684\u80F4\u4F53\u3002\u6BCF\u4E00\u6B21\u803B\u9AA8\u90E8\u4F4D\u7684\u649E\u51FB\uFF0C\u90FD\u662F\u4E00\u9635\u9500\u9B42\u7684\u6FC0\u8361\u3002</p><p>\u4ED6\u548C\u5979\u4E4B\u95F4\u5BB9\u4E0D\u4E0B\u4E00\u5F20\u767D\u7EB8\u7684\u7A7A\u95F4\uFF0C\u6D51\u6D4A\u7684\u6C57\u6DB2\u5374\u4EA4\u878D\u5728\u4E00\u8D77\u3002\u90A3\u5C31\u50CF\u4ED6\u4EEC\u5F7C\u6B64\u7EA0\u7ED3\u7684\u6027\u5668\uFF0C\u5206\u4E5F\u5206\u4E0D\u5F00\u3002</p><p>\u5728\u8FD9\u9AD8\u7A7A\u51CC\u5389\u7684\u98CE\u52A8\u3002</p><p>\u4ED6\u65E0\u6CD5\u542C\u6E05\u5979\u6BCF\u4E00\u8BB0\u547B\u541F\u548C\u6D6A\u53EB\uFF0C\u53EA\u662F\u7528\u624B\u6307\u3001\u7528\u9634\u830E\u3001\u7528\u5FC3\u53BB\u611F\u89C9\u5979\u8EAB\u4F53\u7684\u70ED\u529B\u8282\u62CD\u3002\u4E00\u62BD\u4E00\u9001\uFF0C\u4E00\u5F20\u4E00\u5F1B\uFF0C\u5929\u4E0A\u4EBA\u95F4\u3002</p><p>\u4ED6\u4E0D\u7BA1\u5979\u662F\u5426\u542C\u5F97\u89C1\uFF0C\u4ED6\u8FD8\u662F\u8981\u8BF4\uFF0C\u5BF9\u7740\u5168\u4E16\u754C\u8BF4\u8BDD\uFF1A\u300C\u4F60\uFF0C\u82CD\u5170\u3002\u6211\u7684\u59D0\u59D0\uFF1B\u6211\u686B\u6469\uFF0C\u4F60\u7684\u5F1F\u5F1F\uFF0C\u6211\u73B0\u5728\u5728\u4F60\u7684\u9634\u9053\u4E2D\u62BD\u52A8\u9634\u830E\uFF0C\u641E\u5230\u6211\u7684\u9A9A\u59D0\u59D0\uFF0C\u7FFB\u7740\u767D\u773C\uFF0C\u6D6A\u53EB\u8FDE\u8FDE\u3002\u300D</p><p>\u7EDD\u8272\u503E\u57CE\u7684\u5973\u5B50\uFF0C\u66FE\u7ECF\u51B7\u9510\u3002</p><p>\u800C\u6B64\u523B\u5728\u6B7B\u4EA1\u7684\u57CE\u5E02\u4E0A\u7A7A\uFF0C\u505A\u6210\u6DEB\u884C\u5199\u7167\u3002</p><p>\u5929\u662F\u5B64\u9AD8\u7684\uFF0C\u53EA\u662F\u591A\u51FA\u6052\u4E45\u7684\u610F\u5473\u3002</p><p>\u800C\u90A3\u4E9B\u5C71\u5CE6\u3001\u6CB3\u6D41\u3001\u6D77\u6D0B\u3001\u57CE\u5E02\uFF0C\u8FD8\u6709\u9014\u4EBA\uFF0C\u53EA\u4E0D\u8FC7\u6B20\u7F3A\u4E00\u4E2A\u9AD8\u5EA6\u7684\u85D0\u89C6\u3002</p><p>\u4ED6\u4EEC\u6CE8\u5B9A\u8981\u53D1\u751F\u3001\u58EE\u5927\u3001\u76F8\u9047\u3001\u8352\u5E9F\uFF0C\u6216\u7740\u6B7B\u4EA1\uFF0C\u90FD\u5728\u9075\u5FAA\u5728\u5929\u547D\u7684\u89C4\u7A0B\u3002</p><p>\u4ED6\u611F\u89C9\u5230\u5979\u7684\u9634\u9053\u58C1\u5267\u70C8\u7684\u6536\u7F29\uFF0C\u5979\u8FD8\u5728\u5411\u4E0A\u98DE\uFF0C\u5411\u4E0A\u98DE\u3002</p><p>\u7A81\u7136\uFF0C\u5979\u505C\u4E0B\u6765\u3002</p><p>\u5C31\u8FD9\u6837\u5728\u4ED6\u7684\u62E5\u62B1\u4E2D\u505C\u4E0B\u6765\u3002\u5728\u5979\u7684\u9762\u4E0A\uFF0C\u7ADF\u662F\u56DE\u590D\u4EE5\u5F80\u7684\u865A\u51B7\u76EE\u5149\u3002</p><p>\u4ED6\u7684\u9634\u830E\u4F9D\u7136\u5728\u5162\u5162\u4E1A\u4E1A\u7684\u52B3\u4F5C\u3002\u800C\u5979\u5374\u6D6E\u73B0\u51FA\u60E8\u6DE1\u7684\u7B11\u610F\u3002</p><p>\u300C\u73B0\u5728\uFF0C\u686B\u6469\u3002\u6211\u4EEC\u964D\u4E0B\u53BB\u3002\u300D</p><p>\u4ED6\u8BB0\u5F97\u4E0A\u4E00\u6B21\u5979\u4EE5\u6700\u5FEB\u7684\u901F\u5EA6\u4E0B\u843D\uFF0C\u751A\u81F3\u4E24\u4E2A\u4EBA\u7684\u59FF\u52BF\u90FD\u662F\u96F7\u540C\u3002</p><p>\u5979\u62B1\u7D27\u4ED6\uFF0C\u4ED6\u4EA6\u62E5\u7740\u5979\u3002\u4ED6\u751A\u81F3\u60F3\u63D0\u9192\u5979\u8BF4\u4ED6\u8FD8\u672A\u5C04\u7CBE\u3002\u53EF\u662F\u4ED6\u6E10\u53D1\u89C9\u4E8B\u6001\u7684\u8BE1\u5F02\u3002</p><p>\u5979\u76D8\u65CB\u7740\u4EE5\u6050\u6016\u7684\u901F\u5EA6\u4E0B\u5760\u3002</p><p>\u4ED6\u8BB0\u5F97\u5979\u66FE\u7ECF\u544A\u8BC9\u4ED6\u5979\u6700\u559C\u6B22\u8FD9\u6837\u7684\u611F\u89C9\uFF0C\u559C\u6B22\u8FD9\u6837\u7684\u98CE\u58F0\u547C\u5578\uFF0C\u559C\u6B22\u8FD9\u6837\u523A\u6FC0\u7684\u51CC\u5389\u7684\u4E0B\u5760\u611F\u3002</p><p>\u4ED6\u7684\u9634\u830E\u8FD8\u5728\u5979\u7684\u4F53\u5185\uFF0C\u5979\u7684\u53D1\u50CF\u98DE\u4E2D\u8D77\u821E\u7684\u5929\u82B1\u3002</p><p>\u4ED6\u7EC8\u4E8E\u77E5\u9053\uFF0C\u5979\u60F3\u8DDF\u4ED6\u540C\u5F52\u4E8E\u5C3D\u3002\u800C\u5148\u524D\u7684\u9ED8\u5951\u53EA\u4E0D\u8FC7\u662F\u5973\u4EBA\u5FCD\u8FB1\u8D1F\u91CD\u7684\u8868\u6F14\u3002</p><p>\u5979\u53C8\u4E00\u6B21\u8981\u6740\u6B7B\u81EA\u5DF1\u7684\u4EB2\u5F1F\u3002</p><p>\u4ED6\u53C8\u4E00\u6B21\u88AB\u5979\u6B3A\u9A97\uFF01</p><p>\u300C\u554A\u2014\u2014\u4F60\u8FD9\u5A4A\u5B50\uFF01\u82CD\u5170\u2014\u2014\u4F60\u8FD9\u5A4A\u5B50\uFF01\u300D</p><p>\u5728\u8FD9\u6837\u6025\u5267\u7684\u4E0B\u5760\u4E2D\uFF0C\u5979\u4E0D\u53EF\u80FD\u542C\u89C1\u4ED6\u7684\u8BF4\u8BDD\u3002</p><p>\u5979\u5F77\u4F5B\u770B\u89C1\u524D\u65B9\u662F\u4E00\u5E55\u7EEE\u4E3D\u7684\u5149\u5F71\uFF0C\u5B89\u8BE6\u800C\u4F18\u7F8E\u3002\u90A3\u91CC\u6709\u767E\u8272\u7684\u82B1\uFF0C\u5F2F\u5F2F\u7684\u6708\u7259\u3002\u90A3\u91CC\u6709\u84DD\u84DD\u7684\u592A\u9633\u548C\u6C38\u8FDC\u4E0D\u4F1A\u843D\u5730\u7684\u7FC5\u8180\uFF0C\u98DE\u7FD4\u5728\u4E00\u7247\u4E00\u7247\u72ED\u957F\u7684\u5929\u7A7A\u3002</p><p>\u5979\u5BF9\u7740\u8033\u8FB9\u7684\u98CE\u8BF4\uFF1A\u300C\u6211\u7EC8\u4E8E\u8BDB\u6740\u8FD9\u53EA\u9B54\u9B3C\u3002\u300D</p><p>\u524D\u65B9\u662F\u4E00\u5E55\u7EEE\u4E3D\u7684\u5149\u5F71\uFF0C\u5B89\u8BE6\u800C\u4F18\u7F8E\u3002\u5979\u770B\u89C1\u90A3\u91CC\u98D8\u7740\u96EA\uFF0C\u6BCD\u4EB2\u4E3A\u5979\u7F1D\u597D\u767D\u8272\u7684\u7A84\u88D9\u5B50\uFF0C\u7B49\u5979\u56DE\u6765\u3002</p><p>\u5979\u5BF9\u7740\u8033\u8FB9\u7684\u98CE\u8BF4\uFF1A\u300C\u6211\u56DE\u6765\u3002\u300D</p><p>\u2026\u2026</p><p>\u53EF\u662F\uFF0C\u82CD\u5170\u5374\u6C38\u8FDC\u6CA1\u6709\u56DE\u53BB\u3002</p><p>\u5728\u8DDD\u79BB\u5730\u9762\u4E00\u68F5\u6A61\u6811\u90A3\u822C\u9AD8\u7684\u4F4D\u7F6E\uFF0C\u4E0B\u843D\u7684\u8D8B\u52BF\u7ADF\u560E\u7136\u505C\u6B62\u3002</p><p>\u5979\u518D\u6B21\u7741\u770B\u773C\u775B\u7684\u65F6\u5019\uFF0C\u770B\u89C1\u4E00\u53CC\u5DE8\u5927\u7684\u3001\u9ED1\u8272\u7684\u8759\u8760\u7FFC\u94FA\u5F20\u5F00\u6765\u3002\u5728\u7FFC\u548C\u4ED6\u7684\u80CC\u808C\u4E4B\u95F4\uFF0C\u662F\u4E00\u6761\u4E00\u6761\u6050\u6016\u7684\u9752\u7B4B\u5206\u5E03\u3002</p><p>\u4ED6\u662F\u66B4\u6012\u7684\uFF0C\u4ED6\u53EB\u5979\u5A4A\u5B50\u3002</p><p>\u7136\u540E\u98DE\u5411\u9AD8\u8038\u7684\u5854\u5C16\u3002</p><p>\u8FD9\u4E16\u754C\u6CA1\u6709\u795E\uFF0C\u600E\u4F1A\u6709\u4EBA\u8FF9\u3002</p><p>\u8FD9\u4E16\u754C\u82E5\u6CA1\u6709\u4EBA\uFF0C\u53C8\u662F\u8C01\u5728\u8F9E\u5178\u4E2D\u9020\u51FA\u7684\u9B54\uFF1F</p>',134);function t(c,n){return r}var _=e(a,[["render",t]]);export{_ as default};
