import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const a={},r=p('<h1 id="z" tabindex="-1"><a class="header-anchor" href="#z" aria-hidden="true">#</a> Z\u2026</h1><p>\u82CD\u5170\uFF0C\u8FD9\u5351\u8D31\u7684\u540D\u3002\u5149\u8363\u80CC\u540E\uFF0C\u53EA\u843D\u6C38\u4E16\u7684\u5211\u7F5A\u3002</p><p>\u655E\u5F00\u8EAB\uFF0C\u65E0\u9650\u6B21\u8499\u53D7\u5F1F\u5F1F\u7684\u4FAE\u8FB1\u3002\u5728\u8DDD\u79BB\u5929\u7A7A\u6700\u8FD1\u7684\u5730\u65B9\uFF0C\u5047\u5982\u4E0D\u88AB\u5978\uFF0C\u5979\u4F1A\u88AB\u51B7\u6B7B\u3002\u86C7\u5996\u7684\u820C\u5934\u90A3\u6837\u51B0\u51B7\uFF0C\u4F46\u5B83\u8214\u8FC7\u4E4B\u5904\uFF0C\u603B\u662F\u71E5\u70ED\u96BE\u5B89\u3002</p><p>\u5165\u51AC\u7684\u65F6\u5019\uFF0C\u53EA\u98D8\u8FC7\u4E00\u573A\u96EA\u3002\u4ECE\u7A97\u53E3\u770B\u96EA\u82B1\u5815\u843D\uFF0C\u767D\u832B\u832B\u7684\u96EA\u4F1A\u586B\u5E73\u6EE1\u662F\u521B\u4F24\u7684\u5927\u5730\u3002\u5C31\u50CF\u767D\u832B\u832B\u7684\u7CBE\u6DB2\uFF0C\u586B\u5E73\u5979\u53D7\u4F24\u7684\u8EAB\u4F53\u3002</p><p>\u96EA\u5316\u7684\u65F6\u5019\uFF0C\u5929\u6C14\u4F1A\u683C\u5916\u7684\u5BD2\u3002\u5927\u5730\u7684\u6C9F\u58D1\u53C8\u518D\u73B0\u51FA\u6765\u3002</p><p>\u7CBE\u6DB2\u5316\u53BB\u7684\u65F6\u5019\uFF0C\u65B0\u7684\u707E\u96BE\u53C8\u4F1A\u964D\u4E34\u3002</p><p>\u59EC\u5A1C\u5410\u51FA\u7684\u4E1D\u7EBF\uFF0C\u675F\u7F1A\u7740\u82CD\u5170\uFF0C\u644A\u5F00\u53CC\u817F\uFF0C\u65E0\u4ECE\u52A8\u5F39\u3002\u5B9A\u683C\u6210\u968F\u65F6\u8FCE\u63A5\u63D2\u5165\u7684\u6DEB\u7CDC\u59FF\u6001\u3002</p><p>\u7F8E\u5973\u86C7\uFF0C\u5F02\u578B\u7684\u5996\u5A9A\uFF0C\u662F\u4E3A\u6DEB\u5979\u800C\u751F\u3002\u4ECE\u6BCF\u4E2A\u654F\u611F\u7684\u65B9\u4F4D\uFF0C\u64A9\u5F04\u65E0\u803B\u7684\u60C5\u6B32\uFF0C\u5F53\u8FD9\u6837\u7684\u751F\u6DAF\u5F00\u59CB\u6210\u4E3A\u4E60\u60EF\uFF0C\u4FBF\u4E0D\u518D\u6709\u8BE1\u5F02\u7684\u4E8B\u3002</p><p>\u686B\u6469\u6BCF\u5929\u90FD\u4F1A\u5978\u5979\u3002\u6709\u7684\u65F6\u5019\u662F\u767D\u5929\uFF0C\u6709\u7684\u65F6\u5019\u5728\u6DF1\u591C\u3002\u5979\u5904\u5728\u6781\u5927\u7684\u5C48\u8FB1\u548C\u6028\u5FFF\uFF0C\u5374\u5728\u65F7\u65E5\u6301\u4E45\u7684\u6027\u4EA4\u4E2D\u88AB\u6467\u6BC1\u9EBB\u6728\u3002\u5C31\u4F3C\u5927\u96EA\u521D\u843D\u65F6\uFF0C\u5BD2\u51B7\u523A\u9AA8\u3002\u4F46\u843D\u4E86\u4E45\u4E86\uFF0C\u79EF\u96EA\u539A\u4E86\uFF0C\u4E5F\u5C31\u4E0D\u5728\u4E4E\u591A\u4E24\u884C\u8DB3\u8FF9\u3002</p><p>\u8FD9\u51AC\u5929\uFF0C\u53EA\u98D8\u8FC7\u4E00\u573A\u96EA\u3002\u96EA\u505C\u7684\u65F6\u5019\uFF0C\u5C31\u5230\u4E86\u6625\u3002</p><p>\u5EA6\u8FC7\u66F4\u8FED\u7684\u5B63\u8282\uFF0C\u65E0\u9650\u6B21\u91CD\u590D\u96F7\u540C\u7684\u52A8\u4F5C\u548C\u5BBF\u547D\u3002\u8FD9\u56DA\u7981\u7684\u5854\u697C\uFF0C\u65E0\u671B\u7684\u7FBD\u7FFC\uFF0C\u5982\u6B64\u717D\u60C5\u9053\u5177\u3002</p><p>\u5C38\u82B1\u5C31\u722C\u4E0A\u57CE\u5899\u7684\u65E5\u5B50\u5230\u4E86\u3002</p><p>\u82CD\u5170\u7684\u809A\u5B50\u5C31\u9AD8\u8038\u8D77\u6765\u3002</p><p>\u8FD9\u662F\u9884\u7B97\u4E2D\u7684\u4E8B\u3002</p><p>\u59EC\u5A1C\u65E5\u591C\u7EBA\u7EC7\uFF0C\u86DB\u7F51\u51DD\u6210\u7ED3\u754C\u3002\u65E0\u9650\u7684\u7F51\u8DEF\u4EA4\u9519\u5728\u9ED1\u6697\u7A7A\u95F4\uFF0C\u51B0\u51B7\u8BE1\u5F02\u3002</p><p>\u300C\u82B1\u5F00\u7684\u65F6\u5019\uFF0C\u4F60\u5F53\u542C\u89C1\u80CE\u513F\u6ECB\u957F\u7684\u58F0\u97F3\u3002\u6211\u7684\u59D0\uFF0C\u6211\u5BF9\u4F60\u4E0B\u4E86\u6BD2\u5492\uFF0C\u4ED6\u4E00\u5929\u5929\u5FC5\u4F1A\u6210\u957F\u53D8\u5927\uFF0C\u8695\u98DF\u4F60\u6F02\u4EAE\u7684\u751F\u547D\u3002\u5C31\u50CF\u571F\u58E4\u6CA1\u6709\u517B\u5206\uFF0C\u82B1\u513F\u600E\u582A\u76DB\u5F00\u3002\u300D</p><p>\u8499\u53D7\u4E00\u5343\u6B21\u7684\u5978\u8650\u548C\u6298\u78E8\uFF0C\u5979\u76AE\u80A4\u4F9D\u7136\u6E29\u6DA6\uFF0C\u5F53\u8D1D\u73B2\u8FBE\u7684\u553E\u6DB2\u6D17\u53BB\u5979\u8EAB\u4E0A\u7684\u7CBE\u6591\uFF0C\u4F9D\u7136\u7EC6\u6ED1\u53EF\u4EB2\uFF0C\u72B9\u5982\u8F6F\u7389\u3002</p><p>\u686B\u6469\u53EF\u4EE5\u6467\u6BC1\u4E00\u4F4D\u5973\u7687\u7684\u5C0A\u4E25\uFF0C\u5374\u6BC1\u4E0D\u53BB\u751F\u547D\u7684\u539F\u8272\u3002\u4ED6\u8981\u884C\u7684\uFF0C\u4E0D\u5355\u662F\u4E71\u4F26\u7684\u517D\u6B32\uFF0C\u5979\u7684\u5929\u4F7F\u7FFC\u5C31\u50CF\u4E24\u652F\u7EDD\u4E16\u5229\u5668\uFF0C\u65E0\u65F6\u65E0\u523B\u4E0D\u5728\u523A\u4F24\u3002</p><p>\u9664\u975E\u7FC5\u8180\u90FD\u51CB\u75FF\uFF0C\u5426\u5219\u4ED6\u6C38\u8FDC\u8D70\u4E0D\u51FA\u9634\u5F71\u3002</p><p>\u4E03\u5341\u4E03\u622A\u7684\u9AD8\u5854\uFF0C\u5BB9\u4E0D\u4E0B\u4ED6\u7684\u6028\u5FCC\u3002</p><p>\u5854\u5C16\u7EB5\u4F7F\u63D2\u7834\u4E07\u53E4\u7684\u5C42\u4E91\uFF0C\u9634\u830E\u65E9\u5DF2\u523A\u7A7F\u6700\u5927\u7684\u7981\u5FCC\uFF0C\u5374\u603B\u6709\u67D0\u4E9B\u50CF\u5F81\uFF0C\u662F\u4ED6\u65E0\u6CD5\u8F7B\u8511\u7684\u3002\u4ED6\u8FD9\u6837\u591C\u4EE5\u7EE7\u65E5\u7684\u4FAE\u8FB1\u5979\u3001\u6467\u6B8B\u5979\uFF0C\u4EE4\u5979\u53D8\u6210\u6027\u4EA4\u7684\u5974\u96B6\uFF0C\u5374\u4F9D\u7136\u627E\u4E0D\u5230\u6700\u5927\u7684\u5FEB\u4E50\u3002</p><p>\u4ED6\u8981\u5211\u7F5A\u5979\uFF0C\u53EA\u6709\u5979\u771F\u6B63\u5D29\u6E83\uFF0C\u5FC3\u7075\u6CA6\u4E27\uFF0C\u7FC5\u8180\u67AF\u840E\uFF0C\u8FD9\u624D\u795B\u9664\u4ED6\u7684\u5FC3\u969C\u3002</p><p>\u800C\u5979\u7684\u8089\u4F53\u867D\u88AB\u6DEB\u904D\uFF0C\u8868\u9762\u867D\u662F\u8FCE\uFF0C\u4F46\u4E00\u5BF9\u7FC5\u8180\u7684\u575A\u5F3A\uFF0C\u5374\u6697\u793A\u4E86\u7CBE\u795E\u4E0D\u8D25\u3002\u4ED6\u4EE4\u5979\u6000\u5B55\uFF0C\u5979\u5FC5\u751F\u4E0B\u4E71\u4F26\u7684\u79CD\u3002\u8981\u4EE5\u6B64\u51FB\u6E83\u5979\u3002</p><p>\u98CE\u4E2D\u5C3D\u662F\u6C57\u548C\u4F53\u6DB2\u7684\u6C14\u5473\uFF0C\u86C7\u5996\u7F20\u7ED5\u7740\u82CD\u5170\u6DEB\u90AA\u800C\u59A9\u5A9A\u3002\u4E00\u5BF9\u5982\u6B64\u76F8\u4F3C\u7684\u9762\u5B54\uFF0C\u5404\u81EA\u54C0\u6028\u7684\u5BBF\u547D\u3002\u6BCF\u5F53\u4ED6\u9634\u830E\u5145\u8840\u7684\u65F6\u5019\uFF0C\u8759\u8760\u7684\u9B54\u7FFC\u4FBF\u8840\u8109\u94FA\u5F20\uFF0C\u8FDE\u7740\u80CC\u4E0A\u7684\u808C\u8171\uFF0C\u4E00\u53CC\u773C\u7329\u7EA2\u800C\u72C2\u8E81\uFF0C\u5486\u54EE\u7740\u5978\u6DEB\u7740\u82CD\u5170\u3002</p><p>\u5979\u4E5F\u66FE\u9732\u51FA\u9189\u751F\u68A6\u6B7B\u7684\u60C5\u72B6\uFF0C\u4E5F\u66FE\u6709\u8FC7\u60C5\u4E0D\u81EA\u7981\u7684\u547B\u541F\u3002\u4ED6\u62BD\u51FA\u9634\u830E\uFF0C\u770B\u89C1\u4E00\u6761\u6676\u83B9\u7684\u6C34\u7EBF\u4E00\u7AEF\u5728\u5979\u4F53\u5185\uFF0C\u4E00\u7AEF\u8FDE\u7740\u9F9F\u5934\u3002</p><p>\u686B\u6469\u77E5\u9053\uFF0C\u67D0\u79CD\u575A\u5F3A\u7684\u4FE1\u5FF5\u5728\u652F\u6491\u8FD9\u4E0D\u5E78\u7684\u5973\u5B50\u3002\u5979\u53EF\u4EE5\u5C0A\u4E25\u5C3D\u6563\uFF0C\u5374\u4E0D\u5BB9\u4EBA\u683C\u8DCC\u5815\u3002\u56E0\u4E3A\u6BCF\u6B21\uFF0C\u8D1D\u73B2\u8FBE\u8214\u5979\u8EAB\u4F53\u7684\u65F6\u5019\uFF0C\u5728\u773C\u89D2\u603B\u4F1A\u7247\u523B\u9017\u7559\u3002</p><p>\u90A3\u54B8\u6DA9\u7684\u6CEA\uFF0C\u4EE4\u86C7\u5996\u5F77\u4F5B\u4F3C\u66FE\u76F8\u8BC6\u3002</p><p>\u51B0\u51B7\u7684\u9CDE\u7247\u5212\u8FC7\u82CD\u5170\u7684\u8EAB\u4F53\uFF0C\u4ECA\u6B21\u7ADF\u5F00\u59CB\u89C9\u5F97\u75BC\u75DB\u3002\u5728\u5979\u8116\u9888\u548C\u624B\u81C2\uFF0C\u6709\u51E0\u5904\u76AE\u8089\u5DF2\u5F00\u59CB\u8150\u70C2\uFF0C\u6563\u53D1\u51FA\u8165\u81ED\u7684\u6C14\u5473\u3002</p><p>\u686B\u6469\u9762\u8272\u8868\u60C5\uFF0C\u5206\u4E0D\u6E05\u662F\u6B23\u8D4F\u6291\u6216\u6028\u6012\u3002</p><p>\u300C\u5A74\u5B69\u662F\u5FC5\u8981\u964D\u4E16\u7684\u3002\u300D</p><p>\u300C\u5514\u2026\u2026\u300D</p><p>\u300C\u4F60\u5FC5\u8017\u5C3D\u751F\u547D\u7684\u7CBE\u534E\u53BB\u6ECB\u517B\u4ED6\u3002\u4F46\u4F60\u7684\u8089\u8EAB\u6301\u7EED\u8150\u8D25\uFF0C\u5374\u4E0D\u80AF\u727A\u7272\u591A\u4F59\u7684\u7FC5\u8180\uFF01\u5018\u82E5\u4F60\u628A\u7FC5\u8180\u7684\u751F\u547D\u529B\u8F6C\u6CE8\u5230\u5B50\u5BAB\uFF0C\u4F60\u7684\u8EAB\u662F\u53EF\u4EE5\u4FDD\u5168\u7684\u3002\u300D</p><p>\u300C\u686B\u2026\u2026\u686B\u6469\u3002\u4F60\u8FD9\u2026\u2026\u707E\u53D8\u7684\u9B54\uFF0C\u4F60\u6DEB\u6211\u7684\u8089\u4F53\uFF0C\u5E76\u5728\u4E16\u95F4\u505A\u6076\u3002\u6211\u66FE\u594B\u529B\u6297\u6323\u3002\u8F93\u4E86\u547D\u8FD0\uFF0C\u8F93\u4E86\u8089\u8EAB\uFF0C\u5374\u4ECE\u4E0D\u4F1A\u4F4E\u5934\u3002\u7316\u72C2\u5427\uFF0C\u8D8A\u7316\u72C2\u8D8A\u5F97\u4E0D\u5230\u987A\u670D\u548C\u656C\u754F\uFF01\u4F60\u6240\u80FD\u5F97\u901E\uFF0C\u4E0D\u8FC7\u4E00\u65F6\u7684\u6DEB\u5DE7\u3002\u300D</p><p>\u300C\u54C8\u54C8\u54C8\u54C8\uFF01\u300D\u4ED6\u4E00\u8FB9\u72C2\u7B11\uFF0C\u4E00\u8FB9\u63D2\u5979\u3002\u8FD9\u5177\u7F8E\u5999\u7684\u80F4\u4F53\u6DEB\u8361\u5730\u98A4\u6296\u7740\uFF0C\u6BCF\u4E00\u5BF8\u90FD\u662F\u7EDD\u8272\u4E4B\u5730\u3002\u4ED6\u77E5\u9053\uFF0C\u8FD9\u8089\u8EAB\u4F1A\u4E00\u70B9\u4E00\u70B9\u7684\u8150\u70C2\u6389\uFF0C\u6D41\u51FA\u7EFF\u8272\u7684\u8113\u6C34\uFF0C\u53D1\u51FA\u8165\u81ED\u6C14\u606F\u3002</p><p>\u300C\u6765\u5427\uFF0C\u6487\u5F00\u4F60\u7684\u4FE1\u5FF5\u548C\u521A\u5F3A\uFF0C\u5939\u7D27\u6211\u90A3\u7C97\u58EE\u7684\u9634\u830E\u3002\u4F60\u8981\u53EB\uFF0C\u8981\u626D\u52A8\uFF0C\u5728\u6211\u62BD\u79BB\u7684\u65F6\u5019\uFF0C\u4F60\u8981\u4E5E\u6C42\u6211\u3002\u300D</p><p>\u82CD\u5170\u8FD9\u6837\u88AB\u52A8\u5730\u5598\u606F\u548C\u627F\u53D7\uFF0C\u86DB\u4E1D\u628A\u5979\u6346\u6210\u6DEB\u8D31\u7684\u59FF\u52BF\uFF0C\u86C7\u5996\u64A9\u5F04\u8EAB\u4F53\u5404\u5904\u654F\u611F\u7684\u5730\u5E26\u3002\u5728\u5979\u88AB\u5978\u6DEB\u7684\u65F6\u523B\uFF0C\u662F\u522B\u65E0\u9009\u62E9\u7684\u3002</p><p>\u8EAB\u4F53\u4E00\u5929\u5929\u7684\u8150\u70C2\u53D8\u574F\uFF0C\u5C0F\u8179\u4E5F\u6E10\u6E10\u9686\u8D77\u4E86\u3002</p><p>\u5728\u686B\u6469\u79BB\u5F00\u7684\u65F6\u5019\uFF0C\u5979\u4F1A\u4E00\u4E2A\u4EBA\u547B\u541F\u5598\u606F\u3002\u5979\u7684\u8EAB\u4F53\u4E0D\u518D\u7F8E\u597D\uFF0C\u8113\u6C34\u4ECE\u7EFD\u5F00\u7684\u76AE\u8089\u6EA2\u51FA\u6765\uFF0C\u9634\u98CE\u5439\u62C2\uFF0C\u662F\u51B7\u51BD\u7684\u9635\u75DB\u3002\u8D1D\u73B2\u8FBE\u603B\u662F\u4E0D\u80AF\u653E\u8FC7\uFF0C\u8214\u98DF\u7740\u5979\u8EAB\u4E0A\u6D41\u51FA\u7684\u4EFB\u4F55\u6DB2\u4F53\u3002\u5E76\u5206\u6CCC\u67D0\u79CD\u5EA6\uFF0C\u4EE4\u5979\u7684\u9634\u9053\u59CB\u7EC8\u6CDB\u6EE5\u3002</p><p>\u9664\u975E\u5979\u7684\u6CEA\uFF0C\u4EE4\u5B83\u7247\u523B\u5B89\u6B47\u3002\u4F46\u773C\u6CEA\u8D8A\u591A\uFF0C\u75DB\u89C9\u4FBF\u8D8A\u4E30\u76DB\u3002</p><p>\u7ECF\u4E66\u4E0A\u8BF4\uFF0C\u6371\u8FC7\u5343\u5E74\u7684\u6D74\u8840\uFF0C\u6371\u8FC7\u70C8\u706B\u7684\u714E\u71AC\uFF0C\u5373\u4F1A\u6709\u65B0\u751F\u7684\u6D85\u76D8\u3002\u5F53\u9690\u5FCD\u5230\u6781\u9650\uFF0C\u6700\u5927\u7684\u75DB\u82E6\u4E5F\u5C06\u8981\u8FC7\u53BB\u3002</p><p>\u4E5F\u8BB8\u5979\u7684\u4EA7\u671F\uFF0C\u5373\u662F\u5929\u7A7A\u57CE\u9668\u843D\u7684\u65E5\u5B50\u3002\u57CE\u4E2D\u4EC5\u5269\u8001\u5E7C\u548C\u5987\u5973\uFF0C\u51C4\u54C0\u5730\u5EA6\u8FC7\u6700\u540E\u751F\u547D\u3002\u90A3\u65E5\u62DC\u4E9A\u65AF\u7684\u6FC0\u6218\uFF0C\u5979\u770B\u89C1\u90A3\u4E48\u591A\u52C7\u6562\u7684\u6218\u58EB\u6B7B\u5728\u5996\u7269\u7684\u722A\u7259\u3002\u5728\u686B\u6469\u5C04\u7CBE\u7684\u65F6\u5019\uFF0C\u5979\u5F00\u59CB\u660E\u767D\uFF0C\u539F\u6765\u65F6\u4EE3\u7684\u8FC1\u79FB\u771F\u7684\u4E0D\u53EF\u8FDD\u9006\u3002</p><p>\u76F8\u6BD4\u5386\u53F2\u7684\u56DE\u8F6E\uFF0C\u79CD\u65CF\u7684\u751F\u706D\uFF0C\u4E00\u4E2A\u4EBA\u7684\u75DB\u82E6\u5C31\u5982\u540C\u65E0\u9650\u5929\u5B87\u4E2D\u6563\u843D\u7684\u4E00\u7247\u7FBD\u6BDB\u3002\u518D\u534E\u7F8E\u7684\u8EAB\u8EAF\u7EC8\u8981\u53D8\u4E3A\u5C18\u571F\uFF0C\u518D\u766B\u72C2\u7684\u9B54\u715E\u4E5F\u7EC8\u662F\u96BE\u9003\u8870\u4EA1\u3002</p><p>\u5979\u8981\u6371\u4E0B\u53BB\uFF0C\u4E0D\u662F\u5C48\u670D\uFF0C\u66F4\u4E0D\u662F\u6267\u5B88\u3002\u53EA\u56E0\u5B55\u80B2\u4E00\u4E2A\u751F\u547D\u7684\u79CD\uFF0C\u65E0\u8F9C\u53C8\u7EAF\u7F8E\u7684\u7070\u77B3\u5B54\u3002\u5979\u8981\u770B\u5230\u8FD9\u5A74\u5B69\uFF0C\u8FD9\u662F\u5979\u5728\u6B7B\u53BB\u4E4B\u524D\uFF0C\u552F\u4E00\u80FD\u53CA\u7684\u3002</p><p>\u9B54\u7269\u663C\u591C\u6298\u78E8\u5979\uFF0C\u8150\u574F\u7684\u8EAB\u4F53\u53C8\u60F9\u6765\u82CD\u8747\u3002</p><p>\u60DF\u6709\u9762\u5B54\uFF0C\u4E73\u623F\u548C\u6027\u5668\uFF0C\u8FD8\u662F\u539F\u5148\u7684\u6F02\u4EAE\uFF0C\u4E00\u5BF9\u7FC5\u8180\u5014\u5F3A\u5730\u51CC\u7ACB\u3002\u54EA\u6015\u5F53\u6210\u6446\u8BBE\u7684\u9053\u5177\uFF0C\u5C31\u7B97\u6B7B\u6B7B\u625E\u536B\u6B64\u751F\u7684\u8363\u8A89\u3002</p><p>\u539F\u6765\u4E00\u4E2A\u5973\u4EBA\u9677\u5728\u5982\u6B64\u72FC\u72C8\u7684\u5883\u5730\uFF0C\u7ADF\u4E5F\u53EF\u4EE5\u6709\u9A84\u50B2\u3002</p><p>\u686B\u6469\u629A\u6469\u59D0\u59D0\u7684\u809A\u5B50\uFF0C\u300C\u6211\u8981\u4ED6\uFF0C\u751F\u51FA\u9B54\u9B3C\u7684\u7FFC\u3002\u300D</p><p>\u7099\u70ED\u7684\u7CBE\u6DB2\uFF0C\u65E0\u9650\u6B21\u55B7\u6D12\u5728\u5979\u7684\u5B50\u5BAB\u5185\u58C1\u3001\u53E3\u8154\u5185\u58C1\u3001\u76F4\u80A0\u5185\u58C1\u548C\u8EAB\u4F53\u5916\u90E8\u7684\u8150\u70C2\u808C\u80A4\u3002</p><p>\u5979\u53EF\u4EE5\u611F\u89C9\u75BC\u75DB\uFF0C\u4EA6\u4F1A\u5728\u5978\u8650\u4E2D\u4EA7\u751F\u9AD8\u6F6E\uFF0C\u5B50\u5BAB\u5185\u8815\u52A8\u7684\u65F6\u5019\uFF0C\u5979\u751A\u81F3\u60F3\u6C42\u4ED6\u8F7B\u3002\u60F3\u5230\u7AE5\u5E74\uFF0C\u60F3\u5230\u90A3\u76CF\u82E5\u6709\u82E5\u65E0\u7684\u6CB9\u706F\uFF0C\u60F3\u5230\u5927\u796D\u53F8\u6B7B\u524D\u7684\u8BF4\u8BDD\uFF0C\u60F3\u5230\u67D0\u5929\u66FE\u6253\u5F00\u6697\u5BA4\u7684\u95E8\uFF0C\u89E3\u5F00\u4ED6\u7684\u67B7\u9501\u3002\u60F3\u5230\u4ED6\u5B8C\u5A5A\u7684\u90A3\u65E5\uFF0C\u60F3\u5230\u4ED6\u5199\u7ED9\u5979\u7684\u4FE1\uFF0C\u60F3\u5230\u4ED6\u628A\u59BB\u5B50\u5316\u6210\u5996\u5B7D\uFF0C\u60F3\u5230\u4ED6\u628A\u5973\u7AE5\u90FD\u5978\u6C61\u3002\u65AD\u4E86\u5DE8\u96C0\u5251\u4F9D\u7136\u6740\u4E0D\u6B7B\u4ED6\uFF0C\u4ED6\u628A\u5979\u91CD\u91CD\u5730\u6454\uFF0C\u7136\u540E\u4ED6\u7528\u7259\u9F7F\u62C9\u4E0B\u5979\u7684\u5E95\u88E4\uFF0C\u7B2C\u4E00\u6B21\u628A\u5979\u63D2\u5230\u9AD8\u6F6E\u2026\u2026</p><p>\u4ED6\u4EEC\u662F\u53CC\u751F\u7684\u5B51\u5A74\uFF0C\u4EA6\u662F\u5F7C\u6B64\u6B8B\u5BB3\u7684\u5BBF\u654C\u3002\u90A3\u4E48\u591A\u7684\u7231\u72E0\u4EA4\u7EC7\u5728\u4E00\u8D77\uFF0C\u5316\u6210\u8FD9\u51C4\u60E8\u547D\u8FD0\u3002</p><p>\u5018\u82E5\u6CA1\u6709\u90A3\u7FFC\u671B\u7684\u4F20\u8BF4\uFF0C\u4FBF\u4E0D\u4F1A\u6709\u8FD9\u6BB5\u72E0\u6BD2\u7684\u5386\u53F2\u3002\u5018\u82E5\u6CA1\u6709\u90A3\u7EDD\u4E16\u7684\u4F20\u8BF4\uFF0C\u600E\u4F1A\u6709\u4E24\u5EA7\u57CE\u5E02\u7684\u6B7B\u4EA1\u3002</p><p>\u771F\u7684\uFF0C\u686B\u6469\u3002\u4E00\u4E2A\u4EBA\u7684\u6267\u7740\uFF0C\u8DB3\u591F\u751F\u51FA\u72C2\u5B7D\u3002</p><p>\u6211\u5DF2\u8150\u70C2\u6210\u6050\u6016\u7684\u6050\u6016\u7684\u8EAB\u8EAF\uFF0C\u7ADF\u4E5F\u80FD\u4EE4\u4F60\u5174\u594B\u7684\u5978\u6DEB\u3002\u6211\u660E\u767D\uFF0C\u4F60\u5FC3\u4E2D\u7684\u6028\u5FFF\u662F\u6211\u627F\u53D7\u4E0D\u8D77\u4E4B\u91CD\u3002</p><p>\u686B\u6469\uFF0C\u59D0\u59D0\u662F\u504F\u6267\u53C8\u72ED\u9698\u7684\u3002\u5047\u5982\u5386\u53F2\u53EF\u4EE5\u6539\u53D8\uFF0C\u4F60\u53D8\u4F1A\u539F\u5148\u7684\u6837\u5B50\uFF0C\u6211\u5B81\u53EF\u8150\u81ED\u800C\u6B7B\u3002\u6211\u8981\u8DEA\u4E0B\u6C42\u4F60\u7684\u539F\u8C05\u2014\u2014\u4E3A\u4E86\u5929\u7A7A\u57CE\u7684\u7AE5\u8BDD\uFF0C\u7ADF\u53EF\u4EE5\u727A\u7272\u4EFB\u4F55\u4EBA\u3002</p><p>\u6216\u8005\u4F60\u7684\u5E78\u798F\u3002\u5982\u679C\u8D1D\u73B2\u8FBE\u516C\u4E3B\u662F\u4E00\u4F4D\u4E11\u964B\u65E0\u6BD4\u7684\u8001\u59AA\uFF0C\u4EA6\u4F1A\u5F3A\u8FEB\u4F60\u5B8C\u6210\u4F7F\u547D\u3002</p><p>\u771F\u7684\uFF0C\u686B\u6469\u3002\u59D0\u59D0\u662F\u8FD9\u6837\u60F3\u7684\u3002\u6211\u8BF4\u4E0D\u51FA\u8BDD\u6765\uFF0C\u56E0\u4E3A\u4F60\u7684\u9634\u830E\u4EE4\u6211\u71E5\u70ED\u547B\u541F\uFF0C\u65E0\u6CD5\u8A00\u8BED\u3002\u7B97\u505A\u60E9\u7F5A\u5427\uFF0C\u6211\u8981\u8BA4\u6211\u7684\u7F6A\u3002</p><p>\u4E3A\u4E86\u8BDB\u9B54\uFF0C\u51E0\u6B21\u5F15\u5251\u6740\u6B7B\u4F60\u3002\u76F4\u5230\u6700\u540E\u5173\u5934\uFF0C\u7ADF\u4E0D\u60DC\u4EE5\u8D1E\u70C8\u7684\u8EAB\u4F53\u5145\u5F53\u8BF1\u6740\u7684\u9053\u5177\u3002\u800C\u4F60\uFF0C\u5728\u4E07\u4F17\u9762\u524D\u5978\u6C61\u6211\uFF0C\u73A9\u5F04\u6211\uFF0C\u4EE4\u6211\u8EAB\u4F53\u8150\u574F\uFF0C\u6ECB\u517B\u4E71\u4F26\u7684\u5A74\u3002\u4E5F\u8BB8\u8FD9\u662F\u6211\u751F\u547D\u6700\u540E\u7684\u5173\u5934\uFF0C\u5728\u90A3\u4E71\u4F26\u7684\u5A74\u513F\u964D\u751F\u4E4B\u524D\uFF0C\u4F60\u4F9D\u7136\u50CF\u91CE\u517D\u4E00\u6837\u5F3A\u5978\u3002</p><p>\u800C\u6211\uFF0C\u5374\u5FC3\u5883\u7A7A\u7075\u3002</p><p>\u686B\u6469\u2026\u2026</p><p>\u6211\u4EEC\u7684\u57CE\u5E02\uFF0C\u5373\u5C06\u9668\u843D\u4E86\u3002\u6211\u4EEC\u7684\u5B69\uFF0C\u5373\u5C06\u964D\u4E16\u3002\u5C31\u8BA9\u8FD9\u65E0\u8F9C\u7684\u5A74\u5B69\u5B8C\u7ED3\u8FD9\u573A\u5B7D\u503A\u5427\u3002</p><p>\u686B\u6469\u2026\u2026\u6211\u7684\u5144\u5F1F\u3002</p><p>\u4ED6\u4F3C\u4E4E\u542C\u89C1\u59D0\u59D0\u7684\u5FC3\u58F0\uFF0C\u62BD\u79BB\u96C4\u58EE\u7684\u9634\u830E\u3002\u86C7\u5973\u722C\u8FC7\u6765\uFF0C\u98DF\u4ED6\u4EEC\u7684\u4F53\u6DB2\u3002\u4ED6\u6325\u624B\u65A5\u5F00\u3002</p><p>\u4E00\u7EBF\u65E5\u5149\u7167\u5728\u3002</p><p>\u7A7A\u95F4\u4EA4\u9519\u7684\u86DB\u4E1D\uFF0C\u86C7\u884C\u7684\u8F68\u8FF9\u3002\u8FD9\u4E9B\u90FD\u53CD\u5C04\u51FA\u523A\u773C\u7684\u5149\u8292\u6765\u3002</p><p>\u5149\u8292\u662F\u9634\u51B7\u7684\uFF0C\u6696\u7684\u53EA\u662F\u4F53\u6DB2\u3002</p><p>\u5979\u7CDC\u70C2\u7684\u8EAF\uFF0C\u60DF\u6709\u4E73\u623F\u548C\u6027\u5668\u8FD8\u662F\u539F\u5148\u6837\u5B50\u3002\u82CD\u5170\u7684\u9762\u5B54\uFF0C\u6194\u60B4\u865A\u5F31\uFF0C\u4EA6\u6709\u522B\u6837\u7F8E\u611F\u3002</p><p>\u4ED6\u671B\u671B\u80CC\u4E0A\u94FA\u5F20\u7684\u9ED1\u7FFC\uFF0C\u671B\u671B\u82CD\u5170\u9686\u8D77\u7684\u8179\uFF0C\u767D\u7FBD\u7A81\u7136\u5267\u70C8\u98A4\u6296\uFF0C\u8DDF\u7740\u5979\u6574\u4E2A\u4EBA\u5F00\u59CB\u75BC\u75DB\u7684\u62BD\u6410\u3002</p><p>\u4ED6\u77E5\u5979\u5206\u5A29\u7684\u65F6\u523B\u8FD1\u4E86\uFF0C\u4ED6\u8D70\u8FC7\u53BB\u8FD1\u89C2\uFF0C\u5979\u98A4\u6296\u7740\uFF0C\u52A8\u4F5C\u5938\u5F20\u3002</p><p>\u8D1D\u73B2\u8FBE\u4F38\u51FA\u957F\u820C\u4E00\u70B9\u4E00\u70B9\u5728\u8214\u3002\u8214\u5979\u9634\u9053\u5185\u6CCC\u51FA\u7684\u6C41\u6DB2\u3002</p><p>\u800C\u59EC\u5A1C\u6B63\u7528\u5C16\u9510\u7684\u89E6\u624B\u4FA7\u51FB\u7740\u9634\u8482\u3002</p><p>\u4F1A\u4EE4\u5979\u75C9\u631B\u3002</p><p>\u686B\u6469\u8F7B\u8F7B\u62E7\u634F\u59D0\u59D0\u7684\u4E73\u5934\uFF0C\u89E6\u611F\u6E29\u548C\uFF0C\u72B9\u793A\u5B89\u629A\u3002</p><p>\u2014\u2014\u65E5\u5149\u4E4B\u4E0B\uFF0C\u7ADF\u662F\u8FD9\u4E9B\u5BFB\u5E38\u4E8B\u3002</p>',73);function t(c,n){return r}var _=e(a,[["render",t]]);export{_ as default};
