import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_09" tabindex="-1"><a class="header-anchor" href="#_09" aria-hidden="true">#</a> 09</h1><p>\u7B2C\u4E8C\u5929\uFF0C\u9F99\u6714\u5728\u623F\u5185\u7EC3\u4E86\u534A\u65E5\u5185\u529F\u3002\u5403\u8FC7\u5348\u996D\uFF0C\u4ED6\u8BF4\u9053\uFF1A\u300C\u5E08\u5A18\uFF0C\u6211\u51FA\u53BB\u7EC3\u5251\u3002\u300D</p><p>\u51CC\u96C5\u7434\u4E00\u6014\uFF0C\u300C\u51FA\u53BB\u7EC3\u5251\uFF1F\u300D\u5979\u60F3\u4E86\u4E00\u4F1A\u513F\uFF0C\u5634\u89D2\u6162\u6162\u9732\u51FA\u4E00\u4E1D\u7B11\u610F\uFF0C\u300C\u4E5F\u597D\u3002\u603B\u5F85\u5728\u8FD9\u91CC\u4E5F\u6C14\u95F7\u5462\u3002\u300D</p><p>\u51CC\u96C5\u7434\u8D77\u8EAB\u5230\u5185\u5BA4\u62FF\u51FA\u4E00\u4E2A\u72ED\u957F\u7684\u5305\u88F9\uFF0C\u8BF4\u9053\uFF1A\u300C\u6211\u548C\u4F60\u4E00\u8D77\u53BB\u3002\u300D</p><p>\u300C\u5E08\u5A18\uFF0C\u6211\u81EA\u5DF1\u53BB\u5C31\u884C\u4E86\u3002\u300D</p><p>\u51CC\u96C5\u7434\u7B11\u9053\uFF1A\u300C\u4F60\u5728\u5C71\u4E0A\u4F4F\u4E86\u4E24\u5E74\uFF0C\u4F46\u6574\u5929\u7EC3\u5251\uFF0C\u6050\u6015\u8FD8\u4E0D\u77E5\u9053\u5468\u56F4\u7684\u666F\u8272\u5462\u3002\u300D</p><p>\u5979\u8FB9\u8D70\u8FB9\u8BF4\u9053\uFF1A\u300C\u8FD9\u8BD5\u5251\u5CF0\u662F\u7956\u5E08\u5F00\u5C71\u7ACB\u6D3E\u7684\u5730\u65B9\uFF0C\u4F20\u5230\u56DB\u4EE3\u7956\u5E08\uFF0C\u5728\u5CF0\u4E0B\u5EFA\u4E86\u5251\u9662\uFF0C\u5E7F\u6536\u95E8\u5F92\u3002\u4ECE\u90A3\u4E4B\u540E\uFF0C\u6211\u4E5D\u534E\u5251\u6D3E\u58F0\u8A89\u65E5\u9686\uFF0C\u4F46\u6765\u8BD5\u5251\u5CF0\u7684\u4EBA\u5C31\u5C11\u4E86\u3002\u5F53\u5E74\u5E08\u5A18\u5B66\u827A\u7684\u65F6\u5019\u559C\u6B22\u8FD9\u91CC\u7684\u6E05\u51C0\uFF0C\u5E38\u5230\u5CF0\u540E\u7684\u6C34\u6F6D\u7EC3\u5251\u2026\u2026\u300D</p><p>\u51CC\u96C5\u7434\u5507\u89D2\u6302\u7740\u6DE1\u6DE1\u7684\u7B11\u610F\uFF0C\u52A8\u4EBA\u7684\u7F8E\u76EE\u6E29\u5B58\u5982\u6C34\uFF0C\u4F3C\u4E4E\u5728\u6000\u5FF5\u5C11\u5973\u65F6\u4EE3\u90A3\u4E9B\u7F8E\u597D\u7684\u65E5\u5B50\u3002\u5979\u4ECA\u5E74\u8FD8\u672A\u6EE1\u4E09\u5341\uFF0C\u4F46\u5728\u6C5F\u6E56\u4E2D\u6210\u540D\u5DF2\u6709\u5341\u4F59\u5E74\u3002\u5979\u51FA\u8EAB\u540D\u95E8\uFF0C\u4E0D\u4EC5\u8C8C\u7F8E\u5982\u82B1\uFF0C\u800C\u4E14\u5251\u6CD5\u8D85\u7FA4\uFF0C\u53C8\u5AC1\u4E86\u4E00\u4E2A\u597D\u4E08\u592B\uFF0C\u8981\u4E0D\u4E86\u591A\u4E45\uFF0C\u5C31\u4F1A\u6210\u4E3A\u6C5F\u6E56\u7B2C\u4E00\u5927\u6D3E\u7684\u638C\u95E8\u592B\u4EBA\uFF0C\u53EF\u8C13\u662F\u53D7\u5C3D\u4E0A\u5929\u7684\u7737\u987E\uFF0C\u65E0\u8BBA\u8D70\u5230\u54EA\u91CC\uFF0C\u90FD\u4F1A\u5F15\u6765\u65E0\u6570\u8273\u7FA1\u7684\u76EE\u5149\u3002</p><p>\u7136\u800C\u5728\u5979\u5FC3\u5E95\uFF0C\u5374\u6709\u4E00\u4E2A\u96BE\u4EE5\u5F25\u8865\u7684\u9057\u61BE\u3002\u6709\u65F6\u51CC\u96C5\u7434\u7981\u4E0D\u4F4F\u4F1A\u60F3\uFF0C\u662F\u4E0D\u662F\u56E0\u4E3A\u81EA\u5DF1\u592A\u8FC7\u5E78\u8FD0\uFF0C\u800C\u5931\u53BB\u4E86\u505A\u6BCD\u4EB2\u7684\u8D44\u683C\uFF1F</p><p>\u4F46\u80FD\u5AC1\u7ED9\u81EA\u5DF1\u4ECE\u5C0F\u5C31\u6DF1\u4E3A\u656C\u7231\u7684\u5E08\u54E5\uFF0C\u5979\u5DF2\u7ECF\u5FC3\u6EE1\u610F\u8DB3\uFF0C\u5E08\u54E5\u53C8\u5F85\u5979\u8FD9\u4E48\u597D\uFF0C\u5373\u4F7F\u6CA1\u6709\u5B69\u5B50\u4E5F\u65E0\u6240\u8C13\u4E86\u3002</p><p>\u590F\u65E5\u7684\u9633\u5149\u900F\u8FC7\u5BC6\u6797\uFF0C\u661F\u661F\u70B9\u70B9\u6D12\u5728\u6D45\u9EC4\u7684\u8584\u886B\u4E0A\uFF0C\u5F77\u4F5B\u65E0\u6570\u6447\u66F3\u7684\u82B1\u6735\u3002</p><p>\u51CC\u96C5\u7434\u6CBF\u7740\u82CD\u7FE0\u7684\u5C71\u8DEF\u4E00\u8DEF\u8D70\u6765\uFF0C\u8F7B\u76C8\u7684\u811A\u6B65\u5982\u540C\u4E00\u4E32\u5A49\u5999\u7684\u7434\u58F0\u3002\u5979\u6B63\u5904\u4E8E\u4E00\u4E2A\u5973\u4EBA\u751F\u547D\u4E2D\u6700\u521D\u4E30\u7F8E\u7684\u65F6\u523B\uFF0C\u65E0\u8BBA\u4F53\u8C8C\u6C14\u8D28\u90FD\u5DF2\u544A\u522B\u4E86\u5C11\u5973\u65F6\u7684\u9752\u6DA9\uFF0C\u53D8\u5F97\u6210\u719F\u4E30\u6DA6\u8D77\u6765\u3002\u67D4\u7F8E\u7684\u8EAB\u4F53\u5C31\u50CF\u4E00\u679A\u5C06\u719F\u7684\u6D46\u679C\uFF0C\u6563\u53D1\u51FA\u751C\u7F8E\u800C\u53C8\u82B3\u9999\u7684\u6C14\u606F\u3002\u6B64\u65F6\u5728\u9633\u5149\u6620\u7167\u4E0B\uFF0C\u90A3\u5F20\u6BEB\u65E0\u7455\u75B5\u7684\u7389\u8138\u5A07\u8273\u5F97\u5F77\u4F5B\u8981\u6EF4\u51FA\u6C34\u6765\u3002</p><p>\u9F99\u6714\u671B\u7740\u5E08\u5A18\u51DD\u8102\u822C\u7684\u7389\u988A\uFF0C\u5FC3\u5E95\u50CF\u6709\u6E29\u6C34\u6D41\u8FC7\u822C\u8F6F\u8F6F\u5316\u5F00\u3002\u5728\u4ED6\u8BB0\u5FC6\u4E2D\uFF0C\u6BCD\u4EB2\u4E5F\u662F\u8FD9\u6837\u7684\u6DD1\u96C5\u800C\u53C8\u67D4\u7F8E\uFF0C\u6563\u53D1\u7740\u751C\u751C\u7684\u9999\u6C14\u2026\u2026</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u5C31\u662F\u8FD9\u91CC\u4E86\u3002\u300D\u51CC\u96C5\u7434\u505C\u4E0B\u811A\u6B65\u3002</p><p>\u9762\u524D\u662F\u4E00\u4E2A\u534A\u4EA9\u5927\u5C0F\u7684\u6C34\u6F6D\uFF0C\u6F6D\u6C34\u6E05\u6F88\u89C1\u5E95\uFF0C\u77F3\u9699\u4E2D\u51E0\u5C3E\u8FD1\u4E4E\u900F\u660E\u7684\u5C0F\u9C7C\u6E38\u6765\u6E38\u53BB\uFF0C\u60A0\u7136\u81EA\u5F97\u3002\u5CB8\u65C1\u5927\u5927\u5C0F\u5C0F\u7684\u77F3\u5934\u90FD\u88AB\u6D41\u6C34\u51B2\u5237\u5F97\u5149\u6ED1\u5982\u955C\uFF0C\u5468\u56F4\u53E4\u6728\u53C2\u5929\uFF0C\u6D53\u836B\u906E\u5730\uFF0C\u8FD8\u6709\u51E0\u682A\u82B1\u6811\u5F00\u5F97\u6B63\u8273\u3002\u7A7A\u60A0\u60A0\u7684\u6F6D\u6C34\u4E2D\u6620\u7740\u5929\u9645\u7684\u6D6E\u4E91\uFF0C\u8BA9\u4EBA\u89C1\u800C\u5FD8\u5FE7\u3002</p><p>\u9F99\u6714\u7CBE\u795E\u4E00\u632F\uFF0C\u817E\u8EAB\u8DC3\u4E0A\u4E00\u5757\u684C\u9762\u5927\u5C0F\u7684\u5DE8\u77F3\uFF0C\u7136\u540E\u4E24\u624B\u62B1\u7740\u957F\u5251\u5411\u4E0B\u4E00\u70B9\uFF0C\u65BD\u4E86\u4E2A\u8D77\u624B\u5F0F\uFF0C\u63A5\u7740\u8098\u90E8\u4E00\u7FFB\uFF0C\u5251\u950B\u4ECE\u814B\u4E0B\u500F\u5FFD\u523A\u51FA\u3002</p><p>\u51CC\u96C5\u7434\u5E2D\u5730\u5750\u5728\u82B1\u6811\u4E0B\uFF0C\u53D6\u4E0B\u80A9\u540E\u7684\u5305\u88F9\u653E\u5728\u819D\u4E0A\uFF0C\u9664\u4E0B\u5E03\u56CA\uFF0C\u9732\u51FA\u4E00\u5F20\u6F06\u5F97\u9ED1\u4EAE\u7684\u4E03\u5F26\u6850\u7434\u3002\u8FD9\u7434\u662F\u5979\u65B0\u624B\u6240\u4F5C\uFF0C\u5F53\u5E74\u4E3A\u4E86\u5BFB\u627E\u5408\u9002\u7684\u826F\u6850\uFF0C\u5E08\u54E5\u8E0F\u904D\u5927\u6C5F\u5357\u5317\uFF0C\u8D39\u5C3D\u4E86\u5FC3\u8840\u3002\u5979\u65E0\u610F\u8BC6\u5730\u62E8\u5F04\u51E0\u4E0B\uFF0C\u7434\u5F26\u53D1\u51FA\u94EE\u94EE\u549A\u549A\u7684\u8F7B\u54CD\uFF0C\u60A6\u8033\u4E4B\u6781\u3002</p><p>\u5E08\u54E5\u8BB8\u4E45\u6CA1\u6709\u542C\u81EA\u5DF1\u5F39\u7434\u4E86\u5462\u2026\u2026\u51CC\u96C5\u7434\u9EEF\u7136\u5782\u4E0B\u661F\u7738\uFF0C\u62C9\u8D77\u8896\u5B50\uFF0C\u4E00\u624B\u6309\u4F4F\u5F26\u4E1D\uFF0C\u4E00\u624B\u8F7B\u8F7B\u5F39\u594F\u8D77\u6765\u3002</p><p>\u5C71\u98CE\u62C2\u8FC7\u6797\u68A2\uFF0C\u8EAB\u540E\u7684\u82B1\u6811\u548C\u7F8E\u5987\u9B13\u4FA7\u957F\u957F\u7684\u53D1\u4E1D\u540C\u65F6\u98D8\u821E\u8D77\u6765\u3002\u51CC\u96C5\u7434\u95ED\u4E0A\u773C\uFF0C\u7F8E\u767D\u5982\u7389\u7684\u7EA4\u6307\u4E0B\u6DCC\u51FA\u6D41\u6C34\u822C\u6E05\u60A6\u7684\u7434\u58F0\u3002</p><p>\u6F6D\u5F71\u5C71\u8272\uFF0C\u7EA2\u989C\u7D20\u624B\uFF0C\u7434\u58F0\u82B1\u5F71\u4EA4\u76F8\u8F89\u6620\uFF0C\u4E00\u5207\u90FD\u5B9B\u5982\u7F8E\u5999\u7684\u56FE\u5377\u822C\uFF0C\u6D41\u6DCC\u7740\u8FF7\u4EBA\u7684\u8BD7\u610F\u3002</p><p>\u4E0D\u77E5\u8FC7\u4E86\u591A\u4E45\uFF0C\u4E00\u7247\u82B1\u74E3\u65CB\u8F6C\u7740\u843D\u5728\u5F26\u4E0A\uFF0C\u63A5\u7740\u6570\u4E0D\u6E05\u82B1\u74E3\u96E8\u70B9\u822C\u98D8\u626C\u800C\u843D\uFF0C\u968F\u7740\u7434\u58F0\u4E00\u4E00\u843D\u5728\u5C11\u5987\u53D1\u4E0A\u3001\u80A9\u4E0A\u3001\u88D9\u4E0A\u2026\u2026</p><p>\u51CC\u96C5\u7434\u60A0\u60A0\u53F9\u4E86\u53E3\u6C14\uFF0C\u4E00\u66F2\u672A\u5B8C\u4FBF\u505C\u4E86\u624B\u3002\u8885\u8885\u7684\u7434\u97F3\u4F3C\u4E4E\u8FD8\u7F20\u7ED5\u5728\u7389\u6307\u4E0A\uFF0C\u826F\u4E45\u624D\u968F\u98CE\u6563\u53BB\u3002</p><p>\u9F99\u6714\u6BCF\u4E00\u62DB\u90FD\u4F7F\u8DB3\u529B\u6C14\uFF0C\u4E0D\u591A\u65F6\u4FBF\u6C57\u6D41\u6D43\u80CC\uFF0C\u4ECD\u54AC\u7259\u82E6\u7EC3\u4E0D\u5DF2\u3002\u51CC\u96C5\u7434\u770B\u4E86\u7247\u523B\uFF0C\u7FFB\u624B\u4ECE\u8EAB\u540E\u6298\u4E86\u4E00\u6839\u82B1\u679D\uFF0C\u63A5\u7740\u98DE\u8EAB\u63A0\u4E0A\u5927\u77F3\uFF0C\u6296\u624B\u671D\u9F99\u6714\u80A9\u5934\u523A\u53BB\u3002</p><p>\u9F99\u6714\u6C89\u80A9\u7FFB\u8098\uFF0C\u6A2A\u5251\u6321\u4F4F\u3002\u4ED6\u7684\u5251\u6CD5\u5DF2\u7ECF\u7EAF\u719F\u65E0\u6BD4\uFF0C\u5355\u8BBA\u62DB\u5F0F\uFF0C\u5DF2\u7ECF\u4E0D\u843D\u4E0B\u98CE\u3002\u4F46\u5E08\u5A18\u7565\u5FAE\u4F7F\u4E0A\u4E24\u6210\u5185\u529B\uFF0C\u4ED6\u7684\u5251\u62DB\u4FBF\u6EDE\u91CD\u8D77\u6765\uFF0C\u518D\u8FC7\u4E24\u62DB\u4FBF\u5DE6\u652F\u53F3\u7ECC\uFF0C\u96BE\u4EE5\u62DB\u67B6\u3002\u51CC\u96C5\u7434\u53EA\u597D\u6536\u56DE\u52B2\u529B\uFF0C\u4E13\u5FC3\u8C03\u6559\u9F99\u6714\u5251\u6CD5\u4E0A\u7684\u5F31\u5904\u3002</p><p>\u8FC7\u4E86\u767E\u62DB\u4E4B\u540E\uFF0C\u9F99\u6714\u6C14\u606F\u6E10\u6E10\u7C97\u91CD\uFF0C\u6C57\u6C34\u51E0\u4E4E\u6E7F\u900F\u4E86\u8863\u670D\u3002\u51CC\u96C5\u7434\u6015\u7D2F\u574F\u4E86\u4ED6\uFF0C\u659C\u624B\u5728\u9F99\u6714\u5251\u950B\u4E0A\u4E00\u70B9\uFF0C\u501F\u52BF\u98D8\u5F00\u3002\u5728\u534A\u7A7A\u4E2D\u8170\u80A2\u4E00\u8F6C\uFF0C\u843D\u5728\u7434\u65C1\u3002</p><p>\u5979\u4FE1\u624B\u4E00\u6325\uFF0C\u6EE1\u5730\u7684\u82B1\u74E3\u5B9B\u5982\u7C89\u8776\u822C\u98DE\u821E\u8D77\u6765\uFF0C\u4E00\u7247\u7247\u6CBE\u5728\u82B1\u679D\u4E0A\u3002\u51CC\u96C5\u7434\u5FAE\u5FAE\u4E00\u7B11\uFF0C\u626C\u624B\u6254\u51FA\u82B1\u679D\u3002\u7B49\u82B1\u679D\u98DE\u5230\u6F6D\u4E0A\uFF0C\u90A3\u4E9B\u82B1\u74E3\u540C\u65F6\u6563\u5F00\uFF0C\u597C\u7D2B\u5AE3\u7EA2\u6D12\u843D\u6EE1\u6F6D\u3002</p><p>\u9F99\u6714\u624B\u4E00\u677E\uFF0C\u957F\u5251\u6389\u5728\u77F3\u4E0A\u3002\u51CC\u96C5\u7434\u4E00\u65F6\u5174\u8D77\uFF0C\u9732\u4E86\u4E00\u624B\u5185\u529F\uFF0C\u5374\u89E6\u52A8\u4E86\u5F92\u513F\u7684\u5FC3\u4E8B\uFF0C\u4E0D\u514D\u6709\u4E9B\u6B49\u610F\uFF0C\u4E8E\u662F\u6E29\u8A00\u9053\uFF1A\u300C\u6714\u513F\uFF0C\u6B47\u4E00\u4F1A\u513F\u5427\u3002\u300D\u8BF4\u7740\u5979\u62FF\u8D77\u6BDB\u5DFE\uFF0C\u50CF\u5F80\u5E38\u90A3\u6837\uFF0C\u628A\u9F99\u6714\u63FD\u5728\u6000\u91CC\uFF0C\u4ED4\u7EC6\u64E6\u53BB\u4ED6\u5934\u4E0A\u7684\u6C57\u6C34\u3002</p><p>\u9F99\u6714\u5DF2\u7ECF\u4E60\u60EF\u4E86\u5E08\u5A18\u8FD9\u79CD\u6BCD\u7231\u5F0F\u7684\u4EB2\u6635\uFF0C\u4ED6\u6324\u51FA\u4E00\u4E2A\u50F5\u786C\u7684\u7B11\u5BB9\uFF0C\u6CA1\u6709\u4F5C\u58F0\u3002</p><p>\u5267\u70C8\u8FD0\u52A8\u4E4B\u540E\uFF0C\u90A3\u5F20\u79C0\u7F8E\u7684\u9762\u5B54\u767D\u91CC\u900F\u7EA2\uFF0C\u663E\u5F97\u6108\u53D1\u59E3\u4E3D\u3002\u51CC\u96C5\u7434\u7B11\u9053\uFF1A\u300C\u6714\u513F\u957F\u5F97\u597D\u5FEB\uFF0C\u518D\u8FC7\u4E24\u5E74\u5C31\u8BE5\u8D85\u8FC7\u5E08\u5A18\u4E86\u5462\u3002\u300D</p><p>\u9F99\u6714\u4F4E\u58F0\u9053\uFF1A\u300C\u5F92\u513F\u597D\u7B28\u2026\u2026\u300D</p><p>\u300C\u600E\u4E48\u4F1A\u5462\uFF1F\u300D\u51CC\u96C5\u7434\u9053\uFF1A\u300C\u4F60\u7684\u5251\u6CD5\u6BD4\u5E08\u5A18\u5F53\u5E74\u5B66\u5F97\u8FD8\u5FEB\u2014\u2014\u4F60\u5E08\u7236\u4E5F\u662F\u5165\u95E8\u7B2C\u4E94\u5E74\u624D\u5B66\u4E86\u8FD9\u4E48\u591A\u3002\u300D</p><p>\u300C\u53EF\u6211\u7684\u5185\u529F\u2026\u2026\u300D</p><p>\u51CC\u96C5\u7434\u62C9\u7740\u9F99\u6714\u8D70\u5230\u6F6D\u8FB9\uFF0C\u5E76\u80A9\u5750\u4E0B\uFF0C\u7136\u540E\u62C9\u8D77\u88D9\u88FE\uFF0C\u9664\u53BB\u978B\u889C\uFF0C\u5C06\u7389\u8DB3\u6D78\u5728\u6E29\u51C9\u7684\u6F6D\u6C34\u4E2D\u3002</p><p>\u56DB\u5468\u5BC2\u9759\u5F97\u5F77\u4F5B\u65F6\u95F4\u90FD\u505C\u6B62\u4E86\uFF0C\u4E00\u6735\u6735\u767D\u4E91\u4ECE\u6F6D\u4E2D\u4E0D\u505C\u7684\u98D8\u8FC7\uFF0C\u6CA1\u6709\u7559\u4E0B\u4E00\u4E1D\u75D5\u8FF9\u3002</p><p>\u90A3\u53CC\u7389\u8DB3\u5A07\u5C0F\u73B2\u73D1\uFF0C\u5F77\u4F5B\u4E00\u53CC\u6676\u83B9\u7684\u7389\u74A7\u6C89\u5728\u6F6D\u5E95\u3002\u767D\u7699\u7684\u5C0F\u817F\u66F2\u7EBF\u4F18\u7F8E\uFF0C\u867D\u7136\u884C\u8D70\u6C5F\u6E56\u591A\u5E74\uFF0C\u5374\u6CA1\u6709\u4E00\u4E1D\u98CE\u971C\u7684\u75D5\u8FF9\u3002\u5979\u8FD9\u4E00\u751F\uFF0C\u679C\u7136\u662F\u5E78\u8FD0\u65E0\u6BD4\u3002</p><p>\u300C\u6714\u513F\uFF0C\u300D\u6C89\u9ED8\u826F\u4E45\uFF0C\u51CC\u96C5\u7434\u7EC8\u4E8E\u51B3\u5B9A\u544A\u8BC9\u9F99\u6714\u771F\u76F8\uFF0C\u5979\u59D4\u5A49\u5730\u8BF4\u9053\uFF1A\u300C\u4F60\u4E5F\u77E5\u9053\uFF0C\u4F60\u7684\u4E39\u7530\u66FE\u7ECF\u53D7\u8FC7\u4F24\u3002\u867D\u7136\u68B5\u4ED9\u5B50\u5E2E\u4F60\u6CBB\u597D\u4E86\u4F24\u52BF\uFF0C\u4F46\u6C14\u5E9C\u4E00\u65E6\u53D7\u635F\uFF0C\u5F88\u96BE\u518D\u517B\u70BC\u771F\u6C14\u2026\u2026\u56E0\u6B64\uFF0C\u4F60\u7684\u5185\u529F\u8FDB\u5883\u4F1A\u5F88\u6162\u3002\u300D</p><p>\u9F99\u6714\u9ED8\u9ED8\u60F3\u4E86\u7247\u523B\uFF0C\u7136\u540E\u626C\u8138\u9732\u51FA\u4E00\u4E2A\u52A8\u4EBA\u7684\u7B11\u5BB9\uFF0C\u300C\u6211\u591A\u4E45\u80FD\u7EC3\u6210\u7B2C\u4E8C\u5C42\uFF1F\u300D</p><p>\u300C\u4E09\u5E74\u3002\u300D</p><p>\u300C\u7B2C\u4E09\u5C42\u5462\uFF1F\u300D</p><p>\u51CC\u96C5\u7434\u8FDF\u7591\u4E86\u4E00\u4E0B\uFF0C\u300C\u4E5F\u8BB8\u8981\u4E94\u5E74\u2026\u2026\u300D</p><p>\u300C\u4EC0\u4E48\u65F6\u5019\u80FD\u7EC3\u5F97\u548C\u5E08\u5A18\u4E00\u6837\u5462\uFF1F\u300D</p><p>\u51CC\u96C5\u7434\u671B\u7740\u9F99\u6714\u5E73\u9759\u7684\u53CC\u773C\uFF0C\u786C\u4E86\u786C\u5FC3\u80A0\uFF0C\u7EC8\u4E8E\u8BF4\u9053\uFF1A\u300C\u4E0D\u53EF\u80FD\u7684\u2026\u2026\u300D</p><p>\u9F99\u6714\u6162\u6162\u4F4E\u4E0B\u5934\uFF0C\u4E45\u4E45\u6CA1\u6709\u4F5C\u58F0\u3002</p><p>\u51CC\u96C5\u7434\u5C0F\u5FC3\u5730\u5782\u4E0B\u5934\uFF0C\u5374\u89C1\u4ED6\u5DF2\u7ECF\u6CEA\u6D41\u6EE1\u9762\u3002</p><p>\u300C\u5176\u5B9E\u7EC3\u4E0D\u6210\u5185\u529F\u4E5F\u6CA1\u5173\u7CFB\uFF0C\u6709\u5E08\u5A18\u5728\uFF0C\u6714\u513F\u4EC0\u4E48\u4E5F\u4E0D\u5FC5\u6015\u3002\u300D\u51CC\u96C5\u7434\u67D4\u58F0\u5BBD\u6170\u9053\u3002</p><p>\u300C\u4E0D\uFF01\u300D\u9F99\u6714\u7A81\u7136\u72C2\u543C\u4E00\u58F0\uFF0C\u7EB5\u8EAB\u671D\u6F6D\u4E2D\u6251\u53BB\u3002</p><p>\u300C\u6714\u513F\uFF01\u300D\u51CC\u96C5\u7434\u60CA\u53EB\u7740\u8DF3\u5165\u6E05\u6F6D\uFF0C\u6293\u4F4F\u9F99\u6714\u7684\u624B\u81C2\u3002\u9F99\u6714\u75AF\u72C2\u5730\u6323\u624E\u7740\uFF0C\u50CF\u8981\u6495\u788E\u81EA\u5DF1\u7684\u80F8\u819B\u4E00\u822C\u62DA\u547D\u6495\u626F\u7740\u8863\u670D\u3002</p><p>\u6F6D\u6C34\u770B\u7740\u6E05\u6F88\u89C1\u5E95\uFF0C\u5176\u5B9E\u5374\u6781\u6DF1\uFF0C\u51CC\u96C5\u7434\u6015\u4ED6\u6C14\u8840\u90C1\u96C6\uFF0C\u4E0D\u6562\u5236\u4F4F\u4ED6\u7684\u7A74\u9053\uFF0C\u53EA\u80FD\u62B1\u7740\u4ED6\u7684\u8170\u8EAB\u671D\u6F6D\u8FB9\u6E38\u53BB\u3002</p><p>\u9F99\u6714\u5927\u53E3\u5927\u53E3\u545B\u7740\u6C34\uFF0C\u4E00\u8FB9\u7528\u5636\u54D1\u7684\u58F0\u97F3\u543C\u53EB\u9053\uFF1A\u300C\u4F60\u9A97\u6211\uFF01\u6211\u8981\u7EC3\u6210\u7EDD\u4E16\u6B66\u529F\uFF01\u6211\u8981\u62A5\u4EC7\uFF01\u300D</p><p>\u51CC\u96C5\u7434\u8D39\u5C3D\u529B\u6C14\u628A\u4ED6\u62D6\u5230\u5CB8\u4E0A\uFF0C\u9F99\u6714\u5589\u5499\u4E2D\u5DF2\u7ECF\u545B\u51FA\u8840\u6765\u3002\u8FD9\u4E2A\u4FCA\u79C0\u6E29\u6587\u7684\u5B69\u5B50\u8C61\u53D8\u4E86\u4E00\u4E2A\u4EBA\u822C\uFF0C\u4E24\u773C\u8840\u7EA2\uFF0C\u65E0\u8BBA\u5E08\u5A18\u5982\u4F55\u529D\u6170\uFF0C\u4ED6\u90FD\u5145\u8033\u4E0D\u95FB\uFF0C\u75AF\u4E86\u4E00\u822C\u5BF9\u7740\u5CB8\u8FB9\u7684\u5DE8\u77F3\u53C8\u8E22\u53C8\u6253\uFF0C\u4E0D\u591A\u65F6\u4E24\u624B\u4FBF\u8840\u8089\u6A21\u7CCA\u3002</p><p>\u51CC\u96C5\u7434\u6025\u5F97\u6389\u4E0B\u6CEA\u6765\uFF0C\u4E00\u53E0\u58F0\u53EB\u9053\uFF1A\u300C\u6714\u513F\uFF01\u6714\u513F\uFF01\u4F60\u51B7\u9759\u4E00\u4E9B\uFF01\u300D</p><p>\u300C\u683C\u300D\u7684\u4E00\u58F0\uFF0C\u9F99\u6714\u624B\u9AA8\u6298\u65AD\uFF0C\u4ED6\u54C7\u7684\u5410\u51FA\u4E00\u53E3\u9C9C\u8840\uFF0C\u7A81\u7136\u4E00\u5934\u671D\u77F3\u4E0A\u649E\u53BB\u3002</p><p>\u51CC\u96C5\u7434\u89C1\u5F92\u513F\u5206\u660E\u662F\u8981\u5BFB\u6B7B\uFF0C\u624D\u77E5\u9053\u300C\u62A5\u4EC7\u300D\u548C\u300C\u7EC3\u6B66\u300D\u5BF9\u4ED6\u6765\u8BF4\u610F\u5473\u7740\u4EC0\u4E48\u3002\u5979\u8FDE\u5FD9\u62A2\u8FC7\u53BB\uFF0C\u7528\u8EAB\u4F53\u6321\u5728\u5DE8\u77F3\u524D\u3002</p><p>\u84EC\u7684\u4E00\u58F0\u95F7\u54CD\uFF0C\u9F99\u6714\u4E00\u5934\u649E\u5728\u51CC\u96C5\u7434\u80F8\u53E3\u3002\u4ED6\u8FD9\u4E00\u4E0B\u62FC\u4E86\u6027\u547D\uFF0C\u4F7F\u4E0A\u5168\u8EAB\u7684\u529B\u6C14\uFF0C\u51CC\u96C5\u7434\u6CA1\u6709\u8FD0\u529F\u62A4\u4F53\uFF0C\u987F\u65F6\u75DB\u5F7B\u5FC3\u80BA\u3002\u5979\u5FCD\u4F4F\u75DB\u695A\uFF0C\u4E24\u624B\u6293\u4F4F\u9F99\u6714\u80A9\u5934\uFF0C\u53EB\u9053\uFF1A\u300C\u6714\u513F\uFF0C\u4F60\u2026\u2026\u300D</p><p>\u9F99\u6714\u7C97\u91CD\u5730\u5598\u606F\u7740\uFF0C\u5589\u4E2D\u4E0D\u4F4F\u6E85\u51FA\u8840\u6CAB\u3002\u4ED6\u6D51\u8EAB\u662F\u6C34\uFF0C\u8840\u8089\u6A21\u7CCA\u7684\u53CC\u624B\u5140\u81EA\u4E0D\u4F4F\u98A4\u6296\uFF0C\u4F46\u75AF\u72C2\u7684\u81EA\u6B8B\u4E3E\u52A8\u5374\u610F\u5916\u5730\u505C\u4F4F\u4E86\u3002\u4E24\u773C\u76F4\u76F4\u671B\u7740\u51CC\u96C5\u7434\u7684\u80F8\u53E3\uFF0C\u773C\u795E\u602A\u5F02\u3002</p><p>\u51CC\u96C5\u7434\u4F4E\u5934\u4E00\u770B\uFF0C\u4E0D\u7531\u9762\u7EA2\u8FC7\u8033\u3002\u521A\u624D\u7684\u6323\u624E\u4E2D\uFF0C\u5979\u7684\u8863\u895F\u4E0D\u77E5\u4F55\u65F6\u88AB\u6495\u7834\uFF0C\u4E00\u76F4\u655E\u5230\u8170\u95F4\uFF0C\u9888\u4E2D\u62B9\u80F8\u7684\u7CFB\u5E26\u4E5F\u65AD\u4E86\u4E00\u6839\uFF0C\u4EB5\u8863\u7FFB\u5F00\uFF0C\u4E00\u53EA\u767D\u5149\u5149\u7684\u7389\u4E73\u6B63\u5728\u80F8\u524D\u98A4\u5FAE\u5FAE\u6296\u4E2A\u4E0D\u505C\u3002</p><p>\u300C\u5A18\u3002\u300D\u5FFD\u7136\u95F4\uFF0C\u9F99\u6714\u54D1\u7740\u55D3\u5B50\u53EB\u4E00\u58F0\u3002</p><p>\u51CC\u96C5\u7434\u6B63\u7EA2\u7740\u8138\u906E\u63A9\u80F8\u4E73\uFF0C\u88AB\u9F99\u6714\u8FD9\u4E00\u58F0\u558A\uFF0C\u624B\u6307\u7ACB\u65F6\u50F5\u4F4F\u4E86\u3002</p><p>\u300C\u5A18\uFF01\u300D</p><p>\u51E0\u70B9\u6BB7\u7EA2\u7684\u9C9C\u8840\u4ECE\u9F99\u6714\u5589\u4E2D\u98DE\u51FA\uFF0C\u6E85\u5728\u96EA\u5AE9\u7684\u9165\u4E73\u4E0A\u3002\u51CC\u96C5\u7434\u8863\u886B\u5C3D\u6E7F\uFF0C\u8584\u8584\u7684\u8D34\u5728\u8EAB\u4E0A\uFF0C\u73B2\u73D1\u6709\u81F4\u7684\u5A07\u8EAF\u66F2\u7EBF\u6BD5\u9732\u3002\u90A3\u53EA\u4E30\u7F8E\u7684\u7389\u4E73\u9AD8\u9AD8\u8038\u8D77\uFF0C\u6E7F\u6DCB\u6DCB\u7684\u6C34\u8FF9\u88AB\u4F53\u6E29\u4E00\u84B8\uFF0C\u6563\u53D1\u51FA\u6D53\u90C1\u7684\u4E73\u9999\u3002\u5979\u7684\u4E73\u6655\u4ECD\u662F\u5C11\u5973\u822C\u7C89\u7EA2\u7684\u8272\u6CFD\uFF0C\u7EA2\u5AE9\u7684\u4E73\u5934\u8273\u5982\u739B\u7459\uFF0C\u5C71\u98CE\u62C2\u8FC7\uFF0C\u7ACB\u5373\u786C\u786C\u6311\u8D77\u3002\u5979\u6CA1\u6709\u518D\u8BD5\u56FE\u62C9\u597D\u8863\u670D\uFF0C\u53EA\u65E0\u9650\u601C\u7231\u5730\u671B\u7740\u9F99\u6714\u3002</p><p>\u300C\u5A18\uFF01\uFF01\u300D</p><p>\u9F99\u6714\u8C61\u53D7\u4F24\u7684\u5C0F\u517D\u822C\u5636\u53EB\u4E00\u58F0\uFF0C\u4E00\u5934\u6251\u5230\u51CC\u96C5\u7434\u6000\u4E2D\uFF0C\u6367\u4F4F\u90A3\u53EA\u88F8\u9732\u7684\u96EA\u4E73\u62DA\u547D\u5438\u542E\u8D77\u6765\u3002</p><p>\u4E73\u5934\u88AB\u706B\u70ED\u7684\u5507\u820C\u5438\u542E\u7740\uFF0C\u4F20\u6765\u9635\u9635\u9165\u75D2\u3002\u51CC\u96C5\u7434\u6276\u5728\u9F99\u6714\u80A9\u5934\u7684\u7EA4\u624B\u4E00\u677E\uFF0C\u8EAB\u5B50\u8F6F\u8F6F\u9760\u5728\u77F3\u4E0A\uFF0C\u5979\u4F4E\u4F4E\u53F9\u4E86\u53E3\u6C14\uFF0C\u7136\u540E\u7F13\u7F13\u5408\u4E0A\u7F8E\u76EE\u3002</p><p>\u9633\u5149\u65E0\u58F0\u65E0\u606F\u5730\u5411\u5CF0\u4E0B\u79FB\u53BB\uFF0C\u98CE\u666F\u5982\u753B\u7684\u6F6D\u6C34\u8FB9\uFF0C\u4E00\u4E2A\u8863\u886B\u96F6\u4E71\u7684\u7F8E\u5987\u53CC\u76EE\u7D27\u95ED\uFF0C\u8F6F\u7EF5\u7EF5\u9760\u5728\u5DE8\u77F3\u4E0A\uFF0C\u80F8\u524D\u8863\u886B\u655E\u9732\uFF0C\u633A\u51FA\u4E00\u53EA\u96EA\u56E2\u822C\u7684\u7F8E\u4E73\u3002</p><p>\u4E00\u4E2A\u79C0\u7F8E\u72B9\u5982\u5C11\u5973\u7684\u5B69\u5B50\u6B63\u4F0F\u5728\u5979\u80F8\u524D\uFF0C\u4E00\u8FB9\u54ED\u53EB\u4E00\u8FB9\u5438\u542E\u7740\u7F8E\u5987\u4E30\u6EE1\u7684\u4E73\u623F\u3002\u4ED6\u4E0D\u4F4F\u54B3\u55FD\u7740\u5410\u51FA\u9C9C\u8840\uFF0C\u5C06\u96EA\u767D\u7684\u4E73\u7403\u67D3\u5F97\u4E00\u7247\u901A\u7EA2\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u65E5\u66AE\u65F6\u5206\uFF0C\u4E00\u4E2A\u7FE9\u7FE9\u5C11\u5E74\u6253\u9A6C\u8FDB\u5165\u5B9B\u9675\u3002\u6765\u5F80\u7684\u884C\u4EBA\u770B\u5230\u4ED6\u9762\u8C8C\u90FD\u4E0D\u7531\u773C\u775B\u4E00\u4EAE\uFF0C\u8D5E\u9053\uFF1A\u300C\u597D\u4E2A\u7F8E\u5C11\u5E74\u3002\u300D</p><p>\u90A3\u5C11\u5E74\u8FF3\u76F4\u6765\u5230\u6C88\u5E9C\uFF0C\u4E0B\u9A6C\u5411\u95E8\u53E3\u7684\u5BB6\u4E01\u4F5C\u4E86\u4E00\u4E2A\u63D6\uFF0C\u8BF4\u9053\uFF1A\u300C\u5728\u4E0B\u4E5D\u534E\u5C71\u9F99\u6714\uFF0C\u8BF7\u7980\u544A\u8D35\u4E3B\u4EBA\u3002\u300D\u8BF4\u7740\u5FAE\u5FAE\u4E00\u7B11\uFF0C\u548C\u6C14\u800C\u53C8\u6709\u793C\u3002</p><p>\u90A3\u5BB6\u4E01\u53BB\u4E86\u7247\u523B\uFF0C\u5954\u51FA\u6765\u9053\uFF1A\u300C\u9F99\u5C11\u7237\u5FEB\u8BF7\u8FDB\u3002\u5BB6\u4E3B\u4EBA\u524D\u65E5\u51FA\u95E8\uFF0C\u5C11\u592B\u4EBA\u8BF7\u60A8\u5230\u5185\u5B85\u76F8\u89C1\u3002\u300D</p><p>\u6C88\u6C0F\u662F\u5B9B\u9675\u6709\u540D\u7684\u4E66\u9999\u95E8\u7B2C\uFF0C\u53EF\u5C11\u592B\u4EBA\u6DF3\u4E8E\u7476\u5374\u51FA\u81EA\u6B66\u6797\u4E16\u5BB6\uFF0C\u662F\u4E1C\u6D77\u6DF3\u4E8E\u6C0F\u4E09\u6735\u540D\u82B1\u4E2D\u6700\u5C0F\u7684\u4E00\u4E2A\uFF0C\u4EBA\u79F0\u7F8E\u743C\u7476\u3002\u867D\u7136\u5979\u4ECE\u672A\u5728\u6C5F\u6E56\u8D70\u52A8\uFF0C\u4F46\u65E9\u5DF2\u82B3\u540D\u8FDC\u64AD\u3002\u8FD9\u4E24\u5BB6\u4F1A\u7ED3\u6210\u79E6\u664B\u4E4B\u597D\uFF0C\u7740\u5B9E\u51FA\u4E4E\u6C5F\u6E56\u4E2D\u4EBA\u7684\u610F\u6599\u3002</p><p>\u95E8\u5916\u7684\u5C0F\u5A62\u671B\u4E86\u9F99\u6714\u4E00\u773C\uFF0C\u4E0D\u7531\u8138\u4E0A\u4E00\u7EA2\uFF0C\u8FDE\u5FD9\u7F9E\u6DA9\u5730\u5782\u4E0B\u5934\uFF0C\u6380\u5F00\u73E0\u5E18\u3002</p><p>\u9F99\u6714\u9053\u4E86\u8C22\uFF0C\u7F13\u6B65\u8D70\u8FDB\u5BA4\u5185\uFF0C\u53EA\u89C1\u56DB\u58C1\u9648\u8BBE\u96C5\u6D01\uFF0C\u6BEB\u65E0\u5962\u534E\u6C14\u606F\uFF0C\u679C\u7136\u4E0E\u5BFB\u5E38\u5BCC\u5BA4\u4E0D\u540C\u3002</p><p>\u300C\u9F99\u516C\u5B50\uFF0C\u300D\u53A2\u623F\u5185\u4F20\u6765\u4E00\u4E2A\u751C\u7F8E\u7684\u58F0\u97F3\uFF0C\u300C\u59BE\u8EAB\u884C\u8D70\u4E0D\u4FBF\uFF0C\u8BF7\u516C\u5B50\u8FC7\u6765\u5750\u5427\u3002\u300D</p><p>\u9F99\u6714\u8E0C\u8E87\u4E86\u4E00\u4E0B\uFF0C\u4F9D\u8A00\u8D70\u8FDB\u53A2\u623F\u3002</p><p>\u53EA\u89C1\u4E34\u7A97\u5904\u6446\u7740\u4E00\u5F20\u8F6F\u6905\uFF0C\u4E0A\u9762\u5750\u7740\u4E00\u4E2A\u5C11\u5987\u3002\u5979\u4E0D\u8FC7\u53CC\u5341\u5E74\u534E\uFF0C\u808C\u80A4\u72B9\u5982\u725B\u4E73\u822C\u767D\u5AE9\uFF0C\u4E00\u5F20\u5343\u5A07\u767E\u5A9A\u7684\u4FCF\u8138\u8273\u5149\u56DB\u5C04\uFF0C\u5BB9\u8C8C\u59E3\u4E3D\u66F4\u5728\u6C5F\u6E56\u4F20\u8A00\u4E4B\u4E0A\u3002\u6B64\u65F6\u5A07\u8EAF\u8F6F\u8F6F\u501A\u5728\u9526\u9760\u4E0A\uFF0C\u8BF4\u4E0D\u5C3D\u7684\u98CE\u6D41\u5A49\u8F6C\uFF0C\u6D51\u4E0D\u4F3C\u540D\u95FB\u6B66\u6797\u7684\u7F8E\u5A9B\uFF0C\u5012\u66F4\u50CF\u662F\u540D\u95E8\u671B\u65CF\u500D\u53D7\u8363\u5BA0\u7684\u8D35\u5987\u3002</p><p>\u89C1\u5230\u9F99\u6714\u7684\u4FCA\u79C0\uFF0C\u6DF3\u4E8E\u7476\u4E0D\u7531\u300C\u54CE\u5440\u300D\u4E00\u58F0\u53EB\u4E86\u8D77\u6765\uFF0C\u300C\u597D\u6807\u81F4\u7684\u5C11\u5E74\u3002\u300D</p><p>\u5979\u957F\u957F\u7684\u776B\u6BDB\u4E00\u95EA\u4E00\u95EA\uFF0C\u5999\u76EE\u9976\u6709\u5174\u8DA3\u5730\u4E0A\u4E0B\u6253\u91CF\u7740\u9F99\u6714\uFF0C\u8BF4\u9053\uFF1A\u300C\u65E9\u542C\u8BF4\u68B5\u4ED9\u5B50\u7684\u4E49\u5B50\u751F\u5F97\u7389\u6811\u4E34\u98CE\uFF0C\u4E00\u8868\u4EBA\u6750\uFF0C\u4ECA\u65E5\u4E00\u89C1\u679C\u7136\u662F\u4FCA\u96C5\u975E\u51E1\u3002\u4E0D\u77E5\u8BA2\u4E86\u4EBA\u5BB6\u6CA1\u6709\uFF1F\u300D</p><p>\u9F99\u6714\u4FCA\u8138\u53D1\u70EB\uFF0C\u300C\u5728\u4E0B\u8EAB\u5904\u6B66\u6797\u2026\u2026\u300D</p><p>\u6DF3\u4E8E\u7476\u5410\u4E86\u5410\u820C\u5934\uFF0C\u795E\u60C5\u5A07\u4FCF\u53EF\u559C\uFF0C\u300C\u5728\u8FD9\u91CC\u4F4F\u5F97\u4E45\u4E86\uFF0C\u6765\u6765\u5F80\u5F80\u90FD\u662F\u8FD9\u5BB6\u592A\u592A\u90A3\u5BB6\u592B\u4EBA\uFF0C\u5012\u5FD8\u4E86\u6B66\u6797\u7684\u4E0D\u540C\u3002\u300D\u8BF4\u7740\u76B1\u8D77\u7709\u5934\uFF0C\u300C\u6211\u662F\u4E0D\u662F\u4E5F\u50CF\u4E2A\u65E0\u804A\u7684\u8001\u592A\u5A46\uFF1F\u60F9\u4F60\u8BA8\u538C\u4E86\uFF1F\u300D</p><p>\u9F99\u6714\u89C1\u5979\u7D27\u5F20\u7684\u6837\u5B50\uFF0C\u4E0D\u7531\u7B11\u9053\uFF1A\u300C\u600E\u4E48\u4F1A\u5462\uFF1F\u300D</p><p>\u6DF3\u4E8E\u7476\u677E\u4E86\u53E3\u6C14\uFF0C\u8BF4\u9053\uFF1A\u300C\u6211\u5927\u59D0\u5BB6\u7684\u5973\u513F\u6BD4\u4F60\u5C0F\u4E86\u51E0\u5C81\uFF0C\u65AF\u65AF\u6587\u6587\u4E00\u4E2A\u5C0F\u7F8E\u4EBA\u513F\uFF0C\u89C1\u516C\u5B50\u8FD9\u4E48\u82F1\u4FCA\uFF0C\u7981\u4E0D\u4F4F\u60F3\u7ED9\u4F60\u4EEC\u7ED3\u4E2A\u4EB2\u5462\u3002\u300D</p><p>\u9F99\u6714\u4E0D\u597D\u610F\u601D\u5730\u7B11\u9053\uFF1A\u300C\u591A\u8C22\u963F\u59E8\u597D\u610F\uFF0C\u53EA\u662F\u5728\u4E0B\u5E74\u7EAA\u5C1A\u5C0F\u2026\u2026\u300D</p><p>\u6DF3\u4E8E\u7476\u63A9\u5634\u7B11\u9053\uFF1A\u300C\u5341\u56DB\u4E94\u5C81\u4E5F\u7B97\u4E0D\u5F97\u5C0F\u4E86\uFF0C\u6211\u8DDF\u6C88\u90CE\u5F53\u5E74\u8FD8\u662F\u7239\u7239\u6307\u8179\u4E3A\u5A5A\u5462\u3002\u300D</p><p>\u6DF3\u4E8E\u5BB6\u662F\u4E1C\u6D77\u671B\u65CF\uFF0C\u5982\u4ECA\u5317\u65B9\u80E1\u864F\u5165\u4FB5\uFF0C\u6C49\u4EBA\u5927\u65CF\u7EB7\u7EB7\u5357\u8FC1\uFF0C\u8BB2\u7A76\u95E8\u7B2C\u7684\u6DF3\u4E8E\u6C0F\u4E0D\u5C51\u4E0E\u80E1\u4EBA\u6765\u5F80\uFF0C\u8FD9\u624D\u4E0E\u6C88\u6C0F\u8054\u59FB\u3002</p><p>\u5C11\u5987\u63A9\u5634\u7B11\u4E86\u7247\u523B\uFF0C\u731B\u7136\u60F3\u8D77\u5BA2\u4EBA\u7684\u6765\u610F\uFF0C\u300C\u54CE\u5440\uFF0C\u516C\u5B50\u662F\u6765\u627E\u68B5\u4ED9\u5B50\u7684\u5427\uFF1F\u62C9\u4F60\u8BF4\u4E86\u534A\u5929\u95F2\u8BDD\uFF0C\u771F\u6210\u4E86\u5520\u53E8\u7684\u8001\u592A\u5A46\u5462\u3002\u300D</p><p>\u9F99\u6714\u5BF9\u5979\u7684\u723D\u6717\u5FC3\u6709\u597D\u611F\uFF0C\u7B11\u9053\uFF1A\u300C\u548C\u592B\u4EBA\u8BF4\u8BDD\u5F88\u6709\u8DA3\u554A\u3002\u300D</p><p>\u6DF3\u4E8E\u7476\u9053\uFF1A\u300C\u68B5\u4ED9\u5B50\u559C\u6B22\u6E05\u9759\uFF0C\u5728\u5E9C\u91CC\u4F4F\u4E86\u51E0\u65E5\uFF0C\u5C31\u642C\u5230\u57CE\u5916\u6D41\u97F3\u6EAA\u53BB\u4E86\u3002\u300D\u5979\u4ED4\u7EC6\u8BF4\u4E86\u8DEF\u5F84\uFF0C\u53C8\u9053\uFF1A\u300C\u6211\u884C\u8D70\u4E0D\u4FBF\uFF0C\u4E0D\u80FD\u5E26\u516C\u5B50\u8FC7\u53BB\u4E86\u3002\u300D</p><p>\u9F99\u6714\u8FD9\u624D\u6CE8\u610F\u5230\u5979\u8170\u4E0A\u642D\u7740\u4E00\u6761\u8584\u6BEF\uFF0C\u8179\u90E8\u9AD8\u9AD8\u9686\u8D77\uFF0C\u5DF2\u7ECF\u662F\u6709\u4E86\u8EAB\u5B55\u3002</p><p>\u6DF3\u4E8E\u7476\u8F7B\u8F7B\u629A\u7740\u8179\u90E8\uFF0C\u751C\u871C\u5730\u7B11\u9053\uFF1A\u300C\u4E03\u4E2A\u6708\u4E86\u5462\u3002\u9F99\u516C\u5B50\uFF0C\u5E26\u6211\u5411\u68B5\u4ED9\u5B50\u95EE\u4E2A\u597D\uFF0C\u8FC7\u4E9B\u5929\u8FD8\u8981\u8BF7\u5979\u56DE\u6765\u4F4F\u4E0A\u51E0\u65E5\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5230\u4E86\u6D41\u97F3\u6EAA\u5DF2\u7ECF\u6708\u8FC7\u4E2D\u5929\u3002\u9F99\u6714\u653E\u6162\u901F\u5EA6\uFF0C\u6CBF\u7740\u6797\u95F4\u7684\u5C0F\u8DEF\u7F13\u7F13\u884C\u6765\u3002\u677E\u9488\u7684\u6E05\u9999\u5728\u6708\u5149\u4E2D\u6D6E\u52A8\uFF0C\u8FDC\u5904\u4F20\u6765\u6D41\u6C34\u7684\u6DD9\u6DD9\u58F0\u3002\u60F3\u5230\u4E00\u4F1A\u513F\u5C31\u80FD\u89C1\u5230\u4E49\u6BCD\uFF0C\u9F99\u6714\u7126\u6025\u7684\u5FC3\u7EEA\u6E10\u6E10\u5B81\u9759\u4E0B\u6765\u3002</p><p>\u7ED5\u8FC7\u4E00\u6392\u5782\u67F3\uFF0C\u773C\u524D\u51FA\u73B0\u4E00\u6761\u6E05\u4EAE\u7684\u5C0F\u6EAA\uFF0C\u6708\u8272\u4E0B\u72B9\u5982\u95EA\u4EAE\u7684\u94F6\u5E26\u3002\u6EAA\u6C34\u4ECE\u9AD8\u9AD8\u4F4E\u4F4E\u7684\u9752\u77F3\u4E0A\u6D41\u8FC7\uFF0C\u53D1\u51FA\u6E05\u6CE0\u6CE0\u7684\u6C34\u58F0\u3002</p><p>\u6EAA\u6C34\u65C1\u662F\u4E24\u95F4\u5C0F\u5C0F\u7684\u623F\u820D\uFF0C\u677F\u58C1\u8C61\u88AB\u6E05\u6C34\u6D17\u8FC7\uFF0C\u4E00\u5C18\u4E0D\u67D3\u3002\u6D01\u767D\u7684\u7A97\u7EB8\u900F\u51FA\u4E00\u70B9\u70DB\u5149\uFF0C\u6E29\u6696\u800C\u53C8\u5B89\u7965\u3002</p><p>\u9F99\u6714\u8D70\u4E0A\u53F0\u9636\uFF0C\u8F7B\u8F7B\u53EB\u4E86\u58F0\uFF1A\u300C\u5A18\u3002\u300D</p><p>\u300C\u6714\u513F\uFF1F\u300D\u623F\u5185\u54CD\u8D77\u4E00\u4E2A\u719F\u6089\u7684\u58F0\u97F3\u3002\u63A5\u7740\u623F\u95E8\u4E00\u52A8\uFF0C\u7D20\u8863\u767D\u88D9\u7684\u68B5\u96EA\u828D\u51FA\u73B0\u5728\u773C\u524D\u3002\u5979\u4E00\u624B\u4E3E\u7740\u8721\u70DB\uFF0C\u98CE\u59FF\u7EF0\u7EA6\u5730\u7AD9\u5728\u95E8\u53E3\uFF0C\u76C8\u76C8\u7684\u7F8E\u76EE\u4E2D\u6EE1\u662F\u60CA\u559C\u3002</p><p>\u300C\u5A18\uFF0C\u300D\u5750\u5728\u5185\u5BA4\u51C0\u65E0\u7EA4\u5C18\u7684\u5730\u677F\u4E0A\uFF0C\u9F99\u6714\u9759\u9759\u8BF4\u9053\uFF1A\u300C\u5B69\u513F\u8FD8\u80FD\u7EC3\u6210\u5185\u529F\u5417\uFF1F\u300D</p><p>\u68B5\u96EA\u828D\u5E76\u819D\u5750\u5728\u4ED6\u5BF9\u9762\uFF0C\u6E29\u5A49\u5730\u5F2F\u4E0B\u8170\u80A2\uFF0C\u659F\u4E86\u676F\u8336\u6C34\uFF0C\u653E\u5728\u4ED6\u624B\u91CC\uFF0C\u8F7B\u53F9\u9053\uFF1A\u300C\u4F60\u90FD\u77E5\u9053\u4E86\uFF1F\u300D</p><p>\u6E29\u6DA6\u7684\u8336\u6C34\u4ECE\u820C\u5C16\u6D41\u8FC7\uFF0C\u6E05\u82E6\u4E2D\u8FD8\u5E26\u7740\u4E00\u4E1D\u7518\u751C\u3002\u300C\u5E08\u5A18\u544A\u8BC9\u5B69\u513F\uFF0C\u5B69\u513F\u7684\u4E39\u7530\u96BE\u4EE5\u517B\u70BC\u771F\u6C14\u3002\u300D</p><p>\u68B5\u96EA\u828D\u9759\u9759\u671B\u7740\u4ED6\u7684\u773C\u775B\uFF0C\u826F\u4E45\u624D\u8BF4\u9053\uFF1A\u300C\u662F\u7684\u3002\u4F60\u7684\u4E39\u7530\u88AB\u592A\u4E00\u7ECF\u7684\u771F\u6C14\u91CD\u521B\uFF0C\u516B\u8109\u4FF1\u635F\u3002\u5A18\u867D\u7136\u7ED9\u4F60\u7EED\u597D\u7ECF\u8109\uFF0C\u5DE9\u56FA\u4E39\u7530\uFF0C\u4F46\u4ECE\u4E2D\u63D0\u70BC\u771F\u5143\u8981\u6BD4\u5E38\u4EBA\u8270\u96BE\u767E\u500D\u3002\u300D</p><p>\u300C\u4E16\u95F4\u6CA1\u6709\u529F\u6CD5\u53EF\u4EE5\u4E0D\u4ECE\u4E39\u7530\u70BC\u6C14\uFF1F\u6BD5\u7ADF\u4EBA\u8EAB\u4E0A\u6709\u90A3\u4E48\u591A\u7A74\u9053\u3002\u300D</p><p>\u68B5\u96EA\u828D\u6447\u4E86\u6447\u5934\uFF0C\u300C\u4E39\u7530\u53C8\u540D\u6C14\u5E9C\uFF0C\u4E43\u662F\u771F\u6C14\u7684\u6839\u6E90\uFF0C\u4E16\u95F4\u5947\u529F\u5F02\u6CD5\u867D\u591A\uFF0C\u4E0D\u4ECE\u6B64\u5904\u70BC\u6C14\u7684\u5374\u662F\u7EDD\u65E0\u4EC5\u6709\u3002\u5373\u4F7F\u6700\u4E3A\u795E\u5999\u7684\u51E4\u51F0\u5B9D\u5178\uFF0C\u4E5F\u662F\u884C\u529F\u805A\u6C14\u7684\u7ECF\u8109\u4E0D\u540C\u3002\u4E0D\u53EF\u80FD\u4ECE\u522B\u5904\u63D0\u517B\u771F\u6C14\u3002\u300D</p><p>\u9F99\u6714\u6C89\u9ED8\u534A\u664C\uFF0C\u4F4E\u58F0\u95EE\u9053\uFF1A\u300C\u6211\u7684\u4E39\u7530\u80FD\u84C4\u6C14\u5417\uFF1F\u300D</p><p>\u300C\u84C4\u6C14\u5F53\u7136\u65E0\u59A8\u3002\u300D\u68B5\u96EA\u828D\u629A\u6478\u7740\u9F99\u6714\u7684\u5934\u53D1\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u4E39\u7530\u597D\u6BD4\u4E00\u53E3\u6DF1\u4E95\uFF0C\u5982\u679C\u4E0B\u9762\u6CA1\u6709\u6CC9\u6E90\uFF0C\u4E0D\u8FC7\u662F\u4E2A\u7A7A\u8361\u8361\u7684\u67AF\u4E95\u7F62\u4E86\u3002\u6714\u513F\uFF0C\u4EE5\u4F60\u5982\u4ECA\u7684\u6CC9\u6E90\uFF0C\u60F3\u704C\u6EE1\u4E00\u534A\uFF0C\u53EA\u6015\u4E5F\u8981\u82B1\u4E0A\u4E00\u7532\u5B50\u7684\u65F6\u95F4\u3002\u300D</p><p>\u9F99\u6714\u9759\u9759\u60F3\u4E86\u534A\u664C\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u6211\u660E\u767D\u4E86\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u7B2C\u4E8C\u5929\u4E00\u65E9\uFF0C\u9F99\u6714\u79BB\u5F00\u6D41\u97F3\u6EAA\uFF0C\u987A\u7740\u5C0F\u8DEF\u9A70\u51FA\u5BC6\u6797\u3002</p><p>\u5C0F\u8DEF\u5C3D\u5934\u8FDE\u7740\u5927\u8DEF\uFF0C\u5927\u8DEF\u5374\u6709\u4E09\u6761\uFF0C\u4E00\u6761\u5411\u897F\u901A\u5F80\u5B9B\u9675\uFF0C\u4E00\u6761\u5411\u5357\u901A\u5F80\u4E5D\u534E\u3002\u9F99\u6714\u5728\u8DEF\u53E3\u5CD9\u7ACB\u826F\u4E45\uFF0C\u90A3\u53CC\u660E\u51C0\u7684\u773C\u6674\u4E2D\u6D41\u9732\u51FA\u4E00\u4E1D\u60B2\u54C0\u7684\u7B11\u610F\u3002</p><p>\u70C8\u65E5\u6E10\u6E10\u70BD\u70ED\u8D77\u6765\uFF0C\u90A3\u5339\u9A8F\u9A6C\u7B49\u4E86\u8BB8\u4E45\u4E5F\u4E0D\u89C1\u4E3B\u4EBA\u52A8\u4F5C\uFF0C\u4E8E\u662F\u6602\u8D77\u5934\uFF0C\u6253\u4E86\u4E2A\u54CD\u9F3B\u3002</p><p>\u4E00\u53EA\u7EC6\u767D\u5982\u7389\u7684\u7EA4\u624B\u6296\u4E86\u6296\u7F30\u7EF3\uFF0C\u9A8F\u9A6C\u56DB\u8E44\u4E00\u52A8\uFF0C\u5F00\u59CB\u5C0F\u6B65\u5954\u8DD1\u8D77\u6765\uFF0C\u901F\u5EA6\u8D8A\u6765\u8D8A\u5FEB\u3002</p><p>\u8FD9\u662F\u4E00\u6761\u5411\u5317\u7684\u5927\u8DEF\uFF0C\u9053\u8DEF\u5C3D\u5934\u4E43\u662F\u5EFA\u5EB7\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u79CB\u98CE\u6E10\u8D77\uFF0C\u4E5D\u534E\u5C71\u8471\u7FE0\u7684\u5C71\u6797\u892A\u53BB\u7EFF\u8272\uFF0C\u6E10\u6B21\u8427\u6761\u3002</p><p>\u51CC\u98CE\u5802\u5185\uFF0C\u51CC\u96C5\u7434\u4E00\u8FB9\u64E6\u53BB\u9F99\u6714\u7684\u6EE1\u8138\u7070\u5C18\uFF0C\u4E00\u8FB9\u8D23\u602A\u5730\u8BF4\u9053\uFF1A\u300C\u8BF4\u662F\u63A2\u671B\u68B5\u4ED9\u5B50\uFF0C\u600E\u4E48\u53BB\u8FD9\u4E48\u4E45\uFF1F\u300D</p><p>\u9F99\u6714\u7B11\u9053\uFF1A\u300C\u597D\u4E45\u6CA1\u89C1\u8FC7\u4E49\u6BCD\uFF0C\u4E0D\u77E5\u4E0D\u89C9\u5C31\u4F4F\u4E86\u4E00\u4E2A\u591A\u6708\u3002\u5E08\u7236\u8FD8\u597D\u5417\uFF1F\u300D</p><p>\u300C\u8FD8\u6CA1\u6709\u51FA\u5173\u5462\u3002\u4F60\u82E5\u518D\u4E0D\u56DE\u6765\uFF0C\u5E08\u5A18\u5C31\u8981\u4E0B\u5C71\u53BB\u627E\u4F60\u4E86\u3002\u300D</p><p>\u300C\u5F92\u513F\u5728\u4E49\u6BCD\u90A3\u91CC\uFF0C\u5E08\u5A18\u8FD8\u6709\u4EC0\u4E48\u4E0D\u653E\u5FC3\u7684\uFF1F\u300D</p><p>\u51CC\u96C5\u7434\u51DD\u89C6\u7740\u7231\u5F92\uFF0C\u534A\u664C\u624D\u8BF4\u9053\uFF1A\u300C\u56DE\u6765\u5C31\u597D\u3002\u300D</p><p>\u9F99\u6714\u770B\u51FA\u5E08\u5A18\u773C\u4E2D\u7684\u5FE7\u8272\uFF0C\u77E5\u9053\u5979\u662F\u5728\u62C5\u5FC3\u81EA\u5DF1\uFF0C\u4E8E\u662F\u5FAE\u7B11\u9053\uFF1A\u300C\u5E08\u5A18\uFF0C\u5F92\u513F\u6CA1\u4E8B\u7684\u3002\u4E0D\u4F1A\u518D\u72AF\u50BB\u4E86\u3002\u300D</p><p>\u51CC\u96C5\u7434\u628A\u4ED6\u63FD\u5728\u6000\u91CC\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u597D\u5B69\u5B50\u3002\u300D</p><p>\u9F3B\u7AEF\u4F20\u6765\u6E29\u6696\u7684\u4F53\u9999\uFF0C\u9F99\u6714\u5FC3\u91CC\u4E94\u5473\u6742\u9648\u3002\u4ED6\u8F7B\u8F7B\u79BB\u5F00\u5E08\u5A18\u7684\u6000\u62B1\uFF0C\u5C0F\u58F0\u9053\uFF1A\u300C\u5F92\u513F\u8EAB\u4E0A\u597D\u810F\u7684\u3002\u300D</p><p>\u665A\u95F4\uFF0C\u6D17\u6362\u4E00\u65B0\u7684\u9F99\u6714\u5750\u5728\u706F\u524D\uFF0C\u5F00\u53E3\u9053\uFF1A\u300C\u5E08\u5A18\uFF0C\u4E49\u6BCD\u8BA9\u5B69\u513F\u6BCF\u4E24\u4E2A\u6708\u4E0B\u5C71\u4E00\u6B21\uFF0C\u5728\u5979\u90A3\u91CC\u4F4F\u4E0A\u51E0\u5929\u3002\u300D</p><p>\u300C\u51E0\u5929\uFF1F\u300D</p><p>\u300C\u5927\u6982\u4E00\u4E2A\u6708\u5427\u3002\u300D</p><p>\u51CC\u96C5\u7434\u6CA1\u6709\u5F00\u53E3\uFF0C\u773C\u775B\u5374\u6E10\u6E10\u4EAE\u4E86\u8D77\u6765\u3002</p><p>\u300C\u662F\u7684\u3002\u300D\u9F99\u6714\u7B11\u76C8\u76C8\u9053\uFF1A\u300C\u4E49\u6BCD\u627E\u5230\u4E86\u7ED9\u5F92\u513F\u6CBB\u4F24\u7684\u65B9\u6CD5\u3002\u53EA\u662F\u6CBB\u7597\u65F6\u95F4\u957F\u4E86\u4E9B\u3002\u300D</p><p>\u60F3\u8D77\u9F99\u6714\u5F53\u65E5\u7684\u75AF\u72C2\u4E3E\u52A8\uFF0C\u51CC\u96C5\u7434\u73B0\u5728\u8FD8\u5FC3\u6709\u4F59\u60B8\u3002\u9F99\u6714\u4E0B\u5C71\u7684\u8FD9\u6BB5\u65E5\u5B50\u91CC\uFF0C\u5979\u65F6\u65F6\u523B\u523B\u90FD\u5728\u6302\u5FF5\u7740\u8FD9\u4E2A\u53EF\u601C\u7684\u5B69\u5B50\uFF0C\u751F\u6015\u4ED6\u51FA\u4E86\u4EC0\u4E48\u610F\u5916\u3002</p><p>\u6B64\u65F6\u542C\u5230\u68B5\u4ED9\u5B50\u80FD\u6CBB\u597D\u6714\u513F\u7684\u4F24\u52BF\uFF0C\u51CC\u96C5\u7434\u7531\u8877\u5730\u559C\u60A6\u8D77\u6765\uFF0C\u6E29\u8A00\u9053\uFF1A\u300C\u80FD\u6CBB\u5C31\u597D\uFF0C\u4E0D\u5FC5\u7740\u6025\u3002\u8D76\u4E86\u4E00\u5929\u7684\u8DEF\uFF0C\u4F60\u4E5F\u7D2F\u4E86\uFF0C\u65E9\u4E9B\u4F11\u606F\u5427\u3002\u300D</p><p>\u9F99\u6714\u56DE\u5230\u81EA\u5DF1\u623F\u4E2D\uFF0C\u63D2\u597D\u95E8\u95E9\uFF0C\u7136\u540E\u5728\u5E8A\u4E0A\u76D8\u819D\u5750\u597D\uFF0C\u4ECE\u6000\u91CC\u638F\u51FA\u4E00\u4E2A\u786C\u786C\u7684\u7269\u4F53\u3002</p><p>\u90A3\u662F\u4E00\u4E2A\u624B\u638C\u5927\u5C0F\u7684\u518C\u5B50\uFF0C\u6D45\u7EA2\u8272\u7684\u76AE\u9762\u4E0A\u523A\u7740\u51E0\u4E2A\u7B14\u5212\u7E41\u590D\u7684\u7BC6\u5B57\u3002\u6380\u5F00\u6765\u91CC\u9762\u662F\u4E00\u5806\u67D4\u8F6F\u7684\u767D\u8272\u76AE\u9769\uFF0C\u7528\u53D1\u4E1D\u822C\u7684\u7EC6\u7EBF\u9C7C\u9CDE\u72B6\u7A7F\u5728\u4E00\u8D77\u3002\u6700\u5927\u7684\u4E00\u5F20\u7ED8\u7740\u4E24\u5E45\u661F\u56FE\uFF0C\u5176\u4ED6\u7ED8\u5236\u7740\u5404\u79CD\u4EBA\u4F53\uFF0C\u65C1\u8FB9\u5BC6\u5BC6\u9EBB\u9EBB\u523A\u7740\u65E0\u6570\u5C0F\u5B57\u3002</p><p>\u4E66\u9875\u95F4\uFF0C\u5939\u7740\u4E00\u5F20\u4FE1\u7B3A\uFF1A\u300C\u53F6\u62A4\u6CD5\u884C\u5357\u5C0A\u9A7E\u94A7\u9274\uFF1A\u9877\u63A5\u5E08\u5144\u4E66\u4FE1\uFF0C\u5F97\u77E5\u62A4\u6CD5\u6B32\u7779\u300A\u623F\u5FC3\u661F\u76D1\u300B\u4E4B\u79D8\uFF0C\u5728\u4E0B\u5373\u5F80\u767D\u8863\u5EB5\u8D77\u51FA\uFF0C\u8BF7\u4F9B\u5949\u8F6C\u4EA4\u62A4\u6CD5\u9A7E\u524D\u3002\u5F1F\u5B50\u7075\u5C18\u987F\u9996\u3002\u300D</p><p>\u51CC\u96C5\u7434\u53D1\u73B0\u9F99\u6714\u7684\u5185\u529F\u7A81\u7136\u5927\u8FDB\uFF0C\u867D\u7136\u8FD8\u4E0D\u53CA\u4E5D\u534E\u5251\u6D3E\u7684\u5BFB\u5E38\u5F1F\u5B50\uFF0C\u4F46\u8F83\u4E4B\u4EE5\u5F80\u7684\u8270\u96BE\u5DF2\u6709\u5929\u58E4\u4E4B\u522B\u3002\u7167\u8FD9\u6837\u4E0B\u53BB\uFF0C\u4E0D\u51FA\u4E00\u5E74\u4FBF\u53EF\u7EC3\u6210\u516D\u5408\u529F\u7684\u7B2C\u4E8C\u5C42\u3002</p><p>\u51CC\u96C5\u7434\u81EA\u662F\u6B23\u559C\u4E07\u5206\uFF0C\u9F99\u6714\u5374\u795E\u60C5\u6DE1\u7136\uFF0C\u4F3C\u4E4E\u6CA1\u6709\u592A\u591A\u7684\u559C\u60A6\u3002\u51CC\u96C5\u7434\u770B\u5728\u773C\u91CC\uFF0C\u6697\u9053\u8FD9\u5B69\u5B50\u53D7\u6B64\u632B\u6298\uFF0C\u6027\u5B50\u6108\u53D1\u6C89\u7A33\uFF0C\u8D8A\u6765\u8D8A\u50CF\u5927\u4EBA\u4E86\u3002</p>',133);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
