import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_19" tabindex="-1"><a class="header-anchor" href="#_19" aria-hidden="true">#</a> 19</h1><p>\u67F3\u9759\u83BA\u6025\u4FC3\u5730\u5598\u606F\u7740\uFF0C\u96BE\u4EE5\u8A00\u55BB\u7684\u6050\u60E7\u538B\u5012\u4E86\u8089\u4F53\u7684\u75DB\u695A\uFF0C\u4F7F\u5979\u5FD8\u8BB0\u4E86\u75DB\u82E6\uFF0C\u5C31\u50CF\u770B\u53E6\u4E00\u4E2A\u5973\u5B50\u90A3\u6837\uFF0C\u5446\u5446\u6CE8\u89C6\u7740\u81EA\u5DF1\u88AB\u5256\u8179\u53D6\u9634\u7684\u6574\u4E2A\u8FC7\u7A0B\u3002</p><p>\u9C9C\u7EA2\u7684\u8840\u8089\u5728\u5149\u5929\u5316\u65E5\u4E0B\u8815\u52A8\u7740\uFF0C\u90A3\u4E2A\u7CBE\u81F4\u7684\u7389\u6237\u8131\u79BB\u4E86\u5468\u56F4\u7684\u8089\u4F53\uFF0C\u53EA\u5269\u4E0B\u5E26\u7740\u9634\u961C\u7684\u5916\u9634\u5782\u5728\u817F\u95F4\uFF0C\u540E\u9762\u662F\u72ED\u957F\u8840\u7EA2\u7684\u8154\u4F53\u3002\u63A5\u7740\u4E00\u53EA\u6EF4\u8840\u7684\u7389\u624B\u4F38\u6765\uFF0C\u7EA4\u6307\u5408\u62E2\uFF0C\u8F7B\u8F7B\u63EA\u4F4F\u90A3\u9C9C\u82B1\u822C\u7684\u5973\u9634\uFF0C\u5C06\u5B83\u62FD\u79BB\u8179\u8154\u3002\u9F99\u6714\u4ED4\u7EC6\u5265\u53BB\u8179\u819C\uFF0C\u5C0F\u5FC3\u5730\u5C06\u5916\u9634\u3001\u9634\u9053\u3001\u8FDE\u540C\u7EC6\u957F\u7684\u5BAB\u9888\u5B8C\u6574\u5730\u5265\u79BB\u51FA\u6765\u3002</p><p>\u826F\u4E45\uFF0C\u9F99\u6714\u62AC\u8D77\u5934\uFF0C\u6367\u7740\u90A3\u56E2\u8840\u8089\uFF0C\u4EF0\u8138\u75AF\u72C2\u5730\u5927\u7B11\u8D77\u6765\uFF0C\u90A3\u53CC\u901A\u7EA2\u7684\u4FCA\u76EE\u4E2D\uFF0C\u6EE1\u6EA2\u7740\u72F0\u72DE\u5730\u90AA\u610F\u3002</p><p>\u67F3\u9759\u83BA\u7389\u8138\u96EA\u767D\uFF0C\u8FDE\u7EA2\u5507\u90FD\u5931\u53BB\u4E86\u8840\u8272\u800C\u53D8\u5F97\u900F\u660E\uFF0C\u5979\u56DB\u80A2\u644A\u5F00\uFF0C\u96EA\u767D\u7684\u4E24\u817F\u95F4\u6DCC\u6EE1\u9C9C\u8840\u3002\u767D\u817B\u7684\u5C0F\u8179\u6380\u5F00\u4E00\u4E2A\u72ED\u957F\u7684\u521B\u53E3\u3002\u7A7A\u8361\u8361\u7684\u8179\u8154\u88F8\u9732\u5728\u5916\uFF0C\u4E0B\u4F53\u90A3\u53EA\u5973\u6027\u6700\u9690\u79D8\uFF0C\u6700\u8D35\u7684\u5668\u5B98\u5DF2\u7ECF\u8361\u7136\u65E0\u5B58\uFF0C\u53EA\u5269\u4E0B\u7A7A\u8361\u8361\u7684\u8179\u8154\uFF0C\u5728\u8840\u6CCA\u4E2D\u4E0D\u4F4F\u75C9\u631B\u62BD\u6410\u3002\u5979\u671B\u7740\u90A3\u4E2A\u7A7F\u7740\u6843\u7EA2\u62B9\u80F8\u7684\u5996\u8273\u8EAB\u4F53\uFF0C\u671B\u7740\u90A3\u5F20\u719F\u6089\u800C\u53C8\u964C\u751F\u7684\u79C0\u7F8E\u9762\u5B54\uFF0C\u7136\u540E\u9ED1\u767D\u5206\u660E\u7684\u5927\u773C\u671B\u5411\u65E0\u5C3D\u7684\u82CD\u7A79\uFF0C\u5FAE\u5F31\u5730\u8BF4\u9053\uFF1A\u300C\u9B54\u9B3C\u2026\u2026\u9F99\u54E5\u54E5\uFF0C\u6551\u6211\u2026\u2026\u300D</p><p>\u9F99\u6714\u76EE\u5149\u6E10\u6E10\u5E73\u590D\u4E0B\u6765\uFF0C\u4ED6\u4FEF\u8EAB\u543B\u4F4F\u67F3\u9759\u83BA\u51B0\u51C9\u7684\u5507\u74E3\uFF0C\u4F4E\u58F0\u8BF4\u9053\uFF1A\u300C\u8FDE\u4F60\u4E5F\u628A\u6211\u770B\u4F5C\u9B54\u9B3C\uFF0C\u90A3\u80AF\u5B9A\u662F\u771F\u7684\u4E86\u3002\u597D\u59B9\u59B9\uFF0C\u54E5\u54E5\u4F1A\u73CD\u60DC\u4F60\u7ED9\u6211\u7684\u9634\u6237\uFF0C\u7B49\u4ED6\u4EEC\u7528\u5B8C\uFF0C\u6211\u5C31\u5E26\u7740\u5B83\u6765\u966A\u4F60\u2026\u2026\u300D</p><p>\u4ED6\u6162\u6162\u5408\u4E0A\u5C11\u5973\u672A\u7791\u7684\u7F8E\u76EE\uFF0C\u6258\u8D77\u90A3\u56E2\u5140\u81EA\u5E26\u7740\u4F53\u6E29\u548C\u5904\u5B50\u5E7D\u9999\u7684\u7389\u6237\uFF0C\u8F7B\u8F7B\u543B\u4E86\u4E00\u53E3\uFF0C\u300C\u4EE5\u540E\u4F60\u5C31\u5728\u6211\u8EAB\u4F53\u91CC\u9762\uFF0C\u6211\u8981\u5E26\u7740\u4F60\u53BB\u98A0\u8986\u661F\u6708\u6E56\uFF01\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u68B5\u96EA\u828D\u5931\u58F0\u60CA\u547C\uFF0C\u300C\u4F60\u4ECE\u54EA\u91CC\u5F97\u6765\u7684\uFF1F\u300D</p><p>\u90A3\u53EA\u7389\u6237\u5DF2\u7ECF\u5728\u6D41\u97F3\u6EAA\u6D17\u5F97\u5E72\u5E72\u51C0\u51C0\uFF0C\u6B64\u523B\u9759\u9759\u8EBA\u5728\u94F6\u76D8\u5185\uFF0C\u5C31\u5982\u51B0\u7389\u96D5\u6210\u4E00\u822C\u73B2\u73D1\u5254\u900F\uFF0C\u770B\u4E0D\u5230\u534A\u5206\u6B8B\u5FCD\u7684\u75D5\u8FF9\u3002</p><p>\u300C\u5B69\u513F\u5728\u5C71\u6797\u91CC\u9047\u89C1\u4E00\u9A7E\u9A6C\u8F66\u8DCC\u4E0B\u5C71\u5D16\uFF0C\u8FDE\u5FD9\u8D76\u53BB\u76F8\u6551\uFF0C\u4F46\u91CC\u9762\u7684\u5973\u5B50\u5DF2\u7ECF\u6454\u6B7B\u3002\u5B69\u513F\u770B\u5230\u5979\u7684\u9634\u6237\u4E0E\u5A18\u8BF4\u7684\u76F8\u5408\uFF0C\u5C31\u53D6\u4E86\u4E0B\u6765\u3002\u300D</p><p>\u68B5\u96EA\u828D\u7AEF\u8BE6\u7247\u523B\uFF0C\u5FFD\u7136\u8BF4\u9053\uFF1A\u300C\u4E0D\u5BF9\uFF01\u90A3\u5973\u5B50\u5F53\u65F6\u8FD8\u6D3B\u7740\uFF01\u6714\u513F\uFF01\u300D\u5979\u5389\u559D\u4E00\u58F0\uFF0C\u773C\u7736\u4E0D\u7981\u53D1\u7EA2\u3002</p><p>\u9F99\u6714\u6CA1\u60F3\u5230\u8FDE\u8FD9\u4E5F\u7792\u4E0D\u8FC7\u4E49\u6BCD\uFF0C\u5F53\u5373\u88C5\u505A\u60CA\u8BB6\u5730\u6837\u5B50\uFF0C\u300C\u554A\uFF01\u5979\u8FD8\u6D3B\u7740\u2026\u2026\u300D\u8BF4\u7740\u6D8C\u51FA\u540E\u6094\u7684\u6CEA\u6C34\uFF0C\u300C\u5A18\uFF0C\u5B69\u513F\u89C1\u5979\u6CA1\u6709\u58F0\u606F\uFF0C\u53EA\u4EE5\u4E3A\u5979\u662F\u6B7B\u4E86\uFF0C\u6CA1\u60F3\u5230\u2026\u2026\u5A18\uFF0C\u6211\u5BF9\u4E0D\u8D77\u4F60\u3002\u300D</p><p>\u68B5\u96EA\u828D\u5BF9\u4ED6\u7684\u8BDD\u534A\u4FE1\u534A\u7591\uFF0C\u5979\u77E5\u9053\u8FD9\u5B69\u5B50\u4E3A\u4E86\u62A5\u4EC7\u4E0D\u62E9\u624B\u6BB5\uFF0C\u4F46\u60F3\u4ED6\u8FD8\u4E0D\u81F3\u4E8E\u52AB\u8DEF\u6740\u4EBA\u3002\u6B64\u523B\u5927\u9519\u5DF2\u7ECF\u94F8\u6210\uFF0C\u518D\u96BE\u4EE5\u5F25\u8865\u4E86\u3002\u5979\u5750\u4E86\u826F\u4E45\uFF0C\u6700\u540E\u624D\u8C13\u53F9\u4E00\u58F0\uFF0C\u8D77\u8EAB\u53D6\u6765\u836F\u5323\u3002</p><p>\u5F53\u9F99\u6714\u7741\u5F00\u773C\u775B\uFF0C\u5929\u9645\u5DF2\u7ECF\u6CDB\u8D77\u767D\u8272\u3002\u4ED6\u8BD5\u7740\u52A8\u4E86\u52A8\u8EAB\u5B50\uFF0C\u53EA\u89C9\u4E0B\u8179\u88F9\u7740\u539A\u539A\u7684\u7EB1\u5E03\uFF0C\u4E00\u79CD\u5F02\u6837\u7684\u75DB\u695A\u4ECE\u817F\u95F4\u5347\u8D77\uFF0C\u50CF\u9525\u5B50\u4E00\u6837\u4E00\u76F4\u5EF6\u4F38\u5230\u8179\u8154\u6DF1\u5904\u3002</p><p>\u300C\u522B\u52A8\u3002\u300D\u4E00\u53CC\u7389\u624B\u6309\u5728\u80A9\u4E0A\u3002</p><p>\u300C\u5A18\uFF01\u300D\u9F99\u6714\u6B23\u559C\u5730\u53EB\u9053\uFF1A\u300C\u6211\u771F\u7684\u53D8\u6210\u5973\u4EBA\u4E86\u5417\uFF1F\u300D</p><p>\u68B5\u96EA\u828D\u4FEF\u8EAB\u64E6\u53BB\u4ED6\u989D\u4E0A\u7684\u6C57\u6EF4\uFF0C\u8F7B\u8F7B\u70B9\u4E86\u70B9\u5934\uFF0C\u5999\u76EE\u4E2D\u6D41\u9732\u51FA\u4F3C\u60B2\u4F3C\u601C\u7684\u795E\u60C5\u3002</p><p>\u95FB\u5230\u4E49\u6BCD\u8EAB\u4E0A\u6E29\u6696\u7684\u4F53\u9999\uFF0C\u9F99\u6714\u53C8\u662F\u611F\u6FC0\u53C8\u662F\u559C\u60A6\uFF0C\u4ED6\u5FFD\u7136\u5F20\u5F00\u53CC\u81C2\uFF0C\u6402\u4F4F\u68B5\u96EA\u828D\u7684\u8170\u8EAB\uFF0C\u5728\u5979\u8138\u4E0A\u98DE\u5FEB\u5730\u543B\u4E86\u4E00\u53E3\uFF0C\u8BDA\u631A\u5730\u8BF4\u9053\uFF1A\u300C\u8C22\u8C22\u5A18\u3002\u300D</p><p>\u68B5\u96EA\u828D\u7389\u8138\u98DE\u7EA2\uFF0C\u4E00\u8FB9\u614C\u5FD9\u7406\u597D\u53D1\u4E1D\uFF0C\u4E00\u8FB9\u8D23\u602A\u5730\u8BF4\u9053\uFF1A\u300C\u8840\u8089\u8FD8\u672A\u957F\u597D\uFF0C\u5C0F\u5FC3\u4E0D\u8981\u4E71\u52A8\u3002\u300D</p><p>\u9F99\u6714\u6324\u4E86\u6324\u773C\uFF0C\u7B11\u541F\u541F\u9053\uFF1A\u300C\u4E0A\u6B21\u4E0D\u4E5F\u662F\u8FD9\u6837\uFF0C\u521A\u63A5\u4E0A\u5C31\u52A8\u4E86\uFF0C\u8FD8\u52A8\u5F97\u597D\u5389\u5BB3\u5462\u3002\u5A18\u600E\u4E48\u8FD8\u675F\u7740\u80F8\u5462\uFF1F\u300D</p><p>\u68B5\u96EA\u828D\u8138\u66F4\u7EA2\u4E86\uFF0C\u300C\u90A3\u4E0D\u4E00\u6837\u7684\uFF0C\u4E0A\u6B21\u63A5\u8FDE\u7684\u8840\u8109\u5E76\u4E0D\u591A\uFF0C\u53C8\u662F\u2026\u2026\u4E0D\u8981\u8BF4\u4E86\u2026\u2026\u300D\u60F3\u8D77\u5F53\u65E5\u81EA\u5DF1\u7528\u4E73\u623F\u7ED9\u513F\u5B50\u53D1\u6CC4\u6B32\u706B\u7684\u4E11\u6001\uFF0C\u68B5\u96EA\u828D\u5C31\u7F9E\u6127\u5F97\u65E0\u5730\u81EA\u5BB9\u3002\u5979\u5BF9\u81EA\u5DF1\u7684\u8C6A\u4E73\u6DF1\u4EE5\u4E3A\u803B\uFF0C\u8FDE\u770B\u4E5F\u4E0D\u613F\u88AB\u4EBA\u770B\u5230\uFF0C\u7ED3\u679C\u90A3\u6B21\u5374\u88AB\u513F\u5B50\u62B1\u7740\uFF0C\u7528\u4ED6\u7684\u9633\u5177\u50CF\u4E24\u4E73\u78E8\u64E6\u5F97\u7EA2\u80BF\u4E0D\u582A\u3002</p><p>\u300C\u6015\u4EC0\u4E48\u5462\uFF1F\u9759\u989C\u662F\u5A18\u7684\u4E56\u5973\u513F\u554A\u2026\u2026\u300D</p><p>\u542C\u5230\u9F99\u6714\u5A07\u6EF4\u6EF4\u7684\u58F0\u97F3\uFF0C\u68B5\u96EA\u828D\u4E0D\u7981\u82B3\u5FC3\u6697\u98A4\uFF0C\u5929\uFF0C\u81EA\u5DF1\u7A76\u7ADF\u505A\u4E86\u4EC0\u4E48\uFF1F\u628A\u4E00\u4E2A\u4E0D\u7537\u4E0D\u5973\u7684\u602A\u7269\uFF0C\u53D8\u6210\u4E00\u4E2A\u4EA6\u7537\u4EA6\u5973\u7684\u602A\u7269\u2026\u2026</p><p>\u9F99\u6714\u8138\u4E0A\u7684\u7B11\u5BB9\u6E10\u6E10\u6D88\u892A\u3002\u4ED6\u7528\u7684\u9759\u989C\uFF0C\u662F\u4ECE\u9759\u83BA\u548C\u6BCD\u4EB2\u7684\u540D\u5B57\u91CC\u5404\u53D6\u4E86\u4E00\u5B57\u3002\u53EF\u4ECE\u4ECA\u5F80\u540E\uFF0C\u81EA\u5DF1\u518D\u6CA1\u6709\u9759\u83BA\u59B9\u59B9\u4E86\u3002\u9759\u83BA\u59B9\u59B9\u5DF2\u7ECF\u5728\u81EA\u5DF1\u8EAB\u4F53\u91CC\u9762\uFF0C\u548C\u81EA\u5DF1\u878D\u4E3A\u4E00\u4F53\u2026\u2026</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u6843\u82B1\u8C22\u5C3D\u674F\u82B1\u5F00\uFF0C\u6B63\u503C\u6625\u6F6E\u6DA8\u6C34\u65F6\u5019\uFF0C\u6C34\u6025\u98CE\u5FEB\uFF0C\u6C5F\u4E2D\u4E00\u8258\u5E26\u6845\u7684\u4E2D\u578B\u8239\u8236\u987A\u6D41\u800C\u4E0B\uFF0C\u75BE\u82E5\u5954\u9A6C\u3002\u4E00\u4E2A\u7FE0\u886B\u5C11\u5973\u4FCF\u751F\u751F\u7ACB\u5728\u8239\u5934\uFF0C\u8FDC\u8FDC\u671B\u7740\u70DF\u972D\u4E2D\u7684\u77F3\u5934\u57CE\uFF0C\u6C34\u7075\u7075\u7684\u5999\u76EE\u4F3C\u60B2\u4F3C\u559C\uFF0C\u5E26\u7740\u4E00\u4E1D\u8BA9\u4EBA\u6349\u6478\u4E0D\u900F\u7684\u795E\u91C7\u3002</p><p>\u79BB\u5F00\u9759\u820D\u65F6\uFF0C\u662F\u4E49\u6BCD\u4EB2\u624B\u7ED9\u5979\u68B3\u7406\u88C5\u626E\u3002\u90A3\u662F\u5979\u7B2C\u4E00\u6B21\u4EE5\u5973\u6027\u7684\u8EAB\u4EFD\u4ECE\u6D41\u97F3\u6EAA\u79BB\u5F00\uFF0C\u68B5\u96EA\u828D\u8C61\u5BF9\u81EA\u5DF1\u51FA\u5AC1\u7684\u5973\u513F\u4E00\u6837\uFF0C\u7CBE\u5FC3\u5E2E\u5979\u68B3\u4E86\u4E2A\u6D41\u82CF\u9AFB\uFF0C\u7136\u540E\u5E2E\u5979\u63CF\u7709\u70B9\u5507\uFF0C\u6D82\u62B9\u8102\u7C89\u3002</p><p>\u770B\u7740\u513F\u5B50\u5728\u81EA\u5DF1\u624B\u4E0B\u4E00\u70B9\u4E00\u6EF4\u53D8\u6210\u4E00\u4E2A\u5A07\u7F8E\u8FF7\u4EBA\u7684\u5C11\u5973\uFF0C\u68B5\u96EA\u828D\u5FC3\u5934\u53C8\u9178\u53C8\u6DA9\u3002\u5979\u66FE\u60F3\u51ED\u501F\u81EA\u5DF1\u8D85\u51E1\u7684\u533B\u672F\uFF0C\u8BA9\u513F\u5B50\u6062\u590D\u7537\u513F\u4E4B\u8EAB\uFF0C\u53EA\u9700\u4ED6\u5E9F\u53BB\u6B66\u529F\uFF0C\u4E0D\u518D\u7EC3\u90A3\u5996\u6DEB\u90AA\u6076\u7684\u300A\u623F\u5FC3\u661F\u76D1\u300B\uFF0C\u6BCD\u5B50\u4FE9\u5230\u4E00\u4E2A\u65E0\u4EBA\u77E5\u6653\u7684\u5C71\u6751\u4F4F\u4E0B\uFF0C\u4ECE\u6B64\u8FDC\u79BB\u6C5F\u6E56\u662F\u975E\u3002\u5982\u679C\u770B\u7740\u513F\u5B50\u5A36\u59BB\u751F\u5B50\uFF0C\u50CF\u6B63\u5E38\u4EBA\u90A3\u6837\u5E73\u5E73\u5B89\u5B89\u5EA6\u8FC7\u4E00\u751F\uFF0C\u5373\u4F7F\u6B7B\u4E5F\u65E0\u61BE\u4E86\u3002</p><p>\u4F46\u53EA\u8981\u63D0\u5230\u590D\u4EC7\u4E4B\u4E8B\uFF0C\u9F99\u6714\u5C31\u6BEB\u4E0D\u901A\u878D\u3002\u7236\u6BCD\u7684\u8840\u4EC7\u5DF2\u7ECF\u878D\u5316\u5728\u4ED6\u7684\u8840\u8109\u4E4B\u4E2D\u3002\u300C\u8981\u662F\u4E0D\u80FD\u62A5\u4EC7\uFF0C\u6211\u65E9\u5C31\u81EA\u5C3D\u4E86\u5462\u3002\u300D\u5C11\u5973\u7B11\u76C8\u76C8\u8BF4\u7740\uFF0C\u628A\u4E00\u652F\u73E0\u82B1\u522B\u5728\u9AFB\u4E0A\u3002</p><p>\u300C\u6F02\u4EAE\u5417\uFF1F\u300D\u9759\u989C\u8170\u80A2\u4E00\u626D\uFF0C\u7075\u5DE7\u7684\u79C0\u76EE\u5F80\u7709\u68A2\u779F\u53BB\uFF0C\u90A3\u79CD\u59A9\u5A9A\u7684\u98CE\u60C5\uFF0C\u8FDE\u5973\u5B50\u4E5F\u4E3A\u4E4B\u5FC3\u52A8\u3002</p><p>\u798F\u516E\uFF1F\u7978\u516E\uFF1F\u671B\u7740\u5973\u513F\u5996\u5A06\u7684\u8EAB\u5F71\uFF0C\u68B5\u96EA\u828D\u5FC3\u5934\u6697\u53F9\u3002\u4E3A\u4E86\u90A3\u4E00\u70B9\u5316\u89E3\u4E0D\u5F00\u7684\u51A4\u5B7D\uFF0C\u8FD9\u4E00\u751F\u5F7C\u6B64\u90FD\u62F4\u5728\u4E00\u8D77\u4E86\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u9F99\u9759\u989C\u672C\u4EE5\u4E3A\u9690\u5982\u5EB5\u5728\u57CE\u90CA\u6697\u5904\uFF0C\u4E00\u95EE\u4E4B\u4E0B\u624D\u77E5\u9053\uFF0C\u90A3\u7ADF\u662F\u5EFA\u5EB7\u6700\u5927\u7684\u5C3C\u5EB5\uFF0C\u5C31\u5728\u57CE\u5185\u7684\u7E41\u534E\u5730\u5E26\u3002\u800C\u5999\u82B1\u5E08\u592A\u5219\u662F\u95FB\u540D\u9050\u5C14\u7684\u50E7\u5C3C\uFF0C\u4F20\u8BF4\u9690\u5982\u5EB5\u6C42\u5B50\u6700\u6709\u7075\u9A8C\uFF0C\u8BB8\u591A\u8C6A\u95E8\u8D35\u5987\u90FD\u5728\u5EB5\u5185\u793C\u4F5B\uFF0C\u9999\u706B\u6781\u662F\u65FA\u76DB\u3002</p><p>\u9759\u989C\u4EE5\u5F80\u505A\u5A3C\u5993\u65F6\u4E00\u76F4\u85CF\u8EAB\u80CC\u5DF7\uFF0C\u7ADF\u4E0D\u77E5\u6653\u5EFA\u5EB7\u8FD8\u6709\u8FD9\u7B49\u540D\u5EB5\u3002\u5979\u4F9D\u7740\u6307\u70B9\u6765\u5230\u57CE\u897F\uFF0C\u679C\u7136\u597D\u5927\u4E00\u7247\u9662\u843D\uFF0C\u91CD\u6A90\u6597\u89D2\uFF0C\u91D1\u78A7\u8F89\u714C\uFF0C\u4E00\u76F4\u5EF6\u4F38\u5230\u5185\u79E6\u6DEE\u7554\u3002\u5EB5\u5185\u4F73\u4E3D\u5982\u4E91\uFF0C\u540D\u5A9B\u4ED5\u5973\uFF0C\u7EA2\u7C89\u5A07\u5A25\u5F80\u6765\u5982\u7EC7\u3002</p><p>\u9759\u989C\u8FB9\u8D70\u8FB9\u770B\uFF0C\u5FC3\u5185\u6697\u81EA\u8BB6\u5F02\u3002\u542C\u4E49\u6BCD\u8BF4\uFF0C\u661F\u6708\u6E56\u672C\u662F\u9053\u5BB6\u4E00\u8109\uFF0C\u5BF9\u91CA\u4F5B\u5411\u6765\u4E0D\u5C51\uFF0C\u4E3A\u4F55\u4F1A\u6697\u4E2D\u64CD\u6301\u8FD9\u6837\u4E00\u5EA7\u5EB5\u5802\uFF1F</p><p>\u601D\u7D22\u95F4\uFF0C\u773C\u89D2\u7A81\u7136\u98D8\u8FC7\u4E00\u4E2A\u719F\u6089\u7684\u8EAB\u5F71\uFF0C\u9759\u989C\u4E3E\u76EE\u770B\u53BB\uFF0C\u53EA\u89C1\u90A3\u5973\u5B50\u5E74\u7EAA\u4E0D\u8FC7\u4E8C\u5341\u4F59\u5C81\uFF0C\u8EAB\u6750\u4FEE\u957F\uFF0C\u5BB9\u8C8C\u52A8\u4EBA\uFF0C\u5374\u662F\u592A\u6E56\u53CC\u51E4\u4E4B\u4E00\uFF0C\u65B9\u6D01\u7684\u5E08\u59B9\u9773\u5982\u70DF\u3002</p><p>\u6570\u6708\u524D\uFF0C\u9759\u989C\u5728\u4E49\u5174\u5077\u88AD\u5F97\u624B\uFF0C\u5438\u53D6\u4E86\u65B9\u6D01\u7684\u529F\u529B\uFF0C\u53C8\u5C06\u5979\u73A9\u5F04\u81F3\u6B7B\u3002\u5F53\u65F6\u53EA\u542C\u8BF4\u9773\u5982\u70DF\u5230\u4E86\u5EFA\u5EB7\uFF0C\u6CA1\u60F3\u5230\u7ADF\u4F1A\u5728\u6B64\u5730\u9047\u4E0A\u3002\u9773\u5982\u70DF\u5BB9\u8C8C\u3001\u6B66\u529F\u72B9\u5728\u65B9\u6D01\u4E4B\u4E0A\uFF0C\u96BE\u9053\u5979\u4E5F\u662F\u661F\u6708\u6E56\u4E2D\u4EBA\uFF1F\u9F99\u9759\u989C\u597D\u5947\u5FC3\u8D77\uFF0C\u85C9\u7740\u6E38\u5BA2\u63A9\u62A4\uFF0C\u6084\u6084\u8DDF\u5728\u9773\u5982\u70DF\u8EAB\u540E\u3002</p><p>\u9773\u5982\u70DF\u7ED5\u8FC7\u51E0\u91CD\u5927\u6BBF\uFF0C\u987A\u7740\u6E38\u5ECA\u671D\u897F\u8D70\u53BB\u3002\u8FD9\u91CC\u6E38\u4EBA\u5DF2\u7A00\uFF0C\u7B49\u7A7F\u8FC7\u4E00\u4E2A\u5C0F\u9662\uFF0C\u524D\u9762\u662F\u4E00\u4E2A\u4E0D\u8D77\u773C\u7684\u62F1\u95E8\uFF0C\u4E24\u4E2A\u5999\u9F84\u5C3C\u59D1\u76EE\u4E0D\u659C\u89C6\u5730\u5B88\u5728\u95E8\u524D\u3002</p><p>\u9773\u5982\u70DF\u4F3C\u4E4E\u6EE1\u8179\u5FC3\u4E8B\uFF0C\u6839\u672C\u6CA1\u7559\u610F\u6709\u4EBA\u5728\u540E\u8DDF\u8E2A\u3002\u5979\u8D70\u5230\u95E8\u524D\uFF0C\u5411\u4E86\u4E00\u4E2A\u5C3C\u59D1\u8BF4\u4E86\u51E0\u53E5\uFF0C\u7136\u540E\u4ECE\u9888\u4E2D\u62C9\u51FA\u4E2A\u724C\u5B50\u4EAE\u4E86\u4EAE\uFF0C\u90A3\u5C3C\u59D1\u70B9\u4E86\u70B9\u5934\uFF0C\u644A\u5F00\u7F18\u7C3F\u8BA9\u5979\u753B\u4E86\u62BC\uFF0C\u4FBF\u5373\u653E\u884C\u3002</p><p>\u9773\u5982\u70DF\u8D70\u8FDB\u95E8\u5185\uFF0C\u9759\u989C\u53C8\u7B49\u4E86\u7247\u523B\uFF0C\u8FD9\u624D\u82E5\u65E0\u5176\u4E8B\u5730\u671D\u62F1\u95E8\u8D70\u53BB\u3002</p><p>\u300C\u65BD\u4E3B\u8BF7\u7559\u6B65\uFF0C\u8FD9\u91CC\u662F\u5EB5\u5185\u6E05\u4FEE\u4E4B\u5730\uFF0C\u4E0D\u63A5\u5916\u5BA2\u7684\u3002\u300D</p><p>\u300C\u54E6\uFF0C\u539F\u6765\u662F\u8FD9\u6837\uFF0C\u59BE\u8EAB\u5931\u793C\u4E86\u3002\u300D\u9759\u989C\u67D4\u58F0\u9053\uFF1A\u300C\u5C0F\u5973\u5B50\u60F3\u6C42\u89C1\u5999\u82B1\u5E08\u592A\uFF0C\u5E08\u7236\u53EF\u5426\u901A\u878D\u7980\u544A\u5462\uFF1F\u300D</p><p>\u5973\u5C3C\u5FAE\u7B11\u9053\uFF1A\u300C\u5999\u82B1\u5E08\u592A\u6F5C\u5FC3\u4F5B\u6CD5\uFF0C\u6781\u5C11\u51FA\u9762\u89C1\u5BA2\u3002\u5973\u65BD\u4E3B\u6B64\u8BF7\uFF0C\u6055\u8D2B\u5C3C\u96BE\u4EE5\u5E94\u547D\u3002\u300D</p><p>\u300C\u65E2\u7136\u5982\u6B64\uFF0C\u53EF\u5426\u8BF7\u5E08\u7236\u5C06\u6B64\u4F69\u4EA4\u4E88\u5E08\u592A\uFF0C\u300D\u9759\u989C\u53D6\u51FA\u90A3\u53EA\u7389\u4F69\uFF0C\u300C\u5C31\u8BF4\u662F\u6545\u4EBA\u6C42\u89C1\u3002\u300D</p><p>\u90A3\u5973\u5C3C\u770B\u5230\u4F69\u4E0A\u7684\u661F\u56FE\uFF0C\u4E0D\u7531\u624B\u8155\u4E00\u98A4\u3002\u5979\u8FDE\u5FD9\u65BD\u4E86\u4E00\u793C\uFF0C\u5C0F\u58F0\u9053\uFF1A\u300C\u4E0D\u77E5\u5C0A\u9A7E\u5149\u4E34\uFF0C\u8FD8\u8BF7\u6055\u7F6A\u3002\u8D2B\u5C3C\u2026\u5974\u5A62\u8FD9\u5C31\u53BB\u7980\u62A5\u5E08\u592A\u3002\u300D\u8BF4\u7740\u5306\u5306\u53BB\u4E86\u3002</p><p>\u53E6\u4E00\u4E2A\u5C3C\u59D1\u4E5F\u770B\u5230\u4E86\u7389\u4F69\u7684\u56FE\u6848\uFF0C\u6001\u5EA6\u4E5F\u6108\u53D1\u606D\u656C\uFF0C\u751A\u81F3\u6709\u4E9B\u6050\u60E7\u822C\uFF0C\u602F\u751F\u751F\u7ACB\u5728\u4E00\u65C1\uFF0C\u8FDE\u8BDD\u4E5F\u4E0D\u6562\u8BF4\u3002\u9759\u989C\u6697\u9053\uFF0C\u770B\u6765\u592D\u592D\u90A3\u53E5\u5E76\u6CA1\u6709\u8BF4\u8C0E\uFF0C\u5979\u5728\u661F\u6708\u6E56\u7684\u5730\u4F4D\u679C\u7136\u4E0D\u51E1\u3002</p><p>\u7247\u523B\u540E\uFF0C\u4E00\u4E2A\u7F8E\u8273\u7684\u5973\u5C3C\u6B3E\u6B3E\u8D70\u6765\uFF0C\u5979\u770B\u4E0A\u53BB\u4E0E\u6DF3\u4E8E\u7476\u5E74\u7EAA\u76F8\u4EFF\uFF0C\u5934\u4E0A\u5E26\u7740\u5C3C\u5E3D\uFF0C\u8EAB\u4E0A\u7A7F\u7740\u4EF6\u534A\u65B0\u4E0D\u65E7\u7684\u50E7\u888D\uFF0C\u4E24\u638C\u5408\u4EC0\uFF0C\u5999\u76EE\u4F4E\u5782\uFF0C\u795E\u60C5\u5E84\u91CD\u3002\u82E5\u975E\u9759\u989C\u77E5\u9053\u5979\u4E0E\u661F\u6708\u6E56\u6709\u6240\u7275\u8FDE\uFF0C\u591A\u534A\u4E5F\u4F1A\u628A\u5979\u5F53\u6210\u4FEE\u884C\u6709\u9053\u7684\u4F5B\u95E8\u4E2D\u4EBA\u3002</p><p>\u300C\u963F\u5F25\u9640\u4F5B\uFF0C\u8D2B\u5C3C\u5999\u82B1\uFF0C\u6562\u95EE\u65BD\u4E3B\u82B3\u540D\u3002\u300D</p><p>\u300C\u59BE\u8EAB\u59D3\u9F99\uFF0C\u95FA\u540D\u53EB\u505A\u9759\u989C\uFF0C\u8FD8\u671B\u5E08\u592A\u591A\u591A\u6307\u70B9\u3002\u300D</p><p>\u300C\u4E0D\u6562\u5F53\uFF0C\u8FD8\u8BF7\u65BD\u4E3B\u5165\u5185\u8BF4\u8BDD\u3002\u300D\u5999\u82B1\u65BD\u4E86\u4E00\u793C\uFF0C\u5F53\u5148\u5728\u524D\u5F15\u8DEF\u3002</p><p>\u62F1\u95E8\u5185\u662F\u4E00\u5EA7\u5C0F\u5C0F\u7684\u9662\u843D\uFF0C\u6B63\u4E2D\u4E00\u95F4\u6302\u7740\u533E\u989D\uFF0C\u4E0A\u9762\u5199\u7740\u300C\u51C0\u4FEE\u5802\u300D\u3002\u5999\u82B1\u5E08\u592A\u9886\u9759\u989C\u5165\u5185\uFF0C\u5206\u5BBE\u4E3B\u5750\u4E0B\uFF0C\u65C1\u8FB9\u65E9\u6709\u4EBA\u5949\u4E0A\u9999\u8317\u3002</p><p>\u5999\u82B1\u5E08\u592A\u4E00\u8A00\u4E0D\u53D1\uFF0C\u53EA\u9759\u9759\u996E\u7740\u9999\u8317\uFF0C\u90A3\u53CC\u7075\u52A8\u7684\u5927\u773C\u4E0D\u65F6\u779F\u8FC7\uFF0C\u4E0A\u4E0B\u6253\u91CF\u7740\u9759\u989C\u3002\u9759\u989C\u4E5F\u4E0D\u8BF4\u8BDD\uFF0C\u5979\u4E3E\u7740\u676F\u5B50\uFF0C\u6545\u4F5C\u597D\u5947\u5730\u89C2\u8D4F\u7740\u51C0\u4FEE\u5802\u3002\u5EB5\u5802\u5E76\u4E0D\u751A\u5927\uFF0C\u5802\u9648\u8BBE\u7B80\u964B\uFF0C\u684C\u6905\u90FD\u662F\u4F7F\u8FC7\u591A\u5E74\u7684\u65E7\u7269\uFF0C\u6848\u4E0A\u7684\u6728\u9C7C\u5012\u662F\u7C07\u65B0\u3002</p><p>\u826F\u4E45\uFF0C\u5999\u82B1\u5E08\u592A\u6DE1\u6DE1\u9053\uFF1A\u300C\u65BD\u4E3B\u65E2\u7136\u62FF\u7740\u7389\u4F69\uFF0C\u5BFB\u5230\u6B64\u5904\uFF0C\u60F3\u6765\u662F\u592D\u62A4\u6CD5\u4EB2\u81EA\u5F15\u89C1\u7684\u4E86\u3002\u300D</p><p>\u62A4\u6CD5\uFF1F\u90A3\u4E2A\u4E0D\u7537\u4E0D\u5973\u7684\u5BB6\u4F19\u7ADF\u7136\u662F\u661F\u6708\u6E56\u7684\u62A4\u6CD5\uFF1F\u9759\u989C\u5927\u89C9\u8352\u8C2C\uFF0C\u592D\u592D\u6B66\u529F\u867D\u7136\u7CBE\u5F3A\uFF0C\u4F46\u90A3\u6837\u7684\u5E74\u7EAA\u80FD\u5728\u661F\u6708\u6E56\u5F53\u4E0A\u62A4\u6CD5\uFF0C\u59D4\u5B9E\u4E0D\u53EF\u601D\u8BAE\u3002\u300C\u5E08\u592A\u6240\u8A00\u4E0D\u9519\u3002\u300D\u9759\u989C\u4E00\u7B11\u653E\u4E0B\u8336\u676F\uFF0C\u5979\u867D\u7136\u4E0D\u4FE1\u8336\u5185\u4F1A\u6709\u53E4\u602A\uFF0C\u4F46\u5728\u661F\u6708\u6E56\u591A\u4E00\u5206\u5C0F\u5FC3\u603B\u662F\u597D\u7684\u3002</p><p>\u5999\u82B1\u5E08\u592A\u770B\u51FA\u5979\u7684\u6212\u5907\uFF0C\u5FC3\u4E0B\u6697\u81EA\u72D0\u7591\u3002\u5979\u89C1\u8FC7\u7684\u5973\u5B50\u4E0D\u8BA1\u5176\u6570\uFF0C\u65E0\u8BBA\u662F\u540D\u9707\u6C5F\u6E56\u7684\u4FA0\u5973\uFF0C\u8FD8\u662F\u540D\u95E8\u5DE8\u5BA4\u7684\u8D35\u5987\uFF0C\u7B2C\u4E00\u6B21\u6765\u5230\u8FD9\u91CC\uFF0C\u4ECE\u672A\u6709\u4E00\u4E2A\u50CF\u5979\u8FD9\u6837\u9547\u5B9A\u3002\u7389\u4F69\u786E\u662F\u592D\u592D\u7684\u4E0D\u5047\uFF0C\u5979\u662F\u795E\u6559\u4E09\u62A4\u6CD5\u4E4B\u4E00\uFF0C\u4F69\u4E0A\u4EE5\u592A\u5FAE\u661F\u56FE\u4E3A\u8BB0\u3002\u53EF\u5979\u6574\u5929\u56F4\u7740\u5C0F\u516C\u4E3B\u8F6C\u6765\u8F6C\u53BB\uFF0C\u600E\u4E48\u6709\u95F2\u5FC3\u5F15\u65C1\u4EBA\u5165\u6559\uFF1F\u4E0D\u8FC7\u90A3\u5C0F\u5996\u7CBE\u773C\u5149\u5012\u662F\u4E0D\u5DEE\uFF0C\u8FD9\u5973\u5B50\u4F53\u6001\u5BB9\u8C8C\u90FD\u662F\u4E00\u7B49\u4E00\u7684\u7F8E\u4EBA\u513F\uFF0C\u5C31\u662F\u9001\u5230\u661F\u6708\u5BAB\u4E5F\u662F\u6EE1\u591F\u683C\u7684\u3002</p><p>\u300C\u65BD\u4E3B\u6B64\u6765\uFF0C\u662F\u60F3\u2026\u2026\u300D\u5999\u82B1\u5E08\u592A\u8FD8\u6709\u4E9B\u62FF\u4E0D\u51C6\u5979\u7684\u6765\u610F\uFF0C\u4E07\u4E00\u662F\u592D\u592D\u5F00\u4E2A\u73A9\u7B11\uFF0C\u5F15\u4E2A\u4E0D\u76F8\u5E72\u7684\u4EBA\u8FC7\u6765\uFF0C\u8D70\u6F0F\u4E86\u98CE\u58F0\uFF0C\u5979\u53EA\u7528\u6328\u4E0A\u51E0\u97AD\uFF0C\u81EA\u5DF1\u5C31\u9EBB\u70E6\u4E86\u3002</p><p>\u300C\u5F53\u7136\u662F\u60F3\u52A0\u5165\u8D35\u6559\u4E86\u3002\u300D</p><p>\u300C\u65BD\u4E3B\u53EF\u77E5\u9053\u8FD9\u91CC\u4EC0\u4E48\u5730\u65B9\uFF1F\u300D</p><p>\u9759\u989C\u5AE3\u7136\u7B11\u9053\uFF1A\u300C\u661F\u6708\u6E56\u4E00\u85CF\u5341\u4F59\u5E74\uFF0C\u8C01\u80FD\u60F3\u5230\u4F1A\u662F\u5728\u5EFA\u5EB7\u57CE\u5185\u6700\u5927\u7684\u5C3C\u5EB5\u5462\uFF1F\u300D</p><p>\u5999\u82B1\u5E08\u592A\u5634\u89D2\u9732\u51FA\u4E00\u4E1D\u7B11\u610F\uFF0C\u9759\u82E5\u6B62\u6C34\u7684\u7985\u97F3\u4E00\u77AC\u95F4\u53D8\u5F97\u5996\u5A9A\u5165\u9AA8\uFF0C\u300C\u770B\u6765\u592D\u62A4\u6CD5\u90FD\u5BF9\u4F60\u8BF4\u4E86\u5462\u3002\u300D\u8BF4\u7740\u4EB2\u70ED\u5730\u633D\u8D77\u9759\u989C\u7684\u7EA4\u624B\uFF0C\u300C\u989C\u513F\uFF0C\u8DDF\u6211\u6765\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u9759\u989C\u968F\u7740\u5999\u82B1\u5E08\u592A\u6765\u5230\u4FA7\u623F\uFF0C\u623F\u5185\u51E0\u540D\u5C3C\u59D1\u8FDE\u5FD9\u8D77\u8EAB\uFF0C\u907F\u5230\u4E00\u65C1\uFF0C\u63A5\u7740\u6709\u4EBA\u6273\u5F00\u673A\u62EC\uFF0C\u7D2B\u6A80\u6728\u5899\u7FFB\u5F00\u4E00\u6247\u5C0F\u95E8\uFF0C\u9732\u51FA\u4E00\u6761\u957F\u957F\u7684\u752C\u9053\u3002</p><p>\u752C\u9053\u5F77\u4F5B\u662F\u4E24\u5EA7\u5927\u6BBF\u4E4B\u95F4\u7684\u5939\u9053\uFF0C\u4E24\u58C1\u90FD\u6709\u5341\u4F59\u4E08\u9AD8\uFF0C\u4EF0\u5934\u80FD\u770B\u5230\u6BBF\u5B87\u98DE\u7FD8\u7684\u6A90\u89D2\u3002\u8D70\u51FA\u6570\u5341\u4E08\u8FDC\u8FD1\uFF0C\u8BF5\u7ECF\u58F0\u548C\u9999\u706B\u6C14\u606F\u6E10\u6E10\u8FDC\u53BB\uFF0C\u5999\u82B1\u5E08\u592A\u5728\u4E00\u5757\u6CA1\u6709\u4EFB\u4F55\u6807\u8BB0\u7684\u5899\u6572\u4E86\u51E0\u4E0B\uFF0C\u63A5\u7740\u5899\u4E0A\u4E00\u9707\uFF0C\u7F13\u7F13\u6253\u5F00\u4E00\u9053\u95E8\u6237\u3002</p><p>\u9ED1\u6697\u4E2D\u5F25\u6F2B\u7740\u4E00\u80A1\u8102\u7C89\u9999\u6C14\uFF0C\u8D70\u51FA\u4E08\u8BB8\u4E4B\u540E\u62D0\u4E86\u4E2A\u5C0F\u5F2F\uFF0C\u773C\u524D\u4E00\u4EAE\uFF0C\u5DF2\u7ECF\u5230\u4E86\u4E00\u4E2A\u534E\u4E3D\u65E0\u5339\u7684\u9662\u843D\u4E2D\u3002\u6B63\u4E2D\u4E00\u5EA7\u4E09\u5C42\u9AD8\u7684\u5927\u6BBF\uFF0C\u4E24\u65C1\u5404\u6709\u4E00\u5E62\u9601\u697C\uFF0C\u697C\u9601\u95F4\u5404\u6709\u6865\u5ECA\u76F8\u8FDE\uFF0C\u697C\u4E0A\u7EE3\u623F\u7F57\u5217\uFF0C\u9690\u9690\u56DE\u8361\u7740\u5973\u5B50\u7684\u5A07\u5598\u58F0\u3002</p><p>\u5999\u82B1\u5E08\u592A\u62C9\u7740\u9759\u989C\u7684\u624B\uFF0C\u8FB9\u8D70\u8FB9\u7B11\u9053\uFF1A\u300C\u4F60\u6765\u5F97\u6B63\u597D\uFF0C\u5317\u795E\u5C06\u521A\u5230\u6B64\u5904\uFF0C\u7B2C\u4E00\u6B21\u6765\u5C31\u8BA9\u4F60\u4F3A\u5019\u6559\u5185\u8D35\u4E3B\uFF0C\u8FD9\u53EF\u662F\u770B\u5728\u592D\u62A4\u6CD5\u9762\u5B50\u4E0A\u5462\u2026\u2026\u300D\u8BF4\u7740\u63A9\u53E3\u5403\u5403\u800C\u7B11\uFF0C\u90A3\u653E\u8361\u7684\u795E\u60C5\uFF0C\u4E1D\u6BEB\u770B\u4E0D\u51FA\u5979\u521A\u624D\u7684\u9053\u8C8C\u5CB8\u7136\u6765\u3002</p><p>\u9759\u989C\u5FC3\u5185\u6697\u81EA\u54AC\u7259\uFF0C\u8138\u4E0A\u5374\u5E26\u7740\u7F9E\u6DA9\u7684\u7B11\u5BB9\uFF0C\u4F4E\u58F0\u5E94\u9053\uFF1A\u300C\u989C\u513F\u660E\u767D\u4E86\u3002\u300D</p><p>\u5979\u4E00\u8DEF\u8D70\u6765\u6CA1\u6709\u770B\u5230\u534A\u4E2A\u5B88\u536B\uFF0C\u4F46\u8B66\u89C9\u5730\u610F\u8BC6\u5230\u6700\u5C11\u6709\u4E03\u5904\u6697\u54E8\uFF0C\u53EF\u8C13\u662F\u6212\u5907\u68EE\u4E25\u3002</p><p>\u5927\u6BBF\u5185\u4EFF\u7740\u4F5B\u5802\u7684\u683C\u5C40\uFF0C\u4F46\u672C\u8BE5\u653E\u7740\u4F5B\u50CF\u7684\u53F0\u57FA\u4E0A\uFF0C\u5374\u653E\u7740\u4E09\u6C60\u76F8\u8FDE\u7684\u4E00\u6C6A\u6E05\u6C34\u3002\u6BBF\u5185\u5C3D\u94FA\u5730\u6BEF\uFF0C\u4E24\u4EBA\u5408\u62B1\u7684\u5DE8\u67F1\u96D5\u9F99\u753B\u51E4\uFF0C\u9648\u8BBE\u534E\u4E3D\u4E4B\u6781\u3002</p><p>\u8D70\u4E0A\u6BBF\u95F4\u7684\u697C\u68AF\uFF0C\u9759\u989C\u4E0D\u7531\u4E00\u60CA\u3002\u53EA\u89C1\u4E00\u4E2A\u5973\u5B50\u8D64\u6761\u6761\u8DEA\u5728\u697C\u68AF\u4E0A\uFF0C\u6B63\u6367\u7740\u81EA\u5DF1\u767D\u5AE9\u7684\u4E73\u623F\uFF0C\u50CF\u62B9\u5E03\u90A3\u6837\u64E6\u62ED\u7740\u6276\u624B\u3002\u5979\u64E6\u5F97\u6781\u4E3A\u4ED4\u7EC6\uFF0C\u6709\u4E9B\u96D5\u7EB9\u5185\u7EC6\u5C0F\u7684\u7F1D\u9699\uFF0C\u8FD8\u8981\u634F\u7740\u4E73\u5934\u4E00\u4E00\u62B9\u8FC7\u3002\u5979\u624B\u65C1\u8FD8\u653E\u7740\u6BDB\u5DFE\uFF0C\u4F46\u90A3\u6BDB\u5DFE\u53EA\u7528\u64E6\u6D17\u4E73\u4E0A\u7684\u7070\u5C18\uFF0C\u4E00\u70B9\u4E5F\u4E0D\u6562\u89E6\u5230\u6276\u624B\u3002</p><p>\u770B\u5230\u9759\u989C\u7684\u60CA\u8BB6\uFF0C\u5999\u82B1\u5E08\u592A\u4EB2\u70ED\u5730\u8BF4\u9053\uFF1A\u300C\u4E0D\u7528\u7406\u90A3\u4E2A\u81ED\u5A4A\u5B50\uFF0C\u5979\u6562\u665A\u6765\u4E86\u6574\u6574\u4E00\u5929\uFF0C\u8001\u5A18\u5C31\u8BA9\u5979\u6367\u7740\u5976\u5B50\u628A\u5927\u6BBF\u90FD\u64E6\u4E00\u904D\u3002\u300D</p><p>\u9759\u989C\u7B11\u9053\uFF1A\u300C\u5E08\u592A\u597D\u6709\u8DA3\u554A\u3002\u300D</p><p>\u5999\u82B1\u5E08\u592A\u5F97\u610F\u5730\u6311\u4E86\u6311\u7709\u5934\uFF0C\u300C\u4F60\u6CA1\u89C1\u8FC7\u6211\u4EE5\u524D\u73A9\u59D3\u4F55\u7684\u6B7B\u5A4A\u5B50\uFF0C\u5F53\u5E74\u767D\u6C99\u6D3E\u7684\u7389\u5973\u638C\u95E8\uFF0C\u6700\u540E\u90A3\u6837\u5B50\uFF0C\u771F\u662F\u6709\u8DA3\u6B7B\u4E86\u2026\u2026\u300D\u8BF4\u7740\uFF0C\u5979\u63A8\u5F00\u4E86\u4E2D\u95F4\u7684\u6BBF\u95E8\u3002</p><p>\u4E00\u9635\u60A0\u626C\u7684\u4E1D\u7AF9\u58F0\u98D8\u5165\u8033\u4E2D\u3002\u5927\u6BBF\u88AB\u4E00\u9053\u6708\u6D1E\u95E8\u9694\u5F00\uFF0C\u95E8\u4E0A\u5782\u7740\u73E0\u5E18\uFF0C\u5E18\u5916\u5750\u7740\u51E0\u540D\u5973\u5B50\uFF0C\u5979\u4EEC\u8EAB\u62AB\u7EA2\u7EB1\uFF0C\u7EB1\u4E0B\u7684\u7389\u4F53\u7EA4\u6BEB\u6BD5\u73B0\uFF0C\u624B\u91CC\u62FF\u7740\u7B19\u3001\u7BAB\u3001\u7435\u7436\u5404\u79CD\u4E50\u5668\uFF0C\u6B63\u5728\u6F14\u594F\u3002</p><p>\u5947\u602A\u7684\u662F\uFF0C\u4EBA\u7FA4\u4E2D\u8FD8\u6709\u4E00\u4E2A\u5341\u5C81\u5DE6\u53F3\u7684\u5C0F\u7537\u5B69\uFF0C\u4ED6\u53EA\u7A7F\u4E86\u4E2A\u7EA2\u809A\u515C\uFF0C\u722C\u5728\u4E00\u540D\u5973\u5B50\u817F\u4E0A\uFF0C\u4E24\u53EA\u5C0F\u624B\u63EA\u7740\u90A3\u5973\u5B50\u7684\u4E73\u5934\u4F7F\u52B2\u513F\u5411\u4E0B\u62FD\u3002\u90A3\u5973\u5B50\u75BC\u5F97\u53CC\u76EE\u542B\u6CEA\uFF0C\u8FD8\u5F3A\u5FCD\u7740\u7BAB\u58F0\u4E0D\u7EDD\u3002\u90A3\u5B69\u5B50\u628A\u5979\u5706\u6DA6\u7684\u9999\u4E73\u62FD\u6210\u6241\u957F\uFF0C\u518D\u677E\u624B\u770B\u5B83\u5F39\u56DE\u539F\u72B6\u3002</p><p>\u73A9\u4E86\u4E00\u4F1A\u513F\uFF0C\u90A3\u7537\u5B69\u53C8\u5BF9\u5973\u5B50\u624B\u4E2D\u7684\u6D1E\u7BAB\u6709\u4E86\u5174\u8DA3\uFF0C\u4F38\u624B\u5C31\u6765\u593A\u3002\u90A3\u5973\u5B50\u4E0D\u6562\u65AD\u4E86\u6F14\u594F\uFF0C\u7BAB\u5C3E\u4E00\u6643\uFF0C\u907F\u5F00\u4ED6\u7684\u624B\u6307\u3002\u90A3\u5B69\u5B50\u987F\u65F6\u53D1\u8D77\u6012\u6765\uFF0C\u5BF9\u7740\u90A3\u5973\u5B50\u7684\u4E73\u623F\u72E0\u72E0\u54AC\u4E86\u4E00\u53E3\u3002\u90A3\u5973\u5B50\u7BAB\u58F0\u4E00\u7A92\uFF0C\u4E73\u5C16\u4E0A\u5DF2\u7ECF\u591A\u4E86\u4E24\u6392\u6E17\u8840\u7684\u7259\u5370\u3002</p><p>\u96BE\u9053\u8FD9\u662F\u5317\u795E\u5C06\u7684\u516C\u5B50\uFF1F\u5C0F\u5C0F\u5E74\u7EAA\u5C31\u8FD9\u4E48\u653E\u8086\u2026\u2026\u9759\u989C\u60F3\u7740\uFF0C\u6697\u6697\u77A5\u4E86\u5999\u82B1\u5E08\u592A\u4E00\u773C\u3002\u53EA\u89C1\u5999\u82B1\u5E08\u592A\u7F8E\u8273\u7684\u8138\u5E9E\u8499\u4E0A\u4E00\u5C42\u715E\u6C14\uFF0C\u51B7\u51B7\u54FC\u4E86\u4E00\u58F0\u3002</p><p>\u95FB\u58F0\u4F17\u5973\u5A07\u8EAF\u90FD\u662F\u4E00\u98A4\uFF0C\u90A3\u4E2A\u5C0F\u7537\u5B69\u5374\u9AD8\u5174\u5730\u722C\u4E0B\u6765\uFF0C\u4E00\u7638\u4E00\u62D0\u5730\u8D70\u8FC7\u6765\u62B1\u4F4F\u5E08\u592A\u7684\u5927\u817F\uFF0C\u53E3\u9F7F\u4E0D\u6E05\u5730\u53EB\u9053\uFF1A\u300C\u5A18\u3002\u300D</p><p>\u9759\u989C\u6014\u6014\u770B\u7740\u8FD9\u4E2A\u602A\u5F02\u7684\u7537\u5B69\uFF0C\u4ED6\u4E0D\u4EC5\u4E00\u4FA7\u7684\u624B\u811A\u840E\u7F29\uFF0C\u800C\u4E14\u989D\u5934\u5947\u5927\uFF0C\u53CC\u76EE\u767D\u591A\u9ED1\u5C11\uFF0C\u663E\u7136\u662F\u6709\u5148\u5929\u7684\u7F3A\u9677\u3002\u6CA1\u60F3\u5230\u8FD9\u4E2A\u75C5\u6B8B\u7684\u5B69\u5B50\u7ADF\u7136\u662F\u4E00\u4E2A\u7F8E\u8273\u5C3C\u59D1\u6240\u751F\u3002\u771F\u4E0D\u77E5\u9053\u5999\u82B1\u5E08\u592A\u8FD9\u6837\u7684\u5A9A\u7269\u662F\u8DDF\u4EC0\u4E48\u4E1C\u897F\u4EA4\u5ABE\uFF0C\u624D\u751F\u4E0B\u8FD9\u4E48\u4E2A\u602A\u7269\u3002</p><p>\u5999\u82B1\u5E08\u592A\u7709\u82B1\u773C\u7B11\u5730\u6402\u4F4F\u513F\u5B50\uFF0C\u300C\u4E56\u513F\u5B50\uFF0C\u4F60\u7239\u7239\u5462\uFF1F\u300D</p><p>\u7537\u5B69\u6B6A\u659C\u7684\u5634\u89D2\u6D41\u51FA\u53E3\u6C34\uFF0C\u8D39\u529B\u5730\u8BF4\u9053\uFF1A\u300C\u6B3A\u2026\u2026\u8D1F\u6211\u2026\u2026\u300D</p><p>\u5999\u82B1\u5E08\u592A\u67F3\u7709\u4E00\u6311\uFF0C\u300C\u5B9D\u513F\uFF0C\u544A\u8BC9\u5A18\uFF0C\u8C01\u6B3A\u8D1F\u4F60\u4E86\u3002\u300D</p><p>\u300C\u5979\uFF01\u300D\u5B9D\u513F\u5411\u540E\u6307\u53BB\uFF0C\u6307\u7684\u5374\u662F\u4E00\u4E2A\u5439\u7B1B\u7684\u5973\u5B50\u3002</p><p>\u90A3\u5973\u5B50\u5413\u5F97\u82B1\u5BB9\u5931\u8272\uFF0C\u8FDE\u5FD9\u8DEA\u4E0B\u6765\u9053\uFF1A\u300C\u5974\u5A62\u4E0D\u6562\uFF0C\u516C\u5B50\u2026\u2026\u516C\u5B50\u662F\u8BA4\u9519\u4E86\u3002\u300D</p><p>\u300C\u5478\uFF01\u6211\u513F\u5B50\u600E\u4E48\u4F1A\u8BA4\u9519\uFF1F\u8D31\u5A62\uFF0C\u722C\u8FC7\u6765\uFF01\u300D</p><p>\u5439\u7B1B\u7684\u5973\u5B50\u4E0D\u6562\u518D\u8FA9\uFF0C\u53EA\u597D\u4F0F\u8EAB\u722C\u5230\u4E3B\u4EBA\u811A\u4E0B\u3002</p><p>\u300C\u8FD8\u6709\u4F60\u3002\u300D\u5999\u82B1\u5E08\u592A\u6307\u4E86\u6307\u5439\u7BAB\u7684\u5973\u5B50\uFF0C\u51B7\u51B7\u9053\uFF1A\u300C\u4E5F\u722C\u8FC7\u6765\u3002\u300D</p><p>\u7B49\u4E24\u4E2A\u5973\u5B50\u90FD\u722C\u5230\u811A\u4E0B\uFF0C\u5999\u82B1\u5E08\u592A\u6362\u4E0A\u7B11\u8138\uFF0C\u62C9\u7740\u513F\u5B50\u67D4\u58F0\u9053\uFF1A\u300C\u5B9D\u513F\uFF0C\u5A18\u6559\u4F60\u6345\u8D31\u5C44\u73A9\u2026\u2026\u300D</p><p>\u5979\u5288\u624B\u593A\u4E0B\u7AF9\u7B1B\uFF0C\u90A3\u5973\u5B50\u7ACB\u5373\u892A\u53BB\u7EA2\u7EB1\uFF0C\u4E24\u624B\u62B1\u7740\u9AD8\u7FD8\u7684\u7C89\u81C0\u62DA\u547D\u5206\u5F00\uFF0C\u50CF\u4E00\u6761\u4E0D\u77E5\u5EC9\u803B\u7684\u6BCD\u72D7\u4E00\u6837\uFF0C\u66B4\u9732\u7740\u7F9E\u5904\u3002\u9759\u989C\u5BF9\u8FD9\u4E9B\u5973\u4EBA\u7684\u670D\u4ECE\u53C8\u662F\u60CA\u8BB6\u53C8\u662F\u4E0D\u5C51\u3002\u8FD9\u6837\u6D3B\u7740\uFF0C\u8FDE\u4E00\u6761\u72D7\u90FD\u4E0D\u5982\u5462\u3002</p><p>\u7AF9\u7B1B\u4E00\u633A\uFF0C\u7B14\u76F4\u63D2\u8FDB\u90A3\u5973\u5B50\u5706\u5F20\u7684\u9634\u6237\u5185\u3002\u5E72\u6DA9\u7684\u8089\u7A74\u88AB\u8FD9\u6837\u5F3A\u884C\u63D2\u5165\uFF0C\u90A3\u79CD\u6DF1\u5165\u8179\u8154\u7684\u75BC\u75DB\uFF0C\u5C31\u50CF\u4E00\u67C4\u5229\u5251\u5C06\u8EAB\u4F53\u6345\u7A7F\u3002\u90A3\u5973\u5B50\u6B7B\u6B7B\u54AC\u7740\u7259\u5173\uFF0C\u63B0\u7740\u7C89\u81C0\u7684\u624B\u6307\u4E0D\u4F4F\u98A4\u6296\u3002</p><p>\u5999\u82B1\u5E08\u592A\u4E0B\u624B\u6781\u91CD\uFF0C\u5C3A\u8BB8\u957F\u7684\u7AF9\u7B1B\u51E0\u4E4E\u6574\u652F\u63D2\u5165\u90A3\u5973\u5B50\u4F53\u5185\u3002\u5979\u628A\u7B1B\u5B50\u4EA4\u5230\u513F\u5B50\u624B\u4E2D\uFF0C\u300C\u62D4\u51FA\u6765\u554A\u3002\u300D</p><p>\u5B9D\u513F\u7B28\u62D9\u5730\u62D4\u4E86\u4E00\u4E0B\uFF0C\u90A3\u7AF9\u7B1B\u7EB9\u4E1D\u672A\u52A8\u3002\u5999\u82B1\u5E08\u592A\u6012\u9053\uFF1A\u300C\u8D31\u5A4A\u5B50\uFF0C\u4E00\u6839\u7834\u7B1B\u5B50\u5939\u8FD9\u4E48\u7D27\u5E72\u561B\uFF1F\u60F3\u8BA9\u8001\u5A18\u628A\u4F60\u7684\u9A9A\u5C44\u525C\u6389\u5417\uFF1F\u300D</p><p>\u90A3\u5973\u5B50\u98A4\u58F0\u9053\uFF1A\u300C\u5974\u5A62\u4E0D\u6562\u2026\u2026\u300D\u5979\u5E76\u975E\u6709\u610F\u5939\u7D27\uFF0C\u5B9E\u5728\u662F\u4E0B\u4F53\u5267\u75DB\uFF0C\u8089\u58C1\u60C5\u4E0D\u81EA\u7981\u5730\u6536\u62E2\uFF0C\u624D\u5939\u4F4F\u4E86\u7AF9\u7B1B\u3002</p><p>\u5999\u82B1\u5E08\u592A\u63E1\u4F4F\u7B1B\u7BA1\u7528\u529B\u4E00\u62D4\uFF0C\u90A3\u53EA\u96EA\u767D\u7684\u5706\u81C0\u731B\u7136\u62AC\u8D77\uFF0C\u53C8\u8FDE\u5FD9\u8E1E\u5730\u4F0F\u597D\u3002</p><p>\u53EA\u89C1\u7C89\u81C0\u95F4\u90A3\u53EA\u7EA2\u6DA6\u7684\u7389\u6237\u6E10\u6B21\u7EFD\u5F00\uFF0C\u7AF9\u7B1B\u5F77\u4F5B\u6389\u8FDB\u6CE5\u6DD6\u7684\u91CD\u7269\u4E00\u6837\uFF0C\u4E00\u70B9\u70B9\u79BB\u5F00\u7D27\u5BC6\u7684\u8089\u7A74\u3002</p><p>\u5979\u6709\u610F\u65E0\u610F\u5730\u779F\u4E86\u9759\u989C\u4E00\u773C\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u8FD9\u4E9B\u8D31\u4EBA\uFF0C\u5C31\u662F\u8981\u597D\u597D\u6536\u62FE\u624D\u542C\u8BDD\u5462\u3002\u300D</p><p>\u5999\u82B1\u62FF\u7740\u7AF9\u7B1B\u6CA1\u6709\u534A\u70B9\u601C\u60DC\u5730\u5728\u90A3\u5973\u5B50\u4F53\u5185\u62BD\u9001\u8D77\u6765\uFF0C\u5B9D\u513F\u770B\u7740\u90A3\u53EA\u5C41\u80A1\u4E2D\u4E00\u56E2\u7EA2\u8089\u7FFB\u8FDB\u7FFB\u51FA\uFF0C\u4E0D\u7531\u9AD8\u5174\u5730\u53EB\u9053\uFF1A\u300C\u597D\uFF0C\u597D\u73A9\u2026\u2026\u300D</p><p>\u300C\u90A3\u5B9D\u513F\u597D\u597D\u73A9\u554A\u3002\u8FD9\u4E00\u4E2A\u73A9\u817B\u4E86\uFF0C\u90A3\u91CC\u8FD8\u6709\u4E00\u4E2A\u3002\u300D\u5999\u82B1\u5E08\u592A\u76F4\u8D77\u8170\uFF0C\u98CE\u60C5\u4E07\u79CD\u5730\u6276\u4E86\u6276\u5C3C\u5E3D\uFF0C\u7B11\u9053\uFF1A\u300C\u989C\u513F\u8BE5\u7B49\u6025\u4E86\u5427\uFF0C\u5317\u795E\u5C06\u5C31\u5728\u91CC\u9762\u3002\u300D</p><p>\u9759\u989C\u7B11\u9053\uFF1A\u300C\u4EE4\u90CE\u771F\u662F\u806A\u660E\u53EF\u7231\u3002\u300D\u8BF4\u7740\u8EAB\u540E\u8F6C\u6765\u5973\u5B50\u7684\u95F7\u54FC\uFF0C\u90A3\u5B9D\u513F\u52A8\u4F5C\u7B28\u62D9\u53C8\u4E0D\u8FDE\u8D2F\uFF0C\u63D2\u7740\u63D2\u7740\u5C31\u627E\u9519\u4E86\u5730\u65B9\u3002\u5973\u5B50\u7684\u8089\u7A74\u4F55\u7B49\u5A07\u5AE9\uFF0C\u8BA9\u4ED6\u8FD9\u6837\u4E71\u6345\uFF0C\u9634\u5185\u65E9\u5DF2\u88AB\u7AF9\u7B1B\u5212\u7834\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u73E0\u5E18\u540E\u662F\u4E00\u95F4\u534E\u4E3D\u7684\u5367\u5BA4\uFF0C\u4E2D\u95F4\u653E\u7740\u4E00\u5F20\u5927\u5E8A\u3002\u6B64\u65F6\u4E00\u4E2A\u7F8E\u8C8C\u5973\u5B50\u6B63\u8DEA\u5750\u5728\u4E00\u4E2A\u7537\u5B50\u8170\u95F4\uFF0C\u96EA\u81C0\u4E0A\u4E0B\u8D77\u843D\uFF0C\u7528\u529B\u5957\u5F04\u7740\u81C0\u4E0B\u7684\u8089\u68D2\u3002\u5979\u5A07\u8EAF\u540E\u4EF0\uFF0C\u4E24\u624B\u6491\u5728\u8EAB\u540E\uFF0C\u968F\u7740\u7389\u4F53\u7684\u8D77\u843D\uFF0C\u80F8\u524D\u90A3\u4E24\u56E2\u4E30\u817B\u7684\u96EA\u4E73\u4E5F\u4E0A\u4E0B\u8DF3\u4E2A\u4E0D\u505C\uFF0C\u8361\u51FA\u5C42\u5C42\u8089\u5149\u3002</p><p>\u6B63\u9762\u770B\u6765\uFF0C\u4E24\u4EBA\u4EA4\u5408\u7684\u90E8\u4F4D\u4E00\u89C8\u65E0\u9057\u3002\u90A3\u53EA\u5973\u9634\u82B1\u74E3\u7EFD\u5F00\u6210\u4E0B\u5706\u4E0A\u5C16\u7684\u6843\u53F6\u5F62\u72B6\uFF0C\u5AE9\u8089\u4E0A\u6CBE\u7740\u4EAE\u6676\u6676\u7684\u6DEB\u6DB2\uFF0C\u8272\u6CFD\u6108\u53D1\u7EA2\u6DA6\u3002\u4E00\u6839\u53C8\u7C97\u53C8\u9ED1\u7684\u9633\u5177\u76F4\u633A\u633A\u63D2\u5728\u5973\u5B50\u6700\u67D4\u5AE9\u7684\u7F8E\u8089\u5185\uFF0C\u5C3D\u60C5\u4EAB\u53D7\u7740\u5176\u4E2D\u7684\u7F8E\u5999\u6ECB\u5473\u3002\u90A3\u5973\u5B50\u542C\u5230\u6709\u4EBA\u8FDB\u6765\uFF0C\u52A8\u4F5C\u4E5F\u6CA1\u6709\u7247\u523B\u505C\u987F\uFF0C\u5634\u4E2D\u4F9D\u7136\u662F\u6D6A\u53EB\u4E0D\u7EDD\uFF0C\u4F3C\u4E4E\u65E9\u5DF2\u4E60\u60EF\u4E86\u5728\u4F17\u4EBA\u9762\u524D\u7684\u4EA4\u5ABE\u3002</p><p>\u9759\u989C\u4E00\u76F4\u7559\u610F\u60F3\u770B\u6E05\u5317\u795E\u5C06\u7684\u9762\u5BB9\uFF0C\u5F53\u65E5\u8349\u539F\u4E2D\u90A3\u4E9B\u6C61\u8FB1\u8FC7\u6BCD\u4EB2\u7684\u7537\u4EBA\uFF0C\u5979\u4E00\u4E2A\u90FD\u6CA1\u6709\u5FD8\u8BB0\u3002\u4F46\u90A3\u7537\u5B50\u4E0A\u8EAB\u88AB\u8273\u5973\u906E\u4F4F\uFF0C\u59CB\u7EC8\u65E0\u6CD5\u770B\u6E05\u3002</p><p>\u4E00\u4E2A\u6C89\u7A33\u7684\u7537\u58F0\u4ECE\u9773\u5982\u70DF\u8EAB\u540E\u54CD\u8D77\uFF0C\u90A3\u7537\u5B50\u6DE1\u6DE1\u9053\uFF1A\u300C\u6362\u540E\u8FB9\u7684\u3002\u300D</p><p>\u9759\u989C\u5FC3\u5934\u5FAE\u9707\uFF0C\u8FD9\u4E2A\u58F0\u97F3\u4F3C\u4E4E\u5728\u54EA\u91CC\u542C\u8FC7\uFF0C\u5374\u4E00\u65F6\u60F3\u4E0D\u8D77\u6765\u3002\u96BE\u9053\u771F\u662F\u5F53\u65E5\u90A3\u4F19\u5996\u4EBA\u4E4B\u4E00\uFF1F</p>',105);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
