import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_21" tabindex="-1"><a class="header-anchor" href="#_21" aria-hidden="true">#</a> 21</h1><p>\u6CAE\u6E20\u5927\u5E08\u9053\uFF1A\u300C\u4F60\u5E08\u7236\u5E08\u5A18\u5DF2\u7ECF\u662F\u6B66\u6797\u9876\u5C16\u4EBA\u7269\uFF0C\u5C31\u7B97\u4F60\u662F\u4E2A\u5973\u5B50\u65E0\u6CD5\u63A5\u7BA1\u638C\u95E8\u4E4B\u4F4D\uFF0C\u8D34\u4E0A\u8EAB\u5B50\u5F53\u4E2A\u638C\u95E8\u592B\u4EBA\u4E5F\u662F\u8F7B\u800C\u6613\u4E3E\u3002\u4F55\u5FC5\u6765\u6211\u661F\u6708\u6E56\u5356\u8EAB\u5462\uFF1F\u300D</p><p>\u9759\u989C\u4E00\u65F6\u8BED\u585E\uFF0C\u7247\u523B\u540E\u53F9\u4E86\u53E3\u6C14\uFF0C\u300C\u5927\u5E08\u4FE1\u4E5F\u7F62\uFF0C\u4E0D\u4FE1\u4E5F\u7F62\uFF0C\u5F85\u89C1\u5230\u592D\u62A4\u6CD5\uFF0C\u5927\u5E08\u5C31\u660E\u767D\u4E86\u3002\u300D</p><p>\u72EC\u81C2\u548C\u5C1A\u628A\u9773\u5982\u70DF\u6402\u5728\u6000\u91CC\uFF0C\u4E00\u8FB9\u5728\u5979\u767D\u5149\u5149\u7684\u8089\u4F53\u4E0A\u8086\u610F\u63C9\u634F\uFF0C\u4E00\u8FB9\u51B7\u7B11\u9053\uFF1A\u300C\u65E2\u7136\u65E0\u4EE5\u53D6\u4FE1\u672C\u5EA7\uFF0C\u4F60\u60F3\u89C1\u592D\u62A4\u6CD5\u2026\u2026\u4E0D\u8FC7\u662F\u75F4\u5FC3\u5984\u60F3\u7F62\u4E86\u3002\u300D</p><p>\u9759\u989C\u6C89\u9ED8\u4E00\u4F1A\u513F\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u5C31\u8BF7\u5927\u5E08\u7ED9\u59BE\u8EAB\u5F00\u82DE\u5427\u3002\u300D</p><p>\u6CAE\u6E20\u5927\u5E08\u5927\u7B11\u9053\uFF1A\u300C\u8FC7\u6765\uFF0C\u8BA9\u672C\u5EA7\u5148\u8BD5\u8BD5\u4F60\u7684\u5C0F\u5634\uFF01\u300D</p><p>\u9759\u989C\u6254\u4E0B\u5212\u7834\u7684\u8863\u886B\uFF0C\u8D64\u88F8\u88F8\u722C\u5230\u69BB\u4E0A\u3002\u591A\u5E74\u6765\u517B\u6210\u7684\u4E60\u60EF\uFF0C\u4F7F\u5979\u6709\u610F\u65E0\u610F\u5939\u7D27\u53CC\u817F\uFF0C\u906E\u63A9\u7740\u81EA\u5DF1\u7684\u79D8\u5904\u3002</p><p>\u521A\u5C04\u8FC7\u7CBE\u7684\u9633\u5177\u5E26\u7740\u6D53\u6D53\u7684\u5F02\u5473\uFF0C\u4F46\u9759\u989C\u6CA1\u6709\u9732\u51FA\u4E0D\u60A6\uFF0C\u5979\u64A9\u8D77\u9B13\u4FA7\u7684\u79C0\u53D1\uFF0C\u6E29\u5A49\u5730\u5F20\u5F00\u5C0F\u5634\uFF0C\u5C06\u9633\u5177\u542B\u5165\u53E3\u5185\u3002</p><p>\u6CAE\u6E20\u5927\u5E08\u61D2\u6D0B\u6D0B\u9053\uFF1A\u300C\u65E2\u7136\u592D\u62A4\u6CD5\u8981\u4E86\u4F60\u7684\u5143\u7EA2\uFF0C\u672C\u5EA7\u4E5F\u4E0D\u4E0E\u5979\u4E89\u3002\u4E56\u4E56\u8BA9\u672C\u5EA7\u5728\u4F60\u5634\u91CC\u5C04\u4E0A\u4E00\u56DE\u518D\u8BF4\u3002\u300D</p><p>\u9759\u989C\u4E0D\u518D\u8BF4\u8BDD\uFF0C\u53EA\u8FD0\u8DB3\u5507\u820C\u5DE5\u592B\uFF0C\u7AED\u529B\u4F8D\u5949\u53E3\u4E2D\u7684\u9633\u5177\u3002\u4E0D\u591A\u65F6\uFF0C\u90A3\u6839\u8F6F\u5316\u7684\u8089\u68D2\u4FBF\u575A\u786C\u8D77\u6765\u3002\u6CAE\u6E20\u5927\u5E08\u8FDE\u58F0\u8D5E\u9053\uFF1A\u300C\u8FD9\u5C0F\u5A4A\u5B50\u5634\u5DF4\u771F\u4E0D\u8D56\uFF0C\u6BD4\u5973\u4EBA\u7684\u5C44\u8FD8\u8212\u670D\u3002\u300D</p><p>\u5999\u82B1\u5E08\u592A\u89C1\u5979\u6CA1\u6709\u53CD\u6297\uFF0C\u4FBF\u6254\u4E0B\u77ED\u5251\uFF0C\u628A\u9773\u5982\u70DF\u62D6\u5230\u4E00\u8FB9\uFF0C\u4E00\u50E7\u4E00\u5C3C\u592B\u59BB\u4FE9\u5E76\u80A9\u8EBA\u7740\uFF0C\u655E\u5F00\u5927\u817F\uFF0C\u8BA9\u4E24\u4E2A\u7F8E\u8C8C\u5973\u5B50\u8214\u5F04\u81EA\u5DF1\u7684\u6027\u5668\u6765\u53D6\u4E50\u3002</p><p>\u9759\u989C\u628A\u7C97\u58EE\u7684\u8089\u68D2\u5B8C\u5168\u541E\u5165\uFF0C\u7528\u5589\u5934\u7684\u8F6F\u8089\u505A\u7740\u541E\u54BD\u52A8\u4F5C\uFF0C\u6765\u78E8\u64E6\u9F9F\u5934\u3002\u7136\u540E\u6536\u7D27\u7EA2\u5507\uFF0C\u7D27\u7D27\u88F9\u8089\u68D2\uFF0C\u9999\u820C\u6253\u7740\u65CB\u4ECE\u9633\u5177\u6839\u90E8\u4E00\u76F4\u8214\u5230\u9F9F\u5934\u7684\u5C16\u7AEF\u3002\u6CAE\u6E20\u5927\u5E08\u6EE1\u610F\u5730\u9760\u5728\u6795\u4E0A\uFF0C\u5DE6\u624B\u629A\u6478\u7740\u9759\u989C\u5A07\u7F8E\u7684\u9762\u5B54\uFF0C\u300C\u662F\u4E0D\u662F\u5E2E\u4F60\u5E08\u7236\u8214\u8FC7\u9E21\u5DF4\uFF1F</p><p>\u53E3\u6280\u8FD9\u4E48\u719F\u7EC3\u3002\u300D</p><p>\u9759\u989C\u5C0F\u5634\u88AB\u8089\u68D2\u585E\u6EE1\uFF0C\u54EA\u91CC\u8FD8\u80FD\u7B54\u8BDD\uFF1F\u53EA\u80FD\u52C9\u5F3A\u6447\u4E86\u6447\u5934\uFF0C\u5507\u820C\u4E0D\u505C\u5438\u542E\u3002</p><p>\u6CAE\u6E20\u5927\u5E08\u63C9\u634F\u7740\u5979\u7684\u7389\u988A\u3001\u7C89\u9888\uFF0C\u6700\u540E\u634F\u4F4F\u5979\u8033\u4E0A\u7684\u660E\u73E0\uFF0C\u8170\u8179\u731B\u7136\u4E00\u633A\uFF0C\u7CBE\u6DB2\u72C2\u6D8C\u800C\u51FA\u3002</p><p>\u9759\u989C\u76F4\u8D77\u8EAB\u5B50\uFF0C\u8DEA\u5750\u4E00\u65C1\uFF0C\u7389\u624B\u653E\u5728\u5589\u5934\uFF0C\u8F7B\u8F7B\u54B3\u7740\uFF0C\u5C06\u545B\u5230\u6C14\u7BA1\u7684\u7CBE\u6DB2\u54B3\u51FA\uFF0C\u518D\u4E00\u4E00\u54BD\u4E0B\uFF0C\u7389\u5BB9\u59CB\u7EC8\u5E73\u9759\u65E0\u6CE2\u3002</p><p>\u7B49\u54BD\u5B8C\u6700\u540E\u4E00\u6EF4\u7CBE\u6DB2\uFF0C\u5C11\u5973\u7EC6\u81F4\u5730\u8214\u8FC7\u7EA2\u5507\uFF0C\u8F7B\u58F0\u9053\uFF1A\u300C\u5927\u5E08\uFF0C\u8FD9\u6837\u53EF\u4EE5\u4E86\u5417\uFF1F\u300D</p><p>\u6CAE\u6E20\u5927\u5E08\u62CD\u62CD\u80EF\u4E0B\uFF0C\u5927\u7B11\u9053\uFF1A\u300C\u4E5D\u534E\u5251\u6D3E\u7684\u9AD8\u5F92\u679C\u7136\u98CE\u9AA8\u4E0D\u4FD7\uFF01\u8FD9\u5F20\u5C0F\u5634\u8214\u5F97\u672C\u5EA7\u597D\u8212\u670D\uFF01\u53EA\u4E0D\u77E5\u9053\u2026\u2026\u300D\u4ED6\u773C\u795E\u50CF\u9488\u4E00\u6837\u76EF\u7740\u9759\u989C\u7684\u773C\u775B\uFF0C\u300C\u8FD9\u529F\u592B\u662F\u4E0D\u662F\u4F60\u5E08\u5A18\u6559\u7684\uFF1F\u300D</p><p>\u9759\u989C\u7389\u8138\u53D8\u8272\uFF0C\u8FDE\u9999\u4E73\u4E5F\u7D27\u5F20\u5F97\u7EF7\u4E86\u8D77\u6765\u3002</p><p>\u6CAE\u6E20\u5927\u5E08\u6DE1\u7136\u8BF4\u9053\uFF1A\u300C\u60F3\u5165\u6211\u661F\u6708\u6E56\uFF0C\u9700\u5F97\u6709\u6240\u8BDA\u610F\u3002\u4F60\u4EE5\u4E3A\u8FD9\u6837\u5C31\u591F\u4E86\u5417\uFF1F\u300D\u4ED6\u5FAE\u5FAE\u4E00\u7B11\uFF0C\u300C\u4E5D\u534E\u5251\u6D3E\u638C\u95E8\u592B\u4EBA\u662F\u6B66\u6797\u4E2D\u6709\u540D\u7684\u7F8E\u4EBA\u513F\uFF0C\u672C\u5EA7\u4EF0\u6155\u5DF2\u4E45\u3002</p><p>\u672C\u5EA7\u4E0E\u4F60\u4F5C\u4E2A\u4EA4\u6613\uFF0C\u53EA\u8981\u4F60\u628A\u7434\u58F0\u82B1\u5F71\u732E\u51FA\u6765\uFF0C\u8BA9\u51CC\u5973\u4FA0\u5728\u6B64\u5145\u5F53\u51E0\u65E5\u6DEB\u5974\uFF0C\u672C\u5EA7\u5C31\u8BB8\u4F60\u5165\u661F\u6708\u6E56\uFF01\u300D</p><p>\u6DEB\u5974\u3002\u8FD9\u4E24\u4E2A\u5B57\u51E0\u4E4E\u662F\u523B\u5728\u9759\u989C\u5FC3\u5E95\u3002\u300C\u516B\u6781\u95E8\u638C\u95E8\u592B\u4EBA\uFF0C\u661F\u6708\u6E56\u6DEB\u5974\u5510\u989C\u300D\uFF0C\u8FD9\u662F\u523A\u5728\u6BCD\u4EB2\u4E73\u623F\u4E0A\u7684\u6587\u5B57\u3002</p><p>\u5F53\u5E74\u6BCD\u4EB2\u88AB\u903C\uFF0C\u5728\u661F\u6708\u6E56\u5996\u4EBA\u624B\u4E2D\u5F53\u4E86\u4E00\u5929\u6DEB\u5974\uFF0C\u65F6\u9694\u5341\u4F59\u5E74\uFF0C\u5979\u8FD8\u6E05\u695A\u8BB0\u5F97\uFF0C\u90A3\u4E9B\u4EBA\u5C42\u51FA\u4E0D\u7A77\u7684\u6DEB\u8650\u624B\u6BB5\uFF0C\u8BB0\u5F97\u6BCD\u4EB2\u96BE\u4EE5\u8A00\u8BF4\u7684\u5C48\u8FB1\u3002\u800C\u521A\u624D\u7684\u89C1\u95FB\u66F4\u4F7F\u5979\u8BA4\u8BC6\u5230\uFF0C\u5728\u661F\u6708\u6E56\u6DEB\u5974\u53EA\u662F\u4E00\u79CD\u53EF\u4EE5\u88AB\u4EFB\u610F\u51CC\u8FB1\u7684\u73A9\u7269\uFF0C\u6CA1\u6709\u5C0A\u4E25\uFF0C\u751A\u81F3\u6CA1\u6709\u81EA\u5DF1\uFF0C\u7075\u8089\u90FD\u5C5E\u4E8E\u4E3B\u4EBA\u6240\u6709\u3002</p><p>\u5979\u5DF2\u7ECF\u5931\u53BB\u4E86\u4E00\u4E2A\u6BCD\u4EB2\uFF0C\u96BE\u9053\u8FD8\u8981\u628A\u53E6\u4E00\u4E2A\u6BCD\u4EB2\u4EB2\u624B\u9001\u5165\u661F\u6708\u6E56\uFF0C\u4F5C\u4E00\u4E2A\u8FD9\u6837\u7684\u6DEB\u5974\u5417\uFF1F</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u5468\u5B50\u6C5F\u548C\u51CC\u96C5\u7434\u8FD8\u5728\u4E89\u6267\uFF0C\u9F99\u6714\u5F00\u53E3\u9053\uFF1A\u300C\u5E08\u7236\uFF0C\u6CAE\u6E20\u5927\u5E08\u66FE\u8BF4\uFF0C\u7389\u51CC\u9704\u6DF3\u4E8E\u5973\u4FA0\u6709\u4E9B\u906D\u9047\u96BE\u4EE5\u2026\u2026\u96BE\u4EE5\u542F\u9F7F\uFF0C\u6700\u597D\u8BA9\u5E08\u5A18\u4E5F\u53BB\u4E00\u8D9F\uFF0C\u6709\u4E9B\u8BDD\u8BF4\u8D77\u6765\u6BD4\u8F83\u65B9\u4FBF\u3002\u300D</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u300C\u6CAE\u6E20\u5927\u5E08\u7ADF\u4F1A\u9009\u62E9\u5C3C\u5EB5\u85CF\u8EAB\uFF0C\u771F\u8BA9\u4EBA\u610F\u60F3\u4E0D\u5230\u3002\u300D\u51CC\u96C5\u7434\u8F7B\u7B11\u7740\u8BF4\u9053\u3002\u5979\u4E0A\u8EAB\u7A7F\u7740\u4E00\u4EF6\u7EC7\u9526\u534E\u670D\uFF0C\u5B9D\u84DD\u8272\u7684\u7EB9\u9970\u4E0B\uFF0C\u9732\u51FA\u660E\u9EC4\u8272\u7684\u5E95\u9526\uFF0C\u8272\u6CFD\u534E\u4E3D\u4E4B\u6781\u3002</p><p>\u8863\u9886\u8FB9\u7F18\u7EE3\u7740\u9ED1\u8272\u7684\u6CE2\u7EB9\uFF0C\u9888\u4E2D\u9576\u7740\u4E00\u4E2A\u5C0F\u5C0F\u7684\u7389\u6263\uFF0C\u886C\u5F97\u4FEE\u957F\u7684\u7C89\u9888\u5176\u767D\u5982\u96EA\u3002\u675F\u7740\u5BBD\u5E26\u7684\u8170\u95F4\u60AC\u7740\u4E00\u53EA\u4E94\u5F69\u9999\u56CA\uFF0C\u4E0B\u8EAB\u662F\u4E00\u6761\u6E56\u7EFF\u8272\u7684\u62FD\u5730\u957F\u88D9\uFF0C\u914D\u7740\u5979\u9AD8\u96C5\u7684\u6C14\u5EA6\uFF0C\u66F4\u663E\u5F97\u96CD\u5BB9\u534E\u8D35\u3002</p><p>\u51CC\u96C5\u7434\u662F\u626E\u505A\u6765\u4E0A\u9999\u7684\u8C6A\u95E8\u8D35\u5987\uFF0C\u9F99\u6714\u5219\u62B1\u7740\u4E00\u4E2A\u72ED\u957F\u7684\u5305\u88F9\uFF0C\u8DDF\u5728\u5E08\u5A18\u8EAB\u540E\uFF0C\u5C31\u50CF\u4E00\u4E2A\u7709\u6E05\u76EE\u79C0\u7684\u5C0F\u53AE\u3002\u671B\u7740\u5E08\u5A18\u7684\u80CC\u5F71\uFF0C\u9F99\u6714\u624B\u5FC3\u9ECF\u4E4E\u4E4E\u53C8\u6E7F\u53C8\u51B7\uFF0C\u5F53\u65E5\u5256\u5F00\u9759\u83BA\u59B9\u59B9\u8EAB\u4F53\u65F6\uFF0C\u4ED6\u4E5F\u6CA1\u6709\u5982\u6B64\u7D27\u5F20\u3002</p><p>\u51CC\u96C5\u7434\u5C31\u50CF\u4E00\u4E2A\u6765\u4E0A\u9999\u7684\u8C6A\u95E8\u8D35\u5987\uFF0C\u7D20\u624B\u4EA4\u63E1\u653E\u5728\u8EAB\u524D\uFF0C\u88D9\u88FE\u8F7B\u6446\uFF0C\u8FC8\u7740\u7EC6\u7F13\u7684\u6B65\u5B50\uFF0C\u5728\u4F5B\u5802\u524D\u6B3E\u6B3E\u800C\u884C\u3002\u7F8E\u76EE\u6D41\u8F6C\u95F4\u5149\u8292\u95EA\u52A8\uFF0C\u770B\u4F3C\u4E0D\u7ECF\u610F\u56DB\u5904\u6D41\u89C8\uFF0C\u5176\u5B9E\u5468\u56F4\u7684\u4E00\u4E3E\u4E00\u52A8\u90FD\u672A\u9003\u8FC7\u5979\u7684\u773C\u775B\u3002</p><p>\u5230\u4E86\u51C0\u4FEE\u5802\uFF0C\u9F99\u6714\u4E0A\u524D\u6084\u58F0\u8BF4\u4E86\u51E0\u53E5\uFF0C\u90A3\u4E24\u540D\u5C3C\u59D1\u4E00\u8FB9\u7A3D\u9996\u884C\u793C\uFF0C\u4E00\u8FB9\u8BF7\u4E24\u4EBA\u8FDB\u53BB\u3002\u51CC\u96C5\u7434\u89C1\u4E24\u5C3C\u6B66\u529F\u5E73\u5E73\uFF0C\u4E5F\u672A\u653E\u5728\u5FC3\u4E0A\uFF0C\u63D0\u8D77\u88D9\u88FE\u8DE8\u5165\u62F1\u95E8\u3002</p><p>\u968F\u7740\u5999\u82B1\u5E08\u592A\u7A7F\u8FC7\u957F\u957F\u7684\u752C\u9053\uFF0C\u770B\u5230\u9690\u5982\u5EB5\u5185\u6697\u85CF\u7684\u534E\u5802\uFF0C\u51CC\u96C5\u7434\u4E0D\u7981\u76EE\u9732\u8BB6\u8272\u3002\u5999\u82B1\u5E08\u592A\u89E3\u91CA\u9053\uFF1A\u300C\u8FD9\u672C\u662F\u524D\u671D\u79BB\u5BAB\uFF0C\u7687\u5BB6\u65BD\u820D\u6765\u4F5C\u4E86\u5E99\u5B87\u3002\u56E0\u592A\u8FC7\u534E\u5962\uFF0C\u6050\u60F9\u6765\u975E\u8BAE\uFF0C\u655D\u5EB5\u4E00\u5411\u672A\u66FE\u542F\u7528\uFF0C\u65E5\u524D\u65B9\u4E08\u5927\u5E08\u5230\u6B64\uFF0C\u4FBF\u6682\u5C45\u6B64\u5904\u3002\u300D</p><p>\u5F53\u65F6\u5357\u5317\u4F5E\u4F5B\u6210\u98CE\uFF0C\u7687\u65CF\u738B\u516C\u51FA\u5BB6\u4E3A\u50E7\u4E5F\u4E0D\u5728\u5C11\u6570\uFF0C\u65BD\u820D\u79BB\u5BAB\u4E4B\u4E3E\u867D\u7136\u7F55\u89C1\u5374\u4E5F\u4E0D\u4E4F\u5176\u4F8B\u3002\u542C\u5230\u8FD9\u756A\u89E3\u91CA\uFF0C\u51CC\u96C5\u7434\u4FBF\u5373\u604D\u7136\uFF0C\u6697\u9053\u9690\u5982\u5EB5\u58F0\u52BF\u4E0D\u51E1\u3002</p><p>\u6BBF\u5185\u9648\u8BBE\u5982\u6545\uFF0C\u53EA\u662F\u73E0\u5E18\u5185\u653E\u7740\u4E00\u5F20\u84B2\u56E2\uFF0C\u4E00\u540D\u72EC\u81C2\u50E7\u4EBA\u80CC\u5BF9\u7740\u623F\u95E8\uFF0C\u76D8\u819D\u800C\u5750\uFF0C\u6B63\u6572\u7740\u6728\u9C7C\u4F4E\u58F0\u5FF5\u8BF5\u7740\u4F5B\u7ECF\u3002</p><p>\u51CC\u96C5\u7434\u4E0A\u524D\u65BD\u4E86\u4E00\u793C\uFF0C\u8BF4\u9053\uFF1A\u300C\u5C0F\u5973\u5B50\u53C2\u89C1\u5927\u5E08\u3002\u300D</p><p>\u6CAE\u6E20\u5927\u5E08\u8D77\u8EAB\u9053\uFF1A\u300C\u51CC\u5973\u4FA0\u4EB2\u4E34\u9669\u5883\uFF0C\u8001\u8872\u656C\u4F69\u3002\u300D</p><p>\u5999\u82B1\u5E08\u592A\u5949\u4E0A\u8336\u6C34\uFF0C\u6084\u65E0\u58F0\u606F\u5730\u9000\u4E86\u4E0B\u53BB\u3002\u6CAE\u6E20\u5927\u5E08\u8138\u8272\u9634\u90C1\uFF0C\u4E3E\u676F\u9053\uFF1A\u300C\u8BF7\u3002\u300D</p><p>\u51CC\u96C5\u7434\u4E0D\u4FBF\u63A8\u8F9E\uFF0C\u63ED\u5F00\u7897\u76D6\uFF0C\u6D45\u6D45\u996E\u4E86\u4E00\u53E3\uFF0C\u7136\u540E\u653E\u4E0B\u8336\u753B\uFF0C\u95EE\u9053\uFF1A\u300C\u5927\u5E08\u4FE1\u4E2D\u8BF4\u63A2\u5F97\u7389\u51CC\u9704\u7684\u4E0B\u843D\uFF0C\u4E0D\u77E5\u9704\u59B9\u59B9\u73B0\u5728\u4F55\u5904\uFF1F\u300D</p><p>\u6CAE\u6E20\u5927\u5E08\u7709\u5934\u6DF1\u9501\uFF0C\u53F9\u9053\uFF1A\u300C\u8BF7\u51CC\u5973\u4FA0\u7565\u5750\u7247\u523B\uFF0C\u8001\u8872\u53BB\u8BF7\u6DF3\u4E8E\u5973\u4FA0\u51FA\u6765\u76F8\u89C1\u3002\u300D</p><p>\u51CC\u96C5\u7434\u671B\u7740\u4ED6\u8FDC\u53BB\u7684\u80CC\u5F71\uFF0C\u5A25\u7709\u7F13\u7F13\u76B1\u8D77\u3002\u7247\u523B\u540E\uFF0C\u5979\u6A31\u5507\u4E00\u5F20\uFF0C\u5410\u51FA\u4E00\u53E3\u6C34\u7BAD\uFF0C\u7136\u540E\u8FC5\u901F\u4ECE\u8170\u95F4\u7684\u9999\u56CA\u91CC\u53D6\u51FA\u4E24\u679A\u4E5D\u534E\u5251\u6D3E\u7684\u907F\u6BD2\u4E39\uFF0C\u9012\u7ED9\u9F99\u6714\u8BA9\u4ED6\u670D\u4E0B\uFF0C\u5C0F\u58F0\u9053\uFF1A\u300C\u8336\u6C34\u6709\u4E9B\u4E0D\u59A5\uFF0C\u6B64\u5904\u7EDD\u975E\u5584\u5730\u3002\u4E00\u4F1A\u513F\u4F60\u7D27\u8DDF\u7740\u5E08\u5A18\uFF0C\u5343\u4E07\u4E0D\u53EF\u4E71\u8D70\u3002\u300D</p><p>\u9F99\u6714\u53EA\u89C1\u8FC7\u5E08\u5A18\u6148\u7231\u5F97\u751A\u81F3\u6709\u4E9B\u5A46\u5988\u7684\u6837\u5B50\uFF0C\u6CA1\u60F3\u5230\u5979\u4F1A\u5982\u6B64\u7CBE\u7EC6\uFF0C\u7ADF\u7136\u8FDE\u6CAE\u6E20\u5927\u5E08\u5938\u53E3\u8BF4\u65E0\u8272\u65E0\u5473\u7684\u5931\u795E\u6563\u4E5F\u80FD\u4E00\u773C\u89C6\u7834\u3002\u5E08\u5A18\u7684\u6B66\u529F\u4ED6\u77E5\u4E4B\u751A\u8BE6\uFF0C\u5C31\u7B97\u6CAE\u6E20\u5927\u5E08\u662F\u9760\u771F\u672C\u9886\u5F53\u4E0A\u7075\u9E6B\u5BFA\u7684\u65B9\u4E08\uFF0C\u60F3\u7559\u4E0B\u7434\u5251\u53CC\u4FA0\u4E5F\u4E0D\u5BB9\u6613\u3002</p><p>\u51CC\u96C5\u7434\u4ECE\u5305\u88F9\u4E2D\u53D6\u51FA\u82B1\u5F71\u5251\uFF0C\u5C06\u7476\u7434\u8D1F\u5728\u80CC\u4E0A\uFF0C\u62C9\u7740\u9F99\u6714\u98D8\u8EAB\u63A0\u4E0A\u6A2A\u6881\u3002\u5979\u51DD\u795E\u503E\u542C\u7247\u523B\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u6BBF\u4E0A\u6709\u4EBA\u628A\u5B88\uFF0C\u51FA\u53BB\u65F6\u5343\u4E07\u5C0F\u5FC3\u6697\u5668\u3002\u300D\u60F3\u4E86\u60F3\uFF0C\u53C8\u628A\u9999\u56CA\u4EA4\u7ED9\u9F99\u6714\uFF0C\u300C\u82E5\u4ED6\u4EEC\u65BD\u653E\u8FF7\u70DF\uFF0C\u5C31\u53D6\u4E00\u679A\u670D\u4E0B\u3002\u300D\u51CC\u96C5\u7434\u6697\u81EA\u540E\u6094\uFF0C\u4E0D\u8BE5\u8F7B\u4FE1\u6CAE\u6E20\u5927\u5E08\uFF0C\u7ED3\u679C\u8EAB\u9677\u9669\u5730\uFF0C\u4E07\u4E00\u6714\u513F\u6709\u4E2A\u95EA\u5931\u53EF\u600E\u4E48\u5F97\u4E86\uFF1F</p><p>\u9F99\u6714\u63A5\u8FC7\u9999\u56CA\uFF0C\u4FCA\u8138\u731B\u7136\u6DA8\u7EA2\u3002\u4ED6\u6697\u6697\u5438\u4E86\u53E3\u6C14\uFF0C\u7A33\u4F4F\u5FC3\u795E\uFF0C\u6C89\u58F0\u5E94\u9053\uFF1A\u300C\u5F92\u513F\u77E5\u9053\u4E86\u3002\u300D</p><p>\u6BBF\u5916\u4F20\u6765\u4E00\u9635\u8F7B\u5FAE\u7684\u811A\u6B65\u58F0\uFF0C\u51CC\u96C5\u7434\u82B3\u5FC3\u6697\u60CA\uFF0C\u6765\u8005\u81F3\u5C11\u6709\u4E8C\u5341\u4F59\u4EBA\uFF0C\u6B66\u529F\u4E0E\u4E5D\u534E\u5251\u6D3E\u540C\u8F88\u9AD8\u624B\u76F8\u4EFF\u3002\u96BE\u9053\u6CAE\u6E20\u5927\u5E08\u503E\u5927\u5B5A\u7075\u9E6B\u5BFA\u5168\u5BFA\u4E4B\u529B\uFF0C\u6765\u5BF9\u4ED8\u81EA\u5DF1\u5E08\u5F92\uFF1F\u4ED6\u4E3A\u4F55\u8981\u8FD9\u6837\u505A\uFF1F</p><p>\u300C\u5F6D\u300D\u7684\u4E00\u58F0\u5DE8\u54CD\uFF0C\u6BBF\u95E8\u88AB\u4E00\u53EA\u5DE8\u9524\u7838\u5F97\u7C89\u788E\uFF0C\u6728\u5C51\u7EB7\u98DE\u95F4\uFF0C\u624B\u6301\u5404\u79CD\u5175\u5203\u7684\u9ED1\u8863\u4EBA\u4E00\u6D8C\u800C\u51FA\uFF0C\u58F0\u52BF\u9A87\u4EBA\u3002</p><p>\u4E00\u7FA4\u9ED1\u8863\u4EBA\u4E2D\uFF0C\u6CAE\u6E20\u5927\u5E08\u7684\u5149\u5934\u5206\u5916\u9192\u76EE\u3002\u51CC\u96C5\u7434\u4E5F\u4E0D\u7B54\u8BDD\uFF0C\u4F7F\u51FA\u7A7F\u4E91\u8EAB\u6CD5\uFF0C\u9526\u71D5\u822C\u63A0\u5165\u4EBA\u7FA4\u3002\u8EAB\u5728\u534A\u7A7A\uFF0C\u82B1\u5F71\u5251\u4FBF\u6D12\u4E0B\u4E00\u7247\u94F6\u8F89\uFF0C\u5C06\u4E2D\u95F4\u90A3\u540D\u548C\u5C1A\u7F69\u5728\u5251\u4E0B\u3002</p><p>\u6CAE\u6E20\u5927\u5E08\u6CA1\u60F3\u5230\u5979\u4F1A\u4ECE\u6881\u4E0A\u63A0\u4E0B\uFF0C\u76EE\u5149\u4E00\u5BD2\uFF0C\u5DE6\u624B\u4ECE\u8896\u4E2D\u6325\u51FA\u4E00\u67C4\u6212\u5C3A\uFF0C\u67B6\u4F4F\u957F\u5251\uFF0C\u53F3\u8896\u6A2A\u626B\uFF0C\u671D\u51CC\u96C5\u7434\u8170\u95F4\u51FB\u53BB\u3002\u4ED6\u7684\u52B2\u529B\u6DF3\u539A\u5E73\u548C\uFF0C\u5F77\u4F5B\u662F\u6B63\u5B97\u7684\u4F5B\u95E8\u7384\u529F\u3002\u4F46\u5251\u5C3A\u76F8\u4EA4\uFF0C\u90A3\u67C4\u6212\u5C3A\u7ACB\u751F\u53D8\u5316\uFF0C\u7ADF\u7136\u4ECE\u5C3A\u7AEF\u5F39\u51FA\u4E00\u622A\u4E24\u5BF8\u957F\u7684\u94A2\u9488\uFF0C\u9488\u8EAB\u4E2D\u7A7A\uFF0C\u8FB9\u7F18\u84DD\u6C6A\u6C6A\u5B9B\u5982\u4E00\u53EA\u55DC\u8840\u7684\u6BD2\u7259\u3002</p><p>\u90A3\u4E9B\u9ED1\u8863\u4EBA\u5E94\u53D8\u5947\u901F\uFF0C\u65E9\u6709\u4EBA\u56DE\u624B\u622A\u6740\u3002\u51CC\u96C5\u7434\u5A07\u5412\u4E00\u58F0\uFF0C\u82B1\u5F71\u5251\u5239\u90A3\u95F4\u633D\u51FA\u4E03\u6735\u5251\u82B1\uFF0C\u5148\u6321\u4F4F\u6212\u5C3A\uFF0C\u4E00\u7FFB\u8155\u5288\u65AD\u6BD2\u9488\uFF0C\u63A5\u7740\u683C\u5F00\u5999\u82B1\u5E08\u592A\u7684\u77ED\u5251\uFF0C\u53C8\u5C06\u6CAE\u6E20\u5927\u5E08\u9707\u9000\u4E24\u6B65\uFF0C\u6700\u540E\u4E00\u5251\u5212\u65AD\u4E86\u4ED6\u7684\u8863\u8896\u3002</p><p>\u6CAE\u6E20\u5927\u5E08\u867D\u8D25\u4E0D\u4E71\uFF0C\u6296\u624B\u63B7\u51FA\u6212\u5C3A\uFF0C\u903C\u5F97\u51CC\u96C5\u7434\u56DE\u5251\u6321\u683C\uFF0C\u7136\u540E\u300C\u563F\u300D\u7684\u4E00\u58F0\u4F4E\u559D\uFF0C\u5DE6\u624B\u4F7F\u51FA\u5927\u5B5A\u7075\u9E6B\u5BFA\u7684\u7EDD\u6280\u53C2\u7985\u638C\uFF0C\u4E00\u638C\u62CD\u5728\u51CC\u96C5\u7434\u5251\u810A\u4E0A\u3002</p><p>\u51CC\u96C5\u7434\u5A07\u8EAF\u4E00\u65CB\uFF0C\u5728\u7A7A\u4E2D\u8F7B\u76C8\u5730\u5212\u4E86\u4E2A\u5708\u5B50\uFF0C\u843D\u5728\u6A2A\u6881\u4E0A\u3002\u5979\u7D20\u624B\u6301\u5251\u659C\u6307\u7740\u6CAE\u6E20\u5927\u5E08\uFF0C\u4E94\u5F69\u5149\u534E\u7684\u9526\u886B\u5185\u771F\u6C14\u9F13\u8361\uFF0C\u98D8\u98D8\u800C\u821E\uFF0C\u5F77\u4F5B\u4E00\u6735\u8000\u76EE\u7684\u8299\u84C9\u3002</p><p>\u76DB\u6012\u4E4B\u4E0B\uFF0C\u51CC\u96C5\u7434\u7389\u8138\u5FAE\u5FAE\u53D1\u7EA2\uFF0C\u522B\u5177\u7F8E\u6001\uFF0C\u5979\u6120\u9053\uFF1A\u300C\u6CAE\u6E20\u65B9\u4E08\uFF0C\u6211\u4E5D\u534E\u5251\u6D3E\u4E0E\u4F60\u5927\u5B5A\u7075\u9E6B\u5BFA\u4E00\u5357\u4E00\u5317\uFF0C\u7D20\u6765\u5E76\u65E0\u4EC7\u6028\uFF0C\u5927\u5E08\u4E3A\u4F55\u8BBE\u4E0B\u5708\u5957\uFF0C\u8BF1\u6211\u592B\u5987\u5165\u5F40\uFF1F\u300D</p><p>\u6CAE\u6E20\u5927\u5E08\u9762\u8272\u51DB\u7136\uFF0C\u6C89\u58F0\u9053\uFF1A\u300C\u5996\u5B7D\u8D25\u7C7B\u4EBA\u4EBA\u5F97\u800C\u8BDB\u4E4B\uFF01\u4E5D\u534E\u5251\u6D3E\u52FE\u7ED3\u661F\u6708\u6E56\uFF0C\u5984\u56FE\u4E3A\u7978\u6B66\u6797\uFF0C\u96BE\u9053\u8FD8\u60F3\u62B5\u8D56\u5417\uFF1F\u300D</p><p>\u51CC\u96C5\u7434\u6115\u7136\u9053\uFF1A\u300C\u65B9\u4E08\u4F55\u51FA\u6B64\u8A00\uFF1F\u300D\u8BF4\u7740\u5DE6\u624B\u4E00\u62AC\uFF0C\u6309\u5728\u9F99\u6714\u8155\u4E0A\uFF0C\u963B\u4F4F\u4ED6\u62D4\u5251\u7684\u52A8\u4F5C\uFF0C\u6717\u58F0\u9053\uFF1A\u300C\u6B64\u95F4\u5FC5\u6709\u8BEF\u4F1A\uFF0C\u5927\u5E08\u83AB\u4E0D\u662F\u53D7\u4E86\u5978\u4EBA\u6311\u62E8\uFF1F\u300D</p><p>\u9F99\u6714\u672C\u60F3\u7A81\u65BD\u6697\u7B97\uFF0C\u5374\u88AB\u5E08\u5A18\u8BEF\u8BA4\u4E3A\u662F\u8981\u4E0E\u654C\u4EBA\u53AE\u6740\uFF0C\u4ED6\u5FC3\u5934\u546F\u546F\u76F4\u8DF3\uFF0C\u521A\u624D\u52A8\u4F5C\u82E5\u662F\u518D\u5FEB\u5F97\u4E00\u5206\uFF0C\u5E08\u5A18\u53D1\u73B0\u4ED6\u62D4\u5251\u662F\u8981\u5BF9\u4ED8\u81EA\u5DF1\uFF0C\u4F1A\u4E0D\u4F1A\u626D\u65AD\u4ED6\u7684\u624B\u8155\u5462\uFF1F</p><p>\u6CAE\u6E20\u5927\u5E08\u72B9\u8C6B\u7247\u523B\uFF0C\u7F13\u7F13\u9053\uFF1A\u300C\u90A3\u4EBA\u6240\u8A00\u51FF\u51FF\u6709\u636E\uFF0C\u4E0D\u5BB9\u8001\u8872\u4E0D\u4FE1\uFF0C\u4F46\u8D24\u4F09\u4FEA\u4FA0\u540D\u5F70\u7740\u2026\u2026\u300D</p><p>\u300C\u90A3\u4EBA\u73B0\u5728\u4F55\u5904\uFF1F\u53EF\u5426\u4E0E\u6211\u5F53\u9762\u5BF9\u8D28\uFF1F\u300D</p><p>\u300C\u5C31\u5728\u6B64\u95F4\uFF0C\u8BF7\u51CC\u5973\u4FA0\u4E0B\u6765\u8BF4\u8BDD\u3002\u300D\u6CAE\u6E20\u5927\u5E08\u6446\u4E86\u6446\u624B\uFF0C\u547D\u4F17\u4EBA\u6536\u8D77\u5175\u5203\u3002</p><p>\u51CC\u96C5\u7434\u5237\u7684\u5408\u4E0A\u957F\u5251\uFF0C\u62C9\u7740\u9F99\u6714\u7EB5\u8EAB\u8DC3\u4E0B\u3002\u90A3\u4E9B\u9ED1\u8863\u4EBA\u6563\u5F00\u6210\u4E00\u4E2A\u4E94\u4E08\u7684\u5708\u5B50\uFF0C\u5C06\u4E24\u4EBA\u56E2\u56E2\u56F4\u4F4F\uFF0C\u53EA\u7B49\u5317\u9547\u795E\u5C06\u4E00\u58F0\u4EE4\u4E0B\uFF0C\u5C31\u8981\u4E0A\u524D\u52A8\u624B\u3002\u6CAE\u6E20\u5927\u5E08\u5374\u9053\uFF1A\u300C\u51CC\u5973\u4FA0\u8BF7\u968F\u6211\u6765\u3002\u300D\u8BF4\u7740\u7ED9\u5999\u82B1\u5E08\u592A\u4F7F\u4E86\u4E2A\u773C\u8272\uFF0C\u8BA9\u5979\u5728\u524D\u5F15\u8DEF\u3002\u5999\u82B1\u5FC3\u4E0B\u4F1A\u610F\uFF0C\u77E5\u9053\u662F\u8981\u628A\u5979\u5230\u6BBF\u540E\u7684\u5730\u7262\u4E2D\u3002\u90A3\u5730\u7262\u6DF1\u5728\u5730\u4E0B\u6570\u4E08\uFF0C\u5C3D\u662F\u82B1\u5C97\u5CA9\u780C\u6210\uFF0C\u5230\u4E86\u90A3\u91CC\uFF0C\u5C31\u662F\u4E5D\u534E\u53CC\u5251\u9F50\u81F3\uFF0C\u4E5F\u662F\u63D2\u7FC5\u96BE\u98DE\uFF0C\u9F99\u6714\u77E5\u9053\u6CAE\u6E20\u5927\u5E08\u662F\u5BF9\u5E08\u5A18\u7684\u6B66\u529F\u6DF1\u81EA\u5FCC\u60EE\uFF0C\u624D\u8FD9\u822C\u88C5\u8154\u4F5C\u52BF\uFF0C\u60F3\u5C06\u5979\u8BF1\u5165\u7EDD\u5730\u3002\u5F53\u4E0B\u53EA\u8BC8\u4F5C\u4E0D\u77E5\uFF0C\u968F\u4F17\u4EBA\u671D\u6BBF\u5916\u8D70\u53BB\u3002\u5FFD\u7136\u624B\u5FC3\u4E00\u52A8\uFF0C\u5E08\u5A18\u7528\u6307\u5C16\u5728\u4ED6\u638C\u4E2D\u5212\u9053\uFF1A\u300C\u897F\u9601\uFF0C\u623F\u9876\u3002\u300D\u9F99\u6714\u8BB6\u7136\u4E3E\u76EE\uFF0C\u53EA\u89C1\u51CC\u96C5\u7434\u7389\u5BB9\u65E0\u6CE2\uFF0C\u795E\u60C5\u6DD1\u96C5\u81EA\u82E5\u3002</p><p>\u6BBF\u95E8\u72ED\u7A84\uFF0C\u9ED1\u8863\u4EBA\u7684\u5305\u56F4\u5708\u4E0D\u5F97\u4E0D\u5206\u6210\u4E24\u622A\uFF0C\u5999\u82B1\u5E08\u592A\u548C\u4E94\u516D\u4E2A\u9ED1\u8863\u4EBA\u8D70\u5230\u6BBF\u5916\uFF0C\u6CAE\u6E20\u5927\u5E08\u548C\u4F59\u4E0B\u7684\u8FD8\u5728\u6BBF\u5185\u3002\u51CC\u96C5\u7434\u8D70\u5230\u95E8\u65C1\uFF0C\u5FFD\u7136\u6258\u4F4F\u9F99\u6714\u7684\u8170\u8EAB\uFF0C\u671D\u897F\u8FB9\u7684\u9601\u697C\u4F7F\u529B\u4E00\u63A8\uFF0C\u63A5\u7740\u7EA4\u624B\u5728\u8170\u95F4\u4E00\u62B9\uFF0C\u82B1\u5F71\u5251\u9535\u7136\u51FA\u9798\uFF0C\u5251\u82B1\u5B9B\u5982\u72C2\u98CE\u5439\u843D\u7684\u5BD2\u661F\uFF0C\u671D\u6BBF\u5185\u8BF8\u4EBA\u5C04\u53BB\u3002</p><p>\u6CAE\u6E20\u5C55\u626C\u4E00\u5411\u81EA\u8D1F\u7B97\u65E0\u9057\u7B56\uFF0C\u5374\u4E24\u6B21\u7740\u4E86\u51CC\u96C5\u7434\u7684\u9053\u513F\uFF0C\u7ADF\u88AB\u5979\u85C9\u673A\u9003\u51FA\u5927\u6BBF\uFF0C\u4ED6\u614C\u5FD9\u5927\u559D\u4E00\u58F0\uFF0C\u300C\u5978\u8D3C\uFF01\u679C\u7136\u3001\u679C\u7136\u662F\u4F5C\u8D3C\u5FC3\u865A\uFF01\u300D</p><p>\u51CC\u96C5\u7434\u56DE\u7738\u4E00\u7B11\uFF0C\u300C\u5927\u5E08\u5148\u662F\u8336\u4E2D\u4E0B\u836F\uFF0C\u6212\u5C3A\u5185\u53C8\u6697\u85CF\u6BD2\u9488\uFF0C\u8FD9\u7B49\u5351\u9119\u624B\u6BB5\u5C82\u662F\u5927\u5B5A\u7075\u9E6B\u5BFA\u65B9\u4E08\u7684\u4F5C\u4E3A\uFF1F\u6B64\u523B\u8FD8\u4EE5\u4E3A\u80FD\u9A97\u5F97\u8FC7\u6211\uFF0C\u672A\u514D\u4E5F\u592A\u5C0F\u770B\u96C5\u7434\u4E86\u3002\u300D</p><p>\u4E5D\u534E\u53CC\u5251\u679C\u7136\u540D\u4E0D\u865A\u4F20\uFF0C\u82B1\u5F71\u5251\u65BD\u5C55\u5F00\u6765\uFF0C\u53EA\u89C1\u94F6\u5149\u8000\u76EE\uFF0C\u5C06\u4F17\u4EBA\u963B\u5728\u6BBF\u5185\u3002\u7B49\u5999\u82B1\u5E08\u592A\u56DE\u8EAB\u6740\u6765\uFF0C\u51CC\u96C5\u7434\u5DF2\u7ECF\u523A\u4F24\u4E24\u4EBA\uFF0C\u98DE\u8EAB\u8DC3\u51FA\u91CD\u56F4\u3002</p><p>\u9601\u697C\u8DDD\u5927\u6BBF\u4E0D\u8FC7\u5341\u4E08\u5F00\u5916\uFF0C\u9F99\u6714\u501F\u529B\u8170\u8EAB\u4E00\u7FFB\uFF0C\u4FBF\u4E0A\u4E86\u6A90\u89D2\u3002\u53EA\u542C\u8EAB\u540E\u8863\u8882\u7834\u7A7A\u58F0\u54CD\uFF0C\u5E08\u5A18\u5DF2\u7ECF\u6446\u8131\u8FFD\u5175\uFF0C\u843D\u5728\u8EAB\u65C1\u3002</p><p>\u51CC\u96C5\u7434\u6276\u4F4F\u9F99\u6714\uFF0C\u4F4E\u58F0\u9053\uFF1A\u300C\u5EB5\u540E\u4FBF\u662F\u79E6\u6DEE\u6CB3\uFF0C\u6211\u4EEC\u4E14\u53BB\u90A3\u91CC\uFF0C\u8C05\u4ED6\u4EEC\u4E5F\u4E0D\u6562\u5728\u5149\u5929\u5316\u65E5\u4E0B\u52A8\u624B\u884C\u51F6\u3002\u7B49\u56DE\u5230\u4E5D\u534E\u77E5\u4F1A\u4E86\u4F60\u5E08\u7236\uFF0C\u5FC5\u5B9A\u8981\u4E0A\u6E05\u51C9\u5C71\u95EE\u4E2A\u660E\u767D\u3002\u300D</p><p>\u9F99\u6714\u5FC3\u6025\u5982\u711A\uFF0C\u6EE1\u662F\u51B7\u6C57\u7684\u624B\u638C\u7D27\u7D27\u63E1\u7740\u5251\u67C4\u3002\u5728\u8FD9\u4E48\u8FD1\u7684\u8DDD\u79BB\u7A81\u65BD\u6697\u7B97\uFF0C\u4ED6\u6709\u4E5D\u6210\u7684\u628A\u63E1\u80FD\u523A\u4F24\u51CC\u96C5\u7434\u3002\u4F46\u8BE5\u523A\u54EA\u91CC\u597D\u5462\u2026\u2026\u811A\u7B4B\uFF01\u9F99\u6714\u624B\u6307\u4E00\u7D27\uFF0C\u957F\u5251\u51FA\u9798\u5BF8\u8BB8\u3002</p><p>\u5FFD\u7136\u623F\u540E\u54CD\u8D77\u4E00\u58F0\u5A07\u7B11\uFF0C\u4E24\u4E2A\u62AB\u7740\u7EA2\u7EB1\u7684\u8273\u5973\u9B3C\u9B45\u822C\u51FA\u73B0\u5728\u9601\u4E0A\uFF0C\u4E00\u4E2A\u9053\uFF1A\u300C\u7434\u58F0\u82B1\u5F71\u597D\u5389\u5BB3\u54E6\uFF0C\u5C55\u626C\u54E5\u54E5\u52A8\u4E86\u8FD9\u4E48\u5927\u7684\u9635\u4ED7\u90FD\u7559\u4E0D\u4F4F\u4F60\u5462\u3002\u300D</p><p>\u53E6\u4E00\u4E2A\u55F2\u58F0\u9053\uFF1A\u300C\u597D\u4E45\u4E0D\u89C1\uFF0C\u51CC\u5973\u4FA0\u53C8\u7F8E\u4E86\u51E0\u5206\u5462\u3002\u4E0D\u77E5\u9053\u8FD8\u8BB0\u4E0D\u8BB0\u5F97\u54B1\u4EEC\u59D0\u59B9\u5462\uFF1F\u300D</p><p>\u4E24\u5973\u72B9\u5982\u5E76\u8482\u53CC\u83B2\uFF0C\u4E94\u5B98\u3001\u4F53\u6001\u5206\u6BEB\u4E0D\u5DEE\uFF0C\u6B63\u662F\u5341\u5E74\u524D\u5728\u6D1B\u9633\u9047\u5230\u7684\u90A3\u5BF9\u5B6A\u751F\u59D0\u59B9\u3002\u51CC\u96C5\u7434\u82B3\u5FC3\u6697\u6697\u6536\u7D27\uFF0C\u8FD9\u4E24\u540D\u8273\u5973\u6B66\u529F\u6781\u5F3A\uFF0C\u518D\u52A0\u4E0A\u6CAE\u6E20\u5927\u5E08\u548C\u5999\u82B1\u5E08\u592A\uFF0C\u8981\u8131\u8EAB\u5927\u4E0D\u5BB9\u6613\u3002</p><p>\u9F99\u6714\u5FC3\u91CC\u6BD4\u5E08\u5A18\u66F4\u4E3A\u7D27\u5F20\uFF0C\u751F\u6015\u4E24\u5973\u5F00\u53E3\u63ED\u7834\u4ED6\u7684\u8EAB\u4EFD\u3002\u5E78\u597D\u59D0\u59B9\u4FE9\u76EE\u5149\u779F\u4E5F\u4E0D\u779F\u4ED6\u4E00\u773C\uFF0C\u663E\u7136\u5DF2\u7ECF\u5FC3\u91CC\u6709\u6570\u3002</p><p>\u9690\u5982\u5EB5\u5360\u5730\u8FD1\u5343\u4EA9\uFF0C\u8FD9\u5EA7\u522B\u9662\u6DF1\u85CF\u5EB5\u5185\uFF0C\u524D\u6BBF\u56FA\u7136\u9999\u706B\u9F0E\u76DB\uFF0C\u6B64\u5904\u5374\u662F\u4E0E\u4E16\u9694\u7EDD\u3002\u7AD9\u5728\u91D1\u78A7\u8F89\u714C\u7684\u9601\u697C\u4E0A\uFF0C\u53EA\u770B\u5230\u91CD\u6A90\u53E0\u969C\uFF0C\u542C\u4E0D\u5230\u534A\u70B9\u4EBA\u58F0\u3002</p><p>\u51CC\u96C5\u7434\u795E\u60C5\u4F18\u96C5\u81EA\u82E5\uFF0C\u5FC3\u91CC\u5374\u5728\u82E6\u601D\u8131\u8EAB\u4E4B\u8BA1\u3002\u773C\u89C1\u59D0\u59B9\u4FE9\u773C\u4E2D\u5FAE\u73B0\u84DD\u5149\uFF0C\u663E\u7136\u5341\u5E74\u6765\u90AA\u529F\u5927\u8FDB\uFF0C\u8FDC\u975E\u6614\u65E5\u53EF\u6BD4\u3002\u800C\u8FD9\u4E9B\u5E74\u81EA\u5DF1\u4E00\u5E06\u98CE\u987A\uFF0C\u6CA1\u6709\u534A\u70B9\u6CE2\u6298\uFF0C\u800C\u4E14\u5168\u526F\u5FC3\u795E\u90FD\u653E\u5728\u6714\u513F\u8EAB\u4E0A\uFF0C\u4FEE\u884C\u4E0D\u514D\u6709\u4E9B\u677E\u61C8\uFF0C\u6B64\u6D88\u5F7C\u957F\u4E0B\uFF0C\u6B64\u6218\u51F6\u591A\u5409\u5C11\u2026\u2026</p><p>\u767D\u7389\u83BA\u7B11\u9053\uFF1A\u300C\u5F53\u65E5\u4E00\u89C1\uFF0C\u6211\u4EEC\u59D0\u59B9\u8FD9\u4E9B\u5E74\u6765\u5FF5\u5FF5\u4E0D\u5FD8\uFF0C\u4E00\u76F4\u60F3\u7740\u8981\u53BB\u4E5D\u534E\u62DC\u8BBF\u51CC\u5973\u4FA0\u3002\u53C8\u6015\u51CC\u5973\u4FA0\u8EAB\u4EFD\u9AD8\u8D35\uFF0C\u672A\u5FC5\u770B\u5F97\u8D77\u6211\u4EEC\u2026\u2026\u300D</p><p>\u767D\u7389\u9E42\u63D2\u53E3\u9053\uFF1A\u300C\u4E3A\u7740\u51CC\u5973\u4FA0\uFF0C\u6211\u59D0\u59D0\u60F3\u5F97\u80A0\u5B50\u90FD\u6253\u7ED3\u4E86\u5462\u3002\u542C\u8BF4\u51CC\u5973\u4FA0\u8981\u6765\u5EB5\u91CC\u4E0A\u9999\uFF0C\u6211\u4EEC\u59D0\u59B9\u5DF4\u5DF4\u5730\u8DD1\u4E86\u6765\uFF0C\u60F3\u4E00\u7779\u51CC\u5973\u4FA0\u7684\u98CE\u91C7\u2026\u2026\u300D\u5979\u62BF\u5634\u4E00\u7B11\uFF0C\u5996\u5A06\u5730\u8BF4\u9053\uFF1A\u300C\u51CC\u5973\u4FA0\u770B\u8D77\u6765\u8D8A\u53D1\u6ECB\u6DA6\u5462\uFF0C\u4E0D\u77E5\u9053\u62DC\u7684\u54EA\u5BB6\u83E9\u8428\uFF0C\u70B9\u4E86\u51E0\u67F1\u9999\u554A\uFF1F\u300D</p><p>\u51CC\u96C5\u7434\u7389\u6307\u5728\u5251\u950B\u4E0A\u4E00\u5F39\uFF0C\u4E00\u58F0\u51E4\u9E23\u4F3C\u7684\u6E05\u54CD\u538B\u8FC7\u4E86\u4E24\u5973\u5A9A\u6D6A\u7684\u58F0\u97F3\uFF0C\u300C\u5728\u4E0B\u4E0E\u4E24\u4F4D\u65E0\u6028\u65E0\u4EC7\uFF0C\u4E3A\u4F55\u5C61\u6B21\u76F8\u903C\uFF1F\u300D</p><p>\u767D\u7389\u83BA\u629A\u6478\u7740\u9888\u4E2D\u4E00\u9053\u7EC6\u7EC6\u7684\u7EA2\u75D5\uFF0C\u51B7\u7B11\u9053\uFF1A\u300C\u51CC\u5973\u4FA0\u771F\u662F\u8D35\u4EBA\u591A\u5FD8\u554A\uFF0C\u5F53\u5E74\u6211\u4EEC\u59D0\u59B9\u53EF\u8BF4\u8FC7\u8981\u597D\u751F\u62A5\u7B54\u60A8\u5462\u2026\u2026\u300D</p><p>\u60F3\u8D77\u5979\u4EEC\u5F53\u65F6\u7684\u6C61\u8A00\u79FD\u8BED\uFF0C\u51CC\u96C5\u7434\u7C89\u8138\u987F\u65F6\u6DA8\u7EA2\uFF0C\u5979\u4E00\u6311\u957F\u5251\uFF0C\u76F4\u523A\u767D\u7389\u83BA\u80A9\u5934\uFF0C\u5251\u5F0F\u53C8\u5FEB\u53C8\u6025\u3002</p><p>\u59D0\u59B9\u4FE9\u539F\u672C\u624B\u62C9\u7740\u624B\u5E76\u80A9\u800C\u884C\uFF0C\u51CC\u96C5\u7434\u5251\u98CE\u88AD\u6765\uFF0C\u4E24\u5973\u5404\u81EA\u98DE\u8EAB\u98D8\u5F00\u3002\u5979\u4EEC\u7EA2\u7EB1\u4E0B\u53EA\u7528\u4E86\u6761\u9C9C\u7EA2\u7684\u9526\u5E15\u63A9\u4F4F\u7C89\u8EAF\uFF0C\u767D\u99A5\u99A5\u7684\u9999\u808C\u768E\u7136\u80DC\u96EA\u3002\u6B64\u65F6\u51CC\u7A7A\u8DC3\u8D77\uFF0C\u8F7B\u7EB1\u98D8\u626C\u95F4\u7389\u4F53\u751F\u8F89\uFF0C\u90A3\u66FC\u5999\u9999\u8273\u7684\u8EAB\u59FF\uFF0C\u5B9B\u5982\u753B\u4E2D\u8273\u4E3D\u7684\u98DE\u5929\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u5728\u7A7A\u4E2D\u5212\u4E86\u4E2A\u5706\u5F27\uFF0C\u62A2\u5230\u51CC\u96C5\u7434\u8EAB\u4FA7\u3002\u51CC\u96C5\u7434\u770B\u51C6\u767D\u7389\u83BA\u843D\u811A\u4E4B\u5904\uFF0C\u82B1\u5F71\u5251\u84C4\u52BF\u5F85\u53D1\uFF0C\u5FFD\u7136\u94EE\u7684\u4E00\u58F0\u8F7B\u54CD\uFF0C\u767D\u7389\u83BA\u8EAB\u5F62\u7ADF\u7136\u5947\u8FF9\u822C\u5730\u505C\u5728\u534A\u7A7A\u3002</p><p>\u51CC\u96C5\u7434\u6B63\u81EA\u7EB3\u7F55\uFF0C\u5FFD\u7136\u5FC3\u751F\u8B66\u5146\uFF0C\u8FDE\u5FD9\u4E3E\u5251\u6321\u5728\u80F8\u524D\u3002\u957F\u5251\u731B\u7136\u4E00\u9707\uFF0C\u9669\u4E9B\u8131\u624B\u800C\u98DE\u3002\u5979\u4ED4\u7EC6\u770B\u53BB\uFF0C\u624D\u53D1\u73B0\u90A3\u662F\u4E00\u6761\u7EC6\u82E5\u53D1\u4E1D\u7684\u94F6\u7EBF\u3002</p><p>\u65B9\u624D\u767D\u6C0F\u59D0\u59B9\u4E24\u624B\u76F8\u63E1\uFF0C\u5C31\u62FF\u7740\u8FD9\u6761\u6781\u7EC6\u7684\u94F6\u4E1D\uFF0C\u501F\u52BF\u98D8\u5F00\u65F6\uFF0C\u4E24\u5973\u5404\u81EA\u64CE\u51FA\u77ED\u5251\uFF0C\u6697\u4E2D\u5374\u6492\u5F00\u94F6\u4E1D\uFF0C\u5404\u6267\u4E00\u7AEF\u6084\u65E0\u58F0\u606F\u5730\u671D\u51CC\u96C5\u7434\u5F53\u80F8\u5212\u6765\uFF0C\u624B\u6CD5\u6B79\u6BD2\u4E4B\u6781\u3002</p><p>\u300C\u5351\u9119\uFF01\u300D\u51CC\u96C5\u7434\u95F4\u4E0D\u82E5\u53D1\u4E4B\u9645\u6321\u5F00\u94F6\u4E1D\uFF0C\u7EA4\u8170\u4E00\u62E7\uFF0C\u9000\u5F00\u6570\u4E08\uFF0C\u6267\u5251\u4E0E\u4E24\u5973\u9065\u9065\u76F8\u5BF9\u3002</p><p>\u4E24\u5973\u7EA2\u5507\u540C\u65F6\u4E00\u6487\uFF0C\u300C\u54DF\uFF0C\u8FD9\u7B97\u4EC0\u4E48\u5351\u9119\u5462\uFF1F\u7B49\u51CC\u5973\u4FA0\u843D\u5230\u6211\u4EEC\u624B\u91CC\uFF0C\u518D\u8BA9\u4F60\u77E5\u9053\u4EC0\u4E48\u662F\u5351\u9119\u3001\u65E0\u803B\u3002\u300D</p><p>\u6B64\u65F6\u6CAE\u6E20\u5927\u5E08\u7B49\u4EBA\u5DF2\u7ECF\u62A2\u4E0A\u9601\u697C\uFF0C\u4ED6\u5BF9\u4E24\u5973\u65BD\u4E86\u4E00\u793C\uFF0C\u8BF4\u9053\uFF1A\u300C\u591A\u8C22\u4E24\u4F4D\u63F4\u624B\u3002\u300D\u4F3C\u4E4E\u767D\u6C0F\u59D0\u59B9\u5730\u4F4D\u8FD8\u5728\u4ED6\u4E4B\u4E0A\u3002</p><p>\u767D\u7389\u9E42\u751C\u7B11\u9053\uFF1A\u300C\u5C55\u626C\u54E5\u54E5\u4F55\u5FC5\u591A\u793C\uFF0C\u80FD\u628A\u51CC\u5973\u4FA0\u8BF3\u5230\u8FD9\u91CC\uFF0C\u6211\u4EEC\u59D0\u59B9\u8FD8\u8981\u591A\u8C22\u8C22\u4F60\u5462\u3002\u4E0D\u8FC7\u8BDD\u53EF\u8BF4\u524D\u5934\uFF0C\u529F\u52B3\u7B97\u4F60\u7684\uFF0C\u4EBA\u53EF\u7B97\u6211\u4EEC\u59D0\u59B9\u7684\u3002\u300D</p><p>\u6CAE\u6E20\u5C55\u626C\u82E6\u7B11\u9053\uFF1A\u300C\u5C5E\u4E0B\u4E3A\u4E86\u4E5D\u534E\u5251\u6D3E\u8D39\u5C3D\u82E6\u5FC3\uFF0C\u597D\u4E0D\u5BB9\u6613\u624D\u5C06\u51CC\u5973\u4FA0\u8BF7\u5230\u6B64\u5730\uFF0C\u62A4\u6CD5\u2026\u2026\u300D</p><p>\u300C\u6CAE\u6E20\u5927\u5E08\u8D35\u4E3A\u56DB\u9547\u795E\u5C06\u4E4B\u4E00\uFF0C\u4F4D\u9AD8\u6743\u91CD\uFF0C\u7ADF\u7136\u81EA\u79F0\u5C5E\u4E0B\uFF0C\u5C0F\u5973\u5B50\u600E\u4E48\u6562\u5F53\u5462\uFF1F\u300D\u767D\u7389\u9E42\u8BED\u542B\u8BA5\u523A\uFF0C\u5979\u4E0E\u59D0\u59D0\u5E76\u5217\u4E3A\u661F\u6708\u6E56\u4E09\u62A4\u6CD5\u4E4B\u4E00\uFF0C\u4EE5\u7D2B\u5FAE\u4E3A\u53F7\uFF0C\u5728\u6559\u5185\u5730\u4F4D\u6781\u9AD8\u3002\u56DB\u9547\u795E\u5C06\u867D\u7136\u7565\u900A\u4E00\u7EA7\uFF0C\u4F46\u5404\u636E\u4E00\u65B9\uFF0C\u6743\u52BF\u6E32\u8D6B\uFF0C\u90A3\u79CD\u5A01\u98CE\u5374\u8FDC\u5728\u62A4\u6CD5\u4E4B\u4E0A\uFF0C\u59D0\u59B9\u4FE9\u65E9\u5DF2\u5FC3\u6709\u4E0D\u6EE1\u3002\u5979\u77A5\u4E86\u51CC\u96C5\u7434\u4E00\u773C\uFF0C\u7B11\u9053\uFF1A\u300C\u5C55\u626C\u54E5\u54E5\u5BF9\u51CC\u5973\u4FA0\u4EF0\u6155\u5DF2\u4E45\uFF0C\u600E\u53CA\u6211\u4EEC\u59D0\u59B9\u76F8\u601D\u4E4B\u82E6\u5462\uFF1F\u300D</p><p>\u6CAE\u6E20\u5927\u5E08\u8FD8\u5F85\u518D\u8BF4\uFF0C\u767D\u7389\u83BA\u5DF2\u7ECF\u4E00\u6296\u94F6\u4E1D\uFF0C\u95EA\u8EAB\u671D\u51CC\u96C5\u7434\u653B\u53BB\uFF0C\u51B7\u559D\u9053\uFF1A\u300C\u5148\u64D2\u4E0B\u8FD9\u8D31\u4EBA\u518D\u4F5C\u5546\u8BAE\u3002\u300D</p><p>\u767D\u7389\u9E42\u8D34\u7740\u5C4B\u810A\u5E73\u5E73\u98DE\u6765\uFF0C\u5979\u85C9\u7740\u94F6\u4E1D\u4F20\u6765\u7684\u52B2\u529B\uFF0C\u540E\u53D1\u5148\u81F3\uFF0C\u77ED\u5251\u9752\u5149\u5927\u76DB\uFF0C\u76F4\u903C\u51CC\u96C5\u7434\u8170\u8179\u3002\u51CC\u96C5\u7434\u4E0E\u5979\u7684\u77ED\u5251\u4EA4\u4E86\u4E24\u62DB\uFF0C\u773C\u89C1\u94F6\u4E1D\u9F50\u819D\u5212\u6765\uFF0C\u5FFD\u7136\u5DE6\u624B\u4E00\u626C\uFF0C\u7389\u6307\u4E0A\u98DE\u51FA\u51E0\u6761\u7EC6\u5F26\uFF0C\u7F20\u4F4F\u94F6\u4E1D\uFF0C\u987A\u52BF\u63A0\u4E0B\u3002</p><p>\u5979\u521A\u624D\u6084\u6084\u53D6\u4E0B\u7434\u5F26\u7ED5\u5728\u6307\u4E0A\uFF0C\u6B64\u65F6\u4E00\u7ECF\u65BD\u5C55\uFF0C\u7ACB\u6536\u5947\u5175\u4E4B\u6548\u3002\u767D\u7389\u9E42\u731D\u4E0D\u53CA\u9632\u4E0B\uFF0C\u63E1\u7740\u94F6\u4E1D\u7684\u53F3\u624B\u88AB\u4E94\u6839\u7434\u5F26\u63A5\u8FDE\u51FB\u4E2D\uFF0C\u867D\u7136\u5E26\u7740\u5929\u8695\u624B\u5957\uFF0C\u624B\u6307\u4E5F\u75BC\u5982\u5200\u5272\uFF0C\u53EA\u5F97\u677E\u5F00\u94F6\u4E1D\u3002</p><p>\u51CC\u96C5\u7434\u4E0B\u624B\u518D\u4E0D\u5BB9\u60C5\uFF0C\u65BD\u51FA\u4E5D\u534E\u7EDD\u6280\uFF0C\u82B1\u5F71\u5251\u5149\u534E\u56DB\u5C04\uFF0C\u786C\u5C06\u767D\u6C0F\u59D0\u59B9\u7684\u5408\u51FB\u5C3D\u6570\u6321\u4F4F\uFF0C\u540C\u65F6\u5DE6\u624B\u4E94\u6307\u5FFD\u6311\u5FFD\u62B9\uFF0C\u4E94\u6839\u7434\u5F26\u5229\u5203\u822C\u4E0A\u4E0B\u98DE\u821E\u3002\u767D\u7389\u9E42\u4E00\u4E0D\u7559\u795E\uFF0C\u811A\u8E1D\u4FBF\u88AB\u7434\u5F26\u7F20\u4F4F\uFF0C\u867D\u7136\u8FD0\u529F\u9707\u65AD\u7434\u5F26\uFF0C\u8E1D\u95F4\u5DF2\u7ECF\u9C9C\u8840\u6DCB\u6F13\u3002</p><p>\u51CC\u96C5\u7434\u5FC3\u4E0B\u5FE7\u6025\uFF0C\u5979\u53EA\u662F\u62A2\u5F97\u4E00\u65F6\u5148\u673A\u624D\u52C9\u5F3A\u5360\u4E86\u4E0A\u98CE\uFF0C\u767D\u6C0F\u59D0\u59B9\u914D\u5408\u95F4\u7CBE\u5999\u5F02\u5E38\uFF0C\u518D\u7F20\u6597\u4E0B\u53BB\u81EA\u5DF1\u7EDD\u96BE\u6491\u8FC7\u767E\u62DB\u3002\u5FFD\u7136\u95F4\u80CC\u540E\u8F6C\u6765\u5175\u5203\u4EA4\u9E23\u58F0\uFF0C\u6714\u513F\u5DF2\u7ECF\u4E0E\u654C\u4EBA\u52A8\u8D77\u624B\u6765\u3002</p><p>\u8F6C\u773C\u4F17\u4EBA\u5DF2\u4EA4\u624B\u5341\u4F59\u62DB\uFF0C\u51CC\u96C5\u7434\u89C1\u6CAE\u6E20\u5927\u5E08\u6362\u4E86\u4E00\u67C4\u91D1\u521A\u6775\u7F13\u6B65\u903C\u6765\uFF0C\u7ACB\u5373\u5251\u62DB\u4E00\u7D27\uFF0C\u5C06\u767D\u6C0F\u59D0\u59B9\u903C\u5F00\u4E24\u6B65\uFF0C\u7136\u540E\u4EF0\u8EAB\u5411\u540E\u7FFB\u53BB\uFF0C\u53EB\u9053\uFF1A\u300C\u6714\u513F\uFF01\u300D\u9F99\u6714\u4E00\u54AC\u7259\uFF0C\u4F38\u624B\u6293\u4F4F\u5E08\u5A18\u7684\u7EA4\u638C\uFF0C\u968F\u7740\u5979\u4E00\u540C\u671D\u9AD8\u5899\u63A0\u53BB\u3002</p><p>\u4EBA\u5728\u534A\u7A7A\uFF0C\u9F99\u6714\u5FFD\u7136\u5168\u8EAB\u4E00\u9707\uFF0C\u63A5\u7740\u677E\u5F00\u624B\uFF0C\u76F4\u76F4\u671D\u5730\u4E0A\u843D\u53BB\u3002\u51CC\u96C5\u7434\u82B1\u5BB9\u5931\u8272\uFF0C\u4E0D\u53CA\u591A\u60F3\u4FBF\u6C14\u606F\u6025\u8F6C\uFF0C\u5A07\u8EAF\u98DE\u901F\u4E0B\u6C89\uFF0C\u8DDF\u7740\u9F99\u6714\u4E00\u540C\u843D\u5728\u5730\u4E0A\u3002</p><p>\u6714\u513F\u4F3C\u4E4E\u662F\u88AB\u6697\u5668\u5C04\u4E2D\uFF0C\u5728\u5730\u4E0A\u4E00\u4E2A\u7FFB\u6EDA\uFF0C\u4F0F\u8EAB\u4F4E\u5598\u4E0D\u5DF2\u3002\u51CC\u96C5\u7434\u8FDE\u5FD9\u62D6\u4F4F\u9F99\u6714\u7684\u624B\u81C2\uFF0C\u53EB\u9053\uFF1A\u300C\u6714\u513F\uFF01\u300D</p><p>\u9F99\u6714\u624B\u81C2\u4E00\u62E7\uFF0C\u7FFB\u8155\u6263\u5728\u5979\u7684\u8109\u95E8\u4E0A\uFF0C\u529B\u9053\u5927\u5F97\u5F02\u4E4E\u5BFB\u5E38\u3002\u51CC\u96C5\u7434\u534A\u8EAB\u9178\u9EBB\uFF0C\u82B1\u5F71\u5251\u9535\u7136\u843D\u5730\u3002\u5979\u6025\u5FD9\u5438\u4E86\u53E3\u6C14\uFF0C\u8FD0\u529F\u9707\u5F00\u4ED6\u7684\u624B\u6307\uFF0C\u60F6\u6025\u5730\u53EB\u9053\uFF1A\u300C\u6714\u513F\uFF0C\u662F\u6211\uFF01\u4F60\u9192\u9192\uFF01\u300D</p><p>\u9F99\u6714\u52C9\u5F3A\u62AC\u8D77\u5934\uFF0C\u8138\u8272\u4E00\u7247\u60E8\u767D\u3002\u51CC\u96C5\u7434\u987E\u4E0D\u4E0A\u770B\u5F92\u513F\u4F24\u5728\u4F55\u5904\uFF0C\u7ACB\u5373\u6325\u638C\u9707\u788E\u7A97\u6237\uFF0C\u62B1\u7740\u9F99\u6714\u7FFB\u5165\u5BA4\u5185\u3002</p>',97);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
