import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_26" tabindex="-1"><a class="header-anchor" href="#_26" aria-hidden="true">#</a> 26</h1><p>\u4E00\u4E2A\u660E\u8273\u7684\u5C11\u5973\u5E2D\u5730\u800C\u5750\uFF0C\u96EA\u5AE9\u7684\u8EAB\u5B50\u501A\u5728\u4E00\u5F20\u51B0\u51B7\u7684\u94C1\u5236\u5211\u5E8A\u4E0A\uFF0C\u5149\u4EAE\u7684\u957F\u79C0\u53D1\u62AB\u5728\u80A9\u5934\uFF0C\u4E24\u6761\u96EA\u767D\u7684\u7389\u817F\u4EA4\u53E0\u7740\u653E\u5728\u4E00\u8D77\uFF0C\u66F2\u7EBF\u67D4\u7F8E\u52A8\u4EBA\u3002\u5979\u4E00\u6761\u7EA4\u7F8E\u7684\u7389\u81C2\u642D\u5728\u5E8A\u6CBF\uFF0C\u8EAB\u540E\u6EE1\u662F\u72DE\u5389\u7684\u5211\u5177\uFF0C\u53E6\u4E00\u53EA\u7EC6\u767D\u7684\u7389\u624B\u5219\u653E\u5728\u8EAB\u524D\uFF0C\u629A\u5F04\u7740\u817F\u4E0A\u5C11\u5973\u7684\u67D4\u9888\uFF0C\u5A07\u7F8E\u7684\u8138\u5E9E\u4E0A\u5E26\u7740\u4E00\u4E1D\u6349\u6478\u4E0D\u5B9A\u7684\u795E\u60C5\u3002</p><p>\u592D\u592D\u4E56\u4E56\u4F0F\u5728\u9759\u989C\u817F\u95F4\uFF0C\u4F38\u51FA\u7EA2\u6DA6\u7684\u9999\u820C\uFF0C\u4E00\u70B9\u4E00\u70B9\u7EC6\u81F4\u5730\u8214\u8210\u7740\u5979\u7684\u8089\u68D2\u3002\u90A3\u79CD\u67D4\u987A\u9A6F\u670D\u7684\u6837\u5B50\uFF0C\u5C31\u50CF\u4E00\u53EA\u53EF\u7231\u7684\u5C0F\u732B\u54AA\uFF0C\u5BF9\u4E3B\u4EBA\u65E2\u4F9D\u604B\u53C8\u987A\u4ECE\u3002</p><p>\u300C\u5C0F\u8D31\u8D27\uFF0C\u521A\u624D\u5F00\u5FC3\u5417\uFF1F\u300D</p><p>\u300C\u592D\u592D\u5F00\u5FC3\u6B7B\u4E86\u2026\u2026\u300D</p><p>\u9759\u989C\u5728\u5979\u8138\u4E0A\u626D\u4E86\u4E00\u628A\uFF1A\u300C\u88AB\u63D2\u5C41\u773C\u513F\u8FD8\u8FD9\u4E48\u5F00\u5FC3\uFF0C\u771F\u662F\u4E2A\u4E0B\u8D31\u7684\u5C0F\u9A9A\u8D27\u3002\u300D</p><p>\u592D\u592D\u8138\u8D34\u5728\u9759\u989C\u817F\u4E0A\uFF0C\u78E8\u64E6\u7740\u4E1D\u7EF8\u822C\u5149\u6ED1\u7684\u808C\u80A4\uFF0C\u4E56\u4E56\u8BF4\u9053\uFF1A\u300C\u592D\u592D\u662F\u4E00\u6761\u53C8\u9A9A\u53C8\u8D31\u7684\u5C0F\u6BCD\u72D7\uFF0C\u597D\u59D0\u59D0\uFF0C\u4F60\u7684\u5927\u8089\u68D2\u597D\u5389\u5BB3\uFF0C\u592D\u592D\u7231\u6B7B\u5B83\u4E86\u3002\u300D\u8BF4\u7740\u5F20\u5F00\u5C0F\u5634\uFF0C\u5728\u8089\u68D2\u4E0A\u4EB2\u4E86\u4E00\u4E0B\u3002</p><p>\u9759\u989C\u7B11\u7740\u62AC\u8D77\u7389\u817F\uFF0C\u653E\u5728\u5979\u7684\u8170\u81C0\u4E0A\uFF0C\u300C\u661F\u6708\u6E56\u7684\u5973\u4EBA\u90FD\u662F\u8FD9\u4E48\u6DEB\u8D31\u5417\uFF1F\u300D</p><p>\u592D\u592D\u6709\u4E9B\u5BB3\u7F9E\u5730\u8BF4\u9053\uFF1A\u300C\u4EBA\u5BB6\u4E0D\u80FD\u7B97\u5973\u4EBA\u5566\u2026\u2026\u4E0D\u8FC7\u661F\u6708\u6E56\u7684\u5973\u4EBA\u6BD4\u592D\u592D\u8FD8\u6DEB\u8D31\u5462\u3002\u300D</p><p>\u300C\u5662\uFF1F\u5C0F\u516C\u4E3B\u4E5F\u662F\u5417\uFF1F\u300D</p><p>\u592D\u592D\u6487\u4E86\u6487\u5634\uFF0C\u300C\u5979\u662F\u6700\u4E0D\u8981\u8138\u7684\u3002\u8FD9\u4E48\u5C0F\u5C31\u8DDF\u7537\u4EBA\u4E0A\u5E8A\u4E86\u3002\u300D\u8BF4\u7740\u5979\u6BD4\u4E86\u4E2A\u9AD8\u5EA6\uFF0C\u770B\u4E0A\u53BB\u53EA\u6709\u4E94\u516D\u5C81\u7684\u6837\u5B50\u3002</p><p>\u9759\u989C\u6CA1\u60F3\u5230\u90A3\u5C0F\u516C\u4E3B\u4F1A\u8FD9\u4E48\u6DEB\u8361\uFF0C\u53C8\u95EE\u9053\uFF1A\u300C\u90A3\u6155\u5BB9\u9F99\u7684\u5973\u4EBA\u5462\uFF1F\u300D</p><p>\u300C\u5BAB\u91CC\u6240\u6709\u7684\u5973\u4EBA\u90FD\u662F\u4ED6\u7684\u554A\u3002\u300D</p><p>\u300C\u6211\u8BB0\u5F97\u6709\u4E9B\u4E0D\u4E00\u6837\u5427\u3002\u300D</p><p>\u592D\u592D\u660E\u767D\u8FC7\u6765\uFF0C\u300C\u4F60\u8BF4\u90A3\u4E24\u4E2A\u8D31\u8D27\u554A\uFF1F\u90A3\u4E24\u4E2A\u662F\u6700\u4E0B\u8D31\u7684\u81ED\u5A4A\u5B50\uFF01\u6BD4\u4E16\u4E0A\u6700\u4E0B\u8D31\u7684\u5A4A\u5B50\u8FD8\u8D31\uFF01\u300D</p><p>\u9759\u989C\u626B\u4E86\u5979\u4E00\u773C\uFF0C\u89C1\u5979\u7C89\u816E\u6DA8\u7EA2\uFF0C\u663E\u7136\u662F\u6C14\u607C\u4E4B\u6781\uFF0C\u300C\u542C\u8BF4\u5979\u4EEC\u8981\u56DE\u6765\u4E86\uFF1F\u300D</p><p>\u592D\u592D\u6CA1\u7CBE\u6253\u91C7\u5730\u8BF4\uFF1A\u300C\u662F\u90A3\u4E2A\u8001\u8D31\u8D27\u8981\u56DE\u6765\u3002\u300D</p><p>\u300C\u542C\u8BF4\u662F\u5C0F\u516C\u4E3B\u4EB2\u81EA\u53BB\u63A5\u5462\uFF0C\u4EC0\u4E48\u65F6\u5019\u56DE\u6765\u554A\uFF1F\u300D</p><p>\u300C\u8BF4\u662F\u4E09\u4E2A\u6708\uFF0C\u5230\u516D\u6708\u95F4\u624D\u80FD\u56DE\u6765\u3002\u300D</p><p>\u9759\u989C\u76EE\u5149\u95EA\u95EA\u5730\u76EF\u7740\u592D\u592D\uFF0C\u300C\u4F4F\u5F97\u8FD9\u4E48\u8FDC\uFF1F\u300D</p><p>\u592D\u592D\u4E00\u8138\u4E0D\u5C51\u5730\u8BF4\u9053\uFF1A\u300C\u90A3\u662F\u8981\u808F\u5C0F\u516C\u4E3B\u7684\u8D31\u5C44\u5462\u3002\u51B5\u4E14\u8FD8\u8981\u79CD\u6811\u2026\u2026\u300D</p><p>\u300C\u79CD\u6811\uFF1F\u300D\u9759\u989C\u60F3\u8D77\u5916\u9762\u7684\u571F\u5751\u3002</p><p>\u300C\u662F\u6015\u6652\u7740\u90A3\u4E2A\u8001\u8D31\u8D27\uFF0C\u624D\u8981\u79CD\u6811\u7684\u3002\u300D</p><p>\u300C\u5662\uFF0C\u6155\u5BB9\u9F99\u5BF9\u5979\u53EF\u5BA0\u7231\u5F97\u5F88\u554A\u3002\u300D</p><p>\u300C\u53EF\u4E0D\u662F\u5417\uFF0C\u300D\u592D\u592D\u51B7\u7B11\u9053\uFF1A\u300C\u628A\u5979\u7684\u7B4B\u90FD\u62BD\u4E86\u5462\u3002\u300D</p><p>\u9759\u989C\u4E00\u6123\uFF0C\u6B63\u60F3\u7EC6\u95EE\uFF0C\u592D\u592D\u5374\u76B1\u7740\u5C0F\u8138\u9053\uFF1A\u300C\u4E0D\u8981\u8BF4\u4ED6\u4EEC\u4E86\uFF0C\u597D\u8BA8\u538C\u7684\u3002\u300D</p><p>\u8BF4\u7740\u6367\u4F4F\u9759\u989C\u7684\u9633\u5177\uFF0C\u7528\u751C\u5F97\u53D1\u817B\u7684\u58F0\u97F3\u4F01\u6C42\u9053\uFF1A\u300C\u597D\u59D0\u59D0\uFF0C\u518D\u5E72\u592D\u592D\u4E00\u6B21\u597D\u4E0D\u597D\uFF1F\u300D</p><p>\u4E24\u4EBA\u6574\u6574\u4E00\u5929\u6CA1\u6709\u79BB\u5F00\u77F3\u5BA4\uFF0C\u9759\u989C\u633A\u7740\u8089\u68D2\uFF0C\u628A\u592D\u592D\u5E72\u5F97\u6B7B\u53BB\u6D3B\u6765\uFF0C\u9AD8\u6F6E\u8FED\u8D77\uFF0C\u6700\u540E\u592D\u592D\u88AB\u5E72\u5F97\u7CBE\u75B2\u529B\u5C3D\uFF0C\u8FDE\u8BF4\u8BDD\u7684\u529B\u6C14\u90FD\u6CA1\u6709\uFF0C\u8FD8\u88AB\u9759\u989C\u6402\u7740\u5C41\u80A1\uFF0C\u786C\u63D2\u5F97\u55B7\u5C04\u51FA\u6765\u3002</p><p>\u7B2C\u4E8C\u5929\uFF0C\u5F53\u5A07\u8273\u5982\u521D\u7684\u9759\u989C\u8D70\u51FA\u77F3\u5BA4\u65F6\uFF0C\u4F17\u5973\u90FD\u671B\u7740\u6D51\u8EAB\u762B\u8F6F\u7684\u592D\u62A4\u6CD5\u6123\u4F4F\u4E86\u3002\u4EE5\u5F80\u88AB\u592D\u62A4\u6CD5\u53EC\u53BB\u4F8D\u5BDD\u7684\u5973\u5B50\u51E0\u4E4E\u90FD\u88AB\u5979\u5F04\u5F97\u8D77\u4E0D\u4E86\u8EAB\uFF0C\u80FD\u50CF\u9759\u989C\u8FD9\u6837\u6B65\u5C65\u4ECE\u5BB9\u7684\u7EDD\u65E0\u4EC5\u6709\u3002\u770B\u5979\u4EEC\u4E24\u4E2A\u7684\u6837\u5B50\uFF0C\u5012\u50CF\u662F\u592D\u62A4\u6CD5\u88AB\u8FD9\u4E2A\u521A\u5165\u5BAB\u7684\u5C11\u5973\u8E42\u8E8F\u4E86\u3002</p><p>\u5728\u6E29\u6CC9\u4E2D\u6CE1\u4E86\u4E00\u4E2A\u65F6\u8FB0\uFF0C\u592D\u592D\u8138\u4E0A\u624D\u56DE\u590D\u4E86\u8840\u8272\u3002\u5979\u8F9B\u82E6\u5730\u7741\u5F00\u773C\u775B\uFF0C\u4E00\u770B\u5230\u65C1\u8FB9\u7684\u5C11\u5973\uFF0C\u4FBF\u4F9D\u504E\u8FC7\u53BB\uFF0C\u5624\u549B\u7740\u8BF4\u9053\uFF1A\u300C\u597D\u59D0\u59D0\uFF0C\u8BA9\u592D\u592D\u5F53\u4F60\u7684\u5C0F\u6BCD\u72D7\u597D\u4E0D\u597D\uFF1F\u300D</p><p>\u770B\u5230\u8FD9\u4E2A\u5A9A\u8273\u7684\u5C0F\u5996\u7CBE\u6B7B\u5FC3\u8E4B\u5730\u987A\u4ECE\u4E86\u81EA\u5DF1\uFF0C\u9759\u989C\u4E0D\u7981\u7B11\u4E86\u8D77\u6765\uFF0C\u300C\u54EA\u600E\u4E48\u6210\u5462\uFF1F\u5974\u5A62\u521A\u521A\u5165\u6559\uFF0C\u53EA\u662F\u4E2A\u65E0\u804C\u65E0\u4F4D\u7684\u8D31\u5974\u7F62\u4E86\uFF0C\u60A8\u53EF\u662F\u795E\u6559\u7684\u62A4\u6CD5\u554A\u3002\u300D</p><p>\u592D\u592D\u6025\u9053\uFF1A\u300C\u4EBA\u5BB6\u4E0D\u5F53\u62A4\u6CD5\u4E86\uFF0C\u8BA9\u59D0\u59D0\u6765\u505A\u62A4\u6CD5\uFF0C\u592D\u592D\u5C31\u8DDF\u7740\u59D0\u59D0\uFF0C\u59D0\u59D0\u4EC0\u4E48\u65F6\u5019\u9AD8\u5174\uFF0C\u5C31\u53EF\u4EE5\u63D2\u4EBA\u5BB6\u7684\u5C41\u773C\u513F\u2026\u2026\u300D</p><p>\u9759\u989C\u652F\u8D77\u7389\u988C\uFF0C\u300C\u4F60\u80FD\u505A\u5F97\u4E86\u4E3B\u5417\uFF1F\u300D</p><p>\u60F3\u5230\u6559\u5185\u638C\u6743\u7684\u662F\u5C0F\u516C\u4E3B\uFF0C\u592D\u592D\u4E0D\u7531\u6CC4\u4E86\u6C14\uFF0C\u5FFD\u7136\u95F4\u8111\u4E2D\u7075\u5149\u4E00\u95EA\uFF0C\u300C\u6211\u6709\u529E\u6CD5\u4E86\uFF01\u300D</p><p>\u77F3\u58C1\u4E0A\u4F20\u51FA\u6DD9\u6DD9\u7684\u6D41\u6C34\u58F0\uFF0C\u6C34\u96FE\u7F2D\u7ED5\u7684\u6E05\u6C60\u5185\uFF0C\u5E76\u80A9\u4F0F\u7740\u4E24\u5177\u51DD\u8102\u822C\u7684\u7389\u4F53\u3002\u4E24\u5973\u8DB4\u5728\u6C60\u6CBF\uFF0C\u767D\u5AE9\u7684\u5A07\u8EAF\u82B1\u74E3\u95F4\u6F02\u5728\u6C34\u4E0A\u3002\u4ECE\u80CC\u540E\u770B\u6765\uFF0C\u67D4\u9888\u3001\u7C89\u80CC\u3001\u7EA4\u8170\u3001\u7389\u817F\u3001\u79C0\u8DB3\u65E0\u4E0D\u7CBE\u81F4\u5982\u96D5\uFF0C\u7F8E\u8273\u7EDD\u4F26\uFF0C\u8BA9\u4EBA\u5FCD\u4E0D\u4F4F\u4E00\u4EB2\u9999\u6CFD\u3002</p><p>\u4F46\u6700\u5F15\u4EBA\u6CE8\u76EE\u7684\uFF0C\u8FD8\u662F\u90A3\u4E24\u53EA\u6B3A\u971C\u8D5B\u96EA\u7684\u7F8E\u81C0\u3002\u968F\u7740\u6C34\u6CE2\u7684\u8361\u6F3E\uFF0C\u4E24\u53EA\u534A\u6D78\u5728\u6E29\u6CC9\u7684\u96EA\u81C0\u67D4\u67D4\u8D77\u4F0F\u7740\uFF0C\u90A3\u5706\u6DA6\u7684\u66F2\u7EBF\u6BD4\u6C34\u6CE2\u66F4\u52A0\u67D4\u7F8E\u3002\u6CBE\u7740\u6C34\u73E0\u7684\u81C0\u8089\u53C8\u767D\u53C8\u4EAE\uFF0C\u5AE9\u5AE9\u7684\uFF0C\u5F77\u4F5B\u80FD\u6324\u51FA\u6C34\u6765\u3002\u6563\u53D1\u7740\u767D\u96FE\u7684\u6E05\u6C34\u4E00\u8361\u4E00\u8361\uFF0C\u4ECE\u4E24\u6761\u96EA\u767D\u7684\u5927\u817F\u4E4B\u95F4\u6D8C\u5230\u81C0\u4E0B\uFF0C\u6765\u56DE\u51B2\u5237\u7740\u5149\u6D01\u7684\u81C0\u7F1D\u3002</p><p>\u9759\u989C\u652F\u7740\u7C89\u9888\uFF0C\u6C34\u7075\u7075\u7684\u5999\u76EE\u56DB\u5904\u6253\u91CF\uFF0C\u5BF9\u8FD9\u4E2A\u5F00\u51FF\u4E8E\u5C71\u8179\u4E4B\u4E2D\u7684\u6E29\u6C60\u6697\u81EA\u8BB6\u53F9\u3002\u661F\u6708\u6E56\u7684\u52BF\u529B\u8FDC\u6BD4\u81EA\u5DF1\u60F3\u50CF\u7684\u66F4\u4E3A\u5E9E\u5927\uFF0C\u5343\u4F59\u5E74\u7684\u79EF\u7D2F\u679C\u7136\u975E\u540C\u5BFB\u5E38\u3002\u5979\u4E0D\u77E5\u9053\u661F\u6708\u6E56\u51E0\u5EA6\u4E2D\u8870\uFF0C\u5982\u4ECA\u6B63\u5728\u6E10\u6E10\u6062\u590D\u5143\u6C14\uFF0C\u8FDC\u4E0D\u53CA\u6781\u76DB\u65F6\u7684\u8F89\u714C\u3002</p><p>\u592D\u592D\u8DB4\u5728\u4E00\u65C1\uFF0C\u5174\u594B\u5730\u8BF4\u9053\uFF1A\u300C\u795E\u6559\u7B49\u7EA7\u68EE\u4E25\uFF0C\u62A4\u6CD5\u4E0B\u9762\u8FD8\u6709\u795E\u5C06\u3001\u957F\u8001\u3001\u4F9B\u5949\u3001\u4E03\u661F\u4F8D\u8005\u3001\u5BBF\u536B\u3001\u9999\u4E3B\u3001\u8235\u4E3B\uFF0C\u4E00\u822C\u5973\u5B50\u5728\u6559\u5185\u90FD\u662F\u5145\u5F53\u6DEB\u5974\uFF0C\u60F3\u5F53\u4E2A\u5E73\u5E38\u6559\u4F17\u4E5F\u96BE\u6BD4\u767B\u5929\u3002\u4E0D\u8FC7\u73B0\u5728\u5374\u4E0D\u540C\u7684\uFF0C\u773C\u4E0B\u6B63\u6709\u4E2A\u673A\u4F1A\uFF0C\u53EA\u8981\u80FD\u529E\u5F97\u8BA9\u5C0F\u516C\u4E3B\u6EE1\u610F\uFF0C\u5C31\u662F\u5F53\u4E0A\u62A4\u6CD5\u4E5F\u4E0D\u662F\u4E0D\u53EF\u80FD\u7684\u3002\u300D</p><p>\u300C\u6709\u8FD9\u4E48\u597D\u7684\u4E8B\u5417\uFF1F\u8BF4\u6765\u542C\u542C\u3002\u300D</p><p>\u592D\u592D\u9053\uFF1A\u300C\u8FD8\u8BB0\u5F97\u90A3\u6B21\u5728\u76CA\u5DDE\u592D\u592D\u662F\u627E\u8C01\u7684\u5417\uFF1F\u300D</p><p>\u9759\u989C\u5F53\u7136\u8BB0\u5F97\uFF0C\u300C\u6DF3\u4E8E\u68E0\u3002\u5979\u600E\u4E48\u6837\u4E86\uFF1F\u300D</p><p>\u592D\u592D\u7B11\u4E86\u8D77\u6765\uFF0C\u300C\u5DF2\u7ECF\u5904\u7406\u4E86\uFF0C\u597D\u6F02\u4EAE\u5462\u3002\u5C0F\u516C\u4E3B\u662F\u60F3\u805A\u9F50\u4E86\u6DF3\u4E8E\u5BB6\u7684\u4E09\u6735\u540D\u82B1\uFF0C\u505A\u4E2A\u597D\u73A9\u7684\u4E1C\u897F\u3002\u9526\u6D77\u68E0\u548C\u7389\u51CC\u9704\u5DF2\u7ECF\u6536\u5728\u5BAB\u91CC\uFF0C\u8FD8\u5269\u4E0B\u4E00\u4E2A\u7F8E\u743C\u7476\u2014\u2014\u59D0\u59D0\u542C\u8BF4\u8FC7\u5427\uFF1F\u300D</p><p>\u9759\u989C\u5FAE\u5FAE\u7B11\u9053\uFF1A\u300C\u542C\u8BF4\u8FC7\u7684\u3002\u300D</p><p>\u300C\u53EA\u8981\u59D0\u59D0\u80FD\u628A\u5979\u5F04\u5230\u5BAB\u91CC\uFF0C\u6536\u62FE\u5F97\u6F02\u6F02\u4EAE\u4EAE\uFF0C\u5C0F\u516C\u4E3B\u4E00\u9AD8\u5174\uFF0C\u80AF\u5B9A\u8981\u63D0\u5347\u4F60\u7684\u3002\u82E5\u662F\u59D0\u59D0\u7684\u529F\u52B3\u591F\u5927\uFF0C\u592D\u592D\u5C31\u628A\u62A4\u6CD5\u8BA9\u7ED9\u59D0\u59D0\u6765\u505A\u2026\u2026\u300D</p><p>\u6DF3\u4E8E\u7476\uFF0C\u90A3\u4E2A\u91D1\u4E1D\u96C0\u822C\u65E0\u5FE7\u65E0\u8651\u7684\u7F8E\u5987\u3002\u5B9B\u9675\u6C88\u6C0F\u7684\u5973\u4E3B\u4EBA\uFF0C\u4E0E\u81EA\u5DF1\u6E0A\u6E90\u6781\u6DF1\u7684\u7476\u963F\u59E8\u2026\u2026\u8FD9\u662F\u4E00\u4E2A\u63A5\u8FD1\u5C0F\u516C\u4E3B\u7684\u597D\u673A\u4F1A\u5462\u3002\u300C\u597D\u554A\uFF0C\u4F60\u8BF4\u8981\u600E\u4E48\u505A\uFF0C\u59D0\u59D0\u6765\u60F3\u4E3B\u610F\u3002\u300D</p><p>\u6E29\u6696\u7684\u6CC9\u6C34\u5728\u8EAB\u4E0A\u8361\u6F3E\u7740\uFF0C\u7F8E\u8273\u7684\u8EAF\u4F53\u4E2D\u90A3\u9897\u82B3\u5FC3\u8C61\u94C1\u77F3\u822C\u51B7\u786C\u3002\u6CA1\u6709\u4E1D\u6BEB\u6E29\u60C5\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u56DE\u5230\u5EFA\u5EB7\uFF0C\u5DF2\u7ECF\u662F\u56DB\u6708\u5929\u6C14\u3002\u767D\u6C0F\u59D0\u59B9\u89C1\u9759\u989C\u8FD9\u4E48\u5FEB\u5C31\u5B89\u7136\u5F52\u6765\uFF0C\u4E0D\u7531\u559C\u51FA\u671B\u5916\u3002\u9759\u989C\u6CA1\u6709\u544A\u8BC9\u5979\u4EEC\u81EA\u5DF1\u4E0E\u592D\u592D\u6240\u53D1\u751F\u7684\u4E8B\uFF0C\u53EA\u8BF4\u5230\u4E86\u5BAB\u4E2D\u4FBF\u63A5\u5230\u547D\u4EE4\uFF0C\u8981\u5148\u56DE\u4E5D\u534E\u3002</p><p>\u767D\u7389\u83BA\u601D\u7D22\u9053\uFF1A\u300C\u5C55\u626C\u5E26\u7740\u7384\u6B66\u4E03\u5BBF\u5230\u4E86\u5EFA\u5EB7\uFF0C\u4E94\u884C\u5802\u4E5F\u6709\u957F\u8001\u5728\u6B64\u51FA\u6CA1\uFF0C\u542C\u8BF4\u51E4\u795E\u5C06\u4E5F\u8981\u5E26\u7740\u6731\u96C0\u4E03\u5BBF\u5317\u4E0A\u2026\u2026\u96BE\u9053\u5C0F\u516C\u4E3B\u662F\u8981\u5BF9\u4E5D\u534E\u5251\u6D3E\u4E0B\u624B\u4E86\u5417\uFF1F\u300D</p><p>\u767D\u7389\u9E42\u76B1\u8D77\u7709\u5934\uFF0C\u300C\u4E0D\u4F1A\u5427\uFF0C\u5C0F\u516C\u4E3B\u5BF9\u6C5F\u6E56\u4E0A\u7684\u4E8B\u6781\u5C11\u7406\u4F1A\uFF0C\u600E\u4E48\u5F97\u6709\u5174\u81F4\u53BB\u8DDF\u4E5D\u534E\u5251\u6D3E\u4E3A\u96BE\uFF1F\u300D</p><p>\u9759\u989C\u77E5\u9053\u5C0F\u516C\u4E3B\u5BF9\u4E5D\u534E\u5251\u6D3E\u6CA1\u6709\u4EC0\u4E48\u610F\u56FE\uFF0C\u5012\u662F\u6CAE\u6E20\u5C55\u626C\u4E00\u76F4\u56FE\u8C0B\u8BA9\u4ED6\u7684\u5927\u5B5A\u7075\u9E6B\u5BFA\u91CD\u593A\u6B66\u6797\u7B2C\u4E00\u7684\u4F4D\u7F6E\u3002\u5F53\u4E0B\u53EA\u9053\uFF1A\u300C\u6211\u60F3\u5E26\u5E08\u5A18\u4E00\u8D77\u56DE\u53BB\u3002\u300D</p><p>\u767D\u6C0F\u59D0\u59B9\u95FB\u8A00\u63A9\u53E3\u5403\u5403\u5A07\u7B11\u9053\uFF1A\u300C\u4F60\u90A3\u5E08\u5A18\u53EF\u4E56\u5F97\u5F88\u5462\u3002\u8BA9\u59D0\u59D0\u5E26\u4F60\u53BB\u770B\u770B\u3002\u300D</p><p>\u9759\u989C\u8138\u4E0A\u795E\u60C5\u81EA\u82E5\uFF0C\u5FC3\u5185\u6697\u60F3\u7740\uFF1A\u8FD9\u534A\u4E2A\u591A\u6708\uFF0C\u4E0D\u77E5\u9053\u5E08\u5A18\u5728\u8FD9\u4E2A\u6DEB\u90AA\u7684\u5730\u65B9\u53D8\u6210\u4EC0\u4E48\u6837\u5B50\u4E86\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u51FA\u4E86\u4FA7\u9662\uFF0C\u5374\u6CA1\u6709\u5F80\u5730\u7262\u53BB\uFF0C\u800C\u662F\u9886\u7740\u9759\u989C\u76F4\u63A5\u5230\u4E86\u6CAE\u6E20\u592B\u5987\u6240\u5728\u7684\u5927\u6BBF\u3002\u767D\u7389\u83BA\u7B11\u9053\uFF1A\u300C\u5999\u82B1\u521A\u521A\u624D\u628A\u4F60\u5E08\u5A18\u53EB\u4E86\u53BB\uFF0C\u8FD9\u4F1A\u513F\u6B63\u80FD\u8D76\u4E0A\u770B\u597D\u620F\u5462\u3002\u300D</p><p>\u9759\u989C\u52C9\u5F3A\u9732\u51FA\u4E00\u4E2A\u82E6\u6DA9\u7684\u7B11\u5BB9\uFF0C\u300C\u90A3\u591A\u8C22\u59D0\u59D0\u4E86\u3002\u300D</p><p>\u4E09\u5C42\u7684\u5927\u6BBF\u7A7A\u8361\u8361\uFF0C\u5C31\u8DDF\u90A3\u5929\u5979\u548C\u5E08\u5A18\u6765\u7684\u65F6\u5019\u4E00\u6837\u3002\u9694\u7740\u7A97\u68C2\uFF0C\u80FD\u770B\u5230\u4E00\u4E2A\u98CE\u97F5\u6B63\u6D53\u7684\u7F8E\u5987\u8D64\u6761\u6761\u8DEA\u5728\u6BEF\u4E0A\u3002</p><p>\u4E00\u8EAB\u7F01\u8863\u7684\u5973\u5C3C\u76D8\u819D\u5750\u5728\u6905\u4E2D\uFF0C\u50E7\u670D\u4E0B\u9732\u51FA\u4E00\u622A\u767D\u5149\u5149\u7684\u5927\u817F\uFF0C\u5999\u82B1\u5E08\u592A\u4E00\u624B\u62FF\u7740\u8336\u7897\uFF0C\u4ECE\u773C\u89D2\u779F\u7740\u9762\u524D\u7684\u7F8E\u5987\uFF0C\u7B11\u76C8\u76C8\u6CA1\u6709\u8BF4\u8BDD\u3002</p><p>\u51CC\u96C5\u7434\u5782\u7740\u5934\uFF0C\u4E24\u819D\u5E76\u7D27\uFF0C\u76F4\u76F4\u8DEA\u5728\u5730\u4E0A\u3002\u4E0D\u77E5\u5979\u670D\u7528\u4E86\u4EC0\u4E48\u836F\u7269\uFF0C\u96EA\u767D\u7684\u7389\u4F53\u6108\u53D1\u5149\u6DA6\uFF0C\u5C31\u50CF\u88AB\u65E0\u6570\u624B\u638C\u6469\u6332\u8FC7\u7684\u7389\u50CF\u822C\u53C8\u6ED1\u53C8\u4EAE\u3002\u90A3\u5BF9\u4E30\u8174\u7684\u96EA\u4E73\u8038\u5728\u8EAB\u524D\uFF0C\u4E24\u53EA\u4E73\u5934\u989C\u8272\u6DF1\u4E86\u8BB8\u591A\u3002\u542C\u5230\u95E8\u5916\u7684\u811A\u6B65\u58F0\uFF0C\u5979\u60CA\u614C\u5730\u62AC\u8D77\u773C\uFF0C\u7EC6\u81F4\u5982\u753B\u7684\u7709\u5B87\u95F4\u51DD\u7740\u4E00\u7F15\u6325\u4E4B\u4E0D\u53BB\u7684\u51C4\u5A49\u3002</p><p>\u300C\u5A18\u2026\u2026\u300D\u5B9D\u513F\u8E52\u8DDA\u7740\u8D70\u8FC7\uFF0C\u5927\u7EA2\u8272\u7684\u809A\u515C\u6B6A\u5728\u4E00\u8FB9\uFF0C\u9732\u51FA\u80EF\u4E0B\u90A3\u6839\u7D2B\u9ED1\u8272\u7684\u8089\u68D2\u3002</p><p>\u770B\u5230\u90A3\u4E2A\u5B69\u7AE5\u62D6\u4E00\u6761\u6210\u4EBA\u5927\u5C0F\u7684\u9633\u5177\uFF0C\u7F8E\u5987\u8EAB\u5B50\u4E00\u98A4\uFF0C\u4E73\u5934\u987F\u65F6\u786C\u786C\u6311\u4E86\u8D77\u6765\uFF0C\u63A5\u7740\u8179\u4E0B\u4E00\u70ED\uFF0C\u79D8\u5904\u5DF2\u7ECF\u6E7F\u4E86\u3002</p><p>\u5973\u5C3C\u4F38\u51FA\u811A\u5C16\uFF0C\u6311\u8D77\u7F8E\u5987\u7684\u4E0B\u5DF4\u5DE6\u53F3\u7AEF\u8BE6\uFF0C\u7B11\u541F\u541F\u9053\uFF1A\u300C\u89C1\u5230\u6211\u513F\u5B50\u5F88\u9AD8\u5174\u5427\uFF1F\u300D</p><p>\u65E0\u8BBA\u662F\u8EAB\u4EFD\u3001\u6B66\u529F\u8FD8\u662F\u6C5F\u6E56\u4E2D\u7684\u5730\u4F4D\uFF0C\u4E5D\u534E\u5251\u6D3E\u7684\u7434\u58F0\u82B1\u5F71\u90FD\u8FDC\u5728\u5999\u82B1\u5E08\u592A\u4E4B\u4E0A\uFF0C\u4F46\u5728\u661F\u6708\u6E56\u7684\u6DEB\u5A01\u4E0B\uFF0C\u51CC\u96C5\u7434\u53EA\u80FD\u6EE1\u8138\u597C\u7EA2\u5730\u5C0F\u58F0\u5E94\u9053\uFF1A\u300C\u662F\u2026\u2026\u300D</p><p>\u300C\u597D\u4E56\u54E6\uFF0C\u602A\u4E0D\u5F97\u6211\u513F\u5B50\u559C\u6B22\u4F60\u5462\u2026\u2026\u300D\u5999\u82B1\u5E08\u592A\u6402\u4F4F\u513F\u5B50\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u5B9D\u513F\uFF0C\u8BA9\u5979\u5F53\u4F60\u7684\u8001\u5A46\u597D\u4E0D\u597D\uFF1F\u300D</p><p>\u300C\u8001\u5A46\u2026\u2026\u300D\u5B9D\u513F\u7FFB\u7740\u773C\u60F3\u4E86\u534A\u5929\uFF0C\u6447\u4E86\u6447\u5934\uFF0C\u53C8\u7528\u529B\u70B9\u8D77\u5934\u6765\uFF0C\u300C\u597D\u2026\u597D\u2026\u597D\u2026\u2026\u300D</p><p>\u5999\u82B1\u5E08\u592A\u7B11\u9053\uFF1A\u300C\u6211\u513F\u5B50\u8981\u5A36\u4F60\u5F53\u8001\u5A46\u5462\uFF0C\u51CC\u5A4A\u5B50\uFF0C\u4F60\u597D\u798F\u6C14\u554A\u3002\u300D</p><p>\u90A3\u5B69\u5B50\u4E0D\u6EE1\u5341\u5C81\uFF0C\u6A21\u6837\u53C8\u4E11\u53C8\u602A\uFF0C\u5C31\u50CF\u4E00\u53EA\u88AB\u4EBA\u8E29\u6241\u7684\u5C0F\u8001\u9F20\uFF0C\u504F\u53C8\u751F\u7684\u4EBA\u5F62\uFF0C\u8BA9\u4EBA\u770B\u4E00\u773C\u5C31\u89C9\u5F97\u6076\u5FC3\u3002\u51CC\u96C5\u7434\u8138\u4E0A\u6EE1\u662F\u96BE\u582A\u7684\u7F9E\u8272\uFF0C\u534A\u664C\u624D\u7EC6\u58F0\u8BF4\u9053\uFF1A\u300C\u591A\u8C22\u957F\u8001\u3002\u300D</p><p>\u5999\u82B1\u5E08\u592A\u8BA5\u8BEE\u9053\uFF1A\u300C\u8BE5\u53EB\u6211\u4EC0\u4E48\u554A\uFF1F\u300D</p><p>\u7F8E\u5987\u54AC\u7740\u7EA2\u5507\uFF0C\u7F8E\u76EE\u4E2D\u73E0\u6CEA\u76C8\u7136\uFF0C\u51B2\u7740\u8FD9\u4E2A\u6BD4\u81EA\u5DF1\u8FD8\u5C0F\u7740\u4E24\u5C81\u7684\u5C3C\u59D1\u5524\u4E86\u58F0\uFF1A\u300C\u5A46\u5A46\u2026\u2026\u300D</p><p>\u5B9D\u513F\u6447\u6447\u6643\u6643\u8D70\u4E86\u8FC7\u6765\uFF0C\u4ED6\u7AD9\u7740\u8FD8\u6CA1\u6709\u8DEA\u7740\u8EAB\u5B50\u7684\u51CC\u96C5\u7434\u9AD8\uFF0C\u4EF0\u7740\u8138\u50BB\u7B11\u9053\uFF1A\u300C\u8001\u5A46\u2026\u2026\u300D</p><p>\u770B\u5230\u4ED6\u62D6\u7740\u9F3B\u6D95\u3001\u53E3\u6C34\u7684\u4E11\u6001\uFF0C\u51CC\u96C5\u7434\u4E0D\u7981\u76B1\u8D77\u7709\u5934\uFF0C\u628A\u76EE\u5149\u79FB\u5230\u4E00\u8FB9\u3002\u5973\u5C3C\u67F3\u7709\u4E00\u6311\uFF0C\u5BD2\u58F0\u9053\uFF1A\u300C\u6211\u513F\u5B50\u53EB\u4F60\u5462\uFF01\u300D</p><p>\u51CC\u96C5\u7434\u7389\u8138\u65F6\u7EA2\u65F6\u767D\uFF0C\u6700\u540E\u7528\u8F7B\u5F97\u51E0\u4E4E\u542C\u4E0D\u5230\u7684\u58F0\u97F3\u5524\u9053\uFF1A\u300C\u592B\u541B\u2026\u2026\u300D</p><p>\u5999\u82B1\u5E08\u592A\u7B11\u9053\uFF1A\u300C\u4E56\u5AB3\u5987\uFF0C\u8DDF\u4F60\u8001\u516C\u597D\u751F\u4EB2\u70ED\u4E00\u756A\u3002\u300D</p><p>\u51CC\u96C5\u7434\u53EA\u597D\u5F20\u5F00\u624B\u81C2\uFF0C\u5C06\u8FD9\u4E2A\u8DB3\u4EE5\u4F5C\u81EA\u5DF1\u513F\u5B50\u7684\u5B69\u7AE5\u62B1\u5728\u6000\u91CC\uFF0C\u8BA9\u4ED6\u5728\u81EA\u5DF1\u4E30\u7F8E\u7684\u9999\u4E73\u4E0A\u53C8\u8214\u53C8\u54AC\uFF0C\u628A\u9F3B\u6D95\u3001\u53E3\u6C34\u6D82\u5F97\u6EE1\u4E73\u90FD\u662F\u3002</p><p>\u300C\u8001\u5A46\u2026\u2026\u9999\u2026\u2026\u300D</p><p>\u300C\u662F\uFF0C\u592B\u541B\u2026\u2026\u300D</p><p>\u5999\u82B1\u5E08\u592A\u7709\u82B1\u773C\u7B11\uFF0C\u300C\u6211\u513F\u5B50\u8FD8\u5C0F\uFF0C\u4F60\u8FD9\u5F53\u5AB3\u5987\u53EF\u8981\u591A\u6559\u6559\u4ED6\u554A\u3002\u300D</p><p>\u300C\u662F\uFF0C\u5A46\u5A46\u3002\u300D\u51CC\u96C5\u7434\u77E5\u9053\u5979\u662F\u8981\u81EA\u5DF1\u4E3B\u52A8\u8DDF\u5979\u513F\u5B50\u4EA4\u5408\uFF0C\u53EA\u597D\u4EF0\u8EAB\u8EBA\u5E73\uFF0C\u5F20\u5F00\u53CC\u817F\uFF0C\u8BA9\u90A3\u4E2A\u4E11\u602A\u7684\u5B69\u7AE5\u8DB4\u5728\u81EA\u5DF1\u817F\u95F4\uFF0C\u7136\u540E\u4E00\u624B\u5265\u5F00\u79D8\u5904\uFF0C\u4E00\u624B\u63E1\u4F4F\u4ED6\u7684\u9633\u5177\uFF0C\u671D\u8179\u4E0B\u9001\u53BB\u3002</p><p>\u5999\u82B1\u5E08\u592A\u4E0D\u60A6\u5730\u8BF4\u9053\uFF1A\u300C\u505A\u4E86\u8FD9\u4E48\u591A\u5E74\u638C\u95E8\u592B\u4EBA\uFF0C\u600E\u4E48\u4E00\u70B9\u90FD\u4E0D\u77E5\u9053\u5F53\u5AB3\u5987\u7684\u793C\u6570\uFF1F\u300D</p><p>\u51CC\u96C5\u7F9E\u5F97\u8033\u6839\u90FD\u7EA2\u900F\u4E86\uFF0C\u55EB\u5685\u534A\u664C\uFF0C\u624D\u7EA2\u7740\u8138\u5BF9\u7537\u5B69\u8BF4\uFF1A\u300C\u8BF7\u592B\u541B\u8DDF\u7434\u513F\u884C\u623F\u2026\u2026\u300D</p><p>\u300C\u4EC0\u4E48\u884C\u623F\uFF1F\u6211\u513F\u5B50\u542C\u4E0D\u61C2\u3002\u300D</p><p>\u51CC\u96C5\u7434\u53EA\u597D\u8BF4\u9053\uFF1A\u300C\u8BF7\u592B\u541B\u628A\u9633\u5177\u63D2\u5230\u2026\u2026\u63D2\u5230\u7434\u513F\u8FD9\u91CC\u2026\u2026\u6765\u5E72\u7434\u513F\u7684\u2026\u2026\u300D\u6700\u540E\u4E00\u4E2A\u5B57\uFF0C\u5979\u7EA2\u5507\u53EA\u52A8\u4E86\u52A8\uFF0C\u7F9E\u5F97\u53D1\u4E0D\u51FA\u58F0\u6765\u3002</p><p>\u300C\u597D\u4E86\uFF0C\u6211\u513F\u5B50\u4E5F\u7B49\u6025\u4E86\u5462\u3002\u4F60\u4EEC\u5C0F\u592B\u59BB\u8D76\u7D27\u6069\u7231\u4E00\u756A\uFF0C\u8BA9\u6211\u8FD9\u5F53\u5A46\u5A46\u7684\u4E5F\u9AD8\u5174\u9AD8\u5174\u3002\u300D</p><p>\u63E1\u7740\u90A3\u6839\u575A\u786C\u7684\u9633\u5177\uFF0C\u51CC\u96C5\u7434\u53EA\u89C9\u4E0B\u4F53\u9635\u9635\u53D1\u7D27\uFF0C\u9877\u523B\u95F4\uFF0C\u6DEB\u6DB2\u4FBF\u6EA2\u51FA\u7A74\u53E3\uFF0C\u6253\u6E7F\u4E86\u81C0\u4E0B\u7684\u5730\u6BEF\u3002\u5979\u7389\u817F\u5E73\u4E3E\u7740\u5411\u4E24\u4FA7\u5F20\u5F00\uFF0C\u4E0B\u4F53\u5FAE\u5FAE\u62AC\u8D77\uFF0C\u6446\u6210\u4FBF\u4E8E\u63D2\u5165\u7684\u89D2\u5EA6\uFF0C\u7EC6\u767D\u7684\u7389\u6307\u6309\u4F4F\u7389\u6237\uFF0C\u5C06\u4E24\u7247\u6E7F\u6DCB\u6DCB\u7684\u82B1\u74E3\u8F7B\u8F7B\u62E8\u5F00\uFF0C\u9732\u51FA\u4E00\u53EA\u4E0D\u4F4F\u7FD5\u5408\u7684\u871C\u7A74\u3002\u7136\u540E\u6276\u7740\u90A3\u6839\u7D2B\u9ED1\u8272\u7684\u9633\u5177\uFF0C\u5BF9\u51C6\u7A74\u53E3\uFF0C\u5C0F\u58F0\u9053\uFF1A\u300C\u592B\u541B\uFF0C\u7434\u513F\u7684\u2026\u2026\u53EF\u4EE5\u63D2\u4E86\u2026\u2026\u300D</p><p>\u5B9D\u513F\u53EA\u987E\u63EA\u5F04\u5979\u7684\u4E73\u5934\uFF0C\u51CC\u96C5\u7434\u53EA\u597D\u633A\u8D77\u4E0B\u4F53\uFF0C\u5C06\u9F9F\u5934\u5957\u5165\u7A74\u53E3\u3002\u5B9D\u513F\u5446\u7740\u8138\u6123\u4E86\u4E00\u4F1A\u513F\uFF0C\u624D\u4E22\u5F00\u4E73\u5934\uFF0C\u5C41\u80A1\u5F80\u4E0B\u4E00\u538B\u3002\u7F8E\u5987\u300C\u5440\u300D\u7684\u4E00\u58F0\u4F4E\u53EB\uFF0C\u7389\u4F53\u8C61\u88AB\u70B9\u71C3\u822C\u70ED\u4E86\u8D77\u6765\u3002\u5979\u63E1\u7740\u8089\u68D2\u5728\u4F53\u5185\u62BD\u9001\u51E0\u4E0B\uFF0C\u7B49\u8FDB\u51FA\u987A\u7545\u4E4B\u540E\uFF0C\u4FBF\u677E\u5F00\u624B\uFF0C\u6293\u4F4F\u81C0\u4FA7\u7684\u6BDB\u6BEF\uFF0C\u7389\u817F\u655E\u5206\u7740\u4E3E\u8D77\u79D8\u5904\u3002</p><p>\u5B9D\u513F\u7684\u62BD\u9001\u8D8A\u6765\u8D8A\u719F\u7EC3\uFF0C\u4E5F\u8D8A\u6765\u8D8A\u5FEB\uFF0C\u4ED6\u4EC0\u4E48\u6280\u5DE7\u4E5F\u4E0D\u4F1A\uFF0C\u53EA\u4E00\u5473\u76F4\u8FDB\u76F4\u51FA\uFF0C\u6BCF\u4E00\u4E0B\u9F9F\u5934\u90FD\u6363\u5728\u82B1\u5FC3\u4E0A\u3002\u4E0D\u591A\u65F6\uFF0C\u4ED6\u8EAB\u4E0B\u7684\u7F8E\u5987\u4FBF\u88AB\u5E72\u5F97\u6D6A\u53EB\u8D77\u6765\u3002</p><p>\u51CC\u96C5\u7434\u6700\u540E\u7684\u77DC\u6301\u548C\u5C0A\u4E25\u4E5F\u88AB\u90A3\u6839\u8089\u68D2\u51FB\u5F97\u7C89\u788E\uFF0C\u5979\u5FD8\u60C5\u5730\u626D\u52A8\u7740\u7389\u4F53\uFF0C\u96EA\u767D\u5149\u6DA6\u7684\u5927\u817F\u6536\u62E2\u6765\uFF0C\u628A\u90A3\u4E2A\u53C8\u5E72\u53C8\u7626\u7684\u5B69\u5B50\u7D27\u7D27\u5939\u5728\u817F\u95F4\uFF0C\u80A5\u7F8E\u7684\u96EA\u81C0\u4E00\u633A\u4E00\u633A\uFF0C\u6781\u529B\u8FCE\u5408\u7740\u7537\u5B69\u7684\u62BD\u9001\u3002\u53E3\u4E2D\u6D6A\u58F0\u53EB\u7740\uFF1A\u300C\u592B\u541B\u2026\u2026\u7528\u529B\u63D2\u7434\u513F\u2026\u2026\u300D\u5979\u7684\u592B\u541B\u4E00\u8FB9\u5438\u7740\u9F3B\u6D95\uFF0C\u4E00\u8FB9\u561F\u56D4\u7740\u300C\u8001\u5A46\u300D\uFF0C\u5FFD\u7136\u5475\u5475\u50BB\u7B11\u8D77\u6765\u3002</p><p>\u53EA\u4E00\u67F1\u9999\u5DE5\u592B\uFF0C\u8FD9\u4E2A\u719F\u8273\u7684\u7F8E\u5987\u4FBF\u5A07\u8EAF\u8FDE\u98A4\uFF0C\u88AB\u4E00\u4E2A\u5B69\u5B50\u5E72\u5F97\u6CC4\u4E86\u8EAB\u5B50\u3002\u5979\u4E00\u8FB9\u75C9\u631B\u7740\u55B7\u51FA\u9634\u7CBE\uFF0C\u4E00\u8FB9\u4ECD\u98A4\u6296\u7740\u633A\u8D77\u4E0B\u4F53\uFF0C\u597D\u8BA9\u65B0\u4EFB\u7684\u592B\u541B\u80FD\u5C3D\u60C5\u4EAB\u7528\u81EA\u5DF1\u7684\u8089\u4F53\u3002</p><p>\u7247\u523B\u540E\uFF0C\u5B9D\u513F\u4E5F\u54C6\u55E6\u8D77\u6765\u3002\u51CC\u96C5\u7434\u9AD8\u6F6E\u4F59\u97F5\u672A\u892A\uFF0C\u5979\u7EF7\u7740\u811A\u5C16\uFF0C\u7389\u8DB3\u7D27\u538B\u5728\u7537\u5B69\u7684\u5C41\u80A1\u4E0A\uFF0C\u7389\u6237\u6781\u529B\u633A\u8D77\uFF0C\u5C31\u50CF\u4E00\u4E2A\u6BB7\u5207\u7684\u59BB\u5B50\u90A3\u6837\uFF0C\u8BA9\u4E08\u592B\u9F9F\u5934\u7D27\u9876\u7740\u81EA\u5DF1\u7684\u82B1\u5FC3\uFF0C\u628A\u7CBE\u6DB2\u5C3D\u6570\u5C04\u5728\u81EA\u5DF1\u7684\u5B50\u5BAB\u5185\u3002</p><p>\u7F8E\u5987\u4F38\u51FA\u9999\u820C\uFF0C\u6E29\u5B58\u5730\u8214\u8210\u7740\u7537\u5B69\u8F6F\u5316\u7684\u9633\u5177\uFF0C\u7709\u5B87\u95F4\u7684\u67D4\u987A\uFF0C\u5C31\u50CF\u4E00\u4E2A\u5343\u4F9D\u767E\u987A\u7684\u59BB\u5B50\u4E00\u6837\u3002\u53EA\u662F\u5979\u9762\u524D\u7684\u5E76\u975E\u5468\u5B50\u6C5F\uFF0C\u800C\u662F\u4E00\u4E2A\u672A\u6EE1\u5341\u5C81\uFF0C\u4E00\u4FA7\u624B\u811A\u90FD\u840E\u7F29\u53D8\u5F62\u7684\u4E11\u964B\u7537\u7AE5\u3002</p><p>\u671B\u7740\u96EA\u80A4\u82B1\u8C8C\u7684\u5E08\u5A18\u5982\u6B64\u7528\u5FC3\u5730\u4F8D\u5949\u90A3\u4E2A\u5E72\u762A\u7684\u602A\u80CE\uFF0C\u9759\u989C\u5589\u5934\u53C8\u5E72\u53C8\u6DA9\u3002</p><p>\u5E08\u5A18\u4E00\u751F\u592A\u8FC7\u987A\u5229\uFF0C\u5F53\u65E5\u7684\u8F6E\u5978\u3001\u7F9E\u8FB1\u5DF2\u7ECF\u51FB\u6E83\u4E86\u5979\u7684\u610F\u5FD7\uFF0C\u4F7F\u8FD9\u4E2A\u7F8E\u8273\u7684\u5C11\u5987\u518D\u65E0\u529B\u53CD\u6297\uFF0C\u53EA\u80FD\u50CF\u73A9\u5076\u4E00\u6837\u4EFB\u4EBA\u6446\u5E03\u5979\u6210\u719F\u4E30\u8174\u7684\u8089\u4F53\u3002</p><p>\u51CC\u96C5\u7434\u5C06\u6700\u540E\u4E00\u70B9\u6C61\u8FF9\u5377\u5165\u6731\u5507\uFF0C\u7136\u540E\u653E\u4E0B\u9633\u5177\uFF0C\u4E24\u624B\u4EA4\u53E0\u7740\u653E\u5728\u8EAB\u524D\uFF0C\u8DEA\u5750\u7740\u4F0F\u4E0B\u5A07\u8EAF\uFF0C\u989D\u5934\u78B0\u5728\u624B\u80CC\u4E0A\uFF0C\u50CF\u7F9E\u6DA9\u7684\u65B0\u5987\u90A3\u6837\u4E0D\u6562\u62AC\u5934\u770B\u81EA\u5DF1\u7684\u4E08\u592B\u3002</p><p>\u5999\u82B1\u5E08\u592A\u6120\u9053\uFF1A\u300C\u4E08\u592B\u4E3A\u4F60\u7D2F\u4E86\u90A3\u4E48\u4E45\uFF0C\u4E5F\u4E0D\u77E5\u9053\u9053\u58F0\u8C22\uFF1F\u95EE\u95EE\u4E08\u592B\u9AD8\u4E0D\u9AD8\u5174\uFF1F\u300D</p><p>\u51CC\u96C5\u7434\u53EA\u597D\u542B\u7F9E\u9053\uFF1A\u300C\u591A\u8C22\u592B\u541B\u5782\u7231\u2026\u2026\u4F7F\u7528\u7434\u513F\u7684\u8EAB\u4F53\u2026\u2026\u592B\u541B\u5BF9\u7434\u513F\u7684\u670D\u4F8D\u6EE1\u610F\u5417\uFF1F\u300D</p><p>\u5B9D\u513F\u5BF9\u9633\u5177\u4E0A\u7684\u53E3\u6C34\u5927\u611F\u6709\u8DA3\uFF0C\u4ED6\u62FF\u7740\u6E7F\u6F09\u6F09\u7684\u8089\u68D2\uFF0C\u628A\u9F9F\u5934\u4E0A\u7684\u553E\u6DB2\u5370\u5728\u51CC\u96C5\u7434\u96EA\u767D\u7684\u8089\u4F53\u4E0A\uFF0C\u53E3\u91CC\u8BF4\u9053\uFF1A\u300C\u597D\u2026\u2026\u597D\u73A9\u2026\u2026\u300D</p><p>\u5999\u82B1\u5E08\u592A\u9053\uFF1A\u300C\u5F53\u6211\u4EEC\u6CAE\u6E20\u5BB6\u7684\u5AB3\u5987\u5E76\u4E0D\u96BE\uFF0C\u53EA\u8981\u4F60\u4F3A\u5019\u597D\u516C\u5A46\uFF0C\u4E08\u592B\u8BA9\u4F60\u505A\u4EC0\u4E48\u4F60\u5C31\u505A\u4EC0\u4E48\uFF0C\u966A\u4ED6\u5F00\u5FC3\u5C31\u591F\u4E86\u3002\u300D</p><p>\u300C\u662F\u3002\u591A\u8C22\u5A46\u5A46\u2026\u2026\u300D\u51CC\u96C5\u7434\u8138\u4E0A\u7684\u6F6E\u7EA2\u6E10\u6E10\u892A\u53BB\uFF0C\u7F8E\u76EE\u53C8\u6CDB\u8D77\u6CEA\u5149\u3002\u5979\u4E3A\u81EA\u5DF1\u521A\u624D\u7684\u6DEB\u6001\u800C\u7F9E\u6127\uFF0C\u77ED\u77ED\u4E8C\u5341\u591A\u5929\uFF0C\u5979\u6240\u7ECF\u5386\u7684\u5978\u6DEB\uFF0C\u51E0\u4E4E\u8D85\u8FC7\u4E86\u4E0E\u4E08\u592B\u6210\u4EB2\u5341\u51E0\u5E74\u6765\u7684\u4EA4\u5408\u6B21\u6570\u3002\u5982\u6B64\u9891\u7E41\u7684\u6027\u4EA4\uFF0C\u4F7F\u5979\u7684\u8089\u4F53\u65E0\u6BD4\u654F\u611F\uFF0C\u63A5\u8FDE\u7684\u9AD8\u6F6E\u66F4\u4F7F\u8FD9\u4E2A\u4E45\u65F7\u7684\u6210\u719F\u5C11\u5987\u65E0\u6CD5\u63A7\u5236\u81EA\u5DF1\u7684\u8EAB\u4F53\uFF0C\u8FDE\u4E00\u4E2A\u5B69\u7AE5\u7B80\u5355\u7684\u62BD\u9001\uFF0C\u5C31\u4F7F\u5979\u6DEB\u6001\u6BD5\u9732\uFF0C\u96BE\u4EE5\u81EA\u6301\u3002</p><p>\u5999\u82B1\u5E08\u592A\u6B63\u5F85\u8BF4\u8BDD\uFF0C\u623F\u95E8\u5FFD\u7136\u63A8\u5F00\uFF0C\u767D\u7389\u9E42\u7B11\u9053\uFF1A\u300C\u606D\u559C\u5E08\u592A\uFF0C\u5A36\u4E86\u8FD9\u4E48\u6F02\u4EAE\u4E2A\u5AB3\u5987\u5462\u3002\u300D</p><p>\u5999\u82B1\u5E08\u592A\u8D77\u8EAB\u7B11\u9053\uFF1A\u300C\u4E0D\u8FC7\u662F\u7ED9\u6211\u513F\u5B50\u627E\u4E2A\u73A9\u5177\uFF0C\u5C31\u5F53\u662F\u5E8A\u4E0A\u6446\u4E2A\u591C\u58F6\uFF0C\u6709\u4E2A\u6492\u5C3F\u7684\u5730\u65B9\u3002\u300D</p><p>\u89C1\u5230\u767D\u6C0F\u59D0\u59B9\uFF0C\u51CC\u96C5\u7434\u7ACB\u65F6\u7389\u8138\u96EA\u767D\uFF0C\u5A07\u8EAF\u53D1\u98A4\u3002\u59D0\u59B9\u4FE9\u4E0E\u5979\u5BBF\u6028\u5DF2\u4E45\uFF0C\u5BF9\u5973\u4EBA\u7684\u8106\u5F31\u5904\u53C8\u4E86\u5982\u6307\u638C\uFF0C\u6298\u78E8\u8D77\u5979\u6765\u6CA1\u6709\u534A\u5206\u601C\u60DC\u3002\u60F3\u8D77\u6570\u65E5\u524D\u6240\u5403\u7684\u82E6\u5934\uFF0C\u51CC\u96C5\u7434\u53C8\u75BC\u53C8\u6015\uFF0C\u5979\u5B81\u613F\u518D\u88AB\u7537\u4EBA\u4EEC\u8E42\u8E8F\u4E00\u65E5\uFF0C\u4E5F\u4E0D\u613F\u5728\u59D0\u59B9\u4FE9\u624B\u4E2D\u8FC7\u4E0A\u4E00\u4E2A\u65F6\u8FB0\u3002</p><p>\u767D\u7389\u83BA\u9053\uFF1A\u300C\u4EE4\u516C\u5B50\u5DF2\u7ECF\u5E72\u8FC7\u4E86\uFF0C\u6211\u4EEC\u59D0\u59B9\u60F3\u501F\u4F60\u513F\u5AB3\u5987\u7528\u7528\uFF0C\u4E0D\u77E5\u957F\u8001\u53EF\u80AF\u5272\u7231\uFF1F\u300D</p><p>\u5999\u82B1\u5E08\u592A\u540D\u53EB\u6CAE\u6E20\u660E\u5170\uFF0C\u672C\u662F\u661F\u6708\u6E56\u63B3\u6765\u7684\u6DEB\u5974\uFF0C\u56E0\u54E5\u54E5\u6295\u9760\u661F\u6708\u6E56\uFF0C\u6697\u4E2D\u64CD\u6301\u5927\u5B5A\u7075\u9E6B\u5BFA\u7ACB\u4E0B\u5927\u529F\uFF0C\u5979\u4E5F\u9752\u4E91\u76F4\u4E0A\uFF0C\u5F53\u4E0A\u4E86\u4E94\u884C\u5802\u7684\u6C34\u5802\u957F\u8001\u3002\u4F46\u540D\u6B21\u8FD8\u5728\u767D\u6C0F\u59D0\u59B9\u4E4B\u4E0B\uFF0C\u5A36\u51CC\u96C5\u7434\u5F53\u513F\u5AB3\u5987\u4E5F\u4E0D\u8FC7\u662F\u4E3A\u513F\u5B50\u5BFB\u4E2A\u5F00\u5FC3\uFF0C\u54EA\u91CC\u4F1A\u628A\u8FD9\u4E2A\u8D31\u5974\u653E\u5728\u5FC3\u4E0A\uFF0C\u5F53\u5373\u7B11\u9053\uFF1A\u300C\u62A4\u6CD5\u65E2\u7136\u6709\u7528\uFF0C\u5C31\u7275\u53BB\u4F7F\u597D\u4E86\u3002\u4F55\u5FC5\u5BA2\u6C14\u5462\u3002\u300D</p><p>\u300C\u90A3\u6211\u4EEC\u5C31\u4E0D\u5BA2\u6C14\u4E86\uFF0C\u300D\u767D\u7389\u9E42\u8BF4\u7740\u671D\u51CC\u96C5\u7434\u52FE\u4E86\u52FE\u624B\u6307\uFF0C\u5A07\u7B11\u9053\uFF1A\u300C\u65B0\u5A18\u5B50\uFF0C\u8DDF\u59D0\u59D0\u6765\u554A\u3002\u300D</p><p>\u51CC\u96C5\u7434\u4FEF\u4E0B\u8EAB\u5B50\uFF0C\u56DB\u80A2\u7740\u5730\u5730\u8DDF\u5728\u59D0\u59B9\u4FE9\u8EAB\u540E\u3002\u722C\u52A8\u95F4\uFF0C\u90A3\u53EA\u96EA\u767D\u7684\u5706\u81C0\u4E00\u6643\u4E00\u6643\uFF0C\u6ED1\u817B\u7684\u81C0\u8089\u5FAE\u5FAE\u5F00\u5408\uFF0C\u9732\u51FA\u6E7F\u6DCB\u6DCB\u7684\u81C0\u7F1D\u548C\u5927\u817F\u5185\u4FA7\u5927\u7247\u5927\u7247\u7684\u6C34\u8FF9\u3002</p>',104);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
