import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_28" tabindex="-1"><a class="header-anchor" href="#_28" aria-hidden="true">#</a> 28</h1><p>\u4ECE\u8944\u9633\u5317\u4E0A\uFF0C\u4FBF\u662F\u71D5\u56FD\u5883\u5185\u3002\u6B64\u65F6\u57CE\u4E1C\u6C94\u6C34\u6E21\u53E3\u4E00\u5EA7\u7B80\u964B\u7684\u6728\u68DA\u91CC\uFF0C\u6B63\u805A\u6EE1\u4E86\u7B49\u5F85\u8FC7\u6C5F\u7684\u884C\u65C5\u5BA2\u5546\u3002</p><p>\u300C\u5F53\u4E16\u7B2C\u4E00\u731B\u5C06\uFF0C\u8981\u5C5E\u71D5\u56FD\u7684\u91D1\u5927\u5C06\u519B\uFF01\u300D\u4E00\u4E2A\u5546\u4EBA\u6253\u626E\u7684\u80D6\u5B50\u8BF4\u9053\uFF1A\u300C\u4ECE\u6F7C\u5173\u51FA\u5175\uFF0C\u4E00\u8DEF\u8FDE\u6218\u8FDE\u80DC\uFF0C\u53EA\u6709\u4E86\u56DB\u4E2A\u6708\u5C31\u653B\u4E0B\u91D1\u57CE\uFF0C\u706D\u4E86\u79E6\u56FD\uFF01\u8FD9\u6837\u6709\u52C7\u6709\u8C0B\u7684\u731B\u5C06\uFF0C\u5929\u4E0B\u5C11\u6709\uFF01\u300D</p><p>\u4E00\u4E2A\u6587\u58EB\u9053\uFF1A\u300C\u52C7\u5219\u52C7\u77E3\uFF0C\u53EA\u662F\u6740\u4F10\u672A\u514D\u592A\u8FC7\u3002\u5C60\u5546\u5DDE\u3001\u5C60\u51E4\u7FD4\uFF0C\u653B\u4E0B\u91D1\u57CE\u53C8\u903C\u7740\u51C9\u56FD\u628A\u9003\u4EA1\u5165\u5883\u7684\u79E6\u56FD\u7687\u5BA4\u5168\u90E8\u9012\u89E3\u957F\u5B89\uFF0C\u5C3D\u6570\u5C60\u622E\u4E8E\u5E02\u3002\u8FD9\u6837\u7684\u731B\u5C06\u2026\u2026\u300D\u4ED6\u6447\u5934\u53F9\u9053\uFF1A\u300C\u975E\u6211\u5B8B\u56FD\u4E4B\u798F\u554A\u3002\u300D</p><p>\u8E72\u5728\u95E8\u53E3\u7684\u811A\u592B\u9053\uFF1A\u300C\u5C31\u662F\u8BA9\u5927\u71D5\u6253\u8FC7\u6765\u53C8\u600E\u6837\uFF1F\u6211\u4EEC\u8FD9\u4E9B\u8001\u767E\u59D3\u6307\u4E0D\u5B9A\u8FD8\u80FD\u8FC7\u5F97\u597D\u4E9B\uFF01\u300D</p><p>\u4E00\u6761\u5927\u6C49\u62CD\u684C\u9053\uFF1A\u300C\u5478\uFF01\u90A3\u4E9B\u80E1\u72D7\u62FF\u6211\u6C49\u4EBA\u5F53\u732A\u72D7\u4E00\u822C\u4EFB\u6253\u4EFB\u6740\uFF0C\u5230\u65F6\u5019\u8FDE\u547D\u90FD\u4FDD\u4E0D\u4F4F\uFF01\u8FD8\u60F3\u8FC7\u597D\u65E5\u5B50\uFF01\u300D</p><p>\u811A\u592B\u54FC\u4E86\u4E00\u58F0\uFF0C\u522B\u8FC7\u5934\u3002\u5546\u4EBA\u7B11\u5475\u5475\u9053\uFF1A\u300C\u58EE\u58EB\u6240\u8BF4\u4E5F\u4E0D\u5C3D\u7136\uFF0C\u5728\u4E0B\u56DB\u5904\u7ECF\u5546\uFF0C\u8FD9\u80E1\u4EBA\u8DDF\u6C49\u4EBA\u5176\u5B9E\u90FD\u662F\u4E00\u4E2A\u6837\uFF0C\u4EBA\u5BB6\u4E5F\u60F3\u592A\u592A\u5E73\u5E73\u2026\u2026\u300D</p><p>\u6587\u58EB\u602B\u7136\u9053\uFF1A\u300C\u975E\u6211\u65CF\u7C7B\uFF0C\u5176\u5FC3\u5FC5\u5F02\u3002\u521A\u706D\u4E86\u79E6\u56FD\uFF0C\u71D5\u519B\u5982\u4ECA\u53C8\u5C6F\u5175\u9896\u660C\uFF0C\u6307\u65E5\u4FBF\u8981\u5357\u4E0B\uFF0C\u54EA\u91CC\u6709\u534A\u5206\u826F\u5584\uFF01\u300D</p><p>\u5927\u6C49\u53EB\u9053\uFF1A\u300C\u90A3\u4E9B\u80E1\u72D7\u6562\u8FC7\u6C5F\uFF0C\u8001\u5B50\u7B2C\u4E00\u4E2A\u5E72\u4ED6\u5A18\u7684\uFF01\u300D\u5468\u56F4\u54CD\u8D77\u4E00\u7247\u559D\u5F69\u58F0\uFF0C\u4F17\u4EBA\u8FDE\u58F0\u53EB\u597D\u3002</p><p>\u89D2\u843D\u91CC\u4E00\u4E2A\u5E26\u7740\u9762\u7EB1\u7684\u5C11\u5987\u5077\u5077\u7B11\u9053\uFF1A\u300C\u558A\u8FD9\u4E48\u5927\u58F0\uFF0C\u50CF\u662F\u8981\u6740\u8FC7\u53BB\u4F3C\u7684\uFF0C\u539F\u6765\u662F\u7B49\u4EBA\u5BB6\u8FC7\u6C5F\u5462\u3002\u300D</p><p>\u6DF3\u4E8E\u7476\u96BE\u5F97\u51FA\u95E8\u4E00\u8D9F\uFF0C\u8FD9\u6B21\u9001\u7525\u5973\u6210\u4EB2\uFF0C\u5A46\u5B50\u4E2B\u9B1F\u7BB1\u7B3C\u5AC1\u5986\u5E26\u4E86\u51E0\u8F66\uFF0C\u6BD4\u5BB6\u91CC\u8FD8\u8981\u6C14\u95F7\u3002\u7B49\u6E21\u8239\u7684\u65F6\u5019\uFF0C\u5979\u60F3\u8D77\u81EA\u5DF1\u8FD8\u7B97\u534A\u4E2A\u6C5F\u6E56\u4E2D\u4EBA\uFF0C\u975E\u62C9\u7740\u9F99\u6714\u51FA\u6765\u6563\u5FC3\u3002\u6B64\u523B\u542C\u90A3\u5927\u6C49\u53EB\u5F97\u53E3\u54CD\uFF0C\u4E0D\u7531\u6697\u4E2D\u7A83\u7B11\u3002\u9F99\u6714\u5728\u6C5F\u6E56\u884C\u8D70\u591A\u5E74\uFF0C\u4F46\u5BF9\u8FD9\u4E9B\u4E8B\u6F20\u4E0D\u5173\u5FC3\uFF0C\u5F53\u4E0B\u53EA\u7B11\u4E86\u7B11\uFF0C\u6CA1\u6709\u4F5C\u58F0\u3002</p><p>\u300C\u662F\u662F\u662F\uFF0C\u80E1\u6C49\u4E0D\u4E24\u7ACB\u3002\u300D\u5546\u4EBA\u8FDE\u5FD9\u8F6C\u4E86\u8BDD\u5934\uFF0C\u8BF4\u9053\uFF1A\u300C\u5144\u5F1F\u5728\u5317\u8FB9\u542C\u8BF4\uFF0C\u80E1\u71D5\u7684\u7687\u5E1D\u521A\u7ACB\u4E86\u4E24\u4E2A\u5983\u5B50\uFF0C\u8BCF\u544A\u5929\u4E0B\uFF0C\u70ED\u95F9\u5F97\u5F88\u5462\uFF01\u300D</p><p>\u68DA\u91CC\u6C14\u6C1B\u677E\u61C8\u4E0B\u6765\uFF0C\u6709\u4EBA\u9053\uFF1A\u300C\u80E1\u914B\u767B\u57FA\u6709\u5341\u5E74\u4E86\uFF0C\u4E00\u76F4\u6CA1\u6709\u7ACB\u540E\uFF0C\u5982\u4ECA\u7EB3\u7684\u662F\u54EA\u5BB6\u7684\u5983\u5B50\uFF1F\u300D</p><p>\u6587\u58EB\u9053\uFF1A\u300C\u591A\u534A\u662F\u5D14\u3001\u5362\u3001\u738B\u3001\u8303\u8FD9\u51E0\u5927\u95E8\u9600\u4E86\u3002\u300D</p><p>\u5546\u4EBA\u7B11\u9053\uFF1A\u300C\u6599\u4F60\u4EEC\u4E5F\u731C\u4E0D\u7740\uFF01\u4E00\u4E2A\u662F\u524D\u671D\u7EAA\u5927\u5C06\u519B\u7684\u5973\u513F\uFF0C\u5C01\u4E86\u601D\u5983\u3002\u300D</p><p>\u300C\u54A6\uFF1F\u300D\u6587\u58EB\u5947\u9053\uFF1A\u300C\u542C\u8BF4\u71D5\u5E1D\u4E0E\u59DA\u5468\u6709\u4E0D\u5171\u6234\u5929\u4E4B\u4EC7\uFF0C\u5927\u5468\u4EA1\u56FD\u65F6\u7687\u5BA4\u91CD\u81E3\u90FD\u88AB\u6740\u5F97\u5E72\u5E72\u51C0\u51C0\uFF0C\u7EAA\u5927\u5C06\u519B\u4E5F\u662F\u6EE1\u95E8\u88AB\u65A9\uFF0C\u600E\u4E48\u4F1A\u7ACB\u5979\u7684\u5973\u513F\u4E3A\u5983\uFF1F\u300D</p><p>\u300C\u8FD9\u60A8\u5C31\u6709\u6240\u4E0D\u77E5\u4E86\u3002\u5F53\u5E74\u7EAA\u5927\u5C06\u519B\u88AB\u6740\uFF0C\u5973\u513F\u88AB\u6CA1\u5165\u7687\u5BAB\u4E3A\u5A62\uFF0C\u53D7\u4E86\u7687\u4E0A\u5BA0\u7231\uFF0C\u4E5F\u4E0D\u7A00\u5947\u3002\u7A00\u5947\u7684\u662F\u8FD9\u53E6\u4E00\u4E2A\u2026\u2026\u300D</p><p>\u6709\u4EBA\u60F3\u8D77\u95EE\u9053\uFF1A\u300C\u71D5\u56FD\u4E24\u5E74\u524D\u7ACB\u4E86\u592A\u5B50\uFF0C\u83AB\u975E\u5C31\u662F\u7EAA\u5983\uFF1F\u600E\u4E48\u5F53\u65F6\u6CA1\u6709\u5C01\u53F7\uFF0C\u5982\u4ECA\u53C8\u4E3A\u4F55\u4E0D\u76F4\u63A5\u7ACB\u540E\u5462\uFF1F\u300D</p><p>\u65C1\u4EBA\u5BF9\u65E7\u4E8B\u5374\u4E0D\u5728\u610F\uFF0C\u53EA\u4E00\u4E2A\u52B2\u513F\u8FFD\u95EE\u53E6\u4E00\u4E2A\u5983\u5B50\u662F\u8C01\u3002</p><p>\u300C\u53E6\u4E00\u4E2A\u561B\uFF0C\u6BD4\u7EAA\u5983\u5A18\u5A18\u8FD8\u9AD8\u4E86\u4E00\u7EA7\uFF0C\u5C01\u4E86\u8D35\u5983\u3002\u300D\u5546\u4EBA\u7709\u98DE\u8272\u821E\u5730\u8BF4\u9053\uFF1A\u300C\u8FD9\u4F4D\u8D35\u5983\u5A18\u5A18\u53EF\u7A00\u5947\u7684\u7D27\uFF0C\u975E\u4F46\u4E0D\u662F\u5D14\u5362\u738B\u8303\u8FD9\u4E9B\u5927\u65CF\uFF0C\u4E5F\u4E0D\u662F\u52F3\u8D35\u5B50\u5973\uFF0C\u5374\u662F\u59D3\u7684\u8427\u6C0F\uFF0C\u4E5F\u4E0D\u77E5\u9053\u662F\u4EC0\u4E48\u4EBA\u5BB6\uFF0C\u5C31\u8DDF\u77F3\u5934\u7F1D\u8E66\u51FA\u6765\u4F3C\u7684\uFF0C\u4E00\u4E0B\u5B50\u5C31\u5C01\u4E86\u8D35\u5983\u3002\u5C01\u53F7\u66F4\u662F\u7A00\u5947\uFF0C\u53EB\u505A\u6BCD\u8D35\u5983\u3002\u300D</p><p>\u4F17\u4EBA\u987F\u65F6\u8F70\u5802\u5927\u7B11\u3002\u6709\u4EBA\u9053\uFF1A\u300C\u8FD9\u4E9B\u80E1\u4EBA\u72D7\u5C41\u4E0D\u901A\uFF0C\u54EA\u6709\u53EB\u6BCD\u8D35\u5983\u7684\uFF1F\u96BE\u4E0D\u6210\u662F\u5A36\u4E86\u4E2A\u5A18\u5417\uFF1F\u300D</p><p>\u5546\u4EBA\u7B11\u9053\uFF1A\u300C\u60A8\u8FD8\u522B\u8BF4\uFF0C\u8FD9\u4F4D\u6BCD\u8D35\u5983\u5E74\u7EAA\u771F\u8FD8\u6BD4\u7687\u5E1D\u5927\u7740\u4E9B\u3002\u300D</p><p>\u300C\u5413\uFF01\u8FD9\u80E1\u72D7\u7687\u5E1D\u83AB\u975E\u662F\u4E2A\u4E09\u5C81\u5C0F\u5B69\uFF0C\u6BCF\u5929\u8981\u5A18\u54C4\u7684\uFF1F\u300D</p><p>\u4E00\u65C1\u6709\u4EBA\u602A\u7B11\u9053\uFF1A\u300C\u8BF4\u4E0D\u5B9A\u8FD8\u8981\u5403\u5976\u5462\u3002\u300D</p><p>\u6DF3\u4E8E\u7476\u7EA2\u7740\u8138\u5550\u4E86\u4E00\u53E3\uFF0C\u8FD9\u4E9B\u4EBA\u8BF4\u8BDD\u8FD9\u4E48\u7C97\u4FD7\uFF0C\u5E78\u597D\u6CA1\u6709\u5E26\u5973\u513F\u6765\u3002\u9F99\u6714\u5FC3\u4E0D\u5728\u7109\u5730\u542C\u7740\uFF0C\u8111\u4E2D\u6697\u6697\u76D8\u7B97\u90A3\u4F4D\u82CF\u5A49\u513F\u8981\u5AC1\u7684\u90CE\u541B\uFF0C\u89C1\u7740\u65B0\u90CE\uFF0C\u4E00\u5B9A\u4F1A\u8BA9\u5979\u559C\u51FA\u671B\u5916\u7684\u3002</p><p>\u5546\u4EBA\u9053\uFF1A\u300C\u7687\u4E0A\u5BF9\u8FD9\u4F4D\u6BCD\u8D35\u5983\u53EF\u5BA0\u7231\u5F97\u7D27\u5462\uFF0C\u5355\u662F\u6BCF\u6708\u4E3A\u5979\u91C7\u8D2D\u7684\u9E9D\u9999\u3001\u73CD\u73E0\u5C31\u6709\u51E0\u5341\u4E07\u94B1\uFF01\u300D</p><p>\u5927\u6C49\u9053\uFF1A\u300C\u8FD9\u4E48\u591A\uFF1F\u96BE\u9053\u662F\u5F53\u996D\u5403\u7684\uFF1F\u300D</p><p>\u5546\u4EBA\u9053\uFF1A\u300C\u8BA9\u58EE\u58EB\u8BF4\u4E2D\u4E86\uFF0C\u6B63\u662F\u5F53\u996D\u5403\u7684\u3002\u90A3\u4E9B\u4E0A\u597D\u7684\u73CD\u73E0\u8BA9\u5BAB\u91CC\u4E70\u4E86\u53BB\uFF0C\u90FD\u662F\u7814\u788E\u4E86\uFF0C\u4E00\u534A\u548C\u4E86\u4EBA\u4E73\u670D\u7528\uFF0C\u4E00\u534A\u8C03\u6210\u6CB9\u818F\u62B9\u62ED\u8EAB\u4F53\u7684\u3002\u300D</p><p>\u4F17\u4EBA\u77EB\u820C\u96BE\u4E0B\uFF0C\u7ADF\u7136\u8FD9\u822C\u7684\u8C6A\u5962\uFF0C\u771F\u4E0D\u77E5\u9053\u90A3\u4F4D\u8D35\u5983\u5A18\u5A18\u8BE5\u662F\u5982\u4F55\u7684\u56FD\u8272\u5929\u9999\u4E86\u3002\u6587\u58EB\u6447\u5934\u9053\uFF1A\u300C\u73CD\u73E0\u867D\u53EF\u517B\u989C\u6392\u6BD2\uFF0C\u4F46\u600E\u53EF\u4E45\u670D\uFF1F\u5FC5\u662F\u65E0\u77E5\u4E4B\u5F92\u7684\u5984\u8A00\u3002\u300D</p><p>\u5546\u4EBA\u7B11\u563B\u563B\u9053\uFF1A\u300C\u60A8\u8001\u8BF4\u5F97\u6709\u7406\uFF0C\u4E0D\u8FC7\u5C0F\u4EBA\u5C31\u662F\u8D29\u73CD\u73E0\u7684\uFF0C\u8FD9\u4E8B\u513F\u53EF\u662F\u9119\u4EBA\u4EB2\u8EAB\u6240\u9047\uFF0C\u8FD9\u8D9F\u53BB\u5408\u6D66\u5C31\u662F\u4E70\u73E0\u7684\u5462\u3002\u300D</p><p>\u6709\u4EBA\u9053\uFF1A\u300C\u4F60\u89C1\u8FC7\u8FD9\u4F4D\u5403\u73CD\u73E0\u7684\u6BCD\u8D35\u5983\u5417\uFF1F\u300D</p><p>\u300C\u5C0F\u4EBA\u6CA1\u8FD9\u4E2A\u798F\u6C14\uFF0C\u542C\u8BF4\u6BCD\u8D35\u5983\u8EAB\u4F53\u6709\u75C5\uFF0C\u8FDE\u8DEF\u90FD\u8D70\u4E0D\u5F97\u5462\uFF0C\u6BCF\u5929\u996E\u98DF\u66F4\u8863\u90FD\u8981\u4EBA\u4F3A\u5019\u2026\u2026\u54CE\u5440\uFF0C\u8239\u6765\u4E86\u3002\u300D</p><p>\u4F17\u4EBA\u7EB7\u7EB7\u6D8C\u51FA\uFF0C\u81EA\u89C9\u95EF\u8FC7\u6C5F\u6E56\u7684\u6DF3\u4E8E\u7476\u4E5F\u7AD9\u4E86\u8D77\u6765\uFF0C\u62C9\u7740\u9F99\u6714\u671D\u81EA\u5DF1\u7684\u8F66\u9A6C\u8D70\u53BB\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u4E94\u6708\u521D\u4E00\uFF0C\u661F\u6708\u6E56\u3002</p><p>\u65B0\u683D\u7684\u5DE8\u6811\u7EFF\u836B\u853D\u65E5\uFF0C\u5DCD\u5CE8\u7684\u795E\u6BBF\u5F20\u706F\u7ED3\u5F69\uFF0C\u559C\u6C14\u6D0B\u6D0B\u3002\u8FD1\u767E\u540D\u8EAB\u7740\u4E0D\u540C\u989C\u8272\u52B2\u88C5\u7684\u5927\u6C49\u4E00\u5B57\u6392\u5F00\uFF0C\u4E00\u4E2A\u4E2A\u7CBE\u58EE\u52C7\u608D\uFF0C\u663E\u7136\u6B66\u529F\u4E0D\u5F31\u3002\u53EA\u662F\u4ED6\u4EEC\u795E\u6001\u867D\u7136\u606D\u656C\uFF0C\u773C\u4E2D\u5374\u6709\u610F\u65E0\u610F\u4E2D\u6D41\u9732\u51FA\u620F\u8C11\u7684\u795E\u8272\uFF0C\u8BA9\u6DF3\u4E8E\u7476\u5FC3\u5934\u9690\u9690\u751F\u538C\u3002</p><p>\u7B49\u89C1\u5230\u6BBF\u5185\u7684\u9634\u68EE\uFF0C\u6C88\u83F2\u83F2\u7981\u4E0D\u4F4F\u6402\u4F4F\u6BCD\u4EB2\u7684\u7C89\u9888\uFF0C\u5C0F\u5C0F\u7684\u8EAB\u5B50\u7D27\u5F20\u5F97\u7F29\u5728\u6BCD\u4EB2\u6000\u91CC\u3002\u6DF3\u4E8E\u7476\u52C9\u5F3A\u7B11\u9053\uFF1A\u300C\u83F2\u83F2\u4E0D\u8981\u6015\uFF0C\u4E00\u4F1A\u513F\u5C31\u89C1\u5230\u59E8\u5A18\u4E86\u2026\u2026\u300D</p><p>\u4E00\u540D\u5A07\u8273\u7684\u5C11\u5973\u8FCE\u4E86\u8FC7\u6765\uFF0C\u7B11\u9765\u5982\u82B1\u5730\u8BF4\u9053\uFF1A\u300C\u6211\u53EB\u592D\u592D\uFF0C\u4F60\u5C31\u662F\u7F8E\u743C\u7476\u4E86\u5427\u3002\u6BD4\u4F60\u4E24\u4E2A\u59D0\u59D0\u8FD8\u6F02\u4EAE\u5462\uFF0C\u9526\u6D77\u68E0\u3001\u7389\u51CC\u9704\u3001\u7F8E\u743C\u7476\u4E00\u4E2A\u6BD4\u4E00\u4E2A\u6F02\u4EAE\uFF0C\u602A\u4E0D\u5F97\u662F\u4E09\u6735\u540D\u82B1\u5462\u3002\u300D</p><p>\u6DF3\u4E8E\u7476\u542C\u5979\u53EB\u51FA\u4E8C\u59D0\u7684\u540D\u5B57\uFF0C\u4E0D\u7531\u82B3\u5FC3\u8BB6\u5F02\uFF0C\u9704\u59D0\u6B7B\u4E86\u5DF2\u8FD1\u5341\u5E74\uFF0C\u8FD9\u5973\u5B69\u770B\u4E0A\u53BB\u4E0D\u8FC7\u5341\u4E94\u516D\u5C81\uFF0C\u600E\u4E48\u4F1A\u89C1\u8FC7\u7389\u51CC\u9704\u5462\uFF1F</p><p>\u8FD8\u6CA1\u6765\u5F97\u53CA\u5F00\u53E3\uFF0C\u592D\u592D\u53C8\u7B11\u9053\uFF1A\u300C\u65B0\u90CE\u65B0\u5A18\u8981\u62DC\u5929\u5730\u4E86\uFF0C\u592B\u4EBA\u8BF7\u5230\u540E\u5802\u7A0D\u5750\u7247\u523B\u3002\u300D</p><p>\u51E0\u540D\u7F8E\u8C8C\u5C11\u5973\u8FCE\u8FC7\u6765\uFF0C\u6276\u4F4F\u8499\u7740\u7EA2\u76D6\u5934\u7684\u82CF\u5A49\u513F\uFF0C\u8FD9\u4E9B\u5973\u5B50\u8863\u7740\u66B4\u9732\uFF0C\u9664\u4E86\u592D\u592D\uFF0C\u7ADF\u6CA1\u6709\u4E00\u4E2A\u4EBA\u7A7F\u6709\u4EB5\u88E4\uFF0C\u8D64\u88F8\u7740\u767D\u5149\u5149\u7684\u5927\u817F\uFF0C\u5996\u51B6\u4E4B\u6781\u3002\u6DF3\u4E8E\u7476\u6697\u9053\u661F\u6708\u6E56\u7684\u5973\u5B50\u600E\u4E48\u5982\u6B64\u4E0D\u77E5\u7F9E\u803B\uFF1F\u518D\u770B\u5230\u5468\u56F4\u7684\u5927\u6C49\u4E00\u4E2A\u4E2A\u9732\u51FA\u4E0D\u6000\u597D\u610F\u7684\u76EE\u5149\uFF0C\u53EA\u6015\u662F\u8981\u95F9\u6D1E\u623F\uFF0C\u5A49\u513F\u4E00\u4E2A\u5A07\u602F\u602F\u7684\u5973\u5B69\u5BB6\uFF0C\u82B1\u74E3\u513F\u4F3C\u7684\u8EAB\u5B50\u600E\u4E48\u53D7\u5F97\u4E86\u4ED6\u4EEC\u7684\u63C9\u6413\uFF1F\u5979\u60F6\u6025\u5730\u4E3E\u76EE\u56DB\u987E\uFF0C\u5374\u6CA1\u6709\u89C1\u5230\u59D0\u59D0\uFF0C\u8FDE\u540C\u6765\u7684\u9F99\u6714\u4E5F\u4E0D\u89C1\u4E86\u8E2A\u5F71\u3002</p><p>\u4E24\u540D\u5C11\u5973\u7B11\u76C8\u76C8\u633D\u4F4F\u6DF3\u4E8E\u7476\u7684\u624B\u81C2\uFF0C\u534A\u63A8\u534A\u62C9\u5730\u628A\u5979\u6276\u5230\u5C4F\u98CE\u4E4B\u540E\u3002\u6DF3\u4E8E\u7476\u516D\u795E\u65E0\u4E3B\uFF0C\u53EA\u597D\u62B1\u7740\u5973\u513F\u8DDF\u5979\u4EEC\u53BB\u4E86\u3002\u4E00\u8EAB\u5409\u670D\u7684\u82CF\u5A49\u513F\u8499\u7740\u7EA2\u76D6\u5934\uFF0C\u770B\u4E0D\u5230\u5468\u56F4\u7684\u60C5\u666F\uFF0C\u53EA\u542C\u7740\u4F17\u4EBA\u7684\u7B11\u58F0\u8D8A\u6765\u8D8A\u54CD\uFF0C\u7F9E\u5F97\u62AC\u4E0D\u8D77\u5934\u6765\u3002</p><p>\u300C\u65B0\u90CE\u5230\u3002\u300D\u6709\u4EBA\u602A\u8154\u602A\u8C03\u5730\u53EB\u4E86\u4E00\u58F0\u3002</p><p>\u82CF\u5A49\u513F\u5FC3\u5934\u546F\u546F\u76F4\u8DF3\uFF0C\u5979\u521A\u6EE1\u5341\u516D\uFF0C\u4ECE\u5C0F\u5728\u7236\u6BCD\u7684\u5BA0\u62A4\u4E0B\u957F\u5927\uFF0C\u7236\u6BCD\u601C\u5979\u5A07\u5F31\uFF0C\u8FDE\u6B66\u529F\u90FD\u6CA1\u6709\u8BA9\u5979\u591A\u7EC3\u3002\u6B64\u523B\u5B64\u8EAB\u4E00\u4EBA\u5904\u5728\u5B8C\u5168\u964C\u751F\u7684\u73AF\u5883\u4E2D\uFF0C\u65E9\u7D27\u5F20\u5F97\u4E0D\u77E5\u600E\u4E48\u529E\u624D\u597D\u3002</p><p>\u4E00\u4E2A\u5C11\u5973\u6276\u4F4F\u5979\u7684\u624B\u81C2\uFF0C\u7B11\u9053\uFF1A\u300C\u8BE5\u62DC\u5929\u5730\u4E86\uFF0C\u59D0\u59D0\u8FD8\u4E0D\u5FEB\u8DEA\u4E0B\u6765\u3002\u300D</p><p>\u82CF\u5A49\u513F\u8EAB\u4E0D\u7531\u5DF1\u5730\u8DEA\u4E86\u4E0B\u53BB\uFF0C\u4E0E\u8EAB\u65C1\u7684\u65B0\u90CE\u62DC\u4E86\u5929\u5730\u3002\u8BE5\u62DC\u9AD8\u5802\u65F6\uFF0C\u90A3\u4E2A\u53EB\u592D\u592D\u7684\u5C11\u5973\u7B11\u9053\uFF1A\u300C\u4F60\u516C\u516C\u5A46\u5A46\u4E5F\u4E0D\u597D\u627E\uFF0C\u53CD\u6B63\u65B0\u90CE\u662F\u6211\u517B\u5927\u7684\uFF0C\u4F60\u4EEC\u5C31\u62DC\u672C\u62A4\u6CD5\u597D\u4E86\u3002\u300D</p><p>\u82CF\u5A49\u513F\u542C\u5F97\u83AB\u540D\u5176\u5999\uFF0C\u7CCA\u91CC\u7CCA\u6D82\u5411\u8FD9\u4E2A\u8DDF\u81EA\u5DF1\u5DEE\u4E0D\u591A\u5927\u7684\u5C11\u5973\u62DC\u4E86\u4E0B\u53BB\u3002</p><p>\u300C\u592B\u59BB\u5BF9\u62DC\uFF01\u300D</p><p>\u5F53\u82CF\u5A49\u513F\u88AB\u6276\u7740\u8F6C\u8FC7\u8EAB\u5B50\uFF0C\u5411\u65B0\u90CE\u8DEA\u4E0B\u53BB\u65F6\uFF0C\u5468\u56F4\u7684\u7B11\u58F0\u987F\u65F6\u70ED\u70C8\u8D77\u6765\u3002\u6709\u4EBA\u7B11\u9053\uFF1A\u300C\u592D\u62A4\u6CD5\u8BBE\u8BA1\u5F97\u597D\u59FB\u7F18\uFF0C\u65B0\u90CE\u5A36\u4E86\u4E2A\u5A18\u5B50\uFF0C\u82CF\u5C0F\u59D0\u5AC1\u4E86\u4E2A\u597D\u8001\u516C\u554A\u3002\u300D</p><p>\u300C\u8FD9\u6837\u7684\u4F1F\u4E08\u592B\u6253\u7740\u706F\u7B3C\u4E5F\u96BE\u627E\uFF0C\u8FD9\u90FD\u662F\u82CF\u5C0F\u59D0\u524D\u751F\u4FEE\u6765\u7684\u798F\u6C14\u5462\u3002\u300D</p><p>\u65C1\u8FB9\u4E00\u4E2A\u5C11\u5973\u4F4E\u4F4E\u7B11\u9053\uFF1A\u300C\u5C0A\u592B\u597D\u5A01\u731B\u5462\uFF0C\u4E0A\u6B21\u4E00\u4E2A\u59D0\u59B9\u5C31\u662F\u88AB\u5C0A\u592B\u6D3B\u6D3B\u2026\u2026\u300D</p><p>\u53E6\u4E00\u4E2A\u5C11\u5973\u9053\uFF1A\u300C\u4E0D\u8981\u5413\u7740\u65B0\u5A18\u5B50\u4E86\uFF0C\u5176\u5B9E\u5C0A\u592B\u4E5F\u5F88\u6E29\u67D4\u5462\uFF0C\u4E0A\u6B21\u628A\u4EBA\u5BB6\u8214\u5F97\u9B42\u513F\u90FD\u98DE\u4E86\u2026\u2026\u300D</p><p>\u82CF\u5A49\u513F\u5FC3\u5982\u9E7F\u649E\uFF0C\u624B\u6307\u7D27\u7D27\u634F\u7740\u8863\u8896\uFF0C\u8111\u4E2D\u4E71\u8F70\u8F70\u54CD\u6210\u4E00\u7247\uFF0C\u5A18\u600E\u4E48\u4F1A\u628A\u81EA\u5DF1\u5AC1\u7ED9\u8FD9\u6837\u4E00\u4E2A\u8352\u6DEB\u7C97\u66B4\u7684\u7537\u4EBA\uFF1F</p><p>\u4E00\u53EA\u624B\u638C\u6309\u5728\u80A9\u4E0A\uFF0C\u82CF\u5A49\u513F\u53EA\u597D\u65E0\u5948\u5730\u4F4E\u4E0B\u67D4\u9888\uFF0C\u6DF1\u6DF1\u78D5\u4E0B\u5934\u53BB\uFF0C\u4E8B\u5230\u5982\u4ECA\uFF0C\u53EA\u80FD\u8BA4\u547D\u505A\u4ED6\u7684\u59BB\u5B50\u4E86\u3002</p><p>\u9AFB\u4E0A\u7684\u73E0\u7FE0\u78B0\u5728\u5730\u4E0A\uFF0C\u53D1\u51FA\u6E05\u60A6\u5730\u54CD\u58F0\u3002\u4F17\u4EBA\u620F\u8C11\u5730\u8F70\u7B11\u58F0\u4E2D\uFF0C\u82CF\u5A49\u513F\u542B\u7F9E\u53EB\u4E86\u58F0\uFF0C\u300C\u592B\u541B\u3002\u300D</p><p>\u5BF9\u9762\u7684\u65B0\u90CE\u5374\u6BEB\u65E0\u53CD\u5E94\uFF0C\u592D\u592D\u7B11\u9053\uFF1A\u300C\u4ED6\u4E0D\u4F1A\u8BF4\u8BDD\uFF0C\u6211\u6765\u66FF\u4ED6\u8BF4\u5427\u3002\u5A18\u5B50\u8BF7\u8D77\uFF0C\u4E0E\u4E3A\u592B\u540C\u5165\u6D1E\u623F\u3002\u300D</p><p>\u82CF\u5A49\u513F\u773C\u5708\u4E00\u7EA2\uFF0C\u9669\u4E9B\u6EF4\u4E0B\u6CEA\u6765\uFF0C\u5FC3\u4E2D\u54C0\u6028\u81EA\u5DF1\u547D\u82E6\uFF0C\u7ADF\u7136\u5AC1\u4E86\u4E2A\u54D1\u5DF4\u4E08\u592B\u2026\u2026\u4E24\u4E2A\u5C11\u5973\u6276\u5979\u8D77\u8EAB\uFF0C\u5374\u6CA1\u6709\u8FC8\u6B65\u8D70\u5165\u6D1E\u623F\u3002\u53EA\u542C\u811A\u8FB9\u6089\u6089\u7D22\u7D22\u58F0\u54CD\uFF0C\u94FA\u5F00\u4E00\u6761\u6BE1\u6BEF\uFF0C\u653E\u4E0A\u9526\u88AB\uFF0C\u63A5\u7740\u90A3\u4E24\u540D\u5C11\u5973\u6276\u7740\u5979\u5750\u4E86\u4E0B\u6765\uFF0C\u7ADF\u662F\u628A\u5927\u6BBF\u5F53\u6210\u4E86\u6D1E\u623F\u3002</p><p>\u300C\u8FD9\u600E\u4E48\u53EF\u4EE5\uFF1F\u300D\u82CF\u5A49\u513F\u53C8\u7F9E\u53C8\u6025\uFF0C\u6323\u624E\u7740\u60F3\u8981\u5750\u8D77\u6765\u3002\u4F46\u4E24\u540D\u5C11\u5973\u6B66\u529F\u4E0D\u51E1\uFF0C\u4E5F\u672A\u5C01\u5979\u7A74\u9053\uFF0C\u53EA\u8F7B\u8F7B\u6309\u7740\u80A9\u5934\uFF0C\u4FBF\u8BA9\u5979\u52A8\u5F39\u4E0D\u5F97\u3002\u63A5\u7740\u6709\u4EBA\u6293\u4F4F\u5979\u7684\u811A\u8E1D\uFF0C\u5C06\u4E00\u8EAB\u7EA2\u5986\u7684\u65B0\u5A18\u6309\u5728\u6BEF\u4E0A\uFF0C\u5206\u5F00\u53CC\u817F\u3002</p><p>\u4E00\u53EA\u6BDB\u8338\u8338\u7684\u5E9E\u7136\u5927\u7269\u4ECE\u817F\u95F4\u722C\u5230\u8EAB\u524D\uFF0C\u63A5\u7740\u7EA2\u76D6\u5934\u88AB\u731B\u7136\u626F\u6389\uFF0C\u9732\u51FA\u65B0\u5A18\u6BD4\u7EA2\u5E03\u8FD8\u7EA2\u7684\u7389\u8138\u548C\u5979\u60CA\u9A87\u6B32\u7EDD\u7684\u795E\u60C5\u3002</p><p>\u8033\u7554\u7684\u7B11\u58F0\u8D8A\u6765\u8D8A\u54CD\uFF0C\u5C11\u5973\u7684\u82B3\u5FC3\u5374\u5411\u7740\u65E0\u5E95\u6DF1\u6E0A\u6C89\u53BB\u3002\u90A3\u6015\u662F\u6700\u4E11\u6076\u7684\u7537\u4EBA\uFF0C\u82CF\u5A49\u513F\u4E5F\u8BA4\u547D\u4E86\uFF0C\u53EF\u773C\u524D\u5374\u662F\u4E00\u6761\u8EAB\u957F\u4F53\u58EE\uFF0C\u5A01\u731B\u72F0\u72DE\u7684\u5DE8\u72AC\u3002\u90A3\u4E24\u53EA\u78A7\u6CB9\u6CB9\u7684\u517D\u773C\u95EA\u52A8\u7740\u91CE\u6027\u7684\u51F6\u5149\uFF0C\u8165\u81ED\u7684\u553E\u6DB2\u4ECE\u767D\u68EE\u68EE\u7684\u7259\u9F7F\u4E0A\u6EF4\u843D\uFF0C\u4E00\u6EF4\u6EF4\u6389\u5728\u5C11\u5973\u767D\u7389\u822C\u7684\u9762\u988A\u4E0A\u3002\u5B83\u6D51\u8EAB\u62AB\u7740\u91D1\u9EC4\u8272\u7684\u957F\u6BDB\uFF0C\u6B63\u662F\u90A3\u6761\u9526\u6BDB\u72EE\u3002</p><p>\u300C\u65B0\u5A18\u5B50\u83AB\u975E\u4E0D\u613F\u610F\u4E86\uFF1F\u300D\u592D\u592D\u9C9C\u8273\u7684\u7EA2\u5507\u7FD8\u8D77\u4E00\u89D2\uFF0C\u5A07\u58F0\u9053\uFF1A\u300C\u5B83\u53EF\u662F\u672C\u62A4\u6CD5\u8C62\u517B\u7684\u7231\u72AC\uFF0C\u6709\u54EA\u70B9\u513F\u914D\u4E0D\u4E0A\u4F60\uFF1F\u300D</p><p>\u82CF\u5A49\u513F\u8138\u767D\u5982\u7EB8\uFF0C\u773C\u89D2\u6D8C\u51FA\u5927\u9897\u5927\u9897\u7684\u6CEA\u6C34\u3002\u81EA\u5DF1\u7ADF\u88AB\u9A97\u5F97\u8DDF\u4E00\u6761\u72D7\u62DC\u4E86\u5929\u5730\uFF0C\u8FD9\u6837\u7684\u7F9E\u8FB1\u600E\u4E48\u80FD\u591F\u627F\u53D7\uFF1F\u5F53\u9526\u6BDB\u72EE\u4F38\u51FA\u957F\u820C\uFF0C\u5728\u5979\u7C89\u816E\u4E0A\u4E00\u8214\uFF0C\u5C11\u5973\u7981\u4E0D\u4F4F\u75DB\u54ED\u8D77\u6765\u3002</p><p>\u90A3\u4E9B\u4F8D\u5973\u4E00\u8FB9\u7ED9\u65B0\u5A18\u5BBD\u8863\u89E3\u5E26\uFF0C\u4E00\u8FB9\u7B11\u9053\uFF1A\u300C\u8FD9\u6761\u9526\u6BDB\u72EE\u5728\u795E\u6559\u53EF\u5C0A\u8D35\u5F97\u7D27\u5462\u3002\u4ECE\u6B66\u6797\u4FA0\u5973\u5230\u8C6A\u95E8\u8D35\u5987\uFF0C\u5B83\u4EC0\u4E48\u6837\u7684\u5973\u4EBA\u6CA1\u5E72\u8FC7\uFF1F\u5C31\u662F\u4F60\u8FD9\u6837\u7684\u540D\u95E8\u95FA\u79C0\uFF0C\u5B83\u4E5F\u808F\u6B7B\u8FC7\u51E0\u4E2A\u5462\u3002\u300D</p><p>\u300C\u59B9\u59B9\u4E0D\u7528\u6015\uFF0C\u6211\u4EEC\u8FD9\u4E48\u591A\u4EBA\u770B\u7740\u5462\uFF0C\u7EDD\u4E0D\u4F1A\u8BA9\u5B83\u628A\u65B0\u5A18\u5B50\u5E72\u6B7B\u7684\u3002\u300D</p><p>\u300C\u4EBA\u5BB6\u6D1E\u623F\u82B1\u70DB\u591C\uFF0C\u4F60\u4EEC\u504F\u751F\u90A3\u4E48\u591A\u95F2\u8BDD\uFF0C\u8FD8\u4E0D\u8D76\u7D27\u5E2E\u65B0\u90CE\u65B0\u5A18\u6536\u62FE\u597D\uFF0C\u8BA9\u4ED6\u4EEC\u5408\u537A\u6210\u6B22\uFF1F\u300D</p><p>\u5468\u56F4\u7684\u661F\u6708\u6E56\u6559\u4F17\u563B\u7B11\u81EA\u82E5\uFF0C\u7B49\u7740\u770B\u5DE8\u72AC\u7ED9\u65B0\u5A18\u5F00\u82DE\u7684\u597D\u620F\uFF0C\u4E1D\u6BEB\u4E5F\u4E0D\u89C9\u5F97\u5176\u4E2D\u6709\u4F55\u6B8B\u5FCD\u3002</p><p>\u82CF\u5A49\u513F\u534E\u4E3D\u7684\u5AC1\u8863\u88AB\u5C42\u5C42\u89E3\u5F00\uFF0C\u9732\u51FA\u5706\u9F13\u9F13\u7684\u7C89\u4E73\u548C\u7EA4\u7F8E\u7684\u7389\u4F53\u3002\u5979\u54ED\u53EB\u7740\u62DA\u547D\u6323\u52A8\uFF0C\u4F46\u8FD8\u662F\u88AB\u4EBA\u6258\u8D77\u8170\u80A2\uFF0C\u892A\u53BB\u4EB5\u88E4\u3002\u5F53\u5C11\u5973\u96EA\u6ED1\u7684\u4E0B\u4F53\u66B4\u9732\u5728\u706F\u706B\u4E4B\u4E0B\uFF0C\u5E2E\u4F17\u4EEC\u90FD\u4E0D\u7981\u54BD\u4E86\u53E3\u5410\u6CAB\u3002\u4E24\u5973\u5206\u5F00\u82CF\u5A49\u513F\u7684\u53CC\u817F\uFF0C\u5C06\u4E24\u4E2A\u6795\u5934\u57AB\u5728\u81C0\u4E0B\uFF0C\u4F7F\u5C11\u5973\u4E0B\u4F53\u626C\u8D77\uFF0C\u5904\u5B50\u9C9C\u5AE9\u7684\u7389\u6237\u6B63\u5BF9\u7740\u5DE8\u72AC\u72F0\u72DE\u7684\u517D\u6839\u3002</p><p>\u9526\u6BDB\u72EE\u672C\u5C31\u662F\u4E13\u95E8\u9A6F\u517B\u7684\u6DEB\u517D\uFF0C\u55C5\u5230\u5973\u4EBA\u7684\u4F53\u9999\uFF0C\u90A3\u6839\u5DE8\u5927\u7684\u8089\u68D2\u7ACB\u523B\u52C3\u8D77\uFF0C\u5B83\u9F9F\u5934\u6781\u5C16\uFF0C\u8089\u68D2\u4E2D\u90E8\u5374\u7C97\u5982\u9E45\u5375\uFF0C\u6839\u90E8\u53C8\u7EC6\u4E86\u4E0B\u53BB\uFF0C\u540E\u9762\u8FD8\u6709\u4E00\u4E2A\u6E10\u6E10\u81A8\u80C0\u7684\u8089\u8282\u3002</p><p>\u773C\u770B\u7740\u90A3\u6839\u53EF\u6016\u7684\u517D\u6839\u8D8A\u4F38\u8D8A\u957F\uFF0C\u82CF\u5A49\u513F\u7F9E\u9A87\u5F97\u51E0\u4E4E\u660F\u5012\uFF0C\u300C\u653E\u5F00\u6211\u2026\u2026\u4E0D\u8981\u8BA9\u5B83\u8FC7\u6765\u2026\u2026\u300D\u5C11\u5973\u60F6\u6025\u5730\u54ED\u53EB\u7740\uFF0C\u5FFD\u7136\u53EB\u9053\uFF1A\u300C\u5A18\u2026\u2026\u5A18\u2026\u2026\u6551\u6211\u2026\u2026\u300D</p><p>\u592D\u592D\u751C\u751C\u4E00\u7B11\uFF0C\u300C\u4F60\u5A18\u5728\u540E\u5BAB\u7B49\u4F60\u5462\uFF0C\u8FD8\u6709\u4F60\u4E24\u4E2A\u963F\u59E8\uFF0C\u7B49\u4F60\u8DDF\u65B0\u90CE\u884C\u4E86\u592B\u59BB\u5927\u793C\uFF0C\u6211\u5C31\u8BA9\u4F60\u4EEC\u9616\u5BB6\u56E2\u805A\u3002\u300D</p><p>\u5DE8\u72AC\u5411\u524D\u4E00\u52A8\uFF0C\u8089\u68D2\u719F\u7EC3\u5730\u9876\u5728\u5C11\u5973\u80A1\u95F4\u7684\u79D8\u5904\uFF0C\u76F4\u76F4\u6345\u4E86\u8FDB\u53BB\u3002\u82CF\u5A49\u513F\u53EA\u89C9\u4E0B\u4F53\u4E00\u9635\u5267\u75DB\uFF0C\u72F0\u72DE\u7684\u517D\u6839\u5DF2\u7ECF\u633A\u5165\u79D8\u95ED\u7684\u82B1\u74E3\uFF0C\u593A\u53BB\u4E86\u5979\u5904\u5B50\u7684\u8D1E\u6D01\u3002</p><p>\u65B0\u5A5A\u4E4B\u591C\u6210\u4E86\u4E00\u573A\u96BE\u4EE5\u9192\u6765\u7684\u6076\u68A6\u3002\u65B0\u5A18\u5728\u5BBE\u5BA2\u73AF\u89C6\u4E4B\u4E0B\uFF0C\u5C06\u5A5A\u5BB4\u5927\u5385\u5F53\u4F5C\u4E86\u6D1E\u623F\uFF0C\u4E0E\u65B0\u90CE\u5408\u4F53\u6210\u6B22\uFF0C\u88AB\u4E00\u5934\u5DE8\u72AC\u7834\u53BB\u4E86\u5904\u5B50\u4E4B\u8EAB\u3002\u56F4\u89C2\u8005\u7684\u7B11\u8138\u6E10\u6E10\u6A21\u7CCA\uFF0C\u82CF\u5A49\u513F\u65E0\u6CD5\u76F8\u4FE1\u8FD9\u4E00\u5207\u7ADF\u662F\u771F\u7684\u3002\u81EA\u5DF1\u7684\u65B0\u5A5A\u4E4B\u591C\uFF0C\u771F\u7684\u662F\u5728\u8DDF\u4E00\u6761\u72D7\u4EA4\u5ABE\u3002</p><p>\u300C\u8FD9\u5A4A\u5B50\u7684\u5C44\u8FD8\u771F\u7D27\uFF0C\u8FD8\u6709\u8FD9\u4E48\u957F\u6CA1\u6709\u63D2\u8FDB\u53BB\u5462\u3002\u300D</p><p>\u300C\u518D\u628A\u5979\u7684\u817F\u63B0\u5F00\u4E00\u4E9B\u3002\u300D\u592D\u592D\u6307\u70B9\u9053\uFF1A\u300C\u628A\u5979\u7684\u5C0F\u5AE9\u5C44\u7FFB\u5F00\uFF0C\u8BA9\u5979\u592B\u541B\u5927\u4EBA\u90FD\u63D2\u8FDB\u53BB\u3002\u300D</p><p>\u51E0\u53EA\u624B\u540C\u65F6\u4F38\u8FC7\u6765\u63EA\u4F4F\u5979\u7684\u82B1\u74E3\u5411\u4E24\u65C1\u626F\u5F00\uFF0C\u6709\u4EBA\u751A\u81F3\u76F4\u63A5\u52FE\u4F4F\u843D\u7EA2\u7684\u5AE9\u7A74\uFF0C\u597D\u8BA9\u72D7\u9633\u66F4\u987A\u5229\u5730\u63D2\u5165\uFF0C\u82CF\u5A49\u513F\u56DB\u80A2\u5206\u5F00\uFF0C\u88AB\u4EBA\u7262\u7262\u6309\u5728\u5730\u4E0A\uFF0C\u96EA\u767D\u7684\u5C0F\u8179\u88AB\u57AB\u5F97\u633A\u8D77\u3002\u5A07\u5AE9\u7684\u7389\u6237\u5728\u517D\u6839\u7684\u8086\u8650\u4E0B\u9C9C\u8840\u56DB\u6EA2\uFF0C\u5979\u59E3\u597D\u7684\u7389\u5BB9\u75DB\u82E6\u5730\u626D\u66F2\u7740\uFF0C\u53D1\u51FA\u9635\u9635\u51C4\u5389\u5730\u54ED\u53EB\u3002\u5979\u81C0\u4E0B\u90A3\u5E45\u6309\u4E60\u4FD7\u94FA\u597D\u7684\u767D\u5E03\u4E0A\uFF0C\u6BB7\u7EA2\u7684\u8840\u8FF9\u6885\u82B1\u822C\u7247\u7247\u7EFD\u5F00\uFF0C\u8BB0\u8F7D\u7740\u65B0\u5A18\u5728\u6D1E\u623F\u4E4B\u591C\u6240\u5931\u53BB\u7684\u7AE5\u8D1E\u3002</p><p>\u25C6\u25C6\u25C6\u25C6 \u25C6\u25C6\u25C6\u25C6</p><p>\u9759\u989C\u6CA1\u6709\u53C2\u52A0\u5979\u4E00\u624B\u8BBE\u8BA1\u7684\u5A5A\u793C\u3002\u628A\u6DF3\u4E8E\u7476\u5E26\u5230\u5C9B\u4E0A\uFF0C\u5979\u4FBF\u6084\u7136\u53BB\u5723\u5BAB\u66F4\u8863\u5986\u626E\u3002\u5979\u8D8A\u6765\u8D8A\u559C\u6B22\u81EA\u5DF1\u5973\u88C5\u7684\u611F\u89C9\uFF0C\u4EE5\u5F80\u5979\u662F\u6000\u7740\u4E00\u79CD\u81EA\u66B4\u81EA\u5F03\u7684\u5FC3\u6001\uFF0C\u5C06\u81EA\u5DF1\u6253\u626E\u5F97\u5996\u51B6\u8273\u4E3D\uFF0C\u6BCF\u6B21\u7A7F\u4E0A\u5973\u4EBA\u7684\u4EB5\u8863\uFF0C\u81EA\u5DF1\u4F3C\u4E4E\u5C31\u6210\u4E86\u4E00\u4E2A\u4E0B\u8D31\u7684\u5A3C\u5993\u3002</p><p>\u73B0\u5728\u5979\u7528\u7684\u80ED\u8102\u6C34\u7C89\u8D8A\u6765\u8D8A\u5C11\uFF0C\u5986\u626E\u65F6\u53EA\u7B80\u5355\u5730\u52FE\u4E86\u52FE\u7709\u773C\uFF0C\u955C\u4E2D\u4FBF\u51FA\u73B0\u4E86\u4E00\u4E2A\u5929\u751F\u4E3D\u8D28\u7684\u7F8E\u8C8C\u5C11\u5973\u3002\u5979\u8D8A\u6765\u8D8A\u8BA8\u538C\u7537\u88C5\u7684\u4E0D\u6D01\uFF0C\u4E5F\u8D8A\u6765\u8D8A\u559C\u6B22\u90A3\u4E9B\u5E26\u7740\u5973\u6027\u6C14\u606F\u7684\u9999\u6599\u3002\u5979\u53EF\u4EE5\u5F88\u81EA\u7136\u5730\u4E3A\u81EA\u5DF1\u4E70\u4E00\u4E9B\u5973\u6027\u7684\u9970\u7269\uFF0C\u800C\u5B8C\u5168\u4E0D\u53BB\u60F3\u8FD9\u662F\u5426\u5FC5\u8981\u3002\u5728\u5185\u5FC3\u6DF1\u5904\uFF0C\u5979\u6E10\u6E10\u8BA4\u540C\u4E86\u81EA\u5DF1\u9759\u989C\u7684\u8EAB\u4EFD\uFF0C\u751A\u81F3\u4F1A\u6709\u81EA\u5DF1\u672C\u6765\u5C31\u662F\u5973\u5B50\u7684\u9519\u89C9\u3002</p><p>\u5979\u8D8A\u6765\u8D8A\u8D2A\u604B\u5973\u4EBA\u7684\u8089\u4F53\uFF0C\u559C\u6B22\u4E0E\u5973\u5B50\u8033\u9B13\u53AE\u78E8\u7684\u7F8E\u5999\u611F\u89C9\u3002\u9759\u989C\u4EE5\u4E3A\u8FD9\u4E5F\u662F\u81EA\u5DF1\u5411\u5973\u6027\u8F6C\u53D8\u7684\u53D8\u5316\u4E4B\u4E00\uFF0C\u5374\u6CA1\u6709\u60F3\u5230\u90A3\u5B8C\u5168\u662F\u4E00\u79CD\u7537\u6027\u5FAE\u5999\u7684\u5FC3\u6001\u3002\u9759\u989C\u7AD9\u8D77\u8EAB\u6765\uFF0C\u4E00\u8FB9\u504F\u7740\u8138\u5E26\u4E0A\u8033\u73AF\uFF0C\u4E00\u8FB9\u671D\u4FA7\u5BA4\u8D70\u53BB\u3002</p><p>\u623F\u95E8\u865A\u63A9\u7740\uFF0C\u592D\u592D\u80CC\u5BF9\u7740\u623F\u95E8\uFF0C\u6B63\u5728\u5199\u7740\u4EC0\u4E48\u3002\u9759\u989C\u6084\u6084\u8D70\u8FC7\u53BB\uFF0C\u731B\u7136\u4ECE\u80CC\u540E\u62B1\u4F4F\u90A3\u4E2A\u7C89\u5AE9\u7684\u8EAB\u5B50\uFF0C\u7B11\u9053\uFF1A\u300C\u5C0F\u4E56\u4E56\uFF0C\u5728\u5199\u4EC0\u4E48\u5462\uFF1F\u300D</p><p>\u5A07\u8EAF\u5165\u624B\uFF0C\u9759\u989C\u7ACB\u77E5\u4E0D\u5BF9\uFF0C\u90A3\u5973\u5B50\u80F8\u524D\u4E24\u56E2\u9999\u8F6F\u7684\u9165\u4E73\uFF0C\u53C8\u5706\u53C8\u6ED1\uFF0C\u6BD4\u592D\u592D\u53EF\u8981\u5927\u4E86\u8BB8\u591A\u3002</p><p>\u60CA\u7591\u95F4\uFF0C\u4E0D\u89C1\u90A3\u5C11\u5973\u6709\u4EFB\u4F55\u52A8\u4F5C\uFF0C\u4E00\u5BD2\u4E00\u70ED\u4E24\u80A1\u771F\u6C14\u4FBF\u900F\u4F53\u800C\u5165\uFF0C\u63A5\u7740\u5468\u8EAB\u5341\u4F59\u5904\u5927\u7A74\u540C\u65F6\u4E00\u9EBB\uFF0C\u9759\u989C\u6765\u4E0D\u53CA\u8FD0\u529F\u76F8\u6297\u5C31\u88AB\u5236\u4F4F\u3002\u8FD9\u6837\u9AD8\u660E\u7684\u6B66\u529F\uFF0C\u6BD4\u592D\u592D\u8FD8\u8981\u9AD8\u4E0A\u4E00\u7B79\uFF0C\u81EA\u5DF1\u4E5F\u662F\u9634\u9633\u53CC\u4FEE\uFF0C\u4F46\u6BD4\u8D77\u5979\u7684\u7CBE\u7EAF\u90A3\u662F\u8FDC\u8FDC\u4E0D\u53CA\u4E86\u3002</p><p>\u90A3\u5C11\u5973\u7F13\u7F13\u8F6C\u8FC7\u5934\u6765\uFF0C\u9759\u989C\u53EA\u89C9\u773C\u524D\u4E00\u4EAE\uFF0C\u5F77\u4F5B\u4E00\u6735\u73E0\u7389\u9576\u6210\u7684\u5947\u8469\u5728\u9762\u524D\u5189\u5189\u6D6E\u73B0\uFF0C\u6563\u53D1\u51FA\u4E03\u5B9D\u5149\u534E\u3002\u5979\u5E73\u751F\u89C1\u60EF\u7F8E\u5973\uFF0C\u68B5\u96EA\u828D\u3001\u51CC\u96C5\u7434\u3001\u6DF3\u4E8E\u7476\u65E0\u4E00\u4E0D\u662F\u96BE\u5F97\u7684\u7EDD\u8272\uFF0C\u5C31\u8FDE\u955C\u4E2D\u7684\u81EA\u5DF1\u4E5F\u662F\u5A07\u8273\u5982\u82B1\u3002\u4F46\u5979\u4ECE\u6765\u6CA1\u60F3\u8FC7\u4E16\u4E0A\u8FD8\u6709\u8FD9\u6837\u60CA\u4EBA\u7684\u7F8E\u8C8C\u3002\u90A3\u5F20\u8138\u582A\u79F0\u662F\u8273\u8272\u503E\u57CE\uFF0C\u5373\u4F7F\u5929\u4E0A\u7684\u4ED9\u5B50\u4E5F\u96BE\u6709\u8FD9\u822C\u5B8C\u7F8E\u7684\u5BB9\u989C\u3002\u770B\u5F97\u51FA\u5979\u6CA1\u6709\u4F7F\u7528\u4EFB\u4F55\u8102\u7C89\uFF0C\u56E0\u4E3A\u518D\u7EC6\u7684\u9999\u7C89\uFF0C\u518D\u8273\u7684\u80ED\u8102\u4E5F\u65E0\u6CD5\u4E0E\u5979\u5929\u751F\u7684\u4E3D\u8D28\u76F8\u5AB2\u7F8E\u3002</p><p>\u6014\u6014\u671B\u7740\u5C11\u5973\u7CBE\u81F4\u65E0\u7455\u7684\u7389\u5BB9\uFF0C\u604D\u60DA\u4E2D\uFF0C\u4F3C\u4E4E\u6709\u79CD\u4F3C\u66FE\u76F8\u8BC6\u7684\u611F\u89C9\u2026\u2026\u662F\u4E86\uFF0C\u5979\u7684\u5BB9\u8C8C\u3001\u4F53\u6001\u4E0E\u592D\u592D\u6709\u4E03\u516B\u5206\u76F8\u4F3C\uFF0C\u602A\u4E0D\u5F97\u81EA\u5DF1\u4F1A\u8BA4\u9519\u3002\u592D\u592D\u4E5F\u7B97\u5F97\u4E0A\u662F\u4E2A\u5A07\u4FCF\u7684\u5C0F\u7F8E\u4EBA\u513F\uFF0C\u4F46\u5982\u679C\u773C\u524D\u8FD9\u4E2A\u5C11\u5973\u662F\u51E4\u51F0\uFF0C\u90A3\u4E48\u592D\u592D\u53EA\u914D\u5F53\u91CE\u9E21\u4E86\u3002</p><p>\u90A3\u5C11\u5973\u4E4D\u7136\u770B\u5230\u9759\u989C\u7684\u5BB9\u8C8C\uFF0C\u4E5F\u4E0D\u7981\u4E00\u6123\uFF0C\u65CB\u5373\u53C8\u55D4\u6012\u8D77\u6765\uFF0C\u5979\u4E00\u628A\u5C06\u53D1\u5446\u5730\u9759\u989C\u63A8\u5F00\uFF0C\u6C14\u607C\u5730\u8BF4\u7740\uFF1A\u300C\u8D31\u5A62\uFF01\u300D</p><p>\u9759\u989C\u8111\u4E2D\u7075\u5149\u4E00\u95EA\uFF0C\u5931\u58F0\u53EB\u9053\uFF1A\u300C\u5C0F\u516C\u4E3B\uFF01\u300D</p><p>\u5979\u6CA1\u60F3\u5230\u592D\u592D\u53E3\u4E2D\u6DEB\u8D31\u653E\u8361\u7684\u5C0F\u516C\u4E3B\u4F1A\u662F\u8FD9\u4E2A\u6837\u5B50\uFF0C\u770B\u4E0A\u53BB\u6BD4\u592D\u592D\u8FD8\u7565\u5C0F\u4E00\u4E9B\uFF0C\u808C\u80A4\u6676\u83B9\u5982\u96EA\uFF0C\u73B2\u73D1\u7684\u7389\u4F53\u4E0A\u7A7F\u7740\u4EF6\u4F18\u96C5\u7684\u7EAF\u9ED1\u4E1D\u888D\uFF0C\u7EA4\u7F8E\u7684\u8170\u80A2\u95F4\u675F\u7740\u4E00\u6761\u5BBD\u5E26\uFF0C\u4E0A\u9762\u6302\u7740\u4E00\u5757\u6C34\u82CD\u7384\u7389\u96D5\u6210\u7684\u8170\u4F69\uFF0C\u80F8\u524D\u7528\u6781\u7EC6\u7684\u91D1\u7EBF\u7EE3\u7740\u4E00\u53EA\u76D8\u6210\u5706\u5F62\u7684\u98DE\u51E4\u3002\u6574\u5957\u8863\u670D\u6CA1\u6709\u4EFB\u4F55\u591A\u4F59\u7684\u88C5\u9970\uFF0C\u5374\u663E\u5F97\u534E\u8D35\u65E0\u6BD4\uFF0C\u66F4\u6709\u4E00\u79CD\u4EE4\u4EBA\u81EA\u60ED\u5F62\u79FD\u7684\u51B7\u8273\u6C14\u8D28\u3002</p><p>\u300C\u4F60\u662F\u4EC0\u4E48\u4EBA\uFF01\u600E\u4E48\u6562\u95EF\u5230\u8FD9\u91CC\uFF1F\u300D</p><p>\u9759\u989C\u9732\u51FA\u4E00\u4E2A\u59A9\u5A9A\u7684\u7B11\u5BB9\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u5974\u5A62\u9F99\u9759\u989C\uFF0C\u53C2\u89C1\u516C\u4E3B\u3002\u300D</p><p>\u5C0F\u516C\u4E3B\u538C\u6076\u5730\u76B1\u8D77\u7709\u5934\uFF0C\u5BD2\u58F0\u9053\uFF1A\u300C\u65B0\u6765\u7684\u5973\u5974\u5417\uFF1F\u4F60\u5230\u5BAB\u5916\u53BB\u81EA\u884C\u4E86\u65AD\u597D\u4E86\uFF0C\u672C\u5BAB\u4E0D\u518D\u8FFD\u7A76\u4F60\u5E2E\u4E2D\u8D23\u4EFB\u3002\u300D</p><p>\u9759\u989C\u77A0\u76EE\u7ED3\u820C\uFF0C\u81EA\u5DF1\u53EA\u662F\u65E0\u610F\u5192\u72AF\uFF0C\u7ADF\u7136\u5C31\u8BA9\u5979\u81EA\u5C3D\uFF0C\u542C\u53E3\u6C14\uFF0C\u4F3C\u4E4E\u8FD8\u662F\u83AB\u5927\u7684\u6069\u8D50\u3002</p><p>\u592D\u592D\u4E0D\u77E5\u4F55\u65F6\u5DF2\u7ECF\u5230\u4E86\u95E8\u8FB9\uFF0C\u8FD9\u4E2A\u8273\u4E3D\u72E0\u8FA3\u7684\u5C0F\u5996\u7CBE\u5728\u5C0F\u516C\u4E3B\u9762\u524D\u8FDE\u5927\u6C14\u4E5F\u4E0D\u6562\u51FA\uFF0C\u53EA\u5C0F\u58F0\u8BF4\u9053\uFF1A\u300C\u516C\u4E3B\uFF0C\u5979\u662F\u4E5D\u534E\u5251\u6D3E\u7684\u5F1F\u5B50\u3002\u521A\u521A\u5165\u5BAB\uFF0C\u4E0D\u77E5\u9053\u793C\u6570\uFF0C\u8FD8\u6C42\u516C\u4E3B\u9976\u6055\u5979\u4E00\u6B21\u3002\u300D</p><p>\u300C\u4E5D\u534E\u5251\u6D3E\uFF1F\u300D\u661F\u6708\u6E56\u5C5E\u4E0B\u63A7\u5236\u7740\u6570\u4EE5\u767E\u8BA1\u7684\u5E2E\u4F1A\uFF0C\u4F46\u4E5D\u534E\u5251\u6D3E\u7684\u5F1F\u5B50\u5165\u6559\u8FD8\u662F\u9996\u6B21\u3002\u5C0F\u516C\u4E3B\u8BB6\u9053\uFF1A\u300C\u4F60\u5E08\u7236\u662F\u8C01\uFF1F\u300D</p><p>\u9759\u989C\u786C\u7740\u5934\u76AE\u7B54\u9053\uFF1A\u300C\u5468\u5B50\u6C5F\u3002\u300D</p><p>\u5C0F\u516C\u4E3B\u7F8E\u76EE\u5F02\u5F69\u8FDE\u73B0\uFF0C\u826F\u4E45\u8BF4\u9053\uFF1A\u300C\u90A3\u4F60\u5E08\u5A18\u5C31\u662F\u7434\u58F0\u82B1\u5F71\u51CC\u96C5\u7434\u4E86\u3002\u300D</p><p>\u300C\u662F\u3002\u300D</p><p>\u5C0F\u516C\u4E3B\u62FF\u8D77\u6BDB\u7B14\uFF0C\u7EE7\u7EED\u5199\u8D77\u5B57\u6765\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u9000\u4E0B\u5427\u3002\u300D</p>',97);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
