import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_34" tabindex="-1"><a class="header-anchor" href="#_34" aria-hidden="true">#</a> 34</h1><p>\u300C\u6211\u4E0D\u77E5\u9053\u3002\u300D\u51CC\u96C5\u7434\u671B\u7740\u865A\u7A7A\uFF0C\u50F5\u786C\u5730\u8BF4\u9053\uFF1A\u300C\u7434\u513F\u7684\u8D31\u5C44\u88AB\u4EBA\u5E72\u5F97\u592A\u591A\u4E86\u3002\u4E0D\u77E5\u9053\u6000\u4E86\u8C01\u7684\u5B69\u5B50\u3002\u300D</p><p>\u5468\u5B50\u6C5F\u773C\u524D\u4E00\u9635\u53D1\u9ED1\uFF0C\u4ED6\u4F3C\u4E4E\u770B\u5230\u4E00\u7FA4\u80AE\u810F\u7684\u7537\u4EBA\u56F4\u7740\u59BB\u5B50\u8FF7\u4EBA\u7684\u8089\u4F53\uFF0C\u8F6E\u6D41\u628A\u7CBE\u6DB2\u5C04\u8FDB\u7434\u513F\u5723\u6D01\u7684\u5B50\u5BAB\u5185\u3002</p><p>\u300C\u6BCF\u4E2A\u73A9\u8FC7\u7434\u513F\u7684\u4EBA\u90FD\u8BF4\u7434\u513F\u7684\u5C44\u662F\u540D\u5668\uFF0C\u5E72\u8D77\u6765\u5F88\u8212\u670D\u7684\u3002\u5E08\u54E5\uFF0C\u53EA\u6709\u4F60\u6CA1\u6709\u8BF4\u8FC7\u3002\u300D</p><p>\u623F\u95F4\u91CC\u7A81\u7136\u54CD\u8D77\u4E00\u4E2A\u5996\u5A9A\u7684\u5973\u58F0\uFF0C\u300C\u5468\u638C\u95E8\u662F\u8EAB\u5728\u798F\u4E2D\u4E0D\u77E5\u798F\uFF0C\u591A\u534A\u4EE5\u4E3A\u5973\u4EBA\u90FD\u662F\u8FD9\u4E2A\u6837\u5B50\u7684\u5462\u3002\u300D</p><p>\u5468\u5B50\u6C5F\u8F9B\u82E6\u5730\u62AC\u8D77\u773C\uFF0C\u53EA\u89C1\u7389\u50CF\u65C1\u4E0D\u77E5\u4F55\u65F6\u5DF2\u7ECF\u591A\u4E86\u4E00\u5BF9\u5996\u5A06\u7684\u8273\u5973\u3002\u5979\u4EEC\u8EAB\u4E0A\u7684\u8863\u6599\u7EDD\u5C11\uFF0C\u80F8\u524D\u7F20\u7740\u7684\u90A3\u675F\u7EA2\u7EB1\uFF0C\u4F3C\u4E4E\u5ACC\u70ED\u822C\u677E\u4E86\u5F00\u6765\uFF0C\u9732\u51FA\u5927\u534A\u53EA\u767D\u5149\u5149\u7684\u96EA\u4E73\uFF0C\u4E73\u5934\u7FD8\u5728\u7EA2\u7EB1\u8FB9\u7F18\uFF0C\u770B\u4E0A\u53BB\u53EA\u8981\u8EAB\u5B50\u4E00\u52A8\u5C31\u4F1A\u8DF3\u51FA\u6765\u3002\u8179\u4E0B\u7684\u7EA2\u5DFE\u4E5F\u88AB\u633D\u5728\u8170\u95F4\uFF0C\u4E0D\u4EC5\u5927\u817F\u6839\u90E8\u4E00\u6761\u5149\u6DA6\u7684\u8179\u80A1\u6C9F\u6E05\u6670\u53EF\u89C1\uFF0C\u8FDE\u9634\u6BDB\u4E5F\u9732\u51FA\u51E0\u4E1D\uFF0C\u53EA\u6709\u7EA2\u5DFE\u7EC6\u7EC6\u7684\u4E00\u89D2\u4F4E\u5782\u4E0B\u6765\uFF0C\u63A9\u4F4F\u4E86\u79D8\u5904\u7684\u6625\u5149\u3002</p><p>\u5468\u5B50\u6C5F\u7ACB\u523B\u8BA4\u51FA\u8FD9\u5BF9\u59D0\u59B9\u82B1\uFF0C\u6B63\u662F\u5341\u5E74\u524D\u5728\u6D1B\u9633\u57CE\u5916\u6240\u89C1\u7684\u90A3\u5BF9\u5996\u5973\u2026\u2026\u5F53\u5E74\u5979\u4EEC\u6076\u6BD2\u7684\u8BC5\u5492\u6D6E\u4E0A\u5FC3\u5934\uFF0C\u5468\u5B50\u6C5F\u8138\u8272\u987F\u65F6\u53D8\u5F97\u94C1\u9752\u3002</p><p>\u767D\u7389\u83BA\u7B11\u76C8\u76C8\u9053\uFF1A\u300C\u770B\u6765\uFF0C\u5468\u638C\u95E8\u8FD8\u8BB0\u5F97\u4EBA\u5BB6\u5462\uFF0C\u8FD9\u4E48\u591A\u5E74\u4E0D\u89C1\uFF0C\u5468\u5927\u4FA0\u8FD8\u662F\u96C4\u58EE\u5982\u6614\u5462\u3002\u300D</p><p>\u767D\u7389\u9E42\u7B11\u9053\uFF1A\u300C\u51CC\u5973\u4FA0\u4E5F\u8DDF\u5F53\u5E74\u4E00\u6837\u6D6A\u5462\u3002\u51CC\u5A4A\u5B50\uFF0C\u8BB0\u5F97\u6211\u4EEC\u59D0\u59B9\u5F53\u5E74\u662F\u600E\u4E48\u8BF4\u7684\u5417\uFF1F\u300D</p><p>\u300C\u8D31\u5974\u662F\u4E2A\u5929\u751F\u7684\u5A4A\u5B50\u2026\u2026\u300D</p><p>\u300C\u8BB0\u5F97\u771F\u6E05\u5462\uFF0C\u5148\u8DDF\u5468\u638C\u95E8\u8BB2\u8BB2\uFF0C\u4F60\u662F\u600E\u4E48\u505A\u5A4A\u5B50\u7684\uFF1F\u300D</p><p>\u51CC\u96C5\u7434\u82B1\u5BB9\u60E8\u6DE1\uFF0C\u300C\u4ED6\u4EEC\u559C\u6B22\u7434\u513F\u8DB4\u5728\u5730\u4E0A\uFF0C\u6485\u7740\u5C41\u80A1\u6328\u808F\uFF0C\u4ED6\u4EEC\u8BF4\u90A3\u6837\u8C61\u5E72\u4E00\u6761\u6BCD\u72D7\u3002\u7434\u513F\u5C31\u662F\u4E00\u6761\u9A9A\u6BCD\u72D7\u2026\u2026\u300D</p><p>\u767D\u7389\u83BA\u76EF\u5173\u7740\u5468\u5B50\u6C5F\u7B11\u9053\uFF1A\u300C\u5C0A\u592B\u4EBA\u53EF\u4E56\u5F97\u5F88\u5462\uFF0C\u53C8\u542C\u8BDD\u53C8\u597D\u73A9\uFF0C\u5927\u4F19\u513F\u90FD\u62A2\u7740\u5E72\u5979\u5462\u3002\u662F\u4E0D\u662F\u554A\uFF0C\u51CC\u5A4A\u5B50\uFF1F\u300D</p><p>\u300C\u662F\u3002\u6709\u597D\u51E0\u767E\u6839\u9E21\u5DF4\u63D2\u8FC7\u7434\u513F\u3002\u4ED6\u4EEC\u559C\u6B22\u5E72\u7434\u513F\uFF0C\u4E0D\u5149\u662F\u56E0\u4E3A\u7434\u513F\u751F\u5F97\u7F8E\uFF0C\u6709\u4E2A\u5F88\u597D\u73A9\u7684\u5C44\uFF0C\u662F\u4E2A\u53C8\u4E56\u53C8\u9A9A\u7684\u6D6A\u5A4A\u5B50\uFF0C\u8FD8\u56E0\u4E3A\u5E08\u54E5\u662F\u4E5D\u534E\u5251\u6D3E\u7684\u638C\u95E8\uFF0C\u7434\u513F\u662F\u638C\u95E8\u592B\u4EBA\u3002\u4ED6\u4EEC\u8F6E\u6D41\u5E72\u7434\u513F\uFF0C\u662F\u4E3A\u4E86\u7ED9\u4F60\u5E26\u7EFF\u5E3D\u5B50\u2026\u2026\u300D</p><p>\u5468\u5B50\u6C5F\u773C\u4E2D\u8FF8\u51FA\u9C9C\u8840\uFF0C\u5468\u8EAB\u7684\u808C\u8089\u8C61\u88AB\u5200\u780D\u822C\u5757\u5757\u6536\u7D27\u3002\u767D\u7389\u9E42\u77A5\u4E86\u4ED6\u4E00\u773C\uFF0C\u7B11\u9053\uFF1A\u300C\u5C0A\u592B\u4EBA\u624D\u8C8C\u53CC\u5168\uFF0C\u96BE\u5F97\u5979\u559C\u6B22\u5F53\u5A4A\u5B50\uFF0C\u5B66\u8D77\u5E8A\u4E0A\u529F\u592B\u6765\u53C8\u5FEB\u53C8\u597D\uFF0C\u4EBA\u89C1\u4EBA\u5938\u5462\u3002\u300D</p><p>\u51CC\u96C5\u7434\u6728\u5076\u822C\u5E94\u58F0\u8BF4\u9053\uFF1A\u300C\u5E08\u54E5\uFF0C\u7434\u513F\u4ECE\u6765\u4E0D\u77E5\u9053\u81EA\u5DF1\u7684\u8EAB\u5B50\u6709\u90A3\u4E48\u591A\u5730\u65B9\u53EF\u4EE5\u7528\u3002\u4E0D\u5149\u8D31\u5C44\u53EF\u4EE5\u63D2\uFF0C\u8FD8\u6709\u5C41\u773C\u513F\u548C\u5634\u5DF4\u4E5F\u80FD\u8BA9\u4EBA\u73A9\u2026\u2026\u300D\u5979\u60F3\u8D77\u5F53\u65E5\u7684\u60C5\u666F\uFF0C\u5A07\u8EAF\u6296\u4E86\u4E00\u4E0B\uFF0C\u300C\u7434\u513F\u7684\u5C41\u773C\u513F\u8FD8\u662F\u767D\u62A4\u6CD5\u5F00\u7684\u82DE\u2026\u2026\u7434\u513F\u73B0\u5728\u6BCF\u5929\u90FD\u8981\u81EA\u5DF1\u6345\u5C41\u773C\u513F\uFF0C\u771F\u7684\u597D\u8212\u670D\u2026\u2026\u300D\u8BF4\u7740\u5979\u58F0\u97F3\u98A4\u6296\u8D77\u6765\uFF0C\u5FCD\u4E0D\u4F4F\u62A0\u4F4F\u5C41\u773C\u513F\uFF0C\u7528\u529B\u6405\u5F04\u8D77\u6765\u3002</p><p>\u556A\u7684\u4E00\u58F0\uFF0C\u51CC\u96C5\u7434\u4E73\u4E0A\u73B0\u51FA\u4E00\u53EA\u638C\u5370\uFF0C\u767D\u7389\u83BA\u5BD2\u58F0\u9053\uFF1A\u300C\u6B7B\u5A4A\u5B50\uFF0C\u53C8\u6B20\u5E72\u4E86\u5417\uFF1F\u5468\u638C\u95E8\u8FD8\u7B49\u7740\u542C\u4F60\u600E\u4E48\u5F53\u5A4A\u5B50\u5462\u3002\u300D</p><p>\u51CC\u96C5\u7434\u7684\u5185\u529F\u65E9\u5DF2\u88AB\u59D0\u59B9\u4FE9\u8054\u624B\u7528\u91CD\u697C\u6C14\u9501\u5236\u4F4F\uFF0C\u6BEB\u65E0\u53CD\u6297\u4E4B\u529B\uFF0C\u5F53\u4E0B\u4E56\u4E56\u62D4\u51FA\u624B\u6307\uFF0C\u83CA\u809B\u548C\u8089\u7A74\u5374\u8FD8\u5728\u4E0D\u4F4F\u8815\u52A8\u3002\u55B7\u6D8C\u7684\u6DEB\u6DB2\u4E1D\u6BEB\u6CA1\u6709\u505C\u6B62\u7684\u8FF9\u8C61\uFF0C\u96EA\u767D\u7684\u5927\u5C41\u80A1\u6574\u4E2A\u6D78\u5728\u6E05\u4EAE\u7684\u6DB2\u6C34\u91CC\uFF0C\u53C8\u767D\u53C8\u8F6F\uFF0C\u5F77\u4F5B\u6F02\u5728\u6C34\u4E0A\u7684\u4E91\u6735\u3002</p><p>\u51CC\u96C5\u7434\u5598\u4E86\u53E3\u6C14\uFF0C\u8BF4\u9053\uFF1A\u300C\u4ED6\u4EEC\u7684\u9E21\u5DF4\u53C8\u7C97\u53C8\u786C\uFF0C\u628A\u7434\u513F\u63D2\u5F97\u6B7B\u53BB\u6D3B\u6765\u3002\u4ED6\u4EEC\u8BF4\u7434\u513F\u7684\u529F\u592B\u597D\uFF0C\u5E72\u4E0D\u6B7B\u7684\uFF0C\u53EA\u662F\u8981\u628A\u7434\u513F\u7684\u5C44\u63D2\u70C2\uFF0C\u597D\u8BA9\u7434\u513F\u633A\u7740\u5927\u809A\u5B50\uFF0C\u63B0\u7740\u70C2\u5C44\u7ED9\u5E08\u54E5\u770B\u3002\u300D</p><p>\u51CC\u96C5\u7434\u633A\u8D77\u5706\u9F13\u9F13\u7684\u4E0B\u8179\uFF0C\u4E24\u624B\u6309\u7740\u817F\u6839\uFF0C\u5C06\u53C8\u9ED1\u53C8\u7EA2\u80BF\u5F97\u53D1\u4EAE\u7684\u9634\u6237\u63B0\u5F00\u4E3E\u5230\u4E08\u592B\u9762\u524D\uFF0C\u300C\u5E08\u54E5\uFF0C\u4F60\u770B\u5230\u4E86\u5417\uFF1F\u300D</p><p>\u5468\u5B50\u6C5F\u54C7\u7684\u55B7\u51FA\u4E00\u53E3\u9C9C\u8840\uFF0C\u5411\u540E\u4FBF\u5012\u3002</p><p>\u4E24\u6761\u96EA\u5149\u822C\u7684\u8EAB\u5F71\u500F\u5FFD\u62A2\u4E0A\u524D\u53BB\uFF0C\u4EBA\u5728\u534A\u7A7A\uFF0C\u624B\u4E2D\u4FBF\u5404\u81EA\u7206\u51FA\u4E00\u56E2\u9752\u5149\uFF0C\u5DE6\u53F3\u523A\u5411\u5468\u5B50\u6C5F\u80C1\u4E0B\u3002\u767D\u6C0F\u59D0\u59B9\u8FD1\u5E74\u5F97\u6155\u5BB9\u9F99\u4EB2\u4F20\uFF0C\u6B66\u529F\u8F83\u5F53\u5E74\u66F4\u80DC\u4E00\u7B79\uFF0C\u6B64\u65F6\u8D81\u5468\u5B50\u6C5F\u75DB\u6012\u653B\u5FC3\u65F6\u7A81\u65BD\u6697\u7B97\uFF0C\u7ACB\u65F6\u62A2\u5F97\u5148\u673A\u3002</p><p>\u5468\u5B50\u6C5F\u5FC3\u795E\u6FC0\u8361\u4E0B\uFF0C\u9876\u591A\u53EA\u80FD\u4F7F\u51FA\u4E94\u6210\u529F\u529B\uFF0C\u4ED6\u52C9\u5F3A\u50AC\u53D1\u771F\u6C14\uFF0C\u659C\u638C\u62CD\u5F00\u767D\u7389\u9E42\u624B\u4E2D\u7684\u77ED\u5251\uFF0C\u62E7\u8EAB\u5411\u5385\u89D2\u9000\u53BB\u3002\u767D\u7389\u83BA\u8D81\u4ED6\u4E0D\u53CA\u56DE\u62DB\uFF0C\u62DB\u672F\u4E00\u7D27\uFF0C\u4E00\u5251\u523A\u5230\u5468\u5B50\u6C5F\u8170\u4E0B\u3002</p><p>\u5468\u5B50\u6C5F\u808C\u8089\u672C\u80FD\u5730\u4E00\u6ED1\uFF0C\u907F\u5F00\u8981\u5BB3\uFF0C\u4F46\u5251\u950B\u5DF2\u5165\u8089\u5BF8\u8BB8\u3002\u77ED\u5251\u4E0A\u7684\u6BD2\u836F\u6D41\u7740\u8840\u8109\u8FDB\u5165\u4F53\u5185\uFF0C\u5468\u5B50\u6C5F\u53EA\u89C9\u8EAB\u4F53\u8D8A\u6765\u8D8A\u91CD\uFF0C\u53C8\u6321\u4E86\u51E0\u62DB\uFF0C\u80CC\u4E0A\u4E00\u9EBB\uFF0C\u5DF2\u7ECF\u88AB\u767D\u7389\u9E42\u5C01\u4E86\u7A74\u9053\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u6CA1\u60F3\u5230\u8FD9\u4E48\u8F7B\u6613\u5C31\u5236\u4F4F\u4E86\u4E5D\u534E\u5251\u6D3E\u7684\u638C\u95E8\uFF0C\u4E0D\u7531\u559C\u4E0A\u7709\u68A2\uFF0C\u4E24\u5973\u76F8\u89C6\u4E00\u7B11\uFF0C\u767D\u7389\u9E42\u55F2\u58F0\u9053\uFF1A\u300C\u8FD8\u53F7\u79F0\u5929\u4E0B\u7B2C\u4E00\u5251\u5462\uFF0C\u6789\u6211\u4EEC\u59D0\u59B9\u8FD9\u4E48\u8D39\u5FC3\uFF0C\u8BF7\u51FA\u5C0A\u592B\u4EBA\u63B0\u5C44\u52A9\u5174\u3002\u539F\u6765\u5468\u5927\u4FA0\u624B\u4E0A\u529F\u592B\u8DDF\u5E8A\u4E0A\u4E00\u6837\uFF0C\u90FD\u662F\u4E2D\u770B\u4E0D\u4E2D\u7528\u5594\u3002\u300D</p><p>\u767D\u7389\u83BA\u671D\u5468\u5B50\u6C5F\u629B\u4E86\u4E2A\u5A9A\u773C\uFF0C\u817B\u58F0\u9053\uFF1A\u300C\u59B9\u59B9\u4F60\u542C\u9519\u4E86\u5462\uFF0C\u5929\u4E0B\u7B2C\u4E00\u8D31\u8BF4\u7684\u53EF\u662F\u51CC\u5973\u4FA0\uFF0C\u4F60\u770B\u5979\u7684\u9A9A\u6837\uFF0C\u53EF\u4E0D\u662F\u5929\u4E0B\u7B2C\u4E00\u7684\u8D31\u8D27\u5417\uFF1F\u300D</p><p>\u51CC\u96C5\u7434\u4E24\u624B\u6491\u5728\u817F\u95F4\uFF0C\u4E24\u773C\u76F4\u76F4\u671B\u7740\u4E08\u592B\u8170\u95F4\u7D2B\u9ED1\u8272\u7684\u8840\u8FF9\uFF0C\u826F\u4E45\u624D\u98A4\u58F0\u9053\uFF1A\u300C\u5E08\u54E5\u2026\u2026\u300D</p><p>\u5468\u5B50\u6C5F\u5012\u5728\u5730\u4E0A\u4E00\u52A8\u4E0D\u52A8\uFF0C\u53E3\u9F3B\u95F4\u6C14\u82E5\u6E38\u4E1D\u3002\u767D\u6C0F\u59D0\u59B9\u5251\u4E0A\u6240\u7528\u7684\u6BD2\u836F\u94C1\u6728\u9B42\uFF0C\u4E43\u662F\u53F6\u884C\u5357\u4EB2\u624B\u6240\u5236\u3002\u4E00\u65E6\u89C1\u8840\uFF0C\u4E2D\u6BD2\u8005\u4FBF\u80A2\u4F53\u50F5\u786C\uFF0C\u808C\u8089\u5B9B\u5982\u6728\u77F3\uFF0C\u65E0\u6CD5\u52A8\u4F5C\u3002\u4F46\u5374\u4E0D\u4F1A\u7ACB\u6B7B\uFF0C\u5F77\u4F5B\u4E00\u5177\u6709\u610F\u8BC6\u7684\u6BAD\u5C38\u3002</p><p>\u95E8\u5916\u7684\u9759\u989C\u4E5F\u5446\u4F4F\u4E86\u3002\u957F\u4E45\u4EE5\u6765\uFF0C\u5E08\u7236\u548C\u5E08\u5A18\u5C31\u662F\u5979\u6700\u4EB2\u8FD1\u7684\u4EB2\u4EBA\u3002\u5E08\u5A18\u6E29\u67D4\u6148\u7231\uFF0C\u5BF9\u5979\u7684\u5173\u6000\u65E0\u5FAE\u4E0D\u81F3\uFF0C\u5979\u559C\u6B22\u5E08\u5A18\u8EAB\u4E0A\u90A3\u80A1\u99A5\u534E\u7684\u4F53\u9999\uFF0C\u5C31\u50CF\u6BCD\u4EB2\u4E00\u6837\u3002</p><p>\u5E08\u7236\u5219\u603B\u662F\u663E\u5F97\u5F88\u8FDC\u3002\u4ED6\u6559\u5979\u7EC3\u5251\uFF0C\u6559\u5979\u811A\u6B65\u8EAB\u6CD5\u90FD\u662F\u70B9\u5230\u800C\u6B62\uFF0C\u6709\u65F6\u95ED\u5173\uFF0C\u51E0\u4E2A\u6708\u90FD\u770B\u4E0D\u5230\u4ED6\u7684\u8EAB\u5F71\u3002\u4F46\u6BCF\u6B21\u89C1\u5230\u5E08\u7236\u5185\u655B\u800C\u5145\u6EE1\u5F20\u529B\u7684\u80CC\u5F71\uFF0C\u5979\u603B\u4F1A\u89C9\u5F97\u5F88\u5B89\u5FC3\u3002</p><p>\u4EE5\u524D\u5728\u68A6\u91CC\uFF0C\u81EA\u5DF1\u662F\u4E00\u4E2A\u5B64\u72EC\u7684\u7537\u5B69\uFF0C\u5145\u6EE1\u4E86\u7EDD\u671B\u3002\u6E10\u6E10\u7684\uFF0C\u5979\u5206\u4E0D\u6E05\u81EA\u5DF1\u662F\u7537\u662F\u5973\uFF0C\u5374\u591A\u4E86\u4E00\u5206\u5F02\u6837\u7684\u4F9D\u604B\u3002\u4F3C\u4E4E\u6709\u4E00\u4E2A\u9AD8\u5927\u6709\u529B\u7684\u7537\u4EBA\u4F1A\u7F13\u6B65\u8D70\u6765\uFF0C\u8FDC\u8FDC\u7AD9\u5728\u5979\u8EAB\u540E\u3002\u5E26\u7740\u4ED6\u7684\u6C5F\u6CB3\u5251\u3002</p><p>\u4E0E\u90A3\u5BF9\u5B6A\u751F\u59D0\u59B9\u82B1\u76F8\u4EFF\uFF0C\u51CC\u96C5\u7434\u4F3C\u4E4E\u4E5F\u6709\u4E00\u4E2A\u81EA\u5DF1\u7684\u5F71\u5B50\u3002\u5979\u5750\u5728\u4E00\u67B6\u5BBD\u5927\u7684\u7D2B\u6A80\u5EA7\u57FA\u4E0A\uFF0C\u8EAB\u4E0B\u662F\u90A3\u5C0A\u552F\u5999\u552F\u8096\u7684\u767D\u7389\u89C2\u97F3\u3002\u89C2\u97F3\u8EAB\u4E0A\u96EA\u767D\u7684\u4E1D\u888D\u6ED1\u4E0B\u534A\u5E45\uFF0C\u9732\u51FA\u6829\u6829\u5982\u751F\u7684\u7C89\u9888\u9165\u80F8\u3002\u5979\u957F\u7709\u5982\u753B\uFF0C\u79C0\u76EE\u6A31\u5507\uFF0C\u65E0\u8BBA\u9762\u8C8C\u8FD8\u662F\u4F18\u96C5\u7684\u6C14\u8D28\uFF0C\u90FD\u4E0E\u51CC\u96C5\u7434\u5982\u51FA\u4E00\u8F99\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u6EE1\u5FC3\u5F53\u7740\u5468\u5B50\u6C5F\u7684\u9762\uFF0C\u597D\u597D\u51CC\u8FB1\u4ED6\u7231\u59BB\u4E00\u756A\uFF0C\u4E0D\u6599\u5802\u5802\u4E5D\u534E\u638C\u95E8\u5982\u6B64\u4E0D\u4E2D\u7528\uFF0C\u53EA\u6570\u62DB\u5C31\u675F\u624B\u5C31\u64D2\uFF0C\u4E0D\u514D\u6709\u4E9B\u610F\u72B9\u672A\u5C3D\u3002\u767D\u7389\u83BA\u672C\u60F3\u5524\u9759\u989C\u8FDB\u6765\u53D6\u4E86\u4ED6\u7684\u9996\u7EA7\uFF0C\u4E34\u65F6\u53C8\u6539\u4E86\u4E3B\u610F\u3002\u5979\u53C9\u7740\u8170\u80A2\uFF0C\u4FEE\u957F\u7684\u7389\u817F\u5FAE\u5FAE\u659C\u5206\uFF0C\u626C\u58F0\u9053\uFF1A\u300C\u8D31\u5A4A\u5B50\uFF0C\u628A\u672C\u62A4\u6CD5\u7684\u4E1C\u897F\u62FF\u8FC7\u6765\u3002\u300D</p><p>\u300C\u662F\u3002\u300D\u51CC\u96C5\u7434\u5408\u4E0A\u6EF4\u6C34\u7684\u5927\u817F\uFF0C\u8270\u96BE\u5730\u5750\u4E86\u8D77\u6765\uFF0C\u4F38\u624B\u62E8\u5F00\u9500\u5B50\uFF0C\u5C06\u4FA7\u8EBA\u7684\u7389\u50CF\u5E73\u5E73\u653E\u5012\uFF0C\u7136\u540E\u6253\u5F00\u7389\u50CF\u4E0B\u7684\u6697\u683C\uFF0C\u53D6\u51FA\u4E00\u652F\u9ED1\u8272\u7684\u957F\u7269\uFF0C\u4E24\u624B\u6367\u7740\uFF0C\u8DEA\u5728\u767D\u7389\u83BA\u9762\u524D\u3002</p><p>\u5979\u4E00\u751F\u500D\u53D7\u5BA0\u62A4\uFF0C\u518D\u5927\u7684\u4E8B\u53EA\u8981\u5E08\u54E5\u51FA\u9762\u5C31\u80FD\u8FCE\u5203\u800C\u89E3\u3002\u800C\u5979\u4E5F\u5B89\u5FC3\u8EB2\u85CF\u5728\u4E08\u592B\u7684\u7FBD\u7FFC\u4E0B\uFF0C\u505A\u4E00\u4E2A\u65E0\u5FE7\u65E0\u8651\u7684\u5C0F\u59BB\u5B50\u3002\u53EF\u98CE\u4E91\u7A81\u53D8\uFF0C\u81EA\u5DF1\u5FFD\u7136\u95F4\u843D\u5165\u661F\u6708\u6E56\u7684\u9B54\u638C\uFF0C\u7F8E\u597D\u7684\u751F\u547D\u5C31\u6B64\u5815\u5165\u65E0\u6CD5\u6323\u8131\u7684\u6CE5\u6DD6\u3002\u9677\u5165\u7EDD\u5883\u7684\u51CC\u96C5\u7434\u518D\u6CA1\u6709\u4EFB\u4F55\u53EF\u4EE5\u501A\u9760\u7684\u624B\u81C2\uFF0C\u800C\u5979\u7684\u5C0A\u4E25\u548C\u4FE1\u5FC3\uFF0C\u4E5F\u65E9\u5DF2\u88AB\u90A3\u4E00\u663C\u591C\u65E0\u4F11\u6B62\u7684\u8F6E\u5978\u5F7B\u5E95\u51FB\u6E83\u3002</p><p>\u53EA\u80FD\u50CF\u4E00\u6735\u98D8\u96F6\u7684\u843D\u82B1\u822C\uFF0C\u968F\u6CE2\u9010\u6D41\u3002</p><p>\u53EA\u7247\u523B\u5DE5\u592B\uFF0C\u51CC\u96C5\u7434\u819D\u4E0B\u7684\u9752\u7816\u5DF2\u7ECF\u88AB\u6DEB\u6DB2\u6253\u6E7F\u3002\u5979\u4E24\u624B\u73AF\u8FC7\u767D\u7389\u83BA\u7684\u8170\u8EAB\uFF0C\u7ED1\u7D27\u76AE\u7D22\uFF0C\u7136\u540E\u5C0F\u5FC3\u5730\u5C06\u90A3\u6839\u5047\u9633\u5177\u6276\u6B63\u4F4D\u7F6E\uFF0C\u9876\u5728\u5973\u4E3B\u4EBA\u9634\u961C\u4E0A\u9762\u3002</p><p>\u767D\u7389\u83BA\u6643\u4E86\u6643\u8170\u80A2\uFF0C\u90A3\u6839\u534A\u5C3A\u957F\u7684\u5047\u9633\u5177\u7ACB\u523B\u50CF\u6D3B\u7269\u4E00\u6837\u8DF3\u52A8\u8D77\u6765\uFF0C\u300C\u51CC\u5A4A\u5B50\uFF0C\u5148\u53BB\u6F14\u4E00\u573A\u6625\u5BAB\uFF0C\u8BA9\u4F60\u592B\u541B\u770B\u770B\u3002\u300D</p><p>\u5468\u5B50\u6C5F\u53CC\u76EE\u7D27\u95ED\uFF0C\u8138\u8272\u7070\u767D\uFF0C\u8170\u95F4\u90A3\u6EE9\u7D2B\u9ED1\u8272\u7684\u8840\u6DB2\u5DF2\u7ECF\u5F00\u59CB\u5E72\u6DB8\u3002\u51CC\u96C5\u7434\u51C4\u7136\u8F6C\u8FC7\u8138\uFF0C\u8E29\u7740\u81EA\u5DF1\u7684\u6DEB\u6C34\u8D70\u4E0A\u83B2\u53F0\u3002</p><p>\u4E1D\u888D\u6ED1\u843D\u4E0B\u6765\uFF0C\u9732\u51FA\u89C2\u97F3\u5149\u6655\u6D41\u6DCC\u7684\u7389\u4F53\u3002\u771F\u4E0D\u77E5\u6CAE\u6E20\u5927\u5E08\u76EE\u5149\u5982\u4F55\u7280\u5229\uFF0C\u96D5\u51FA\u7684\u7389\u50CF\u7ADF\u4E0E\u51CC\u96C5\u7434\u7684\u88F8\u4F53\u5206\u6BEB\u4E0D\u5DEE\u3002\u4FA7\u5367\u7684\u7389\u50CF\u5E73\u8EBA\u4E0B\u6765\uFF0C\u5B9B\u5982\u6276\u816E\u800C\u7761\u7684\u51CC\u96C5\u7434\uFF0C\u9759\u9759\u5367\u5728\u7D2B\u6A80\u83B2\u53F0\u4E0A\uFF0C\u68A6\u4E2D\u8FD8\u9732\u51FA\u4E9B\u8BB8\u7B11\u610F\u3002</p><p>\u51CC\u96C5\u7434\u5728\u7389\u50CF\u8179\u4E0B\u4E00\u6309\uFF0C\u53EA\u89C1\u89C2\u97F3\u7D27\u5E76\u7684\u53CC\u817F\u95F4\uFF0C\u7F13\u7F13\u4F38\u51FA\u4E00\u6761\u6BDB\u8338\u8338\u7684\u7C97\u957F\u7269\u4F53\uFF0C\u5E73\u5E73\u6A2A\u5728\u8179\u524D\u3002\u90A3\u662F\u4E00\u6839\u7D2B\u6A80\u96D5\u6210\u7684\u9633\u5177\uFF0C\u4E3A\u4E86\u903C\u771F\uFF0C\u5916\u9762\u8FD8\u5305\u4E86\u4E00\u5C42\u517D\u76AE\uFF0C\u4F46\u6545\u610F\u6CA1\u6709\u9664\u53BB\u4E0A\u9762\u7684\u6BDB\u53D1\uFF0C\u770B\u4E0A\u53BB\u5C31\u50CF\u4E00\u6761\u517D\u6839\uFF0C\u72F0\u72DE\u800C\u53C8\u90AA\u6076\u3002</p><p>\u51CC\u96C5\u7434\u5206\u5F00\u53CC\u817F\uFF0C\u4FEF\u5728\u7389\u50CF\u8EAB\u4E0A\uFF0C\u96EA\u81C0\u8D34\u7740\u7389\u50CF\u5149\u6ED1\u7684\u8868\u9762\u5411\u4E0B\u6ED1\u53BB\u3002\u4ECE\u540E\u770B\u6765\uFF0C\u53EA\u89C1\u90A3\u53EA\u767D\u5AE9\u7684\u5927\u5C41\u80A1\u9AD8\u9AD8\u7FD8\u8D77\uFF0C\u4E24\u817F\u95F4\u80A5\u8F6F\u65E0\u6BDB\u7684\u7389\u961C\u9F13\u8D77\u4E00\u56E2\u5706\u5706\u7684\u767D\u4EAE\uFF0C\u633A\u7740\u6DEB\u6C34\u56DB\u6EA2\u7684\u9634\u6237\u671D\u7389\u50CF\u8179\u4E0B\u7684\u6728\u68D2\u9001\u53BB\u3002</p><p>\u9634\u6237\u5728\u575A\u786C\u7684\u9F9F\u5934\u4E0A\u4E00\u89E6\uFF0C\u7ACB\u523B\u719F\u7EC3\u5730\u627E\u597D\u89D2\u5EA6\uFF0C\u5C06\u6728\u523B\u7684\u9F9F\u5934\u5957\u5165\u8089\u7A74\u3002</p><p>\u4E24\u4E2A\u6708\u6765\uFF0C\u51CC\u96C5\u7434\u5DF2\u7ECF\u4E0E\u7389\u50CF\u4EA4\u6B22\u6570\u5EA6\uFF0C\u4F46\u6728\u68D2\u5165\u4F53\uFF0C\u5979\u8FD8\u662F\u7981\u4E0D\u4F4F\u76B1\u8D77\u5A25\u7709\uFF0C\u4F4E\u4F4E\u53EB\u4E86\u4E00\u58F0\u3002</p><p>\u300C\u58F0\u97F3\u5927\u4E9B\u554A\u3002\u300D\u767D\u7389\u9E42\u7728\u7740\u5929\u771F\u65E0\u90AA\u7684\u5927\u773C\u775B\uFF0C\u529D\u9053\uFF1A\u300C\u53EB\u51FA\u6765\u561B\uFF0C\u90A3\u4E9B\u7537\u4EBA\u597D\u559C\u6B22\u542C\u4F60\u53EB\u5E8A\u5462\u3002\u300D</p><p>\u5C48\u8FB1\u7684\u6CEA\u6C34\u4E00\u6EF4\u6EF4\u6389\u5728\u7389\u50CF\u8138\u4E0A\uFF0C\u5C31\u50CF\u89C2\u97F3\u843D\u4E0B\u7684\u773C\u6CEA\u3002\u300C\u554A\u2026\u2026\u300D\u51CC\u96C5\u7434\u54ED\u7740\u6D6A\u53EB\u8D77\u6765\uFF0C\u96EA\u81C0\u4E00\u633A\uFF0C\u5C06\u88F9\u7740\u517D\u76AE\u7684\u6728\u68D2\u5C3D\u6839\u541E\u6CA1\u3002</p><p>\u4E24\u5177\u4E00\u6A21\u4E00\u6837\u7684\u7389\u4F53\u7528\u529B\u78E8\u64E6\u7740\uFF0C\u51CC\u96C5\u7434\u5149\u6DA6\u7684\u7389\u4E73\u6CBF\u7740\u7389\u50CF\u4F18\u7F8E\u7684\u66F2\u7EBF\u6765\u56DE\u6ED1\u52A8\uFF0C\u53C8\u5706\u53C8\u5927\u7684\u5C41\u80A1\u524D\u540E\u633A\u52A8\uFF0C\u65F6\u957F\u65F6\u77ED\u5730\u541E\u5410\u7740\u7389\u50CF\u817F\u95F4\u7C97\u957F\u7684\u517D\u6839\u3002</p><p>\u6D82\u6EE1\u6DEB\u836F\u7684\u9634\u6237\u65E9\u5DF2\u9965\u6E34\u96BE\u5F53\uFF0C\u51CC\u96C5\u7434\u88AB\u8FEB\u53EB\u4E86\u51E0\u58F0\uFF0C\u6700\u540E\u60C5\u6B32\u52C3\u53D1\uFF0C\u6D6A\u53EB\u58F0\u4E0D\u7531\u8D8A\u6765\u8D8A\u54CD\u3002\u6728\u68D2\u6233\u5F04\u4E0B\uFF0C\u9634\u6237\u4E2D\u6DEB\u6DB2\u6CC9\u6D8C\uFF0C\u80BF\u80C0\u7684\u9634\u5507\u65F6\u9F13\u65F6\u6536\uFF0C\u50CF\u4E00\u6735\u7FD5\u5F20\u7684\u6B8B\u82B1\uFF0C\u7528\u5973\u4EBA\u6700\u7F8E\u5999\u6700\u6ED1\u817B\u7684\u871C\u8089\u8214\u8210\u7740\u517D\u76AE\u4E0A\u7684\u6BDB\u53D1\u3002\u96EA\u81C0\u633A\u52A8\u95F4\uFF0C\u6DF1\u85CF\u5176\u4E2D\u7684\u83CA\u809B\u66B4\u9732\u51FA\u6765\uFF0C\u5728\u767D\u751F\u751F\u7684\u81C0\u8089\u95F4\u4E00\u6536\u4E00\u5F20\u3002\u90A3\u56E2\u7EA2\u7EA2\u7684\u809B\u7AA6\u5403\u529B\u5730\u6536\u7F29\u7740\uFF0C\u4F3C\u4E4E\u60F3\u7F29\u56DE\u4F53\u5185\u3002</p><p>\u767D\u7389\u83BA\u4FEE\u957F\u7684\u7389\u817F\u6B3E\u6B3E\u8FC8\u7740\u6B65\u5B50\uFF0C\u8179\u4E0B\u9AD8\u4E3E\u7684\u5047\u9633\u5177\u4E0D\u505C\u98A4\u52A8\uFF0C\u5F77\u4F5B\u4E00\u6761\u4F3A\u673A\u5F85\u53D1\u7684\u6012\u86C7\u3002\u300C\u53EB\u5F97\u771F\u597D\u542C\u5462\uFF0C\u51CC\u5A4A\u5B50\uFF0C\u4F60\u8FD8\u6709\u54EA\u4E2A\u9A9A\u6D1E\u53EF\u4EE5\u7528\u554A\uFF1F\u300D</p><p>\u51CC\u96C5\u7434\u5598\u606F\u7740\u8BF4\u9053\uFF0C\u300C\u8D31\u5974\u7684\u5C41\u773C\u513F\u8FD8\u53EF\u4EE5\u7528\u2026\u2026\u300D</p><p>\u300C\u6211\u600E\u4E48\u770B\u4E0D\u5230\u5462\uFF1F\u300D</p><p>\u51CC\u96C5\u7434\u5403\u529B\u5730\u62B1\u4F4F\u81C0\u4FA7\uFF0C\u5C06\u80A5\u5AE9\u7684\u5927\u767D\u5C41\u80A1\u7528\u529B\u63B0\u5F00\uFF0C\u98A4\u58F0\u9053\uFF1A\u300C\u6C42\u2026\u2026\u6C42\u62A4\u6CD5\u63D2\u8D31\u5974\u7684\u5C41\u773C\u513F\u2026\u2026\u300D</p><p>\u767D\u7389\u83BA\u63E1\u4F4F\u9633\u5177\u6839\u90E8\uFF0C\u7528\u9F9F\u5934\u6572\u4E86\u6572\u5979\u7684\u5706\u81C0\uFF0C\u9119\u5937\u5730\u8BF4\u9053\uFF1A\u300C\u597D\u6D6A\u7684\u9A9A\u8D27\uFF0C\u5C44\u91CC\u63D2\u7740\u4E00\u6839\u8FD8\u60F3\u8981\uFF0C\u602A\u4E0D\u5F97\u4F1A\u5077\u6C49\u5B50\u3002\u7ED9\u4F60\u597D\u4E86\u3002\u300D</p><p>\u51CC\u96C5\u7434\u8FDE\u5FD9\u63A5\u8FC7\u9633\u5177\uFF0C\u5BF9\u51C6\u81EA\u5DF1\u7684\u5C41\u773C\u513F\u7528\u529B\u6345\u4E86\u8FDB\u53BB\u3002\u4E24\u6839\u9633\u5177\u540C\u65F6\u8FDB\u5165\uFF0C\u51E0\u4E4E\u5360\u636E\u4E86\u8179\u8154\u6240\u6709\u7A7A\u95F4\u3002\u51CC\u96C5\u7434\u53EA\u89C9\u5C41\u80A1\u50CF\u8981\u88C2\u5F00\u822C\u88AB\u6324\u5F97\u81A8\u80C0\u8D77\u6765\uFF0C\u5BC6\u95ED\u7684\u5C41\u773C\u513F\u88AB\u786C\u7269\u6324\u5F97\u5706\u5706\u5F20\u5F00\uFF0C\u80A0\u9053\u5185\u65E9\u5DF2\u53D1\u75D2\u7684\u8089\u58C1\u7ACB\u523B\u4F20\u6765\u4E00\u80A1\u96BE\u8A00\u7684\u5FEB\u611F\u3002</p><p>\u300C\u554A\u2026\u2026\u300D\u5802\u5185\u56DE\u54CD\u8D77\u51CC\u96C5\u7434\u5A49\u8F6C\u7684\u5A9A\u53EB\u58F0\u3002\u5979\u7D27\u7D27\u6402\u7740\u8EAB\u4E0B\u7684\u7389\u50CF\uFF0C\u50CF\u4E00\u6761\u6DEB\u8D31\u7684\u6BCD\u72D7\u822C\u6485\u7740\u5C41\u80A1\uFF0C\u88AB\u524D\u9634\u540E\u5EAD\u7684\u4E24\u6761\u5047\u9633\u5177\u5E72\u5F97\u6D51\u5FD8\u4E86\u4E00\u5207\u3002\u6DCB\u6F13\u7684\u6DEB\u6C34\u4ECE\u79D8\u5904\u98DE\u6E85\u800C\u51FA\uFF0C\u6D41\u5F97\u7389\u50CF\u6EE1\u8EAB\u90FD\u662F\u3002</p><p>\u767D\u7389\u9E42\u671D\u8EB2\u5728\u6697\u5904\u7684\u9759\u989C\u7728\u4E86\u7728\u773C\uFF0C\u8273\u7EA2\u7684\u5C0F\u5634\u671D\u51CC\u96C5\u7434\u4E0D\u5C51\u5730\u4E00\u6487\uFF0C\u5632\u5F04\u9053\uFF1A\u300C\u51CC\u5A4A\u5B50\uFF0C\u8FD9\u662F\u4F60\u5BB6\u54CE\uFF0C\u5F53\u7740\u4F60\u7537\u4EBA\u7684\u9762\u53EB\u8FD9\u4E48\u54CD\uFF0C\u4E0D\u89C9\u5F97\u4E22\u4EBA\u5417\uFF1F\u300D</p><p>\u51CC\u96C5\u7434\u795E\u60C5\u604D\u60DA\u5730\u7741\u7740\u7F8E\u76EE\uFF0C\u6731\u5507\u98A4\u6296\u7740\u53D1\u51FA\u65AD\u65AD\u7EED\u7EED\u7684\u6D6A\u53EB\u3002\u591A\u5E74\u7684\u6559\u517B\uFF0C\u4F18\u96C5\u7684\u4E3E\u6B62\uFF0C\u7AEF\u5E84\u7684\u98CE\u97F5\u2026\u2026\u90FD\u50CF\u5979\u90A3\u4E9B\u8863\u7269\u4E00\u6837\u6D12\u843D\u6EE1\u5730\uFF0C\u53EA\u5269\u4E0B\u8D64\u88F8\u7684\u8089\u4F53\u548C\u672C\u80FD\u7684\u6B32\u671B\u3002</p><p>\u300C\u8FD9\u79CD\u8D31\u8D27\uFF0C\u7A7F\u4E0A\u8863\u670D\u88C5\u5F97\u5723\u5973\u4E00\u6837\uFF0C\u6252\u4E86\u8863\u670D\u5C31\u662F\u4E00\u6761\u6BCD\u72D7\u2026\u2026\u300D\u767D\u7389\u83BA\u7B11\u541F\u541F\u5E72\u7740\u51CC\u96C5\u7434\u7684\u5C41\u773C\u513F\uFF0C\u4E00\u624B\u6309\u7740\u5979\u7684\u8170\u80A2\uFF0C\u4F7F\u5979\u7684\u80A5\u81C0\u7FD8\u5F97\u66F4\u9AD8\u3002</p><p>\u767D\u7389\u83BA\u62BD\u9001\u95F4\u5E76\u975E\u987A\u7740\u80A0\u9053\u76F4\u8FDB\u76F4\u51FA\uFF0C\u800C\u662F\u6709\u610F\u5411\u4E0B\u7528\u529B\u3002\u5168\u7136\u4E0D\u987E\u51CC\u96C5\u7434\u8FD8\u6000\u7740\u8EAB\u5B55\uFF0C\u53EA\u597D\u73A9\u5730\u9694\u7740\u80A0\u58C1\u548C\u8179\u819C\uFF0C\u53BB\u9876\u5F04\u8089\u7A74\u4E2D\u7684\u90A3\u6761\u517D\u76AE\u6728\u68D2\u3002</p><p>\u51CC\u96C5\u7434\u5F88\u5FEB\u5C31\u6CC4\u4E86\u8EAB\u5B50\uFF0C\u53EF\u767D\u7389\u83BA\u4E1D\u6BEB\u6CA1\u6709\u505C\u6B47\u7684\u610F\u601D\uFF0C\u5979\u62FD\u7740\u51CC\u96C5\u7434\u7684\u79C0\u53D1\u7528\u529B\u4E00\u633A\uFF0C\u7B11\u9053\uFF1A\u300C\u8FD9\u4E48\u5FEB\u5C31\u6CC4\u4E86\uFF0C\u600E\u4E48\u5F53\u5A4A\u5B50\u5462\uFF1F\u300D</p><p>\u767D\u7389\u9E42\u7B11\u9053\uFF1A\u300C\u5979\u4E0B\u9762\u8FD8\u5728\u6D41\u6C34\u5462\uFF0C\u50CF\u5468\u592B\u4EBA\u8FD9\u79CD\u9A9A\u8D27\uFF0C\u8981\u6CC4\u4E2A\u5341\u6765\u6B21\u624D\u80FD\u715E\u75D2\u5462\u3002\u300D\u5979\u8F6C\u76EE\u671D\u5730\u4E0A\u770B\u53BB\uFF0C\u5A07\u58F0\u9053\uFF1A\u300C\u4EE5\u524D\u8F9B\u82E6\u5468\u638C\u95E8\u4E86\uFF0C\u4EE5\u540E\u5C31\u4E0D\u7528\u62C5\u5FC3\u5C0A\u592B\u4EBA\u518D\u53D1\u9A9A\u2014\u2014\u300D\u8BF4\u7740\u5979\u8138\u8272\u7A81\u7136\u4E00\u53D8\uFF0C\u98DE\u8EAB\u8DC3\u8D77\uFF0C\u62AC\u638C\u671D\u5468\u5B50\u6C5F\u80F8\u53E3\u5370\u53BB\u3002</p><p>\u546F\u7684\u4E00\u58F0\uFF0C\u5468\u5B50\u6C5F\u6BEB\u65E0\u53CD\u5E94\u5730\u4E2D\u4E86\u4E00\u638C\u3002\u767D\u7389\u9E42\u5374\u662F\u5927\u9A87\u9000\u5F00\uFF0C\u7389\u638C\u5FAE\u5FAE\u53D1\u98A4\u3002</p><p>\u300C\u600E\u4E48\u4E86\uFF1F\u300D\u767D\u7389\u83BA\u5947\u602A\u5730\u95EE\u9053\u3002</p><p>\u300C\u6211\u7684\u638C\u529B\u2026\u2026\u300D\u521A\u624D\u767D\u7389\u9E42\u770B\u5230\u5468\u5B50\u6C5F\u8EAB\u5B50\u5FAE\u52A8\uFF0C\u8FDE\u5FD9\u51FA\u624B\uFF0C\u6CA1\u60F3\u5230\u4E00\u638C\u5370\u4E0B\uFF0C\u975E\u4F46\u6CA1\u6709\u91CD\u4F24\u5468\u5B50\u6C5F\uFF0C\u53CD\u800C\u88AB\u4ED6\u5438\u53BB\u4E86\u638C\u529B\u3002</p><p>\u5468\u5B50\u6C5F\u8170\u4FA7\u731B\u7136\u6E85\u51FA\u4E00\u80A1\u8840\u7BAD\uFF0C\u8FD9\u6B21\u518D\u975E\u4E2D\u6BD2\u7684\u7D2B\u9ED1\uFF0C\u800C\u662F\u9C9C\u7EA2\u7684\u65B0\u8840\u3002\u4ED6\u51B7\u51B7\u5F20\u5F00\u864E\u76EE\uFF0C\u4E0D\u89C1\u6709\u4EFB\u4F55\u52A8\u4F5C\u4FBF\u5E73\u5E73\u98DE\u8D77\uFF0C\u5728\u534A\u7A7A\u4E2D\u624B\u811A\u4E00\u52A8\uFF0C\u50F5\u786C\u7684\u8EAB\u4F53\u8C61\u6C34\u6CE2\u4E00\u6837\u6D41\u52A8\u8D77\u6765\u3002</p><p>\u300C\u4E0D\u597D\uFF01\u300D\u767D\u6C0F\u59D0\u59B9\u540C\u58F0\u53EB\u9053\u3002</p><p>\u53EA\u89C1\u5468\u5B50\u6C5F\u8EAB\u5B50\u4E00\u6298\uFF0C\u8D34\u7740\u5899\u58C1\u7F13\u7F13\u6ED1\u4E0B\uFF0C\u63A5\u7740\u53F3\u624B\u5411\u540E\u4E00\u62B9\uFF0C\u60AC\u5728\u58C1\u4E0A\u7684\u6C5F\u6CB3\u5251\u7ACB\u5373\u7834\u9798\u800C\u51FA\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u76F8\u987E\u5931\u8272\uFF0C\u767D\u7389\u83BA\u6765\u4E0D\u53CA\u8D77\u8EAB\uFF0C\u53CD\u624B\u4ECE\u51CC\u96C5\u7434\u53D1\u4E0A\u62D4\u4E0B\u94F6\u9497\uFF0C\u66F2\u6307\u4E00\u5F39\uFF0C\u94F6\u9497\u7075\u86C7\u822C\u5728\u7A7A\u4E2D\u5F2F\u5F2F\u66F2\u66F2\u5212\u8FC7\uFF0C\u671D\u5468\u5B50\u6C5F\u5C04\u53BB\u3002\u767D\u7389\u9E42\u98D8\u8EAB\u800C\u8D77\uFF0C\u8DB3\u5C16\u5728\u6881\u4E0A\u4E00\u70B9\uFF0C\u8F7B\u98CE\u822C\u8FFD\u5728\u94F6\u9497\u4E4B\u540E\u3002</p><p>\u5468\u5B50\u6C5F\u5DE6\u624B\u659C\u659C\u5F53\u80F8\u5212\u8FC7\uFF0C\u7A33\u7A33\u5212\u4E86\u4E2A\u5706\u5F27\u3002\u767D\u7389\u9E42\u77ED\u5251\u8D34\u5728\u8155\u4E0A\uFF0C\u5728\u7A7A\u4E2D\u5A07\u8EAF\u4E00\u626D\uFF0C\u767D\u5149\u5149\u7684\u7C89\u817F\u526A\u5200\u822C\u5939\u5411\u5468\u5B50\u6C5F\u9888\u4E2D\u3002\u8179\u4E0B\u7684\u7EA2\u5DFE\u9006\u98CE\u5377\u5230\u8170\u4E0A\uFF0C\u80A1\u95F4\u9C9C\u7F8E\u7684\u7389\u6237\u6B63\u5BF9\u7740\u5468\u5B50\u6C5F\u7684\u53CC\u773C\uFF0C\u5F77\u4F5B\u8981\u51D1\u4E0A\u53BB\u8BA9\u4ED6\u4EB2\u543B\u4E00\u6837\u3002</p><p>\u767D\u7389\u83BA\u63B7\u51FA\u7684\u94F6\u9497\u7528\u4E0A\u4E86\u72EC\u95E8\u624B\u6CD5\uFF0C\u89D2\u5EA6\u53D8\u5E7B\u4E0D\u5B9A\uFF0C\u53EF\u5468\u5B50\u6C5F\u5E73\u6DE1\u65E0\u5947\u5730\u62AC\u8D77\u624B\uFF0C\u4E0D\u504F\u4E0D\u501A\u6B63\u62C8\u4F4F\u9497\u5C3E\u3002\u63A5\u7740\u4E00\u9053\u9739\u96F3\u822C\u7684\u5251\u5149\u95EA\u8FC7\uFF0C\u6C5F\u6CB3\u5251\u4ECE\u4ED6\u5DE6\u624B\u4E0B\u7FFB\u51FA\uFF0C\u76F4\u5212\u767D\u7389\u9E42\u817F\u95F4\u3002</p><p>\u767D\u7389\u9E42\u9B42\u98DE\u9B44\u6563\uFF0C\u5979\u6545\u610F\u65BD\u51FA\u6B64\u62DB\uFF0C\u662F\u7B97\u8BA1\u7740\u5468\u5B50\u6C5F\u8EAB\u4E3A\u6B63\u4EBA\u541B\u5B50\uFF0C\u505A\u4E86\u90A3\u4E48\u591A\u5E74\u597D\u4EBA\uFF0C\u603B\u4E0D\u597D\u610F\u601D\u5BF9\u7740\u5973\u4EBA\u4E0B\u4F53\u731B\u77A7\uFF0C\u53EA\u8981\u4ED6\u626D\u5934\u95EA\u907F\uFF0C\u81EA\u5DF1\u62A2\u5165\u5251\u5708\uFF0C\u8155\u4E2D\u7684\u77ED\u5251\u5C31\u53EF\u6709\u4E86\u7528\u6B66\u4E4B\u5730\u3002\u6CA1\u6709\u60F3\u5230\u5468\u5B50\u6C5F\u975E\u4F46\u4E0D\u95EA\u4E0D\u907F\uFF0C\u53CD\u800C\u4E00\u5251\u523A\u5411\u5979\u7684\u4E0B\u9634\uFF0C\u5206\u660E\u662F\u629B\u5F00\u4E00\u5207\uFF0C\u53EA\u6C42\u53D6\u5979\u6027\u547D\u3002</p><p>\u9759\u989C\u5728\u5916\u9762\u770B\u5F97\u76EE\u7729\u795E\u9A70\uFF0C\u5E08\u7236\u8FD9\u62DB\u7EF5\u91CC\u9488\u672C\u662F\u4E5D\u534E\u5251\u6D3E\u7684\u5165\u95E8\u529F\u592B\uFF0C\u53EF\u4ECE\u4ED6\u624B\u91CC\u65BD\u5C55\u51FA\u6765\uFF0C\u5DE6\u624B\u7684\u5706\u5F27\u548C\u53F3\u5251\u7684\u76F4\u523A\u4E00\u6162\u4E00\u5FEB\uFF0C\u6C14\u52BF\u51DD\u91CD\u84EC\u52C3\uFF0C\u6D51\u82E5\u5929\u6210\uFF0C\u4E0D\u6127\u662F\u5929\u4E0B\u7B2C\u4E00\u540D\u5251\u3002</p><p>\u767D\u7389\u9E42\u6B66\u529F\u4E5F\u81EA\u4E0D\u51E1\uFF0C\u5979\u6765\u4E0D\u53CA\u626D\u8170\u95EA\u907F\uFF0C\u7ACB\u5373\u53F3\u811A\u4F4E\u5782\uFF0C\u5DE6\u817F\u6781\u529B\u671D\u540E\u8E22\u53BB\uFF0C\u53CC\u817F\u7531\u6A2A\u5939\u53D8\u4E3A\u4FA7\u5288\uFF0C\u582A\u582A\u8EB2\u8FC7\u6C5F\u6CB3\u5251\u7684\u950B\u9510\u3002\u811A\u5C16\u70B9\u5230\u5730\u9762\uFF0C\u7ACB\u5373\u5F39\u5F00\uFF0C\u9AD8\u4E3E\u7684\u5DE6\u817F\u987A\u52BF\u5408\u62E2\uFF0C\u671D\u540E\u98DE\u63A0\u3002\u8FD9\u51E0\u62DB\u7528\u7684\u90FD\u662F\u8170\u817F\u529F\u592B\uFF0C\u914D\u5408\u7740\u5979\u4E0D\u7740\u4E00\u4E1D\u7684\u4E0B\u4F53\uFF0C\u53EA\u89C1\u7C89\u817F\u7FFB\u98DE\u95F4\u80A4\u5149\u56DB\u5C04\uFF0C\u7389\u6237\u5FFD\u5F00\u5FFD\u5408\uFF0C\u9999\u8273\u65E0\u6BD4\u3002</p><p>\u4F46\u5468\u5B50\u6C5F\u5BF9\u5979\u6DEB\u8273\u7684\u59FF\u6001\u89C6\u82E5\u65E0\u7779\uFF0C\u5DE6\u624B\u4E24\u6307\u4E00\u5F39\uFF0C\u51CC\u96C5\u7434\u7684\u94F6\u9497\u95EA\u7535\u822C\u98DE\u51FA\uFF0C\u76F4\u76F4\u6CA1\u5165\u767D\u7389\u9E42\u5A07\u5AE9\u7684\u7389\u80A1\u95F4\u3002\u767D\u7389\u9E42\u53CC\u817F\u4E00\u8F6F\uFF0C\u8DEA\u5728\u5730\u4E0A\u3002\u90A3\u6839\u94F6\u9497\u6B63\u523A\u5728\u5979\u4E24\u817F\u6B63\u4E2D\u7684\u4F1A\u9634\u5904\uFF0C\u53EA\u5269\u4E00\u622A\u9497\u5C3E\u9732\u5728\u76AE\u8089\u5916\u3002\u4E00\u7F15\u7EC6\u7EC6\u7684\u9C9C\u8840\u7F13\u7F13\u6D41\u4E0B\uFF0C\u5F77\u4F5B\u4E00\u4E32\u6BB7\u7EA2\u7684\u739B\u7459\u5370\u5728\u767D\u7389\u822C\u7684\u5927\u817F\u4E0A\u3002\u5468\u5B50\u6C5F\u5DE6\u624B\u4E00\u6325\uFF0C\u767D\u7389\u9E42\u8C61\u53EA\u98CE\u7B5D\u822C\u4ECE\u655E\u5F00\u7684\u5927\u95E8\u4E2D\u98DE\u51FA\u51CC\u98CE\u5802\u3002</p><p>\u5468\u5B50\u6C5F\u4ED7\u5251\u800C\u7ACB\uFF0C\u7070\u767D\u7684\u8138\u4E0A\u900F\u51FA\u4E00\u80A1\u5947\u5F02\u7684\u7EA2\u5149\u3002\u767D\u6C0F\u59D0\u59B9\u6700\u5927\u7684\u5931\u8BEF\u5C31\u662F\u8F7B\u89C6\u4E86\u8FD9\u4F4D\u4E5D\u534E\u638C\u95E8\u3002\u4E24\u4EBA\u8D81\u7740\u4ED6\u8840\u8109\u9006\u884C\u7684\u65F6\u5019\u5236\u4F4F\u4ED6\u7A74\u9053\uFF0C\u5374\u56E0\u4E3A\u6DEB\u73A9\u51CC\u96C5\u7434\u800C\u5FD8\u4E86\u75DB\u4E0B\u6740\u624B\u3002\u5468\u5B50\u6C5F\u5728\u8FD9\u4E00\u4F1A\u513F\u5DE5\u592B\u7A33\u4F4F\u5FC3\u795E\uFF0C\u8C03\u771F\u51DD\u6C14\uFF0C\u4E0D\u4F46\u903C\u51FA\u4E86\u5267\u6BD2\uFF0C\u8FD8\u51B2\u5F00\u4E86\u7A74\u9053\uFF0C\u867D\u7136\u4ED8\u51FA\u4E86\u4E24\u6210\u529F\u529B\u7684\u4EE3\u4EF7\uFF0C\u4F46\u5DF2\u7ECF\u8DB3\u4EE5\u5E94\u4ED8\u4EFB\u4F55\u654C\u4EBA\u3002</p><p>\u767D\u7389\u83BA\u77AA\u5927\u7F8E\u76EE\uFF0C\u671B\u7740\u59B9\u59B9\u98DE\u51FA\u7684\u8EAB\u5F71\uFF0C\u7A81\u7136\u5C16\u53EB\u4E00\u58F0\uFF0C\u4ECE\u51CC\u96C5\u7434\u809B\u4E2D\u62D4\u51FA\u8EAB\u5B50\uFF0C\u4E0D\u987E\u4E00\u5207\u5730\u671D\u5468\u5B50\u6C5F\u6251\u6765\u3002</p><p>\u5468\u5B50\u6C5F\u957F\u5251\u72B9\u5982\u6C5F\u6CB3\u6CFB\u5730\uFF0C\u9690\u9690\u5E26\u7740\u98CE\u96F7\u4E4B\u58F0\u3002\u4ED6\u4E00\u751F\u4E2D\u672A\u9047\u52B2\u654C\uFF0C\u672C\u6765\u5DF2\u96BE\u6709\u5BF8\u8FDB\uFF0C\u4F46\u5F53\u65E5\u5728\u6D1B\u9633\u9047\u5230\u7684\u90A3\u540D\u5927\u6C49\uFF0C\u5374\u4F7F\u4ED6\u5341\u5E74\u82E6\u7EC3\uFF0C\u4E0D\u6562\u7A0D\u6709\u677E\u61C8\u3002\u767D\u7389\u83BA\u867D\u7136\u62FC\u4E86\u6027\u547D\uFF0C\u77ED\u5251\u4E5F\u65E0\u6CD5\u9012\u5230\u4ED6\u8EAB\u65C1\u5C3A\u8BB8\u3002</p><p>\u767D\u7389\u83BA\u62AB\u5934\u6563\u53D1\uFF0C\u84DD\u6C6A\u6C6A\u7684\u77ED\u5251\u4E0A\u6311\u4E0B\u62B9\uFF0C\u8BF8\u822C\u9634\u6BD2\u7684\u624B\u6CD5\u65BD\u5C55\u65E0\u4F59\u3002\u59B9\u59B9\u50CF\u662F\u6D88\u5931\u822C\u6CA1\u6709\u534A\u70B9\u58F0\u97F3\uFF0C\u5979\u5FC3\u5934\u53D1\u6025\uFF0C\u7EA2\u7740\u773C\u53EB\u9053\uFF1A\u300C\u51CC\u5A4A\u5B50\uFF0C\u5927\u58F0\u53EB\uFF0C\u8BA9\u4F60\u7537\u4EBA\u770B\u770B\u4F60\u7684\u9A9A\u6837\uFF01\u300D</p><p>\u51CC\u96C5\u7434\u6014\u6014\u671B\u7740\u4E08\u592B\uFF0C\u8EAB\u5B50\u4E00\u52A8\u4E0D\u52A8\u3002</p><p>\u300C\u8D31\u4EBA\uFF01\u4F5C\u6B7B\u5417\uFF01\u300D</p><p>\u51CC\u96C5\u7434\u75F4\u75F4\u4F0F\u5728\u7389\u50CF\u4E0A\uFF0C\u5C31\u50CF\u4F0F\u5728\u6C34\u4E0A\u7684\u4ED9\u5B50\uFF0C\u6620\u7740\u81EA\u5DF1\u7684\u5F71\u5B50\u3002</p><p>\u767D\u7389\u83BA\u7834\u53E3\u5927\u9A82\u9053\uFF1A\u300C\u6B7B\u5A4A\u5B50\uFF0C\u5E72\u8FC7\u4F60\u7684\u7537\u4EBA\u6CA1\u6709\u4E00\u5343\u4E5F\u6709\u516B\u767E\uFF0C\u5C44\u90FD\u88AB\u808F\u5F97\u53D1\u9ED1\uFF0C\u8FD8\u88C5\u4EC0\u4E48\u9A9A\u6837\uFF01\u300D</p><p>\u6C5F\u6CB3\u5251\u731B\u7136\u4E00\u7D27\uFF0C\u51CC\u5389\u7684\u5251\u98CE\u5F53\u80F8\u5212\u8FC7\uFF0C\u65A9\u65AD\u4E86\u675F\u80F8\u7684\u7EA2\u7EB1\u3002\u4E24\u53EA\u767D\u5149\u5149\u7684\u4E73\u623F\u987F\u65F6\u5F39\u5F00\uFF0C\u5728\u80F8\u524D\u6296\u51FA\u4E00\u7247\u96EA\u817B\u7684\u8089\u5149\u3002\u767D\u7389\u83BA\u7D22\u6027\u626F\u4E0B\u8170\u95F4\u7684\u7EA2\u5DFE\uFF0C\u5DE6\u624B\u4E00\u62E7\uFF0C\u7EA2\u5DFE\u7ACB\u523B\u5706\u8F6E\u72B6\u5F20\u5F00\uFF0C\u5B9B\u5982\u4E00\u53EA\u8F6F\u76FE\u3002</p><p>\u767D\u7389\u83BA\u4E24\u4E73\u5FFD\u65CB\u5FFD\u6446\uFF0C\u6CA1\u6709\u7247\u523B\u5B89\u5B81\uFF0C\u4E73\u8089\u76F8\u51FB\uFF0C\u53D1\u51FA\u4E0D\u7EDD\u4E8E\u8033\u7684\u8106\u54CD\uFF0C\u8BA9\u4EBA\u4EE5\u4E3A\u90A3\u4E24\u53EA\u6C89\u7538\u7538\u7684\u4E73\u7403\u968F\u65F6\u90FD\u4F1A\u88AB\u649E\u5F97\u7C89\u788E\u3002\u67D4\u8F6F\u7684\u8170\u80A2\u5F2F\u6298\u95F4\u7075\u5DE7\u4E4B\u6781\uFF0C\u4E24\u6761\u7389\u817F\u65F6\u66F2\u65F6\u5F2F\uFF0C\u4E0D\u65F6\u9732\u51FA\u79D8\u5904\u7EA2\u6DA6\u7684\u5AE9\u8089\u3002\u817E\u632A\u65F6\uFF0C\u767D\u767D\u7684\u5706\u81C0\u6446\u4E2A\u4E0D\u505C\uFF0C\u81C0\u6C9F\u65F6\u5F00\u65F6\u5408\uFF0C\u751A\u81F3\u80FD\u770B\u5230\u91CC\u9762\u7EA2\u7EA2\u7684\u83CA\u809B\u3002\u5C24\u4E3A\u5996\u5F02\u7684\u662F\u5979\u8179\u4E0B\u90A3\u6839\u672A\u6765\u5F97\u53CA\u53D6\u4E0B\u7684\u5047\u9633\u5177\uFF0C\u9ED1\u4EAE\u7684\u68D2\u8EAB\u6CBE\u6EE1\u4E86\u6DEB\u6DB2\uFF0C\u5728\u8273\u5973\u767D\u5AE9\u7684\u9634\u961C\u4E0A\u4E00\u7529\u4E00\u7529\uFF0C\u4F3C\u4E4E\u8FD8\u5728\u51CC\u96C5\u7434\u4F53\u5185\u51B2\u7A81\u3002</p><p>\u5468\u5B50\u6C5F\u9762\u6C89\u5982\u6C34\uFF0C\u6C5F\u6CB3\u5251\u94F6\u5149\u56DB\u5C04\uFF0C\u5C06\u8EAB\u524D\u5996\u8273\u7684\u88F8\u5973\u903C\u5F97\u6B65\u6B65\u540E\u9000\u3002\u767D\u7389\u83BA\u7684\u62DB\u672F\u8D8A\u6765\u8D8A\u6C89\uFF0C\u5DF2\u7ECF\u88AB\u5468\u5B50\u6C5F\u7684\u6D69\u7136\u6B63\u6C14\u538B\u5728\u4E0B\u98CE\uFF0C\u5979\u53EB\u9A82\u9053\uFF1A\u300C\u59D3\u5468\u7684\uFF0C\u4F60\u6562\u4F24\u6211\u59B9\u59B9\uFF0C\u6211\u5C31\u628A\u90A3\u8D31\u5A4A\u5B50\u9001\u5230\u9896\u660C\uFF0C\u8BA9\u519B\u6C49\u8F6E\u756A\u5E72\u4F60\u8001\u5A46\u7684\u8D31\u5C44\uFF01\u4EC0\u4E48\u65F6\u5019\u5E72\u6B7B\u4E86\uFF0C\u518D\u628A\u5979\u5927\u5378\u516B\u5757\uFF0C\u62E3\u51FA\u4F60\u8001\u5A46\u88AB\u4EBA\u73A9\u70C2\u7684\u81ED\u5C44\u5582\u72D7\uFF01\u300D</p><p>\u5468\u5B50\u6C5F\u5251\u6CD5\u4E00\u53D8\uFF0C\u6C5F\u6CB3\u5251\u94F6\u5149\u6E10\u6536\uFF0C\u4F46\u767D\u7389\u83BA\u5374\u4E1D\u6BEB\u6CA1\u6709\u8F7B\u677E\uFF0C\u77ED\u5251\u6108\u53D1\u6EDE\u91CD\uFF0C\u5979\u5FFD\u7136\u610F\u8BC6\u5230\u5468\u5B50\u6C5F\u662F\u8981\u8017\u5C3D\u5979\u7684\u529F\u529B\uFF0C\u8BA9\u5979\u6162\u6162\u7B49\u6B7B\u3002\u5979\u54AC\u7259\u4E00\u7B11\uFF0C\u300C\u5468\u5B50\u6C5F\uFF01\u4F60\u5C31\u7B97\u6740\u4E86\u6211\uFF0C\u4F60\u8001\u5A46\u7684\u5C44\u4E5F\u6D17\u4E0D\u5E72\u51C0\u4E86\uFF01\u4F60\u90A3\u5A4A\u5B50\u8001\u5A46\u4E00\u5929\u559D\u7684\u7CBE\u6DB2\uFF0C\u6BD4\u4F60\u4E00\u8F88\u5B50\u5C04\u5230\u5979\u5C44\u91CC\u7684\u8FD8\u591A\uFF01\u4F60\u2014\u2014\u300D\u767D\u7389\u83BA\u7684\u58F0\u97F3\u7A81\u7136\u505C\u4F4F\uFF0C\u4E00\u67C4\u96EA\u4EAE\u7684\u957F\u5251\u5E73\u5E73\u67B6\u5728\u4E73\u4E0B\uFF0C\u5706\u6EDA\u6EDA\u7684\u4E73\u7403\u5728\u5251\u950B\u4E0A\u4E00\u8361\u4E00\u8361\uFF0C\u6162\u6162\u5B89\u9759\u4E0B\u6765\u3002\u5468\u5B50\u6C5F\u624B\u8155\u5411\u524D\u4E00\u633A\uFF0C\u4FBF\u53EF\u523A\u5165\u5979\u7684\u5FC3\u810F\uFF1B\u5411\u4E0A\u4E00\u6311\uFF0C\u4F1A\u5272\u4E0B\u5979\u4E24\u53EA\u4E73\u623F\uFF1B\u5411\u4E0B\u4E00\u5212\uFF0C\u5C31\u662F\u80A0\u7A7F\u809A\u88C2\uFF0C\u5411\u540E\u4E00\u9000\u2026\u2026\u5979\u4E0D\u6562\u60F3\u81EA\u5DF1\u4F1A\u6709\u90A3\u6837\u7684\u597D\u8FD0\u6C14\u3002</p>',86);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
