import{e as p}from"./app.630af5f2.js";import{_ as e}from"./plugin-vue_export-helper.21dcd24c.js";const r={},a=p('<h1 id="_38" tabindex="-1"><a class="header-anchor" href="#_38" aria-hidden="true">#</a> 38</h1><p>\u90A3\u5973\u5B50\u5728\u7A7A\u4E2D\u8170\u8EAB\u4E00\u62E7\uFF0C\u4E00\u6761\u94F6\u7D22\u4ECE\u8170\u95F4\u7B14\u76F4\u4F38\u51FA\uFF0C\u9876\u7AEF\u4E00\u67C4\u7389\u767D\u8272\u7684\u5F2F\u94A9\u75BE\u5C04\u800C\u81F3\uFF0C\u786C\u751F\u751F\u6495\u5F00\u5251\u5E55\u3002\u9759\u989C\u53EA\u89C9\u957F\u5251\u8FDE\u9707\uFF0C\u4E00\u80A1\u5145\u6C9B\u4E4B\u6781\u7684\u52B2\u6C14\u5E2D\u5377\u800C\u6765\uFF0C\u70BD\u70ED\u7684\u6C14\u6D41\u8F7B\u6613\u4FBF\u900F\u8FC7\u62A4\u6CD5\u771F\u6C14\uFF0C\u4E0E\u5979\u7684\u300A\u623F\u5FC3\u661F\u76D1\u300B\u786C\u62DA\u4E00\u8BB0\u3002</p><p>\u771F\u6C14\u76F8\u4EA4\uFF0C\u9759\u989C\u7ACB\u65F6\u89C9\u5BDF\u51FA\u5979\u4F7F\u7684\u662F\u7384\u95E8\u6B63\u5B97\u7684\u7EDD\u5B66\uFF0C\u90A3\u80A1\u771F\u6C14\u9187\u539A\u5E73\u6B63\uFF0C\u4E0D\u6CBE\u534A\u5206\u90AA\u6C14\uFF0C\u53EA\u662F\u5979\u8EAB\u4E3A\u5973\u5B50\uFF0C\u7EC3\u7684\u5185\u529F\u5374\u9633\u521A\u4E4B\u6781\uFF0C\u8FDE\u90A3\u53EA\u7389\u8F6E\u8FB9\u7F18\u4E5F\u8361\u6F3E\u7740\u7EEF\u7EA2\u7684\u5149\u8292\uFF0C\u5B9B\u5982\u70C8\u65E5\u7684\u706B\u7130\u3002</p><p>\u90A3\u80A1\u7EAF\u9633\u6B63\u6C14\u88AB\u300A\u623F\u5FC3\u661F\u76D1\u300B\u7684\u771F\u6C14\u4E00\u963B\uFF0C\u9661\u7136\u589E\u5F3A\u6570\u500D\uFF0C\u9759\u989C\u5A07\u8EAF\u5267\u9707\uFF0C\u6301\u5251\u7684\u53F3\u81C2\u8C61\u88AB\u70C8\u706B\u70E7\u7099\u822C\u5267\u75DB\u96BE\u5F53\u3002\u300C\u53EE\u300D\u7684\u4E00\u58F0\u8F7B\u54CD\uFF0C\u7389\u8F6E\u65A9\u65AD\u957F\u5251\uFF0C\u5C06\u9759\u989C\u9707\u5F97\u5012\u98DE\u51FA\u53BB\u3002</p><p>\u4E0D\u7B49\u5979\u8EAB\u5B50\u843D\u5730\uFF0C\u94F6\u7D22\u5982\u5F71\u968F\u5F62\u98DE\u6765\u7F20\u4F4F\u7EA4\u8170\u3002\u9759\u989C\u594B\u529B\u63B7\u51FA\u6B8B\u5251\uFF0C\u7EA4\u638C\u4E00\u7FFB\uFF0C\u5C06\u94F6\u7D22\u7ED5\u5728\u8155\u4E0A\uFF0C\u63A5\u7740\u5DE6\u624B\u4F38\u51FA\uFF0C\u8BD5\u56FE\u626F\u65AD\u94F6\u7D22\u3002</p><p>\u94F6\u7D22\u7A81\u7136\u4E00\u6296\uFF0C\u9759\u989C\u53CC\u624B\u89E6\u7535\u822C\u5F39\u5F00\uFF0C\u5979\u8E59\u7709\u75DB\u53EB\u4E00\u58F0\uFF0C\u53EA\u89C9\u53F3\u8155\u53F3\u8155\u75DB\u5982\u5200\u5272\uFF0C\u5DE6\u624B\u638C\u5FC3\u8C61\u634F\u4F4F\u4E00\u6761\u70E7\u7EA2\u7684\u94C1\u94FE\uFF0C\u75DB\u5F7B\u5FC3\u80BA\u3002\u63A5\u7740\u8170\u95F4\u4E00\u9707\uFF0C\u706B\u70ED\u7684\u6C14\u6D41\u4ECE\u94F6\u7D22\u4E0A\u900F\u4F53\u800C\u5165\uFF0C\u9759\u989C\u8D2F\u6EE1\u7684\u771F\u6C14\u731B\u7136\u4E00\u677E\uFF0C\u5A07\u8EAF\u8F6F\u8F6F\u5782\u4E0B\u3002</p><p>\u767D\u8863\u5973\u5B50\u62AC\u624B\u628A\u5979\u62C9\u5230\u8EAB\u524D\uFF0C\u51DB\u7136\u9053\uFF1A\u300C\u65E0\u803B\u6DEB\u8D3C\uFF0C\u4F60\u8FD8\u6709\u4F55\u8BDD\u8BF4\uFF1F\u300D</p><p>\u9759\u989C\u8863\u88D9\u51CC\u4E71\uFF0C\u4EB5\u88E4\u6389\u5728\u8E1D\u95F4\uFF0C\u4E0B\u4F53\u8D64\u88F8\uFF0C\u521A\u5438\u6536\u4E86\u9634\u7CBE\u7684\u9633\u5177\u8FD8\u65E0\u6CD5\u6536\u56DE\u4F53\u5185\uFF0C\u786C\u786C\u6311\u5728\u82B1\u5507\u95F4\uFF0C\u770B\u4E0A\u53BB\u8981\u591A\u72FC\u72C8\u6709\u591A\u72FC\u72C8\u3002\u5979\u5FC3\u5934\u53C8\u6068\u53C8\u6094\uFF0C\u5047\u5982\u62A5\u4EC7\u4E0D\u6210\u9001\u4E86\u6027\u547D\uFF0C\u90A3\u4E5F\u7F62\u4E86\u3002\u53EF\u8FD9\u4F1A\u513F\u83AB\u540D\u5176\u5999\u6740\u51FA\u6765\u4E00\u4F4D\u5973\u4FA0\uFF0C\u628A\u81EA\u5DF1\u5F53\u4F5C\u65BD\u5C55\u6DEB\u672F\u7684\u5996\u4EBA\u62FF\u4E0B\uFF0C\u66FF\u5929\u884C\u9053\uFF0C\u53EF\u771F\u662F\u6B7B\u4E0D\u7791\u76EE\u3002\u8FD9\u5973\u5B50\u6B66\u529F\u4E4B\u5F3A\uFF0C\u5DF2\u7ECF\u5230\u4E86\u532A\u5937\u6240\u601D\u7684\u5883\u5730\uFF0C\u4E0D\u4F46\u8FDC\u80DC\u5E08\u7236\u5168\u76DB\u4E4B\u65F6\uFF0C\u8FDE\u521A\u624D\u7684\u6C90\u58F0\u4F20\u4E5F\u975E\u5979\u654C\u624B\u3002\u5373\u4F7F\u81EA\u5DF1\u5316\u5C3D\u5E08\u5A18\u7684\u7CBE\u5143\uFF0C\u6574\u8863\u518D\u6218\uFF0C\u4E5F\u662F\u6709\u8D25\u65E0\u80DC\u3002</p><p>\u5973\u4FA0\u68EE\u7136\u9053\uFF1A\u300C\u65E2\u7136\u65E0\u8BDD\u53EF\u8BF4\uFF0C\u90A3\u5C31\u7EB3\u547D\u6765\u5427\uFF01\u300D</p><p>\u300C\u4E0D\u8981\u2026\u2026\u300D\u8BF4\u8BDD\u7684\u5374\u662F\u51CC\u96C5\u7434\uFF0C\u5979\u5934\u4E0A\u7F69\u7740\u9ED1\u5E03\uFF0C\u770B\u4E0D\u5230\u52A8\u624B\u7684\u60C5\u666F\uFF0C\u4F46\u4E24\u4EBA\u7684\u5BF9\u8BDD\u5374\u542C\u5F97\u6E05\u6E05\u695A\u695A\uFF0C\u6B64\u65F6\u542C\u5230\u8FD9\u4F4D\u5973\u4FA0\u8981\u53D6\u9759\u989C\u6027\u547D\uFF0C\u5979\u800C\u7528\u529B\u6491\u8D77\u8EAB\u5B50\uFF0C\u7528\u865A\u5F31\u7684\u58F0\u97F3\u8BF4\u9053\uFF1A\u300C\u4E0D\u8981\u6740\u5979\u2026\u2026\u662F\u6211\u8BA9\u5979\u505A\u7684\u2026\u2026\u300D</p><p>\u90A3\u5973\u5B50\u7F8E\u76EE\u751F\u5BD2\uFF0C\u5389\u58F0\u9053\uFF1A\u300C\u4E0D\u8981\u8138\u7684\u8D31\u8D27\uFF01\u63B0\u7740\u5C41\u80A1\u8BA9\u4EBA\u808F\u5417\uFF1F\u300D\u8BF4\u7740\u4E00\u811A\u5C06\u51CC\u96C5\u7434\u8E22\u5012\uFF0C\u624B\u4E00\u626C\uFF0C\u4E09\u679A\u94F6\u9488\u4E0D\u5DEE\u6BEB\u5398\u5730\u7A7F\u900F\u4E86\u5979\u7684\u4E73\u5934\u548C\u82B1\u8482\u3002</p><p>\u9488\u4E0A\u4E00\u80A1\u82E5\u6709\u82E5\u65E0\u7684\u771F\u6C14\u4FB5\u5165\u4F53\u5185\uFF0C\u4E09\u5904\u5730\u65B9\u540C\u65F6\u70ED\u4E86\u8D77\u6765\uFF0C\u521A\u5E73\u606F\u7684\u6B32\u706B\u518D\u5EA6\u70B9\u71C3\uFF0C\u51CC\u96C5\u7434\u75DB\u82E6\u5730\u8737\u7740\u7389\u4F53\uFF0C\u624B\u6307\u98A4\u6296\u7740\u78B0\u5230\u94F6\u9488\uFF0C\u5374\u600E\u4E48\u4E5F\u65E0\u529B\u62D4\u51FA\u3002</p><p>\u9759\u989C\u5FC3\u5934\u5FD0\u5FD1\uFF0C\u8FD9\u5973\u5B50\u4E0D\u77E5\u662F\u4F55\u65B9\u795E\u5723\uFF0C\u4F9D\u5979\u7684\u6B66\u529F\uFF0C\u5E94\u8BE5\u5728\u6C5F\u6E56\u4E2D\u9F0E\u9F0E\u6709\u540D\uFF0C\u4E3A\u4F55\u81EA\u5DF1\u5374\u4ECE\u672A\u542C\u8BF4\u8FC7\u8FD9\u4E48\u4E00\u53F7\u4EBA\u7269\uFF1F\u5979\u6B63\u6C14\u51DB\u7136\u7684\u795E\u60C5\uFF0C\u4E00\u770B\u4FBF\u662F\u95EF\u8361\u6C5F\u6E56\u884C\u4FA0\u4ED7\u4E49\u7684\u4FA0\u5973\uFF0C\u53EF\u5979\u51FA\u73B0\u5F97\u83AB\u540D\u5176\u5999\uFF0C\u6B66\u529F\u5F3A\u5F97\u83AB\u540D\u5176\u5999\uFF0C\u5BF9\u5F85\u5E08\u5A18\u7684\u4E3E\u52A8\u66F4\u662F\u83AB\u540D\u5176\u5999\u2026\u2026</p><p>\u5FFD\u7136\u8170\u95F4\u4E00\u7D27\uFF0C\u5973\u4FA0\u5C55\u81C2\u63FD\u4F4F\u5979\u7684\u7EA4\u8170\uFF0C\u7D27\u7D27\u8D34\u5728\u8179\u4E0A\u3002\u9694\u7740\u8584\u8584\u7684\u767D\u8863\uFF0C\u80FD\u6E05\u6670\u5730\u611F\u89C9\u5230\u5979\u8EAB\u4F53\u7684\u6E29\u70ED\u548C\u5F39\u6027\u3002</p><p>\u300C\u8FD9\u4E48\u6807\u81F4\u7684\u9634\u9633\u4EBA\uFF0C\u672C\u5EA7\u8FD8\u662F\u7B2C\u4E00\u6B21\u9047\u4E0A\u5462\u3002\u300D\u90A3\u5973\u5B50\u63FD\u4F4F\u9759\u989C\u7684\u8170\u81C0\uFF0C\u7528\u4E0B\u8179\u78E8\u64E6\u7740\u5979\u7684\u9633\u5177\u3002\u9759\u989C\u6115\u7136\u4E3E\u76EE\uFF0C\u53EA\u89C1\u5979\u8138\u4E0A\u7684\u8083\u6740\u4E49\u6124\u50CF\u70C8\u65E5\u4E0B\u7684\u6625\u96EA\u4E00\u6837\u5316\u53BB\uFF0C\u9732\u51FA\u5996\u51B6\u7684\u98CE\u60C5\uFF0C\u8F6C\u773C\u95F4\u5C31\u53D8\u5F97\u53C8\u55F2\u53C8\u9A9A\uFF0C\u4E0E\u521A\u624D\u7684\u5927\u4E49\u51DB\u7136\u5224\u82E5\u4E24\u4EBA\u3002</p><p>\u90A3\u5973\u5B50\u793A\u5A01\u4F3C\u7684\u633A\u8D77\u8EAB\u4F53\uFF0C\u4E00\u5BF9\u80A5\u7855\u7684\u4E73\u7403\u6324\u538B\u8FC7\u6765\uFF0C\u786C\u786C\u7684\u4E73\u5934\u8C61\u5C0F\u77F3\u5B50\u4E00\u6837\u9876\u5728\u80F8\u524D\u3002\u9759\u989C\u7684\u4E73\u623F\u5DF2\u7ECF\u8DB3\u591F\u4E30\u6EE1\uFF0C\u4F46\u6BD4\u8D77\u8FD9\u4F4D\u5973\u4FA0\u7684\u8C6A\u4E73\u5374\u900A\u8272\u8BB8\u591A\uFF0C\u80A5\u8F6F\u7684\u4E73\u8089\u6CE2\u6D9B\u4E00\u6837\u5728\u80F8\u53E3\u6D8C\u52A8\uFF0C\u6324\u5F97\u5979\u51E0\u4E4E\u5598\u4E0D\u8FC7\u6C14\u6765\u3002</p><p>\u300C\u5C0F\u5996\u7CBE\uFF0C\u628A\u672C\u5EA7\u4F3A\u5019\u597D\u4E86\uFF0C\u4E0D\u59A8\u653E\u4F60\u4E00\u6761\u751F\u8DEF\uFF0C\u4E0D\u7136\u2026\u2026\u300D\u90A3\u5973\u5B50\u634F\u4F4F\u5979\u7684\u8089\u68D2\uFF0C\u7528\u6307\u7532\u5728\u4E0A\u9762\u5212\u7740\uFF0C\u300C\u672C\u5EA7\u5C31\u7ED9\u4F60\u6765\u4E2A\u5148\u5978\u540E\u6740\uFF01\u300D</p><p>\u9759\u989C\u671B\u7740\u8FD9\u4F4D\u9177\u4F3C\u5973\u4FA0\u7684\u91C7\u82B1\u5927\u76D7\uFF0C\u557C\u7B11\u7686\u975E\uFF0C\u5979\u88AB\u90A3\u5BF9\u7855\u4E73\u6324\u5F97\u900F\u4E0D\u8FC7\u6C14\u6765\uFF0C\u53EA\u80FD\u52C9\u5F3A\u70B9\u4E86\u70B9\u5934\u3002</p><p>\u767D\u8863\u5973\u5B50\u62AC\u8D77\u624B\uFF0C\u6CA1\u89C1\u5979\u5982\u4F55\u4F5C\u52BF\uFF0C\u6307\u5C16\u4FBF\u5229\u5203\u822C\u5212\u7834\u80F8\u895F\uFF0C\u63E1\u4F4F\u9759\u989C\u7684\u4E00\u53EA\u96EA\u4E73\u62C9\u4E86\u51FA\u6765\u3002\u9759\u989C\u677E\u4E86\u53E3\u6C14\uFF0C\u606D\u7EF4\u9053\uFF1A\u300C\u5973\u4FA0\u7684\u4E73\u623F\u597D\u4E30\u6EE1\u5462\uFF0C\u5C0F\u5973\u5B50\u4E24\u53EA\u52A0\u8D77\u6765\u4E5F\u4E0D\u53CA\u60A8\u5462\u3002\u300D</p><p>\u90A3\u5973\u5B50\u50B2\u7136\u4E00\u7B11\uFF0C\u8EAB\u5B50\u5FAE\u5FAE\u4E00\u633A\uFF0C\u53EA\u89C1\u4E00\u56E2\u767D\u817B\u7684\u96EA\u8089\u6491\u5F00\u8863\u895F\uFF0C\u8DC3\u7136\u800C\u51FA\uFF0C\u90A3\u5938\u5F20\u7684\u5C3A\u5BF8\uFF0C\u8BA9\u4EBA\u6000\u7591\u8FD9\u662F\u4E0D\u662F\u771F\u7684\u3002\u5979\u7684\u4E73\u5934\u53C8\u7EA2\u53C8\u786C\uFF0C\u6BD4\u5E38\u4EBA\u5927\u4E86\u8BB8\u591A\uFF0C\u4E2D\u95F4\u660E\u663E\u6709\u4E00\u4E2A\u7A81\u8D77\uFF0C\u4F3C\u4E4E\u4E73\u773C\u4E2D\u585E\u7740\u4EC0\u4E48\u5F02\u7269\u3002\u9633\u5177\u88AB\u5979\u8179\u4E0B\u4E00\u56E2\u5706\u9F13\u9F13\u7684\u5AE9\u8089\u78E8\u64E6\u7740\uFF0C\u90A3\u79CD\u51FA\u5947\u7684\u80A5\u8F6F\u5012\u4E0E\u7EAA\u5983\u7684\u6027\u5668\u6709\u4E9B\u76F8\u50CF\u2026\u2026</p><p>\u90A3\u5973\u5B50\u634F\u4E86\u634F\u9759\u989C\u7684\u4E73\u623F\uFF0C\u54C2\u9053\uFF1A\u300C\u8FD9\u4E48\u8F6F\uFF0C\u88AB\u591A\u5C11\u7537\u4EBA\u6478\u8FC7\u4E86\uFF1F\u300D</p><p>\u9759\u989C\u9762\u5E26\u5A9A\u7B11\uFF0C\u5FC3\u91CC\u4E00\u4E2A\u52B2\u513F\u54AC\u7259\u3002\u5973\u5B50\u626F\u5F00\u5979\u7684\u80F8\u8863\uFF0C\u51C6\u5907\u628A\u73A9\u90A3\u53CC\u96EA\u4E73\uFF0C\u5374\u89C1\u4E00\u67C4\u5C0F\u5C0F\u7684\u5315\u9996\u4ECE\u4E73\u6C9F\u95F4\u6ED1\u843D\u51FA\u6765\u3002\u770B\u5230\u90A3\u53EA\u8C61\u7259\u5C0F\u67C4\uFF0C\u5979\u76EE\u5149\u987F\u65F6\u4E00\u8DF3\u3002</p><p>\u767D\u8863\u5973\u5B50\u773C\u4E2D\u5F02\u5F69\u8FDE\u73B0\uFF0C\u5FFD\u7136\u628A\u5315\u9996\u6A2A\u5728\u9759\u989C\u7684\u9633\u5177\u4E0B\uFF0C\u5BD2\u58F0\u9053\uFF1A\u300C\u4F60\u8FD9\u4E0D\u7537\u4E0D\u5973\u7684\u5996\u7269\uFF0C\u4E0D\u77E5\u5BB3\u4E86\u591A\u5C11\u826F\u5584\uFF0C\u4ECA\u65E5\u6211\u5C31\u9609\u4E86\u4F60\u7684\u5996\u6839\uFF0C\u4E3A\u6C11\u9664\u5BB3\uFF01\u300D</p><p>\u95E8\u5916\u4E00\u58F0\u8F7B\u7B11\uFF0C\u300C\u59D0\u59D0\uFF0C\u53EF\u522B\u5413\u574F\u6211\u7684\u5C0F\u59B9\u5B50\u4E86\u3002\u300D\u8BF4\u7740\u767D\u6C0F\u59D0\u59B9\u63A8\u5F00\u95E8\uFF0C\u7B11\u76C8\u76C8\u5E76\u80A9\u800C\u5165\u3002</p><p>\u767D\u8863\u5973\u5B50\u56DE\u55D4\u4F5C\u559C\uFF0C\u300C\u6211\u8BF4\u5462\uFF0C\u539F\u6765\u662F\u4F60\u4EEC\u8FD9\u4E24\u4E2A\u9A9A\u8D27\u505A\u7684\u597D\u4E8B\u3002\u300D</p><p>\u767D\u7389\u83BA\u7B11\u7740\u62C9\u5F00\u9759\u989C\uFF0C\u300C\u6211\u8FD9\u5C0F\u59B9\u5B50\u8FD8\u662F\u9EC4\u82B1\u95FA\u5973\u5462\uFF0C\u51E4\u795E\u5C06\u4E00\u89C1\u9762\u5C31\u558A\u6253\u558A\u6740\uFF0C\u8FD9\u7EC6\u76AE\u5AE9\u8089\u7684\uFF0C\u78B0\u7834\u4E86\u591A\u5FC3\u75BC\u5462\u3002\u300D</p><p>\u767D\u7389\u9E42\u5374\u62BF\u5634\u7B11\u9053\uFF1A\u300C\u8273\u51E4\u59D0\u59D0\u4ECE\u54EA\u91CC\u627E\u6765\u7684\u8863\u670D\uFF1F\u6253\u626E\u8D77\u6765\u8FD8\u771F\u50CF\u4E00\u4F4D\u82F1\u6C14\u903C\u4EBA\u7684\u2014\u2014\u9A9A\u5973\u4FA0\u5462\u3002\u300D</p><p>\u8273\u51E4\u6536\u8D77\u65E5\u6708\u94A9\uFF0C\u98CE\u9A9A\u5730\u5377\u8D77\u8863\u6446\uFF0C\u9732\u51FA\u4E24\u6761\u96EA\u767D\u7684\u5927\u817F\uFF0C\u5A9A\u58F0\u9053\uFF1A\u300C\u4EBA\u5BB6\u8FD9\u4E00\u8DEF\u4E0A\u884C\u4FA0\u4ED7\u4E49\uFF0C\u505A\u4E86\u4E0D\u5C11\u597D\u4E8B\u5462\u3002\u300D</p><p>\u767D\u7389\u83BA\u7B11\u9053\uFF1A\u300C\u51E4\u795E\u5C06\u505A\u7684\u597D\u4E8B\u53EF\u662F\u6709\u53E3\u7686\u7891\u5462\uFF0C\u90A3\u5E74\u5728\u8D8A\u5DDE\uFF0C\u6709\u4E2A\u5C0F\u59D1\u5A18\u88AB\u4EBA\u903C\u7740\u5356\u8EAB\uFF0C\u51E4\u795E\u5C06\u4ED7\u4E49\u51FA\u624B\uFF0C\u66FF\u5979\u505A\u4E86\u5A4A\u5B50\uFF0C\u628A\u90A3\u6076\u9738\u5438\u5F97\u5E72\u5E72\u51C0\u51C0\u2026\u2026\u540E\u6765\u628A\u90A3\u5C0F\u59D1\u5A18\u5356\u53BB\u5F53\u8425\u5993\uFF0C\u5C0F\u59D1\u5A18\u8FD8\u611F\u6069\u4E0D\u5C3D\u5462\u3002\u300D</p><p>\u767D\u7389\u9E42\u9053\uFF1A\u300C\u8FD8\u6709\u90A3\u6B21\u5462\uFF0C\u51AF\u5C9B\u4E3B\u7684\u592B\u4EBA\u76DB\u6C14\u51CC\u4EBA\uFF0C\u54B1\u4EEC\u8273\u51E4\u5973\u4FA0\u627E\u4E0A\u95E8\u53BB\uFF0C\u8981\u8DDF\u4EBA\u5BB6\u6BD4\u8C01\u7684\u5976\u5B50\u5927\uFF0C\u5F53\u573A\u5C31\u628A\u51AF\u592B\u4EBA\u7684\u5976\u5B50\u5272\u4E86\u2026\u2026\u300D</p><p>\u300C\u8C01\u8BA9\u5979\u7684\u5976\u5B50\u4E0D\u53CA\u6211\u5462\uFF1F\u300D\u8273\u51E4\u8038\u4E86\u8038\u6C89\u7538\u7538\u7684\u4E73\u7403\uFF0C\u8138\u8272\u51DD\u91CD\u8D77\u6765\uFF0C\u300C\u522B\u7ED5\u5708\u5B50\u4E86\uFF0C\u5979\u662F\u4EC0\u4E48\u4EBA\uFF1F\u300D</p><p>\u300C\u65B0\u6765\u7684\u5973\u5974\uFF0C\u300D\u767D\u7389\u9E42\u9053\uFF1A\u300C\u9759\u989C\uFF0C\u6765\u89C1\u8FC7\u51E4\u795E\u5C06\u3002\u300D</p><p>\u8273\u51E4\u51B7\u7B11\u9053\uFF1A\u300C\u957F\u7740\u9E21\u5DF4\u7684\u6DEB\u5974\uFF1F\u4E0D\u6015\u5C0F\u516C\u4E3B\u63ED\u4E86\u4F60\u4EEC\u7684\u76AE\uFF1F\u300D</p><p>\u300C\u5F53\u7136\u662F\u4E2A\u5047\u7684\u4E86\uFF0C\u300D\u767D\u7389\u83BA\u82E5\u65E0\u5176\u4E8B\u5730\u8BF4\uFF1A\u300C\u51E4\u795E\u5C06\u82E5\u662F\u60F3\u8981\uFF0C\u8BA9\u53F6\u62A4\u6CD5\u4E5F\u7ED9\u4F60\u88C5\u4E00\u6839\u597D\u4E86\u3002\u300D</p><p>\u542C\u5230\u53F6\u62A4\u6CD5\u7684\u540D\u5B57\uFF0C\u8273\u51E4\u6CA1\u6709\u518D\u8FFD\u95EE\u4E0B\u53BB\uFF0C\u767D\u7389\u9E42\u53C8\u62A2\u7740\u9053\uFF1A\u300C\u51E4\u59D0\u59D0\u600E\u4E48\u6765\u8FD9\u4E48\u5FEB\uFF1F\u300D</p><p>\u8273\u51E4\u671D\u9759\u989C\u629B\u4E86\u4E2A\u5A9A\u773C\uFF0C\u300C\u4E5D\u534E\u4E00\u5E26\u95F9\u5F97\u5929\u7FFB\u5730\u8986\uFF0C\u6CA1\u4E2A\u5B89\u751F\u7684\u53BB\u5904\u3002\u59D0\u59D0\u53EA\u597D\u6765\u6E05\u6C5F\u4F1A\uFF0C\u6CA1\u60F3\u5230\u4E00\u8FDB\u95E8\u6B63\u78B0\u4E0A\u8FD9\u4E2A\u5C0F\u59B9\u59B9\uFF0C\u5E72\u7684\u597D\u4E8B\u2026\u2026\u300D</p><p>\u767D\u7389\u9E42\u7B11\u9053\uFF1A\u300C\u6211\u8FD8\u4EE5\u4E3A\u8273\u51E4\u59D0\u59D0\u662F\u542C\u5230\u4E86\u90A3\u4E2A\u4EBA\u7684\u6D88\u606F\uFF0C\u624D\u820D\u5F97\u79BB\u5F00\u5357\u6D77\u5462\u3002\u300D</p><p>\u767D\u7389\u83BA\u6015\u5979\u518D\u7EA0\u7F20\u9759\u989C\uFF0C\u4E8E\u662F\u6709\u610F\u65E0\u610F\u5730\u8BF4\u9053\uFF1A\u300C\u8FD8\u771F\u5DE7\u5462\uFF0C\u4EE4\u5F92\u4E5F\u5728\u6B64\u95F4\u3002\u300D</p><p>\u8273\u51E4\u8138\u8272\u4E00\u53D8\uFF0C\u300C\u8C01\uFF1F\u300D</p><p>\u300C\u7EAA\u5A18\u5A18\uFF0C\u5C31\u5728\u9644\u8FD1\u7684\u7518\u9732\u5BFA\u3002\u300D</p><p>\u8273\u51E4\u6C89\u541F\u7247\u523B\uFF0C\u8EAB\u5F71\u4E00\u95EA\uFF0C\u5DF2\u7136\u63A0\u5230\u9662\u5916\u3002</p><p>\u9759\u989C\u677E\u4E86\u53E3\u6C14\uFF0C\u6B63\u5F85\u5F00\u53E3\uFF0C\u4E00\u53EA\u7389\u624B\u4F38\u8FC7\u6765\u633D\u4F4F\u4E86\u5979\u7684\u9633\u5177\u3002\u767D\u7389\u83BA\u4F3C\u7B11\u975E\u7B11\u5730\u671B\u7740\u5979\uFF0C\u300C\u5C0F\u574F\u86CB\uFF0C\u8FD9\u662F\u600E\u4E48\u56DE\u4E8B\uFF1F\u300D</p><p>\u767D\u6C0F\u59D0\u59B9\u628A\u91C7\u5C3D\u771F\u5143\u7684\u51CC\u96C5\u7434\u9501\u5728\u623F\u4E2D\uFF0C\u5E26\u7740\u9759\u989C\u56DE\u5230\u4F4F\u5904\u3002\u4E24\u5973\u94FA\u597D\u88AB\u8925\uFF0C\u767D\u7389\u9E42\u5077\u5077\u77A7\u4E86\u9759\u989C\u4E00\u773C\uFF0C\u8138\u4E0A\u5C45\u7136\u6709\u4E86\u51E0\u5206\u7F9E\u8272\u3002</p><p>\u300C\u8C01\u5148\u6765\uFF1F\u300D\u767D\u7389\u83BA\u95EE\u9053\u3002\u770B\u5230\u59B9\u59B9\u5FF8\u6029\u7684\u7F9E\u6001\uFF0C\u7B11\u9053\uFF1A\u300C\u6211\u5148\u6765\u597D\u4E86\u3002\u300D</p><p>\u7EA2\u7EB1\u4E00\u677E\uFF0C\u90A3\u5BF9\u534A\u906E\u534A\u9732\u7684\u96EA\u4E73\u987F\u65F6\u8361\u51FA\u4E24\u56E2\u8089\u5149\uFF0C\u767D\u7389\u83BA\u89E3\u4E0B\u7EA2\u5DFE\uFF0C\u4EF0\u8EAB\u8EBA\u5728\u5E8A\u4E0A\uFF0C\u6E29\u987A\u5730\u5F20\u5F00\u7389\u817F\uFF0C\u67D4\u58F0\u9053\uFF1A\u300C\u5C0F\u6714\uFF0C\u8FDB\u6765\u5427\u3002\u300D</p><p>\u767D\u7389\u83BA\u7684\u808C\u80A4\u5149\u6D01\u800C\u53C8\u767D\u7699\uFF0C\u5F77\u4F5B\u88AB\u53CD\u8986\u628A\u73A9\u8FC7\u7684\u7389\u5668\uFF0C\u6709\u79CD\u6DEB\u8273\u7684\u5149\u6CFD\u3002</p><p>\u90A3\u662F\u591A\u5E74\u6DEB\u4E71\u6240\u7559\u4E0B\u7684\u75D5\u8FF9\uFF0C\u5979\u8EAB\u4E0A\u51E0\u4E4E\u6BCF\u4E00\u5BF8\u808C\u80A4\uFF0C\u90FD\u66FE\u7ECF\u906D\u53D7\u8FC7\u51F6\u6B8B\u7684\u6298\u78E8\u3002</p><p>\u5F53\u5E74\u5979\u548C\u59B9\u59B9\u4EE5\u5904\u5B50\u4E4B\u8EAB\u6CA6\u4E3A\u661F\u6708\u6E56\u7684\u6DEB\u5974\uFF0C\u4E0D\u51FA\u4E24\u6708\uFF0C\u5C31\u8DDF\u51CC\u96C5\u7434\u4E00\u6837\uFF0C\u88AB\u4EBA\u5E72\u5F97\u4E73\u5934\u53D1\u9ED1\uFF0C\u6027\u5668\u53C8\u677E\u53C8\u810F\u3002\u6155\u5BB9\u9F99\u628A\u59D0\u59B9\u4FE9\u62DB\u4E3A\u8D34\u8EAB\u5974\u5A62\uFF0C\u5ACC\u5979\u4EEC\u8089\u4F53\u4E0D\u6D01\uFF0C\u7279\u610F\u547D\u53F6\u884C\u5357\u7528\u836F\u6DB2\u6D78\u6CE1\uFF0C\u518D\u7EC6\u5FC3\u6253\u78E8\uFF0C\u6062\u590D\u4E86\u808C\u80A4\u539F\u6709\u7684\u5A07\u67D4\u7C89\u5AE9\u3002</p><p>\u81EA\u6B64\u59D0\u59B9\u4FE9\u6BCF\u5E74\u90FD\u8981\u6C42\u53F6\u62A4\u6CD5\u51FA\u624B\uFF0C\u6D17\u53BB\u8089\u4F53\u7684\u6DEB\u8FF9\u3002\u7ECF\u8FC7\u8FD9\u4E48\u591A\u5E74\u6BEB\u65E0\u8282\u5236\u7684\u6DEB\u4E71\uFF0C\u4E73\u5934\u548C\u6027\u5668\u4ECD\u7136\u662F\u5904\u5B50\u7684\u7C89\u7EA2\u8272\u6CFD\u3002\u4F46\u5F53\u5E74\u90A3\u5BF9\u7EAF\u6D01\u79C0\u7F8E\u7684\u59D0\u59B9\u82B1\uFF0C\u65E9\u5DF2\u4E00\u53BB\u4E0D\u8FD4\u3002</p><p>\u9759\u989C\u633A\u52A8\u8170\u8EAB\uFF0C\u9F9F\u5934\u987A\u7740\u6ED1\u817B\u7684\u9634\u6237\u9876\u5230\u4E86\u7389\u961C\u4E0A\u3002\u767D\u7389\u83BA\u4F4E\u53EB\u4E00\u58F0\uFF0C\u98A6\u7D27\u7709\u5934\u3002\u300C\u50BB\u5F1F\u5F1F\uFF0C\u300D\u5979\u8F7B\u8F7B\u633D\u4F4F\u8089\u68D2\uFF0C\u9001\u5165\u871C\u7A74\uFF0C\u5C0F\u58F0\u9053\uFF1A\u300C\u662F\u8FD9\u91CC\u5462\u2026\u2026\u300D</p><p>\u9633\u5177\u987A\u7740\u6E29\u6DA6\u7684\u8089\u7A74\u8FDB\u5165\u4F53\u5185\uFF0C\u4E00\u76F4\u9876\u5230\u82B1\u5FC3\uFF0C\u8FD8\u5269\u4E86\u4E24\u4E2A\u8089\u8282\u9732\u5728\u5916\u9762\u3002</p><p>\u767D\u7389\u83BA\u5F20\u5F00\u7F8E\u76EE\uFF0C\u67D4\u60C5\u65E0\u9650\u5730\u671B\u7740\u5C11\u5973\u5A07\u7F8E\u7684\u7389\u9765\uFF0C\u8F7B\u58F0\u9053\uFF1A\u300C\u5C0F\u6714\u7684\u7B2C\u4E00\u6B21\uFF0C\u8FD8\u662F\u5C04\u5728\u59D0\u59D0\u91CC\u9762\u5462\u3002\u90A3\u65F6\u4F60\u624D\u90A3\u4E48\u5C0F\uFF0C\u73B0\u5728\u5DF2\u7ECF\u8FD9\u4E48\u5927\u2026\u2026\u8FD9\u4E48\u957F\u4E86\u3002\u5982\u679C\u5E08\u5A18\u77E5\u9053\uFF0C\u4E0D\u77E5\u9053\u6709\u591A\u9AD8\u5174\u5462\u2026\u2026\u300D</p><p>\u9633\u5177\u7F13\u7F13\u62BD\u51FA\uFF0C\u767D\u7389\u83BA\u547B\u541F\u7740\u5408\u4E0A\u7F8E\u76EE\uFF0C\u773C\u89D2\u5FFD\u7136\u6D8C\u51FA\u51E0\u6EF4\u6CEA\u82B1\u3002\u5979\u614C\u5FD9\u626F\u8FC7\u6795\u5934\u76D6\u5728\u5934\u4E0A\uFF0C\u74EE\u58F0\u74EE\u6C14\u5730\u8BF4\uFF1A\u300C\u5C0F\u6714\uFF0C\u4F60\u7528\u529B\u5E72\u5427\uFF0C\u4E0D\u5FC5\u5728\u610F\u59D0\u59D0\u2026\u2026\u59D0\u59D0\u53D7\u5F97\u4E86\u7684\u3002\u300D</p><p>\u9759\u989C\u5FC3\u91CC\u6CDB\u8D77\u96BE\u8A00\u7684\u6ECB\u5473\uFF0C\u521A\u624D\u5979\u8BF4\u81EA\u5DF1\u7684\u9633\u5177\u5E76\u6CA1\u6709\u5B8C\u5168\u635F\u6BC1\uFF0C\u53C8\u9047\u4E0A\u4E00\u4E2A\u9AD8\u660E\u7684\u5927\u592B\u6539\u9020\u4E86\u8EAB\u4F53\u3002\u767D\u6C0F\u59D0\u59B9\u79C1\u4E0B\u5546\u91CF\u51E0\u53E5\uFF0C\u4FBF\u8981\u4E0E\u5979\u5408\u4F53\u4EA4\u6B22\u3002\u9759\u989C\u53EA\u4EE5\u4E3A\u59D0\u59B9\u662F\u6DEB\u8361\u6210\u6027\uFF0C\u60F3\u5C1D\u5C1D\u8FD9\u6839\u517D\u9633\u7684\u6ECB\u5473\uFF0C\u4F46\u6B64\u523B\u770B\u767D\u7389\u83BA\u7684\u795E\u60C5\uFF0C\u5374\u53C8\u4E0D\u50CF\u3002</p><p>\u9759\u989C\u6CA1\u6709\u8C61\u5BF9\u5F85\u5E08\u5A18\u90A3\u6837\u65BD\u5C55\u6280\u5DE7\uFF0C\u800C\u662F\u6536\u62E2\u9F9F\u5934\uFF0C\u50CF\u9525\u5B50\u4E00\u6837\u6345\u91CD\u91CD\u9876\u5728\u767D\u7389\u83BA\u7684\u82B1\u5FC3\u4E0A\u3002\u5979\u7684\u9633\u5177\u672C\u662F\u79FB\u690D\u7684\u9E7F\u9633\uFF0C\u4E0D\u4F46\u53C8\u957F\u53C8\u786C\uFF0C\u8FD8\u53EF\u7528\u771F\u6C14\u63A7\u5236\u52C3\u8D77\u7684\u5C3A\u5BF8\uFF0C\u56E0\u6B64\u80FD\u94BB\u8FDB\u7EC6\u5C0F\u7684\u82B1\u5FC3\uFF0C\u91C7\u8865\u9634\u7CBE\u3002\u5F53\u65E5\u662F\u767D\u6C0F\u59D0\u59B9\u4EB2\u624B\u628A\u6BCD\u4EB2\u7A7F\u5728\u6728\u67F1\u4E0A\uFF0C\u65E0\u8BBA\u59D0\u59B9\u4FE9\u5982\u4F55\u7EF4\u62A4\u81EA\u5DF1\uFF0C\u5979\u7684\u6068\u610F\u7EC8\u7A76\u65E0\u6CD5\u91CA\u7136\u3002</p><p>\u770B\u5F97\u51FA\u767D\u7389\u83BA\u7684\u75BC\u75DB\u8FDC\u591A\u4E8E\u6B22\u6109\uFF0C\u5979\u4E00\u8FB9\u54AC\u7259\u82E6\u5FCD\uFF0C\u4E00\u8FB9\u4E0D\u65F6\u633A\u8D77\u4E0B\u8179\uFF0C\u8FCE\u5408\u9759\u989C\u51F6\u72E0\u7684\u62BD\u9001\uFF0C\u597D\u8BA9\u5979\u8FDB\u5F97\u66F4\u6DF1\uFF0C\u611F\u53D7\u66F4\u8212\u670D\u3002</p><p>\u5929\u9645\u9690\u9690\u53D1\u767D\uFF0C\u9759\u989C\u5DF2\u7ECF\u62BD\u9001\u4E86\u534A\u4E2A\u65F6\u8FB0\u3002\u5BF9\u5979\u800C\u8A00\uFF0C\u8FD9\u6837\u7684\u4EA4\u5408\u4E0E\u62FF\u7740\u4E00\u6839\u6728\u68CD\u6345\u5F04\u5973\u4EBA\u7684\u9634\u6237\u6CA1\u6709\u533A\u522B\u3002\u65E0\u8BBA\u662F\u4EE5\u7537\u4EBA\u7684\u8EAB\u4EFD\u53BB\u5E72\u5973\u4EBA\uFF0C\u8FD8\u662F\u88AB\u7537\u4EBA\u5E72\uFF0C\u5979\u90FD\u4ECE\u672A\u6709\u8FC7\u4EFB\u4F55\u5FEB\u611F\u3002\u770B\u7740\u7537\u4EBA\u5728\u5979\u8EAB\u4E0A\u6B32\u4ED9\u6B32\u6B7B\uFF0C\u5973\u4EBA\u5728\u5979\u8EAB\u4E0B\u5A49\u8F6C\u5A9A\u53EB\uFF0C\u5979\u603B\u4F1A\u89C9\u5F97\u5F88\u8352\u8C2C\u3002\u4ECE\u5979\u7684\u4F53\u4F1A\u6765\u8BF4\uFF0C\u5FEB\u611F\u662F\u4E0D\u5B58\u5728\u7684\uFF0C\u552F\u4E00\u771F\u5B9E\u7684\uFF0C\u53EA\u6709\u75DB\u82E6\u3002</p><p>\u9633\u5177\u518D\u6B21\u6345\u5165\u82B1\u5FC3\uFF0C\u767D\u7389\u83BA\u7EC6\u767D\u7684\u67D4\u9888\u5411\u540E\u4EF0\u8D77\uFF0C\u7389\u9F7F\u54AC\u7740\u6795\u89D2\uFF0C\u5507\u89D2\u56E0\u4E3A\u8FD9\u7A81\u5982\u5176\u6765\u7684\u75BC\u75DB\u800C\u62BD\u52A8\u8D77\u6765\u3002\u9759\u989C\u5FFD\u7136\u60F3\u8D77\u5F88\u4E45\u4EE5\u524D\u90A3\u6B21\u4EA4\u5408\u3002\u90A3\u65F6\u5979\u53EA\u6709\u4E03\u5C81\uFF0C\u4F46\u5374\u662F\u4E2A\u5B8C\u5B8C\u5168\u5168\u7684\u7537\u4EBA\u3002\u83BA\u59D0\u59D0\u628A\u5979\u6402\u5728\u6000\u91CC\uFF0C\u8BA9\u5979\u611F\u53D7\u7740\u5973\u4EBA\u7684\u6E7F\u6DA6\u4E0E\u6ED1\u817B\u3002\u90A3\u65F6\u81EA\u5DF1\u6CA1\u6709\u529B\u91CF\u4F7F\u5979\u75BC\u75DB\uFF0C\u5047\u5982\u6709\uFF0C\u5979\u4F1A\u4E0D\u4F1A\u7518\u613F\u627F\u53D7\u5462\uFF1F</p><p>\u9759\u989C\u53C8\u72E0\u72E0\u9876\u4E86\u51E0\u4E0B\uFF0C\u767D\u7389\u83BA\u59CB\u7EC8\u6CA1\u6709\u963B\u6B62\u5979\u7C97\u66B4\u7684\u52A8\u4F5C\uFF0C\u7EB5\u7136\u75BC\u5F97\u7389\u5BB9\u626D\u66F2\uFF0C\u4E5F\u5F3A\u5FCD\u7740\u672A\u75DB\u53EB\u51FA\u58F0\u3002\u9759\u989C\u505C\u4F4F\u7EE7\u7EED\u6DF1\u5165\u7684\u4F01\u56FE\uFF0C\u5979\u62AC\u8D77\u8EAB\uFF0C\u7F13\u7F13\u62BD\u51FA\u8089\u68D2\u3002</p><p>\u300C\u4E0D\u2026\u2026\u300D\u767D\u7389\u83BA\u7389\u817F\u5408\u62E2\uFF0C\u5939\u4F4F\u9759\u989C\u7EA4\u7F8E\u7684\u8170\u80A2\uFF0C\u300C\u4E0D\u8981\u62D4\u51FA\u6765\u2026\u2026\u300D</p><p>\u5979\u62FF\u5F00\u6795\u5934\uFF0C\u9732\u51FA\u6EE1\u662F\u6CEA\u75D5\u7684\u4FCF\u8138\uFF0C\u7136\u540E\u633A\u8D77\u7C89\u5AE9\u7684\u7389\u6237\uFF0C\u4E24\u624B\u6402\u4F4F\u9759\u989C\u7684\u8170\u81C0\uFF0C\u5C06\u90A3\u6839\u8840\u7EA2\u7684\u517D\u6839\u671D\u4F53\u5185\u6DF1\u5904\u9001\u53BB\uFF0C\u8F7B\u58F0\u9053\uFF1A\u300C\u5168\u63D2\u8FDB\u6765\u5427\uFF0C\u59D0\u59D0\u4F1A\u8BA9\u5C0F\u6714\u5FEB\u4E50\u7684\u2026\u2026\u300D</p><p>\u8089\u68D2\u786C\u786C\u6491\u5F00\u82B1\u5FC3\uFF0C\u987A\u7740\u7EC6\u957F\u7684\u5BAB\u9888\u671D\u6E29\u6DA6\u7684\u5B50\u5BAB\u4F38\u53BB\uFF0C\u90A3\u79CD\u7A7F\u900F\u7684\u75DB\u695A\uFF0C\u4F7F\u767D\u7389\u83BA\u5A07\u8EAF\u8F7B\u98A4\uFF0C\u90A3\u79CD\u5A49\u8F6C\u627F\u6B22\u7684\u5A07\u6001\uFF0C\u5C31\u50CF\u4E00\u4E2A\u542B\u7F9E\u5FCD\u75DB\u7684\u5904\u5B50\uFF0C\u88AB\u5FC3\u7231\u7684\u60C5\u90CE\u7834\u4F53\u3002</p><p>\u8089\u68D2\u5728\u72ED\u7A84\u7684\u5BAB\u9888\u91CC\u62BD\u52A8\u8D77\u6765\uFF0C\u767D\u7389\u83BA\u6402\u7740\u9759\u989C\u82AC\u9999\u7684\u7389\u4F53\uFF0C\u67D4\u8F6F\u7684\u7EA2\u5507\u7EC6\u7EC6\u4EB2\u543B\u7740\u5979\u7684\u7389\u988C\u3001\u7C89\u9888\u3001\u9999\u80A9\u2026\u2026</p><p>\u4EB2\u7740\u4EB2\u7740\uFF0C\u767D\u7389\u83BA\u8273\u7EA2\u7684\u5507\u89D2\u6162\u6162\u5F2F\u4E86\u4E0B\u53BB\uFF0C\u5979\u98A4\u58F0\u8BF4\u4E86\u53E5\uFF0C\u300C\u5E08\u5A18\uFF0C\u83BA\u513F\u4E0D\u662F\u6545\u610F\u7684\u2026\u2026\u300D\u4FBF\u75DB\u54ED\u8D77\u6765\u3002</p><p>\u8FC7\u4E86\u534A\u664C\uFF0C\u9759\u989C\u4FEF\u4E0B\u8EAB\uFF0C\u5728\u767D\u7389\u83BA\u5507\u89D2\u8F7B\u8F7B\u4E00\u543B\uFF0C\u8F7B\u58F0\u9053\uFF1A\u300C\u6211\u77E5\u9053\u7684\u3002\u300D</p><p>\u767D\u7389\u83BA\u54ED\u5F97\u6108\u53D1\u4F24\u5FC3\uFF0C\u5979\u6342\u7740\u8138\uFF0C\u6CEA\u6C34\u4ECE\u6307\u7F1D\u95F4\u4E0D\u4F4F\u6D8C\u51FA\u3002\u9759\u989C\u4E0D\u77E5\u9053\u8BE5\u5982\u4F55\u5B89\u6170\u5979\u5FC3\u5E95\u90A3\u9053\u5341\u4F59\u5E74\u6EF4\u8840\u4E0D\u7EDD\u7684\u4F24\u75D5\uFF0C\u53EA\u597D\u8BA9\u5979\u75DB\u54ED\u4E0B\u53BB\u3002</p><p>\u767D\u7389\u9E42\u7EA2\u7740\u773C\u775B\u62C9\u4E86\u62C9\u5979\u7684\u624B\u6307\uFF0C\u7136\u540E\u5367\u5728\u767D\u7389\u83BA\u8EAB\u8FB9\uFF0C\u50CF\u59D0\u59D0\u90A3\u6837\u5F20\u5F00\u53CC\u817F\u3002\u5979\u5148\u62B1\u8FC7\u4E00\u4E2A\u6795\u5934\u906E\u4F4F\u8138\uFF0C\u624D\u5C0F\u58F0\u8BF4\u9053\uFF1A\u300C\u6765\u7528\u9E42\u513F\u7684\u8EAB\u5B50\u2026\u2026\u300D</p><p>\u9759\u989C\u4E00\u6539\u521A\u624D\u7684\u7C97\u66B4\uFF0C\u9633\u5177\u6D45\u62BD\u7F13\u9001\uFF0C\u6E29\u5B58\u4E4B\u6781\u3002\u8FDE\u767D\u7389\u9E42\u6EE5\u4EA4\u65E0\u5EA6\u7684\u8089\u4F53\uFF0C\u4E0D\u591A\u65F6\u4E5F\u88AB\u5979\u6311\u9017\u5F97\u9AD8\u6F6E\u8FED\u8D77\u3002</p><p>\u8FD9\u8FB9\u767D\u7389\u83BA\u6E10\u6E10\u6B62\u4F4F\u54ED\u58F0\uFF0C\u5979\u62B9\u7740\u773C\u6CEA\u5750\u4E86\u8D77\u6765\uFF0C\u6B49\u7136\u9053\uFF1A\u300C\u5BF9\u4E0D\u8D77\uFF0C\u59D0\u59D0\u4E0D\u662F\u53D7\u4E0D\u4E86\u75BC\u624D\u54ED\u7684\u2026\u2026\u300D</p><p>\u300C\u6211\u77E5\u9053\uFF0C\u300D\u9759\u989C\u907F\u5F00\u5979\u7684\u773C\u795E\uFF0C\u300C\u6211\u5A18\u4E0D\u4F1A\u602A\u4F60\u4EEC\u7684\u2026\u2026\u300D</p><p>\u767D\u7389\u9E42\u5728\u6795\u5934\u4E0B\u54C7\u7684\u4E00\u58F0\u54ED\u4E86\u8D77\u6765\uFF0C\u5979\u8FDE\u5FD9\u6402\u7D27\u6795\u5934\uFF0C\u63A9\u4F4F\u54ED\u58F0\uFF0C\u4E24\u817F\u76D8\u5728\u9759\u989C\u8170\u95F4\uFF0C\u4E00\u8FB9\u54FD\u54BD\uFF0C\u4E00\u8FB9\u633A\u52A8\u4E0B\u8179\uFF0C\u53BB\u5957\u5F04\u5979\u7684\u9633\u5177\uFF0C\u4F3C\u4E4E\u8981\u628A\u6EE1\u5FC3\u75DB\u82E6\u90FD\u85C9\u6B64\u53D1\u6CC4\u51FA\u6765\u3002\u767D\u7389\u83BA\u9634\u5185\u8FD8\u9635\u9635\u4F5C\u75DB\uFF0C\u5979\u6536\u4E86\u6CEA\uFF0C\u88C5\u4F5C\u82E5\u65E0\u5176\u4E8B\u5730\u7AD9\u8D77\u8EAB\u6765\uFF0C\u4ECE\u540E\u9762\u62B1\u4F4F\u9759\u989C\u7684\u7C89\u80CC\uFF0C\u628A\u8138\u8D34\u5728\u5979\u7684\u9888\u4E0A\u3002</p><p>\u300C\u62A4\u6CD5\u3002\u300D\u5434\u971C\u8339\u5728\u95E8\u5916\u5C0F\u58F0\u8BF4\u9053\uFF1A\u300C\u6628\u665A\u51FA\u4E86\u4E8B\uFF0C\u90A3\u5A4A\u5B50\u88AB\u4EBA\u52AB\u8D70\u4E86\uFF0C\u8FD8\u6298\u4E86\u516D\u540D\u5C5E\u4E0B\u3002\u90FD\u662F\u5974\u5A62\u65E0\u80FD\uFF0C\u8BF7\u62A4\u6CD5\u8D23\u7F5A\u2026\u2026\u300D</p><p>\u767D\u7389\u83BA\u6CA1\u60F3\u5230\u9759\u989C\u4F1A\u6740\u4E86\u516D\u4EBA\uFF0C\u5979\u626C\u8D77\u8138\u6C89\u58F0\u9053\uFF1A\u300C\u6B64\u4E8B\u662F\u672C\u62A4\u6CD5\u4E00\u624B\u6240\u4E3A\uFF0C\u4F60\u4E0D\u5FC5\u7406\u4F1A\u3002\u300D</p><p>\u5434\u971C\u8339\u677E\u4E86\u53E3\u6C14\uFF0C\u5982\u679C\u90A3\u6DEB\u5974\u771F\u5728\u81EA\u5DF1\u624B\u4E2D\u4E22\u4E86\uFF0C\u62A4\u6CD5\u8D23\u7F5A\u4E0B\u6765\uFF0C\u5FC5\u7136\u662F\u751F\u4E0D\u5982\u6B7B\u3002</p><p>\u767D\u7389\u83BA\u53C8\u9053\uFF1A\u300C\u901A\u77E5\u5317\u795E\u5C06\uFF0C\u6211\u4EEC\u59D0\u59B9\u4E09\u65E5\u540E\u4E0E\u51E4\u795E\u5C06\u540C\u56DE\u5EFA\u5EB7\u3002\u4F60\u4E0D\u5FC5\u518D\u6765\u6253\u6270\uFF0C\u9000\u4E0B\u5427\u3002\u300D</p><p>\u5434\u971C\u8339\u5E94\u58F0\u9000\u4E0B\uFF0C\u767D\u7389\u83BA\u89C1\u7A97\u5916\u5DF2\u7ECF\u662F\u7EA2\u65E5\u9AD8\u7167\uFF0C\u4FBF\u62AC\u624B\u653E\u4E0B\u5E8A\u5E10\uFF0C\u5C55\u81C2\u62E5\u4F4F\u9759\u989C\u67D4\u7F8E\u7684\u7389\u4F53\uFF0C\u7AED\u529B\u670D\u4F8D\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u751F\u5F97\u4E00\u6A21\u4E00\u6837\uFF0C\u6B64\u65F6\u4E00\u4E2A\u5728\u524D\uFF0C\u4E00\u4E2A\u5728\u540E\uFF0C\u4E24\u5177\u7C89\u5AE9\u7684\u5A07\u8EAF\u5C06\u9759\u989C\u5939\u5728\u4E2D\u95F4\uFF0C\u56DB\u53EA\u96EA\u6ED1\u7684\u4E73\u623F\u6EDA\u6765\u6EDA\u53BB\uFF0C\u65E0\u5FAE\u4E0D\u81F3\u5730\u78E8\u64E6\u7740\u9759\u989C\u7684\u6BCF\u4E00\u5BF8\u808C\u80A4\u3002</p><p>\u4E09\u4EBA\u5DF2\u7ECF\u4EA4\u5408\u4E86\u4E00\u4E2A\u591A\u65F6\u8FB0\uFF0C\u767D\u6C0F\u59D0\u59B9\u8F6E\u6D41\u4E0A\u9635\uFF0C\u7EB3\u5165\u5979\u7684\u517D\u9633\u3002\u9759\u989C\u6697\u89C9\u5947\u602A\uFF0C\u59D0\u59B9\u4FE9\u5DF2\u7ECF\u88AB\u5979\u5978\u5F97\u6CC4\u8EAB\u6570\u6B21\uFF0C\u767D\u7389\u9E42\u66F4\u662F\u4F53\u8F6F\u5982\u7EF5\uFF0C\u8170\u90FD\u76F4\u4E0D\u8D77\u6765\uFF0C\u5374\u8FD8\u5728\u82E6\u82E6\u75F4\u7F20\u7740\u5979\uFF0C\u518D\u8FD9\u6837\u4E0B\u53BB\uFF0C\u4E24\u5973\u975E\u4F24\u4E86\u8EAB\u5B50\u4E0D\u53EF\u3002</p><p>\u601D\u7D22\u95F4\uFF0C\u767D\u7389\u83BA\u5728\u8EAB\u4E0B\u53C8\u6CC4\u4E86\u51FA\u6765\uFF0C\u767D\u7389\u9E42\u5DF2\u7ECF\u65E0\u529B\u8FCE\u5408\uFF0C\u53EA\u80FD\u52C9\u5F3A\u62C9\u8FC7\u6795\u5934\uFF0C\u57AB\u5728\u81C0\u4E0B\uFF0C\u597D\u65B9\u4FBF\u9759\u989C\u7684\u63D2\u5165\u3002</p><p>\u9759\u989C\u4E0D\u6562\u518D\u6545\u610F\u6311\u9017\u5979\u7684\u654F\u611F\u90E8\u4F4D\uFF0C\u53EA\u6D45\u6D45\u62BD\u9001\uFF0C\u597D\u8BA9\u5979\u4F11\u606F\u4E00\u4F1A\u513F\u3002\u767D\u7389\u83BA\u9AD8\u6F6E\u672A\u892A\uFF0C\u4FBF\u722C\u8D77\u8EAB\u6765\uFF0C\u5979\u5782\u9996\u5728\u9759\u989C\u81C0\u4E0A\u543B\u4E86\u4E00\u53E3\uFF0C\u5A07\u5598\u9053\uFF1A\u300C\u5C0F\u6714\u7684\u5C41\u80A1\u771F\u7F8E\u2026\u2026\u300D\u8BF4\u7740\u64A9\u8D77\u79C0\u53D1\uFF0C\u5C06\u90A3\u5F20\u6EE1\u662F\u7EA2\u6655\u7684\u7389\u8138\u57CB\u5728\u9759\u989C\u6676\u83B9\u7684\u7C89\u81C0\u95F4\uFF0C\u4F38\u51FA\u9999\u820C\uFF0C\u5728\u9759\u989C\u7EA2\u5AE9\u7684\u83CA\u809B\u4E0A\u7528\u529B\u8214\u8210\u8D77\u6765\u3002</p><p>\u9759\u989C\u4E00\u60CA\uFF0C\u300C\u83BA\u59D0\u59D0\uFF0C\u597D\u810F\u7684\u2026\u2026\u300D</p><p>\u767D\u7389\u83BA\u67D4\u67D4\u4E00\u7B11\uFF0C\u300C\u59D0\u59D0\u4E0D\u6015\u7684\uFF0C\u53EA\u8981\u5C0F\u6714\u9AD8\u5174\u5C31\u597D\u3002\u300D</p><p>\u6ED1\u817B\u7684\u5C0F\u820C\u4ECE\u81C0\u6C9F\u63A0\u8FC7\uFF0C\u5728\u809B\u6D1E\u7EC6\u5BC6\u7684\u83CA\u7EB9\u4E0A\u6253\u7740\u8F6C\u671D\u809B\u5185\u94BB\u53BB\uFF0C\u767D\u7389\u83BA\u7684\u820C\u5C16\u7075\u5DE7\u4E4B\u6781\uFF0C\u800C\u4E14\u6781\u4E3A\u5356\u529B\uFF0C\u5979\u65F6\u8214\u65F6\u5438\uFF0C\u65F6\u800C\u7FD8\u8D77\u820C\u5C16\uFF0C\u5728\u9759\u989C\u5E7D\u9999\u7684\u5904\u5B50\u7389\u6237\u4E0A\u4E00\u63A0\u800C\u8FC7\uFF0C\u53C8\u6DF1\u6DF1\u94BB\u5165\u809B\u6D1E\u3002\u90A3\u79CD\u5F02\u6837\u7684\u6E7F\u6ED1\u4E0E\u6E29\u987A\uFF0C\u4F7F\u9759\u989C\u611F\u53D7\u5230\u4E00\u79CD\u96BE\u4EE5\u8A00\u8BF4\u7684\u6ECB\u5473\uFF0C\u5979\u7B2C\u4E00\u6B21\u5E0C\u671B\uFF0C\u90A3\u4E2A\u5F02\u7269\u80FD\u8FDB\u5F97\u66F4\u6DF1\u4E00\u4E9B\u3002</p><p>\u767D\u7389\u9E42\u7684\u8EAB\u5B50\u53C8\u6218\u6817\u8D77\u6765\uFF0C\u9759\u989C\u8FDE\u5FD9\u505C\u4F4F\u62BD\u9001\uFF0C\u53EF\u767D\u7389\u9E42\u5DF2\u7ECF\u7389\u8138\u53D8\u8272\uFF0C\u8089\u7A74\u6709\u8282\u5F8B\u7684\u4E00\u6536\u4E00\u7F29\uFF0C\u773C\u770B\u5C31\u8981\u6CC4\u8EAB\u3002\u5979\u59D4\u5C48\u5730\u6241\u8D77\u5634\uFF0C\u300C\u59D0\u59D0\u4EEC\u597D\u6CA1\u7528\u2026\u2026\u5C0F\u6714\uFF0C\u4F60\u600E\u4E48\u8FD8\u4E0D\u5C04\u2026\u2026\u300D</p><p>\u9759\u989C\u6123\u4F4F\u4E86\uFF0C\u534A\u664C\u624D\u9053\uFF1A\u300C\u6211\u2026\u2026\u6211\u4E0D\u4F1A\u5C04\u2026\u2026\u300D</p><p>\u6B63\u5728\u5979\u81C0\u95F4\u4EB2\u543B\u7684\u767D\u7389\u83BA\u677E\u5F00\u5507\u820C\uFF0C\u300C\u5B83\u53EA\u80FD\u52C3\u8D77\uFF0C\u4E0D\u4F1A\u5C04\u7CBE\u5417\uFF1F\u300D</p><p>\u9759\u989C\u7EA2\u7740\u8138\u6447\u4E86\u6447\u5934\u3002</p><p>\u767D\u7389\u83BA\u5931\u671B\u5730\u5782\u4E0B\u5934\uFF0C\u88AB\u517D\u9633\u63D2\u5F97\u7EA2\u80BF\u7684\u7389\u6237\u5FAE\u5FAE\u7EFD\u5F00\uFF0C\u5F77\u4F5B\u4E00\u6735\u7EA2\u8273\u7684\u9C9C\u82B1\u5D4C\u5728\u81C0\u4E0B\u3002</p><p>\u9759\u989C\u4F3C\u4E4E\u6709\u4E9B\u660E\u767D\u4E86\uFF0C\u300C\u83BA\u59D0\u59D0\uFF0C\u9E42\u59D0\u59D0\uFF0C\u4F60\u4EEC\u662F\u60F3\u8BA9\u6211\u5C04\u7CBE\u5417\uFF1F\u300D</p><p>\u767D\u7389\u83BA\u82E6\u7B11\u9053\uFF1A\u300C\u59D0\u59D0\u662F\u60F3\u7559\u4E0B\u4F60\u7684\u9AA8\u8840\u2026\u2026\u300D</p><p>\u767D\u7389\u9E42\u8EAB\u5B50\u5F13\u8D77\uFF0C\u5A07\u5598\u7740\u518D\u6B21\u6CC4\u51FA\u9634\u7CBE\u3002\u5979\u7528\u529B\u62B1\u7D27\u9759\u989C\uFF0C\u5C06\u5979\u575A\u633A\u7684\u9633\u5177\u671D\u81EA\u5DF1\u6218\u6817\u7684\u8089\u7A74\u5185\u9001\u53BB\uFF0C\u4E00\u76F4\u7EB3\u5165\u82B1\u5FC3\uFF0C\u628A\u9634\u7CBE\u72C2\u6D8C\u7684\u8089\u5B54\u5957\u5728\u9F9F\u5934\u4E0A\uFF0C\u98A4\u58F0\u9053\uFF1A\u300C\u5C0F\u6714\uFF0C\u59D0\u59D0\u7684\u9634\u7CBE\u90FD\u7ED9\u4F60\u2026\u2026\u300D</p><p>\u4ECE\u4E5D\u534E\u79BB\u5F00\uFF0C\u4E00\u8DEF\u4E0A\u59D0\u59B9\u4FE9\u5C31\u767E\u822C\u529D\u8C15\uFF0C\u5E0C\u671B\u9759\u989C\u80FD\u653E\u5F03\u62A5\u4EC7\uFF0C\u53EF\u9759\u989C\u53EA\u7B11\u800C\u4E0D\u8BED\uFF0C\u5206\u660E\u662F\u4E00\u610F\u5B64\u884C\u3002\u59D0\u59B9\u4FE9\u6DF1\u77E5\u661F\u6708\u6E56\u5B9E\u529B\uFF0C\u9759\u989C\u6B64\u53BB\u5FC5\u7136\u662F\u51F6\u591A\u5409\u5C11\uFF0C\u5979\u4EEC\u4E0D\u613F\u89C1\u5E08\u5A18\u552F\u4E00\u7684\u9AA8\u8089\u5C31\u6B64\u9001\u547D\uFF0C\u5374\u53C8\u4E00\u7B79\u83AB\u5C55\u3002</p><p>\u76F4\u5230\u770B\u89C1\u9759\u989C\u7684\u9633\u5177\uFF0C\u59D0\u59B9\u4FE9\u7ACB\u523B\u5546\u91CF\u51B3\u5B9A\uFF0C\u5404\u81EA\u4E0E\u9759\u989C\u4EA4\u6B22\uFF0C\u597D\u7528\u5979\u4EEC\u7684\u5B50\u5BAB\u6765\u4FDD\u5B58\u5E08\u5A18\u7684\u8840\u8109\uFF0C\u4E24\u5973\u9759\u989C\u7F20\u7EF5\u7ADE\u591C\uFF0C\u65BD\u51FA\u79CD\u79CD\u6280\u5DE7\uFF0C\u60F3\u8BA9\u5979\u628A\u7CBE\u6DB2\u7559\u5728\u81EA\u5DF1\u4F53\u5185\u3002\u6CA1\u60F3\u5230\u9759\u989C\u7A7A\u6709\u9633\u5177\uFF0C\u5374\u65E0\u6CD5\u5C04\u7CBE\u2026\u2026</p><p>\u9759\u989C\u6C89\u9ED8\u826F\u4E45\uFF0C\u5FFD\u7136\u626C\u8138\u5C55\u989C\u4E00\u7B11\uFF0C\u300C\u8F9B\u82E6\u4E24\u4F4D\u59D0\u59D0\u4E86\uFF0C\u9759\u989C\u8C22\u8C22\u4F60\u4EEC\u3002\u300D</p><p>\u5979\u4FEF\u4E0B\u81FB\u9996\uFF0C\u5728\u59D0\u59B9\u4FE9\u5507\u4E0A\u8BA4\u771F\u4E00\u543B\uFF0C\u7136\u540E\u8D77\u8EAB\u62AB\u4E0A\u8863\u886B\u3002</p><p>\u767D\u6C0F\u59D0\u59B9\u671B\u7740\u5979\u7684\u8089\u68D2\u6E10\u6E10\u7F29\u5C0F\uFF0C\u6536\u5165\u82B1\u74E3\u4E4B\u4E2D\uFF0C\u6D88\u5931\u5F97\u65E0\u5F71\u65E0\u8E2A\uFF0C\u5B9B\u7136\u662F\u4E00\u4E2A\u767D\u74A7\u65E0\u7455\u7684\u59E3\u597D\u5C11\u5973\uFF0C\u4E0D\u7531\u76F8\u987E\u5931\u8272\u3002\u5979\u8BF4\u7684\u5927\u592B\u624B\u6BB5\u7ADF\u7136\u5982\u6B64\u9AD8\u660E\uFF0C\u5373\u4F7F\u6BD4\u8D77\u53F6\u795E\u533B\u4E5F\u4E0D\u8D8B\u591A\u8BA9\u3002</p><p>\u9759\u989C\u7A7F\u6234\u6574\u9F50\uFF0C\u5750\u5728\u5986\u53F0\u524D\u4E00\u8FB9\u68B3\u7406\u5986\u626E\uFF0C\u4E00\u8FB9\u67D4\u58F0\u9053\uFF1A\u300C\u6211\u5C31\u4E0D\u53BB\u5EFA\u5EB7\u4E86\uFF0C\u52B3\u70E6\u4E24\u4F4D\u59D0\u59D0\u7167\u6599\u5E08\u5A18\u3002\u4ECE\u73B0\u5728\u8D77\uFF0C\u9759\u989C\u5C31\u4E0D\u518D\u8DDF\u4E24\u4F4D\u59D0\u59D0\u8054\u7CFB\uFF0C\u5982\u679C\u2026\u2026\u300D</p><p>\u5979\u659F\u914C\u534A\u664C\uFF0C\u300C\u5982\u679C\u5931\u624B\u3002\u59D0\u59D0\u4E5F\u4E0D\u5FC5\u6765\u6551\u6211\uFF0C\u53EA\u8981\u7ED9\u8BBE\u6CD5\u9759\u989C\u4E00\u4E2A\u75DB\u5FEB\uFF0C\u9759\u989C\u5C31\u611F\u6FC0\u4E0D\u5C3D\u4E86\u3002\u300D</p><p>\u767D\u7389\u83BA\u6062\u590D\u4E86\u51B7\u9759\uFF0C\u62AC\u624B\u4E0E\u9759\u989C\u51FB\u4E86\u4E00\u638C\uFF0C\u6DE1\u6DE1\u9053\uFF1A\u300C\u653E\u5FC3\u3002\u300D</p><p>\u767D\u7389\u9E42\u773C\u5708\u53C8\u7EA2\u4E86\u8D77\u6765\uFF0C\u5979\u63A5\u8FC7\u68B3\u5B50\uFF0C\u5E2E\u9759\u989C\u68B3\u7406\u957F\u53D1\uFF0C\u8BF4\u9053\uFF1A\u300C\u5C0F\u6714\uFF0C\u8FD8\u6709\u4EC0\u4E48\u4E0D\u653E\u5FC3\u7684\u4E8B\u2026\u2026\u59D0\u59D0\u53EF\u4EE5\u5E2E\u4F60\u7684\u3002\u300D</p><p>\u6240\u6709\u7684\u4EB2\u4EBA\u90FD\u88AB\u81EA\u5DF1\u51FA\u5356\u5F97\u5E72\u5E72\u51C0\u51C0\uFF0C\u53EA\u5269\u4E00\u4E2A\u4E49\u6BCD\u2026\u2026\u9759\u989C\u60F3\u4E86\u60F3\uFF0C\u300C\u5B9B\u9675\u57CE\u5916\u4E03\u91CC\uFF0C\u6709\u4E00\u7247\u6811\u6797\uFF0C\u8DDD\u6D41\u97F3\u6EAA\u4E0D\u8FDC\u7684\u5730\u65B9\uFF0C\u6709\u4E00\u5EA7\u571F\u575F\uFF0C\u65C1\u8FB9\u79CD\u7740\u4E00\u68F5\u94F6\u674F\u6811\u3002\u5982\u679C\u53EF\u80FD\uFF0C\u6211\u5E0C\u671B\u80FD\u846C\u5728\u90A3\u91CC\u3002\u300D</p><p>\u767D\u7389\u9E42\u773C\u6CEA\u4E00\u4E0B\u5B50\u6389\u4E86\u4E0B\u6765\u3002</p><p>\u9759\u989C\u5410\u4E86\u5410\u820C\u5C16\uFF0C\u59A9\u5A9A\u5730\u5A07\u7B11\u8D77\u6765\uFF0C\u300C\u4E0D\u8981\u90A3\u4E48\u62C5\u5FC3\u5566\uFF0C\u8BF4\u4E0D\u5B9A\u8981\u4E0D\u4E86\u51E0\u65E5\uFF0C\u6211\u5C31\u80FD\u5272\u4E86\u6155\u5BB9\u9F99\u7684\u8111\u888B\uFF0C\u4E00\u8FB9\u5E72\u4ED6\u7684\u5973\u513F\uFF0C\u4E00\u8FB9\u73A9\u4ED6\u7684\u90A3\u4E9B\u5983\u5B50\u5A18\u5A18\u5462\u3002\u300D</p><p>\u767D\u7389\u83BA\u51B7\u51B7\u9053\uFF1A\u300C\u4F60\u65E2\u7136\u77E5\u9053\u4ED6\u5F53\u4E86\u7687\u5E1D\uFF0C\u8FD8\u4EE5\u4E3A\u80FD\u62A5\u5F97\u4E86\u4EC7\u5417\uFF1F\u300D</p><p>\u9759\u989C\u626D\u8FC7\u8170\u80A2\uFF0C\u6446\u4E86\u4E2A\u52A8\u4EBA\u7684\u5A9A\u6001\uFF0C\u751C\u751C\u7B11\u9053\uFF1A\u300C\u4EBA\u5BB6\u820D\u5F97\u5356\u5C41\u80A1\u54E6\uFF0C\u54EA\u4E2A\u7537\u4EBA\u80FD\u4E0D\u52A8\u5FC3\u5462\u3002\u300D\u8BF4\u7740\u5979\u62FF\u8D77\u521A\u624D\u6240\u7528\u7684\u80ED\u8102\u76D2\uFF0C\u300C\u597D\u751C\u5462\uFF0C\u59D0\u59D0\uFF0C\u8FD9\u4E2A\u7ED9\u6211\u597D\u4E0D\u597D\uFF1F\u300D</p><p>\u767D\u7389\u83BA\u4E00\u8FB9\u628A\u80ED\u8102\u76D2\u585E\u5230\u5979\u8896\u4E2D\uFF0C\u4E00\u8FB9\u9053\uFF1A\u300C\u4F60\u5982\u679C\u89C1\u8FC7\u4ED6\u6700\u5FC3\u7231\u7684\u4E24\u4E2A\u8D31\u8D27\u662F\u4EC0\u4E48\u4E0B\u573A\uFF0C\u5C31\u77E5\u9053\u4ED6\u6839\u672C\u4E0D\u662F\u4EBA\u3002\u300D</p><p>\u9759\u989C\u60F3\u8D77\u90A3\u4E2A\u4E00\u76F4\u672A\u89C1\u8FC7\u7684\u7EA2\u8863\u5973\u5B50\uFF0C\u5979\u5E94\u8BE5\u5C31\u662F\u5C0F\u516C\u4E3B\u7684\u751F\u6BCD\u4E86\uFF0C\u4E3A\u4F55\u8FDE\u5983\u5B50\u4E5F\u6CA1\u6709\u5C01\u5462\uFF1F</p><p>\u767D\u7389\u9E42\u62E5\u4F4F\u5979\u7684\u8170\u8EAB\uFF0C\u8D34\u5728\u8033\u8FB9\u8BF4\uFF1A\u300C\u5982\u679C\u4F60\u618B\u4E0D\u4F4F\uFF0C\u60F3\u5E72\u5973\u4EBA\uFF0C\u5343\u4E07\u4E0D\u8981\u78B0\u90A3\u91CC\u7684\u5973\u5974\u2014\u2014\u4F1A\u9732\u4E86\u9A6C\u811A\u7684\u3002\u60F3\u8981\uFF0C\u5C31\u627E\u59D0\u59D0\u966A\u4F60\u5F00\u5FC3\u3002\u8FD8\u6709\uFF0C\u5343\u4E07\u63D0\u9632\u5C0F\u516C\u4E3B\uFF0C\u5979\u5F88\u5389\u5BB3\u7684\u2026\u2026\u300D</p><p>\u6709\u4E00\u4E2A\u95EE\u9898\uFF0C\u5DF2\u7ECF\u5728\u5FC3\u91CC\u618B\u4E86\u8BB8\u4E45\u3002\u9759\u989C\u6162\u6162\u8C03\u7740\u9999\u8102\uFF0C\u5F77\u4F5B\u65E0\u610F\u5730\u95EE\u4E86\u4E00\u53E5\uFF0C\u300C\u5C0F\u516C\u4E3B\u53EB\u4EC0\u4E48\u540D\u5B57\uFF1F\u300D</p><p>\u300C\u6674\u96EA\u3002\u6155\u5BB9\u6674\u96EA\u3002\u300D\u767D\u7389\u83BA\u6DE1\u6DE1\u9053\u3002</p>',110);function t(_,c){return a}var d=e(r,[["render",t]]);export{d as default};
